INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12505, 5, 12523, "", 6, 1, "「地獄の番犬」と呼ばれる、三つ首の怪物", 12465, 2, "ケルベロス", "これ、何と言う？", 5, 6090, 12525, 5, 0, 5, 12473, "ケルベロス");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12488, 5, 12523, "", 6, 3, "有名な三つ首の犬の怪物は「ケルベロス」。ではその兄弟で、二つ首の怪物は？", 12458, 2, "オルトロス", "これ、何と言う？", 5, 6091, 12525, 5, 0, 5, 12473, "オルトロス");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (4, 12418, 4, 12367, "", 6, 2, "漢字では「付喪神」と書く、長い年月が経った道具などに宿るとされる神様", 12388, 2, "つくもがみ", "これ、何と言う？", 4, 6092, 12364, 4, 0, 4, 12415, "つくもがみ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (4, 12425, 4, 12410, "のっぺらぼう", 6, 1, "とても有名な妖怪。目も鼻も口も無いまっさらな顔をしている", 12398, 2, "のぺらぼう", "これ、何と言う？", 4, 6093, 12412, 4, 0, 4, 12358, "のっぺらぼう");
