INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (4, 12429, 4, 12367, "", 6, 1, "首が長く伸びる妖怪。普通にしている分には、人間と変わらない姿をしている", 12429, 2, "ろくろくび", "これ、何と言う？", 4, 6160, 12367, 4, 0, 4, 12403, "ろくろくび");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (4, 12356, 4, 12414, "", 6, 2, "つむじ風に乗って現れ、人を切りつける妖怪。痛みは無く、血も出ず、傷だけが残ると言われる", 12363, 2, "かまいたち", "これ、何と言う？", 4, 6161, 12383, 4, 0, 4, 12385, "かまいたち");
