INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12525, 5, 12460, "", 6, 3, "太古の海に存在していた、史上最大のサメ", 12513, 2, "メガロドン", "これ、何と言う？", 5, 6168, 12489, 5, 0, 5, 12531, "メガロドン");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (4, 12365, 4, 12392, "", 6, 3, "「犬歯」の通称として、「八重歯」以外になじみ深いもの。糸が切れるほど尖っているのが由来", 12356, 2, "いときりば", "これ、何と言う？", 4, 6169, 12426, 4, 0, 4, 12400, "いときりば");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12471, 5, 12493, "カーネーション", 6, 1, "「母の日」の贈り物としても親しまれる花。赤やピンクの鮮やかな花びらを持つ", 12459, 2, "カネション", "これ、何と言う？", 5, 6170, 12519, 5, 0, 5, 12531, "カーネーション");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12520, 5, 12452, "", 6, 2, "桜の木の種類として非常にポピュラーな一つ。一般に桜のイメージとして最も浸透している", 12513, 2, "メイヨシノ", "これ、何と言う？", 5, 6171, 12471, 5, 0, 5, 12494, "ソメイヨシノ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12452, 5, 12521, "", 6, 3, "「タンポポ」の英名。とある哺乳類の動物に由来している", 12487, 2, "デライオン", "これ、何と言う？", 5, 6172, 12458, 5, 0, 5, 12531, "ダンデライオン");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (4, 12426, 4, 12356, "", 6, 3, "「四聖獣」と呼ばれる生き物、朱雀・白虎・玄武、あとは何？（ひらがなで）", 12379, 2, "せいりゅう", "これ、何と言う？", 4, 6173, 12421, 4, 0, 4, 12358, "せいりゅう");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (4, 12431, 4, 25975, "", 6, 1, "小さな子どもの姿で、座敷や土間に現れる妖怪。遭遇した者には幸運が舞い込むとされる", 24231, 2, "座敷わらし", "これ、何と言う？", 4, 6174, 12425, 6, 0, 6, 12375, "座敷わらし");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12481, 5, 12531, "", 6, 2, "猫の種類の一つ。「小さい」「子ども」という意味を持ち、小柄で脚が短いのが特徴", 12510, 2, "マンチカン", "これ、何と言う？", 5, 6175, 12459, 5, 0, 5, 12531, "マンチカン");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12521, 5, 12517, "", 6, 2, "伝承に登場する、首の無い騎士", 12487, 2, "デュラハン", "これ、何と言う？", 5, 6176, 12495, 5, 0, 5, 12531, "デュラハン");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12489, 5, 12483, "", 6, 2, "両目でそれぞれ瞳の色が異なること。いろいろな動物に見られるが、特に猫に多い", 12458, 2, "オッドアイ", "これ、何と言う？", 5, 6177, 12450, 5, 0, 5, 12452, "オッドアイ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (4, 12419, 4, 12376, "", 6, 1, "「エノコログサ」という草の通称。猫の目の前で振ると興味を示し、よくじゃれつくことから", 29483, 2, "猫じゃらし", "これ、何と言う？", 4, 6178, 12425, 4, 0, 6, 12375, "猫じゃらし");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12461, 5, 12521, "", 6, 1, "非常によく知られた怪物。獲物の血を吸うのが特徴で、十字架やニンニクが弱点とされる", 12489, 2, "ドラキュラ", "これ、何と言う？", 5, 6179, 12517, 5, 0, 5, 12521, "ドラキュラ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12525, 5, 12467, "", 6, 1, "動物のフンをボール状にして、コロコロ転がすという変わった習性を持つ昆虫", 12531, 2, "ンコロガシ", "これ、何と言う？", 5, 6180, 12460, 5, 0, 5, 12471, "フンコロガシ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12489, 5, 12464, "", 6, 3, "「ぬいぐるみ」という意味を持つ、猫の種類。抱っこが好きで、毛並みがフサフサなことから", 12521, 2, "ラグドール", "これ、何と言う？", 5, 6181, 12540, 5, 0, 5, 12523, "ラグドール");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12478, 5, 12461, "エキゾチック・ショートヘア", 6, 3, "猫の種類の一つ。鼻が低く、ムスッとしたような愛嬌のある顔立ちが特徴的", 12456, 2, "エキゾチク", "これ、何と言う？", 5, 6182, 12481, 5, 0, 5, 12463, "エキゾチック・ショートヘア");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (4, 12423, 4, 12375, "", 6, 2, "ハトの帰巣本能を使って遠くに手紙を届けること。通信技術が発達する前は、広く活用された", 12435, 2, "んしょばと", "これ、何と言う？", 4, 6183, 12400, 4, 0, 4, 12392, "でんしょばと");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12465, 5, 12507, "ホーホケキョ", 6, 2, "「ウグイス」の鳴き声を文字に起こした表現。春の訪れの象徴としてもなじみ深い", 12507, 2, "ホホケキョ", "これ、何と言う？", 5, 6184, 12461, 5, 0, 5, 12519, "ホーホケキョ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12488, 5, 12488, "", 6, 1, "武将の性格を比喩した「鳴かぬなら～～」の俳句に使われている鳥の名前は？", 12507, 2, "ホトトギス", "これ、何と言う？", 5, 6185, 12462, 5, 0, 5, 12473, "ホトトギス");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12452, 5, 12499, "", 6, 2, "「ヘビがいそうな場所に生える」ことに由来する、イチゴに似た植物。食用には向かない", 12504, 2, "ヘビイチゴ", "これ、何と言う？", 5, 6186, 12481, 5, 0, 5, 12468, "ヘビイチゴ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (4, 12387, 4, 12412, "", 6, 2, "松かさとも呼ばれる、概略すると松の木の果実の部分。クリスマスツリーの飾りにもよく使われる", 26494, 2, "松ぼっくり", "これ、何と言う？", 4, 6187, 12367, 4, 0, 6, 12426, "松ぼっくり");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12540, 5, 12478, "", 6, 1, "", 12450, 1, "アゾールド", "このキャラの名前は？", 5, 6188, 12523, 5, 6188, 5, 12489, "アゾールド");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (4, 12385, 4, 12490, "", 6, 3, "", 12474, 1, "ズナちょむ", "左のキャラは右のキャラを何と呼ぶ？", 4, 6189, 12423, 5, 6189, 5, 12416, "ズナちょむ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12540, 5, 12457, "", 6, 2, "猫の種類の一つ。垂れた耳が特徴的", 12501, 2, "フォールド", "これ、何と言う？", 5, 6190, 12523, 5, 0, 5, 12489, "スコティッシュフォールド");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12523, 5, 12540, "", 6, 1, "非常にポピュラーな種の犬で、フサフサとした黄色系の体毛と、大きな体が特徴", 12468, 2, "ゴールデン", "これ、何と言う？", 5, 6191, 12487, 5, 0, 5, 12531, "ゴールデンレトリーバー");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12464, 5, 12483, "", 6, 2, "家庭犬がリードをつけずに自由に走り回れるように設けられた、フェンスで囲われた広いスペース", 12489, 2, "ドッグラン", "これ、何と言う？", 5, 6192, 12521, 5, 0, 5, 12531, "ドッグラン");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12521, 5, 12502, "ラブラドールレトリーバー", 6, 1, "レトリーバー種としてゴールデンレトリーバーに並び有名な犬。盲導犬・警察犬としても活躍する", 12521, 2, "ラブラドル", "これ、何と言う？", 5, 6193, 12489, 5, 0, 5, 12523, "ラブラドールレトリーバー");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12461, 5, 12540, "", 6, 1, "動物が自分の体臭や尿などを残すことで、そこが自分のなわばりであるとアピールする習性", 12510, 2, "マーキング", "これ、何と言う？", 5, 6194, 12531, 5, 0, 5, 12464, "マーキング");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (4, 12371, 6, 12356, "", 6, 2, "しきりに偉い人のご機嫌をとるような人間を、ある楽器の名前を使ってなんと呼ぶ？", 12383, 2, "たいこ持ち", "これ、何と言う？", 4, 6195, 25345, 4, 0, 4, 12385, "たいこ持ち");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12462, 5, 12522, "", 6, 1, "有名な童話の中で、「アリ」と対比して、なまけ者として描かれている昆虫", 12461, 2, "キリギリス", "これ、何と言う？", 5, 6196, 12522, 5, 0, 5, 12473, "キリギリス");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12510, 5, 12523, "", 6, 1, "エビの一種。身体にクッキリとシマシマがあり、丸まった姿が車のホイールに見える", 12463, 2, "クルマエビ", "これ、何と言う？", 5, 6197, 12456, 5, 0, 5, 12499, "クルマエビ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12471, 5, 12452, "", 6, 3, "ヘビの一種で、身体は青緑色。由来は諸説あるが「ヘビの中でも特に大物の風格がある」ことなど", 12480, 2, "ダイショウ", "これ、何と言う？", 5, 6198, 12519, 5, 0, 5, 12454, "アオダイショウ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (4, 12385, 4, 12381, "", 6, 2, "太古の時代に存在した鳥で、その名の通り、鳥類の始まりとなった鳥とされている", 12375, 2, "しそちょう", "これ、何と言う？", 4, 6199, 12423, 4, 0, 4, 12358, "しそちょう");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12473, 5, 12454, "", 6, 2, "「最古の人類」とされている種類の猿人", 12450, 2, "アウストラ", "これ、何と言う？", 5, 6200, 12488, 5, 0, 5, 12521, "アウストラロピテクス");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12454, 5, 12517, "", 6, 1, "ペットとしても親しまれる手乗りサイズの鳥で、人の声をマネして鳴くのが上手い", 12461, 2, "キュウカン", "これ、何と言う？", 5, 6201, 12459, 5, 0, 5, 12531, "キュウカンチョウ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12488, 5, 12483, "", 6, 2, "姿が似ていることで「アシカ」と混同されがちな哺乳類。分類上も、「アシカ科」に含まれる", 12458, 2, "オットセイ", "これ、何と言う？", 5, 6202, 12475, 5, 0, 5, 12452, "オットセイ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12450, 5, 12472, "", 6, 1, "「象」の種類、1つは「アフリカゾウ」で、もう一つは？", 12450, 2, "アジアゾウ", "これ、何と言う？", 5, 6203, 12478, 5, 0, 5, 12454, "アジアゾウ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12471, 5, 12491, "", 6, 3, "ちょうちょの種類の一つ。色は紅色で、また羽のシルエットが「とある貝類」に似ている", 12505, 2, "ベニシジミ", "これ、何と言う？", 5, 6204, 12472, 5, 0, 5, 12511, "ベニシジミ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12454, 5, 12517, "", 6, 2, "テントウムシの一種。身体に13個の点々模様がある", 12472, 2, "ジュウサン", "これ、何と言う？", 5, 6205, 12469, 5, 0, 5, 12531, "ジュウサンホシテントウ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12517, 5, 12472, "", 6, 3, "テントウムシの一種。身体に28個の点々模様がある", 12491, 2, "ニジュウヤ", "これ、何と言う？", 5, 6206, 12454, 5, 0, 5, 12516, "ニジュウヤホシテントウ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12469, 5, 12491, "", 6, 2, "生の魚を食べる際にはよく注意する必要がある、有名な寄生虫。白い糸くずのような姿", 12450, 2, "アニサキス", "これ、何と言う？", 5, 6207, 12461, 5, 0, 5, 12473, "アニサキス");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (4, 12426, 4, 12383, "", 6, 1, "季節の移ろいに合わせて長距離を移動する鳥たちの総称。ハクチョウなどが有名", 12431, 2, "わたりどり", "これ、何と言う？", 4, 6208, 12393, 4, 0, 4, 12426, "わたりどり");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12508, 5, 12531, "", 6, 3, "ペンギンの一種で、身体を横切る黒いラインが特徴的。動物園で見るペンギンはこれが最も多い", 12501, 2, "フンボルト", "これ、何と言う？", 5, 6209, 12523, 5, 0, 5, 12488, "フンボルトペンギン");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12471, 5, 12508, "", 6, 3, "鳥の一種。その名の通り、頭の羽が「烏帽子」を被っているようなシルエットをしている", 12456, 2, "エボシドリ", "これ、何と言う？", 5, 6210, 12489, 5, 0, 5, 12522, "エボシドリ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12521, 5, 12470, "ネザーランドドワーフ", 6, 3, "ウサギの種類として最もよく知られたもの。身体は小さめで、耳がピンと立っている", 12493, 2, "ネザランド", "これ、何と言う？", 5, 6211, 12531, 5, 0, 5, 12489, "ネザーランドドワーフ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12521, 5, 12540, "", 6, 3, "ウサギの種類としてよく知られた一つ。最大の特徴は、垂れ下がった大きな耳", 12507, 2, "ホーランド", "これ、何と言う？", 5, 6212, 12531, 5, 0, 5, 12489, "ホーランドロップ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12524, 5, 12455, "", 6, 2, "ペットとしてもよく親しまれるイタチの一種。長い胴体に短い脚の独特なフォルムをしている", 12501, 2, "フェレット", "これ、何と言う？", 5, 6213, 12483, 5, 0, 5, 12488, "フェレット");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12488, 5, 12531, "", 6, 2, "大別すると猿の仲間にあたる動物で、マントを羽織っているように見える特徴的な体毛を持つ", 12510, 2, "マントヒヒ", "これ、何と言う？", 5, 6214, 12498, 5, 0, 5, 12498, "マントヒヒ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12490, 5, 12525, "", 6, 2, "最も大きい動物としてよく知られる種類のクジラ", 12471, 2, "シロナガス", "これ、何と言う？", 5, 6215, 12460, 5, 0, 5, 12473, "シロナガスクジラ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12452, 5, 12525, "", 6, 3, "普通のイルカほど口が尖っておらず、のほほんとした顔立ちで人気の真っ白なイルカ", 12471, 2, "シロイルカ", "これ、何と言う？", 5, 6216, 12523, 5, 0, 5, 12459, "シロイルカ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (4, 12358, 4, 12429, "", 6, 2, "神話に登場する、有名な動物", 12375, 2, "しろうさぎ", "これ、何と言う？", 4, 6217, 12373, 4, 0, 4, 12366, "いなばのしろうさぎ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12500, 5, 12467, "さそり＝スコーピオン", 6, 1, "カタカナでの呼び方は？", 12473, 2, "スコピオン", "これ、何と言う？", 5, 6218, 12458, 5, 0, 5, 12531, "さそり＝スコーピオン");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (4, 12425, 4, 12363, "", 6, 2, "腕を曲げて力を込めた時に、二の腕のポッコリと盛り上がる部分の俗称", 12385, 2, "ちからこぶ", "これ、何と言う？", 4, 6219, 12371, 4, 0, 4, 12406, "ちからこぶ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (4, 12363, 4, 12427, "", 6, 2, "昔ばなしの一つ。とある生き物が、自分の親の仇を討つために、様々な仲間を集めて仕返しをする", 12373, 2, "さるかに合", "これ、何と言う？", 6, 6220, 12395, 4, 0, 4, 21512, "さるかに合戦");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12497, 5, 12531, "", 6, 1, "よく親しまれた替え歌の歌詞。ある動物の名前が入る", 12481, 2, "チンパンジ", "これ、何と言う？", 5, 6221, 12531, 5, 0, 5, 12472, "♪サル、ゴリラ、チンパンジー");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12452, 5, 12458, "", 6, 3, "釣り人には釣り餌として非常におなじみの、ミミズのような虫", 12450, 2, "アオイソメ", "これ、何と言う？", 5, 6222, 12477, 5, 0, 5, 12513, "アオイソメ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12531, 5, 12513, "", 6, 3, "精神力が強く、何があってもへこたれない人の俗的な呼び方。「とある金属」にたとえられる", 37628, 2, "鋼メンタル", "これ、何と言う？", 5, 6223, 12479, 5, 0, 6, 12523, "鋼メンタル");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12498, 5, 12478, "", 6, 3, "クウカに代表される「ドM」という言葉、この「M」は何という言葉の略？", 12510, 1, "マゾヒスト", "これ、何と言う？", 5, 6224, 12473, 5, 6224, 5, 12488, "マゾヒスト");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12488, 5, 12450, "", 6, 3, "太古に実在した、非常に大きな翼竜。神話に登場する神様から名付けられた", 12467, 2, "コアトルス", "これ、何と言う？", 5, 6225, 12523, 5, 0, 5, 12473, "ケツァルコアトルス");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12484, 5, 12490, "", 6, 3, "ほら穴の中に巣を作ることが多いツバメで、高級食材である「ツバメの巣」はこの種のもの", 12450, 2, "アナツバメ", "これ、何と言う？", 5, 6226, 12496, 5, 0, 5, 12513, "アナツバメ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12481, 5, 12531, "", 6, 3, "鳥の「ツル」で、最もなじみの深い種。頭のてっぺん（頂）が、丹色であることに由来する", 12479, 2, "タンチョウ", "これ、何と言う？", 5, 6227, 12519, 5, 0, 5, 12454, "タンチョウヅル");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12479, 5, 12522, "", 6, 3, "ちょうちょの種類の一つ。キレイな瑠璃色の羽を立てた状態で休むのが特徴", 12523, 2, "ルリタテハ", "これ、何と言う？", 5, 6228, 12486, 5, 0, 5, 12495, "ルリタテハ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12531, 5, 12522, "", 6, 1, "特定の相手の能力\nを高める権能を持\nった存在", 12503, 1, "プリンセス", "これ、何と言う？", 5, 6229, 12475, 5, 6229, 5, 12473, "プリンセスナイト");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (4, 12416, 4, 12467, "", 6, 3, "", 12493, 1, "ネコむすめ", "このパネルの「プリコネ読み」は？", 4, 6230, 12377, 5, 6230, 5, 12417, "ネコむすめ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (6, 21361, 6, 28357, "", 6, 2, "数が減少し、絶滅の危機に晒されている生物", 32118, 2, "絶滅危惧種", "これ、何と言う？", 6, 6231, 24807, 6, 0, 6, 31278, "絶滅危惧種");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12521, 5, 12502, "", 6, 1, "よく知られたセミの一種。「ジージー」という鳴き声が、揚げ物料理をするときの音に似ている", 12450, 2, "アブラゼミ", "これ、何と言う？", 5, 6232, 12476, 5, 0, 5, 12511, "アブラゼミ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12524, 5, 12461, "", 6, 1, "足首の後ろ側にある大きな腱で、神話上のとある英雄の、唯一の弱点として描かれている", 12450, 2, "アキレス腱", "これ、何と言う？", 6, 6233, 12473, 5, 0, 5, 33137, "アキレス腱");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12471, 5, 12523, "", 6, 1, "自己愛が非常に強く、自分に陶酔した人、英語にすると？", 12490, 1, "ナルシスト", "これ、何と言う？", 5, 6234, 12473, 5, 6234, 5, 12488, "ナルシスト");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (4, 12385, 4, 12435, "", 6, 3, "", 12384, 1, "だんちょう", "左のキャラは右のキャラを何と呼ぶ？", 4, 6235, 12423, 4, 6235, 4, 12358, "だんちょう");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (4, 12394, 4, 12356, "", 6, 2, "若いうちから結婚が約束された相手のこと", 12356, 2, "いいなずけ", "これ、何と読む？", 4, 6236, 12378, 4, 0, 4, 12369, "許嫁＝いいなずけ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (4, 12364, 4, 12383, "", 6, 3, "「古事記」などに登場する、三本足の神聖なカラス", 12420, 2, "やたがらす", "これ、何と言う？", 4, 6237, 12425, 4, 0, 4, 12377, "やたがらす");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (4, 12376, 4, 12372, "", 6, 2, "「西遊記」で孫悟空と行動を共にする、河童の姿のキャラクター", 12373, 2, "さごじょう", "これ、何と言う？", 4, 6238, 12423, 4, 0, 4, 12358, "さごじょう");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12540, 5, 12451, "", 6, 2, "カジノで、ルーレットを回したりカードを配ったりとゲームの進行を行う役目", 12487, 2, "ディーラー", "これ、何と言う？", 5, 6239, 12521, 5, 0, 5, 12540, "ディーラー");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12503, 5, 12531, "", 6, 3, "「皇帝」は「エンペラー」。では「女帝」は？", 12456, 2, "エンプレス", "これ、何と言う？", 5, 6240, 12524, 5, 0, 5, 12473, "エンプレス");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12473, 5, 12531, "", 6, 3, "銃のメンテナンスを職業として行う人のこと", 12460, 2, "ガンスミス", "これ、何と言う？", 5, 6241, 12511, 5, 0, 5, 12473, "ガンスミス");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12499, 5, 12452, "", 6, 1, "熱帯の地域を象徴する花で、カオリの故郷でも親しまれる。赤や黄色の鮮やかな花びらをつける", 12495, 1, "ハイビスカ", "これ、何と言う？", 5, 6242, 12473, 5, 0, 5, 12459, "ハイビスカス");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12451, 5, 12454, "ウィル・オ・ウィスプ", 6, 3, "「ひとだま」と呼ばれる超常現象、英語で言うと？", 12458, 2, "オウィスプ", "これ、何と言う？", 5, 6243, 12473, 5, 0, 5, 12503, "ウィル・オ・ウィスプ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12461, 5, 12471, "", 6, 2, "魚の「鯉」の中でも観賞用として好まれる種で、高級な織物の柄によく似ていることが由来", 12491, 2, "ニシキゴイ", "これ、何と言う？", 5, 6244, 12468, 5, 0, 5, 12452, "ニシキゴイ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12497, 5, 12469, "レッサーパンダ", 6, 1, "「小さなパンダ」が由来の、動物園の人気者", 12524, 2, "レサパンダ", "これ、何と言う？", 5, 6245, 12531, 5, 0, 5, 12480, "レッサーパンダ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12467, 5, 12490, "", 6, 1, "ヘビの一種で、大きなものでは数メートルもある。映画で恐ろしいヘビとして描かれることが多い", 12450, 2, "アナコンダ", "これ、何と言う？", 5, 6246, 12531, 5, 0, 5, 12480, "アナコンダ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12531, 5, 12521, "", 6, 1, "「毒グモの代名詞」としてよく知られる大型のクモ。しかし、ペットとして好む人も多い", 12479, 2, "タランチュ", "これ、何と言う？", 5, 6247, 12481, 5, 0, 5, 12517, "タランチュラ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12462, 5, 12452, "", 6, 1, "「牛」の一種で、地域によって家畜としてもなじみ深い動物。その名の通り、水辺を好む", 12473, 2, "スイギュウ", "これ、何と言う？", 5, 6248, 12517, 5, 0, 5, 12454, "スイギュウ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12473, 5, 12512, "", 6, 1, "ネズミの仲間にあたる動物で、ヒマワリの種などを口いっぱいに頬張る姿が象徴的", 12495, 2, "ハムスター", "これ、何と言う？", 5, 6249, 12479, 5, 0, 5, 12540, "ハムスター");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12493, 5, 12522, "", 6, 1, "身を守るためのトゲトゲの体毛が非常に特徴的な動物。愛好家も多く、英名は「ヘッジホッグ」", 12495, 2, "ハリネズミ", "これ、何と言う？", 5, 6250, 12474, 5, 0, 5, 12511, "ハリネズミ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12454, 5, 12519, "", 6, 3, "猛毒を持つタコ。蛍光色のヒョウ柄の模様が、恐ろしさを助長している", 12498, 2, "ヒョウモン", "これ、何と言う？", 5, 6251, 12514, 5, 0, 5, 12531, "ヒョウモンダコ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12524, 5, 12513, "", 6, 3, "ネネカといつも一緒にいる動物", 12459, 1, "カメレオン", "これ、何と言う？", 5, 6252, 12458, 5, 6252, 5, 12531, "カメレオン");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (4, 12385, 4, 12452, "", 6, 3, "『ミサキ』がいつも一緒にいる、生きた杖の名前", 12450, 1, "アイちゃん", "これ、何と言う？", 4, 6253, 12419, 5, 6048, 5, 12435, "アイちゃん");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (4, 12365, 4, 12367, "", 6, 1, "ネコ目の動物の足にある、ぷにぷにとやわらかい部分の通称", 12395, 2, "にくきゅう", "これ、何と言う？", 4, 6254, 12421, 4, 0, 4, 12358, "にくきゅう");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (4, 12398, 6, 24746, "", 6, 3, "", 35576, 1, "諸悪の根源", "初対面のとき何と呼んだ？", 6, 6255, 26681, 6, 6255, 6, 28304, "諸悪の根源");
