INSERT INTO `v1_b67b73e44c571cf7c4a1dcc64c8aafdef39555d75d0334f53a53f7a4c666b8c5` VALUES (30003, 3, 9, 0, "リンのナッツ入りぐうたらあんパン", "リンからもらった非常食のあんパン。ナッツが\n練り込まれクランチな食感で食べ飽きない。\nゲームのため部屋に引きこもる際のお供であり、\n必要な栄養素が全て賄える完全栄養食、らしい。", "リンの\nナッツ入りぐうたらあんパン", "このターンに振るダイスの目が「1・2」のみに\nなる代わりに、その場で福引券を3枚獲得する。", "なし", 500, 2, 0, 10, 52028, 1, 1, 51028);
INSERT INTO `v1_b67b73e44c571cf7c4a1dcc64c8aafdef39555d75d0334f53a53f7a4c666b8c5` VALUES (30004, 2, 0, 0, "シェフィの氷晶煌めく手作りアイス", "シェフィが自身の魔力の氷を使い作ったアイス。\nペコリーヌにねだられて作ったものだがキャル達\nにも好評である。コッコロ曰くひんやりしている\nのに食べると心が温かくなってきます、との事。", "シェフィの\n氷晶煌めく手作りアイス", "このターンに振るダイスの目が必ず「2」になる。さらに、次のターン数のカウントをスキップする。", "なし", 500, 3, 0, 0, 52029, 1, 1, 51029);
INSERT INTO `v1_b67b73e44c571cf7c4a1dcc64c8aafdef39555d75d0334f53a53f7a4c666b8c5` VALUES (1, 1, 0, 0, "ミソラの気まぐれ味噌ラーメン★", "ミソラが作ったラーメン。適当に言った好きな物\nをランファが憶えていたために、材料を用意され\n仕方なく作ったものだが味はイイコト級の一品。\n美味しそうに食べられるのは嫌いではない様子。", "ミソラの\n気まぐれ味噌ラーメン★", "3ターンの間、すべての「マイルマス」をランク3の「トレジャーマス」に変化させる。", "なし", 500, 12, 0, 0, 52030, 3, 3, 51030);
INSERT INTO `v1_b67b73e44c571cf7c4a1dcc64c8aafdef39555d75d0334f53a53f7a4c666b8c5` VALUES (20009, 3, 0, 0, "イロハの最強クワガタゼリー（人間用）", "イロハがいつも持ち歩いているカラフルなゼリー\n…なのだがアルケス錬金堂で作られたクワガタ用\n餌ゼリーとそっくりな見た目をしている。甘くて\n美味しいが、人間用か尋ねると目を逸らされる。", "イロハの\n最強クワガタゼリー（人間用）", "このターンに振るダイスの目が「1・6・6・6・8・8」になる。", "なし", 500, 2, 0, 0, 52031, 1, 1, 51031);
INSERT INTO `v1_b67b73e44c571cf7c4a1dcc64c8aafdef39555d75d0334f53a53f7a4c666b8c5` VALUES (4, 3, 3, 0, "グレイスの目ん玉飛び出るカップケーキ", "グレイスお手製のカップケーキ。ショッキングな\n見た目に全振り…と思わせて目玉も食べることが\n可能で大変美味…と思わせて一部だけ魂消るほど\n甘い部分がある。原材料は頑なに秘されている。", "グレイスの\n目ん玉飛び出るカップケーキ", "料理マスをスキップして移動できる。効果は2ターンの間継続する。", "次にダイスを振った際、3マス多く進める。", 500, 9, 0, 11, 52032, 2, 2, 51032);
INSERT INTO `v1_b67b73e44c571cf7c4a1dcc64c8aafdef39555d75d0334f53a53f7a4c666b8c5` VALUES (3, 1, 0, 0, "ソノの今日は特別ご褒美オムライス", "ソノが作ったオムライス。イロハの大好物であり\n家庭の味。普段は面倒くさがりやらないがイロハ\nが良い子の時はケチャップでクワガタを描いて\nあげている。その時は普段より美味しいらしい。", "ソノの今日は特別ご褒美オムライス", "RANK3のマイルショップを開く。", "なし", 500, 8, 0, 0, 52033, 0, 3, 51033);
