INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("エリコ", "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", 0.0, 100, 129901, 10142, 1, 10.85, "2024/03/31 12:00:00", 7, 24, 0.0, "2030/12/17 14:59:59", 100);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("エリコ", "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", 0.0, 101, 129901, 10142, 1, 11.15, "2024/03/31 12:00:00", 2, 24, 0.0, "2030/12/17 14:59:59", 101);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("エリコ", "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", 0.0, 102, 129901, 10142, 7, 6.4, "2024/03/31 12:00:00", 5, 24, 0.0, "2030/12/17 14:59:59", 102);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("エリコ", "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", 0.0, 103, 129901, 10142, 3, 0.0, "2024/03/31 12:00:00", 3, 24, 0.0, "2030/12/17 14:59:59", 103);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("エリコ", "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", 0.0, 104, 129901, 10142, 2, 2.43, "2024/03/31 12:00:00", 8, 24, 0.0, "2030/12/17 14:59:59", 104);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("エリコ", "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", 0.0, 105, 129901, 10142, 4, 9.08, "2024/03/31 12:00:00", 3, 24, 0.0, "2030/12/17 14:59:59", 105);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("エリコ", "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", 0.0, 106, 129901, 10142, 6, 0.0, "2024/03/31 12:00:00", 6, 24, 0.0, "2030/12/17 14:59:59", 106);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("エリコ", "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", 0.0, 107, 129901, 10142, 3, 10.6, "2024/03/31 12:00:00", 8, 24, 0.0, "2030/12/17 14:59:59", 107);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("エリコ", "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", 0.0, 108, 129901, 10142, 6, 7.54, "2024/03/31 12:00:00", 4, 24, 0.0, "2030/12/17 14:59:59", 108);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("エリコ", "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", 0.0, 109, 129901, 10142, 2, 11.5, "2024/03/31 12:00:00", 7, 24, 0.0, "2030/12/17 14:59:59", 109);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("エリコ", "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", 0.0, 110, 129901, 10142, 2, 8.83, "2024/03/31 12:00:00", 1, 24, 0.0, "2030/12/17 14:59:59", 110);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("エリコ", "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", 0.0, 111, 129901, 10142, 1, 6.68, "2024/03/31 12:00:00", 2, 24, 0.0, "2030/12/17 14:59:59", 111);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("エリコ", "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", 0.0, 112, 129901, 10142, 6, 8.8, "2024/03/31 12:00:00", 2, 24, 0.0, "2030/12/17 14:59:59", 112);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("エリコ", "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", 0.0, 113, 129901, 10142, 2, 8.9, "2024/03/31 12:00:00", 1, 24, 0.0, "2030/12/17 14:59:59", 113);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("エリコ", "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", 0.0, 114, 129901, 10142, 2, 3.7, "2024/03/31 12:00:00", 5, 24, 0.0, "2030/12/17 14:59:59", 114);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("マコト", "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", 0.0, 200, 129801, 10142, 1, 7.6, "2024/03/31 12:00:00", 2, 24, 0.0, "2030/12/17 14:59:59", 200);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("マコト", "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", 0.0, 201, 129801, 10142, 2, 2.25, "2024/03/31 12:00:00", 1, 24, 0.0, "2030/12/17 14:59:59", 201);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("マコト", "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", 0.0, 202, 129801, 10142, 2, 3.28, "2024/03/31 12:00:00", 1, 24, 0.0, "2030/12/17 14:59:59", 202);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("マコト", "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", 0.0, 203, 129801, 10142, 4, 2.98, "2024/03/31 12:00:00", 1, 24, 0.0, "2030/12/17 14:59:59", 203);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("マコト", "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", 0.0, 204, 129801, 10142, 4, 3.0, "2024/03/31 12:00:00", 1, 24, 0.0, "2030/12/17 14:59:59", 204);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("マコト", "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", 0.0, 205, 129801, 10142, 1, 10.3, "2024/03/31 12:00:00", 2, 24, 0.0, "2030/12/17 14:59:59", 205);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("マコト", "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", 0.0, 206, 129801, 10142, 6, 2.93, "2024/03/31 12:00:00", 1, 24, 0.0, "2030/12/17 14:59:59", 206);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("マコト", "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", 0.0, 207, 129801, 10142, 3, 9.3, "2024/03/31 12:00:00", 1, 24, 0.0, "2030/12/17 14:59:59", 207);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("マコト", "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", 0.0, 208, 129801, 10142, 4, 6.9, "2024/03/31 12:00:00", 1, 24, 0.0, "2030/12/17 14:59:59", 208);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("マコト", "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", 0.0, 209, 129801, 10142, 2, 0.0, "2024/03/31 12:00:00", 2, 24, 0.0, "2030/12/17 14:59:59", 209);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("マコト", "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", 0.0, 210, 129801, 10142, 1, 5.95, "2024/03/31 12:00:00", 2, 24, 0.0, "2030/12/17 14:59:59", 210);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("マコト", "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", 0.0, 211, 129801, 10142, 2, 6.73, "2024/03/31 12:00:00", 1, 24, 0.0, "2030/12/17 14:59:59", 211);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("マコト", "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", 0.0, 212, 129801, 10142, 2, 0.0, "2024/03/31 12:00:00", 2, 24, 0.0, "2030/12/17 14:59:59", 212);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("マコト", "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", 0.0, 213, 129801, 10142, 1, 6.65, "2024/03/31 12:00:00", 2, 24, 0.0, "2030/12/17 14:59:59", 213);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("マコト", "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", 0.0, 214, 129801, 10142, 6, 3.05, "2024/03/31 12:00:00", 1, 24, 0.0, "2030/12/17 14:59:59", 214);
