INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("2030/12/17 14:59:59", 129901, 100, 0.0, "2024/03/31 12:00:00", "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", 7, "エリコ", 1, 10142, 24, 100, 10.85, 0.0);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("2030/12/17 14:59:59", 129901, 101, 0.0, "2024/03/31 12:00:00", "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", 2, "エリコ", 1, 10142, 24, 101, 11.15, 0.0);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("2030/12/17 14:59:59", 129901, 102, 0.0, "2024/03/31 12:00:00", "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", 5, "エリコ", 7, 10142, 24, 102, 6.4, 0.0);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("2030/12/17 14:59:59", 129901, 103, 0.0, "2024/03/31 12:00:00", "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", 3, "エリコ", 3, 10142, 24, 103, 0.0, 0.0);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("2030/12/17 14:59:59", 129901, 104, 0.0, "2024/03/31 12:00:00", "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", 8, "エリコ", 2, 10142, 24, 104, 2.43, 0.0);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("2030/12/17 14:59:59", 129901, 105, 0.0, "2024/03/31 12:00:00", "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", 3, "エリコ", 4, 10142, 24, 105, 9.08, 0.0);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("2030/12/17 14:59:59", 129901, 106, 0.0, "2024/03/31 12:00:00", "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", 6, "エリコ", 6, 10142, 24, 106, 0.0, 0.0);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("2030/12/17 14:59:59", 129901, 107, 0.0, "2024/03/31 12:00:00", "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", 8, "エリコ", 3, 10142, 24, 107, 10.6, 0.0);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("2030/12/17 14:59:59", 129901, 108, 0.0, "2024/03/31 12:00:00", "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", 4, "エリコ", 6, 10142, 24, 108, 7.54, 0.0);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("2030/12/17 14:59:59", 129901, 109, 0.0, "2024/03/31 12:00:00", "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", 7, "エリコ", 2, 10142, 24, 109, 11.5, 0.0);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("2030/12/17 14:59:59", 129901, 110, 0.0, "2024/03/31 12:00:00", "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", 1, "エリコ", 2, 10142, 24, 110, 8.83, 0.0);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("2030/12/17 14:59:59", 129901, 111, 0.0, "2024/03/31 12:00:00", "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", 2, "エリコ", 1, 10142, 24, 111, 6.68, 0.0);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("2030/12/17 14:59:59", 129901, 112, 0.0, "2024/03/31 12:00:00", "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", 2, "エリコ", 6, 10142, 24, 112, 8.8, 0.0);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("2030/12/17 14:59:59", 129901, 113, 0.0, "2024/03/31 12:00:00", "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", 1, "エリコ", 2, 10142, 24, 113, 8.9, 0.0);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("2030/12/17 14:59:59", 129901, 114, 0.0, "2024/03/31 12:00:00", "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", 5, "エリコ", 2, 10142, 24, 114, 3.7, 0.0);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("2030/12/17 14:59:59", 129801, 200, 0.0, "2024/03/31 12:00:00", "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", 2, "マコト", 1, 10142, 24, 200, 7.6, 0.0);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("2030/12/17 14:59:59", 129801, 201, 0.0, "2024/03/31 12:00:00", "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", 1, "マコト", 2, 10142, 24, 201, 2.25, 0.0);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("2030/12/17 14:59:59", 129801, 202, 0.0, "2024/03/31 12:00:00", "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", 1, "マコト", 2, 10142, 24, 202, 3.28, 0.0);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("2030/12/17 14:59:59", 129801, 203, 0.0, "2024/03/31 12:00:00", "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", 1, "マコト", 4, 10142, 24, 203, 2.98, 0.0);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("2030/12/17 14:59:59", 129801, 204, 0.0, "2024/03/31 12:00:00", "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", 1, "マコト", 4, 10142, 24, 204, 3.0, 0.0);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("2030/12/17 14:59:59", 129801, 205, 0.0, "2024/03/31 12:00:00", "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", 2, "マコト", 1, 10142, 24, 205, 10.3, 0.0);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("2030/12/17 14:59:59", 129801, 206, 0.0, "2024/03/31 12:00:00", "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", 1, "マコト", 6, 10142, 24, 206, 2.93, 0.0);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("2030/12/17 14:59:59", 129801, 207, 0.0, "2024/03/31 12:00:00", "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", 1, "マコト", 3, 10142, 24, 207, 9.3, 0.0);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("2030/12/17 14:59:59", 129801, 208, 0.0, "2024/03/31 12:00:00", "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", 1, "マコト", 4, 10142, 24, 208, 6.9, 0.0);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("2030/12/17 14:59:59", 129801, 209, 0.0, "2024/03/31 12:00:00", "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", 2, "マコト", 2, 10142, 24, 209, 0.0, 0.0);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("2030/12/17 14:59:59", 129801, 210, 0.0, "2024/03/31 12:00:00", "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", 2, "マコト", 1, 10142, 24, 210, 5.95, 0.0);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("2030/12/17 14:59:59", 129801, 211, 0.0, "2024/03/31 12:00:00", "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", 1, "マコト", 2, 10142, 24, 211, 6.73, 0.0);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("2030/12/17 14:59:59", 129801, 212, 0.0, "2024/03/31 12:00:00", "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", 2, "マコト", 2, 10142, 24, 212, 0.0, 0.0);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("2030/12/17 14:59:59", 129801, 213, 0.0, "2024/03/31 12:00:00", "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", 2, "マコト", 1, 10142, 24, 213, 6.65, 0.0);
INSERT INTO `v1_b70540e3fd8c0a7341b873cca839a12aab4a719417624db522f8b123fa42056a` VALUES ("2030/12/17 14:59:59", 129801, 214, 0.0, "2024/03/31 12:00:00", "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", 1, "マコト", 6, 10142, 24, 214, 3.05, 0.0);
