INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES ("迷宮内部で発⾒された、⼸の遺物\n装備者は烈⾵の⼒を授かり、獲物\nの急所を貫きやすくなる。", 3, 2800, 23004, 10, 2, "スカイストライク", 0, 2, 23004, "物理/魔法クリティカル＋20％");
