INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (1800, 11001, 1, 11001, "加速の玉飾り", "迷宮内で見つかったイヤリング。\n装備者に活力加速を与える。", 0, 1, 1, "ＴＰ上昇＋5", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (1800, 11002, 1, 11002, "水蓮の盾", "迷宮内で見つかった円盾。受けた\n攻撃をエネルギーに変換できる。", 0, 1, 1, "ダメージを受けるたびＴＰ100回復（最大１回）", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (1800, 11003, 1, 11003, "青輝の環", "迷宮内で見つかった腕輪。戦いの\n始まりとともに、装備者にエネル\nギーを与える。", 0, 1, 1, "バトル開始時ＴＰ100回復", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (1800, 11004, 1, 11004, "冷玉の杖", "迷宮内で⾒つかった杖。装備者に\n力を温存させる効果をもたらす。", 0, 1, 1, "ＴＰ消費軽減＋2", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (1800, 11005, 1, 11005, "風渡りの靴", "迷宮内で⾒つかった靴。装備した\n者は迅速に動くことができる。", 0, 1, 1, "重複可能の行動速度＋10％", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (1800, 12001, 2, 12001, "紫晶ビザリオ", "迷宮内で見つかった首飾り。\n敵に異常を与えやすくなる。", 0, 1, 1, "状態異常命中＋20", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (1800, 12002, 2, 12002, "魔杖メジナエ", "迷宮内で見つかった杖。所持する\nだけで敵を呪うことができる。", 0, 1, 1, "敵全体に呪い付与（毎秒【弱体】数×500ダメージ）", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (1800, 12003, 2, 12003, "暗剣ポワゾン", "迷宮内で見つかった短剣。持つだ\nけで敵を毒にすることができる。", 0, 1, 1, "敵全体に毒付与（毎秒【弱体】数×500ダメージ）", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (1800, 12004, 2, 12004, "破守帽ディスペル", "迷宮内で見つかった帽子。装備す\nると敵の攻撃を弱体させる。", 0, 1, 1, "敵の物理/魔法攻撃力－10％", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (1800, 12005, 2, 12005, "崩刀ヤマツキ", "迷宮内で見つかった刀。\n敵の守りを断ち崩す力がある。", 0, 1, 1, "敵の物理/魔法防御力－10％", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (1800, 13001, 3, 13001, "キラースピア", "迷宮内で見つかった槍。鋭い一撃\nで敵に深い傷を負わせる。", 0, 1, 1, "敵の物理/魔法被クリダメ＋10％", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (1800, 13002, 3, 13002, "ハナミノカタナ", "迷宮内で見つかった刀。致命の攻\n撃を与えたときの威力を高める。", 0, 1, 1, "物理/魔法クリダメ＋10％", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (1800, 13003, 3, 13003, "クリーヴフィスト", "迷宮内で見つかった籠手。強化\nされた一撃を放つことができる。", 0, 1, 1, "通常攻撃強化＋30", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (1800, 13004, 3, 13004, "クリティカルアーク", "迷宮内で⾒つかった⼸。装備する\nと致命の一射を与えやすくなる。", 0, 1, 1, "物理/魔法クリティカル＋10％", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (1800, 13005, 3, 13005, "アブゾーブハチェット", "迷宮内で見つかった斧。攻撃と共\nに敵の体力を吸い取る力を宿す。", 0, 1, 1, "ＨＰ吸収＋10", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (1800, 14001, 4, 14001, "強化のジェム", "迷宮内で見つかった耳飾り。装備\n者の強化効果を高める。", 0, 1, 1, "バフ強化＋30", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (1800, 14002, 4, 14002, "抗鉄のプレート", "迷宮内で⾒つかった鎧。同朋の\n強靭を、自身の攻撃に変換する。", 0, 1, 1, "物理/魔法攻撃力アップ\n（先頭キャラの物理防御力×5）", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (1800, 14003, 4, 14003, "抗魔のドレス", "迷宮内で⾒つかったドレス。装備\n者の抗魔の才を攻撃⼒に換える。", 0, 1, 1, "物理/魔法攻撃力アップ\n（先頭キャラの魔法防御力×5）", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (1800, 14004, 4, 14004, "蛇紋のツルギ", "迷宮内で見つかった大剣。装備者\nの攻撃力を強化する。", 0, 1, 1, "物理/魔法攻撃力＋20％", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (1800, 14005, 4, 14005, "防守のメイル", "迷宮内で見つかった鎧。装備者の\n守りの力を高めることができる。", 0, 1, 1, "物理/魔法防御力＋10％", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (1800, 15001, 5, 15001, "回生杖", "迷宮内で⾒つかった杖。\n癒しの⼒を高めてくれる。", 0, 1, 1, "回復強化＋10", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (1800, 15002, 5, 15002, "豊命輪", "迷宮内で見つかった指輪。\n装備者の生命力を強化する。", 0, 1, 1, "ＨＰ＋10％", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (1800, 15003, 5, 15003, "鉄防甲", "迷宮内で見つかった鎧。\n装備者を物理攻撃から守る。", 0, 1, 1, "20秒ごとに物理無効バリア展開（【守備】数×500無効）", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (1800, 15004, 5, 15004, "魔封衣", "迷宮内で見つかったローブ。\n装備者を魔法攻撃から守る。", 0, 1, 1, "20秒ごとに魔法無効バリア展開（【守備】数×500無効）", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (1800, 15005, 5, 15005, "再生ノ腕輪", "迷宮内で見つかった腕輪。\n装備者に再生の力を与える。", 0, 1, 1, "継続ＨＰ回復（毎秒【守備】数×500回復）", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (2800, 21001, 1, 21001, "流烈の耳飾り", "迷宮内部で発見された耳飾りの形\nをした遺物。流水の激しさのよう\nに、活力加速を増幅させる。", 0, 2, 2, "ＴＰ上昇＋10", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (2800, 21002, 1, 21002, "星涙の輝盾", "迷宮内部で発見された円盾の形を\nした遺物。装備者に星の癒しを与\nえ攻撃をエネルギーに変換する。", 0, 2, 2, "ダメージを受けるたびＴＰ100回復（最大２回）", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (2800, 21003, 1, 21003, "蒼珠の腕輪", "迷宮内部で発⾒された腕輪の遺物\nあしらわれた蒼珠が、敵を感知し\n力を授ける。", 0, 2, 2, "バトル開始時ＴＰ200回復", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (2800, 21004, 1, 21004, "星明りの儀杖", "迷宮内部で発⾒された杖の遺物。\n封じられた星々の神秘が、力の消\n耗を肩代わりしてくれる。", 0, 2, 2, "ＴＰ消費軽減＋4", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (2800, 21005, 1, 21005, "疾風の翼靴", "迷宮内部で発見された、翼の生え\nた靴形の遺物。装備した者に疾風\nの力を施し、行動を速める。", 0, 2, 2, "重複可能の行動速度＋20％", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (2800, 22001, 2, 22001, "闇至宝アノマリーラ", "迷宮内部で発⾒された、⾸飾りの\n遺物。宝⽯の輝きで敵を虜にし、\n怪異をもたらすという。", 0, 2, 2, "状態異常命中＋40", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (2800, 22002, 2, 22002, "邪眼杖ドゥーム", "迷宮内部で発見された、杖の形を\nした遺物。杖先のコアから流れる\n邪気が敵に呪いを付与する。", 0, 2, 2, "敵全体に呪い付与（毎秒【弱体】数×1000ダメージ）", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (2800, 22003, 2, 22003, "影刃剣プラール", "迷宮内部で発見された、短剣の形\nをした遺物。刃に仕込まれた毒が\n気化し、接敵と共に毒を与える。", 0, 2, 2, "敵全体に毒付与（毎秒【弱体】数×1000ダメージ）", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (2800, 22004, 2, 22004, "薔薇飾帽イレース", "迷宮内部で発見された、帽子の形\nをした遺物。絡みつく茨の様に、\n敵の攻撃力を阻害する。", 0, 2, 2, "敵の物理/魔法攻撃力－20％", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (2800, 22005, 2, 22005, "斬赤刃ディバイド", "迷宮内部で発⾒された、⼑の遺物\n血で⾚く染まった⼑⾝で、敵の装\n甲をたやすく断ち割る。", 0, 2, 2, "敵の物理/魔法防御力－20％", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (2800, 23001, 3, 23001, "フェイタリティ", "迷宮内部で発見された、槍の遺物\n特殊な形の穂先が、敵により深い\n傷を刻み込む。", 0, 2, 2, "敵の物理/魔法被クリダメ＋20％", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (2800, 23002, 3, 23002, "クレナイノタチ", "迷宮内部で発見された、刀の形を\nした遺物。秋の紅を映す美しき刃\nは致命の攻撃をさらに強化する。", 0, 2, 2, "物理/魔法クリダメ＋20％", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (2800, 23003, 3, 23003, "ラプチャーガントレット", "迷宮内部で発⾒された籠⼿の遺\n物。装備者は研ぎ澄まされた強\n力な一撃を放つことができる。", 0, 2, 2, "通常攻撃強化＋60", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (2800, 23004, 3, 23004, "スカイストライク", "迷宮内部で発⾒された、⼸の遺物\n装備者は烈⾵の⼒を授かり、獲物\nの急所を貫きやすくなる。", 0, 2, 2, "物理/魔法クリティカル＋20％", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (2800, 23005, 3, 23005, "インヘイルアックス", "迷宮内部で発⾒された斧の遺物。\n繰り出される⼀撃は、掠めるだけ\nで獲物の魂を吸い取る。", 0, 2, 2, "ＨＰ吸収＋20", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (2800, 24001, 4, 24001, "進化のイヤリング", "迷宮内部で発見された、耳飾りの\n形をした遺物。魔石に込められた\n魔力が強化の力を増幅させる。", 0, 2, 2, "バフ強化＋60", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (2800, 24002, 4, 24002, "紅反鋼のアーマー", "迷宮内部で発⾒された、鎧の遺物\n紅き鋼の加護は、守備力を攻撃力\nへと変換する力を持つ。", 0, 2, 2, "物理/魔法攻撃力アップ\n（先頭キャラの物理防御力×10）", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (2800, 24003, 4, 24003, "聖減術のテラドレス", "迷宮内部で発⾒された、ドレスの\n遺物。精霊の加護が、防魔結界か\nら力を集めてくれる。", 0, 2, 2, "物理/魔法攻撃力アップ\n（先頭キャラの魔法防御力×10）", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (2800, 24004, 4, 24004, "炎王のグレートソード", "迷宮内部で発見された、大剣の形\nをした遺物。湧き出る炎の力が、\n攻撃の能力を大きく高める。", 0, 2, 2, "物理/魔法攻撃力＋40％", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (2800, 24005, 4, 24005, "桜堅牢のコート", "迷宮内部で発⾒された鎧の遺物。\n千年咲く桜の如く、盤石な堅牢さ\nを装備者に与える。", 0, 2, 2, "物理/魔法防御力＋20％", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (2800, 25001, 5, 25001, "創世緑杖", "迷宮内部で発⾒された杖の遺物。\n装備者に⼤樹の恵みを施し、治癒\n術の才覚を授けてくれる。", 0, 2, 2, "回復強化＋20", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (2800, 25002, 5, 25002, "空命護珠", "迷宮内部で発見された、指輪の形\nをした遺物。装備者を天空の護り\nで包み込み生命力を増幅させる。", 0, 2, 2, "ＨＰ＋20％", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (2800, 25003, 5, 25003, "鋼装堅鎧", "迷宮内部で発見された、鎧の形を\nした遺物。高度な技術で精錬され\nた鋼は、物理攻撃から身を護る。", 0, 2, 2, "20秒ごとに物理無効バリア展開（【守備】数×1000無効）", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (2800, 25004, 5, 25004, "魔護霊装", "迷宮内部で発⾒された、ローブの\n遺物。神樹の蔦が織り込まれ、装\n備者を魔法から護る。", 0, 2, 2, "20秒ごとに魔法無効バリア展開（【守備】数×1000無効）", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (2800, 25005, 5, 25005, "緑護ノ盾輪", "迷宮内部で発⾒された、腕輪の遺\n物。溢れ出す⾃然の⼒は装備者に\n生命の息吹をもたらす。", 0, 2, 2, "継続ＨＰ回復（毎秒【守備】数×1000回復）", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (3800, 30001, 0, 30001, "エレメントサージオーブ", "迷宮中枢部に隠された黎明の秘宝\nデータ保管のため属性の⼒の断⽚\nが封じられており、内なるアスト\nラルを著しく上昇させる。", 0, 3, 0, "属性ボーナス＋100％", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (3800, 31001, 1, 31001, "瞬閃蝶の月飾り", "迷宮の奥深くに眠っていた月を象\nる耳飾り。内部から発する魔力は\n装備者に蝶のような瞬麗さを授け\n活力加速を大きく上昇させる。", 0, 3, 3, "ＴＰ上昇＋15", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (3800, 31002, 1, 31002, "海宝華の煌盾", "迷宮の奥深くに眠る宝石を飾った\n円盾。海のような青き魔力は装備\n者に庇護を与え、迫り来る脅威す\nらエネルギーへと昇華させる。", 0, 3, 3, "ダメージを受けるたびＴＰ100回復（最大３回）", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (3800, 31003, 1, 31003, "碧貴石の聖輪", "迷宮の奥深くに眠る聖なる腕輪。\n失われた技術で装飾された宝石が\n装備者に加護を齎し、危機が迫る\nと膨大な力を分け与える。", 0, 3, 3, "バトル開始時ＴＰ300回復", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (3800, 31004, 1, 31004, "永天鏡の王笏", "迷宮の奥深くに眠る、かつて天界\nに伝えられし杖。天にかざすと鏡\n⽟に篭められた祈りが溢れ出して\n尽きることなき力を授ける。", 0, 3, 3, "ＴＰ消費軽減＋6", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (3800, 31005, 1, 31005, "熾天使の翔靴", "迷宮の奥深くに眠る、天使の加護\nを宿した戦靴。装備するだけで神\n性を授かり、天使のような自在さ\nで空を翔ることができる。", 0, 3, 3, "重複可能の行動速度＋30％", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (9999, 31006, 1, 31006, "風神の護風恩寵", "迷宮最奥部に鎮祀されし神業の遺\n物。風を司る神々の恩寵が宿され\nており、装備者は無尽蔵の活力が\n疾風の如き速さで体を駆け巡る。", 0, 3, 5, "ＴＰ上昇＋100", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (3800, 32001, 2, 32001, "幻獣飾テンタシオン", "迷宮の奥深くに眠る、幻獣を象る\n首飾り。この宝石の神秘に魅せら\nれた者は、一切の異変や脅威に抗\nうことができなくなってしまう。", 0, 3, 3, "状態異常命中＋60", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (3800, 32002, 2, 32002, "深淵蝕杖マリディクト", "迷宮の奥深くに眠る、月を蝕む杖\n存在するだけで、絶えず呪力の奔\n流を溢れさせ、近づく全ての敵を\n呪いで浸食していく。", 1, 3, 3, "敵全体に呪い付与（毎秒【弱体】数×1500ダメージ）", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (3800, 32003, 2, 32003, "滅刻凶刃ヴェノマス", "迷宮の奥深くに眠る、黒刃の刺剣\n刃に埋め込まれた宝玉が、絶え間\nなく邪毒を撒き散らし、破滅へと\n刻々と時を数える。", 0, 3, 3, "敵全体に毒付与（毎秒【弱体】数×1500ダメージ）", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (3800, 32004, 2, 32004, "燦然御纏アノラ・ベイラ", "迷宮の奥深くに眠る、宝飾をあし\nらったベール。蔽布の下から滲み\n出るオーラが、相手の敵意を解き\nほぐし、無力化させてしまう。", 0, 3, 3, "敵の物理/魔法攻撃力－30％", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (3800, 32005, 2, 32005, "断壁鎖刃フラグメント", "迷宮の奥深くに眠る、⼑⾝に鎖を\n這わせた妖⼑。主の意思に逆らい\n暴れる鎖が怨敵の守りを打ち崩し\n紫黒の凶刃が悉くを断ち斬る。", 0, 3, 3, "敵の物理/魔法防御力－30％", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (9999, 32006, 2, 32006, "霜影虚装アビスモナーク", "迷宮深層部に静眠せし虚影の装衣\n深淵から出でし蝕影は、存在全て\nを霜雪の如く凍てつかせ、抗う敵\nの剣盾両翼を虚壊させる。", 0, 3, 5, "敵の物理/魔法与ダメ－25％\n敵の物理/魔法被ダメ＋25％", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (3800, 33001, 3, 33001, "リーサルハルバード", "迷宮の奥深くに眠る、地神の力を\n宿した烈槍。大地の力を糧に放つ\n無比の一閃は、地に立つすべての\n敵に甚大なダメージを与える。", 0, 3, 3, "敵の物理/魔法被クリダメ＋30％", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (3800, 33002, 3, 33002, "オオトリノカタナ", "迷宮の奥深くに眠る、伝説に語ら\nれる極みの一振り。鳳凰の力を宿\nした真紅の刃は、焔の如き赫く激\n烈な一撃を与える。", 0, 3, 3, "物理/魔法クリダメ＋30％", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (3800, 33003, 3, 33003, "アースデストラクション", "迷宮の奥深くに眠る、地核のエネ\nルギーを封じた籠手。振るう一撃\nは、世界を破壊するかの如く、通\n常攻撃を大きく強化する。", 0, 3, 3, "通常攻撃強化＋90", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (3800, 33004, 3, 33004, "ジャッジメントアロー", "迷宮の奥深くに眠る、咎⼈を裁き\nし聖⼸。この弓に選ばれし装備者\nは神⾵の加護を得て、対象に慈悲\nを与えず一瞬で急所を射貫く。", 0, 3, 3, "物理/魔法クリティカル＋30％", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (3800, 33005, 3, 33005, "ザ・ハーヴェスト", "迷宮の奥深くに眠る、金色の⼤戦\n斧。刈り取った獲物の魂を糧に、\nその絢爛なる刃は燦燦と輝きを増\nしてゆく。", 0, 3, 3, "ＨＰ吸収＋30", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (9999, 33006, 3, 33006, "セレスティアルエイジ", "迷宮最深部に残された久遠の遺物\n神代の大戦より聖騎士に脈々と受\nけ継がれ、装備者へ戦いの記憶と\nともに不滅の力を分け与える。", 0, 3, 5, "通常攻撃強化＋500\nＨＰ吸収＋50", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (3800, 34001, 4, 34001, "極醒のアミュレット", "迷宮の奥深くに眠る、炎天の魔力\nを封じた耳飾り。潜在能力を究極\nまで呼び覚まし、強化の力を大幅\nに上昇させる。", 0, 3, 3, "バフ強化＋90", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (3800, 34002, 4, 34002, "断刃拒絶のアルギュロス", "迷宮の奥深くに眠る、⽩⾦の鎧。\n⽩⻯の鱗を繋ぎ合わせた外装が盟\n友の力を集結させる効果を持ち強\n力無比な一撃を可能にする。", 0, 3, 3, "物理/魔法攻撃力アップ\n（先頭キャラの物理防御力×15）", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (3800, 34003, 4, 34003, "緋華栄華のグラナート", "迷宮の奥深くに眠る、栄華の象徴\nとして仕立てられたドレス。深紅\nに咲き乱れる花の加護は、装備者\nの絶魔の⼒を攻撃⼒へ変換する。", 0, 3, 3, "物理/魔法攻撃力アップ\n（先頭キャラの魔法防御力×15）", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (3800, 34004, 4, 34004, "覇光帝龍のグローリア", "迷宮の奥深くに眠る、光⿓の⼒を\n授かりし⼤剣。その⼀振りはかつ\nての栄光の羽ばたきを想起させ、\n極限の覇気を世界に放つ。", 0, 3, 3, "物理/魔法攻撃力＋60％", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (3800, 34005, 4, 34005, "華舞玲瓏のハルナデシコ", "迷宮の奥深くに眠る、東の国に伝\nわりし聖鎧。玉の様に透き通る輝\nきを放つ鎧は、装備者に華の加護\nを授け、守護の力を高める。", 0, 3, 3, "物理/魔法防御力＋30％", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (9999, 34006, 4, 34006, "天戴冠のゼニスブレード", "迷宮深淵部に佇む歴戦無敗の⼤剣\n台座から剣を⼿にした者は天その\nものを冠すかの如き全能感を得て\n千軍を容易く退ける剛⼒を得る。", 0, 3, 5, "バトル開始時、物理/魔法攻撃力アップフィールド展開\n（現在値＋50％）", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (3800, 35001, 5, 35001, "妖精霊杖・癒光", "迷宮の奥深くに眠る、妖精の輝き\nを封じた杖。宝⽟から無限の⽣命\n⼒が溢れ出し、増幅された快癒の\n⼒であらゆる苦しみを取り除く。", 0, 3, 3, "回復強化＋30", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (3800, 35002, 5, 35002, "泉湧命環・豊穣", "迷宮の奥深くに眠る、翠緑に彩ら\nれた指輪。装備者に、かつて豊穣\nに溢れた楽園の光景を想起させ、\n際限なき⽣命⼒を与える。", 0, 3, 3, "ＨＰ＋30％", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (3800, 35003, 5, 35003, "白光輝鎧・断壁", "迷宮の奥深くに眠る、白金の輝き\nを放つ鎧。顕現する翅は、意思を\n持つかのよう、装備者に呼応して\n物理攻撃を断つ障壁を展開する。", 0, 3, 3, "20秒ごとに物理無効バリア展開（【守備】数×1500無効）", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (3800, 35004, 5, 35004, "幽玄煌装・絶魔", "迷宮の奥深くに眠る、幽庵の煌き\nを宿す鎧。顕現する翅は、意思を\n持つかのよう、装備者に呼応して\n魔法攻撃を絶つ障壁を展開する。", 0, 3, 3, "20秒ごとに魔法無効バリア展開（【守備】数×1500無効）", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (3800, 35005, 5, 35005, "輪廻ノ翡翠珠輪", "迷宮の奥深くに眠る、翠宝玉をあ\nしらった腕輪。常に滾々とマナが\n湧き出し、生命力を湛え続ける。", 0, 3, 3, "継続ＨＰ回復（毎秒【守備】数×1500回復）", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (9999, 35006, 5, 35006, "天命授環・伊邪那", "迷宮最下層に供奉された伝承の環\n選ばれし者がひとたび⼿にすれば\n創造神話の如く、千万無量の⽣命\n⼒を齎すと云われる。", 0, 3, 5, "ＨＰ＋100％", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (3200, 40001, 5, 40001, "プリンセスフォース", "【美⾷殿】と共に迷宮を冒険する\nための遺物。王家の装備を精巧に\n模しており、味方の⾝体能⼒を⼤\nきく向上させてくれる。", 0, 3, 1, "ＨＰ＋15％\n物理/魔法与ダメ＋15％", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (3200, 40002, 4, 40002, "アストラルブースト", "【トゥインクルウィッシュ】と共\nに迷宮を冒険するための遺物。\n３人のアストラルに呼応して、属\n性の力を増幅させてくれる。", 0, 3, 1, "属性ボーナス＋50％", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (3200, 40003, 1, 40003, "サレンディアエンゲージ", "【サレンディア救護院】と共に迷\n宮を冒険するための遺物。主人が\n築いた信頼関係のおかげで、商品\nの価格を値引きしてもらえる。", 0, 3, 1, "ショップの販売価格－10％", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (3200, 40004, 3, 40004, "ナイツオブフェイス", "【王宮騎士団】と共に迷宮を冒険\nするための遺物。団長の愛⽤品を\n模し会⼼率と引き換えに、会⼼時\nの威⼒を⾼めてくれる。", 0, 3, 1, "物理/魔法クリダメ＋50％\n物理/魔法クリティカル－25％", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (3200, 40005, 2, 40005, "ラビリンスオーソリティ", "【ラビリンス】と共に迷宮を冒険\nするための遺物。迷宮女王はその\n権能でナイトがどこに居ようとも\n即座に参戦してくれる。", 0, 3, 1, "挑戦開始時にラビリスタが加入", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (3200, 50001, 4, 50001, "プリンセスエンチャント", "【美⾷殿】と共に迷宮を冒険する\nための遺物。冒険の道中で絆を結\nべば結ぶほどに、味⽅の攻撃⼒を\n⾼めてくれる。", 0, 3, 1, "物理/魔法攻撃力アップ（加入キャラ数×4％、最大100％）", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (3200, 50002, 5, 50002, "トゥインクルブレス", "【トゥインクルウィッシュ】と共\nに迷宮を冒険するための遺物。ギ\nルドマスターの諦めない心が、味\n方を一度だけ敗北から守る。", 1, 3, 1, "ボスバトルを除いて一度だけバトル敗北を免除", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (3200, 50003, 3, 50003, "サレンディアノーブル", "【サレンディア救護院】と共に迷\n宮を冒険するための遺物。主人の\n気品を漂わせる剣は、築いた富を\n⼒に変転させる効果を持つ。", 0, 3, 1, "通常攻撃強化アップ（アルファルピ所持数×0.01、最大100）", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (3200, 50004, 2, 50004, "レジーナガーラ", "【王宮騎士団】と共に迷宮を冒険\nするための遺物。副団長の催す闘\n争の宴は、敵味⽅が受けるダメー\nジをみな悉く増加させる。", 0, 3, 1, "敵の物理/魔法被ダメ＋25％\n味方の物理/魔法被ダメ＋25％", 10);
INSERT INTO `v1_b7d74fd63eea8e8964052c3d38d1f77275ecb1de582c166173ccaeae790ff96d` VALUES (3200, 50005, 1, 50005, "マスターオブラビリンス", "【ラビリンス】と共に迷宮を冒険\nするための遺物。マスターの⼒を\n冠した剣は、開発者権限の⼀部を\n解放し、遺物を即座に獲得する。", 0, 3, 1, "挑戦開始時にランダムな★1の迷宮遺物を１個獲得", 10);
