INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 1800, 11001, "迷宮内で見つかったイヤリング。\n装備者に活力加速を与える。", 11001, "加速の玉飾り", 0, 1, "ＴＰ上昇＋5", 1, 1);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 1800, 11002, "迷宮内で見つかった円盾。受けた\n攻撃をエネルギーに変換できる。", 11002, "水蓮の盾", 0, 1, "ダメージを受けるたびＴＰ100回復（最大１回）", 1, 1);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 1800, 11003, "迷宮内で見つかった腕輪。戦いの\n始まりとともに、装備者にエネル\nギーを与える。", 11003, "青輝の環", 0, 1, "バトル開始時ＴＰ100回復", 1, 1);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 1800, 11004, "迷宮内で⾒つかった杖。装備者に\n力を温存させる効果をもたらす。", 11004, "冷玉の杖", 0, 1, "ＴＰ消費軽減＋2", 1, 1);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 1800, 11005, "迷宮内で⾒つかった靴。装備した\n者は迅速に動くことができる。", 11005, "風渡りの靴", 0, 1, "重複可能の行動速度＋10％", 1, 1);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 1800, 12001, "迷宮内で見つかった首飾り。\n敵に異常を与えやすくなる。", 12001, "紫晶ビザリオ", 0, 1, "状態異常命中＋20", 1, 2);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 1800, 12002, "迷宮内で見つかった杖。所持する\nだけで敵を呪うことができる。", 12002, "魔杖メジナエ", 0, 1, "敵全体に呪い付与（毎秒【弱体】数×500ダメージ）", 1, 2);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 1800, 12003, "迷宮内で見つかった短剣。持つだ\nけで敵を毒にすることができる。", 12003, "暗剣ポワゾン", 0, 1, "敵全体に毒付与（毎秒【弱体】数×500ダメージ）", 1, 2);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 1800, 12004, "迷宮内で見つかった帽子。装備す\nると敵の攻撃を弱体させる。", 12004, "破守帽ディスペル", 0, 1, "敵の物理/魔法攻撃力－10％", 1, 2);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 1800, 12005, "迷宮内で見つかった刀。\n敵の守りを断ち崩す力がある。", 12005, "崩刀ヤマツキ", 0, 1, "敵の物理/魔法防御力－10％", 1, 2);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 1800, 13001, "迷宮内で見つかった槍。鋭い一撃\nで敵に深い傷を負わせる。", 13001, "キラースピア", 0, 1, "敵の物理/魔法被クリダメ＋10％", 1, 3);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 1800, 13002, "迷宮内で見つかった刀。致命の攻\n撃を与えたときの威力を高める。", 13002, "ハナミノカタナ", 0, 1, "物理/魔法クリダメ＋10％", 1, 3);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 1800, 13003, "迷宮内で見つかった籠手。強化\nされた一撃を放つことができる。", 13003, "クリーヴフィスト", 0, 1, "通常攻撃強化＋30", 1, 3);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 1800, 13004, "迷宮内で⾒つかった⼸。装備する\nと致命の一射を与えやすくなる。", 13004, "クリティカルアーク", 0, 1, "物理/魔法クリティカル＋10％", 1, 3);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 1800, 13005, "迷宮内で見つかった斧。攻撃と共\nに敵の体力を吸い取る力を宿す。", 13005, "アブゾーブハチェット", 0, 1, "ＨＰ吸収＋10", 1, 3);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 1800, 14001, "迷宮内で見つかった耳飾り。装備\n者の強化効果を高める。", 14001, "強化のジェム", 0, 1, "バフ強化＋30", 1, 4);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 1800, 14002, "迷宮内で⾒つかった鎧。同朋の\n強靭を、自身の攻撃に変換する。", 14002, "抗鉄のプレート", 0, 1, "物理/魔法攻撃力アップ\n（先頭キャラの物理防御力×5）", 1, 4);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 1800, 14003, "迷宮内で⾒つかったドレス。装備\n者の抗魔の才を攻撃⼒に換える。", 14003, "抗魔のドレス", 0, 1, "物理/魔法攻撃力アップ\n（先頭キャラの魔法防御力×5）", 1, 4);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 1800, 14004, "迷宮内で見つかった大剣。装備者\nの攻撃力を強化する。", 14004, "蛇紋のツルギ", 0, 1, "物理/魔法攻撃力＋20％", 1, 4);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 1800, 14005, "迷宮内で見つかった鎧。装備者の\n守りの力を高めることができる。", 14005, "防守のメイル", 0, 1, "物理/魔法防御力＋10％", 1, 4);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 1800, 15001, "迷宮内で⾒つかった杖。\n癒しの⼒を高めてくれる。", 15001, "回生杖", 0, 1, "回復強化＋10", 1, 5);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 1800, 15002, "迷宮内で見つかった指輪。\n装備者の生命力を強化する。", 15002, "豊命輪", 0, 1, "ＨＰ＋10％", 1, 5);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 1800, 15003, "迷宮内で見つかった鎧。\n装備者を物理攻撃から守る。", 15003, "鉄防甲", 0, 1, "20秒ごとに物理無効バリア展開（【守備】数×500無効）", 1, 5);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 1800, 15004, "迷宮内で見つかったローブ。\n装備者を魔法攻撃から守る。", 15004, "魔封衣", 0, 1, "20秒ごとに魔法無効バリア展開（【守備】数×500無効）", 1, 5);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 1800, 15005, "迷宮内で見つかった腕輪。\n装備者に再生の力を与える。", 15005, "再生ノ腕輪", 0, 1, "継続ＨＰ回復（毎秒【守備】数×500回復）", 1, 5);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 2800, 21001, "迷宮内部で発見された耳飾りの形\nをした遺物。流水の激しさのよう\nに、活力加速を増幅させる。", 21001, "流烈の耳飾り", 0, 2, "ＴＰ上昇＋10", 2, 1);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 2800, 21002, "迷宮内部で発見された円盾の形を\nした遺物。装備者に星の癒しを与\nえ攻撃をエネルギーに変換する。", 21002, "星涙の輝盾", 0, 2, "ダメージを受けるたびＴＰ100回復（最大２回）", 2, 1);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 2800, 21003, "迷宮内部で発⾒された腕輪の遺物\nあしらわれた蒼珠が、敵を感知し\n力を授ける。", 21003, "蒼珠の腕輪", 0, 2, "バトル開始時ＴＰ200回復", 2, 1);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 2800, 21004, "迷宮内部で発⾒された杖の遺物。\n封じられた星々の神秘が、力の消\n耗を肩代わりしてくれる。", 21004, "星明りの儀杖", 0, 2, "ＴＰ消費軽減＋4", 2, 1);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 2800, 21005, "迷宮内部で発見された、翼の生え\nた靴形の遺物。装備した者に疾風\nの力を施し、行動を速める。", 21005, "疾風の翼靴", 0, 2, "重複可能の行動速度＋20％", 2, 1);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 2800, 22001, "迷宮内部で発⾒された、⾸飾りの\n遺物。宝⽯の輝きで敵を虜にし、\n怪異をもたらすという。", 22001, "闇至宝アノマリーラ", 0, 2, "状態異常命中＋40", 2, 2);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 2800, 22002, "迷宮内部で発見された、杖の形を\nした遺物。杖先のコアから流れる\n邪気が敵に呪いを付与する。", 22002, "邪眼杖ドゥーム", 0, 2, "敵全体に呪い付与（毎秒【弱体】数×1000ダメージ）", 2, 2);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 2800, 22003, "迷宮内部で発見された、短剣の形\nをした遺物。刃に仕込まれた毒が\n気化し、接敵と共に毒を与える。", 22003, "影刃剣プラール", 0, 2, "敵全体に毒付与（毎秒【弱体】数×1000ダメージ）", 2, 2);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 2800, 22004, "迷宮内部で発見された、帽子の形\nをした遺物。絡みつく茨の様に、\n敵の攻撃力を阻害する。", 22004, "薔薇飾帽イレース", 0, 2, "敵の物理/魔法攻撃力－20％", 2, 2);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 2800, 22005, "迷宮内部で発⾒された、⼑の遺物\n血で⾚く染まった⼑⾝で、敵の装\n甲をたやすく断ち割る。", 22005, "斬赤刃ディバイド", 0, 2, "敵の物理/魔法防御力－20％", 2, 2);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 2800, 23001, "迷宮内部で発見された、槍の遺物\n特殊な形の穂先が、敵により深い\n傷を刻み込む。", 23001, "フェイタリティ", 0, 2, "敵の物理/魔法被クリダメ＋20％", 2, 3);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 2800, 23002, "迷宮内部で発見された、刀の形を\nした遺物。秋の紅を映す美しき刃\nは致命の攻撃をさらに強化する。", 23002, "クレナイノタチ", 0, 2, "物理/魔法クリダメ＋20％", 2, 3);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 2800, 23003, "迷宮内部で発⾒された籠⼿の遺\n物。装備者は研ぎ澄まされた強\n力な一撃を放つことができる。", 23003, "ラプチャーガントレット", 0, 2, "通常攻撃強化＋60", 2, 3);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 2800, 23004, "迷宮内部で発⾒された、⼸の遺物\n装備者は烈⾵の⼒を授かり、獲物\nの急所を貫きやすくなる。", 23004, "スカイストライク", 0, 2, "物理/魔法クリティカル＋20％", 2, 3);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 2800, 23005, "迷宮内部で発⾒された斧の遺物。\n繰り出される⼀撃は、掠めるだけ\nで獲物の魂を吸い取る。", 23005, "インヘイルアックス", 0, 2, "ＨＰ吸収＋20", 2, 3);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 2800, 24001, "迷宮内部で発見された、耳飾りの\n形をした遺物。魔石に込められた\n魔力が強化の力を増幅させる。", 24001, "進化のイヤリング", 0, 2, "バフ強化＋60", 2, 4);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 2800, 24002, "迷宮内部で発⾒された、鎧の遺物\n紅き鋼の加護は、守備力を攻撃力\nへと変換する力を持つ。", 24002, "紅反鋼のアーマー", 0, 2, "物理/魔法攻撃力アップ\n（先頭キャラの物理防御力×10）", 2, 4);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 2800, 24003, "迷宮内部で発⾒された、ドレスの\n遺物。精霊の加護が、防魔結界か\nら力を集めてくれる。", 24003, "聖減術のテラドレス", 0, 2, "物理/魔法攻撃力アップ\n（先頭キャラの魔法防御力×10）", 2, 4);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 2800, 24004, "迷宮内部で発見された、大剣の形\nをした遺物。湧き出る炎の力が、\n攻撃の能力を大きく高める。", 24004, "炎王のグレートソード", 0, 2, "物理/魔法攻撃力＋40％", 2, 4);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 2800, 24005, "迷宮内部で発⾒された鎧の遺物。\n千年咲く桜の如く、盤石な堅牢さ\nを装備者に与える。", 24005, "桜堅牢のコート", 0, 2, "物理/魔法防御力＋20％", 2, 4);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 2800, 25001, "迷宮内部で発⾒された杖の遺物。\n装備者に⼤樹の恵みを施し、治癒\n術の才覚を授けてくれる。", 25001, "創世緑杖", 0, 2, "回復強化＋20", 2, 5);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 2800, 25002, "迷宮内部で発見された、指輪の形\nをした遺物。装備者を天空の護り\nで包み込み生命力を増幅させる。", 25002, "空命護珠", 0, 2, "ＨＰ＋20％", 2, 5);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 2800, 25003, "迷宮内部で発見された、鎧の形を\nした遺物。高度な技術で精錬され\nた鋼は、物理攻撃から身を護る。", 25003, "鋼装堅鎧", 0, 2, "20秒ごとに物理無効バリア展開（【守備】数×1000無効）", 2, 5);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 2800, 25004, "迷宮内部で発⾒された、ローブの\n遺物。神樹の蔦が織り込まれ、装\n備者を魔法から護る。", 25004, "魔護霊装", 0, 2, "20秒ごとに魔法無効バリア展開（【守備】数×1000無効）", 2, 5);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 2800, 25005, "迷宮内部で発⾒された、腕輪の遺\n物。溢れ出す⾃然の⼒は装備者に\n生命の息吹をもたらす。", 25005, "緑護ノ盾輪", 0, 2, "継続ＨＰ回復（毎秒【守備】数×1000回復）", 2, 5);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 3800, 30001, "迷宮中枢部に隠された黎明の秘宝\nデータ保管のため属性の⼒の断⽚\nが封じられており、内なるアスト\nラルを著しく上昇させる。", 30001, "エレメントサージオーブ", 0, 3, "属性ボーナス＋100％", 0, 0);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 3800, 31001, "迷宮の奥深くに眠っていた月を象\nる耳飾り。内部から発する魔力は\n装備者に蝶のような瞬麗さを授け\n活力加速を大きく上昇させる。", 31001, "瞬閃蝶の月飾り", 0, 3, "ＴＰ上昇＋15", 3, 1);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 3800, 31002, "迷宮の奥深くに眠る宝石を飾った\n円盾。海のような青き魔力は装備\n者に庇護を与え、迫り来る脅威す\nらエネルギーへと昇華させる。", 31002, "海宝華の煌盾", 0, 3, "ダメージを受けるたびＴＰ100回復（最大３回）", 3, 1);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 3800, 31003, "迷宮の奥深くに眠る聖なる腕輪。\n失われた技術で装飾された宝石が\n装備者に加護を齎し、危機が迫る\nと膨大な力を分け与える。", 31003, "碧貴石の聖輪", 0, 3, "バトル開始時ＴＰ300回復", 3, 1);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 3800, 31004, "迷宮の奥深くに眠る、かつて天界\nに伝えられし杖。天にかざすと鏡\n⽟に篭められた祈りが溢れ出して\n尽きることなき力を授ける。", 31004, "永天鏡の王笏", 0, 3, "ＴＰ消費軽減＋6", 3, 1);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 3800, 31005, "迷宮の奥深くに眠る、天使の加護\nを宿した戦靴。装備するだけで神\n性を授かり、天使のような自在さ\nで空を翔ることができる。", 31005, "熾天使の翔靴", 0, 3, "重複可能の行動速度＋30％", 3, 1);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 9999, 31006, "迷宮最奥部に鎮祀されし神業の遺\n物。風を司る神々の恩寵が宿され\nており、装備者は無尽蔵の活力が\n疾風の如き速さで体を駆け巡る。", 31006, "風神の護風恩寵", 0, 3, "ＴＰ上昇＋100", 5, 1);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 3800, 32001, "迷宮の奥深くに眠る、幻獣を象る\n首飾り。この宝石の神秘に魅せら\nれた者は、一切の異変や脅威に抗\nうことができなくなってしまう。", 32001, "幻獣飾テンタシオン", 0, 3, "状態異常命中＋60", 3, 2);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 3800, 32002, "迷宮の奥深くに眠る、月を蝕む杖\n存在するだけで、絶えず呪力の奔\n流を溢れさせ、近づく全ての敵を\n呪いで浸食していく。", 32002, "深淵蝕杖マリディクト", 1, 3, "敵全体に呪い付与（毎秒【弱体】数×1500ダメージ）", 3, 2);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 3800, 32003, "迷宮の奥深くに眠る、黒刃の刺剣\n刃に埋め込まれた宝玉が、絶え間\nなく邪毒を撒き散らし、破滅へと\n刻々と時を数える。", 32003, "滅刻凶刃ヴェノマス", 0, 3, "敵全体に毒付与（毎秒【弱体】数×1500ダメージ）", 3, 2);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 3800, 32004, "迷宮の奥深くに眠る、宝飾をあし\nらったベール。蔽布の下から滲み\n出るオーラが、相手の敵意を解き\nほぐし、無力化させてしまう。", 32004, "燦然御纏アノラ・ベイラ", 0, 3, "敵の物理/魔法攻撃力－30％", 3, 2);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 3800, 32005, "迷宮の奥深くに眠る、⼑⾝に鎖を\n這わせた妖⼑。主の意思に逆らい\n暴れる鎖が怨敵の守りを打ち崩し\n紫黒の凶刃が悉くを断ち斬る。", 32005, "断壁鎖刃フラグメント", 0, 3, "敵の物理/魔法防御力－30％", 3, 2);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 9999, 32006, "迷宮深層部に静眠せし虚影の装衣\n深淵から出でし蝕影は、存在全て\nを霜雪の如く凍てつかせ、抗う敵\nの剣盾両翼を虚壊させる。", 32006, "霜影虚装アビスモナーク", 0, 3, "敵の物理/魔法与ダメ－25％\n敵の物理/魔法被ダメ＋25％", 5, 2);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 3800, 33001, "迷宮の奥深くに眠る、地神の力を\n宿した烈槍。大地の力を糧に放つ\n無比の一閃は、地に立つすべての\n敵に甚大なダメージを与える。", 33001, "リーサルハルバード", 0, 3, "敵の物理/魔法被クリダメ＋30％", 3, 3);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 3800, 33002, "迷宮の奥深くに眠る、伝説に語ら\nれる極みの一振り。鳳凰の力を宿\nした真紅の刃は、焔の如き赫く激\n烈な一撃を与える。", 33002, "オオトリノカタナ", 0, 3, "物理/魔法クリダメ＋30％", 3, 3);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 3800, 33003, "迷宮の奥深くに眠る、地核のエネ\nルギーを封じた籠手。振るう一撃\nは、世界を破壊するかの如く、通\n常攻撃を大きく強化する。", 33003, "アースデストラクション", 0, 3, "通常攻撃強化＋90", 3, 3);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 3800, 33004, "迷宮の奥深くに眠る、咎⼈を裁き\nし聖⼸。この弓に選ばれし装備者\nは神⾵の加護を得て、対象に慈悲\nを与えず一瞬で急所を射貫く。", 33004, "ジャッジメントアロー", 0, 3, "物理/魔法クリティカル＋30％", 3, 3);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 3800, 33005, "迷宮の奥深くに眠る、金色の⼤戦\n斧。刈り取った獲物の魂を糧に、\nその絢爛なる刃は燦燦と輝きを増\nしてゆく。", 33005, "ザ・ハーヴェスト", 0, 3, "ＨＰ吸収＋30", 3, 3);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 9999, 33006, "迷宮最深部に残された久遠の遺物\n神代の大戦より聖騎士に脈々と受\nけ継がれ、装備者へ戦いの記憶と\nともに不滅の力を分け与える。", 33006, "セレスティアルエイジ", 0, 3, "通常攻撃強化＋500\nＨＰ吸収＋50", 5, 3);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 3800, 34001, "迷宮の奥深くに眠る、炎天の魔力\nを封じた耳飾り。潜在能力を究極\nまで呼び覚まし、強化の力を大幅\nに上昇させる。", 34001, "極醒のアミュレット", 0, 3, "バフ強化＋90", 3, 4);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 3800, 34002, "迷宮の奥深くに眠る、⽩⾦の鎧。\n⽩⻯の鱗を繋ぎ合わせた外装が盟\n友の力を集結させる効果を持ち強\n力無比な一撃を可能にする。", 34002, "断刃拒絶のアルギュロス", 0, 3, "物理/魔法攻撃力アップ\n（先頭キャラの物理防御力×15）", 3, 4);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 3800, 34003, "迷宮の奥深くに眠る、栄華の象徴\nとして仕立てられたドレス。深紅\nに咲き乱れる花の加護は、装備者\nの絶魔の⼒を攻撃⼒へ変換する。", 34003, "緋華栄華のグラナート", 0, 3, "物理/魔法攻撃力アップ\n（先頭キャラの魔法防御力×15）", 3, 4);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 3800, 34004, "迷宮の奥深くに眠る、光⿓の⼒を\n授かりし⼤剣。その⼀振りはかつ\nての栄光の羽ばたきを想起させ、\n極限の覇気を世界に放つ。", 34004, "覇光帝龍のグローリア", 0, 3, "物理/魔法攻撃力＋60％", 3, 4);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 3800, 34005, "迷宮の奥深くに眠る、東の国に伝\nわりし聖鎧。玉の様に透き通る輝\nきを放つ鎧は、装備者に華の加護\nを授け、守護の力を高める。", 34005, "華舞玲瓏のハルナデシコ", 0, 3, "物理/魔法防御力＋30％", 3, 4);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 9999, 34006, "迷宮深淵部に佇む歴戦無敗の⼤剣\n台座から剣を⼿にした者は天その\nものを冠すかの如き全能感を得て\n千軍を容易く退ける剛⼒を得る。", 34006, "天戴冠のゼニスブレード", 0, 3, "バトル開始時、物理/魔法攻撃力アップフィールド展開\n（現在値＋50％）", 5, 4);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 3800, 35001, "迷宮の奥深くに眠る、妖精の輝き\nを封じた杖。宝⽟から無限の⽣命\n⼒が溢れ出し、増幅された快癒の\n⼒であらゆる苦しみを取り除く。", 35001, "妖精霊杖・癒光", 0, 3, "回復強化＋30", 3, 5);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 3800, 35002, "迷宮の奥深くに眠る、翠緑に彩ら\nれた指輪。装備者に、かつて豊穣\nに溢れた楽園の光景を想起させ、\n際限なき⽣命⼒を与える。", 35002, "泉湧命環・豊穣", 0, 3, "ＨＰ＋30％", 3, 5);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 3800, 35003, "迷宮の奥深くに眠る、白金の輝き\nを放つ鎧。顕現する翅は、意思を\n持つかのよう、装備者に呼応して\n物理攻撃を断つ障壁を展開する。", 35003, "白光輝鎧・断壁", 0, 3, "20秒ごとに物理無効バリア展開（【守備】数×1500無効）", 3, 5);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 3800, 35004, "迷宮の奥深くに眠る、幽庵の煌き\nを宿す鎧。顕現する翅は、意思を\n持つかのよう、装備者に呼応して\n魔法攻撃を絶つ障壁を展開する。", 35004, "幽玄煌装・絶魔", 0, 3, "20秒ごとに魔法無効バリア展開（【守備】数×1500無効）", 3, 5);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 3800, 35005, "迷宮の奥深くに眠る、翠宝玉をあ\nしらった腕輪。常に滾々とマナが\n湧き出し、生命力を湛え続ける。", 35005, "輪廻ノ翡翠珠輪", 0, 3, "継続ＨＰ回復（毎秒【守備】数×1500回復）", 3, 5);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 9999, 35006, "迷宮最下層に供奉された伝承の環\n選ばれし者がひとたび⼿にすれば\n創造神話の如く、千万無量の⽣命\n⼒を齎すと云われる。", 35006, "天命授環・伊邪那", 0, 3, "ＨＰ＋100％", 5, 5);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 3200, 40001, "【美⾷殿】と共に迷宮を冒険する\nための遺物。王家の装備を精巧に\n模しており、味方の⾝体能⼒を⼤\nきく向上させてくれる。", 40001, "プリンセスフォース", 0, 3, "ＨＰ＋15％\n物理/魔法与ダメ＋15％", 1, 5);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 3200, 40002, "【トゥインクルウィッシュ】と共\nに迷宮を冒険するための遺物。\n３人のアストラルに呼応して、属\n性の力を増幅させてくれる。", 40002, "アストラルブースト", 0, 3, "属性ボーナス＋50％", 1, 4);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 3200, 40003, "【サレンディア救護院】と共に迷\n宮を冒険するための遺物。主人が\n築いた信頼関係のおかげで、商品\nの価格を値引きしてもらえる。", 40003, "サレンディアエンゲージ", 0, 3, "ショップの販売価格－10％", 1, 1);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 3200, 40004, "【王宮騎士団】と共に迷宮を冒険\nするための遺物。団長の愛⽤品を\n模し会⼼率と引き換えに、会⼼時\nの威⼒を⾼めてくれる。", 40004, "ナイツオブフェイス", 0, 3, "物理/魔法クリダメ＋50％\n物理/魔法クリティカル－25％", 1, 3);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 3200, 40005, "【ラビリンス】と共に迷宮を冒険\nするための遺物。迷宮女王はその\n権能でナイトがどこに居ようとも\n即座に参戦してくれる。", 40005, "ラビリンスオーソリティ", 0, 3, "挑戦開始時にラビリスタが加入", 1, 2);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 3200, 50001, "【美⾷殿】と共に迷宮を冒険する\nための遺物。冒険の道中で絆を結\nべば結ぶほどに、味⽅の攻撃⼒を\n⾼めてくれる。", 50001, "プリンセスエンチャント", 0, 3, "物理/魔法攻撃力アップ（加入キャラ数×4％、最大100％）", 1, 4);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 3200, 50002, "【トゥインクルウィッシュ】と共\nに迷宮を冒険するための遺物。ギ\nルドマスターの諦めない心が、味\n方を一度だけ敗北から守る。", 50002, "トゥインクルブレス", 1, 3, "ボスバトルを除いて一度だけバトル敗北を免除", 1, 5);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 3200, 50003, "【サレンディア救護院】と共に迷\n宮を冒険するための遺物。主人の\n気品を漂わせる剣は、築いた富を\n⼒に変転させる効果を持つ。", 50003, "サレンディアノーブル", 0, 3, "通常攻撃強化アップ（アルファルピ所持数×0.01、最大100）", 1, 3);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 3200, 50004, "【王宮騎士団】と共に迷宮を冒険\nするための遺物。副団長の催す闘\n争の宴は、敵味⽅が受けるダメー\nジをみな悉く増加させる。", 50004, "レジーナガーラ", 0, 3, "敵の物理/魔法被ダメ＋25％\n味方の物理/魔法被ダメ＋25％", 1, 2);
INSERT INTO `v1_b8fc380275d76b69871c023d30084ab8ba5bf4887188930cfcd9b19322716594` VALUES (10, 3200, 50005, "【ラビリンス】と共に迷宮を冒険\nするための遺物。マスターの⼒を\n冠した剣は、開発者権限の⼀部を\n解放し、遺物を即座に獲得する。", 50005, "マスターオブラビリンス", 0, 3, "挑戦開始時にランダムな★1の迷宮遺物を１個獲得", 1, 1);
