INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100101, 1, 1, 0, 1, 0, "ここに困ってる人は\nいないよね…？\nよかった♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100101, 2, 1, 0, 2, 0, "冒険の準備準備…\nうん、忘れ物はなし！\n…あっ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100101, 3, 1, 0, 3, 0, "リズムを意識して…\nわんつーわんつー！\nうん、調子はバッチリ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100101, 4, 1, 0, 4, 0, "ここで特訓していい？\n物は壊さないように\n気をつけるね！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100101, 5, 1, 0, 5, 0, "なんか落ち着かないや\nもしかして、\n困ってる人が\n近くにいるのかな…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 100101, 6, 1, 0, 101, 0, "えへへ…いつも\n一緒にいてくれて\nありがとねっ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100101, 7, 1, 0, 102, 0, "ちゃんと覚えてるよ\n騎士くんの声…\nあの時はありがとね！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100101, 8, 1, 0, 1, 0, "あははっ！\nやったなー！\nお返しだー！\nこちょこちょこちょ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100101, 9, 1, 0, 2, 0, "どうしたの？\nもしかして困ってる？\nあたしにまかせて！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100101, 10, 1, 0, 3, 0, "えっとね…しっぽは\n触っちゃダメだよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100101, 11, 1, 0, 4, 0, "反応しない特訓…\nだったけど無理～！\n悔しいよぉ～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 100101, 12, 1, 0, 101, 0, "ふぁふぃ？\nやられた～！　ほっぺ\nぷにぷにしないでぇ～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100101, 13, 1, 0, 102, 0, "えへへ…\n騎士くんの手だ…\nあったかいな…♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100101, 14, 1, 1, 1, 0, "おはよー！\n今日も元気に\nいっちゃうよー！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100101, 15, 1, 2, 2, 0, "ユイちゃんに\nおやつ作って\nもらおうかな～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100101, 16, 1, 3, 3, 0, "寝るのも修行だって\nこないだレイさんが\n言ってたんだ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100101, 17, 1, 0, 1, 0, "ちゃんとケアして\n傷が残らないように\nしないと…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100101, 18, 1, 0, 2, 0, "一回休むと\nこのままずるずる\nいきそう…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100101, 19, 1, 0, 3, 0, "新しい練習相手に…\nって冗談だよ冗談！\n本気じゃないから！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100101, 20, 1, 0, 4, 0, "落ち着くなぁ～\n心だけじゃなくて\n体もほぐれそう♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100101, 21, 1, 0, 5, 0, "あったかい所に\nいると体が丸く\nなっちゃう…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100101, 22, 1, 0, 6, 0, "いつ何が起きるか\n分からないし、\nもっと力をつけないと", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100101, 23, 1, 0, 7, 0, "美味しいけど、\n食べ過ぎないように\nしないと…！！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100101, 24, 1, 0, 8, 0, "文字が多いと\n眠くなっちゃう…\n危ない危ない！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100102, 26, 1, 0, 1, 0, "リズムを意識して…\nわんつーわんつー！\nうん、調子はバッチリ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100102, 27, 1, 0, 2, 0, "のんびりするのって\n退屈かも…\n何かして遊ばない？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100102, 28, 1, 0, 3, 0, "留守番はちょっと\n苦手かも…\nなんか寂しいんだもん", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100102, 29, 1, 0, 4, 0, "あたしのお友達も\n呼んできていい？\nきっと楽しいよ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100102, 30, 1, 0, 5, 0, "誰も困ってないよね？\n…何をすれば\nいいのか分かんないや", 1);
