INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100102, 35, 1, 0, 3, 0, "あたしの元気を\nわけてあげるね！\nフレー！　フレー！", 2);
