INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100102, 37, 1, 0, 101, 0, "ひゃうっ！　もう…\nしっぽはだめだってば\n次やったらデコピン！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100102, 38, 1, 0, 102, 0, "えへへ…\n騎士くんの手だ…\nあったかいな…♪", 2);
