INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100102, 42, 1, 0, 1, 0, "これでよし♪\n騎士くん\n気づくかな…？", 3);
