INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100102, 49, 1, 0, 8, 0, "色んなことを\n知っておくのは\n大事だよね！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100201, 51, 1, 0, 1, 0, "お片付けも済んだし\n今度は洗濯ものを\nたたもうかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100201, 52, 1, 0, 2, 0, "この気持ち\nもっとちゃんと\n伝えなくっちゃ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100201, 53, 1, 0, 3, 0, "お茶をいれようと\n思うんだけど\n他に飲みたい人は\nいるかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100201, 54, 1, 0, 4, 0, "戦いは\n得意じゃないけど\nお料理なら…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100201, 55, 1, 0, 5, 0, "こういう平和な\n時間が、もっと\n続くといいな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 100201, 56, 1, 0, 101, 0, "わたし…\nもうちょっとだけ勇気\n出してみようかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100201, 57, 1, 0, 102, 0, "みんなのことが大好き\nも、もちろん、\n騎士クンのことも…\nな、仲間として！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100201, 58, 1, 0, 1, 0, "ひゃうっ！\nな、なに？\nびっくりしたぁ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100201, 59, 1, 0, 2, 0, "男の子って、\nやっぱりおっきいね。\nその…手とか\n他にもいろいろ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100201, 60, 1, 0, 3, 0, "困ったことが\nあったら\nいつでも話してね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100201, 61, 1, 0, 4, 0, "え！　む、虫っ！？\n取って取って！\nはやく～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 100201, 62, 1, 0, 101, 0, "あのね…\n一緒におでかけ\nしたいんだけど…\nダメかな？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100201, 63, 1, 0, 102, 0, "騎士クンに\n伝えたいことがあるの\nとっても大切な…\nこ、今度、話すね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100201, 64, 1, 1, 1, 0, "お、おはよう！\nあの…朝ごはんは\nもう食べちゃった？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100201, 65, 1, 2, 2, 0, "お昼休みだね\nあの…良かったら\nお弁当…食べない？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100201, 66, 1, 3, 3, 0, "遅くまでお疲れ様\n明日も頑張ろうね\nふわぁ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100201, 67, 1, 0, 1, 0, "よし…\nこれなら\n褒めてもらえるかな？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100201, 68, 1, 0, 2, 0, "がんばることも\n必要だけど、\nお休みも大切だよね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100201, 69, 1, 0, 3, 0, "かわいいなぁ…\nわたしも今度\n作ってみよう。\nえっと、材料は…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100201, 70, 1, 0, 4, 0, "戦う前にもこうやって\n落ち着けたら\nいいのに", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100201, 71, 1, 0, 5, 0, "…はへっ？\nも、もしかして今\n寝ちゃってた…？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100201, 72, 1, 0, 6, 0, "騎士クンと一緒に\n冒険するなら\n体力がないと…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100201, 73, 1, 0, 7, 0, "おいしいものって\n食べ過ぎちゃうけど…\nしょうがない…よね？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100201, 74, 1, 0, 8, 0, "…そっか\nこうすれば魔法が\nもっと強くなるんだ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100202, 76, 1, 0, 1, 0, "洗濯と掃除\nどっちから\nはじめよう？", 1);
