INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100202, 91, 1, 3, 3, 0, "もうこんな時間…\nそろそろ寝なくちゃ\nおやすみなさい", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100202, 92, 1, 0, 1, 0, "よし…\nこれなら\n褒めてもらえるかな？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100202, 93, 1, 0, 2, 0, "がんばることも\n必要だけど\nお休みも大切だよね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100202, 94, 1, 0, 3, 0, "かわいい～♪\n騎士クンは\nこういうのって好き？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100202, 95, 1, 0, 4, 0, "戦う前にもこうやって\n落ち着けたら\nいいのに", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100202, 96, 1, 0, 5, 0, "あの…！\nよ、良かったら\n一緒に…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100202, 97, 1, 0, 6, 0, "騎士クンと一緒に\n冒険するなら\n体力がないと…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100202, 98, 1, 0, 7, 0, "最近服のサイズが\n合わないんだけど…\n食べ過ぎなのかな？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100202, 99, 1, 0, 8, 0, "…やっぱり\n月刊恋愛指南は\nためになる…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100301, 101, 1, 0, 1, 0, "装備の手入れでも\nしようかな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100301, 102, 1, 0, 2, 0, "次に鍛えるべきは\n肉体面か精神面か\nどちらにしよう…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100301, 103, 1, 0, 3, 0, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100301, 104, 1, 0, 4, 0, "仲間なんていらない\nそう思ってた頃も\nあったな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100301, 105, 1, 0, 5, 0, "考えごとをしていると\nつい部屋中を\n歩いてしまうな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 100301, 106, 1, 0, 101, 0, "彼は\nどこまで強くなるのか\n私も負けられないな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100301, 107, 1, 0, 102, 0, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100301, 108, 1, 0, 1, 0, "ああ、キミだったのか\n人の気配がしたから\n誰かと思ったよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100301, 109, 1, 0, 2, 0, "斬りかかるつもりなら\nちゃんとかかってきて\n…呼んだだけ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100301, 110, 1, 0, 3, 0, "なに？　私に相談？\n戦いについてなら\n喜んで", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100301, 111, 1, 0, 4, 0, "今のが突き？\n軽い！　もっと鋭く！\nさあ、もう一回！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 100301, 112, 1, 0, 101, 0, "ひゃっ！　ど、どこを\n触ってるの！？\n…油断した", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100301, 113, 1, 0, 102, 0, "キミにはこれからも\n私のそばに…\nいや、なんでもない", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100301, 114, 1, 1, 1, 0, "おはよう\n今日も充実した\n一日にしていこう", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100301, 115, 1, 2, 2, 0, "そろそろ\nお茶の時間か\nキミも一緒にどう？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100301, 116, 1, 3, 3, 0, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100301, 117, 1, 0, 1, 0, "戦いを経てもなお\n美しくあるように…\nこれでよし♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100301, 118, 1, 0, 2, 0, "戦ってばかりで\n休まないと、また\nユイに怒られるな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100301, 119, 1, 0, 3, 0, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100301, 120, 1, 0, 4, 0, "これはいい…\n戦いの前の精神統一に\nもってこいじゃないか", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100301, 121, 1, 0, 5, 0, "時々は落ち着いた\n時間を過ごすのも\n悪くない…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100301, 122, 1, 0, 6, 0, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100301, 123, 1, 0, 7, 0, "食事は大切だけど\n食べ過ぎには\n注意しておこう…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100301, 124, 1, 0, 8, 0, "ユイ…！\nこ、こんな本を\n読んでいたのか…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100302, 126, 1, 0, 1, 0, "海だけじゃなくて\n川でも釣りがしたいな\n道具も新調しよう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100302, 127, 1, 0, 2, 0, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100302, 128, 1, 0, 3, 0, "彼は\nどこまで強くなるのか\n私も負けられないな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100302, 129, 1, 0, 4, 0, "そろそろ修行の\n内容を新しくしよう\nより厳しいものにね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100302, 130, 1, 0, 5, 0, "強くなって…誰にも\n妨げられない自由な\n生き方がしたい", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100302, 131, 1, 0, 101, 0, "まさか風邪を引くとは\n彼の看病のおかげで\nすっかり良く\nなったけど", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100302, 132, 1, 0, 102, 0, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100302, 133, 1, 0, 1, 0, "どうしたの？\nなんでもない？\nそれなら良かった", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100302, 134, 1, 0, 2, 0, "あんまり突っつくと\nお返しするよ？\n覚悟は出来てる？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100302, 135, 1, 0, 3, 0, "ねぇ、今度また\n釣りに行かない？\n大きいのを狙おう", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100302, 136, 1, 0, 4, 0, "修行がしたいの？\nそれを早く言ってよ\nすぐに出発しよう！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100302, 137, 1, 0, 101, 0, "ひょっとして\n今のも一本だなんて\n言わないよね？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100302, 138, 1, 0, 102, 0, "キミにはこれからも\n私のそばに…\nいや、なんでもない", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100302, 139, 1, 1, 1, 0, "今日は仕事？\nそれとも修行？\n良ければ付き合うよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100302, 140, 1, 2, 2, 0, "休息も時には\n必要だと、仲間から\n教わったよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100302, 141, 1, 3, 3, 0, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100302, 142, 1, 0, 1, 0, "髪の長さを少し\n変えてみようかな…\nわ、私は何を…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100302, 143, 1, 0, 2, 0, "休息があってこそ\n万全な状態で\n戦えるんだ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100302, 144, 1, 0, 3, 0, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100302, 145, 1, 0, 4, 0, "体と同様に\n心もほぐれるね…\nはぁ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100302, 146, 1, 0, 5, 0, "休む時は休む\nヒヨリもそろそろ\n覚えて欲しいな…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100302, 147, 1, 0, 6, 0, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100302, 148, 1, 0, 7, 0, "修行のせいか\n最近食べる量が\n増えてきたような…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100302, 149, 1, 0, 8, 0, "ユイ…！\nこ、こんな本を\n読んでいたのか…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100401, 151, 1, 0, 1, 0, "お宝の予感！\n部屋中を\n探検しちゃうぞ～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100401, 152, 1, 0, 2, 0, "落とし穴…どこに\n作ったんだっけ？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100401, 153, 1, 0, 3, 0, "うえ～ん！\n…嘘泣きしたら\n相手は油断するかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100401, 154, 1, 0, 4, 0, "ハニートラップって\nすごく甘いお菓子で\nイタズラするのかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100401, 155, 1, 0, 5, 0, "かくれんぼする人\nこの指、と～まれ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 100401, 156, 1, 0, 101, 0, "ミソギ以外の人に\nイタズラされちゃ\nダメだよ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100401, 157, 1, 0, 102, 0, "ミソギと遊ぶ？\nミソギに遊ばれる？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100401, 158, 1, 0, 1, 0, "いいこと思いついた！\nい、イタズラとかじゃ\nないよ～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100401, 159, 1, 0, 2, 0, "うわっ！？\n　にいちゃんが、\nミソギにイタズラ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100401, 160, 1, 0, 3, 0, "にいちゃんの\n通りそうなところに\nバナナの皮を…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100401, 161, 1, 0, 4, 0, "にいちゃんのオデコと\nホッペと鼻と目と耳に\n落書きを…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 100401, 162, 1, 0, 101, 0, "ここ、ミソギ探検隊の\n基地にしようよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100401, 163, 1, 0, 102, 0, "だ～れだ…って？\nにいちゃんの声くらい\nわかるよ！\n大好きだもん♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100401, 164, 1, 1, 1, 0, "にいちゃん、おはよ！\n目覚まし時計を\n止めておいたのに、\nよく起きられたね～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100401, 165, 1, 2, 2, 0, "にいちゃんが\n昼寝したら、\nイタズラし放題♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100401, 166, 1, 3, 3, 0, "夜の探検も\nおもしろそう！\nねぇ出かけちゃダメ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100401, 167, 1, 0, 1, 0, "イタズラには\nお色気も必要…\nうん、完璧！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100401, 168, 1, 0, 2, 0, "座ったら、ブーッって\nいう音が出たら、\nおもしろいな～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100401, 169, 1, 0, 3, 0, "かわいい！\nミソギ探検隊の\nマスコットにしよう♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100401, 170, 1, 0, 4, 0, "吸うとくしゃみが\n止まらなくなるお香を\nここに…くしゅっ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100401, 171, 1, 0, 5, 0, "にひひ…イタズラの\n証拠、ここで\n燃やしちゃおう～！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100401, 172, 1, 0, 6, 0, "探検家は\n体力勝負だよー！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100401, 173, 1, 0, 7, 0, "みんなに分けて\nあげようかな～\n激辛ソースも\n入れちゃうけどね！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100401, 174, 1, 0, 8, 0, "こういう所に\n宝の地図が\n隠れてるんだよね～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100402, 176, 1, 0, 1, 0, "お宝の予感！\n部屋中を\n探検しちゃうぞ～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100402, 177, 1, 0, 2, 0, "うぅ、探検に出たい！\nじっとしてなんか\nいられないよ～！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100402, 178, 1, 0, 3, 0, "うえ～ん！\n…嘘泣きしたら\n相手は油断するかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100402, 179, 1, 0, 4, 0, "ハニートラップって\nすごく甘いお菓子で\nイタズラするのかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100402, 180, 1, 0, 5, 0, "かくれんぼする人\nこの指、と～まれ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100402, 181, 1, 0, 101, 0, "にいちゃんのオデコと\nホッペと鼻と目と耳に\n落書きを…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100402, 182, 1, 0, 102, 0, "にいちゃんが喜ぶ\nイタズラ、何かない\nかな～？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100402, 183, 1, 0, 1, 0, "ミソギがしかけた\nイタズラ\n見つけられるかな？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100402, 184, 1, 0, 2, 0, "探検する？\n冒険する？\nそれとも…\nイタズラされる？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100402, 185, 1, 0, 3, 0, "将来の夢は探検家！\n探したい物はないけど", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100402, 186, 1, 0, 4, 0, "にいちゃんに宿題！\n落とし穴を10個\n掘ること！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100402, 187, 1, 0, 101, 0, "だ～れだ…って？\nにいちゃんの声くらい\nわかるよ！\n大好きだもん♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100402, 188, 1, 0, 102, 0, "ここ、ミソギ探検隊の\n基地にしようよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100402, 189, 1, 1, 1, 0, "にいちゃん\n探検に行こうよ！\n今からなら遭難しても\n夜には帰れるよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100402, 190, 1, 2, 2, 0, "にいちゃんが昼寝したら\nイタズラし放題♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100402, 191, 1, 3, 3, 0, "夜の探検も\nおもしろそう！\nねぇ出かけちゃダメ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100402, 192, 1, 0, 1, 0, "イタズラには\nお色気も必要…\nうん、完璧！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100402, 193, 1, 0, 2, 0, "ゆっくりまったりして\n次のイタズラを\n考えよ～！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100402, 194, 1, 0, 3, 0, "かわいい！\nミソギ探検隊の\nマスコットにしよう♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100402, 195, 1, 0, 4, 0, "吸うとくしゃみが\n止まらなくなるお香を\nここに…くしゅっ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100402, 196, 1, 0, 5, 0, "にひひ…イタズラの\n証拠、ここで\n燃やしちゃおう～！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100402, 197, 1, 0, 6, 0, "探検家は\n体力勝負だよー！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100402, 198, 1, 0, 7, 0, "探検のおやつに\nぴったりかも～！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100402, 199, 1, 0, 8, 0, "こういう所に\n宝の地図が\n隠れてるんだよね～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100601, 201, 1, 0, 1, 0, "音楽は全部好きだけど\n一番好きなのは\nやっぱりジャズかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100601, 202, 1, 0, 2, 0, "はぁ、はぁ…\n一人でもいっぱい\nしないと…\n特訓…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100601, 203, 1, 0, 3, 0, "お姉ちゃんには\nもっと自信を\n持ってほしいなぁ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100601, 204, 1, 0, 4, 0, "お姉ちゃんのマネ\nしちゃおうかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100601, 205, 1, 0, 5, 0, "双子って、\n好みのタイプも\n似ちゃうのかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 100601, 206, 1, 0, 101, 0, "お兄ちゃんと私は\n相性抜群です♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100601, 207, 1, 0, 102, 0, "もう待ちきれない…\nお兄ちゃん、\n早く帰って来て！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100601, 208, 1, 0, 1, 0, "あんっ…着替えよう\nと思ったのに\nお兄ちゃんのエッチ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100601, 209, 1, 0, 2, 0, "うふふ、お兄ちゃん！\n今日は何を\nするんですか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100601, 210, 1, 0, 3, 0, "そんなところ\n触っちゃダメ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100601, 211, 1, 0, 4, 0, "お兄ちゃん、イジワル\nしないでください…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 100601, 212, 1, 0, 101, 0, "お兄ちゃん、もっと\n上級編のテクニック、\n教えてくださいね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100601, 213, 1, 0, 102, 0, "コーチ、\n今日も特訓\nお願いします！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100601, 214, 1, 1, 1, 0, "おはようございます\n昨夜は楽しかった\nですね、うふふ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100601, 215, 1, 2, 2, 0, "こんな明るい時間から\nす、するんですか…\n二人っきりの特訓…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100601, 216, 1, 3, 3, 0, "夜はこれからですよ\nお兄ちゃん♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100601, 217, 1, 0, 1, 0, "世界一、かわいく\nなるんだから♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100601, 218, 1, 0, 2, 0, "気持ちいい♪\nもう立てないよ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100601, 219, 1, 0, 3, 0, "かわいい♪\nアカリのライバル\nだね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100601, 220, 1, 0, 4, 0, "はぁ…気持ちいい、\n私、このままダメに\nなっちゃいそう…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100601, 221, 1, 0, 5, 0, "はぁ…アカリ、\nとろけちゃう…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100601, 222, 1, 0, 6, 0, "はぁ…はぁはぁ、\n私…もう…ダメ\n限界です…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100601, 223, 1, 0, 7, 0, "わぁ、おいしい～\nけど食べ過ぎは美容に\nよくないですよね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100601, 224, 1, 0, 8, 0, "この楽譜、\nサックスで吹いて\nみたいなぁ♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100602, 226, 1, 0, 1, 0, "このごろ一人でいると\nお兄ちゃんのこと\nばかり考えちゃう…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100602, 227, 1, 0, 2, 0, "はぁ、はぁ…もっと\nいっぱいしなくちゃ\n…練習", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100602, 228, 1, 0, 3, 0, "このお部屋、なんだか\n落ち着きます…\nお兄ちゃんが\nいるから？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100602, 229, 1, 0, 4, 0, "みんなかわいいな～\n世界一かわいくなる\nのは、難しいな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100602, 230, 1, 0, 5, 0, "双子って、\n好みのタイプも\n似ちゃうのかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100602, 231, 1, 0, 101, 0, "いつか世界でいちばん\nかわいくなって\nお兄ちゃんと…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100602, 232, 1, 0, 102, 0, "もう待ちきれない…\nお兄ちゃん、\n早く帰って来て！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100602, 233, 1, 0, 1, 0, "あんっ…着替えよう\nと思ったのに\nお兄ちゃんのエッチ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100602, 234, 1, 0, 2, 0, "二人きりの秘密の特訓\n今度はいつですか？\n待ち遠しいです…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100602, 235, 1, 0, 3, 0, "え？　ここでするの？\nみんなの前で恥ずか\nしいな…筋トレ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100602, 236, 1, 0, 4, 0, "お兄ちゃん、イジワル\nしないでください…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100602, 237, 1, 0, 101, 0, "お兄ちゃんといると\nわ、私…ドキドキ\nしてきちゃう…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100602, 238, 1, 0, 102, 0, "コーチ、\n今日も特訓\nお願いします！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100602, 239, 1, 1, 1, 0, "おはようございます\n昨夜は楽しかった\nですね、うふふ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100602, 240, 1, 2, 2, 0, "お兄ちゃんに\n食べてもらいたいなぁ\n手作りのお昼ごはん", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100602, 241, 1, 3, 3, 0, "お風呂入ってきますね\nそのあとは…\n二人でい～っぱい\n遊びましょうね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100602, 242, 1, 0, 1, 0, "お姉ちゃんのマネ\nしちゃおうかな？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100602, 243, 1, 0, 2, 0, "気持ちいい♪\nもう立てないよ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100602, 244, 1, 0, 3, 0, "かわいい♪\nアカリのライバル\nだね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100602, 245, 1, 0, 4, 0, "はぁ…気持ちいい、\n私、このままダメに\nなっちゃいそう…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100602, 246, 1, 0, 5, 0, "あったか～い…\nまるでお兄ちゃんが\n隣にいる時みたい", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100602, 247, 1, 0, 6, 0, "汗をかくと\n気持ちいいですよね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100602, 248, 1, 0, 7, 0, "お兄ちゃんの特訓に\n付き合うために\nたっぷり\n食べておかないと♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100602, 249, 1, 0, 8, 0, "この楽譜\nサックスで吹いて\nみたいなぁ♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100701, 251, 1, 0, 1, 0, "プリン、プリン、\nプリン、プリン、\nプリン、プリン～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100701, 252, 1, 0, 2, 0, "う～ら～め～し～や～\nなの～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100701, 253, 1, 0, 3, 0, "プリンがないなら\n死んだほうがマシなの\nあ、もう死んでたの～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100701, 254, 1, 0, 4, 0, "幽霊の正体見たり、\nミヤコなの～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100701, 255, 1, 0, 5, 0, "ミヤコがいきなり\n消えたら、みんな\n驚くかもなの～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 100701, 256, 1, 0, 101, 0, "アイツがいて、\nプリンもある。\nきっとここが天国なの", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100701, 257, 1, 0, 102, 0, "アイツはプリンセス\nナイトじゃなくて\nプリンナイトなの～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100701, 258, 1, 0, 1, 0, "姿を消して、オマエを\n見張ってやるの～\nむふふ～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100701, 259, 1, 0, 2, 0, "もし成仏した先に\nプリンがなかったら\n地獄なの～…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100701, 260, 1, 0, 3, 0, "右にプリン左にプリン\n後ろにもプリン♪\n前にはオマエなの～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100701, 261, 1, 0, 4, 0, "死んだ人には\nお供え物をするの～\nミヤコにも、プリンを\n毎日お供えするの～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 100701, 262, 1, 0, 101, 0, "たくさんオマエと\n遊べて、\n成仏しそうなくらい\nうれしいの～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100701, 263, 1, 0, 102, 0, "オマエのこと\n気に入ったからずっと\nとり憑いてやるの～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100701, 264, 1, 1, 1, 0, "う～　眠いの…\n二度目の二度寝を\nするの…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100701, 265, 1, 2, 2, 0, "お昼ご飯はプリンなの\n３時のおやつも、\nプリンなの～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100701, 266, 1, 3, 3, 0, "ディナーは豪華に、\nプリンのフルコース\nなの～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100701, 267, 1, 0, 1, 0, "体が透けちゃうから\nうまく使えないの～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100701, 268, 1, 0, 2, 0, "まったりなの～\nあとはプリンがあれば\n完璧なの～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100701, 269, 1, 0, 3, 0, "む～…かわいいけど\nプリンは分けて\nあげないの～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100701, 270, 1, 0, 4, 0, "は～　いい気分なの…\n成仏しそうなの…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100701, 271, 1, 0, 5, 0, "も、もしかして\nミヤコ、ここで\n火葬されちゃうの？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100701, 272, 1, 0, 6, 0, "幽霊には、体力も\n筋力も関係ないの～！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100701, 273, 1, 0, 7, 0, "ぱくぱくモグモグ…\nミヤコ、幽霊だけど、\nお腹はすくの～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100701, 274, 1, 0, 8, 0, "プリンの写真集が\nあればいいのに\nなの～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100702, 276, 1, 0, 1, 0, "うらめしや～…\n裏の飯より\nプリンがいいの～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100702, 277, 1, 0, 2, 0, "プリン♪　プリン♪\nプリン♪　なの～♪　", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100702, 278, 1, 0, 3, 0, "世界中のケーキ屋が\nプリンのおいしさを\n競い合えばいいの～！\n審査員はミヤコなの～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100702, 279, 1, 0, 4, 0, "買ってきたプリンには\n全部ミヤコの名前を\n書いておくの～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100702, 280, 1, 0, 5, 0, "プリンがないなら\n死んだほうがマシなの\nあ、もう死んでたの～ ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100702, 281, 1, 0, 101, 0, "アイツがいて\nプリンもある\nきっとここが天国なの", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100702, 282, 1, 0, 102, 0, "アイツはプリンセス\nナイトじゃなくて\nプリンナイトなの～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100702, 283, 1, 0, 1, 0, "右にプリン左にプリン\n後ろにもプリン♪\n前にはオマエなの～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100702, 284, 1, 0, 2, 0, "もし成仏した先に\nプリンがなかったら\n地獄なの～…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100702, 285, 1, 0, 3, 0, "今日はオマエと\nプリンを買いに行くの\n強制なの～　", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100702, 286, 1, 0, 4, 0, "死んだ人には\nお供え物をするの～\nミヤコにも、プリンを\n毎日お供えするの～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100702, 287, 1, 0, 101, 0, "たくさんオマエと\n遊べて\n成仏しそうなくらい\nうれしいの～！ ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100702, 288, 1, 0, 102, 0, "ミヤコのプリン\nオマエにだったら\n分けてあげるの～ ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100702, 289, 1, 1, 1, 0, "う～　眠いの…\n二度目の二度寝を\nするの… ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100702, 290, 1, 2, 2, 0, "う～ら～め～し～や～\n…明るい時間だと\n驚かせないの～ ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100702, 291, 1, 3, 3, 0, "ひゅ～どろどろ～ん☆\n真っ暗なうちに\nいたずらしまくるの～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100702, 292, 1, 0, 1, 0, "体が透けちゃうから\nうまく使えないの～ ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100702, 293, 1, 0, 2, 0, "落ち着くの～\nいまプリン食べたら\nきっといつもより\nおいしいの～♪ ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100702, 294, 1, 0, 3, 0, "む～…かわいいけど\nプリンは分けて\nあげないの～　", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100702, 295, 1, 0, 4, 0, "は～　いい気分なの…\n成仏しそうなの…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100702, 296, 1, 0, 5, 0, "も、もしかして\nミヤコ、ここで\n火葬されちゃうの？ ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100702, 297, 1, 0, 6, 0, "幽霊には、体力も\n筋力も関係ないの～！　", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100702, 298, 1, 0, 7, 0, "おいしいの～\nプリン味だったら\nもっとおいしいの～ ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100702, 299, 1, 0, 8, 0, "プリンの写真集が\nあればいいのに\nなの～ ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100801, 301, 1, 0, 1, 0, "もし太陽が滅んでも\nボクの美しさで大地を\n照らしてあげる！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100801, 302, 1, 0, 2, 0, "う～ん、この部屋には\nボクの肖像画や\n彫像が必要だね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100801, 303, 1, 0, 3, 0, "美の女神と愛の女神が\nボクを作る時、本気\n出しちゃったんだね♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100801, 304, 1, 0, 4, 0, "ボクの誕生とともに\n全ての美しさは過去の\n遺物になったんだよ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100801, 305, 1, 0, 5, 0, "うわっ昨日より可愛く\nなってる。明日は\nどうなっちゃう\nのかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 100801, 306, 1, 0, 101, 0, "このボクがいるだけで\nあの人への\nおもてなしだよね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100801, 307, 1, 0, 102, 0, "ボクの可愛さは、\nふふ…あの人の\nためのものさ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100801, 308, 1, 0, 1, 0, "ねぇ～、ボクって\n可愛い？\nふふ、知ってる☆", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100801, 309, 1, 0, 2, 0, "呼んだ？\n可愛いボクと話したい\n気持ちはわかるよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100801, 310, 1, 0, 3, 0, "ふふ、ボクに触ると\nキミも美しく\nなっちゃうかもよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100801, 311, 1, 0, 4, 0, "どうかな、\n今日のボク。\n美しい？　可愛い？\nねえ、どっち？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 100801, 312, 1, 0, 101, 0, "あの人も今、\n可愛いボクのこと\n考えてるかな…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100801, 313, 1, 0, 102, 0, "美しい…\nキミの瞳に写るボクは\nより一層きれいだ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100801, 314, 1, 1, 1, 0, "美しいボクの\n美しい一日が\n始まるよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100801, 315, 1, 2, 2, 0, "さーて、午後からも\n可愛く美しく\n華麗に優雅に♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100801, 316, 1, 3, 3, 0, "バラの花びら\nいっぱい浮かべた\nお風呂に入ってから\n寝ようっと♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100801, 317, 1, 0, 1, 0, "鏡よ、鏡。この世で\n一番可愛いボクに\nお似合いの人はだれ？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100801, 318, 1, 0, 2, 0, "ボクが座ったソファは\n「美しいソファ」に\nランクアップした！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100801, 319, 1, 0, 3, 0, "わあ、可愛い！\n…まあ、ボクほど\nじゃないけどさ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100801, 320, 1, 0, 4, 0, "服を羽織るように\n香りをまとうボク…\n美しい、美しすぎる！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100801, 321, 1, 0, 5, 0, "気安く触ったら\n火傷する…\n炎はボクに似ているね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100801, 322, 1, 0, 6, 0, "運動は苦手なのに\n運動するボクの姿は\n可愛いなんて…罪♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100801, 323, 1, 0, 7, 0, "ボクが食べると、\n食べ物まで\n可愛いなあ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100801, 324, 1, 0, 8, 0, "ボクが触った瞬間に、\nこの本は美を\n携えてしまうんだ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100802, 326, 1, 0, 1, 0, "ボクの美しさは\nみんなのために\nあるんだよ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100802, 327, 1, 0, 2, 0, "う～ん、この部屋には\nボクの肖像画や\n彫像が必要だね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100802, 328, 1, 0, 3, 0, "これ以上美しくなると\nボクをめぐって\n大陸中で戦争が\n始まっちゃう！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100802, 329, 1, 0, 4, 0, "ボクの誕生とともに\n全ての美しさは過去の\n遺物になったんだよ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100802, 330, 1, 0, 5, 0, "ボクの可愛さについて\nみんな話し合わない\nのかな…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100802, 331, 1, 0, 101, 0, "このボクがいるだけで\nあの人への\nおもてなしだよね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100802, 332, 1, 0, 102, 0, "あの人も今\n可愛いボクのこと\n考えてるかな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100802, 333, 1, 0, 1, 0, "美の女神と愛の女神が\nボクを作る時、本気\n出しちゃったんだね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100802, 334, 1, 0, 2, 0, "ボクの美しさに嫉妬\nしたって意味ないよね\n次元が違うんだから", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100802, 335, 1, 0, 3, 0, "ボクの化粧品使う？\n一緒に可愛くなろうよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100802, 336, 1, 0, 4, 0, "ボクがいるだけで\nこの部屋は美の神殿♪\nランドソルの新たな\n観光名所になるね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100802, 337, 1, 0, 101, 0, "呼んだ？\n可愛いボクと話したい\n気持ちはわかるよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100802, 338, 1, 0, 102, 0, "美しい…\nキミの瞳に写るボクは\nより一層綺麗だ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100802, 339, 1, 1, 1, 0, "朝日と共に\nボクの美しさも\n周りを照らすよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100802, 340, 1, 2, 2, 0, "ボクの美しさを\n維持するために\nお昼ご飯食べなくちゃ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100802, 341, 1, 3, 3, 0, "バラの花びらいっぱい\n浮かべたお風呂に\n入ってから\n寝ようっと♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100802, 342, 1, 0, 1, 0, "鏡よ、鏡。この世で\n一番可愛いボクに\nお似合いの人はだれ？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100802, 343, 1, 0, 2, 0, "ボクが座ったソファは\n「美しいソファ」に\nランクアップした！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100802, 344, 1, 0, 3, 0, "可愛いものを\n可愛いって言える\nボクって可愛いよね！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100802, 345, 1, 0, 4, 0, "服を羽織るように\n香りをまとうボク…\n美しい、美しすぎる！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100802, 346, 1, 0, 5, 0, "冷えは美容の大敵\nなんだから！\nここ、ボクの場所ね！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100802, 347, 1, 0, 6, 0, "運動は苦手なのに\n運動するボクの姿は\n可愛いなんて…罪♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100802, 348, 1, 0, 7, 0, "ボクが食べると\n食べ物まで\n可愛いなあ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100802, 349, 1, 0, 8, 0, "ボクが触った瞬間に\nこの本は美を\n携えてしまうんだ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100901, 351, 1, 0, 1, 0, "「第１０部２０章\n終焉戦争、開幕」\n…これでよし！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100901, 352, 1, 0, 2, 0, "今度シグルドに\n私の魔法を一つ\n伝授してやるか", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100901, 353, 1, 0, 3, 0, "ここでは風が\n吹いてこないな…\n換気したい！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100901, 354, 1, 0, 4, 0, "そうだ、ナナカに\nこの間借りた本を\n返さないと…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100901, 355, 1, 0, 5, 0, "まずは包帯をとって\n手から龍の形の\n炎を出して…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 100901, 356, 1, 0, 101, 0, "シグルド…\n今頃何処で何を\nしているのだろう…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100901, 357, 1, 0, 102, 0, "最近、彼の姿が\nまぶしく見える…\nどうして…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100901, 358, 1, 0, 1, 0, "辞書を知らないか？\n…探している\n理由は聞くな", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100901, 359, 1, 0, 2, 0, "そうだ！\n貴様の技にも\n名前をつけてやろう！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100901, 360, 1, 0, 3, 0, "私に触れても力を\n奪われないのは\n貴様くらいだぞ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100901, 361, 1, 0, 4, 0, "何事だ？\nまさか我が瞳に\n魅入られたのか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 100901, 362, 1, 0, 101, 0, "貴様がそばにいると\nとても安心できる…\n…不思議だ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100901, 363, 1, 0, 102, 0, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100901, 364, 1, 1, 1, 0, "「覚醒」めたか…\n行くぞ、悪を討つ\n戦いに出発だ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100901, 365, 1, 2, 2, 0, "昼間は「ヤツら」も\n襲ってはこない…\n束の間の休息だ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100901, 366, 1, 3, 3, 0, "どうした？　夜はまだ\nこれから…ふぁ…\nね、眠くなどないぞ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100901, 367, 1, 0, 1, 0, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100901, 368, 1, 0, 2, 0, "くっ…！\n私を眠りの国へ\n誘うつもりか！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100901, 369, 1, 0, 3, 0, "このようなものに\n懐柔されるものか！\n…されるものか！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100901, 370, 1, 0, 4, 0, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100901, 371, 1, 0, 5, 0, "これが我らの求めた\n安寧か…\n悪くない…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100901, 372, 1, 0, 6, 0, "この程度\n前世での激闘に\n比べれば…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100901, 373, 1, 0, 7, 0, "最近金欠でな…\nいやぁありがたい…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100901, 374, 1, 0, 8, 0, "ここにも\n前世の記憶が\n眠っていたとは…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100902, 376, 1, 0, 1, 0, "「第１３部１章\n閃光のシグルド」\n…ついに登場だな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100902, 377, 1, 0, 2, 0, "何故だ…何故\nこうもあっさり金欠に\n逆戻りするのだ！？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100902, 378, 1, 0, 3, 0, "やはり転生者という\n設定はカッコイイ…\n天才だな、私！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100902, 379, 1, 0, 4, 0, "そうだ、ナナカに\nこの間借りた本を\n返さないと…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100902, 380, 1, 0, 5, 0, "そろそろ今日の分の\n魔力を溜めないと\nまた魔力切れが…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100902, 381, 1, 0, 101, 0, "最近奴がそばに\nいるだけで、鼓動が\n早くなるのは何故だ？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100902, 382, 1, 0, 102, 0, "シ、シグルド…！\nだめだ…妄想でも\n上手く言えない…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100902, 383, 1, 0, 1, 0, "なんだ？　新たな\n賞金の情報を\n持ってきたのか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100902, 384, 1, 0, 2, 0, "名乗りやポーズは\nその強度で相手を\n圧倒する技術だ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100902, 385, 1, 0, 3, 0, "いいか、魔法の\n詠唱中は絶対に\n声をかけるなよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100902, 386, 1, 0, 4, 0, "おお、ちょうど今\n貴様専用の設定集を\n作成中だぞ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100902, 387, 1, 0, 101, 0, "貴様がそばにいると\nとても安心できる…\n…不思議だ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100902, 388, 1, 0, 102, 0, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100902, 389, 1, 1, 1, 0, "夜更かしのせいか\nまだ眠気が…いや！\nこれは組織の罠だ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100902, 390, 1, 2, 2, 0, "平穏な時に\n出来るだけ魔力を\n溜めておかねば…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100902, 391, 1, 3, 3, 0, "夜になると、力が\n増した気分になるな\n…なりませんか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100902, 392, 1, 0, 1, 0, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100902, 393, 1, 0, 2, 0, "お、おのれ\nヒュプノス…\n私は負けないぞ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100902, 394, 1, 0, 3, 0, "くっくっくっ…\n貴様は特別団員に\nしてやろう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100902, 395, 1, 0, 4, 0, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100902, 396, 1, 0, 5, 0, "これが我らの求めた\n安寧か…\n悪くない…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100902, 397, 1, 0, 6, 0, "新たな戦いに備えて\n魔力だけでなく\n体力も鍛えねば！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100902, 398, 1, 0, 7, 0, "食事は魔力に\n変換する体力の\n元になるのだ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100902, 399, 1, 0, 8, 0, "フフフ…\nこれを覚えれば\nもっと強く…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101001, 401, 1, 0, 1, 0, "王子はんほんまに\nはんなりしてて、\n王子はんって感じやわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101001, 402, 1, 0, 2, 0, "この絵本の王子はん、\n王子はんみたいで\nええなぁ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101001, 403, 1, 0, 3, 0, "うちがガラスの靴を\n落としたら、王子はん\n見つけてくれるやろか", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101001, 404, 1, 0, 4, 0, "マホマホ王国\n次期国王の王子はん\n…見てみたいわ～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101001, 405, 1, 0, 5, 0, "王子はんのために\n糸を紡いで、\n編み物でもしまひょか", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 101001, 406, 1, 0, 101, 0, "マホ姫と王子はん、\n絵本の世界より\nベストカップルや", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 101001, 407, 1, 0, 102, 0, "王子はんと\n一緒にいると\n胸がせわしのー\nうごいてかなん", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101001, 408, 1, 0, 1, 0, "うちら並んでたら\nお雛様みたいに\n見えると思いまへん？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101001, 409, 1, 0, 2, 0, "王子はんを\n国に連れて帰ったら\nみんな大歓迎して\nくれるやろなぁ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101001, 410, 1, 0, 3, 0, "王子はんは、\n絵本から出てきた人\nみたいやね～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101001, 411, 1, 0, 4, 0, "うちの話し方\nのんびりしてます\nやろか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 101001, 412, 1, 0, 101, 0, "マホ姫の手は\nつべたいから、\n王子はんが温めて？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 101001, 413, 1, 0, 102, 0, "うちがもし悪い魔法に\nかかったら、王子はん\n口づけしてくれはる？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101001, 414, 1, 1, 1, 0, "朝は小鳥はんも\nおなかがすいてるから\nよう飛び回りはるねえ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101001, 415, 1, 2, 2, 0, "お茶請けはお大福が\nよろしおすけど\nクッキーもええなあ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101001, 416, 1, 3, 3, 0, "うち、普段から\n夢見がちやけど…\nほんに眠い時間やわぁ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101001, 417, 1, 0, 1, 0, "王子はんに\n会いに行くなら、\nおめかしせな\nあかんなぁ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101001, 418, 1, 0, 2, 0, "はんなりした\nええお椅子やわぁ\nほっこりしますなあ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101001, 419, 1, 0, 3, 0, "かいらしもんが\nいっぱいで、\nマホマホ王国みたい\nやわぁ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101001, 420, 1, 0, 4, 0, "心地ええどす。まるで\nお花畑にいるようやね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101001, 421, 1, 0, 5, 0, "ぽかぽかどす。\n心まであったかく\nなりますわぁ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101001, 422, 1, 0, 6, 0, "はぁはぁ、お姫はんは\n激しい運動は\nしぃひんもんどす～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101001, 423, 1, 0, 7, 0, "もぐもぐ…あんまり\n速う食べられへんけど\n美味しいわぁ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101001, 424, 1, 0, 8, 0, "絵本、絵本～\n幸せやわぁ♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101002, 426, 1, 0, 1, 0, "王子はんにとって\n自慢の姫になるえ～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101002, 427, 1, 0, 2, 0, "カボチャの馬車に\nガラスの靴…\n欲しいなぁ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101002, 428, 1, 0, 3, 0, "うちがガラスの靴を\n落としたら、王子はん\n見つけてくれるやろか", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101002, 429, 1, 0, 4, 0, "マホマホ王国\n次期国王の王子はん\n…見てみたいわ～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101002, 430, 1, 0, 5, 0, "ほんまなぁ\nこの絵本が面白いんよ\n王子はんに\n読んであげよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 101002, 431, 1, 0, 101, 0, "マホ姫と王子はん\n絵本の世界より\nベストカップルや", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 101002, 432, 1, 0, 102, 0, "王子はんと\n一緒にいると\n胸がせわしのー\nうごいてかなん", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101002, 433, 1, 0, 1, 0, "たまには、王子はんの\n好きな絵本も\n知りたいな～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101002, 434, 1, 0, 2, 0, "王子はんを\n国に連れて帰ったら\nみんな大歓迎して\nくれるやろなぁ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101002, 435, 1, 0, 3, 0, "そのうち、王子はんの\nお国にも連れてって\nもらえますやろか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101002, 436, 1, 0, 4, 0, "絵本読むんも\nええなぁ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 101002, 437, 1, 0, 101, 0, "うちがもし悪い魔法に\nかかったら、王子はん\n口づけしてくれはる？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 101002, 438, 1, 0, 102, 0, "うちら並んでたら\nお雛様みたいに\n見えると思いまへん？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101002, 439, 1, 1, 1, 0, "朝は小鳥はんも\nおなかがすいてるから\nよう飛び回りはるねえ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101002, 440, 1, 2, 2, 0, "マホマホ王国では\nこの時分は\nみんなお昼寝\nしてたんよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101002, 441, 1, 3, 3, 0, "夜に飛んだり鳴いたり\nする鳥はんもおるんよ\n昼は寝てはるんかねぇ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101002, 442, 1, 0, 1, 0, "王子はんに\n会いに行くなら\nおめかしせな\nあかんなぁ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101002, 443, 1, 0, 2, 0, "王子はんも一緒に\nゆっくりしていっても\nええんよ？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101002, 444, 1, 0, 3, 0, "かいらしい子やねぇ\nうちとお友だちに\nなってくれるん？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101002, 445, 1, 0, 4, 0, "癒しアイテムって\nほんま、好っきゃわぁ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101002, 446, 1, 0, 5, 0, "うちのお城にも\nほしいわぁ。帰ったら\nさっそく準備せな\nあかんなぁ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101002, 447, 1, 0, 6, 0, "部屋の中で運動\nできるのも、なかなか\n面白いもんやわぁ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101002, 448, 1, 0, 7, 0, "おいひいわ～\nマホマホ王国には\nない味やわぁ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101002, 449, 1, 0, 8, 0, "しっかり知識を\n身につけんと\n魔法も使えへん\nからなぁ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101101, 451, 1, 0, 1, 0, "１、２、３、４…\nあれ？　いま、\n何段目まで\n編んだっけ？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101101, 452, 1, 0, 2, 0, "おやつのケーキは\n苺かチョコ…\nどっちも食べよう！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101101, 453, 1, 0, 3, 0, "い、いけない！\n縫い針落としちゃった", 1);
