INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101101, 458, 1, 0, 1, 0, "ねぇ、おにいちゃん\n一緒に、スイーツでも\n食べにいかない？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101101, 459, 1, 0, 2, 0, "クリームついてた！？\nさっき食べた、\nケーキかも…！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101101, 460, 1, 0, 3, 0, "勉強、見てくれるん\nですか？　やった～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101101, 461, 1, 0, 4, 0, "服がほつれてますよ？\n直してあげます！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 101101, 462, 1, 0, 101, 0, "私をお嫁さんに\nしたいなら、予約は\nお早目に、ですよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 101101, 463, 1, 0, 102, 0, "ねぇねぇお兄ちゃん！\n…えへへ、\n呼んでみただけ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101101, 464, 1, 1, 1, 0, "今日も１日\n勉強がんばります！\n…少しは遊ぼうかな", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101101, 465, 1, 2, 2, 0, "お昼休憩に\n甘いものが\n食べたいです～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101101, 466, 1, 3, 3, 0, "ねむいです～\nでももうちょっと\n勉強しないと～…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101101, 467, 1, 0, 1, 0, "この服、かわいい♪\n今度作って\nみよっと～！", 3);
