INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101101, 469, 1, 0, 3, 0, "かわいい♪\n私が服を作って\nあげよっか？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101101, 470, 1, 0, 4, 0, "リラックスできます～\nお兄ちゃんが隣に\nいるみたい！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101101, 471, 1, 0, 5, 0, "のんびりしたら、\nまた勉強しよっと！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101101, 472, 1, 0, 6, 0, "運動すると勉強も\nはかどるよね！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101101, 473, 1, 0, 7, 0, "おいし～！\nお兄ちゃんにも\nおすそ上げしますね！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101101, 474, 1, 0, 8, 0, "お兄ちゃんの記憶を\n戻す方法、どこかに\n書いてないかな？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101102, 476, 1, 0, 1, 0, "ケーキの食べ過ぎで\nお腹が…うぅ、\n因果横暴だよー…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101102, 477, 1, 0, 2, 0, "１、２、３、４…\nあれ？　いま、\n何段目まで\n編んだっけ？", 1);
