INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 101102, 482, 1, 0, 102, 0, "お兄ちゃんが帰って\nきたら、何して遊ぼう\nかな～？", 1);
