INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101102, 485, 1, 0, 3, 0, "おにいちゃん！\nそろそろ、おやつに\nしませんか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101102, 486, 1, 0, 4, 0, "勉強は…\n明日にしようっと", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 101102, 487, 1, 0, 101, 0, "妹だけど、大人な\nレディですからね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 101102, 488, 1, 0, 102, 0, "な、ナデナデとか\n恥ずかしいな～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101102, 489, 1, 1, 1, 0, "今日も１日\n勉強がんばります！\n…少しは遊ぼうかな", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101102, 490, 1, 2, 2, 0, "お昼休憩に\n甘いものが\n食べたいです～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101102, 491, 1, 3, 3, 0, "お兄ちゃんは\n夜強いんですね\n私は眠くて…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101102, 492, 1, 0, 1, 0, "服装や髪形が変わると\n気分も変わるよね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101102, 493, 1, 0, 2, 0, "ここで寝ちゃったら\n受験勉強が～…\nぐーー", 3);
