INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101102, 495, 1, 0, 4, 0, "いまならどんな試験も\n合格できそうです！", 3);
