INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101102, 499, 1, 0, 8, 0, "この本の服\nかわいいなぁ♪\n私に作れるかな？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101201, 501, 1, 0, 1, 0, "う～ん、今なら\nどこでだって\n眠れちゃいそう…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101201, 502, 1, 0, 2, 0, "たくさん寝すぎて、\n夢の中か現実か\nわかんなく\nなっちゃうんだよね～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101201, 503, 1, 0, 3, 0, "…はっ！？\nね、寝てないよ？\nホントだよっ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101201, 504, 1, 0, 4, 0, "ぐっすり眠って\nパワーを\nフルチャージっ☆", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101201, 505, 1, 0, 5, 0, "もし、超能力のことが\nバレたら…ブルブル", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 101201, 506, 1, 0, 101, 0, "テレパシーで\nキミの心を覗けたら\n…なんてね！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 101201, 507, 1, 0, 102, 0, "キミを抱き枕に…\nじょ、冗談冗談！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101201, 508, 1, 0, 1, 0, "ん～、なになに？\n一緒にお昼寝する？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101201, 509, 1, 0, 2, 0, "忘れ物しちゃった。\nま、いっか！\nえ、ダメ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101201, 510, 1, 0, 3, 0, "おおっ、相談ごと？\nなにかな\nなにかな～？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101201, 511, 1, 0, 4, 0, "う～ん、もしかして\nキミにも仲良しになる\n超能力があるのかも！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 101201, 512, 1, 0, 101, 0, "むむ～…どう？\nテレパシーで、私の\n気持ちを送ったよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 101201, 513, 1, 0, 102, 0, "えっ？　私とキミとの\n相性？　むむ～…\nうん、バッチリだよ☆", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101201, 514, 1, 1, 1, 0, "まだこんな時間？\nベッドに行って、\n二度寝しなくちゃ～", 2);
