INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101201, 521, 1, 0, 5, 0, "気持ちいい…\nはっ！　ダメダメ！\nまた寝るとこだった！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101201, 522, 1, 0, 6, 0, "眠くならないように、\n運動しよっと！", 3);
