INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101202, 546, 1, 0, 5, 0, "うう～ん、なんだか\nいつもより眠ーく\nなってきたよ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101202, 547, 1, 0, 6, 0, "よーし\n困ってる人のために\nパワーアップ☆", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101202, 548, 1, 0, 7, 0, "食べてすぐ寝ると\n牛になるんだっけ？\nまぁ、いいかぁ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101202, 549, 1, 0, 8, 0, "睡眠学習って\n本を読みながら\n寝るんだっけ？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101601, 551, 1, 0, 1, 0, "モデルは楽しいよ！\nファンの子みんな\n応援してくれるしー！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101601, 552, 1, 0, 2, 0, "今日のおやつは\n駄菓子に決定～！\nいただきまーす！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101601, 553, 1, 0, 3, 0, "んー、今日のおやつは\n何にしよう？\nやっぱアメ玉かな！", 1);
