INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101701, 609, 1, 0, 2, 0, "あぎひゃっ！\n急に声かけたら、\nビックリするさ～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101701, 610, 1, 0, 3, 0, "踊りなら、\n私が手とり足とり\n教えてあげるさ～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101701, 611, 1, 0, 4, 0, "空手の型の\n練習するさー\nキミもどう？", 2);
