INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101701, 616, 1, 3, 3, 0, "故郷では見えない星が\n見られるんだねえ。\nなんか不思議さぁ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101701, 617, 1, 0, 1, 0, "夢中で踊ってたら、\n髪の毛ぼさぼさに\nなってるさ～！？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101701, 618, 1, 0, 2, 0, "よく食べてよく踊って\nよく休む。\nそれが元気の秘訣さ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101701, 619, 1, 0, 3, 0, "ちゅらかーぎーねー！\nキミはどこから\n来たのー？", 3);
