INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101701, 621, 1, 0, 5, 0, "故郷はあんまり暖房を\n使わなかったから、\n新鮮さー！", 3);
