INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101702, 634, 1, 0, 2, 0, "ん？　どうしたの～？\nヒマなら一緒に\n踊ろうさ～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101702, 635, 1, 0, 3, 0, "キミと一緒だから\n毎日楽しいこと\nばっかりさー", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101702, 636, 1, 0, 4, 0, "ハイターイ！　元気な\nあいさつが一番さ～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 101702, 637, 1, 0, 101, 0, "キミと踊ってる時が\n一番幸せさ～\nキミもそう思ってたら\nうれしいさ～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 101702, 638, 1, 0, 102, 0, "海に沈む夕日は\nとっても綺麗さ～！\n今度一緒に見ようね～ ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101702, 639, 1, 1, 1, 0, "気持ちのいい朝さー！\nひとっ走り\nしてくるさー！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101702, 640, 1, 2, 2, 0, "午後も頑張るために\nお昼ご飯は\nてびちにするさー！ ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101702, 641, 1, 3, 3, 0, "故郷では見えない星が\n見られるんだねえ\nなんか不思議さぁ ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101702, 642, 1, 0, 1, 0, "夢中で踊ってたら\n髪の毛ぼさぼさに\nなってるさ～！？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101702, 643, 1, 0, 2, 0, "よく食べてよく踊って\nよく休む\nそれが元気の秘訣さ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101702, 644, 1, 0, 3, 0, "ちゅらかーぎーねー！\nキミはどこから\n来たのー？ ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101702, 645, 1, 0, 4, 0, "いい香り～、って\n私もカオリだったさ！\nなんだか照れるね☆ ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101702, 646, 1, 0, 5, 0, "故郷はあんまり暖房を\n使わなかったから\n新鮮さー！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101702, 647, 1, 0, 6, 0, "徹夜で踊り続ける\nためにも、まずは\n体を鍛えるさ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101702, 648, 1, 0, 7, 0, "もぐもぐ…もっと\nおかわりがほしいさー", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101702, 649, 1, 0, 8, 0, "おおー、故郷の民話！\nこんなとこで見るとは\n懐かしいさぁ～！ ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101801, 651, 1, 0, 1, 0, "はあ…\nすてきな小説だった～\nキュンキュンしちゃう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101801, 652, 1, 0, 2, 0, "このお部屋\n女の子ばかりね。\n魔族の女子校を\n思い出しちゃう♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101801, 653, 1, 0, 3, 0, "次のテスト\n恋愛小説から\n出題したら\nダメかしら…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101801, 654, 1, 0, 4, 0, "キンコンカンコ～ン♪\n授業をはじめま～す", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101801, 655, 1, 0, 5, 0, "はー…\nちょっと肩が\nこっちゃった…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 101801, 656, 1, 0, 101, 0, "私、あの子のこと\n無意識に特別扱い\nしちゃってるかな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 101801, 657, 1, 0, 102, 0, "物語だと生徒が先生を\n好きになるのにな…\n逆の場合は…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101801, 658, 1, 0, 1, 0, "あん！　見ちゃダメよ\n明日のテストの問題を\n作ってるとこなの！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101801, 659, 1, 0, 2, 0, "勉強勉強で\n疲れたよね？\n膝枕してあげる！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101801, 660, 1, 0, 3, 0, "テストがんばったね～\nご褒美に先生が\n特別な授業\nしてあげるね？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101801, 661, 1, 0, 4, 0, "学院の外でも\n私は君の先生よ\nなんでも聞いてね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 101801, 662, 1, 0, 101, 0, "今読んでるのは\nヒロインが年下の子と\n恋に落ちる話…あ、\n深い意味はないのよ～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 101801, 663, 1, 0, 102, 0, "生徒は全員可愛いけど\n君は少し特別…\nみんなには内緒よ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101801, 664, 1, 1, 1, 0, "学校に\n遅刻しないように\n早く準備しないとね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101801, 665, 1, 2, 2, 0, "男の子といっしょに\nお弁当を食べるの、\n夢なんだ～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101801, 666, 1, 3, 3, 0, "明日の予習はやった？\n先生も手伝うから\n寝る前にやりましょ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101801, 667, 1, 0, 1, 0, "もしかして…\n少し露出多いかしら？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101801, 668, 1, 0, 2, 0, "リラックスすると\nα波が出て、勉強が\nはかどるんですって", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101801, 669, 1, 0, 3, 0, "かわいい♪　あなたも\n私の生徒になる？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101801, 670, 1, 0, 4, 0, "学院にもこういう\n安らぐ設備が\n必要かしらね？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101801, 671, 1, 0, 5, 0, "まだ寒いわぁ\n誰か隣に\n来てくれたら…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101801, 672, 1, 0, 6, 0, "運動部の顧問も\nできるかしら？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101801, 673, 1, 0, 7, 0, "学校の給食でも\n食べたいわぁ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101801, 674, 1, 0, 8, 0, "いい参考書を\n見つけちゃった♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101802, 676, 1, 0, 1, 0, "私の生徒に\n不良なんていないわ\nみーんないい子♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101802, 677, 1, 0, 2, 0, "このお部屋\n女の子ばかりね\n魔族の女子校を\n思い出しちゃう♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101802, 678, 1, 0, 3, 0, "あの子、まだかな…\n宿題も見てあげたいし\n早く来ないかしら？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101802, 679, 1, 0, 4, 0, "はあ…\nすてきな小説だった～\nキュンキュンしちゃう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101802, 680, 1, 0, 5, 0, "学校の図書室に\n恋愛小説を\n置いて欲しいなあ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 101802, 681, 1, 0, 101, 0, "私、あの子のこと\n無意識に特別扱い\nしちゃってるかな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 101802, 682, 1, 0, 102, 0, "物語だと生徒が先生を\n好きになるのにな…\n逆の場合は…　", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101802, 683, 1, 0, 1, 0, "あん！　見ちゃダメよ\n明日のテストの問題を\n作ってるとこなの！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101802, 684, 1, 0, 2, 0, "勉強勉強で\n疲れたよね？\n膝枕してあげる！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101802, 685, 1, 0, 3, 0, "宿題はもうやった？\n苦手なところは\n先生が見てあげる♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101802, 686, 1, 0, 4, 0, "んん？　なんだか\n元気がないみたい\n先生がぎゅぅ～って\nしてあげる…　", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 101802, 687, 1, 0, 101, 0, "君のお勉強も\nだいぶ進んできたね～\n補習卒業も近いかな？\n少し寂しいけど…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 101802, 688, 1, 0, 102, 0, "テストがんばったね～\nご褒美に先生が\n特別な授業\nしてあげるね？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101802, 689, 1, 1, 1, 0, "朝の特別授業\nしてあげましょうか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101802, 690, 1, 2, 2, 0, "午後の授業って\n眠いでしょう？\n気持ちは分かるけど\nがんばってね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101802, 691, 1, 3, 3, 0, "明日の予習はやった？\n先生も手伝うから\n寝る前にやりましょ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101802, 692, 1, 0, 1, 0, "もしかして…\n少し露出多いかしら？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101802, 693, 1, 0, 2, 0, "私の膝枕と、どっちが\nフカフカかしら？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101802, 694, 1, 0, 3, 0, "可愛いわね～\n教室に置いたら\n生徒が喜ぶかしら？　", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101802, 695, 1, 0, 4, 0, "は～落ち着くなぁ～\n教師には\nこういうゆとりも\n必要よね♪　", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101802, 696, 1, 0, 5, 0, "まだ寒いわぁ\n誰か隣に\n来てくれたら…　", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101802, 697, 1, 0, 6, 0, "運動部の顧問も\nできるかしら？　", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101802, 698, 1, 0, 7, 0, "あん！\nお胸にこぼしちゃった", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101802, 699, 1, 0, 8, 0, "あぁーん♪\nこういう物語みたいな\n恋愛あこがれる～　", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102001, 701, 1, 0, 1, 0, "おにいちゃんは、\nにんじん食べられる？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102001, 702, 1, 0, 2, 0, "えへへ、\nお絵かきしよ～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102001, 703, 1, 0, 3, 0, "あのね、ミミと\nあそんでほしいな～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102001, 704, 1, 0, 4, 0, "さっきね、きれいな\nお花見つけたよ。\nおにいちゃんにも\n見せてあげたいな～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102001, 705, 1, 0, 5, 0, "うさぎさん、\nぴょんぴょん\nぴょーん♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 102001, 706, 1, 0, 101, 0, "おにいちゃんの絵、\nかいてあげるね♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102001, 707, 1, 0, 102, 0, "おっきくなったら\nおにいちゃんの\nお嫁さんになるんだ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102001, 708, 1, 0, 1, 0, "ふわぁ！\nおにいちゃんの手、\nおっきいね～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102001, 709, 1, 0, 2, 0, "やった～！\nおにいちゃんが\nほめてくれた～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102001, 710, 1, 0, 3, 0, "ねえねえ\nおにいちゃん、\n一緒にあそぼっ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102001, 711, 1, 0, 4, 0, "おにいちゃん、\n遊んでくれるの？\n　わぁい♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 102001, 712, 1, 0, 101, 0, "わぁい！\nおにいちゃんだ～♪\nぎゅ～っ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102001, 713, 1, 0, 102, 0, "あのねあのね、\nミミの頭ポンポンって\nしてほしいな", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102001, 714, 1, 1, 1, 0, "おひさまさん、\nおはよ～！\n毎朝、早おきだね～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102001, 715, 1, 2, 2, 0, "うわぁ！\nお日さまがすごーく\n高いところにある～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102001, 716, 1, 3, 3, 0, "おにいちゃん\nおやすみのご本\nよんで～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102001, 717, 1, 0, 1, 0, "ミミも、\nうさぎさんみたいに、\nかわいくなりたいな♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102001, 718, 1, 0, 2, 0, "のんびり～♪\nゆったり～♪\nまったり～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102001, 719, 1, 0, 3, 0, "わぁ～かわいい～♪\nミミ、幸せ～♪\n抱きついていいかな？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102001, 720, 1, 0, 4, 0, "いいにおい～♪\nお鼻ピクピク♪\nうさぎさんも喜ぶよ♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102001, 721, 1, 0, 5, 0, "あったか～♪\nぬくぬく～♪\nポカポカ～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102001, 722, 1, 0, 6, 0, "ふ、ふぇ～　ミミ、\n運動苦手だよ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102001, 723, 1, 0, 7, 0, "はむ♪　いっぱい\n食べて、はやく\nおっきくなりたいな～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102001, 724, 1, 0, 8, 0, "ミミね！\nこのお話だーい好き！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102002, 726, 1, 0, 1, 0, "ぴょんぴょん\n跳ねるよ\nうっさぎっさ～ん♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102002, 727, 1, 0, 2, 0, "うさぎさんの\nマネだよ～\n似てるかな～？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102002, 728, 1, 0, 3, 0, "モフモフすると\nきもちい～よ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102002, 729, 1, 0, 4, 0, "ミソギちゃん\nキョウカちゃん\nあ～そ～ぼっ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102002, 730, 1, 0, 5, 0, "おにいちゃん\nおやすみのご本\nよんで～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102002, 731, 1, 0, 101, 0, "ねえ、おにいちゃん\nミミのこと、すき？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102002, 732, 1, 0, 102, 0, "おっきくなったら\nおにいちゃんの\nお嫁さんになるんだ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102002, 733, 1, 0, 1, 0, "やった～！\nおにいちゃんが\nほめてくれた～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102002, 734, 1, 0, 2, 0, "おにいちゃん\n遊んでくれるの？\nわぁい♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102002, 735, 1, 0, 3, 0, "おにいちゃん\nあのね…\nいいこいいこして～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102002, 736, 1, 0, 4, 0, "あはは～\nくすぐったいよ～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102002, 737, 1, 0, 101, 0, "おにいちゃんの\nおひざの上、とっても\n安心するんだ～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102002, 738, 1, 0, 102, 0, "わぁい♪\nおにいちゃんの\nなでなで\nうれし～な♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102002, 739, 1, 1, 1, 0, "おひさまさん\nおはよ～！\n毎朝、早おきだね～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102002, 740, 1, 2, 2, 0, "お昼寝お昼寝～♪\nおにいちゃんと一緒に\nお昼寝～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102002, 741, 1, 3, 3, 0, "眠くなっちゃった～\nおにいちゃん！\n今日は一緒に寝よ～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102002, 742, 1, 0, 1, 0, "ミミも\nうさぎさんみたいに\nかわいくなりたいな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102002, 743, 1, 0, 2, 0, "ふわふわ～モコモコ～\n気持ちいい～♪\nミミもゴロゴロ～ン♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102002, 744, 1, 0, 3, 0, "わぁ～かわいい～♪\nミミ、幸せ～♪\n抱きついていいかな？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102002, 745, 1, 0, 4, 0, "いいにおい～♪\nお鼻ピクピク♪\nうさぎさんも喜ぶよ～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102002, 746, 1, 0, 5, 0, "あったか～♪\nぬくぬく～♪\nポカポカ～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102002, 747, 1, 0, 6, 0, "はぁはぁ…\nふえ～ミミ\n疲れちゃうよ～！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102002, 748, 1, 0, 7, 0, "はむ♪　いっぱい\n食べて、はやく\nおっきくなりたいな～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102002, 749, 1, 0, 8, 0, "ミミね！\nこのお話だーい好き！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102101, 751, 1, 0, 1, 0, "はぅぅ…\n部屋のどこにいたら\nいいんだろう…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102101, 752, 1, 0, 2, 0, "お外より\nお部屋の中の方が\n落ち着くな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102101, 753, 1, 0, 3, 0, "クッキー焼いたら\nみんな食べてくれる\nかなぁ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102101, 754, 1, 0, 4, 0, "あ、あめんぼあかいな\nあいうえお！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102101, 755, 1, 0, 5, 0, "お芝居には\n人間観察が大事…\nじーーーーーー…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 102101, 756, 1, 0, 101, 0, "お兄ちゃんを見ると、\n顔が赤くなっちゃうの\n…な、なんでかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102101, 757, 1, 0, 102, 0, "れ、恋愛劇…\nお、お兄ちゃんと\n練習…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102101, 758, 1, 0, 1, 0, "こ、この本、とっても\nおもしろいんですよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102101, 759, 1, 0, 2, 0, "い、一緒に遊んで\nくれるんですか？\nや、やったぁ～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102101, 760, 1, 0, 3, 0, "ランドソルには\n慣れましたか？\nわ、私はまだ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102101, 761, 1, 0, 4, 0, "お、お兄ちゃん、\nお、お話の練習に\n付き合って欲しいです", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 102101, 762, 1, 0, 101, 0, "リボンがかわいい？\nほ、ほぇぇぇ～～\nは、恥ずかしいですぅ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102101, 763, 1, 0, 102, 0, "お兄ちゃん…♪\nえへへ、呼んでみた\nだけです…♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102101, 764, 1, 1, 1, 0, "今日は一日、\n恥ずかしがらずに\n過ごせるかなあ…？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102101, 765, 1, 2, 2, 0, "お昼ご飯、どうぞ♪\nわ、私、お料理、\nうまくなったかな？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102101, 766, 1, 3, 3, 0, "おにいちゃんが\n帰ってくるまで、\nがんばって\n起きてました", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102101, 767, 1, 0, 1, 0, "可愛い女の子に変身！\n出来ますように…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102101, 768, 1, 0, 2, 0, "ふかふかで\nあったかくて…\nお兄ちゃんみたい♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102101, 769, 1, 0, 3, 0, "この子を相手に、\n演技の練習～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102101, 770, 1, 0, 4, 0, "リボンや服に\n染み込ませておこう。\nいつでもいい匂い♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102101, 771, 1, 0, 5, 0, "えへへ。\nぽっかぽかです…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102101, 772, 1, 0, 6, 0, "はぁはぁ…\nふひぃ…\nはうぅぅぅ～～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102101, 773, 1, 0, 7, 0, "もぐもぐ…えへへ、\n美味しいものを\n食べてると幸せです", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102101, 774, 1, 0, 8, 0, "戯曲を読んで\n自分が演じるところを\n想像するの…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102102, 776, 1, 0, 1, 0, "かわいいものを\nこうして並べて…\nはぁ…落ちつくぅ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102102, 777, 1, 0, 2, 0, "ふぁ～、この部屋は\n時間がゆったりと\n流れてるみたい", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102102, 778, 1, 0, 3, 0, "この台本、感動…\n涙が止まらないよぉ\nいつか演じてみたいな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102102, 779, 1, 0, 4, 0, "お芝居には\n人間観察が大事…\nじーーーーーー…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102102, 780, 1, 0, 5, 0, "あえいうえおあお\nかけきくけ、かっ…\nむ、難しいですぅぅ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102102, 781, 1, 0, 101, 0, "お、お腹が空いた時は\n言ってくださいね\nが、頑張って\n作ります！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102102, 782, 1, 0, 102, 0, "れ、恋愛劇…\nお、お兄ちゃんと\n練習…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102102, 783, 1, 0, 1, 0, "こ、この本、とっても\nおもしろいんですよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102102, 784, 1, 0, 2, 0, "お兄ちゃんと\nいっぱい話せた日は、\n嬉しくなっちゃいます", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102102, 785, 1, 0, 3, 0, "何か私にできる事\n…そうだ！\nお掃除しますね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102102, 786, 1, 0, 4, 0, "私、ちょっとずつ、\n自分に自信が\nついてきたかもです…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102102, 787, 1, 0, 101, 0, "リボンがかわいい？\nほ、ほぇぇぇ～～\nは、恥ずかしいですぅ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102102, 788, 1, 0, 102, 0, "お兄ちゃん…♪\nえへへ、呼んでみた\nだけです…♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102102, 789, 1, 1, 1, 0, "おはようございます♪\n声出しして、お芝居の\n練習しなくちゃ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102102, 790, 1, 2, 2, 0, "お茶にしますか？\nクッキー焼いたので、\nよかったら…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102102, 791, 1, 3, 3, 0, "寝る前に\n台本を読むと、\n覚えやすいんです", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102102, 792, 1, 0, 1, 0, "可愛い女の子に変身！\n出来ますように…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102102, 793, 1, 0, 2, 0, "はぅ…\nフッカフカァ…\n癒されちゃいます…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102102, 794, 1, 0, 3, 0, "この子を相手に\n演技の練習～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102102, 795, 1, 0, 4, 0, "リボンや服に\n染み込ませておこう\nいつでもいい匂い♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102102, 796, 1, 0, 5, 0, "えへへ\nぽっかぽかです…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102102, 797, 1, 0, 6, 0, "え、演劇には\nた、体力も重要\nだから～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102102, 798, 1, 0, 7, 0, "残さず\n食べますね…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102102, 799, 1, 0, 8, 0, "戯曲を読んで\n自分が演じるところを\n想像するの…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102201, 801, 1, 0, 1, 0, "ねえ、人見知りを\n治すための練習に\n付き合ってくれない？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102201, 802, 1, 0, 2, 0, "アカリみたいに\nかわいくなるには、\nどうすればいいのかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102201, 803, 1, 0, 3, 0, "レアカードをゲット！\nねえねえ、\nすごいでしょ！？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102201, 804, 1, 0, 4, 0, "ア、アイツと同じ\n部屋にいるのよね…\nって、だから何よ！？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102201, 805, 1, 0, 5, 0, "楽しいゲームって\nやめどきが\n見つからないわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 102201, 806, 1, 0, 101, 0, "もっと女の子らしく\nなったらアイツも\n嬉しいかな…って、\n何考えてるの！？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102201, 807, 1, 0, 102, 0, "うぅ…最近、\nアイツを見るとなんか\nドキドキするのよね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102201, 808, 1, 0, 1, 0, "ゲームに負けた罰\nだからって、\nくすぐるのは…\nあはは…ダメだって！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102201, 809, 1, 0, 2, 0, "アンタと話すのは割と\n緊張しないのよね\n…なんでかな", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102201, 810, 1, 0, 3, 0, "待ちきれない…\n新作ゲームの発売日が\n待ちきれない…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102201, 811, 1, 0, 4, 0, "アンタは人見知り\nしないタチよね？\nいいなあ～…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 102201, 812, 1, 0, 101, 0, "ア、アンタってさ\n好きな人とかい…\nなな、何でもない！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102201, 813, 1, 0, 102, 0, "アンタと…そ、その\nこうして話してるのも\nわわ、悪くないわね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102201, 814, 1, 1, 1, 0, "おおおおはよう！\n…よし！　ちゃんと\nあいさつできたわ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102201, 815, 1, 2, 2, 0, "一緒にごはんとか…\nううん、やっぱいい！\nなんでもないからっ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102201, 816, 1, 3, 3, 0, "夜はこれからよ！\n朝まで\nゲームするからね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102201, 817, 1, 0, 1, 0, "アカリみたいに\n可愛くなるのは\n難しいかな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102201, 818, 1, 0, 2, 0, "寝っ転がりながら\nゲームができれば\n気分が良さそう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102201, 819, 1, 0, 3, 0, "可愛い…けど、\n私には似合わないかな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102201, 820, 1, 0, 4, 0, "はあ、リラックスして\nゲームができるわ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102201, 821, 1, 0, 5, 0, "ん…暖かくて、\n寝落ちしちゃいそう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102201, 822, 1, 0, 6, 0, "ゲーム感覚でやると、\n体動かすのも\n楽しいわね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102201, 823, 1, 0, 7, 0, "ゲームは頭使うから\nそのぶん\n栄養がいるのよ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102201, 824, 1, 0, 8, 0, "ふむふむ、なるほど\n友だちを作るには\n笑顔が大事っと…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102202, 826, 1, 0, 1, 0, "さあ、今日は\n何のゲームで\n遊ぼうかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102202, 827, 1, 0, 2, 0, "一緒にゲームができる\n人がいるっていうのも\n新鮮だな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102202, 828, 1, 0, 3, 0, "レアカードをゲット！\nすごいでしょ！？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102202, 829, 1, 0, 4, 0, "アカリってどうやって\n友達作ってるんだろ？\nコツがあるのかな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102202, 830, 1, 0, 5, 0, "楽しいゲームって\nやめどきが\n見つからないわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102202, 831, 1, 0, 101, 0, "うぅ…最近\nアイツを見るとなんか\nドキドキするのよね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102202, 832, 1, 0, 102, 0, "ね、ねえ\nもう少しその…\n一緒にゲームしない？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102202, 833, 1, 0, 1, 0, "ねえ、人見知りを\n治すための練習に\n付き合ってくれない？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102202, 834, 1, 0, 2, 0, "欲しいゲームが\n見つからなくて…\n探すの手伝って！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102202, 835, 1, 0, 3, 0, "ねえ、見て！\n新しいゲームを\n買って来たの！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102202, 836, 1, 0, 4, 0, "ゲームに負けた罰\nだからって、くすぐる\nのは…あ、あはは！\nだ、ダメだって！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102202, 837, 1, 0, 101, 0, "ア、アンタってさ\n好きな人とかい…\nなな、何でもない！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102202, 838, 1, 0, 102, 0, "あ、あのさ…\nもう少し、そこに\nいてくれてもいいわよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102202, 839, 1, 1, 1, 0, "おおおおはよう！\n…よし！　ちゃんと\nあいさつできたわ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102202, 840, 1, 2, 2, 0, "一緒にごはんとか…\nううん、やっぱいい！\nなんでもないからっ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102202, 841, 1, 3, 3, 0, "夜はこれからよ！\n朝まで\nゲームするからね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102202, 842, 1, 0, 1, 0, "もっと女の子らしく\nなったらアイツも\n嬉しいかな…って、\n何考えてるの！？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102202, 843, 1, 0, 2, 0, "ふああ…\n徹夜明けには\n辛い誘惑だな…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102202, 844, 1, 0, 3, 0, "可愛い…けど、\n私には似合わないかな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102202, 845, 1, 0, 4, 0, "こういうのに詳しいと\n女の子っぽく\n見えたりするのかな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102202, 846, 1, 0, 5, 0, "寒い日は\nここでゲームするのが\n最高のぜいたくよね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102202, 847, 1, 0, 6, 0, "最近ゲームばかりだし\nたまにはいいかな？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102202, 848, 1, 0, 7, 0, "ゲームは頭使うから\nそのぶん\n栄養がいるのよ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102202, 849, 1, 0, 8, 0, "ふむふむ、なるほど\n友だちを作るには\n笑顔が大事っと…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102301, 851, 1, 0, 1, 0, "じゃんけんぽん！\nあっち向いてほい！\nあ～ん、負けたぁ\n　『勝ったぜ！』", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102301, 852, 1, 0, 2, 0, "ぷうきちがいれば、\nなんでも出来ちゃうよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102301, 853, 1, 0, 3, 0, "発声練習しよっと\nぷうきち\nフルスイ～ング！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102301, 854, 1, 0, 4, 0, "ここで、ぷうきち\nフルスイングは、\nまずいかな～？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102301, 855, 1, 0, 5, 0, "この部屋、ぷうきちを\n置く場所はないのかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 102301, 856, 1, 0, 101, 0, "お兄ちゃんって…\nちょっとぷうきちに\n似てるかも♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102301, 857, 1, 0, 102, 0, "お兄ちゃんにも、\nぷうきちのこと\n好きになって\nもらいたいな～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102301, 858, 1, 0, 1, 0, "おやつは、\n私とぷうきちの\n二人分用意してね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102301, 859, 1, 0, 2, 0, "ぷうきちが、お腹\nペコペコだって～\n何かちょうだい！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102301, 860, 1, 0, 3, 0, "『アヤネに手出したら\n…坊ちゃんでも、\n分かってんな？』", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102301, 861, 1, 0, 4, 0, "ぷうきちとの\n連携を磨いてるの！\nもっと仲良くなるよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 102301, 862, 1, 0, 101, 0, "特別だからね？\nもっと触っていいよ\n…ぷうきちのこと", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102301, 863, 1, 0, 102, 0, "お兄ちゃんのこと、\n「ぷう兄」って\n呼ぼうかな？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102301, 864, 1, 1, 1, 0, "お兄ちゃんって\n朝早いんだね～\n私はまだ眠いよ～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102301, 865, 1, 2, 2, 0, "ふぁ～…ぷうきちと\nお昼寝してくるね…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102301, 866, 1, 3, 3, 0, "ぷうきちと\nおしゃべりしてたら\nもうこんな時間…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102301, 868, 1, 0, 2, 0, "『座り心地がいいな』\nぷうきちは座れない\nでしょ？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102301, 869, 1, 0, 3, 0, "かわいい～！\nぷうきちのお友達に\nしちゃおうかな？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102301, 870, 1, 0, 4, 0, "いいニオイ～♪\nぷうきちも\n気に入ったみたい！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102301, 871, 1, 0, 5, 0, "ぽわぽわする～\nぷうきちも\n気持ちいい？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102301, 873, 1, 0, 7, 0, "おいしい～\nぷうきちも\n食べてみる？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102301, 874, 1, 0, 8, 0, "ぷうきち！\nこの本、読んで～！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102501, 901, 1, 0, 1, 0, "どうして失敗ばっかり\nこうなったら\n腹を切るしか～！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102501, 902, 1, 0, 2, 0, "あれ？　あれ？\nお部屋の中で迷子に\nなっちゃいました～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102501, 903, 1, 0, 3, 0, "どうしたら転ばなく\nなるんでしょうか？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102501, 904, 1, 0, 4, 0, "わわわわ～～っ\nふう…\nきゃ、きゃあっ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102501, 905, 1, 0, 5, 0, "私のドジで、\nこのお部屋を\n壊しちゃったら\nどうしましょう～～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 102501, 906, 1, 0, 101, 0, "おいしい料理で\nお出迎えしなくちゃ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102501, 907, 1, 0, 102, 0, "一生懸命\nご奉仕しますよ～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102501, 908, 1, 0, 1, 0, "耳かきしましょうか？\nえ？　こわい？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102501, 909, 1, 0, 2, 0, "お疲れでしたら、\nお昼寝しませんか？\n膝枕もしますよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102501, 910, 1, 0, 3, 0, "おかえりなさいませ\nご主人さま♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102501, 911, 1, 0, 4, 0, "すみません\nすみません\nすみません～～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 102501, 912, 1, 0, 101, 0, "メイドの私が、\nこんな気持ちを持って\nいいんでしょうか…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102501, 913, 1, 0, 102, 0, "私のこと、\n専属メイドと思って\n頼ってくださいね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102501, 914, 1, 1, 1, 0, "おはようございます♪\n今日も一日がんば…\nきゃ、きゃあ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102501, 915, 1, 2, 2, 0, "お昼ごはん…\n失敗しちゃいましたぁ\nお、おやつでリベンジします！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102501, 916, 1, 3, 3, 0, "明日の目標は\nドジをしないです！\nき、昨日の目標も\nドジをしないでした", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102501, 917, 1, 0, 1, 0, "あ、メイド服が\nほつれてる\n後でお裁縫しなくちゃ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102501, 918, 1, 0, 2, 0, "休憩が終わったら\nお洗濯にお料理に\nお掃除もしないと！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102501, 919, 1, 0, 3, 0, "かわいい♪\nあとでお洗濯\nしてあげますね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102501, 920, 1, 0, 4, 0, "いい香り…\nお嬢様のお部屋にも\n置いてあげたいです", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102501, 921, 1, 0, 5, 0, "ここでお洗濯ものを\n乾かしましょうか", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102501, 922, 1, 0, 6, 0, "あわわわっ…\nこ、転びそうです～！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102501, 923, 1, 0, 7, 0, "これくらい\nおいしいものを\n作ってみたいです～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102501, 924, 1, 0, 8, 0, "物語のメイドさんって\n御主人さまを好きに\nなるんですよね…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102502, 926, 1, 0, 1, 0, "失敗したら、この\nお部屋追い出され\nちゃうかも～？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102502, 927, 1, 0, 2, 0, "あれ？　あれ？\nお部屋の中で迷子に\nなっちゃいました～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102502, 928, 1, 0, 3, 0, "どうやったら、立派な\nメイドになれるのかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102502, 929, 1, 0, 4, 0, "塩にはＳマーク\n砂糖にはＳマーク\n…あ、あれ！？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102502, 930, 1, 0, 5, 0, "ご用があれば\nなんでも言って\nくださいね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102502, 931, 1, 0, 101, 0, "迷惑をかけすぎて…\n嫌われちゃったら\nどうしましょう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102502, 932, 1, 0, 102, 0, "メイドの私が\nこんな気持ちを持って\nいいんでしょうか…　", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102502, 933, 1, 0, 1, 0, "おでかけですか？\nいってらっしゃいませ\nご主人さま♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102502, 934, 1, 0, 2, 0, "どうして失敗ばっかり\nこうなったら\n腹を切るしか～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102502, 935, 1, 0, 3, 0, "私、またなにか\nやっちゃいましたか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102502, 936, 1, 0, 4, 0, "おいしいお茶っ葉が\n手に入りました～♪\n一緒に飲みませんか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102502, 937, 1, 0, 101, 0, "転んだふりであの人に\n…って！\nなんでもないですよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102502, 938, 1, 0, 102, 0, "私、できることなら\nなんでもやりますよ\nあなたのためですから", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102502, 939, 1, 1, 1, 0, "おはようございます♪\n今日も一日がんば…\nう、うわあ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102502, 940, 1, 2, 2, 0, "お昼ごはん…\n失敗しちゃいましたぁ\nお、おやつでリベンジします！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102502, 941, 1, 3, 3, 0, "明日の目標は\nドジをしないです！\nき、昨日の目標も\nドジをしないでした", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102502, 942, 1, 0, 1, 0, "あ、メイド服が\nほつれてる\n後でお裁縫しなくちゃ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102502, 943, 1, 0, 2, 0, "いい座り心地です～\nこれならお嬢さまも\nゆったりできますね♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102502, 944, 1, 0, 3, 0, "かわいい♪\nあとでお洗濯\nしてあげますね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102502, 945, 1, 0, 4, 0, "いい香り…\nお嬢様のお部屋にも\n置いてあげたいです", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102502, 946, 1, 0, 5, 0, "暖かいですけど\n…私が火に近づいて\n大丈夫でしょうか！？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102502, 947, 1, 0, 6, 0, "運動しようとすると\nいつも怪我するん\nですよね…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102502, 948, 1, 0, 7, 0, "こんなに美味しいもの\nメイドの私には\nもったいないです～！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102502, 949, 1, 0, 8, 0, "物語のメイドさんって\n御主人さまを好きに\nなるんですよね…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102701, 951, 1, 0, 1, 0, "あの方と私だけの\n部屋にしたいですわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102701, 952, 1, 0, 2, 0, "あの方は私のもの…\n眼も耳も鼻も口も髪も\n首も胸も腰も手も脚も\nぬくもりも心も…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102701, 953, 1, 0, 3, 0, "運命とは決して\n抗えないもの…\nクスクス…クスクス", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102701, 954, 1, 0, 4, 0, "部屋からあの方の\n香りが…\nああ、至福ですわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102701, 955, 1, 0, 5, 0, "毒にも薬にもならない\n存在なんて嫌ですわ\nどうせならとびきりの\n毒でありたい…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 102701, 956, 1, 0, 101, 0, "クスクス…\nあの方とデートする\nイメトレ中ですわ\nクスクスクスクス…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102701, 957, 1, 0, 102, 0, "記憶を戻す研究より\n記憶をねつ造する研究\nのほうが良いかしら？\n伴侶という設定で…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102701, 958, 1, 0, 1, 0, "運命を信じますか？\nもちろん絶対当然\n信じますわよね？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102701, 959, 1, 0, 2, 0, "この薬は起床時就寝前\n食前食後食間に、毎日\n服用してください。\n惚れ薬ですわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102701, 960, 1, 0, 3, 0, "お願いがあるのです。\n新しい研究の実験台に\n…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102701, 961, 1, 0, 4, 0, "こっそり栽培している\n毒きのこが毒々しく\n育ちましたわ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 102701, 962, 1, 0, 101, 0, "あなた様の前では\n普通の女の子…\n少し毒薬づくりが\n得意なだけの…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102701, 963, 1, 0, 102, 0, "私の身も心も、全てが\nあなた様のもの。\nあなた様の全ても…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102701, 964, 1, 1, 1, 0, "おはようございます\n一晩中、あなた様の\n寝顔を見ていましたわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102701, 965, 1, 2, 2, 0, "お昼を用意しましたの\n何も入れていませんわ\nクスクス…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102701, 966, 1, 3, 3, 0, "あなた様が眠るまで\nお傍で見守りますわ。\n…あの、どうして\n眠らないのですか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102701, 967, 1, 0, 1, 0, "いっそ鏡の中に\n閉じ込めてしまえたら\n私だけのものに…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102701, 968, 1, 0, 2, 0, "はっ！\nリラックスしたら\n閃きましたわ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102701, 969, 1, 0, 3, 0, "あら、キメラの素体に\nぴったりですわ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102701, 970, 1, 0, 4, 0, "このお部屋は\n良い香りがしますわ。\nはっ、あなた様の匂い\nなのでは…！？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102701, 971, 1, 0, 5, 0, "あたたかい…\n加熱調合にも\n使えそうですわね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102701, 972, 1, 0, 6, 0, "この運動エネルギー\n何かにうまく\n利用できないかしら？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102701, 973, 1, 0, 7, 0, "美味しい。あの方にも\n差し上げましょう…\n薬を入れて…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102701, 974, 1, 0, 8, 0, "あの方を虜にする\n方法がどこかに\n載ってるはずですわ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102702, 976, 1, 0, 1, 0, "あの方と私だけの\n部屋にしたいですわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102702, 977, 1, 0, 2, 0, "こっそり栽培している\n毒きのこが毒々しく\n育ちましたわ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102702, 978, 1, 0, 3, 0, "新しい薬\nどうやってあの方に\n飲ませようかしら", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102702, 979, 1, 0, 4, 0, "部屋からあの方の\n香りが…\nああ、至福ですわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102702, 980, 1, 0, 5, 0, "記憶を戻す研究より\n記憶をねつ造する研究\nのほうが良いかしら？\n伴侶という設定で…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102702, 981, 1, 0, 101, 0, "クスクス…\nあの方とデートする\nイメトレ中ですわ\nクスクスクスクス…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102702, 982, 1, 0, 102, 0, "魔法も科学も錬金術も\n全て駆使して\nあの方のために…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102702, 983, 1, 0, 1, 0, "この部屋には雌犬…\n失礼、女の方がとても\n沢山いますのね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102702, 984, 1, 0, 2, 0, "あなた様には私の伴侶\nとして高い教養を…\nあ、逃げられてしまい\nましたわ…！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102702, 985, 1, 0, 3, 0, "お願いがあるのです\n新しい研究の\n実験台に…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102702, 986, 1, 0, 4, 0, "あなた様が寝ている時\n私は…クスクス\nこれ以上は言えません", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102702, 987, 1, 0, 101, 0, "想像の中のあなた様は\nすっかり私の虜なのに\n現実は…\nもどかしいですわね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102702, 988, 1, 0, 102, 0, "あなた様の前では\n普通の女の子…\n少し毒薬づくりが\n得意なだけの…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102702, 989, 1, 1, 1, 0, "おはようございます\n一晩中、あなた様の\n寝顔を見ていましたわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102702, 990, 1, 2, 2, 0, "さあ、お食事ですよ\n味は保証しますわ\n特製ソースで少しだけ\n体が痺れますけど…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102702, 991, 1, 3, 3, 0, "あなた様が眠るまで\nお傍で見守りますわ\n…あの、どうして\n眠らないのですか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102702, 992, 1, 0, 1, 0, "私ももう少し\n外見に気を配る\nべきでしょうか…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102702, 993, 1, 0, 2, 0, "はっ！\nリラックスしたら\n閃きましたわ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102702, 994, 1, 0, 3, 0, "この私を差し置いて\nあなた様に媚びるなど\n言語道断…っ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102702, 995, 1, 0, 4, 0, "このお部屋は\n良い香りがしますわ\nはっ、あなた様の匂い\nなのでは…！？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102702, 996, 1, 0, 5, 0, "素敵なぬくもり…\nまるであの方に\n抱きしめられたような", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102702, 997, 1, 0, 6, 0, "この運動エネルギー\n何かにうまく\n利用できないかしら？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102702, 998, 1, 0, 7, 0, "美味しい。あの方にも\n差し上げましょう…\n薬を入れて…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102702, 999, 1, 0, 8, 0, "あの方を虜にする\n方法がどこかに\n載ってるはずですわ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102801, 1001, 1, 0, 1, 0, "子供たちの遊びって\nあたしが子供の頃と\nあまり変わらないのね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102801, 1002, 1, 0, 2, 0, "使えるものは\n再利用しないと\n…あ、これとか\nまだ使えるわね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102801, 1003, 1, 0, 3, 0, "贅沢は敵！　とはいえ\n気持ちに余裕のない\n暮らしはダメよね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102801, 1004, 1, 0, 4, 0, "お腹空いたわね、\n残り物で\n何か作ろうかしら", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102801, 1005, 1, 0, 5, 0, "商売はノンゼロサムが\n理想だなぁ。誰も損を\nしないってことだもの", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 102801, 1006, 1, 0, 101, 0, "あんたが手伝って\nくれるから、仕事も\n前より楽しいのよね♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102801, 1007, 1, 0, 102, 0, "焼きそばパンが\n食べたいな～\nあいつと…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102801, 1008, 1, 0, 1, 0, "ねぇあんた、どこか\n安いお店知らない？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102801, 1009, 1, 0, 2, 0, "あたし、エレガントに\n振る舞えてる？\n…そう？　嬉しい♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102801, 1010, 1, 0, 3, 0, "いっぱい働いて遊んで\nよく休んでまた働く。\nそれが健全な生活よね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102801, 1011, 1, 0, 4, 0, "一緒に部屋の\n整頓でもする？\nハマると楽しいわよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 102801, 1012, 1, 0, 101, 0, "あんたがいると\n子供たちも喜ぶわ♪\nもちろんあたしもね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102801, 1013, 1, 0, 102, 0, "ね、あんた今、幸せ？\n何びっくりしてるのよ\n大事なことじゃないの", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102801, 1014, 1, 1, 1, 0, "面倒な用事は午前中に\n片づけてしまうのが、\n時間の使い方のコツよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102801, 1015, 1, 2, 2, 0, "午後のティータイム\nとかどう？\n良いカフェがあるの♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102801, 1016, 1, 3, 3, 0, "さ、エレガントな夜を\n楽しみましょ。\nモダンな音楽でも\nかけましょうか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102801, 1017, 1, 0, 1, 0, "レディたるもの\n身嗜みはしっかりとね\n見本にならないと♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102801, 1018, 1, 0, 2, 0, "これで仕事は一段落ね\nちょっと休憩…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102801, 1019, 1, 0, 3, 0, "うふふ、\nかわいいものを\n見るのは幸せね～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102801, 1020, 1, 0, 4, 0, "部屋中がいい香り…\nなんだかすごい贅沢\nしてる気分♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102801, 1021, 1, 0, 5, 0, "風邪を引かないように\n身体を温めなくちゃ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102801, 1022, 1, 0, 6, 0, "さっ！　家の中でも\n効率的に\nトレーニングよ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102801, 1023, 1, 0, 7, 0, "もぐもぐ…はっ、\n口の周り、\n汚れてないわよね？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102801, 1024, 1, 0, 8, 0, "知識から過去を学んで\n未来に活かすわよ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102802, 1026, 1, 0, 1, 0, "安売りのお店の情報\n整理しないと…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102802, 1027, 1, 0, 2, 0, "どんなに苦労してても\nそう見せないのが真の\nエレガントなレディよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102802, 1028, 1, 0, 3, 0, "安く済んであいつが\n喜ぶ料理とか…\nないかしら", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102802, 1029, 1, 0, 4, 0, "子供たちの遊びって\nあたしが子供の頃と\nあまり変わらないのね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102802, 1030, 1, 0, 5, 0, "商売はノンゼロサムが\n理想だなぁ。誰も損を\nしないってことだもの", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102802, 1031, 1, 0, 101, 0, "焼きそばパンが\n食べたいな～\nあいつと…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102802, 1032, 1, 0, 102, 0, "あいつと一緒に…\n色々なところに\nいきたいな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102802, 1033, 1, 0, 1, 0, "今度買い物に\n付き合いなさい\nセールがあるのよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102802, 1034, 1, 0, 2, 0, "あんたがいると子供\nたちも喜ぶわ♪\nもちろんあたしもね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102802, 1035, 1, 0, 3, 0, "いっぱい働いて遊んで\nよく休んでまた働く\nそれが健全な生活よね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102802, 1036, 1, 0, 4, 0, "あんたとこうして\nのんびりするのも\nいいものね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102802, 1037, 1, 0, 101, 0, "ねえ、今度遊びに\n行きたいな\nもちろん一緒に、よ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102802, 1038, 1, 0, 102, 0, "あんたが手伝って\nくれるから、仕事も\n前より楽しいのよね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102802, 1039, 1, 1, 1, 0, "面倒な用事は午前中に\n片づけてしまうのが\n時間の使い方のコツよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102802, 1040, 1, 2, 2, 0, "ランチにして午後の\n仕事に備えましょ！\nやることは一杯よ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102802, 1041, 1, 3, 3, 0, "ふわあ～\nもうこんな時間\n子供たちは\nもう寝たかしら", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102802, 1042, 1, 0, 1, 0, "忙しい時こそ\n身だしなみは隙なく\nぴしっとね！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102802, 1043, 1, 0, 2, 0, "これで仕事は一段落ね\nちょっと休憩…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102802, 1044, 1, 0, 3, 0, "ふふふ\nずっと眺めてたく\nなっちゃうわね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102802, 1045, 1, 0, 4, 0, "部屋中がいい香り…\nなんだかすごい贅沢\nしてる気分♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102802, 1046, 1, 0, 5, 0, "風邪を引かないように\n身体を温めなくちゃ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102802, 1047, 1, 0, 6, 0, "さっ！　家の中でも\n効率的に\nトレーニングよ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102802, 1048, 1, 0, 7, 0, "しっかり食べて\n栄養補給、栄養補給！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102802, 1049, 1, 0, 8, 0, "知識から過去を学んで\n未来に活かすわよ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102901, 1051, 1, 0, 1, 0, "可愛い子が\nいっぱいいるなぁ…\n誰か私とユニット\n組んでくれないかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102901, 1052, 1, 0, 2, 0, "今度のライブ会場、\nいつもより大きいから\n楽しみなんだ～！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102901, 1053, 1, 0, 3, 0, "新曲の衣装、もうすぐ\n出来あがるんだって！\n楽しみだな～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102901, 1054, 1, 0, 4, 0, "彼とデュエット\nしてみたいな～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102901, 1055, 1, 0, 5, 0, "キラキラ無病息災、\nドキドキ一日一善…\n作詞って難しい～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 102901, 1056, 1, 0, 101, 0, "ファンはもちろん\n大事だけど…\n彼のことは、もっと\n大切にしたいな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102901, 1057, 1, 0, 102, 0, "写真集見なくても、\n本物がここに\nいるのにな～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102901, 1058, 1, 0, 1, 0, "握手会の練習、\n付き合ってくれない？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102901, 1059, 1, 0, 2, 0, "写真集のポーズ\n練習中～！\nど、どうかな？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102901, 1060, 1, 0, 3, 0, "何て書いてあるか\n読めないって？\nもー！\nこれは私のサイン！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102901, 1061, 1, 0, 4, 0, "ね、次のライブ\n観に来てくれる？\nいい席とっとくから！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 102901, 1062, 1, 0, 101, 0, "ねぇ、台本読むの\nつきあってくれる？\n恋人役に\nなってほしいんだ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102901, 1063, 1, 0, 102, 0, "新曲、聞いてくれた？\nキミとのことを\nテーマにしたんだよ。\nどこかは秘密だけど♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102901, 1064, 1, 1, 1, 0, "朝の発声練習に\n行くけど、キミも\n一緒に来ない？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102901, 1065, 1, 2, 2, 0, "この時間は\nお腹すくけど…\n水着撮影があるから\nお昼抜いてるんだ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102901, 1066, 1, 3, 3, 0, "夜の帳が二人を包み\n…こういう方向性の\n歌詞も悪くないなぁ。", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102901, 1067, 1, 0, 1, 0, "身だしなみオッケー！\nアイドルはいつでも\n完璧でいなくちゃ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102901, 1068, 1, 0, 2, 0, "ふぅ…\nこんな姿、ファンには\n見せられないな～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102901, 1069, 1, 0, 3, 0, "かわいい～♪\nこれは強力な\nライバル登場かな！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102901, 1070, 1, 0, 4, 0, "ステキな香り～♪\n歌詞のイメージが\n浮かんできそう！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102901, 1071, 1, 0, 5, 0, "体も暖まったし！\nダンスレッスンに\n行こうかな♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102901, 1072, 1, 0, 6, 0, "ベストタイム更新！\nライブに向けて\n体力つけないと…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102901, 1073, 1, 0, 7, 0, "太っちゃったら\nどうしよう…？\nでも手が止まらない～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102901, 1074, 1, 0, 8, 0, "ステキなお話…\n新曲の作詞の\n参考になりそう♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102902, 1076, 1, 0, 1, 0, "このお部屋すてき！\nファンに追われた時の\n隠れ家にピッタリね！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102902, 1077, 1, 0, 2, 0, "そろそろイメチェン\nしてみようかな…\nそれともこの路線で\nもう少し…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102902, 1078, 1, 0, 3, 0, "みんながくれた\nファンレター…\n私の大事な宝物…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102902, 1079, 1, 0, 4, 0, "彼とデュエット\nしてみたいな～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102902, 1080, 1, 0, 5, 0, "疾風怒濤、威風堂々\n獅子奮迅、唯我独尊…\nラブソングの作詞って\n難しいな～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102902, 1081, 1, 0, 101, 0, "ファンはもちろん\n大事だけど…\n彼のことは、もっと\n大切にしたいな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102902, 1082, 1, 0, 102, 0, "トップアイドルの\nさらに先…\n彼とならその景色も\n見られるかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102902, 1083, 1, 0, 1, 0, "新曲の衣装、もうすぐ\n出来あがるんだって！\n楽しみだな～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102902, 1084, 1, 0, 2, 0, "アイドルノゾミだけ\nじゃなくて、私の事も\nちゃんと見てね？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102902, 1085, 1, 0, 3, 0, "キミに響かないなら\nファンにも響くわけ\nないよ！　練習練習！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102902, 1086, 1, 0, 4, 0, "衣装のスカートは\n鉄壁なんだよ！\n残念でした～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102902, 1087, 1, 0, 101, 0, "新曲、聞いてくれた？\nキミとのことを\nテーマにしたんだよ\nどこかは秘密だけど♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102902, 1088, 1, 0, 102, 0, "ねぇ、私がトップ\nアイドルになっても\nちゃんと隣にいてね\n遠くに行かないでね？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102902, 1089, 1, 1, 1, 0, "朝の発声練習に\n行くけど、キミも\n一緒に来ない？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102902, 1090, 1, 2, 2, 0, "お昼ごはん行かない？\n誰にも知られてない\nお店があるんだけど！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102902, 1091, 1, 3, 3, 0, "夜の帳が二人を包み\n…こういう方向性の\n歌詞も悪くないなぁ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102902, 1092, 1, 0, 1, 0, "身だしなみオッケー！\nアイドルはいつでも\n完璧でいなくちゃ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102902, 1093, 1, 0, 2, 0, "ふぅ…\nこんな姿、ファンには\n見せられないな～", 3);
