INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102902, 1095, 1, 0, 4, 0, "みんなのイメージする\nアイドルの香りは\nこんな感じかな～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102902, 1096, 1, 0, 5, 0, "体も暖まったし！\nダンスレッスンに\n行こうかな♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102902, 1097, 1, 0, 6, 0, "ベストタイム更新！\nライブに向けて\n体力つけないと…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102902, 1098, 1, 0, 7, 0, "太っちゃったら\nどうしよう…？\nでも手が止まらない～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102902, 1099, 1, 0, 8, 0, "この恋愛小説ステキ！\n私もアイドルじゃ\nなかったら…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103001, 1101, 1, 0, 1, 0, "手裏剣の数が\n合わないデス！\nどこかに落とした\nデスか…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103001, 1102, 1, 0, 2, 0, "クセモノ、隠れても\nむだデス！\n…気のせいデシタ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103001, 1103, 1, 0, 3, 0, "金は天下の\nマワリモノ！\nところで、\nどういう意味デス？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103001, 1104, 1, 0, 4, 0, "むむ、アクダイカンの\n気配がするデース", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103001, 1105, 1, 0, 5, 0, "クニトリ合戦、\n楽しみデース！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 103001, 1106, 1, 0, 101, 0, "ショーグンと\n出会えて、\nワタシは果報者デス！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 103001, 1107, 1, 0, 102, 0, "最近、ショーグンの\nそばにいると\nドキドキするデス…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103001, 1108, 1, 0, 1, 0, "なんでも\n命令するデス！\nショーグンの命は\n絶対デス！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103001, 1109, 1, 0, 2, 0, "あっ、ショーグン、\n出陣はいつで\nゴザルデス！？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103001, 1110, 1, 0, 3, 0, "さ、さすが\nショーグンデス…\nいつの間に背後を！？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103001, 1111, 1, 0, 4, 0, "出陣の合図に\n備えるデス！\nほら貝は\nどこデスか！？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 103001, 1112, 1, 0, 101, 0, "見つめられると\nドキドキするデス…\n新しい忍法デスか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 103001, 1113, 1, 0, 102, 0, "ショーグン！\nえへへ…これからも\nおそばに置いて\nクダサイね？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103001, 1114, 1, 1, 1, 0, "おはようデス！\n今日も一日\n忍法日和デス！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103001, 1115, 1, 2, 2, 0, "そろそろお腹\n空いてきたデス。\n昼ごはんにするデス！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103001, 1116, 1, 3, 3, 0, "明日もきっと\nいい日デスよ、\nショーグン！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103001, 1117, 1, 0, 1, 0, "ちょんまげに\nしてみるのも\n面白いかもデス！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103001, 1118, 1, 0, 2, 0, "心地よくて\n眠くなってきマス…\nもしやテキコクの\n罠デスか！？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103001, 1119, 1, 0, 3, 0, "これを変わり身の\n術に…かわいそう\nだから止めるデス", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103001, 1120, 1, 0, 4, 0, "ワビサビというもの\nデスね！\n素晴らしいデス！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103001, 1121, 1, 0, 5, 0, "身体を暖めて\nイクサに向かうデス！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103001, 1122, 1, 0, 6, 0, "アクロバットな忍法を\n使うには、体を\n鍛えないと危険デス！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103001, 1123, 1, 0, 7, 0, "うーん、\n美味しいデス…\n幸せデス～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103001, 1124, 1, 0, 8, 0, "これは…\nＢＵＳＨＩＤＯ…\nデスか？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103002, 1126, 1, 0, 1, 0, "ふんふんふ～ん\n武器の手入れは\n念入りにしマスよ～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103002, 1127, 1, 0, 2, 0, "クセモノ、隠れても\nむだデス！\n…気のせいデシタ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103002, 1128, 1, 0, 3, 0, "金は天下の\nマワリモノ！\nところで\nどういう意味デス？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103002, 1129, 1, 0, 4, 0, "むむ、アクダイカンの\n気配がするデース", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103002, 1130, 1, 0, 5, 0, "クニトリ合戦\n楽しみデース！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 103002, 1131, 1, 0, 101, 0, "最近、ショーグンの\nそばにいると\nドキドキするデス…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 103002, 1132, 1, 0, 102, 0, "ショーグンの身は\n何があってもワタシが\n守るデス！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103002, 1133, 1, 0, 1, 0, "あっ、ショーグン\n出陣はいつで\nゴザルデス！？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103002, 1134, 1, 0, 2, 0, "イクサの前に\nホネ休めも大事デスよ\nゆっくりするデース！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103002, 1135, 1, 0, 3, 0, "さ、さすがショーグン\nデス…いつの間に\n背後を！？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103002, 1136, 1, 0, 4, 0, "一緒に忍法考える\nデス！\nショーグン用の\n忍法も必要デスね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 103002, 1137, 1, 0, 101, 0, "見つめられると\nドキドキするデス…\n新しい忍法デスか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 103002, 1138, 1, 0, 102, 0, "ショーグン！\nえへへ…これからも\nおそばに置いて\nクダサイね？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103002, 1139, 1, 1, 1, 0, "いい朝デス！\nトーゴク風の朝ゴハン\n作るデス！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103002, 1140, 1, 2, 2, 0, "もうお昼デス\nお昼寝するデスか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103002, 1141, 1, 3, 3, 0, "そろそろおネム\nデース…\n一緒に寝るデス？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103002, 1142, 1, 0, 1, 0, "服の乱れは心の乱れ\nデス！　きっちり\nしないといけないデス", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103002, 1143, 1, 0, 2, 0, "心地よくて眠く\nなってきマス…\nもしやテキコクの\n罠デスか！？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103002, 1144, 1, 0, 3, 0, "これを変わり身の\n術に…かわいそう\nだから止めるデス", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103002, 1145, 1, 0, 4, 0, "ワビサビというもの\nデスね！\n素晴らしいデス！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103002, 1146, 1, 0, 5, 0, "身体を暖めてイクサに\n向かうデス！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103002, 1147, 1, 0, 6, 0, "ショーグンのお役に\n立つため、修行\nあるのみデス！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103002, 1148, 1, 0, 7, 0, "美味しいデス…\n酢昆布を和えて\nみたいデス", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103002, 1149, 1, 0, 8, 0, "物事を知って\n来たるべきイクサに\n活かすデス", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103101, 1151, 1, 0, 1, 0, "お父さん、そろそろ\n子離れを…しない？\nそうですか…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103101, 1152, 1, 0, 2, 0, "父は、いつも男性に\nきびしいですね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103101, 1153, 1, 0, 3, 0, "占ってほしい人は、\nいつでも言って\nください", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103101, 1154, 1, 0, 4, 0, "あなたの手相は…\nて、手を握るのって\n緊張しますね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103101, 1155, 1, 0, 5, 0, "この部屋、風水的にも\n運気が上がりそうです", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 103101, 1156, 1, 0, 101, 0, "あの人とのこと占って\nみようかな…\n好き…嫌い…\n好き♪…き、嫌い", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 103101, 1157, 1, 0, 102, 0, "私との相性ですか？\nそれなら占う必要は\nありませんよ…\nふふっ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103101, 1158, 1, 0, 1, 0, "女難の相が\n出ていますね…\n気をつけてください", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103101, 1159, 1, 0, 2, 0, "今日のあなたの運勢は\n大きょ…\n大吉です", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103101, 1160, 1, 0, 3, 0, "父と散歩中です。\n毎日決まったコースを\n散歩させないと、\nストレスが溜まるので", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103101, 1161, 1, 0, 4, 0, "小僧！\nシノブに触るとは\nいい度胸じゃねぇか！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 103101, 1162, 1, 0, 101, 0, "あなたに触られると、\n動悸が早くなります\nもしかして心霊現象？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 103101, 1163, 1, 0, 102, 0, "今日もたくさんの幽霊\nに慕われていますね。\n素敵です", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103101, 1164, 1, 1, 1, 0, "おはようございます\n今日の運勢を\n占いましょうか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103101, 1165, 1, 2, 2, 0, "今日のラッキー\nアイテムはドクロです\n良ければ父を\nお貸ししましょうか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103101, 1166, 1, 3, 3, 0, "今日のあなたの運気は\n最高潮でした。\nもうすぐ今日は\n終わりますが", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103101, 1167, 1, 0, 1, 0, "お父さん。\nいつも、身だしなみに\n気を使ってるけど、\nあまり変わってないよ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103101, 1168, 1, 0, 2, 0, "一休みしましょう。\n憑かれて疲れました", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103101, 1169, 1, 0, 3, 0, "かわいい…\n付喪神が宿らないかな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103101, 1170, 1, 0, 4, 0, "お父さん、\nリラックスし過ぎると\n成仏しちゃうよ？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103101, 1171, 1, 0, 5, 0, "気持ちいいです…\n極楽浄土みたい…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103101, 1172, 1, 0, 6, 0, "はぁはぁ…\nお、お父さん、\n代わりに運動して…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103101, 1173, 1, 0, 7, 0, "お父さんも\n食べたいの？\n死んでるのに？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103101, 1174, 1, 0, 8, 0, "この本…\nもしかして予言書？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103102, 1176, 1, 0, 1, 0, "ここにいる幽霊は\nみんな幸せそうです", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103102, 1177, 1, 0, 2, 0, "お父さんが\n女の子ばかりに\n声をかけるので\n困っています", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103102, 1178, 1, 0, 3, 0, "成仏はうれしいこと\nです…でも友達が\n減るのは寂しいですね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103102, 1179, 1, 0, 4, 0, "幽霊に好かれる人は\n素敵な方です\nもちろん、あなたも", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103102, 1180, 1, 0, 5, 0, "お父さん、そろそろ\n子離れを…しない？\nそうですか…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 103102, 1181, 1, 0, 101, 0, "生きている人に\n好かれるには、\nどうすればいい\nのでしょう？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 103102, 1182, 1, 0, 102, 0, "お父さん、どうしたら\nあの人のことを\n認めてくれますか？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103102, 1183, 1, 0, 1, 0, "この部屋、風水的にも\n運気が上がりそうです", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103102, 1184, 1, 0, 2, 0, "シノブには指一本\n触れさせん！\nガブッ！！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103102, 1185, 1, 0, 3, 0, "あなたの手相は…\nて、手を握るのって\n緊張しますね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103102, 1186, 1, 0, 4, 0, "ラップ音？\nいえ、父のいびきです", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 103102, 1187, 1, 0, 101, 0, "私との相性ですか？\nそれなら占う必要は\nありませんよ…\nふふっ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 103102, 1188, 1, 0, 102, 0, "前世って信じますか？\n私とあなたは\nもしかすると…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103102, 1189, 1, 1, 1, 0, "今日のラッキー\nアイテムはドクロです\n良ければ父を\nお貸ししましょうか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103102, 1190, 1, 2, 2, 0, "明るい時間は\nあまり得意では\nないんです…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103102, 1191, 1, 3, 3, 0, "夜のお散歩に\n行きませんか？\n知り合いの幽霊に\n挨拶して回るんです", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103102, 1192, 1, 0, 1, 0, "お父さん\nいつも、身だしなみに\n気を使ってるけど\nあまり変わってないよ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103102, 1193, 1, 0, 2, 0, "うとうと…\nあれ…\n走馬灯が見える…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103102, 1194, 1, 0, 3, 0, "かわいい…\nお父さんみたい…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103102, 1195, 1, 0, 4, 0, "お父さん\nリラックスし過ぎると\n成仏しちゃうよ？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103102, 1196, 1, 0, 5, 0, "快適です…\n幽霊もどんどん\n集まってきますね…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103102, 1197, 1, 0, 6, 0, "はぁはぁ…\nお、お父さん、\n代わりに運動して…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103102, 1198, 1, 0, 7, 0, "お父さんの分も、\n私が食べるね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103102, 1199, 1, 0, 8, 0, "この本の占い\n私の占いと違う…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103201, 1201, 1, 0, 1, 0, "私だって、一人で\n買い物ぐらい\nできますわ…きっと", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103201, 1202, 1, 0, 2, 0, "そろそろ新しい\n案件を始めないと\nいけませんわね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103201, 1203, 1, 0, 3, 0, "一日も早く、\n一人前の商人に\nなりたいものです", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103201, 1204, 1, 0, 4, 0, "平和を買うのには\nどれほどのお金が\n必要なのかしら？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103201, 1205, 1, 0, 5, 0, "ここももう少し広いと\n落ち着くのですけど。\n改築しませんこと？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 103201, 1206, 1, 0, 101, 0, "あの方はお金で雇った\nわけではないのに\n私といてくださる…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 103201, 1207, 1, 0, 102, 0, "たとえ私が間違っても\nきっとあの方が\n正してくれますわ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103201, 1208, 1, 0, 1, 0, "私と、庶民向けの\nお店へ一緒に\n参りませんか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103201, 1209, 1, 0, 2, 0, "資産運用？\nお幾らほど\n増やしたいのです？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103201, 1210, 1, 0, 3, 0, "どんぶり勘定だとよく\n言われるのですが…\n例えが小さすぎます！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103201, 1211, 1, 0, 4, 0, "お金で解決するのは\n悪いことでは\nありませんわよね？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 103201, 1212, 1, 0, 101, 0, "その、よろしければ\n私とお散歩など\nいかがです？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 103201, 1213, 1, 0, 102, 0, "もしあなたさまが\n道に迷われるなら、\n私もお供いたします！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103201, 1214, 1, 1, 1, 0, "早起きは三ルピの徳と\n申しますが…\nもっとお得ですわね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103201, 1215, 1, 2, 2, 0, "昼食は町の屋台で\n立ったまま食べて\nみたいですわ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103201, 1216, 1, 3, 3, 0, "よろしければ\nお休み前のミルクを\n御一緒にいかが？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103201, 1217, 1, 0, 1, 0, "だらしない身なりでは\n取引相手に信用して\nいただけませんものね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103201, 1218, 1, 0, 2, 0, "この感触と安らぎ…\n素晴らしい\n費用対効果ですわ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103201, 1219, 1, 0, 3, 0, "何の見返りも求めず\nただひたすら愛でて\nいたいのです！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103201, 1220, 1, 0, 4, 0, "目に見えないものに\nお金をかけるのが\n真の贅沢ですわ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103201, 1221, 1, 0, 5, 0, "誰ひとり寒い思いを\nしない町にするために\n私にできる事は…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103201, 1222, 1, 0, 6, 0, "健康はお金では\n買えませんが\n運動器具は別ですわ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103201, 1223, 1, 0, 7, 0, "なかなか\n美味ですわ\nシェフはどちらに？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103201, 1224, 1, 0, 8, 0, "経済の仕組みを\nしっかりと\n学ばなくては…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103202, 1226, 1, 0, 1, 0, "そろそろ新しい\n案件を始めないと\nいけませんわね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103202, 1227, 1, 0, 2, 0, "ええと、これは\nおいくらで買ったん\nでしたっけ？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103202, 1228, 1, 0, 3, 0, "お金を見せたら\n態度が変わる人は\n信用できませんわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103202, 1229, 1, 0, 4, 0, "地図が読めずとも\n道に迷わない方って\nいらっしゃるのですね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103202, 1230, 1, 0, 5, 0, "この狭さも\n慣れれば\n快適ですわ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 103202, 1231, 1, 0, 101, 0, "あの方はお金で雇った\nわけではないのに\n私といてくださる…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 103202, 1232, 1, 0, 102, 0, "たとえ私が間違っても\nきっとあの方が\n正してくれますわ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103202, 1233, 1, 0, 1, 0, "一人前の商人に\n相応しい居振る舞いを\nしたいものですわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103202, 1234, 1, 0, 2, 0, "幼い頃\nお店屋さんごっこを\nなさいましたか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103202, 1235, 1, 0, 3, 0, "資産運用？\nお幾らほど\n増やしたいのです？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103202, 1236, 1, 0, 4, 0, "今度一緒に…\nパーティにでも\n行きませんこと？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 103202, 1237, 1, 0, 101, 0, "その、よろしければ\n私とお散歩など\nいかがです？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 103202, 1238, 1, 0, 102, 0, "あなたさまの優しさは\nお金には\n代えられませんわね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103202, 1239, 1, 1, 1, 0, "なぜだか\n眠り足りないような\n感じがしますわ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103202, 1240, 1, 2, 2, 0, "昼食は町の屋台で\n立ったまま食べて\nみたいですわ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103202, 1241, 1, 3, 3, 0, "よろしければ\nお休み前のミルクを\n御一緒にいかが？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103202, 1242, 1, 0, 1, 0, "ドレスは汚れていない\nようですが…やだ\n髪がほつれてますわ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103202, 1243, 1, 0, 2, 0, "こういったものに\n腰を沈めると…\nき、気が緩んで…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103202, 1244, 1, 0, 3, 0, "何の見返りも求めず\nただひたすら愛でて\nいたいのです！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103202, 1245, 1, 0, 4, 0, "香りは質のいいものと\nそうでないものが\nはっきり分かりますわ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103202, 1246, 1, 0, 5, 0, "誰ひとり寒い思いを\nしない町にするために\n私にできる事は…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103202, 1247, 1, 0, 6, 0, "目標は遠くに\n置いた方が励みに\nなりますわ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103202, 1248, 1, 0, 7, 0, "なかなか\n美味ですわ\nシェフはどちらに？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103202, 1249, 1, 0, 8, 0, "商人って、物語では\n悪役にされやすいの\nですね…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103301, 1251, 1, 0, 1, 0, "家の中のものを使って\n物ボケ、考えるべ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103301, 1252, 1, 0, 2, 0, "毎日の日課…それは\n炊事、洗濯、大喜利\nだべ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103301, 1253, 1, 0, 3, 0, "うちに塀ができたら、\nへー、って言いたい！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103301, 1254, 1, 0, 4, 0, "オラが作る料理は\nこの後スタッフで\n美味しくいただくべよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103301, 1255, 1, 0, 5, 0, "エリザベスも\n連れてきてぇ～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 103301, 1256, 1, 0, 101, 0, "あんちゃんも、\nエリザベスと同じ位\nめんこいべ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 103301, 1257, 1, 0, 102, 0, "オラとあんちゃん、\n息がぴったりに\nなってきたべ…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103301, 1258, 1, 0, 1, 0, "おぉ！　そんな急に\nツッコまれたら、\nびっくらこくべやー！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103301, 1259, 1, 0, 2, 0, "ネタはオラが書くけど\nあんちゃんが\n書きたかったら\n書いてもいいんだぞ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103301, 1260, 1, 0, 3, 0, "あんちゃん、オラには\n容赦なんかせずに\nツッコんでくれて\nいいから！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103301, 1261, 1, 0, 4, 0, "押すなよ、\n絶対押すなよ\n…なんて\n言ってないべさ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 103301, 1262, 1, 0, 101, 0, "うう…あんちゃんに\nツッコまれたくて\nうずうずしてきたべ～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 103301, 1263, 1, 0, 102, 0, "将来的には夫婦漫才\nってのも…あ、ひ、\nひとりごとだべさ～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103301, 1264, 1, 1, 1, 0, "おはよう！　一緒に\n牛の乳しぼりいくべ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103301, 1265, 1, 2, 2, 0, "お昼だ～腹が減っては\n笑いはできぬ～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103301, 1266, 1, 3, 3, 0, "今日もよく働いたべ！\nさ、今からは\nお笑いの練習だ～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103301, 1267, 1, 0, 1, 0, "舞台に上がる前に\n身なりは整えねぇと\nだめだべ～！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103301, 1268, 1, 0, 2, 0, "ナイスなイスに\n座りたい、\nなんちゃって！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103301, 1269, 1, 0, 3, 0, "めんこいのは、\nスベっても許される\nからずるいべな～？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103301, 1270, 1, 0, 4, 0, "必要以上に\n癒されちまうべや～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103301, 1271, 1, 0, 5, 0, "ぽかぽかだ…\nウケた会場みてぇだ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103301, 1272, 1, 0, 6, 0, "お笑い芸人は、\n体が資本だべさ～！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103301, 1273, 1, 0, 7, 0, "もぐもぐ…\nボケることも\n忘れちまう美味さだ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103301, 1274, 1, 0, 8, 0, "ネタ作りのヒントが\n載ってねぇか探すべ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103302, 1276, 1, 0, 1, 0, "さぁ今日も張り切って\n漫才の練習するべさ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103302, 1277, 1, 0, 2, 0, "この部屋でも、商品\n売っていいんだべか？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103302, 1278, 1, 0, 3, 0, "眼鏡を頭の上に乗せて\n「メガネメガネ～」\nって言う練習した方が\nいいべか？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103302, 1279, 1, 0, 4, 0, "漫才を持ち時間に\n収めるには、何度も\n練習するしかねぇべ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103302, 1280, 1, 0, 5, 0, "エリザベスも\n連れてきてぇ～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 103302, 1281, 1, 0, 101, 0, "うう…あんちゃんに\nツッコまれたくて\nうずうずしてきたべ～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 103302, 1282, 1, 0, 102, 0, "あんちゃんに\n採れたての食材を\n食わしてやりてぇ～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103302, 1283, 1, 0, 1, 0, "押すなよ\n絶対押すなよ\n…なんて\n言ってないべさ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103302, 1284, 1, 0, 2, 0, "あんちゃん、百連\nツッコミ特訓だ～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103302, 1285, 1, 0, 3, 0, "あんちゃん、オラには\n容赦なんかせずに\nツッコんでくれて\nいいから！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103302, 1286, 1, 0, 4, 0, "「なんでだべ～！」\nって言いながら\nツッコむといいべ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 103302, 1287, 1, 0, 101, 0, "あはは～あんちゃんの\nツッコミはやっぱり\n気持ちいいなぁ～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 103302, 1288, 1, 0, 102, 0, "将来的には夫婦漫才\nってのも…あ、ひ\nひとりごとだべさ～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103302, 1289, 1, 1, 1, 0, "おはよう！\n今日もいい\nネタ作り日和だ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103302, 1290, 1, 2, 2, 0, "お昼だな\nご飯を食べたら、\nネタの練習するべ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103302, 1291, 1, 3, 3, 0, "もうこんな時間だべ\nネタ作りは\n明日にするべ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103302, 1292, 1, 0, 1, 0, "うーん…衣装、\nずれてねぇべか？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103302, 1293, 1, 0, 2, 0, "ナイスなイスに\n座りたい\nなんちゃって！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103302, 1294, 1, 0, 3, 0, "はう～\nなまらめんこいべや～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103302, 1295, 1, 0, 4, 0, "必要以上に\n癒されちまうべや～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103302, 1296, 1, 0, 5, 0, "笑いをとることを\n忘れるほど\nあったけぇべ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103302, 1297, 1, 0, 6, 0, "たくさん運動して\n動きでも笑いがとれる\nように頑張るべ～！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103302, 1298, 1, 0, 7, 0, "もぐもぐ…\nボケることも\n忘れちまう美味さだ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103302, 1299, 1, 0, 8, 0, "幅広く教養を\n身につけて\nいいネタ作るべ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103401, 1301, 1, 0, 1, 0, "通りにいる\nカップル…爆発\nすればいいのに…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103401, 1302, 1, 0, 2, 0, "う～ん、仕事なんて\nしないで、\n飲んでたいな～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103401, 1303, 1, 0, 3, 0, "今日は一日、\n外に出ない！\nもう決めたわ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103401, 1304, 1, 0, 4, 0, "お腹空いた…\nけど、外に出るのは\n面倒だなぁ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103401, 1305, 1, 0, 5, 0, "うぅ…頭がガンガン\nする…昨日、\n飲み過ぎたかな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 103401, 1306, 1, 0, 101, 0, "デートとか…\n誘っても嫌がられ\nないかな…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 103401, 1307, 1, 0, 102, 0, "もしかして運命の\n相手だったり…\nするのかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103401, 1308, 1, 0, 1, 0, "やっぱり一杯目が\n一番おいしいわ～！\n…え、見てた！？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103401, 1309, 1, 0, 2, 0, "頼れるお姉さんだって\nところ、もっと見せて\nあげるからね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103401, 1310, 1, 0, 3, 0, "ねえ、一緒に買い物に\n行かない？　その…\nの、飲み物とか…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103401, 1311, 1, 0, 4, 0, "ひっく、うふふ…\nもっとお姉さんと\nお話しようよ～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 103401, 1312, 1, 0, 101, 0, "極力人付き合いは\nしない主義なんだけど\nキミは例外かも…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 103401, 1313, 1, 0, 102, 0, "いつか…\nキミと一緒に飲めたら\n楽しいだろうな", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103401, 1314, 1, 1, 1, 0, "おはよう…朝は\nちょっとつらいな…\n低血圧なのよ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103401, 1315, 1, 2, 2, 0, "おっひるー♪\nなに食べよっかな～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103401, 1316, 1, 3, 3, 0, "ふわあ…\n眠る前にちょっと\n一杯…冗談よ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103401, 1317, 1, 0, 1, 0, "うう…\n髪がボサボサ…\nちゃんとしなきゃ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103401, 1318, 1, 0, 2, 0, "自然体でゴロゴロ\nするのって\n幸せだなぁ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103401, 1319, 1, 0, 3, 0, "こういうの男の子に\nプレゼントして\nもらいたいな…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103401, 1320, 1, 0, 4, 0, "うわあ、\nこれ、私の好きな\n香りだー！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103401, 1321, 1, 0, 5, 0, "ぽっかぽか…\nあとは熱い一杯が\nあれば…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103401, 1322, 1, 0, 6, 0, "家でゴロゴロして\n付いちゃったお肉を\n落とさないと…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103401, 1323, 1, 0, 7, 0, "もぐもぐ…\nおーいしー！\n…太らないよね？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103401, 1324, 1, 0, 8, 0, "大人の女らしく\n色んなことを学んで\nおかなくっちゃ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103402, 1326, 1, 0, 1, 0, "仕事終わりの一杯の\nために、今日も\n頑張らないと", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103402, 1327, 1, 0, 2, 0, "今日は\nのんびり\n過ごそうかな～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103402, 1328, 1, 0, 3, 0, "あ…\nお皿、洗って\nなかった…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103402, 1329, 1, 0, 4, 0, "もう少し、大人の\n魅力が出せるように\nなりたいな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103402, 1330, 1, 0, 5, 0, "素敵な彼氏…\n欲しいなぁ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 103402, 1331, 1, 0, 101, 0, "彼と私\n釣り合うかな…\n大丈夫よね？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 103402, 1332, 1, 0, 102, 0, "今後のために\nちょっとは花嫁修業\nしてみようかな…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103402, 1333, 1, 0, 1, 0, "何事も\nお姉さんと一緒に\n楽しく頑張りましょ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103402, 1334, 1, 0, 2, 0, "ね、ねえ！\nキミは年上と年下\nどっちが好きかな？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103402, 1335, 1, 0, 3, 0, "もう…\n構ってくれないと\n泣いちゃうぞ～？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103402, 1336, 1, 0, 4, 0, "お姉さんが～\n可愛がって\nあげようか～？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 103402, 1337, 1, 0, 101, 0, "極力人付き合いは\nしない主義なんだけど\nキミは例外かも…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 103402, 1338, 1, 0, 102, 0, "お姉さんとチュー\nする？　ふふふ～\n冗談で～す！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103402, 1339, 1, 1, 1, 0, "さーて、今日も\nいっちょやるぞー！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103402, 1340, 1, 2, 2, 0, "後でのんびり\nストレッチでも\nしようっと♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103402, 1341, 1, 3, 3, 0, "一日よく働いた～！\n自分にごほうび\nあげたいなー♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103402, 1342, 1, 0, 1, 0, "仕事の前に、髪とか\n整えないと…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103402, 1343, 1, 0, 2, 0, "自然体でゴロゴロ\nするのって\n幸せだなぁ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103402, 1344, 1, 0, 3, 0, "もう大人だし…\nあんまり可愛いのは\n似合わないかな…？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103402, 1345, 1, 0, 4, 0, "うわあ\nこれ、私の好きな\n香りだー！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103402, 1346, 1, 0, 5, 0, "あったかーい…\nだ、だらけ\nちゃいそう…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103402, 1347, 1, 0, 6, 0, "地道な努力が\nリア充への\n第一歩！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103402, 1348, 1, 0, 7, 0, "おーいしっ♪\nこのために\n生きてるわねえ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103402, 1349, 1, 0, 8, 0, "大人の女らしく\n色んなことを学んで\nおかなくっちゃ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103801, 1351, 1, 0, 1, 0, "大切な人に\n本をすすめるのって\nけっこう難しいなぁ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103801, 1352, 1, 0, 2, 0, "き、きらーん☆\nお姉ちゃんに\n似てるかな…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103801, 1353, 1, 0, 3, 0, "そろそろ好きな\n作家さんの新刊が\n出るから楽しみ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103801, 1354, 1, 0, 4, 0, "ちょっと、休憩…\n本でも読もうかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103801, 1355, 1, 0, 5, 0, "もっとたくさんの本に\n囲まれて生活したいな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 103801, 1356, 1, 0, 101, 0, "むかしむかしある所に\nシオリと\nあの人が…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 103801, 1357, 1, 0, 102, 0, "この本の主人公、\nあの人に\n似ているかも…\nかっこいいな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103801, 1358, 1, 0, 1, 0, "ケホケホ…\nコンコン…\nゴホッゴホッ…\nへ、平気ですよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103801, 1359, 1, 0, 2, 0, "あ…お姉ちゃんと\n約束があったんだ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103801, 1360, 1, 0, 3, 0, "しおりを探している？\nあ、本に挟む方ですね\nドキドキしました…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103801, 1361, 1, 0, 4, 0, "最近調子がいいのは\n牧場の牛乳やチーズの\nおかげでしょうか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 103801, 1362, 1, 0, 101, 0, "読みたい本も\nあなたと行きたい所も\n増えるばっかりです", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 103801, 1363, 1, 0, 102, 0, "私の病気、\n悪化してるのかも！？\nすごく胸がドキドキ\nするんです…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103801, 1364, 1, 1, 1, 0, "えっと、朝食の前には\nこの薬を飲んで…\n食後にはこの薬を…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103801, 1365, 1, 2, 2, 0, "ふぅ…午前中\n動いたので体力が…\nすこし休みますね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103801, 1366, 1, 3, 3, 0, "早く寝ないと\n体に良くないですよね\nでも気になっている\n本があって…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103801, 1367, 1, 0, 1, 0, "寝てばかりなので、\n髪が乱れてないかな\n…？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103801, 1368, 1, 0, 2, 0, "少し休もう\n…やっぱり\nたくさん休もう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103801, 1369, 1, 0, 3, 0, "かわいいものが\n枕元にあると\n安心できるんですよ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103801, 1370, 1, 0, 4, 0, "いい香り…\n病は気からと言うので\n雰囲気も大事ですね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103801, 1371, 1, 0, 5, 0, "こうして温まって\nいると、病気も\n治る気がします", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103801, 1372, 1, 0, 6, 0, "運動…イメージ\nトレーニングだけじゃ\nだめでしょうか…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103801, 1373, 1, 0, 7, 0, "少食ですけど\nこれならたくさん\n食べられそうです", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103801, 1374, 1, 0, 8, 0, "続きが気になって\n読むのを止める\nタイミングが…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103802, 1376, 1, 0, 1, 0, "しっかり休まないと\n早く回復するように", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103802, 1377, 1, 0, 2, 0, "き、きらーん☆\nお姉ちゃんに\n似てるかな…？　", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103802, 1378, 1, 0, 3, 0, "病室よりも…\nずっとこの部屋に\nいたいですね…　", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103802, 1379, 1, 0, 4, 0, "そろそろ好きな\n作家さんの新刊が\n出るから楽しみ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103802, 1380, 1, 0, 5, 0, "大切な人に\n本をすすめるのって\nけっこう難しいなぁ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 103802, 1381, 1, 0, 101, 0, "むかしむかしある所に\nシオリと\nあの人が…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 103802, 1382, 1, 0, 102, 0, "あの人の\nためにも早く\n病気を治さないと…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103802, 1383, 1, 0, 1, 0, "ケホケホ…\nコンコン…\nゴホッゴホッ…\nへ、平気ですよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103802, 1384, 1, 0, 2, 0, "新記録です！\n一週間連続、\n平熱を保ちました！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103802, 1385, 1, 0, 3, 0, "あ…お薬の時間…\n苦いけど我慢しなきゃ　", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103802, 1386, 1, 0, 4, 0, "最近調子がいいのは\n牧場の牛乳やチーズの\nおかげでしょうか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 103802, 1387, 1, 0, 101, 0, "しおりを探している？\nあ、本に挟む方ですね\nドキドキしました…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 103802, 1388, 1, 0, 102, 0, "私の病気\n悪化してるのかも！？\nすごく胸がドキドキ\nするんです…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103802, 1389, 1, 1, 1, 0, "えっと、朝食の前には\nこの薬を飲んで…\n食後にはこの薬を…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103802, 1390, 1, 2, 2, 0, "…お昼の薬？\nあ、飲み忘れてました\nありがとうございます", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103802, 1391, 1, 3, 3, 0, "この薬を飲むと\n眠くなっちゃうんです\nでもまだ、あなたと\nお話したいし～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103802, 1392, 1, 0, 1, 0, "寝てばかりなので、\n髪が乱れてないかな\n…？　", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103802, 1393, 1, 0, 2, 0, "少し休もう\n…やっぱり\nたくさん休もう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103802, 1394, 1, 0, 3, 0, "この子がいれば\n病室に一人でも\n寂しくないですね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103802, 1395, 1, 0, 4, 0, "いろんな効能が？\n健康になる香りも\nあるのでしょうか？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103802, 1396, 1, 0, 5, 0, "こうして温まって\nいると、病気も\n治る気がします", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103802, 1397, 1, 0, 6, 0, "私も、少しは\n体力をつけないと\nいけませんよね　", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103802, 1398, 1, 0, 7, 0, "いっぱい食べて\n健康な体にならないと", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103802, 1399, 1, 0, 8, 0, "続きが気になって\n読むのを止める\nタイミングが…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104001, 1401, 1, 0, 1, 0, "友達ができたら、\n山の上でお弁当…\n夢が広がります…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104001, 1402, 1, 0, 2, 0, "あ、忘れずに\nお花に水を\nあげなきゃ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104001, 1403, 1, 0, 3, 0, "お菓子を作って、\nみなさんに振る舞って\n友達作戦！　とか…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104001, 1404, 1, 0, 4, 0, "そろそろ新しい\n草人形を作って\n友達作りの特訓を…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104001, 1405, 1, 0, 5, 0, "植物のお世話って、\nぼっちにとっては、\n救い…なんです…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 104001, 1406, 1, 0, 101, 0, "ＢＢ団の活動日…\nまだかな…\nうふふふっ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104001, 1407, 1, 0, 102, 0, "もっともっとあの人の\nそばで、一緒に友達を\n作っていきたいな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104001, 1408, 1, 0, 1, 0, "あ、こんにちは！\n今日もＢＢ団で\nお世話になります！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104001, 1409, 1, 0, 2, 0, "あの…私の友達作りの\n練習方法、間違って\nませんよね…？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104001, 1410, 1, 0, 3, 0, "そうだ、今度、\n森に一緒に木の苗を\n植えに行きませんか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104001, 1411, 1, 0, 4, 0, "人とお話するのって、\n私、大好きです！\n友達ふやすぞ～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 104001, 1412, 1, 0, 101, 0, "あなたと話してると、\nまるで私がぼっちじゃ\nないみたいです！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104001, 1413, 1, 0, 102, 0, "友達もほしいけど…\nあなたに会えたことが\n私の一番の幸せです", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104001, 1414, 1, 1, 1, 0, "おはようございます！\n今日もＢＢ団、\nがんばるぞー！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104001, 1415, 1, 2, 2, 0, "なんだか、お腹が\n空いてきました。\nお昼ごはんー♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104001, 1416, 1, 3, 3, 0, "もう眠いかもです…\n明日は友達、\nできるといいなあ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104001, 1417, 1, 0, 1, 0, "私がもっと\n可愛ければ、\n友達できるかなあ？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104001, 1418, 1, 0, 2, 0, "ふわあ…のんびり\nしすぎないように\n気を付けなきゃです", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104001, 1419, 1, 0, 3, 0, "かわいいなあ。\nこの子とも\n友達になりたい…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104001, 1420, 1, 0, 4, 0, "いい香りです…\n精神統一して\n友達のイメトレ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104001, 1421, 1, 0, 5, 0, "灯りを見てると、\n心がなごみます～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104001, 1422, 1, 0, 6, 0, "もぐもぐ…\nおいしいです…！\nみなさんもぜひ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104001, 1423, 1, 0, 7, 0, "おいしいです…\n私に友達がいたら、\n分けてあげたいな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104001, 1424, 1, 0, 8, 0, "友達作りの\n参考になりそうなこと\n書いてないかな～？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104002, 1426, 1, 0, 1, 0, "友達作りは、焦らず\n慌てず、じっくりと…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104002, 1427, 1, 0, 2, 0, "ひとつ屋根の下\nって感じ…\nいいですねえ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104002, 1428, 1, 0, 3, 0, "煮汁、煮汁が\n噴きこぼれて\nいませんか～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104002, 1429, 1, 0, 4, 0, "今度、みなさんと\nピクニックとか…\nいってみたいな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104002, 1430, 1, 0, 5, 0, "ぼっちでもいい\nたくましく育てば\n…なんて", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104002, 1431, 1, 0, 101, 0, "最近、毎日あの人の\nこと考えちゃいます\nどうしてだろう…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104002, 1432, 1, 0, 102, 0, "もっともっとあの人の\nそばで、一緒に友達を\n作っていきたいな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104002, 1433, 1, 0, 1, 0, "あ！　ＢＢ団の活動\nですか？　嬉しくて\n飛び跳ねちゃいます！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104002, 1434, 1, 0, 2, 0, "あなたとのお話で友達\n作りの練習になるなら\n一石二鳥です！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104002, 1435, 1, 0, 3, 0, "私少しは人と話しても\nテンパらなくなって\nきましたかね…？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104002, 1436, 1, 0, 4, 0, "あの、友達作りの\n新しい作戦\n考えてみたんですっ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104002, 1437, 1, 0, 101, 0, "あなたとお話する時の\nこの気持ち、私の宝物\nなんです…えへへ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104002, 1438, 1, 0, 102, 0, "友達もほしいけど…\nあなたに会えたことが\n私の一番の幸せです", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104002, 1439, 1, 1, 1, 0, "朝ですね！\nこんないい朝は\n友達作りのチャンス！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104002, 1440, 1, 2, 2, 0, "あの…そろそろお腹\nすきませんか？\nお昼ごはんは…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104002, 1441, 1, 3, 3, 0, "もう眠いかもです…\n明日は友達\nできるといいなあ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104002, 1442, 1, 0, 1, 0, "私…見た目\nそこまで変じゃ\nないですよね…？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104002, 1443, 1, 0, 2, 0, "ほわあ～…\nふかふかって\nいいものですね～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104002, 1444, 1, 0, 3, 0, "この子みたいな\nあいくるしさを\n目指したいです！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104002, 1445, 1, 0, 4, 0, "灯りを見てると\n心がなごみます～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104002, 1446, 1, 0, 5, 0, "ぬくぬくで、なんだか\n眠くなっちゃいますね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104002, 1447, 1, 0, 6, 0, "身体を鍛えて\nみなさんを\n守ります！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104002, 1448, 1, 0, 7, 0, "もぐもぐ…\nおいしいです…！\nみなさんもぜひ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104002, 1449, 1, 0, 8, 0, "友達作りの\n参考になりそうなこと\n書いてないかな～？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104201, 1451, 1, 0, 1, 0, "唱喚の歌は、みだりに\n歌っていいものでは\nないんです", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104201, 1452, 1, 0, 2, 0, "ここでアイドルの\n練習は、さすがに\n迷惑ですよね？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104201, 1453, 1, 0, 3, 0, "練習してもあんまり\n疲れなくなった…\nアイドルのおかげかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104201, 1454, 1, 0, 4, 0, "人々の平和を守る旅に\n同行してくれる人を\n探していて…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104201, 1455, 1, 0, 5, 0, "あとで曲を復習して\nおかないと…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 104201, 1456, 1, 0, 101, 0, "踊りを見られるのは\nまだ恥ずかしいけど\nあの人になら…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104201, 1457, 1, 0, 102, 0, "浜辺で曲を書こうと\n思うんですけど…\n一緒に行きませんか？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104201, 1458, 1, 0, 1, 0, "いつも応援ありがとう\nございます♪　これは\nあ、握手の練習です！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104201, 1459, 1, 0, 2, 0, "え、もう練習の時間？\nごめんなさい\nすぐ準備します！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104201, 1460, 1, 0, 3, 0, "守るべき人たちに\n笑顔でいてもらうのも\n大切なんですね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104201, 1461, 1, 0, 4, 0, "歌える曲を増やそうと\n思って。どんな曲が\n聞きたいですか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 104201, 1462, 1, 0, 101, 0, "～～♪　あれ？\n今私、歌ってました？\n気づきませんでした", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104201, 1463, 1, 0, 102, 0, "…歌って欲しい？\n喜んで♪\nどの歌にしましょう？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104201, 1464, 1, 1, 1, 0, "おはようございます♪\n少し歌の練習を\nしてきますね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104201, 1465, 1, 2, 2, 0, "もうお昼なんですか？\n練習に夢中で\n気づきませんでした", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104201, 1466, 1, 3, 3, 0, "明日の予定は…\nアイドルはやることが\n多いみたいで…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104201, 1467, 1, 0, 1, 0, "見た目については\n前よりも気をつけてと\nノゾミに言われてます", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104201, 1468, 1, 0, 2, 0, "歌の調子をよくする\nためには休息も\n必要ですよね…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104201, 1469, 1, 0, 3, 0, "この子たちを\n相手に練習して\nみようかな…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104201, 1470, 1, 0, 4, 0, "…疲れてるのかな\n落ち着けているのが\n自分でもわかります", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104201, 1471, 1, 0, 5, 0, "ここにいると\n穏やかな旋律が\n浮かんできます…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104201, 1472, 1, 0, 6, 0, "歌うためには\n体力づくりは\n欠かせません", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104201, 1473, 1, 0, 7, 0, "歌った後は\nお腹が空いて\nしまうんです", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104201, 1474, 1, 0, 8, 0, "音楽について\n理論も勉強\nしないと…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104202, 1476, 1, 0, 1, 0, "歌うことの楽しさ\n分かってきました\nもっと歌いたいな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104202, 1477, 1, 0, 2, 0, "練習してもあんまり\n疲れなくなった…\nアイドルのおかげかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104202, 1478, 1, 0, 3, 0, "精霊も強くなったし\nもっとたくさんの人を\n守りたいです", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104202, 1479, 1, 0, 4, 0, "最近、よく頭の中に\n歌が浮かんできます\n不思議な感覚…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104202, 1480, 1, 0, 5, 0, "楽譜を買いました\n思いついた歌を\n書き留めておくために", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104202, 1481, 1, 0, 101, 0, "浜辺で曲を書こうと\n思うんですけど…\n一緒に行きませんか？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104202, 1482, 1, 0, 102, 0, "私の中にある\n私だけの歌…\n作ってみようかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104202, 1483, 1, 0, 1, 0, "守るべき人たちに\n笑顔でいてもらうのも\n大切なんですね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104202, 1484, 1, 0, 2, 0, "歌える曲を増やそうと\n思って。どんな曲が\n聞きたいですか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104202, 1485, 1, 0, 3, 0, "魔物退治ですか！？\n…違います？\n良かった…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104202, 1486, 1, 0, 4, 0, "いつも応援ありがとう\nございます♪　これは\nあ、握手の練習です！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104202, 1487, 1, 0, 101, 0, "…歌って欲しい？\n喜んで♪\nどの歌にしましょう？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104202, 1488, 1, 0, 102, 0, "～～♪　あれ？\n今私、歌ってました？\n気づきませんでした", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104202, 1489, 1, 1, 1, 0, "呼吸を深く、大きく…\nこれでちゃんと\n目が覚めますよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104202, 1490, 1, 2, 2, 0, "少し休んだら\n午後の練習に\n行ってきますね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104202, 1491, 1, 3, 3, 0, "曲を作っていたら\nもうこんな時間…\nおやすみなさい", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104202, 1492, 1, 0, 1, 0, "見た目については\n前よりも気をつけてと\nノゾミに言われてます", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104202, 1493, 1, 0, 2, 0, "あ～…♪\nよく体がほぐれます…\nいい歌が歌えそうです", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104202, 1494, 1, 0, 3, 0, "かわいいと思う\n気持ちを、歌にしても\nいいかも知れませんね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104202, 1495, 1, 0, 4, 0, "…疲れてるのかな\n落ち着けているのが\n自分でもわかります", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104202, 1496, 1, 0, 5, 0, "ここにいると\n穏やかな旋律が\n浮かんできます…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104202, 1497, 1, 0, 6, 0, "もっと体力をつけて\n最高のライブを\nファンに届けます！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104202, 1498, 1, 0, 7, 0, "これでも一応\n体形には気をつけて\n食べてるんですよ？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104202, 1499, 1, 0, 8, 0, "読書は歌に\n新しい風を\n吹き込んでくれます", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104301, 1501, 1, 0, 1, 0, "店の新メニューでも\n考えるかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104301, 1502, 1, 0, 2, 0, "あたしの店から、\n出前を届けようか？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104301, 1503, 1, 0, 3, 0, "パトロール、\nパトロール…\nここは異常なしかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104301, 1504, 1, 0, 4, 0, "腹へったら食う！\n悩んだら寝る！\nそれが一番だろ？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104301, 1505, 1, 0, 5, 0, "のんびりし過ぎるのも\n…性に合わねぇな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 104301, 1506, 1, 0, 101, 0, "そろそろあいつ、\n腹すかしてるん\nじゃないかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104301, 1507, 1, 0, 102, 0, "今日の献立は…\nあいつの好きなものに\nしてやるか", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104301, 1508, 1, 0, 1, 0, "強くなりたいなら、\nあたしがビシバシ\n鍛えてやるよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104301, 1509, 1, 0, 2, 0, "天気がいい日は\n外で運動したく\nならねぇか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104301, 1510, 1, 0, 3, 0, "腹減ったか？　なんか\n作ってやろうか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104301, 1511, 1, 0, 4, 0, "ん？　なんだよ、\n相談事か？\nなんでも言ってくれ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 104301, 1512, 1, 0, 101, 0, "お前が好きな飯って\nなんだ？\n今度、作ってやるよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104301, 1513, 1, 0, 102, 0, "お前って不思議だよな\n一緒にいるとなんだか\n…なんでもねーよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104301, 1514, 1, 1, 1, 0, "気持ちいい朝だな！\n体を動かしたい気分だ\n付き合ってくれるか", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104301, 1515, 1, 2, 2, 0, "昼飯もう食ったか？\nまだなら、あたしが\nなんか作ってやるよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104301, 1516, 1, 3, 3, 0, "日中たっぷり動いたら\nそのぶん夜はしっかり\n休んどけよな", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104301, 1517, 1, 0, 1, 0, "た、たまにはその…\nかわいい格好とか\nしても…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104301, 1518, 1, 0, 2, 0, "ふわぁ、\n一休みすっかなぁ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104301, 1519, 1, 0, 3, 0, "うっ！　か、かわいい\nじゃねぇか", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104301, 1520, 1, 0, 4, 0, "へー　いい匂いだな\nあいつもこういうの\n好きなのかな…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104301, 1521, 1, 0, 5, 0, "ふわぁ〜！\n温かいと、つい\nウトウトしちまうな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104301, 1522, 1, 0, 6, 0, "オラオラオラーーー！\n燃えるぜーーー！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104301, 1523, 1, 0, 7, 0, "いい味してるぜ！\nうちの店でも\n出してみたいな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104301, 1524, 1, 0, 8, 0, "文字を読んでると\nどうしても眠く\nなっちまうんだよなぁ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104302, 1526, 1, 0, 1, 0, "パトロール終了～\n本日も異常なしっと！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104302, 1527, 1, 0, 2, 0, "あたしの店から\n出前を届けようか？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104302, 1528, 1, 0, 3, 0, "あいつ、早く\n帰ってこないかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104302, 1529, 1, 0, 4, 0, "あいつ\n腹すかせてないと\nいいんだけど…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104302, 1530, 1, 0, 5, 0, "のんびりし過ぎるのも\n…性に合わねぇな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104302, 1531, 1, 0, 101, 0, "あいつの事を思うと\n胸があったかくなる？\nなんだコレ…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104302, 1532, 1, 0, 102, 0, "今日の献立は…\nあいつの好きなものに\nしてやるか", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104302, 1533, 1, 0, 1, 0, "ん？　なんだよ\n相談事か？\nなんでも言ってくれ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104302, 1534, 1, 0, 2, 0, "天気がいい日は\n外で運動したく\nならねぇか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104302, 1535, 1, 0, 3, 0, "腹減ったか？　なんか\n作ってやろうか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104302, 1536, 1, 0, 4, 0, "腹へったら食う！\n悩んだら寝る！\nそれが一番だろ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104302, 1537, 1, 0, 101, 0, "あのさ…できれば\nもっとそばに…いや\nなんでもねぇ忘れろ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104302, 1538, 1, 0, 102, 0, "お前って不思議だよな\n一緒にいるとなんだか\n…なんでもねーよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104302, 1539, 1, 1, 1, 0, "気持ちいい朝だな！\n体を動かしたい気分だ\n付き合ってくれるか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104302, 1540, 1, 2, 2, 0, "昼飯もう食ったか？\nまだなら、あたしが\nなんか作ってやるよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104302, 1541, 1, 3, 3, 0, "あんま夜更しすんなよ\n明日に響いたら\n大変だろ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104302, 1542, 1, 0, 1, 0, "た、たまにはその…\nかわいい格好とか\nしても…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104302, 1543, 1, 0, 2, 0, "ふぅ～♪\n疲れがとれるな～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104302, 1544, 1, 0, 3, 0, "うっ！　か、かわいい\nじゃねぇか", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104302, 1545, 1, 0, 4, 0, "へー　いい匂いだな\nあいつもこういうの\n好きなのかな…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104302, 1546, 1, 0, 5, 0, "ふわぁ～！\n温かいと、つい\nウトウトしちまうな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104302, 1547, 1, 0, 6, 0, "オラオラオラーーー！\n燃えるぜーーー！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104302, 1548, 1, 0, 7, 0, "いい味してるぜ！\nうちの店でも\n出してみたいな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104302, 1549, 1, 0, 8, 0, "料理だけじゃなく\n経営術とかも\n学んどかねぇと…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104501, 1551, 1, 0, 1, 0, "最近なにも\nしてくれません…\n焦らしてるんですね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104501, 1552, 1, 0, 2, 0, "ぐふふ、じゅるり…\nい、いけない、また\nいつの間にかよだれが", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104501, 1553, 1, 0, 3, 0, "好きな武器といえば\n剣より槍より\nやっぱり鞭…ぐふふ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104501, 1554, 1, 0, 4, 0, "「無数の触手が\nクウカの柔肌に\n食い込み」…ぐふふ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104501, 1555, 1, 0, 5, 0, "もしドＳさんが\nＭに転じたらと思うと\n身の毛がよだちます！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 104501, 1556, 1, 0, 101, 0, "ドＳさんを登場\nさせるだけで、妄想に\n無限の広がりが！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104501, 1557, 1, 0, 102, 0, "あっ…\nジロジロ見てたの\n気づいちゃいました？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104501, 1558, 1, 0, 1, 0, "視線を感じると\n思ったら…やはり\nドＳさんでしたか", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104501, 1559, 1, 0, 2, 0, "今日は\nどんなヒドイことを\nしてくれるんですか？\nぐふふふ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104501, 1560, 1, 0, 3, 0, "いつの間にそこに！？\nまさかクウカを監視\nしていたのですか！？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104501, 1561, 1, 0, 4, 0, "クウカに触るつもり\nですか？　触るつもり\nなのですねっ！？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 104501, 1562, 1, 0, 101, 0, "クウカにナニをする\nつもりですか…\n期待してしまいます！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104501, 1563, 1, 0, 102, 0, "ドＳさんの視界に入る\nだけでドキドキが…\nこれが恋！？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104501, 1564, 1, 1, 1, 0, "おはようございます。\n朝からお盛んですね？\nぐふふ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104501, 1565, 1, 2, 2, 0, "こんなにも日の高い\nうちからドＳさんは…\nじゅるり", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104501, 1566, 1, 3, 3, 0, "夜はこれから、\nですよ…\nぐふ、ぐふふふ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104501, 1567, 1, 0, 1, 0, "キレイにおめかし\nしたのに力ずくで…\nぐふふふ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104501, 1568, 1, 0, 2, 0, "ソファの背にクウカを\n押し付けて…ぐふふ\nいいですね\nソファドン", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104501, 1569, 1, 0, 3, 0, "「見た目とは裏腹に\nそれは激しく攻め\nたてて」…ぐふふふ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104501, 1570, 1, 0, 4, 0, "香りを吸ったクウカの\n頬は紅潮し目はうるみ\nもはや抗う力も…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104501, 1571, 1, 0, 5, 0, "ああ、身体が\n火照って…\nぐふ、ぐふふ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104501, 1572, 1, 0, 6, 0, "はぁ、はぁ、こうして\nクウカの体力を\n奪ってから…ぐふふ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104501, 1573, 1, 0, 7, 0, "食欲が満たされたら\nつ、次はやはり…！\nそうなのですね！？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104501, 1574, 1, 0, 8, 0, "この本の下に、\nそういう本を\nカムフラージュ？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104502, 1576, 1, 0, 1, 0, "「ドＳさんと踊り子\n乱舞の一夜」…\n次の日記はこれで…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104502, 1577, 1, 0, 2, 0, "最近なにも\nしてくれません…\n焦らしてるんですね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104502, 1578, 1, 0, 3, 0, "も、妄想が\n足りません…\n禁断症状がっ…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104502, 1579, 1, 0, 4, 0, "もしドＳさんが\nＭに転じたらと思うと\n身の毛がよだちます！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104502, 1580, 1, 0, 5, 0, "次はどんなドＳを\nされるのか、想像する\nだけで…ぐふふふ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104502, 1581, 1, 0, 101, 0, "じっとしてると\nドＳさんのことばかり\n考えてしまいますね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104502, 1582, 1, 0, 102, 0, "妄想が止まらなくて\nクウカどうにかなって\nしまいそうです！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104502, 1583, 1, 0, 1, 0, "ドＳさんといると体が\n火照って…クウカは\nいやらしい子です！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104502, 1584, 1, 0, 2, 0, "あっ…\nジロジロ見てたの\n気づいちゃいました？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104502, 1585, 1, 0, 3, 0, "ドＳさんのおかげで\n新しい扉が開けそう\nです…じゅるり", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104502, 1586, 1, 0, 4, 0, "いやー！　ドＳさんに\n襲われ…襲って\nくれないんですか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104502, 1587, 1, 0, 101, 0, "クウカはドＳさんに\n身も心も堕とされて\nしまったのですね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104502, 1588, 1, 0, 102, 0, "ドＳさんがお望みなら\nクウカは\nどんなことだって！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104502, 1589, 1, 1, 1, 0, "朝だからこそ\nできる妄想が\nあります…ぐふふ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104502, 1590, 1, 2, 2, 0, "こんな日の高いうち\nからドＳさんは…\nじゅるり", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104502, 1591, 1, 3, 3, 0, "今夜は一晩中…\nなんですね？\nぐふふ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104502, 1592, 1, 0, 1, 0, "意外と難しいんですよ\n正しい亀甲縛りと\nいうものは…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104502, 1593, 1, 0, 2, 0, "くつろいで油断\nしているスキに何を\nされるのでしょう！？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104502, 1594, 1, 0, 3, 0, "「見た目とは裏腹に\nそれは激しく攻め\nたてて」…ぐふふふ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104502, 1595, 1, 0, 4, 0, "これは禁断の香り…\nああっ一段階上の\n妄想が開花するうぅ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104502, 1596, 1, 0, 5, 0, "ああっ、暖気がまるで\nロウソクのように\nクウカを攻めますっ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104502, 1597, 1, 0, 6, 0, "激しい攻めを受けきる\nためには、鍛えないと\nいけないのですね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104502, 1598, 1, 0, 7, 0, "こんなにいっぱい\n入りません…\nお腹にっ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104502, 1599, 1, 0, 8, 0, "どんな本も\nよく見ると\nいやらしいです…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104601, 1601, 1, 0, 1, 0, "この前遊んだ猫、\nモッフモフ\nだったにゃあ…♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104601, 1602, 1, 0, 2, 0, "ほっぺやお腹が、\n肉球ぐらいぷにぷに…\nこれはまずいにゃ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104601, 1603, 1, 0, 3, 0, "大きな仕事が\n決まったにゃ！\n腕が鳴るにゃ～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104601, 1604, 1, 0, 4, 0, "おやつのたい焼き、\nつぶあんとこしあん、\nどっちに\nしようかにゃ？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104601, 1605, 1, 0, 5, 0, "うにゃー…\nもうちょっとお昼寝\nしたかったにゃ…\nむにゃむにゃ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 104601, 1606, 1, 0, 101, 0, "キミに呼ばれると、\n猫まっしぐらな気分に\nなっちゃうにゃ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104601, 1607, 1, 0, 102, 0, "キミのおかげで\nギルドの仕事も\n大成功だにゃ♪\nありがとにゃ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104601, 1608, 1, 0, 1, 0, "うずうず…その指、\nじゃれついても\nいいかにゃ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104601, 1609, 1, 0, 2, 0, "にゃ？　あたしに何か\nご入り用かにゃ？\nお安くしとくにゃ～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104601, 1610, 1, 0, 3, 0, "うーん、なんだか\n退屈にゃ～\n遊んで、遊んで～？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104601, 1611, 1, 0, 4, 0, "素早い動きのコツ？\nタダじゃ教えて\nあげられないにゃ～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 104601, 1612, 1, 0, 101, 0, "そんなにあたしを\nなでたいにゃ？\nキミならなでさせて\nあげてもいいかにゃー", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104601, 1613, 1, 0, 102, 0, "ふにゃあ、ねえねえ、\n一緒にゴロゴロ\nしようにゃあ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104601, 1614, 1, 1, 1, 0, "いい朝にゃ！\n顔を洗ってくるにゃ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104601, 1615, 1, 2, 2, 0, "むーん、そろそろ\nお昼…猫たちにも\n猫缶を…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104601, 1616, 1, 3, 3, 0, "ふわあ…眠いにゃ…\n猫は布団で\n丸くなるにゃ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104601, 1617, 1, 0, 1, 0, "客商売では、\n外見のチェックも\n欠かせないのにゃ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104601, 1618, 1, 0, 2, 0, "うにゃ～！\nお昼寝に\nぴったりにゃ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104601, 1619, 1, 0, 3, 0, "これ…量産したら\n子どもにバカ売れ\nしそうだにゃ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104601, 1620, 1, 0, 4, 0, "この灯り、まるで\n猫みたいな\n癒し効果にゃ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104601, 1621, 1, 0, 5, 0, "ふにゃあ、\nあったかいと猫は丸く\nなっちゃうのにゃあ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104601, 1622, 1, 0, 6, 0, "本当は外で\n運動したいん\nだけどにゃ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104601, 1623, 1, 0, 7, 0, "むーん、\n美味しいにゃー！\nたい焼きに\n匹敵するにゃ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104601, 1624, 1, 0, 8, 0, "ふむふむ…猫の\nしつけ方は、と…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104602, 1626, 1, 0, 1, 0, "かわいいネコの置物を\nゲットしたにゃ！\nどこに飾ろうか\nにゃ～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104602, 1627, 1, 0, 2, 0, "仕事の交渉がうまく\n行ったにゃ～♪\nいい気分だにゃ～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104602, 1628, 1, 0, 3, 0, "ねこじゃらしには\n食いついちゃうにゃ\nびしっ！　びしっ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104602, 1629, 1, 0, 4, 0, "ほっぺやお腹が、肉球\nぐらいぷにぷに…\nこれはまずいにゃ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104602, 1630, 1, 0, 5, 0, "さっきのたいやき\nおいしかったにゃ♪\nごろごろごろ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104602, 1631, 1, 0, 101, 0, "手作りのたい焼き\nあいつは食べて\nくれるかにゃ…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104602, 1632, 1, 0, 102, 0, "あいつには、いつか\nたっぷり恩返しが\nしたいにゃ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104602, 1633, 1, 0, 1, 0, "にゃーに？　あたしの\nことがそんなに気に\nなるのかにゃ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104602, 1634, 1, 0, 2, 0, "あたしのこと…\nしっかり捕まえ\nといてにゃ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104602, 1635, 1, 0, 3, 0, "あたしの力が\n必要かにゃ？　猫の手\n貸してあげるにゃ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104602, 1636, 1, 0, 4, 0, "たいやきとクレープを\n合わせれば…\n新しい商売のニオイが\nするにゃ…！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104602, 1637, 1, 0, 101, 0, "ふにゃあ、ねえねえ\n一緒にゴロゴロ\nしようにゃあ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104602, 1638, 1, 0, 102, 0, "キミと一緒にいると\n楽しくなってつい\nじゃれちゃうにゃ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104602, 1639, 1, 1, 1, 0, "いい朝にゃ！\n顔を洗ってくるにゃ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104602, 1640, 1, 2, 2, 0, "そろそろおっひる～\nにゃーんか\n食べたい…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104602, 1641, 1, 3, 3, 0, "うーん、眠くなって\nきたにゃ。あたしは\n夜行性じゃないのにゃ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104602, 1642, 1, 0, 1, 0, "にゃにゃ…\n髪が乱れてるにゃ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104602, 1643, 1, 0, 2, 0, "ふっかふかにゃ～\n猫がねころん\nじゃうにゃ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104602, 1644, 1, 0, 3, 0, "これ、猫と匹敵する\nぐらいかわいいにゃ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104602, 1645, 1, 0, 4, 0, "猫でもこわくない\n優しい灯りにゃ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104602, 1646, 1, 0, 5, 0, "ぽっかぽか\nしあわせな気分にゃ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104602, 1647, 1, 0, 6, 0, "雨の日でも運動\nできるのは、濡れ\nなくて便利だにゃ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104602, 1648, 1, 0, 7, 0, "美味しいにゃ～！\nこれ、朝昼晩\n食べたいにゃ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104602, 1649, 1, 0, 8, 0, "本を読んで\n猫の気持ちを\n勉強するのにゃ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104801, 1651, 1, 0, 1, 0, "内職手伝える人\nいないかしら？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104801, 1652, 1, 0, 2, 0, "次の依頼は、\nこういう計画で…\nうん、完璧ね！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104801, 1653, 1, 0, 3, 0, "次の特売セールに\n向けて、しっかり\n準備しないと！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104801, 1654, 1, 0, 4, 0, "配達の後に売り子\n次に採取のバイト…\nうん、効率的ね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104801, 1655, 1, 0, 5, 0, "一日に受ける\nバイトの数、もう少し\n増やしたいわね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 104801, 1656, 1, 0, 101, 0, "師匠として\n頼りにされたいし\nもっと頑張らなきゃ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104801, 1657, 1, 0, 102, 0, "男の子と効率的に\n仲良くなる方法…\nこればっかりは難題ね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104801, 1658, 1, 0, 1, 0, "あら、\nちょうどよかった\n今から一緒に\nバイトでもどう？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104801, 1659, 1, 0, 2, 0, "相談事かしら？\nそれくらいなら\n無料でいいわよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104801, 1660, 1, 0, 3, 0, "背後の気配なら\nすぐ分かるわ\n試してみる？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104801, 1661, 1, 0, 4, 0, "道具やお金の管理は\n大丈夫？　手伝うから\nいつでも言って", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 104801, 1662, 1, 0, 101, 0, "暇ができたから\n働きたいけど…\nキミとの時間も大切ね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104801, 1663, 1, 0, 102, 0, "ねえ、あの…\n私はキミにとって…\nいや、何でもないわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104801, 1664, 1, 1, 1, 0, "おはよう！\n今日も朝から\nバイトよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104801, 1665, 1, 2, 2, 0, "お昼時ね。\n少し休憩したら、\nまた働くわよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104801, 1666, 1, 3, 3, 0, "もうこんな時間！\n明日もバイトで\n朝早いのよ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104801, 1667, 1, 0, 1, 0, "接客業の依頼が\nある時は、外見も\nキレイにしないとね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104801, 1668, 1, 0, 2, 0, "はぁ～バイトで\n疲れたから癒される～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104801, 1669, 1, 0, 3, 0, "これを内職で\n作ったら\n儲かるかしら？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104801, 1670, 1, 0, 4, 0, "リフレッシュできたわ\nこれでまた、バリバリ\n働けるわね！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104801, 1671, 1, 0, 5, 0, "風邪を引いたら効率が\n落ちちゃうから、\n暖めないと！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104801, 1672, 1, 0, 6, 0, "働き続けるには\n健康第一！　それには\n運動が一番よ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104801, 1673, 1, 0, 7, 0, "もぐもぐ…\n腹が減っては\n戦ができぬ、よ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104801, 1674, 1, 0, 8, 0, "日々絶えず学んで、\n生活に活かすのよ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104802, 1676, 1, 0, 1, 0, "家計簿をつけなきゃ！\n覚えている時に\nやっておかないとね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104802, 1677, 1, 0, 2, 0, "次の依頼は\nこういう計画で…\nうん、完璧ね！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104802, 1678, 1, 0, 3, 0, "ギルドの経費\nあとちょっとだけ\n削れそうね…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104802, 1679, 1, 0, 4, 0, "少しでも時給のいい\nバイトがしたいけど\nなかなか\n出会えないのよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104802, 1680, 1, 0, 5, 0, "今度のバイトも\nなかなか大変そう\nね…腕が鳴るわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104802, 1681, 1, 0, 101, 0, "男の子と効率的に\n仲良くなる方法…\nこればっかりは難題ね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104802, 1682, 1, 0, 102, 0, "私、どうしたんだろ？\n仕事中もずっと\n彼のこと考えてる…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104802, 1683, 1, 0, 1, 0, "この間はヘルプに\n来てくれてありがとう\n頼んで正解だったわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104802, 1684, 1, 0, 2, 0, "見つけた！　ちょうど\n頼みたいことが\nあるんだけど、いい？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104802, 1685, 1, 0, 3, 0, "今から\n休憩するところなの\n一緒にどう？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104802, 1686, 1, 0, 4, 0, "ん？　どうしたの？\n私のことが\n気になるの？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104802, 1687, 1, 0, 101, 0, "…たまには甘えても\nいい？　私だって\n弱る時はあるのよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104802, 1688, 1, 0, 102, 0, "暇ができたから\n働きたいけど…\nキミとの時間も大切ね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104802, 1689, 1, 1, 1, 0, "おはよう！\nいい朝ね。一緒に\nランニングでもいく？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104802, 1690, 1, 2, 2, 0, "お昼時ね\n少し休憩したら\nまた働くわよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104802, 1691, 1, 3, 3, 0, "もうこんな時間！\n明日もバイトで\n朝早いのよ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104802, 1692, 1, 0, 1, 0, "女の子らしく…\nどうも忘れちゃう\nのよね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104802, 1693, 1, 0, 2, 0, "ここで少し\n休んだら\nまたバイトよ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104802, 1694, 1, 0, 3, 0, "これを内職で\n作ったら\n儲かるかしら？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104802, 1695, 1, 0, 4, 0, "仕事から帰ってきて\nこういうのがあると\n素敵ね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104802, 1696, 1, 0, 5, 0, "しっかり身体を\n温めて、血流を\nよくしなくっちゃ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104802, 1697, 1, 0, 6, 0, "これで休憩中も\n無駄なく\n鍛えられるわ♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104802, 1698, 1, 0, 7, 0, "もぐもぐ…よく\n食べて、よく働く！\nそれが私のモットーよ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104802, 1699, 1, 0, 8, 0, "日々絶えず学んで\n生活に活かすのよ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104901, 1701, 1, 0, 1, 0, "暇だし、お料理と\nお掃除とお洗濯と\n買い物しようかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104901, 1702, 1, 0, 2, 0, "こうして歩いていれば\n弟くんが声をかけて\nくれるはず…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104901, 1703, 1, 0, 3, 0, "弟くんがどんな生活を\nしてるのか調べるのも\nお姉ちゃんの務め！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104901, 1704, 1, 0, 4, 0, "くんくん、\n弟くんのにおい…\n近くにいるね？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104901, 1705, 1, 0, 5, 0, "お姉ちゃんの痕跡を\n部屋中に\n残しちゃおっと♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 104901, 1706, 1, 0, 101, 0, "この部屋を、\n私と弟くんの\n愛の巣に…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104901, 1707, 1, 0, 102, 0, "はぁ～…弟くん\n禁断症状だよ～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104901, 1708, 1, 0, 1, 0, "弟くんの部屋は\nお姉ちゃんの\n部屋だよね～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104901, 1709, 1, 0, 2, 0, "いいこいいこ～♪\nどう、お姉ちゃんの\nナデナデは？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104901, 1710, 1, 0, 3, 0, "姉と弟の外出は\nデートって\nいうのかな？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104901, 1711, 1, 0, 4, 0, "もう！\nイタズラする子には\nお姉ちゃんチョップ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 104901, 1712, 1, 0, 101, 0, "お姉ちゃんが\nぎゅ～ってして\nあげるね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104901, 1713, 1, 0, 102, 0, "あ、あのね…\nたまにはお姉ちゃんも\n甘えていい？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104901, 1714, 1, 1, 1, 0, "着替えは？　荷物は？\nお出かけの準備は\nお姉ちゃんが手伝うね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104901, 1715, 1, 2, 2, 0, "出かけるなら\n明るいうちに\n帰ってくるんだよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104901, 1716, 1, 3, 3, 0, "まだ起きてるの？\nそれじゃあ、\nお姉ちゃんも\n夜更かししちゃお☆", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104901, 1717, 1, 0, 1, 0, "きれいなお姉ちゃん\n好きだよね♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104901, 1718, 1, 0, 2, 0, "ここにお姉ちゃんの\n匂いを、た～っぷり\n残しておこうっと♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104901, 1719, 1, 0, 3, 0, "かわいい♪\nお姉ちゃんチョップ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104901, 1720, 1, 0, 4, 0, "この香り、\n弟くん好きかな？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104901, 1721, 1, 0, 5, 0, "これ、弟くんみたいに\nあったかいね～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104901, 1722, 1, 0, 6, 0, "ダイエットダイエット\nきれいなお姉ちゃんに\nならなくちゃ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104901, 1723, 1, 0, 7, 0, "あ、これ\n弟くんが好きな\n味付けだね！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104901, 1724, 1, 0, 8, 0, "弟くんが読んでいいか\nお姉ちゃんが先に\n中を確認しなくちゃ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104902, 1726, 1, 0, 1, 0, "暇だし、お料理と\nお掃除とお洗濯と\n買い物しようかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104902, 1727, 1, 0, 2, 0, "こうして歩いていれば\n弟くんが声をかけて\nくれるはず…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104902, 1728, 1, 0, 3, 0, "弟くんのお友達は\nみんな私の妹だよ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104902, 1729, 1, 0, 4, 0, "弟くんがどんな生活を\nしてるのか調べるのも\nお姉ちゃんの務め！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104902, 1730, 1, 0, 5, 0, "はっ！\n弟くんが困ってる\n気がする！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104902, 1731, 1, 0, 101, 0, "はぁ～…弟くん、\n禁断症状だよ～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104902, 1732, 1, 0, 102, 0, "お姉ちゃんの痕跡を\n部屋中に\n残しちゃおっと♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104902, 1733, 1, 0, 1, 0, "お姉ちゃん\nキミのお願いなら\nなんでも聞いて\nあげちゃうぞっ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104902, 1734, 1, 0, 2, 0, "弟くんの部屋は\nお姉ちゃんの\n部屋だよね～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104902, 1735, 1, 0, 3, 0, "お姉ちゃんの胸に\n飛び込んでおいでっ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104902, 1736, 1, 0, 4, 0, "いいこいいこ～♪\nどう、お姉ちゃんの\nナデナデは？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104902, 1737, 1, 0, 101, 0, "あ、あのね…\nたまにはお姉ちゃんも\n甘えていい？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104902, 1738, 1, 0, 102, 0, "お風呂にする？\nごはんにする？\nそれとも…うふふ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104902, 1739, 1, 1, 1, 0, "着替えは？　荷物は？\nお出かけの準備は\nお姉ちゃんが手伝うね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104902, 1740, 1, 2, 2, 0, "お姉ちゃんが\nお昼ごはん\n作ってあげようか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104902, 1741, 1, 3, 3, 0, "眠れないの？\nお姉ちゃんが子守歌\n歌ってあげるね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104902, 1742, 1, 0, 1, 0, "弟くんを\n誘惑しちゃうぞ～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104902, 1743, 1, 0, 2, 0, "ここにお姉ちゃんの\n匂いを、た～っぷり\n残しておこうっと♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104902, 1744, 1, 0, 3, 0, "かわいい♪\nお姉ちゃんチョップ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104902, 1745, 1, 0, 4, 0, "お肌に潤いが\nでてきたみたい～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104902, 1746, 1, 0, 5, 0, "これ、弟くんみたいに\nあったかいね～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104902, 1747, 1, 0, 6, 0, "ダイエットダイエット\nきれいなお姉ちゃんに\nならなくちゃ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104902, 1748, 1, 0, 7, 0, "弟くんの手作りなら\nもっとおいしくなると\n思うんだけどなぁ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104902, 1749, 1, 0, 8, 0, "弟くんをきちんと\n教育するために\nお姉ちゃんは勉強を\nかかさないのだ☆", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105001, 1751, 1, 0, 1, 0, "ん～、エレガントで\n大人なお部屋ね！\nアタシにぴったり！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105001, 1752, 1, 0, 2, 0, "身長とバスト以外は\n完璧な大人\nなのになぁ～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105001, 1753, 1, 0, 3, 0, "アタシの大人コーデで\nあいつも夢中ね♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105001, 1754, 1, 0, 4, 0, "朝顔の観察したら\n理系女子になれる\nかしら？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105001, 1755, 1, 0, 5, 0, "レディは、どんな時も\nおしとやかに…って\nアタシも騒ぎたいー！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 105001, 1756, 1, 0, 101, 0, "大人っぽい子が\n多いわね…アタシも\n負けてられないわ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 105001, 1757, 1, 0, 102, 0, "アタシが待ってる\nなんて、アイツは\n幸せものよねー", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105001, 1758, 1, 0, 1, 0, "わーい、アメだー♪\n食べる食べるー♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105001, 1759, 1, 0, 2, 0, "熱いハニーラテが\n飲みたーい！\n淹れてー淹れてー！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105001, 1760, 1, 0, 3, 0, "なによ？　もしかして\n好きな子にイタズラ\nしたくなるってやつ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105001, 1761, 1, 0, 4, 0, "アタシに合う服\n雑誌で探そうっと！\nサイズじゃないわ、\nデザインとかの話よ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 105001, 1762, 1, 0, 101, 0, "ねーねー！\nアタシ、かわいい？\nかわいいわよね？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 105001, 1763, 1, 0, 102, 0, "これから出かけるの？\nしっかりアタシを\nエスコートしてね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105001, 1764, 1, 1, 1, 0, "朝のスキンケアは\n大事なの！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105001, 1765, 1, 2, 2, 0, "お腹すいたー\nダイエット\nやめちゃおうかなー", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105001, 1766, 1, 3, 3, 0, "エイジングケアを\nしないとねー", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105001, 1767, 1, 0, 1, 0, "この角度のほうが\n大人っぽく見えるかな\nそれともこっちかしら", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105001, 1768, 1, 0, 2, 0, "あ！　これ雑誌で\n見たことあるわ～\nだからきっと\nオシャレなのよ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105001, 1769, 1, 0, 3, 0, "か、かわいい♪　けど\n大人はこんなもの…\nでも、かわいいし～！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105001, 1770, 1, 0, 4, 0, "オシャレ～♪\n大人なレディって\n感じよね♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105001, 1771, 1, 0, 5, 0, "あったかーい♪\nここで宿題\nしようかしら？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105001, 1772, 1, 0, 6, 0, "運動したら成長\nしないかしら？\n背とか胸とか…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105001, 1773, 1, 0, 7, 0, "食べすぎると\n太っちゃうな…\nでも、おいしいし～っ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105001, 1774, 1, 0, 8, 0, "おもしろーい！\n…でも絵本って\n子供っぽいかしら？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105002, 1776, 1, 0, 1, 0, "レディは、どんな時も\nおしとやかに…って\nアタシも騒ぎたいー！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105002, 1777, 1, 0, 2, 0, "恋人にするなら\nイケメンか\nただイケよね～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105002, 1778, 1, 0, 3, 0, "もう少し背が欲しいな\n…もっと牛乳を\n飲まなくちゃ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105002, 1779, 1, 0, 4, 0, "朝顔の観察したら\n理系女子になれる\nかしら？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105002, 1780, 1, 0, 5, 0, "こ、これは補習の\nお知らせプリント！\nアイツに見つかる前に\n隠さなくちゃ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 105002, 1781, 1, 0, 101, 0, "アイツおっそいなー\n別に会いたいわけじゃ\nないけど…\n来ないと気になる…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 105002, 1782, 1, 0, 102, 0, "アタシが待ってる\nなんて、アイツは\n幸せものよねー ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105002, 1783, 1, 0, 1, 0, "なーに？\nアタシに甘えたいの？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105002, 1784, 1, 0, 2, 0, "アタシに合う服\n雑誌で探そうっと！\nサイズじゃないわ\nデザインとかの話よ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105002, 1785, 1, 0, 3, 0, "頭撫でないで！\nお子様みたいに\nあつかわないでって\n言ってるのに～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105002, 1786, 1, 0, 4, 0, "壁ドンの練習…\nあれ？　壁ドンって\n男がするんだっけ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 105002, 1787, 1, 0, 101, 0, "ねーねー！\nアタシ、かわいい？\nかわいいわよね？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 105002, 1788, 1, 0, 102, 0, "これから出かけるの？\nしっかりアタシを\nエスコートしてね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105002, 1789, 1, 1, 1, 0, "レディの朝は\n忙しいのよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105002, 1790, 1, 2, 2, 0, "お腹すいたー\nダイエット\nやめちゃおうかなー", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105002, 1791, 1, 3, 3, 0, "エイジングケアを\nしないとねー", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105002, 1792, 1, 0, 1, 0, "身だしなみチェック\nオッケー！　これで\nいつアイツが来ても\n大丈夫ね！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105002, 1793, 1, 0, 2, 0, "あ！　これ雑誌で\n見たことあるわ～\nだからきっと\nオシャレなのよ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105002, 1794, 1, 0, 3, 0, "あたしがもっと\nオシャレにかわいく\nしてあげようか？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105002, 1795, 1, 0, 4, 0, "オシャレ～♪\n大人なレディって\n感じよね♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105002, 1796, 1, 0, 5, 0, "あったかーい♪\nここで宿題\nしようかしら？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105002, 1797, 1, 0, 6, 0, "運動したら成長\nしないかしら？\n背とか胸とか…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105002, 1798, 1, 0, 7, 0, "レディは、食べ物にも\n気を使うのよ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105002, 1799, 1, 0, 8, 0, "おもしろーい！\n…でも絵本って\n子供っぽいかしら？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105101, 1801, 1, 0, 1, 0, "魔法と科学\nどっちも極めて\nみせるわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105101, 1802, 1, 0, 2, 0, "仕事も片付いたし\n部屋で\n実験実験…♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105101, 1803, 1, 0, 3, 0, "たまった治療代の\n請求書を\nまとめないと…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105101, 1804, 1, 0, 4, 0, "新薬が出来たし\nすぐに試したいけど…\n実験台がいないわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105101, 1805, 1, 0, 5, 0, "部屋にこもりすぎると\n曜日の感覚が\nなくなるわね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 105101, 1806, 1, 0, 101, 0, "あの子の記憶を\n取り戻せる薬…\n作れないものかしら", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 105101, 1807, 1, 0, 102, 0, "好意に気づいて貰える\n薬って、どうすれば\n作れるのかしら", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105101, 1808, 1, 0, 1, 0, "あら、ヒマなら\n薬草の整理を\n手伝って", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105101, 1809, 1, 0, 2, 0, "もしかして実験に\n付き合ってくれるの？\nえ？　違う？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105101, 1810, 1, 0, 3, 0, "具合が悪い？\nなら診てあげる\nまずは触診から…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105101, 1811, 1, 0, 4, 0, "植物に興味あるの？\nなら教えてあげるわ\n色々とね…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 105101, 1812, 1, 0, 101, 0, "キミの傍にいるだけで\n高揚しちゃうのは\n何故かしら…？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 105101, 1813, 1, 0, 102, 0, "私はおしおきする\n側のことが多いけど…\nキミにはされたいかも", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105101, 1814, 1, 1, 1, 0, "お昼までに調合用の\n花を集めたいの\n手伝ってくれる？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105101, 1815, 1, 2, 2, 0, "あら、もうお昼？\nそろそろ一息\n入れましょうか", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105101, 1816, 1, 3, 3, 0, "夜も更けて来たわね\n私は朝まで実験だけど\n見学していく？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105101, 1817, 1, 0, 1, 0, "医者たるもの\nいつだって\n清潔に…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105101, 1818, 1, 0, 2, 0, "いいわねこれ\n仮眠の時に\n使わせてもらうわ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105101, 1819, 1, 0, 3, 0, "ぬいぐるみなら\n動物も…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105101, 1820, 1, 0, 4, 0, "いい香り…\n仕事場にも\n置いてみようかしら", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105101, 1821, 1, 0, 5, 0, "ここにいると\nついウトウト\nしちゃうわ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105101, 1822, 1, 0, 6, 0, "座ってる時間が多いと\n運動不足になるし\nたまにはいいわね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105101, 1823, 1, 0, 7, 0, "研究していると\nつい食べるのを\n忘れちゃうのよね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105101, 1824, 1, 0, 8, 0, "実験もいいけど\nいつか成果を\n本にしないと…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105102, 1826, 1, 0, 1, 0, "徹夜はツラいけど\n集中すると時間の\n感覚がなくなって…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105102, 1827, 1, 0, 2, 0, "痛みに悶える顔って\nどうしてあんなに\n興奮するのかしら？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105102, 1828, 1, 0, 3, 0, "彼に罵られたら\nどんな気分かしら\n意外と快感かも？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105102, 1829, 1, 0, 4, 0, "毒を薄めると\n薬になるのって\n今でも不思議だわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105102, 1830, 1, 0, 5, 0, "綺麗なバラには\nトゲがあるけど\n魅力の一つよね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 105102, 1831, 1, 0, 101, 0, "カレをさりげなく\n惚れさせる方法を\n研究するわよ…♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 105102, 1832, 1, 0, 102, 0, "好意に気づいて貰える\n薬って、どうすれば\n作れるのかしら", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105102, 1833, 1, 0, 1, 0, "構って欲しいの？\nじゃあ、君の体を\n調べさせて？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105102, 1834, 1, 0, 2, 0, "実験に付き合って\n大丈夫よ、試すのは\nただの栄養剤だから", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105102, 1835, 1, 0, 3, 0, "欲しい薬が\nあったら言ってね\n調合してみるから", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105102, 1836, 1, 0, 4, 0, "おしおきされたいの？\nキミにだったら\nいつでもしてあげる", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 105102, 1837, 1, 0, 101, 0, "キミの傍にいるだけで\n高揚しちゃうのは\n何故かしら…？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 105102, 1838, 1, 0, 102, 0, "私はおしおきする\n側のことが多いけど…\nキミにはされたいかも", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105102, 1839, 1, 1, 1, 0, "朝しか取れない\n植物があるの\n採取しに行くわよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105102, 1840, 1, 2, 2, 0, "お昼時ってやけに\n眠たくなるわよね\n…調べたくなるわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105102, 1841, 1, 3, 3, 0, "今日くらいは\nゆっくり眠りたいわ\n…キミと一緒にね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105102, 1842, 1, 0, 1, 0, "身なりについて\n研究するのも\n楽しそうね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105102, 1843, 1, 0, 2, 0, "休息に必要な道具は\n妥協せずに選ぶ！\nこれだけは譲れないわ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105102, 1844, 1, 0, 3, 0, "たまにはこういう\n可愛らしいものに\n触れないとね♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105102, 1845, 1, 0, 4, 0, "いい香り…\n仕事場にも\n置いてみようかしら", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105102, 1846, 1, 0, 5, 0, "仕事がなかったら\nこのまますーっと\n眠りたいのに…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105102, 1847, 1, 0, 6, 0, "運動しないと\n体型の維持が…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105102, 1848, 1, 0, 7, 0, "適度な食事も\n大切よね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105102, 1849, 1, 0, 8, 0, "まさか私が\n恋愛読本を読む\n日が来るなんて…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105201, 1851, 1, 0, 1, 0, "ぺっぺっ！\n…い、いけないっ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105201, 1852, 1, 0, 2, 0, "いっけな～い！\nひづめで床に傷が！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105201, 1853, 1, 0, 3, 0, "太っちゃったかも～！\n伐採でダイエット\nしなくっちゃ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105201, 1854, 1, 0, 4, 0, "きちんと生活すると\n毛づやが良くなる\nみたいなのよね～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105201, 1855, 1, 0, 5, 0, "みんな、体重\nどれくらいなのかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 105201, 1856, 1, 0, 101, 0, "体力なら自信\nあるんだけど…\n恋にはちょっぴり\n臆病になっちゃう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 105201, 1857, 1, 0, 102, 0, "いつか人間の女の子\nみたいなデート、\nしてみたいな～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105201, 1858, 1, 0, 1, 0, "私ね「どこにでもいる\n普通の女の子」って\n言葉が憧れなんだ～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105201, 1859, 1, 0, 2, 0, "チャームポイント？\n自分では分からない\nけど…まつげかなぁ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105201, 1860, 1, 0, 3, 0, "リマリマ～♪\nはっ！？\nわ、私ったら今何を？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105201, 1861, 1, 0, 4, 0, "力仕事ならまかせて！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 105201, 1862, 1, 0, 101, 0, "人間になりたいな～\nそれか、あなたが\n私みたいな姿に…♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 105201, 1863, 1, 0, 102, 0, "しょうがないなあ、\nこんなにモフらせるの\nあなただけだよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105201, 1864, 1, 1, 1, 0, "今朝は寝癖がひどいわ\nしっぽの辺りとか～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105201, 1865, 1, 2, 2, 0, "ランチは\nおしゃれなカフェで\nしてみたいな～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105201, 1866, 1, 3, 3, 0, "お肌のケアに\nパックしながら\n寝ようかな～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105201, 1867, 1, 0, 1, 0, "やだっ、\nまた寝ぐせついてる！\n直さないと！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105201, 1868, 1, 0, 2, 0, "ミシッっていった！\n私、重すぎるかも！？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105201, 1869, 1, 0, 3, 0, "あ！　あなたも\n美人さんね～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105201, 1870, 1, 0, 4, 0, "はぁ、まったり…\n四足歩行しちゃい\nそうだわ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105201, 1871, 1, 0, 5, 0, "あったか～い♪\n…私、毛皮だから\n暑すぎるかも", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105201, 1872, 1, 0, 6, 0, "な、なんだか野生に\n帰っちゃいそう！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105201, 1873, 1, 0, 7, 0, "ぱくぱく、もぐもぐ、\nぺっぺっ…\nやだ、はしたない！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105201, 1874, 1, 0, 8, 0, "きゃーーー！\nこの恋愛小説\nとっても\nドキドキしちゃう～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105202, 1876, 1, 0, 1, 0, "素敵ね～、居るだけで\n女子力があがりそうな\nお部屋だわ～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105202, 1877, 1, 0, 2, 0, "私、大丈夫かしら？\n他の子に怖がられたり\nしないかな…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105202, 1878, 1, 0, 3, 0, "動物苑…って\n嫌なギルド名ね…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105202, 1879, 1, 0, 4, 0, "好き、嫌い、好き…\nあっ！　うっかりして\n花占いのお花\n食べちゃった！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105202, 1880, 1, 0, 5, 0, "ここ、女の子らしい\nものでいっぱいね～！\nときめいちゃうわ～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 105202, 1881, 1, 0, 101, 0, "体力なら自信\nあるんだけど…\n恋にはちょっぴり\n臆病になっちゃう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 105202, 1882, 1, 0, 102, 0, "あの人が\n来てくれるまで薪割り\nでもしてよっかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105202, 1883, 1, 0, 1, 0, "一緒にお出かけ？\nうれしいけど…\n心の準備が！\nはわわわ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105202, 1884, 1, 0, 2, 0, "どうしたの？\n急に抱きついてきて…\n癒されたいの？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105202, 1885, 1, 0, 3, 0, "ぺっぺっ！\n…い、いけないっ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105202, 1886, 1, 0, 4, 0, "メタモルアップルを\nここで食べたら、\nみんな驚くわよね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 105202, 1887, 1, 0, 101, 0, "しょうがないなあ、\nこんなにモフらせるの\nあなただけだよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 105202, 1888, 1, 0, 102, 0, "人間になりたいな～\nそれか、あなたが\n私みたいな姿に…♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105202, 1889, 1, 1, 1, 0, "今朝は寝癖がひどいわ\nしっぽの辺りとか～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105202, 1890, 1, 2, 2, 0, "ダイエット中だから\nお昼は抜いてるの～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105202, 1891, 1, 3, 3, 0, "お肌のケアに\nパックしながら\n寝ようかな～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105202, 1892, 1, 0, 1, 0, "ちゃんとかわいく\nしてないと刈られ…\n狩られちゃうもんね！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105202, 1893, 1, 0, 2, 0, "ミシッっていった！\n私、重すぎるかも！？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105202, 1894, 1, 0, 3, 0, "あ！　あなたも\n美人さんね～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105202, 1895, 1, 0, 4, 0, "いい香り～\n森の中にいるみたいに\nとっても落ち着く～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105202, 1896, 1, 0, 5, 0, "素敵～♪　…\nきゃっ！　私の毛が\nたいへんなことに～！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105202, 1897, 1, 0, 6, 0, "な、なんだか野生に\n帰っちゃいそう！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105202, 1898, 1, 0, 7, 0, "何を食べるかより\n誰と食べるかが\n大事なのよね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105202, 1899, 1, 0, 8, 0, "きゃーーー！\nこの恋愛小説\nとっても\nドキドキしちゃう～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105301, 1901, 1, 0, 1, 0, "ヴァイスフリューゲル\nを盛り上げる方法、\n共に語らおう！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105301, 1902, 1, 0, 2, 0, "私は子供ではない…！\nが、お菓子は\nいただこう！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105301, 1903, 1, 0, 3, 0, "部屋はきちんと\n片づけておかなくては\n部屋の汚れは\n心の汚れだ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105301, 1904, 1, 0, 4, 0, "むむむ…明日\n目が覚めたら、身長が\n伸びていないものか…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105301, 1905, 1, 0, 5, 0, "お菓子が常備されて\nいる部屋。そんな\nものが、私の好みだ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 105301, 1906, 1, 0, 101, 0, "あいつとの新たな\n鍛錬が\n待ち遠しいな…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 105301, 1907, 1, 0, 102, 0, "最近、あいつと\n過ごしていて、毎日が\n充実しているな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105301, 1908, 1, 0, 1, 0, "軍人の本分は日々の\n研鑽にあるからな。\n今日も鍛錬に\n行かないか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105301, 1909, 1, 0, 2, 0, "毎日牛乳を飲んでいる\nのだが、なかなか背が\n伸びないものだな…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105301, 1910, 1, 0, 3, 0, "貴公にも、飴をくれて\nやろうか？\nあまいぞ～！\n美味しいぞ～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105301, 1911, 1, 0, 4, 0, "私ともっと高く、\n共に飛ぼう！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 105301, 1912, 1, 0, 101, 0, "貴公は、本当に頼りに\nなる男だな…いや、\nふとそう思っただけだ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 105301, 1913, 1, 0, 102, 0, "貴公と共にいられる\nことは私の喜びだ。\nこれからも\nよろしく頼むぞ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105301, 1914, 1, 1, 1, 0, "うむ、いい朝だ。\n本国の夜明けを\n思い出してしまうな！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105301, 1915, 1, 2, 2, 0, "そろそろ昼食に\nするか。腹が減っては\n戦はできないからな", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105301, 1916, 1, 3, 3, 0, "ふわああ…私は子供\nではないが…眠い時は\n眠いのだ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105301, 1917, 1, 0, 1, 0, "身だしなみは、心の\n在りようを映すから\n気をつけなくてはな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105301, 1918, 1, 0, 2, 0, "ふう…軍人たるもの\n常日頃、油断は\nできないのだが…\nお、落ち着くな…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105301, 1919, 1, 0, 3, 0, "うむ…このかわいさ…\n本国に持って\n帰りたい…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105301, 1920, 1, 0, 4, 0, "このともし火、戦いに\n明け暮れる我らの心を\n癒すものだな…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105301, 1921, 1, 0, 5, 0, "はあ～、ぽかぽか…\nい、いや！\n気を緩めないぞっ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105301, 1922, 1, 0, 6, 0, "うおおおおおっ！\n鍛錬、鍛錬、\n鍛錬っ！！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105301, 1923, 1, 0, 7, 0, "いつ戦いになっても\n困らぬように、\n腹八分目に\nしておくのだ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105301, 1924, 1, 0, 8, 0, "これを読んで、新たな\n戦術を編み出すぞっ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105302, 1926, 1, 0, 1, 0, "たまには掃除でも\nするか。部屋が\n綺麗になるのは\n喜ばしいことだ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105302, 1927, 1, 0, 2, 0, "部屋の中では\n剣を振り回せないのが\n玉に瑕だな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105302, 1928, 1, 0, 3, 0, "お菓子が常備されて\nいる部屋。そんな\nものが、私の好みだ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105302, 1929, 1, 0, 4, 0, "毎日牛乳を飲んでいる\nのだが、なかなか背が\n伸びないものだな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105302, 1930, 1, 0, 5, 0, "家庭を守れない者が\n国を守れるはずが\nない。この部屋も\n私が守るぞ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 105302, 1931, 1, 0, 101, 0, "あいつは今頃、何を\nしているのだろうな…\nふふっ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 105302, 1932, 1, 0, 102, 0, "あいつと会える日が\nこんなにも待ち遠しく\nなるとはな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105302, 1933, 1, 0, 1, 0, "軍人の本分は日々の\n研鑽にあるからな\n今日も鍛錬に\n行かないか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105302, 1934, 1, 0, 2, 0, "おお、今日も鍛錬の\n時間だったか！\nすまないな\nすぐ行く！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105302, 1935, 1, 0, 3, 0, "私は子供ではない！\nが…たまには童心に\n帰りたい時もある…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105302, 1936, 1, 0, 4, 0, "貴公にも、飴を\nくれてやろうか？\nあまいぞ～！\n美味しいぞ～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 105302, 1937, 1, 0, 101, 0, "私と歩きたいのか…？\nそうだな。二人で\n散歩でも行こうか", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 105302, 1938, 1, 0, 102, 0, "貴公と共にいられる\nことは私の喜びだ\nこれからも\nよろしく頼むぞ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105302, 1939, 1, 1, 1, 0, "うむ、いい朝だ\n本国の夜明けを\n思い出してしまうな！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105302, 1940, 1, 2, 2, 0, "そろそろ昼時だな\n鍛錬の手を緩めて\n昼食にするか", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105302, 1941, 1, 3, 3, 0, "寝る前に…あと少し\nだけ、鍛錬を…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105302, 1942, 1, 0, 1, 0, "ヴァイス\nフリューゲルの一員と\nして恥ずかしくないよう\n身なりを整えなくては", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105302, 1943, 1, 0, 2, 0, "ふう…軍人たるもの\n常日頃、油断は\nできないのだが…\nお、落ち着くな…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105302, 1944, 1, 0, 3, 0, "うむ…このかわいさ…\n本国に持って\n帰りたい…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105302, 1945, 1, 0, 4, 0, "このともし火\n戦いに明け暮れる\n我らの心を\n癒すものだな…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105302, 1946, 1, 0, 5, 0, "はあ～、ぽかぽか…\nい、いや！\n気を緩めないぞっ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105302, 1947, 1, 0, 6, 0, "うおおおおおっ！\n鍛錬、鍛錬\n鍛錬っ！！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105302, 1948, 1, 0, 7, 0, "いつ戦いになっても\n困らぬように\n腹八分目に\nしておくのだ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105302, 1949, 1, 0, 8, 0, "うむ…脳細胞が\n活性化してきたぞ\n読書は大事だな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105701, 1951, 1, 0, 1, 0, "私たちの騎空艇\nどんなデザインに\nしよう…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105701, 1952, 1, 0, 2, 0, "団員募集の\nポスター\n作ろうっと♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105701, 1953, 1, 0, 3, 0, "父さん…\n今頃どこで\n何してるんだろ？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105701, 1954, 1, 0, 4, 0, "騎空艇に乗って、\n誰もいったことがない\n場所を見つけたいな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105701, 1955, 1, 0, 5, 0, "父さん以上の\n世界一の\n騎空士になりたい！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 105701, 1956, 1, 0, 101, 0, "仲間と行く空の旅…\n何度想像しても\nドキドキするなぁ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 105701, 1957, 1, 0, 102, 0, "父さん…\n私にもね、大切な\n仲間ができたよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105701, 1958, 1, 0, 1, 0, "仕事の依頼？\nすぐに支度\nしてくるね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105701, 1959, 1, 0, 2, 0, "ねえねえ！\n団員募集のポスター\n一緒に作ろうよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105701, 1960, 1, 0, 3, 0, "なーにー？\nどうしたの？\n今呼んだよね？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105701, 1961, 1, 0, 4, 0, "今は休憩中だよ？\nだからあなたも\nゆっくりしてて", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 105701, 1962, 1, 0, 101, 0, "私にとってあなたは\nとっても頼りになる、\n自慢の団員だよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 105701, 1963, 1, 0, 102, 0, "お父さんに会えたら、\n大切な仲間として\nあなたを紹介するね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105701, 1964, 1, 1, 1, 0, "おはよう！\n体の調子はどう？\n今日は頑張れそう？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105701, 1965, 1, 2, 2, 0, "ねえねえ、お昼\n食べに行こうよ！\nお腹空いちゃった", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105701, 1966, 1, 3, 3, 0, "…あれ？\nおしゃべりしてたら\nもうこんな時間？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105701, 1967, 1, 0, 1, 0, "どうせならオシャレな\n騎空士って\n呼ばれたいな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105701, 1968, 1, 0, 2, 0, "ふぅ、疲れた～…\nちょっとだけ\n休んじゃおっと", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105701, 1969, 1, 0, 3, 0, "よし！　キミは\n今から団長代理だ！\n…なんてね♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105701, 1970, 1, 0, 4, 0, "いい香り～♪\n気持ちが\n落ち着く～…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105701, 1971, 1, 0, 5, 0, "これって\n騎空艇に\n置けるかな…？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105701, 1972, 1, 0, 6, 0, "どんな仕事でも\nこなせるような\n体力がないとね…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105701, 1973, 1, 0, 7, 0, "あむっ…うん！\n美味しい～！\n今度作ってみよう♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105701, 1974, 1, 0, 8, 0, "空を冒険するには\n色んなことを\n知っておかないと…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105702, 1976, 1, 0, 1, 0, "…戻らなくなったよ\nどうすれば元に\n戻る…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105702, 1977, 1, 0, 2, 0, "暇だよ…\nどこかに強い人は\nいないか？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105702, 1978, 1, 0, 3, 0, "ジョブチェンジ！\n…叫んでも\n戻らないよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105702, 1979, 1, 0, 4, 0, "今のうちに、\n魔物退治の\n仕事に行くね！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105702, 1980, 1, 0, 5, 0, "ハァ～…\nうん、気の巡りは\n問題ないね！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 105702, 1981, 1, 0, 101, 0, "私の力の源は\n仲間との\n絆だよ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 105702, 1982, 1, 0, 102, 0, "いつかこの拳を\n空を越えた、星まで\n届かせたいね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105702, 1983, 1, 0, 1, 0, "功夫が足りない人は\nどうすればいいか？\nもちろん修行だよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105702, 1984, 1, 0, 2, 0, "私はいつも通りに\n喋ってるつもり\nだよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105702, 1985, 1, 0, 3, 0, "どうした？\nもしかして手合せ\nしたいか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105702, 1986, 1, 0, 4, 0, "騎空士としても\n拳法家としても\n目指すは世界一ね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 105702, 1987, 1, 0, 101, 0, "背中はあなたに\n預けたよ！\nよろしくね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 105702, 1988, 1, 0, 102, 0, "あなたと一緒なら\nきっと、今よりもっと\n強くなれるよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105702, 1989, 1, 1, 1, 0, "ちゃんと起きてるか？\n寝ぼけてたら\nダメよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105702, 1990, 1, 2, 2, 0, "腹減ったね…\nなにか食べに\n行くよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105702, 1991, 1, 3, 3, 0, "修行のしすぎは\n良くないよ？\n気をつけるね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105702, 1992, 1, 0, 1, 0, "美しい技は\n美しい体が\nあってこそ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105702, 1993, 1, 0, 2, 0, "よいしょっと…\nちょっとのんびり\nさせてもらうよ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105702, 1994, 1, 0, 3, 0, "…練習に\n使うのはさすがに\n可哀そうね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105702, 1995, 1, 0, 4, 0, "これは…\n瞑想の時にも\n使ってみたいね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105702, 1996, 1, 0, 5, 0, "出来ればもっと\n熱いほうが\n嬉しいよ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105702, 1997, 1, 0, 6, 0, "ここでも\n修行出来るなんて\n最高だよ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105702, 1998, 1, 0, 7, 0, "気力の源は体力！\n体力の源は\n健康とご飯！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105702, 1999, 1, 0, 8, 0, "んー…\n難しいことは\nわかんないよ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105801, 2001, 1, 0, 1, 0, "わたしたちの\nギルドハウスへ\nようこそ～！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105801, 2002, 1, 0, 2, 0, "ぐるるる～…\nあ、お腹が\n鳴っちゃいました…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105801, 2003, 1, 0, 3, 0, "ん？\n食べ物のにおい？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105801, 2004, 1, 0, 4, 0, "みんなと仲良く食卓を\n囲みたいですね～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105801, 2005, 1, 0, 5, 0, "この部屋が、わたしの\n帰る場所です♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 105801, 2006, 1, 0, 101, 0, "あの人を見ると\n胸が満たされます～\nお腹は空いてますが！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 105801, 2007, 1, 0, 102, 0, "あの人、\n大食いの子って\n嫌いかな？", 1);
