INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106002, 2097, 1, 0, 6, 0, "特訓特訓！\nあたしは\nやればできる！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106002, 2098, 1, 0, 7, 0, "おいしいものを\nたくさん食べる…\nギルド活動って\n楽しい～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106002, 2099, 1, 0, 8, 0, "魔法を使うにも\n知識が必要だものね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105901, 2101, 1, 0, 1, 0, "この空間は主さまの\n香りがします\n…ふふふ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105901, 2102, 1, 0, 2, 0, "炊事、洗濯、お掃除、\nお買いものもしなくて\nはいけませんね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105901, 2103, 1, 0, 3, 0, "アメスさまへの、\nお祈りができそうな\n場所は…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105901, 2104, 1, 0, 4, 0, "さすが都会…\nおいしいものが\nたくさんあるのですね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105901, 2105, 1, 0, 5, 0, "主さまが不自由なく\n使える部屋に\nしなくては…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 105901, 2106, 1, 0, 101, 0, "できるなら…\n主さまと二人だけの\n部屋の方が…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 105901, 2107, 1, 0, 102, 0, "主さまの隣には\nわたくしが…\nわたくしの隣には、\n主さまが…幸せです", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105901, 2108, 1, 0, 1, 0, "わたくし、背が伸び\nましたでしょうか？\n…そうですか", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105901, 2109, 1, 0, 2, 0, "もしお疲れでしたら\n肩でも\nお揉みしましょうか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105901, 2110, 1, 0, 3, 0, "主さま、なにか\nしてほしいことは\nありませんか？\n欲しいものは？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105901, 2111, 1, 0, 4, 0, "今日のおこづかいです\n何かおいしいものを\n召し上がってください", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 105901, 2112, 1, 0, 101, 0, "主さまのおてて\n温かいですね…♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 105901, 2113, 1, 0, 102, 0, "主さまのおそばに\nいられて、わたくし、\n嬉しいです…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105901, 2114, 1, 1, 1, 0, "おはようございます。\n洗濯をしなければ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105901, 2115, 1, 2, 2, 0, "そろそろお昼ですね。\nおいしいものを\n食べましょう", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105901, 2116, 1, 3, 3, 0, "おやすみになる時は、\nお声をかけてください\nお供致します", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105901, 2117, 1, 0, 1, 0, "もう少し…\nおっきくなりたい\nです", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105901, 2118, 1, 0, 2, 0, "このふかふかの優しさ\n主さまのようです", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105901, 2119, 1, 0, 3, 0, "かわいい♪\n主さまにそっくり♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105901, 2120, 1, 0, 4, 0, "故郷の香りとは\n違いますが、これも\n心地よいものです…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105901, 2121, 1, 0, 5, 0, "ここで洗濯物を\n乾かせるでしょうか", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105901, 2122, 1, 0, 6, 0, "都会の人々は\nこんなふうに\n運動を…！？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105901, 2123, 1, 0, 7, 0, "これは…主さまにも\n食べさせて\n差し上げたい…\nあーんって♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105901, 2124, 1, 0, 8, 0, "よく学んで、\n主さまのお役に\n立たなければ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105902, 2126, 1, 0, 1, 0, "主さまがいつ戻って\n来ても平気なよう\n部屋を綺麗に保たねば", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105902, 2127, 1, 0, 2, 0, "節約のためにも\n自家菜園などするのは\nどうでしょう…　", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105902, 2128, 1, 0, 3, 0, "アメスさまへの\nお祈りができそうな\n場所は…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105902, 2129, 1, 0, 4, 0, "瞑想をすると\n心が安らぎます…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105902, 2130, 1, 0, 5, 0, "都会の方の身なりを\n勉強した方が\n良いのでしょうか？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 105902, 2131, 1, 0, 101, 0, "わたくしの居場所は\n主さまのお傍です\nずっと、ずっと…♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 105902, 2132, 1, 0, 102, 0, "主さまの隣には\nわたくしが…\nわたくしの隣には\n主さまが…幸せです", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105902, 2133, 1, 0, 1, 0, "主さま、なにか\nしてほしいことは\nありませんか？\n欲しいものは？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105902, 2134, 1, 0, 2, 0, "わたくし、背が伸び\nましたでしょうか？\n…そうですか", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105902, 2135, 1, 0, 3, 0, "お出かけですか？\nわたくしもお供\nいたします", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105902, 2136, 1, 0, 4, 0, "ひゃん！\n主さま…\nいじわるです", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 105902, 2137, 1, 0, 101, 0, "主さま、なにか御用は\nありませんか？\nわたくし、主さまの\nお役にたちたいのです", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 105902, 2138, 1, 0, 102, 0, "主さまの優しさに\nわたくしはいつも\n癒されています…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105902, 2139, 1, 1, 1, 0, "おはようございます♪\n主さまの可愛らしい\n寝顔、堪能致しました", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105902, 2140, 1, 2, 2, 0, "のんびりした時間帯\nですね…ふぁ～…\nし、失礼致しました", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105902, 2141, 1, 3, 3, 0, "ふむ、都会は夜でも\nこんなにも、にぎやか\nなのですね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105902, 2142, 1, 0, 1, 0, "主さまに会う前に\n身なりを整えなくては\nいけませんね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105902, 2143, 1, 0, 2, 0, "このふかふかの優しさ\n主さまのようです", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105902, 2144, 1, 0, 3, 0, "かわいい♪\n主さまにそっくり♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105902, 2145, 1, 0, 4, 0, "この心地よさ…\n主さまのようです♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105902, 2146, 1, 0, 5, 0, "ここで洗濯物を\n乾かせるでしょうか　", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105902, 2147, 1, 0, 6, 0, "はぁっはぁっはぁっ\n…すべては\n主さまのため…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105902, 2148, 1, 0, 7, 0, "都会の味…\nとてもおいしいです\n…もぐもぐもぐ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105902, 2149, 1, 0, 8, 0, "都会のことを\n学ばないと…\nふむふむ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 190801, 2151, 1, 0, 1, 0, "ふぁ～…あっ\n見られちゃい\nましたか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 190801, 2152, 1, 0, 2, 0, "ギルドハウスの\n使い心地は\nどうですか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 190801, 2153, 1, 0, 3, 0, "ご用があれば\nなんでも言って\nくださいね～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 190801, 2154, 1, 0, 4, 0, "ときどきみなさんと\nクエストに行きたいな\nって思っちゃいます", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 190801, 2155, 1, 1, 1, 0, "おはようございます！\nお掃除の方、\nはじめていきますね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 190801, 2156, 1, 2, 2, 0, "皆さん、お昼ごはんの\nご要望がありましたら\n用意致しますよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 190801, 2157, 1, 3, 3, 0, "ここは夜でも\nにぎやかですね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104701, 2158, 1, 0, 1, 0, "…\n少しうとうと\nしてしまった", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104701, 2159, 1, 0, 2, 0, "室内だし兜を脱ぐか\n…いや\nやはりやめておこう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104701, 2160, 1, 0, 3, 0, "部屋にいると\n鎧が蒸し暑くなるが\n我慢せねば…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104701, 2161, 1, 0, 4, 0, "仕事が門番だからか\n部屋にいても\nじっとしてしまう…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104701, 2162, 1, 0, 5, 0, "少年と会話したことを\n思いだすと\n自然と楽しくなるな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 104701, 2163, 1, 0, 101, 0, "少年のことを考えると\n顔がにやけてしまう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104701, 2164, 1, 0, 102, 0, "少年は今頃\n平和に過ごせて\nいるかな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104701, 2165, 1, 0, 1, 0, "ん？　悪いが\n兜は脱がないぞ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104701, 2166, 1, 0, 2, 0, "私と遊びたいのか？\nそう言われても\n何をしていいやら…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104701, 2167, 1, 0, 3, 0, "鎧が重くないかって？\n大丈夫。けっこう\n鍛えているからな", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104701, 2168, 1, 0, 4, 0, "少年、寒くはないか？\nいや自分がこうなので\n人が薄着に見えてね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 104701, 2169, 1, 0, 101, 0, "君といると、兜の中が\nいつもより暑いんだ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104701, 2170, 1, 0, 102, 0, "私としゃべりたい？\n物好きだな\nでも、嬉しいよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104701, 2171, 1, 1, 1, 0, "早いな少年\nえ？　私の仕事に\n合わせてくれたのか", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104701, 2172, 1, 2, 2, 0, "昼食の誘い？\n嬉しいけど\n人前では食べないんだ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104701, 2173, 1, 3, 3, 0, "王宮騎士団の\n日報を書かなければ…\n『何事もなし』、と", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104701, 2175, 1, 0, 2, 0, "鎧を着ているから\n感触が分かりづらい\nのが残念だ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104701, 2176, 1, 0, 3, 0, "か、かわいいな…\n私には似合わないが\nぎゅ…ってしよう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104701, 2177, 1, 0, 4, 0, "良い香りだ…\n普段はどうしても\n鉄くさくなるからな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104701, 2178, 1, 0, 5, 0, "わかっていたけど\n鎧を着たまま暖炉に\nあたると暑い…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104701, 2180, 1, 0, 7, 0, "兜を脱がずに食べる\nうん、段々コツが\n掴めてきた…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104701, 2181, 1, 0, 8, 0, "兜があっても\n意外と本くらいは\n難なく読めるんだ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102601, 2183, 1, 0, 1, 0, "居心地よくて\nゴロゴロしちゃうよ～\n…いつも通りだね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102601, 2184, 1, 0, 2, 0, "ドングリの木\n近くに生えてない？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102601, 2185, 1, 0, 3, 0, "快適な寝床があれば\nそれだけで幸せだねぇ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102601, 2186, 1, 0, 4, 0, "あたしと一緒に\nだらけてくれる人が\nいそうだねぇ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102601, 2187, 1, 0, 5, 0, "ここなら牧場よりも\n速く配達が\n届きそうだね～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 102601, 2188, 1, 0, 101, 0, "あんパン買ったし\nあいつにも\n分けてあげよう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102601, 2189, 1, 0, 102, 0, "あいつと一緒に\nだらけられたら\n最高に幸せだね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102601, 2190, 1, 0, 1, 0, "ドングリでも\nつまみながら\n一緒にゲームしようよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102601, 2191, 1, 0, 2, 0, "横になりたいから\n枕になっておくれよ～\nあんたあったかいしさ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102601, 2192, 1, 0, 3, 0, "ドングリあげるから\nあんパン買ってきて～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102601, 2193, 1, 0, 4, 0, "う～\n疲れちゃったよ～\nおんぶして運んで～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 102601, 2194, 1, 0, 101, 0, "あんたといると\nリア充って\n感じがするよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 102601, 2195, 1, 0, 102, 0, "あんたが一生\n世話してくれたら\n人生安泰なんだけどね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102601, 2196, 1, 1, 1, 0, "ふわ～っ…\nおはよう…あたしは\nまだ眠いよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102601, 2197, 1, 2, 2, 0, "そろそろお昼寝でも\nしたい気分だねぇ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102601, 2198, 1, 3, 3, 0, "徹夜でゲームするけど\nあんたもどうだい？\nあたしは強いよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102601, 2200, 1, 0, 2, 0, "あ…だめだこれ。\n気持ち良くて\n落ちそう…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102601, 2201, 1, 0, 3, 0, "かわいいものに\n囲まれて寝るのも\nよさそうだねぇ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102601, 2202, 1, 0, 4, 0, "心身ともに\nリラックスした状態で\nぐうたら出来そうだね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102601, 2203, 1, 0, 5, 0, "ふい～…\nごくらくごくらく…\n暖かいねぇ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102601, 2205, 1, 0, 7, 0, "食べてすぐ寝ると\n牛になるって\n本当なのかな？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 102601, 2206, 1, 0, 8, 0, "おー！　この本なら\nあたしも居眠りせずに\n読めそうだよ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106301, 2208, 1, 0, 1, 0, "ランドソル…\nおっきな街だなぁ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106301, 2209, 1, 0, 2, 0, "ロザリア…\nきっと、いるよね？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106301, 2210, 1, 0, 3, 0, "最近、弓の練習\nサボっちゃってるな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106301, 2211, 1, 0, 4, 0, "今日の私は\n森番じゃなくて\n家番だよ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106301, 2212, 1, 0, 5, 0, "ちょっとだけ\n慣れてきたかも？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 106301, 2213, 1, 0, 101, 0, "いつか、\nあの人と一緒に…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 106301, 2214, 1, 0, 102, 0, "…なんか最近、\nあの人のこと\nばっかり…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106301, 2215, 1, 0, 1, 0, "もしよかったら\n街に出かけない？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106301, 2216, 1, 0, 2, 0, "この部屋、\n妖精さんも\n気に入るかも！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106301, 2217, 1, 0, 3, 0, "今日は\nなににしよっか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106301, 2218, 1, 0, 4, 0, "今日はどこ行く？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 106301, 2219, 1, 0, 101, 0, "たまには二人で…\nな、なんちゃって！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 106301, 2220, 1, 0, 102, 0, "最近、\nずっと楽しいんだ！\nあなたのおかげかな", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106301, 2221, 1, 1, 1, 0, "おはよう！\n今日も一緒に\n頑張ろうね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106301, 2222, 1, 2, 2, 0, "お昼はどうする？\nあなたのオススメに\n連れて行って欲しいな", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106301, 2223, 1, 3, 3, 0, "ランドソルは\n夜でも賑やかだね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106301, 2225, 1, 0, 2, 0, "わぁっ！\nすっごく\nふわふわだよ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106301, 2226, 1, 0, 3, 0, "かわい～！\nぎゅってしたく\nなっちゃうかも！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106301, 2227, 1, 0, 4, 0, "くんくん\nなんか…いい匂い？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106301, 2228, 1, 0, 5, 0, "すっごいなー\nあったかいなー！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106301, 2230, 1, 0, 7, 0, "わぁっ、おいしい！\nもっと食べたい\nかも…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106301, 2231, 1, 0, 8, 0, "も、文字は苦手…\nでも、頑張らないと\nだよね…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103601, 2233, 1, 0, 1, 0, "にんじんとピーマンを\n食べないでいい方法\nないかな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103601, 2234, 1, 0, 2, 0, "カップケーキが\n食べたいな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103601, 2235, 1, 0, 3, 0, "予習が終わりました！\nさて、次は復習を\nしないと…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103601, 2236, 1, 0, 4, 0, "あの人の部屋にいる\n私って…もしかして\nふしんしゃなんじゃ？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103601, 2237, 1, 0, 5, 0, "男の人は、みんな\nへんたいだと\n聞きました…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 103601, 2238, 1, 0, 101, 0, "あの人に、一緒に\n遊んでほしいけど…\nな、なんていえば？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 103601, 2239, 1, 0, 102, 0, "早く大人になりたいな\n…あの人と同じくらい", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103601, 2240, 1, 0, 1, 0, "ぼ、防犯魔石を\n鳴らしますよ！？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103601, 2241, 1, 0, 2, 0, "へんたい！？　…\nあ、あなたでしたか～\nやっぱりへんたーい！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103601, 2242, 1, 0, 3, 0, "これ宿題です\nやっておいて\nくださいね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103601, 2243, 1, 0, 4, 0, "あ、遅刻ですよ\n待っていたんですから", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 103601, 2244, 1, 0, 101, 0, "にらめっこですか？\nあ、あなたの前で変顔\nするのはちょっと…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 103601, 2245, 1, 0, 102, 0, "あの…何歳まで\n子供だと思いますか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103601, 2246, 1, 1, 1, 0, "顔を洗って、着替えて\n朝ごはんを食べて、\n歯を磨いて…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103601, 2247, 1, 2, 2, 0, "て、鉄棒の練習！？　\nほ、他のことを\nしませんか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103601, 2248, 1, 3, 3, 0, "ね、眠くないです\n子供ではありません\nから…ふわぁ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103601, 2250, 1, 0, 2, 0, "ひと休みしよっと\n………うん、\nひと休みおしまい♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103601, 2251, 1, 0, 3, 0, "つ、つぶらな瞳には\n弱いんです", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103601, 2252, 1, 0, 4, 0, "リラックスできますね\n集中力も\n上がりそうです", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103601, 2253, 1, 0, 5, 0, "あったかいと\n眠くなっちゃうな…\n勉強しなくちゃなのに", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103601, 2255, 1, 0, 7, 0, "ピーマンもこれくらい\nおいしければ\nいいのに…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103601, 2256, 1, 0, 8, 0, "この本、むずかしい\n…もっと勉強\nがんばらなくちゃ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101501, 2258, 1, 0, 1, 0, "今日はみんなに\nなにを読んで\nあげようかしら", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101501, 2259, 1, 0, 2, 0, "子どもたちと同じ\n目線で考えるのが\n大事よね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101501, 2260, 1, 0, 3, 0, "お天気が良い日は\nみんなでお散歩に\n行きたいわね～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101501, 2261, 1, 0, 4, 0, "鬼ごっこにかくれんぼ\n…みんなは\nどっちが好きかしら", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101501, 2262, 1, 0, 5, 0, "お遊戯会の配役、\n考え出すと\n大変だわ～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 101501, 2263, 1, 0, 101, 0, "彼は優しいから、\nきっと良い\nお父さんになるわね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 101501, 2264, 1, 0, 102, 0, "彼には私がいないと\n…っていうのは少し\n過保護すぎるかしら？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101501, 2265, 1, 0, 1, 0, "保育園の行事を\n考えてるんだけど…\n良い案ないかしら？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101501, 2266, 1, 0, 2, 0, "今度、子どもたちと\nおやつを作るから、\n食べに来てね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101501, 2267, 1, 0, 3, 0, "よかったら一緒に\n絵本を作らない？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101501, 2268, 1, 0, 4, 0, "あなたはいつも元気ね\n先生も見習わないと♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 101501, 2269, 1, 0, 101, 0, "先生は\nずーっと、ずーっと、\nあなたの味方だからね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 101501, 2270, 1, 0, 102, 0, "あなたみたいな\nいい子をお世話できて\n先生は幸せものだわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101501, 2271, 1, 1, 1, 0, "おはよう～\nちゃんと朝ごはんは\n食べた？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101501, 2272, 1, 2, 2, 0, "お昼寝の時間ね♪\nあなたも一緒に\n寝ましょうか", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101501, 2273, 1, 3, 3, 0, "すっかり真っ暗ね。\n今日も一日、\nよくできました♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101501, 2275, 1, 0, 2, 0, "ふかふかねぇ♪\n年甲斐もなく、\n飛び跳ねちゃいそう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101501, 2276, 1, 0, 3, 0, "あらあら、かわいい！\n保育園のみんなに\n見せてあげたいわ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101501, 2277, 1, 0, 4, 0, "いい香りね～\n保育園にも\n置こうかしら", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101501, 2278, 1, 0, 5, 0, "寒い日は、ここで\n子どもたちに絵本を\n読んであげましょ♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101501, 2280, 1, 0, 7, 0, "美味しい～♪\n遠足に持っていったら\nみんな喜びそう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101501, 2281, 1, 0, 8, 0, "みんなが\n楽しめる読み聞かせの\n方法特集♪　素敵ねぇ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104401, 2283, 1, 0, 1, 0, "世界の前に、\nこの部屋を征服\nしてやろう♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104401, 2284, 1, 0, 2, 0, "拠点とはやはり\n斯くあるべきじゃの", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104401, 2285, 1, 0, 3, 0, "眷属の物はわらわの物\n故に、この部屋は\nわらわの部屋じゃな！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104401, 2286, 1, 0, 4, 0, "永く眠っている間に\n随分と世界は変わった\nようじゃな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104401, 2287, 1, 0, 5, 0, "うるさいのう～\nまったく、近頃の\n若いもんは…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 104401, 2288, 1, 0, 101, 0, "あやつが来てから\n動悸が治まらぬ…\n如何ような病で\nあろうかの…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104401, 2289, 1, 0, 102, 0, "眷属よ、どこに行った\nのじゃ？\nわらわの世話を\n許可するのじゃ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104401, 2290, 1, 0, 1, 0, "いずれ世界は\n我が手中に収まる\nふふふ、\n楽しみじゃのう", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104401, 2291, 1, 0, 2, 0, "眷属よ、わらわから\n離れてはならんぞ！\n子供に戻ってしまう\nではないか！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104401, 2292, 1, 0, 3, 0, "トマトジュースが\n欲しいのじゃ！\n野菜ジュースでは\nダメだぞ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104401, 2293, 1, 0, 4, 0, "蚊にくわれたぞ！\nわらわの血を吸う\nとは～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 104401, 2294, 1, 0, 101, 0, "こら、頭をなでるな\nお主はこの姿でも\n子供扱いするのか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 104401, 2295, 1, 0, 102, 0, "お主が望むなら世界の\n半分をくれてやっても\n良いのじゃぞ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104401, 2296, 1, 1, 1, 0, "朝早くから感心じゃの\nさすがわらわの\n眷属じゃ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104401, 2297, 1, 2, 2, 0, "太陽は苦手じゃ…\nこの部屋の中で\n過ごそうぞ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104401, 2298, 1, 3, 3, 0, "夜も更けてきた！\nわらわ達の時間は\nこれからじゃ～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104401, 2300, 1, 0, 2, 0, "よい座り心地じゃ\nわらわの玉座としよう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104401, 2301, 1, 0, 3, 0, "可愛いではないか\nわらわの眷属に\nしてやろうかのう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104401, 2302, 1, 0, 4, 0, "うむ、良い気持ちじゃ\nこれを作った者、\n褒めてつかわすぞ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104401, 2303, 1, 0, 5, 0, "あたたかい…\n身も心も\n浄化されるようじゃ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104401, 2305, 1, 0, 7, 0, "吸血鬼のわらわが、\nこのようなもの…\nうまい♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 104401, 2306, 1, 0, 8, 0, "このまま大人の姿を\n保つ方法…どこかに\n書いてないかのう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105401, 2308, 1, 0, 1, 0, "いろんな人がいて\nいろんな服を着てて…\n目の保養になります♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105401, 2309, 1, 0, 2, 0, "レイ様への新作\nどんな服にしよう…\n方向性が絞れない～！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105401, 2310, 1, 0, 3, 0, "忘れないうちに\n隠し糸のメンテナンス\nやっておこうっと！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105401, 2311, 1, 0, 4, 0, "むっ！　あの人の\nスリーサイズは…\nほほう…なるほど♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105401, 2312, 1, 0, 5, 0, "ノゾミさんの\n衣装はああして\nチカさんのは…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 105401, 2313, 1, 0, 101, 0, "私がアイドル…\n…実感ないけど\n精一杯やってみよう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 105401, 2314, 1, 0, 102, 0, "まさか私が\nアイドルになるなんて\n思ってなかったです", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105401, 2315, 1, 0, 1, 0, "なんですかぁ？\n衣装作りで疲れて\n眠いんですぅ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105401, 2316, 1, 0, 2, 0, "あーっ！　また\n服がほつれてる…\nしょうがないですね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105401, 2317, 1, 0, 3, 0, "はっ！　ちょうど今\n新しいデザインが\n思い浮かびました！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105401, 2318, 1, 0, 4, 0, "暇そうですね？\n時間あるなら仕事\n手伝ってください！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 105401, 2319, 1, 0, 101, 0, "どうしました？\n新しい服が\n欲しくなりましたか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 105401, 2320, 1, 0, 102, 0, "騎士さんのおかげで\nカルミナでも\n頑張れてますよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105401, 2321, 1, 1, 1, 0, "おはようございます…\nなんで朝って毎日\nやってくるんでしょう", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105401, 2322, 1, 2, 2, 0, "お腹空きましたね\n騎士さんはちゃんと\n御飯食べましたか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105401, 2323, 1, 3, 3, 0, "夜の方が\n作業が捗るのは\n私だけですかね？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105401, 2325, 1, 0, 2, 0, "このふかふか具合\nいい素材を\n使ってますね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105401, 2326, 1, 0, 3, 0, "小さくて可愛い♪\nそうだ！　レイ様の\nぬいぐるみを…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105401, 2327, 1, 0, 4, 0, "こういう時くらい\n何も考えずに\nリラックスしないと♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105401, 2328, 1, 0, 5, 0, "炎を使った服って\nカッコ良いかも…\nでも材料がな～…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105401, 2330, 1, 0, 7, 0, "何をするにも\n食事は大切！\n体力が資本です！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105401, 2331, 1, 0, 8, 0, "私もいつか\nデザインを\nまとめて本に…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107501, 2333, 1, 0, 1, 0, "またお魚を\n捕りにいこうかな♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107501, 2334, 1, 0, 2, 0, "そろそろみんなも\nお腹が空くころ\nじゃないですか～？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107501, 2335, 1, 0, 3, 0, "ここなら、\n日焼けの心配は\nありませんね！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107501, 2336, 1, 0, 4, 0, "お刺身に焼き貝…\nお腹が魚介類を\n求めてますっ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107501, 2337, 1, 0, 5, 0, "泳ぐのも食べるのも\n大得意です！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 107501, 2338, 1, 0, 101, 0, "あの人と二人で\n浜辺を散歩…\n素敵ですねぇ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 107501, 2339, 1, 0, 102, 0, "あの人に水着を\n褒めてもらえたら、\nすごく嬉しいなぁ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107501, 2340, 1, 0, 1, 0, "たまには釣りじゃなく\n一緒に素潜りでも\n行きませんか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107501, 2341, 1, 0, 2, 0, "男の子に水着を\n見られるのって\n少し緊張しちゃいます", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107501, 2342, 1, 0, 3, 0, "ギルドハウスの\nみんなとも、\n海に行きたいですね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107501, 2343, 1, 0, 4, 0, "あなたの水着、\n私が選んであげます！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 107501, 2344, 1, 0, 101, 0, "泳ぎの練習なら\n付き合いますよ！\n手取り足取り♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 107501, 2345, 1, 0, 102, 0, "水着姿が好きなら\nいくらでも\n着てあげますよぉ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107501, 2346, 1, 1, 1, 0, "朝の海も\n幻想的で\nいいですよねぇ～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107501, 2347, 1, 2, 2, 0, "お昼はみんなで、\n海鮮バーベキューとか\nどうでしょう！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107501, 2348, 1, 3, 3, 0, "夜の海は星が\nきれいですよ！\n見に行きましょう！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107501, 2350, 1, 0, 2, 0, "泳ぎ疲れたので\nちょっと休憩です～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107501, 2351, 1, 0, 3, 0, "あなたにも、\n可愛い水着を\n着せてあげたいです", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107501, 2352, 1, 0, 4, 0, "なんだか、\nリゾート気分です…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107501, 2353, 1, 0, 5, 0, "ふい～、泳ぎで冷えた\n身体を暖めましょう～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107501, 2355, 1, 0, 7, 0, "おいひ～です～\nこれ、海水浴にも\n持っていきたいですね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107501, 2356, 1, 0, 8, 0, "南国のフルーツ図鑑\n…じゅるり…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107501, 2358, 1, 0, 10, 0, "わ～♪　おいしそ…\nか、かわいいですね～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107501, 2359, 1, 0, 11, 0, "これくらい大きな貝…\n食べたいな～\n食べたいな～～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107601, 2360, 1, 0, 1, 0, "この格好で\n部屋をうろつくのは\n不思議な感じがします", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107601, 2361, 1, 0, 2, 0, "南国の果実を使って\n主さまにデザートを\nお作りしましょう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107601, 2362, 1, 0, 3, 0, "せっかくの水着ですし\nこれから定期的に\n海水浴に行きたいです", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107601, 2363, 1, 0, 4, 0, "海の生き物も、\n森の生き物に負けず\n可愛かったです…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107601, 2364, 1, 0, 5, 0, "クロールの練習なら、\n部屋でもできそうです", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 107601, 2365, 1, 0, 101, 0, "主さまに、水着姿を\n褒めていただけた…\nえへへ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 107601, 2366, 1, 0, 102, 0, "水着で主さまの\nお世話をしたら、\n喜びますでしょうか…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107601, 2367, 1, 0, 1, 0, "お疲れでしたら\nわたくしの膝を\nお使いくださいね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107601, 2368, 1, 0, 2, 0, "主さま、海の家で\nつかうおこづかいを\n渡しておきますね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107601, 2369, 1, 0, 3, 0, "海の美しさは、\n森とはちがう\n魅力がありました", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107601, 2370, 1, 0, 4, 0, "この髪飾りですか？\n造花ですから\n水の中も平気です", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 107601, 2371, 1, 0, 101, 0, "水着でくっついて\n眠ると、いつもより\n密着して暖かいです", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 107601, 2372, 1, 0, 102, 0, "はじめての海が、\n主さまと一緒で\n幸せです♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107601, 2373, 1, 1, 1, 0, "おはようございます。\n泳ぎに行く準備は\n万端です", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107601, 2374, 1, 2, 2, 0, "午後は泳ぐのをやめて\nスイカ割りなど\nいかがでしょう？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107601, 2375, 1, 3, 3, 0, "夜も更けてきました…\n波の音を聞きながら\n少しお話しませんか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107601, 2377, 1, 0, 2, 0, "あぅ…この家具には、\n人をダメにする魔法が\nかけられています…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107601, 2378, 1, 0, 3, 0, "ふふっ♪\nあなたも、海水浴に\n行きますか？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107601, 2379, 1, 0, 4, 0, "この香り…\nきっと主さまも\n気に入ります", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107601, 2380, 1, 0, 5, 0, "これは暖かいです…\n後ほど主さまと一緒に\nあたりましょう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107601, 2382, 1, 0, 7, 0, "泳いだ後の食事は\n一段と美味しく\n感じます", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107601, 2383, 1, 0, 8, 0, "深海魚の生態…\nこれは主さまにも\nお伝えしなくては…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107601, 2385, 1, 0, 10, 0, "顔立ちが、主さまに\n似ております…\nかわいらしい♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107601, 2386, 1, 0, 11, 0, "ふかふかですね\n主さまのようです♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107701, 2387, 1, 0, 1, 0, "お掃除、お掃除\nふんふふ～ん☆\nあ、聞かれてました？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107701, 2388, 1, 0, 2, 0, "日焼けには回復魔法が\n効きますよ。どうして\n逃げるんですか～！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107701, 2389, 1, 0, 3, 0, "今日はドジを\nしませんように！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107701, 2390, 1, 0, 4, 0, "接客業を極めれば\nメイドとして\n成長できるかも？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107701, 2391, 1, 0, 5, 0, "水着って\n動きやすいんですけど\n人の目が気になります", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 107701, 2392, 1, 0, 101, 0, "あの人の気持ちを知る\n魔法とかあれば…\nな、なんでもないです", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 107701, 2393, 1, 0, 102, 0, "水着でご奉仕したら、\nあの人は喜ぶかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107701, 2394, 1, 0, 1, 0, "暑いのでしたら、\n私が魔法で涼しく…\nえ？　いらない？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107701, 2395, 1, 0, 2, 0, "何か御用ですか？\n特にないのでしたら、\nお話ししましょう♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107701, 2396, 1, 0, 3, 0, "水着姿でも\nメイドはメイドです\nご奉仕しますよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107701, 2397, 1, 0, 4, 0, "ひゃっ！　あ、危うく\n転ぶところでした…\n…きゃうっ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 107701, 2398, 1, 0, 101, 0, "どうかしましたか？\nあなたの御用なら、\n喜んでお受けしますよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 107701, 2399, 1, 0, 102, 0, "水着姿をあなたに\n見られると、\nドキドキしちゃいます", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107701, 2400, 1, 1, 1, 0, "朝は暑くてやる気が\n出ない？　でしたら、\nえぇい、元気になぁれ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107701, 2401, 1, 2, 2, 0, "お腹がぺこぺこなら、\nお昼は海の家で\n食べましょう♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107701, 2402, 1, 3, 3, 0, "夜の浜辺でお散歩も\nいいですね。たくさん\n転びそうですけど…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107701, 2404, 1, 0, 2, 0, "休憩しながら、\nお掃除すれば\n一石二鳥です！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107701, 2405, 1, 0, 3, 0, "きれいにすれば\nかわいさも倍増\nですよ～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107701, 2406, 1, 0, 4, 0, "素敵な香りです～\n救護院に置けば、\nみんな喜ぶかも～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107701, 2407, 1, 0, 5, 0, "あったかいですね…\nはっ！　いけません！\nメイドがサボるなんて", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107701, 2409, 1, 0, 7, 0, "料理のできない\nメイドなんて…\nはぐはぐ、おいしい♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107701, 2410, 1, 0, 8, 0, "水着姿で接客をすると\n売り上げが倍になる\n…本当ですか、これ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107701, 2412, 1, 0, 10, 0, "えへへ♪\n南の島まで\n乗せてください！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107701, 2413, 1, 0, 11, 0, "なんだか、\n人魚姫に\nなった気分です♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107801, 2414, 1, 0, 1, 0, "みんなで過ごす\n休日っていいわよね…\nずっと続かないかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107801, 2415, 1, 0, 2, 0, "海に浮輪でぷかぷか\n浮かぶのって\n癖になりそう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107801, 2416, 1, 0, 3, 0, "ビーチにいると\n日焼けが心配だわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107801, 2417, 1, 0, 4, 0, "水着姿って楽よね。\n室内だとちょっと\n落ち着かないけど…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107801, 2418, 1, 0, 5, 0, "お、泳ぎの練習なんて\nしないからね！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 107801, 2419, 1, 0, 101, 0, "楽しすぎて立場を\n忘れそう…あんたの\nせいなんだから！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 107801, 2420, 1, 0, 102, 0, "みんなで遊んで\n美味しいもの食べて\nほんっとに楽しいっ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107801, 2421, 1, 0, 1, 0, "マジであっついわね…\nよっし！　海に入って\n涼みましょ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107801, 2422, 1, 0, 2, 0, "シャワー浴びたばかり\nなのに…また、遊び\nたくなってきたわ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107801, 2423, 1, 0, 3, 0, "遊び足りないって？\nしょうがないわね…\n付き合ってあげるわ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107801, 2424, 1, 0, 4, 0, "一応伝えておくけど\n猫って水が苦手なの！\nわかった！？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 107801, 2425, 1, 0, 101, 0, "あたしと話したいの？\nふふ。\nまぁ、いいわよ。\nほら隣に座りなさい", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 107801, 2426, 1, 0, 102, 0, "こんなに楽しいの\n初めてかも…\nずっと続けばいいのに", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107801, 2427, 1, 1, 1, 0, "は、早起きしたのは、\n楽しみで眠れなかった\nからじゃないわよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107801, 2428, 1, 2, 2, 0, "昼食後のストレッチ\n終わり！　さあ、\nビーチが待ってるわ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107801, 2429, 1, 3, 3, 0, "夜の海に散歩に\n行こうかな～\nあんたも来なさいよ☆", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107801, 2431, 1, 0, 2, 0, "海ではしゃぎすぎて\n疲れたわ…ちょっと\n休もうかしら？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107801, 2432, 1, 0, 3, 0, "こんな魔物がいたら\n操るのも楽しいかも", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107801, 2433, 1, 0, 4, 0, "にゃう～♪\nまるで南国にいる\n気分ね♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107801, 2434, 1, 0, 5, 0, "あったかぁい♪\n身体が冷えてきたから\n丁度いいわ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107801, 2436, 1, 0, 7, 0, "運動後のご飯って\nなんでこんなに\n美味しいんだろ♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107801, 2437, 1, 0, 8, 0, "どんなカナヅチでも、\n絶対必ず泳げる方法、\n書いてないかしら？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107801, 2439, 1, 0, 10, 0, "あんたもあたしが\n操ってあげる！\nなんてね～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107801, 2440, 1, 0, 11, 0, "リゾートリゾートっ\nのんびりしましょ♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108001, 2441, 1, 0, 1, 0, "ここなら思う存分\nデスクワークに\n励めるわね！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108001, 2442, 1, 0, 2, 0, "武器のメンテナンスも\nしっかりしないと\n最高効率は望めないわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108001, 2443, 1, 0, 3, 0, "準備をしっかり整えて\nもっともっと\n稼がなくちゃね！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108001, 2444, 1, 0, 4, 0, "少し休んだら\n次のバイトに\n行ってくるわね！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108001, 2445, 1, 0, 5, 0, "この格好でここに\nいるのって\n気恥ずかしいわね…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 108001, 2446, 1, 0, 101, 0, "キミが望むなら\n今日のバイト\n休んでもいいわよ？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 108001, 2447, 1, 0, 102, 0, "キミだけね…\n私のことをここまで\n理解してくれるのは", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108001, 2448, 1, 0, 1, 0, "普段と違う格好\nだからって\nそんなに見ないでよ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108001, 2449, 1, 0, 2, 0, "キミを立派な\nバイト戦士にするのが\n師匠としての役目よね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108001, 2450, 1, 0, 3, 0, "そろそろ次のバイトに\n行かないと…\nキミも一緒に来る？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108001, 2451, 1, 0, 4, 0, "効率は大事だけど\nゆっくりするのも\n悪くないわね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 108001, 2452, 1, 0, 101, 0, "一緒の時間が楽しくて\nバイトのことを\n忘れちゃいそうね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 108001, 2453, 1, 0, 102, 0, "キミと一緒なら\nどんなミッションも\n楽勝ね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108001, 2454, 1, 1, 1, 0, "おはよう、いい朝ね！\n今日もめいっぱい\n稼ぐわよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108001, 2455, 1, 2, 2, 0, "もうお昼だったのね…\n内職のバイトに\n集中しすぎちゃったわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108001, 2456, 1, 3, 3, 0, "スケジュールは、と…\nこういう管理の徹底が\n効率を生み出すのよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108001, 2458, 1, 0, 2, 0, "しっかり働いて休む…\n最高効率にまた一歩\n近づいたわね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108001, 2459, 1, 0, 3, 0, "なごんじゃうわね…\nよぉし、次のバイトも\n頑張らないと！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108001, 2460, 1, 0, 4, 0, "いい香りね…\n働く意欲が\n湧いてくるわ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108001, 2461, 1, 0, 5, 0, "身体が冷えたから\n助かるわ…冷えは\n女の天敵だもの", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108001, 2463, 1, 0, 7, 0, "海のバイトは\n体力勝負よ\nエネルギーはしっかり\n摂取しないとね！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108001, 2464, 1, 0, 8, 0, "座学も大切ね\nもっと知識を\n身につけないと…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108001, 2466, 1, 0, 10, 0, "海の楽園みたいね…\n今だけは効率を\n忘れちゃいそう…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108001, 2467, 1, 0, 11, 0, "落ち着くわ…\n海とひとつになった\n気分ね！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107901, 2468, 1, 0, 1, 0, "ちょっと休憩にゃ～\n潮風は体に\n良くないにゃ～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107901, 2469, 1, 0, 2, 0, "猫グッズで\n海の家を飾りたいにゃ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107901, 2470, 1, 0, 3, 0, "海の幸は美味しいけど\n水はイヤだにゃ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107901, 2471, 1, 0, 4, 0, "開店にゃ！\nこのたい焼きなら\nきっと大繁盛にゃ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107901, 2472, 1, 0, 5, 0, "海と言えば水着にゃ\n似合ってるかにゃ？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 107901, 2473, 1, 0, 101, 0, "キミが頼むなら\n手伝ってあげても\nいいにゃ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 107901, 2474, 1, 0, 102, 0, "あたしの宝は\nここにあるにゃ～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107901, 2475, 1, 0, 1, 0, "にゃ？\nそんなに見つめても\n何も出ないにゃ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107901, 2476, 1, 0, 2, 0, "今からキミを\nあたしの相棒に\n任命するにゃ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107901, 2477, 1, 0, 3, 0, "これから\n開店の準備にゃ\nキミも手伝って\nくれるのかにゃ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107901, 2478, 1, 0, 4, 0, "海の家で出す\n新しいメニューを\n考え中なのにゃ～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 107901, 2479, 1, 0, 101, 0, "キミと一緒なら\n売上げナンバーワンに\nなれるにゃ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 107901, 2480, 1, 0, 102, 0, "キミと一緒にいると\n楽しくて、もっと\nいたくなるにゃ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107901, 2481, 1, 1, 1, 0, "おはようにゃ！\n今日も頑張って\nいくにゃー！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107901, 2482, 1, 2, 2, 0, "お腹すいたにゃ～…\nお魚、ちょっとだけ、\nつまみ食いするにゃ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107901, 2483, 1, 3, 3, 0, "明日の準備は万端にゃ\nいつでも\n出発できるにゃ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107901, 2485, 1, 0, 2, 0, "このふかふか具合\nまるで子猫の\n毛並みのようにゃ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107901, 2486, 1, 0, 3, 0, "にゃにゃ～\nなんてかわいさにゃ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107901, 2487, 1, 0, 4, 0, "この匂い…\nもう少し香ばしいと\nいいかもしれないにゃ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107901, 2488, 1, 0, 5, 0, "にゃ～…\nあったかくて\nぬくぬくだにゃ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107901, 2490, 1, 0, 7, 0, "働くにはまず体力にゃ\nしっかり食べて\nスタミナアップ\nだにゃ!", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107901, 2491, 1, 0, 8, 0, "ふむふむ…\nここにお宝が\n眠ってるんだにゃ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107901, 2493, 1, 0, 10, 0, "シードレイクにゃ！\n本物は怖かったけど、\nぬいぐるみなら\nかわいいにゃ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107901, 2494, 1, 0, 11, 0, "貝なのにふかふかの\nクッションにゃ！\n屋台の前にも\n置いてみたいにゃあ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103701, 2495, 1, 0, 1, 0, "人の家って、道場とは\n違った香りがするね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103701, 2496, 1, 0, 2, 0, "ちょっと退屈だね…\nあの人をからかいに\n行こうかな♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103701, 2497, 1, 0, 3, 0, "普通の女の子みたいに\n遊びたいって思う\nこともあるんだよね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103701, 2498, 1, 0, 4, 0, "剣の手入れを怠らない\nようにしないと。\nいざという時、困るし", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103701, 2499, 1, 0, 5, 0, "もっと\n上手く立ち回って\n出世したいな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 103701, 2500, 1, 0, 101, 0, "おかえりなさい！\nふふ、なんだか奥さん\nになった気分だよ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 103701, 2501, 1, 0, 102, 0, "恥ずかしくて、あなた\nを見られないよ…\n私らしくないなぁ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103701, 2502, 1, 0, 1, 0, "私、お話好きなんだ。\nどうせなら楽しい話を\nしたいな♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103701, 2503, 1, 0, 2, 0, "女の子っぽい子と、\n男勝りな子、あなたは\nどっちが好き？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103701, 2504, 1, 0, 3, 0, "そろそろ見回りの\n時間かな。あなたも\n一緒に行く？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103701, 2505, 1, 0, 4, 0, "構って欲しいのかな？\nそれじゃ一緒に\nランニングに行こうか", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 103701, 2506, 1, 0, 101, 0, "二人で出かけるなら、\n手を繋がないとね。\nあはは、冗談冗談♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 103701, 2507, 1, 0, 102, 0, "ねえ、あなた♪\nおや、顔が真っ赤だ！\n…ふふ、私も、かな？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103701, 2508, 1, 1, 1, 0, "私は朝から鍛錬だよ。\n慣れてるけど大変だ。\nお互いに頑張ろうね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103701, 2509, 1, 2, 2, 0, "鍛錬でお腹ぺこぺこ。\nお昼ごはんが\n楽しみだね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103701, 2510, 1, 3, 3, 0, "今日も一日見回りと\n鍛錬を頑張ったよ。\nあなたもお疲れさま", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103701, 2512, 1, 0, 2, 0, "いざという時のため\nにも、休憩は大事だね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103701, 2513, 1, 0, 3, 0, "こういうものには\n今まで\n縁がなかったけど。\nうん、可愛いね♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103701, 2514, 1, 0, 4, 0, "精神統一に通じる\nものがあるね。\nふぅ、癒されるなぁ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103701, 2515, 1, 0, 5, 0, "あったかいなぁ。\nたまにはのんびり\nしようか", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103701, 2517, 1, 0, 7, 0, "鍛錬後はすごくお腹が\n空くんだよね。\nう～ん、おいしい♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 103701, 2518, 1, 0, 8, 0, "剣術書だけで強くなる\nには飛びぬけた才能と\n努力が必要だよね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100501, 2520, 1, 0, 1, 0, "正義の刃で\n悪を挫く！\n…キマったッス♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100501, 2521, 1, 0, 2, 0, "ヒーローの\n秘密特訓を\n始めるッスよ～！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100501, 2522, 1, 0, 3, 0, "正義の味方目指して\n今日も明日も\n全力ッスよ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100501, 2523, 1, 0, 4, 0, "一日一善！\nヒーローだったら\n当たり前ッス♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100501, 2524, 1, 0, 5, 0, "どうッスか\nこのポーズ！\nイイ感じッスか？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 100501, 2525, 1, 0, 101, 0, "た、たまには\nよく頑張ったねって\n褒めてほしいッス…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100501, 2526, 1, 0, 102, 0, "自分を信じてくれる\nあんたと一緒なら\n何でもやれるッス！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100501, 2527, 1, 0, 1, 0, "おつかれッス♪\n冒険の準備は\nばっちりッスよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100501, 2528, 1, 0, 2, 0, "どうしたんッスか？\n…はっ！　もしや敵が\n迫ってるんスかっ！？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100501, 2529, 1, 0, 3, 0, "べ、別に\n頭を撫でてほしいとか\n思ってないッスよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100501, 2530, 1, 0, 4, 0, "スーパーヒーロー\nトラタイガーは\n絶対無敵ッス！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 100501, 2531, 1, 0, 101, 0, "あんたと自分で\n正義の心を燃やして\n悪を一網打尽ッス！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100501, 2532, 1, 0, 102, 0, "あんたと一緒だと\n幸せな気持ちに\nなるんッス…♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100501, 2533, 1, 1, 1, 0, "お、早いッスね～♪\n今日も一日一善\nよろしくッス！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100501, 2534, 1, 2, 2, 0, "一休みしながら\nヒーローについて\n語り合うッスよ～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100501, 2535, 1, 3, 3, 0, "夜のパトロールで\n一緒に平和を\n守るッス！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100501, 2537, 1, 0, 2, 0, "ふぅぅ…\n落ち着くッスね\n戦士の休息ッス♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100501, 2538, 1, 0, 3, 0, "くぅぅ、可愛いッス…\nヒーローが守るべき\n愛らしさッスね！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100501, 2539, 1, 0, 4, 0, "なんスかこの香り？\nすぅぅ…ふぅぅ…\n力がみなぎるッス！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100501, 2540, 1, 0, 5, 0, "優しい光ッスね…\nヒーローの優しさ\nそのものッス！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100501, 2542, 1, 0, 7, 0, "あむあむあむ…\nんぅう、元気が溢れて\n止まらないッス！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100501, 2543, 1, 0, 8, 0, "トラタイガーのお話は\n笑いあり涙ありで\n超おすすめッス！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108101, 2545, 1, 0, 1, 0, "ハッピーハロウィン\nお父さん、お菓子は…\n食べられないね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108101, 2546, 1, 0, 2, 0, "トリックオアトリート\nお菓子をくれないと\n不吉な占いをします", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108101, 2547, 1, 0, 3, 0, "ふふ、あなたの後ろで\n霊のみなさんが\n踊り始めました", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108101, 2548, 1, 0, 4, 0, "霊感がいつも以上に\n冴えているので\n占いの調子も良好です", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108101, 2549, 1, 0, 5, 0, "父はハロウィンが\n大好きだそうです\n大胆な格好をする子が\n多いので…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 108101, 2550, 1, 0, 101, 0, "あなたは霊たちにも\n大人気ですよね\n私も見てほしいな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 108101, 2551, 1, 0, 102, 0, "ハロウィンの賑やかさ\n今は好きです…\nあなたといられるので", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108101, 2552, 1, 0, 1, 0, "霊のみなさんとの\nダンスパーティー…\n楽しみです", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108101, 2553, 1, 0, 2, 0, "お父さん、元気だね\nえ？　ここは色んな\n女の子に会えて幸せ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108101, 2554, 1, 0, 3, 0, "ハロウィンのお菓子\n食べすぎちゃったな…\nでも、美味しかった…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108101, 2555, 1, 0, 4, 0, "あなたの女難の相…\n悪化していますね\nお祓いが必要かも", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 108101, 2556, 1, 0, 101, 0, "あの、霊のみなさんが\nあなたと踊りたいと…\nえっと、私も…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 108101, 2557, 1, 0, 102, 0, "あなたと手を繋ぐと\n胸が苦しくなります\nでも、繋ぎたいな…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108101, 2558, 1, 1, 1, 0, "ハッピーハロウィン…\n父が元気なので\n今日の運気は良好です", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108101, 2559, 1, 2, 2, 0, "父はお昼寝中です\n本当に寝ているかは\n私にも分かりませんが", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108101, 2560, 1, 3, 3, 0, "お父さん、\nもう眠っちゃったの？\n…眠れるんだったっけ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108101, 2562, 1, 0, 2, 0, "落ち着きますね…\n霊のみなさんも\n喜んでいます", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108101, 2563, 1, 0, 3, 0, "つん、つん。ふふっ♪\n癒やされます…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108101, 2564, 1, 0, 4, 0, "精神が研ぎ澄まされる\n香りですね…\n占いが捗ります", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108101, 2565, 1, 0, 5, 0, "あったかいね\nお父さん…\nえ？　分からない？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108101, 2567, 1, 0, 7, 0, "霊のみなさんは\n食べられないので…\n私がいただきます", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108101, 2568, 1, 0, 8, 0, "お父さんを元に戻す\n手掛かり…\n書かれていないかな？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108101, 2570, 1, 0, 12, 0, "わぁ、お菓子が沢山…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108201, 2572, 1, 0, 1, 0, "無料でプリンが\nもらえるなんて、\nハロウィンはすごいの", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108201, 2573, 1, 0, 2, 0, "生きてるときも、\nミヤコはハロウィンが\n好きだった気がするの", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108201, 2574, 1, 0, 3, 0, "みんなに仮装を\n見せつけて、\nプリンをもらうの～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108201, 2575, 1, 0, 4, 0, "この仮装で、\nみんなに恐怖を\n教えてやるの！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108201, 2576, 1, 0, 5, 0, "アオーン、アオーン！\nミヤコ、狼女に\nなりきってるの～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 108201, 2577, 1, 0, 101, 0, "アイツに秘密で、\nパーティーの\n飾り付けをするの♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 108201, 2578, 1, 0, 102, 0, "アイツが狼男の\n仮装をすれば、\nおそろいなの～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108201, 2579, 1, 0, 1, 0, "プリンをくれなきゃ、\nオマエをカボチャに\nしてやるの～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108201, 2580, 1, 0, 2, 0, "ハロウィンは幽霊でも\n参加できるから\n楽しいの～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108201, 2581, 1, 0, 3, 0, "プリンがないなら\nカボチャプリンを\n食べればいいの！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108201, 2582, 1, 0, 4, 0, "プリンの可能性は\n無限大なの～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 108201, 2583, 1, 0, 101, 0, "オマエの隣で\nプリンを食べると、\nいつもより美味しいの", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 108201, 2584, 1, 0, 102, 0, "オマエが死んでも、\nミヤコはずーっと\n一緒にいてやるの～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108201, 2585, 1, 1, 1, 0, "朝ごはんは\nカボチャプリンなの！\nハロウィンっぽいの～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108201, 2586, 1, 2, 2, 0, "ランチは豪華に\nプリンアラモードなの", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108201, 2587, 1, 3, 3, 0, "夜は幽霊たちと\nハロウィンするの！\nオマエも来るの！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108201, 2589, 1, 0, 2, 0, "幽霊にも休息は\n必要なの！\nプリンもってこいなの", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108201, 2590, 1, 0, 3, 0, "オマエもミヤコの\nパーティーに\n招待してあげるの～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108201, 2591, 1, 0, 4, 0, "癒されるの～\nまるで天国なの～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108201, 2592, 1, 0, 5, 0, "ここでなら、\n焼きプリンが\n作れるの！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108201, 2594, 1, 0, 7, 0, "腹が減っては\nハロウィンは\nできぬなの！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108201, 2595, 1, 0, 8, 0, "丸ごとカボチャ\nプリン特集！？\n美味しそうなの～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108201, 2597, 1, 0, 12, 0, "お菓子がたくさんで、\nミヤコはとっても\nハッピーなの♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108301, 2599, 1, 0, 1, 0, "魔女以外にも、\n色々な仮装を\nしてみたいわね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108301, 2600, 1, 0, 2, 0, "チョコやアメよりも、\nアタシに相応しい\nスイーツがあるはず…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108301, 2601, 1, 0, 3, 0, "お菓子の食べ過ぎは、\nお母さんに\n怒られるかも…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108301, 2602, 1, 0, 4, 0, "この格好で学院に\n行ったら、\nみんな驚くかしら", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108301, 2603, 1, 0, 5, 0, "大人のレディは、\nどういうハロウィンを\n過ごすべきかしら…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 108301, 2604, 1, 0, 101, 0, "お菓子、アイツの分も\nとっといてあげよ！\n大人の余裕ってやつね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 108301, 2605, 1, 0, 102, 0, "アイツを魅了する\n魔法とかないかしら？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108301, 2606, 1, 0, 1, 0, "おしゃれスイーツを\nくれなきゃ\nイタズラするわよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108301, 2607, 1, 0, 2, 0, "アタシの大人センスで\nアンタに似合う仮装を\n選んであげるわ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108301, 2608, 1, 0, 3, 0, "不気味よねぇ…\nジャックオーランタン\nこ、怖くないわよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108301, 2609, 1, 0, 4, 0, "ハロウィンが楽しくて\n宿題やり忘れてたわ…\nて、手伝って～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 108301, 2610, 1, 0, 101, 0, "お菓子をもらうのも\nアンタと一緒なら\n恥ずかしくないわ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 108301, 2611, 1, 0, 102, 0, "アタシのエスコートを\n任せられるのは、\nアンタだけなんだから", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108301, 2612, 1, 1, 1, 0, "朝食はオシャレに\nグリーンスムージー！\n…に、にがぁ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108301, 2613, 1, 2, 2, 0, "昼下がりはカフェで\n優雅なティータイム\n…憧れるわ～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108301, 2614, 1, 3, 3, 0, "ハロウィンナイトの\nはじまりよ！\n街に繰り出しましょ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108301, 2616, 1, 0, 2, 0, "あう～、お菓子の\n食べすぎで\n動けな～い", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108301, 2617, 1, 0, 3, 0, "アンタも仮装する？\nいまよりもーっと\nかわいくなるわよ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108301, 2618, 1, 0, 4, 0, "大人な香りに\n包まれてる～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108301, 2619, 1, 0, 5, 0, "ポカポカね～♪\n…ああー！\nチョコが溶けてる！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108301, 2621, 1, 0, 7, 0, "おいしい～♪\n…じゃなくて！\nび、美味ですわ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108301, 2622, 1, 0, 8, 0, "この服かわい～♪\nって、なにこの値段！\nお小遣い何年分よ！？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108301, 2624, 1, 0, 12, 0, "お、お菓子なんかで\n喜ぶほど、子供じゃ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107101, 2626, 1, 0, 1, 0, "次はどんな策で団長を\nからかってみようか…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107101, 2627, 1, 0, 2, 0, "む？\n血の臭いがするな\n祭りの時間か☆", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107101, 2628, 1, 0, 3, 0, "平和に飼い殺される\n前に、混沌の愛を\n育みたいね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107101, 2629, 1, 0, 4, 0, "本気を出した団長と\nいつか手加減なしに\n殺り合いたいな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107101, 2630, 1, 0, 5, 0, "おばさん呼ばわりする\n新米騎士の小娘を\n懲らしめてやろうか", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 107101, 2631, 1, 0, 101, 0, "胸を触られるまで\n誓約のことなど\nすっかり忘れていたな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 107101, 2632, 1, 0, 102, 0, "まさかこのワタシが\nプロポーズするとはな\n…笑えるじゃないか", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107101, 2633, 1, 0, 1, 0, "面白い暇つぶしを\n思いついたんだ\n戯れに付き合え☆", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107101, 2634, 1, 0, 2, 0, "退屈で死にそうだ\n愉しい話題を\nワタシに提供してくれ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107101, 2635, 1, 0, 3, 0, "なんだ、暇なのか？\n心躍ることでも探しに\n出かけようではないか", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107101, 2636, 1, 0, 4, 0, "このワタシを愉快に\nさせてくれる事でも\nあったのか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 107101, 2637, 1, 0, 101, 0, "ワタシに求婚した男の\n数だと？\nいちいち覚えてないな", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 107101, 2638, 1, 0, 102, 0, "最強無敵の女と\n結婚できるんだ\n誇りに思えよ☆", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107101, 2639, 1, 1, 1, 0, "なにか愉快なことが\n起こりそうな朝だ☆\nワクワクするね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107101, 2640, 1, 2, 2, 0, "うーむ、腹がすいたな\n愉快なことで\nこの空腹を満たしたい", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107101, 2641, 1, 3, 3, 0, "これからは大人の\n時間だ。愉しみ方を\n知りたいのかい？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107101, 2643, 1, 0, 2, 0, "このワタシとした事が\n穏やかな気分に\nなってしまうとは…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107101, 2644, 1, 0, 3, 0, "可愛い姿をしたものが\n惨たらしい\n醜態を晒すのを\n想像してしまう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107101, 2645, 1, 0, 4, 0, "悪くはない香りだ\nだが、ワタシは血の\n臭いを嗅ぎたいぞ☆", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107101, 2646, 1, 0, 5, 0, "ぬるい、ぬるすぎる！\n地獄の業火には\nほど遠い熱さだ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107101, 2648, 1, 0, 7, 0, "ワタシの腕前に\n勝るとも劣らない\nなかなかの味だ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107101, 2649, 1, 0, 8, 0, "意中の男性を絶対に\n落とす方法…？\nく、下らんっ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105601, 2651, 1, 0, 1, 0, "明日はあの釣り場に\n行くとしようかな\nあの子も誘ってみるか", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105601, 2652, 1, 0, 2, 0, "ぷはーっ！\n体を動かしたあとの\n一杯は格別だね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105601, 2653, 1, 0, 3, 0, "天気が良いと\n昼寝か釣りかで\n迷っちまうな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105601, 2654, 1, 0, 4, 0, "大物が釣れたときの\n快感は、他じゃ\n味わえないね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105601, 2655, 1, 0, 5, 0, "ふあぁ～…\n昼寝でもするか", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 105601, 2656, 1, 0, 101, 0, "ふふっ…\nあの子、見た目より\n根性あるんだね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 105601, 2657, 1, 0, 102, 0, "あの子が強くなるには\nどんな魚を食べさせて\nあげればいいのやら…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105601, 2658, 1, 0, 1, 0, "姉御って呼ぶんじゃ\nないよ。からかうと\nタダじゃおかないよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105601, 2659, 1, 0, 2, 0, "釣りのコツ？\n何事も経験が大事だ\n今から行くかい？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105601, 2660, 1, 0, 3, 0, "どうしたのさ？\n助太刀が必要かい？\nアタシにまかせときな", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105601, 2661, 1, 0, 4, 0, "困ってる人がいたら\nまた力を貸しておくれ\n頼りにしてるよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 105601, 2662, 1, 0, 101, 0, "なにか困りごとかい？\nどうやら昼寝は\nまた今度だね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 105601, 2663, 1, 0, 102, 0, "よしよし！\nお人好しのお前さんを\n労ってあげるよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105601, 2664, 1, 1, 1, 0, "おはよう、お前さん\n魚屋は早起きでなきゃ\nつとまらないよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105601, 2665, 1, 2, 2, 0, "昼飯かい？　今朝\n仕入れた魚があるんだ\n食べてみるかい？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105601, 2666, 1, 3, 3, 0, "夜釣りにぴったりの\n月が出てるよ\nいい釣りが出来そうだ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105601, 2668, 1, 0, 2, 0, "コイツに座って\nのんびり釣りが\nできりゃいいのにな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105601, 2669, 1, 0, 3, 0, "よしよし…♪\nおっと、かわいい物も\nつい抱きしめちまうな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105601, 2670, 1, 0, 4, 0, "これで魚の臭いを\n消せれば、猫も\n寄らなくなるのかね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105601, 2671, 1, 0, 5, 0, "冬場の釣りも\nこれくらい暖かけりゃ\nいいんだけどね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105601, 2673, 1, 0, 7, 0, "腹が減っては\n釣りもできぬ、ってな\nまずは腹ごしらえだ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105601, 2674, 1, 0, 8, 0, "へぇ…こんな魚が\nいたとは…いつか\n釣りたいもんだね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101301, 2676, 1, 0, 1, 0, "流石に室内で\n魔法ぶっぱしたら\n怒られるよねぇ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101301, 2677, 1, 0, 2, 0, "可愛い女の子が\nたくさんいる！\nここは楽園か！？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101301, 2678, 1, 0, 3, 0, "みんなに漫画とか\n勧めてみようかな！\n布教用のがあるし！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101301, 2679, 1, 0, 4, 0, "わ、私一人の\n部屋じゃないから\n色々自重せねば！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101301, 2680, 1, 0, 5, 0, "「ぬるぽ」\n…くっ、\n誰も反応なしか！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 101301, 2681, 1, 0, 101, 0, "次はどの漫画を\n貸そうかなー♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 101301, 2682, 1, 0, 102, 0, "次の定期巡回で\nとっておきの店に\n連れてってあげよう！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101301, 2683, 1, 0, 1, 0, "君に似合いそうな\n衣装を買ったよ！\n君もいざ魔法少女に！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101301, 2684, 1, 0, 2, 0, "さあ！\n暇なら定期巡回に\nレッツラゴー！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101301, 2685, 1, 0, 3, 0, "戦闘だけじゃなくて、\n日常にも役立つ\n魔法を習得したいね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101301, 2686, 1, 0, 4, 0, "新魔法思いついた！\n私って天才！？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 101301, 2687, 1, 0, 101, 0, "決め台詞は超大事！\n君の決め台詞も\n私が考えてあげよう！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 101301, 2688, 1, 0, 102, 0, "私ってもしやリア充？\n…そんなバカな！\n非リア代表のはずが！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101301, 2689, 1, 1, 1, 0, "おはよー…\n徹夜で本読んでたから\nまだ眠い～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101301, 2690, 1, 2, 2, 0, "昼飯食べて、\n午後の定期巡回も\n頑張りましょー！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101301, 2691, 1, 3, 3, 0, "夜からが、\nオタ活本番でしょ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101301, 2693, 1, 0, 2, 0, "ふいー！\nくつろいで本を読む！\n最高だねー！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101301, 2694, 1, 0, 3, 0, "君、可愛いね～♪\n魔法少女の\n使い魔みたい！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101301, 2695, 1, 0, 4, 0, "良い匂いに\n釣られてしまった…\nまさか、孔明の罠！？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101301, 2696, 1, 0, 5, 0, "暖かいけど、\nここで本を読むのは\nちょっち危ないかな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101301, 2698, 1, 0, 7, 0, "ウマ―！\nこれの詳細きぼんぬ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101301, 2699, 1, 0, 8, 0, "こ、古代語の本！？\n現代語でおk…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 190901, 2701, 1, 0, 1, 0, "ふふふっ♪\n次はどんな悪戯を\nしてやろうかしら♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 190901, 2702, 1, 0, 2, 0, "幻影湖の霧を吸った\n人間たちの\nリアクションって、\nサイコーよねぇ～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 190901, 2703, 1, 0, 3, 0, "室内だけとはいえ…\n自由に動けるって、\nやっぱりいいわ～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 190901, 2704, 1, 0, 4, 0, "改めて見ると良い\n部屋ね…悪戯とか\nしやすそうだしぃ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 190901, 2705, 1, 0, 5, 0, "私を封じていた部屋\nなのに…急に居心地が\n良くなったわね♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 190901, 2706, 1, 0, 1, 0, "あんたの頭、座り心地\n良いわね…私専用の\n椅子にしてあげる♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 190901, 2707, 1, 0, 2, 0, "外は大変みたいねぇ…\nまあ、私は好き勝手に\nやらせてもらうわ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 190901, 2708, 1, 0, 3, 0, "顔に落書された？\n誰か知らないけど、\n悪いことする奴も\nいるものねぇ～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 190901, 2709, 1, 0, 4, 0, "フィオのやつ…\nどこをウロチョロ\nしてるのかしら？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 190901, 2710, 1, 1, 1, 0, "人間の朝は大変ねぇ…\n私はその様を見ながら\nゆっくりするわね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 190901, 2711, 1, 2, 2, 0, "う～私も、お日様を\n浴びながら、お外を\nお散歩したーい！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 190901, 2712, 1, 3, 3, 0, "この暗さ…閉じ込め\nられていた時を\n思い出すわね…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 190901, 2714, 1, 0, 2, 0, "悪戯しすぎて\n疲れたから、ちょっと\n休憩しよっと♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 190901, 2715, 1, 0, 3, 0, "ガイド妖精たる私が、\nこんな造形物に\n惹かれる…わけが……\nかわいい♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 190901, 2716, 1, 0, 4, 0, "この香りを囮にして、\n周りに悪戯を仕込む…\n完璧ね♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 190901, 2717, 1, 0, 5, 0, "不思議ねぇ…私は\n温度とか関係ないのに\n離れられないわ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 190901, 2719, 1, 0, 7, 0, "食事の必要がない体…\nつまり、食べたければ\n食べていいのよね～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 190901, 2720, 1, 0, 8, 0, "へぇ…最近はこんな\n悪戯もあるのねぇ…\nあいつで試さないと♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108401, 2722, 1, 0, 1, 0, "メリークリスマス！\n歌で幸せを\n届けます…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108401, 2723, 1, 0, 2, 0, "あなたと出会えたのは\n偶然ではなくて…\nクリスマスの奇跡だと\n思うんです", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108401, 2724, 1, 0, 3, 0, "クリスマスは華やかで\nつい子供のように\nはしゃいでしまいます", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108401, 2725, 1, 0, 4, 0, "曲が書けるのは\nあなたが側に\nいてくれるからです…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108401, 2726, 1, 0, 5, 0, "感動を与えられる歌…\nあなたと一緒になら\n生み出せるはず…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 108401, 2727, 1, 0, 101, 0, "クリスマスの幸せを\nあなたと\n感じ合えたらな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 108401, 2728, 1, 0, 102, 0, "あなたの幸せを願った\nクリスマスソング…\nいつか披露しますね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108401, 2729, 1, 0, 1, 0, "みなさんの笑顔を\n守りたいから…\n私は歌い続けます", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108401, 2730, 1, 0, 2, 0, "歌を届ける\nサンタさんに\nなりたいな…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108401, 2731, 1, 0, 3, 0, "お暇でしたら\nクリスマスのお祝いを\nしませんか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108401, 2732, 1, 0, 4, 0, "ステージに立つ前に\n喉を整えないと…\n発声練習は念入りに…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 108401, 2733, 1, 0, 101, 0, "まわりはかわいい子\nばっかりだけど…\nこの服なら\nアピールになるかな？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 108401, 2734, 1, 0, 102, 0, "歌でならいくらでも\nあの人への気持ちを\n込められるのに…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108401, 2735, 1, 1, 1, 0, "今日も一日\n幸せな歌で世界が\n包まれますように…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108401, 2736, 1, 2, 2, 0, "お茶を飲みながら\n好きな音楽を\n語り合いませんか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108401, 2737, 1, 3, 3, 0, "ふふ、寝かしつけの\n子守唄を\nお望みですか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108401, 2739, 1, 0, 2, 0, "ふかふかですね\n心地良すぎて\n眠ってしまいそう…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108401, 2740, 1, 0, 3, 0, "素敵…\n優しい歌みたいに\n気持ちが落ち着く…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108401, 2741, 1, 0, 4, 0, "インスピレーションが\n溢れてくる…\nいい曲が書けそう…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108401, 2742, 1, 0, 5, 0, "この光みたいに\n灯火になるような\n歌を歌いたい…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108401, 2744, 1, 0, 7, 0, "喉の健康のためにも\n食事はしっかり\nとるようにしています", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108401, 2745, 1, 0, 8, 0, "物語には\n曲作りのヒントが\n詰まっているんです", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108401, 2747, 1, 0, 14, 0, "絵が飛び出してくる\nなんて…精霊が宿って\nいるのでしょうか？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108401, 2748, 1, 0, 15, 0, "か、可愛い…♪\nちょっとだけ触っても\nいいですよね…？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108501, 2749, 1, 0, 1, 0, "クリスマスでも、\n演劇の練習は\nしっかりやらないと！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108501, 2750, 1, 0, 2, 0, "あうぅ…\nクリスマスの準備が\n終わらないよぅ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108501, 2751, 1, 0, 3, 0, "アヤネちゃんが\n摘み食いしないように\n見ておかないと…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108501, 2752, 1, 0, 4, 0, "サンタさんみたいに、\n皆に笑顔で接する…\nうぅ…私には無理ぃ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108501, 2753, 1, 0, 5, 0, "新しいお洋服～♪\nスズメお姉ちゃんに\nお礼をいわないと♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 108501, 2754, 1, 0, 101, 0, "お兄ちゃんの為に、\nプレゼントを\n用意しないと♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 108501, 2755, 1, 0, 102, 0, "クリスマス…\n恋人の日…ひゃわっ！\nわ、私ってば何を…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108501, 2756, 1, 0, 1, 0, "メリー\nクリスマスです、\nお兄ちゃん♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108501, 2757, 1, 0, 2, 0, "そ、そんなに\n見られると…\n恥ずかしいよぉ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108501, 2758, 1, 0, 3, 0, "ひゃわぁっ！？\n急に触られると、\n驚くからやめてぇ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108501, 2759, 1, 0, 4, 0, "あ…クリスマスの\n準備を手伝って\nくれるんですか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 108501, 2760, 1, 0, 101, 0, "お兄ちゃんと\n過ごすクリスマス、\n楽しみですぅ…♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 108501, 2761, 1, 0, 102, 0, "クリスマスはその…\n二人で…あうぅ…\nなんでもないですぅ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108501, 2762, 1, 1, 1, 0, "うう…パーティーが\n楽しみで、あんまり\n眠れなかったよぅ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108501, 2763, 1, 2, 2, 0, "ケーキを食べられなく\nなっちゃうから、\nお昼は軽めにしないと", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108501, 2764, 1, 3, 3, 0, "ちょっと眠いけど…\n今日は少しだけ\n夜更かししたいです♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108501, 2766, 1, 0, 2, 0, "ふかふかぁ…\nクリスマスの準備中に\n眠くなっちゃいますぅ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108501, 2767, 1, 0, 3, 0, "この子と一緒に\nおままごとしたいな♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108501, 2768, 1, 0, 4, 0, "いい匂い…♪\nみんなで\nアロマパーティー\nしたいなぁ…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108501, 2769, 1, 0, 5, 0, "ぬっくぬく…♪\nもう、ここから\n離れたくないよぉ…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108501, 2771, 1, 0, 7, 0, "はむはむ…♪\nクリスマスのお料理\nより美味しいかも～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108501, 2772, 1, 0, 8, 0, "すごい…\nクリスマスの\n演劇って、\nこんなにあるんだ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108501, 2774, 1, 0, 14, 0, "すごい…まるで、\nクリスマスの演劇を\n観てるようですぅ…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108501, 2775, 1, 0, 15, 0, "とっても可愛いです♪\nお芝居の相手役に\nしたいなぁ…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108601, 2776, 1, 0, 1, 0, "甘ぁいケーキも、\nデッカイ七面鳥も、\nぜーんぶ私のだー♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108601, 2777, 1, 0, 2, 0, "毎日が\nクリスマスだったら\nいいのにな～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108601, 2778, 1, 0, 3, 0, "プレゼントはあるし、\n料理は美味しいし、\nクリスマス最高ー♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108601, 2779, 1, 0, 4, 0, "ここでぷうきちの\nクリスマス一発芸♪\n『無茶ぶりするな！』", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108601, 2780, 1, 0, 5, 0, "ぷうきちがいつもより\n重いかも…太ったの？\n『衣装の重さだよ！』", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 108601, 2781, 1, 0, 101, 0, "お兄ちゃんが\n一緒にいてくれると\nクリスマス楽しいね♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 108601, 2782, 1, 0, 102, 0, "お兄ちゃんに抱きつく\nと、なんだかドキドキ\nする…なんでかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108601, 2783, 1, 0, 1, 0, "メリークリスマス♪\nプレゼントは、\nいつでも受付中だよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108601, 2784, 1, 0, 2, 0, "このクマさん耳\n可愛いでしょ♪\nぷうきちと\nお揃いなんだー♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108601, 2785, 1, 0, 3, 0, "お兄ちゃん…私、\n甘ぁいケーキが\n食べたいなぁ…♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108601, 2786, 1, 0, 4, 0, "クリスマスぷうきちは\nぬくぬく感が凄いよ！\nお兄ちゃんもさわる？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 108601, 2787, 1, 0, 101, 0, "えへへ…お兄ちゃんの\n手は、大きくて、\n暖かくて大好き～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 108601, 2788, 1, 0, 102, 0, "来年のクリスマスも、\nこうやって\nお兄ちゃんに\n甘えていたいな～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108601, 2789, 1, 1, 1, 0, "あれぇ…\n私のケーキはぁ…？\n『ちゃんと起きろ』", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108601, 2790, 1, 2, 2, 0, "お昼はデッカイ\n七面鳥が食べたーい♪\n『夜まで我慢しろ』", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108601, 2791, 1, 3, 3, 0, "『もう寝る時間だぞ』\nサンタさんが来る\nまで起きてるぅ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108601, 2793, 1, 0, 2, 0, "このふかふか具合…\nぷうきち！\n負けてられないよ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108601, 2794, 1, 0, 3, 0, "この子、\nすっごくかわいい～♪\n『ライバル登場か！』", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108601, 2795, 1, 0, 4, 0, "良い匂い…あっ！\nぷうきちにこの\n匂いを移せば…！\n『匂い袋にするな』", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108601, 2796, 1, 0, 5, 0, "あったか～い♪\nえへへ、ぷうきちも\nあっためてあげる♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108601, 2798, 1, 0, 7, 0, "ぷうきち！　これ、\nとってもおいしいよ♪\n『食べすぎるなよ』", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108601, 2799, 1, 0, 8, 0, "お、大人の私なら、\nこれぐらい…読め…\n『無理するな…』", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108601, 2801, 1, 0, 14, 0, "絵が飛び出す…\nぷうきちに絵本を\n入れれば、イタズラに\n使えるかも…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108601, 2802, 1, 0, 15, 0, "か、可愛いけど…\nぷうきちサンタの方が\n可愛いんだからぁ…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108901, 2803, 1, 0, 1, 0, "着物も慣れると\n動きやすいものさ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108901, 2804, 1, 0, 2, 0, "鼻緒が緩んでしまった\n下駄で修業は\n無理があったかな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108901, 2805, 1, 0, 3, 0, "小腹が空いた…\nユイが作った餅、\nまだ食べられるかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108901, 2806, 1, 0, 4, 0, "服装も変わったし\nいつもと違う戦い方を\n想定しないと…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108901, 2807, 1, 0, 5, 0, "…ヒヨリは動きすぎて\n着崩れしてないか\n心配だ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 108901, 2808, 1, 0, 101, 0, "これからもずっと\n仲間と、彼と\n一緒に…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 108901, 2809, 1, 0, 102, 0, "願いを叶えるために\nもっと自分を\n鍛えていかないと！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108901, 2810, 1, 0, 1, 0, "頭の飾りがずれてた？\nそうか、直してくれて\nありがとう", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108901, 2811, 1, 0, 2, 0, "着物がそんなに\n珍しい？\nキミも着てみる？\n…冗談だよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108901, 2812, 1, 0, 3, 0, "肩掛けの触り心地が\n気に入った？\nならもっと触る？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108901, 2813, 1, 0, 4, 0, "ああ、この刀？\n釣り仲間に見繕って\nもらったんだ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 108901, 2814, 1, 0, 101, 0, "キミといると\n気持ちが段々と\n安らいでいくよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 108901, 2815, 1, 0, 102, 0, "キミは私の行く先を\n照らしてくれる\n光のような存在だよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108901, 2816, 1, 1, 1, 0, "おはよう\n朝の修行を始めるけど\nキミもどう？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108901, 2817, 1, 2, 2, 0, "この時間に軽く\n休憩をとるといい\n能率がよくなるよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108901, 2818, 1, 3, 3, 0, "もう夜か…\n明日もまた頑張ろう\nそれじゃ、おやすみ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108901, 2820, 1, 0, 2, 0, "…疲れていたのかな\nもう少しで眠る\nところだった…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108901, 2821, 1, 0, 3, 0, "ふふっ、正月衣装を\n着せてやるのも\nよさそうだ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108901, 2822, 1, 0, 4, 0, "ふぅ…いい香りだ\n慌ただしい日々から\n心が解放される", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108901, 2823, 1, 0, 5, 0, "この心地よさ…\n寝てしまわないよう\n気をつけないと", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108901, 2825, 1, 0, 7, 0, "おいしいけど\n帯がきつくなる前に\n手を止めよう…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108901, 2826, 1, 0, 8, 0, "外を出歩くのも\nいいけど、中で\n読書もありだな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108801, 2828, 1, 0, 1, 0, "…騎士クンって\nおせち料理で\n何が好きなんだろう？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108801, 2829, 1, 0, 2, 0, "着物でお掃除や\nお洗濯するの\n慣れてきた♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108801, 2830, 1, 0, 3, 0, "今年は\nどんな福袋を\n買おうかな…♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108801, 2831, 1, 0, 4, 0, "お餅に使う調味料\nみんなで違うから\n色々用意しなきゃ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108801, 2832, 1, 0, 5, 0, "ほっぺがお餅みたいに\nぷにぷに…\n食べ過ぎちゃった…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 108801, 2833, 1, 0, 101, 0, "来年も、再来年も\nみんなで一緒に\n新年を迎えたいな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 108801, 2834, 1, 0, 102, 0, "お願いです…\nいつかこの気持ちが\n伝わりますように…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108801, 2835, 1, 0, 1, 0, "髪飾りが落ちそう？\nじゃあ…騎士クン\nつけ直してくれる？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108801, 2836, 1, 0, 2, 0, "着物が着たいの？\nじゃあ今度一緒に\n買いに…行く？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108801, 2837, 1, 0, 3, 0, "あっ、騎士クン\nお雑煮のお餅\nいくつ入れたい？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108801, 2838, 1, 0, 4, 0, "外、寒かったよね？\nはいこれ。熱い甘粥\n飲むと温まるよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 108801, 2839, 1, 0, 101, 0, "今年は騎士クンと…\nえっ？　騎士クン！？\nな、なんでもないよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 108801, 2840, 1, 0, 102, 0, "騎士クンの願い事\nわたしが\n叶えてあげたいな…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108801, 2841, 1, 1, 1, 0, "おはよう\n太陽の光に当たると\n元気が出るよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108801, 2842, 1, 2, 2, 0, "お昼ごはん\n作らなきゃ…\n騎士クンも食べる？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108801, 2843, 1, 3, 3, 0, "一日が終わるのは\nあっという間だよね\n一年も同じなのかな？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108801, 2845, 1, 0, 2, 0, "ふぅ…\n帯も少し\nゆるめちゃおう…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108801, 2846, 1, 0, 3, 0, "お正月用に\n着物を縫って\nあげたいな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108801, 2847, 1, 0, 4, 0, "これでお肌が\n保湿出来たら\nいいのになぁ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108801, 2848, 1, 0, 5, 0, "ん～…\nぬくぬくしてて\n動きたくないよ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108801, 2850, 1, 0, 7, 0, "…冬眠しないのに\n熊さんみたいに\n食べちゃいそう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108801, 2851, 1, 0, 8, 0, "年末年始は\n告白のチャンス…\nむ、無理無理！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108701, 2853, 1, 0, 1, 0, "みんな忙しそう…\n待っててね、\nすぐ助けるから！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108701, 2854, 1, 0, 2, 0, "ユイちゃんと\nレイさん…よし！\nどっちも手伝おう！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108701, 2855, 1, 0, 3, 0, "あっ、着崩れてる！\nわわっ、どうしよ！\n帯が結べないよー！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108701, 2856, 1, 0, 4, 0, "初詣楽しみー！\n出店はどこから\n行こうかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108701, 2857, 1, 0, 5, 0, "お正月だし\nみんなで楽しく\n遊んじゃおー！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 108701, 2858, 1, 0, 101, 0, "うーん…\n騎士くんはお正月\n何してるんだろ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 108701, 2859, 1, 0, 102, 0, "ううぅー…\n騎士くんを見てると\nドキってなっちゃう…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108701, 2860, 1, 0, 1, 0, "あたしから\nおいしそうな匂い？\nくんくん…ソースだ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108701, 2861, 1, 0, 2, 0, "ヘアピンが…\n拾ってくれたんだ！\nありがとう騎士くん！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108701, 2862, 1, 0, 3, 0, "髪飾りがかわいい？\n耳の近く触りたい？\n…優しくね？　", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108701, 2863, 1, 0, 4, 0, "グローブで食べるの\n大変そう？　特訓して\n今はお箸も使えるよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 108701, 2864, 1, 0, 101, 0, "えっ、ほっぺが赤い？\nそうかな…？\n気のせい気のせい！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 108701, 2865, 1, 0, 102, 0, "騎士くんといるとね…\nなんでもない！\nまだ内緒っ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108701, 2866, 1, 1, 1, 0, "おはよー！\n今日も明るく元気に\nがんばろー！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108701, 2867, 1, 2, 2, 0, "お腹すいてない？\n一緒にご飯食べよ！\n何にしようかなー♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108701, 2868, 1, 3, 3, 0, "今日もいっぱい\nがんばったー！\nいつも？　…そう？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108701, 2870, 1, 0, 2, 0, "休憩休憩♪\nちょっと休んで\nガンガン動くぞー！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108701, 2871, 1, 0, 3, 0, "かわいい♪\nでも、壊しそうで\nちょっと怖い…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108701, 2872, 1, 0, 4, 0, "はぁー♪\nいい香りー…\n気分スッキリ♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108701, 2873, 1, 0, 5, 0, "ほっこりしてて\nぬくぬく…\n丸まりたい…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108701, 2875, 1, 0, 7, 0, "ど、どうしよう…！\n手が、止まんないよ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 108701, 2876, 1, 0, 8, 0, "へー…お年玉って\n昔はお餅だったんだ\n…今のほうがいいね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109001, 2878, 1, 0, 1, 0, "甘いチョコの香りを\n漂わせて…ふふっ\nこれで準備万端ですわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109001, 2879, 1, 0, 2, 0, "ここを私のチョコで\n埋め尽くしますわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109001, 2880, 1, 0, 3, 0, "エプロンを着て\nあの方を待つ私…\nまるで…クスクス", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109001, 2881, 1, 0, 4, 0, "キッチンはどこかしら\n今のうちに作って\nおきたいものが…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109001, 2882, 1, 0, 5, 0, "あのお方を私のチョコ\nなしでは生きられない\n体に…ふふっ\n冗談ですわ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 109001, 2883, 1, 0, 101, 0, "このチョコは運命の味\n一口食べれば私の虜…\nそれも運命ですわ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 109001, 2884, 1, 0, 102, 0, "私ともあろうものが\nなんだかドキドキして\nきましたわ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109001, 2885, 1, 0, 1, 0, "あなた様。私に\n隠しているものなど\nありませんよね…？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109001, 2886, 1, 0, 2, 0, "私の手作りチョコです\n味の方にも自信\nありますのよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109001, 2887, 1, 0, 3, 0, "少し苦いのは\nチョコがビターなだけ\nお気になさらず…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109001, 2888, 1, 0, 4, 0, "解毒薬はどこに\nいれたかしら…？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 109001, 2889, 1, 0, 101, 0, "運命で結ばれている\nあなた様と私…\nチョコも特別でないと\nいけませんわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 109001, 2890, 1, 0, 102, 0, "チョコを食べた後は\nメインディッシュ…\nさぁ、私を召し上がれ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109001, 2891, 1, 1, 1, 0, "目覚めのチョコですわ\nすっきり目が覚めます\nしばらく眠れなく\nなりますけど…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109001, 2892, 1, 2, 2, 0, "おやつのチョコですわ\nデザートにどうぞ\n空腹を感じなく\nなりますわ…クスクス", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109001, 2893, 1, 3, 3, 0, "お夜食のチョコですわ\nとてもよく眠れます\nさ、溶けてしまう前に", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109001, 2895, 1, 0, 2, 0, "この心地良さ…\n参考になりますわ\n次の薬は…クスクス", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109001, 2896, 1, 0, 3, 0, "この中、何か\n入りそうですわね\n…チョコレート、とか", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109001, 2897, 1, 0, 4, 0, "いい香り…この香りと\n同じものを調合して\n私も持っていたい", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109001, 2898, 1, 0, 5, 0, "この暖かさ…\nチョコが溶けないよう\n気を付けなくては", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109001, 2900, 1, 0, 7, 0, "いい味をしていますわ\n私も腕を磨きましょう\n料理と、調合の腕を…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109001, 2901, 1, 0, 8, 0, "勉強になりますわ\n知識はいくらあっても\n困りませんもの\n特に、人体の知識は…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109101, 2903, 1, 0, 1, 0, "弟くんのことを\n考えると\nあっという間に時間が\n過ぎちゃう…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109101, 2904, 1, 0, 2, 0, "お姉ちゃんの\n元気の源は、弟くんの\n喜ぶ顔なんだから♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109101, 2905, 1, 0, 3, 0, "弟くんと会ったら\nまずは抱きしめて\nそれからそれから…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109101, 2906, 1, 0, 4, 0, "今日のお姉ちゃんは\n昨日のお姉ちゃんより\nいいお姉ちゃんだよ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109101, 2907, 1, 0, 5, 0, "弟くん、もっと\nお姉ちゃんに\n甘えてもいいのに♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 109101, 2908, 1, 0, 101, 0, "お姉ちゃんのチョコ\n食べさせてあげたい…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 109101, 2909, 1, 0, 102, 0, "ドキドキしてきた…\nお姉ちゃんなのに\nどうしてかなぁ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109101, 2910, 1, 0, 1, 0, "お姉ちゃんを\n好きなだけ\n触っていいよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109101, 2911, 1, 0, 2, 0, "そんなにお姉ちゃんの\nチョコが欲しいの？\n食いしん坊さん♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109101, 2912, 1, 0, 3, 0, "ナデナデするのは\nお姉ちゃんの仕事！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109101, 2913, 1, 0, 4, 0, "ありがとうなんて…\nお姉ちゃんとして\n当たり前だよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 109101, 2914, 1, 0, 101, 0, "どうしたのかなっ？\nお姉ちゃんはいつでも\n弟くんのそばにいるよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 109101, 2915, 1, 0, 102, 0, "チョコを作ったよ\n隠し味は、たくさんの\n弟くんへの愛情♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109101, 2916, 1, 1, 1, 0, "おはよ。お姉ちゃんが\n顔を洗うの\n手伝ってあげるね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109101, 2917, 1, 2, 2, 0, "ご飯を食べたら\n眠たくなるよね…\nお姉ちゃんと寝る？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109101, 2918, 1, 3, 3, 0, "そろそろ眠くなった？\nお姉ちゃんが添い寝\nしてあげるね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109101, 2920, 1, 0, 2, 0, "この気持ちよさは\nお姉ちゃんも太鼓判♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109101, 2921, 1, 0, 3, 0, "このかわいさは\nお姉ちゃんも大満足♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109101, 2922, 1, 0, 4, 0, "この香り、チョコに\n使えるかな…？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109101, 2923, 1, 0, 5, 0, "弟くんと一緒に\nぬくぬくしたいな…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109101, 2925, 1, 0, 7, 0, "すごくおいしいねっ♪\nあとで運動しなきゃ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109101, 2926, 1, 0, 8, 0, "本を読んでる\n知的なお姉ちゃん\n素敵でしょ♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106101, 2928, 1, 0, 1, 0, "アタシって…\nここにいても\nいいのか…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106101, 2929, 1, 0, 2, 0, "ちゃんとした建物に\nいるのが久しぶりで\nそわそわする…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106101, 2930, 1, 0, 3, 0, "ここで天楼覇断剣\n出す練習したら\nアイツら怒るかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106101, 2931, 1, 0, 4, 0, "ギルドハウスか…\nこういう隠れ家\n欲しかったんだよな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106101, 2932, 1, 0, 5, 0, "めちゃくちゃ暇だ…\n…なんかやること\nないのか？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 106101, 2933, 1, 0, 101, 0, "みんなが早く\nアタシのこと\n思い出さないかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 106101, 2934, 1, 0, 102, 0, "アイツはいいヤツだし\n戦う時はアタシも\n協力してやるか！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106101, 2935, 1, 0, 1, 0, "ちゃんはいらん！\nノウェムでいい！\n分かったか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106101, 2936, 1, 0, 2, 0, "どうした？\n戦うのか！？\n…なんだ、違うのか", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106101, 2937, 1, 0, 3, 0, "身長のことは\nあんまり言うな！\n気にしてるからな！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106101, 2938, 1, 0, 4, 0, "アタシの戦い方は\nなんでもアリだ！\nだから見習うなよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 106101, 2939, 1, 0, 101, 0, "オマエとの思い出か…\n敵として戦ってばっか\nだったな！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 106101, 2940, 1, 0, 102, 0, "オマエやオクトーと\n一緒に、あちこち\nいってみたいんだ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106101, 2941, 1, 1, 1, 0, "よっ！　おはよう！\n朝だぞー！\n起きてるかー？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106101, 2942, 1, 2, 2, 0, "腹減ったー！\n飯食うぞ、飯！\nオマエも行くよな？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106101, 2943, 1, 3, 3, 0, "ふわぁ…\nまだ起きてんのか？\n元気だなオマエ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106101, 2945, 1, 0, 2, 0, "ふぃ～…\n極楽極楽…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106101, 2946, 1, 0, 3, 0, "おいオマエ！\n寝技の練習に\n付き合ってもらうぞ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106101, 2947, 1, 0, 4, 0, "くんくん…\nたまにはこういうのも\n悪くないかもな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106101, 2948, 1, 0, 5, 0, "あー…\nもっと前にコイツと\n出会いたかった…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106101, 2950, 1, 0, 7, 0, "VRでも飯はうまい！\n腹が減るのはちゃんと\n生きてる気がするな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106101, 2951, 1, 0, 8, 0, "勉強は大事だな！\nうん！…だめだ！\n全然わからん！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101401, 2953, 1, 0, 1, 0, "探偵というのは\n謎を追い求める\n生き物なのさっ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101401, 2954, 1, 0, 2, 0, "事件のオチは\n往々にして\n取るに足らないものさ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101401, 2955, 1, 0, 3, 0, "読書で得る知識が\n探偵の\n血肉になるんだよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101401, 2956, 1, 0, 4, 0, "この前の\n捜査資料…\nどこにあったかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101401, 2957, 1, 0, 5, 0, "どんな小さな謎も\n私にとっては\n宝石みたいなものさ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 101401, 2958, 1, 0, 101, 0, "助手くんと一緒だと\nよい発想が浮かぶ…\nどうしてだろうね？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 101401, 2959, 1, 0, 102, 0, "助手くんの好きな\nタイプって…\nす、推理してみるっ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101401, 2960, 1, 0, 1, 0, "珈琲、淹れたてだよ\n一杯どうだい？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101401, 2961, 1, 0, 2, 0, "事件の考察を\nまとめてみたんだけど\n聞いてくれるかな？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101401, 2962, 1, 0, 3, 0, "小さな違和感を\n見逃さない…\n探偵の心得だよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101401, 2963, 1, 0, 4, 0, "機能的な手帳とペン…\nこの仕事には\n欠かせないね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 101401, 2964, 1, 0, 101, 0, "助手くんに\n珈琲…\n淹れてほしいなぁ～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 101401, 2965, 1, 0, 102, 0, "助手くんは\n私にとって\n最高の相棒だよ☆", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101401, 2966, 1, 1, 1, 0, "朝はいいひらめきが\n得られるんだ…\nさあ捜査を始めよう！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101401, 2967, 1, 2, 2, 0, "一休みしたら\n推理の答え合わせを\nしてみようか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101401, 2968, 1, 3, 3, 0, "夜の張りこみかい？\nなら私も\n同行させてほしいな！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101401, 2970, 1, 0, 2, 0, "脳を酷使した後は\nゆっくり休む…\nこれも仕事だよ？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101401, 2971, 1, 0, 3, 0, "この愛らしさ…\n要捜査対象だね！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101401, 2972, 1, 0, 4, 0, "思考が活性化される\n素敵な香りだね…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101401, 2973, 1, 0, 5, 0, "くぅ、心地よすぎる…\n今だけは捜査も\n一休みだね♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101401, 2975, 1, 0, 7, 0, "いっぱい食べて\nいっぱい考える\n探偵の本分だね！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 101401, 2976, 1, 0, 8, 0, "探偵だからね\nミステリには\n一家言あるんだ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105501, 2978, 1, 0, 1, 0, "先輩、素敵だなぁ…\nもっと近づければ\nいいのに…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105501, 2979, 1, 0, 2, 0, "せ、先輩がいた部屋！\n私には刺激が強い…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105501, 2980, 1, 0, 3, 0, "先輩を観察しやすい\n場所はあるかな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105501, 2981, 1, 0, 4, 0, "バ、バイト代出たし、\nせ、先輩をご飯とかに\n誘いたい…な", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105501, 2982, 1, 0, 5, 0, "今頃先輩は\nあの辺かな…？\n見に行ってみよう！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 105501, 2983, 1, 0, 101, 0, "私と先輩の出会いって\n思い返すと少女漫画\nみたい…えへへ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 105501, 2984, 1, 0, 102, 0, "せ、先輩になら\n観察されてもいいなぁ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105501, 2985, 1, 0, 1, 0, "バイト先に新しい子が\n入って来たんです…\nわ、私、先輩です♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105501, 2986, 1, 0, 2, 0, "せ、先輩…？\nえっと、あの、その、\nあわわわわ…！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105501, 2987, 1, 0, 3, 0, "えへへ…えへへ～\nせ、先輩から\n話しかけてくれました", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105501, 2988, 1, 0, 4, 0, "ま、また…\n先輩と一緒に\nバイトしたい…です", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 105501, 2989, 1, 0, 101, 0, "私と先輩が同じ空間に\n…ゆ、夢みたいです", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 105501, 2990, 1, 0, 102, 0, "せ、先輩のそばに\n居られて、私\n今、とっても\n幸せです…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105501, 2991, 1, 1, 1, 0, "せ、先輩…！\nおひゃっ！\nおはようございます！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105501, 2992, 1, 2, 2, 0, "先輩ランチに\n行きましょう！\nよし、練習はバッチリ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105501, 2993, 1, 3, 3, 0, "あ、せ、先輩。\nえと、そ、外…\nもう真っ暗ですねぇ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105501, 2995, 1, 0, 2, 0, "こ、ここで先輩も\n休んだり…はっ！\nわ、私ったらなにを！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105501, 2996, 1, 0, 3, 0, "かわいいですねー\n君も一緒に、\n先輩を観察する？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105501, 2997, 1, 0, 4, 0, "ここから先輩の\n香りがすればなあ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105501, 2998, 1, 0, 5, 0, "先輩と一緒に\nぬくぬくできたら\n最高なんだけどなぁ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105501, 3000, 1, 0, 7, 0, "おいし～\nこれは\n先輩も好きな味かな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 105501, 3001, 1, 0, 8, 0, "す、好きな人に\n近づく方法…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109201, 3003, 1, 0, 1, 0, "私の力は、大切な\n人たちを護るために\n役立てたいんだ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109201, 3004, 1, 0, 2, 0, "きれいな景色と\n美味しいご飯！\nこの世界も素敵だね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109201, 3005, 1, 0, 3, 0, "この世界のみんなとも\nもっと仲良く\nなりたいな！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109201, 3006, 1, 0, 4, 0, "わー！\nあれもこれも\n気になっちゃうな！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109201, 3007, 1, 0, 5, 0, "しっかりしなきゃって\n思ってても、\nグレアといるとつい\n甘えちゃうんだよね～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 109201, 3008, 1, 0, 101, 0, "違う世界でも\n友だちができて、\nすごくうれしいよ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 109201, 3009, 1, 0, 102, 0, "みんなとも一緒に\n授業とか受けたいな～\n絶対楽しそうだもん！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109201, 3010, 1, 0, 1, 0, "歩き方がきれい？\nえへへ、そうかな…？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109201, 3011, 1, 0, 2, 0, "勉強は好きだよ！\n知識が増えていくのは\nやっぱり楽しいから", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109201, 3012, 1, 0, 3, 0, "私たちの国マナリアは\n人々の間に魔法が\n伝えられた、その\n始まりの地なんだ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109201, 3013, 1, 0, 4, 0, "この部屋にも、\nもっとたくさん本棚が\n増えないかな～…？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 109201, 3014, 1, 0, 101, 0, "あなたも魔法、\n覚えてみる？\n私、教えるよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 109201, 3015, 1, 0, 102, 0, "お茶の時間に\nしたいな～…\n一緒にどう？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109201, 3016, 1, 1, 1, 0, "おはようっ！\n今日も素敵な\n一日にしようね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109201, 3017, 1, 2, 2, 0, "お昼ご飯は\nちゃんと食べた？\n午後も頑張ろうね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109201, 3018, 1, 3, 3, 0, "つい本の続きが\n気になって、夜更かし\nしちゃうんだよね～…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109201, 3020, 1, 0, 2, 0, "はぁ、気持ちいい～…\nちょっとだけ\n眠っちゃおうかな…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109201, 3021, 1, 0, 3, 0, "わあ、これ可愛い！\nグレアにも見せて\nあげたいな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109201, 3022, 1, 0, 4, 0, "いい香り～！\nなんだか気持ちが\n落ち着くね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109201, 3023, 1, 0, 5, 0, "あぁ、あったかくて\n幸せ～♪　身体が\nポカポカしてきたよ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109201, 3025, 1, 0, 7, 0, "こ、これ美味し～！\n止められないよー！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109201, 3026, 1, 0, 8, 0, "ふんふん…\n…はっ！　む、夢中に\nなっちゃってた…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109301, 3028, 1, 0, 1, 0, "新しい建物に入ると\n必ず一度は迷子に\nなっちゃうですよ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109301, 3029, 1, 0, 2, 0, "おやつはどこですか？\nお腹が空いて\nきちゃったですぅ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109301, 3030, 1, 0, 3, 0, "宿題、７つぐらい\n出しそびれたですけど\nまあ大丈夫ですぅ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109301, 3031, 1, 0, 4, 0, "不思議なこと\nばかりですけど…\nまぁ大丈夫です！\nこれもせいしゅんです", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109301, 3032, 1, 0, 5, 0, "なにか面白そうなもの\nないですかね～？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 109301, 3033, 1, 0, 101, 0, "ここのみんなとも\nもうお友だちですぅ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 109301, 3034, 1, 0, 102, 0, "おなかが減ったです…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109301, 3035, 1, 0, 1, 0, "ごはんも美味しいし\nここはいいところです\nルゥは幸せですぅ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109301, 3036, 1, 0, 2, 0, "魔法の話ですか？\nそれはアンせんぱいに\n聞いてほしいですぅ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109301, 3037, 1, 0, 3, 0, "あなたもお勉強\n苦手ですか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109301, 3038, 1, 0, 4, 0, "ルゥの帽子、\nかわいいです？\nお気に入りですぅ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 109301, 3039, 1, 0, 101, 0, "お友だちいっぱいで、\nルゥはうれしいです♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 109301, 3040, 1, 0, 102, 0, "一緒にお勉強…！？\nぐえぇ、イヤですぅ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109301, 3041, 1, 1, 1, 0, "朝ですねぇ…ふわわ…\nルゥはまだ夢の中に\nいたいですぅ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109301, 3042, 1, 2, 2, 0, "午後もいっぱい\n遊ぶですよ～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109301, 3043, 1, 3, 3, 0, "夜ふかしって\nドキドキするです！\nせいしゅんですぅ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109301, 3045, 1, 0, 2, 0, "ふかふかですぅ～♪\nおひるねが\nしたくなるですぅ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109301, 3046, 1, 0, 3, 0, "これふかふかですぅ！\n机でうとうとする時の\n枕によさそうですぅ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109301, 3047, 1, 0, 4, 0, "ふぁぁ、いいにおい…\nなんだか気持ちが\nよくなってきたですぅ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109301, 3048, 1, 0, 5, 0, "ぽっかぽかですぅ～！\nあったまったら\nまた遊びにいくですぅ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109301, 3050, 1, 0, 7, 0, "お、おいしいですぅ！\n全部ルゥに\nくださいですぅ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109301, 3051, 1, 0, 8, 0, "本は…読むと、\nすぐ眠気がぁ…\n…ぐぅ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109401, 3053, 1, 0, 1, 0, "ピアノ…\nどこかにないかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109401, 3054, 1, 0, 2, 0, "ふぅ…\n落ち着くな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109401, 3055, 1, 0, 3, 0, "マナリアはいつも\n賑やかなんだ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109401, 3056, 1, 0, 4, 0, "みんな…\n今頃何してるかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109401, 3057, 1, 0, 5, 0, "まさか違う世界に\n来ちゃうなんて…\nびっくりだよね…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 109401, 3058, 1, 0, 101, 0, "人と話すのは\n得意じゃないな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 109401, 3059, 1, 0, 102, 0, "ここのみんなと一緒に\n学院に通えたら…\nちょっと面白いかも", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109401, 3060, 1, 0, 1, 0, "やっぱり、私の翼や\n尻尾…気になる？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109401, 3061, 1, 0, 2, 0, "一番得意なのは、\nやっぱり炎魔法かな…\n加減は難しいけど…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109401, 3062, 1, 0, 3, 0, "私の故郷は竜の国…\n険しい山脈を\n越えた先にあるんだ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109401, 3063, 1, 0, 4, 0, "アン…？\nあっ、ご、ごめんね！\n間違えちゃった…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 109401, 3064, 1, 0, 101, 0, "ふん…ふん…♪\n…わっ！\n今の、聞いてた…？\nは、恥ずかしい…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 109401, 3065, 1, 0, 102, 0, "私に手伝えること、\nなにかある…？\nなんでも言ってね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109401, 3066, 1, 1, 1, 0, "朝の空気って\nすごく\n静かで透き通ってて…\nなんだか落ち着くよね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109401, 3067, 1, 2, 2, 0, "学院食堂のランチも\n美味しいけど…\nここのご飯も好きかな", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109401, 3068, 1, 3, 3, 0, "そろそろベッドに\n行かないとだね\n…寝相、今夜こそ\n気を付けよう…！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109401, 3070, 1, 0, 2, 0, "羽を伸ばしたく\nなっちゃうね…ふふ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109401, 3071, 1, 0, 3, 0, "わ、私が持つには\nかわいすぎるかな…\nでも、素敵だね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109401, 3072, 1, 0, 4, 0, "いい香り…\nこれ、\nアンも好きそう…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109401, 3073, 1, 0, 5, 0, "火を見ていると\n少し落ち着くんだ…\nへ、変かな？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109401, 3075, 1, 0, 7, 0, "わ、美味しい…！\nこれ、どうやって\n作ったのかな…？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109401, 3076, 1, 0, 8, 0, "この本…アンにも\n見せてあげたら\n喜ぶかな、ふふっ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109501, 3078, 1, 0, 1, 0, "着物はきれいですが\n歩きにくいです…\nいっそ脱いで…ぐふふ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109501, 3079, 1, 0, 2, 0, "あっ、いけません\n着物がはだけて…はっ\nもしやドＳさんが…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109501, 3080, 1, 0, 3, 0, "この部屋は妄想する\nのにうってつけです…\nぐふふ…じゅるり", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109501, 3081, 1, 0, 4, 0, "扇をゆっくりと開く\nこの感触…なぜだか\n艶めかしさを感じます", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109501, 3082, 1, 0, 5, 0, "「マッサージをすると\n言いながらドＳさんの\n手は次第に」…ぐふふ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 109501, 3083, 1, 0, 101, 0, "…ドＳさんの視線を\n思い出しながら踊りの\n練習です…イイ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 109501, 3084, 1, 0, 102, 0, "よいではないか\nよいではないか～\nいけませんご堪忍を～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109501, 3085, 1, 0, 1, 0, "あ～れ～～～！\nドＳさんもお好きな人\nですねぇ…ぐふふ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109501, 3086, 1, 0, 2, 0, "どこにいてもドＳさん\nの気配を感じ取れる\nようになりました…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109501, 3087, 1, 0, 3, 0, "こんな場所で\nなんというプレイを！\n…妄想でした", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109501, 3088, 1, 0, 4, 0, "クウカを押し倒して\nそのまま…じゅるり\nああっ、着物が…！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 109501, 3089, 1, 0, 101, 0, "ドＳさんは優しいです\n優しいのにドＳとは\nさすが人格破綻者…！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 109501, 3090, 1, 0, 102, 0, "ドＳさんの考えている\nことが伝わってきます\nはい…クウカ脱ぎます", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109501, 3091, 1, 1, 1, 0, "眩しい朝日がクウカの\n体を焦がします\n…それもイイ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109501, 3092, 1, 2, 2, 0, "お昼のランチは\nクウカがいいだなんて\n大胆過ぎです…じゅる", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109501, 3093, 1, 3, 3, 0, "…今夜のドＳさんは\n大胆です…ぐふふ…\nはっ、視線が…！？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109501, 3095, 1, 0, 2, 0, "この心地良さ、\n心が休まりすぎて\n体が疼いてきます…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109501, 3096, 1, 0, 3, 0, "すごく愛らしいです…\n妄想も出てこない\nくらい…じゅるり", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109501, 3097, 1, 0, 4, 0, "この香りに包まれて\nいると、いい気持ちに\nなって…いきますぅ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109501, 3098, 1, 0, 5, 0, "「熱を帯びた体は\nどうしようもなく\n誰かを求め…」ぐふふ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109501, 3100, 1, 0, 7, 0, "お腹が膨らんで\nいきます…それは\nまるで…ぐふふ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109501, 3101, 1, 0, 8, 0, "クウカの探している\nジャンルはどこに\nありますかね？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109601, 3103, 1, 0, 1, 0, "カチョーフーゲツは\nトーゴクの心を\n表した言葉デスね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109601, 3104, 1, 0, 2, 0, "オー！　新しい忍法を\n思いついたデス！\nさっそく試すデス！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109601, 3105, 1, 0, 3, 0, "トーゴクの天気には\n槍が降る日も\nあるそうデス…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109601, 3106, 1, 0, 4, 0, "我がショーグンは\n永遠にフメツデース\nいざ出陣デース！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109601, 3107, 1, 0, 5, 0, "壁に耳あり\n障子に…ん？\nメアリーって誰デス？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 109601, 3108, 1, 0, 101, 0, "ショーグンがいれば\nワタシはどんな\n忍法も使いこなせマス", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 109601, 3109, 1, 0, 102, 0, "不届き者はどこで\nゴザルデス？　忍法で\n退治してくれヨウ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109601, 3110, 1, 0, 1, 0, "ショーグン！　新しい\n忍法を覚えたデス！\n見てほしいデース！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109601, 3111, 1, 0, 2, 0, "レッツ手裏剣道デス！\n投げてソウロウで\nゴザルデース！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109601, 3112, 1, 0, 3, 0, "ふりかかる火の粉は\nワタシが払うデス！\nバンバン！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109601, 3113, 1, 0, 4, 0, "とっておきの忍法が\n吹き荒れるデス！\nよそ見はダメデスヨ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 109601, 3114, 1, 0, 101, 0, "ショーグンは\n何階建てのお城を\n建てたいデスか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 109601, 3115, 1, 0, 102, 0, "テンシュカクから\n平和な城下町を\n眺めてみたいデス…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109601, 3116, 1, 1, 1, 0, "おはようございマス！\n本日はオヒガラモヨク\n良い日になりそうデス", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109601, 3117, 1, 2, 2, 0, "お腹すいたデース…\nショーグンもご飯に\nしまショウ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109601, 3118, 1, 3, 3, 0, "闇討ちに遭わぬよう\n夜は気を付けて\n歩くデスよー？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109601, 3120, 1, 0, 2, 0, "天下分け目の\n戦いに備えて…\nマッタリデース", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109601, 3121, 1, 0, 3, 0, "口寄せの術で\n召喚したいくらい\nかわいいデース", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109601, 3122, 1, 0, 4, 0, "いい匂いデ～ス\n香り玉の開発の\n参考になりそうデスね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109601, 3123, 1, 0, 5, 0, "ワタシの懐よりも\nゾーリが暖まりマス…\nライバル出現デス…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109601, 3125, 1, 0, 7, 0, "腹が減っては\n戦はできぬデース！\n天下統一に向けて\n腹ごしらえデス！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109601, 3126, 1, 0, 8, 0, "ムム、暗号文デス！？\n解読には時間が\nかかりそうデース…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109701, 3128, 1, 0, 1, 0, "掃除も終わりましたし\nそろそろ、食事の\n準備をしなくては", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109701, 3129, 1, 0, 2, 0, "異世界の演劇…\n許されるならば、\n観てみたいです", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109701, 3130, 1, 0, 3, 0, "こちらの世界には、\nマヨネーズが普通に\n存在するのですね…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109701, 3131, 1, 0, 4, 0, "こちらの世界は食材も\n面白い物が多いですね\nなにかレシピを\n考えてみましょうか", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109701, 3132, 1, 0, 5, 0, "みょんみょんみょん…\n魔法の練習は\nこれぐらいですね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 109701, 3133, 1, 0, 101, 0, "倒木の撤去で思わぬ\n収入となりました\nサレン様に\nお渡ししておかないと", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 109701, 3134, 1, 0, 102, 0, "料理を美味しく\n召し上がって頂けると\nメイド冥利につきます", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109701, 3135, 1, 0, 1, 0, "お出かけでしたら、\nレムも同行\nさせてくださいね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109701, 3136, 1, 0, 2, 0, "レムにできること\nでしたら、なんでも\nお申し付けください", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109701, 3137, 1, 0, 3, 0, "じーー。いえ、\nその髪の長さが少し、\nとても少し、かなり\n少し気になったもので", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109701, 3138, 1, 0, 4, 0, "鉄球ですか？\nあれは護身用ですので\n問題はありませんよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 109701, 3139, 1, 0, 101, 0, "計算の勉強は\n進んでおりますか？\nレムでよろしければ、\nいつでもお教えします", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 109701, 3140, 1, 0, 102, 0, "次はもっと、大きな\n魔物料理に挑戦します\nワイバーンの姿焼き\nなんてどうでしょうか", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109701, 3141, 1, 1, 1, 0, "おはようございます\n朝食の準備が\nできておりますので、\n食卓におこしください", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109701, 3142, 1, 2, 2, 0, "昼食の時間ですね\n何か食べたいものは\nありますか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109701, 3143, 1, 3, 3, 0, "寝ないのか、ですか？\nメイドの就寝は、\n屋敷で最後と\n決まっておりますので", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109701, 3145, 1, 0, 2, 0, "ふんわり…\nこのふんわりさは、\n鬼をも黙らせます…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109701, 3146, 1, 0, 3, 0, "ふふっ…異世界でも\nかわいいものは\n共通なのですね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109701, 3147, 1, 0, 4, 0, "よい香りですね\n魔女教の悪臭とは\n大違いです", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109701, 3148, 1, 0, 5, 0, "ぬくぬくです…はっ！\nこれはいけません！\n人を…いえ、\n鬼をだめにします！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109701, 3150, 1, 0, 7, 0, "はむはむ…美味しい！\nこの味を再現できたら\n褒めてもらえる\nかもしれません…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109701, 3151, 1, 0, 8, 0, "絵がいっぱい…\n異世界の本は\nとても面白いですね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109801, 3153, 1, 0, 1, 0, "異世界であろうと、\nラムのやるべきことは\n変わらないわ\nまずは休憩するわよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109801, 3154, 1, 0, 2, 0, "マヨネーズがある\nということは、\nバルスの故郷が近い\nのかもしれないわね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109801, 3155, 1, 0, 3, 0, "この世界では、バルス\nの言う言葉が使われる\nときがあるのね\n忌々しいかぎりだわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109801, 3156, 1, 0, 4, 0, "ラムたち以外にも\nメイドが居るのね\nラムが…いえ、\n仕事を押しつけるわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109801, 3157, 1, 0, 5, 0, "…千里眼で視るかぎり\n周囲に異変はないわね\n危険がないなら、\nあとはレムに任せるわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 109801, 3158, 1, 0, 101, 0, "…ラムもまた、\n「お姉ちゃん」って\n呼ばれる日が\nくるのかしらね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 109801, 3159, 1, 0, 102, 0, "ハッ！　ラムが作る\n蒸かし芋は完璧ね\n…気が向いたら、また\n作ってやろうかしら", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109801, 3160, 1, 0, 1, 0, "ラムに構う暇が\nあるなら働きなさい\nそうすれば、\nラムが楽をできるわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109801, 3161, 1, 0, 2, 0, "ラムに何かを\n期待しても無駄よ\nラムに命令できるのは\nロズワール様だけだわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109801, 3162, 1, 0, 3, 0, "暇なら買い物に\n付き合いなさい\n荷物を\n持たせてあげるわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109801, 3163, 1, 0, 4, 0, "ラムになんの用？\nふざけた内容なら\nねじ切るわよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 109801, 3164, 1, 0, 101, 0, "そういえば、あなたは\n記憶喪失だったわね…\n思い出す努力をなさい\n…家族が悲しむわよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 109801, 3165, 1, 0, 102, 0, "蒸かし芋が欲しい？\n随分と厚かましいわね\n…まだ残りがあるから\n好きに食べなさい", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109801, 3166, 1, 1, 1, 0, "だらしない顔ね\n顔を洗って\nしゃんとなさい", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109801, 3167, 1, 2, 2, 0, "お腹が空く時間ね\nラムの昼食を\n用意させてあげるわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109801, 3168, 1, 3, 3, 0, "メイドが寝るのは\n屋敷で最後…\nラムが寝るために、\nさっさと寝なさい", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109801, 3170, 1, 0, 2, 0, "…ここから動かない\nという、誓いを\n立てたくなるわね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109801, 3171, 1, 0, 3, 0, "これは…かわいいわね\nもちろん、\nラムほどではないけど", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109801, 3172, 1, 0, 4, 0, "…香炉かしら？\nこちらの世界にも\n質のよい物があるのね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109801, 3173, 1, 0, 5, 0, "見つけてしまったの\nかもしれないわ…\nラムの楽園を！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109801, 3175, 1, 0, 7, 0, "…はむっ\nラムの蒸かし芋ほど\nではないけど、\nなかなかいけるわね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109801, 3176, 1, 0, 8, 0, "異世界ならではの\n面白い内容もあるわね\nとても興味深いわ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109901, 3178, 1, 0, 1, 0, "ねえパック、\nにゃんにゃか\nにゃかにゃんしよっか", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109901, 3179, 1, 0, 2, 0, "時間もあるし、\nこっちの世界の石で\n石積みしようかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109901, 3180, 1, 0, 3, 0, "私もお姉さんだし、\nサレンさんを\n見習わないと…うん！\n私、すごーく頑張る！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109901, 3181, 1, 0, 4, 0, "この世界は、銀髪で\nハーフエルフでも受け\n入れてくれるのね…\nなんだか嬉しいな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109901, 3182, 1, 0, 5, 0, "今日のご飯…\nピーマルは\n入ってないわよね…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 109901, 3183, 1, 0, 101, 0, "また、みんなで\n一緒に買い物に\n行きたいな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 109901, 3184, 1, 0, 102, 0, "パック以外から\n家族って呼ばれたの\n初めて…ふふっ♪\nちょっと嬉しいかも！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109901, 3185, 1, 0, 1, 0, "今からパックの\n毛繕いをするけど…\n一緒にする？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109901, 3186, 1, 0, 2, 0, "私も何かお手伝いを…\nあ、そうだ！\n料理とかどうかな！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109901, 3187, 1, 0, 3, 0, "ねえ、ここで\n歌の練習って、\nしてもいいかな？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109901, 3188, 1, 0, 4, 0, "…あ、ごめんなさい！\n話しかけられるのって\nあまり慣れてなくて…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 109901, 3189, 1, 0, 101, 0, "すごーく頑張るから、\nこれからも勉強を\n手伝ってね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 109901, 3190, 1, 0, 102, 0, "せーの、びくとりー！\nふふっ…また、一緒に\nラジーオタイソー\nしようね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109901, 3191, 1, 1, 1, 0, "ちゃんと起きてる？\nふふっ…よかった。\nあ、べ、別に心配した\nわけじゃないからね？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109901, 3192, 1, 2, 2, 0, "お昼ご飯が楽しみね♪\nあ、こっちの世界って\nまよねーずはあるの？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109901, 3193, 1, 3, 3, 0, "そろそろ寝る時間よ。\n夜更かししたら、\nめっ！　だからね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109901, 3195, 1, 0, 2, 0, "勉強も終わったし…\nちょっ、ちょっとだけ\n休んでもいいよね…？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109901, 3196, 1, 0, 3, 0, "か、かわいい…！\nパックのかわいさに\n匹敵するかも…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109901, 3197, 1, 0, 4, 0, "良い香り…\nロズワールの屋敷にも\nこういうのって\nあるのかな？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109901, 3198, 1, 0, 5, 0, "あったかい…\nずっとここに…はっ！\nダメダメ！　王選の\nために勉強しないと！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109901, 3200, 1, 0, 7, 0, "あ…これ美味しい！\nこっちの世界は色んな\n食べ物があるのね。", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 109901, 3201, 1, 0, 8, 0, "知らないことが\nいっぱい書いてある…\n異世界の本って、\n面白いのね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110001, 3203, 1, 0, 1, 0, "海ってマジ最高！\nビーチの撮影も\n超楽しいしー♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110001, 3204, 1, 0, 2, 0, "20パーって何パ？\n今度ヒデサイに\n聞いてみよっ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110001, 3205, 1, 0, 3, 0, "夏の思い出を\nヒデサイと一緒に\n作りたいな～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110001, 3206, 1, 0, 4, 0, "テンションあがる\n水着ゲットして\n超ラッキーだよ～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110001, 3207, 1, 0, 5, 0, "休憩が終わったら\n撮影にもどらないと！\n気合入れてこ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 110001, 3208, 1, 0, 101, 0, "ヒデサイとビーチで\nパーティーしたいな～\n超楽しそうだよね！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 110001, 3209, 1, 0, 102, 0, "ヒデサイといると\n楽しくてうれしくて…\nカレシってこんな感じ\nなのかなぁ？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110001, 3210, 1, 0, 1, 0, "見て見て～！\n夏らしいネイルに\nしてみたんだ～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110001, 3211, 1, 0, 2, 0, "ヒデサイ～\nこの水着どうかな？\nイケてるっしょ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110001, 3212, 1, 0, 3, 0, "水着姿の写真？\nもちいいよ！\nかわいく撮ってね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110001, 3213, 1, 0, 4, 0, "うちだって\n夏の海みたいに\nキラキラしちゃうよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 110001, 3214, 1, 0, 101, 0, "ヒデサイとうちの\nフィーリングって\nめっちゃエモいよね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 110001, 3215, 1, 0, 102, 0, "ねーねーヒデサイ！\n一緒に泳ぎにいこっ！\nほら、手繋いで♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110001, 3216, 1, 1, 1, 0, "水着オッケー！\nネイルもばっちり！\n朝から遊んじゃおー♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110001, 3217, 1, 2, 2, 0, "お昼食べたら\n昼寝して\nリフレッシュしよー♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110001, 3218, 1, 3, 3, 0, "水着ってマジで\n最強かも…このまま\nお風呂にも入れるし♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110001, 3220, 1, 0, 2, 0, "マジ気持ちいいー♪\nおに癒やされるん\nですけど～！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110001, 3221, 1, 0, 3, 0, "やばばばばー！\nかわいすぎだってば！\nマジらぶだよ～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110001, 3222, 1, 0, 4, 0, "あ、この香り超好き！\nモデル仲間に\n教えたげよ～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110001, 3223, 1, 0, 5, 0, "あったか～い♪\n夏の太陽みたい！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110001, 3225, 1, 0, 7, 0, "マジ美味しいねこれ♪\nビーチで\n流行っちゃうかも！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110001, 3226, 1, 0, 8, 0, "夏のために\n最新ファッションを\n勉強ちう！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110001, 3228, 1, 0, 10, 0, "なにコレもにカワ～♪\nなんて生き物？\nヒデサイ知ってる？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110001, 3229, 1, 0, 11, 0, "うわっ超フカフカ～\nなんかうち眠く\nなってきちゃった…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110101, 3230, 1, 0, 1, 0, "海の家でのんびり\n過ごしましょう\n熱中症には注意してね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110101, 3231, 1, 0, 2, 0, "はぁ、どうやったら\n泳げるように\nなれるのかしら～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110101, 3232, 1, 0, 3, 0, "海で日焼け止めを\n塗らないと、あとで\n大変なことになるわよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110101, 3233, 1, 0, 4, 0, "久しぶりに水着を\n着たんだけど\n変じゃなかったかな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110101, 3234, 1, 0, 5, 0, "波の音って落ち着くわ\n授業中に聞いたら\nみんな寝ちゃうかも？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 110101, 3235, 1, 0, 101, 0, "海が楽しいって\n思えたのも\nあの子のおかげね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 110101, 3236, 1, 0, 102, 0, "あの子と二人で海に\n来られてよかったわ\n一日があっという間ね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110101, 3237, 1, 0, 1, 0, "男の子と海だなんて\n少女漫画みたいで\n憧れてたの～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110101, 3238, 1, 0, 2, 0, "夏休みの宿題は\nちゃんとやった？\n遊んでばかりではダメ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110101, 3239, 1, 0, 3, 0, "昼の海と夜の海\nどっちが好き？　私は\n君といる海が一番よ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110101, 3240, 1, 0, 4, 0, "ふう、疲れたわ～\n冷たいジュースで\n元気回復ね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 110101, 3241, 1, 0, 101, 0, "海で見た君の背中\nすごく大きく見えたわ\nやっぱり男の子なのね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 110101, 3242, 1, 0, 102, 0, "君と一緒に見た\n夜の海…\n忘れられそうにないわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110101, 3243, 1, 1, 1, 0, "準備ができたら\n海に出かけようよ\n今日も楽しみましょう", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110101, 3244, 1, 2, 2, 0, "あら、もうお昼？\n海の家でランチに\nしましょ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110101, 3245, 1, 3, 3, 0, "夜の海岸についてきて\nくれる？　暗がりは\n一人じゃ怖くて…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110101, 3247, 1, 0, 2, 0, "泳ぐ練習をしたら\n疲れちゃったわ～\nちょっと一休み…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110101, 3248, 1, 0, 3, 0, "かわいいわね～\n浮き輪に乗せて\n海に浮かべたいわ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110101, 3249, 1, 0, 4, 0, "この匂い…\nなんだか夏の海を\n思い出すわ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110101, 3250, 1, 0, 5, 0, "夜の海風で冷えない\nように、しっかりと\n身体を暖めましょうね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110101, 3252, 1, 0, 7, 0, "海で遊ぶ前に\nしっかり食べて\n体力つけましょう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110101, 3253, 1, 0, 8, 0, "夏休み中でも\nしっかりお勉強\nしないと！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110101, 3255, 1, 0, 10, 0, "この子に乗れば\n海の上でも\nすいすい進めるかしら", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110101, 3256, 1, 0, 11, 0, "ビーナスの誕生！\n…なんちゃって\nはしゃぎすぎかしら？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110301, 3257, 1, 0, 1, 0, "夏の海には商機が\nあるはず…\nのがす手はないわね♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110301, 3258, 1, 0, 2, 0, "あたしが作った\n日焼け止め…\nこれは売れそうね！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110301, 3259, 1, 0, 3, 0, "子どもたちのために\n浮き輪を用意して\nおかないと…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110301, 3260, 1, 0, 4, 0, "海っていいわよね！\nみんなで羽を伸ばしに\n行きたいわ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110301, 3261, 1, 0, 5, 0, "波の音を聞いて\nのんびりするのも\n悪くないわね…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 110301, 3262, 1, 0, 101, 0, "ただの砂遊びも\nあんたと一緒なら\n楽しいから不思議ね…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 110301, 3263, 1, 0, 102, 0, "あんたがあたしの\nなんであれ、大切な\n存在ではあるのよね…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110301, 3264, 1, 0, 1, 0, "夏の海って\nすごい人よね…\n迷子にならないでよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110301, 3265, 1, 0, 2, 0, "ぼんやり海に浮かんで\n波に揺られるのも\n悪くないわよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110301, 3266, 1, 0, 3, 0, "ビーチで子どもたちと\n一緒に砂のお城を\n作りたいわ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110301, 3267, 1, 0, 4, 0, "海で食べる\n焼きそばパン…\nいいかもしれないわね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 110301, 3268, 1, 0, 101, 0, "あたしの水着\n似合ってる？\nそう、ありがとう♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 110301, 3269, 1, 0, 102, 0, "あんたが居ると\nうれしい…って、\n子どもたちが\n言ってたわ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110301, 3270, 1, 1, 1, 0, "朝の浜辺って\nいいわよね\n散歩しに行きたいわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110301, 3271, 1, 2, 2, 0, "海の家のご飯って\nなんであんなに\nおいしいのかしら♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110301, 3272, 1, 3, 3, 0, "夜の海って、\nロマンチックよね。\nずっと眺めてたいわ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110301, 3274, 1, 0, 2, 0, "やわらかくて\n気持ちいい…\nこのまま寝ちゃいそう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110301, 3275, 1, 0, 3, 0, "海に一緒に入れる\nぬいぐるみとか\nないかしら？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110301, 3276, 1, 0, 4, 0, "夏らしい\nいい香りね…\nなんの香りかしら？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110301, 3277, 1, 0, 5, 0, "あったかい…\n濡れた体を乾かすには\nちょうどいいわね♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110301, 3279, 1, 0, 7, 0, "泳いだ後の食事は\n普段の何倍も\nおいしく感じるわね！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110301, 3280, 1, 0, 8, 0, "海に行く前にいろいろ\n調べなきゃね。備え\nあれば憂いなしよ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110301, 3282, 1, 0, 10, 0, "海の守り神？\nずいぶんかわいらしい\n神様ね♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110301, 3283, 1, 0, 11, 0, "このぐらい\n大きな貝があったら\n食費も浮くかしら？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110401, 3284, 1, 0, 1, 0, "浜辺で食うものって\nなんであんなに\nうまいのかな～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110401, 3285, 1, 0, 2, 0, "いてて…\nちょっと日焼け\nしすぎたかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110401, 3286, 1, 0, 3, 0, "砂浜で走ると\nいいトレーニングに\nなるんだぜ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110401, 3287, 1, 0, 4, 0, "夏ならではの\n料理…か\n何がいいかな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110401, 3288, 1, 0, 5, 0, "やっぱりここは\n涼しくて快適だな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 110401, 3289, 1, 0, 101, 0, "泳ぎの練習だったら\nいつでも付き合って\nやるからな！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 110401, 3290, 1, 0, 102, 0, "水着で部屋の中に\nいるのって\n変な感じだな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110401, 3291, 1, 0, 1, 0, "うおっ！\nきゅ、急に\n触るなって…\nびっくりした", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110401, 3292, 1, 0, 2, 0, "え、砂がついてた？\n払ってくれたのか\nありがとよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110401, 3293, 1, 0, 3, 0, "夏バテしてないか？\nここで休んでけよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110401, 3294, 1, 0, 4, 0, "…そう見られると\n照れるっていうか…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 110401, 3295, 1, 0, 101, 0, "カキ氷とか\n食いたくないか？\n氷、削ってやるよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 110401, 3296, 1, 0, 102, 0, "おまえと\n夏の思い出ができて\nよかったよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110401, 3297, 1, 1, 1, 0, "ふう…\n朝一番で泳ぐの\nやっぱ気持ちいいな", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110401, 3298, 1, 2, 2, 0, "一番あちぃ時間だ\n水分補給は\nこまめにな！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110401, 3299, 1, 3, 3, 0, "今日もお疲れ\n明日のパトロールも\nよろしくな", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110401, 3301, 1, 0, 2, 0, "泳ぎ疲れたからな\n少し休憩だ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110401, 3302, 1, 0, 3, 0, "か、かわいい…\nいや、なんでもねぇ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110401, 3303, 1, 0, 4, 0, "この香り落ち着くな\nこういうのも\n悪くねーかも…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110401, 3304, 1, 0, 5, 0, "あったけえ…！\n泳ぎ終わった後って\nけっこう体冷えてよ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110401, 3306, 1, 0, 7, 0, "海で食べると\nまた一味違うよな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110401, 3307, 1, 0, 8, 0, "夏のサバイバル特集…\nちょっと読んでみるか", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110401, 3309, 1, 0, 10, 0, "へー！　こいつ、\nよくできてんな！\nいいなあ、実物も\n乗れたらいいのに", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110401, 3310, 1, 0, 11, 0, "か、かわ…！\nいや、じゃなくて\nあ、あたしがここに\n寝ていいのか…？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110501, 3311, 1, 0, 1, 0, "故郷の花を飾っても\nいいかな～？\n部屋が楽しくなるよ～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110501, 3312, 1, 0, 2, 0, "水着で歩いてると\n泳ぎたくなるさ～\n我慢できないさ～！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110501, 3313, 1, 0, 3, 0, "夏が眩しいのは\nてぃーだがにこにこ\n笑ってるからさ～！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110501, 3314, 1, 0, 4, 0, "泳ぎ疲れたら\n涼しい木陰で\nお昼寝するさ～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110501, 3315, 1, 0, 5, 0, "クロールも\n犬かきも\n大得意さ～！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 110501, 3316, 1, 0, 101, 0, "なんだか歌いたい\n気分さ～\nキミもどう～？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 110501, 3317, 1, 0, 102, 0, "海の近くに住んで\nキミと歌って踊って\nそれが理想さ～！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110501, 3318, 1, 0, 1, 0, "私とキミ\nどっちがたくさん\n泳げるか\n競争するさ～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110501, 3319, 1, 0, 2, 0, "キミも三線に興味が\nあるのかな～？\n私が教えるさ～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110501, 3320, 1, 0, 3, 0, "嫌なことがあっても\n私と歌って踊れば\nなんくるないさー！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110501, 3321, 1, 0, 4, 0, "たまには時間を忘れて\n私とのーんびり\n海を眺めるさ～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 110501, 3322, 1, 0, 101, 0, "ぶるぶるぶるっ！\n毛が濡れちゃっても\nへっちゃらさ～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 110501, 3323, 1, 0, 102, 0, "泳ぐ前は準備体操～！\n背中押してあげるから\nしっかりやるさ～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110501, 3324, 1, 1, 1, 0, "朝の浜辺は\nちゅらさんよ～\n一緒に歩こうよー！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110501, 3325, 1, 2, 2, 0, "お昼ご飯は\n決まったかな～？\n私はタコライスさ～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110501, 3326, 1, 3, 3, 0, "星と波音の中で\n三線鳴らすのも\nオツなものさ～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110501, 3328, 1, 0, 2, 0, "ちょうどよかったさー\nゆっくり休んだら\nまた泳ぎに行くさ～！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110501, 3329, 1, 0, 3, 0, "砂浜に飾って\nおきたいさ～\nきっとかわいいさ～！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110501, 3330, 1, 0, 4, 0, "くんくん…はあ\n癒されるさ…\n故郷の夏の匂いさ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110501, 3331, 1, 0, 5, 0, "おおー…！\n濡れた髪が\nみるみる乾いてくさ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110501, 3333, 1, 0, 7, 0, "ん～！　これ、\n海で食べたら\nもっとおいしいさ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110501, 3334, 1, 0, 8, 0, "海の生物図鑑ね～\nこの前みかけた魚も\n載ってるかな～？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110501, 3336, 1, 0, 10, 0, "わぁ～！　みてみて！\n背中に乗れるさー！\nキミと一緒に\n乗りたいさ～！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110501, 3337, 1, 0, 11, 0, "ふかふかさ～\n海の底の\n人魚になった気分さ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 105801, 3338, 1, 0, 103, 0, "あの人が\nわたしの騎士だったら\nいいのにな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 105801, 3339, 1, 0, 103, 0, "あなたのおかげで\nわたしの心はいつも\nお腹いっぱいです♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 105901, 3340, 1, 0, 103, 0, "許されるのならば、\n今すぐにでも、\n主さまのおそばに\n行きたいです…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 105901, 3341, 1, 0, 103, 0, "主さま…\nお慕いしております…\nいつまでも、おそばに\n居させてください…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 106001, 3342, 1, 0, 103, 0, "なんか最近、\nあいつのことしか\n考えてない…\nあたし…どうしよう…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 106001, 3343, 1, 0, 103, 0, "そ、外に行くなら、\n連れていきなさいよね\nその…あたしが\n守ってあげるから…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110601, 3344, 1, 0, 1, 0, "部屋の中は涼しくて\n気持ちええどすなぁ\n天国やわぁ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110601, 3345, 1, 0, 2, 0, "クラゲはんって\nフリルいっぱいの\nドレスみたいやねぇ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110601, 3346, 1, 0, 3, 0, "外でセミはんが\n元気に鳴いてはるねぇ\n今日もええ天気やわぁ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110601, 3347, 1, 0, 4, 0, "こないに暑いと\n海に入って\n涼みたくなるわぁ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110601, 3348, 1, 0, 5, 0, "いつか\nマホマホ王国にも\n海の家作りまひょ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 110601, 3349, 1, 0, 101, 0, "うちも\n付き合いますえ\n泳ぎの練習しまひょ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 110601, 3350, 1, 0, 102, 0, "うちの里帰り\n王子はんも一緒に\n来まへんか？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110601, 3351, 1, 0, 1, 0, "王子はんは日焼けより\n色白美人の方が\n好きでっしゃろか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110601, 3352, 1, 0, 2, 0, "うちと一緒に\n浮き輪で海、\nプカプカしまへん？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110601, 3353, 1, 0, 3, 0, "日が落ちたら\n線香花火\nやりたいわぁ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110601, 3354, 1, 0, 4, 0, "木陰でのんびり\nお昼寝…\n最高やね～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 110601, 3355, 1, 0, 101, 0, "お祭ではぐれても\n王子はんなら見つけて\nくれはります？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 110601, 3356, 1, 0, 102, 0, "楽しい夏やわぁ\nずっとこんな日が\nつづくとええね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110601, 3357, 1, 1, 1, 0, "うちにしては早起き？\n暑くて目が早う\n覚めてもうたんどす", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110601, 3358, 1, 2, 2, 0, "お昼は冷たい\nおそうめんとか\nどうでっしゃろ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110601, 3359, 1, 3, 3, 0, "夜は星がきれいやね\n二人で浜辺でも\n行きまへんか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110601, 3361, 1, 0, 2, 0, "うーん、遊び疲れた\n身体にちょうどええ\nほっこり具合やわぁ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110601, 3362, 1, 0, 3, 0, "かいらしい子は\nみーんな\nマホマホ王国の\n住人どすえ♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110601, 3363, 1, 0, 4, 0, "この香り…\n南国にいる気分やねぇ\n癒されるわぁ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110601, 3364, 1, 0, 5, 0, "これで\n冷えた身体も\nポカポカやねぇ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110601, 3366, 1, 0, 7, 0, "外はまだ暑いやろうし\nバテんよう\nしっかり食べんとね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110601, 3367, 1, 0, 8, 0, "海の中にもお城が\nあるんやねぇ\n行ってみたいわぁ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110601, 3369, 1, 0, 10, 0, "この子、ふかふかやね\nこのままマホマホ王国\n乗せてってくれはる？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110601, 3370, 1, 0, 11, 0, "人魚姫になっても\n最後は王子はんと\nハッピーエンド\nどすえ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107001, 3371, 1, 0, 1, 0, "真似したくなる\n人がここには\n大勢いますね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107001, 3372, 1, 0, 2, 0, "偽物を一人\n紛れこませました\n…冗談です", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107001, 3373, 1, 0, 3, 0, "動物に変身して\nその辺りを\n散歩してきます", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107001, 3374, 1, 0, 4, 0, "失った記憶は\nどの程度回復するの\nでしょうね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107001, 3375, 1, 0, 5, 0, "人や物だけでなく\nいずれは世界すら\n真似てみせます", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 107001, 3376, 1, 0, 101, 0, "次は彼に\nどんなことを\n教えましょうか…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 107001, 3377, 1, 0, 102, 0, "知識はともかく\n私の力はまだ\n必要なようですね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107001, 3378, 1, 0, 1, 0, "どうしました？\n…何でもない？\n…そうですか", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107001, 3379, 1, 0, 2, 0, "退屈しています\n芸でもして\n楽しませなさい", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107001, 3380, 1, 0, 3, 0, "研究のためです\n少し話し相手に\nなりなさい", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107001, 3381, 1, 0, 4, 0, "何か真似して欲しい？\n私の能力を何だと\n思っているのですか", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 107001, 3382, 1, 0, 101, 0, "貴方にはまだ色々と\n教えるべきことが\nありますね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 107001, 3383, 1, 0, 102, 0, "随分多くの女性と\n知り合いなのですね\n驚きました", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107001, 3384, 1, 1, 1, 0, "…\nおはようございます\n…まだ朝ですよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107001, 3385, 1, 2, 2, 0, "食事にします\n…どうしました？\n早く持ってきなさい", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107001, 3386, 1, 3, 3, 0, "早く寝なさい\n夜更かしすると\n背が伸びませんよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107001, 3388, 1, 0, 2, 0, "マサキより\n座り心地は\nいいですね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107001, 3389, 1, 0, 3, 0, "意外に複雑な\n構造ですね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107001, 3390, 1, 0, 4, 0, "真似るなら\n香りまで\n再現しなくては", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107001, 3391, 1, 0, 5, 0, "発火現象の\n模倣…なかなか\n難しいものです", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107001, 3393, 1, 0, 7, 0, "偽物や分身を作るには\nエネルギーが\n必要不可欠なのです", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 107001, 3394, 1, 0, 8, 0, "暇つぶしには\nちょうどよさそうです\n…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110701, 3396, 1, 0, 1, 0, "学院かぁ…\n友達の作り方の\n授業はないかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110701, 3397, 1, 0, 2, 0, "学院でぼっちに\nなったときのために、\n避難場所を\n探しておかないと…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110701, 3398, 1, 0, 3, 0, "ぼっちの私が学院…\nな、なんだか\n目まいがしてきました", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110701, 3399, 1, 0, 4, 0, "クラスメイトに\n話しかける練習…\nむ、難しすぎます…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110701, 3400, 1, 0, 5, 0, "目指せ友達100人…\n無理かな…10人…\nや、やっぱり一人！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 110701, 3401, 1, 0, 101, 0, "あなたと学院生活が\n送れるなんて、\nとっても\nうれしいです！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 110701, 3402, 1, 0, 102, 0, "えへへ…\n学院でも、\nあなたと一緒に\n居られてうれしいな♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110701, 3403, 1, 0, 1, 0, "が、学院ってあんなに\n人が居るんですね…\n友達作りのイメトレを\nやり直さないと！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110701, 3404, 1, 0, 2, 0, "私にとって\n部活動とはＢＢ団！\n今日もたくさん\n活動しましょうね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110701, 3405, 1, 0, 3, 0, "休み時間は寝たふり…\nご飯はおトイレ…\nぼっち対策は完璧です", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110701, 3406, 1, 0, 4, 0, "学院で友達を作る\n勉強は完璧ですよ！\n購買部でパンを買って\n渡すんですよね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 110701, 3407, 1, 0, 101, 0, "学院でも、\nBB団の活動を\nしていきましょうね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 110701, 3408, 1, 0, 102, 0, "ぼっちに学院生活は\n厳しいですね…でも、\nあなたと一緒なら\n乗りこえられそうです", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110701, 3409, 1, 1, 1, 0, "もうこんな時間…\nあの…一緒に学院に\n行ってくれませんか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110701, 3410, 1, 2, 2, 0, "お昼ですね！\n言ってくれれば、\nパンでもお握りでも\n買ってきますよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110701, 3411, 1, 3, 3, 0, "今日もぼっち…\nいつか友達と一緒に、\nパジャマパーティー\nしたいなぁ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110701, 3413, 1, 0, 2, 0, "森とは違う\n心地良さが…このまま\n眠っちゃいそうです…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110701, 3414, 1, 0, 3, 0, "かわいい…この子を、\n話す練習の相手役に\nしてみようかな？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110701, 3415, 1, 0, 4, 0, "いい香り…\nいつ友達ができても\nいいように、私の\n部屋にも置こうかな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110701, 3416, 1, 0, 5, 0, "こ、これは…\nぼっちで\n日陰者の私には\n暖かすぎます！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110701, 3418, 1, 0, 7, 0, "おいしい…！\nお弁当にして、学院に\n持っていけないかな？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110701, 3419, 1, 0, 8, 0, "学院の授業に\nついていくためにも、\n勉強しておこうかな…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110801, 3421, 1, 0, 1, 0, "あんた、何してんの。\nいいけど、なんでも", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110801, 3422, 1, 0, 2, 0, "邪魔。どいて。\nいや怒ってないし。\nコレがフツーだから", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110801, 3423, 1, 0, 3, 0, "いいけど、\n散らかしめの部屋だと\n片付けたくなるよね…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110801, 3424, 1, 0, 4, 0, "暇なときは明日の\nコーデ考えてるよ。\n考えるのはタダだし", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110801, 3425, 1, 0, 5, 0, "昔よくシオリン\n構いすぎてハツネに\n文句言われたなァ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 110801, 3426, 1, 0, 101, 0, "なんかさ。あんたって\n謎に女子トモ多いよね\n…いいけど、べつに", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 110801, 3427, 1, 0, 102, 0, "ねえ。あのさあ。\nあんた弁当で好きな\n具とかってあんの", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110801, 3428, 1, 0, 1, 0, "あ？　何。なんか用。\nや、ないならないで\nいいけど、べつに", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110801, 3429, 1, 0, 2, 0, "ごめ、ちょっと\n黙っててくんない。\n明日の試験ヤバだから", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110801, 3430, 1, 0, 3, 0, "歩き方が静か？\n癖になってるんだよね\n足音を殺すの", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110801, 3431, 1, 0, 4, 0, "うちの血液型？\n大雑把のОだけど。\nなんか文句あんの", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 110801, 3432, 1, 0, 101, 0, "ひゃんっ！\nや、だからさ…急に\n話しかけんなっての", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 110801, 3433, 1, 0, 102, 0, "な、何…あんまジッと\n見ないでくんない…\nなんか、その、ハズい", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110801, 3434, 1, 1, 1, 0, "早起きは得意な方だと\n思うけど。昔から\nそれがデフォだったし", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110801, 3435, 1, 2, 2, 0, "こんな時間か…\n昼ごはん\nサクッと作っちゃお", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110801, 3436, 1, 3, 3, 0, "あ？　夜遊び？\nはッ…そんなん\n５歳で卒業したっての", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110801, 3438, 1, 0, 2, 0, "あーヤバじゃんコレ…\nふかついてる場合\nじゃないっつーのに…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110801, 3439, 1, 0, 3, 0, "なんだこれ\nかわいいかよ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110801, 3440, 1, 0, 4, 0, "ふーん。\nこの匂い…\nなんかいいじゃん", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110801, 3441, 1, 0, 5, 0, "暖炉はアドじゃん。\n煮物、焼き物、蒸し物\nいろいろ作れそう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110801, 3443, 1, 0, 7, 0, "何コレ\n食べ物困んないじゃん\nうちにも一台くんない", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110801, 3444, 1, 0, 8, 0, "本かァー…んー\n学校の勉強だけで\nいいかなァ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111101, 3446, 1, 0, 1, 0, "イタズラってなにを\nしたらいいのかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111101, 3447, 1, 0, 2, 0, "カップケーキ\n上手に作れました\n食べてくれるかな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111101, 3448, 1, 0, 3, 0, "あの人、\nお菓子くれるかな？\nくれなかったら…ふふ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111101, 3449, 1, 0, 4, 0, "オバケ…いないよね？\n怖くなんてないけど！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111101, 3450, 1, 0, 5, 0, "ハロウィンは\n楽しいけどオバケの\nお祭りなんだよね…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 111101, 3451, 1, 0, 101, 0, "にゃ～、にゃお～！\nちゃんと怖いかな？\n早く帰ってこないかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 111101, 3452, 1, 0, 102, 0, "あの人のことだから\nお菓子欲しがるよね…\n準備はバッチリです♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111101, 3453, 1, 0, 1, 0, "えへへ。猫さんのお耳\nよく出来てるでしょ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111101, 3454, 1, 0, 2, 0, "触りましたね？\nイタズラしましたね？\nへんた～い！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111101, 3455, 1, 0, 3, 0, "フー！　頭を撫でたら\n怒りますよ！　あれ？\n猫みたいでした？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111101, 3456, 1, 0, 4, 0, "きゃっ！　も、もう、\n脅かさないでください\n怖くありませんけど！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 111101, 3457, 1, 0, 101, 0, "お菓子ですか？\nありがとうございます\nハッピーハロウィン♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 111101, 3458, 1, 0, 102, 0, "ゴロゴロ…\nね、猫さんの真似です\n…似てましたか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111101, 3459, 1, 1, 1, 0, "さぁ、お菓子作りを\n始めますよ。下準備は\n大事ですからね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111101, 3460, 1, 2, 2, 0, "今、つまみ食い\nしましたね！？　あっ\nおやつの時間でしたか", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111101, 3461, 1, 3, 3, 0, "さぁ、ハロウィンの\n始まりです…ふぁ…\nね、眠くありません！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111101, 3463, 1, 0, 2, 0, "ちょっと疲れたかも…\nパーティの時間まで\nひと休みしなきゃ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111101, 3464, 1, 0, 3, 0, "私も今、こんな感じ\nなのかな？　こんなに\nかわいいといいなぁ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111101, 3465, 1, 0, 4, 0, "甘い匂いがいっぱい…\nお菓子の香りかな？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111101, 3466, 1, 0, 5, 0, "温かくなると、\n丸まりたくなって…\n…猫になっちゃう！？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111101, 3468, 1, 0, 7, 0, "ニンジンもこれくらい\nおいしければ\nいいのに…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111101, 3469, 1, 0, 8, 0, "ハロウィンの本、\nオバケがたくさん\n載ってて怖いかも…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111101, 3471, 1, 0, 12, 0, "美味しそうなお菓子が\nいっぱい…これなら\nイタズラされませんね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111201, 3473, 1, 0, 1, 0, "たくさんいたずら\nできるから\nハロウィン大好き！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111201, 3474, 1, 0, 2, 0, "ハロウィンだから\nいつもよりすごい\nいたずらしたいな～！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111201, 3475, 1, 0, 3, 0, "カボチャを綺麗に\nくり抜いてすごく怖い\nランタン作っちゃお！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111201, 3476, 1, 0, 4, 0, "お菓子がたくさんで\nどれから食べるか\n迷っちゃうな～！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111201, 3477, 1, 0, 5, 0, "どうすればみんなを\nびっくりさせられる\nかな～？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 111201, 3478, 1, 0, 101, 0, "カボチャ見てたら\nお腹空いて来たな～\nお菓子食べちゃお♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 111201, 3479, 1, 0, 102, 0, "にいちゃんには\n特別ないたずらを\n用意してあげるね！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111201, 3480, 1, 0, 1, 0, "カボチャにクラッカー\nを仕掛けて…わっ！？\nみ、見ちゃダメだよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111201, 3481, 1, 0, 2, 0, "ん？　なぁに？\nお菓子ほしいの？\nもう、しかたないな～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111201, 3482, 1, 0, 3, 0, "にいちゃんの\n面白い仮装が\n見てみたいな～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111201, 3483, 1, 0, 4, 0, "お菓子くれたお礼に\nとびっきりの\nいたずらしちゃうね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 111201, 3484, 1, 0, 101, 0, "にいちゃんは\nハロウィンでも最高の\nいたずら相手だよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 111201, 3485, 1, 0, 102, 0, "にいちゃんと一緒に\nハロウィンパレード\n歩きたいな～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111201, 3486, 1, 1, 1, 0, "おはようにいちゃん！\n仮装して出かける\n準備しなきゃ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111201, 3487, 1, 2, 2, 0, "お腹すいたな～\nもらったお菓子\n食べちゃだめかな？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111201, 3488, 1, 3, 3, 0, "たくさんいたずらして\nもうクタクタ～\nミソギもう寝るね…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111201, 3490, 1, 0, 2, 0, "やわらか～い…\nここでひと休みして\nまたいたずらしよう！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111201, 3491, 1, 0, 3, 0, "かわいいもので\n油断させてわっ！\nってするのどうかな？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111201, 3492, 1, 0, 4, 0, "いいにお～い！\nいたずらのアイデアが\nわいてくるよ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111201, 3493, 1, 0, 5, 0, "もらったお菓子\nあったかくしたら\nおいしいかな～？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111201, 3495, 1, 0, 7, 0, "お菓子ばっかりじゃ\n飽きちゃうもんね！\nご飯も食べよう！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111201, 3496, 1, 0, 8, 0, "本のなかに\nいたずらのヒント\nないかな～？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111201, 3498, 1, 0, 12, 0, "お菓子がたくさん！\n一人で食べていいの？\nやっぱりだめか～！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 101101, 3500, 1, 0, 103, 0, "お勉強は順調ですよ\nやっぱりお兄ちゃんの\nおかげなんでしょうか", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 101101, 3501, 1, 0, 103, 0, "お兄ちゃんとの\n思い出の場所\nまた行きましょうね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111301, 3502, 1, 0, 1, 0, "えへへ～♪\nハロウィンの\nミイラさんうさぎさん\nぴょんぴょ～ん！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111301, 3503, 1, 0, 2, 0, "るんたった～\nハロウィンハロウィン\n楽しいな～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111301, 3504, 1, 0, 3, 0, "ハロウィンって\nどんなお菓子を\nもらえるのかな～？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111301, 3505, 1, 0, 4, 0, "お菓子をもらう\n練習をしなくちゃ！\nトリックオア\nトリート～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111301, 3506, 1, 0, 5, 0, "おにいちゃんと\nハロウィンするの\nミミ楽しみだな～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 111301, 3507, 1, 0, 101, 0, "おにいちゃんと\nいっしょにお菓子を\n食べたいな～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 111301, 3508, 1, 0, 102, 0, "おにいちゃんと\n毎日ハロウィン\nできたらいいな～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111301, 3509, 1, 0, 1, 0, "えへへ～\nおにいちゃん！\nハッピーハロウィン♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111301, 3510, 1, 0, 2, 0, "おにいちゃんも\nいっしょに\nハロウィンパレード\nしようね～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111301, 3511, 1, 0, 3, 0, "ミイラさんうさぎさん\nだぞ～…えへへ\nミミこわかったかな？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111301, 3512, 1, 0, 4, 0, "おにいちゃん！\nハロウィンのお菓子\nおいしいね～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 111301, 3513, 1, 0, 101, 0, "おにいちゃんと\nいっしょなら\nおばけさんも\nこわくないよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 111301, 3514, 1, 0, 102, 0, "ミミね\nハロウィンも\nおにいちゃんも\nだ～いすき♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111301, 3515, 1, 1, 1, 0, "おひさまさんも\nハロウィンの\nおようふく\n着たいかな～？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111301, 3516, 1, 2, 2, 0, "ねえねえおにいちゃん\nお昼ごはんはもらった\nお菓子でもい～い？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111301, 3517, 1, 3, 3, 0, "まだハロウィン…？\nうぅん…眠くないよぅ\nでもちょっと…ふぁ～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111301, 3519, 1, 0, 2, 0, "ふかふか～♪\nおばけさんが\n中にいるのかな～？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111301, 3520, 1, 0, 3, 0, "わぁ、かわいい～♪\nミミといっしょに\nあそぼ～！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111301, 3521, 1, 0, 4, 0, "ふわぁ～\nミミ、このにおい\nだ～いすき♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111301, 3522, 1, 0, 5, 0, "あったかいね～♪\nおばけさんも\nポカポカしてるかな？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111301, 3524, 1, 0, 7, 0, "おいしい～♪\nおにいちゃんも\n食べようよー！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111301, 3525, 1, 0, 8, 0, "この本\nおにいちゃんと\nいっしょに\n読みたいな～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111301, 3527, 1, 0, 12, 0, "ハッピーハロウィン♪\nえへへ、お菓子が\nいっぱいだね～！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111401, 3529, 1, 0, 1, 0, "ここ、好きだよ。\nみんな優しいから…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111401, 3530, 1, 0, 2, 0, "なんかさみしいな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111401, 3531, 1, 0, 3, 0, "知らない人怖いな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111401, 3532, 1, 0, 4, 0, "お部屋おっきいねー", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111401, 3533, 1, 0, 5, 0, "ルナね…\nお昼寝したいかも", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 111401, 3534, 1, 0, 101, 0, "おにいさん！\n今日は何して遊ぶの？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 111401, 3535, 1, 0, 102, 0, "あのね、\nお料理教えてほしい！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111401, 3536, 1, 0, 1, 0, "あっ、おにいさんだ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111401, 3537, 1, 0, 2, 0, "ルナは元気だよ！\nいいこだもん！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111401, 3538, 1, 0, 3, 0, "今日は何しようかな", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111401, 3539, 1, 0, 4, 0, "ルナと遊んでよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 111401, 3540, 1, 0, 101, 0, "ねーねー、\nルナとお出かけして！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 111401, 3541, 1, 0, 102, 0, "おにいさんと、\n遊びたいなー！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111401, 3542, 1, 1, 1, 0, "ふふっ！\nなんかまぶしいね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111401, 3543, 1, 2, 2, 0, "ルナ、お腹空いたな", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111401, 3544, 1, 3, 3, 0, "どこに行こうかな？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111401, 3546, 1, 0, 2, 0, "ルナ、これ好きだな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111401, 3547, 1, 0, 3, 0, "かわいい！　好き！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111401, 3548, 1, 0, 4, 0, "なんかいい匂い！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111401, 3549, 1, 0, 5, 0, "あったかいねー", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111401, 3551, 1, 0, 7, 0, "ふふっ\nおいしいね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111401, 3552, 1, 0, 8, 0, "ルナ、\n眠くなっちゃう…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 105201, 3554, 1, 0, 103, 0, "えへへ。あの人と\nランドソルでデート\nできたらうれしいな♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 105201, 3555, 1, 0, 103, 0, "あなたとなら\nどんなことだって\nできる気がするわ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106501, 3556, 1, 0, 1, 0, "あーあ、退屈だな\nカレでも探しに\nいこうかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106501, 3557, 1, 0, 2, 0, "この前のケンカは\n最高に燃えたなぁ\n…拳が疼いてきたぜ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106501, 3558, 1, 0, 3, 0, "一体、この世界は\nどうなっちまったって\nいうんだ…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106501, 3559, 1, 0, 4, 0, "もう仕事の時間か\nボスも人使いが荒いぜ\n…ま、いいけどさ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106501, 3560, 1, 0, 5, 0, "オレたち以外に\nドラゴン族はあと何人\nいるんだろうな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 106501, 3561, 1, 0, 101, 0, "カレの真剣な顔\n面白かったな\nまた遊びたいぜ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 106501, 3562, 1, 0, 102, 0, "どうしてカレがそばに\nいると、腹の底から\n力が湧いてくるんだ？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106501, 3563, 1, 0, 1, 0, "よっす！\nなんかオイシイ話でも\nあったりしない？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106501, 3564, 1, 0, 2, 0, "なあ、オレと遊ぼうぜ\n心配すんなよ。危ない\nことはよしとくからさ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106501, 3565, 1, 0, 3, 0, "どうした。オレに\nケンカ売ろうってか？\n…ははは、冗談だよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106501, 3566, 1, 0, 4, 0, "ちょうど退屈してた\nところだ、特訓に\n付き合ってくれよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 106501, 3567, 1, 0, 101, 0, "ったくよぉ…\nオレがかわいいなんて\n冗談はもうよせよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 106501, 3568, 1, 0, 102, 0, "次の仕事も一緒に\n来るだろ？　断っても\n連れて行くけどな！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106501, 3569, 1, 1, 1, 0, "おはよう！　今日の\n予定はまずケンカして\nで、その後もケンカだ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106501, 3570, 1, 2, 2, 0, "よっす！\n腹ごしらえが済んだら\nオレと遊ぼうぜ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106501, 3571, 1, 3, 3, 0, "夜は危ないから\nあんま出歩くな？\n…だから面白えんだろ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106501, 3573, 1, 0, 2, 0, "次のケンカに備えて\n今は休んでおくか…\n全力で暴れたいからな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106501, 3574, 1, 0, 3, 0, "ボスの飼ってる\nラーゴが、中に\n入ってるんじゃ？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106501, 3575, 1, 0, 4, 0, "ああ、たぎる匂いが\nする…ひと暴れ\nしたくなってきた！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106501, 3576, 1, 0, 5, 0, "おおぉ…\nオレの拳もなんだか\n熱くなってきたぜ…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106501, 3578, 1, 0, 7, 0, "いつでも全力で\nケンカするために\n力を蓄えないとな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106501, 3579, 1, 0, 8, 0, "やっぱりな…オレが\n知ってる地図とは\nまるで違ってやがる", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111501, 3581, 1, 0, 1, 0, "さぁプレゼントで\n埋め尽くしてやろう！\n大いに喜ぶがいいよ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111501, 3582, 1, 0, 2, 0, "メリークリスマス☆\n聖夜の宴だ\n派手に楽しむとしよう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111501, 3583, 1, 0, 3, 0, "煙突から入る？\nいいや、ワタシは\nいつでも正面からさ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111501, 3584, 1, 0, 4, 0, "サンタクロースの\nお出ましだ！\n歓迎するがいいよ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111501, 3585, 1, 0, 5, 0, "飾り付けが足りん！\nワタシが演出して\nやろうじゃないか！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 111501, 3586, 1, 0, 101, 0, "人の喜ぶ顔を見るのも\n悪くはないな…おっと\nワタシらしくないな☆", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 111501, 3587, 1, 0, 102, 0, "プレゼントはまだか？\nワタシを喜ばせたら\nお返しは弾むぞ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111501, 3588, 1, 0, 1, 0, "ワタシを呼んだか？\n退屈な要件だったら\n覚悟しておくことだ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111501, 3589, 1, 0, 2, 0, "なんだ？\nワタシへの挑戦か？\nよし、受けた♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111501, 3590, 1, 0, 3, 0, "おっと、当たらんよ\nワタシの乱数聖域は\n絶対だからな…♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111501, 3591, 1, 0, 4, 0, "なんだ、オマエも\n暇なのか？\n奇遇だな、ワタシもさ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 111501, 3592, 1, 0, 101, 0, "なにか愉しいことでも\nしようじゃないか\nさぁ、何をする？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 111501, 3593, 1, 0, 102, 0, "こうもワタシに\n気安く触れるのは\nオマエくらいだよ…♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111501, 3594, 1, 1, 1, 0, "朝だろうと関係ない！\nさぁ愉しく苛烈に\n騒ごうじゃないか♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111501, 3595, 1, 2, 2, 0, "調子の出てくる時間だ\nしっかりワタシを\n愉しませてくれよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111501, 3596, 1, 3, 3, 0, "眠いなどと言わせんぞ\nまだまだ一日は\n終わらんからな！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111501, 3598, 1, 0, 2, 0, "いい具合じゃないか\nこのまま持って帰って\nしまおうか…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111501, 3599, 1, 0, 3, 0, "なかなか愛らしい\n造形をしているな？\nワタシには負けるがな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111501, 3600, 1, 0, 4, 0, "ほう、悪くない香りだ\n戦いの匂いをうまく\n隠してくれそうだ…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111501, 3601, 1, 0, 5, 0, "体が温まってくると\nひと暴れしたくなるな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111501, 3603, 1, 0, 7, 0, "ワタシの舌は厳しいぞ\n…ふーむ、なるほど\n認めざるをえんな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111501, 3604, 1, 0, 8, 0, "クリスティーナさんは\n知識の収集も\n欠かさないんだよ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111501, 3606, 1, 0, 14, 0, "これは驚いたな\nだがもっと、もっとだ\nワタシを退屈させるな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111501, 3607, 1, 0, 15, 0, "こんな戯れだって\n軽くこなしてしまう\nクリスティーナさんさ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111601, 3608, 1, 0, 1, 0, "メリークリスマス！\nさあ、パーティーを\n盛り上げるよ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111601, 3609, 1, 0, 2, 0, "衣装一つで全然\n気分が違うね！\n最高のクリスマスに\nなりそうだよ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111601, 3610, 1, 0, 3, 0, "ツリーのてっぺんの\n星って、なんだか\n欲しくなるんだよね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111601, 3611, 1, 0, 4, 0, "めいっぱい\nクリスマスを楽しむよ\n遊びだって本気\nなんだからね！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111601, 3612, 1, 0, 5, 0, "赤、白、緑、金\nクリスマスカラー\nってお洒落だよね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 111601, 3613, 1, 0, 101, 0, "雪、降らないかな？\nホワイトクリスマス\nってちょっと\n憧れるなぁ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 111601, 3614, 1, 0, 102, 0, "キミと過ごすこの\n楽しい時間がずーっと\n続けばいいのにな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111601, 3615, 1, 0, 1, 0, "クリスマスライブ\nチケット用意するから\n絶対来てよね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111601, 3616, 1, 0, 2, 0, "カンパーイ！\nジュースだけど\nちょっと大人の\n気分だね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111601, 3617, 1, 0, 3, 0, "クリスマス衣装どう？\nかわいいでしょ\nあっ、どこ見てるの！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111601, 3618, 1, 0, 4, 0, "ねえ、後で街に\n行こうよ\n大きなツリーを\n一緒に見たいんだ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 111601, 3619, 1, 0, 101, 0, "ねえ、ダンスの練習に\n付き合ってくれない？\n大丈夫、手取り足取り\n教えてあげるから♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 111601, 3620, 1, 0, 102, 0, "来年のクリスマスも\n隣にキミが\nいてくれたら\nうれしいのにな", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111601, 3621, 1, 1, 1, 0, "クリスマスって朝から\nドキドキしちゃうね\nなんだか子どもみたい", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111601, 3622, 1, 2, 2, 0, "パーティーの準備も\n大分進んだね\n一息ついて\nランチにしよっか", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111601, 3623, 1, 3, 3, 0, "静かな夜だね\n心臓の音まで\n聞こえちゃいそう…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111601, 3625, 1, 0, 2, 0, "あ～、この絶妙な\n柔らかさ…\nいい仕事してるなぁ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111601, 3626, 1, 0, 3, 0, "アイドル顔負けの\nかわいさ、ズルい！\nでもかわいい！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111601, 3627, 1, 0, 4, 0, "ん～、落ち着くなぁ\n今日はゆっくり\n眠れそうだよ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111601, 3628, 1, 0, 5, 0, "ぬくぬく～\nどうしよう、ここから\n動きたくないなぁ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111601, 3630, 1, 0, 7, 0, "ダンスの練習もしたし\nちょっとくらいなら\nいいよね！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111601, 3631, 1, 0, 8, 0, "アイドルといえど\nかわいいだけじゃダメ\n知識も必要だよね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111601, 3633, 1, 0, 14, 0, "面白ーい！\nこの演出、ライブでも\nできないかな？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111601, 3634, 1, 0, 15, 0, "もふもふ～♪\n寒い日には最高の\n抱き心地だね！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 101801, 3635, 1, 0, 103, 0, "彼ともっとお話し\nしたいな…もう少し\n補習を増やそうかしら", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 101801, 3636, 1, 0, 103, 0, "君のこともっともっと\n教えて欲しいな。私に\n補習授業してくれる？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111701, 3637, 1, 0, 1, 0, "この姿ならあやつも\n子ども扱いできまい！\n…じゃがなぜか、少し\n物足りないような…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111701, 3638, 1, 0, 2, 0, "時間もあるし、\n聖夜の宴の\n準備をするかのう♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111701, 3639, 1, 0, 3, 0, "…わらわの知る聖夜と\n随分違うが、宴は宴\n楽しませて\nもらおうかのう♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111701, 3640, 1, 0, 4, 0, "大人の姿で\nできること…ふむ\n少しあやつを\nからかってやるか♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111701, 3641, 1, 0, 5, 0, "…クリスマスは\n恋人たちの夜！？\nち、近頃の若いもんは\n破廉恥なのじゃ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 111701, 3642, 1, 0, 101, 0, "…あやつがおらんと、\n宴も物足りぬな\n…馬鹿者が\n早く帰ってこい…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 111701, 3643, 1, 0, 102, 0, "誰も見ておらぬな…？\n…よし、あやつへの\nプレゼントを\n準備するとしようか！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111701, 3644, 1, 0, 1, 0, "クリスマスの\nチキンにケーキ…\nべ、別に楽しみになど\nしておらぬぞ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111701, 3645, 1, 0, 2, 0, "この姿では、わらわの\n頭をなでるのは\n難しかろう…べ、\n別に残念ではないわ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111701, 3646, 1, 0, 3, 0, "メリー\nクリスマスじゃ！\nさあ、わらわのために\n宴の準備をするのじゃ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111701, 3647, 1, 0, 4, 0, "そんなにこの衣装が\n気になるのか？\n…だったら、直に\n触ってみるがよい♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 111701, 3648, 1, 0, 101, 0, "いつもと逆に、お主を\n子ども扱いしてやるか\nほれ、膝枕をしてやる\nからそばに来い♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 111701, 3649, 1, 0, 102, 0, "…せ、聖夜の予定は\n空けておくのじゃぞ？\nなんでって…\nな、なんでもじゃ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111701, 3650, 1, 1, 1, 0, "もう朝か…聖夜の宴が\n楽しみで…ではなく！\n準備のせいで、\nあまり眠れんかったぞ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111701, 3651, 1, 2, 2, 0, "…ずっと雪が\n降っておれば、\n陽光が強いこの時間も\n怯えずにすむのにのう", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111701, 3652, 1, 3, 3, 0, "時は来た…\nさあ眷属よ！\nわらわと共に、\n聖夜の宴を楽しもうぞ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111701, 3654, 1, 0, 2, 0, "良い椅子ではないか\nここから聖夜の宴を\n眺めるのもよいかもな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111701, 3655, 1, 0, 3, 0, "か、かわい…こほん\nなかなか良い\nデザインをしておるな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111701, 3656, 1, 0, 4, 0, "ふむ、良い香りじゃ\n聖夜の宴でも\nこれを使うとしよう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111701, 3657, 1, 0, 5, 0, "これが癒し…はっ！\nあやうく浄化される\nところであった…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111701, 3659, 1, 0, 7, 0, "ニ、ニンニクは\n入っておらぬよな？\n…うむ、美味じゃ♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111701, 3660, 1, 0, 8, 0, "聖夜は恋人たちの夜…\n…き、気になるわけ\nではないが、\n少し調べてみるか", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111701, 3662, 1, 0, 14, 0, "ほう…これは面白い\n魔法の使い方じゃな\nまるで本物の\n演劇のようじゃ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111701, 3663, 1, 0, 15, 0, "こ、この程度の\nかわいさに誘惑される\nわらわでは…\nふわもこなのじゃ～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112001, 3664, 1, 0, 1, 0, "おせち料理も\n魔物尽くしだったり\nしないわよね！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112001, 3665, 1, 0, 2, 0, "初詣で何をお祈り\nしようかしら…\n定番でいいわよね？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112001, 3666, 1, 0, 3, 0, "初日の出って\n特別な感じがして\nいいわよね…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112001, 3667, 1, 0, 4, 0, "寒い中並んで飲む\n甘酒って不思議なほど\nおいしいわよね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112001, 3668, 1, 0, 5, 0, "羽根つきで勝負…\nあいつにだったら\n勝てるかしら？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 112001, 3669, 1, 0, 101, 0, "年賀状になんて書こう\n…いろいろと\n恥ずかしくなるのよね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 112001, 3670, 1, 0, 102, 0, "今年もあいつと\n居られること…\n結構うれしいわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112001, 3671, 1, 0, 1, 0, "何じっと\n見てんのよ？\nお年玉はあげないわよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112001, 3672, 1, 0, 2, 0, "おせち料理一緒に\n食べる？\n結構おいしいわよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112001, 3673, 1, 0, 3, 0, "羽根つきで負けたら\n顔に墨塗られるのよ\n覚悟しなさい！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112001, 3674, 1, 0, 4, 0, "門松飾るから\nちょっと手伝って\nくれる？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 112001, 3675, 1, 0, 101, 0, "あんたと一緒に\n初詣行くのも悪くない\n…かも", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 112001, 3676, 1, 0, 102, 0, "初詣で何をお祈り\nしたか？　うっさい！\n内緒よ！内緒！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112001, 3677, 1, 1, 1, 0, "もう朝なの…\n正月くらいゆっくり\n寝ていたいわよね…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112001, 3678, 1, 2, 2, 0, "おせちに飽きたら\n次は何を食べよう\nかしら…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112001, 3679, 1, 3, 3, 0, "そろそろ寝ましょ\nいい初夢が見られると\nいいわね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112001, 3681, 1, 0, 2, 0, "この格好でいるのも\nなかなか疲れるわね\n休憩～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112001, 3682, 1, 0, 3, 0, "か、かわいい…う～ん\n鏡餅も顔を描けば\nかわいくなるかしら？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112001, 3683, 1, 0, 4, 0, "いい匂いね\n落ち着いて\n眠れそう…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112001, 3684, 1, 0, 5, 0, "ここでお餅焼いたら\n一段とおいしく\nなりそうね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112001, 3686, 1, 0, 7, 0, "正月に食べるものは\nなんでもおいしく\n感じちゃうわね！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112001, 3687, 1, 0, 8, 0, "魔物を使わない\nおせちを\n調べておかなきゃ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112101, 3689, 1, 0, 1, 0, "新年を気持ちよく\n過ごせるように\nお掃除がんばります！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112101, 3690, 1, 0, 2, 0, "神殿に通っていたら\nいつの間にか足腰が\n鍛えられたような…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112101, 3691, 1, 0, 3, 0, "神殿付近は屋台も\nあるので、良い香りが\n漂ってくるんですよ～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112101, 3692, 1, 0, 4, 0, "おせちを作れるように\nなれば、子どもたちも\n喜んでくれるかな…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112101, 3693, 1, 0, 5, 0, "コホォォォォォ…！\nふぅ、深呼吸すると\n落ち着きますね♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 112101, 3694, 1, 0, 101, 0, "お仕事以外の時も\nあなたと…い、いえ！\nなんでもないです！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 112101, 3695, 1, 0, 102, 0, "あなたと一緒だと\nなんだかポカポカ\nしてくるんです…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112101, 3696, 1, 0, 1, 0, "この格好をしていると\nなんだか神殿にいる\n気分になりますね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112101, 3697, 1, 0, 2, 0, "そういえば神殿で\n甘酒をもらったんです\nあなたも飲みますか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112101, 3698, 1, 0, 3, 0, "あっ、髪飾りが\nズレちゃってました！\nいけないいけない…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112101, 3699, 1, 0, 4, 0, "ひゃわっ！？\nく、くすぐったり\nしないでください～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 112101, 3700, 1, 0, 101, 0, "そ、そんなに\n見つめられると\nて、照れちゃいます…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 112101, 3701, 1, 0, 102, 0, "今度、私が考えた\n運気上昇のおまじない\nかけてあげますね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112101, 3702, 1, 1, 1, 0, "おはようございます\n神殿のお手伝いに行く\n準備もしなきゃですね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112101, 3703, 1, 2, 2, 0, "お昼になりましたし\nご飯は私が作…\nえっ、ダメですか！？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112101, 3704, 1, 3, 3, 0, "明日も早いですし\nそろそろ寝る支度を…\nふわぁ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112101, 3706, 1, 0, 2, 0, "お掃除も終わったので\n少し休憩を…ふわぁ…\n急に眠気が…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112101, 3707, 1, 0, 3, 0, "新年を気持ちよく\n過ごせるようこの子も\nきれいにしましょう♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112101, 3708, 1, 0, 4, 0, "神殿のお香のような\n香りだと落ち着いて\n過ごせそうですよね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112101, 3709, 1, 0, 5, 0, "ぽかぽかですね～…\n甘酒も飲んで体の\n芯から温めましょう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112101, 3711, 1, 0, 7, 0, "今年こそ\n料理も上手に…\nもぐもぐもぐもぐ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112101, 3712, 1, 0, 8, 0, "失敗続きだと\n逆に運が良くなる…\nほ、本当ですかぁ…？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 103401, 3714, 1, 0, 103, 0, "キミにならもう\nどんな私を見られても\n大丈夫かな…♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 103401, 3715, 1, 0, 103, 0, "なになにどしたのぉ？\nお姉さんが撫でたげる\nこっちにきなさぁい♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111901, 3716, 1, 0, 1, 0, "わたくしの舞を\n主さまは喜んで\nくださるでしょうか…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111901, 3717, 1, 0, 2, 0, "年を越したあとに\n食べるおそばも\nおいしいです", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111901, 3718, 1, 0, 3, 0, "甘酒を飲むと\n心まで温まる\n気がいたします", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111901, 3719, 1, 0, 4, 0, "主さまのために\n真心を込めたおせちを\nご用意しなくては", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111901, 3720, 1, 0, 5, 0, "おみくじを引くのは\nドキドキしつつも\n楽しいものです", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 111901, 3721, 1, 0, 101, 0, "主さまの温もりは\n心まで温めてくれる\nようです", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 111901, 3722, 1, 0, 102, 0, "うまくできなくとも\n心を込めて精いっぱい\n舞いましょう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111901, 3723, 1, 0, 1, 0, "主さま、この晴れ着は\n似合っている\nでしょうか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111901, 3724, 1, 0, 2, 0, "お餅はゆっくりと\n召し上がって\nくださいまし", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111901, 3725, 1, 0, 3, 0, "主さまのお顔の\n福笑いなら、完璧に\nできる気がいたします", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111901, 3726, 1, 0, 4, 0, "主さまはどのような\n初夢をご覧になった\nのでしょう？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 111901, 3727, 1, 0, 101, 0, "甘酒の飲み過ぎは\nいけませんよ？\nめっ、でございます", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 111901, 3728, 1, 0, 102, 0, "二人羽織とは\nあんなにも暖かい\nものなのですね…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111901, 3729, 1, 1, 1, 0, "おはようございます\n寒い季節の朝日は\nとても美しいですね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111901, 3730, 1, 2, 2, 0, "おいしくとも\n食べ過ぎは正月太りの\nもとでございます", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111901, 3731, 1, 3, 3, 0, "いい夢が見られるよう\n暖かくして\n眠るといたしましょう", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111901, 3733, 1, 0, 2, 0, "ふかふか…\nこのまま\n寝てしまいそうです", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111901, 3734, 1, 0, 3, 0, "あなたと一緒に\nお正月遊びを\n楽しんでみたいです", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111901, 3735, 1, 0, 4, 0, "神殿の独特な香りを\nおうちに持ち込むのは\nどうでしょうか", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111901, 3736, 1, 0, 5, 0, "風邪をひきませんよう\n暖かくいたしましょう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111901, 3738, 1, 0, 7, 0, "食べ過ぎは\n正月太りの\nもとだというのに…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111901, 3739, 1, 0, 8, 0, "主さまのために\n参拝の作法を\n予習しておきましょう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112201, 3741, 1, 0, 1, 0, "…この杖、変身前には\n無いパーツがあるけど\nこれはどこから…うん\n考えないようにしよう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112201, 3742, 1, 0, 2, 0, "フ、フリフリの\nかわいい衣装…はっ！\n素に戻ってはダメだ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112201, 3743, 1, 0, 3, 0, "魔法探偵\nミスティ★カスミ！\n魔法の力で捜査開始！\n…練習終わりっと", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112201, 3744, 1, 0, 4, 0, "怪しい視線を感じる…\nまさか、近くに\n闇の力を持つ人が！？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112201, 3745, 1, 0, 5, 0, "時間もあるし、\n魔法少女の修行を…\nじゃ、じゃなくて！\n探偵の仕事をしよう！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 112201, 3746, 1, 0, 101, 0, "助手くんとずっと\n一緒に居られるし、\n魔法少女も\n案外悪くないかも♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 112201, 3747, 1, 0, 102, 0, "魔法少女の訓練のため\n助手くんの所に行こう\nかな…あ、あくまで\n訓練のためにね！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112201, 3748, 1, 0, 1, 0, "…髪型が急に\n変わって驚いた？\nき、気になるなら…\n少し触ってみるかい？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112201, 3749, 1, 0, 2, 0, "助手くんは魔法少女に\nなりたいのかい？\nその場合は魔法騎士…\nいや、魔法助手とか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112201, 3750, 1, 0, 3, 0, "頭に浮かぶ呪文…\n強制変身…助手くん\n魔法少女とは\n呪いか何かなのかな？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112201, 3751, 1, 0, 4, 0, "あ、あまり触らないで\nくれ…魔法少女になる\nと感覚が鋭くなって、\n耳も敏感に…ひゃう！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 112201, 3752, 1, 0, 101, 0, "や、やっぱりこの\n格好は変かな…え、\nかわいい？　じゃあ…\nいや、でも、う～ん", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 112201, 3753, 1, 0, 102, 0, "…この変身は助手くん\nのせいでもあるんだよ\nだから…せ、責任を\n取ってもらうからね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112201, 3754, 1, 1, 1, 0, "おはよう助手くん\nさあ、そろそろ\n街のパトロールに\n行こうじゃないか！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112201, 3755, 1, 2, 2, 0, "もうお昼…助手くん、\nそろそろ本業に戻ろう\n…その顔は何かな？\n私の本業は探偵だぞ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112201, 3756, 1, 3, 3, 0, "夜だけど、私は\nもう少し仕事をするよ\n魔法少女の強い体は\nこんなときに便利だね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112201, 3758, 1, 0, 2, 0, "気持ちいい…\nこの癒しを、魔法と\n論理の二つの観点から\n解明したいね…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112201, 3759, 1, 0, 3, 0, "ダ、ダメ！！\nここで感情のままに\n飛びついたら、私の\nイメージがぁ～！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112201, 3760, 1, 0, 4, 0, "心が安らぐ香りだね…\n…今なら、魔法少女も\nすんなりと受け入れ…\nてはダメだっ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112201, 3761, 1, 0, 5, 0, "あったかい…\n魔法少女だろうと\n休息は必要だし、少し\n休んでいこうかな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112201, 3763, 1, 0, 7, 0, "魔法少女も探偵も、\nやっぱり体が資本！\n食べられるときに\n食べておかないとね♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112201, 3764, 1, 0, 8, 0, "…魔法少女\nセリイちゃんの本は\nどこにあるのかな？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112301, 3766, 1, 0, 1, 0, "魔法少女かぁ…\nせっかくだし思う存分\n体を動かしてみたいな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112301, 3767, 1, 0, 2, 0, "ニャットちゃん…\nこの格好を見て\n私って気づくかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112301, 3768, 1, 0, 3, 0, "ふふ…\n今日はなんだか\n調子がいいかも", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112301, 3769, 1, 0, 4, 0, "魔法は便利だけど\nあまり頼りすぎちゃ\nダメだよね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112301, 3770, 1, 0, 5, 0, "さっき街で小さな子に\n声かけられちゃった\n照れるけどうれしいな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 112301, 3771, 1, 0, 101, 0, "あの人がいると\nなんだかホッとする\n不思議…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 112301, 3772, 1, 0, 102, 0, "魔法少女になっても\nあの人への気持ちは\nずっと同じ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112301, 3773, 1, 0, 1, 0, "今日の私…ですか？\nはいとっても元気です\n…あなたのおかげかも", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112301, 3774, 1, 0, 2, 0, "よかったら魔法の\n練習に付き合って\nくれませんか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112301, 3775, 1, 0, 3, 0, "あの…\nこの格好どうですか？\n似合ってると…いいな", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112301, 3776, 1, 0, 4, 0, "…な、なんですか？\nあまり見られると\n恥ずかしいです…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 112301, 3777, 1, 0, 101, 0, "私、あなたのそばに\nいられたら、それで…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 112301, 3778, 1, 0, 102, 0, "想い人と結ばれる\n魔法は…ひゃっ！？\n今の聞いてましたか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112301, 3779, 1, 1, 1, 0, "おはようございます\n今日も街の平和の\nために頑張らなきゃ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112301, 3780, 1, 2, 2, 0, "お腹すきましたね。\nあの…一緒にランチは\nいかがですか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112301, 3781, 1, 3, 3, 0, "あ、もうこんな時間…\nふふ、まだこんなに\n体力が余ってるのに", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112301, 3783, 1, 0, 2, 0, "気持ちいい…\nこの気持ち良さは\nどんな魔法も勝てない", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112301, 3784, 1, 0, 3, 0, "何これ、かわいい…\nお姉ちゃんにも\n見せてあげたいな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112301, 3785, 1, 0, 4, 0, "ん～、いい香り\n…これ、魔法で\n出せたりしないかな？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112301, 3786, 1, 0, 5, 0, "あったか～い\nえへへ…なんだか\nお姉ちゃんみたい", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112301, 3788, 1, 0, 7, 0, "おいしいなぁ、これ\n魔法少女になってから\n食欲旺盛かも…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112301, 3789, 1, 0, 8, 0, "ふふ…\n魔法少女になっても\n読書はやめられないな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 101001, 3791, 1, 0, 103, 0, "みらくるまほりん\nくるりんぱ～♪\n王子はんのために\n花嫁修業や～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 101001, 3792, 1, 0, 103, 0, "あ、あのな王子はん\nできたらその…\nお姫さまだっこを\nお願いしてもええ…？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180401, 3793, 1, 0, 1, 0, "やっぱりわたしたちの\nギルドハウスの\n居心地は最高ですね☆", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180401, 3794, 1, 0, 2, 0, "王宮じゃできないこと\nやっちゃいましょう♪\n例えば、えっと～…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180401, 3795, 1, 0, 3, 0, "そうだ、隠しておいた\nおやつがありました！\nまだ食べられますね☆", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180401, 3796, 1, 0, 4, 0, "ごはんはみんなで\n楽しく食べましょう！\nそれが一番の\n調味料です♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180401, 3797, 1, 0, 5, 0, "やっぱりここが、\nわたしの帰る場所です\n…ただいま☆", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 180401, 3798, 1, 0, 101, 0, "なんだか物足りません\nきっと、あの人が隣に\nいないからですね…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 180401, 3799, 1, 0, 102, 0, "どこを見ても、大切な\n思い出がたくさん\n詰まっていますね♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180401, 3800, 1, 0, 1, 0, "無礼者～、なんて\n冗談です♪　どんどん\n触っちゃってください", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180401, 3801, 1, 0, 2, 0, "ぷにぷにでしょう～？\nい～っぱい食べるのが\nぷにぷにの秘訣です！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180401, 3802, 1, 0, 3, 0, "握手したいんですか？\nはい、にぎにぎ～！\nもう離しませんよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180401, 3803, 1, 0, 4, 0, "どうかしましたか？\n退屈してるんでしたら\nお散歩に行きましょう", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 180401, 3804, 1, 0, 101, 0, "ごはん王子♪\nわたしは王女ですから\nこれでピッタリですね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 180401, 3805, 1, 0, 102, 0, "大好きです～\n愛してます～\n…ほんと、ですよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180401, 3806, 1, 1, 1, 0, "朝ごはんは大事です！\n山盛りごはんを食べて\n元気いっぱいですよ☆", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180401, 3807, 1, 2, 2, 0, "お腹ぺこぺこになる\n時間ですね～！\nごはん特盛、一つ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180401, 3808, 1, 3, 3, 0, "お腹いっぱい、今日は\nよく眠れそうですね♪\n明日もおいしい一日を", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180401, 3810, 1, 0, 2, 0, "休憩休憩～♪\nお腹を休めて、次の\nごはんに挑みます…☆", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180401, 3811, 1, 0, 3, 0, "かわいらしい\nぬいぐるみですね～\nキャルちゃんみたい☆", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180401, 3812, 1, 0, 4, 0, "すんすん…この匂い\n食欲増進の効果が\nありそうですね…？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180401, 3813, 1, 0, 5, 0, "体温が上がってくると\n眠くなっちゃいます…\nほわぁ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180401, 3815, 1, 0, 7, 0, "もぐもぐもぐもぐもぐ\nむぐむぐむぐむぐむぐ\nはぐはぐはぐはぐはぐ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180401, 3816, 1, 0, 8, 0, "教養を身に着けるには\n読書が一番です！\nえっと次のレシピは～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112401, 3818, 1, 0, 1, 0, "アイドルらしくなる\nコツって、何か\nあるんでしょうか？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112401, 3819, 1, 0, 2, 0, "色んな人と話すのが\n好きなんです。つい\n長くなっちゃって…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112401, 3820, 1, 0, 3, 0, "アイドルを目指すって\n決めてから、毎日が\nワクワクなんです♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112401, 3821, 1, 0, 4, 0, "ちゃんとストレッチ\nしておかなくっちゃ\nよいしょ、よいしょ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112401, 3822, 1, 0, 5, 0, "リンちゃんミオちゃん\n二人に負けないように\n私、頑張ります！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 112401, 3823, 1, 0, 101, 0, "私の笑顔は素敵だって\n誰かに褒められた…\nそんな気がするんです", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 112401, 3824, 1, 0, 102, 0, "私たちを支えてくれる\n誰かが、ずっと一緒に\nいてくれたような…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112401, 3825, 1, 0, 1, 0, "どうかしましたか？\n何か困ってるのなら\nお手伝いしますよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112401, 3826, 1, 0, 2, 0, "みなさん個性的で\n普通じゃない人ばかり\nいるような気が…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112401, 3827, 1, 0, 3, 0, "今日はポカポカした\nいい天気ですね～\nお散歩日和です！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112401, 3828, 1, 0, 4, 0, "今、ミルクティーを\n淹れてたんです♪\nあなたも飲みますか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 112401, 3829, 1, 0, 101, 0, "私の得意なこと？\nうーんなんでしょう…\nあっ、肩たたきです！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 112401, 3830, 1, 0, 102, 0, "生・ハム・メローン！\nあ、ライブの掛け声を\n練習してたんですよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112401, 3831, 1, 1, 1, 0, "おはようございます！\n今日も一日\n頑張りましょうね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112401, 3832, 1, 2, 2, 0, "しっかりお昼を食べて\n午後からも張り切って\nいきましょうね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112401, 3833, 1, 3, 3, 0, "ぐっすり眠れるよう\n早めに用事を済ませて\nお休みしましょうね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112401, 3835, 1, 0, 2, 0, "ちょっと休んだら\nレッスンの続きも\n頑張ります！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112401, 3836, 1, 0, 3, 0, "えへへ～\nふわふわもこもこで\nかわいいです～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112401, 3837, 1, 0, 4, 0, "リラックスできる\nいい香りですね～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112401, 3838, 1, 0, 5, 0, "わぁ、ポカポカです♪\n温かい飲み物も\nほしくなりますね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112401, 3840, 1, 0, 7, 0, "レッスンしたら\nお腹が空いちゃって…\nぱくぱくもぐもぐ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112401, 3841, 1, 0, 8, 0, "レッスンに役立つ\n運動の情報とか\n載ってるでしょうか…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112601, 3843, 1, 0, 1, 0, "きらきらーん☆\nよし、次のステージは\nこれで行けるかも！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112601, 3844, 1, 0, 2, 0, "私の元気でみんなも\n元気になってくれたら\nうれしいな～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112601, 3845, 1, 0, 3, 0, "ちょっとレッスンで\n疲れちゃったかも…\nでも頑張るぞっ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112601, 3846, 1, 0, 4, 0, "晩ごはん何にしよう？\nそーだ、しぶりんにも\n聞いてみよっと！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112601, 3847, 1, 0, 5, 0, "元気だから笑顔になる\nじゃなくて\n笑顔だから\n元気になるんだよね～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 112601, 3848, 1, 0, 101, 0, "声援を聞いてる時ね\nすごく懐かしいって\n思うことがあるんだ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 112601, 3849, 1, 0, 102, 0, "私を応援してくれる\n一番大事な人が\n隣にいた気がする…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112601, 3850, 1, 0, 1, 0, "なになにー？\nミオちゃんに\n何か用かな～？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112601, 3851, 1, 0, 2, 0, "おっ、やっほー！\n元気そうだね～、キミ\n私？\nもちろん元気～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112601, 3852, 1, 0, 3, 0, "ねぇねぇ、ちょっと\n遊びに行かない？\nたまには息抜きで！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112601, 3853, 1, 0, 4, 0, "ね、この魔物料理さ\nプリンって言い張れば\n食べてもらえそうかな", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 112601, 3854, 1, 0, 101, 0, "えへへ\nいつもありがとねっ！\nパワーもらってるよ～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 112601, 3855, 1, 0, 102, 0, "どうしたどうした～\nミオちゃんの魅力に\nメロメロなのかい？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112601, 3856, 1, 1, 1, 0, "おっはよ～！\n一日の始まりだし\nテンション上げてこ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112601, 3857, 1, 2, 2, 0, "お腹空いた～…\nねぇねぇ、一緒に\nお昼食べようよ～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112601, 3858, 1, 3, 3, 0, "ふわぁ～…\n眠くなってきちゃった\nそろそろ休もっかなぁ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112601, 3860, 1, 0, 2, 0, "わぁ、ふっかふか～！\nレッスンの疲れも\n吹っ飛んじゃうね☆", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112601, 3861, 1, 0, 3, 0, "何これ…かわいーっ！\n早くしまむーに\n教えてあげなきゃ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112601, 3862, 1, 0, 4, 0, "すぅ～…\nうんっ、これ私の\n好きな香りだな～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112601, 3863, 1, 0, 5, 0, "ポッカポカだね～♪\n…あ～ん、ここから\n動きたくないーっ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112601, 3865, 1, 0, 7, 0, "わっ、おーいしー！\nこれはテンション\n上がっちゃうね♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112601, 3866, 1, 0, 8, 0, "ふむふむ…\nふむふむふむ…！\n勉強になった、ブイ☆", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 100101, 3868, 1, 0, 103, 0, "なんでドキドキが\n止まらないの…？\n騎士くんのせいだー！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 100101, 3869, 1, 0, 103, 0, "ドキドキしてるの\nあたしだけ、かな…\n騎士くんはどう…？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 100201, 3870, 1, 0, 103, 0, "騎士クンの前で\n好きって言えるように\n練習しないと…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 100201, 3871, 1, 0, 103, 0, "騎士クン…大好き\n…き、騎士クン！？\nなんでここに！？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 100301, 3872, 1, 0, 103, 0, "困ったな…\n彼が居ないだけで、\nここまで\n寂しいと思うなんて…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 100301, 3873, 1, 0, 103, 0, "えっと…\n今日は訓練じゃなくて\nその…二人でゆっくり\n過ごしたいな…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112501, 3874, 1, 0, 1, 0, "アイドルの心構え…\nどういう風に考えると\nしっくりくるのかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112501, 3875, 1, 0, 2, 0, "何か始めるのって\nドキドキするよね\nだから最近楽しいんだ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112501, 3876, 1, 0, 3, 0, "冒険者の時って毎日\nバタバタしてたけど…\n目標ができると違うね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112501, 3877, 1, 0, 4, 0, "少しお腹が空いた…\nチョコレート一粒だけ\n食べようかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112501, 3878, 1, 0, 5, 0, "ウヅキもミオも\n頑張ってるし\n私も負けられないね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 112501, 3879, 1, 0, 101, 0, "レッスンしてる時さ\n何か、誰かが足りない\nそう思うことがあるの", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 112501, 3880, 1, 0, 102, 0, "大事なのは笑顔…\n誰か、大切な人から\n言われた気がするんだ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112501, 3881, 1, 0, 1, 0, "どうしたの、何か用？\n手伝えることがあれば\nすぐに言ってね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112501, 3882, 1, 0, 2, 0, "姿勢がいい？\n普段から鍛えてるし\n自然とそうなるのかも", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112501, 3883, 1, 0, 3, 0, "さっき散歩してたら\n犬を見かけたんだ\n人懐っこい子だったよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112501, 3884, 1, 0, 4, 0, "今から冒険？\nなら私も一緒に行くよ\n前衛は任せておいて", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 112501, 3885, 1, 0, 101, 0, "今からレッスンに\n行くつもりだったんだ\n少し手伝ってくれる？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 112501, 3886, 1, 0, 102, 0, "蒼の剣を受けよ！\nえ、何してるのって？\nただの発声練習だけど", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112501, 3887, 1, 1, 1, 0, "おはよう、早いね\n今日は依頼もないし\nゆっくりしようかな", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112501, 3888, 1, 2, 2, 0, "そろそろお昼だね\nうーん、今日は\n何を食べよう…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112501, 3889, 1, 3, 3, 0, "もうこんな時間…\n明日も早いし\nそろそろ寝なくちゃ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112501, 3891, 1, 0, 2, 0, "レッスンで疲れたし\nちょっとだけ\n休んでおこうかな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112501, 3892, 1, 0, 3, 0, "ふふっ、かわいい\nふわふわしたものって\n撫でたくなるんだよね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112501, 3893, 1, 0, 4, 0, "うん、いい香り…\nこういうものがあると\n落ち着くからいいよね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112501, 3894, 1, 0, 5, 0, "温かい場所って\n居心地がいいから\nのんびりしちゃうよ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112501, 3896, 1, 0, 7, 0, "レッスン頑張ったら\nすぐお腹が空くし\n食べておかないと…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112501, 3897, 1, 0, 8, 0, "アイドルのことを\nもっと勉強して\n成長しなくちゃ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111001, 3899, 1, 0, 1, 0, "ああ、知りたい…\nなんでもかんでも\nとにかく知りたい…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111001, 3900, 1, 0, 2, 0, "ああ、眠い…\nいつでもどこでも\nとにかく眠い…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111001, 3901, 1, 0, 3, 0, "君、気を付けたまえ\nぼーっと歩いていると\nほわっ！　転んだ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111001, 3902, 1, 0, 4, 0, "ミツキ師にいただいた\n新作サプリ…丁度よい\n被験体はいないものか", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111001, 3903, 1, 0, 5, 0, "サレンディア救護院に\nユニちゃん８歳として\n潜入済みだが何か？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 111001, 3904, 1, 0, 101, 0, "君がユニちゃんズの\n同志であること、\nぼくは誇りに思うぞ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 111001, 3905, 1, 0, 102, 0, "ぼくは青春がしたい\nおい、君\nぼくと青春しないか", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111001, 3906, 1, 0, 1, 0, "ああ、そうだな。\nぼくもそう思う。\n……えっ、何が？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111001, 3907, 1, 0, 2, 0, "待て、話しかけるな。\n今の閃きをメモに…\n…ああっ、忘れた！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111001, 3908, 1, 0, 3, 0, "歩き方が綺麗？\n何を今更なこと\n言っとるんだ君は", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111001, 3909, 1, 0, 4, 0, "ぼくの血液型？\n大らかのОだ。\n何か異論あるかね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 111001, 3910, 1, 0, 101, 0, "君の声は精神に\n高揚をもたらすな…\n波形を分析したいぞ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 111001, 3911, 1, 0, 102, 0, "妙だな…最近\n君に話しかけられると\n心拍に乱れを覚える", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111001, 3912, 1, 1, 1, 0, "おっと、朝だな。\nそろそろ寝るか、\n朝更かしは体に悪い", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111001, 3913, 1, 2, 2, 0, "ふむ、昼餉時か…\n今日のランチは\nサプリ定食にしよう", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111001, 3914, 1, 3, 3, 0, "もう夜だな、\nそろそろ起きるか。\n最高の一日にするぞ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111001, 3916, 1, 0, 2, 0, "ほわぁ～\n副交感神経が…\n優位に…ふふっひ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111001, 3917, 1, 0, 3, 0, "かわいすぎるな…\nセクシーユニには\nミスマッチか…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111001, 3918, 1, 0, 4, 0, "ほぁあアあァぁー…\nいい香りー…\nあアァあ…イイ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111001, 3919, 1, 0, 5, 0, "炎はいいぞ\nそこには人類発展の\nドラマがある", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111001, 3921, 1, 0, 7, 0, "フオオー！\n次から次へと固形食物\n原理気になるー！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111001, 3922, 1, 0, 8, 0, "良書は時間を忘れる\n…ふむ、今度\n布団を持ってこよう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 101201, 3924, 1, 0, 103, 0, "きらーん☆\nハツネちゃんに\n不可能はないのだ☆", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 101201, 3925, 1, 0, 103, 0, "キミと一緒だと\n寝ちゃうのが\nもったいないよ～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 192101, 3933, 1, 0, 1, 0, "ちぇるーん☆", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110901, 3951, 1, 0, 1, 0, "ちぇる～ん☆\n今日もチエルは\nかわいいですよね～？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110901, 3952, 1, 0, 2, 0, "どこかに楽しいもの\n落ちてないかにゃー", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110901, 3953, 1, 0, 3, 0, "チエルのオキニは\n甘々カフェラテ！\nシクヨロでぇ～す☆", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110901, 3954, 1, 0, 4, 0, "先輩、ひまですよね？\nチエル遊んであげても\nいいですけど？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110901, 3955, 1, 0, 5, 0, "はぁー…しゅき…\nカルミナ尊い…\n一生すこれる…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 110901, 3956, 1, 0, 101, 0, "先輩、ひまー\nちょっとは構えよーう\nデュクシ！デュクシ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 110901, 3957, 1, 0, 102, 0, "先輩なんか\nおごって～！\nてか…おごらなくても\nいいから遊んでよ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110901, 3958, 1, 0, 1, 0, "え、なんですか？\n告白だったら\n間に合ってますけど", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110901, 3959, 1, 0, 2, 0, "ちょっと静かに！\n今ヤバかわポーズの\nイメトレ中なんです！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110901, 3960, 1, 0, 3, 0, "足取りが軽やか？\nあー…ダンスとか\n習ってるからですかね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110901, 3961, 1, 0, 4, 0, "チエルの血液型？\n大型新人のОです\n何か問題でも？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 110901, 3962, 1, 0, 101, 0, "先輩って何気に\nいい声してますよね\n言うことは変だけど", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 110901, 3963, 1, 0, 102, 0, "え、なんですか？\nたまにだったら\n告白してもいいですよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110901, 3964, 1, 1, 1, 0, "おはちぇる～ん☆\nハイ、ご一緒に～？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110901, 3965, 1, 2, 2, 0, "今日のランチは\nどこで\n映えよっかにゃー", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110901, 3966, 1, 3, 3, 0, "こんばんちぇるるん☆\nハイ、ご一緒に～？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110901, 3968, 1, 0, 2, 0, "ふかふかチエルン\n略して、ふかルン♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110901, 3969, 1, 0, 3, 0, "かわいいこの子と\nかわいいチエル！\n映ぁ～え～る～！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110901, 3970, 1, 0, 4, 0, "かわいい香り～！\n持って帰ろっと！\nすぅーっ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110901, 3971, 1, 0, 5, 0, "マシュマロは\n焼かない方のが\nかわいくないです？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110901, 3973, 1, 0, 7, 0, "チエル食べても\n太らない体質\nなんでぇ～！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 110901, 3974, 1, 0, 8, 0, "読書は好きですよ\nシンプルに楽しいし\nギャップ萌えでしょ？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112701, 3976, 1, 0, 1, 0, "みんなのあたしを\n見る目が違うね\nま、気にしないけどね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112701, 3977, 1, 0, 2, 0, "今日も疲れた～\nやる気ゼロだし\nもう寝ちゃおうかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112701, 3978, 1, 0, 3, 0, "あたしの代わりに誰か\n働いてくれないかね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112701, 3979, 1, 0, 4, 0, "労働の後に食べる\nあんパン最高～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112701, 3980, 1, 0, 5, 0, "仕事の合間に\nちょっと一息いれよ\nあ～ドングリ美味し～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 112701, 3981, 1, 0, 101, 0, "報酬も入ったことだし\nあいつに何か\n買ってあげようかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 112701, 3982, 1, 0, 102, 0, "次の休みはあいつと\n一日中だらだら\nしたいな～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112701, 3983, 1, 0, 1, 0, "あたしは褒められて\n伸びるタイプなんだ\nだから褒めて褒めて", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112701, 3984, 1, 0, 2, 0, "お、差し入れ？\n気が利くねぇ\nあんがと", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112701, 3985, 1, 0, 3, 0, "新しい依頼？\n今日はもう無理～\n閉店で～す", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112701, 3986, 1, 0, 4, 0, "サ、サボってないよ！\nちょっと休憩してた\nだけだから！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 112701, 3987, 1, 0, 101, 0, "ねえ、一緒に\nサボろうよ？\n働き過ぎはよくないよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 112701, 3988, 1, 0, 102, 0, "あんたが近くにいると\n仕事の疲れも\n和らぐんだよね～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112701, 3989, 1, 1, 1, 0, "はぁ…もう起きなきゃ\nつらい労働の\nはじまりだ～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112701, 3990, 1, 2, 2, 0, "うぅ、お昼に\nあんパン食べ過ぎた…\n苦しい…眠い…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112701, 3991, 1, 3, 3, 0, "明日は仕事ないし\nガッツリ夜ふかし\nしちゃうよ～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112701, 3993, 1, 0, 2, 0, "猫みたいに\n丸くなっちゃうよ～\nあたしリスだけどね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112701, 3994, 1, 0, 3, 0, "ふかふか～\nあたしの尻尾と\n互角とはやるね～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112701, 3995, 1, 0, 4, 0, "あんパンの香りの\nアロマとか\nあればいいのにな～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112701, 3996, 1, 0, 5, 0, "これは危険な\nあたたかさだね～\n労働意欲が\nなくなるよ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112701, 3998, 1, 0, 7, 0, "いっぱい働いたから\nいっぱい食べても\nカロリー０だよ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112701, 3999, 1, 0, 8, 0, "あたしだってたまには\n本くらい読むって\n全然似合わないけどね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 104601, 4001, 1, 0, 103, 0, "あたしの作った\nオリジナルたい焼き…\nあいつ、\n喜んでくれるかにゃ？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 104601, 4002, 1, 0, 103, 0, "あ、お帰りにゃー♪\nえへへ…キミに\nお帰りって言えるのが\nなんかうれしいにゃ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112801, 4003, 1, 0, 1, 0, "フロンティア\nスピリッツを\nお見せするべ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112801, 4004, 1, 0, 2, 0, "この鎧を着ると\n気が引き締まる\n感じがするべ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112801, 4005, 1, 0, 3, 0, "搾りたての牛乳飲んで\n今日も一日\nがんばるべ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112801, 4006, 1, 0, 4, 0, "みんなに牧場の\n乳製品を\n味わってもらいてぇべ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112801, 4007, 1, 0, 5, 0, "ここはいいとこだべ\nうちの動物たちも\n連れてきてやりてぇな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 112801, 4008, 1, 0, 101, 0, "みんなオラより\n若ぇのに働いてて\n立派だべな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 112801, 4009, 1, 0, 102, 0, "牧場もギルドの\nみんなもオラが\nまとめて面倒見るべ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112801, 4010, 1, 0, 1, 0, "さあ、今日も\n忙しくなるべよ\n気合入れてくべ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112801, 4011, 1, 0, 2, 0, "お、あんちゃん\n丁度いいとこに！\n一緒に街に\n行商しに行くべ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112801, 4012, 1, 0, 3, 0, "牧場仕事で培った力が\n戦いでも役立つなら\n一石二鳥だべさ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112801, 4013, 1, 0, 4, 0, "疲れた時は牧場産の\nミルク菓子で\n糖分補給だべ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 112801, 4014, 1, 0, 101, 0, "これ、牧場の\n新製品だべ\n一番最初に味見\nしてほしいべさ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 112801, 4015, 1, 0, 102, 0, "働きづめはよくねぇベ\n膝枕してやっから\nこっち来るべさ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112801, 4016, 1, 1, 1, 0, "ふぁあ…\n酪農家の朝は\n早ぇんだべ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112801, 4017, 1, 2, 2, 0, "ごはんの時間だべ！\n腹が減っては酪農も\n戦いもできねえべ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112801, 4018, 1, 3, 3, 0, "そろそろ寝るべ！\n健全なお笑いは\n健全な肉体に宿るべ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112801, 4020, 1, 0, 2, 0, "牧草みたいに\nふっかふかだべ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112801, 4021, 1, 0, 3, 0, "この子、目の感じが\nエリザベスに似てるべ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112801, 4022, 1, 0, 4, 0, "いい匂いだべ～\n疲れた体に\nしみるべな～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112801, 4023, 1, 0, 5, 0, "あったけぇな～\n牧場にも\n置いてみっかな～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112801, 4025, 1, 0, 7, 0, "うまいべ！\n生産者のこだわりを\n感じるべさ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112801, 4026, 1, 0, 8, 0, "酪農も戦いも\nまずは知識だべ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113001, 4028, 1, 0, 1, 0, "ぴょん、ぴょん♪\nこの服のおかげで、\n体が軽いなぁ…♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113001, 4029, 1, 0, 2, 0, "今の内に部屋の\n掃除をしておこっと…\nみんな喜んで\nくれるといいなぁ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113001, 4030, 1, 0, 3, 0, "耳と尻尾…この服、\n本当にウサギさん\nみたいだなぁ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113001, 4031, 1, 0, 4, 0, "なんだか先輩の\n気配がするような…\n気のせいかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113001, 4032, 1, 0, 5, 0, "ウサギさんの真似を\nしたら、存在感が薄い\nとか言われないかな？\nア、アユミだぴょん♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 113001, 4033, 1, 0, 101, 0, "えへへ…\nいつもとは違う先輩が\nたくさん見られて、\nうれしいな♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 113001, 4034, 1, 0, 102, 0, "本の中の世界で先輩と\n旅をするなんて…\n幸せすぎて、罰が\n当たりそうだよぉ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113001, 4035, 1, 0, 1, 0, "じ～…はっ！？\nす、すみません！\n話しかけてもらえる\nなんて思ってなくて！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113001, 4036, 1, 0, 2, 0, "先輩がすぐそばに…\nし、幸せすぎて\n倒れそうですぅ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113001, 4037, 1, 0, 3, 0, "え、えっと…あの…！\nうう…いざ先輩と\n向き合うと\n緊張しちゃうよぉ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113001, 4038, 1, 0, 4, 0, "はうっ！？\nしょ、正面から見る\n先輩は、私にとって\n神々しすぎますぅ…！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 113001, 4039, 1, 0, 101, 0, "先輩…その…\nい、一緒にご飯でも\nどうでひゅか！\nうう…またかんだぁ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 113001, 4040, 1, 0, 102, 0, "せ、先輩…\nハーブティーを\n入れたんですが、\n一緒にどうですか…？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113001, 4041, 1, 1, 1, 0, "せ、先輩！\nおはようございます！\nえへへ、練習通りに\nバッチリ言えたぁ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113001, 4042, 1, 2, 2, 0, "あ、もうお昼ですね\n時計を持ってて\n良かったです", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113001, 4043, 1, 3, 3, 0, "もうこんな時間…\nお休みする\n準備をしちゃいますね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113001, 4045, 1, 0, 2, 0, "あ…ちょっとだけ\n先輩の匂いがする…\nえへへ、幸せだなぁ♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113001, 4046, 1, 0, 3, 0, "…私もウサギさんの\nぬいぐるみとして、\n先輩のそばに…はっ！\n私ったら何を…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113001, 4047, 1, 0, 4, 0, "あ、いい香り…\n先輩もこういうのが\n好きなのかな？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113001, 4048, 1, 0, 5, 0, "先輩が隣に\n居たら、もっと\n暖かいのになぁ…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113001, 4050, 1, 0, 7, 0, "おいしい…でも、\n先輩と一緒に\n食べたら、もっと\nおいしいだろうな…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113001, 4051, 1, 0, 8, 0, "ウサギさんって、穴を\n掘ったりするんだ…\n私も、隠れたいときに\n掘ってみようかな？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180501, 4053, 1, 0, 1, 0, "ネビアさまのように\n普段からパタパタと\n飛んでみるのも…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180501, 4054, 1, 0, 2, 0, "ご立派になられた\n主さまと並べるように\nもっと精進しなくては", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180501, 4055, 1, 0, 3, 0, "月々の食費が\n増えてしまいました\n申し訳ございません…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180501, 4056, 1, 0, 4, 0, "ワンワンのほうの\n主さまは、この姿に\n驚くでしょうか…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180501, 4057, 1, 0, 5, 0, "主さまと離れていても\n主さまとの絆は\n胸に感じております…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 180501, 4058, 1, 0, 101, 0, "このギルドハウスは\nみなさまの帰る場所…\nわたくしが守ります", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 180501, 4059, 1, 0, 102, 0, "どんな未来が\n待ち受けようとも\n主さまと添い遂げます", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180501, 4060, 1, 0, 1, 0, "おでかけですか？\nではわたくしも\nお供いたします", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180501, 4061, 1, 0, 2, 0, "もう二度と主さまと\n離れたくはありません\nいつ何時も…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180501, 4062, 1, 0, 3, 0, "なにかお困りごと\nでございますか？\nわたくしに何なりと…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180501, 4063, 1, 0, 4, 0, "どうぞゆっくりと\n心ゆくまでくつろいで\nくださいまし、主さま", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 180501, 4064, 1, 0, 101, 0, "この羽で主さまを\nどこまでも遠く…\n高くへと導きます", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 180501, 4065, 1, 0, 102, 0, "主さま、主さま…♪\nおそばにいられるのが\nうれしくて、つい…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180501, 4066, 1, 1, 1, 0, "おはようございます\n今日も一日、主さまの\nサポートをいたします", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180501, 4067, 1, 2, 2, 0, "今日も頑張っている\n主さまに、スタンプを\nもう一つ…ポン！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180501, 4068, 1, 3, 3, 0, "寝付けないのでしたら\nわたくしが子守唄を\n歌ってさしあげます", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180501, 4070, 1, 0, 2, 0, "ふかふか…\nまるで主さまのような\n柔らかさでございます", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180501, 4071, 1, 0, 3, 0, "あなたも一緒に\n主さまと遊びますか？\n至福の時間ですよ♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180501, 4072, 1, 0, 4, 0, "主さまの優しくて\nくすぐったい匂いが\nたまらなく恋しいです", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180501, 4073, 1, 0, 5, 0, "主さまの心と\n繋がったときのような\n温もりを感じます", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180501, 4075, 1, 0, 7, 0, "いけませんっ…！\n元の姿に戻ると、また\n食べてしまうのに…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180501, 4076, 1, 0, 8, 0, "主さまに読み聞かせる\n絵本は…妖精が主役の\n物語にいたしましょう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 104801, 4078, 1, 0, 103, 0, "…彼のこと弟みたいに\n思ってきたのに…私、\nどうしたらいいの…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 104801, 4079, 1, 0, 103, 0, "よ、予定がなかったら\nでいいんだけど…\nその、ふ、二人で\n訓練とか…どうかな？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112901, 4080, 1, 0, 1, 0, "おめかしもしたし\nあとはお兄ちゃんを\n待つだけですね～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112901, 4081, 1, 0, 2, 0, "お兄ちゃんの身の回り\nをお片付け～、これぞ\nデキる妹アピール！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112901, 4082, 1, 0, 3, 0, "あ、衣装がほつれてる\nこれくらいなら自分で\n繕えますね…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112901, 4083, 1, 0, 4, 0, "この格好で歩いてると\n見慣れた景色も\n違うものに見えますね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112901, 4084, 1, 0, 5, 0, "勉強もちゃんとします\n…でも、一人だと\n集中力がもちません～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 112901, 4085, 1, 0, 101, 0, "お兄ちゃんを大好きな\n妹が待ってますよ～\n…まだですかね～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 112901, 4086, 1, 0, 102, 0, "お兄ちゃんのために\n掃除洗濯をする私って\nお嫁さんみたい…\n…えへへ～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112901, 4087, 1, 0, 1, 0, "おに～いちゃん！\n縫い物、一緒に\nやりましょうよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112901, 4088, 1, 0, 2, 0, "かわいい妹を\n愛でたくなりました？\n思う存分、どうぞ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112901, 4089, 1, 0, 3, 0, "わっ、ビックリした～\nもう、お兄ちゃんてば\nイタズラっ子ですね～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112901, 4090, 1, 0, 4, 0, "一緒にお買い物\n行きませんか？\n妹とデートですよっ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 112901, 4091, 1, 0, 101, 0, "お兄ちゃん…頭を\n撫でてくれませんか？\nえへへ、これが一番♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 112901, 4092, 1, 0, 102, 0, "お兄ちゃん大好き♪\n何度でも言いますよ！\nお兄ちゃん大好きっ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112901, 4093, 1, 1, 1, 0, "おはよ～お兄ちゃん！\n妹モーニングコールの\n時間ですよっ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112901, 4094, 1, 2, 2, 0, "そろそろ休憩ですね！\n甘いお菓子で\n元気回復しましょ～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112901, 4095, 1, 3, 3, 0, "夜更かし厳禁！\nでも、もう少しだけ\nお話ししたいなぁ～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112901, 4097, 1, 0, 2, 0, "ふっかふかですよ！\nお兄ちゃんと一緒に\n座りたいなぁ…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112901, 4098, 1, 0, 3, 0, "このぬいぐるみの\nかわいさ…う～ん\n負けられません…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112901, 4099, 1, 0, 4, 0, "香りにも気をつかって\nパーフェクトな妹に\nなっちゃいますから！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112901, 4100, 1, 0, 5, 0, "はぁ～ぬくぬくです～\nお兄ちゃんと一緒に\n温まりたいなぁ…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112901, 4102, 1, 0, 7, 0, "これは止まりませんね\nもぐもぐ…いくらでも\n食べられちゃいます", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 112901, 4103, 1, 0, 8, 0, "これ、デザインの\n参考になりそう！\n私にも作れるかなぁ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106601, 4105, 1, 0, 1, 0, "退屈です…\nなにか楽しいことは\nないですか？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106601, 4106, 1, 0, 2, 0, "ギャングの仕事は\n今日はお休みです\nのんびりするですよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106601, 4107, 1, 0, 3, 0, "ひうっ！　あたしの\n尻尾なんて食べても\nおいしくないですよ？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106601, 4108, 1, 0, 4, 0, "ふう…どうやら\n例の件はまだボスに\nバレてないですね…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106601, 4109, 1, 0, 5, 0, "下克上…いい言葉です\nって、ボスに聞かれて\nないですよね…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 106601, 4110, 1, 0, 101, 0, "いつか絶対ビッグに\nなってやるです！\nあたしを信じるです！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 106601, 4111, 1, 0, 102, 0, "あたしが覚醒したら\nきっと誰よりも\n強くなるはずです！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106601, 4112, 1, 0, 1, 0, "なんですか？\n貢ぎ物なら\nいつでも歓迎ですよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106601, 4113, 1, 0, 2, 0, "あたしだって\nやる時はやるですよ！\nその時がこないだけ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106601, 4114, 1, 0, 3, 0, "しりとり勝負ですか？\nいい度胸ですね、\nかかってくるです！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106601, 4115, 1, 0, 4, 0, "お兄さんは\nヒマそうですね…\nうらやましいです", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 106601, 4116, 1, 0, 101, 0, "かまってほしいです？\nまったく…いいですよ\nしょうがないですね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 106601, 4117, 1, 0, 102, 0, "お兄さんのことは\nこのあたしが\n守ってやるですよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106601, 4118, 1, 1, 1, 0, "デキるギャングの\n朝は早いのです…\nふぁあ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106601, 4119, 1, 2, 2, 0, "お腹がすいたですね\nなにか食べにいくです\nお兄さんのおごりで！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106601, 4120, 1, 3, 3, 0, "ふぁあ…むにゅう…\nまだ起きてるですよぉ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106601, 4122, 1, 0, 2, 0, "はぁ～…\nもうここから一歩も\n動きたくないです", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106601, 4123, 1, 0, 3, 0, "なかなかいいモフりを\nしてやがるです…\n舎弟にしてやるですよ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106601, 4124, 1, 0, 4, 0, "なんかおしゃれな\n匂いがするですね…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106601, 4125, 1, 0, 5, 0, "ふふーん！\nあたしのブレスの方が\n火力は上ですね！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106601, 4127, 1, 0, 7, 0, "あたしに貢ぎ物とは\nよく分かってるですね\n食べてやるですよ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106601, 4128, 1, 0, 8, 0, "ふーむ…\nもっと楽しい本は\nないですかね？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113201, 4130, 1, 0, 1, 0, "海に眠る秘宝…\n心躍る響きだ！\n早く見つけたい…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113201, 4131, 1, 0, 2, 0, "うう～、暑いぃ…\nかき氷を食べて\nすっきりしたいぃ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113201, 4132, 1, 0, 3, 0, "アターック！\nいや、もっとこう…\nアタァーック！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113201, 4133, 1, 0, 4, 0, "スイカ割りで\n目を回さない\nコツはなんだろうか…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113201, 4134, 1, 0, 5, 0, "せっかく海に来たのだ\n泳ぎも上手に\nなりたいものだな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 113201, 4135, 1, 0, 101, 0, "ふっふっふ…\n疼く…疼くぞ！\n日焼けの痕が…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 113201, 4136, 1, 0, 102, 0, "シグルドと私は\n来世でも共にある…\nつまり我らはずっと…\nはわわ…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113201, 4137, 1, 0, 1, 0, "シグルド、私と共に\n日光浴に行かないか？\n光の力を集めよう！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113201, 4138, 1, 0, 2, 0, "この格好だからできる\n必殺技を考えている\n何か案はないか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113201, 4139, 1, 0, 3, 0, "シグルド～！\nスイカを貰ったから\n一緒に食べよう！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113201, 4140, 1, 0, 4, 0, "こう日差しが強いと\n日焼けしそうだな\n跡になってないか…？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 113201, 4141, 1, 0, 101, 0, "我らの夏の黙示録\nしっかりと記録\nしておかねばな", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 113201, 4142, 1, 0, 102, 0, "ちゃんと日焼けどめを\n塗ってもらうべき\nだったかな…？\nシ、シグルドに…！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113201, 4143, 1, 1, 1, 0, "朝だぞシグルド！\nさぁ、日光を浴びて\n光の加護を得るのだ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113201, 4144, 1, 2, 2, 0, "もう昼食の時間か…\n私の魔力も減ったし\n早く補給しなければ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113201, 4145, 1, 3, 3, 0, "夜でも我らの活動は\n終わることはない！\nさぁ準備を…ふわぁ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113201, 4147, 1, 0, 2, 0, "海で遊んでいると\n疲れてしまうからな\n少し休まるとしよう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113201, 4148, 1, 0, 3, 0, "えへへ～、君の名前は\n何にしまちょうかね♪\n…はっ、見たのか！？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113201, 4149, 1, 0, 4, 0, "心地よい香りで\n我が精神も安らぐ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113201, 4150, 1, 0, 5, 0, "別に寒くなどないぞ！\n寒くないが、ちょっと\n暖まりたくなってな！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113201, 4152, 1, 0, 7, 0, "もぐ…食べ過ぎた分は\n魔力に変換されるのだ\n…たぶん", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113201, 4153, 1, 0, 8, 0, "…なな、なんだ！？\nシグルドはこういう\n趣味なのか…！？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113201, 4155, 1, 0, 10, 0, "むむ、この乗り心地…\nいざゆかん、\nシードラゴンよ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113201, 4156, 1, 0, 11, 0, "疾風の冥姫の名に\nふさわしき\n深海の玉座よ…\nふああ…柔らかい～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180201, 4157, 1, 0, 1, 0, "この姿でいると\nわたしもお姫様に\nなれた気がする…♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180201, 4158, 1, 0, 2, 0, "お腹空いてきちゃった\nまずは買い出しに…\n…変身解かなくちゃ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180201, 4159, 1, 0, 3, 0, "胸のところが空いてて\nスースーするの\nまだ慣れないな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180201, 4160, 1, 0, 4, 0, "治癒魔法に使ってた\n魔力を攻撃魔法に…\n頭の中でも練習練習", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180201, 4161, 1, 0, 5, 0, "何もできなかった\nわたしから、少しは\n変われたのかな…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 180201, 4162, 1, 0, 101, 0, "騎士クン…\n今どこにいるの…？\n…会いたいな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 180201, 4163, 1, 0, 102, 0, "離れてても分かるよ…\n温かい力で騎士クンに\n守られてるの", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180201, 4164, 1, 0, 1, 0, "わたしが戦えてるのは\n騎士クンのおかげだよ\nいつもありがとう！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180201, 4165, 1, 0, 2, 0, "お腹空いてそう…？\n…内緒で作ったおやつ\n騎士クンも食べる？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180201, 4166, 1, 0, 3, 0, "ひゃうっ！　え！？\nせ、背中はダメ～！\n言ってから触って～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180201, 4167, 1, 0, 4, 0, "この格好、どうかな？\nえ？　似合ってる…？\n本当？　…うれしい！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 180201, 4168, 1, 0, 101, 0, "かわいい？　そうだね\nひらひらが…わたし？\nええっ、そ、そんな！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 180201, 4169, 1, 0, 102, 0, "騎士クン、これからも\nずっと…わたしの\nそばにいてください", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180201, 4170, 1, 1, 1, 0, "朝からこんなに\n食べる日が来るなんて\n思ってなかったよ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180201, 4171, 1, 2, 2, 0, "お昼を作らなくちゃ\n騎士クン、一緒に\nお料理してみる？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180201, 4172, 1, 3, 3, 0, "騎士クン、もう眠い？\n眠るまで、わたしが\nずっとそばにいるね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180201, 4174, 1, 0, 2, 0, "攻撃参加するからこそ\n適度に体を休めるのは\n大切だよね！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180201, 4175, 1, 0, 3, 0, "かわいい…騎士クンと\nシェフィちゃんにも\nプレゼントしたいな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180201, 4176, 1, 0, 4, 0, "いい香り～…\nあれ？　お腹が空いて\nきちゃった…！？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180201, 4177, 1, 0, 5, 0, "温かい…\n騎士クンがくれる\n力みたい…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180201, 4179, 1, 0, 7, 0, "ぱくっ、\nあむ、はむっ…\nううぅ～…やっぱり\n手が止まらないよぉ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180201, 4180, 1, 0, 8, 0, "新しく覚えたいな…\nみんなで食べられる\n大皿向けの料理♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 104901, 4182, 1, 0, 103, 0, "お姉ちゃんの愛\nこのままじゃ\nあふれちゃうよ…♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 104901, 4183, 1, 0, 103, 0, "弟くん♪\n弟くんっ♪\nおっとうっとくんっ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113101, 4184, 1, 0, 1, 0, "泳ぐのもいいけど\n今日は砂浜で\nのんびりしようかね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113101, 4185, 1, 0, 2, 0, "さてと\nちょっくらひと泳ぎ\n行ってくるかね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113101, 4186, 1, 0, 3, 0, "あっついねぇ…\nうちわがないと\nしんどいったらないよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113101, 4187, 1, 0, 4, 0, "冷たすぎる飲み物で\n体を冷やさないよう\n気をつけないと…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113101, 4188, 1, 0, 5, 0, "夏はあっという間だし\n悔いがないように\n楽しまないとね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 113101, 4189, 1, 0, 101, 0, "きっとこの夏は\nアタシにとって大事な\n想い出になるね…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 113101, 4190, 1, 0, 102, 0, "…前に比べたら\nアタシたちの距離も\n近づいたみたいだね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113101, 4191, 1, 0, 1, 0, "もし暇なら\n今日も笹舟釣り\n一緒にどうだい？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113101, 4192, 1, 0, 2, 0, "お前さん、釣りに\n興味が湧いたなら今度\n道具を見に行こうよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113101, 4193, 1, 0, 3, 0, "今から砂浜に行くんだ\nお前さんも一緒に\n散歩するかい？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113101, 4194, 1, 0, 4, 0, "え、日焼け跡？\nおや…本当だ\nすぐ焼けちまうねえ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 113101, 4195, 1, 0, 101, 0, "ひゃっ！？\nちょっと！\n日焼け跡をいきなり\n触らないでおくれ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 113101, 4196, 1, 0, 102, 0, "お前さんは、その…\nアタシと一緒にいて\n楽しかったかい？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113101, 4197, 1, 1, 1, 0, "おはようさん\n今日も砂浜に行って\nゆっくりしようか", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113101, 4198, 1, 2, 2, 0, "お昼か…\nそうだ、よかったら\n刺身定食、作ろうか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113101, 4199, 1, 3, 3, 0, "夜の空を見てるとさ\nなつかしい気持ちに\nなるんだよね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113101, 4201, 1, 0, 2, 0, "適度に休憩して\n夏バテしないよう\n気をつけないとね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113101, 4202, 1, 0, 3, 0, "あんたたちも\n海を楽しみたいって\n思っているのかね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113101, 4203, 1, 0, 4, 0, "ん～…香りでも\nバカンスらしさを\n楽しめるもんだね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113101, 4204, 1, 0, 5, 0, "こういう格好だと\n少し冷えちまうんだよ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113101, 4206, 1, 0, 7, 0, "もぐもぐもぐ…\n遊んだ分、お腹が\n空いちまってねぇ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113101, 4207, 1, 0, 8, 0, "…ん、なんだいこの本\n催眠…うーん読めない\nナナカに聞いてみるか", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113101, 4209, 1, 0, 10, 0, "へえ…\nなかなかいい\n乗り心地じゃないか", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113101, 4210, 1, 0, 11, 0, "そうそうハマグリって\n竿でも釣れるんだよ\n知ってたかい？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113301, 4211, 1, 0, 1, 0, "海を満喫しなきゃね\nちょっとひと泳ぎ\n行ってこようかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113301, 4212, 1, 0, 2, 0, "う～ん…♪\nいつもと違った\n開放感～\nなんちゃって！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113301, 4213, 1, 0, 3, 0, "今のナナカちゃんは\nたぶん水属性！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113301, 4214, 1, 0, 4, 0, "水着もバッチリ！\nサイカワサイツヨ\nナナカちゃ～ん☆", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113301, 4215, 1, 0, 5, 0, "んっふっふ\nドキ！　水着だらけの\nギルドハウス！\nとかどうかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 113301, 4216, 1, 0, 101, 0, "そんなに見られると\nちょ～っと\n恥ずかしいかも…\nなんてねなんてね！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 113301, 4217, 1, 0, 102, 0, "ちょっとくらい\nドキッとしてくれても\nいいのにな～、もう…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113301, 4218, 1, 0, 1, 0, "水着魔法少女の\n相方がほしいなぁ～…\nね、君やってみない？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113301, 4219, 1, 0, 2, 0, "いつもの格好と同じ？\nも～、わかってない！\nセクスィー度が違う！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113301, 4220, 1, 0, 3, 0, "どうかなどうかな？\nこのひらひらのとこが\nかわいーかなー\nとか思うんだけど！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113301, 4221, 1, 0, 4, 0, "せっかくだから\nギャルゲーイベント\nごっことか\nやってみる？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 113301, 4222, 1, 0, 101, 0, "ナナカちゃんの\n好感度を上げたい？\nならおでかけだー！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 113301, 4223, 1, 0, 102, 0, "おうちで水着って\nなんだかコスプレ\nみたいで楽しいかも♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113301, 4224, 1, 1, 1, 0, "おっはよーう！\n今日はバカンス日和！\n海へゴーですぞ～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113301, 4225, 1, 2, 2, 0, "お昼ご飯どうしよ～…\nあ、ナナカちゃんと\nどっか食べに行く？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113301, 4226, 1, 3, 3, 0, "夜は本を読むのが\n捗りまくりですな～！\n今日はなに読もっかな", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113301, 4228, 1, 0, 2, 0, "次のイベントもあるし\nちょっと休んで体力を\n回復しなきゃね～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113301, 4229, 1, 0, 3, 0, "こういうマスコット\n私にもいたらなぁ\nいっそ作っちゃう…？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113301, 4230, 1, 0, 4, 0, "水着魔法少女に\n似合う香りってのも\n考えてみよっかな♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113301, 4231, 1, 0, 5, 0, "夜になると急に冷えて\n肌寒いんだよね～…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113301, 4233, 1, 0, 7, 0, "あんまり食べると\nお腹が出て…でも…\nうう、止まらない～！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113301, 4234, 1, 0, 8, 0, "この催眠魔法の本\n誰にもばれないように\n保管しとかなきゃね…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113301, 4236, 1, 0, 10, 0, "ゴーゴーゴーゴーゴー\nナナカちゃーん♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113301, 4237, 1, 0, 11, 0, "ナナカちゃんの\n誕生～\nなんちゃって", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113401, 4238, 1, 0, 1, 0, "ファイトファイト☆\n一生懸命\n応援するね！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113401, 4239, 1, 0, 2, 0, "みんながんばってる！\n私も応援でみんなを\n盛り上げなきゃ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113401, 4240, 1, 0, 3, 0, "もし元気のない人が\nいたら…応援で少し\nでも元気にしたいな！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113401, 4241, 1, 0, 4, 0, "がんばれ～☆\nよし、発声練習OK！\n応援は、声も大事！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113401, 4242, 1, 0, 5, 0, "せっかく水着なんだし\n泳いだり潜ったりして\n遊びたいな～！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 113401, 4243, 1, 0, 101, 0, "チアリーダーとして\nキミを精一杯\n応援したいな…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 113401, 4244, 1, 0, 102, 0, "キミへの応援\nちゃんと\n届いてるかな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113401, 4245, 1, 0, 1, 0, "えへへ…今ね、\nキミへの応援歌を\n考えてたのだ～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113401, 4246, 1, 0, 2, 0, "どうしたの？\n元気がないなら\n私が応援するよ☆", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113401, 4247, 1, 0, 3, 0, "がんばったあとは休憩\nしなきゃね！　私と\n一緒にお昼寝する？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113401, 4248, 1, 0, 4, 0, "シオリンとおそろいの\n水着で応援…\nしてみたいなぁ～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 113401, 4249, 1, 0, 101, 0, "次はなにして遊ぶ？\nキミがしたいことを\n教えてよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 113401, 4250, 1, 0, 102, 0, "キミを応援\nすることができて…\n私、すごく幸せ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113401, 4251, 1, 1, 1, 0, "キミって早起き\nなんだね！　私も\n見習わなきゃ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113401, 4252, 1, 2, 2, 0, "ちょっとお疲れかな？\nそれじゃあ\n応援してあげるね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113401, 4253, 1, 3, 3, 0, "遅い時間まで\nがんばってえらいね！\nでも無理はダメだよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113401, 4255, 1, 0, 2, 0, "ちょっとだけ休憩…\n…あう、座り心地が\n良すぎて…眠気が…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113401, 4256, 1, 0, 3, 0, "ここにいるみんなを\n私の応援でもっと\n元気にするのだ～！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113401, 4257, 1, 0, 4, 0, "元気の出る香り～♪\nこれもうまく応援に\n活かせないかな～？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113401, 4258, 1, 0, 5, 0, "ぽかぽかあったまって\n元気をチャージ\nするのだ～☆", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113401, 4260, 1, 0, 7, 0, "いっぱい食べて、\nみんなを応援する\nパワーをつけなきゃ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113401, 4261, 1, 0, 8, 0, "みんなを元気にする\n方法は…どこかに\n書いてないかなぁ…？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113401, 4263, 1, 0, 10, 0, "わぁ、この子\n怪獣さんみたいで\nかわいいね～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113401, 4264, 1, 0, 11, 0, "水着でこれに\n乗ってると海にいる\n気分になれるね！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113501, 4265, 1, 0, 1, 0, "すごく忙しいけど\nとても充実した気分\nこれが青春なのかしら", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113501, 4266, 1, 0, 2, 0, "森の静かな雰囲気も\n海の開放的な空気も\nどちらも素敵ね～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113501, 4267, 1, 0, 3, 0, "汚れたお洗濯物は\nみんなが頑張った証\nなんだか愛おしいわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113501, 4268, 1, 0, 4, 0, "みんなを見てると\n何だか私も\n走りたくなっちゃうわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113501, 4269, 1, 0, 5, 0, "せっかくの水着だし\n泳ぐ練習でも\nしてみようかしら", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 113501, 4270, 1, 0, 101, 0, "みんな～ファイト～！\n応援するのも大事よね\n練習しておかなきゃ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 113501, 4271, 1, 0, 102, 0, "先生に教えられること\nまだまだい～っぱいね\n次は何がいいかしら♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113501, 4272, 1, 0, 1, 0, "今日も暑くなりそうね\nこまめな水分補給を\n忘れないでね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113501, 4273, 1, 0, 2, 0, "手伝ってくれるの？\nありがとう♪\n先生、頼りにしてるわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113501, 4274, 1, 0, 3, 0, "休憩はこまめに\nとりましょうね\n無理は厳禁よ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113501, 4275, 1, 0, 4, 0, "海の遊びって\nどんなのがあるの？\n先生に教えてね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 113501, 4276, 1, 0, 101, 0, "水着は初めてだけど\nどう？　変じゃない？\nふふ、ありがとう", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 113501, 4277, 1, 0, 102, 0, "あなたと過ごした\nこの夏は、きっと…\n忘れられない思い出ね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113501, 4278, 1, 1, 1, 0, "おはよう～\n朝練前にちゃんと\nごはん食べましょうね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113501, 4279, 1, 2, 2, 0, "お弁当作ってきたの\n栄養いっぱい摂って\n午後も頑張ろうね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113501, 4280, 1, 3, 3, 0, "もうお休みしなきゃね\n先生が子守唄を歌って\nあげましょうか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113501, 4282, 1, 0, 2, 0, "ふふ、いい気持ちねぇ\n先生、少しだけ\n休憩しちゃおうかしら", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113501, 4283, 1, 0, 3, 0, "あらかわいい♪\nチームにもこういう\nマスコットが欲しいわ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113501, 4284, 1, 0, 4, 0, "あら、海の香り？\n波の音まで聞こえて\nきちゃいそうだわ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113501, 4285, 1, 0, 5, 0, "うふふ\nこれなら濡れた水着を\n乾かすのも簡単ね♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113501, 4287, 1, 0, 7, 0, "これ、美味しいわね♪\nでも、食べ過ぎは\n禁物よ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113501, 4288, 1, 0, 8, 0, "先生だって日々勉強よ\n新しいことを知るのは\nとても楽しいわ♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113501, 4290, 1, 0, 10, 0, "あらあら、楽しいわね\nみんなもきっと\nおおはしゃぎね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113501, 4291, 1, 0, 11, 0, "ふふ、これで\n夏らしい気分を\n満喫するわよ♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 102301, 4292, 1, 0, 103, 0, "サンドイッチも\n作ったし、\nお兄ちゃんのお出迎え\n準備は完璧だね♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 102301, 4293, 1, 0, 103, 0, "えへへ、お兄ちゃんと\nぷうきちが居てくれる\nだけで、なんだか\nうれしくなっちゃう♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113601, 4294, 1, 0, 1, 0, "少し赤くなっているな\n日焼けなんてしたのは\nいつ以来だろう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113601, 4295, 1, 0, 2, 0, "この格好で\n過ごすのは\n少し勇気がいるな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113601, 4296, 1, 0, 3, 0, "暑いのは平気だが\n肌が外気に触れるの\nにはまだ慣れないな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113601, 4297, 1, 0, 4, 0, "この格好でも\n堂々としていれば\n恥ずかしくはない…\nのかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113601, 4298, 1, 0, 5, 0, "おっと…鎧姿の時と\n重心が違うせいか\nちょっと不安定だな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 113601, 4299, 1, 0, 101, 0, "この水着…\n私に似合って\nいるのだろうか…？\n少年には聞きづらいな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 113601, 4300, 1, 0, 102, 0, "少年との\n海のバカンス…\nまた行きたい\nものだな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113601, 4301, 1, 0, 1, 0, "あまり見るのは\nよしてくれ…\n恥ずかしいじゃ\nないか…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113601, 4302, 1, 0, 2, 0, "どうかしたのかい？\n私でよければ\n話し相手くらいには\nなろうじゃないか", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113601, 4303, 1, 0, 3, 0, "ひゃっ！\nす、すまない\nちょっと過敏に\nなっているようだ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113601, 4304, 1, 0, 4, 0, "…っくしゅん！\nすまない\n薄着には慣れて\nないものでね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 113601, 4305, 1, 0, 101, 0, "まあ、少年になら\n見られても…\nい、いや！\nなんでもない！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 113601, 4306, 1, 0, 102, 0, "私がこうして\nゆっくりできるのは\n少年がそばにいて\nくれるからだよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113601, 4307, 1, 1, 1, 0, "この時間なら\nビーチにも人は\n少ないだろうね…\n遊びに行くかい？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113601, 4308, 1, 2, 2, 0, "昼食のお誘いかい？\nいいよ、付き合おう", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113601, 4309, 1, 3, 3, 0, "もう夜か…\n一日が経つのは\nはやいものだね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113601, 4311, 1, 0, 2, 0, "これは楽でいいな…\nいつもは鎧姿で\n家具を傷つけないか\n気を使っているからね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113601, 4312, 1, 0, 3, 0, "うん…ふわふわの\n感触が\nよくわかるな…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113601, 4313, 1, 0, 4, 0, "いつもは\n鎧に匂いが\n移ってしまうのが\n悩みなんだよ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113601, 4314, 1, 0, 5, 0, "うーむ…\n涼しいのに暖かい\nなんだか奇妙な気分だ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113601, 4316, 1, 0, 7, 0, "兜を気にせず\n食べられるのは\n楽でいいな…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113601, 4317, 1, 0, 8, 0, "ふむ…鎧がないと\nページがめくりやすい", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113601, 4319, 1, 0, 10, 0, "身軽でいいな…\n鎧を着ていると\n騎乗訓練の時\n馬に申し訳がなくてね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113601, 4320, 1, 0, 11, 0, "貝をうまく使えば\n水中でも\n守りを固められそうだ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106801, 4321, 1, 0, 1, 0, "どこをアジトと\nつなげようかな～…\nこのへんにしよっと♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106801, 4322, 1, 0, 2, 0, "ここってちょっと\n変わった建物だね\n妖精がいるでしょ？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106801, 4323, 1, 0, 3, 0, "色んな人を観察できて\n眼福眼福…♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106801, 4324, 1, 0, 4, 0, "家具の配置変更なら\nアタシにおまかせあれ\n…家の形は変えない？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106801, 4325, 1, 0, 5, 0, "新メニューどうしよう\n思いついた人がいたら\nアタシに教えてね！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 106801, 4326, 1, 0, 101, 0, "少年、早く帰って\nこないかな…\nよし、迎えに行こう！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 106801, 4327, 1, 0, 102, 0, "結局またあちこち\n移動する日々だし…\n少年に会いたいな～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106801, 4328, 1, 0, 1, 0, "どしたの少年？\nお姉さんに何でも\n相談してみな～？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106801, 4329, 1, 0, 2, 0, "あれをああして…\nおや少年！　ごめんね\nすぐに気づけなくて！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106801, 4330, 1, 0, 3, 0, "何？　クリスが\nアタシを探してる？\n…いないって言って！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106801, 4331, 1, 0, 4, 0, "よければリノちゃんと\nシズルちゃんにも\n顔を見せてあげて", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 106801, 4332, 1, 0, 101, 0, "少年～！\nもうヘトヘトだよ～！\nお願い癒して～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 106801, 4333, 1, 0, 102, 0, "…アタシが素直に\n助けてって言えるのは\n少年だけだよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106801, 4334, 1, 1, 1, 0, "朝だよ少年！\n爽やかな朝の\nクレープはいかが？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106801, 4335, 1, 2, 2, 0, "お腹空いたな～\nお昼のクレープ\n少年は食べる？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106801, 4336, 1, 3, 3, 0, "夜までクレープは\nやめとけ？　ごめん\nそれだけは無理…！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106801, 4338, 1, 0, 2, 0, "アタシこんなに\nゆっくりしちゃって\nいいんだっけ～？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106801, 4339, 1, 0, 3, 0, "…これ\nネネカが変身\nしてないよね？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106801, 4340, 1, 0, 4, 0, "いい香りだ…\n解析完了\n今度生成しようっと♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106801, 4341, 1, 0, 5, 0, "安心するな～…\nアジトに置けないから\n羨ましいよ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106801, 4343, 1, 0, 7, 0, "食べ物を消化するって\n仕組み、どんなものか\n気になる…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106801, 4344, 1, 0, 8, 0, "速読できるけど\n本を読む時くらい\n文字にじっくり\n浸かりたいんだよね…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113701, 4346, 1, 0, 1, 0, "エンジェルとして\nみんなを元気に…！\nアカリ、頑張ります♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113701, 4347, 1, 0, 2, 0, "お姉ちゃんと一緒に\nお兄ちゃんを…えへへ\nまだかな、お兄ちゃん", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113701, 4348, 1, 0, 3, 0, "エンジェルの音色を\n目指して、今日も\nサックスの練習です♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113701, 4349, 1, 0, 4, 0, "お兄ちゃんとの特訓も\n頑張らないと…\n厳しくしてくださいね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113701, 4350, 1, 0, 5, 0, "かわいいエンジェルの\n格好、お兄ちゃんなら\n喜んでくれますよね♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 113701, 4351, 1, 0, 101, 0, "エンジェルの力で\nお兄ちゃんのハートも\nイチコロですよ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 113701, 4352, 1, 0, 102, 0, "アカリが一番\n元気にしたいのは\nお兄ちゃんですよ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113701, 4353, 1, 0, 1, 0, "お兄ちゃん？\n触りたいなら…\nこことかどうですか♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113701, 4354, 1, 0, 2, 0, "特訓の時間ですか？\nアカリ、楽しみに\nしてたんですよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113701, 4355, 1, 0, 3, 0, "あ、お兄ちゃん！\nそろそろアカリと\n休憩…しませんか♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113701, 4356, 1, 0, 4, 0, "この羽、触り心地\nいいですか？　あんっ\nくすぐったいですよぉ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 113701, 4357, 1, 0, 101, 0, "お兄ちゃんも元気に\nなりたいんですか？\nアカリにお任せです♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 113701, 4358, 1, 0, 102, 0, "やんっ♪\nお兄ちゃんったら\nダイタンですね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113701, 4359, 1, 1, 1, 0, "お兄ちゃん、朝ですよ\nすっごく気持ちのいい\nお天気ですよ…♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113701, 4360, 1, 2, 2, 0, "お昼寝しませんか\nお兄ちゃん？\nアカリの膝枕で…♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113701, 4361, 1, 3, 3, 0, "夜ですね、お兄ちゃん\nアカリ、なんだか胸が\nドキドキしちゃいます", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113701, 4363, 1, 0, 2, 0, "フカフカですね…\n隣にお兄ちゃんが\nいてくれたらなぁ…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113701, 4364, 1, 0, 3, 0, "えへへ、アカリも\nこんな風にかわいく\nなりたいな～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113701, 4365, 1, 0, 4, 0, "お兄ちゃんが好きな\n匂いなのかな？\n参考にしますね！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113701, 4366, 1, 0, 5, 0, "体が火照ってきて\nなんだかアカリ…\nクラクラしちゃう…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113701, 4368, 1, 0, 7, 0, "はむ、んぐ…あっ\nほっぺについちゃった\nベトベトですよぉ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113701, 4369, 1, 0, 8, 0, "エンジェルって色んな\n物語に出てきますよね\nもっと調べなくちゃ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 103201, 4371, 1, 0, 103, 0, "あの方の前では\n常に気高く\nありたいですわね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 103201, 4372, 1, 0, 103, 0, "どんなに道に迷っても\nあなたさまの元には\n絶対に辿り着く\n自信がありますわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 102801, 4373, 1, 0, 103, 0, "あんたと一緒に\nいるときが一番楽しい\n一時かもしれないわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 102801, 4374, 1, 0, 103, 0, "いつかあんたと一緒に\nサレンディア救護院を\n切り盛り…なんてね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113801, 4375, 1, 0, 1, 0, "今日も\nエンジェルとして、\nみんなの幸せの\nために頑張らないと", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113801, 4376, 1, 0, 2, 0, "せっかく羽が\nついてるのに\n飛べないのが残念だわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113801, 4377, 1, 0, 3, 0, "この格好、子供たちに\n妙にウケがいいわね\nま、悪くないけど", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113801, 4378, 1, 0, 4, 0, "や、やっぱりみんな\n注目してくるわね…\nエンジェルって珍しい\nからしかたないか", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113801, 4379, 1, 0, 5, 0, "エンジェルになると\n人見知りがなおる！\n…わけないか", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 113801, 4380, 1, 0, 101, 0, "エンジェルの活動は\n大変だけど、自分が\n成長してるって\n実感がうれしいわね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 113801, 4381, 1, 0, 102, 0, "もし誰かの願いを\nかなえられるなら\n私はアイツの…\nって何考えてるのよ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113801, 4382, 1, 0, 1, 0, "何ボサっとしてるの！\nさあ、街に出て\n人助けするわよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113801, 4383, 1, 0, 2, 0, "エンジェリック\nアロー！\nなんてね…え、アンタ\nいつから見てたのよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113801, 4384, 1, 0, 3, 0, "コラ！\n羽は触らないで！\n壊れたらどうするのよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113801, 4385, 1, 0, 4, 0, "エンジェルじゃなく\nても、アンタはいつも\n人助けしてるわよね\n…ちょっと尊敬するわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 113801, 4386, 1, 0, 101, 0, "この服なら他の子より\n私を見てくれるかも…\nって、な、何\n見てるのよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 113801, 4387, 1, 0, 102, 0, "ね、ねえ、もし私が\n困ってたら、一番に\n駆けつけて…やっぱり\n何でもないわ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113801, 4388, 1, 1, 1, 0, "おはよう…あ、あの、\nエンジェルとして\n町に行くんだけど…\n一緒に行かない？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113801, 4389, 1, 2, 2, 0, "ふぅ、いいことした\n後のランチは\nおいしいわね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113801, 4390, 1, 3, 3, 0, "もうこんな時間…\n夜更かししてないで\n早く寝なさいよね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113801, 4392, 1, 0, 2, 0, "いい座り心地ね～\nまさに天にも昇る\n心地よさだわ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113801, 4393, 1, 0, 3, 0, "ふふ、かわいい…\nあ、羽付けたら\nもっとかわいく\nなるんじゃないかな？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113801, 4394, 1, 0, 4, 0, "はぁ～落ち着くわ～\n回復アイテムみたいで\n癒やされちゃうわ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113801, 4395, 1, 0, 5, 0, "わっ、とと…\n危ない、羽が\nこげちゃうところ\nだったわ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113801, 4397, 1, 0, 7, 0, "人の役に立つのって\nすごく大変だから、\nこれでライフ回復よ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113801, 4398, 1, 0, 8, 0, "エンジェルに関する\n本はあるかしら…あ！\nこれ探してた攻略本！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113901, 4400, 1, 0, 1, 0, "ふっふっふ\n吸血鬼らしく\n驚かせてあげましょう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113901, 4401, 1, 0, 2, 0, "獲物はどこですか～！\nう～ん、もうちょっと\n迫力が欲しいですね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113901, 4402, 1, 0, 3, 0, "トリックオアトリート\n…返事がないので\nイタズラしておきます", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113901, 4403, 1, 0, 4, 0, "ハロウィンですし\nたまには騎士さんでも\n誘ってあげますかね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113901, 4404, 1, 0, 5, 0, "マントは動きに\nメリハリがついて\nいいですね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 113901, 4405, 1, 0, 101, 0, "色んな仮装が見られて\nやっぱりハロウィンは\n最高ですね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 113901, 4406, 1, 0, 102, 0, "吸血鬼らしい行動って\n噛みついたり…いや\nそれは大胆過ぎです！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113901, 4407, 1, 0, 1, 0, "呼びましたか？\n手短にお願いしますよ\n忙しいんですから！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113901, 4408, 1, 0, 2, 0, "え？　衣装に穴が？\nありがとうございます\n…よく見てるんですね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113901, 4409, 1, 0, 3, 0, "ふわあ…吸血鬼の\n姿でも、眠い時は\n眠いんですよね…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113901, 4410, 1, 0, 4, 0, "ハロウィンってライブ\nとは違った賑やかさが\nあってワクワクします", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 113901, 4411, 1, 0, 101, 0, "私を驚かそうとしても\n無駄ですよ！\nすぐ分かるんですから", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 113901, 4412, 1, 0, 102, 0, "騎士さん、一緒に\nハロウィンを…いいえ\nなんでもないですっ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113901, 4413, 1, 1, 1, 0, "仕事で寝不足だから\n太陽がまぶしいです…\n私、吸血鬼っぽいかも", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113901, 4414, 1, 2, 2, 0, "お昼ご飯は真っ赤な…\nトマト尽くしのお弁当\nにしてみました", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113901, 4415, 1, 3, 3, 0, "夜はハロウィンが\n盛り上がる時間…\n行きますよ騎士さん！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113901, 4417, 1, 0, 2, 0, "このフィット感\nどうにか服作りにも\n活かせれば…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113901, 4418, 1, 0, 3, 0, "よく出来てますね…！\nぬいぐるみの服を作る\nのも楽しそうですね！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113901, 4419, 1, 0, 4, 0, "いい感じですね…\n服に合わせた香りって\nいうのも、いいかも…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113901, 4420, 1, 0, 5, 0, "はぁ、温まりますね…\nおっと、マントに火が\n点いちゃいそうです！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113901, 4422, 1, 0, 7, 0, "あぁ、美味しい～\n食べた分は、働いて\n燃焼しますからね！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113901, 4423, 1, 0, 8, 0, "伝説の吸血鬼…\n夜を統べる者…\nほうほう…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 113901, 4425, 1, 0, 12, 0, "やっぱりハロウィンと\nいえばお菓子です！\nさぁ配りましょうか！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114101, 4427, 1, 0, 1, 0, "この格好…\nカッコイイのに\n動きやすくて\n着心地も最高ッス！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114101, 4428, 1, 0, 2, 0, "キメポーズはこう…\nいやこっちのほうが…\nなやましいッス…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114101, 4429, 1, 0, 3, 0, "トリックオア\nジャスティス！\nって言ったら何か\nもらえるッスかね？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114101, 4430, 1, 0, 4, 0, "新しい必殺技の\n特訓もしなくちゃ\nいけないッスね…\n忙しいッス！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114101, 4431, 1, 0, 5, 0, "自分のこの姿\nトモね～ちゃんにも\nみせてあげるッス！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 114101, 4432, 1, 0, 101, 0, "ホワイトタイガー\n参上ッス！\n今のポーズは\nキマったッス！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 114101, 4433, 1, 0, 102, 0, "あの人と一緒だと\nほんとにヒーローに\nなれる気がするッス！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114101, 4434, 1, 0, 1, 0, "どうッスか？\nこの色、自分に\n似合ってるッスかね？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114101, 4435, 1, 0, 2, 0, "ヒマだったら\n必殺技の特訓に\n付き合ってもらって\nいいッスか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114101, 4436, 1, 0, 3, 0, "ちょうどいいところに\n来たッスね！\nこのキメポーズ\nどうッスか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114101, 4437, 1, 0, 4, 0, "お！　この格好、\n気になるッスか？\nじっくり見ても\nいいッスよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 114101, 4438, 1, 0, 101, 0, "あんたも一緒に\n新しい技の練習\nどうッスか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 114101, 4439, 1, 0, 102, 0, "今日もあんたと一緒に\nランドソルの平和を\n守るッスよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114101, 4440, 1, 1, 1, 0, "朝ッス！\n一緒に早朝特訓\nやるッスよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114101, 4441, 1, 2, 2, 0, "お昼休憩ッス！\nヒーローにも\n休息は必要ッス！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114101, 4442, 1, 3, 3, 0, "闇夜に映える\n白い衣装で\n平和を守るッスよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114101, 4444, 1, 0, 2, 0, "トラも狩りの時以外は\nごろごろしてるって\n本で読んだッスよー", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114101, 4445, 1, 0, 3, 0, "もこもこッスね♪\nこのかわいらしさを\n守ってあげるのも\nヒーローの役目ッス！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114101, 4446, 1, 0, 4, 0, "いい匂いッスね…\nリラックスして\n瞑想するッス！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114101, 4447, 1, 0, 5, 0, "燃える炎は\nヒーローの心意気ッス\nいつまでも\n見てられるッスよ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114101, 4449, 1, 0, 7, 0, "もぐもぐ…\nいっぱい食べて\n強いヒーローに\nなるッスよ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114101, 4450, 1, 0, 8, 0, "ちゃんと勉強の本も\n読んでるッスよ…！\nヒーローには賢さも\n必要らしいッス！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114101, 4452, 1, 0, 12, 0, "わわっ、お菓子が\nいっぱいッス！\nみんなで\n食べるッスよ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 103001, 4454, 1, 0, 103, 0, "ワタシがチューシンと\nなった今、天下統一は\n目の前デス！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 103001, 4455, 1, 0, 103, 0, "もうショーグンってば\nそれはセイシツに\nすることデ～ス…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114001, 4456, 1, 0, 1, 0, "女幽霊…うらめしや…\nうーん、これは\nちょっと違う気が\nするな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114001, 4457, 1, 0, 2, 0, "何だか寒気がする…\nま、まさか\nこの部屋にも幽霊が？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114001, 4458, 1, 0, 3, 0, "ハロウィンは楽しいね\n童心に帰ったような\n気分だよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114001, 4459, 1, 0, 4, 0, "今度のハロウィンは\nユイとヒヨリを\n誘ってみようかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114001, 4460, 1, 0, 5, 0, "ハロウィンのお菓子は\nどれもかわいいね\n…一つくらいなら\nいいかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 114001, 4461, 1, 0, 101, 0, "次は彼と\nどんなハロウィンを\n過ごそうかな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 114001, 4462, 1, 0, 102, 0, "こんな賑やかさも\nいいものだね\n彼といられるのなら\nなおさら…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114001, 4463, 1, 0, 1, 0, "…いたずらしようと\nしてない、キミ？\nふふ、お見通しだよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114001, 4464, 1, 0, 2, 0, "え、私にもお菓子を？\nありがとう…ふふ\nうれしいものだね\nハッピーハロウィン！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114001, 4465, 1, 0, 3, 0, "どうしたの？\n出かけるなら\n私も同行しようか", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114001, 4466, 1, 0, 4, 0, "この格好、舞踏会にも\n出られそうだね\n一曲、踊ってみる？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 114001, 4467, 1, 0, 101, 0, "やるからには本気で\nやらせてもらおう\nトリックオア\nトリート！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 114001, 4468, 1, 0, 102, 0, "他の子に目移りする\nのも分かるが、その…\nせっかくの特別な衣装\n見てほしいな", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114001, 4469, 1, 1, 1, 0, "寝起きの体を\n慣らしておこうか\nほら、剣を取って", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114001, 4470, 1, 2, 2, 0, "お菓子もいいけど\nちゃんと昼食も\nとるんだよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114001, 4471, 1, 3, 3, 0, "さあ、行こうか！\nハロウィンは\nこれからが本番だよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114001, 4473, 1, 0, 2, 0, "ハロウィンはまだ\nこれからだ\n一休みしようか", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114001, 4474, 1, 0, 3, 0, "キミたちも\n仮装に興味は\nあるかな？　…ふふ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114001, 4475, 1, 0, 4, 0, "いい香りだけど\n幽霊が集まったり\nしないだろうか…？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114001, 4476, 1, 0, 5, 0, "暖かい…一息ついたら\nハロウィンの\nパレードを\n見に行こうかな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114001, 4478, 1, 0, 7, 0, "ハロウィンを本気で\n楽しむなら\n食事もしっかり\nとっておかないとね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114001, 4479, 1, 0, 8, 0, "ハロウィンの\nホラー小説か…\nけ、結構\n怖いものだね…？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114001, 4481, 1, 0, 12, 0, "お菓子がいっぱいだ…\nこういうのって\n見ているだけで心が\n躍ってしまうね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114201, 4483, 1, 0, 1, 0, "こんなにかわいい\n武器で戦えるとは…\nすごいものだな\n魔法少女は", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114201, 4484, 1, 0, 2, 0, "決めポーズの訓練も\nしっかりやっておくか\n…誰も見ていないな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114201, 4485, 1, 0, 3, 0, "こういう格好は\nユキあたりがすごく\n似合いそうだな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114201, 4486, 1, 0, 4, 0, "子どもたちにサインを\n求められてしまった…\nこれもまた私の使命！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114201, 4487, 1, 0, 5, 0, "この格好は目立つな…\nいや、提督とは人々の\n視線を集めるものだ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 114201, 4488, 1, 0, 101, 0, "あいつと作戦会議を\nしておくか。あれで\nなかなか頼れるからな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 114201, 4489, 1, 0, 102, 0, "あいつには私の勇姿を\nちゃんと見ていて\nほしいものだな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114201, 4490, 1, 0, 1, 0, "一仕事終えたことだし\n一緒に甘いものでも\n食べに行くか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114201, 4491, 1, 0, 2, 0, "怪しげな気配がするな\n貴公も警戒を\n怠るなよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114201, 4492, 1, 0, 3, 0, "シオリに魔法少女の\n本を借りたんだ\n一緒に読まないか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114201, 4493, 1, 0, 4, 0, "巡回の時間まで\nお茶にでもするか\n貴公も付き合うがいい", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 114201, 4494, 1, 0, 101, 0, "貴公がサポートして\nくれるのなら心強いな\n頼りにしているぞ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 114201, 4495, 1, 0, 102, 0, "頭を撫でるな～！\n…まったく、貴公だけ\nだぞ、こんな風に私を\n撫でてくるのは…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114201, 4496, 1, 1, 1, 0, "起きろ貴公！\n魔法少女の朝は\n早いぞ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114201, 4497, 1, 2, 2, 0, "ほら、食後の\nデザートだ\nて、適度な糖分は\n体に必要なんだ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114201, 4498, 1, 3, 3, 0, "ふわぁ…魔法少女も…\n眠気、には…い、いや\n寝てないぞ！？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114201, 4500, 1, 0, 2, 0, "任務や訓練で\n疲れた体に\nこれは効くなぁ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114201, 4501, 1, 0, 3, 0, "うむ、かわいいな\nモーラのやつを隣に\n並べようか", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114201, 4502, 1, 0, 4, 0, "ああ、身も心も\n安らぐ…\nまるで魔法だな…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114201, 4503, 1, 0, 5, 0, "この温かさ…\n愛と希望に\n通じるものがある…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114201, 4505, 1, 0, 7, 0, "食事の大切さは\n魔法少女も軍人も\n変わりない\nしっかり食べるんだ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114201, 4506, 1, 0, 8, 0, "魔法少女の本を\n読んでるのだが\nわ、私もこんななのか", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 103301, 4508, 1, 0, 103, 0, "オラとあんちゃんは\nボケもツッコミも\nあうんの呼吸だべ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 103301, 4509, 1, 0, 103, 0, "あんちゃんと二人で\nネタ合わせ…初めての\n共同作業だべ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 192501, 4510, 1, 0, 1, 0, "ぴっぷる！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 192501, 4511, 1, 0, 2, 0, "ぴっぷるぴっぷる♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 192501, 4512, 1, 0, 3, 0, "ぴっ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 192501, 4513, 1, 0, 4, 0, "ぷっぷー", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 192501, 4514, 1, 0, 5, 0, "ぴぃ？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 192501, 4515, 1, 0, 1, 0, "ぴっぷるんるん♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 192501, 4516, 1, 0, 2, 0, "ぷぅ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 192501, 4517, 1, 0, 3, 0, "ぴぴっ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 192501, 4518, 1, 0, 4, 0, "ぷぅー…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 192501, 4519, 1, 1, 1, 0, "ちゅ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 192501, 4520, 1, 2, 2, 0, "ぷはー", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 192501, 4521, 1, 3, 3, 0, "ぴぇー", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114301, 4531, 1, 0, 1, 0, "魔法少女になったら\n髪型も変わるんだね\n色々試してみようかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114301, 4532, 1, 0, 2, 0, "…煌く剣技で、\n悪を断つ！\nうん、今日も\n完璧なポーズだね♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114301, 4533, 1, 0, 3, 0, "ミクマ流剣術で、\n魔法少女の技に使え\nそうなものは…よし、\n早速試してみよっと♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114301, 4534, 1, 0, 4, 0, "魔法少女になったから\nには、サインの練習も\nしないと…\nよし、頑張るぞ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114301, 4535, 1, 0, 5, 0, "魔法少女の姿はすごく\nかわいいけど…この\n姿はジュンさんたちに\n見せられないなぁ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 114301, 4536, 1, 0, 101, 0, "あの人は魔法少女\nのこと好きなのかな？\n…好きだったら\nうれしいな♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 114301, 4537, 1, 0, 102, 0, "…やっぱり、\nあの人がいないと\n特訓に身が入らない…\n早く帰ってこないかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114301, 4538, 1, 0, 1, 0, "もし時間があるなら、\n私と街の見回りに\n行かない？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114301, 4539, 1, 0, 2, 0, "あ、いいところに！\n新しい魔法剣術を\n編み出したから、\n見てくれないかな？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114301, 4540, 1, 0, 3, 0, "魔法少女の姿が気に\nなるのは分かるけど…\nず、ずっと見られる\nのは照れるからやめて", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114301, 4541, 1, 0, 4, 0, "変身ポーズ中の動き、\n少し変えた方が\nいいかな…\nあなたはどう思う？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 114301, 4542, 1, 0, 101, 0, "よければ、\n魔法少女の特訓に\n付き合ってくれない？\nその…二人きりでさ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 114301, 4543, 1, 0, 102, 0, "私…あなただけの\n魔法少女になっても…\nふふっ、な～んてね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114301, 4544, 1, 1, 1, 0, "今日もいい朝だね\nさあ、一緒に\n街の見回りに行こうか", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114301, 4545, 1, 2, 2, 0, "もう昼か…なにか\n食べたいものある？\n簡単なのでいいなら、\n私が作ってあげるよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114301, 4546, 1, 3, 3, 0, "そろそろ休もうか\n…なんなら、添い寝\nしてあげようか？\nふふっ、冗談だよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114301, 4548, 1, 0, 2, 0, "これは…\n心身ともに安らぐね\nなんだか\n眠くなっちゃいそう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114301, 4549, 1, 0, 3, 0, "か、かわいい…これ、\n魔法で動かせたり\nしないかな？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114301, 4550, 1, 0, 4, 0, "いい香りだね\nなんだか頭が冴えて\nきたし、新しい技でも\n考えようかな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114301, 4551, 1, 0, 5, 0, "あったかいな…\nこの温もりは、\n魔法少女の魔法でも\n再現不可能だね…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114301, 4553, 1, 0, 7, 0, "こ、これは…\nおいしすぎて手が\n止まらないかも", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114301, 4554, 1, 0, 8, 0, "この本の\nキャラの動き、\n変身ポーズの\n参考にできるかも！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114401, 4556, 1, 0, 1, 0, "メリークリスマス！\nさあ、素敵な\nパーティーを始めると\nいたしましょう！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114401, 4557, 1, 0, 2, 0, "ふふっ、\nいくつになっても、\nやはりクリスマスは、\nワクワクしますわね♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114401, 4558, 1, 0, 3, 0, "自分で料理をしてみて\nシェフたちのすごさが\n改めて分かりましたわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114401, 4559, 1, 0, 4, 0, "何事も自分で\n体験してみると\n新しい発見が\nあるものですわね♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114401, 4560, 1, 0, 5, 0, "やはり、クリスマスは\n街が活発になり\nますわね…これは、\n絶好の商機ですわ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 114401, 4561, 1, 0, 101, 0, "他のクリスマス料理も\n作れるようになったら\nあのお方は食べて\nくださるかしら…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 114401, 4562, 1, 0, 102, 0, "あのお方に贈る\nプレゼント…\nこれはかなりの\n難題ですわね…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114401, 4563, 1, 0, 1, 0, "メリークリスマス！\nよろしければ、一緒に\nクリスマスの準備を\nいたしませんか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114401, 4564, 1, 0, 2, 0, "まあ、クリスマスの\n飾りつけを手伝って\nいただけるのですか？\nありがとうございます", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114401, 4565, 1, 0, 3, 0, "クリスマスに合わせて\nあつらえた自慢の衣装\nなのですが…その、\nいかがでしょうか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114401, 4566, 1, 0, 4, 0, "幼い頃はサンタさんが\nいらっしゃるのを\n待ちわびたものです\n今は…内緒ですわ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 114401, 4567, 1, 0, 101, 0, "も、もしお時間が\nあるようでしたら、\nクリスマスの街を\n一緒に歩きませんか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 114401, 4568, 1, 0, 102, 0, "来年のクリスマスも\nあのお方と一緒に\n迎えられたら…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114401, 4569, 1, 1, 1, 0, "時は金なり…\nせっかくの朝ですもの\n時間を有効活用\nいたしましょう！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114401, 4570, 1, 2, 2, 0, "お昼になりましたわね\nさあ、\nパーティーの準備を\n進めますわよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114401, 4571, 1, 3, 3, 0, "静かな夜ですわね…\nもうちょっとだけ、\nあなたさまとお話を\nしていたい気分です", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114401, 4573, 1, 0, 2, 0, "適度な休息は\n成功の秘訣ですわ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114401, 4574, 1, 0, 3, 0, "このかわいらしさ…\nいつまでも見て\nいられそうですわ♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114401, 4575, 1, 0, 4, 0, "人をもてなすには\nこうしたところにも\n気を配らないと\nいけませんわね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114401, 4576, 1, 0, 5, 0, "暖かい…\nこういう憩いの\nひと時こそ、大事に\nしたいものですわね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114401, 4578, 1, 0, 7, 0, "この奥深い味わい…\n何か隠し味など\n使われていまして？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114401, 4579, 1, 0, 8, 0, "クリスマス料理の\nレシピも、\nこれだけあると\n迷ってしまいますわね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114401, 4581, 1, 0, 14, 0, "この絵本、とっても\n面白いつくりですわね\nうちの商品に\nしたいぐらいですわ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114401, 4582, 1, 0, 15, 0, "これだけ大きな\n着ぐるみ…\n職人の技術が\n光りますわね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114601, 4583, 1, 0, 1, 0, "クリスマスといえば\nパーティーよね！\nいっぱい飲むわよ～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114601, 4584, 1, 0, 2, 0, "私を麦しゅわだけの\n女と思ったら\n大間違いなんだから♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114601, 4585, 1, 0, 3, 0, "クリスマスらしい\n飲み物も結構いいわね\n私も今度\n作ってみようかしら", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114601, 4586, 1, 0, 4, 0, "暖かい部屋で飲む\n麦しゅわも\nまた格別なのよね…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114601, 4587, 1, 0, 5, 0, "今日はオフの日だし\n朝から飲んでも\n怒られないわよね？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 114601, 4588, 1, 0, 101, 0, "クリスマスの\nデートスポットで\n彼と一緒に…ってなに\n考えてるのユカリ！？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 114601, 4589, 1, 0, 102, 0, "背伸びしなくても\nありのままの自分を\n受け入れてくれるって\nすごく…幸せ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114601, 4590, 1, 0, 1, 0, "大丈夫よ～\n飲んでない\n飲んでないひゃら～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114601, 4591, 1, 0, 2, 0, "クリスマスは\n特別だもの\n特別な一杯を\n飲まないとね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114601, 4592, 1, 0, 3, 0, "メリークリスマース！\nほらほらぁ\n乾杯しましょ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114601, 4593, 1, 0, 4, 0, "お姉さんの\n頼れるところ\n見せてあげるわね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 114601, 4594, 1, 0, 101, 0, "何かあったら\n頼ってくれても\nいいんだからね？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 114601, 4595, 1, 0, 102, 0, "ふふ…これからも\nキミのこと…\n頼りにしちゃうからね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114601, 4596, 1, 1, 1, 0, "さわやかな朝ね\n…二日酔いがないと\n朝ってこんなに\n気持ちいいのね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114601, 4597, 1, 2, 2, 0, "昼間から飲むのって\nまた格別よね…って\nガマン、ガマンよ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114601, 4598, 1, 3, 3, 0, "もうこんな時間かあ…\nあとは寝るだけだし\nちょっとくらいは…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114601, 4600, 1, 0, 2, 0, "ゴロゴロするのって\n最高ね～\nあとは麦しゅわが\nあればなぁ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114601, 4601, 1, 0, 3, 0, "私だって…\nかわいいものは\n好きでいいわよね？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114601, 4602, 1, 0, 4, 0, "んっ～…いい香りね\n日頃の疲れが\n取れる気がするわ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114601, 4603, 1, 0, 5, 0, "ポカポカする～\n…なんだかのどが\nかわいてきたわね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114601, 4605, 1, 0, 7, 0, "もぐもぐ…労働の\n後のご飯はやっぱり\nおいしいわぁ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114601, 4606, 1, 0, 8, 0, "クリスマスデート\n完全攻略マニュアル？\n…読んでおかなくちゃ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114601, 4608, 1, 0, 14, 0, "あら、絵が飛び出して\n見えるけど…\n酔っ払ってるせいじゃ\nないわよね…？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114601, 4609, 1, 0, 15, 0, "いい手触りね～\nウチにもほしいけど\n麦しゅわをこぼしたら\n大変そうだわ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 101601, 4610, 1, 0, 103, 0, "ヒデサイの視線\nずっと独り占め\nしてたいな～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 101601, 4611, 1, 0, 103, 0, "ヒデサイといるだけで\nうち、おに楽しいし！\nヒデサイもっしょ～？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114501, 4612, 1, 0, 1, 0, "メリークリスマス！\nふふ、口にするだけで\n何だか楽しくなるわね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114501, 4613, 1, 0, 2, 0, "いつもは子どもたちを\n見守る方だけど\n今ははしゃいでも\nいいわよね？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114501, 4614, 1, 0, 3, 0, "ツリーや料理\nそしてプレゼント！\nいくつになっても\nやっぱり心躍るわね♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114501, 4615, 1, 0, 4, 0, "飾り付けも料理も\nいいセンスしてるわね\n救護院のパーティーの\n参考にさせてもらうわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114501, 4616, 1, 0, 5, 0, "焼きそばパンを\nクリスマスの定番に\nするにはどうすれば…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 114501, 4617, 1, 0, 101, 0, "これ、あいつの\n好きそうな料理だわ\n取っといて食べさせて\nあげようかしら♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 114501, 4618, 1, 0, 102, 0, "あいつのプレゼント\nちょっとだけ奮発\nしたのよね…\n喜んでくれるかしら", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114501, 4619, 1, 0, 1, 0, "このドレス\n似合ってるかしら？\n今だけは見とれても\n許してあげるわ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114501, 4620, 1, 0, 2, 0, "見て、このケーキ！\nおいしそうでしょ\nたまにはこんな贅沢も\nいいわよね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114501, 4621, 1, 0, 3, 0, "料理も飲み物も\nおいしいわね！\nいっぱい食べて\n元を取らないと♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114501, 4622, 1, 0, 4, 0, "子どもたちと一緒に\n雪遊び、楽しそうね\nあんたもどう？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 114501, 4623, 1, 0, 101, 0, "せっかくの\nクリスマスなんだもの\nエスコート\nよろしくね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 114501, 4624, 1, 0, 102, 0, "寒い？　そう…\nなら、もっとこっち\n来ていいわよ…？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114501, 4625, 1, 1, 1, 0, "ほら、早く起きて？\nパーティー用の\n買い出しに\n付き合ってちょうだい", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114501, 4626, 1, 2, 2, 0, "パーティーの料理の\n準備をしながらお昼も\n作っておいたわ\n我ながらいい手際ね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114501, 4627, 1, 3, 3, 0, "にぎやかなパーティー\nもいいけど、聖夜の\n余韻を感じるのも\n結構素敵ね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114501, 4629, 1, 0, 2, 0, "いい座り心地だわ～\n腕のいい職人の仕事ね\nうちでも扱いたいわ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114501, 4630, 1, 0, 3, 0, "かわいい！\n子どもたちに\nプレゼントしたら\n喜んでくれるかも", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114501, 4631, 1, 0, 4, 0, "華やかな香りね\nクリスマスに\nピッタリだわ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114501, 4632, 1, 0, 5, 0, "寒い日にはやっぱり\nこれよね…\nはぁ、極楽極楽♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114501, 4634, 1, 0, 7, 0, "んー、おいしいわ！\n焼きそばパン味の\nものも、作れない\nかしら？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114501, 4635, 1, 0, 8, 0, "たまにはのんびり\n読書でも…なんだか\nラブロマンスばかりね\nクリスマスだから？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114501, 4637, 1, 0, 14, 0, "これ、面白いわね！\n子どもたちの\nプレゼントに\nピッタリだわ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114501, 4638, 1, 0, 15, 0, "こんないい毛並み\n初めて見るわ…\n一体どこで手に\n入るのかしら？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114701, 4639, 1, 0, 1, 0, "アタシもみんなと\n一緒に正月を\n楽しんでいいのか？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114701, 4640, 1, 0, 2, 0, "今年こそ、もう少し\n身長が伸びます\nように…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114701, 4641, 1, 0, 3, 0, "げっ！　お年玉が\n全然残ってないぞ！？\nそんな、嘘だろ…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114701, 4642, 1, 0, 4, 0, "大勢で過ごす\n正月か…初めてだけど\n悪くないもんだな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114701, 4643, 1, 0, 5, 0, "んー…ここじゃ\n天楼覇断剣の\n出番はなさそうだな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 114701, 4644, 1, 0, 101, 0, "アイツらと作る\n戦い以外の思い出も\nもっと増やしたいな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 114701, 4645, 1, 0, 102, 0, "こんなに賑やかな\n正月は初めてだ\n…温かくて、いいな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114701, 4646, 1, 0, 1, 0, "記憶がなくったって\n楽しい思い出は\n新しく作れるよな？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114701, 4647, 1, 0, 2, 0, "早くでかけるぞ！\nアタシはもっと\n正月を楽しみたい！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114701, 4648, 1, 0, 3, 0, "なあ、オマエはもう\nノウェムって名前\n覚えてくれたよな？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114701, 4649, 1, 0, 4, 0, "この服、アタシに\n似合ってるのか？\n…そっか。\nありがとな！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 114701, 4650, 1, 0, 101, 0, "オマエらと見た朝日は\nきっと忘れないぞ！\n思い出ができたな！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 114701, 4651, 1, 0, 102, 0, "いつかオクトーも\n一緒に、みんなで\n正月を過ごせたらな…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114701, 4652, 1, 1, 1, 0, "朝から元気だな！\nアタシもいっちょ\n見習ってみるか！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114701, 4653, 1, 2, 2, 0, "昼飯は何にする？\n正月はうまいものが\n多くて迷うよな！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114701, 4654, 1, 3, 3, 0, "いい夜だな！\nアタシと一緒に\n夜更かしを楽しむか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114701, 4656, 1, 0, 2, 0, "正月らしく、たまには\nゆっくりするのも\nいいかもな…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114701, 4657, 1, 0, 3, 0, "なんだ？\nオマエも初詣に\n行きたいのか？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114701, 4658, 1, 0, 4, 0, "すーはー…\n神殿のニオイがするな\nいい事ありそうだ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114701, 4659, 1, 0, 5, 0, "コイツがあれば\nうまい魚が\n焼けそうだな…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114701, 4661, 1, 0, 7, 0, "ううー\n食べ過ぎて帯が\nきつくなってきたぞ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 114701, 4662, 1, 0, 8, 0, "おみくじって\n書いてあることが\nむずかしいんだよな…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180101, 4664, 1, 0, 1, 0, "困ってる人は\nいないみたいだし…\nちょっとここで、\n特訓しようかな♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180101, 4665, 1, 0, 2, 0, "空が飛べるんだから\nもっとたくさんの人を\n助けにいけちゃう！\n…ここじゃ飛べないや", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180101, 4666, 1, 0, 3, 0, "炎の翼\nかっこいいなー！\nこれ、もっと大きく\nできないのかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180101, 4667, 1, 0, 4, 0, "やっぱり変身すると\nお腹すくんだね…\n…今ならペコさんの\n食欲の理由が分かるよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180101, 4668, 1, 0, 5, 0, "色なんかは違うけど…\nマントがあるから\n騎士くんとお揃いに\nなったみたい♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 180101, 4669, 1, 0, 101, 0, "…騎士くんがいないと\nなんか調子でない…\n早く会いたいよぉ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 180101, 4670, 1, 0, 102, 0, "この姿になると、\n騎士くんを近くに\n感じる…なんだろ\nドキドキしちゃう…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180101, 4671, 1, 0, 1, 0, "騎士くん騎士くん！\n今大丈夫？\nトレーニングメニュー\n考えるの手伝って！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180101, 4672, 1, 0, 2, 0, "…うにゃっ！？\nこ、この姿になっても\n尻尾は触っちゃ\nダメー！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180101, 4673, 1, 0, 3, 0, "もしかして、この服が\n気になってるの？\nだったら、もっと\n近くで見ていいよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180101, 4674, 1, 0, 4, 0, "あ、騎士くん！\n困ってる人がいないか\n見回りに行くんだけど\nついてきて欲しいな！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 180101, 4675, 1, 0, 101, 0, "耳に触りたいの？\nだったら頭もなでて！\nそしたら、好きなだけ\n触っていいよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 180101, 4676, 1, 0, 102, 0, "…あたし、強くなって\n騎士くんを守るよ！\nだから…これからも\n一緒にいていい？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180101, 4677, 1, 1, 1, 0, "おっはよー！\nさあさあ騎士くん！\n今日も一緒に\nはりきっていこー♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180101, 4678, 1, 2, 2, 0, "お昼、お昼～♪\n今日のご飯は何かな～\nあ、騎士くん！\n早くお昼食べ行こ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180101, 4679, 1, 3, 3, 0, "今日もお疲れ様ー！\n騎士くん、あたしが\nマッサージしよっか？\n結構評判なんだよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180101, 4681, 1, 0, 2, 0, "うにゃぁ…\nこの気持ちよさ…\n溶けちゃいそう…\nにゃぁ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180101, 4682, 1, 0, 3, 0, "か、かわいい～\nこれ、なでなで\nしていいんだよね…？\nよしよし…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180101, 4683, 1, 0, 4, 0, "とってもいい香り！\n気分も落ち着くし、\n特訓の後なんかに\nいいかもー！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180101, 4684, 1, 0, 5, 0, "ぬくぬくだぁ…\nなんだか、\nふにゃふにゃに\nなっちゃうよぉ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180101, 4686, 1, 0, 7, 0, "お、おいひぃ…！\nこの姿になると\nお腹すくし、すっごく\n助かっちゃった！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180101, 4687, 1, 0, 8, 0, "この本で勉強して、\nぶんぶりょーどーの\n超ウルトラヒヨリに\nなっちゃうぞー！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111801, 4689, 1, 0, 1, 0, "今年もみんなで\nおいしいものを\nたくさん食べたいです", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111801, 4690, 1, 0, 2, 0, "挑戦は大切ですよね☆\nというわけで\nおせちに魔物を…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111801, 4691, 1, 0, 3, 0, "おせちは縁起物です！\n食べれば食べるほど\n幸せになるはずです！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111801, 4692, 1, 0, 4, 0, "食べ物がたくさん\n入った福袋は\nないんでしょうか…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111801, 4693, 1, 0, 5, 0, "初詣で何を\nお願いしましょう…\n五穀豊穣？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 111801, 4694, 1, 0, 101, 0, "ずっとこうして\n年の始まりを\n過ごしたいですね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 111801, 4695, 1, 0, 102, 0, "いつか二人きりで\n初日の出を\n見たいです", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111801, 4696, 1, 0, 1, 0, "お餅おかわりです！\nいくらでも\n食べられますよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111801, 4697, 1, 0, 2, 0, "おせちの食材を\n取りに行きませんか？\nいい運動です☆", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111801, 4698, 1, 0, 3, 0, "凧揚げって\n難しいですね～\n糸が絡まっちゃいます", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111801, 4699, 1, 0, 4, 0, "一年の計は元旦にあり\n今年一年分の\n献立を作りましょう！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 111801, 4700, 1, 0, 101, 0, "ほらほら！\nもっと近くで着物を\n見ていいんですよ☆", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 111801, 4701, 1, 0, 102, 0, "あなたはどんな\n初夢を見ましたか？\nわたしは…内緒です☆", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111801, 4702, 1, 1, 1, 0, "年明けの朝は\n心なしか\n気持ちいいですね☆", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111801, 4703, 1, 2, 2, 0, "お雑煮のあとは\nおしるこで\nおやつタイムです！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111801, 4704, 1, 3, 3, 0, "あたたかくして\n幸せな初夢を\n見ましょうね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111801, 4706, 1, 0, 2, 0, "はぁ～屋台巡りを\n満喫しました☆\n一休み一休み", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111801, 4707, 1, 0, 3, 0, "縁起のいい\nぬいぐるみを並べて\n福を呼びましょう！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111801, 4708, 1, 0, 4, 0, "おしるこのアロマって\nないんでしょうか？\nそれなら作るしか…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111801, 4709, 1, 0, 5, 0, "ここでお餅を\n焼いたらおいしく\nなりそうですね～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111801, 4711, 1, 0, 7, 0, "おいしすぎて\n食べる手が\n止まりません～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 111801, 4712, 1, 0, 8, 0, "新しいおせち料理の\nヒントは…なるほど…\nふむふむ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 100601, 4714, 1, 0, 103, 0, "お兄ちゃんとあんな\n特訓やこんな特訓が\nしたいなぁ、えへへ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 100601, 4715, 1, 0, 103, 0, "お兄ちゃんに褒めて\nもらうと、身体が\nじぃーんと\n熱くなっちゃいます♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115001, 4716, 1, 0, 1, 0, "見目麗しい衣装に\n身を包むのも\n悪い気分では\nありませんね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115001, 4717, 1, 0, 2, 0, "縁起物で\n験を担ぐというのも\nたまにはよいでしょう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115001, 4718, 1, 0, 3, 0, "非効率なので\nあまり行いませんが\n私も手料理くらいは\n作れるのですよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115001, 4719, 1, 0, 4, 0, "研究のためなら\n時に遠路を超え\n実地へ赴くことも\n必要なのです", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115001, 4720, 1, 0, 5, 0, "トゥンヌスの\n突然変異…\n大いに興味を\nそそられますね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 115001, 4721, 1, 0, 101, 0, "漁師の技法も\n再現できたことで\nまた一つ、仮説が\n実証できました", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 115001, 4722, 1, 0, 102, 0, "正月くらいは\n私ものんびりしたい\n気持ちなのですよ\n意外ですか？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115001, 4723, 1, 0, 1, 0, "模倣に頼らずとも\n衣装に合わせた\n所作程度は\nできて当然です", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115001, 4724, 1, 0, 2, 0, "お年玉の使い道は\n思いつきましたか\nゆっくり考えても\nいいのですよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115001, 4725, 1, 0, 3, 0, "トランプでも花札でも\n相手をしましょう\n手心が必要なら\nいつでも言いなさい", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115001, 4726, 1, 0, 4, 0, "初詣でお祈りする\n内容ですか\n…ヒミツ、として\nおきましょう", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 115001, 4727, 1, 0, 101, 0, "私たちでオーマ中の\nトゥンヌスを\n釣り上げましょうか\n…冗談ですよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 115001, 4728, 1, 0, 102, 0, "見返りを求めない願い\nそれは祈りに通じるの\nかもしれませんね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115001, 4729, 1, 1, 1, 0, "…貴方がその眠たげな\n顔を洗ってくる間に、\n温かいお茶でも\n淹れておきましょう", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115001, 4730, 1, 2, 2, 0, "さて、本格的に\n活動を再開します\n貴方も同行しなさい\n昼食は済みましたか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115001, 4731, 1, 3, 3, 0, "私はもう少し\n作業を片付けてから\n寝ることにします\n早く眠りなさい", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115001, 4733, 1, 0, 2, 0, "少し休みます…\n時間が来たら\n起こすように", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115001, 4734, 1, 0, 3, 0, "贈り物を模倣して\n潜入…試してみる\n価値はありそうですね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115001, 4735, 1, 0, 4, 0, "嗅覚から得られる\n情報量は侮れません\n動物の模倣において\n注意すべき点ですよ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115001, 4736, 1, 0, 5, 0, "穏やかな炎による\nリラックス効果は\n本能によるものかも\nしれませんね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115001, 4738, 1, 0, 7, 0, "いかなる強者でも\n兵站、補給は\n必要なものです", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115001, 4739, 1, 0, 8, 0, "あらゆる学問は\n完璧な模倣のための\n大きな礎となります", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115501, 4741, 1, 0, 1, 0, "新たに得たこの力で\n仲間と目指す未来を\n切り拓いてみせます", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115501, 4742, 1, 0, 2, 0, "ここは、みなさまと…\nわたくしの\n帰る場所でございます", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115501, 4743, 1, 0, 3, 0, "主さまも精霊の召喚を\n試してみますか？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115501, 4744, 1, 0, 4, 0, "主さま、おこづかいは\n足りていますか？\n遠慮は無用ですよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115501, 4745, 1, 0, 5, 0, "この装束をまとうと\n自然と背筋が伸びる\n思いがいたします", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 115501, 4746, 1, 0, 101, 0, "進化した精霊に\n負けぬよう\n修行に励みます", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 115501, 4747, 1, 0, 102, 0, "仲間たちといられる\nこの幸せが、末永く\n続きますように…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115501, 4748, 1, 0, 1, 0, "主さまのお手手は\n大きくて温かいですね\nぎゅっぎゅっ…ふふ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115501, 4749, 1, 0, 2, 0, "今度、主さまにも\n里に伝わる昔話を\nお話しいたしましょう", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115501, 4750, 1, 0, 3, 0, "里で採れた果物を\n持って参りました\n召し上がってください", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115501, 4751, 1, 0, 4, 0, "里のお土産を\nここのみなさまに\nお配りいたしましょう", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 115501, 4752, 1, 0, 101, 0, "仲間と囲む食卓…この\n温もりは、みなさまの\n心の温かさなのですね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 115501, 4753, 1, 0, 102, 0, "主さまに名前を\n呼んでいただくと\n心が温かくなります♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115501, 4754, 1, 1, 1, 0, "ふふっ…主さま\n寝癖がついて\nおりますよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115501, 4755, 1, 2, 2, 0, "今日の昼食は\n里の伝統料理です\nお口に合うでしょうか", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115501, 4756, 1, 3, 3, 0, "主さまが眠れるよう\n安らぎの風を…\nおやすみなさいませ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115501, 4758, 1, 0, 2, 0, "この癒し…\n主さまにも味わって\nいただきたい…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115501, 4759, 1, 0, 3, 0, "この愛らしさ…\n精霊たちと似たものを\n感じます…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115501, 4760, 1, 0, 4, 0, "精霊よ…この安らぐ\n香りを、隅々まで\n届けてくださいまし", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115501, 4761, 1, 0, 5, 0, "瞑想するのに\nちょうどよい暖かさで\nございます", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115501, 4763, 1, 0, 7, 0, "進化した精霊を\n使役するためにも\nよく食べて体力を…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115501, 4764, 1, 0, 8, 0, "風の精霊について\nもっと詳しく知って\nおかなければ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115601, 4766, 1, 0, 1, 0, "儀式の時を思い出して\n精霊に呼びかける…\n出ておいで～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115601, 4767, 1, 0, 2, 0, "ひゃうっ！　もう…\n急に背中を触るなんて\nいたずら好きな精霊に\n育てた覚えはないのに", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115601, 4768, 1, 0, 3, 0, "儀式用の衣装…\nあんまり激しい動きは\nできないよね…でも\n戦う時は気にしない！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115601, 4769, 1, 0, 4, 0, "そうだ…精霊に\n騎士クンのそばで\n見守っていてって\n頼んでみようかな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115601, 4770, 1, 0, 5, 0, "ふふっ、こうやって、\n楽しく精霊とお話し\nしていられる、平穏な\n日々が続くといいな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 115601, 4771, 1, 0, 101, 0, "き、きき、騎士クン！\nわたしと、あの…！\nうう…練習ですら\n上手く誘えないよぉ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 115601, 4772, 1, 0, 102, 0, "あっ、精霊が…\nふふ、応援ありがとう\nわたしの想い…いつか\n必ず伝えるね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115601, 4773, 1, 0, 1, 0, "新しく覚えた料理\n作ってみたんだけど\n騎士クンも食べない？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115601, 4774, 1, 0, 2, 0, "騎士クン、\n疲れてない？\nわたしと精霊で\n治療するよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115601, 4775, 1, 0, 3, 0, "もう、いたずらしない\n…騎士クン！？\nご、ごめんなさい！\n精霊だと思って！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115601, 4776, 1, 0, 4, 0, "騎士クン！\n時間が合ったら、\n精霊と一緒に\nお話しない？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 115601, 4777, 1, 0, 101, 0, "…こういう服着るの\n初めてなんだけど…\nえっ、かわいい…？\nあ、ありがとう…！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 115601, 4778, 1, 0, 102, 0, "困ったことがあったら\n相談してね。わたし、\n騎士クンのためなら…\n色々できるよ、色々！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115601, 4779, 1, 1, 1, 0, "おはよう騎士クン！\n今からお外で\n瞑想するんだけど…\n騎士クンもどうかな？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115601, 4780, 1, 2, 2, 0, "お昼の時間だ\nご飯ならわたしが\n用意するから\nゆっくりしてて", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115601, 4781, 1, 3, 3, 0, "もうこんな時間…\n騎士クン、はいどうぞ\n寝る前の温かいミルク\nぐっすり眠れるよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115601, 4783, 1, 0, 2, 0, "…うん、落ち着く\n精霊との魔法で\nこんな風に誰かを\n安心させたいな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115601, 4784, 1, 0, 3, 0, "かわいい…\n精霊も気に入ってる…\nそうだ、同じ物\n作ってあげようかな？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115601, 4785, 1, 0, 4, 0, "いい香り…これ、\nうちに置いたら、\nお母さん喜ぶかも！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115601, 4786, 1, 0, 5, 0, "あったかい…\nまるで誰かが\n抱きしめてくれた\nみたい…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115601, 4788, 1, 0, 7, 0, "自然の恵みに\n感謝して…\nいただきます♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115601, 4789, 1, 0, 8, 0, "もっと精霊について\n勉強して、みんなを\n守れるように\nなりたいな…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 102201, 4791, 1, 0, 103, 0, "アイツといると胸が\nぎゅーっとなる…\nこれってもしかして…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 102201, 4792, 1, 0, 103, 0, "アンタとならきっと\nどんなゲームでも…\nそ、その、たた、\n楽しいって思えるわ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106401, 4793, 1, 0, 1, 0, "色んな人と話したら\n記憶探しの手がかりが\n見つかるかも…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106401, 4794, 1, 0, 2, 0, "アイスクリームでも\n作ろうかしら？\nバニラにミントに…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106401, 4795, 1, 0, 3, 0, "ネビアに話しかけると\n逃げられるのよね…\nもう、襲わないのに…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106401, 4796, 1, 0, 4, 0, "「コッコロさん」、\n「コッコロさん」…\n…間違えないように…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106401, 4797, 1, 0, 5, 0, "最近少し太ったかも？\nきっとおいしいもの、\n食べ過ぎね…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 106401, 4798, 1, 0, 101, 0, "ここにいると、\nとても落ち着くわ…\n自分の家、だものね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 106401, 4799, 1, 0, 102, 0, "みんなと一緒の時間、\nずっと記憶に残したい\n…そう思っているのよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106401, 4800, 1, 0, 1, 0, "ドラゴン族って、\nそんなに珍しいの？\nあまり実感ないけど…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106401, 4801, 1, 0, 2, 0, "料理、練習してるのよ\n…味見してもらえる？\n期待はしないでね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106401, 4802, 1, 0, 3, 0, "頭を叩いたら、\n記憶が戻ったり\nしないかしら…？\nやらなくていいわよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106401, 4803, 1, 0, 4, 0, "赤ちゃんみたいだった\n時のことは、忘れて…\nお願いだから…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 106401, 4804, 1, 0, 101, 0, "ひゃんっ！？\nし、しっぽを掴むのは\nダメよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 106401, 4805, 1, 0, 102, 0, "記憶が戻っても…\n私は、あなたたちと\n一緒にいたいわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106401, 4806, 1, 1, 1, 0, "おはよう。\n寝ぐせついてるわよ？\n…ふふっ、これでよし", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106401, 4807, 1, 2, 2, 0, "そろそろお昼かしら？\nおいしいごはんを、\nみんなで一緒に…ね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106401, 4808, 1, 3, 3, 0, "もうこんな時間ね。\nそろそろ休みましょ？\n…ふわぁ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106401, 4810, 1, 0, 2, 0, "ふかふか、ぎゅう～…\n…あっ、これはその…\nストレッチしててっ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106401, 4811, 1, 0, 3, 0, "あまり、興味ないわ。\nけど…けど…\n…か、かわいい…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106401, 4812, 1, 0, 4, 0, "いい香りね。\nとっても落ち着くわ…\n…っくしゅっ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106401, 4813, 1, 0, 5, 0, "冷やす方が得意だけど\nあったかいのも、\nキライじゃないのよ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106401, 4815, 1, 0, 7, 0, "別に、食いしん坊\nじゃないのよ？\nでも、もう一口だけ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106401, 4816, 1, 0, 8, 0, "ん…なるほどね。\n勉強になるわ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115701, 4818, 1, 0, 1, 0, "水着の探偵か…\nちょっと恥ずかしい\n気がしてきたよ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115701, 4819, 1, 0, 2, 0, "依頼があれば\n海でも山でも\n赴くさ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115701, 4820, 1, 0, 3, 0, "今のうちに\n助手くんの秘密を…\nわ…私はなにをっ！？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115701, 4821, 1, 0, 4, 0, "休暇も時には必要さ\n海でゆっくりと…\nむっ？　事件の気配！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115701, 4822, 1, 0, 5, 0, "探偵の秘密道具…\n水着だと隠す\n場所がない…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 115701, 4823, 1, 0, 101, 0, "そろそろ助手くんが\n現れる時間かな？\nふふっ、楽しみだね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 115701, 4824, 1, 0, 102, 0, "真実は私と助手くん\n二人だからこそ\n解き明かせるのさ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115701, 4825, 1, 0, 1, 0, "ひゃああっ！？\nわ、私の不意を突く\nとは…やるね助手くん", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115701, 4826, 1, 0, 2, 0, "さて、なにか気づいた\nことはあるかな？\n言ってごらん♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115701, 4827, 1, 0, 3, 0, "この感触…分かった！\n助手くんだね！\nふふっ、やっぱり♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115701, 4828, 1, 0, 4, 0, "水着…どうかな？\n探偵らしさは\n残してるつもりだけど", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 115701, 4829, 1, 0, 101, 0, "ん？　依頼かな？\n助手くんの頼みなら\nなんでもお安い御用さ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 115701, 4830, 1, 0, 102, 0, "助手くん、私と海に\n行かないかな\n…事件じゃないよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115701, 4831, 1, 1, 1, 0, "珈琲でも淹れて\n目を覚ますとしよう\n一緒に飲むかい？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115701, 4832, 1, 2, 2, 0, "昼食は済ませた？\nなにを食べたか\n当ててあげよう！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115701, 4833, 1, 3, 3, 0, "夜は謎が深まる時間…\n闇に覆い隠された真実\n必ず暴いてみせる！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115701, 4835, 1, 0, 2, 0, "さて、推理の時間だ\n…サボってないよ？\n考えてるんだからね？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115701, 4836, 1, 0, 3, 0, "これはもしや、中に\n秘密のメッセージが？\n…かわいいだけだね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115701, 4837, 1, 0, 4, 0, "ふむ、なんだか\n頭がスッキリするね\n…閃いた！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115701, 4838, 1, 0, 5, 0, "ポカポカするね…\n助手くん\n君もおいでよ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115701, 4840, 1, 0, 7, 0, "はむ、はむ…んぐっ\nた、食べてるところを\n見つめられると～…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115701, 4841, 1, 0, 8, 0, "秘密の依頼とか\nこういう場所に\n隠したりするよね…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115701, 4843, 1, 0, 10, 0, "これはいい乗り心地だ\n事件現場にこれで登場\n…は、やめておこう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115701, 4844, 1, 0, 11, 0, "不思議な感触だね…\nでも、いい発想が\n生まれてきそうだ…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180601, 4845, 1, 0, 1, 0, "これはあたしが\n前へ進むために\nあいつがくれた\n大切な力…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180601, 4846, 1, 0, 2, 0, "チェンジ・\nプリンセスフォーム！\n練習練習、っと", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180601, 4847, 1, 0, 3, 0, "もうあいつに助けて\nもらいっぱなしの\nあたしは…\n卒業しなきゃね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180601, 4848, 1, 0, 4, 0, "気ままに、お気楽に\nあいつと冒険する…\nそれって幸せよね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180601, 4849, 1, 0, 5, 0, "プリンセスフォーム…\nお姫様みたいね\nあたしがお姫様か…\nそっかぁ…ふふ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 180601, 4850, 1, 0, 101, 0, "あいつと繋がって\n生まれたこの絆は\nあたしの宝物だわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 180601, 4851, 1, 0, 102, 0, "あたしの命の使い方…\n全て受け止めてくれた\nあいつにだったら…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180601, 4852, 1, 0, 1, 0, "ふふ～ん♪\nプリンセスフォームの\n天才魔法使い\nキャル様に用かしら？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180601, 4853, 1, 0, 2, 0, "これからは\nあたしの力で\nなんでもパパっと\n解決してあげるわね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180601, 4854, 1, 0, 3, 0, "魔力がみなぎってる…\nねえ、もしかして\n今のあたしって本気で\nすごいんじゃない？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180601, 4855, 1, 0, 4, 0, "変身の反動で\nお腹が空いても\n魔物料理だけは\nいやだからね！？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 180601, 4856, 1, 0, 101, 0, "ねぇ、あんたになら\nあたしの心…\n全部預けていいかも", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 180601, 4857, 1, 0, 102, 0, "あいつと一緒に\nいられたら、他には\n何も…って、あああ\nあんたいつの間に！？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180601, 4858, 1, 1, 1, 0, "早起きも悪くないわね\n読みかけの魔導書\n今のうちに\n読んじゃおうかしら", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180601, 4859, 1, 2, 2, 0, "ねぇ～お腹すいたー\nあんたなんか\n作りなさいよ\n魔物料理以外でね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180601, 4860, 1, 3, 3, 0, "ふぁぁ…眠すぎて\n今なら変身しても\nお腹が減らない\n自信があるわ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180601, 4862, 1, 0, 2, 0, "あー♪　最っ高！\n休んで魔力を高めて…\nあいつの役に\n立ってあげなきゃね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180601, 4863, 1, 0, 3, 0, "なにこれかわいい…！\nコレクションにしたく\nなっちゃうじゃない", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180601, 4864, 1, 0, 4, 0, "この香り…すごく\nリラックスできるわ…\nあ、いい感じの魔法\n思いついちゃったかも", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180601, 4865, 1, 0, 5, 0, "あったかい…\n落ち着くわ…\nあいつの手の\nぬくもりみたいね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180601, 4867, 1, 0, 7, 0, "プリンセスフォーム…\nこ、こんなに\nお腹が減るのね\n甘く見てたわ…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180601, 4868, 1, 0, 8, 0, "ま、魔物料理の本！？\n誰よこんな本\n買ったの！　って…\n一人しかいないか", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115801, 4870, 1, 0, 1, 0, "憧れの舞踏会…\n私、きちんと\n踊れるかしら", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115801, 4871, 1, 0, 2, 0, "アン・ドゥ・トロワ\nアン・ドゥ・トロワ\n練習はばっちりね♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115801, 4872, 1, 0, 3, 0, "こんなにすてきな\nドレスを着られて\n本当に夢みたいだわ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115801, 4873, 1, 0, 4, 0, "魔法はいつか解ける…\nでも、その時間は心に\n残り続けるわ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115801, 4874, 1, 0, 5, 0, "ガラスの靴は夢へ\n踏み出す魔法の道具…\n私、がんばっちゃう！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 115801, 4875, 1, 0, 101, 0, "舞踏会であなたと\n一緒に踊れたら\nいいのにな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 115801, 4876, 1, 0, 102, 0, "舞踏会に行く前に\n運命の王子様には\n出会ってるのよね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115801, 4877, 1, 0, 1, 0, "どうかしら？\n私、きちんとドレス\n着られてる？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115801, 4878, 1, 0, 2, 0, "あなたも一緒に\n踊ってみる？\nしゃなりしゃなり♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115801, 4879, 1, 0, 3, 0, "あなたにエスコート\nされてきれいなお城を\n歩いてみたいわ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115801, 4880, 1, 0, 4, 0, "どう？　レッスンの\n成果はきちんと\n出てるかしら？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 115801, 4881, 1, 0, 101, 0, "あなたが隣にいれば\n私も立派なお姫様に\n見えるかしら♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 115801, 4882, 1, 0, 102, 0, "私にとって\n一番の魔法はあなたが\nくれる勇気よ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115801, 4883, 1, 1, 1, 0, "さぁ！　今日も一日\n立派な淑女を目指して\nいきましょう♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115801, 4884, 1, 2, 2, 0, "お昼ごはん代わりに\nメタモルアップルを\n食べておきましょう", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115801, 4885, 1, 3, 3, 0, "そろそろ魔法が解ける\n時間…でも淑女の\n心得は忘れないわ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115801, 4887, 1, 0, 2, 0, "淑女にも休息は\n必要よね…少しだけ\n休ませてもらおっと", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115801, 4888, 1, 0, 3, 0, "かわいいものに\n囲まれた部屋も\n女の子の夢よね…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115801, 4889, 1, 0, 4, 0, "すてきな香り…\nお花かしら？\n森で探してみようかな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115801, 4890, 1, 0, 5, 0, "暖かい…暖炉のそばで\nおとぎ話を聞くのも\nいいわよね♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115801, 4892, 1, 0, 7, 0, "レッスンのために\n栄養補給して\nおかないと！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115801, 4893, 1, 0, 8, 0, "教養を身に着ける\nことも大切よね！\nなになに…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115901, 4895, 1, 0, 1, 0, "や、やっぱスカートは\n落ち着かねえな…\nなんか、\nスースーするしよ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115901, 4896, 1, 0, 2, 0, "せっかくこんな格好\nしてるし、いつもとは\n違う剣術の訓練を\nしてみるかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115901, 4897, 1, 0, 3, 0, "さて、他の連中が\n帰ってくる前に、\nなんか料理でも\n作っといてやるか", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115901, 4898, 1, 0, 4, 0, "こ、こんな服着てる\nわけだし、紅茶とか\n楽しんじゃっても\n変じゃないよな！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115901, 4899, 1, 0, 5, 0, "暇だし運動でも…\nあ、あぶねえ！\nスカートなの忘れてた\n…誰も見てねえよな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 115901, 4900, 1, 0, 101, 0, "紅茶が切れてるな…\nよし、あいつと一緒に\n買い物にでも行くか♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 115901, 4901, 1, 0, 102, 0, "…あいつがいねえと、\nつまんねえな…ったく\n飯用意してやるから、\n早く帰ってこいよな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115901, 4902, 1, 0, 1, 0, "あ、あんまり\nジロジロ見んなよ\n…結構、\n恥ずかしいんだからな", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115901, 4903, 1, 0, 2, 0, "ダンスの相手は\n無理だが、\n剣の訓練なら\nいつでも付き合うぜ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115901, 4904, 1, 0, 3, 0, "この服に\n触りたいのか？\nすごい作りだし、\n気持ちは分かるぜ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115901, 4905, 1, 0, 4, 0, "お、いいところに！\nクッキーを焼く\nところだったんだが、\nおまえも食うか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 115901, 4906, 1, 0, 101, 0, "おまえにもこういう服\n似合うんじゃねえか？\n今度あたしが\n選んでやるよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 115901, 4907, 1, 0, 102, 0, "…おまえの前でなら、\nこういう格好を\nすんのも…\nな、なんでもねえよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115901, 4908, 1, 1, 1, 0, "朝の訓練を始めるか\n時間あるなら、\nおまえも付き合えよな", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115901, 4909, 1, 2, 2, 0, "もうこんな時間か…\n昼飯作るけど、\nなんか\n食いたいものあるか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115901, 4910, 1, 3, 3, 0, "眠れないなら、\nホットミルク飲むか？\n体あったまって、\n眠くなると思うぜ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115901, 4912, 1, 0, 2, 0, "ちょっと休憩して…\nおっと、服にしわが\nできないように\nしねえとな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115901, 4913, 1, 0, 3, 0, "だ、誰も\n見てねえよな？\n…よ～しよしよし、\nかわいいなおまえー♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115901, 4914, 1, 0, 4, 0, "すげぇいい香りだな\nうちの居酒屋にも…\nいや、合わねえか", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115901, 4915, 1, 0, 5, 0, "あったけぇ…\nちょっとだけなら、\n寝ちまっても\nいいよな…？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115901, 4917, 1, 0, 7, 0, "これうめえな！\nたぶん材料は…よし！\nあたしも作ってみるか", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 115901, 4918, 1, 0, 8, 0, "この本は…\n誰でも分かる作法…か\n最低限このぐらいは\n勉強しとくか…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 102001, 4920, 1, 0, 103, 0, "ミミがおとなに\nなっても\nおにいちゃんとは\nずーっといっしょ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 102001, 4921, 1, 0, 103, 0, "ミミとくべつなだっこ\nしてほしいな…うん！\nおひめさまだっこ～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 100401, 4922, 1, 0, 103, 0, "次はにいちゃんに\nどんなイタズラしよう\n早く会いたいな～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 100401, 4923, 1, 0, 103, 0, "ミソギの特別な\nイタズラ見てくれる？\nいいよねっ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 103601, 4924, 1, 0, 103, 0, "大人になったら、\nあの人と何しようかな\n…楽しみだなぁ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 103601, 4925, 1, 0, 103, 0, "頭を撫でないで\nください！　もう、\n少しだけ、ですからね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116001, 4926, 1, 0, 1, 0, "くるくる〜♪\nうちのドレス\nかわええなぁ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116001, 4927, 1, 0, 2, 0, "日差しは苦手やけど\nたまにはひなたの下で\nぽかぽかしたいわぁ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116001, 4928, 1, 0, 3, 0, "外はお花が咲いてる\n久しぶりにお花を\n活けたいなぁ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116001, 4929, 1, 0, 4, 0, "お花をたくさん集めて\nギルドハウスに飾れば\n喜んでくれはるかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116001, 4930, 1, 0, 5, 0, "あたたかい日は\n紅茶を飲みながら\nゆっくりしたいなぁ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 116001, 4931, 1, 0, 101, 0, "お稽古事を始めるなら\nうちに言ってな\nどんなことも教えたる", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 116001, 4932, 1, 0, 102, 0, "うちにいろんな夢を\n見せてくれはって\nほんま、おおきにな♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116001, 4933, 1, 0, 1, 0, "王子はん\n一緒におとぎの国へ\nお散歩にいきまひょ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116001, 4934, 1, 0, 2, 0, "ふんふんふ〜ん♪\n今な、王子はんに\n花冠を作ってるんよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116001, 4935, 1, 0, 3, 0, "王子はんは\nお菓子好きどす？\nうち、作りますわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116001, 4936, 1, 0, 4, 0, "うーん。ドレスの柄は\nお花とお星さま\nどっちにしまひょか", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 116001, 4937, 1, 0, 101, 0, "王子はんとの\n思い出が増えるたび\nうちの宝物も増えます", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 116001, 4938, 1, 0, 102, 0, "王子はんと一緒なら\nどんな場所でも\n夢の国になりますわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116001, 4939, 1, 1, 1, 0, "夢の中におりたいけど\n王子はんに会いたくて\n起きてもうたわ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116001, 4940, 1, 2, 2, 0, "王子はん一緒に\nアフタヌーンティー\nいかがやろか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116001, 4941, 1, 3, 3, 0, "夜の舞踏会…\n夢が広がるわぁ\nもちろんお相手は…♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116001, 4943, 1, 0, 2, 0, "ふわふわや〜\nなんや心地よぉて\n眠ってしまいそうや…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116001, 4944, 1, 0, 3, 0, "かいらしいなぁ\n新しいお洋服でも\n作ってあげまひょか♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116001, 4945, 1, 0, 4, 0, "お花やろうか…\nリラックスできて\nええ香りやねぇ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116001, 4946, 1, 0, 5, 0, "こういう場所で\n絵本を読むと\n気分が出てええなぁ♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116001, 4948, 1, 0, 7, 0, "舞踏会を楽しむなら\nたくさん食べて\nたくさん踊りまひょ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116001, 4949, 1, 0, 8, 0, "うち、この本好きや\n王子さまと結ばれる\nお姫さまのお話なんよ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116301, 4951, 1, 0, 1, 0, "はぁ、宿題めんどい…\nこれは楽しくない…\nこんな青春いらない…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116301, 4952, 1, 0, 2, 0, "奨学金の道は遠いなぁ\nメルクリちゃんたちが\nお金くれればいいのに", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116301, 4953, 1, 0, 3, 0, "アオイちゃん元気かな\nチエルに会いたくて\n泣いてるんだろうな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116301, 4954, 1, 0, 4, 0, "暇だし鏡でも見るか…\nうわ、顔良っ！\nえ誰この超絶美少女！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116301, 4955, 1, 0, 5, 0, "ワンツーワンツー♪\nうーん…カルミナの\n振りコピ難しいにゃー", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 116301, 4956, 1, 0, 101, 0, "先輩も運動してます？\nチエルが一緒に走って\nあげてもいいですよ？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 116301, 4957, 1, 0, 102, 0, "来年のテレフェスも\n今から楽しみだなあ…\nまた一緒にお化け屋敷\n入ってくださいね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116301, 4958, 1, 0, 1, 0, "ちぇる～ん☆\nフェス仕様でも\nかわいいちえるん！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116301, 4959, 1, 0, 2, 0, "いま話しかけないで！\n来年の聖学祭ライブの\nイメトレ中なんです！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116301, 4960, 1, 0, 3, 0, "クロエ先輩のウェア姿\nめっちゃカッコよ！\n思わずおへそ触ったら\nぶたれましたけど", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116301, 4961, 1, 0, 4, 0, "ユニ先輩の体操着姿…\nあれはあれで実に良き\n一生見てられますよね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 116301, 4962, 1, 0, 101, 0, "先輩って体操着持って\nないんですか？　ハ？\nチエルのを貸せと…？\nえっ…変態かな？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 116301, 4963, 1, 0, 102, 0, "テレフェスでは先輩も\n地味に頑張ってました\nね。まあ格好よかった\nんじゃないですか…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116301, 4964, 1, 1, 1, 0, "おはちぇる～ん☆\nさあ今日も楽しいを\n探しに行きますよー！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116301, 4965, 1, 2, 2, 0, "先輩ビッグニュース！\n今からお昼ごはん\nおごらせてあげます！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116301, 4966, 1, 3, 3, 0, "こんばんちぇるるん☆\nさあ朝まで楽しいを\n探しに行きますよー！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116301, 4968, 1, 0, 2, 0, "はぁ…ふかふか～…\n運動の後は…眠たく…\nなり…ま…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116301, 4969, 1, 0, 3, 0, "ユニ先輩に似合いそ！\nいやクロエ先輩かな？\nチエルは当然すぎて…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116301, 4970, 1, 0, 4, 0, "やん、いい香り～！\nまるでチエルのために\n存在するみたい！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116301, 4971, 1, 0, 5, 0, "この火を使って\n聖火リレーしましょう\n先輩、一本抜いて！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116301, 4973, 1, 0, 7, 0, "あむあむ…ちゃんと\n食べないとユニ先輩\nみたいになっちゃう！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116301, 4974, 1, 0, 8, 0, "ふむふむ…ちゃんと\n読まないとクロエ先輩\nみたいになっちゃう！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 102901, 4976, 1, 0, 103, 0, "今なら書ける気がする\n私の本当の\n想いを込めた\nラブソングを！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 102901, 4977, 1, 0, 103, 0, "輝きを増した私を\nいっぱい見てね！\n…ちょ、ちょっと\n見つめすぎだよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116201, 4978, 1, 0, 1, 0, "テレフェスかぁー…\nあんまいい記憶ないな\nよし、忘れとこ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116201, 4979, 1, 0, 2, 0, "暇だし鏡でも見るか…\nふふ～ん…いいじゃん\nあ？　何見てんだコラ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116201, 4980, 1, 0, 3, 0, "ワンツー、ワンツー♪\nああクソ、難しいな…\nあ？　何見てんだコラ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116201, 4981, 1, 0, 4, 0, "アオイ元気してるかな\nまた絶叫したり白目\n剥いたりしてんのかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116201, 4982, 1, 0, 5, 0, "ミフユねえさんが\nバイトリーダーしてる\n工場…うちも行くか…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 116201, 4983, 1, 0, 101, 0, "うーん…今日の晩御飯\n何にすっかなァ…\nあんたも食べてく？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 116201, 4984, 1, 0, 102, 0, "来年のテレフェスは\nどーなンだかねえ？\nま、一緒にまた弟の\n相手してやってよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116201, 4985, 1, 0, 1, 0, "体操着なんてフツーに\n着てもつまんないし？\nその辺センス出るよね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116201, 4986, 1, 0, 2, 0, "奨学金の道、遠くね？\nアキノ先輩と仲良く\nなった方が早いかも…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116201, 4987, 1, 0, 3, 0, "あんた体操着は持って\nないの。ハ？　うちの\n貸せって？　いいけど\n…いや、よくねえよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116201, 4988, 1, 0, 4, 0, "にゃんっ！\nや、だからさ…急に\n話しかけんなっての", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 116201, 4989, 1, 0, 101, 0, "え、なに？　この服？\nハ？　似合ってる？\n…え、聞こえなかった\nちょ、もっかい言って", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 116201, 4990, 1, 0, 102, 0, "あんたもテレフェスで\n一応は頑張ってたよね\nま格好よかったんじゃ\nないの。知んないけど", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116201, 4991, 1, 1, 1, 0, "ふぁ～あ…おはーす\n昨日も寝たの遅くて\nまだ目がショボだよ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116201, 4992, 1, 2, 2, 0, "なに。あんた昼御飯\nまだなの。じゃ、うち\n適当になんか作ろうか", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116201, 4993, 1, 3, 3, 0, "夜っていいよね。色々\n見えすぎない感じとか\n…この話したっけ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116201, 4995, 1, 0, 2, 0, "あ、ヤバ…運動の後に\nこのフカついたのは…\n秒でスヤれる…すやぁ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116201, 4996, 1, 0, 3, 0, "へーこんなんチエルに\n似合いそうじゃん。\n…いやユニ先輩かな？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116201, 4997, 1, 0, 4, 0, "お、なんか良さげな\n香りじゃん。これは\n何の…いや分かんね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116201, 4998, 1, 0, 5, 0, "熱ッ！　この野郎なに\n爆ぜてンだコラァ！\n濡らすぞ木、てめえ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116201, 5000, 1, 0, 7, 0, "何コレよく分かんない\nけど、今日モヤシしか\n食ってないから助かる", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116201, 5001, 1, 0, 8, 0, "今日、体操着だしね。\n読書には逆アドかな。\nうん帰ろ。いやあ残念", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116501, 5003, 1, 0, 1, 0, "ビッグボス・イノリ様\nふっふっふ…\nしびれる響きです！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116501, 5004, 1, 0, 2, 0, "いつかお兄さんを\nあたしの側近に\nしてやるですよ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116501, 5005, 1, 0, 3, 0, "ビッグボスになったら\nみんなからたくさん\nお菓子をもらうです！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116501, 5006, 1, 0, 4, 0, "ふふん！\nこの服はあたしにしか\n着こなせませんよ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116501, 5007, 1, 0, 5, 0, "ばーんと立って\nどーんと腕を組む！\nこれがボスのポーズ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 116501, 5008, 1, 0, 101, 0, "イノリ様の側近に\n選ばれるなんて\n名誉なことですよ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 116501, 5009, 1, 0, 102, 0, "未来の側近として\nあたしをしっかり\n甘やかすですよ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116501, 5010, 1, 0, 1, 0, "あたしがお兄さんを\n立派なギャングに\nしてあげますからね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116501, 5011, 1, 0, 2, 0, "側近たるもの\nボスを甘やかなさいと\nだめですよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116501, 5012, 1, 0, 3, 0, "なに見てやがるです…\nくぅ、分かりました！\nお菓子は半分こです！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116501, 5013, 1, 0, 4, 0, "お兄さんも\nあたしがボスなら\nうれしいですよね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 116501, 5014, 1, 0, 101, 0, "お兄さんが側近なら\nあたしも安心して\n夜眠れるですよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 116501, 5015, 1, 0, 102, 0, "ビッグボスになったら\nお兄さんにいい思い\nさせてあげますから！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116501, 5016, 1, 1, 1, 0, "ボスたるもの\n早起きは鉄則ですよ！\nふぁあ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116501, 5017, 1, 2, 2, 0, "お腹が空きました…\nご飯食べにいきますよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116501, 5018, 1, 3, 3, 0, "ボスたるものぉ〜…\n夜更かしぐらい当然…\nぐー…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116501, 5020, 1, 0, 2, 0, "は〜これは人をだめに\nしやがるやつですね\nあたし専用です", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116501, 5021, 1, 0, 3, 0, "これはけしからん\nかわいらしさですね\nあたしにぴったりです", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116501, 5022, 1, 0, 4, 0, "高級な香りがするです\nボスになったら\n部屋に置いてやるです", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116501, 5023, 1, 0, 5, 0, "あったかいですねぇ\nちょっとこのまま\n一休憩です…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116501, 5025, 1, 0, 7, 0, "はむはむ♪\nボスたるもの食べねば\n戦はできませんよ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116501, 5026, 1, 0, 8, 0, "ボスなら経済書ぐらい\n…う。難しくて\n読めねーです…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 102501, 5028, 1, 0, 103, 0, "あなたはドジな私を\n受け入れてくれる…\nだから、頑張れます！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 102501, 5029, 1, 0, 103, 0, "何でも仰ってください\nちょっぴり大胆な\nことでも…な～んて♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116601, 5030, 1, 0, 1, 0, "あーあー、よし\nのどの調子もいいし\nはりきってガイド\nしてくるか！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116601, 5031, 1, 0, 2, 0, "さあ、そろそろ行くか\nガイドが\n遅刻したんじゃ\n話になんないからな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116601, 5032, 1, 0, 3, 0, "しれっとドラゴン族が\n旅行に来たり\nしてくれないかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116601, 5033, 1, 0, 4, 0, "たぎるガイド先…\n火山、魔物の巣…\nダメだ、そんなとこ\nオレしか行けないや", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116601, 5034, 1, 0, 5, 0, "この前行った絶景\nスポットよかったな\nキレイな景色を見る\nのもいいもんだぜ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 116601, 5035, 1, 0, 101, 0, "お、今日はカレと\n一緒か。なんだか\nやる気出てきたぜ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 116601, 5036, 1, 0, 102, 0, "カレにガイドして\nもらったら\nどこに行っても\n楽しいだろうな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116601, 5037, 1, 0, 1, 0, "よっしゃあ！\nどっちが上手く\nガイドできるか\n勝負しようぜ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116601, 5038, 1, 0, 2, 0, "飯でも食いがてら\nおススメの店の\n下調べでもしようぜ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116601, 5039, 1, 0, 3, 0, "なかなかいいだろ？\nこの格好。キミも\n作ってもらったら\nどうだ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116601, 5040, 1, 0, 4, 0, "普通は行けない\n場所とか行きたいよな\nキミならどんな\nとこがいい？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 116601, 5041, 1, 0, 101, 0, "ガイドの仕事は\nキミの方が得意そうだ\n参考にさせてもらうぜ\nよろしくな！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 116601, 5042, 1, 0, 102, 0, "キミの探してきた店\n結構よさそうじゃん\nそうだ、今度二人で\n下見に行こうぜ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116601, 5043, 1, 1, 1, 0, "いい朝だな。絶好の\n仕事日和だぜ！\n今日もよろしくな！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116601, 5044, 1, 2, 2, 0, "よし、昼飯にしよう\nこの後も激務だし\n飯抜きじゃあ\nぶっ倒れちまうからな", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116601, 5045, 1, 3, 3, 0, "お疲れさん！　さーて\n次はオレと遊んで\nくれよ。まだまだ\nいけるだろ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116601, 5047, 1, 0, 2, 0, "ガイドは一日\n歩きっぱなしだからな\nゆっくり休ませて\nもらうぜ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116601, 5048, 1, 0, 3, 0, "これ、どこで\n売ってるんだろ？\n土産の情報も\n仕入れとかなきゃな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116601, 5049, 1, 0, 4, 0, "アロマとかよく\n分かんねえけど\n土産にはいいかもな\n覚えておくぜ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116601, 5050, 1, 0, 5, 0, "体が温まると心も\n熱くなるんだよな\nいい仕事が\nできそうだぜ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116601, 5052, 1, 0, 7, 0, "食べ放題か\nこれはお客さんも\n喜んでくれそうだな！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116601, 5053, 1, 0, 8, 0, "へえ、こんなスポット\nあったんだ。やっぱ\nたまには勉強も\nしとくもんだな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116701, 5055, 1, 0, 1, 0, "バイト先になじめる\nように笑顔の練習を！\nに、にへぇ〜…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116701, 5056, 1, 0, 2, 0, "働いて働いて働いて\n…いつかその先に\nぼっちの出口が\n見えるのかも…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116701, 5057, 1, 0, 3, 0, "バイトのイメトレを\n始めます。ポーズを\n決めて…よし！\n角度を変えて…よし！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116701, 5058, 1, 0, 4, 0, "バイト仲間に入る\nためには厳しい修業が\n必須と聞きました…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116701, 5059, 1, 0, 5, 0, "どんどん技術を磨いて\nバイト仲間として\n認めてもらうんです！\n目指せ職人技…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 116701, 5060, 1, 0, 101, 0, "あの人が応援して\nくれる…だから必ず\nバイト仲間の輪に\n入ってみせます…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 116701, 5061, 1, 0, 102, 0, "あの人がいてくれると\n勇気が湧いてきます…\nぼっち卒業を夢見ても\nいいんだ、って…♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116701, 5062, 1, 0, 1, 0, "緊張するけど\n思い切って声をかけて\nみます…バイト仲間に\n入るために…！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116701, 5063, 1, 0, 2, 0, "ぼっちの私が\nおもちゃ作りで\n人と共同作業…！\nか、感動です…！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116701, 5064, 1, 0, 3, 0, "好きなおもちゃって\nありますか？　私は\nもちろんマイフレンド\nくんです！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116701, 5065, 1, 0, 4, 0, "私が作ったおもちゃも\n誰かの友達に\nなってたら\nいいなぁ…♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 116701, 5066, 1, 0, 101, 0, "今日も明日もバイト\nですね！　バイト\n仲間ができる日が\n楽しみです…♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 116701, 5067, 1, 0, 102, 0, "脱ぼっちを目指して\nこれからも一緒に\n頑張りましょう！\n目指せ、バイト仲間！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116701, 5068, 1, 1, 1, 0, "おはようございます\n今日も気合いを入れて\nおもちゃ作りします！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116701, 5069, 1, 2, 2, 0, "ランチの時間です…♪\nもちろん工場の隅で\nぼっち飯です…！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116701, 5070, 1, 3, 3, 0, "ふわぁ…今日も\nお疲れさまでした\n明日も働きます～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116701, 5072, 1, 0, 2, 0, "バイトで頑張った日は\nふわふわの誘惑に\n勝てません…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116701, 5073, 1, 0, 3, 0, "こういうおもちゃの\nアイデアも\n素敵かも…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116701, 5074, 1, 0, 4, 0, "いい香りがする\nおもちゃはどうかな…\n森の薬草を使って…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116701, 5075, 1, 0, 5, 0, "ぽかぽか…\n幸せなひとときですね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116701, 5077, 1, 0, 7, 0, "もぐもぐ…たくさん\n食べて厳しいバイトを\n乗り切ります…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116701, 5078, 1, 0, 8, 0, "ふむふむ…おもちゃの\n設計図を見るのも\n勉強になりますね！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116801, 5080, 1, 0, 1, 0, "う～ん\n次はどんなたい焼きを\n作ろうかにゃぁ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116801, 5081, 1, 0, 2, 0, "あたしが売る\nたい焼きには\n夢が詰まってるにゃ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116801, 5082, 1, 0, 3, 0, "いつか絶対に\nたい焼き工場を\n建て直してみせるにゃ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116801, 5083, 1, 0, 4, 0, "たまにたい焼き以外の\nおやつも食べるにゃ\nたとえば煮干しとか！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116801, 5084, 1, 0, 5, 0, "悪い商売人がいたら\nファントムキャッツに\n知らせるにゃ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 116801, 5085, 1, 0, 101, 0, "キミと一緒にいると\nたい焼きのアイデアが\n次々出てくるにゃ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 116801, 5086, 1, 0, 102, 0, "出来ないことも\nキミと一緒にいれば\n出来る気がするにゃ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116801, 5087, 1, 0, 1, 0, "なんにゃ～？\nお姉さんにかまって\nほしいのかにゃ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116801, 5088, 1, 0, 2, 0, "キミ、いいところに！\nあたしとライバル店へ\n視察にいくにゃ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116801, 5089, 1, 0, 3, 0, "くんくん…この匂い！\nたい焼き持ってるなら\nちょうだいにゃ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116801, 5090, 1, 0, 4, 0, "次はどんなたい焼きを\n売ろうかにゃ～\nキミの案も欲しいにゃ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 116801, 5091, 1, 0, 101, 0, "ファントムキャッツに\n相棒が必要にゃ\nたとえばキミとか…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 116801, 5092, 1, 0, 102, 0, "相棒！\n…ってキミのこと\n呼んでいいかにゃ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116801, 5093, 1, 1, 1, 0, "早起きは三文の徳にゃ\n今日もたい焼き\n売れますよーににゃ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116801, 5094, 1, 2, 2, 0, "お腹空いたにゃ…\n賄いのたい焼きを\nいただくにゃ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116801, 5095, 1, 3, 3, 0, "猫は夜が本番にゃ\nでもあたしは…\nふわぁ～眠いにゃ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116801, 5097, 1, 0, 2, 0, "ふかふかにゃ～\nここで体を丸めて\n一眠りしたいにゃ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116801, 5098, 1, 0, 3, 0, "これは…！\n猫にも負けない\nかわいさだにゃ…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116801, 5099, 1, 0, 4, 0, "いい香りにゃ～\nゴロゴロのどが\n鳴りそうだにゃ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116801, 5100, 1, 0, 5, 0, "ふにゃ～…\nこのまま働かずに\nまるまってたいにゃ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116801, 5102, 1, 0, 7, 0, "商売は戦いにゃ\nたくさん食べて\nたくさん働くにゃ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116801, 5103, 1, 0, 8, 0, "ふむふむ。たい焼きの\nアレンジレシピ集…\nためになるにゃ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 102101, 5105, 1, 0, 103, 0, "お芝居の前に\nお兄ちゃんの顔を\n見ると、とっても\n落ち着きますぅ…♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 102101, 5106, 1, 0, 103, 0, "お兄ちゃんにもっと\n見てもらいたい…\nだから私…\n強くなりたいです…！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116901, 5107, 1, 0, 1, 0, "さらなる効率化で\nみんなといい仕事を\nしたいわね！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116901, 5108, 1, 0, 2, 0, "他のバイトとの\nスケジュールを\n調整しないと…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116901, 5109, 1, 0, 3, 0, "時給がアップしたわ！\n働きが認められると\nやる気が出るわね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116901, 5110, 1, 0, 4, 0, "効率的に動ければ\n業務を追加できる…\n燃えてきたわ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116901, 5111, 1, 0, 5, 0, "帳簿付けのコツ…\nユカリさんに教わって\nみようかしら", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 116901, 5112, 1, 0, 101, 0, "バイトリーダーとして\nみんなをビシバシ\n鍛えていくわ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 116901, 5113, 1, 0, 102, 0, "戦いでもバイトでも\nキミなら安心して\n背中を預けられるわね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116901, 5114, 1, 0, 1, 0, "どうしたの？\n仕事のことなら\nなんでも相談してね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116901, 5115, 1, 0, 2, 0, "さすがは私の弟子ね\nこれなら安心して\n仕事を任せられるわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116901, 5116, 1, 0, 3, 0, "みんなのお手本になる\n働きぶりを見せないと\nいけないわね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116901, 5117, 1, 0, 4, 0, "キミのこと…もっと\n頼りにしちゃっても\nいいのかしら…？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 116901, 5118, 1, 0, 101, 0, "ふふ、キミも一人前の\nバイト戦士として\n立派に成長したわね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 116901, 5119, 1, 0, 102, 0, "新しいおもちゃの\n企画書を作ってみたの\nちょっと見てくれる？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116901, 5120, 1, 1, 1, 0, "朝は一番効率がいい\n時間帯よ！\nバリバリ働くわよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116901, 5121, 1, 2, 2, 0, "さ、お昼に行きましょ\n今日の日替わり定食は\n何かしら…楽しみね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116901, 5122, 1, 3, 3, 0, "もうこんな時間…\nちゃんと寝て明日に\n備えなくちゃね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116901, 5124, 1, 0, 2, 0, "適度な労働と休息の\nサイクルが最大効率を\n生み出すのよ…ふぅ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116901, 5125, 1, 0, 3, 0, "はぁ～なんて素敵な\n抱き心地なのかしら…\n最高効率の癒やしね♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116901, 5126, 1, 0, 4, 0, "落ち着く香りね…\nなんだか集中力が\nアップした気がするわ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116901, 5127, 1, 0, 5, 0, "ふぅ…癒されるわね…\n仕事のオンオフの\n切り替えは重要よね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116901, 5129, 1, 0, 7, 0, "働くためには\nエネルギーをちゃんと\n摂っておかないと", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116901, 5130, 1, 0, 8, 0, "効率を上げるため\n生産管理の勉強も\nしておきたいわね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180301, 5132, 1, 0, 1, 0, "変身している間に\n色々と試しておこう\nまずは…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180301, 5133, 1, 0, 2, 0, "飛べるというのは\n便利だな。料理が\n暖かいうちに\n持ち帰れる", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180301, 5134, 1, 0, 3, 0, "プリンセスか…\n柄ではないけれど\n嫌な気はしないな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180301, 5135, 1, 0, 4, 0, "この力で\n何を成すべきなのか…\n剣に聞いてみよう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180301, 5136, 1, 0, 5, 0, "変身したとて私は私\n一時的に強くなろうと\n元が弱くてはだめだ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 180301, 5137, 1, 0, 101, 0, "変身していない時でも\n彼がそばにいると\n力が湧いてくる…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 180301, 5138, 1, 0, 102, 0, "私の思い…\nいつか伝えてみせるよ\n…きっといつか！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180301, 5139, 1, 0, 1, 0, "何者かと戦うの？\nそれとも修行？\nどちらだとしても\n付き合うよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180301, 5140, 1, 0, 2, 0, "変身し続けたら\nどれだけ空腹に…\n私は試さないからね？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180301, 5141, 1, 0, 3, 0, "本物のプリンセスに\nなるのは御免だが\nナイトがいて\nくれるのは頼もしいよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180301, 5142, 1, 0, 4, 0, "いつかこの力を\n誰かを救うために…\n戦い以外に使える時が\nくることを願うよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 180301, 5143, 1, 0, 101, 0, "キミに感じてほしいな\n私に流れてくる\nキミからの力を…\nその…私の、思いも…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 180301, 5144, 1, 0, 102, 0, "思いを告げる…\nそれがこんなにも\n難しいなんて…\nでも、いつか必ず…！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180301, 5145, 1, 1, 1, 0, "おはよう！\nいい変身日和だ！\n…ふふっ、冗談だよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180301, 5146, 1, 2, 2, 0, "いつもより多く\n昼食を用意しよう\n変身が解けたら大変だ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180301, 5147, 1, 3, 3, 0, "一日お疲れ様\n私もそろそろ変身を\n解いて休むとするよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180301, 5149, 1, 0, 2, 0, "変身してしまうと\n動かずにいても\n空腹になるのかな…？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180301, 5150, 1, 0, 3, 0, "私もキミたちくらい\n可愛げがある姿に\nなっているかな？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180301, 5151, 1, 0, 4, 0, "香りに対する感覚も\nいつもより鋭く\nなっているみたいだ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180301, 5152, 1, 0, 5, 0, "ありがたいな\n少し足元が\n冷えていたところだ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180301, 5154, 1, 0, 7, 0, "これは…！\n持ち運べるように\n改良できないかな？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 180301, 5155, 1, 0, 8, 0, "祖父に貰った\n技法書を読んで\n初心に帰ろう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117001, 5157, 1, 0, 1, 0, "最強の惚れ薬を作って\nあのおかたの心を\n私の思いのままに…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117001, 5158, 1, 0, 2, 0, "夏といえば海にスイカ\n花火に水着…\nそして惚れ薬ですわ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117001, 5159, 1, 0, 3, 0, "あのおかたの心を\n手に入れるための\n準備を進めましょう\nこの夏こそ確実に…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117001, 5160, 1, 0, 4, 0, "あのおかたに近づく\n虫どもを、どうやって\n片付けようかしら…\nまったく忌々しいこと", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117001, 5161, 1, 0, 5, 0, "…クスクス\n薬の調合を考えるのは\n至福の時間ですわ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 117001, 5162, 1, 0, 101, 0, "あのおかたの全てが\n早く私のものに\nなりますように…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 117001, 5163, 1, 0, 102, 0, "かき氷も花火も海も…\nあなた様がいてこそ…\nそう、あなた様は\n私の全てなのですわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117001, 5164, 1, 0, 1, 0, "二人だけの夏を\n楽しみましょう…？\n…クスクス", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117001, 5165, 1, 0, 2, 0, "あら、あなた様\n私に秘密でどこに\n行こうとして\nいますの…？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117001, 5166, 1, 0, 3, 0, "海に行きませんか？\nあなた様と一緒に\n過ごすには\n最高の場所ですから…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117001, 5167, 1, 0, 4, 0, "ちょうどいいところに\nいらっしゃいました\nできたての薬を…\nさぁどうぞ…クスクス", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 117001, 5168, 1, 0, 101, 0, "夏の海のきらめきも\nあなた様の存在が\nなければ色あせて\nしまいますわ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 117001, 5169, 1, 0, 102, 0, "私とあなた様は\n結ばれる運命に\nあるのです…\n逃がしませんわ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117001, 5170, 1, 1, 1, 0, "起きてすぐに会いに\n来てくださるなんて\nようやく私の伴侶たる\n自覚が…クスクス", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117001, 5171, 1, 2, 2, 0, "お食事はまだですか？\n特製の薬草ランチを\n召し上がれ…クスクス", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117001, 5172, 1, 3, 3, 0, "私の夢を見られる薬を\n差し上げますわ…\nおやすみなさいませ\nあなた様…クスクス", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117001, 5174, 1, 0, 2, 0, "いい肌触りですわ…\n考え事にもぴったり\n眠くなるのが\n玉に瑕ですが…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117001, 5175, 1, 0, 3, 0, "こういったものに\n揮発性の薬を仕込む\nのも一つの手ですわね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117001, 5176, 1, 0, 4, 0, "この香り…開発中の\n薬に使えそうな気が\nしますわ…クスクス", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117001, 5177, 1, 0, 5, 0, "あのおかたの\nぬくもりを\n思い出しますわ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117001, 5179, 1, 0, 7, 0, "美味しい…この味なら\n薬を混ぜても違和感は\nなさそうですわ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117001, 5180, 1, 0, 8, 0, "この薬の調合法…\n今度試してみましょう\nもちろんあのおかたに\n飲んでいただいて…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117001, 5182, 1, 0, 10, 0, "二人乗りができると\nいいですわね…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117001, 5183, 1, 0, 11, 0, "ふかふかですわ…\nあのおかたの分も\n欲しくなります…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 101701, 5184, 1, 0, 103, 0, "今日はキミと\nいつまでも歌って\n踊りたい気分さ～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 101701, 5185, 1, 0, 103, 0, "あは～♪\nキミの隣は、故郷より\n落ち着くかも～…♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117101, 5186, 1, 0, 1, 0, "この水着…\n一番に弟くんに\n見せたいな♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117101, 5187, 1, 0, 2, 0, "弟くんをこっそり\n海に運んで…\nサプライズしちゃお♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117101, 5188, 1, 0, 3, 0, "いつもと違う髪型に\nしてみたけど…\n気に入ってくれるかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117101, 5189, 1, 0, 4, 0, "海は楽しみだけど\n日焼けしないように\n気を付けなきゃね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117101, 5190, 1, 0, 5, 0, "まだかな、弟くん\n水着のお姉ちゃんが\nお出迎えだよっ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 117101, 5191, 1, 0, 101, 0, "海が見えるおうちで\n弟くんと一緒に\n暮らしたいなぁ～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 117101, 5192, 1, 0, 102, 0, "弟くんの水着…\nどんな感じかなぁ\n…あ、想像してたら\nドキドキして…えへへ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117101, 5193, 1, 0, 1, 0, "こーら、水着の\nお姉ちゃんは\nおさわり禁止♪\n…なんてね？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117101, 5194, 1, 0, 2, 0, "なぁに？\nふふっ、何回でも\n呼んでいいんだよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117101, 5195, 1, 0, 3, 0, "海で迷子に\nならないように\n手を繋ごうね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117101, 5196, 1, 0, 4, 0, "お姉ちゃんが\n海の楽しみ方を\n教えてあげるねっ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 117101, 5197, 1, 0, 101, 0, "わっ、弟くん\nどこ触ってるの？\nも～驚いちゃったぞ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 117101, 5198, 1, 0, 102, 0, "お姉ちゃん、海でも\nずっとキミを見てるよ\n…安心してね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117101, 5199, 1, 1, 1, 0, "まだ眠そうだね？\nほら、海が待ってるよ\n早く起きて起きて♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117101, 5200, 1, 2, 2, 0, "大丈夫、疲れてない？\n海で遊び疲れた時は\n素直に休憩しようね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117101, 5201, 1, 3, 3, 0, "ほら、早く寝ないと\n明日もお姉ちゃんと\n遊べないぞ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117101, 5203, 1, 0, 2, 0, "だらけたお姉ちゃんは\n弟くんには\n見せられないな～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117101, 5204, 1, 0, 3, 0, "ふふっ、かわいいね♪\n弟くんが持つと\nもっとかわいいよっ♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117101, 5205, 1, 0, 4, 0, "いい香りだね…\n一味違うお姉ちゃんに\nなれそう…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117101, 5206, 1, 0, 5, 0, "あったか～い♪\nほら、弟くんも\nおいでよ♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117101, 5208, 1, 0, 7, 0, "よく食べて、いつでも\n弟くんの呼び声に\n応える準備を…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117101, 5209, 1, 0, 8, 0, "弟くんの水着の写真\nこの辺りに隠して\nおこうかな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117101, 5211, 1, 0, 10, 0, "この子の背に乗って\nお姉ちゃんと弟くんの\n楽園へ…な～んて♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117101, 5212, 1, 0, 11, 0, "海を感じる感触だね\n弟くんとの楽しい夏を\n思い出しちゃうな…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117201, 5213, 1, 0, 1, 0, "この夏、誰よりも\nキラキラしてみせる！\nだって私は\nアイドルだから！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117201, 5214, 1, 0, 2, 0, "日傘にアームカバー\n帽子にサングラス…\n紫外線対策は完璧！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117201, 5215, 1, 0, 3, 0, "とっておきの\nサマーソングを\nみんなに届けるよ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117201, 5216, 1, 0, 4, 0, "なんにもしないのって\n苦手かも…\n彼、早く来ないかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117201, 5217, 1, 0, 5, 0, "ラムネって\n夏って感じするよね\n後で彼と飲もっと♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 117201, 5218, 1, 0, 101, 0, "夏は恋の季節！\n燃えるような恋…\nいつかしたいな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 117201, 5219, 1, 0, 102, 0, "今年の夏の目標は\n…彼との思い出を\nいっぱい作ること！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117201, 5220, 1, 0, 1, 0, "私の水着写真集\n見てくれたんだ！\nどう、かわいかった？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117201, 5221, 1, 0, 2, 0, "スイカ割りしたいの？\nいいね、楽しそう！\nみんなも誘おうよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117201, 5222, 1, 0, 3, 0, "ねぇ、見て見て！\n水着の映える\nポーズ考えたんだ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117201, 5223, 1, 0, 4, 0, "ライブ中に\n花火を打ち上げたら\n絶対盛り上がるよね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 117201, 5224, 1, 0, 101, 0, "最近、忙しいけど…\nキミの顔見たら\n疲れなんて\n消えちゃった！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 117201, 5225, 1, 0, 102, 0, "この夏もいーっぱい\nドキドキさせるから！\n覚悟しててよね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117201, 5226, 1, 1, 1, 0, "外は朝から暑いね\nけど、負けないから！\nお仕事、頑張るぞー！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117201, 5227, 1, 2, 2, 0, "自分へのご褒美に、\nアイス食べちゃお♪\nキミも一緒に食べよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117201, 5228, 1, 3, 3, 0, "夏の夜って\nワクワクするね♪\nホタル見に行こうよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117201, 5230, 1, 0, 2, 0, "ふわぁ…眠い…\n仕事とかの疲れ、\nたまってるのかな…？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117201, 5231, 1, 0, 3, 0, "私のぬいぐるみが\nグッズになったら…\n彼にかわいがって\nもらえるかな？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117201, 5232, 1, 0, 4, 0, "この香り…\n夏限定のやつでしょ？\nいいセンスしてるね♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117201, 5233, 1, 0, 5, 0, "暑～い！\nけど…汗かいて\n気持ちいいかも！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117201, 5235, 1, 0, 7, 0, "レッスンの後のご飯は\n特別おいしい♪\n…もうひとつだけ\n食べちゃおっかな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117201, 5236, 1, 0, 8, 0, "あ、この前撮影した\n水着グラビアだ\nうんうん、いい感じ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117201, 5238, 1, 0, 10, 0, "この子、もふもふだね\n触るのクセになりそう\nもふもふもふもふ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117201, 5239, 1, 0, 11, 0, "わ、かわいい♪\n今度のグラビア撮影で\nこれ使ってもいい？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117401, 5240, 1, 0, 1, 0, "水着っていいですよね\n海に浮かぶ一輪の花\nって感じがして！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117401, 5241, 1, 0, 2, 0, "自分の水着を作るのも\n誰かの水着を作るのも\n楽しいですね♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117401, 5242, 1, 0, 3, 0, "水着の最終チェック！\n伸縮性や動きやすさは\n…いい感じですね！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117401, 5243, 1, 0, 4, 0, "振り付けや衣装作りに\n歌や踊りの練習…\n…やることがいっぱい", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117401, 5244, 1, 0, 5, 0, "はぁ…レイ様の水着…\n私に作らせて\nもらえないかなぁ～…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 117401, 5245, 1, 0, 101, 0, "騎士さんのことは\n気に入らないですけど\n別に嫌いという\nわけじゃなくて…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 117401, 5246, 1, 0, 102, 0, "騎士さん暇そうですね\nに、荷物持ちとして\n誘ってみようかな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117401, 5247, 1, 0, 1, 0, "騎士さんいいところに\nこの衣装\nどう思います？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117401, 5248, 1, 0, 2, 0, "何見てるんですか？\nもしかしてこの水着の\n良さが分かります！？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117401, 5249, 1, 0, 3, 0, "真夏でも涼しい…\nそれも水着の良さの\n一つですね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117401, 5250, 1, 0, 4, 0, "仕立て屋も\nアイドルも\n諦めません！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 117401, 5251, 1, 0, 101, 0, "夏と言えば\nバカンスですけど…\n予定、あるんですか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 117401, 5252, 1, 0, 102, 0, "う～ん…騎士さんを\n海へ誘うには…って\nなんでもないですよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117401, 5253, 1, 1, 1, 0, "ふー…夏はやっぱり\n朝が一番過ごしやすい\nですね～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117401, 5254, 1, 2, 2, 0, "暑い時こそ海です！\n体力づくりのためにも\n泳ぎにいきますよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117401, 5255, 1, 3, 3, 0, "外で虫が鳴いてる…\n夏って感じがして\n風流ですねぇ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117401, 5257, 1, 0, 2, 0, "少し休憩です…\nこのまま寝ないよう\n気をつけないと…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117401, 5258, 1, 0, 3, 0, "か、かわいい…！\nこの感動を次の\nステージ衣装にも…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117401, 5259, 1, 0, 4, 0, "いい香りですね～\n波の音が聞こえて\nきそうです…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117401, 5260, 1, 0, 5, 0, "泳いだ後は\n少し寒いので\nちょうどいいですね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117401, 5262, 1, 0, 7, 0, "夏バテしないように\n栄養はしっかり\n取らないと！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117401, 5263, 1, 0, 8, 0, "次のトレンドは…\nふむふむ、やっぱり\nこう来ましたか…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117401, 5265, 1, 0, 10, 0, "デザインはもちろん\n触り心地も\n最高ですね♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117401, 5266, 1, 0, 11, 0, "夏の撮影スポットに\n活用できそうですね\nさっそく衣装考えて…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 104001, 5267, 1, 0, 103, 0, "えへへ…♪\n団長さんが初めての\n友達で本当に良かった", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 104001, 5268, 1, 0, 103, 0, "あっ、団長さん！\n今日のＢＢ団の活動\nなんですけど…二人で\n大縄跳びしませんか！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117301, 5269, 1, 0, 1, 0, "この衣装で\nライブするのは\n恥ずかしいな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117301, 5270, 1, 0, 2, 0, "せっかくの夏だし\n肝試しに\n行ってみたいかも…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117301, 5271, 1, 0, 3, 0, "歌もダンスも\nより上を目指すために\n夏合宿をしよう…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117301, 5272, 1, 0, 4, 0, "夏の暑さにも負けない\n熱いライブに\nしてみせる…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117301, 5273, 1, 0, 5, 0, "新曲のアイデア\nまとめたいな…\n少し海辺を散歩しよう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 117301, 5274, 1, 0, 101, 0, "かき氷、食べたいな\n…あの人は何味が\n好きなんだろう？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 117301, 5275, 1, 0, 102, 0, "水着、似合ってるって\n言ってもらえるかな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117301, 5276, 1, 0, 1, 0, "サインして、ですか？\nいいですけど…\n少し照れますね…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117301, 5277, 1, 0, 2, 0, "少し待ってください\n今、新曲の楽譜を\n書いているので…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117301, 5278, 1, 0, 3, 0, "なんでしょう？\nもしかして…\nボイトレに興味が？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117301, 5279, 1, 0, 4, 0, "今度のライブでは\n夏らしい爽やかな歌を\nファンにお届けします", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 117301, 5280, 1, 0, 101, 0, "…水鉄砲で遊ぼう？\nふふ。楽しそうですね\nお手柔らかに…♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 117301, 5281, 1, 0, 102, 0, "あなたの声を\n聴いていると、\n何だか落ち着きます…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117301, 5282, 1, 1, 1, 0, "今日も暑くなりそう…\nお互いしっかりと\n水分補給しましょうね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117301, 5283, 1, 2, 2, 0, "お腹空きましたよね\n海の家で、\nなにか買ってきます", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117301, 5284, 1, 3, 3, 0, "夜の海で月明かりと\n波の音を楽しもうかと\n…一緒に行きますか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117301, 5286, 1, 0, 2, 0, "レッスンだけでなく\nリラックスすることも\n大事、ですよね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117301, 5287, 1, 0, 3, 0, "ふふ、かわいい\n精霊も負けないくらい\nかわいいですけどね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117301, 5288, 1, 0, 4, 0, "夏らしい爽やかで\nいい匂い…あなたに\nピッタリな香りですね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117301, 5289, 1, 0, 5, 0, "海に入った後は、\n体が冷えてしまいます\n一緒に温まりましょう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117301, 5291, 1, 0, 7, 0, "アイドルとして、\n食べすぎには\n気をつけないと…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117301, 5292, 1, 0, 8, 0, "夏は恋の季節…\nガンガン攻めるべし…\nなるほど…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117301, 5294, 1, 0, 10, 0, "この子に乗りながら\nライブをする…\nというのはどうですか", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117301, 5295, 1, 0, 11, 0, "まるで人魚になった\n気分…なんて、ふふ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118301, 5296, 1, 0, 1, 0, "お姉ちゃんとして\nシオリンにかっこいい\nとこ見せなくっちゃ☆", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118301, 5297, 1, 0, 2, 0, "いつかシオリンと\n一緒に空を飛んで\n空中散歩してみたいな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118301, 5298, 1, 0, 3, 0, "すこし眠くなってきた\nかも…シオリンと\nお昼寝しようかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118301, 5299, 1, 0, 4, 0, "お散歩してこよっと！\nシオリンとお出かけ\nするのにいい場所\n探さなくっちゃ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118301, 5300, 1, 0, 5, 0, "シオリンに過保護って\n言われちゃった…♪\nそれでも心配しちゃう\nのが姉心…だよね？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 118301, 5301, 1, 0, 101, 0, "気持ちいい日差し！\nシオリンと彼と、\nお昼寝したら\n気持ちよさそ～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 118301, 5302, 1, 0, 102, 0, "今度天気がいい日には\nシオリンと彼を誘って\n一緒にお出かけ\nしたいな～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118301, 5303, 1, 0, 1, 0, "今日はご機嫌なんだ♪\nだって、シオリンの\n調子がいいんだもん☆", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118301, 5304, 1, 0, 2, 0, "今からシオリンと\nピクニックに\n行くんだ♪　そうだ！\nキミも一緒にどう？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118301, 5305, 1, 0, 3, 0, "わっ、びっくりした！\nシオリンから借りた本\n読むのに集中\nしてたから…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118301, 5306, 1, 0, 4, 0, "うん？　どうしたの？\nあっ、もしかして\nこの服、気になる？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 118301, 5307, 1, 0, 101, 0, "シオリンもキミも元気\nいっぱいだと、私も\n元気になっちゃうよ☆", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 118301, 5308, 1, 0, 102, 0, "シオリンの前だけ\nじゃなくて、キミにも\nかっこいい私の姿を\n見てほしいな☆", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118301, 5309, 1, 1, 1, 0, "おはよう！　えへへっ\n今日は早起きなんだ♪\nシオリンにかっこ悪い\nとこ見せたくないから", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118301, 5310, 1, 2, 2, 0, "おなかすいたなぁ～…\nシオリンと一緒に\nおいしいもの食べに\n行こうかな！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118301, 5311, 1, 3, 3, 0, "もうこんな時間！？\nシオリンと一緒だと\n時間が早く\n過ぎていっちゃうね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118301, 5313, 1, 0, 2, 0, "この快適な寝心地…！\nシオリンにもおすすめ\nしなきゃ…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118301, 5314, 1, 0, 3, 0, "わあ、かわいい！\nそれにさわり心地も\nいいし、抱き枕に\nちょうどいいかも！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118301, 5315, 1, 0, 4, 0, "いいにおい…♪\nなんだかリラックス\nできて…すやぁ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118301, 5316, 1, 0, 5, 0, "ぽかぽか～☆\nシオリンと一緒に\nぬくぬくしたら\n気持ちよさそう♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118301, 5318, 1, 0, 7, 0, "仲のいい人が一緒だと\nつい食が進んじゃうね\nえへへ…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118301, 5319, 1, 0, 8, 0, "ふむふむ…この本…\nシオリンが好きかも！\n今度おすすめしよっと", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118401, 5322, 1, 0, 1, 0, "お姉ちゃん、\n私のために無理は\nしてないかな…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118401, 5323, 1, 0, 2, 0, "新しく買った絵本…\nこういうのなら\nお姉ちゃんと楽しく\n読めるよね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118401, 5324, 1, 0, 3, 0, "ニャットちゃんを\nここに連れてきて\nみんなで遊べないかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118401, 5325, 1, 0, 4, 0, "本屋さんに行こうかな\nみんなで読める\n面白い本が\nあるといいなぁ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118401, 5326, 1, 0, 5, 0, "たまには…読むだけ\nじゃなくて、自分で\nお話を書いて\nみようかな…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 118401, 5327, 1, 0, 101, 0, "時々…お姉ちゃんと\nあの人が似てるって\n思うことがあります…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 118401, 5328, 1, 0, 102, 0, "お姉ちゃんとあの人…\n優しくて頼もしい\n二人に囲まれて…\n私、幸せ者だなぁ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118401, 5329, 1, 0, 1, 0, "どうかしましたか？\nもしかして…あなたも\n一緒にこの本を\n読みたい…とか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118401, 5330, 1, 0, 2, 0, "けほっ…けほっ…\nあっ、心配いりません\n飲み物を飲んで\nむせただけですから", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118401, 5331, 1, 0, 3, 0, "この格好…\n似合ってますか…？\nえへへ、照れますね…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118401, 5332, 1, 0, 4, 0, "なぁに、お姉ちゃん？\n…あっ、すみません\n読書に集中していて…\n間違えちゃいました…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 118401, 5333, 1, 0, 101, 0, "お姉ちゃん、時々無理\nしちゃうから…\nあなたも一緒だと\n安心できます", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 118401, 5334, 1, 0, 102, 0, "お姉ちゃんとあなたと\n頼れる人に囲まれて\n私は幸せものですね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118401, 5335, 1, 1, 1, 0, "なんだか朝から\n調子がいい…今日は\nいい日になりそうです", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118401, 5336, 1, 2, 2, 0, "せっかくだから、\nお姉ちゃんを誘って\nお外でランチも\nいいかも", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118401, 5337, 1, 3, 3, 0, "お姉ちゃんに心配\nかけないように…もう\nちょっと本を読んだら\n寝なくちゃね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118401, 5339, 1, 0, 2, 0, "ふんわり柔らかくて\n気持ちいい手触り…\nお姉ちゃんが寝るのに\nちょうどよさそう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118401, 5340, 1, 0, 3, 0, "ふふっ、かわいい♪\n絵本の物語みたいに\nおしゃべりできない\nかな…？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118401, 5341, 1, 0, 4, 0, "素敵な落ち着く香り♪\nなんだかお姉ちゃん\nみたい、ふふっ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118401, 5342, 1, 0, 5, 0, "あったかくてほっと\nするなぁ\nここでゆっくり\n読書しようかな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118401, 5344, 1, 0, 7, 0, "この味…\nお姉ちゃんが好きそう\n少しもらって、\n持って行こうかな…？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118401, 5345, 1, 0, 8, 0, "この本みたいな、\n心躍る大冒険も\nお姉ちゃんと一緒なら\nできちゃいそう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117501, 5348, 1, 0, 1, 0, "いい部屋ね\nここを私の分院に\nするのもいいかも\nしれないわね…♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117501, 5349, 1, 0, 2, 0, "珍しいカンポーが\n手に入ったから\n色々試してみたいわ\n色々と、ね♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117501, 5350, 1, 0, 3, 0, "この服を着ると\n自分が医者だって\nより実感するわね\nまあ、闇医者だけどね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117501, 5351, 1, 0, 4, 0, "新薬開発のために\n薬草を採取して\nおこうかしら", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117501, 5352, 1, 0, 5, 0, "いつ急患が来ても\n対応できるように\nしておきましょうか", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 117501, 5353, 1, 0, 101, 0, "あの子の研究資料も\nだいぶ増えてきたわね\nちゃんとまとめて\nおかないと♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 117501, 5354, 1, 0, 102, 0, "あの子は大事な\n研究対象。だから\nいつまでも健康で\nいてもらわないとね♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117501, 5355, 1, 0, 1, 0, "薬に興味があるの？\nなら、今度ゆっくりと\n教えてあげるわね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117501, 5356, 1, 0, 2, 0, "少し時間があいたから\n一緒に新薬の実験を\nしましょうか♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117501, 5357, 1, 0, 3, 0, "どうしたの？\nまさか急患…！？\nあら、違うのね\nならよかったわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117501, 5358, 1, 0, 4, 0, "具合が悪い時はすぐに\n言うのよ。君には\nいつでも健康でいて\nほしいから", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 117501, 5359, 1, 0, 101, 0, "少し仮眠したいわね\n肩…いいえ、膝を\n貸してもらえる？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 117501, 5360, 1, 0, 102, 0, "たまには…君の隣なら\n闇医者も悪い研究者も\nお休みにするのも\n悪くないわね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117501, 5361, 1, 1, 1, 0, "たまには朝の陽ざしを\n浴びるのも…\n悪くないわね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117501, 5362, 1, 2, 2, 0, "天気がいい日は\nいい薬草が採れるの\n君も集めるの、\n手伝ってくれる？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117501, 5363, 1, 3, 3, 0, "いけない、もうこんな\n時間なのね\n診察に没頭すると\n時間感覚がなくなるわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117501, 5365, 1, 0, 2, 0, "もう少し固い方が\n私好みだけど…\n悪くない寝心地ね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117501, 5366, 1, 0, 3, 0, "このぬいぐるみ…\n闇医者として\n興味深いわね…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117501, 5367, 1, 0, 4, 0, "落ち着く香りね\n私も診察室で\n使ってみようかしら", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117501, 5368, 1, 0, 5, 0, "冷えは病につながるわ\nしっかり温めて\n体をいたわりましょ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117501, 5370, 1, 0, 7, 0, "おいしい♪\n研究のお供に\nもってこいの\n味だわ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117501, 5371, 1, 0, 8, 0, "医学は日進月歩…\n闇医者とはいえ\n最新の論文には\n目を通しておかないと", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117601, 5400, 1, 0, 1, 0, "白衣の天使かあ\nまさにボクのための\n呼び方だね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117601, 5401, 1, 0, 2, 0, "美しさとは\n日々の努力が\n作るものなんだよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117601, 5402, 1, 0, 3, 0, "大変だ…\nボクは美しく\nなりすぎたかも", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117601, 5403, 1, 0, 4, 0, "ボクのかわいさで\n癒やされて\nみんな健康に\nなっちゃうね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117601, 5404, 1, 0, 5, 0, "どんな服装をしても\nボクの美しさは\n変わらないね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 117601, 5405, 1, 0, 101, 0, "これからはボクも\n美の伝道師として\nがんばらないとね♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 117601, 5406, 1, 0, 102, 0, "ボクのかわいい笑顔を\n近くで見る以上の\n癒やしはないよね♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117601, 5407, 1, 0, 1, 0, "どう？　看護師姿の\nボクもかわいいでしょ\nもっと近くで見る？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117601, 5408, 1, 0, 2, 0, "ほら！　美しいボクの\n看護師姿を見たら\n元気でちゃうでしょ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117601, 5409, 1, 0, 3, 0, "白衣の天使のボクも\n最高に美しいね\n存分に癒されてよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117601, 5410, 1, 0, 4, 0, "具合が悪いの？\nならボクの美しい顔を\n処方してあげる♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 117601, 5411, 1, 0, 101, 0, "さあ今日もボクと\n一緒に美の追求に\n付き合ってもらうよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 117601, 5412, 1, 0, 102, 0, "かわいい天使のボクが\nいつもそばにいるんだ\nキミって幸せ者だね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117601, 5413, 1, 1, 1, 0, "さすがボク\n目が覚めるような\n美しさでしょ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117601, 5414, 1, 2, 2, 0, "美は食で作られる\n美容にいいものを\n食べようよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117601, 5415, 1, 3, 3, 0, "ふああ…早く寝よ？\n夜更かしは美容の\n大敵だからね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117601, 5417, 1, 0, 2, 0, "ふう～…\nリラックスする\nボクも美しい", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117601, 5418, 1, 0, 3, 0, "ボクが抱きしめると\n普段よりかわいく\n見えちゃうね♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117601, 5419, 1, 0, 4, 0, "美しいボクは\n常にいい香りを\nしていなくちゃね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117601, 5420, 1, 0, 5, 0, "光がボクを照らしてる\nんじゃなくて、ボクが\n光を輝かせてるのさ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117601, 5422, 1, 0, 7, 0, "バランスのいい食事が\n美しいボクを\n作るのさ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117601, 5423, 1, 0, 8, 0, "ふむふむ…なるほど\nこうすればより美しく\nなれるかもしれない", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 105701, 5425, 1, 0, 103, 0, "仲間のためなら\nきっと…どこまでも\n強くなれるよね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 105701, 5426, 1, 0, 103, 0, "プリンセスフォーム？\n私と同じような力を\n持ってる人がいるの？\n会ってみたいな～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118001, 5427, 1, 0, 1, 0, "ここの空気は、\nどうもゆるすぎて…\n落ち着きませんわね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118001, 5428, 1, 0, 2, 0, "陛下のお越しになる\n別宅としては、\n少し警備が\n甘すぎでは？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118001, 5429, 1, 0, 3, 0, "あらあら？\nサッシにホコリが！\nちょっとネコ助！\n掃除は念入りに！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118001, 5430, 1, 0, 4, 0, "いつまで入り浸って\nいるのかと、\n秘書に叱られて\nしまいましたわ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118001, 5431, 1, 0, 5, 0, "もういっそ、\nここに間借りして\n仕事しようかしら…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 118001, 5432, 1, 0, 101, 0, "邪魔している身ですし\n家事くらい\nお手伝いさせなさいな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 118001, 5433, 1, 0, 102, 0, "本当は、意外と\n居心地が良いのかも…\n陛下の気持ち、\n少し分かりましたわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118001, 5434, 1, 0, 1, 0, "ちょっ…わたくしを\n叩いても、ホコリなど\nあまり出ませんわよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118001, 5435, 1, 0, 2, 0, "陛下っ？\nなんだ…貴方でしたの\n用件は手短にお願いね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118001, 5436, 1, 0, 3, 0, "別に、好き嫌いは\nありませんけれど…\n魔物料理って、意外と\n美味なものですわね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118001, 5437, 1, 0, 4, 0, "言っておきますけれど\nわたくしは商売人です\n荒事は専門外ですわよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 118001, 5438, 1, 0, 101, 0, "プリンセスナイトの\n能力…なにか儲け話に\n使えませんかしらね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 118001, 5439, 1, 0, 102, 0, "貴方がたのことも、\n少しくらいは\n信用してないことも\nありませんでしてよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118001, 5440, 1, 1, 1, 0, "おはようございます\nさ、今日もバリバリ\n働きますわよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118001, 5441, 1, 2, 2, 0, "お昼ですわね\n栄養補給をしつつ\n働きますわよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118001, 5442, 1, 3, 3, 0, "定時ですって？\n関係ありません！\nわたくし、まだまだ\n働きますわよ～っ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118001, 5444, 1, 0, 2, 0, "うっ…いけませんわ\n日頃の疲れが…\nふわぁ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118001, 5445, 1, 0, 3, 0, "もふもふ…ふふっ♪\n…なんですのっ、\nなにかおかしくて！？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118001, 5446, 1, 0, 4, 0, "いい香りですわね\nわたくしの執務室にも\n置きたいくらいですわ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118001, 5447, 1, 0, 5, 0, "いけませんわ…\nこんなに暖かいと\n日頃の疲れが…ふわぁ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118001, 5449, 1, 0, 7, 0, "もう一つ食べたら\n仕事に戻りますわよ？\nあと一つだけ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118001, 5450, 1, 0, 8, 0, "商人にとって\n知識は武器ですわ\n陛下のため、貴方も\nもっと勉強なさい？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117701, 5452, 1, 0, 1, 0, "故郷のお菓子を\nいろいろ作ったんだ～\nみんなに\n配ってくるさ～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117701, 5453, 1, 0, 2, 0, "彼ってどんな仮装が\n似合うかな～？\nシーサーとか？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117701, 5454, 1, 0, 3, 0, "カボチャチャンプルー\n作ってみよっと！\nきっとおいしいさ～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117701, 5455, 1, 0, 4, 0, "視線を感じるさ～\nもしかして幽霊？\nおしゃべりするさ～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117701, 5456, 1, 0, 5, 0, "さっき作った\nサーターアンダギー、\n早く彼に\n食べてほしいさ～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 117701, 5457, 1, 0, 101, 0, "彼のために、\n特別なイタズラを\n考えとくさ～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 117701, 5458, 1, 0, 102, 0, "いつか彼と、\n故郷のハロウィンを\n楽しみたいさ～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117701, 5459, 1, 0, 1, 0, "ハロウィンは\n私たちが守るさ～♪\nさ、見回りに行こ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117701, 5460, 1, 0, 2, 0, "この制服、\nハロウィンらしくて\n大好きさ～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117701, 5461, 1, 0, 3, 0, "イタズラする気ね～？\nどんなことされるのか\n楽しみさ～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117701, 5462, 1, 0, 4, 0, "カボチャのランタン、\n一緒に作ろ！\n絶対楽しいよ～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 117701, 5463, 1, 0, 101, 0, "お菓子くれるの？\nあは～、ありがとー♪\nもぐもぐ…\n優しい味がするさ～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 117701, 5464, 1, 0, 102, 0, "キミと一緒に過ごす\nハロウィンは、\nドキドキとワクワクが\n止まらないさ～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117701, 5465, 1, 1, 1, 0, "ハロウィンは\n朝から大忙しさ～！\nお菓子作り、\n手伝ってほしいさ～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117701, 5466, 1, 2, 2, 0, "お化け屋敷に行こ！\n一緒にいっぱい\nドキドキしようね～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117701, 5467, 1, 3, 3, 0, "ここからが\nハロウィン本番さ～！\n幽霊たちと\nいっぱい遊ぶさ～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117701, 5469, 1, 0, 2, 0, "ちょっと休んだら、\nお菓子作りを\n再開するさ～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117701, 5470, 1, 0, 3, 0, "でーじかわいいね～♪\nこの子に仮装させたら\nきっと似合うさ～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117701, 5471, 1, 0, 4, 0, "お菓子みたいに\n甘い匂い…\nハロウィンらしい\nいい香りさ～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117701, 5472, 1, 0, 5, 0, "ランドソルの秋って\n冷えるけど…\nこれがあれば\n体も心もぽかぽかさ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117701, 5474, 1, 0, 7, 0, "故郷のご飯も\nおいしいけど、\nこっちのご飯も\nでーじおいしいさ～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117701, 5475, 1, 0, 8, 0, "パンプキンパイの\nレシピは…なるほど～\n勉強になるさ～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117701, 5477, 1, 0, 12, 0, "全部おいしそうで\nどれから食べるか\n迷っちゃうさ～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117801, 5479, 1, 0, 1, 0, "ムっ！　幽霊の気配を\n感じるデス！\nさあ姿を現すデス！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117801, 5480, 1, 0, 2, 0, "ムムム…\n妖魔の力を忍法に\n使えないでショウか…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117801, 5481, 1, 0, 3, 0, "怪しい妖魔を\n成敗するデス！\nハロウィンの平和は\nワタシが守りマスよ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117801, 5482, 1, 0, 4, 0, "ワタシの祭囃子が\n聞こえマスかー？\nさあ集まるデース！\n陽気に踊りまショウ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117801, 5483, 1, 0, 5, 0, "お祭りは\n楽しんだ者勝ちデス！\n踊らにゃ\nソンソンデス！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 117801, 5484, 1, 0, 101, 0, "幽霊になる前に\n天下統一を\n果たしたいデスね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 117801, 5485, 1, 0, 102, 0, "あの世に行っても\nショーグンは\nワタシの大事な\nショーグンデス！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117801, 5486, 1, 0, 1, 0, "ショーグン！\n悪霊退治に\n出かけるデース！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117801, 5487, 1, 0, 2, 0, "妖魔を家来に\nしてみるのは\nどうデス？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117801, 5488, 1, 0, 3, 0, "マントを使って\nウツセミの術の精度を\n高めるデス！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117801, 5489, 1, 0, 4, 0, "ハロウィンにちなんだ\n新しい忍法…\nすなわち妖忍法の\n開発を急ぐデス！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 117801, 5490, 1, 0, 101, 0, "あの世でも\nショーグンに\nお仕えしマスよー！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 117801, 5491, 1, 0, 102, 0, "ショーグンもワタシと\nハロウィンパレードを\n踊り明かしまセンか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117801, 5492, 1, 1, 1, 0, "おはようございマス！\n天下統一に向けて\n急いで準備するデス！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117801, 5493, 1, 2, 2, 0, "お腹減ってまセンか？\nハロウィンスイーツで\n休憩するデ〜ス", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117801, 5494, 1, 3, 3, 0, "夜更かしすると幽霊が\nでるかもしれまセン…\nヒュ〜ドロロロ〜…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117801, 5496, 1, 0, 2, 0, "もふもふなところが\nわたあめちゃんに\n似ていマス。わんっ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117801, 5497, 1, 0, 3, 0, "ぬいぐるみや人形には\n魂が宿るという\n言い伝えがありマス…\nマカフシギデ〜ス", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117801, 5498, 1, 0, 4, 0, "ウ〜ム…\nなんとも怪しい\n香りがしマスね！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117801, 5499, 1, 0, 5, 0, "パレードの熱気に\n負けないくらい\n熱いデス！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117801, 5501, 1, 0, 7, 0, "ついつい食べすぎて\nしまうのは\n妖魔のせいデスか！？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117801, 5502, 1, 0, 8, 0, "トーゴク妖怪の\n図鑑デス！　個性的な\n妖怪ばかりデスね！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117801, 5504, 1, 0, 12, 0, "ハロウィンの甘味を\n集めまシタよ！\nショーグンへの\n貢ぎ物デス！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 101301, 5531, 1, 0, 103, 0, "彼と二人で\n買い物に…って…\nこれってデートに\nなっちゃうのかな！？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 101301, 5532, 1, 0, 103, 0, "とうっ！　どうどう？\n今の決めポーズ！\nかっこいいよね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117901, 5533, 1, 0, 1, 0, "ヒデサイとおそろの\nハロウィンコスとか\nしてみたいな～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117901, 5534, 1, 0, 2, 0, "カボチャ料理かぁ…\n作ったらヒデサイ\n食べてくれるかな～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117901, 5535, 1, 0, 3, 0, "ハロウィンスイーツ…\nどれもおいしすぎて\n激ヤバなんですけど！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117901, 5536, 1, 0, 4, 0, "霊界ってどんなとこ\nなんだろ～\nわりと近所だったり？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117901, 5537, 1, 0, 5, 0, "服をハロウィンっぽく\nアレンジするのも\nたのしーよね♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 117901, 5538, 1, 0, 101, 0, "ヒデサイと一緒に\nハロウィンパーティー\n…えへへ、楽しみ～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 117901, 5539, 1, 0, 102, 0, "ヒデサイといると\nドキドキする…\nなんでだろ～？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117901, 5540, 1, 0, 1, 0, "ねねね、うちらも\nハロウィンパレード\n見に行こー？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117901, 5541, 1, 0, 2, 0, "ハロウィンセイバーズ\nしゅつどーう！\nヒデサイも行こ～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117901, 5542, 1, 0, 3, 0, "マントのロゴ\nかっこいいっしょ～？\nカボきちってゆーんだ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117901, 5543, 1, 0, 4, 0, "トリックオア\nトリート！\nねえねえヒデサイ\nお菓子ちょうだい♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 117901, 5544, 1, 0, 101, 0, "ヒデサイも\nメイクしてみる？\nハロウィンぽいやつ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 117901, 5545, 1, 0, 102, 0, "ヒデサイのこと\nうちがいつでも\nあっためたげるね！\n…あ！　手、手の話！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117901, 5546, 1, 1, 1, 0, "ハロウィンのイベント\n朝からでも\nやってるかな～？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117901, 5547, 1, 2, 2, 0, "カボチャでおやつ\n作ってみたんだ～\nヒデサイ食べてみて！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117901, 5548, 1, 3, 3, 0, "ハロウィンの夜って\nやっぱテンション\n上がるよね～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117901, 5550, 1, 0, 2, 0, "超ふかふかしてる～\nちょっと休憩させて\nもらっちゃお", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117901, 5551, 1, 0, 3, 0, "はぁ…\nもにカワすぎて\nつらいんですけど～！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117901, 5552, 1, 0, 4, 0, "ハロウィン限定の\nフレグランスじゃん！\nいい匂いする～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117901, 5553, 1, 0, 5, 0, "やばばば…\nあったかくて\n眠くなっちゃう～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117901, 5555, 1, 0, 7, 0, "これ食べて\nもうひとがんばり\nしよーっと！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117901, 5556, 1, 0, 8, 0, "ハロウィンっぽい\nファッションとか\n載ってないかなぁ？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 117901, 5558, 1, 0, 12, 0, "お菓子いっぱいだ～！\nあとでヒデサイにも\n持ってってあげよ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118501, 5560, 1, 0, 1, 0, "お掃除が終わったら\n次は書類を片付けて…\nその次は…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118501, 5561, 1, 0, 2, 0, "皆さんが冒険している\n間の留守は私に\nお任せください♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118501, 5562, 1, 0, 3, 0, "美食殿の皆さんが\n持ってきてくれる\nお土産が楽しみです♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118501, 5563, 1, 0, 4, 0, "ネビアさんとは\nときどき一緒にお茶\nする仲なんです", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118501, 5564, 1, 0, 5, 0, "ギルドハウスの乱れは\nギルドの乱れ\n整理整頓が大切です", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 118501, 5565, 1, 0, 101, 0, "素敵なギルドハウスの\n管理人ができて\nとっても幸せです♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 118501, 5566, 1, 0, 102, 0, "皆さんをほんの少し\n特別扱いしちゃう\n私がいます", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118501, 5567, 1, 0, 1, 0, "何かわからないことが\nあるときは私に\n聞いてくださいね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118501, 5568, 1, 0, 2, 0, "書類を書いていると\n落ち着きます…\n一緒にどうですか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118501, 5569, 1, 0, 3, 0, "あなたのまわりは\nいつも賑やかで\n楽しいですね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118501, 5570, 1, 0, 4, 0, "あっ！　お頼みしたい\nことがあって\n探してたんです♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 118501, 5571, 1, 0, 101, 0, "ギブアンドテイクは\n大切です。たまには\nお礼させてください♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 118501, 5572, 1, 0, 102, 0, "陰に日向にあなたを\n支えていきます♪\n期待していてください", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118501, 5573, 1, 1, 1, 0, "今日も一日\n管理人として\nがんばります♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118501, 5574, 1, 2, 2, 0, "お腹がすきました…\nそろそろお昼に\nしませんか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118501, 5575, 1, 3, 3, 0, "もうこんな時間…\n皆さんといると一日が\nあっという間です", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118501, 5577, 1, 0, 2, 0, "良質なお仕事は\n良質な休憩から\nです♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118501, 5578, 1, 0, 3, 0, "かわいいものに\n囲まれてお仕事できる\nのは幸せですね♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118501, 5579, 1, 0, 4, 0, "お仕事に集中できる\nアロマを設置するのも\nよさそうですね♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118501, 5580, 1, 0, 5, 0, "ポカポカ温まりながら\n書類仕事をするのも\nいいものですね…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118501, 5582, 1, 0, 7, 0, "栄養補給しないと\n頭が回りませんからね\nきちんと食べましょう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118501, 5583, 1, 0, 8, 0, "新条例はこのように\n活用されている、と…\nふむふむ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106701, 5585, 1, 0, 1, 0, "会合用の資料と\n連載のネーム…\nどっちを片付けよう？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106701, 5586, 1, 0, 2, 0, "イノリちゃん、また\nお金くすねてる…\n帰ってきたら\nお尻ペンペンだ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106701, 5587, 1, 0, 3, 0, "カヤちゃんの装備\n傷むの早いなぁ〜\nメンテしてあげないと", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106701, 5588, 1, 0, 4, 0, "そうだ、ラーゴのご飯\n買いに行かないと\n今日は何がいいかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106701, 5589, 1, 0, 5, 0, "ここの人たちは見てて\n飽きない人ばっかり♪\n漫画の参考にしよ〜", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 106701, 5590, 1, 0, 101, 0, "また一緒に買い物にも\n行きたいし、冒険も\nしたいな〜…♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 106701, 5591, 1, 0, 102, 0, "…君は見てるだけで\n満足なの？ ちゃんと\n触って欲しいな…♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106701, 5592, 1, 0, 1, 0, "私の目が見たいの？\nいいけど…魅了されて\n魂が壊れちゃうよ…？\n…冗談で〜す☆", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106701, 5593, 1, 0, 2, 0, "ラーゴがね、君に\n遊んで欲しいって☆\n仲良くしてあげてね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106701, 5594, 1, 0, 3, 0, "アシスタントの仕事\n気に入ってくれたなら\nまたお願いしていい？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106701, 5595, 1, 0, 4, 0, "私が覚醒できるか？\n見せてあげたいけど…\nすごいことになるから\nまた今度で♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 106701, 5596, 1, 0, 101, 0, "楽しいトラブルが\nあったら、とりあえず\n声はかけて欲しいな♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 106701, 5597, 1, 0, 102, 0, "マッサージお願い♪\n変なところは…\n少しだけなら☆", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106701, 5598, 1, 1, 1, 0, "おはよう〜♪\n今日も一日、一緒に\nがんばっていこうね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106701, 5599, 1, 2, 2, 0, "お腹空いちゃった…\n一緒にお昼行かない？\nいいお店があるんだ〜", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106701, 5600, 1, 3, 3, 0, "君、眠れないの？\n誰かに見られてる？\n…きっと気のせいだよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106701, 5602, 1, 0, 2, 0, "ちょっと休憩〜…\nこのまま原稿すれば\n疲れなくなるのかな？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106701, 5603, 1, 0, 3, 0, "かわいい〜♪\nラーゴのお友達に\nなってくれる？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106701, 5604, 1, 0, 4, 0, "いい香り〜♪\nこの目でどう見えるか\n伝えられたらいいのに", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106701, 5605, 1, 0, 5, 0, "ほどほどなのが\nちょうどいい時も\nあるよね…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106701, 5607, 1, 0, 7, 0, "この味付け…\nあっちでも再現\nできるかな？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106701, 5608, 1, 0, 8, 0, "ふふっ、面白い♪\nイノリちゃんの買う\n漫画にハズレなし☆", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119001, 5610, 1, 0, 1, 0, "私…23歳だもん\n魔法少女だなんて\n名乗れないわ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119001, 5611, 1, 0, 2, 0, "あん、どぅ、とろわ〜\nみんなイイ子に\nな〜る♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119001, 5612, 1, 0, 3, 0, "あっ！　イケナイ子の\n気配がするわ！\n風紀の乱れは\n先生が許しませんっ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119001, 5613, 1, 0, 4, 0, "ここで闇の力が\n発動したら大変だわ！\nやだっ…そう思ったら\n緊張して…ダメぇ〜！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119001, 5614, 1, 0, 5, 0, "オイタした子はみんな\nどうしてうれしそうに\n私の愛のムチを\n欲しがるのかしら…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 119001, 5615, 1, 0, 101, 0, "あの子に次は何を\n教えてあげようかしら\n愛？　希望？\nそれとも…♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 119001, 5616, 1, 0, 102, 0, "あの子のことを\n考えると、愛と希望で\n胸がいっぱいになるの", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119001, 5617, 1, 0, 1, 0, "先生なのに闇の力を\n持ってるのは秘密よ？\n他の子には\n知られたくないから…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119001, 5618, 1, 0, 2, 0, "秘密の授業の内容？\nそれは…あんなことや\nこんなことよ♪\nそれから、それから…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119001, 5619, 1, 0, 3, 0, "しーっ！　私の正体が\nルーセント学院の\n先生だって、ここでは\n秘密にしててね？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119001, 5620, 1, 0, 4, 0, "あら？　補習して\nほしいの？　いいわよ\n君のお願いなら\nなんでも聞いちゃう♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 119001, 5621, 1, 0, 101, 0, "イイ子にはごほうびを\nあげなくちゃ！\n君はなにが欲しい？\n遠慮しちゃダメよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 119001, 5622, 1, 0, 102, 0, "君との秘密が\nまた一つ\n増えちゃったね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119001, 5623, 1, 1, 1, 0, "今日も一日\n元気に過ごしましょう\n先生が指導してあげる", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119001, 5624, 1, 2, 2, 0, "夜まで頑張れるように\nお昼ご飯はしっかり\n食べましょう〜！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119001, 5625, 1, 3, 3, 0, "夜更かしはダメよ？\nイケナイ子が\nすることです！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119001, 5627, 1, 0, 2, 0, "闇の力に振り回されて\nもうヘトヘトよ〜\n心も体も\n癒されたいわ〜", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119001, 5628, 1, 0, 3, 0, "『秘密の学び舎』に\nこっそり連れて\n行っちゃおうかしら♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119001, 5629, 1, 0, 4, 0, "イケナイ甘い香り…\nなんだか胸の奥が\n熱くなってきたわ…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119001, 5630, 1, 0, 5, 0, "やんっ♪\n燃え上がる愛みたい♪\n火照ってきちゃった♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119001, 5632, 1, 0, 7, 0, "生徒の見本に\nなれるように\n好き嫌いはしないの", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119001, 5633, 1, 0, 8, 0, "『学び舎に咲く禁断の\n愛』？　気になるわ…\nううん、ダメよっ！\nでも…ちょっとだけ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 106301, 5635, 1, 0, 103, 0, "次に会う時は、\nまたあそこに一緒に…\nえへへ…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 106301, 5636, 1, 0, 103, 0, "横顔見てるの、\nバレてないよね…？\n…ちらっ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 105301, 5637, 1, 0, 103, 0, "背が少し伸びたか！？\nあ、あいつのところへ\n報告に行かねばっ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 105301, 5638, 1, 0, 103, 0, "私も貴公の隣に\n立っていられるよう\n日々精進していくぞ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119101, 5639, 1, 0, 1, 0, "ああ～\nドＭミストが\nあふれてしまいます～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119101, 5640, 1, 0, 2, 0, "魔法少女として\nクウカ頑張ります～！\n愛と希望の妄想を…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119101, 5641, 1, 0, 3, 0, "決めポーズの練習より\nやられポーズの練習を\nしておきます…♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119101, 5642, 1, 0, 4, 0, "快感を溜めに溜めて\n放つドＭビームは\n最高ですよ～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119101, 5643, 1, 0, 5, 0, "はっ！\nこれは闇の気配！\n…クウカ自身でした", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 119101, 5644, 1, 0, 101, 0, "ドＳさん…ぐふふ…\nクウカを縛りあげ…\nじゅるりぃ～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 119101, 5645, 1, 0, 102, 0, "ドＳさんに攻められる\nことで、クウカは進化\nしてゆくのです～！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119101, 5646, 1, 0, 1, 0, "はぅっ！\nもっと激しく…！\n情熱的に～～～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119101, 5647, 1, 0, 2, 0, "クウカの変身シーンを\nじっくり眺めたい！？\nは、恥ずかしいです～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119101, 5648, 1, 0, 3, 0, "「本当に魔法少女か\n確かめてやるよ…！」\nドＳさんはそう言って\nクウカを…ぐふふ…♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119101, 5649, 1, 0, 4, 0, "愛と希望の力が\nクウカの中に…\nこの胸の高鳴りが…？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 119101, 5650, 1, 0, 101, 0, "ひゃあんっ！\nそこです～っ！\nもっと、もっと～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 119101, 5651, 1, 0, 102, 0, "クウカをこんなに\n悦ばせるのは\nドＳさんだけです…♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119101, 5652, 1, 1, 1, 0, "明るい時間に出歩くと\n目立ちますね\n魔法少女の衣装…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119101, 5653, 1, 2, 2, 0, "お昼の見回りです～♪\nああ～うっかりドＭを\n増やしてしまいました", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119101, 5654, 1, 3, 3, 0, "夜！　それはクウカが\n活性化する時間です！\n闇の活動開始です～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119101, 5656, 1, 0, 2, 0, "気の緩んだクウカを\nドＳさんの鋭い牙が\n狙っています～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119101, 5657, 1, 0, 3, 0, "魔法少女のマスコット\nにいいかもしれません\nもっと鎖とか飾りを…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119101, 5658, 1, 0, 4, 0, "魅惑的な香りです～\nドＭミストの香りに\n似て…いや違いますね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119101, 5659, 1, 0, 5, 0, "火照る体…紅潮した\nクウカの肢体を熱く\n見つめるドＳさん…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119101, 5661, 1, 0, 7, 0, "食欲を満たした次は\n何を満たせば\nいいですかねぇ～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119101, 5662, 1, 0, 8, 0, "魔法少女がひどい目に\n遭う物語は…\nなさそうですね…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119901, 5664, 1, 0, 1, 0, "ケーキも悪くないけど\nプリンが一番なの\nでもせっかくだから\n両方もらってやるの！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119901, 5665, 1, 0, 2, 0, "クリスマスって聞くと\nなんだかあったかい\n気分になるの～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119901, 5666, 1, 0, 3, 0, "煙突がなくても\nミヤコはどこからでも\nおうちに入れるの♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119901, 5667, 1, 0, 4, 0, "イブもプリン！\nクリスマスもプリン！\nプレゼントもプリン！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119901, 5668, 1, 0, 5, 0, "クリスマスツリーの\n高いところの\n飾りつけは、ミヤコに\nまかせろなの～！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 119901, 5669, 1, 0, 101, 0, "アイツのプレゼント\n楽しみなの～\nきっと最っ高に特別な\nプリンなの～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 119901, 5670, 1, 0, 102, 0, "ミヤコがサンタなら\nアイツはトナカイなの\nつまりお似合いって\nことなの～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119901, 5671, 1, 0, 1, 0, "ミヤコ、プレゼントを\n配りにいくの～♪\nオマエも手伝えなの！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119901, 5672, 1, 0, 2, 0, "オマエにも飾りつけ\nしてやるの！\n頭にプリンを乗せると\nかわいいの♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119901, 5673, 1, 0, 3, 0, "幽霊のミヤコが\nいるなら、サンタも\nいて当たり前なの～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119901, 5674, 1, 0, 4, 0, "靴下にプリンを\n入れたらつぶれるの\nだからプリンは直接\nミヤコによこすの～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 119901, 5675, 1, 0, 101, 0, "今日はプリンだけじゃ\n満足してやらないの！\nミヤコをたくさん\n甘やかせなの！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 119901, 5676, 1, 0, 102, 0, "ミヤコのプリン\nオマエになら一個…\nうう～、一口だけ\nあげてもいいの！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119901, 5677, 1, 1, 1, 0, "サンタの朝は早いの～\nミヤコも早起きして\nプレゼント準備したの\nとってもえらいの♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119901, 5678, 1, 2, 2, 0, "今日は一日中\nみんなと遊ぶの～！\nオマエも来いなの！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119901, 5679, 1, 3, 3, 0, "みんなが寝たら\nチャンスなの～\nこっそりプレゼント\n配りにいくの♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119901, 5681, 1, 0, 2, 0, "むふー、気持ちいいの\nこういうプレゼントも\nいいかもしれないの", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119901, 5682, 1, 0, 3, 0, "これ気に入ったの～\nプリンとアイツの\n次くらいなの♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119901, 5683, 1, 0, 4, 0, "いいにおいなの～\nうっかり成仏\nしちゃいそうなの…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119901, 5684, 1, 0, 5, 0, "ここに飾っといた\n雪だるま\nなくなっちゃったの\nきっと成仏したの～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119901, 5686, 1, 0, 7, 0, "これはオマエがくれる\nプリンとは\n別腹なの～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119901, 5687, 1, 0, 8, 0, "プリンケーキ…\nこれならミヤコにも\n作れそうなの！\nさっそく作るの！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119901, 5689, 1, 0, 14, 0, "びっくりしたの～！\n幽霊のミヤコを\n驚かすなんて…\nなかなかやるの！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119901, 5690, 1, 0, 15, 0, "コイツの中…\nあったかいの～♪\n昼寝にぴったりなの～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119201, 5691, 1, 0, 1, 0, "メリークリスマス！\n聖夜に笑顔を\nお届けするべ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119201, 5692, 1, 0, 2, 0, "クリスマス前は\n乳製品がよく売れて\nありがてぇけんど…\n目が回る忙しさだ～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119201, 5693, 1, 0, 3, 0, "牧場で売る\nクリスマスリースを\n作っておかねぇと…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119201, 5694, 1, 0, 4, 0, "うちの動物たちにも\nクリスマスプレゼント\n用意しなきゃなぁ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119201, 5695, 1, 0, 5, 0, "クリスマスに向けた\n限定商品とか\n作ってみるべか？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 119201, 5696, 1, 0, 101, 0, "あんちゃんへの\nプレゼント選び…\nここでボケるか…？\nやめておくべか…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 119201, 5697, 1, 0, 102, 0, "オラ、あんちゃんから\n本当にたくさんの物を\nもらってんなぁ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119201, 5698, 1, 0, 1, 0, "雪遊びがしたいべか？\nへへっ、あんちゃん\nには負けねぇど！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119201, 5699, 1, 0, 2, 0, "ディナーと一緒に\n聖夜のお笑いライブを\nすんのはどうだべか？\nツッコミは任せたべ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119201, 5700, 1, 0, 3, 0, "寒くなってくると\n鍋が食べたくなるべや\nあんちゃんはどうだ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119201, 5701, 1, 0, 4, 0, "あんちゃんは\nサンタさんのこと\n信じてるだか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 119201, 5702, 1, 0, 101, 0, "どでかいクリスマス\nケーキをこさえるべ\nあんちゃんも作るの\n手伝ってくんろ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 119201, 5703, 1, 0, 102, 0, "あんちゃんが\nオラの旦那だったら…\nって、あんちゃん！\n今の聞いてただか！？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119201, 5704, 1, 1, 1, 0, "牧場の朝は早いど！\nさぁてと、まずは\n雪かきしに行くべや", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119201, 5705, 1, 2, 2, 0, "あんちゃんも一緒に\nお昼食べてくか？\n採れたての冬野菜は\nうんまいど～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119201, 5706, 1, 3, 3, 0, "よい子は早く寝ねぇと\nサンタさんが\n来てくれねぇべよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119201, 5708, 1, 0, 2, 0, "こういうのリンリンが\n好きそうだな…\nプレゼント候補に\n入れておくべ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119201, 5709, 1, 0, 3, 0, "もふもふしてて\nなまらめんこくて…\nあぁ…癒されるべや…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119201, 5710, 1, 0, 4, 0, "この匂い…\n気持ちが和むべや\n夢心地になっちまうな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119201, 5711, 1, 0, 5, 0, "あったけぇところは\n落ち着くなぁ…\nつい眠くなっちまう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119201, 5713, 1, 0, 7, 0, "こ、この味は…！\n…うーん、もっと\nリアクション芸も\n練習しねぇとな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119201, 5714, 1, 0, 8, 0, "シオシオって\nどんな本読んでんだ？\n…難しい字が\nぎっしりだべや…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119201, 5716, 1, 0, 14, 0, "この仕掛け…\nオラのコントに\n活かせるかも\nしんねぇな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119201, 5717, 1, 0, 15, 0, "この着ぐるみで\nリマリマのモノマネを\n練習してみるべか…？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 105001, 5718, 1, 0, 103, 0, "もっとキレイになって\nアイツにぎゃふんと\n言わせてやるんだから", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 105001, 5719, 1, 0, 103, 0, "アタシのこと\nどう思う？\nかわいいって…そこは\nキレイって言ってよね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119301, 5720, 1, 0, 1, 0, "ハートマークの刺繍…\nは、やりすぎですかね\nいやちょっとくらい\nアピールしてみても…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119301, 5721, 1, 0, 2, 0, "雪降らないかな～\nそしたらお兄ちゃんと\n雪だるま作ったり\nできるのに～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119301, 5722, 1, 0, 3, 0, "プレゼントを入れる\n靴下…いくつ編んでも\n足りません～！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119301, 5723, 1, 0, 4, 0, "お兄ちゃんとの\nクリスマスデートは\n早い者勝ちですからね\n負けませんよ～！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119301, 5724, 1, 0, 5, 0, "クリスマスだし…\n受験勉強は今日だけ\nお休みです♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 119301, 5725, 1, 0, 101, 0, "お兄ちゃんと\nおそろいのコーデで\nクリスマスデート…\nしたいなぁ～！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 119301, 5726, 1, 0, 102, 0, "お兄ちゃんに\nプレゼントする編み物\nこれで３着目～と♪\nあれ、作りすぎ？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119301, 5727, 1, 0, 1, 0, "えへへ、もっと近くで\n見たくありませんか？\n私のとっておきの\nクリスマスコーデ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119301, 5728, 1, 0, 2, 0, "お兄ちゃんが風邪を\n引いたら一大事です！\n私の編み物で暖かく\nしてあげます♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119301, 5729, 1, 0, 3, 0, "お兄ちゃん、ほっぺに\nケーキのクリームが\nついちゃってますよ♪\n私がとってあげます！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119301, 5730, 1, 0, 4, 0, "お兄ちゃん！\nケーキ、あーんって\nしてあげます！\nほら、あーん♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 119301, 5731, 1, 0, 101, 0, "クリスマスは大切な\n人と過ごすものっ！\nお兄ちゃん…妹の予定\n空いてますよっ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 119301, 5732, 1, 0, 102, 0, "クリスマスはずっと、\nお兄ちゃんと一緒に\nいたい…って言ったら\n欲張り…すぎですか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119301, 5733, 1, 1, 1, 0, "早起きは３本お得！\n…クリスマスなら\n追加で10本ぐらい\nもらえちゃうかも！？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119301, 5734, 1, 2, 2, 0, "お昼はケーキ？\nそれとも七面鳥？\nごちそういっぱいで\n迷っちゃいますね～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119301, 5735, 1, 3, 3, 0, "お兄ちゃんと\nクリスマスに夜更かし\n…えへへ、ちょっぴり\nドキドキしちゃう♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119301, 5737, 1, 0, 2, 0, "編み物をするには\n絶好のくつろぎ空間\nですね～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119301, 5738, 1, 0, 3, 0, "なんというかわいさ…\nクリスマス\nプレゼントに\nもってこいですね！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119301, 5739, 1, 0, 4, 0, "これがお兄ちゃんの\nお部屋の香り…？\nんん～！　ずっと\nかいでいたいです～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119301, 5740, 1, 0, 5, 0, "ふぁ、あったか～い…\n冬場はこういうのが\nありがたいですね～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119301, 5742, 1, 0, 7, 0, "ごちそうだぁ～♪\nほら、お兄ちゃんも\n食べて食べてっ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119301, 5743, 1, 0, 8, 0, "クリスマスデートの\n必勝法…こ、これは\n読むしかないです！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119301, 5745, 1, 0, 14, 0, "わっ、すごいです～！\nこれを飾っておけば\nクリスマスが一層\n賑やかになりますね♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 119301, 5746, 1, 0, 15, 0, "さぷらーいずっ！\nリマちゃんじゃなくて\nリノちゃんでした～！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120601, 5747, 1, 0, 1, 0, "ミミにもミソギにも\nあの人にも頼りに\nしてもらえるように\nしっかりしなきゃ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120601, 5748, 1, 0, 2, 0, "ミミはにんじんを\n食べられてすごいな…\n私も見習わなきゃ\nがんばろう…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120601, 5749, 1, 0, 3, 0, "ミソギから怖い話\n聞いちゃった…\nうぅ…誰か一緒に\nいてくれないかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120601, 5750, 1, 0, 4, 0, "立派な大人に\nなれるように\nいっぱい勉強\nしておかなくっちゃ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120601, 5751, 1, 0, 5, 0, "なんだか…あの人の\nあやしい視線を\n感じるような…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 120601, 5752, 1, 0, 101, 0, "あの人に勉強を\n教えられるように\nしっかり予習を\nしておこうっと…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 120601, 5753, 1, 0, 102, 0, "今日はあの人と\n一緒に冒険できる…\nなんだかワクワク\nしてきちゃった…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120601, 5754, 1, 0, 1, 0, "あ、いいとこに来て\nくれましたね！\nあなたに頼みたい\nことがあるんです！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120601, 5755, 1, 0, 2, 0, "きゃあっ！？\nへ、変なところを\n触らないでください！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120601, 5756, 1, 0, 3, 0, "どうですかこの格好？\nかわいい、ですか…？\nそ、そうですか…\nありがとうございます", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120601, 5757, 1, 0, 4, 0, "ひゃあっ…！\nすみません、勉強に\n集中してたので\n驚いちゃいました", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 120601, 5758, 1, 0, 101, 0, "一緒に勉強して立派な\n大人になりましょうね\n私がそばで見張ってて\nあげます", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 120601, 5759, 1, 0, 102, 0, "まだ立派ではないかも\nですけど、私よりも\n大人なあなたのことは\n頼りにしてますからね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120601, 5760, 1, 1, 1, 0, "立派な大人は\n朝の時間の使い方が\n上手だと聞きました\nあなたはどうですか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120601, 5761, 1, 2, 2, 0, "ミミとミソギと\nあなたと…大事な人と\n一緒のお昼ごはんは\nやさしい味がしますね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120601, 5762, 1, 3, 3, 0, "うぅ…怖い話を\n思い出しちゃいました\n寝るまで一緒に\nいてくれますか…？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120601, 5764, 1, 0, 2, 0, "すごい座り心地…！\nとっても勉強が\nはかどりそう…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120601, 5765, 1, 0, 3, 0, "この子…なんだか\nミミに似てるような\n気がする…ふふっ♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120601, 5766, 1, 0, 4, 0, "いい香り…♪\n勉強で疲れた\n頭がリフレッシュ\nできそうです", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120601, 5767, 1, 0, 5, 0, "ぽかぽかだ…♪\nミミとミソギと\nあの人と一緒に\nいるときみたい", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120601, 5769, 1, 0, 7, 0, "いっぱい食べたら\n早く大人になれたり\nしないかな…？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120601, 5770, 1, 0, 8, 0, "あう…\n知らない単語が…\nちゃんと調べて\n覚えなくっちゃ…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120401, 5773, 1, 0, 1, 0, "うさぎさんが\nぴょ～んぴょん♪\nみんなえがおで\nるんたった～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120401, 5774, 1, 0, 2, 0, "キョウカちゃんも\nいつかにんじんさんを\n好きになってくれたら\nうれしいな～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120401, 5775, 1, 0, 3, 0, "今日のミソギちゃんの\nイタズラ、びっくり\nしちゃったけど\nおもしろかったな～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120401, 5776, 1, 0, 4, 0, "このおうち、いつも\nいろんな人がいて\nドキドキぴょんぴょん\nって楽しいな～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120401, 5777, 1, 0, 5, 0, "いいお天気～♪\nお外でみんなと\nいっぱい遊びたいな♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 120401, 5778, 1, 0, 101, 0, "ミソギちゃんも\nキョウカちゃんも\nおにいちゃんがいると\nずっと笑顔だね～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 120401, 5779, 1, 0, 102, 0, "おにいちゃんにはやく\nあいたいな～♪\nまだかな、まだかな♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120401, 5780, 1, 0, 1, 0, "えへへ♪\nおにいちゃんに\nなでなでされると\nぽかぽかするね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120401, 5781, 1, 0, 2, 0, "聞いて、おにいちゃん\nさっきね、かわいい\nちょうちょさんに\n会ったんだよ～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120401, 5782, 1, 0, 3, 0, "ふわっ…！\nおにいちゃんの顔を\nお絵かきしてたから\nびっくりしちゃった♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120401, 5783, 1, 0, 4, 0, "おにいちゃんも\n遊びたいの？\nそれじゃあ二人も\n呼んで遊ぼう♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 120401, 5784, 1, 0, 101, 0, "おにいちゃんも\nみんなも、優しい人\nばっかりで\nミミ幸せ～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 120401, 5785, 1, 0, 102, 0, "おにいちゃん♪\n高い高いしてほしいな\nすっごくぴょんぴょん\nできて、好きなんだ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120401, 5786, 1, 1, 1, 0, "おはよう～♪\nおにいちゃん、今日は\nおひさまみたいに\nきらきら元気だね～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120401, 5787, 1, 2, 2, 0, "おひるごはんだね～♪\nにんじんさんを\nいっぱい食べて\n元気いっぱい♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120401, 5788, 1, 3, 3, 0, "ふわあ…\n眠くなってきちゃった\nおにいちゃん、\n一緒に寝よう～？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120401, 5790, 1, 0, 2, 0, "ふわぁ、気持ちいい♪\nずっとこうやって\n座ってたくなるね～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120401, 5791, 1, 0, 3, 0, "わあ、かわいい～\nぎゅ～っ、ぎゅ～っ♪\nこれで仲よしだね\nえへへ♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120401, 5792, 1, 0, 4, 0, "気持ちいいにおい～♪\nぴょんぴょんしたく\nなってきちゃった♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120401, 5793, 1, 0, 5, 0, "あったかぽかぽか♪\nおにいちゃんも\nきてくれたら、もっと\nあったかくなりそう♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120401, 5795, 1, 0, 7, 0, "もぐもぐ…ごっくん♪\nおっきくなりたいから\nいっぱい食べるよ～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120401, 5796, 1, 0, 8, 0, "えへへ、おもしろ～い\nこの絵本ね、\nキョウカちゃんから\n教えてもらったんだ♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120501, 5799, 1, 0, 1, 0, "この部屋…探索の\nしがいがありそう！\nついでにイタズラも\nしかけちゃお♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120501, 5800, 1, 0, 2, 0, "ミミってああ見えて\n実は大胆で度胸も\nあるよね…ちょっと\nかっこいいな…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120501, 5801, 1, 0, 3, 0, "怖い話を聞いた時の\nキョウカって\n面白いんだよね～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120501, 5802, 1, 0, 4, 0, "にいちゃんが\n慣れちゃわないように\nまた新しいイタズラを\n考えなきゃ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120501, 5803, 1, 0, 5, 0, "イタズラもいいけど\nミミとキョウカと\nにいちゃんと、普通に\n遊ぶのも楽しいなぁ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 120501, 5804, 1, 0, 101, 0, "ミミにキョウカ、\nそれににいちゃん！\nみんなそろえば愉快な\n冒険が始まりそう！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 120501, 5805, 1, 0, 102, 0, "次はどんなイタズラを\nしたら、にいちゃんは\n喜んでくれるかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120501, 5806, 1, 0, 1, 0, "今ミソギに触ったね？\nヤバいよにいちゃん…\n逃げないとトラップが\n発動しちゃうよ…！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120501, 5807, 1, 0, 2, 0, "わわっ！？　びっくり\nしたあ…！　新しい\nトラップを作ってて\n気づかなかったよ～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120501, 5808, 1, 0, 3, 0, "お？　にいちゃん、\nイタズラする側にも\n興味があるの？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120501, 5809, 1, 0, 4, 0, "にいちゃんなにか用？\nあ、もしかして…\nミソギと遊びたく\nなっちゃった？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 120501, 5810, 1, 0, 101, 0, "ベストタイミングだよ\nにいちゃん！\n今からみんなで探検に\n行くところなんだ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 120501, 5811, 1, 0, 102, 0, "にいちゃんってさ\nホントに優しいよね\nなにしても怒らないし\n…ありがとね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120501, 5812, 1, 1, 1, 0, "にいちゃんおはよー！\n景気づけに本日\n一発目のイタズラ、\nやっちゃうよ～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120501, 5813, 1, 2, 2, 0, "お昼ごはんはロシアン\nサンドイッチだよ！\nさあ、にいちゃん！\n好きなの選んでよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120501, 5814, 1, 3, 3, 0, "夜ってなんだか\nワクワクしてなかなか\n眠れないんだよね\nにいちゃんはどう？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120501, 5816, 1, 0, 2, 0, "この座り心地のよさ…\nなにかのトラップに\nうまく使えないかな？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120501, 5817, 1, 0, 3, 0, "こういうのに細工して\nミミやキョウカを\n驚かせると、いい反応\nするんだよね♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120501, 5818, 1, 0, 4, 0, "においをうまく使った\nイタズラって…\nなにかないかな？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120501, 5819, 1, 0, 5, 0, "あったまりながら\n新しいイタズラを\n考えると、いい案が\n出て来たりするんだ♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120501, 5821, 1, 0, 7, 0, "おいしくてついつい\n食べ過ぎちゃうなぁ…\nはっ…！？　これって\nまさかトラップ…！？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120501, 5822, 1, 0, 8, 0, "このキョウカから\n借りた冒険物語\nすっごく\nおもしろいんだよね！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120801, 5825, 1, 0, 1, 0, "晴れ着を着ると\nなんだか心が躍るね\n足取りも軽く\nなるってもんさ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120801, 5826, 1, 0, 2, 0, "正月の食べ物ってのは\n美味いもんだね\nいくらでも\n食べれちまうよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120801, 5827, 1, 0, 3, 0, "普段は好き勝手してる\nアタシらも\n正月くらいは\n大人しくするさ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120801, 5828, 1, 0, 4, 0, "年越しそばに雑煮\nこの季節は\n温かいものが\n体に沁みるね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120801, 5829, 1, 0, 5, 0, "正月だからって\nのんびりしすぎたね\nちょっと釣りにでも\n行こうかね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 120801, 5830, 1, 0, 101, 0, "いい正月だね\nいつかあの子と\n一杯やりたいもんだよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 120801, 5831, 1, 0, 102, 0, "大事な人と過ごす\n正月ってのがこんなに\n楽しいもんだとはね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120801, 5832, 1, 0, 1, 0, "部屋があったかくて\n顔が火照っちまったよ\nちょいと外に涼みに\n行かないか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120801, 5833, 1, 0, 2, 0, "餅ってのは\n何をつけてもうまいね\nお前さんは\nどの味が好きだい？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120801, 5834, 1, 0, 3, 0, "お祝いの鯛が\n足りないって？\nそれじゃあアタシが\n釣ってきてやるよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120801, 5835, 1, 0, 4, 0, "おせちが美味いのは\n分かるけど\n食べ過ぎには\n気を付けるんだよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 120801, 5836, 1, 0, 101, 0, "お前さんのおかげで\nいい正月を\n迎えられたよ\nありがとさん！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 120801, 5837, 1, 0, 102, 0, "慣れない晴れ着も\nお前さんに見て\nもらえたなら\n着たかいがあったね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120801, 5838, 1, 1, 1, 0, "おはようさん\n寒いのは分かるけど\nいい加減布団から\n出て顔洗ってきな", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120801, 5839, 1, 2, 2, 0, "今日の昼飯は雑煮だ\nアタシの郷土の味\nしっかり味わって\nおくれよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120801, 5840, 1, 3, 3, 0, "おや、お前さんも\n夜ふかしかい？\n退屈してたところさ\n付き合っておくれよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120801, 5842, 1, 0, 2, 0, "晴れ着だと座り方にも\n気を遣うね\nしわになったら大変さ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120801, 5843, 1, 0, 3, 0, "せっかくだし\nこの子にも晴れ着を\n着せてあげたいね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120801, 5844, 1, 0, 4, 0, "これは冬の花の香り…\n風情があって\nいいじゃないか", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120801, 5845, 1, 0, 5, 0, "あったかいねぇ…\nこんな寒い日には\nまさに天国だよ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120801, 5847, 1, 0, 7, 0, "いつも食べてる\nものでも正月に\n食べると\n新鮮な気がするね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120801, 5848, 1, 0, 8, 0, "せっかくの正月休みだ\nたまにはゆっくりと\n本でも読もうかね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120701, 5850, 1, 0, 1, 0, "新年のあいさつ…\n私も街のみんなに\nしてみようかしら…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120701, 5851, 1, 0, 2, 0, "正月はゆっくり過ごす\nのよね…ゆっくりって\n何したらいいのかしら", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120701, 5852, 1, 0, 3, 0, "この格好も\n慣れてくれば\n動きやすいわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120701, 5853, 1, 0, 4, 0, "う～ん、今年の抱負…\nやりたいことが\nたくさんあるのよね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120701, 5854, 1, 0, 5, 0, "おせち料理\nネビアの分も\n用意しなくちゃね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 120701, 5855, 1, 0, 101, 0, "ふふっ\n正月っていいわね\nみんな楽しそうだもの", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 120701, 5856, 1, 0, 102, 0, "新年の思い出…\nこれからもっと作るわ\nそれが私の抱負よ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120701, 5857, 1, 0, 1, 0, "今日は家で過ごすの？\nあなたと一緒なら\nきっと楽しいわね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120701, 5858, 1, 0, 2, 0, "どう？　この格好？\nコッコロさんは\n褒めてくれたけど…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120701, 5859, 1, 0, 3, 0, "ちょうど良かったわ\nあなたも大掃除\n手伝ってくれる？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120701, 5860, 1, 0, 4, 0, "これから料理するけど\nお雑煮と焼き餅\nどっちが好きかしら？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 120701, 5861, 1, 0, 101, 0, "『今の私』にとって\n初めての正月だもの\nあなたと楽しまないと", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 120701, 5862, 1, 0, 102, 0, "去年よりも今年…\nもっと成長するからね\n…お兄ちゃん♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120701, 5863, 1, 1, 1, 0, "おはよう\nせっかくの正月だもの\n朝から楽しまないとね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120701, 5864, 1, 2, 2, 0, "ねぇ…\n良かったら正月の遊び\n教えてくれるかしら？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120701, 5865, 1, 3, 3, 0, "ふわぁ～…今日が\n終わっちゃうのが…\nもったいないわね…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120701, 5867, 1, 0, 2, 0, "ふぅ…お参り後だから\n心地よすぎて眠って\nしまいそうだわ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120701, 5868, 1, 0, 3, 0, "ふふっ\nかわいいものって\n癒されるわね…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120701, 5869, 1, 0, 4, 0, "…ん\nこれが梅の香り？\n…いい匂いね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120701, 5870, 1, 0, 5, 0, "キャルじゃないけど…\nこのまま丸く\nなりたいところね…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120701, 5872, 1, 0, 7, 0, "はむはむっ…♪\nせっかくの正月だもの\n堪能しなくちゃね♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120701, 5873, 1, 0, 8, 0, "…ふんふん\n帯の結び方って\nこんなにあるのね…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 104201, 5875, 1, 0, 103, 0, "今の私が作れる\n最高の一曲…\nあの人に届くといいな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 104201, 5876, 1, 0, 103, 0, "あの、歌を…\n聞いてくれませんか？\nあなたに贈る\n特別な歌を", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120901, 5877, 1, 0, 1, 0, "ひい、ふう…\nむむ、みなに配る\nお年玉のポチ袋が\n残りわずかなのじゃ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120901, 5878, 1, 0, 2, 0, "宴の準備はわらわに\n任せておくのじゃ！\n支配者の手腕、\n見せつけてくれる！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120901, 5879, 1, 0, 3, 0, "新年の願掛けなら\nわらわにすればよい\n支配者に頼むほうが\n確実じゃろう？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120901, 5880, 1, 0, 4, 0, "みなからもらった\nこの年賀状とやら…\nわらわも\n書いてみようかの♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120901, 5881, 1, 0, 5, 0, "正月飾りにもいろいろ\n種類があるんじゃの～\nどれも華々しくて\nわらわ好みじゃ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 120901, 5882, 1, 0, 101, 0, "あやつへのお年玉は\nちと多めにしておくか\n…日ごろの感謝も\n込めてのう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 120901, 5883, 1, 0, 102, 0, "あやつのことを想うと\nそれだけで行く年\n来る年が楽しみになる\nふふ、愉快なやつじゃ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120901, 5884, 1, 0, 1, 0, "みなに晴れ着を\n褒められて、とても\nよい気分なのじゃ♪\n次はお主の番じゃぞ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120901, 5885, 1, 0, 2, 0, "お主へのお年玉は\nいちばん最後じゃ！\n残り物には福が宿る\nそうじゃからのう", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120901, 5886, 1, 0, 3, 0, "この季節は手が\nかじかむのう…ほれ、\nお主！　わらわの手を\nあたためるのじゃ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120901, 5887, 1, 0, 4, 0, "お主のために雑煮を\n手作りしてみたのじゃ\n食べていくがよい♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 120901, 5888, 1, 0, 101, 0, "眷属のお主に\n正月休みはまだ早い！\nずっとわらわのそばに\n控えておくのじゃ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 120901, 5889, 1, 0, 102, 0, "ふふ、これほど楽しい\n正月は初めてじゃ…\nお主と過ごす時間は\nいつだって特別じゃな", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120901, 5890, 1, 1, 1, 0, "早起きしてわらわを\n待っておったのか？\nさすがはわらわの眷属\n殊勝な心がけじゃな♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120901, 5891, 1, 2, 2, 0, "食事どきじゃな\n正月の宴会料理が\nわらわたちを\n待っておるぞ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120901, 5892, 1, 3, 3, 0, "夜も更けてきたのう\n冬の夜は長い…\n心ゆくまでお主と\n過ごせるわけじゃな♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120901, 5894, 1, 0, 2, 0, "実に心地がよい♪\nこのまま寝正月も\n悪くないかも\nしれぬのう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120901, 5895, 1, 0, 3, 0, "愛いやつじゃのう\nわらわのそばに置いて\nやってもよいぞ♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120901, 5896, 1, 0, 4, 0, "品のある香りじゃのう\n寝室に置いておけば\nなにかご利益に\nあずかれそうじゃな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120901, 5897, 1, 0, 5, 0, "心地よい…\nわらわはしばらく\nここから動かぬぞ\nお主も付き合うがよい", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120901, 5899, 1, 0, 7, 0, "舌鼓が鳴るのう♪\n正月の宴には\n持ってこいの品じゃ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120901, 5900, 1, 0, 8, 0, "ほう、新春にはそんな\n習わしが…正月を\n制するには、まだまだ\n勉強が必要じゃのう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121001, 5902, 1, 0, 1, 0, "久しぶりの我が家って\n感じです！　やっぱり\nここが一番ですね〜！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121001, 5903, 1, 0, 2, 0, "さあ食べましょう！\nみんなで囲める食卓を\n心から味わいますよ☆", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121001, 5904, 1, 0, 3, 0, "ランドソルに\n帰ってきた途端に\nお腹が空きました〜！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121001, 5905, 1, 0, 4, 0, "ただいま☆\n幸せ特盛りの\nわたしの日常！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121001, 5906, 1, 0, 5, 0, "ようやくランドソルに\n帰ってきたことですし\nやっぱり魔物料理を\nいただきましょう☆", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 121001, 5907, 1, 0, 101, 0, "あの人との絆を\n守るために\nもっともっと食べて\n強くならなくちゃ☆", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 121001, 5908, 1, 0, 102, 0, "いつもありがとうって\nあの人を思いっきり\n抱きしめたいです☆", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121001, 5909, 1, 0, 1, 0, "あなたの想いは\nこの身をかけて\n全力で受け止めます！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121001, 5910, 1, 0, 2, 0, "おかえりなさい☆\nここにいるみんなも\nあなたの帰りをずっと\n待っていましたよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121001, 5911, 1, 0, 3, 0, "このギルドハウスで\n冒険の疲れを\n癒しましょう〜！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121001, 5912, 1, 0, 4, 0, "あなたの想いが\n全力全開を超える\n力をくれました！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 121001, 5913, 1, 0, 101, 0, "日常が続く幸せを\nあなたと一緒に\nかみしめたいです☆", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 121001, 5914, 1, 0, 102, 0, "ぎゅ〜っ！\nわたしたちは\nどんなことがあっても\nずっと一緒ですよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121001, 5915, 1, 1, 1, 0, "おはようございます！\nあなたと過ごせる\n幸せを、今日も\n味わいますよ〜！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121001, 5916, 1, 2, 2, 0, "ご飯の時間ですよー！\nあなたも一緒に\n食べましょうね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121001, 5917, 1, 3, 3, 0, "明日もあなたが隣に\nいてくれることを\n心から願ってますよ\nおやすみなさい☆", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121001, 5919, 1, 0, 2, 0, "ふい〜…\n安心できる場所で\nくつろげるって\n幸せですね〜…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121001, 5920, 1, 0, 3, 0, "じゅるり…\n食べたくなっちゃう\nくらい、かわいい☆", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121001, 5921, 1, 0, 4, 0, "くんくん…おおー！\nステーキの香り！\nやばいですね☆", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121001, 5922, 1, 0, 5, 0, "体がぽかぽかしてきて\nわたしの食欲も\n燃えてきましたよー！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121001, 5924, 1, 0, 7, 0, "わたしの食欲に\nついてこられますか？\nまだまだ食べますよ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121001, 5925, 1, 0, 8, 0, "これがキャルちゃんの\n言っていた、キスで\n目覚める王子様の\nおとぎ話ですか〜☆", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121201, 5927, 1, 0, 1, 0, "自分で自分を\n分析するのも、これは\nこれで面白い…♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121201, 5928, 1, 0, 2, 0, "強過ぎる力の扱いには\n注意しないと…\n七冠がいい例だね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121201, 5929, 1, 0, 3, 0, "少年の交友関係は\n計り知れないな…\nアタシも含むのか！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121201, 5930, 1, 0, 4, 0, "原理を徹底解明して\n再発を防ぐぞー！\n…誰か手伝って～！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121201, 5931, 1, 0, 5, 0, "これは祝福にも\n呪いにもなる…\n忘れずにいよう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 121201, 5932, 1, 0, 101, 0, "少年が\n目を覚ましてくれて\n本当によかった…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 121201, 5933, 1, 0, 102, 0, "彼以外を選んでいたら\n今回の事態は\n乗り切れなかったな…\nさすがだよ、少年", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121201, 5934, 1, 0, 1, 0, "少年！　調子はどう？\nクレープ食べられる？\nっていうか食べて？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121201, 5935, 1, 0, 2, 0, "元気そうで何よりだ\nまた店を手伝いに\nきてよ。二人もキミに\n会いたがってたしさ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121201, 5936, 1, 0, 3, 0, "キミの診察もあるし\n今はなるべくそばに\nいるよ。迷惑じゃ\nなければだけど", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121201, 5937, 1, 0, 4, 0, "やっぱり少年は\nあちこちで人を\n助けてる時が\n一番輝いてるよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 121201, 5938, 1, 0, 101, 0, "アタシは大したこと\nしてないけど\nみんなのことは\n褒めてあげてね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 121201, 5939, 1, 0, 102, 0, "みんなを笑顔に\nできるのは\nやっぱり少年だね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121201, 5940, 1, 1, 1, 0, "やあ少年\n朝寝坊はしてない？\nそれじゃ、よい１日を", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121201, 5941, 1, 2, 2, 0, "おや、少年！\nまかないのクレープ\n作ったけど、食べる？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121201, 5942, 1, 3, 3, 0, "ふわぁ…もう夜か…\n研究してたら\nあっという間だね…\nおやすみ、少年", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121201, 5944, 1, 0, 2, 0, "体がなまってたのか\n前線に出ると疲れる…\nまさか…年のせい！？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121201, 5945, 1, 0, 3, 0, "キミたちも今の\nアタシと同じ感じに\nアレンジしてあげよう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121201, 5946, 1, 0, 4, 0, "誰が置いたんだろう？\nいい香りだねぇ…\nうん、気に入った♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121201, 5947, 1, 0, 5, 0, "何も考えてない時間も\n実は思考にとって\n大事なんだよね～…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121201, 5949, 1, 0, 7, 0, "食の大切さは\nいつの食事からも\n感じられるね♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121201, 5950, 1, 0, 8, 0, "ふむ…面白い！\n今度全巻\n借りにこようっと", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116401, 5952, 1, 0, 1, 0, "聖学祭か…\n良い記憶はないな\nよし忘れよう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116401, 5953, 1, 0, 2, 0, "健全なる精神は\n健全なる肉体に宿る…\nはっ、知ったことかよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116401, 5954, 1, 0, 3, 0, "暇だし鏡でも見るか…\nえへへ、ユニだよ♪\nよし子供料金いけるな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116401, 5955, 1, 0, 4, 0, "アオイ君は元気に\nしているだろうか…\n彼女は人一倍ぼくに\n懐いていたからな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116401, 5956, 1, 0, 5, 0, "そろそろシズル君は\n姉という幻想から\n目覚めただろうか…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 116401, 5957, 1, 0, 101, 0, "ぼくは来年も聖学祭に\n行くさ…卒業しても\n君らとは永遠だ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 116401, 5958, 1, 0, 102, 0, "最近同志の登校率が\n減少傾向だな…\nまあ仕方ないのだが…\nないのだが…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116401, 5959, 1, 0, 1, 0, "ほわあああ！\nおい急に話しかけるな\n学びがこぼれる", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116401, 5960, 1, 0, 2, 0, "この体操着、周囲から\n大層評判が良いのだよ\nたいそうだけにな\nどやっ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116401, 5961, 1, 0, 3, 0, "何を着ていようと\nぼくはぼくさ\n外見など所詮は\n魂の識別符に過ぎん", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116401, 5962, 1, 0, 4, 0, "救護院のアヤネ君が\n最近ぼくの自称年齢に\n疑念を持ち始めてな…\nあの熊…殺るか…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 116401, 5963, 1, 0, 101, 0, "うん？　なんだね？\nなに、用は無い？\n用も無いのに呼ぶとは\nもしや…これが友達！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 116401, 5964, 1, 0, 102, 0, "この服が似合ってる？\nな、なんだね突然…\nやめたまえよでゅふふ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116401, 5965, 1, 1, 1, 0, "ふぁ～あ…\n朝も更けてきたな…\nそろそろ寝るか…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116401, 5966, 1, 2, 2, 0, "ふぁ～あ…もう昼だ…\n人間の起きている\n時間ではないな…\n早く寝なくては…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116401, 5967, 1, 3, 3, 0, "うーん、よく寝た！\n気持ちのいい夜だな\n今日も一日がんばるぞ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116401, 5969, 1, 0, 2, 0, "ほわぁ～…\nもしやこれは都で噂の\n人を駄目にするやつ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116401, 5970, 1, 0, 3, 0, "ふむ、愛くるしいな\nだが似合い過ぎる…\nぼくが持つと我ながら\n子供にしか見えん", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116401, 5971, 1, 0, 4, 0, "アロマか、興味深いな\nミツキ印ポーションも\n気化させて鼻腔から\n吸入してみるか…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116401, 5972, 1, 0, 5, 0, "んふ、んふふ…\n火ってイイよね…\nずっと見てられるね…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116401, 5974, 1, 0, 7, 0, "や、やめろ…\nとめどなく固形食物を\n生産するのはやめろ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 116401, 5975, 1, 0, 8, 0, "この可愛げのない筆致\nやはり作者は理系か！\nぐああ目が理系になる", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121101, 5977, 1, 0, 1, 0, "…あいつのためなら\n無理くらいいくらでも\nしてやるわよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121101, 5978, 1, 0, 2, 0, "この姿、\n魔力の制御ムズ過ぎ…\nま、あたしは優秀だし\nどうとでもなるけど！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121101, 5979, 1, 0, 3, 0, "あいつのこと、\n全部受け入れるって\n決めたから！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121101, 5980, 1, 0, 4, 0, "今なら誰だろうと\n負ける気がしないわ\nドラゴンでもなんでも\nかかってこいっての！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121101, 5981, 1, 0, 5, 0, "散々助けられた分、\n今度はあたしが\nあいつのこと助けるわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 121101, 5982, 1, 0, 101, 0, "あいつとの\nこれまでの冒険は\nどれも、あたしの\n大切な思い出…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 121101, 5983, 1, 0, 102, 0, "もしまたあいつが\n寝たままになったら\nなんだってするわ\nキ…キスでも…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121101, 5984, 1, 0, 1, 0, "なに、お腹空いたの？\nなら、おいしいご飯を\n食べに行きましょ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121101, 5985, 1, 0, 2, 0, "やっぱ、\nあんたのアホ面見ると\n安心するわ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121101, 5986, 1, 0, 3, 0, "【美食殿】はあんたと\nぺコリーヌがいなきゃ\n始まんないっての", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121101, 5987, 1, 0, 4, 0, "ひゃあっ！？\n急に声かけないで！？\n魔力が暴発しても\n知らないわよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 121101, 5988, 1, 0, 101, 0, "…あんたとこうして\nぼーっと過ごす時間、\nまあ…嫌いじゃないわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 121101, 5989, 1, 0, 102, 0, "あんたが信じて\n見守っててくれれば…\nあたし、\nなんだってできるわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121101, 5990, 1, 1, 1, 0, "おはよ\n早く顔洗いなさいよ、\nこのねぼすけ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121101, 5991, 1, 2, 2, 0, "今日のお昼は\nどこで食べる？\nあんたが\n決めていいわよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121101, 5992, 1, 3, 3, 0, "もしあんたがまた\n夢に囚われても\nあたしが助けるわ\n安心して寝なさいよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121101, 5994, 1, 0, 2, 0, "気持ちいい～…♪\nあいつもたまには\nこうして休ませなきゃ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121101, 5995, 1, 0, 3, 0, "一目惚れしたかも…！\nこの子、\n部屋に飾りたいわー♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121101, 5996, 1, 0, 4, 0, "へぇ、あいつって\nこういう匂いが\n好きなのかしら…？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121101, 5997, 1, 0, 5, 0, "暖かくて最高…♪\n丸くなっちゃいそう…\nうにゃあ…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121101, 5999, 1, 0, 7, 0, "イケるわね、これ！\nやっぱり食べるって\n大事よねー♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121101, 6000, 1, 0, 8, 0, "ファーストキスは\nどんな味、ね…\nふ、ふ～ん…？\nなるほど…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118101, 6002, 1, 0, 1, 0, "お友だち…\nできるかしら…？\nこんなわたしでも…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118101, 6003, 1, 0, 2, 0, "あまり、きょろきょろ\nしちゃダメよね…\n変な子だと、\n思われてしまうわ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118101, 6004, 1, 0, 3, 0, "広い場所、\n落ち着かない…\nはぅ…どこにいれば…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118101, 6005, 1, 0, 4, 0, "～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118101, 6006, 1, 0, 5, 0, "【レイジ・レギオン】\n…またいつか、\nみんなと…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 118101, 6007, 1, 0, 101, 0, "…一度でいいから、\nわたしもママって…\nあっ、その…\n…な、なんでもないわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 118101, 6008, 1, 0, 102, 0, "ここが、今のわたしに\nとっての帰る場所…\n…そう思っても、\nいいのかしら…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118101, 6009, 1, 0, 1, 0, "ええと…\nお腹、空いてる？\nおねむじゃない？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118101, 6010, 1, 0, 2, 0, "ここにいると\n邪魔かしら…？\nならもっと隅っこに…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118101, 6011, 1, 0, 3, 0, "お喋り…？\nわたしでいいの…？\nうれしい…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118101, 6012, 1, 0, 4, 0, "あなたの好きな歌…\n教えて…？\n練習、するから…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 118101, 6013, 1, 0, 101, 0, "ここは賑やかだけど…\nうるさいとは感じない\nむしろ、心地いい…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 118101, 6014, 1, 0, 102, 0, "安心、して…\nわたしの歌で、\nあなたを守るから…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118101, 6015, 1, 1, 1, 0, "ちょっとだけ…\n朝は、苦手…\n…ふぁ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118101, 6016, 1, 2, 2, 0, "お昼は、食べた？\n良かったら、一緒に…\n少し…焦げてるけど…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118101, 6017, 1, 3, 3, 0, "眠れないのかしら…\nじゃ、じゃあ…\n子守歌…聴く？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118101, 6019, 1, 0, 2, 0, "こんな特等席…\n恐縮、です…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118101, 6020, 1, 0, 3, 0, "い、今のうちに、\nこの子でお世話の\n練習を…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118101, 6021, 1, 0, 4, 0, "いい香り…\n…つらい気持ちも\n癒してくれる", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118101, 6022, 1, 0, 5, 0, "あったかい…\nいつまでも、\nこうしていられそう…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118101, 6024, 1, 0, 7, 0, "おいしく出来てる…\nすごいわ…\n見習わなくっちゃ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118101, 6025, 1, 0, 8, 0, "『赤ちゃんとママの\nスキンシップ入門』…\n…べ、勉強になるわ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121301, 6027, 1, 0, 1, 0, "きれいなお部屋…！\n立派な舞台の\nセットみたい…♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121301, 6028, 1, 0, 2, 0, "お芝居では私が先輩\nなんだから、しっかり\n私がミサキちゃんを\nエスコートしなきゃ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121301, 6029, 1, 0, 3, 0, "～～♪\n歌うお芝居も多いから\nもっと上手く\nなりたいな…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121301, 6030, 1, 0, 4, 0, "ここにいる人たちと\n仲良くなりたいな…\nそしたらきっと演技の\n幅も広げられるよね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121301, 6031, 1, 0, 5, 0, "機会があったら\nアヤネちゃんとも\nお芝居してみたいな♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 121301, 6032, 1, 0, 101, 0, "お兄ちゃんに褒めて\nもらえるように\nお芝居の練習\nいっぱいしなきゃ…♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 121301, 6033, 1, 0, 102, 0, "あう…お兄ちゃんを\n意識したら、台詞が\n飛んじゃったよぅ…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121301, 6034, 1, 0, 1, 0, "あっ、お兄ちゃん…♪\nお芝居の練習を\n見てくれませんか…？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121301, 6035, 1, 0, 2, 0, "いまの発声練習\nどうでしたか？\nひゃん…！？\n頭は撫でないでぇ～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121301, 6036, 1, 0, 3, 0, "ミサキちゃんも\nお芝居、とっても\n上達してるんですよ\n私も負けられません♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121301, 6037, 1, 0, 4, 0, "また物語を聞かせて\nほしい、ですか…？\n私なんかでよければ\nもちろんよろこんで♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 121301, 6038, 1, 0, 101, 0, "新しいお話を\n考えてみました\n聞いてくれますか…？\nよかった…♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 121301, 6039, 1, 0, 102, 0, "いつか…舞台上じゃ\nなくても、照れずに\nお兄ちゃんに気持ちを\n伝えてみせますね…♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121301, 6040, 1, 1, 1, 0, "おはようございます♪\nマヒルさんにもらった\nミルク、一緒に\n飲みませんか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121301, 6041, 1, 2, 2, 0, "牧場のみなさんに\nドングリパンを\nいただきました～\nおひとつどうぞ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121301, 6042, 1, 3, 3, 0, "寝る前にまたココアを\n飲みながら、お話を\n聞いてもらえますか？\nえへへ、うれしいな♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121301, 6044, 1, 0, 2, 0, "お芝居の練習の\nあとは、ここで\nお兄ちゃんと\nゆっくりしたいな♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121301, 6045, 1, 0, 3, 0, "今日も演技の\n練習に付き合ってね♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121301, 6046, 1, 0, 4, 0, "素敵な香り…\nこのイメージも\nお芝居に\n活かせないかな…？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121301, 6047, 1, 0, 5, 0, "私もお芝居で\nみんなの心を\nぽかぽか温められる\nようになりたいな…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121301, 6049, 1, 0, 7, 0, "舞台に立つんだから\n食べ過ぎないように\n気をつけないと。\nでも…おいしいなぁ♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121301, 6050, 1, 0, 8, 0, "このお話、面白い…\n脚本を書く\n参考にもなりそう…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 107501, 6052, 1, 0, 103, 0, "えへへ…♪\n二人で見た夕日…\nキレイでしたね…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 107501, 6053, 1, 0, 103, 0, "これから泳ぎの\n練習ですか？\n付き合いますよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 107601, 6054, 1, 0, 103, 0, "主さまを導くことで\nわたくしも未知なる\n領域へと辿り着けます", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 107601, 6055, 1, 0, 103, 0, "主さまとなら\n海よりも深く\n空よりも高い場所へも\n目指せそうです", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 107801, 6056, 1, 0, 103, 0, "あいつと二人で\n夏の遠い海に…って\nあたしってば\nなに考えてるのよ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 107801, 6057, 1, 0, 103, 0, "あたしが楽しく\n過ごせるのはあんたが\nいてくれたからよ\nか、感謝してるわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121401, 6058, 1, 0, 1, 0, "はぁ…アタシってば\nこんなにかわいくって\nキレイなんて罪よね～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121401, 6059, 1, 0, 2, 0, "舞台の衣装を\nクリーニング\nしとかなくちゃ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121401, 6060, 1, 0, 3, 0, "舞台映えするメイク…\nスズナなら\n知ってるかしら？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121401, 6061, 1, 0, 4, 0, "物産展の会計係と\n山盛りの算数の宿題…\nどっちが簡単かしら…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121401, 6062, 1, 0, 5, 0, "牧場と温泉の仕事に\nお芝居の練習に…\nちょ、ちょっと休憩～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 121401, 6063, 1, 0, 101, 0, "お客さんの視線も\nうれしいけど…\nあいつも見てて\nくれてるかしら？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 121401, 6064, 1, 0, 102, 0, "もっともっと\nセクシーになって\nあいつをビックリ\nさせてやるわ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121401, 6065, 1, 0, 1, 0, "アンタも休憩？\nしっかり働くために\n休むのも大事よね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121401, 6066, 1, 0, 2, 0, "ふふん♪　アタシも\n料理ができるように\nなったんだから！\n簡単なのだけだけど…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121401, 6067, 1, 0, 3, 0, "温泉ってお肌に\nいいのよね？\nもっとキレイに\nなったらどうしよう〜", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121401, 6068, 1, 0, 4, 0, "物産展の荷物は\nこれでよし…っと\nそれじゃアンタも\n運ぶの手伝ってよね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 121401, 6069, 1, 0, 101, 0, "いつもと違うアタシに\n見とれちゃった？\n仕方ないわね～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 121401, 6070, 1, 0, 102, 0, "お花見って風流で\n大人って感じ♪\n…でもお団子も\n忘れないでよね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121401, 6071, 1, 1, 1, 0, "みんなに朝ごはんを\n作ってみたら\n喜んでくれるかしら？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121401, 6072, 1, 2, 2, 0, "お客さんがいっぱい\n来る時間帯ね！\n元気出していくわ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121401, 6073, 1, 3, 3, 0, "そろそろ寝ないと…\n睡眠不足はお肌の\n大敵なんだから", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121401, 6075, 1, 0, 2, 0, "ふかふかしてて\n気持ちいい～♪\n疲れが癒されるぅ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121401, 6076, 1, 0, 3, 0, "この子もアタシと\nおそろいの衣装に\nしてあげようかしら♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121401, 6077, 1, 0, 4, 0, "素敵な衣装で\nいい香りを\n身にまとう…ふふ、\n大人のたしなみよね♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121401, 6078, 1, 0, 5, 0, "あったか～い♪\nここでお昼寝したら\n気持ちよさそう…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121401, 6080, 1, 0, 7, 0, "お仕事がんばると\nいつもよりお腹\n空いちゃう～！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121401, 6081, 1, 0, 8, 0, "最新のトレンド\n載ってないかしら？\n物産展の売り物に\n取り入れなくちゃ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121501, 6083, 1, 0, 1, 0, "怪盗のこと…\nお父さんお母さんに\nもし知られたら\nなんて説明しよう…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121501, 6084, 1, 0, 2, 0, "たとえ怪盗でも、人の\n家にお邪魔するときは\n身だしなみをしっかり\nしないと…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121501, 6085, 1, 0, 3, 0, "鍵開けって難しい…\nうーん、もっと\n練習しておかなくちゃ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121501, 6086, 1, 0, 4, 0, "姿が見えない\n怪盗の噂…？\nわ、私のことじゃ\nありませんように…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121501, 6087, 1, 0, 5, 0, "予告状って…\n切手はどこに貼れば\nいいんだろう…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 121501, 6088, 1, 0, 101, 0, "怪盗活動にも\n慣れてきたけど…\nやっぱり先輩が\nいないと心細いなぁ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 121501, 6089, 1, 0, 102, 0, "せ、先輩の心も\nいつか盗めるかな…？\n…わわわ、私ってば\nなに言ってるんだろ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121501, 6090, 1, 0, 1, 0, "怪盗が大活躍って\n新聞で読んだ…？\nだっ、誰のこと\nでしょうね…あはは", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121501, 6091, 1, 0, 2, 0, "尾行に潜入…\n存在感がないのって\n意外と役立つことも\nあるんですね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121501, 6092, 1, 0, 3, 0, "せせせ、先輩っ！\n帽子をぎゅーって\nかぶったら、抜けなく\nなっちゃいました…！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121501, 6093, 1, 0, 4, 0, "ぬ、盗み聞きなんて\nしてませんよ？\nみんなが私に気づかず\n話し始めるだけで…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 121501, 6094, 1, 0, 101, 0, "秘密道具が見たい？\nそ、それは内緒です！\n秘密道具ですから…\nで、でも先輩になら…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 121501, 6095, 1, 0, 102, 0, "えへへ\n見つかっちゃいました\n先輩はいつも私を\n見つけてくれますね…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121501, 6096, 1, 1, 1, 0, "おはようございまふ…\n秘密道具のお手入れで\n夜更かししちゃって…\nちょっと眠たいです～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121501, 6097, 1, 2, 2, 0, "怪盗が昼に活動する…\n案外そのほうが\n意外性が\nあるのかも…？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121501, 6098, 1, 3, 3, 0, "外が暗くなりましたね\n…そろそろ怪盗の活動\n始めましょうか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121501, 6100, 1, 0, 2, 0, "ここでくつろぐと\n疲れが取れます～\n…今日も怪盗\nがんばろっと", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121501, 6101, 1, 0, 3, 0, "わぁ、かわいい…！\n心を盗まれるって\nこういうことかな…？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121501, 6102, 1, 0, 4, 0, "素敵な香り…でも\n服に香りがついたら\n隠れてるときに\n見つかっちゃうかな…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121501, 6103, 1, 0, 5, 0, "ぽかぽかで\nいい気持ち…\n夜道を歩く前に\n温まっておこうっと…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121501, 6105, 1, 0, 7, 0, "怪盗活動に備えて\n今のうちにしっかり\n食べておかなきゃ…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121501, 6106, 1, 0, 8, 0, "女の子は秘密が\n多いほうが魅力的…？\nそ、そうなんだ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 104501, 6108, 1, 0, 103, 0, "ドＳさんはいつも\nクウカの妄想の中に…\nぐふふ、じゅるり～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 104501, 6109, 1, 0, 103, 0, "やさしい触り方も\n嫌いじゃありません…\nは…恥ずかしいですが", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121601, 6110, 1, 0, 1, 0, "この大怪盗さまに\n盗めないものなんて\nないです！　たぶん！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121601, 6111, 1, 0, 2, 0, "あたしにふさわしい\nビッグなお宝は\nどこにあるですかね～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121601, 6112, 1, 0, 3, 0, "そろーりそろーり…\n気配を消せばお菓子も\n食べ放題…にやり", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121601, 6113, 1, 0, 4, 0, "またサボってたのが\nバレたです…\nボスの地獄耳～！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121601, 6114, 1, 0, 5, 0, "ギャングで大怪盗…！\nこの肩書の多さ！\nまさに有能の証です！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 121601, 6115, 1, 0, 101, 0, "お兄さんだったら…\nお宝を山分けして\nあげてもいいですよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 121601, 6116, 1, 0, 102, 0, "お兄さんはアレですよ\n大怪盗イノリさまの\n相棒みたいなものです", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121601, 6117, 1, 0, 1, 0, "何事も用心深さが\n大切ですよ。ビ…\nビビりじゃないです！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121601, 6118, 1, 0, 2, 0, "ぬ、盗み聞き？\nしてないですよっ？\nぴゅーぴゅーぴゅー", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121601, 6119, 1, 0, 3, 0, "情報が欲しければ\nお菓子と交換ですよ\nさぁ、交換交換っ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121601, 6120, 1, 0, 4, 0, "しーっ！\n今、尾行中なんです\n話はあとで…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 121601, 6121, 1, 0, 101, 0, "お兄さんと話してると\n時間が速いような…？\nこの時間泥棒！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 121601, 6122, 1, 0, 102, 0, "ちょうどよかった！\nいい話があるんです\n一口乗りませんか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121601, 6123, 1, 1, 1, 0, "おてんとうさまが\n出てやがるですね…\n悪事がやりにくいです", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121601, 6124, 1, 2, 2, 0, "もうこんな時間ですか\nご飯にお昼寝…\nいやー忙しいです♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121601, 6125, 1, 3, 3, 0, "ふっふっふ！　夜こそ\n大怪盗の時間です！\n……ふぁあ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121601, 6127, 1, 0, 2, 0, "うーん…\n警備の目がないと\n気楽でいいですねぇ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121601, 6128, 1, 0, 3, 0, "くう…！\nこのかわいさは\n盗めないです…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121601, 6129, 1, 0, 4, 0, "犯行現場に残ったのは\n香水の匂いだけ…\nふふ、おしゃれ大怪盗", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121601, 6130, 1, 0, 5, 0, "意外とこういう場所に\n隠し扉があったり…\nしないです？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121601, 6132, 1, 0, 7, 0, "もぐもぐ…\nこの美味しさ…\nとんでもない罠ですね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121601, 6133, 1, 0, 8, 0, "むむむ…ちっとも\n分からないです…\nほとんど暗号ですよ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121701, 6135, 1, 0, 1, 0, "あら、サレンさんは\nどこに行かれた\nのかしら？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121701, 6136, 1, 0, 2, 0, "ふんふんふーん♪\nなんだか華やいだ\n気分ですわ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121701, 6137, 1, 0, 3, 0, "さて…次の事業は\nどうしましょう\n何か面白いことは…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121701, 6138, 1, 0, 4, 0, "おーっほっほっほ！\nあら…楽しくて、つい\n笑ってしまいましたわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121701, 6139, 1, 0, 5, 0, "センスがあって\nいいお部屋ですわね\n私も見習いたいですわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 121701, 6140, 1, 0, 101, 0, "サレンさんとあのお方\n仲がいいですわね\nも、もちろん仲がいい\nのはよいことですわ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 121701, 6141, 1, 0, 102, 0, "どんなときでも\n正々堂々！\nそれが私の\n誇りですわ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121701, 6142, 1, 0, 1, 0, "ふふ、ごきげんよう\n私、絶好調でしてよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121701, 6143, 1, 0, 2, 0, "どうかされましたか？\n何かお困りごと\nでしたら\n是非力になりますわよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121701, 6144, 1, 0, 3, 0, "たまにはこうして\nゆっくりと\nお話しするのも\nいいものですわね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121701, 6145, 1, 0, 4, 0, "次の事業に関して\n考え事をしてましたの\nよければ相談に乗って\nくださるかしら？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 121701, 6146, 1, 0, 101, 0, "よろしければ\nお話しませんこと？\nサレンさんのお話を\n聞かせてほしいですわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 121701, 6147, 1, 0, 102, 0, "ふふっ…\n先ほどサレンさんと\nあなたさまのお話を\nしていたところですわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121701, 6148, 1, 1, 1, 0, "さあ！\n今日も一日\n全力で参りますわ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121701, 6149, 1, 2, 2, 0, "お昼はどうしましょう\nサレンさんをお誘い\nしてみようかしら", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121701, 6150, 1, 3, 3, 0, "もう少しだけ…\n明日の準備をして\nおかないと…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121701, 6152, 1, 0, 2, 0, "はぁ～…いいですわね\nはっ、私としたことが\nはしたないですわね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121701, 6153, 1, 0, 3, 0, "かわいらしいですわ\nいくつになっても\nぎゅっと\nしたくなりますわね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121701, 6154, 1, 0, 4, 0, "落ち着きますわね\nサレンさんはどんな\n香りが好きなのかしら", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121701, 6155, 1, 0, 5, 0, "ふふっ、燃え盛る\n情熱の炎…\nいいですわね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121701, 6157, 1, 0, 7, 0, "このおいしさで\n元手がゼロ…\nすばらしい\n商品ですわ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121701, 6158, 1, 0, 8, 0, "これは…ふむふむ\n新しい事業のヒントに\nなりそうですわ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121801, 6161, 1, 0, 1, 0, "さて、と…\n今日は何をしなくちゃ\nいけないんだっけ？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121801, 6162, 1, 0, 2, 0, "やることは山積み\nだけど、手を動かす\nのは嫌いじゃないわ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121801, 6163, 1, 0, 3, 0, "あら？　アキノさんの\n声が聞こえたような\n気がしたんだけど…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121801, 6164, 1, 0, 4, 0, "アキノさんといると\nついこっちまで笑顔に\nなっちゃうのよね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121801, 6165, 1, 0, 5, 0, "弱音なんか\nはいてられないわ\nアキノさんに\n笑われちゃうもの", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 121801, 6166, 1, 0, 101, 0, "気が付けばアキノさん\nのペースだけど…\n不思議と嫌では\nないのよね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 121801, 6167, 1, 0, 102, 0, "あたしを好敵手って\n言ってくれる\nアキノさんのためにも\nしっかりしなくちゃ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121801, 6168, 1, 0, 1, 0, "どうしたの？\nアキノさんなら\nさっきそこに\nいなかった？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121801, 6169, 1, 0, 2, 0, "あたしなら大丈夫\n救護院だって\nみんなが助けて\nくれるからね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121801, 6170, 1, 0, 3, 0, "さっきアキノさんが\nあんたを探してたわよ\nなにかしたの？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121801, 6171, 1, 0, 4, 0, "こう見えてヒマじゃ\nないんだけど…\n少しくらいなら\n付き合ってあげるわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 121801, 6172, 1, 0, 101, 0, "ねえ、あんたと\nアキノさんって…\nううん、やっぱり\nなんでもないわ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 121801, 6173, 1, 0, 102, 0, "アキノさんも誘って\nお茶でもしない？\n話したいことが\nたくさんあるの♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121801, 6174, 1, 1, 1, 0, "早起きは三文の得よ\nいい一日を迎えるため\n目を覚まさなきゃ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121801, 6175, 1, 2, 2, 0, "今日のお昼は…\nアキノさんと同席する\n予定だったわね\nあんたもどう？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121801, 6176, 1, 3, 3, 0, "ふぁ…っと、だめね\nたるんだところを\nアキノさんに\n見せるわけには", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121801, 6178, 1, 0, 2, 0, "ん～…\n落ち着くわね\nこのまま\n寝ちゃいそう…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121801, 6179, 1, 0, 3, 0, "救護院の皆も\nぬいぐるみが\n大好きなのよ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121801, 6180, 1, 0, 4, 0, "いい香りね…\nこれはアキノさんの\n趣味かしら？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121801, 6181, 1, 0, 5, 0, "こんなに激しく\n燃えているのに\nあったかくて落ち着く\nまるでアキノさんね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121801, 6183, 1, 0, 7, 0, "どんどん出てくる…\nこれ、救護院にも\nほしいわね…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121801, 6184, 1, 0, 8, 0, "ふむふむ…\n前にアキノさんが\n言ってたのは\nこのことかしら？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122001, 6187, 1, 0, 1, 0, "この衣装、私には\nちょっとかわい過ぎて\n恥ずかしいな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122001, 6188, 1, 0, 2, 0, "一部の人からは\n海賊より海賊らしいと\n評判の父です", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122001, 6189, 1, 0, 3, 0, "潮風で錆びないよう\n武器の手入れを\nしておきましょうか", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122001, 6190, 1, 0, 4, 0, "宝探しのお手伝いなら\n力になれると思います\n父のやる気次第ですが", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122001, 6191, 1, 0, 5, 0, "除霊は気を遣います\n一歩間違えると父まで\n成仏させてしまうので", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 122001, 6192, 1, 0, 101, 0, "あの人と見た\n海の景色を…ずっと\n忘れずにいたいな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 122001, 6193, 1, 0, 102, 0, "幽霊になっても\n一目会えるなら…\n私はきっとあの人に…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122001, 6194, 1, 0, 1, 0, "ボートを漕ぐの、少し\n上手になったんですよ\n今度お見せしますね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122001, 6195, 1, 0, 2, 0, "航海に向けた\n買い出しが必要ですね\n予定は空いていますか", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122001, 6196, 1, 0, 3, 0, "呪われた財宝の\n解呪依頼が多くて…\nふぅ…えっと次は…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122001, 6197, 1, 0, 4, 0, "夜の航海では\n父が明かり代わりに\nなってくれるんです", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 122001, 6198, 1, 0, 101, 0, "船の上では食材が\n限られるので料理の\nいい練習になります", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 122001, 6199, 1, 0, 102, 0, "この先もずっと\nあなたと一緒に\nたくさんの思い出を\n作っていきたいです", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122001, 6200, 1, 1, 1, 0, "あの人の寝顔…\nもう少しだけ\n眺めていたいな…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122001, 6201, 1, 2, 2, 0, "船の上では\n規則正しい生活が\n重要ですから", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122001, 6202, 1, 3, 3, 0, "夜の海は幽霊が\n見分けやすいんです\n青白く光るので", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122001, 6204, 1, 0, 2, 0, "海に揺られるような\n心地よさですね\n気が休まります…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122001, 6205, 1, 0, 3, 0, "あなたも一緒に\n海を見に行きますか？\n…なんて。ふふっ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122001, 6206, 1, 0, 4, 0, "爽やかな香りですね…\n一面の青い海が\n目に浮かぶようです", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122001, 6207, 1, 0, 5, 0, "温かい…\n潮風で冷えた体に\n沁みますね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122001, 6209, 1, 0, 7, 0, "あ、おいしい…\n日持ちするなら船旅に\n持っていこうかな…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122001, 6210, 1, 0, 8, 0, "アンナさんに教えて\nもらった小説は…\nあ、これですね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 102601, 6212, 1, 0, 103, 0, "なにもしないを\nしてるんだよ\nあ～いそがしいそがし", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 102601, 6213, 1, 0, 103, 0, "はぁ～あんたといると\nどこまでも気持ちよく\nダラダラできるね～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121901, 6214, 1, 0, 1, 0, "暗黒騎士にして、\n伝説の海賊でもある\n私に不可能はないっ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121901, 6215, 1, 0, 2, 0, "海賊団の船員、\n随時募集中です…と\nこれで新たな盟友が\n増えるはずだ…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121901, 6216, 1, 0, 3, 0, "肩に乗せるペットが\nほしいな。今のうちに\nペット用品を買いに\n行かなくては…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121901, 6217, 1, 0, 4, 0, "財宝の地図はどこで\n売っているのだろう…\n古本屋？　古物商？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121901, 6218, 1, 0, 5, 0, "この海賊衣装に\n身を包んでいると\n前世の新たな記憶が\nよみがえってくるな！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 121901, 6219, 1, 0, 101, 0, "いずれはシグルドと\n海原への冒険に\n繰り出したいな…\nきっと楽しいぞ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 121901, 6220, 1, 0, 102, 0, "待ちわびたぞ盟友よ！\n私と貴様あっての\n海賊団…だからその…\n今日はそばにいてくれ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121901, 6221, 1, 0, 1, 0, "シグルド！　港町で\nお魚のおすそ分けを\nもらったんだ！\n一緒に食べよう！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121901, 6222, 1, 0, 2, 0, "シグルドもこのコート\n羽織ってみないか？\nふふっ、きっとすごく\n似合うと思うぞ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121901, 6223, 1, 0, 3, 0, "海賊船の新たな\n秘密兵器を作ったぞ！\nシグルド、これから\n見に来ないか！？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121901, 6224, 1, 0, 4, 0, "私は伝説の海賊だから\n泳ぐのだって得意だ！\nふ、深いところは\n少し怖いが…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 121901, 6225, 1, 0, 101, 0, "ふふ、いつ無人島に\n漂着してもいいように\n非常食は準備済みだ\nシグルドの分もある！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 121901, 6226, 1, 0, 102, 0, "冥風戦記の新章が\n完成したぞ…！\nシグルド、まずは\n貴様に読んでほしい！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121901, 6227, 1, 1, 1, 0, "ふっ、天気明朗\n絶好の航海日和だ！\n早速海に繰り出そう！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121901, 6228, 1, 2, 2, 0, "昼食用に魚を\n釣っておいたぞ！\nふふ、いかにも\n海賊っぽいだろう？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121901, 6229, 1, 3, 3, 0, "夜の海は真っ黒で\n少しだけ怖いな…\nシグルド、私の\nそばを離れるなよ…！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121901, 6231, 1, 0, 2, 0, "大海賊がくつろぐのに\n相応しい逸品だ…！\nえっと…これ、どこの\nお店で買えますか？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121901, 6232, 1, 0, 3, 0, "わわ、かわいい…！\n船室のベッドに\n置いておきたいな…\n連れてってはダメか？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121901, 6233, 1, 0, 4, 0, "おぉ、この香りは…！\n集中力が上がりそうだ\n海賊船の船長室に\nぜひ置きたいな！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121901, 6234, 1, 0, 5, 0, "これが船乗りの\n伝承に聞く、海原を\nさまよう神秘の火か！\n…暖まるな！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121901, 6236, 1, 0, 7, 0, "腹が減っては\n海賊はできぬ！\n…小説も書けぬ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 121901, 6237, 1, 0, 8, 0, "女海賊と騎士はその後\n恋に落ち…？　恋愛\nモノではないかっ！？\nき、興味深くはあるが", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122101, 6239, 1, 0, 1, 0, "いつもの山籠もりが\nブームになるなんて…\n人生、なにが起きるか\n分からないなぁ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122101, 6240, 1, 0, 2, 0, "キャンプ道具…\nいえ、キャンプギア！\nカッコいい響き…♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122101, 6241, 1, 0, 3, 0, "レッツ、ＢＢキャン！\nこのフレーズ\n流行るかも…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122101, 6242, 1, 0, 4, 0, "キャンプ仲間…\n募集してみようかな？\n友達も兼任で…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122101, 6243, 1, 0, 5, 0, "ユカリさんと\nジュンさん…元気かな\nまた一緒に\nキャンプしたいなぁ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 122101, 6244, 1, 0, 101, 0, "私の取材が載った本…\n【フォレスティエ】に\n送らなくちゃ…♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 122101, 6245, 1, 0, 102, 0, "もし、あの人が\n落ち込むことが\nあったら…その時は\n私が力になりたいな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122101, 6246, 1, 0, 1, 0, "ブッシュクラフトは\nマイフレンドくんの\nカスタムにも\n活かせる技術です！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122101, 6247, 1, 0, 2, 0, "会話に花が咲く…\nって言いますよね\n私の場合、会話相手が\nお花そのものですけど", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122101, 6248, 1, 0, 3, 0, "この服、実は…\n昔ミサト先生に\nこしらえてもらった\n物なんです！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122101, 6249, 1, 0, 4, 0, "昔はやまびこで会話の\n練習をしてたんです\nほとんどオウム返しで\n気まずかったなぁ～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 122101, 6250, 1, 0, 101, 0, "どうもマイスターです\n…あ、すみません\nみなさんの期待に\n応えようと練習中で…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 122101, 6251, 1, 0, 102, 0, "たとえぼっちを\n卒業しても…\n私は一生ＢＢ団です！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122101, 6252, 1, 1, 1, 0, "小鳥さんのさえずりで\n目が覚める…\n心地いいものですね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122101, 6253, 1, 2, 2, 0, "キャンプといえば\nおいしいご飯です！\n腕を振るいますよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122101, 6254, 1, 3, 3, 0, "すっかり夜ですね…\nおやすみの前に\n星でも見ませんか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122101, 6256, 1, 0, 2, 0, "都会でくつろぎ…\n自然の中でくつろぐ…\nくつろぎ無限ループ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122101, 6257, 1, 0, 3, 0, "しっかり観察すれば\nブッシュクラフトで\n再現できるかも…？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122101, 6258, 1, 0, 4, 0, "はぁ～…やっぱり\n木の香りを嗅ぐと\n肩の力が抜けますねぇ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122101, 6259, 1, 0, 5, 0, "ああ…あったまります\nなんだか心に\nゆとりがでますね…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122101, 6261, 1, 0, 7, 0, "このおいしさ…！\n今度はキャンプに\n持っていこうかな…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122101, 6262, 1, 0, 8, 0, "ふむふむ…\nいろんな知識があると\nキャンプはもっと\n楽しくなりますよね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122201, 6264, 1, 0, 1, 0, "部屋がキャンプ用品で\n埋まっていく…\nキャンプ…なんて\n恐ろしい趣味なの…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122201, 6265, 1, 0, 2, 0, "仕事でも\nアウトドアでも\n頼れるお姉さんに\nなれたらいいなぁ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122201, 6266, 1, 0, 3, 0, "この服、かわいい上に\n本当に快適ね～♪\nおしゃれにキャンプを\n楽しめて最高だわ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122201, 6267, 1, 0, 4, 0, "ん〜次はどんな\nキャンプ飯を\n作ろうかしら？\n彼の好物は、っと…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122201, 6268, 1, 0, 5, 0, "早く週末が\n来ないかしら…\n仕事を忘れてパーっと\nキャンプ飲みを…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 122201, 6269, 1, 0, 101, 0, "新しいキャンプ飯の\nレシピを覚えたわ！\n彼も喜んで\nくれるはず…よね♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 122201, 6270, 1, 0, 102, 0, "彼の分の食器も\nこっそり買い揃えちゃ\nおうかな…そ、それは\nさすがにまだ早いか…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122201, 6271, 1, 0, 1, 0, "今度、財団主催で\n大規模なキャンプを\nやってみるのも\nいいかもしれないわね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122201, 6272, 1, 0, 2, 0, "ソロキャンプも\n悪くはないけど\n私はやっぱりキミと…\nな、なんでもない！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122201, 6273, 1, 0, 3, 0, "キャンプのことなら\n私に聞いて。なんでも\n教えてあげるから！\n…調べた上でね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122201, 6274, 1, 0, 4, 0, "キャンプ場で飲む\n最高の一杯は\n他じゃ味わえないわ！\nさあ、乾杯っ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 122201, 6275, 1, 0, 101, 0, "うん、決めた！　次は\n禁しゅわキャンプ！\nだってキミとゆっくり\n話したいんだもん♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 122201, 6276, 1, 0, 102, 0, "キミのおかげで\n本当の最高の一杯が\n味わえたわ…\nありがとう♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122201, 6277, 1, 1, 1, 0, "んー、気持ちいい！\nキャンプが趣味に\nなってから、寝覚めが\nよくなったわ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122201, 6278, 1, 2, 2, 0, "お昼は私がごちそう\nするわ。キャンプ飯で\n鍛えた料理の腕前を\n披露しちゃうわよ～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122201, 6279, 1, 3, 3, 0, "そろそろ寝ましょ？\n明日も朝日を眺める\nためにね。もちろん\nコーヒー付きで♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122201, 6281, 1, 0, 2, 0, "くつろぐのに\n持ってこいね～\nテントの中に置いても\nよさそうだわ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122201, 6282, 1, 0, 3, 0, "ふふ、かわいいわね～\n…今度キャンプ場に\n連れていこうかしら？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122201, 6283, 1, 0, 4, 0, "うん、いい匂い…♪\nキャンプ場を思い出す\n自然の香りだわ〜", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122201, 6284, 1, 0, 5, 0, "はぁ、温まるぅ～\n冬のキャンプで\n使ってみたいわね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122201, 6286, 1, 0, 7, 0, "おいしい～♪\nキャンプ飲みの\nおつまみに\nぴったりね！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122201, 6287, 1, 0, 8, 0, "へえ…こんなキャンプ\n飯もあるんだ？\n彼、好きだったり\nするかな…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 100701, 6289, 1, 0, 103, 0, "一生ついててやるの\n幽霊の一生は\nなっが～～～いの！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 100701, 6290, 1, 0, 103, 0, "む～…あんまり触ると\nすり抜けてやるの！\nウソなの。むふふ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122301, 6291, 1, 0, 1, 0, "んー…\n今日はベスちゃんと\nなにして遊ぼーかなー", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122301, 6292, 1, 0, 2, 0, "ヴァンピィちゃんも\nけんぞくの友だちと\n仲良くなりたいな♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122301, 6293, 1, 0, 3, 0, "けんぞくのお部屋は\nヴァンピィちゃんが\n守ってあげなくちゃ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122301, 6294, 1, 0, 4, 0, "お掃除するなら\nヴァンピィちゃんも\n手伝ってあげよー♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122301, 6295, 1, 0, 5, 0, "ふーん、ふーん♪\nけんぞくってこーゆー\nお部屋が好きなんだー", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 122301, 6296, 1, 0, 101, 0, "ひまだなー\nけんぞくをさそって\nお散歩いこっかな♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 122301, 6297, 1, 0, 102, 0, "今日のスープに\nけんぞくの大好物を\n入れてあげよっと♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122301, 6298, 1, 0, 1, 0, "けんぞくー！\nヴァンピィちゃんと\nお出かけしよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122301, 6299, 1, 0, 2, 0, "よしよし！\n今日のけんぞくも\nいい子だね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122301, 6300, 1, 0, 3, 0, "んんー…\nやっぱりけんぞくと\nベスちゃんは似てる？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122301, 6301, 1, 0, 4, 0, "いーい？\nヴァンピィちゃんの\nいうこと聞くのよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 122301, 6302, 1, 0, 101, 0, "ヴァンピィちゃんの\nけんぞくだもん！\n大切にするからね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 122301, 6303, 1, 0, 102, 0, "えへへっ！\nやっぱりけんぞくって\nやさしー匂いがする♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122301, 6304, 1, 1, 1, 0, "おはよー…\nヴァンピィちゃんは\nもうちょっと眠るねー", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122301, 6305, 1, 2, 2, 0, "ヴァンピィちゃん\nひなたぼっこ\nだーいすき！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122301, 6306, 1, 3, 3, 0, "んー！　夜だ♪\nヴァンピィちゃん\n絶好調だよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122301, 6308, 1, 0, 2, 0, "わー！\nふかふかだ！\nきもちぃー♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122301, 6309, 1, 0, 3, 0, "かわいー♪\nベスちゃんに負けない\n抱き心地！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122301, 6310, 1, 0, 4, 0, "この香り…なんだろ？\nヴァンピィちゃん\n気に入っちゃった♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122301, 6311, 1, 0, 5, 0, "あったかいねー…\nヴァンピィちゃん\nずっとここにいるー…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122301, 6313, 1, 0, 7, 0, "はむはむ…\nんー！\nおいしー♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122301, 6314, 1, 0, 8, 0, "ヴァンピィちゃん\nこの本\n好きなんだよねー♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122501, 6316, 1, 0, 1, 0, "夏といえば\nかき氷だよね\n近くにお店は…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122501, 6317, 1, 0, 2, 0, "この格好でいるのも\nなじんできたし\nちょっと鍛錬でも…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122501, 6318, 1, 0, 3, 0, "動きやすいけれど…\nいつもと違う服装は\n少し落ち着かないな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122501, 6319, 1, 0, 4, 0, "夏の空気に\n当てられると思わず\n気が緩んでしまうね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122501, 6320, 1, 0, 5, 0, "砂浜で走ると\nいい具合に体幹を\n鍛えられそうだ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 122501, 6321, 1, 0, 101, 0, "終わらない夏は\nないけれど…\nつい願ってしまうな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 122501, 6322, 1, 0, 102, 0, "胸を張って彼と\n並び立てるよう\n私も、もっと強く…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122501, 6323, 1, 0, 1, 0, "どうしたの？\n行きたい場所が\nあるなら付き合おうか", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122501, 6324, 1, 0, 2, 0, "快晴で風は穏やか…\n絶好の釣り日和だね\nさあ、出かけようか", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122501, 6325, 1, 0, 3, 0, "今日の予定を\n立ててるんだけど…\nキミの希望も\n聞きたいな", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122501, 6326, 1, 0, 4, 0, "ビーチスポーツか…\nキミとなら\nいい勝負ができそうだ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 122501, 6327, 1, 0, 101, 0, "私たちの夏に\n遠慮は無用だよ\n思いきり楽しもう！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 122501, 6328, 1, 0, 102, 0, "いつかまた一緒に\n花火を見に行こう\n…約束だよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122501, 6329, 1, 1, 1, 0, "潮騒の音で\n目覚めるのも\nいいものだね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122501, 6330, 1, 2, 2, 0, "今朝釣ってきた魚が\nあるんだ。これで\nお昼ご飯にしようか", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122501, 6331, 1, 3, 3, 0, "夜にもイベントが\nあるみたいなんだ\n一緒に見に行かない？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122501, 6333, 1, 0, 2, 0, "気持ちが安らぐね…\nうっかり眠って…\nしまい…そうだ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122501, 6334, 1, 0, 3, 0, "肌触りのよさが\nこの格好だと\nよく分かるよ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122501, 6335, 1, 0, 4, 0, "海をイメージさせる\n香りだね…\n爽やかで落ち着くよ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122501, 6336, 1, 0, 5, 0, "濡れたままだと\n風邪を引いてしまうよ\nほら、キミもおいで", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122501, 6338, 1, 0, 7, 0, "夏を楽しみきるには\nエネルギーが\n必要だからね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122501, 6339, 1, 0, 8, 0, "大切な人と過ごせる\n観光スポット紹介…\n…こ、このページは\nもういいかな！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122501, 6341, 1, 0, 10, 0, "濡らしてもいいなら\n海に持っていって\n乗るのもよさそうだ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122501, 6342, 1, 0, 11, 0, "こういう名画を\n見た気がするな…\nこんな視点なんだね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122401, 6343, 1, 0, 1, 0, "水着って\n動きやすくて\nいいよねー♪\nいっちにーさんしー♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122401, 6344, 1, 0, 2, 0, "ユイちゃんにもらった\n日焼け止め\n塗っておいて\nよかった～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122401, 6345, 1, 0, 3, 0, "なんだか暑いなあ…\nせっかくだし\nプールで泳いで\nこようかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122401, 6346, 1, 0, 4, 0, "この格好で\n出かけようとしたら\nレイさんに\n怒られちゃった…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122401, 6347, 1, 0, 5, 0, "たとえ水着姿でも\n困ってる人を見たら\n助けに行くからね！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 122401, 6348, 1, 0, 101, 0, "バカンス楽しかった～\nまた騎士くんと\n一緒に行きたいな♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 122401, 6349, 1, 0, 102, 0, "この水着、あたしに\n似合ってるかなあ？\n騎士くんはどう\n思ってるんだろ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122401, 6350, 1, 0, 1, 0, "騎士くん！\n海に行くなら\n準備運動\n忘れずにね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122401, 6351, 1, 0, 2, 0, "泳ぐ練習がしたいの？\nいいよ！　水着だから\nなにかあったらすぐに\n助けてあげるね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122401, 6352, 1, 0, 3, 0, "騎士くんの水着姿も\nかっこよかったよ！\nまた遊びに行こ！\n約束ね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122401, 6353, 1, 0, 4, 0, "このグローブの向日葵\nかわいいでしょ？\nユイちゃんが\n選んでくれたんだ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 122401, 6354, 1, 0, 101, 0, "実はこの水着\nいつも着てる\n服とおなじなんだ…\nって、冗談だよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 122401, 6355, 1, 0, 102, 0, "変な日焼けしてないか\n見てもらってもいい？\n…なし！　今のなし！\nやっぱりなしー！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122401, 6356, 1, 1, 1, 0, "夏のお日様に\n負けないよう\n今日も元気いっぱい\nがんばるぞー！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122401, 6357, 1, 2, 2, 0, "お腹減ってきちゃった\n騎士くんはお腹\n空いてない？\n…聞こえちゃった♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122401, 6358, 1, 3, 3, 0, "今日も一日\n楽しかったー！\n明日もまた\n楽しもうね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122401, 6360, 1, 0, 2, 0, "ふわあ…\nふわふわなのが\nいつもより\nよくわかるよー…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122401, 6361, 1, 0, 3, 0, "そうだ！　水着なら\n濡れてもいいし\n今度洗ってあげるね♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122401, 6362, 1, 0, 4, 0, "んー…いい匂い♪\nなんだか\n身体がぽかぽか\nしてきちゃった！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122401, 6363, 1, 0, 5, 0, "水着だから、えっと…\n暖か涼しい…！\n不思議な感じー", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122401, 6365, 1, 0, 7, 0, "思いっきり身体を\n動かした後だから\nいくらでも\n食べちゃえるよー！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122401, 6366, 1, 0, 8, 0, "ふむふむ…\nなるほどー\n水着の種類って\nそんなにあるんだぁ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122401, 6368, 1, 0, 10, 0, "乗り心地バッチリだね\nしゅっぱつしんこー！\nなんちゃって♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122401, 6369, 1, 0, 11, 0, "わっ、ぽよんぽよん！\nトランポリンみたいで\nた～のし～！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 101501, 6370, 1, 0, 103, 0, "彼のことを考えてると\n幸せで胸が温かくなる\n…私ったら、もう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 101501, 6371, 1, 0, 103, 0, "うふふふふ\nくすぐったいわ～♪\nもう、イタズラっ子ね\n次は先生の番よ～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122601, 6372, 1, 0, 1, 0, "騎士クンの汗\n拭いてあげたいな…\n余計なお世話かな…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122601, 6373, 1, 0, 2, 0, "夏バテ気味かも…\nそうだ、今夜は\nかば焼きにしよう♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122601, 6374, 1, 0, 3, 0, "青い空、白い雲\n眩しい日差し…！\n夏って好きだな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122601, 6375, 1, 0, 4, 0, "ヒリヒリする…？\n日に焼けちゃったかな\nふー…ふー…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122601, 6376, 1, 0, 5, 0, "花火大会があるんだ…\n夜、浜辺…2人きり…\nむ、無理だよ〜！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 122601, 6377, 1, 0, 101, 0, "水着って不思議…\n引っ込み思案が少し\nおさまる…気がする", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 122601, 6378, 1, 0, 102, 0, "騎士クンとまた\n海に行きたいな…\nまた、一緒に…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122601, 6379, 1, 0, 1, 0, "ダ、ダイエット！？\nしてないよ！？\n元々これ位だよ…？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122601, 6380, 1, 0, 2, 0, "スイカ割りのコツ？\nわたしは、騎士クンに\n教わりたいな…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122601, 6381, 1, 0, 3, 0, "騎士クン。うちわで\n扇いであげるね\nどこに置いたかな…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122601, 6382, 1, 0, 4, 0, "うぅ…暑いね…\n騎士クン、温度計は\n見ない方がいいよ～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 122601, 6383, 1, 0, 101, 0, "じ、じっと見ないで…\nええっと…ちょっと！\nちょっとならいいよ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 122601, 6384, 1, 0, 102, 0, "え、肝試し？\nう〜ん、うう〜ん…\n…騎士クンとなら！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122601, 6385, 1, 1, 1, 0, "おはよう騎士クン\n見て、あっちの空\nもう入道雲が出てる", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122601, 6386, 1, 2, 2, 0, "夕立、来るかなぁ…\nもし雷が鳴ったら\nそばにいてくれる…？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122601, 6387, 1, 3, 3, 0, "しー。…ほら、虫の声\n…わ、わたしの心臓の\n音は気にしないで…！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122601, 6389, 1, 0, 2, 0, "ふふ、水着でゴロゴロ\nするのってなんだか…\nいけないことみたい", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122601, 6390, 1, 0, 3, 0, "かわいい～♪\n日光浴させて\nあげたいな♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122601, 6391, 1, 0, 4, 0, "ほんのり潮の香りが…\n香ばしくて塩辛くて…\nあ…おなか鳴っちゃう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122601, 6392, 1, 0, 5, 0, "水着、乾かせるかも…\nそうだ、みんなの分も\n持ってこよう！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122601, 6394, 1, 0, 7, 0, "夏バテなんてしてたら\nみんなの足を\n引っ張っちゃうよね\nいただきます！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122601, 6395, 1, 0, 8, 0, "騎士クンの、どうして\n海はしょっぱいの？\nって質問の答えは…\n分かんないよぉ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122601, 6397, 1, 0, 10, 0, "顔は怒ってるのに\nなんだかかわいい…\n誰かに似てる…？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122601, 6398, 1, 0, 11, 0, "これを枕に、ビーチで\n騎士クンと…なんて…\nなんて…なんて…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122901, 6399, 1, 0, 1, 0, "夏休み、\n毎日たのしいな～♪\nつぎはなにしよう？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122901, 6400, 1, 0, 2, 0, "自由研究、\nなににしよっかな～？\nプチグリフォンさんの\nかんさつにっきとか？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122901, 6401, 1, 0, 3, 0, "夏休みって、\n給食ないから…\nそれはちょっぴり\nさみしいかも…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122901, 6402, 1, 0, 4, 0, "砂浜をはだしで歩くの\nきもちいいよね♪\nくすぐったいけど\nミミ、好きなんだ～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122901, 6403, 1, 0, 5, 0, "きれいな貝がら\n見つけちゃった♪\nミミのたからもの！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 122901, 6404, 1, 0, 101, 0, "ミミ、浴衣を\n買ってもらったんだ♪\n今度、おにいちゃんに\n見せてあげたいな！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 122901, 6405, 1, 0, 102, 0, "ミミ、おにいちゃんと\nいっぱい夏休みの\n思い出つくりたいな♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122901, 6406, 1, 0, 1, 0, "ねえ、おにいちゃん…\n夏休みの宿題、\nわかんないとこ\nおしえてくれる…？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122901, 6407, 1, 0, 2, 0, "おかあさんにね、\n麦茶つくるの上手って\nほめられたんだ～♪\n飲んでみて♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122901, 6408, 1, 0, 3, 0, "夏休みの宿題で\nポスターかくんだ～♪\nおにいちゃんを\nかいてもいい？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122901, 6409, 1, 0, 4, 0, "線香花火やりたいな♪\nミミ、長持ちさせるの\n上手なんだよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 122901, 6410, 1, 0, 101, 0, "おにいちゃんの\n好きな本ってなあに？\nその本で読書感想文\n書こうかな♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 122901, 6411, 1, 0, 102, 0, "海であそんだ後ね、\nおにいちゃんに\n髪を拭いてもらうの\nすきなんだ～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122901, 6412, 1, 1, 1, 0, "夏休みって、\nワクワクして\n早起きしちゃうよね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122901, 6413, 1, 2, 2, 0, "わぁ♪　お昼は、\n流しそうめんなんだ？\nミミ、流してみたい♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122901, 6414, 1, 3, 3, 0, "こわい話聞いて、\nねむれないの…\nねえ、おにいちゃん、\nいっしょに寝ていい？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122901, 6416, 1, 0, 2, 0, "ちょっぴりやすんだら\nまた、いっしょに\n海であそぼうね♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122901, 6417, 1, 0, 3, 0, "すっごくかわいいね♪\nこの子のこと、\n絵日記にかこうっと♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122901, 6418, 1, 0, 4, 0, "くんくん…\nスイカのにおいだね♪\nこのにおいだいすき！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122901, 6419, 1, 0, 5, 0, "およいだあとは、\nちゃんと体を\nあっためなくちゃね！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122901, 6421, 1, 0, 7, 0, "夏休みのご飯って\nなんだかとくべつ\nおいしい気がするね♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122901, 6422, 1, 0, 8, 0, "夏の星座って\nすっごくきれいだね♪\nいつかみんなと\nいっしょに見たいな♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122901, 6424, 1, 0, 10, 0, "わぁ♪　この子、\nとってもかわいいね！\nおともだちに\nなりたいな♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122901, 6425, 1, 0, 11, 0, "ミミ、人魚姫に\nなったみたい！\nおにいちゃん、\nとなりにすわって♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122801, 6426, 1, 0, 1, 0, "水着姿になると\nバカンス気分を\n味わえちゃうね！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122801, 6427, 1, 0, 2, 0, "海に行くの待ちきれ\nなくて、もう水着に\n着替えちゃった！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122801, 6428, 1, 0, 3, 0, "海に行ったら\nずぶぬれ系のイタズラ\nたくさんしかけよう！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122801, 6429, 1, 0, 4, 0, "部屋の中で泳ぎの練習\nしよう！　にいちゃん\nも水着に着替えて！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122801, 6430, 1, 0, 5, 0, "お日さまとお揃いの\n水着なんだ～♪\n似合ってるかなぁ？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 122801, 6431, 1, 0, 101, 0, "海といえば冒険！\n海底に眠る財宝を\n見つけちゃおう！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 122801, 6432, 1, 0, 102, 0, "にいちゃんと一緒だと\n部屋にいても海気分♪\nスイカわりしよっか！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122801, 6433, 1, 0, 1, 0, "にいちゃん！\n早く海に行く準備\nしなよ～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122801, 6434, 1, 0, 2, 0, "ねーにいちゃん\n何秒息止められるか\n勝負しよーよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122801, 6435, 1, 0, 3, 0, "にいちゃんはアイスと\nかき氷どっちが好き？\nミソギは…どっちも！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122801, 6436, 1, 0, 4, 0, "日焼け止めクリーム\nキョウカに\nもらったんだ～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 122801, 6437, 1, 0, 101, 0, "水中メガネがあれば\n水鉄砲くらっても\n平気だね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 122801, 6438, 1, 0, 102, 0, "水着なら、毎日夏休み\n気分！　にいちゃんも\n着てみなよ～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122801, 6439, 1, 1, 1, 0, "にいちゃんおはよー！\n早く顔洗って、\nいっぱい遊んじゃお！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122801, 6440, 1, 2, 2, 0, "にいちゃんデザートは\nイチゴのかき氷だよ！\n…激辛ってバレた？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122801, 6441, 1, 3, 3, 0, "今日は楽しかった～！\n明日もイタズラ日和に\nなるといいな！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122801, 6443, 1, 0, 2, 0, "落とし穴の下に\nやわらかいの置けば\nケガしないよね！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122801, 6444, 1, 0, 3, 0, "かわいいね～！\nこの子も海に\n連れてっちゃおうよ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122801, 6445, 1, 0, 4, 0, "このにおいを利用して\nイタズラするには…\nにいちゃんどう思う？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122801, 6446, 1, 0, 5, 0, "水着を着て暖炉で\nあったまるのって、\n変でおもしろいね～！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122801, 6448, 1, 0, 7, 0, "海辺で食べる\nお昼ごはんって最高♪\nってことで海行こう！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122801, 6449, 1, 0, 8, 0, "いつか海を越えて、\n地図にも載ってない\n場所を見つけたいな！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122801, 6451, 1, 0, 10, 0, "この口から水が出たら\nみんな驚くよね！\nぷぷ…細工しちゃお！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122801, 6452, 1, 0, 11, 0, "変な音がするイタズラ\nを仕込んどいて…\n誰か座らないかな～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 102701, 6453, 1, 0, 103, 0, "あなた様と私の愛を\n周りに見せつけて\n差し上げましょう♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 102701, 6454, 1, 0, 103, 0, "呼びましたか？\n呼びましたね？\nでは愛を語りましょう\nそうしましょう", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122701, 6455, 1, 0, 1, 0, "水着で歩き回るのは\n恥ずかしいな…\n誰も見てないよね？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122701, 6456, 1, 0, 2, 0, "夏はやっぱり\nかき氷ですよね\n何色がいいかなぁ…♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122701, 6457, 1, 0, 3, 0, "あの人、泳げないって\n聞きました！\n私が教えてあげないと", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122701, 6458, 1, 0, 4, 0, "夏休みの宿題は\n計画的に進めましょう\n当たり前のことです！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122701, 6459, 1, 0, 5, 0, "防犯魔石、どこに\n着けておこうかなぁ\n…ここにしようかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 122701, 6460, 1, 0, 101, 0, "海の楽しみ方は\n予習済みです！\nあとは実践ですね♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 122701, 6461, 1, 0, 102, 0, "水着…きっとあの人に\n見られちゃいますよね\n…褒めてくれるかな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122701, 6462, 1, 0, 1, 0, "触りたいんですか？\n…いいですよ\n帽子くらいなら", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122701, 6463, 1, 0, 2, 0, "夏は体調管理に\n気をつけましょう\n私が管理してあげます", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122701, 6464, 1, 0, 3, 0, "海に行くのが楽しみで\nたまらないんですか？\n私はそこまで…嘘です", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122701, 6465, 1, 0, 4, 0, "いつもより薄着だから\nなんだか恥ずかしい…\nみ、見ないでください", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 122701, 6466, 1, 0, 101, 0, "へんたいふしんしゃ～\nえっ？　ああ、これは\n海に叫ぶ練習です", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 122701, 6467, 1, 0, 102, 0, "あなたと一緒に\n海に行くなんて…\nなんだか大人…えへへ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122701, 6468, 1, 1, 1, 0, "おはようございます\nさぁ、海に行って\n私と運動しましょう", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122701, 6469, 1, 2, 2, 0, "あっつ～い…\nはっ！？　ダメですよ\nシャキッとしないと！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122701, 6470, 1, 3, 3, 0, "明日も海で\n遊ぶために、しっかり\nお休みしましょうね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122701, 6472, 1, 0, 2, 0, "夏を楽しむときは\n休憩も大事ですよね\n計画的に、計画的に！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122701, 6473, 1, 0, 3, 0, "かわいいなぁ～\n海のお土産屋さんに\n売ってるかなぁ？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122701, 6474, 1, 0, 4, 0, "大人な香りがします\n私も違いが分かる\n素敵な女性になります", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122701, 6475, 1, 0, 5, 0, "濡れた体を\n乾かしましょう\nタオル、タオル…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122701, 6477, 1, 0, 7, 0, "泳いだ後は\n栄養補給…だけど\n…おなか膨らんじゃう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122701, 6478, 1, 0, 8, 0, "遊ぶだけじゃなくて\nお勉強もしてますよ\nあなたもどうですか？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122701, 6480, 1, 0, 10, 0, "ミミが喜びそう…♪\nわ、私ははしゃいだり\nしませんけど…っ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 122701, 6481, 1, 0, 11, 0, "ミソギがいたずらに\n使いそう…しっかりと\n試しておかないと！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123001, 6482, 1, 0, 1, 0, "あたしがまさか\nここにいるなんて…\n自分でも不思議だわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123001, 6483, 1, 0, 2, 0, "昔みたいにあちこち\n飛び回れないのって\nちょっと不便かも", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123001, 6484, 1, 0, 3, 0, "こうして誰かと一緒に\n暮らせるのもあいつや\nみんなのおかげね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123001, 6485, 1, 0, 4, 0, "ここで厄介になるなら\nあたしも家事とか\n手伝わないと…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123001, 6486, 1, 0, 5, 0, "…だめ！　誰かの\n世話をせずには\nいられないわ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 123001, 6487, 1, 0, 101, 0, "あいつたちが\n帰ってくるところだし\n綺麗にしないと…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 123001, 6488, 1, 0, 102, 0, "せっかくあたしが\nいるっていうのに\n少しは構いなさいよ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123001, 6489, 1, 0, 1, 0, "おいしい店とか\n知らないの？\n食べに行きましょ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123001, 6490, 1, 0, 2, 0, "あんた暇でしょ？\nあたしのやること\n手伝いなさい", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123001, 6491, 1, 0, 3, 0, "コッコロたんと\n遊んでくるから\n留守番よろしく♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123001, 6492, 1, 0, 4, 0, "あらおかえり…って\nボロボロじゃない！\nまた人助けしてたの？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 123001, 6493, 1, 0, 101, 0, "やっぱりあたしは\nあんたのそばが\n一番落ち着くみたい", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 123001, 6494, 1, 0, 102, 0, "どこまでだって\nあんたと一緒に\nいてやるんだから", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123001, 6495, 1, 1, 1, 0, "ふわぁ…おはよう\n…大きくなったせいか\n朝は特に体が重いわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123001, 6496, 1, 2, 2, 0, "お昼ご飯が食べたい？\nしょうがないわね…\nなんか作って\nあげるから", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123001, 6497, 1, 3, 3, 0, "あんたまだ成長期だし\nさっさと寝なさい\n大きくなれないわよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123001, 6499, 1, 0, 2, 0, "たまにはあたしも\nゆっくりしないと\nもたないのよ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123001, 6500, 1, 0, 3, 0, "かわいいじゃな～い♪\nコッコロたんに\nプレゼントしたいわ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123001, 6501, 1, 0, 4, 0, "いい香りね\n塔にもこういうの\n置いておこうかしら", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123001, 6502, 1, 0, 5, 0, "炎か…前は\n羽が焼けそうで\n怖かったっけ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123001, 6504, 1, 0, 7, 0, "あむ…妖精に\n体重増減の設定って\nあったっけ…？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123001, 6505, 1, 0, 8, 0, "記憶のデータと違って\n物語はいいわね\n結末が読めなくて", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123101, 6507, 1, 0, 1, 0, "冒険のお土産\nみんなにおすそわけ\nせなあきまへんな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123101, 6508, 1, 0, 2, 0, "おとぎ話よりも\n不思議なことが\nあるもんやねぇ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123101, 6509, 1, 0, 3, 0, "冒険しに出かけるなら\n動きやすい服装に\nせななぁ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123101, 6510, 1, 0, 4, 0, "地図に水筒に…\n他に忘れもんは\nあらへんやろか？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123101, 6511, 1, 0, 5, 0, "これまでの冒険のこと\nノートに書いて\nまとめてますんよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 123101, 6512, 1, 0, 101, 0, "王子はんとの冒険は\nどれもマホマホ王国の\n大事な歴史や♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 123101, 6513, 1, 0, 102, 0, "どこへ冒険するときも\nうちと王子はんは\n一緒どす♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123101, 6514, 1, 0, 1, 0, "魔法で火ぃつける\n練習してますんよ～\nよう爆発しますけど…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123101, 6515, 1, 0, 2, 0, "うちと王子はんで\n冒険道具をお揃いに\nするんもええなぁ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123101, 6516, 1, 0, 3, 0, "テント張りも随分\n上達したんよ\n王子はんのおかげやね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123101, 6517, 1, 0, 4, 0, "ランスロットたちの\n新しい冒険衣装\n思いついたんよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 123101, 6518, 1, 0, 101, 0, "迷子になっても\n王子はんと一緒なら\nきっと大丈夫やね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 123101, 6519, 1, 0, 102, 0, "王子はんのこと、\nきっちり守れるように\nなってみせますえ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123101, 6520, 1, 1, 1, 0, "お寝坊さんは\n布団めくって\n起こしにいきますえ～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123101, 6521, 1, 2, 2, 0, "支度が終わったら\n今日の冒険に\n出かけまひょか", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123101, 6522, 1, 3, 3, 0, "もうちょっと\nおしゃべりしたいん？\nあと少しだけやで♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123101, 6524, 1, 0, 2, 0, "冒険で疲れた体に\nこの癒しぱわーは\nあきまへん…すや…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123101, 6525, 1, 0, 3, 0, "ふふ、あんさんも\nうちと一緒に\n冒険します？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123101, 6526, 1, 0, 4, 0, "虫除けのアロマは\n冒険の必需品どす\n忘れんようにせんと", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123101, 6527, 1, 0, 5, 0, "うちも魔法使わんでも\n火ぃ起こせるよう\n練習せんとあかんなぁ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123101, 6529, 1, 0, 7, 0, "この料理…\n冒険するときに\n作ってみまひょか", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123101, 6530, 1, 0, 8, 0, "王子はんとの\n愛と冒険のおとぎ話…\nはぁ…素敵やわぁ♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 103101, 6532, 1, 0, 103, 0, "あの人との\n最良の未来のために\n占いも霊能力ももっと\n精進しなきゃ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 103101, 6533, 1, 0, 103, 0, "あなたともっと深く\nつながりたいです\n絆も、そして魂までも", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 104401, 6534, 1, 0, 103, 0, "絆の契約…\nあやつが望むのなら\n考えぬでもないがのう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 104401, 6535, 1, 0, 103, 0, "なんじゃ？\nわらわの姿に\n見惚れたかの？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123201, 6536, 1, 0, 1, 0, "旅の荷物って多いな…\nねぇぷうきち～\nすこしは持ってよぉ～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123201, 6537, 1, 0, 2, 0, "傘はなくてもいっか\nぷうきちがいるし♪\n『おいなんだと…？』", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123201, 6538, 1, 0, 3, 0, "旅先で人見知りなんか\nしてちゃだめだよね…\nまずはあいさつだ…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123201, 6539, 1, 0, 4, 0, "おみやげはやっぱり\nご当地ぬいぐるみ\nだよね♪　ワクワク…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123201, 6540, 1, 0, 5, 0, "知らない場所かぁ…\nうぅ…考えただけで\nすでにホームシック…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 123201, 6541, 1, 0, 101, 0, "野宿するってことは…\nお兄ちゃんのマントで\n寝るチャンスかも…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 123201, 6542, 1, 0, 102, 0, "どんなに危険な冒険も\nお兄ちゃんと一緒なら\n大丈夫！　…だよね？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123201, 6543, 1, 0, 1, 0, "ぷうきちのアイマスク\nも腹巻も持ってくの！\n旅の必需品なの～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123201, 6544, 1, 0, 2, 0, "これ全部背負うのって\nけっこうむずかしいの\n順番まちがえると大変", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123201, 6545, 1, 0, 3, 0, "えへへ～\nなでなでして？　帽子\nの上からでもいいよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123201, 6546, 1, 0, 4, 0, "『悪いが絆創膏の部分\nはアンタッチャブルだ\n綿が飛び出ちまう』", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 123201, 6547, 1, 0, 101, 0, "お兄ちゃんと一緒に\nもっともっといろんな\n景色が見たいな！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 123201, 6548, 1, 0, 102, 0, "旅って、終わる時は\nきっとさびしいよね…\nお兄ちゃん、帰り道は\n手をつないでもいい？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123201, 6549, 1, 1, 1, 0, "出発が楽しみすぎて\n寝られなかった…\n『旅行のお約束だな』", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123201, 6550, 1, 2, 2, 0, "わぁ　いいお天気！\n旅の間もずーっと\nぴーかんだといいな♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123201, 6551, 1, 3, 3, 0, "知らない場所で寝るの\n怖いな…寝る時だけ\n帰ってこようかな…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123201, 6553, 1, 0, 2, 0, "旅先にはクッションも\nソファもないよね…\n『俺でいいだろ』", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123201, 6554, 1, 0, 3, 0, "ぬいぐるみのみんな\n旅の無事を\n祈ってて…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123201, 6555, 1, 0, 4, 0, "旅先の香りも\n思い出に持って\n帰れたらいいのにな～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123201, 6556, 1, 0, 5, 0, "旅行用に携帯用暖炉\nがあるといいかも…！\n『まだ荷物増やす？』", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123201, 6558, 1, 0, 7, 0, "『もう出発だぜ？』\nえーまってまって！\n腹ごしらえがまだ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123201, 6559, 1, 0, 8, 0, "世界ってこんなに広い\nんだ…！　はやく自分\nの目で見てみたいな～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123301, 6561, 1, 0, 1, 0, "はちゃめちゃヒマ～\nなーんか楽し～コト\nないかなー", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123301, 6562, 1, 0, 2, 0, "ここ、いートコだねー\nこんどカリザきゅんも\nつれてこよー", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123301, 6563, 1, 0, 3, 0, "かわゆいショタが\n見た～い♪\n触りた～～～い♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123301, 6564, 1, 0, 4, 0, "なんか服きっついなー\nちょい太ったかー？\nん～\nま・気のせーっしょ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123301, 6565, 1, 0, 5, 0, "あ～やべ\nおねえちゃんに\nメッセすんの忘れたー\nまいっかー", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 123301, 6566, 1, 0, 101, 0, "パンケーキ食いてー\n騎士きゅん\n誘ってみよーっと♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 123301, 6567, 1, 0, 102, 0, "きょーも～\n好きぴに囲まれて～\nシ・ア・ワ・セ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123301, 6568, 1, 0, 1, 0, "アタシと遊ばな～い？\nってなんか\nナンパみスゴー\nウケる～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123301, 6569, 1, 0, 2, 0, "んー？\n今はだるいからパース\nあとでねー", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123301, 6570, 1, 0, 3, 0, "カリザきゅん\n見なかった～？\nおねえちゃんから\n逃げるとかオシオキー", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123301, 6571, 1, 0, 4, 0, "ネイルしたいけど\n無理なんよねー\n爪の間にめっちゃ\nスライム入っからさー", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 123301, 6572, 1, 0, 101, 0, "おねえちゃんに\nかまってほし～の？\nかわゆ～い♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 123301, 6573, 1, 0, 102, 0, "な～に騎士きゅん♪\nここでイチャイチャ～\nし・ちゃ・う～？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123301, 6574, 1, 1, 1, 0, "ねんむ～\nけど外から\nショタの声したんで～\nつい起きちった～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123301, 6575, 1, 2, 2, 0, "ランチの後はやっぱ\nデザートっしょー\nベツバラってヤツ～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123301, 6576, 1, 3, 3, 0, "こんな時間まで\n起きてるコは～\n食べちゃうぞ～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123301, 6578, 1, 0, 2, 0, "あーラクー\nいっしょーここで\nキューケーできるわー", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123301, 6579, 1, 0, 3, 0, "なにこれ～\nかわいいじゃ～ん\nショタの次くらいに～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123301, 6580, 1, 0, 4, 0, "いーニオイする～\nなんのニオイとかー\nよくわかんないけどー", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123301, 6581, 1, 0, 5, 0, "あったか～♪\n溶けてスライム\n戻りそ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123301, 6583, 1, 0, 7, 0, "ン・ン・ン～♪\nお・い・し～♪\nも～っと食べるぅ～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123301, 6584, 1, 0, 8, 0, "わ～なんかムズー\n読めなー\nウケるー", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123501, 6586, 1, 0, 1, 0, "あんパンって\nお菓子に入るのかな？\nハロウィンで\nもらえたりしない？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123501, 6587, 1, 0, 2, 0, "お菓子をくれなきゃ\nぐうたらするぞ～！\nハロウィン以外でも！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123501, 6588, 1, 0, 3, 0, "お化けになったら\n仕事しないで\nゴロゴロし放題\nなのかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123501, 6589, 1, 0, 4, 0, "おっと…この仮装\n袖がすごく長いからさ\n引っ掛けないように\n気を付けないとね～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123501, 6590, 1, 0, 5, 0, "イタズラするのも\n体力がいるよね～\n楽にできそうなの\n何かないかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 123501, 6591, 1, 0, 101, 0, "あいつって\n仮装とかするのかな？\n似合いそうなのは\nどんなお化けだろ？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 123501, 6592, 1, 0, 102, 0, "ずっとハロウィンなら\nあいつと遊び放題\nなんだけどな～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123501, 6593, 1, 0, 1, 0, "今の街はハロウィン\n一色だからね～\n人混みが大変だよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123501, 6594, 1, 0, 2, 0, "この仮装、どうかな？\n気に入ったなら\n来年もしてあげるよ～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123501, 6595, 1, 0, 3, 0, "実はこの長い袖\nドングリとかを\nため込めるんだよね…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123501, 6596, 1, 0, 4, 0, "カボチャランタンが\nあるんなら\nドングリランタンも\nあっていいよね～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 123501, 6597, 1, 0, 101, 0, "ハロウィンでも\nやる気があれば\nぐうたらできる…\nうん、いいこと言った", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 123501, 6598, 1, 0, 102, 0, "一緒にお菓子食べよ？\n寝転びながら堪能する\nハロウィンスイーツは\n最高だからね～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123501, 6599, 1, 1, 1, 0, "お化けは寝る時間…\n今のあたしはお化け…\nだから、おやすみ～…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123501, 6600, 1, 2, 2, 0, "お昼か～…\n食後のデザートは\nハロウィンのお菓子で\n決まりだね～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123501, 6601, 1, 3, 3, 0, "ふぁ～、寝よ…\nお化けは夜に\n出るものだけど\nあたしはリスだからね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123501, 6603, 1, 0, 2, 0, "ハロウィンを楽しむ\nためにも、ぐっすり\n寝とかないとね～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123501, 6604, 1, 0, 3, 0, "…袖から手が\n出せないから\nこのまま撫でよう\nよしよし", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123501, 6605, 1, 0, 4, 0, "リラックスできる\n素敵な香りだね～\n成仏しそうだよ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123501, 6606, 1, 0, 5, 0, "あったかいね～\nここで寝てもいい？\n…着替えないとダメか", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123501, 6608, 1, 0, 7, 0, "もぐもぐ…\nお化けもリスも\nお腹は空くんだよ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123501, 6609, 1, 0, 8, 0, "キョンシーって\nどんなお化けなのか\n本で調べようかな？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123501, 6611, 1, 0, 12, 0, "より取り見取りだね～\nとりあえず、これと\nこのお菓子はキープで", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123601, 6613, 1, 0, 1, 0, "よし、衣装もばっちり\nハロウィンパレードへ\n出かけよう♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123601, 6614, 1, 0, 2, 0, "仮装してても\n剣は手放せないね\nほとんど体の一部だよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123601, 6615, 1, 0, 3, 0, "見回りに誘うふりして\n二人でパレードに\n参加しちゃおうかな♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123601, 6616, 1, 0, 4, 0, "…あれ？　手錠の鍵\n落としちゃった…？\nやば、出かける前に\n見つけないと…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123601, 6617, 1, 0, 5, 0, "お家でコスプレも\nこれはこれで楽しいな\n決めポーズの練習\nたくさんしておこう！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 123601, 6618, 1, 0, 101, 0, "他にも、お姫様に\nメイドさんに学生服…\nやりたいコスプレが\n多すぎるよ～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 123601, 6619, 1, 0, 102, 0, "漫画もゲームも好き！\n仮装するのも大好き！\n…好きを言葉にすると\nもっと好きになる♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123601, 6620, 1, 0, 1, 0, "ずっと目が合ってるね\n見つめてくれるのは\nいいんだけど、今日は\n衣装を見てほしいな♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123601, 6621, 1, 0, 2, 0, "トリックオアトリート\nお菓子をくれなきゃ\n逮捕しちゃうぞ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123601, 6622, 1, 0, 3, 0, "あなたにだけはこの姿\nいつでも見せてあげる\nふふ、なんちゃって♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123601, 6623, 1, 0, 4, 0, "どうしたの？\nイタズラするなら\n隙を突かなきゃ\nこんなふうに、ね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 123601, 6624, 1, 0, 101, 0, "コスプレしてても\n剣の稽古は休まないよ\nちょっと相手になって\nくれるかな？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 123601, 6625, 1, 0, 102, 0, "ひゃっ、びっくりした\n急に触らないでよ～\nハロウィンだからって\nイタズラしないでね？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123601, 6626, 1, 1, 1, 0, "おはよう！\n今日も朝から\nパトロールに行こう！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123601, 6627, 1, 2, 2, 0, "カボチャのケーキを\n用意してるよ！\nデザートを食べて\n午後も頑張ろう！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123601, 6628, 1, 3, 3, 0, "暗くなってきたね\nカボチャのランタンに\n火を灯そうよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123601, 6630, 1, 0, 2, 0, "ふかふか気持ちいい～\nあっでも衣装がシワに\nなっちゃうかも…？\n…でも気持ちいい～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123601, 6631, 1, 0, 3, 0, "…かわいい\nこの子もパレードに\n連れて行きたいな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123601, 6632, 1, 0, 4, 0, "はぁ～癒される♪\nハロウィンの見回りが\n終わった後は、\nまったりくつろごう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123601, 6633, 1, 0, 5, 0, "ここで二人寄り添って\nカボチャスープ飲もう\n…なんて想像したら\nポカポカしてきた…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123601, 6635, 1, 0, 7, 0, "パレードに参加すると\n体力を使うから、\nいっぱい食べておこう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123601, 6636, 1, 0, 8, 0, "この絵本のお姫様\nかわいいね\nこの仮装もいいな♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123601, 6638, 1, 0, 12, 0, "わあ、おいしそう！\n見回りに行く前に\n少しつまんじゃおう♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 103701, 6640, 1, 0, 103, 0, "今日こそは、あの人に\n気持ちを打ち明けて…\nそのあとは…うう\nど、どうしよう？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 103701, 6641, 1, 0, 103, 0, "私が剣なら、あなたは\nさしずめ私の鞘…\nふふ、私の帰る\n場所ってことだよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123701, 6642, 1, 0, 1, 0, "やっぱり\nコスプレしてると\nテンションあがる～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123701, 6643, 1, 0, 2, 0, "キャラコスにも\n興味あるけど…\nマジカル、ラブリー\nどっちにしよっかな～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123701, 6644, 1, 0, 3, 0, "キャラコスといえば\nミスティーシャイニー\nもいいんだよねえ…\nうはー！　悩むー！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123701, 6645, 1, 0, 4, 0, "ギルドハウスで\n宅コスしてる人\n実は結構\nいるんじゃないかな～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123701, 6646, 1, 0, 5, 0, "コスプレだけど普段着\nって言いはれそう…\nって、ダメダメ！\nオタクは節度が大事！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 123701, 6647, 1, 0, 101, 0, "フェスで買ってきた\n薄い本～♪\nギルドハウスに持って\nきちゃおっかな～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 123701, 6648, 1, 0, 102, 0, "ようこそ\nコスプレ沼へ～\nなんちゃって♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123701, 6649, 1, 0, 1, 0, "どうどう？\nナナカちゃんの\nハロウィンコスプレ\n似合ってる？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123701, 6650, 1, 0, 2, 0, "ハロウィン魔法少女\n参上カボ～！\n…やっぱ今の語尾\nナシで！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123701, 6651, 1, 0, 3, 0, "ハロウィンといえば\nトリックオア～…\nコスプレ！\nなんちゃって", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123701, 6652, 1, 0, 4, 0, "ランフェスの\n戦利品～♪\nねえねえ\n見たい？　見たい？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 123701, 6653, 1, 0, 101, 0, "私にしてほしい\nコスプレがあったら\n教えて♪　君のために\nや～ってやるぜぇ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 123701, 6654, 1, 0, 102, 0, "一緒にコスプレしたい\nならナナカちゃんは\nいつでも大歓迎だよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123701, 6655, 1, 1, 1, 0, "ふぁあ…ねむ\nって、ありゃりゃ　\n昨日コスプレしたまま\n寝ちゃってたかあ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123701, 6656, 1, 2, 2, 0, "今日のお昼は\nなんにしよ？\nカボチャ料理に\nしよっかな～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123701, 6657, 1, 3, 3, 0, "もうこんな時間かあ\nそういえばオタクって\n夜行性の人が多い\n気がするぜぇ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123701, 6659, 1, 0, 2, 0, "ふっふっふ\nこのパニエは\n座るときはちゃんと\nふにゃってなるのさ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123701, 6660, 1, 0, 3, 0, "いや～\nかわいいものをみると\n癒されますな～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123701, 6661, 1, 0, 4, 0, "新しい薄い本の匂いの\nアロマとか…ダメ？\n私はあの匂い、なんか\n好きなんだよね～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123701, 6662, 1, 0, 5, 0, "暖か～い…\nけど近づきすぎて\n衣装が燃えないように\n気を付けなきゃ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123701, 6664, 1, 0, 7, 0, "コス衣装のために\n体型維持は必須だけど\nついつい\n食べちゃうんだよね～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123701, 6665, 1, 0, 8, 0, "おっ、次はこの本に\nのってるキャラのコス\nしてみよっかな？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123701, 6667, 1, 0, 12, 0, "おおっ！\nお菓子がいっぱい！\nベルルちゃんチョコ\n入ってないかな～？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123801, 6669, 1, 0, 1, 0, "むっ…\nこの耳の手触り…\n驚嘆に値する…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123801, 6670, 1, 0, 2, 0, "獲物を狩りまくる…\nそんなウサギがいても\nいいだろう？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123801, 6671, 1, 0, 3, 0, "ワナなど怖くないぞ？\nむしろ積極的に\n踏んでいく性分だ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123801, 6672, 1, 0, 4, 0, "一匹狼ならぬ\n一匹ウサギか\nどっちでもかまわん♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123801, 6673, 1, 0, 5, 0, "人間とて本を正せば獣\n本能に従えばよいのだ\n遠慮は無用さ☆", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 123801, 6674, 1, 0, 101, 0, "脱兎のごとく…\n逃げるものか\n坊やを追い回すのさ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 123801, 6675, 1, 0, 102, 0, "獣の道は険しいが…\n坊やめ、音を上げずに\nちゃんとついてきたな\n褒めてやるとしよう♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123801, 6676, 1, 0, 1, 0, "ニンジンはいらん\n好物は好敵手だ\nさぁどんどんおくれ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123801, 6677, 1, 0, 2, 0, "尻尾に触りたいだと？\nワタシの乱数聖域を\n崩せたらな…☆", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123801, 6678, 1, 0, 3, 0, "頭で考えるな\n体に率直になれ\n己を野放しにするのさ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123801, 6679, 1, 0, 4, 0, "そうさモコフワさ♪\nなで心地は最高だが\n意外にどう猛だぞ…？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 123801, 6680, 1, 0, 101, 0, "ワタシにピョンピョン\n跳ねてほしいのか？\nあとでじっくり、な♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 123801, 6681, 1, 0, 102, 0, "ワタシと番になるか？\nオマエさえよければ\nいつでもかまわんぞ☆", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123801, 6682, 1, 1, 1, 0, "太陽と共に目覚め\n活動する\nそれが獣の生き方さ☆", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123801, 6683, 1, 2, 2, 0, "昼寝をすればいい\nワタシが周囲を\n警戒していてやる", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123801, 6684, 1, 3, 3, 0, "暗くなれば眠るだけさ\nそうすれば余計な\nストレスなどたまらん", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123801, 6686, 1, 0, 2, 0, "むむっ！？\n尻尾がつっかえて\nうまく座れんな…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123801, 6687, 1, 0, 3, 0, "愛らしさでワタシに\n張り合おうというのか\n５億年後に出直せ☆", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123801, 6688, 1, 0, 4, 0, "野生の芳香か…\n本能が呼び覚まされる\nいいセンスだ♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123801, 6689, 1, 0, 5, 0, "寒さも野生のうちだ\n…とはいえ\n温まっていくか♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123801, 6691, 1, 0, 7, 0, "悪くはないが物足りん\nこう見えて肉食なのだ\n肉はないのか肉は☆", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123801, 6692, 1, 0, 8, 0, "ウサギ族は地獄耳？\nワタシとの共通点だな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 106401, 6694, 1, 0, 103, 0, "ここが私の家…\nそれはこれからも\nずっと変わらないわ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 106401, 6695, 1, 0, 103, 0, "ええと、その…今から\nデート…じゃなくって\n散歩でもどうかしら？\n…ふ、二人きりで…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123901, 6696, 1, 0, 1, 0, "ギルドハウスに\nヒーロー参上！\nみんな仲良くッスよ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123901, 6697, 1, 0, 2, 0, "料理もできて\n鎧も作れる！\nお母さんはすごいッス", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123901, 6698, 1, 0, 3, 0, "ううう…\nまたマントをドアに\n挟んじゃったッス…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123901, 6699, 1, 0, 4, 0, "ずっと鎧を着ていれば\nジュンさんに近づける\nかも？　燃えるッス！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123901, 6700, 1, 0, 5, 0, "サインの練習…\nもっとしておいた\nほうがいいッスかね～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 123901, 6701, 1, 0, 101, 0, "半人前でも\n胸を張るッス！\nそのうち似合う日が\nくるッスから！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 123901, 6702, 1, 0, 102, 0, "種族の違いも\n強い魂があれば\n超えられるッス！\n信じてるッスよ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123901, 6703, 1, 0, 1, 0, "困ったときは\n自分を呼ぶッス！\nあと楽しいときにも\n呼んでほしいッス！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123901, 6704, 1, 0, 2, 0, "特訓でも行くッスか？\n望むところッス！\n目指せ、一人前！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123901, 6705, 1, 0, 3, 0, "新しいショーの台本を\n読みこんでるッス！\nところで…この言葉の\n意味、分かるッスか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123901, 6706, 1, 0, 4, 0, "ヒミツがなくなると\nなんか恥ずかしいッス\nあんたはどうッスか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 123901, 6707, 1, 0, 101, 0, "ヒーローは遅れて\nやってくる…\nあんたもそうッスね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 123901, 6708, 1, 0, 102, 0, "誰よりも早く\nあんたに見せたいッス\n一人前になった自分！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123901, 6709, 1, 1, 1, 0, "うーん、今日も\nよく眠れたッス～！\n絶好調、間違いなし！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123901, 6710, 1, 2, 2, 0, "おお、もうお昼ッスか\nどうりでお腹が空くと\n思ったッス～えへへ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123901, 6711, 1, 3, 3, 0, "パトロールのあとは\n歯みがきに宿題に…\n大忙しッスね、夜！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123901, 6713, 1, 0, 2, 0, "こんなにリラックス\nできるなんて…\n平和な証拠ッスね～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123901, 6714, 1, 0, 3, 0, "か、かわいい…♪\nさあ、自分と握手…\nううん、抱っこッス♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123901, 6715, 1, 0, 4, 0, "優しい匂いッスね…♪\nこれくらいみんなを\n包み込めるように\nなりたいッス～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123901, 6716, 1, 0, 5, 0, "ひっくちゅ！　うー…\nちょっと冷えるッスね\nぬくぬくするッス～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123901, 6718, 1, 0, 7, 0, "特訓した分、\nいっぱい食べるッス！\nでっかくなるッス！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 123901, 6719, 1, 0, 8, 0, "いつか自分の\nヒミツ図鑑が出たり\nして…いやいやいや", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106901, 6721, 1, 0, 1, 0, "牢獄よりは\nマシな環境ね\n我慢してあげるわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106901, 6722, 1, 0, 2, 0, "退屈ね…\nキャルでもいじって\n暇をつぶそうかしら", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106901, 6723, 1, 0, 3, 0, "ここにいる連中\n私を天気予報士と\n勘違いしてない？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106901, 6724, 1, 0, 4, 0, "音波情報が多いわね…\nここ、もう少し静かに\nならないの…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106901, 6725, 1, 0, 5, 0, "共同生活なんて\n久しぶりだわ\n…懐かしいわね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 106901, 6726, 1, 0, 101, 0, "あの子たちのギルド\n経営が雑すぎるわ…！\n教育が必要みたいね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 106901, 6727, 1, 0, 102, 0, "とどめを刺される\n準備はできているのに\nまだなのかしら…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106901, 6728, 1, 0, 1, 0, "私と戦う気になった？\nあら、まだなのね…\nその時を待ってるわよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106901, 6729, 1, 0, 2, 0, "私というリソースは\n有効活用しなさい\n子守なんてごめんだわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106901, 6730, 1, 0, 3, 0, "どうしてそんなに\n頭がいいか？\n生まれつきよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106901, 6731, 1, 0, 4, 0, "視力は権能に\n影響しないわよ\n当たり前じゃない", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 106901, 6732, 1, 0, 101, 0, "あなたまで天気を\n教えろですって…？\nぶっ殺すわよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 106901, 6733, 1, 0, 102, 0, "暇なら付き合いなさい\n好きなゲームでいいわ\nどうせ私が勝つもの", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106901, 6734, 1, 1, 1, 0, "人生朝露…\n悔いのない一日を\n過ごしなさい", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106901, 6735, 1, 2, 2, 0, "午睡はいいわよ…\nふわぁ…眠りは思考に\n必要不可欠なの…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106901, 6736, 1, 3, 3, 0, "夙興夜寝なんて\nやめておきなさい\n休息は大切よ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106901, 6738, 1, 0, 2, 0, "私を満足させるなんて\n大したものね…晶に\n大量生産させるわ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106901, 6739, 1, 0, 3, 0, "キャルの代わりに\nズタズタにする物が\n欲しかったの…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106901, 6740, 1, 0, 4, 0, "いい香りじゃない\n見たことのない調合ね\n誰が用意したの？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106901, 6741, 1, 0, 5, 0, "パワーが足りないわね\nもっとよ、もっと私を\n寛がせなさい", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106901, 6743, 1, 0, 7, 0, "栄養価はともかく\n味のバランスは\n悪くないわね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 106901, 6744, 1, 0, 8, 0, "つまらない本は\n私が書き直すわ\nこれはどうかしら？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124001, 6746, 1, 0, 1, 0, "今年はアカリと\nお姉ちゃんでサンタが\n二人…つまり幸せも\n二倍ですね♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124001, 6747, 1, 0, 2, 0, "クリスマスに向けての\nサックス練習…\nお兄ちゃんと一緒に\n猛特訓です！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124001, 6748, 1, 0, 3, 0, "クリスマスツリーの\n飾りつけ、完璧です♪\nお兄ちゃんお姉ちゃん\n喜んでくれるかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124001, 6749, 1, 0, 4, 0, "わあ、お外が真っ白！\n生クリームが\nいーっぱいの\nケーキみたいです♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124001, 6750, 1, 0, 5, 0, "煙突から入ると\nお洋服が汚れちゃう…\n玄関からじゃ\nダメなのかなぁ？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 124001, 6751, 1, 0, 101, 0, "お兄ちゃんが来たら\nサンタのお洋服\nいっぱい褒めて\nもらおっと♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 124001, 6752, 1, 0, 102, 0, "聖夜をお兄ちゃんと\n過ごせたら…\nアカリもちょっぴり\n大人になれるかも♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124001, 6753, 1, 0, 1, 0, "今のアカリは\nサンタさんですから♪\nなんでもお願いして\nくださいね？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124001, 6754, 1, 0, 2, 0, "お外は寒いですから\nアカリとくっついて\nお出かけしましょ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124001, 6755, 1, 0, 3, 0, "お兄ちゃんは\nアカリのトナカイさん\nですから、ずーっと\nそばにいてください♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124001, 6756, 1, 0, 4, 0, "お兄ちゃん、助けて！\nアカリのカラダで\nラッピング練習したら\n絡まっちゃいました…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 124001, 6757, 1, 0, 101, 0, "プレゼント、いっぱい\n準備できました♪\nお兄ちゃんの分は…\nまだヒミツでーす♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 124001, 6758, 1, 0, 102, 0, "アカリ、プレゼントは\n大人っぽいのがいいな\n早くお兄ちゃんに\n追いつきたいから♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124001, 6759, 1, 1, 1, 0, "おはようございます♪\nふふ、アカリサンタは\nいい子のお手本なので\n早寝早起きなんです♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124001, 6760, 1, 2, 2, 0, "あれ、もうお昼？\nクリスマスナンバーの\n練習してたら\nもうこんな時間…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124001, 6761, 1, 3, 3, 0, "冬の夜空は星が\nキレイですね～\nお兄ちゃん、一緒に\n見に行きませんか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124001, 6763, 1, 0, 2, 0, "はぁ、快適すぎて\nとろけちゃいそう…\nこういうプレゼントも\nいいかもです…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124001, 6764, 1, 0, 3, 0, "やん、かわいいです♪\nえへへ、お兄ちゃんは\nどんなプレゼントが\n欲しいですか～？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124001, 6765, 1, 0, 4, 0, "わわ、大人な香り…\nロマンチックな\nクリスマスが\n過ごせそうです♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124001, 6766, 1, 0, 5, 0, "これでカラダを温めて\nアカリの温もり…\nお兄ちゃんに\nおすそ分けしよっと♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124001, 6768, 1, 0, 7, 0, "お腹がパンパンです…\nでもこれで\nちょっとサンタさんに\n近づけたかも…？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124001, 6769, 1, 0, 8, 0, "クリスマスイブの\n恋人との過ごし方…？\nはわわ、アカリには\nまだ早いかもです…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124001, 6771, 1, 0, 14, 0, "わっ、すごいすごい！\n不思議な絵本ですね♪\nこの本でお兄ちゃんに\nイタズラしちゃお♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124001, 6772, 1, 0, 15, 0, "アカリ、ホカホカに\nなっちゃいました～♪\n…ぎゅってしても\nいいですよ？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124101, 6773, 1, 0, 1, 0, "またエンジェルになる\nかもとは思ってたけど\n…サンタさん要素の\n追加は想定外だったわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124101, 6774, 1, 0, 2, 0, "ラフィたちがいない\nときは私がなんとか\nしないとね\n一応…エンジェルだし", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124101, 6775, 1, 0, 3, 0, "部屋の飾りつけとか\nするのも…あり？\nふふ、浮かれすぎかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124101, 6776, 1, 0, 4, 0, "パーティーだったら\nみんなで大人数向けの\nボードゲームもできる\nかも…やったっ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124101, 6777, 1, 0, 5, 0, "この服のモコモコ…\n気づいたら触っちゃう\n…やるわね、ラフィ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 124101, 6778, 1, 0, 101, 0, "アイツと一緒にいると\n人見知りとは違う\n緊張があるのよね…\nって今の聞いてたっ？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 124101, 6779, 1, 0, 102, 0, "ぬいぐるみ…\nいじめてないわよね？\n…大事にしてるって？\nそれはそれで…あうぅ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124101, 6780, 1, 0, 1, 0, "いい子と悪い子って\nどう見分けるのかしら\n…まさか素直さとか？\nだったら困る！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124101, 6781, 1, 0, 2, 0, "プレゼントといえば\nやっぱりゲーム！\n年末商戦サイコー♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124101, 6782, 1, 0, 3, 0, "サンタさんはお仕事を\n見られちゃ\nいけないのよね？\n…天職？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124101, 6783, 1, 0, 4, 0, "この服は汚れ対策も\nバッチリなのよね\n煙突に入れるように\n…いや私はNGよ！？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 124101, 6784, 1, 0, 101, 0, "アンタのお願い…\n今なら一つくらい\n聞いてあげるわよ？\nほら、サンタ衣装だし", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 124101, 6785, 1, 0, 102, 0, "私たちって\nなぜかぬいぐるみと\n縁があるわよね\n…ふふ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124101, 6786, 1, 1, 1, 0, "お、おはよっ\n…ふぅ、寝ぐせを\n直すのが間に合って\nよかったぁ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124101, 6787, 1, 2, 2, 0, "え…一緒にごはん？\nそ、そう…うん。OK\nまあ、せっかくだし？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124101, 6788, 1, 3, 3, 0, "この時間のゲームって\n妙に楽しいのよね\n…ということで、\nちょっと遊ばない？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124101, 6790, 1, 0, 2, 0, "ああ～ダメになるぅ…\nもうここでずっと\nゲームしてた～い", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124101, 6791, 1, 0, 3, 0, "プレゼントのために\n研究は必要よね、うん\n…はぁ、かわいい～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124101, 6792, 1, 0, 4, 0, "集中力を高める香りも\nあるのよね…\n勝負をするときには\n持ち歩こうかしら", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124101, 6793, 1, 0, 5, 0, "なんだかまったり\nしちゃうわね…\n時間が溶けるわ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124101, 6795, 1, 0, 7, 0, "お腹いっぱい\n食べられるのも\n幸せなことよね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124101, 6796, 1, 0, 8, 0, "ちっちゃいときは\nアカリと一緒に絵本を\n読んだなぁ…懐かしい", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124101, 6798, 1, 0, 14, 0, "飛び出す絵本…\nこれでゲームブックを\n作ったらおもしろく\nなる気がするわ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124101, 6799, 1, 0, 15, 0, "あったまる…\nモフモフ…ふわふわ…\nちょっと休憩しても…\nいいよね…？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 105101, 6800, 1, 0, 103, 0, "あら、どうしたの？\nもしかして、私と\n新しい実験を\n始めたくなった？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 105101, 6801, 1, 0, 103, 0, "調子が悪いときは\n診療所に来て\n君は特別に、じっくり\n診察してあげるわ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124201, 6802, 1, 0, 1, 0, "特別じゃなくても\nまったり過ごす\nクリスマスも\nいいものだね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124201, 6803, 1, 0, 2, 0, "ニットの帽子に\nマフラーに耳当て…\n素顔用の防寒対策を\n整えなければ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124201, 6804, 1, 0, 3, 0, "靴下が\n見当たらなかったから\n枕元にはかわりに\n鎧を置いておこう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124201, 6805, 1, 0, 4, 0, "聖女なんて言われる程\n素晴らしくはないけど\n心は聖くいたいものだ\n日々精進だね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124201, 6806, 1, 0, 5, 0, "兜がないから\nイルミネーションが\nいつもよりまぶしく\n感じるよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 124201, 6807, 1, 0, 101, 0, "皆の幸福を\n祈願して\nクリスマスリースを\n飾ろうか", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 124201, 6808, 1, 0, 102, 0, "私にはかわいすぎる\n衣装だが…開き直って\nクリスマス気分を\n楽しんでしまおう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124201, 6809, 1, 0, 1, 0, "ツリーの飾りつけ\n手伝ってくれるかい？\nセンスは少年に\nお任せするよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124201, 6810, 1, 0, 2, 0, "パーティーは\n用意してる時も楽しい\nねえ少年、この気持ち\n分かってくれるかい？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124201, 6811, 1, 0, 3, 0, "なんでトナカイの鼻は\n赤いのかって？\n無灯ソリ運転は違反で\n取り締まられるからだ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124201, 6812, 1, 0, 4, 0, "トモちゃんや\nマツリちゃんは\nいい子だから、きっと\nサンタさんに会えるね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 124201, 6813, 1, 0, 101, 0, "ヒイラギの髪飾り\n実は気にいってるんだ\n少年にも\n褒めてもらいたいな", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 124201, 6814, 1, 0, 102, 0, "ねえ少年…そろそろ\n見慣れてほしい\nそんなにまじまじ\n見つめられると照れる", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124201, 6815, 1, 1, 1, 0, "朝日に照らされて\n街がキラキラ白銀に\n輝いてるよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124201, 6816, 1, 2, 2, 0, "お昼ご飯を食べて\nちょっと散歩しようか\n祝祭に沸く街は\nとてもきれいだよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124201, 6817, 1, 3, 3, 0, "やっぱりクリスマスは\n夜が本番って感じだね\nキャンドルを灯して\n厳かに過ごそうか", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124201, 6819, 1, 0, 2, 0, "鎧姿では無縁だったが\nふかふかしていて\n気持ちいいな…\nふかふか…ふかふか…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124201, 6820, 1, 0, 3, 0, "ふふ、かわいいね\n抱きしめると\nあの雪の日に産まれた\n赤ちゃんを思い出す", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124201, 6821, 1, 0, 4, 0, "兜を脱いだおかげで\nこの香りに出会えた\n…クリスマスの魔法も\n悪くないかもね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124201, 6822, 1, 0, 5, 0, "指先から体の芯まで\n暖まるね\nこれなら雪の日でも\n安心だよ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124201, 6824, 1, 0, 7, 0, "もぐ、もぐ…\nあまり食べるところ\n見ないでほしい", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124201, 6825, 1, 0, 8, 0, "素敵なレストランの\n特集記事が載ってる\n聖夜に予約が取れたら\n最高に幸せだろうね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124201, 6827, 1, 0, 14, 0, "わっ！\n…………いや\n驚いてなどいない", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124201, 6828, 1, 0, 15, 0, "ふふ…頬に直接\nふかふかを感じられる\n温かくて気持ちいいよ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124301, 6829, 1, 0, 1, 0, "ピアノに読書…\nグレアと一緒に\nなにをしようかな～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124301, 6830, 1, 0, 2, 0, "元の世界へ戻った時\n授業に遅れないよう\nグレアと勉強しとこう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124301, 6831, 1, 0, 3, 0, "街に魔物料理屋さんが\nあるんだ！　グレアと\n食べに行ってみよう♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124301, 6832, 1, 0, 4, 0, "せっかく新しい魔法を\n覚えたし、グレアにも\n見てもらいたいな！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124301, 6833, 1, 0, 5, 0, "グレアも誘って\nランドソルをもっと\n探索したい！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 124301, 6834, 1, 0, 101, 0, "私たちも\nあの人を見習って\nバイトとかしよう！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 124301, 6835, 1, 0, 102, 0, "いつかあの人を\n私たちの学院に\n招待したいな！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124301, 6836, 1, 0, 1, 0, "あのね！\nこれからグレアと\n魔法愛好会に\n行くんだ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124301, 6837, 1, 0, 2, 0, "これからなにするの？\n私たちもついて行って\nいいかな！？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124301, 6838, 1, 0, 3, 0, "グレアと一緒に\nあなたも魔法の練習\nしてみない？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124301, 6839, 1, 0, 4, 0, "グレアとよく話すの\nマナリアの制服\nあなたに着せたいって", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 124301, 6840, 1, 0, 101, 0, "グレアのそばって\n温かいんだ～！\nぬくぬくだよ～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 124301, 6841, 1, 0, 102, 0, "あなたは\nグレアと私の友達！\nこれからもずっと！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124301, 6842, 1, 1, 1, 0, "おはよう！\n今朝はグレアと一緒に\n外を散歩してきたよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124301, 6843, 1, 2, 2, 0, "お腹空いたな…\nよし、グレアと一緒に\n魔物料理を作ろう！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124301, 6844, 1, 3, 3, 0, "もうこんな時間だね\n明日のために\nそろそろ寝ようかな！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124301, 6846, 1, 0, 2, 0, "はぁ～この感触…\nグレアの尻尾みたいで\n安らぐ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124301, 6847, 1, 0, 3, 0, "ぬいぐるみって\nいいよね…私もよく\nグレアに抱き付くんだ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124301, 6848, 1, 0, 4, 0, "いい香り～…\nグレアもこういうの\n好きなんだよ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124301, 6849, 1, 0, 5, 0, "あったかいね～…\nグレアにくっついてる\n時を思い出すよ～…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124301, 6851, 1, 0, 7, 0, "この世界にしかない\nおいしいものが\nたくさんあるよね！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124301, 6852, 1, 0, 8, 0, "世界を繋ぐ魔法の\nヒント…本に書いて\nないかなぁ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124401, 6855, 1, 0, 1, 0, "ピアノに読書…\n今日のアンは\nなにをするのかな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124401, 6856, 1, 0, 2, 0, "私はアンほど\n勉強はできないから\nちゃんと自習しないと", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124401, 6857, 1, 0, 3, 0, "一度始めたら\nとことん追求するのが\nアンなんだよね…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124401, 6858, 1, 0, 4, 0, "ふふ…夢中になってる\nアンを眺めてるだけで\n一日があっという間…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124401, 6859, 1, 0, 5, 0, "ランドソルは広いね…\nアンと一緒にいろんな\n場所へ行ってみたい", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 124401, 6860, 1, 0, 101, 0, "アンやあの人の笑顔を\n見てると、すごく\n安心するんだ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 124401, 6861, 1, 0, 102, 0, "もしあの人が私たちの\n学院に編入したら…\nふふ、楽しそうだね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124401, 6862, 1, 0, 1, 0, "アンもあなたも\nすごいな…\nみんなとすぐに\n仲良くなっちゃうから", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124401, 6863, 1, 0, 2, 0, "あなたはこれから\nなにをするの？\nその…アンと一緒に\nついていってもいい？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124401, 6864, 1, 0, 3, 0, "アンから魔法の練習に\n誘われた？　実は私も\n…一緒にどうかな？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124401, 6865, 1, 0, 4, 0, "あなたがマナリアに\n編入したら\n楽しいのになって\nアンとよく話すんだ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 124401, 6866, 1, 0, 101, 0, "こっちの世界にも\nアンのように\n私を受け入れてくれる\n人たちがいるんだね…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 124401, 6867, 1, 0, 102, 0, "元の世界へ帰る日が\n来たとしても私たちは\n「さよなら」より\n「またね」がいいよね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124401, 6868, 1, 1, 1, 0, "おはよう。さっき\nランドソルの朝日を\nアンと見てきたんだ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124401, 6869, 1, 2, 2, 0, "私たちは\nこれからなんだけど\nあなたはお昼ごはん\n何を食べたの？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124401, 6870, 1, 3, 3, 0, "そろそろ\nアンが眠る時間だ…\nあなたもおやすみ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124401, 6872, 1, 0, 2, 0, "はぁ、疲れが取れる…\nこの尻尾…\nちょっと重いんだよね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124401, 6873, 1, 0, 3, 0, "これ…\nアンに見せたら\n喜ぶだろうな…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124401, 6874, 1, 0, 4, 0, "いい香り…\nアンの感想も\n聞いてみたいな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124401, 6875, 1, 0, 5, 0, "あったかい…\nアンがここにいたら\n本を読みながら\n寝ちゃうんだろうな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124401, 6877, 1, 0, 7, 0, "おいしいからたくさん\n食べれちゃうけど\nまた尻尾…\n大きくなっちゃうかな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124401, 6878, 1, 0, 8, 0, "アンがおススメ\nしてくれた本は\nこれかな…？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124701, 6881, 1, 0, 1, 0, "凶、凶、凶…大凶？\nなによこのおみくじ\n自宅用だからって\n排出率が適当すぎよ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124701, 6882, 1, 0, 2, 0, "今年こそ最高の新薬が\nもはや偶然に近い\n最小限の手間で\n作れますように", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124701, 6883, 1, 0, 3, 0, "喉に詰まる前に\n溶けてなくなるお餅…\n最優先の研究課題だわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124701, 6884, 1, 0, 4, 0, "よし、完成♪\nミツキ特製…\n「新解釈おせち料理」\n医学的根拠が満載よ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124701, 6885, 1, 0, 5, 0, "お年玉を彼に…\nあげるべき…？\n色んな意味で微妙ね…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 124701, 6886, 1, 0, 101, 0, "ふふ、今年もまた一年\n彼のバイタルを\n記録できるのね♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 124701, 6887, 1, 0, 102, 0, "今年こそ\n私の薬で彼を魔改造…\nううん、大人の男に！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124701, 6888, 1, 0, 1, 0, "コマ回しのコマを\n見てたら目が回った？\nそう…手当は不要よ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124701, 6889, 1, 0, 2, 0, "私の顔で\n福笑いを作った？\nあら…隻眼まで\n再現度が高いわね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124701, 6890, 1, 0, 3, 0, "門松よし、しめ縄よし\nこんなギルドでもね\n一応縁起を担ぐのよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124701, 6891, 1, 0, 4, 0, "本気の薬草を煮込んだ\n本気の七草粥よ\n色は気にしちゃダメ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 124701, 6892, 1, 0, 101, 0, "君が今年も健康で\nつつがなく…実験台を\n務められますように", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 124701, 6893, 1, 0, 102, 0, "いくつ新年を迎えても\n俗世の垢にまみれない\nそんな君こそが…\n私には最高のお薬よ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124701, 6894, 1, 1, 1, 0, "お正月なんだから\nいつまでも寝てないで\nさぁ、実験開始よ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124701, 6895, 1, 2, 2, 0, "カルタも双六もしたし\nあとは実験くらいしか\nやることがないわね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124701, 6896, 1, 3, 3, 0, "日頃からたいがい\n意味深な夢を見るけど\n初夢はまた別かしら…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124701, 6898, 1, 0, 2, 0, "さすがにこの格好で\n寝正月を敢行するのは\nためらわれるわね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124701, 6899, 1, 0, 3, 0, "や、闇医者たる者\nこんな愛くるしい物に\n心奪われるわけには…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124701, 6900, 1, 0, 4, 0, "お雑煮のいい香り…\n食欲が増進されるわ\nアロマも立派なお薬ね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124701, 6901, 1, 0, 5, 0, "胸元は閉じないわよ？\nでも時々ここで\nこっそり暖まるの…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124701, 6903, 1, 0, 7, 0, "無から有が生まれてる\n新年早々物理の法則を\n無視してくるのね…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124701, 6904, 1, 0, 8, 0, "どれもこれも読んだわ\n新しい婚活本の\n入荷はまだかしら…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124601, 6906, 1, 0, 1, 0, "彼が一生懸命\n書いてくれた年賀状\n気持ちがこもっていて\nすごくうれしいわ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124601, 6907, 1, 0, 2, 0, "しっかり丁寧に\n大掃除したから\nとってもいい気持ちで\n新年を迎えられたわ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124601, 6908, 1, 0, 3, 0, "彼の着物姿\nきっと凛々しいわよね\n想像しただけで\n笑顔になるわ～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124601, 6909, 1, 0, 4, 0, "お雑煮は地域によって\n違いがあるけれど\n愛情が隠し味なのは\nどこも一緒よね♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124601, 6910, 1, 0, 5, 0, "彼と凧揚げして\n遊びたいけど\n森では引っかかって\nできないかしら…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 124601, 6911, 1, 0, 101, 0, "今年も彼が素直に\n成長できるように\nたくさんいい子いい子\nしてあげましょう…♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 124601, 6912, 1, 0, 102, 0, "絵馬に彼と\n書いた通り\n今年もみんなが笑って\n過ごせますように", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124601, 6913, 1, 0, 1, 0, "先生と初詣に\n行きましょう〜♪\n迷子にならないように\n手を繋いで、ね？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124601, 6914, 1, 0, 2, 0, "初売りセールいいわね\nでも気を付けないと\nあれこれ欲しくなって\n買い過ぎちゃうかも…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124601, 6915, 1, 0, 3, 0, "風邪をひかないように\n温かくするんですよ～\nえ？　先生、温めて？\nふふ、甘えん坊さんね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124601, 6916, 1, 0, 4, 0, "あなたのために\nちょっと豪華なおせち\n用意したのよ\nお口に合うかしら？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 124601, 6917, 1, 0, 101, 0, "あなたも着物着たい？\nふふ、貫禄出ちゃって\n先生ドキドキ\nしちゃうかも…♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 124601, 6918, 1, 0, 102, 0, "あなたの成長は\nとってもうれしいけど\n今年もまだ、あなたの\n先生でいたいな…♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124601, 6919, 1, 1, 1, 0, "あら、起きたの？\nお雑煮できてるから\nちゃんとお顔\n洗ってらっしゃいね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124601, 6920, 1, 2, 2, 0, "お昼ご飯を\nしっかり食べて\nみんなでかるた大会\nしましょう〜♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124601, 6921, 1, 3, 3, 0, "は～い、良い子は\nもう寝る時間ですよ\n素敵な初夢が\n見られますように♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124601, 6923, 1, 0, 2, 0, "このフカフカちゃんも\n天日干ししたいけど…\nその前にちょっと\n休憩しちゃおうかしら", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124601, 6924, 1, 0, 3, 0, "うふふ、かわいい～\nアオイちゃんや\nハツネちゃんも\nきっと気に入るわね♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124601, 6925, 1, 0, 4, 0, "お正月は空気も\n透き通ってるから\nいつも以上に\n素敵な香りに感じるわ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124601, 6926, 1, 0, 5, 0, "火のぬくもりに\n抱かれていると\n不思議と眠気が…\nふああ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124601, 6928, 1, 0, 7, 0, "愛情のこもった料理は\nおいしいだけじゃなく\n心も満たされちゃう♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124601, 6929, 1, 0, 8, 0, "お正月のお作法も\nたくさんあるのね～\n先生も日々勉強だわ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 104701, 6931, 1, 0, 103, 0, "少年への感謝をどんな\n形で伝えるべきか…\n言葉よりも物の方が\n良いのだろうか？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 104701, 6932, 1, 0, 103, 0, "話し相手になって\nもらってもいいかな？\n少年と一緒にいると\nとても楽しいんだ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124501, 6933, 1, 0, 1, 0, "お正月だからって\nギャングのボスは\n油断できないよ～？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124501, 6934, 1, 0, 2, 0, "イノリちゃんがお年玉\n欲しいんだって♪\n悪い子だったし\n10ルピでいいかな☆", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124501, 6935, 1, 0, 3, 0, "お正月だからお雑煮を\n作ったんだけど\nカヤちゃんには\n大好評だったよ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124501, 6936, 1, 0, 4, 0, "ラーゴがお餅を\n食べたがってるけど…\nこの子にあげるのは\nちょっと心配かな～？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124501, 6937, 1, 0, 5, 0, "お正月は晴れ着の子が\nやっぱり多いよね～\n今度、私の漫画でも\n出してみようかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 124501, 6938, 1, 0, 101, 0, "君と二人っきりで\n神殿に初詣…\n行ってみたいな～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 124501, 6939, 1, 0, 102, 0, "君とお正月を\n過ごしていると\n面倒なことも\n全部忘れられそう…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124501, 6940, 1, 0, 1, 0, "この着物、どう思う？\n似合ってたら\nうれしいけどな～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124501, 6941, 1, 0, 2, 0, "袖を引っ張ったり\nしないでね？\n着崩れしちゃうから♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124501, 6942, 1, 0, 3, 0, "お正月ならではの\n漫画ネタってある？\n参考にしたいんだよね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124501, 6943, 1, 0, 4, 0, "昔は凧あげとか\n駒回しとかしてたけど\n大人になるとそういう\n機会も減るよね…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 124501, 6944, 1, 0, 101, 0, "君にもお年玉を\nあげようか？\n好きな額を言ってね☆", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 124501, 6945, 1, 0, 102, 0, "来年も、再来年も…\nこうしてみんなで\n新年がお祝いできると\n素敵だね～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124501, 6946, 1, 1, 1, 0, "おはよ～♪\n朝ごはんは\n焼いたお餅とか\nどうかな？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124501, 6947, 1, 2, 2, 0, "お昼だね～\nおせちを用意したから\nみんなで食べよっか", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124501, 6948, 1, 3, 3, 0, "もうこんな時間だね～\n一日中着物だったから\n肩が凝っちゃったよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124501, 6950, 1, 0, 2, 0, "このまま寝たら\nしわになるかな？\n着替えた方がいい？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124501, 6951, 1, 0, 3, 0, "こういうのって\nラーゴのおもちゃに\nちょうどいいかも♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124501, 6952, 1, 0, 4, 0, "いい香りだけど\n着物に移らないように\n気をつけないとね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124501, 6953, 1, 0, 5, 0, "ちょうどいい\n火加減だね♪\nぽかぽかするよ～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124501, 6955, 1, 0, 7, 0, "着物の時は\nほどほどにしないと…\n欲張ったら、お腹が\n苦しくなるからね～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124501, 6956, 1, 0, 8, 0, "ふーん\nイノリちゃんは最近\nこんな漫画を\n読んでるんだ～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124801, 6958, 1, 0, 1, 0, "楽園の気配は…ナシ！\nみんな前を向いてるね\nそうこなくっちゃ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124801, 6959, 1, 0, 2, 0, "レッスンの成果も\nこの格好くらい\n目に見えたら\n分かりやすいのになぁ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124801, 6960, 1, 0, 3, 0, "次の曲はワイルドで\n悪い感じもいいなぁ…\nカヤとムイミちゃん\nみたいなイメージで！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124801, 6961, 1, 0, 4, 0, "私…服、着てるよね？\nうんうん、着てる！\nあんまり体が軽いと\n急に焦る瞬間あるな～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124801, 6962, 1, 0, 5, 0, "最近ファンの年齢層が\n幅広くなってきたかも\nこれって私…国民的\nアイドルに近づいた？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 124801, 6963, 1, 0, 101, 0, "パティスリーとコラボ\nしたらどんな感じかな\n…ノゾミンマカロン♪\nとか？　…いけるかも", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 124801, 6964, 1, 0, 102, 0, "彼と一緒にいると…\nうれしくって苦しいな\nでも両方諦められない\n…アイドルらしいや", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124801, 6965, 1, 0, 1, 0, "この格好、チカと\nツムギも似合うと\n思うんだけどな～\nキミもそう思うよね？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124801, 6966, 1, 0, 2, 0, "街を歩いてたらね？\nライブとか写真集の\n告知イベントだって\n思われちゃった！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124801, 6967, 1, 0, 3, 0, "私が持ってた剣に\n結んであったリボン…\n何か気づいてくれた？\nヒントは…色だよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124801, 6968, 1, 0, 4, 0, "キミの気配ってすごく\n分かりやすいんだよ\n近くにいたらすぐに\nピンとくるんだ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 124801, 6969, 1, 0, 101, 0, "あ、それ…はじめて\nキミにあげた音響盤！\nね、一緒に聴こうよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 124801, 6970, 1, 0, 102, 0, "ぼうっとしてたら\nイタズラしちゃうよ♪\nもっともっと私のこと\n見てないとダーメ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124801, 6971, 1, 1, 1, 0, "朝は今日のはじまり！\n昨日よりももっと\nいい一日にしようね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124801, 6972, 1, 2, 2, 0, "レッスンがんばったし\nまだお昼だし…\nランチ大盛りにしても\nセーフだよね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124801, 6973, 1, 3, 3, 0, "寝る前に明日の予定を\n確認しておかないと！\nえっと、集合場所は…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124801, 6975, 1, 0, 2, 0, "一休みのつもりが\nなが～い休憩になるの\nあるあるだよね～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124801, 6976, 1, 0, 3, 0, "ノゾミンの熱烈ハグ♪\n…ここだけ聞いたら\nスクープになっちゃう\nかも？　なーんて！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124801, 6977, 1, 0, 4, 0, "香りってなんだか\n記憶に残るよねー\n…こういうの私だけ？\nみんなも同じだよね？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124801, 6978, 1, 0, 5, 0, "なんだかライブの\n熱気を思い出して\nきちゃった…！\nレッスンしよっと！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124801, 6980, 1, 0, 7, 0, "お腹が減ってたら\nいいパフォーマンスは\nできないもんね！\n…んー、おいし～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124801, 6981, 1, 0, 8, 0, "いい歌詞を書くには\nしっくりくる言葉を\n知っておかないと！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124901, 6983, 1, 0, 1, 0, "うだうだ悩んでる\nヤツがいたら\nオレのとこ連れてこい\n背中押してやるからさ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124901, 6984, 1, 0, 2, 0, "最近どことなく\nファミリーの連中が\n浮き足立ってんだよな\n…なんでだ？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124901, 6985, 1, 0, 3, 0, "イノリのヤツ\nやたらと脇腹\nつついてきやがって…\nくすぐったいっての", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124901, 6986, 1, 0, 4, 0, "身軽になったのは\nいいが…くそっ\nどこもかしこも\nスースーする…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124901, 6987, 1, 0, 5, 0, "思い出に浸るのも\n悪くはないが\nやっぱ人間\n未来に生きないとな！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 124901, 6988, 1, 0, 101, 0, "またムイミやノゾミと\nツルむのもいいな\nそこにカレもいたら\nなおいいぜ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 124901, 6989, 1, 0, 102, 0, "未来のことを考えると\nなぜかいつもカレが\n頭に浮かんじまう…\nええい、やめだやめ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124901, 6990, 1, 0, 1, 0, "ちょっくらオレと\n腕相撲しねえか？\nこの新しい力\n試させてくれ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124901, 6991, 1, 0, 2, 0, "なあ、悪いが上着\n貸してくれないか？\nこのカッコ、割と\n肌寒いんだよ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124901, 6992, 1, 0, 3, 0, "おっ、生まれ変わった\n喧嘩屋にご用命か？\nいいぜ、キミの\n依頼なら二つ返事だ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124901, 6993, 1, 0, 4, 0, "なんか悩みごとが\nあるなら、なんでも\n話してくれよな\nオレとキミの仲だろ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 124901, 6994, 1, 0, 101, 0, "このカッコが\nきれいだって？\nや、やめろよ…\n照れくさいだろ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 124901, 6995, 1, 0, 102, 0, "キミがいると、なにを\nするにも退屈しないぜ\nだから今日も\n付き合ってくれよな！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124901, 6996, 1, 1, 1, 0, "おはよう！　新しい\n未来がやってきたぜ！\n一秒だって時間は\n無駄にするなよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124901, 6997, 1, 2, 2, 0, "そろそろ昼飯に\nしないか？\n穴場のうまい店に\n案内してやるからさ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124901, 6998, 1, 3, 3, 0, "夜更かしは\n未来の前借りだ\n体壊さないように\nほどほどにしとけよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124901, 7000, 1, 0, 2, 0, "お、いいなこれ\nファミリーの\nアジトにも\n置いときたいぜ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124901, 7001, 1, 0, 3, 0, "べ、別にかわいいもん\nなんて、そんなに\n好きじゃないし…\n嫌いでもないけどさ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124901, 7002, 1, 0, 4, 0, "嗅いだことない\n香りだな\nなかなか気に入った！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124901, 7003, 1, 0, 5, 0, "薄着で暖を取るのって\n最高にぜいたくだよな\nへへっ、堪能させて\nもらうとするか", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124901, 7005, 1, 0, 7, 0, "こいつはいいや\nこのカッコだと\n消耗が激しくて\nやたら腹が減るからな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 124901, 7006, 1, 0, 8, 0, "甘辛コーデ…\nへぇ、そんなのが\nあるのか…\nわ、悪くねえかもな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 105501, 7008, 1, 0, 103, 0, "はぁ…\nもっともっと長い時間\n先輩と過ごせたらなぁ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 105501, 7009, 1, 0, 103, 0, "えへへ…♪\n先輩が、すぐそばに\nいてくれるなんて…\n幸せだなぁ…♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125001, 7010, 1, 0, 1, 0, "はーっはっは！\n強くてカッコいい\n大悪党、ここに参上！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125001, 7011, 1, 0, 2, 0, "みんなおしゃれで\nカッコいいけど\n今のアタシも\n負けてないぞ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125001, 7012, 1, 0, 3, 0, "ギャングのアジトも\nよかったけど\nここも賑やかで\n悪くないな！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125001, 7013, 1, 0, 4, 0, "悪党にも休息は必要だ\nここならゆっくり\n休めそうだ！\n邪魔するぞ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125001, 7014, 1, 0, 5, 0, "いっぱい人がいるな…\nアタシの悪党っぷりを\n広めるチャンスだ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 125001, 7015, 1, 0, 101, 0, "ここにいれば\nいい思い出がたくさん\n作れそうだな！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 125001, 7016, 1, 0, 102, 0, "いつかアタシも\nオクトーと一緒に\nこんなアジトを手に\n入れてやるからな！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125001, 7017, 1, 0, 1, 0, "ん？　アタシの格好が\n気になるのか？\nならもっと近くで\n見てもいいぞ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125001, 7018, 1, 0, 2, 0, "おい、暇だから\nアタシと遊べ！\nカヤとノゾミも呼んで\n大騒ぎするぞ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125001, 7019, 1, 0, 3, 0, "オマエ、前より強く\nなってないか！？\nくっそー！　アタシも\n負けてらんないぞー！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125001, 7020, 1, 0, 4, 0, "今日は特別にアタシの\n思い出話を聞かせて\nやる！　さーて、どの\n悪事から話そうか…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 125001, 7021, 1, 0, 101, 0, "オマエとの付き合いも\n結構長くなったよな？\nこれからも\nよろしく頼むぞ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 125001, 7022, 1, 0, 102, 0, "オマエまでいなく\nなったらアタシが困る\n勝手にどっか\n行くんじゃないぞ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125001, 7023, 1, 1, 1, 0, "起きろ、起きろー！\n大悪党の朝は早いんだ\nさっさと\n顔洗ってこい！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125001, 7024, 1, 2, 2, 0, "昼飯どうしようか…\nそうだ！　オマエの\nおススメに連れてけ！\nいい店期待してるぞ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125001, 7025, 1, 3, 3, 0, "明日を思い切り\n楽しむためにも\n早く寝とけよ！\nまた明日な！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125001, 7027, 1, 0, 2, 0, "こうやって座ると…\nおお！　すっごく悪党\nっぽいぞ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125001, 7028, 1, 0, 3, 0, "オマエ、仲間に\nなりたそうな目を\nしてるな！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125001, 7029, 1, 0, 4, 0, "うん、いい匂いだ！\n何の匂いかは全然\n分かんないけどな！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125001, 7030, 1, 0, 5, 0, "炎ってなんだか\nカッコいいよな！\n見てるだけで心が\n燃えてくるぞ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125001, 7032, 1, 0, 7, 0, "いっぱい食べれば\nちょっとは\n背が伸びるかな…？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125001, 7033, 1, 0, 8, 0, "ぐぬぅ…たまには\n本でもと思ったけど\nやっぱりアタシは\nバトル担当だな！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118201, 7035, 1, 0, 1, 0, "お邪魔しまーす★\nなーんてもう\nすっかりくつろいで\nますけどー♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118201, 7036, 1, 0, 2, 0, "騎士さんの\nギルドハウス…★\nなんだかドキドキ\nしちゃいます♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118201, 7037, 1, 0, 3, 0, "ぺんぽこりんの\n置き場所はー…\nま、このへんで★", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118201, 7038, 1, 0, 4, 0, "うーん、これは\n壊しがいがありそ…\nウソウソ、冗談です★", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118201, 7039, 1, 0, 5, 0, "オトモダチでも\n作ってみますかー\nくすっ★", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 118201, 7040, 1, 0, 101, 0, "退屈ですねー\n騎士さん、構って\nくれないかなー", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 118201, 7041, 1, 0, 102, 0, "もっともっと早く\n騎士さんに逢えてたら\n…なんて、わたし\nらしくないですね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118201, 7042, 1, 0, 1, 0, "わたし一人にするの\n不安だったりします？\nちゃーんとイイコに\nしてますってー★", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118201, 7043, 1, 0, 2, 0, "お出かけします？\nそれともおうちで\nまったりしますー？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118201, 7044, 1, 0, 3, 0, "女の子をすぐ家に\nあげちゃうなんて…\nふふ、やっぱり隅に\n置けないですねー★", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118201, 7045, 1, 0, 4, 0, "騎士さん騎士さーん★\nウザ絡みしても\nいいですかー？\n騎士さんってばー★", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 118201, 7046, 1, 0, 101, 0, "一緒にゴロゴロ\nしませんかー？\nコイビトっぽくて\n楽しそうですし♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 118201, 7047, 1, 0, 102, 0, "うふふ★\nこーやってあなたに\n優しくされると…\nときめいちゃいます♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118201, 7048, 1, 1, 1, 0, "おはようございます★\nあれれー？\nまだおねむですかー？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118201, 7049, 1, 2, 2, 0, "騎士さん、お昼は\nもう食べましたー？\nわたし、ラーメンが\n食べたいでーす★", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118201, 7050, 1, 3, 3, 0, "あ、夜更かししてちゃ\nダメですよー？\nほらほら、お布団に\nごーごー★", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118201, 7052, 1, 0, 2, 0, "ふかふかのふわふわー\nついついまーったり\nしちゃいます★", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118201, 7053, 1, 0, 3, 0, "かっわいー★\n名前はー、んー…\nぱんぱかぷんで★", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118201, 7054, 1, 0, 4, 0, "くんくん★\nうーん…\nたぶん素敵な香り♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118201, 7055, 1, 0, 5, 0, "あったかーい★\n心も体も\nぽっかぽかですねー★", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118201, 7057, 1, 0, 7, 0, "もぐもぐ★\nんー、おいしくって\n止まらないですねー★", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118201, 7058, 1, 0, 8, 0, "へー★\nほほー★\nふんふーん★", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125101, 7060, 1, 0, 1, 0, "さ～て時間もあるし\nラテアートの練習でも\nするかにゃ～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125101, 7061, 1, 0, 2, 0, "カフェの猫たちも\n元気に働いてくれて\nお客さんからの評判も\n上々にゃ～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125101, 7062, 1, 0, 3, 0, "カフェのカウンターに\nファントムキャッツの\n目安箱を置こうかにゃ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125101, 7063, 1, 0, 4, 0, "あんこもコーヒーも\n原料は豆にゃ～\nそりゃ相性いいに\n決まってるにゃ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125101, 7064, 1, 0, 5, 0, "あいつの為に\nタマキ特製ブレンドを\n開発したいにゃ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 125101, 7065, 1, 0, 101, 0, "あいつと働いてると\nなぜかあっという間に\n閉店時間が\n来ちゃうのにゃ～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 125101, 7066, 1, 0, 102, 0, "あいつといると\n新しいアイデアが\n溢れるほど\n出てきちゃうのにゃ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125101, 7067, 1, 0, 1, 0, "キミがコーヒーを\n淹れてくれたのにゃ？\nにゃはは、常温で\n猫舌にも優しいにゃ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125101, 7068, 1, 0, 2, 0, "料理が上手くなったら\nカフェご飯メニューも\n充実させたいにゃ～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125101, 7069, 1, 0, 3, 0, "お店の経営は大変\nだけど、キミとなら\n乗り越えられるって\n確信してるにゃ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125101, 7070, 1, 0, 4, 0, "ん？　この制服\n似合ってるにゃ？\nにゃはは！　褒めても\n１ルピも出ないにゃ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 125101, 7071, 1, 0, 101, 0, "あたしが好きなのは\n猫に好かれる人にゃ♪\n…ん、キミ猫に懐かれ\nまくってるにゃ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 125101, 7072, 1, 0, 102, 0, "キミの制服姿\nけっこーイケてるにゃ\nあたしがお客さんなら\n通いつめちゃうにゃ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125101, 7073, 1, 1, 1, 0, "朝は一杯の\nコーヒーから始まる…\nにゃふふん♪\nシャレた朝だにゃ～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125101, 7074, 1, 2, 2, 0, "お昼はたい焼きと\nコーヒーのセットで\nまったり休憩\nしようにゃ～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125101, 7075, 1, 3, 3, 0, "コーヒー淹れたにゃ\n星空を見ながら\n二人で飲もうにゃ…♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125101, 7077, 1, 0, 2, 0, "ふにゃ～ん…ゴロゴロ\nコーヒーと同じくらい\n癒されるにゃ～…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125101, 7078, 1, 0, 3, 0, "こいつかわいいにゃ～\nうちの猫たちとも\nいい勝負なのにゃ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125101, 7079, 1, 0, 4, 0, "ん～いい香りだにゃ～\nコーヒーもそうだけど\n香りの癒し効果は\n抜群にゃ～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125101, 7080, 1, 0, 5, 0, "猫に暖かいものは\nまるまれと言ってる\nようなもんにゃ…♪\nまるまる…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125101, 7082, 1, 0, 7, 0, "にゃは～うまいにゃ♪\nカフェの新メニューに\n採用しちゃおう\nかにゃ～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125101, 7083, 1, 0, 8, 0, "にゃるほど…\n調べれば調べるほど\nコーヒーは\n奥が深いのにゃ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 107101, 7085, 1, 0, 103, 0, "ワタシの手料理は\n坊やの口に合うらしい\n胃袋は討ち取ったぞ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 107101, 7086, 1, 0, 103, 0, "胸に再度触れると\n誓約は無効になるのか\n…だと？　鋭い質問だ\n安心しろ。ならん☆", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125201, 7087, 1, 0, 1, 0, "ようやくこの服にも\n慣れてきたかな？\nふふっ、たまには\nこういう服も悪くない", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125201, 7088, 1, 0, 2, 0, "今日も店は好調だが、\n私は軍人だからな\n当初の目的も\n忘れないようにしよう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125201, 7089, 1, 0, 3, 0, "猫が近くに来ると\nついつい見てしまうな\n仕事が終わったら\n遊んでやるかな♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125201, 7090, 1, 0, 4, 0, "コーヒーの淹れ方が\nうまくなってきたな♪\nどうせならお菓子も\n研究してみるか！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125201, 7091, 1, 0, 5, 0, "利益を考えると\n価格をあげるべきか？\n…いや、利益よりも\n皆の笑顔の方が大事だ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 125201, 7092, 1, 0, 101, 0, "またあいつを\n呼んでしまったな…\n今度、コーヒーでも\n淹れて労ってやるか♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 125201, 7093, 1, 0, 102, 0, "次に入荷するお菓子は\nあいつの意見を聞くか\nどんな味が好きか\n気になるしな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125201, 7094, 1, 0, 1, 0, "なに、コーヒーを\n淹れてほしい？\nふふっ♪\n仕方のない奴だな！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125201, 7095, 1, 0, 2, 0, "…そうだ。\nコーヒーと乾パンは\n合うのではないか？\n今度試してみよう！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125201, 7096, 1, 0, 3, 0, "コーヒーもいいが、\nココアも悪くないぞ♪\n貴公も一緒にどうだ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125201, 7097, 1, 0, 4, 0, "魔法少女に比べれば\n大人しい服だろう？\nだが、カフェには\nこの服でバッチリだ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 125201, 7098, 1, 0, 101, 0, "貴公はコーヒーを\n飲めるのかっ？それは\nうらやましいな…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 125201, 7099, 1, 0, 102, 0, "貴公の働きぶりには\n目を見張るものがある\nその姿は、その…\n結構かっこいいぞっ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125201, 7100, 1, 1, 1, 0, "さぁ、今日も一日、\n頑張るぞっ！\n気合いを入れて\n開店準備だっ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125201, 7101, 1, 2, 2, 0, "昼は稼ぎ時だからな！\n荒波を乗り切るため\n貴公も一緒に\n頑張ってくれるか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125201, 7102, 1, 3, 3, 0, "ふあぁ…もう夜か\n今日は頑張ったからな\nはやめに休むとしよう", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125201, 7104, 1, 0, 2, 0, "はふぅ～…\nたまにはこうして\nゆったりするのも\n悪くないな…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125201, 7105, 1, 0, 3, 0, "猫もかわいいが、\nこいつもなかなか…\n見ていると、\n撫でたくなるな♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125201, 7106, 1, 0, 4, 0, "これは良い香りだな\nふむ、コーヒーも\nより香り高いものが\n作れるよう試すか！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125201, 7107, 1, 0, 5, 0, "ふぅ～…\n体を温めなければ、\nすぐに動けないからな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125201, 7109, 1, 0, 7, 0, "これもうまいが、\n私はお菓子も…\nいや、食べすぎは\n体に良くないな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125201, 7110, 1, 0, 8, 0, "…書物は参考になるな\n次はこのやり方を\n試してみるかな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120001, 7112, 1, 0, 1, 0, "愛と希望の魔法少女！\n弟くんの平和は\nお姉ちゃんが守るよ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120001, 7113, 1, 0, 2, 0, "魔法少女の戦い方を\n見せてあげる！\nお姉ちゃんチョップ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120001, 7114, 1, 0, 3, 0, "闇の力が高まってる…\n早く弟くんを\nギュ～ってしないと！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120001, 7115, 1, 0, 4, 0, "ミニ弟くんを\n部屋中に呼び出して\n弟くんハウスに～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120001, 7116, 1, 0, 5, 0, "決めポーズの練習でも\nしよっかな…\nはい、ポーズ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 120001, 7117, 1, 0, 101, 0, "魔法少女の力で\n弟くんのそばに\n瞬間移動したいなぁ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 120001, 7118, 1, 0, 102, 0, "禁じられた必殺技\nその名も──\n闇のお姉ちゃん頭突き", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120001, 7119, 1, 0, 1, 0, "魔法少女な\nお姉ちゃんに\n興味津々だねっ？\n全部教えてあげるね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120001, 7120, 1, 0, 2, 0, "闇のお姉ちゃんとして\n世界を弟くんのものに\n…な～んて冗談冗談♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120001, 7121, 1, 0, 3, 0, "弟くんって魔法少女に\nなりたがってたよね？\n…うん。応援するね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120001, 7122, 1, 0, 4, 0, "サインが欲しいの？\nふふっ、任せて！\n愛する弟くんへ、と♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 120001, 7123, 1, 0, 101, 0, "ふんふふ～ん♪\n魔法少女お姉ちゃんの\nテーマソング完成っ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 120001, 7124, 1, 0, 102, 0, "えへへ、弟くんからの\n愛と希望を感じるよ…\n更に変身しちゃうぞ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120001, 7125, 1, 1, 1, 0, "弟くんの朝を魔法で\n守りに来たよ！\n希望の朝だねっ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120001, 7126, 1, 2, 2, 0, "弟くんのお昼を魔法で\n守りに来たよ！\n愛のお弁当をどうぞ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120001, 7127, 1, 3, 3, 0, "弟くんの夜を魔法で\n守りに来たよ！\nここからは闇の時間♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120001, 7129, 1, 0, 2, 0, "魔法少女の一休み♪\n愛と希望と…闇の力が\n回復していくね～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120001, 7130, 1, 0, 3, 0, "かわいいかわいい♪\nお姉ちゃんの魔法で\n増やしちゃうぞ…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120001, 7131, 1, 0, 4, 0, "この香り…\n弟くんと同じ…\nくんくん、くんくん…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120001, 7132, 1, 0, 5, 0, "体を温めておいて…\nこの熱を、ぜ～んぶ\n弟くんに伝えるよ…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120001, 7134, 1, 0, 7, 0, "ミニ弟くんを呼ぶには\nいっぱい食べないと…\n魔法ってお腹減るから", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 120001, 7135, 1, 0, 8, 0, "弟くんの平和を守る…\nそのためには\nチェックチェック…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125301, 7137, 1, 0, 1, 0, "この服も、最初は\n慣れなかったのですが\n今ではすっかり\n体に馴染んでおります", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125301, 7138, 1, 0, 2, 0, "自然を守るためにも\nお勉強は欠かせません\n日々精進でございます", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125301, 7139, 1, 0, 3, 0, "採取した薬草を\n調合しておきましょう\nいつ必要になるか\n分かりませんからね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125301, 7140, 1, 0, 4, 0, "合宿は過酷でしたが\nおかげでちょっと\n力持ちになったような\n気がいたします…♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125301, 7141, 1, 0, 5, 0, "わんわんのほうの\n主さまに、ごはんを\nあげる時間ですね…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 125301, 7142, 1, 0, 101, 0, "もっともっと訓練や\nお勉強を重ねて\n主さまのお役に\n立たなくては…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 125301, 7143, 1, 0, 102, 0, "おや、そろそろ\nわんわんのほうの\n主さまをお散歩に\n連れていく時間です", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125301, 7144, 1, 0, 1, 0, "主さま、よろしければ\nお散歩に行きませんか\n今日の森は穏やかで\n過ごしやすいかと", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125301, 7145, 1, 0, 2, 0, "わんわんのほうの\n主さまも、今では\n【美食殿】の大切な\n仲間でございます", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125301, 7146, 1, 0, 3, 0, "森には危険が\nいっぱいでございます\nケガをなさらないよう\nお気をつけください", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125301, 7147, 1, 0, 4, 0, "もし森や動物のことで\n気になることがあれば\n気軽にお尋ねください", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 125301, 7148, 1, 0, 101, 0, "森へ行く前には\n必ず装備の点検を\nお願いいたします\n約束ですよ、主さま？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 125301, 7149, 1, 0, 102, 0, "森の中で主さまたちと\nゆったり過ごす時間は\nわたくしにとって\n大切なひとときです", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125301, 7150, 1, 1, 1, 0, "おはようございます\n朝ごはんをしっかり\n食べて、お勤めに\n備えましょう", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125301, 7151, 1, 2, 2, 0, "わんわんのほうの\n主さまにおやつを\nあげる時間です\nどうぞ、召し上がれ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125301, 7152, 1, 3, 3, 0, "どれだけ野営に\n慣れたとしても\nベッドで眠れるなら\nそうしたいところです", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125301, 7154, 1, 0, 2, 0, "こちらなのですが\nわんわんのほうの\n主さまもお気に入りの\nようです", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125301, 7155, 1, 0, 3, 0, "わんわんのほうの\n主さまにも、こうした\n玩具を与えるべき\nでしょうか…？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125301, 7156, 1, 0, 4, 0, "心地よい香りです\n次は、自然由来の\n香料も試しましょう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125301, 7157, 1, 0, 5, 0, "暖をとるのは\n野営の時などに\nとても大事なことで\nございますね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125301, 7159, 1, 0, 7, 0, "食べられる時に食べて\nおくのも、大自然では\n重要でございます", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125301, 7160, 1, 0, 8, 0, "より知識を蓄え\n自然の力を活用できる\nようにいたしましょう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 100901, 7162, 1, 0, 103, 0, "今回の「冥風戦記」も\nなかなかよく書けたぞ\nふふっ、シグルドも\n喜んでくれるかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 100901, 7163, 1, 0, 103, 0, "貴様のためならば\n我が真なる魔力\nいつでも\n解き放とうぞ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125401, 7164, 1, 0, 1, 0, "ニャットちゃんに\nブラッシングを\nしてあげようかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125401, 7165, 1, 0, 2, 0, "ふふ…\nこれがレンジャーの\n制服ですか", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125401, 7166, 1, 0, 3, 0, "そろそろ\nパトロールの\n時間だ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125401, 7167, 1, 0, 4, 0, "魔物について\nもっと知りたいな\n本を探しに行こう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125401, 7168, 1, 0, 5, 0, "レンジャーになって\n体調を崩す日が\n減った気がします…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 125401, 7169, 1, 0, 101, 0, "本だけじゃわからない\n世界のことを、\nもっと知りたいです…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 125401, 7170, 1, 0, 102, 0, "こんな私にも\nできることはある…\nそう信じています", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125401, 7171, 1, 0, 1, 0, "あの…良かったら\n一緒に見回りへ\n行きませんか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125401, 7172, 1, 0, 2, 0, "またあなたと一緒に\nレンジャーの仕事が\nできたらうれしいです", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125401, 7173, 1, 0, 3, 0, "けほっこほっ\n…だ、大丈夫です\nたまに出るだけなので", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125401, 7174, 1, 0, 4, 0, "ニャットちゃん\nあなたにも\n懐いてきましたね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 125401, 7175, 1, 0, 101, 0, "ニャットちゃんと\n散歩に出かけようかな\n…あの人も誘って", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 125401, 7176, 1, 0, 102, 0, "あなたと肩を並べて\nレンジャーの仕事が\nできてうれしいです…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125401, 7177, 1, 1, 1, 0, "おはようございます\nレンジャー・シオリ\n元気に出勤します", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125401, 7178, 1, 2, 2, 0, "ニャットちゃんにも\nお昼ごはんを\n用意しなくちゃ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125401, 7179, 1, 3, 3, 0, "明日は仕事だから\n早起きしなくちゃ\nおやすみなさい", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125401, 7181, 1, 0, 2, 0, "ふぅ…仕事で\n走り回ったので\n少し休憩します", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125401, 7182, 1, 0, 3, 0, "仕事が終わった後に\nこうして触れていると\n癒されますね…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125401, 7183, 1, 0, 4, 0, "森の香りがします…\n今日はよく\n眠れそうです…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125401, 7184, 1, 0, 5, 0, "ニャットちゃんと\n同じぐらい\n暖かいです…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125401, 7186, 1, 0, 7, 0, "レンジャーの仕事を\nしていると\nお腹が空きます", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125401, 7187, 1, 0, 8, 0, "どんなに忙しくても\n読書だけは\nやめられませんね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125801, 7189, 1, 0, 1, 0, "兄様のお留守の間\nわたくしが、兄様の\n御家を守ります！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125801, 7190, 1, 0, 2, 0, "に、に、兄様の御家…\n招待いただいた以上は\n失礼のないよう\n振る舞わなくては…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125801, 7191, 1, 0, 3, 0, "ふふっ…兄様の素敵な\nお人柄が、家中から\nにじみ出ています…♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125801, 7192, 1, 0, 4, 0, "靴を脱がずに\n御家に上がるのに\n慣れていなくて…あ！\n飛べばいいのでは…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125801, 7193, 1, 0, 5, 0, "ここに花を生けたら\n華やぎそうですね\n兄様はお花…\n好きでしょうか…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 125801, 7194, 1, 0, 101, 0, "ずっとここにいたい…\nなどと言ったら\n兄様を困らせて\nしまいますよね…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 125801, 7195, 1, 0, 102, 0, "お料理などを作って\n兄様を出迎えられたら\nいいのに…料理の腕も\n磨かなくては…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125801, 7196, 1, 0, 1, 0, "このような姿で\n失礼しております…！\nネ、ネズミがいたので\nつい本気を…！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125801, 7197, 1, 0, 2, 0, "おかえりなさいませ\nお忙しい御身をどうぞ\n癒してくださいね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125801, 7198, 1, 0, 3, 0, "兄様の話し相手…！？\nど、どうしましょう…\nわたくしには面白い\n話なんて…はうぅ…！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125801, 7199, 1, 0, 4, 0, "なぜか、動物などに\n好かれる体質で…\n迷い猫捜しのバイトの\nお役に立てますか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 125801, 7200, 1, 0, 101, 0, "に、兄様の家に来たら\n『添い寝』をするのが\n礼儀とリアさんが…\n本当…でしょうか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 125801, 7201, 1, 0, 102, 0, "わたくしはここに\nいて…よいのですね？\n『仲間』として…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125801, 7202, 1, 1, 1, 0, "朝は得意なんです\nもし兄様が苦手なら\nわたくしが起こして\n差し上げますよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125801, 7203, 1, 2, 2, 0, "兄様…昼餉を作って\nみたのですが、失敗\nしてしまいました…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125801, 7204, 1, 3, 3, 0, "ふあぁ…\nはっ！　お…\nお恥ずかしい所を…！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125801, 7206, 1, 0, 2, 0, "こ、これは…！\n人様の御家なのに\nつい…駄目になって…\nしまいますぅ…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125801, 7207, 1, 0, 3, 0, "と、飛びかかっては\nこない…ですよね…？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125801, 7208, 1, 0, 4, 0, "すぅ…はぁ…\nこのかぐわしさ\n心が洗われます…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125801, 7209, 1, 0, 5, 0, "なんて暖かい…\n兄様の優しくお広い\n御心のようです…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125801, 7211, 1, 0, 7, 0, "こんなに食べていたら\nはしたない…\nでしょうか？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125801, 7212, 1, 0, 8, 0, "兄様の本棚はまるで\n知識の宝庫ですね\n己の世間知らずぶりを\n実感してしまいます", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125501, 7214, 1, 0, 1, 0, "次の冒険に向けて\n騎空士弁当を作ろっと\nまずはおにぎりから！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125501, 7215, 1, 0, 2, 0, "魔法の修行しなくちゃ\nもっともっと頼れる\n団長になりたいから！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125501, 7216, 1, 0, 3, 0, "ビカラちゃんを\n遊びに誘いたいけど…\nどういう場所が\n好きなんだろう？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125501, 7217, 1, 0, 4, 0, "仕事を真面目に\nしっかりこなすことが\n騎空艇への\n一番の近道だよね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125501, 7218, 1, 0, 5, 0, "ここにいる人の中で\n騎空団に興味ある人\nいたりしないかな…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 125501, 7219, 1, 0, 101, 0, "これまでも想い出は\nいっぱいできたけど…\nこれからもたくさん\n想い出を作りたいな！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 125501, 7220, 1, 0, 102, 0, "仲間がいるから\n騎空士は限界だって\n超えられるんだね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125501, 7221, 1, 0, 1, 0, "この衣装\n魔法使いらしいでしょ\n服に着られないよう\n頑張らなくちゃね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125501, 7222, 1, 0, 2, 0, "あなたとなら\nどんな相手だって\n怖くなんてないよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125501, 7223, 1, 0, 3, 0, "カリンさんから\n新しい依頼が来たよ！\n一緒に頑張ろ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125501, 7224, 1, 0, 4, 0, "操舵士になるのは\nずいぶん難しそう…\nいつか頼れる仲間が\n見つかるといいな", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 125501, 7225, 1, 0, 101, 0, "手合わせしたいの？\nいいよ、たまには\n団長らしいところ\n見せてあげるね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 125501, 7226, 1, 0, 102, 0, "まだ二人きりだけど…\nこの騎空団は\n私の宝物だよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125501, 7227, 1, 1, 1, 0, "おはよう～…\n昨日は遅くまで\n魔法の研究してて…\nふわぁ…眠いなぁ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125501, 7228, 1, 2, 2, 0, "さてと、お昼休み！\n紅茶淹れてくるから\n一緒に飲もうよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125501, 7229, 1, 3, 3, 0, "遅くまでお疲れさま！\n無理しない程度に\nお互い頑張ろう！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125501, 7231, 1, 0, 2, 0, "魔法の研究は\n一旦、休憩！\n頭を休めなくちゃ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125501, 7232, 1, 0, 3, 0, "魔法みたいに\nかわいい！\n騎空団に入る？\nなんてね！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125501, 7233, 1, 0, 4, 0, "いい匂い…\nそれになんだか\n集中力が増したかも", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125501, 7234, 1, 0, 5, 0, "体だけじゃなくて\n心までポカポカする…\nなんだか魔法みたい", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125501, 7236, 1, 0, 7, 0, "いろいろ考え事したら\nお腹が空いちゃった！\nいただきまーす！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125501, 7237, 1, 0, 8, 0, "『摂理の彼方へ\n至るには…』\n…この魔導書\n勉強になるなあ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125701, 7239, 1, 0, 1, 0, "こちらの服を着ると\n冒険に出かけたく\nなっちゃいますね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125701, 7240, 1, 0, 2, 0, "冒険者の心得その一\n準備はできる限り\nしっかりとこなすべし\nなんてどうでしょう？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125701, 7241, 1, 0, 3, 0, "調合に必要な素材は\nあれとこれとそれと…\n時間があるとついつい\n考えちゃいますね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125701, 7242, 1, 0, 4, 0, "せっかくですから\nより良い素材を\n自分で集めたいですね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125701, 7243, 1, 0, 5, 0, "このポーションが\nみなさんの役に立つと\nうれしいですね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 125701, 7244, 1, 0, 101, 0, "冒険者の心得その二\n危なくなった時は\nまず逃げるべし！\n大事なことですね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 125701, 7245, 1, 0, 102, 0, "あの人の冒険は\nいつも楽しそうで\n元気がもらえます", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125701, 7246, 1, 0, 1, 0, "ぜひおすすめしたい\n依頼があるんです\nこちらをどうぞ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125701, 7247, 1, 0, 2, 0, "ほしいお薬があれば\n錬金術で作って\nさしあげますよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125701, 7248, 1, 0, 3, 0, "依頼で気になる\nところがあれば\n何でもご確認ください", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125701, 7249, 1, 0, 4, 0, "このギルドハウスに\n錬金術の実験設備を…\nえ？　だめですか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 125701, 7250, 1, 0, 101, 0, "ポーションをどうぞ\n大丈夫、効果は\n確認済みですので♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 125701, 7251, 1, 0, 102, 0, "次はどんな冒険に\n行かれるんですか？\nいつでもお供しますよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125701, 7252, 1, 1, 1, 0, "今日もいい一日に\nなりそうですね\n冒険日和といった\nところでしょうか", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125701, 7253, 1, 2, 2, 0, "いい時間ですし\nランチにしませんか？\n次の冒険の\n作戦会議も兼ねて♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125701, 7254, 1, 3, 3, 0, "夜も遅いですし\n明日の冒険のため\n今日は早く寝ましょう", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125701, 7256, 1, 0, 2, 0, "ふう…やはり冒険は\n事務仕事と違いますね\n少し休憩を…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125701, 7257, 1, 0, 3, 0, "冒険は緊張の連続です\nかわいい物で\n心を癒しましょう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125701, 7258, 1, 0, 4, 0, "ふむ、この香りは…\n素晴らしいバランスで\n調合されていますね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125701, 7259, 1, 0, 5, 0, "この火力…\n錬金術の調合に\n使えそうですね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125701, 7261, 1, 0, 7, 0, "冒険の際は食べられる\n時に食べておく！\n…とペコリーヌさんも\nおっしゃってましたね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125701, 7262, 1, 0, 8, 0, "ふむふむ…なるほど\nこんな調合も\nあるのですね…\n奥が深いです", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 106101, 7264, 1, 0, 103, 0, "アイツどこ行った…？\n戦うならアタシも\n連れてけよなー！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 106101, 7265, 1, 0, 103, 0, "今日も悪いヤツ退治…\nの前に腹ごしらえだ！\nなんか食べに行くぞ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125601, 7266, 1, 0, 1, 0, "みんなー！\nギルドハウスで\nぼくと握手！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125601, 7267, 1, 0, 2, 0, "種族も年齢も違うけど\nみんな楽しそうだね！\nやっぱりここはぼくの\nゆ～とぴあさ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125601, 7268, 1, 0, 3, 0, "ハハッ！　まるで\nパーティー会場だね！\nこれは楽しませがいが\nあるってものだよ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125601, 7269, 1, 0, 4, 0, "やぁみんな！\nご利益満点のネズ耳は\nいかがかな？\nひとつ３ルピだよ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125601, 7270, 1, 0, 5, 0, "ここにいれば\n遊び相手に困らないね\nかしこみかしこみ～！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 125601, 7271, 1, 0, 101, 0, "どこか遊びに\n行こうかな♪\nそうだ、あの人にも\n声をかけてみよう！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 125601, 7272, 1, 0, 102, 0, "あの人といると\nぼくまで楽しくなって\nくるんだよね！\n毎日パレード気分さ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125601, 7273, 1, 0, 1, 0, "ネズ耳の在庫が\n切れたんだ！\n作るのを\n手伝ってくれるかい？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125601, 7274, 1, 0, 2, 0, "ん？　握手かい？\nいいとも！\nいつでも何度でも\nしてあげるよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125601, 7275, 1, 0, 3, 0, "この衣装に興味が\nあるのかい？\nきみ、なかなか\n見る目があるね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125601, 7276, 1, 0, 4, 0, "しばらくここに厄介に\nなるつもりなんだ\nあとで街を案内\nしてくれるかい？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 125601, 7277, 1, 0, 101, 0, "とっておきのチーズ\nきみと半分こだ！\nみんなには内緒だよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 125601, 7278, 1, 0, 102, 0, "きみはいてほしい時に\nいつもそばに\nいてくれるね\n本当にかしこみだよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125601, 7279, 1, 1, 1, 0, "やぁ、おはよう！\n気持ちのいい朝だね\n今日も楽しくネズ耳\n売りにはげもうか！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125601, 7280, 1, 2, 2, 0, "お昼だね！\nおいしいチーズを\n持ってきたから\nぼくと半分こだ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125601, 7281, 1, 3, 3, 0, "今日も一日\n楽しかったね！\nいい夢が見られること\n間違いなしさ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125601, 7283, 1, 0, 2, 0, "ぼくにも休息は\n必要さ！\nリラックス〜\nリラックス〜♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125601, 7284, 1, 0, 3, 0, "ぼくのぬいぐるみも\n作って売ろうかな…\n修繕費も一気に\n集まるかもね！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125601, 7285, 1, 0, 4, 0, "いい香りだね！\nトーゴクのお香に\nよく似てるよ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125601, 7286, 1, 0, 5, 0, "はぁ…温まるねぇ…\nそうだ、この火で\nチーズでも炙って\nみようかな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125601, 7288, 1, 0, 7, 0, "うーん、この味…\nトーゴクを思い出すよ\nどれ、もう一つ\nいただこうか！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 125601, 7289, 1, 0, 8, 0, "これはランドソルの\nガイド本だね！\nありがたく参考に\nさせてもらうよ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126201, 7291, 1, 0, 1, 0, "まさか、わらわが\nこのような装束を\n纏う日がくるとはな…\n感慨深いものじゃ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126201, 7292, 1, 0, 2, 0, "わらわの美貌で\nこのギルドハウスも\nより華やかに\nなったようじゃな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126201, 7293, 1, 0, 3, 0, "つまらぬ戦いで\n晴れ着を汚しとうない\n今日は一日\nゆっくりするかの", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126201, 7294, 1, 0, 4, 0, "今日はわらわが食事の\n準備をして、あやつの\n帰りを待ってやろう\n献立は何がよいかの…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126201, 7295, 1, 0, 5, 0, "ユキといったか…\n掴みどころのない\nやつじゃが、根性は\n認めてやるかの", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 126201, 7296, 1, 0, 101, 0, "こうして、仲間と\nのんびり過ごす時間も\n悪くないのう♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 126201, 7297, 1, 0, 102, 0, "あやつを誘って\n月を見に行こうかの\n夜が楽しみじゃ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126201, 7298, 1, 0, 1, 0, "いかなる衣装で\nあろうとも、わらわは\n完璧に着こなして\nしまうのじゃ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126201, 7299, 1, 0, 2, 0, "なんじゃ？\nわらわの姿に\n見惚れておったとな？\nす、素直でよいのじゃ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126201, 7300, 1, 0, 3, 0, "吸血鬼であるわらわに\n相応しい装束じゃろう\nあとは真っ赤な血を\n添えたいのう…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126201, 7301, 1, 0, 4, 0, "お主、疲れておるな？\nわらわが癒してやろう\nほれ、近う寄るのじゃ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 126201, 7302, 1, 0, 101, 0, "お主がおるだけで\nギルドハウスが\n賑やかになるのう\nある種の才能じゃな♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 126201, 7303, 1, 0, 102, 0, "眷属よ、風呂にするか\nご飯にするか\nそれとも、わらわに…\nいや、忘れるのじゃ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126201, 7304, 1, 1, 1, 0, "朝一番、わらわに\n会いたかったとな…\n眷属よ\n褒めてつかわすぞ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126201, 7305, 1, 2, 2, 0, "眷属よ、わらわが\n食事を用意したぞ\n忌々しい太陽に負けず\n頑張るのじゃ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126201, 7306, 1, 3, 3, 0, "眠るには惜しい夜じゃ\n眷属よ、もうしばらく\nわらわの傍に\nおるがよい", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126201, 7308, 1, 0, 2, 0, "ふかふかじゃの♪\n今度あやつにも\n座らせてやろう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126201, 7309, 1, 0, 3, 0, "愛いやつじゃ\n少しばかり、わらわが\n遊んでやろうかの", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126201, 7310, 1, 0, 4, 0, "気品ある香りじゃのう\nわらわの古城にも\n一つ欲しいところじゃ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126201, 7311, 1, 0, 5, 0, "よいぬくもりじゃ…\n忌々しい太陽とは\n大違いじゃの", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126201, 7313, 1, 0, 7, 0, "味付けの参考に\nあと、もう一つだけ…\n止まらんのじゃ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126201, 7314, 1, 0, 8, 0, "吸血鬼の弱点について\n記されておるのか…\nふむ、眠気が\nさめてしもうたぞ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 100801, 7316, 1, 0, 103, 0, "このままボクが美しく\nなり続けても、彼なら\nきっと隣にいてくれる\nはずだよね！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 100801, 7317, 1, 0, 103, 0, "もっとそばに来なよ\nボクの隣は\nキミのための\n特等席なんだから", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126301, 7318, 1, 0, 1, 0, "ドレスの影響かな…\n前より黒や赤が\n好きになったかも", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126301, 7319, 1, 0, 2, 0, "ボクがいるだけで\nここがぐんっと\n華やかになったね♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126301, 7320, 1, 0, 3, 0, "ボクを描いた絵画\n今度持ってきて\n飾ってあげようかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126301, 7321, 1, 0, 4, 0, "ボクはボクに\n魅了されっぱなしさ♪\nまさに魔性の存在だよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126301, 7322, 1, 0, 5, 0, "美のアドバイスを\n聞きたい人いるかい？\nボクがもっともっと\n輝かせてあげるよ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 126301, 7323, 1, 0, 101, 0, "あの人、今度はボクを\nどう褒めるだろう？\nもし語彙が尽きても\n許してあげよっと♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 126301, 7324, 1, 0, 102, 0, "あの人に似合うコーデ\n考えてあげよっと\n案外、なんでも\n着こなしそうだよね♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126301, 7325, 1, 0, 1, 0, "吸血鬼になるなんて\nやっぱりゴメンだね！\n永遠の美よりも\n大事なことがあるのさ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126301, 7326, 1, 0, 2, 0, "お肌にいいものは\n大体好きだよ\nトマトジュースとかね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126301, 7327, 1, 0, 3, 0, "銀のアクセサリーが\nボクは大好きでね\nいつでもプレゼント\n待ってるよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126301, 7328, 1, 0, 4, 0, "イリヤみたいにお城に\n住んでみたいんだ♪\n美を統べる者に\n相応しいでしょ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 126301, 7329, 1, 0, 101, 0, "今のボクがカップを\n触ると壊しちゃうんだ\nだからほら、キミが\nボクに紅茶を飲ませて", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 126301, 7330, 1, 0, 102, 0, "キミも美を磨けば\nもっと輝けるはずさ！\nボクがメイクとか\nしてあげようか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126301, 7331, 1, 1, 1, 0, "んん、まだ眠い…\n美のためにも、\nもうひと眠りするよ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126301, 7332, 1, 2, 2, 0, "ボクにぴったりな\n日傘を買ったんだ♪\n散歩にでも行こうよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126301, 7333, 1, 3, 3, 0, "夜こそボクの時間！\nさぁ、あまねく人々へ\nボクの美を広めよう！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126301, 7335, 1, 0, 2, 0, "リラックスは大事だよ\n心の余裕こそが\n美の秘訣だからね♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126301, 7336, 1, 0, 3, 0, "キミもボクの\n美の虜にしてあげる♪\nうれしいでしょ？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126301, 7337, 1, 0, 4, 0, "人々を魅了するほどの\n魔性の香り…\nボクにふさわしいね♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126301, 7338, 1, 0, 5, 0, "冷えは美の大敵さ\nボクのように\n美しくなりたいなら\n体を温めるべきだよ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126301, 7340, 1, 0, 7, 0, "食事をするボクの\nなまめかしさ…♪\n吸血鬼すらも\nドキッとするだろうね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126301, 7341, 1, 0, 8, 0, "吸血鬼って\n美しい存在だよね…\nボクほどではないけど", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126401, 7343, 1, 0, 1, 0, "ここがナイトさんの\nお家なんですねーっ！\n雰囲気そっくりっ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126401, 7344, 1, 0, 2, 0, "ドアも壁もお菓子じゃ\nないんですよねっ？\n不思議ですねーっ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126401, 7345, 1, 0, 3, 0, "失礼のないように\n気をつけないとっ！\nライラエルさまとも\n約束してますからっ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126401, 7346, 1, 0, 4, 0, "向こうに帰るころには\nお土産話がいっぱい\nできてるといいな～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126401, 7347, 1, 0, 5, 0, "戦闘用のアームの他に\n観光用のアームも\nあったらいいのにな～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 126401, 7348, 1, 0, 101, 0, "ナイトさんの充填と\nライラエルさまの充填\nどっちも違って\nどっちも大好きです♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 126401, 7349, 1, 0, 102, 0, "…ナイトさんが\nマスターになって\nくれたら…えへへっ\nなんでもないですっ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126401, 7350, 1, 0, 1, 0, "お手伝いだったら\n任せてくださいっ！\nお家の移転とか\nしちゃいますかっ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126401, 7351, 1, 0, 2, 0, "こっちに来るときは\nポポおじいさんが\nしっかりメンテして\nくれるんですよっ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126401, 7352, 1, 0, 3, 0, "お家の中にいるのも\nいいですけど、お外も\n気になります～っ！\nお出かけしましょう♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126401, 7353, 1, 0, 4, 0, "ヴォルガーノさんに\nそっくりの子猫さんを\n見かけたんですっ！\nご親戚ですかねっ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 126401, 7354, 1, 0, 101, 0, "ここだけの話ですよ？\n…実はケーブルって\n触られると、ちょっと\nくすぐったいんですっ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 126401, 7355, 1, 0, 102, 0, "このお家の持ち主は\nナイトさんですよね？\nそれじゃここでは\nご主人さまですねっ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126401, 7356, 1, 1, 1, 0, "おはようございますっ\n今日もフルパワーで\nがんばりましょうっ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126401, 7357, 1, 2, 2, 0, "お昼の時間ですねっ！\nランチの充填、\nお願いしまーすっ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126401, 7358, 1, 3, 3, 0, "そろそろ眠らなくて\n大丈夫ですかっ？\n夜ふかしさんなら\nお付き合いしますよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126401, 7360, 1, 0, 2, 0, "エネルギーの消費を\n抑えるのも大切\nですよね～っ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126401, 7361, 1, 0, 3, 0, "わたしが触っても\n平気なんて…！\nかわいくて頑丈っ！\n好きですっ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126401, 7362, 1, 0, 4, 0, "センサーによると…\nこの香りはいい香りに\n該当するみたいですっ\nなるほどーっ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126401, 7363, 1, 0, 5, 0, "あったかいのって\nいいですよねっ！\nみなさんうれしそう\nですしっ♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126401, 7365, 1, 0, 7, 0, "これがこっちの世界の\nごはんなんですねっ\nおいしい気がします！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126401, 7366, 1, 0, 8, 0, "そーっとそーっと…\n本を破かないように…\n書いてある内容より\n難しいです～っ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126701, 7368, 1, 0, 1, 0, "ウィスタリア家の\nプライベートビーチも\nご案内しますわね♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126701, 7369, 1, 0, 2, 0, "世界を巡る\nリゾートクルーズも\nご用意できますわよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126701, 7370, 1, 0, 3, 0, "避暑地に専用の\n別荘がありますの\n今度ご招待しますわね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126701, 7371, 1, 0, 4, 0, "日焼けは乙女の\n天敵ですわね\n備えはしっかりと…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126701, 7372, 1, 0, 5, 0, "線香花火だけでは\n物足りませんわね\nもっとドカーンと…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 126701, 7373, 1, 0, 101, 0, "ギルド活動も全力で！\nバカンスも全力で！\nそれが私ですわ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 126701, 7374, 1, 0, 102, 0, "私のセンスで選んだ\n特注の水着ですもの\n似合って当然ですわ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126701, 7375, 1, 0, 1, 0, "ご希望ならすぐにでも\n夏の飛空艇遊覧ツアー\nへご招待しますわ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126701, 7376, 1, 0, 2, 0, "夏の賑やかな\n雰囲気は好きですが…\n暑さには参りますわね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126701, 7377, 1, 0, 3, 0, "日の光を浴びるほどに\n輝きを増す夏の私！\n今日も絶好調ですわ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126701, 7378, 1, 0, 4, 0, "夏祭りに協力するなら\n資金と人材、どちらも\n用意しないとですわね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 126701, 7379, 1, 0, 101, 0, "あなたさま…その…\nそうも見つめられると\n照れてしまいます…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 126701, 7380, 1, 0, 102, 0, "タマキさんの働く\n海の家も訪れて\nみたいですわね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126701, 7381, 1, 1, 1, 0, "港の市場が\n賑わう頃ですわね！\n私たちも参りますわよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126701, 7382, 1, 2, 2, 0, "冷たい飲み物でも\nご一緒にいかが？\nうちのシェフに用意\nさせてありますの", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126701, 7383, 1, 3, 3, 0, "まだもう少し…\nこの夏の夜を\nあなたさまと…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126701, 7385, 1, 0, 2, 0, "あなたさまも\n良ければこちらへ\nいらっしゃいな♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126701, 7386, 1, 0, 3, 0, "夏に合わせた装いに\n替えてみるのも\n楽しそうですわね♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126701, 7387, 1, 0, 4, 0, "この夏めいた爽やかな\n香り…期間限定で\n売り出したいですわ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126701, 7388, 1, 0, 5, 0, "く…我慢です…\nこの暑さに耐えてこそ\n美しさにも磨きが…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126701, 7390, 1, 0, 7, 0, "暑い季節に働く\nみなさまへ差し入れも\n必要ですわね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126701, 7391, 1, 0, 8, 0, "夏のトレンドを\n押さえておくのも\n大事ですわね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126701, 7393, 1, 0, 10, 0, "上手くいけば\nヒット商品になる\nポテンシャルを\n感じますわね…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126701, 7394, 1, 0, 11, 0, "この極上の肌触り…\n言い値で買いますわ\n一体おいくらですの？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126601, 7395, 1, 0, 1, 0, "せっかくのバカンス…\n自由に羽を\n伸ばすとしましょう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126601, 7396, 1, 0, 2, 0, "たとえどのような\n秘密を持とうとも\n私には筒抜けですよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126601, 7397, 1, 0, 3, 0, "子供だましの怪談に\n関心はありませんが…\n霊には興味があります\n観察対象として、ね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126601, 7398, 1, 0, 4, 0, "彼の絵日記…\nあとでゆっくりと\n読ませてもらいます", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126601, 7399, 1, 0, 5, 0, "セミの鳴き声に入道雲\nそしてクリームソーダ\nこれでこそ夏ですね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 126601, 7400, 1, 0, 101, 0, "夏休みの宿題でも\n作るとしましょうか\n彼の限界を少し超えた\n負荷を与えるには…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 126601, 7401, 1, 0, 102, 0, "彼と過ごす夏を\n特別なものにしたい…\nそれが私の望みです", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126601, 7402, 1, 0, 1, 0, "夏だからといって\n浮かれすぎないように\n節度を持って\n楽しみなさい？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126601, 7403, 1, 0, 2, 0, "ああ見えてもマサキは\n私のプリンセスナイト\nあまり侮られては\n困りますよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126601, 7404, 1, 0, 3, 0, "私の秘密ですか？\nまんじゅうが怖い…\nというのはどうです？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126601, 7405, 1, 0, 4, 0, "ここは誰かの秘密を\n盗み聞くのに\n最適な場所ですね\n…ジョークですよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 126601, 7406, 1, 0, 101, 0, "一緒に風鈴の音を\nのんびり楽しもう…？\n…別に構いませんよ\n貴方とならば", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 126601, 7407, 1, 0, 102, 0, "貴方に約束しましょう\n決して忘れられない\n夏にしてあげますよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126601, 7408, 1, 1, 1, 0, "早起きとは感心ですね\nでは一緒に\n体操でもしましょうか", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126601, 7409, 1, 2, 2, 0, "昼食は流しそうめん？\n非効率的ですね\n…食べないとは\n一言も言っていません", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126601, 7410, 1, 3, 3, 0, "…貴方が望むならば\nいくらでも怖い話を\nしてあげますが…\nいかがですか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126601, 7412, 1, 0, 2, 0, "七冠にも休息は\n必要なのです\nしばらくここで\n休ませてもらいますよ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126601, 7413, 1, 0, 3, 0, "なかなか気に入った…\nというのは\n誰にも秘密です", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126601, 7414, 1, 0, 4, 0, "薫風を思わせる\nこのさわやかな香り…\n悪くないセンスですね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126601, 7415, 1, 0, 5, 0, "夏だからこそ\n冷え対策は\n重要なのです\n…とはいえ暑いですね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126601, 7417, 1, 0, 7, 0, "私がどのような味が\n好きなのかは…\n当然秘密です", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126601, 7418, 1, 0, 8, 0, "夏を舞台にした\n小説は数多くあります\n彼が好きそうな本を\n何冊か見繕いましょう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126601, 7420, 1, 0, 10, 0, "見た目も手触りも\n完璧に覚えました\nいつでも模倣できます", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126601, 7421, 1, 0, 11, 0, "ふむ…夏らしい\nクッションですね\nナイトプールに\n映えそうです", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 105401, 7422, 1, 0, 103, 0, "レイ様も格好良いけど\n騎士さんもなかなか…\nって、今の聞こえて\nませんよねっ？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 105401, 7423, 1, 0, 103, 0, "騎士さんはシンプルな\n服が似合いそうです！\n今度、一緒に\n見に行きましょう♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126801, 7424, 1, 0, 1, 0, "最初はどうかな～って\n思ったけど…\n水着姿でも案外\n問題ないのよね…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126801, 7425, 1, 0, 2, 0, "水着で飲みすぎると\nお腹が冷える、って\n言うけど…麦しゅわ\nなら問題ないわね！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126801, 7426, 1, 0, 3, 0, "暑ければ暑いほど\nその後の麦しゅわは\nおいしくなるのよ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126801, 7427, 1, 0, 4, 0, "しっかりと仕事を\nこなしてから、\nしっかりと麦しゅわ！\nやっぱりこれね！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126801, 7428, 1, 0, 5, 0, "ふふっ、デザインも\n着心地も最高ね\n奮発して用意して\n正解だったわ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 126801, 7429, 1, 0, 101, 0, "伝説の麦しゅわ、\n飲めるものなら\nまた飲みたいわね…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 126801, 7430, 1, 0, 102, 0, "二つ名に恥じない\n聖騎士として\n私…飲むわ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126801, 7431, 1, 0, 1, 0, "この格好なら水に\n濡れても大丈夫！\nなんだけど、さすがに\n書類は濡らせないわね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126801, 7432, 1, 0, 2, 0, "そうやってじっと\n見られると…ちょっと\n恥ずかしいわ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126801, 7433, 1, 0, 3, 0, "今日もいい天気ね\nふふっ、あとで\nビーチに遊びに\n行きましょ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126801, 7434, 1, 0, 4, 0, "水着姿も慣れると\n案外快適ね\nそうだ、キミも\n水着になっちゃう？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 126801, 7435, 1, 0, 101, 0, "例え水着姿であろうと\n聖騎士としての誇りは\n忘れてないわ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 126801, 7436, 1, 0, 102, 0, "キミとだったら\n麦しゅわなしでも\n最高の夏のバカンスに\nなると思うわ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126801, 7437, 1, 1, 1, 0, "仕事終わりの\n麦しゅわのために\n今日も一日\n頑張るわよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126801, 7438, 1, 2, 2, 0, "今日のお昼は\nどうしようかしら…\nさ、さすがに昼からは\n自重してるわよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126801, 7439, 1, 3, 3, 0, "さ、一日の疲れを\n洗い流すために…\n軽～く飲むわよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126801, 7441, 1, 0, 2, 0, "こうやって\n直に触れると\n肌触りの良さが\nよく分かるわ♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126801, 7442, 1, 0, 3, 0, "今日もかわいいわね～\nどう？　今度\nお姉さんの一人飲みに\n付き合わない？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126801, 7443, 1, 0, 4, 0, "いい香りね～…はっ！\n麦しゅわの匂いの\nアロマポットなんて\nあったらよくない？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126801, 7444, 1, 0, 5, 0, "こんな格好で火に\n当たってると焙られる\nスルメの気分が\nちょっと分かるわ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126801, 7446, 1, 0, 7, 0, "もぐもぐ…うん、\n結構おつまみにも\nむいてそうね～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126801, 7447, 1, 0, 8, 0, "試験に使った参考書、\nたまには読み返して\nおかないとね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126801, 7449, 1, 0, 10, 0, "ふふ♪\nいい手触りね～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126801, 7450, 1, 0, 11, 0, "…はふう～…\nゴロゴロするのって\n最高ね～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126901, 7451, 1, 0, 1, 0, "ん…やっぱり\nこの水着…\n大胆、なんじゃ…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126901, 7452, 1, 0, 2, 0, "海でもお世話って、\nできるのかしら…？\n浮き輪代わりになる、\nとか…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126901, 7453, 1, 0, 3, 0, "～♪\nふふ…海に行く\n準備も、楽しい…かも", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126901, 7454, 1, 0, 4, 0, "あうぅ…水着で\n歩いてると…お尻…\nすぐ食い込んじゃう…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126901, 7455, 1, 0, 5, 0, "海では、はしゃいだ\nほうがいいのよね…\nわ、わー、い…♪\n…こうかしら？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 126901, 7456, 1, 0, 101, 0, "あなたとなら…\n楽しい夏の思い出、\n作れる…気がするの…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 126901, 7457, 1, 0, 102, 0, "また来年も…\n再来年も…\nあなたと海へ\n行けますように…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126901, 7458, 1, 0, 1, 0, "水着の着方を\n練習してるの…\n後ろ、変じゃない…？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126901, 7459, 1, 0, 2, 0, "海に行く支度…\n進んでるかしら…？\nお手伝い、する…？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126901, 7460, 1, 0, 3, 0, "ひ、日除けの準備は\n万全よ…\nずっと太陽の下に\nいたら…溶けちゃう…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126901, 7461, 1, 0, 4, 0, "泳ぐ練習…\nこ、ここでするのは\n…さすがに変？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 126901, 7462, 1, 0, 101, 0, "人混みに入るのは\nまだ不安で…\nでも、あなたといれば\nきっと…大丈夫…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 126901, 7463, 1, 0, 102, 0, "あなたのおかげで\n海の楽しいところを\nたくさん知ったわ…\nありがとう…♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126901, 7464, 1, 1, 1, 0, "お出かけの日の朝は\n早い、のよね…？\nだから、ずっと\n起きていたわ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126901, 7465, 1, 2, 2, 0, "今頃ビーチの日差しは\n強いんでしょうね…\nそ、想像しただけで…\nめまいが…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126901, 7466, 1, 3, 3, 0, "ビーチの夜といえば\n花火って、聞いたわ…\nバン、って言わない\n花火…ある…？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126901, 7468, 1, 0, 2, 0, "素肌にふわふわが…\n気持ちいい…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126901, 7469, 1, 0, 3, 0, "うふふ…\n一緒に海…行く？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126901, 7470, 1, 0, 4, 0, "ふぅ…♪\n気分まで…\n晴れやかになるわ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126901, 7471, 1, 0, 5, 0, "暑いような…\n寒いような…\n不思議な気持ちに\nなるわ…ね？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126901, 7473, 1, 0, 7, 0, "泳いだ後には\nいっぱい食べたほうが\n体によさそう、ね…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126901, 7474, 1, 0, 8, 0, "ビーチの日陰で\n読書するのも…\n気持ちよさそう…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126901, 7476, 1, 0, 10, 0, "これで合ってる…？\n遊び方わからなくて…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126901, 7477, 1, 0, 11, 0, "ふわふわ…♪\n海の底にいるみたい…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127001, 7478, 1, 0, 1, 0, "恥ずかしい格好で\n室内を歩くこの快感\nクセになりそうです…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127001, 7479, 1, 0, 2, 0, "この水着…\nどうしてかポーズを\n決めたくなりますね…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127001, 7480, 1, 0, 3, 0, "海にはどんな生き物が\nいるのでしょう～\n胸が高鳴ります～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127001, 7481, 1, 0, 4, 0, "夏は暑いですね…\nクウカの体が熱いのも\n夏のせい…ぐふふ…♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127001, 7482, 1, 0, 5, 0, "ドＳさんと無人島に\n取り残されて…という\n妄想はかなりイイです", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 127001, 7483, 1, 0, 101, 0, "ドＳさんの\n欲望に満ちた眼差し…\nクウカ、受け止めます", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 127001, 7484, 1, 0, 102, 0, "海に行くのが\n楽しみです…\nドＳさんと…ぐふふ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127001, 7485, 1, 0, 1, 0, "「もっと過激な水着を\n見せてみろよ」なんて\nいけませんドＳさん…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127001, 7486, 1, 0, 2, 0, "水着のままで\n踊ればいいのですね…\nドＳさんの望みなら！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127001, 7487, 1, 0, 3, 0, "ドＳさんの熱い視線を\n感じます…じゅるり…\nもっと見てください…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127001, 7488, 1, 0, 4, 0, "こんな格好のクウカを\n放っておくなんて…\nさすがですドＳさん！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 127001, 7489, 1, 0, 101, 0, "海で水着といえば\n何かが起きる予感…！\nドＳさんの新たな攻め\n期待していますね…！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 127001, 7490, 1, 0, 102, 0, "恥ずかしいですけど\n頑張っちゃいました\nドＳさん…こういう\n水着、好きですか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127001, 7491, 1, 1, 1, 0, "こんな朝からクウカを\n水着に着替えさせて…\n本当にドＳさんは\n欲望に正直ですね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127001, 7492, 1, 2, 2, 0, "こんな昼間から\nドＳさんがヌルヌルの\n液体をクウカに～！\nえっ…日焼け止め？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127001, 7493, 1, 3, 3, 0, "ドＳさん…これから\nクウカと夜の海を\n見に行きませんか？\nきっと綺麗ですよ…♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127001, 7495, 1, 0, 2, 0, "ここなら妄想に\n集中できます～♪\nじゅるり～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127001, 7496, 1, 0, 3, 0, "こうもかわいいと\n妄想に登場させるのも\n気が引けます…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127001, 7497, 1, 0, 4, 0, "クウカを昂らせる\nこの香り…！\nは！　ドＳさんの罠！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127001, 7498, 1, 0, 5, 0, "体を熱くさせて\n水着を脱がそうと！？\nテクニカルです…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127001, 7500, 1, 0, 7, 0, "クウカの口の中に\nたくさん…♪\n止まりません…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127001, 7501, 1, 0, 8, 0, "読書は心が穏やかに\nなりますね…！\nぐふふ…穏やかに…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127001, 7503, 1, 0, 10, 0, "いい乗り心地です…♪\nあっ…普通に楽しんで\nしまいました…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127001, 7504, 1, 0, 11, 0, "これは…\n妄想に役立ちそうな\n感触です…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 100501, 7505, 1, 0, 103, 0, "成長した自分に\nあわせて、もっと\nかっこいいポーズを\n研究するッス！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 100501, 7506, 1, 0, 103, 0, "これからも一緒に\n最高のヒーローを\n目指していくッスよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127101, 7507, 1, 0, 1, 0, "お父さん、娘の\n水着姿がそんなに\nうれしいの？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127101, 7508, 1, 0, 2, 0, "怖い話を子どもたちに\n聞かせたら泣かれて\nしまいました…\n加減が難しいです…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127101, 7509, 1, 0, 3, 0, "海に行くと父が\n水着の女性ばかり\n追いかけて困ります…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127101, 7510, 1, 0, 4, 0, "拾った貝殻で\n新しい占いでも\nはじめてみようかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127101, 7511, 1, 0, 5, 0, "貝殻を耳に当てると\n波の音が…あっ\nこれはダメです\nよくない声がします", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 127101, 7512, 1, 0, 101, 0, "この飲み物、スーッと\n体に沁み込んでくる…\nあの人にも\n教えてあげたいです", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 127101, 7513, 1, 0, 102, 0, "強い日差しも騒がしい\n海も苦手なのに\nあの人となら不思議と\n楽しく思えるんです", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127101, 7514, 1, 0, 1, 0, "夏限定、水と氷を\n使った特別な占いは\nいかがですか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127101, 7515, 1, 0, 2, 0, "暑い日々にはやはり\n怪談ですね\n厳選フルコースで\nお聞かせします", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127101, 7516, 1, 0, 3, 0, "寒気がする？　それは\n霊ではなくかき氷の\n食べ過ぎですよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127101, 7517, 1, 0, 4, 0, "今日の占い、あなたの\nラッキーパーソンは…\n白い水着の女性です", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 127101, 7518, 1, 0, 101, 0, "あの、よければ\n日焼け止めを塗って\nもらえませんか？\n背中は届かなくて…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 127101, 7519, 1, 0, 102, 0, "海に行きませんか？\n二人きりで静かな\n砂浜を散歩したいです", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127101, 7520, 1, 1, 1, 0, "潮騒だけが響く朝の海\n心が落ち着きます\nこれから一緒に散歩に\n行ってみませんか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127101, 7521, 1, 2, 2, 0, "不思議です。どうして\n普通のご飯でも、海で\n食べると特別おいしく\n感じるのでしょう…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127101, 7522, 1, 3, 3, 0, "夜の海に行きませんか\n今なら夜光虫が\n見られるかも\nしれませんよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127101, 7524, 1, 0, 2, 0, "夏は知らぬ間に体力を\n消耗しています\n適度に休息して\nいきましょう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127101, 7525, 1, 0, 3, 0, "このぬいぐるみ…\n大丈夫です\n何も憑いては\nいませんよ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127101, 7526, 1, 0, 4, 0, "ほのかにお線香\nみたいな香り…\nなんだか\n落ち着きます", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127101, 7527, 1, 0, 5, 0, "人魂、鬼火\nセントエルモの火…\n炎を見ていると色々と\n連想してしまいます", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127101, 7529, 1, 0, 7, 0, "お腹を壊さぬよう\n食い合わせには\nご注意ください", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127101, 7530, 1, 0, 8, 0, "これはいい\n怪談集ですね…\nつい読みふけって\nしまいます", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127101, 7532, 1, 0, 10, 0, "ふふ、こういうのに\n乗るとなんだか\n子どもに戻った\nみたいですね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127101, 7533, 1, 0, 11, 0, "こ、これは…\nちょっとメルヘン\n過ぎて恥ずかしい\nですね…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126501, 7534, 1, 0, 1, 0, "本当に素敵なお家…♪\n騎士たちの人柄を\n感じられるようで…\n不思議と落ち着きます", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126501, 7535, 1, 0, 2, 0, "調度品をつい間食…\nという習慣がこちらの\n世界にはないのですね\n…気をつけないと", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126501, 7536, 1, 0, 3, 0, "手土産のお菓子は\nどちらに置けば\nよいでしょうか？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126501, 7537, 1, 0, 4, 0, "まるでおとぎ話の中に\n入ってしまったかの\nよう…別世界とは\nよく言ったものです", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126501, 7538, 1, 0, 5, 0, "お忍び…ふふっ♪\nときめく響きです\nなんだかお茶目で…\n密やかな印象が", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 126501, 7539, 1, 0, 101, 0, "ふわ…\nああ、ついあくびが…\n居心地がよすぎるのも\n困り物ですね…ふふ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 126501, 7540, 1, 0, 102, 0, "騎士には不思議な力が\nあるだけでなく\n何者かの加護が\nあるようですね…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126501, 7541, 1, 0, 1, 0, "この『王家の装備』は\n少し気難しいのです\nわたし以外が触れると\nその…ビリビリっと", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126501, 7542, 1, 0, 2, 0, "子どもたちに新しい\n遊びを教わりました♪\nにーらめっこしましょ\nあっぷっ…ぷ～っ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126501, 7543, 1, 0, 3, 0, "実は憧れていたのです\n友人の家を訪ねると\nいう経験に…ふふっ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126501, 7544, 1, 0, 4, 0, "ぎるどはうす…\nぎるど活動の拠点だと\n管理協会？　の方に\nうかがいました", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 126501, 7545, 1, 0, 101, 0, "あなたは運命を\n信じますか？\nわたしは…信じます\nよい運命だけですが♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 126501, 7546, 1, 0, 102, 0, "もしわたしが本当に\n天使だったとしたら…\nどうされますか？\n…ふふ、冗談です♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126501, 7547, 1, 1, 1, 0, "パンケーキの香りで\n目覚める朝は\nとても良いものですよ\nぜひともお試しを♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126501, 7548, 1, 2, 2, 0, "ひと休みの時間ですね\n食事にお昼寝…\nすべきことを行って\n英気を養いましょう", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126501, 7549, 1, 3, 3, 0, "そろそろ休まれては？\n眠れないようであれば\n寝物語か子守歌でも\nふふ、遠慮は無用です", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126501, 7551, 1, 0, 2, 0, "すう………はっ！\n眠ってはいませんよ？\nいくら居心地が良い\nからといって…ふわぁ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126501, 7552, 1, 0, 3, 0, "まあ、あいらしい…♪\nぜひわたしの寝室に\n招待させてください", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126501, 7553, 1, 0, 4, 0, "なんて、かぐわしい…\nオーブンからただよう\n焼き菓子の香りとは\nまた異なる魅力が…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126501, 7554, 1, 0, 5, 0, "みなで暖をとりながら\n互いに語り笑い合う\n…心休まる時間ですね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126501, 7556, 1, 0, 7, 0, "食は心と体に\nゆとりを生みます\n…辛い食べ物の場合？\nそれは、その…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126501, 7557, 1, 0, 8, 0, "こちらの世界の知識を\n蓄えておかなければ\nテーブルマナーに\nついてのページは…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127201, 7559, 1, 0, 1, 0, "新しい色の蛍光ペン\n買ったのよね\nこれで勉強が\n楽しくなるわ～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127201, 7560, 1, 0, 2, 0, "学食のランチ、結構\nおいしいのよね～\n今度ペコリーヌに\n教えてやろうかしら？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127201, 7561, 1, 0, 3, 0, "ふふふ…扉に黒板消し\n仕掛けてるの…あいつ\n引っかかるかしら？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127201, 7562, 1, 0, 4, 0, "部活するなら運動部も\n悪くないかもね～\n夕日に向かって\n突っ走ってやるわ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127201, 7563, 1, 0, 5, 0, "毎日窮屈な制服着て\n学生も大変ね～\n…ま、かわいいけど", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 127201, 7564, 1, 0, 101, 0, "あいつに貸したノート\n変な落書きとか\nしてなかったよね…？\n…その、相合傘とか…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 127201, 7565, 1, 0, 102, 0, "昨日の家庭科で\n作ったクッキー…\nあいつにどうやって\n渡そうかな…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127201, 7566, 1, 0, 1, 0, "宿題教えてって？\nしょうがないわね～\nじゃあ一緒に\nやりましょ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127201, 7567, 1, 0, 2, 0, "あ、明日あたしたち\n日直当番よね？\nめんどい仕事は\nあんたに任せるわ～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127201, 7568, 1, 0, 3, 0, "寮生活は楽しいけど\n就寝時間が早すぎよね\nいきなり消灯されると\nビックリするわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127201, 7569, 1, 0, 4, 0, "校則で禁止だけど、\nにけつでほうき通学\nとか青春っぽくない？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 127201, 7570, 1, 0, 101, 0, "制服って好きな人とも\n自然にお揃いになれて\nいいわよね…って別に\nあんたとって訳じゃ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 127201, 7571, 1, 0, 102, 0, "あんたネクタイ\n曲がってるわよ～？\nほら直したげるから\nこっち向いて？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127201, 7572, 1, 1, 1, 0, "おはよ～！\n今日も元気に登校よ！\n忘れ物ないようにね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127201, 7573, 1, 2, 2, 0, "午前の授業疲れたわ～\nしっかりお昼を食べて\n午後も頑張るわよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127201, 7574, 1, 3, 3, 0, "うう…学校の怪談\n思い出しちゃった…\n…別に怖くないけど！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127201, 7576, 1, 0, 2, 0, "ふかふか～\n…気持ち良すぎて\n制服のまま\n寝ちゃいそうね…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127201, 7577, 1, 0, 3, 0, "かわいくて癒されるわ\n寮に持ち込んでも\n大丈夫かしら…？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127201, 7578, 1, 0, 4, 0, "集中力の高まる香りで\n勉強の効率爆上がりね\n次のテストで\nいい点取るわよ～！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127201, 7579, 1, 0, 5, 0, "うう、ちょっと冷えて\n来たわね…\nスカートの下に\nジャージ穿こうかしら", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127201, 7581, 1, 0, 7, 0, "もぐっ…もぐっ…\n食べとかないと教室で\nお腹なっちゃうわ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127201, 7582, 1, 0, 8, 0, "図書室で借りた本\n明日返却日なのよね…\n今日中に読むわよ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 104301, 7584, 1, 0, 103, 0, "あいつに背中を\n預けるのもいい気分だ\nいつかは、全部…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 104301, 7585, 1, 0, 103, 0, "な、なんだよ？\nじっと見るんじゃねぇ\n尻尾が揺れちまうだろ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127301, 7586, 1, 0, 1, 0, "魔法の練習！\nむむむ～…\nそれっ…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127301, 7587, 1, 0, 2, 0, "そだ！　これ着て\nルー学行こっかな？　\n制服自慢しちゃお♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127301, 7588, 1, 0, 3, 0, "赤点回避の魔法とか\nあったら最高じゃね？\n誰かうちに教えて～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127301, 7589, 1, 0, 4, 0, "制服が一番映える\n場所ってどこだろ？\n教室？　遊園地？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127301, 7590, 1, 0, 5, 0, "ベンキョーつらたん…\n今度キャルっちに\n助けてもらお", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 127301, 7591, 1, 0, 101, 0, "部活か…\nヒデサイと一緒に\nやったら楽し～かも♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 127301, 7592, 1, 0, 102, 0, "男子の制服\nもにカワだった…\n今度ヒデサイに\n借りよっかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127301, 7593, 1, 0, 1, 0, "ちょすー、うちの\nさいきょー魔法で\nテンアゲしてあげる～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127301, 7594, 1, 0, 2, 0, "九九って呪文ぽくね？\nほら、例えばヒチシ…\nヒチシ、なんだっけ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127301, 7595, 1, 0, 3, 0, "ヒデサイセンセー\nうちにベンキョー\n教えて！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127301, 7596, 1, 0, 4, 0, "学割マジ助かるー\nヒデサイ、一緒に\nどんどん使ってこー♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 127301, 7597, 1, 0, 101, 0, "今度ヒデサイに\nお弁当作ったげる\n屋上で一緒に食べよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 127301, 7598, 1, 0, 102, 0, "ヒデサイのバイブス\n上がる言葉教えてよ\nつらたんな時、うちが\nいっぱい言ったげるよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127301, 7599, 1, 1, 1, 0, "ちょすちょす～\n今日も朝から\nバイブス十分♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127301, 7600, 1, 2, 2, 0, "お昼どうしよっかな\nたまにはガッツリ\nいっちゃう系？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127301, 7601, 1, 3, 3, 0, "やべ、宿題まだ\nできてないし…\nヒデサイ助けて～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127301, 7603, 1, 0, 2, 0, "充電ちう～\n…ガッコーのイスも\nこれにして～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127301, 7604, 1, 0, 3, 0, "かわいいし、\n癒されるし、キュン死\nしちゃうじゃん…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127301, 7605, 1, 0, 4, 0, "リフレッシュの\n魔法発見！\nいい香り～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127301, 7606, 1, 0, 5, 0, "ぽかぽかサイコー♪\n今日はもーずーっと\nここにいたいかも～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127301, 7608, 1, 0, 7, 0, "超おいし～♪\n学食にあったら\n毎日リピする～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127301, 7609, 1, 0, 8, 0, "ふむふむ…\nねむみパない…\n続きはまた今度～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127501, 7611, 1, 0, 1, 0, "今度は子どもたちも\nサラサリアに連れて\n行ってあげたいわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127501, 7612, 1, 0, 2, 0, "子どもたちから\nお姫さまみたいって\n言われちゃった♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127501, 7613, 1, 0, 3, 0, "この剣…火を点けたり\nするのに便利なのよ\nコントロールも効くし", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127501, 7614, 1, 0, 4, 0, "あのサラサリアの\nカラっとした暑さが\nちょっと恋しいわ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127501, 7615, 1, 0, 5, 0, "買ってきたお土産、\nお世話になってる\nみんなに配りましょ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 127501, 7616, 1, 0, 101, 0, "あんたと一緒なら\nどこの国でだって\n暮らしていけそうね♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 127501, 7617, 1, 0, 102, 0, "今の事業を大きくして\nあんたと世界中の国を\n飛び回ったり…なんて\nすてきじゃない？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127501, 7618, 1, 0, 1, 0, "サラサリアで食べた\n料理が恋しい…？\nもう…また今度\n作ってあげるわよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127501, 7619, 1, 0, 2, 0, "今度旅行するなら\nどこに行こうかしら？\n希望があれば教えてね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127501, 7620, 1, 0, 3, 0, "サラサリアの香辛料、\nランドソルでも\n人気が出そうよね\nいいビジネスの予感！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127501, 7621, 1, 0, 4, 0, "そんなにじっと\n見られるとさすがに\n恥ずかしいってば…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 127501, 7622, 1, 0, 101, 0, "お嫁さんは憧れるけど\nお婿さんになって\nもらうのもありよね…\nあんたはどう思う？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 127501, 7623, 1, 0, 102, 0, "やっぱり、あんたには\nいつもあたしの隣に\n居てほしいな…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127501, 7624, 1, 1, 1, 0, "陽射しが強くなる前に\n雑多な用事は\n片付けちゃいましょ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127501, 7625, 1, 2, 2, 0, "サラサリアで食べた\n料理を再現してみたの\n味見してくれる？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127501, 7626, 1, 3, 3, 0, "暑さに負けないように\n睡眠はちゃんと\n取っておかないと", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127501, 7628, 1, 0, 2, 0, "こっちでもあっちでも\n忙しいのは変わらない\nわね…ちょっと休憩～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127501, 7629, 1, 0, 3, 0, "サラサリアで見た\nぬいぐるみも\nかわいかったわね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127501, 7630, 1, 0, 4, 0, "お香って地域ごとの\n特色が出るわよね\nこの香りは…\nちょっと神秘的？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127501, 7631, 1, 0, 5, 0, "砂漠地方も夜は冷える\nのよね。温かいものが\nあると安心するわ…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127501, 7633, 1, 0, 7, 0, "食べ慣れた味って\nどうしてほっと\nするのかしら…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127501, 7634, 1, 0, 8, 0, "今後の取引相手に\nなるかもしれないし\n向こうの文化も\n勉強しておかないと…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 101401, 7636, 1, 0, 103, 0, "犯人はこの中にいる！\n探偵として一度は\n言ってみたいセリフさ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 101401, 7637, 1, 0, 103, 0, "助手くんの情報を\n仕入れておかないと…\n理想の私を、ずっと\n見ていてほしいからね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127601, 7638, 1, 0, 1, 0, "久々の長旅で\n里心がついちまったね\nしばらくランドソルで\nのんびりしようか", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127601, 7639, 1, 0, 2, 0, "たまには違う服装\n違う得物を使うのも\n悪くないね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127601, 7640, 1, 0, 3, 0, "サラサリアにいた時は\n砂まみれで大丈夫かと\n思ってたけど、今じゃ\n懐かしく感じるよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127601, 7641, 1, 0, 4, 0, "魔法の絨毯、あれは\n楽だし速いし便利\nだったねえ…土産に\n一枚欲しかったな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127601, 7642, 1, 0, 5, 0, "砂上での戦いは\nなかなかいい鍛錬に\nなったね…今度は\n修行しに行くとしよう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 127601, 7643, 1, 0, 101, 0, "サラサリアの人たちも\n気のいい連中だったが\n元気にやってるかねえ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 127601, 7644, 1, 0, 102, 0, "旅の恥はかき捨てって\nいうけど、悪い旅じゃ\nなかったね。次は\nアイツとどこに行こう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127601, 7645, 1, 0, 1, 0, "この格好を冷やかしに\n来たのかい？\n生憎、もうすっかり\n慣れちまったよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127601, 7646, 1, 0, 2, 0, "ギルドの連中に\n土産をわたしに\n行くところなんだ\n付き合っておくれよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127601, 7647, 1, 0, 3, 0, "久々に水場で\n釣りがしたいねえ…\nもちろんお前さんも\nくるんだろう？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127601, 7648, 1, 0, 4, 0, "おっと\nサラサリアの連中から\n便りが来てるよ\nさ、一緒に読もうか", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 127601, 7649, 1, 0, 101, 0, "最近サラサリアの踊り\nを練習してるんだ\nあとで振付けを\n見ておくれよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 127601, 7650, 1, 0, 102, 0, "なんだいお前さん？\n…なんだか若奥さん\nっぽいって？　まあ、\nそりゃあ、ねえ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127601, 7651, 1, 1, 1, 0, "いい時間だね\n日が昇りきる前に\nやることをすませ\nとかないと", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127601, 7652, 1, 2, 2, 0, "昼はどうしようかね\nサラサリア風の\n食事でもつくろうか", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127601, 7653, 1, 3, 3, 0, "さて、早めに寝ようか\n砂漠の夜は冷えるから\n…っと、まだ旅行中の\n気分でいたよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127601, 7655, 1, 0, 2, 0, "良い座り心地だ\nサラサリア宮殿の\n応接間を\n思い出すねえ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127601, 7656, 1, 0, 3, 0, "ランプの魔神に\nあげたら喜ぶかねえ？\n次に会う時までに\n見繕ってみようか", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127601, 7657, 1, 0, 4, 0, "この香り…\nサラサリア土産の\n香木かい？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127601, 7658, 1, 0, 5, 0, "旅先での焚火を\n思い出すよ\nなんだかほっとするね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127601, 7660, 1, 0, 7, 0, "ふふ、慣れてる味も\nいいけど、むこうの\n味付けが懐かしく\nなっちまうね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127601, 7661, 1, 0, 8, 0, "ふむふむ…\n千夜一夜物語か\nおもしろいもんだねえ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126001, 7663, 1, 0, 1, 0, "立派なギルドハウス♪\n帰る場所が素敵だと\n冒険に出るのも\n楽しくなりそうです♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126001, 7664, 1, 0, 2, 0, "お掃除でも…と\n思いましたけど、\nさすがお兄さんのお家\nきれいにしてますね♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126001, 7665, 1, 0, 3, 0, "わたしたちもいつか\n立派なギルドハウスが\n持てる冒険者に\nなりたいです…♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126001, 7666, 1, 0, 4, 0, "わたし冒険者ですけど\n家を守ってお兄さんの\n帰りを待つのも\n悪くない…かも？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126001, 7667, 1, 0, 5, 0, "お菓子焼こうかな…♪\nちょっと多めに作って\nお兄さんにも\n食べてもらおうっと♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 126001, 7668, 1, 0, 101, 0, "このお家、とっても\n大好きになりました♪\nだからその…もう少し\nいてもいいですか…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 126001, 7669, 1, 0, 102, 0, "お兄さんに\n感謝の手紙を書いて\n枕元に隠して\nおこうかな…♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126001, 7670, 1, 0, 1, 0, "…お兄さん\nお暇なんですか？\nじゃあ一緒に冒険へ\n出かけましょう♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126001, 7671, 1, 0, 2, 0, "お兄さんは苦手な食材\nとかありますか？\nあの、もしよければ\n今から手料理を…♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126001, 7672, 1, 0, 3, 0, "まったり過ごす\nお家時間もいいですね\n冒険者には羽休めも\n大事ですから♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126001, 7673, 1, 0, 4, 0, "いつでもお兄さんを\n守れるように\nこの姿でいることに\n慣れておきますね…！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 126001, 7674, 1, 0, 101, 0, "お兄さん眠そうです…\nちょっとお昼寝\nしちゃいましょうか♪\nはい、膝枕…♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 126001, 7675, 1, 0, 102, 0, "お兄さん怪我を…！？\nわたしが治療を…\nふー…ふー…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126001, 7676, 1, 1, 1, 0, "おはようございます♪\n朝、元気に挨拶すれば\n幸せな一日が過ごせる\nそうですよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126001, 7677, 1, 2, 2, 0, "お昼ごはんの時間です\n…手がふさがってる？\nわかりました、じゃあ\nあ～んしますね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126001, 7678, 1, 3, 3, 0, "ふわぁ…眠くなって\nきちゃいました…\nお兄さん…お泊りして\nいいですか…？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126001, 7680, 1, 0, 2, 0, "ふにゃ～～…♪\nこんな姿、他の人には\n見せられません…♪\nもふもふ～～…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126001, 7681, 1, 0, 3, 0, "お兄さんこういうの\n好きなのかな～…\nふふっ、わたしも♪\nぎゅ～～～っ♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126001, 7682, 1, 0, 4, 0, "アロマの癒し効果…\n上手くアレンジして\nみなさんの治療に\n活かせないかな～？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126001, 7683, 1, 0, 5, 0, "は～気持ちいい～♪\n身体が暖まると\n心もぽかぽかです♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126001, 7685, 1, 0, 7, 0, "うふふ、おいしい♪\nプレシアちゃんなら\n無限にぱくぱく～って\nしちゃいそうです…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126001, 7686, 1, 0, 8, 0, "本はわたしを\n知らない世界に\n連れていってくれる…\nまるでお兄さんです♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 103801, 7688, 1, 0, 103, 0, "話題の新刊…\nあの人も読んでるかな\n今度会う時、感想\n聞いてみたいな…♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 103801, 7689, 1, 0, 103, 0, "うにゃっ…あ、熱を\n測ってくれたんですか\nありがとうございます\nバッチリ元気ですよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126101, 7690, 1, 0, 1, 0, "お兄の、けはい…\nん、みつけた\nぎゅ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126101, 7691, 1, 0, 2, 0, "きょろきょろ\nここも\nぱとろる、するよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126101, 7692, 1, 0, 3, 0, "おうち\nにぎやか\nさみしく、ない", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126101, 7693, 1, 0, 4, 0, "きょだい、おにぎり\nきょだい、肉まん\nきょだい…お兄？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126101, 7694, 1, 0, 5, 0, "はっぴーちゃん\nどこ？\nお兄、たべた？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 126101, 7695, 1, 0, 101, 0, "くんくん…この、へや\nお兄の、におい\nすんでる\nすめる？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 126101, 7696, 1, 0, 102, 0, "おそうじ、にがて\nでも\nれんしゅう\nきゅっきゅ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126101, 7697, 1, 0, 1, 0, "みみ、さわり、たい？\nんー…だめ…\nむずむず\nなので", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126101, 7698, 1, 0, 2, 0, "くろぷれしあ\nかっこいい？\nん…あざます", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126101, 7699, 1, 0, 3, 0, "お兄の、おうち\nきょうみ、ましまし\nたんけん\nするね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126101, 7700, 1, 0, 4, 0, "なでなで、される…と\nねむ…く…\nぶぅ…ぶぅ…ぶぅ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 126101, 7701, 1, 0, 101, 0, "かみ、りぼん\nおてつだい\nよろ\nです", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 126101, 7702, 1, 0, 102, 0, "ここ\nすむ\nね？\nまいにち、いっしょ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126101, 7703, 1, 1, 1, 0, "ぐっもにん\nあさごはん\nあさごはん", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126101, 7704, 1, 2, 2, 0, "お兄\nおつかれ？\nれっつ、まいぺーす", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126101, 7705, 1, 3, 3, 0, "ひとりで、ねれる\nでも\nひとりじゃ、なくても\nねれる、よ…？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126101, 7707, 1, 0, 2, 0, "ふかふか\nパパの、おなか\nみたい", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126101, 7708, 1, 0, 3, 0, "こんにちは\nぷれしあ、です\nおともだち、なろ？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126101, 7709, 1, 0, 4, 0, "いい、におい\nでも、お兄\nもっと、いい、におい", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126101, 7710, 1, 0, 5, 0, "とりま\nお肉、やこ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126101, 7712, 1, 0, 7, 0, "もぐもぐ…\nむげん肉まん、も\nきぼう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 126101, 7713, 1, 0, 8, 0, "ぜんぶ、よんだ\nもっと、ぶあついほん\nでも、いーよ？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127901, 7715, 1, 0, 1, 0, "幸せなクリスマスを\n過ごすため\nギルドハウスを\nきれいにしなくちゃ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127901, 7716, 1, 0, 2, 0, "えへへ♪\n彼へのプレゼント…\n枕元にそっと\nおいておきましょう☆", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127901, 7717, 1, 0, 3, 0, "クリスマスといえば\n七面鳥の丸焼き…☆\nよだれを拭いて\nいざ準備です♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127901, 7718, 1, 0, 4, 0, "彼に用意した\nプレゼント…\n大きすぎて\nくつ下に入りません…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127901, 7719, 1, 0, 5, 0, "プレゼントは\n貰う方も、贈る方も\nどちらも幸せを\n感じますね…☆", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 127901, 7720, 1, 0, 101, 0, "夜景が見える\nレストランであの人と\nクリスマスデート…\n憧れちゃいますね☆", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 127901, 7721, 1, 0, 102, 0, "プレゼントはわたし…\n恥ずかしいですけど\nこの贈り物、彼に\n喜んでほしいです…☆", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127901, 7722, 1, 0, 1, 0, "オーナメントを\nたくさん用意したので\n二人でツリーに\n飾りつけましょう☆", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127901, 7723, 1, 0, 2, 0, "あはは、お腹\n空いちゃいました？\nパーティーまで\n我慢してください♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127901, 7724, 1, 0, 3, 0, "ジングルベ～ル♪\nあなたも一緒に\nクリスマスソング\n歌っちゃいましょう♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127901, 7725, 1, 0, 4, 0, "サプライズプレゼント\nの用意をして…って\nあなた！？　い、今の\n聞いてませんよね…？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 127901, 7726, 1, 0, 101, 0, "クリスマスの夜は\nパーティーしましょう\nできれば…その…\n二人っきりで…☆", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 127901, 7727, 1, 0, 102, 0, "あなたと過ごした\n最高のクリスマス…\nずっとずっと心の奥に\nしまっておきますね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127901, 7728, 1, 1, 1, 0, "おはようございます！\n皆の願いが叶うような\n素敵な一日に\nしましょうね☆", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127901, 7729, 1, 2, 2, 0, "お昼ですね！\nディナーの買い出しが\n済んだら、少しお昼寝\nしちゃいましょうか☆", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127901, 7730, 1, 3, 3, 0, "そろそろ就寝の\n時間ですね～\n枕元にくつ下の準備を\nお忘れなく～☆", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127901, 7732, 1, 0, 2, 0, "ふ～気持ちいい…☆\nお家でまったり過ごす\nクリスマスも\nいいですよね…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127901, 7733, 1, 0, 3, 0, "かわいいですね～♪\nサンタさんからの\n贈り物でしょうか～？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127901, 7734, 1, 0, 4, 0, "すう～…はあ～…\nすごく素敵な香り…\n炊き立てのご飯と\nいい勝負です…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127901, 7735, 1, 0, 5, 0, "暖かいですけど…\nあの日の山小屋を\n思い出して\n恥ずかしいですね…☆", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127901, 7737, 1, 0, 7, 0, "むしゃむしゃもぐもぐ\n止まりませんよ\nクリスマスですから\nむしゃむしゃもぐもぐ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127901, 7738, 1, 0, 8, 0, "物語に出てくる\nサンタさんみたいに\nわたしも幸せを\n運びたいです…☆", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127901, 7740, 1, 0, 14, 0, "わっ！\nこ…これは素敵な\nサプライズプレゼント\nですね…☆", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127901, 7741, 1, 0, 15, 0, "これを着ればわたしも\n立派なサンタさんに\nなれますかね…？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 106601, 7742, 1, 0, 103, 0, "ここが今のあたしの\n居場所ってやつです\nふふっ、なかなか\n悪くねー気分ですよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 106601, 7743, 1, 0, 103, 0, "これからはボス代行\nとしてブイブイ\nいわせてやるです\nついてくるですよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128001, 7744, 1, 0, 1, 0, "陛下～♪\n貴女のクレジッタが\nおめかしして\nはせ参じましたわ～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128001, 7745, 1, 0, 2, 0, "パーティーとは景気の\n良さを見せつける場…\n倹約などは\nもっての外でしてよ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128001, 7746, 1, 0, 3, 0, "この天井の高さなら\n搬入できそうですわね\n陛下用の特注ケーキ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128001, 7747, 1, 0, 4, 0, "…ぶぇっくしゅ！\nふう…少し背中を\n開けすぎたかしら…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128001, 7748, 1, 0, 5, 0, "ここは秘書の目も\n届きませんから\nチクりメモの心配も\nありませんのよね～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 128001, 7749, 1, 0, 101, 0, "いくらでも二度見して\nよろしくてよ？\n本当にわたくしだと\n心から納得できるまで", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 128001, 7750, 1, 0, 102, 0, "袖の下以外の贈り物も\nなかなかどうして…\n悪くないものですのね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128001, 7751, 1, 0, 1, 0, "こう見えてダンスの\nたしなみはありますの\nお相手してあげても\nよろしくってよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128001, 7752, 1, 0, 2, 0, "ドレスはもちろん\n特注品ですわ♪\nもしや貸衣装だと\n思いまして？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128001, 7753, 1, 0, 3, 0, "バッグの中身？\n仕事に必要なものと…\n心を潤わせるための\n宝石がいくつか♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128001, 7754, 1, 0, 4, 0, "やる気というものは\n仕事に必要な条件です\nいわばドレスコードの\nようなものですわね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 128001, 7755, 1, 0, 101, 0, "全ての仕事は自分の\n血となり肉となり\nお金にもなるもの\nなのです。経験談です", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 128001, 7756, 1, 0, 102, 0, "今年の教訓を活かして\n来年は更に素晴らしい\nクリスマスにして\n差し上げますわ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128001, 7757, 1, 1, 1, 0, "おはようございます\n今日という稼ぎ時の\nはじまりですわよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128001, 7758, 1, 2, 2, 0, "安くて早くておいしい\nそれがランチに必須の\n栄養素ですわ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128001, 7759, 1, 3, 3, 0, "宵越しの仕事は\n安眠の敵！\n熟睡したいのであれば\n今が勝負時ですわよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128001, 7761, 1, 0, 2, 0, "たまにはこうして\n休息を取ることも…\n…はっ！？\nドレスにシワが～っ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128001, 7762, 1, 0, 3, 0, "…わたくしの勘が\n言っていますわ\nこの愛らしさ！\n爆売れ確・定！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128001, 7763, 1, 0, 4, 0, "いい香りですわね…\nお金の匂いには\n敵いませんけれども♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128001, 7764, 1, 0, 5, 0, "……ハッ！？\n完全にボーッとして\nしまいましたわ…！\nぬくもり…恐ろしい！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128001, 7766, 1, 0, 7, 0, "考えませんわ…\n考えませんわよ！\nカロリーのことはっ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128001, 7767, 1, 0, 8, 0, "ビジネス本以外の本を\n読むのも、たまには\n悪くありませんわね…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128001, 7769, 1, 0, 14, 0, "よくできていますわね\nこの絵本！\n子ども向けだと甘く\n見ていましたわ…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128001, 7770, 1, 0, 15, 0, "この視界の狭さ…\n思い出しますわね\nティッシュ配りの\nアルバイト時代を…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128101, 7771, 1, 0, 1, 0, "ふふふ…こっそり\nスイーツを食べる\nリノちゃん見～っけ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128101, 7772, 1, 0, 2, 0, "お姉ちゃんの抜き打ち\nお部屋チェック～♪\n…ケーキの食べこぼし\nこれはリノちゃんだね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128101, 7773, 1, 0, 3, 0, "この衣装は目立つから\nどこにいても弟くんに\n気付いてもらえるね！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128101, 7774, 1, 0, 4, 0, "弟くん、協力して！\nリノちゃんにお勉強\nしなさいって圧を\nかけるんだよっ…♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128101, 7775, 1, 0, 5, 0, "絆の力を感じるよ\nこれならもっと万全な\n弟くんのお世話が\nできそうだねっ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 128101, 7776, 1, 0, 101, 0, "お姉ちゃんもたまには\nリノちゃんみたいに\n甘えてみようかな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 128101, 7777, 1, 0, 102, 0, "弟くんとリノちゃん\nどっちも大切な\n私のきょうだいだよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128101, 7778, 1, 0, 1, 0, "分かってるよ♪\nお姉ちゃんの膝枕を\nご所望だねっ？　え？\n違う？　違わないよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128101, 7779, 1, 0, 2, 0, "リノちゃんがね\nお姉ちゃんチョップを\n防ぐ妹白刃取りを\n訓練してるんだよね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128101, 7780, 1, 0, 3, 0, "お姉ちゃん頭突きは\n封印しないとね…\nだって、今使うと\n頭飾りで威力が…ね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128101, 7781, 1, 0, 4, 0, "ふふっ♪\n目をつぶってても\n弟くんは見えてるよ\n…そこでしょ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 128101, 7782, 1, 0, 101, 0, "お姉ちゃんも\nリノちゃんみたいに\nかわいさいっぱいに\n振舞っちゃうぞ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 128101, 7783, 1, 0, 102, 0, "弟くんっ♪\nいつもお姉ちゃんの\n声が届く範囲にいてね\n…絶対だよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128101, 7784, 1, 1, 1, 0, "おはよう、弟くん！\nリノちゃんが起こして\nくれたんだね♪\n…先を越されちゃった", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128101, 7785, 1, 2, 2, 0, "お姉ちゃんランチの\n時間だぞ♪\nさ、食べさせてあげる\nお口を開けて…♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128101, 7786, 1, 3, 3, 0, "おやすみの時間だよ\n…弟くんは真ん中ね\n左右には私たち姉妹が\nいるから安心だよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128101, 7788, 1, 0, 2, 0, "お姉ちゃんは全力で\nリラックスしてても\n弟くんの声一つで\nすぐにでも動けるよ♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128101, 7789, 1, 0, 3, 0, "リノちゃんこういうの\n好きそうだよね\n今度プレゼントして\nみようかな？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128101, 7790, 1, 0, 4, 0, "いい匂いがするね～\nあれ？\nリノちゃんのケーキの\n甘い匂いも混ざってる", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128101, 7791, 1, 0, 5, 0, "お姉ちゃんの\n弟くんへの愛も\n熱く燃えあがるね…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128101, 7793, 1, 0, 7, 0, "たくさん食べて\n栄養を補給して\nお姉ちゃん力に\n全て変換するよっ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128101, 7794, 1, 0, 8, 0, "ことわざ辞典…\nリノちゃんにこれを\n読ませようかな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128201, 7797, 1, 0, 1, 0, "そろ～り、そろり…\nお姉ちゃんが居ぬ間に\nスイーツを～…♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128201, 7798, 1, 0, 2, 0, "いつか\nお姉ちゃんに似合う服\n作ってみたいですね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128201, 7799, 1, 0, 3, 0, "あいたっ！？\nひらひらしたとこ\n踏んづけちゃいました", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128201, 7800, 1, 0, 4, 0, "お姉ちゃんと\nお兄ちゃんの二人から\n勉強しろって圧力が～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128201, 7801, 1, 0, 5, 0, "私の中にも秘めた力が\n眠っているはず…\nそう、妹パワーが", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 128201, 7802, 1, 0, 101, 0, "私もお兄ちゃんを\n甘やかしてみたいです\n…お姉ちゃんみたいに", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 128201, 7803, 1, 0, 102, 0, "お兄ちゃんの部屋を\nこっそり拝見～♪\nはっ！？　これは\nお姉ちゃんの視線…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128201, 7804, 1, 0, 1, 0, "お姉ちゃん頭突きは\n大地を揺らす威力です\n私とお兄ちゃんは\n挨拶感覚で喰らいます", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128201, 7805, 1, 0, 2, 0, "お姉ちゃんチョップは\n雲を裂く切れ味です\n早く妹白刃取りを\n完成させないと…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128201, 7806, 1, 0, 3, 0, "お姉ちゃんみたいに\n大人っぽく着こなす\nには一体どうしたら…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128201, 7807, 1, 0, 4, 0, "弓のデザインも\nかわいいんですよっ♪\n常に持ち歩きたいです\nけど…危ないですよね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 128201, 7808, 1, 0, 101, 0, "きれいな服を着てると\nお姫さま気分に\nなれますね…あとは\n王子さまがいれば…♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 128201, 7809, 1, 0, 102, 0, "お兄ちゃんっ！\n私は褒められて伸びる\nタイプなのでもっと\nナデナデしてください", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128201, 7810, 1, 1, 1, 0, "妹モーニングコール♪\nお姉ちゃんより先に\n起こしに来れました！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128201, 7811, 1, 2, 2, 0, "ああっ、お姉ちゃん！\nお兄ちゃんにアーン\nするなんてずるいです\n私もしたいですっ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128201, 7812, 1, 3, 3, 0, "おやすみの時間ですね\nえへへ、きょうだいで\n並んで寝るのって\n楽しいですよね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128201, 7814, 1, 0, 2, 0, "スイーツを食べた後は\nお腹を休めましょう～\n平気平気…ちゃぁんと\n運動もしますからぁ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128201, 7815, 1, 0, 3, 0, "お姉ちゃんも\nこういうかわいいもの\n好きなんですかね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128201, 7816, 1, 0, 4, 0, "くんくん…\nなんの匂いでしょうか\n新しいスイーツ？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128201, 7817, 1, 0, 5, 0, "きょうだい３人\n肩を寄せて暖まるとか\nよくないですかっ？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128201, 7819, 1, 0, 7, 0, "いつも元気な妹で\nいるためには\nよく食べるのが大事！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128201, 7820, 1, 0, 8, 0, "なんか姉に関する本が\nちらほらと混ざって…\nこれお姉ちゃんの\n仕業ですね！？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128301, 7823, 1, 0, 1, 0, "ふんふんふ～ん\nお正月で世間は\n浮かれてるですねえ\nま、いいですけど♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128301, 7824, 1, 0, 2, 0, "は～、お正月は\nだらだらできて\n最高ですね\n…いつも通りですけど", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128301, 7825, 1, 0, 3, 0, "ビッグな幹部は\nビッグなお年玉が\nもらえるはずです\n楽しみですね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128301, 7826, 1, 0, 4, 0, "今度ドラちゃんに\nお年玉を持っていって\nやりたいですね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128301, 7827, 1, 0, 5, 0, "そういえばアユアユと\n約束してたですよ…\nそろそろ出かける\nですかね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 128301, 7828, 1, 0, 101, 0, "お年玉がもらえたら\n何に使うか…\n今のうちに\n考えとくです", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 128301, 7829, 1, 0, 102, 0, "年神の報酬の駄菓子が\nたまってきたですね…\nガキどもにでも\n配っちまうですよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128301, 7830, 1, 0, 1, 0, "あたしのレアな\n晴れ着姿、今のうちに\nしっかり目に焼き付け\nとくがいいですよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128301, 7831, 1, 0, 2, 0, "お兄さんは今日も\nヒマそうですねぇ…\nしょうがないから\nかまってやるですよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128301, 7832, 1, 0, 3, 0, "そういえばカヤぴぃ\n見なかったですか？\n喧嘩屋初売りセール\nとか言ってたですけど", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128301, 7833, 1, 0, 4, 0, "お正月ならではの\n大きなシノギ…\nなにかないですかね？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 128301, 7834, 1, 0, 101, 0, "いいとこに来たですよ\nさっき走ったら帯が\n曲がっちゃったです\n直すの手伝うです！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 128301, 7835, 1, 0, 102, 0, "年神の仕事ですか？\nこれからいくとこです\nお兄さんも一緒に\n来やがるですよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128301, 7836, 1, 1, 1, 0, "ふぁあ…\nどうしたですか？\nお正月に早起きなんて\n無粋ってヤツですよ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128301, 7837, 1, 2, 2, 0, "小腹が減ったですね\n一緒におせちでも\nつつきにいくですよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128301, 7838, 1, 3, 3, 0, "夜はあたしの時間\nと言いたいとこですが\n初夢のために早く\n寝とくですかね…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128301, 7840, 1, 0, 2, 0, "正月は食っちゃ寝\nするのが仕事…\nって聞いたこと\nあるですよ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128301, 7841, 1, 0, 3, 0, "ふふっ、晴れ着の\nあたしの次くらいには\nかわいいですね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128301, 7842, 1, 0, 4, 0, "なんかいい匂い…？\nよく知らんですけど\n晴れ着からも同じ\n匂いがしてるですね…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128301, 7843, 1, 0, 5, 0, "ふ～あったまるです…\nお正月の唯一の弱点は\n寒いことですからね…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128301, 7845, 1, 0, 7, 0, "もぐもぐ…正月太り？\nあたしには関係ない\n言葉ですね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128301, 7846, 1, 0, 8, 0, "あったあった…\nトーゴクには\nドラゴンの神宮も\nあるんですね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128401, 7848, 1, 0, 1, 0, "お正月のトラブルも\nハツネちゃんが\nめでたく解決\nしてみせるのだ～！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128401, 7849, 1, 0, 2, 0, "そうだ、新年会に\nサプライズ大作戦を\nしかけちゃおう！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128401, 7850, 1, 0, 3, 0, "イノリちゃんも\nカスミンも振袖姿が\nかわいかったな～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128401, 7851, 1, 0, 4, 0, "シオリンに\nお年玉あげたいし\n彼にバイトを紹介\nしてもらおうかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128401, 7852, 1, 0, 5, 0, "今度はどんな凧を\n飛ばそうかな？\nみんなが驚くような\n凧がいいよね～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 128401, 7853, 1, 0, 101, 0, "ひらめいた！\n彼への年賀状は\nきらーんっ☆って\n感じにしよう！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 128401, 7854, 1, 0, 102, 0, "来年も彼の隣で\n初日の出が\n見られるといいな～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128401, 7855, 1, 0, 1, 0, "今日はキミが\nギルドハウスに来る\n予知夢を見たんだよ～\nふふ、正夢になったね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128401, 7856, 1, 0, 2, 0, "この振袖どうかな？\nすっごくかわいいよね\nえへへ、ありがとう～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128401, 7857, 1, 0, 3, 0, "ハツネちゃんおみくじ\n開始！　きらーんっ☆\nキミは大吉なのだ～\nどう元気出たかな？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128401, 7858, 1, 0, 4, 0, "今度エルフの森にある\n薬草を使って七草粥を\n作ろうと思うんだ～\nぜひ食べてほしいな♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 128401, 7859, 1, 0, 101, 0, "お正月っぽい遊びを\n色々と用意したのだ～\nたまにはギルドハウス\nでゆっくり過ごそうよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 128401, 7860, 1, 0, 102, 0, "もしかして眠たいの？\n…じゃあ、また膝枕\nしてあげてもいいよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128401, 7861, 1, 1, 1, 0, "いっぱい寝たから\n元気いっぱいだよ～\n今日もがんばろう！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128401, 7862, 1, 2, 2, 0, "お昼だね～\nおせちがあるから\nキミも食べるでしょ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128401, 7863, 1, 3, 3, 0, "そろそろ寝る時間だね\n今日はどんな夢を\n見られるかな～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128401, 7865, 1, 0, 2, 0, "いい座り心地～\n寝正月で過ごすのも\n最高なのだ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128401, 7866, 1, 0, 3, 0, "かわいいな～\n癒されてぐっすり\n眠れそうだよ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128401, 7867, 1, 0, 4, 0, "いい香り～\nリラックスすると\n眠たくなっちゃうよ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128401, 7868, 1, 0, 5, 0, "ポカポカいい気持ち～\n初日の出の温もりを\n思い出しちゃうな～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128401, 7870, 1, 0, 7, 0, "もぐもぐ、おいし～\nでもいっぱい食べたら\n眠くなっちゃった…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128401, 7871, 1, 0, 8, 0, "ふむふむ\nお正月料理も\n色々あるんだね～\n今度作ってみようかな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 106501, 7873, 1, 0, 103, 0, "最近カレを見てたら\n何故かおいしそうに\n思えてくるんだよな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 106501, 7874, 1, 0, 103, 0, "手握っててくれるか？\nじゃないとオレ…\n力が抑えきれない…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128501, 7875, 1, 0, 1, 0, "つかの間の平和を享受\nしながらぬくぬくと\n読書に耽る…\nふふ、最高だね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128501, 7876, 1, 0, 2, 0, "お正月飾りなんて\n置いたらはしゃいでる\nように見えるかな…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128501, 7877, 1, 0, 3, 0, "ま、まずい…\n一人遊びしすぎて\nお正月遊びをほぼ\n極めつつある…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128501, 7878, 1, 0, 4, 0, "ミステリーは\n時と場所を選ばない\nつまり…新年でも\n探偵は休まないよ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128501, 7879, 1, 0, 5, 0, "お世話になった人に\n年賀状でも書こうかな\n助手くん、喜んで\nくれるかな…えへへっ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 128501, 7880, 1, 0, 101, 0, "絵馬に願い事を書こう\n助手くんと一緒に\nいられますように…♪\n…は、恥ずかしいな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 128501, 7881, 1, 0, 102, 0, "助手くんはもっと\n派手な着物のほうが\n好みだったかな…？\nき、気になる…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128501, 7882, 1, 0, 1, 0, "やあ。助手くんも\nお正月休みを\n持て余してるのかい？\nふふ、似た者同士だね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128501, 7883, 1, 0, 2, 0, "助手くん助手くん！\n凧あげしにいこう！\n…そ、捜査の一環で", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128501, 7884, 1, 0, 3, 0, "着物は転びそうで\n怖いんだよなあ…\nお、押さないでね？\nフリじゃないよ！？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128501, 7885, 1, 0, 4, 0, "ひ、人目を憚る\n本を読むなら\nみんなが帰省中の今！\nはっ！　助手くん！？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 128501, 7886, 1, 0, 101, 0, "ね、ねえ…助手くん…\n一緒に初詣いかない？\nあ、いや！　深い\n意味はないんだけど！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 128501, 7887, 1, 0, 102, 0, "あはは…助手くんと\n一緒だと家でごろごろ\nしてるだけでも\n楽しいや…♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128501, 7888, 1, 1, 1, 0, "ふぁ～…\nお正月休みのおかげで\n昼夜逆転中だよ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128501, 7889, 1, 2, 2, 0, "お正月も依頼が\nひっきりなしだね\nさあ、街へ出よう！\nもちろん君も一緒にね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128501, 7890, 1, 3, 3, 0, "も、もっと\n近くにきてもいいよ？\n冬の夜は…寒いからね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128501, 7892, 1, 0, 2, 0, "ううぅ～…！\nこのままじゃ\n寝正月だぁ…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128501, 7893, 1, 0, 3, 0, "みんなが帰省中でも\nこれがあれば\n寂しくない…かな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128501, 7894, 1, 0, 4, 0, "うーん…♪\nお正月休みの\nリフレッシュに\nちょうどいいや♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128501, 7895, 1, 0, 5, 0, "ふぅ…あったまるな\n冬は寒いから\n助かるよ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128501, 7897, 1, 0, 7, 0, "うーん、おいしい♪\n読書のお供に\nちょうどいい", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128501, 7898, 1, 0, 8, 0, "時間に余裕があると\n読書が進むよ\nいざ、本棚読破だ♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128801, 7900, 1, 0, 1, 0, "この新しい力…\nさらに使いこなせる\nようになるには…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128801, 7901, 1, 0, 2, 0, "私に眠る属性の力…\n風の力以外には\nどんな力があるのかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128801, 7902, 1, 0, 3, 0, "今の私なら\nどこまでいけるのか…\nやれるだけ\nやってみよう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128801, 7903, 1, 0, 4, 0, "風の力の制御…\n段々とコツが\n掴めてきたな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128801, 7904, 1, 0, 5, 0, "風の力か…\n応用の幅は広そうだ\n何ができそうか\n色々試してみよう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 128801, 7905, 1, 0, 101, 0, "今度、私とキミで\n料理対決でもする？\n簡単には負けないよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 128801, 7906, 1, 0, 102, 0, "この胸に溢れる\n温かな気持ちは…\nきっと彼への…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128801, 7907, 1, 0, 1, 0, "一段と剣技が\n冴えてきたような…\nいや…油断禁物だ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128801, 7908, 1, 0, 2, 0, "かっこいいって？\nありがとう…そんなに\n真っすぐ褒められると\n照れてしまうな…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128801, 7909, 1, 0, 3, 0, "どれほど厚い壁が\n立ち塞がろうと\n打ち破ってみせる", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128801, 7910, 1, 0, 4, 0, "手が空いてるなら\n剣の稽古に少し\n付き合ってくれるかな", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 128801, 7911, 1, 0, 101, 0, "試練を乗り越えて…\n私の中にあるものが\nはっきりと分かったよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 128801, 7912, 1, 0, 102, 0, "キミから流れてくる\n力の温かさ…これが\n私たちの絆なんだ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128801, 7913, 1, 1, 1, 0, "朝だよ、そろそろ\n起きる時間だ\nそれとも私に起こして\nほしいのかな？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128801, 7914, 1, 2, 2, 0, "昼食は私が作るよ\nリクエストはある？\nできる範囲で応えるよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128801, 7915, 1, 3, 3, 0, "おやすみ。また明日も\n一緒に冒険しよう\n約束だよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128801, 7917, 1, 0, 2, 0, "休める時は休む…\n戦い続けるには\n必要なことだからね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128801, 7918, 1, 0, 3, 0, "心の安らぎの大切さ…\nそれを知れたのは\nキミたちのおかげだね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128801, 7919, 1, 0, 4, 0, "気持ちが落ち着くな…\nこの香り、風の力で\n纏えたりしないかな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128801, 7920, 1, 0, 5, 0, "暖まった空気の流れ…\n以前より風の変化に\n気づくようになったな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128801, 7922, 1, 0, 7, 0, "この味付け…\n今度試してみようか\n味見は任せたからね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128801, 7923, 1, 0, 8, 0, "心技体、すべてを\n鍛えるには座学も\n必要だからね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128901, 7925, 1, 0, 1, 0, "瞑想するのに\nいいところはあるかな\n集中できて…お昼寝は\n我慢できるところ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128901, 7926, 1, 0, 2, 0, "ここにはあったかい\n空気が流れてるなぁ…\nみんなが受け入れて\nくれるおかげだね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128901, 7927, 1, 0, 3, 0, "まるで心の中に\n光が満ちてるみたい…\nこの気持ち、みんなに\nおすそ分けしたいな♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128901, 7928, 1, 0, 4, 0, "この衣装は属性の力を\n反映しやすいんだよね\nキラキラしてるのも\n納得だよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128901, 7929, 1, 0, 5, 0, "エルフの里のみなさん\n元気かなぁ…\nまた会いに行きたいな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 128901, 7930, 1, 0, 101, 0, "まだ変身できてる…\nわたしと騎士クンの\n修行の成果\n出てるなぁ…ふふっ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 128901, 7931, 1, 0, 102, 0, "この服を脱いだら\nどうなるのかな…\nた、試す勇気は\nないけど…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128901, 7932, 1, 0, 1, 0, "ふぅ…あ、騎士クン\nちょうど瞑想してたの\n一緒に…する？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128901, 7933, 1, 0, 2, 0, "不思議だけど\nいつの間にか\n出たり消えたりするの\n…背中の輪っか！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128901, 7934, 1, 0, 3, 0, "この服って肌触りが\nとってもいいんだよ\nさ、触ってみる？\nスベスベだよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128901, 7935, 1, 0, 4, 0, "アストラルの力…\nすごく持続できてるね\n修行の成果が出てるよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 128901, 7936, 1, 0, 101, 0, "この力があれば\n暗いところで\n落とし物をしても\n見つけやすいかも…？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 128901, 7937, 1, 0, 102, 0, "冒険の準備なら\nできてるよ\nいつでも声をかけてね\n…楽しみにしてる！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128901, 7938, 1, 1, 1, 0, "おはよう騎士クン\n一緒にお散歩とか…\nどうかな？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128901, 7939, 1, 2, 2, 0, "お昼ご飯のあとの\n瞑想って難しいんだよ\n眠くなっちゃって…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128901, 7940, 1, 3, 3, 0, "夜ふかしさんは\nほどほどにね？\n一日頑張った自分を\n休ませてあげないと", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128901, 7942, 1, 0, 2, 0, "のんびりしてたら\nケーキが食べたく\nなっちゃうなぁ…\nでも太る…よね…？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128901, 7943, 1, 0, 3, 0, "わあ、かわいい…！\n見てるだけで\nにやけちゃう…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128901, 7944, 1, 0, 4, 0, "これは…花の香りかな\n知ってる花かも…？\n当ててみようっ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128901, 7945, 1, 0, 5, 0, "あったかいと\nどうしてこんなに\n落ち着くんだろう…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128901, 7947, 1, 0, 7, 0, "食べ過ぎたらお腹が\n目立っちゃうかもって\n分かってるのに…！\n止められないよ～っ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128901, 7948, 1, 0, 8, 0, "こ、恋の精霊とか\nいたりするのかな…？\nはっ！　ダメダメっ！\n実力で頑張らないと", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128701, 7950, 1, 0, 1, 0, "お部屋の中でも\n修行、修行っ♪\nでも燃やさないように\n注意しなきゃだね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128701, 7951, 1, 0, 2, 0, "うーん…この衣装\n厚着か薄着か\nどっちなんだろう…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128701, 7952, 1, 0, 3, 0, "いつもの肉球がない分\nお片付けしやすいな♪\n騎士くんのお部屋\nピカピカにしとこー！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128701, 7953, 1, 0, 4, 0, "お休みは騎士くんと\nＢＢＱに行きたいなー\nあたしと一緒なら\n火の心配はいらないよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128701, 7954, 1, 0, 5, 0, "この姿ならもっと\n人を助けられる…！\nえへへっ！\n燃えてきたーーっ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 128701, 7955, 1, 0, 101, 0, "騎士くんが帰ったら\nアイラみたいな笑顔で\nお疲れさまさまって\nお出迎えしよっと…♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 128701, 7956, 1, 0, 102, 0, "この姿の時、いつも\n胸が熱く燃えてるのは\nずっと騎士くんのこと\n考えてるからかな…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128701, 7957, 1, 0, 1, 0, "騎士くん騎士くん！\nあたしに触れると\nやけどしちゃうよ…？\nなんちゃってー♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128701, 7958, 1, 0, 2, 0, "おっ、騎士くん\n修行に行きたいの？\nえへへっ、いいねー♪\n二人で燃えちゃおー！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128701, 7959, 1, 0, 3, 0, "そうだ騎士くん\nじゃんけんしよっか？\nグローブ外してるから\nチョキも出せるよっ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128701, 7960, 1, 0, 4, 0, "この姿でいられる時間\nすごく増えてきた…！\nえへへ！　あたしたち\n修行の成果出てるね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 128701, 7961, 1, 0, 101, 0, "じっと見つめないでよ\n顔が真っ赤に…やば！\nほんとに顔から\n火噴いちゃいそう…！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 128701, 7962, 1, 0, 102, 0, "火には強くなれても\n騎士くんに触られた\nほっぺはずーっと\n熱くてたまんないや…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128701, 7963, 1, 1, 1, 0, "おはよー騎士くん！\n今日も一日\n燃え滾っていくよー！\nファイヤーッ！！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128701, 7964, 1, 2, 2, 0, "騎士くんお昼だよ！\nちゃんとご飯食べて\n午後からの修行も\n乗り切ろうねー！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128701, 7965, 1, 3, 3, 0, "今日もお疲れさま！\nそうだ、寝る前に\nキャンプファイヤー\n囲んじゃおっか♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128701, 7967, 1, 0, 2, 0, "ぼふーんっ！\nえへへ、気持ちいい…\n修行の前に\nちょっと休憩ー♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128701, 7968, 1, 0, 3, 0, "かわいいな～♪\nなでなでなでなでなで\nなでなでなでなでっ！\nあははっ、摩擦熱！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128701, 7969, 1, 0, 4, 0, "ん～いい香り♪\n少し熱したほうが\n香りが広がるね…って\n蒸発しちゃった！！！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128701, 7970, 1, 0, 5, 0, "外でも自分の火で\nあったまれるけど\nお部屋でぬくぬく\nするのが一番だよね♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128701, 7972, 1, 0, 7, 0, "もぐもぐっ！\nお腹が減っては\n人助けはできずっ！\nもぐもぐもぐっ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 128701, 7973, 1, 0, 8, 0, "ふむふむ…\nなるほどなるほど…\n…これが上手な\nＢＢＱのやり方か…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129001, 7975, 1, 0, 1, 0, "…壁も扉も柱も\n全て記録で見たまま\n本当に…来ているのね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129001, 7976, 1, 0, 2, 0, "…騒がしい場所\n騎士クンが受け入れて\nくれるのをいいことに", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129001, 7977, 1, 0, 3, 0, "ここが…騎士クンの\n守りたかった場所\n騎士クンの思い出が\n詰まった場所…ね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129001, 7978, 1, 0, 4, 0, "視線を感じる…\nこちらの様子を\n探るような…心の動き\n取るに足らないけれど", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129001, 7979, 1, 0, 5, 0, "…そこかしこで…\n言葉にもしたくない\n相手が過ごしていた\n記録を思い出す…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 129001, 7980, 1, 0, 101, 0, "人は多いけれど…\n雪深い山中よりは\n過ごしやすい…か", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 129001, 7981, 1, 0, 102, 0, "自覚したことは\nあまりなかったけれど\nひどく…諦めの悪い女\nなのかもしれない", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129001, 7982, 1, 0, 1, 0, "お招きありがとう\nそれで…何をして\nいればいい…かな？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129001, 7983, 1, 0, 2, 0, "ここは…明るいね\nわたしの城と比べると\nなんだか眩しいくらい", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129001, 7984, 1, 0, 3, 0, "仮面はもう必要ないよ\nだって…わたしの顔を\n騎士クンに見て\nもらえなくなるから", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129001, 7985, 1, 0, 4, 0, "騎士クンさえよければ\nわたしの城に専用の\n部屋を用意するよ\n…いつでも", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 129001, 7986, 1, 0, 101, 0, "…騎士クンのマント\nまた貸してほしいな\nきれいに使うから\n…だめ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 129001, 7987, 1, 0, 102, 0, "いつか二人の願いが\n重なり合えるって…\n信じているね\n…いつまでだって…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129001, 7988, 1, 1, 1, 0, "おはよう、騎士クン\n…昨日は夢の中で\nお話ししすぎちゃって\nごめんね。ふふっ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129001, 7989, 1, 2, 2, 0, "お腹は…空いてない？\n食べ物はないけれど\n空腹を忘れる魔法なら\nかけてあげられるよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129001, 7990, 1, 3, 3, 0, "騎士クン…\nまだ、眠らないの？\n…そう。それなら…\nわたしも起きているね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129001, 7992, 1, 0, 2, 0, "肩の力を抜くには…\nまだ時間がかかりそう\nきっと…騎士クンと\n二人きりなら別なのに", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129001, 7993, 1, 0, 3, 0, "ぺんぽこりん…\nそう、確かあれも\nこんな感じだった…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129001, 7994, 1, 0, 4, 0, "花の香りは…好き\nつかの間\n心を穏やかに\nしてくれるから", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129001, 7995, 1, 0, 5, 0, "あたたかい…けれど\n騎士クンの優しさに\n比べれば、まだまだ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129001, 7997, 1, 0, 7, 0, "おい、しい…？\nこの感覚が、それ？\nなぜか…納得できる", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129001, 7998, 1, 0, 8, 0, "情報を入手するには\nわざわざ本を開く\n必要があるだなんて…\nプレイヤーは面倒ね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129301, 8000, 1, 0, 1, 0, "メルクリゾートか…\n良い記憶はないな\nよし忘れよう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129301, 8001, 1, 0, 2, 0, "知性の戦士ユニ！\nその恐るべき筋力！\nふおお！　うにゃー！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129301, 8002, 1, 0, 3, 0, "ぽーん！\n石用の水着ください！\nセクシーロゼッタが、\n渚の視線を独占です！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129301, 8003, 1, 0, 4, 0, "学院から奨学金を\n勝ち取るより某財団を\nだまくらかした方が\n早い気がしてきたな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129301, 8004, 1, 0, 5, 0, "わんつぅ、わんつぅ…\nんぎゃあ足攣ったー！\nおのれカルミナとやら\n絶対に許さん！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 129301, 8005, 1, 0, 101, 0, "ところで…なぜぼくは\nプールでもないのに\n水着のままなんだ…\nおのれ世界め！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 129301, 8006, 1, 0, 102, 0, "彼と二人で撮った動画\nうう、凝視に耐えん…\nなんだこのムズ痒さは", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129301, 8007, 1, 0, 1, 0, "なに？　水着姿で\n寒くないのかだと？\n普段からもっと薄着の\n娘が沢山いるだろう", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129301, 8008, 1, 0, 2, 0, "シノブ君にメリゾの\n招待状を送ったのだが\n御尊父の暴走を\n心配していたな…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129301, 8009, 1, 0, 3, 0, "ミミ君らにメリゾの\n招待状を送ったのだよ\n迎える準備をせねば…\nおしゃぶり持ってる？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129301, 8010, 1, 0, 4, 0, "本質の賢者…？\nいや元気にしているが\nなぜ君がぼくの身内を\n知っているんだ…？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 129301, 8011, 1, 0, 101, 0, "水着が似合ってる？\nな、なんだね突然…\nやめたまえよふひひ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 129301, 8012, 1, 0, 102, 0, "今度は夏にメリゾへ\n行きたいものだ\nむろん君も一緒だぞ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129301, 8013, 1, 1, 1, 0, "ぐああっ、朝陽が…！\n朝陽が昇っている…！\n夜行性ユニ死んじゃう", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129301, 8014, 1, 2, 2, 0, "はぁ…もう昼か…\nそろそろ寝ないとな\n規則正しい生活を\nしないと不健康だ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129301, 8015, 1, 3, 3, 0, "ひゃっはー、夜だー！\nオレたちの時間だぜ！\nふおおブチ上がり～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129301, 8017, 1, 0, 2, 0, "これは…ぼくにここで\n寝ろということか？\n一向に構わんが…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129301, 8018, 1, 0, 3, 0, "きゃっきゃっ！\nこれかわい～！\n…よし、今日も\n子供の擬態完璧だな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129301, 8019, 1, 0, 4, 0, "ふむ、良い香りだ\nだがそれだけか…\nとくに精神高揚効果は\nないのか…ちぇー", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129301, 8020, 1, 0, 5, 0, "火を見ていると…\n魔導罐を思いだすな…\nかに座、元気かな…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129301, 8022, 1, 0, 7, 0, "ええい、\n固形食物はいらん！　\nポーションを出せ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129301, 8023, 1, 0, 8, 0, "やはり書は文系に限る\nなんと心地よい筆致…\nぐああ突然理系文が！\nひぃ目が理系になる！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129301, 8025, 1, 0, 10, 0, "ほああ～！\n知性の海上騎士ユニ！\nこれは新たな可能性！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129301, 8026, 1, 0, 11, 0, "これは…なんだ？\n何が面白いんだ？\n難しいな…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 108901, 8027, 1, 0, 103, 0, "大切な人と過ごす\n正月の思い出、か…\n昔の私だったら\n考えられなかったな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 108901, 8028, 1, 0, 103, 0, "叶えたい願いなら\nたくさんあるよ\nそれでも一番を選ぶ\nなら、やはりキミと…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 108801, 8029, 1, 0, 103, 0, "神殿にあった\n恋愛おみくじ…\n引いてみようかな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 108801, 8030, 1, 0, 103, 0, "騎士クン。来年も\nまた一緒におみくじ\n結ぼうね。約束だよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 108701, 8031, 1, 0, 103, 0, "今度は騎士くんと\n一緒にうつった写真、\nおばあちゃんに\n送ろうっと♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 108701, 8032, 1, 0, 103, 0, "あ、騎士くん！\n今年も…ううん\nこれからも、よろしく\nおねがいします♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129401, 8033, 1, 0, 1, 0, "旧校舎の地下牢…\n真冬のサマー計画…\n等身大のＰＲ…\nうッ、頭が…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129401, 8034, 1, 0, 2, 0, "暇だし鏡でも見るか。\nお、いい腹筋育って…\nあ？　何見てんだコラ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129401, 8035, 1, 0, 3, 0, "オレは金色の疾風！\nんん…なんか違うな。\n我が名は金色の…\nあ？　何見てんだコラ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129401, 8036, 1, 0, 4, 0, "モニカとユニ先輩って\n口調と身長似てるけど\n子供扱いに対する\n反応は真逆だよね…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129401, 8037, 1, 0, 5, 0, "シオリン元気かな…\n昔くちゃくちゃに\n撫でくり回して\n嫌がられたっけなァ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 129401, 8038, 1, 0, 101, 0, "べつにいんだけどさ…\nあんたの知り合いって\n普段から水着みたいな\nカッコの女多くない？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 129401, 8039, 1, 0, 102, 0, "そだ、弟らがあんたに\n会いたがってたンだ。\n暇だったらいつでも\nウチ来てやってよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129401, 8040, 1, 0, 1, 0, "特別講座は色々あった\nけど…メリゾ自体は\nまあ悪くなかったよね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129401, 8041, 1, 0, 2, 0, "水着と合わせる小物に\nセンス出るよね、\nって話をミィコ先輩が\nしててマジわかりみ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129401, 8042, 1, 0, 3, 0, "水着のミフユねえさん\nやっぱ仕上がってたね\nあれが竜殺しの体か…\nバリかっけーな…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129401, 8043, 1, 0, 4, 0, "はにょんっ！？\nや、だからさ…\n後ろから声かけんな、\nつってンじゃん…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 129401, 8044, 1, 0, 101, 0, "メリゾの特別講座が\n上手くいったのも\n敏腕カメラマンがいた\nお陰だよ。ありがとね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 129401, 8045, 1, 0, 102, 0, "あ？　この水着が？\n似合っ…え、なんて？\nごめ、聞こえんくて。\nもっかい言ってみて", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129401, 8046, 1, 1, 1, 0, "もう朝とかマジないわ\n昨日二人に付き合って\nオールでカラオケだよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129401, 8047, 1, 2, 2, 0, "はぁ授業行かなきゃ…\n真っ昼間から勉強とか\nマジつらたん過ぎ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129401, 8048, 1, 3, 3, 0, "今日これから夜勤で…\nいやバイトちゃーくて\nボランティアの話よ？\n校則違反よくない", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129401, 8050, 1, 0, 2, 0, "うわ、ふっかふか…\nオニほど安らぐわ…\nやば、ユニってくる…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129401, 8051, 1, 0, 3, 0, "うちも妹いりゃ\nこーゆーのもなァ…\nま自分用でもいいか", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129401, 8052, 1, 0, 4, 0, "ふーん…\nいいじゃんこの香り\n抜群に嗅げるわ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129401, 8053, 1, 0, 5, 0, "うちン家のルーツの森\n大火災でなくなった\nらしンだよね…\n生まれる前の話だけど", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129401, 8055, 1, 0, 7, 0, "何コレありがてぇー\n食料ほくほく過ぎて草\n米ガチで尊いわァ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129401, 8056, 1, 0, 8, 0, "いいけど、なんでうち\n水着で本読んでンだよ\nどーゆープレイだよ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129401, 8058, 1, 0, 10, 0, "金色の海上騎士クロエ\n推して参る！　覇ァ！\nあ？　何見てんだコラ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129401, 8059, 1, 0, 11, 0, "何コレ、貝の椅子？\nどーやって遊ぶんだ…\nいや座って終わりか…\nまァそりゃそーか…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129801, 8060, 1, 0, 1, 0, "さてと…\n明日の訓練メニュー\n考えねえとな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129801, 8061, 1, 0, 2, 0, "また新入りの女子に\n弁当もらっちまった…\nありがたく食うけどよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129801, 8062, 1, 0, 3, 0, "種族なんて関係なく\nみんなが力を合わせて\nランドソルを守る…\nいい時代になったよな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129801, 8063, 1, 0, 4, 0, "誰にだって長所はある\n新入りのいいところを\n見つけてやるのも\n教官の仕事だ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129801, 8064, 1, 0, 5, 0, "厳し過ぎても\n甘過ぎてもダメ…\n教官ってのは難しいぜ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 129801, 8065, 1, 0, 101, 0, "戦いが苦手な新入りを\nどうフォローするか…\n後であいつと\n相談してみるか", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 129801, 8066, 1, 0, 102, 0, "あいつは優し過ぎて\n戦いに向いてねえ\n…そういう甘さ\n嫌いじゃねえけどな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129801, 8067, 1, 0, 1, 0, "強くなる近道はねえ\n地道な鍛錬あるのみだ\nさ、訓練始めるぞ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129801, 8068, 1, 0, 2, 0, "新入りにも\nいつも言ってるが…\n装備の手入れは\n念入りにしろよな？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129801, 8069, 1, 0, 3, 0, "座学も受けてえのか？\nいい心がけだな！\nなんでも教えてやるよ\n…魔法以外ならな", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129801, 8070, 1, 0, 4, 0, "新入りたちがよく\n恋バナしててな…\nお、おまえは…誰か\n気になるやついるか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 129801, 8071, 1, 0, 101, 0, "なあ、ストレッチ\n手伝ってくれねえか？\n訓練前にしっかり\n筋肉ほぐさねえと…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 129801, 8072, 1, 0, 102, 0, "ずいぶん頑張って\n鍛えたよな、おまえ\nよし！　ご褒美に\nお菓子作ってやるよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129801, 8073, 1, 1, 1, 0, "訓練日和だな！\nさぁて…まずは\n走り込みといくか！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129801, 8074, 1, 2, 2, 0, "昼飯はしっかり食えよ\nでなきゃ、\nこの後もたねえぜ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129801, 8075, 1, 3, 3, 0, "とっくに消灯時間だ\nさっさと寝な？\n明日も早いんだからよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129801, 8077, 1, 0, 2, 0, "休むのも訓練のうちだ\n今はのんびり\nさせてもらうぜ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129801, 8078, 1, 0, 3, 0, "いい面構えだ\n気に入ったぜ！\nあたしの班に入るか？\n…なんてな♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129801, 8079, 1, 0, 4, 0, "…いやされる匂いだな\n新入りたちにも\nすすめてみるか…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129801, 8080, 1, 0, 5, 0, "あったけえからって\n寝ないようにしねえと\n教官としての\n威厳に関わるからな…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129801, 8082, 1, 0, 7, 0, "食事も訓練の一環だ\nとはいえ食い過ぎには\n気をつけねえと…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129801, 8083, 1, 0, 8, 0, "トーゴクの剣術…か\nなにか訓練に\n活かせるかもしれねえ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129901, 8085, 1, 0, 1, 0, "あのおかたに近付く\n不埒な輩には、特別な\n訓練で地獄を見て\nいただきましょう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129901, 8086, 1, 0, 2, 0, "これだけ広ければ\n多少派手に\n動き回っても\n問題なさそうですね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129901, 8087, 1, 0, 3, 0, "…今誰か私を\n「鬼」教官と\n呼びませんでしたか？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129901, 8088, 1, 0, 4, 0, "新しく開発したこの薬\n誰か試してくれる人は\nいませんか？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129901, 8089, 1, 0, 5, 0, "厳しくすれば投げ出し\n優しくすればサボる\n人というのはどこまで\n度し難いのでしょう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 129901, 8090, 1, 0, 101, 0, "はじめましょう\nあのおかたの心を\n射貫くための\nシミュレーションを", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 129901, 8091, 1, 0, 102, 0, "あらゆる苦難と\n忌々しい女から守る\nためにも、私自身も\n鍛えておかねば", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129901, 8092, 1, 0, 1, 0, "あなた様、そんなに\n見つめられると…え？\nエリコはカッコいい？\nええ、当然ですとも", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129901, 8093, 1, 0, 2, 0, "不調があればすぐ\n報告してください\n体のケアも教官の\n大事な役目ですので", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129901, 8094, 1, 0, 3, 0, "新しい訓練器具の\n使い心地はどう…え？\n重すぎる？　これでも\n軽くしたのですが…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129901, 8095, 1, 0, 4, 0, "運動後には特製の\n栄養ドリンクをどうぞ\nクスクス、何も\n入れたりしてませんわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 129901, 8096, 1, 0, 101, 0, "よくがんばりました\nあなた様。あぁ…\nお疲れになったお顔も\n素敵ですわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 129901, 8097, 1, 0, 102, 0, "あなた様、二人だけの\n遠征訓練へ\nまいりましょう。\n既に準備万端ですわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129901, 8098, 1, 1, 1, 0, "まだ眠いですか？\nならばストレッチで\n体を痛めつ…ではなく\nほぐしていきましょう", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129901, 8099, 1, 2, 2, 0, "お昼はお弁当をご用意\nいたしました。量も\nスタミナも増し増し\n残さず食べてください", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129901, 8100, 1, 3, 3, 0, "よい睡眠こそ健やかな\n体に不可欠。ですので\n私が寝ずの番をさせて\nいただきますわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129901, 8102, 1, 0, 2, 0, "休憩用に使おうと思い\nましたが、心地よくて\n立てなくなって\nしまいますわ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129901, 8103, 1, 0, 3, 0, "中に重しを詰めれば\n愛でながら運動が\n可能ですわ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129901, 8104, 1, 0, 4, 0, "香りでリラックスさせ\n同時に暗示を\nかけてみましょうか", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129901, 8105, 1, 0, 5, 0, "…くっ、眠気を誘う\nこの暖かさ…\n油断も隙もありません", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129901, 8107, 1, 0, 7, 0, "主食はこちらで十分\nまかなえますわね\nそれでは私はおかずを\n作りましょう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129901, 8108, 1, 0, 8, 0, "まあ、実用書の類も\n揃っているのですね\nとても助かりますわ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129601, 8110, 1, 0, 1, 0, "……（そわそわ）", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129601, 8111, 1, 0, 2, 0, "ここに寄らずの花陣…\n作ったらダメですよね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129601, 8112, 1, 0, 3, 0, "部屋の隅が\n落ち着きます\nそれか、陽の当たる所", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129601, 8113, 1, 0, 4, 0, "身を隠せる場所が\nあると…\n助かるんですけど", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129601, 8114, 1, 0, 5, 0, "複雑な造りの家ですね\n触って壊れたり─\nしませんか？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 129601, 8115, 1, 0, 101, 0, "出入りするひと\nみんな、笑顔…\n…変わった家ですね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 129601, 8116, 1, 0, 102, 0, "この家なら安全─\nかもしれません\n魔物が出ないので", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129601, 8117, 1, 0, 1, 0, "これも、視察の一環\n遊びに来たわけじゃ\nないです", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129601, 8118, 1, 0, 2, 0, "長話─苦手です\n疲れるので…\nふぅ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129601, 8119, 1, 0, 3, 0, "ここで友達を作れと\nネアさんが…\nそう、言われても…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129601, 8120, 1, 0, 4, 0, "世話をするのは\n得意です\n…花の、ですよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 129601, 8121, 1, 0, 101, 0, "媛へのお土産を\n探してるんですけど…\n…貴方のおすすめ、\n聞いておきます", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 129601, 8122, 1, 0, 102, 0, "平和でのんきで…\n貴方らしい家です\n一応、褒めてます", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129601, 8123, 1, 1, 1, 0, "花に、お水─？\n…いりません", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129601, 8124, 1, 2, 2, 0, "日が高いうちは\nむやみに外へ出ません\n面倒事、多いので…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129601, 8125, 1, 3, 3, 0, "夜番。私がします\nあまり眠らない\n性質なので…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129601, 8127, 1, 0, 2, 0, "ここなら根を張れる\nかもしれません\n…張りませんけど", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129601, 8128, 1, 0, 3, 0, "こんなにやわな体じゃ\n身を守れませんよ─\n…あ。生き物じゃ\nないんですね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129601, 8129, 1, 0, 4, 0, "花みたいに─\nいい匂い、です", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129601, 8130, 1, 0, 5, 0, "火を見てると、\nジオ・ゲヘナを\n思い出します…ふぅ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129601, 8132, 1, 0, 7, 0, "食事も立派な戦支度\n媛が言ってました", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129601, 8133, 1, 0, 8, 0, "知識も武器の一つだと\n媛が…でも、\nこれでどうやって…？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130701, 8135, 1, 0, 1, 0, "天気もいいしお花畑に\nピクニックでも\n行こうかな♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130701, 8136, 1, 0, 2, 0, "あ、どこからか\nお花の匂いが…\nいい香りですね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130701, 8137, 1, 0, 3, 0, "新しいシャベルとか\nじょうろもほしいなぁ\nお小遣いしっかり\nためないとね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130701, 8138, 1, 0, 4, 0, "お花飾りたいけど\n花瓶はどこかな…\nあとであの人に\n聞いてみよっと", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130701, 8139, 1, 0, 5, 0, "お花の観察日記\n書かなきゃ\n日々勉強は\n欠かせませんよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 130701, 8140, 1, 0, 101, 0, "衣装にお花を足して\nみたけど、あの人\n気付いてくれるかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 130701, 8141, 1, 0, 102, 0, "この花冠、上手に\n作れたかも…\n日頃のお礼にあの人に\nあげようかな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130701, 8142, 1, 0, 1, 0, "あなたは何のお花が\n好きですか？　ほら、\n一応知っておいたら\n役に立つかもですし", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130701, 8143, 1, 0, 2, 0, "そのお花、乱暴に\n扱っちゃダメですよ\n花粉が服について\nとれなくなりますから", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130701, 8144, 1, 0, 3, 0, "土いじりしたから\n服が汚れてますよ\nはたいてあげますから\nじっとしててください", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130701, 8145, 1, 0, 4, 0, "そんなにすぐに芽は\n出ませんよ\nまあ、楽しみなのは\n私も同じですけど…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 130701, 8146, 1, 0, 101, 0, "この衣装、そんなに\n気になります？　なら\nこうしてお花を\n着ければおそろいです", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 130701, 8147, 1, 0, 102, 0, "お花の種を私に？\nありがとうございます\nせっかくなので一緒に\nお世話しませんか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130701, 8148, 1, 1, 1, 0, "お花にお水をあげるの\n手伝ってもらえます？\nお水は朝にあげるのが\nいいんですよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130701, 8149, 1, 2, 2, 0, "とっておきのお花見\nスポットに\nご案内しますよ\nお弁当も忘れずに！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130701, 8150, 1, 3, 3, 0, "もうこんな時間\n早く寝ましょう\n草木も眠るなんとか\nって言いますからね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130701, 8152, 1, 0, 2, 0, "お花のお手入れって\nけっこう疲れますね…\nここでちょっと一息\nしちゃおっと", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130701, 8153, 1, 0, 3, 0, "耳にお花を着ければ…\nえへへ、すっごく\nかわいくなったね！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130701, 8154, 1, 0, 4, 0, "これはラベンダー\nこっちはミモザ\nフローラル系の香り\nっていいですねぇ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130701, 8155, 1, 0, 5, 0, "ポカポカしますね～\nまるで春の陽気\nみたいです～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130701, 8157, 1, 0, 7, 0, "お花を一輪さすだけで\nご飯の時間がとても\n華やかになりますね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130701, 8158, 1, 0, 8, 0, "お花の本が\nいっぱいある！\n今日はこれで\nお勉強です！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130801, 8160, 1, 0, 1, 0, "ふんふ～ん♪\n華やかな服を着ると\n心がウキウキします♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130801, 8161, 1, 0, 2, 0, "花祭りで出会った\nみなさん…\nお元気でしょうか？\nまたお会いしたいです", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130801, 8162, 1, 0, 3, 0, "はっ！　そうでした\nこの服の時は\nメイドのお仕事は\nお休みでした…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130801, 8163, 1, 0, 4, 0, "この服は替えが\nありませんから\n大きなドジには\n気を付けないと…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130801, 8164, 1, 0, 5, 0, "目立ったほつれもなし\n…うん、いい感じに\n補修できました♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 130801, 8165, 1, 0, 101, 0, "メイド姿でなくても\n心はいつでも\nお嬢さまの\nメイドです♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 130801, 8166, 1, 0, 102, 0, "この服を着ると心が\n浮き立つのはあのかた\nと一緒にいる気持ちに\nなるからでしょうか？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130801, 8167, 1, 0, 1, 0, "はい、今日は一日\nこの服で過ごそうと\n思います\n変じゃないですよね？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130801, 8168, 1, 0, 2, 0, "花祭りのおかげで\n少しだけお花に\n詳しくなった…\nような気がします！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130801, 8169, 1, 0, 3, 0, "花祭り、今度は\nお嬢さまと救護院の\nみんなと一緒に\n行ってみたいですね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130801, 8170, 1, 0, 4, 0, "今度キョウカちゃんと\n花祭りの服でお花を\n見にお出かけ\nするんです", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 130801, 8171, 1, 0, 101, 0, "メイド服を全部洗濯\nしちゃうなんて…\nドジっちゃいましたぁ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 130801, 8172, 1, 0, 102, 0, "花祭りの服は私たちの\n思い出の服ですから…\nこれからも\n大切にします♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130801, 8173, 1, 1, 1, 0, "はう、そうでした\nお花にお水を\nあげる時間です！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130801, 8174, 1, 2, 2, 0, "お昼ご飯は\nバスケットを持って\nお花畑に食べ\nに行きませんか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130801, 8175, 1, 3, 3, 0, "ふぁあ…繕い物を\nしていたらこんな時間\nになってしまいました\n早く寝ないと…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130801, 8177, 1, 0, 2, 0, "ふわふわしてて\nきもちいいです～\nどんな生地を\nつかってるんだろ～？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130801, 8178, 1, 0, 3, 0, "かわいいですね～\nぬいぐるみさんにも\n服をつくって\nあげたいです♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130801, 8179, 1, 0, 4, 0, "いい香りです～\nあれ？　もしかして\n私の服についてる花と\n同じ香りが…？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130801, 8180, 1, 0, 5, 0, "暖かいですけど\nあまり近づいちゃうと\n頭の花飾りが乾燥\nしちゃいそうですね…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130801, 8182, 1, 0, 7, 0, "もぐもぐ…\nいい花を咲かせるには\nしっかりとした栄養が\n必要だそうです！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130801, 8183, 1, 0, 8, 0, "なるほどなるほど…\nお花のお手入れは\n奥が深いですね…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131001, 8185, 1, 0, 1, 0, "マホマホ王国にも\nサーカス団設立や\n夢と希望を届けますえ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131001, 8186, 1, 0, 2, 0, "せっかくの晴れ舞台や\nかっこいいポーズも\nできるようにしたいわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131001, 8187, 1, 0, 3, 0, "むむむむ～っ。うーん\nクルルはんみたいに\n胸から宝石を出すんは\n難しいみたいやわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131001, 8188, 1, 0, 4, 0, "あむっ…んん～♪\nやっぱり練習のあとは\n甘いものに限るわぁ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131001, 8189, 1, 0, 5, 0, "アーサーたちの衣装も\n今のうちに考えんと…\n生地、足りるやろか", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 131001, 8190, 1, 0, 101, 0, "王子はんとなら\nどんなアイデアも\n見つけられます♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 131001, 8191, 1, 0, 102, 0, "うちの晴れ姿が\n王子はんの目に一等\n輝いて見えますように", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131001, 8192, 1, 0, 1, 0, "キリっとしたうちも\nかっこいい？\nもう、上手やなぁ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131001, 8193, 1, 0, 2, 0, "新しい演目考えたんよ\nなぁ王子はん、意見\n聞かせてもらえます？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131001, 8194, 1, 0, 3, 0, "聞いてや王子はん\n動物さんたちとさらに\n息が合うてきたんよ\nみんな頑張り屋さんや", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131001, 8195, 1, 0, 4, 0, "演技の感想なら\n大歓迎どすえ♪\nたんと聞かせてや～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 131001, 8196, 1, 0, 101, 0, "王子はん、本番当日は\n最前席で見たってな\nそしたらうち、もっと\n頑張れますさかい♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 131001, 8197, 1, 0, 102, 0, "この技をあなたに\n捧げます…なんて♪\n驚いたやろ？\nどっきり作戦成功や♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131001, 8198, 1, 1, 1, 0, "素敵なショーのためや\n今日も焦らず一歩ずつ\n成長していきまひょ\nな、王子はん♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131001, 8199, 1, 2, 2, 0, "お弁当、王子はんも\n一緒にいかがどす？\nみんなで食べて団結や", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131001, 8200, 1, 3, 3, 0, "夜のサーカスも\n幻想的でとっても\n魅力的なんよ\n一緒にいきまひょか♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131001, 8202, 1, 0, 2, 0, "練習が終わったあとは\nの～んびりして\n体力を回復せな…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131001, 8203, 1, 0, 3, 0, "この子たちとなら\n空中ブランコや\n玉乗りもええなぁ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131001, 8204, 1, 0, 4, 0, "いい香りやなぁ\n本番前の緊張も\n和らげてくれそうやわ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131001, 8205, 1, 0, 5, 0, "このあったかい感じ…\nスポットライトの熱を\n思い出すわぁ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131001, 8207, 1, 0, 7, 0, "ちゃんと食べへんと\nいい演技はできまへん\n団長はんも言うてたわ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131001, 8208, 1, 0, 8, 0, "動物さんたちのことも\nもっと勉強せな\n大事な仲間やさかい", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 105601, 8210, 1, 0, 103, 0, "器用じゃないが、\n情には厚いよ\nこういう女を「一途」\nってそう言うんだろ？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 105601, 8211, 1, 0, 103, 0, "斬れないものはないと\n思ってきたさ。だけど\n…この気持ちだけは\n斬れないね、絶対に", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130901, 8212, 1, 0, 1, 0, "んしょ、んしょっ…！\n日頃の感謝を込めて\nヒーローさんのお家を\nきれいにしますです♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130901, 8213, 1, 0, 2, 0, "ふむふむ…\n新アトラクションは\nこのギルドハウスを\nモチーフに…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130901, 8214, 1, 0, 3, 0, "ごそごそ、こそこそ…\nヒーローさんに内緒で\nどこかにお礼の宝石を\n隠しておきましょう…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130901, 8215, 1, 0, 4, 0, "パークの運営が\n落ち着いたら\nわたしもギルド活動？\nしてみたいですます♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130901, 8216, 1, 0, 5, 0, "こんなすてきな\nおうちに住めたら…\nいえ！　うさぎ小屋で\n十分です～っ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 130901, 8217, 1, 0, 101, 0, "ヒーローさんのおうち\nだって意識したら\nアトラクションよりも\nドキドキしてきます…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 130901, 8218, 1, 0, 102, 0, "ヒーローさんのことを\n考えると顔が赤く…\nこれってもしかして\nひょっとして…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130901, 8219, 1, 0, 1, 0, "あっ、ヒーローさん！\n話しかけてくれた記念\nということで、宝石\n受け取ってください♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130901, 8220, 1, 0, 2, 0, "元気がない時は\n語尾にぴょんですっ！\nさあヒーローさんも\nやってみるぴょん♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130901, 8221, 1, 0, 3, 0, "ヒーローさんへのご恩\nおばあちゃんうさぎに\nなるまでに、お返し\nできるでしょうか…？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130901, 8222, 1, 0, 4, 0, "ヒーローさん！\n二人で一緒に\nパレードの練習\nしませんか～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 130901, 8223, 1, 0, 101, 0, "あはは♪\nヒーローさんは\n人を笑顔にする力が\nありますです♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 130901, 8224, 1, 0, 102, 0, "ヒーローさんに\n一日一恩返しですっ！\n返し終わるまで\nやめませんですます♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130901, 8225, 1, 1, 1, 0, "おはようございます♪\nんしょ、んしょっ…！\n気持ちよく起きられた\n記念に宝石どうぞ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130901, 8226, 1, 2, 2, 0, "お昼にフードコートで\nかばばん君バーガー\n買ってきましたよ！\n一緒に食べますです♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130901, 8227, 1, 3, 3, 0, "今日も一日\nお疲れさまですっ♪\nマッサージしますから\n寝そべってください♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130901, 8229, 1, 0, 2, 0, "はふぅ～…♪\nお仕事の疲れも\nぴょんぴょ～んって\n飛んでっちゃいます♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130901, 8230, 1, 0, 3, 0, "わぁっ、とっても\nかわいいですますっ！\nギルドハウスの\nマスコットですね♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130901, 8231, 1, 0, 4, 0, "ヒーローさんの好きな\n香り…これを宝石に\nつければ、受け取って\nもらえるでしょうか？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130901, 8232, 1, 0, 5, 0, "はあ、気持ちいい♪\nヒーローさんの\n好きな温度に\n調整しときますです♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130901, 8234, 1, 0, 7, 0, "支配人は体が資本っ！\nたくさん食べ…すぎて\n動けなくなっちゃい\nましたぁ～…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130901, 8235, 1, 0, 8, 0, "パークの\nガイドブックとか\nあったらいいですね♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131101, 8237, 1, 0, 1, 0, "外あつすぎ溶けるわ～\nやっぱ部屋ん中で\n水着がラクだねー", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131101, 8238, 1, 0, 2, 0, "ン～\nカリザきゅんどこー？\nもしかして逃げた？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131101, 8239, 1, 0, 3, 0, "ミソラちゃんと\nリリちゃん誘って\nそろそろイイコト\nしよっかなー？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131101, 8240, 1, 0, 4, 0, "おねえちゃんに\n頼まれたシゴトは\nまた今度にすっかー\n夏休みだしー♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131101, 8241, 1, 0, 5, 0, "騎士きゅんの\nプリンセスフォームも\n見てみたいんだよなー\nンフ♡", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 131101, 8242, 1, 0, 101, 0, "スライムを\nこーしてあーしたら\n騎士きゅんの泳ぐ\n練習に使えるっぽ？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 131101, 8243, 1, 0, 102, 0, "おかえり～♡\nって水着で迎えたら\n騎士きゅん喜んで\nくれるかなー？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131101, 8244, 1, 0, 1, 0, "騎士きゅん\nこのサングラス\nかけてみる？\nぜってー似合うよー", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131101, 8245, 1, 0, 2, 0, "日焼け痛かったら\nいつでも言ってねー\nスライムでひんやり\nしてあげるよ～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131101, 8246, 1, 0, 3, 0, "今から外で\n焼いてこっかなー\n騎士きゅんもどー？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131101, 8247, 1, 0, 4, 0, "スライムマッサージ\nしてあげよっかー\nほーれ、もみもみ～♡", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 131101, 8248, 1, 0, 101, 0, "騎士きゅんが選んだ\n水着も着てみたーい\nネアおねえちゃんに\nどんなの着てほしい？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 131101, 8249, 1, 0, 102, 0, "騎士きゅ～ん\n尻相撲しよっかー♪\nおねえちゃんに\n勝てたらご褒美だぞ～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131101, 8250, 1, 1, 1, 0, "暑すぎてムダに\n早起きしちった～\n二度寝すっか～\n…ふぁあ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131101, 8251, 1, 2, 2, 0, "夏バテしても\nスイカとかかき氷なら\n全然ウェルカム\nなんだよなー", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131101, 8252, 1, 3, 3, 0, "今日はこのまま\n水着で寝るかー\n夢ん中で泳ごー", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131101, 8254, 1, 0, 2, 0, "いったんキューケイ…\nンー…よし\n充電カンリョ～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131101, 8255, 1, 0, 3, 0, "仲間になりたそうに\nこっち見てんねー\nきゃわいいしおっけ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131101, 8256, 1, 0, 4, 0, "ン～いいニオーイ\nお日様あたった\n騎士きゅんの\n次くらいかな～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131101, 8257, 1, 0, 5, 0, "カラダ冷えてきたから\nたすかるーおかげで\nまだまだ水着イケるわ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131101, 8259, 1, 0, 7, 0, "うめ～\nとまんねぇ～\n夏バテどこいった～？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131101, 8260, 1, 0, 8, 0, "ふむふむ…\nこれは意外と\nぬるっと読めるっぽー", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131101, 8262, 1, 0, 10, 0, "ンフフ～\n海の戦士ってカンジ？\nアリよりのアリだね～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131101, 8263, 1, 0, 11, 0, "あーやべ\n一瞬で寝そー\nヤバだわ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129701, 8264, 1, 0, 1, 0, "服や紙が易々と\n燃えぬとは\nランドソルとはまこと\n住み易き国で候", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129701, 8265, 1, 0, 2, 0, "どの世界にあっても\n民は活力に溢れ\n街は賑わうもの…\n善哉、善哉", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129701, 8266, 1, 0, 3, 0, "食べ歩きに世界征服…\nギルドとはかくも\n自由気儘な徒党に\nありんすなぁ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129701, 8267, 1, 0, 4, 0, "連絡は…今日もなし\n我が妹子は相変わらず\n放埓なことよ喃", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129701, 8268, 1, 0, 5, 0, "モネは相変わらず\n警戒しきりなれど…\nそのようなところも\n愛らしくあり申す", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 129701, 8269, 1, 0, 101, 0, "なんと長閑な世にや\nありけむ…この平和は\n騎士殿たちの努力の\n賜物にありんす喃", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 129701, 8270, 1, 0, 102, 0, "『王家の装備』は\n使わぬのかと？\n…フフ。すでに\n用いており申す", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129701, 8271, 1, 0, 1, 0, "今ばかりは媛という\n立場は置いて\nこの地の者らと\n歓談したく候", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129701, 8272, 1, 0, 2, 0, "強さの秘密？　…フフ\n女子の秘密を暴くのは\n野暮にござんす\nご寛恕いただきたく候", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129701, 8273, 1, 0, 3, 0, "此方の家屋、\n見目以上に\n堅牢であり申す\n何ぞ仕掛けがありや？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129701, 8274, 1, 0, 4, 0, "心地よい時間とは\n疾く過ぎ去りゆくもの\n此処で過ごすと改めて\n実感できる喃", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 129701, 8275, 1, 0, 101, 0, "視線が気になる？\nこれはすみませぬ\nなにぶん、お前さまは\n見ていて飽きませぬ故", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 129701, 8276, 1, 0, 102, 0, "お前さまの前では\n誰もが心を開く…\nなんとまぁ罪作りな\n色男にありんすな？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129701, 8277, 1, 1, 1, 0, "よく眠れましたかの？\n寝る子は良く育つ、と\n古くより申します由", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129701, 8278, 1, 2, 2, 0, "美食殿の作法に倣い\n皆で共に昼餉を頂くと\n致しんしょう", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129701, 8279, 1, 3, 3, 0, "逢魔ヶ時は望まぬ客も\n参るもの…お前さまも\n気をつけなんし", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129701, 8281, 1, 0, 2, 0, "わっちの蛇に\n勝るとも劣らぬ\n座り心地で候", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129701, 8282, 1, 0, 3, 0, "魂、万物に宿る\nとの云いあらば\n情愛を注いでやるのが\nよかろうの", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129701, 8283, 1, 0, 4, 0, "いと香しき哉…\n騎士殿は香の道にも\n詳しくあられるか", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129701, 8284, 1, 0, 5, 0, "ジオ・ゲヘナの\n灼熱と比ぶれば\nそよ風と変わり\nござんせん", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129701, 8286, 1, 0, 7, 0, "美味なる哉…この味\nぺコリーヌ姫が好むも\n道理にありんす", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129701, 8287, 1, 0, 8, 0, "トーゴクに、オーマ…\n書で見るだけでも\n心惹かれる土地よの", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131201, 8289, 1, 0, 1, 0, "暑い日には\n冷たいものですよねー\nかき氷とか食べて\nすっきりしたいです★", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131201, 8290, 1, 0, 2, 0, "海への行き来は\nテレポーテーションに\nお任せです★", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131201, 8291, 1, 0, 3, 0, "トロピカルジュースが\n飲みたいでーす♪\n海の家で見たんですよ\nカップル用ジュース★", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131201, 8292, 1, 0, 4, 0, "ご飯どうしようかなー\nあ、海鮮ラーメンとか\n美味しそうかも★", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131201, 8293, 1, 0, 5, 0, "水鉄砲、普通に撃つと\n少し物足りないかも…\nどるるるる～ん★って\n感じが欲しいですよね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 131201, 8294, 1, 0, 101, 0, "コイビトっぽいこと\nアオハルっぽいこと\nいっぱいしたいです★\n修羅場とかも♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 131201, 8295, 1, 0, 102, 0, "騎士さんが溺れたら\n絶対助けに行きますね\n人工呼吸でもなんでも\nしてあげますから★", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131201, 8296, 1, 0, 1, 0, "騎士さーん\n日焼け止めの塗り直し\nお願いします★\n優しくしてくださいね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131201, 8297, 1, 0, 2, 0, "海で食べたフルーツ？\nはい、まだありますよ\nこっちでもあーんして\n食べさせましょうか★", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131201, 8298, 1, 0, 3, 0, "ぺんぽこりんは\nどうしたのかって？\nどうしたんでしょうね\nどーでもいいです★", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131201, 8299, 1, 0, 4, 0, "今日はどんな\nイイコトしましょうか\n思い出作り？\n人助けもいいですね★", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 131201, 8300, 1, 0, 101, 0, "ヒモを引っ張ったら…\n「セ・キ・ニ・ン」\n取ってもらいますよ？\nなーんてウソですけど", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 131201, 8301, 1, 0, 102, 0, "みんなでイイコトも\n楽しいですけど～\n…二人でイイコト\nしちゃいますか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131201, 8302, 1, 1, 1, 0, "朝ですよー！\n今日も一日、\nイイコト日和ですね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131201, 8303, 1, 2, 2, 0, "日差しにご注意ですね\n日焼け止め…誰かに\n塗ってほしいなー？\n…ちらっ★", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131201, 8304, 1, 3, 3, 0, "夜の海って\n惹かれちゃいますよね\n…ロマンチックです★", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131201, 8306, 1, 0, 2, 0, "う～ん、まったり～\nこれぞ新ジャンル\nおうちバカンス★", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131201, 8307, 1, 0, 3, 0, "よーしよーし★\nわたしとイイコト\n一緒にしましょうねー", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131201, 8308, 1, 0, 4, 0, "いい香りですねー\nリラックスタイムに\nちょうどよさそう★", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131201, 8309, 1, 0, 5, 0, "水遊びのあとは\nしっかり体を\n温めないとですよね★", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131201, 8311, 1, 0, 7, 0, "栄養補給もバッチリ★\n遊びもイイコトも\nお腹が減ってたら\nできませんからねー？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131201, 8312, 1, 0, 8, 0, "今だと海関連の本とか\n興味あるんですよねー\n魚とか海難救助とか★", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131201, 8314, 1, 0, 10, 0, "かっこいー★\n名前はー…\nぷんぷくぽんとか？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131201, 8315, 1, 0, 11, 0, "映えますねー\n写真撮ったら\n素敵な思い出に\nなりそうです★", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131601, 8316, 1, 0, 1, 0, "海でもないのに\nこの姿でいるのは…\nやはり、場違いでは\nありませんか…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131601, 8317, 1, 0, 2, 0, "スイカとは割ることと\n見つけたり！\n…ですが目隠しすれば\n見当たらないもの…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131601, 8318, 1, 0, 3, 0, "まぁ、膝に砂が…\n気がつきませんでした\nまるでわんぱくな\nお子様のよう…ふふっ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131601, 8319, 1, 0, 4, 0, "家の中でスイカを割る\nわけにはまいりません\nここは潔く包丁で\n切りましょう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131601, 8320, 1, 0, 5, 0, "ミソラ様やネア様の\n大胆さを見習って…\nき、騎士きゅーん…★\n難しいですね…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 131601, 8321, 1, 0, 101, 0, "きゅ、急に暗く…！？\n…失礼しました\n鉢巻きが下がってきた\nだけでした…ううぅ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 131601, 8322, 1, 0, 102, 0, "わたくしに剣の才は\nあるのでしょうか…\nいえ、何事もまずは\nやってみなくては", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131601, 8323, 1, 0, 1, 0, "なにか羽織った方が\nよろしいですよね…？\nそのままでいい？\nそれはそれで…その…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131601, 8324, 1, 0, 2, 0, "あの、兄様…？\nスイカの種を飛ばす\n遊びもあるというのは\n…本当なのでしょうか", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131601, 8325, 1, 0, 3, 0, "強くなるための修行は\nまだ始まったばかり\nまずは己の目指すべき\n道を見定めなくては", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131601, 8326, 1, 0, 4, 0, "同じ薄着であっても\n黒い天使の姿より…\n気が楽です", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 131601, 8327, 1, 0, 101, 0, "はい、ただいまー！\n…あっ！\n海の家でのお手伝いの\nくせが、つい…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 131601, 8328, 1, 0, 102, 0, "兄様さえよろしければ\nまた一緒に海へ…\nあ…申し訳ありません\n浮ついてしまいました", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131601, 8329, 1, 1, 1, 0, "本日もイイコトをして\n清々しい一日を\n過ごしたいものです\nまさに一日一善ですね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131601, 8330, 1, 2, 2, 0, "お昼時になると\n海の家の料理を\n思い出してしまいます\n焼きそばにかき氷…♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131601, 8331, 1, 3, 3, 0, "兄様…よろしければ\n浜辺を歩きませんか？\nきっと、月が\nきれいでしょうから", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131601, 8333, 1, 0, 2, 0, "ふわふわ心地よすぎて\n眠くなってしまいます\n水着姿では\nいけません…ふぁ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131601, 8334, 1, 0, 3, 0, "なんてよい手触り…\n癖になってしまいます\nふふ、よしよし…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131601, 8335, 1, 0, 4, 0, "爽やかな香り…♪\n日焼け止めクリームを\n思い出してしまいます", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131601, 8336, 1, 0, 5, 0, "冷たい水で冷えた体が\n温まります…♪\nまるで兄様のようです", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131601, 8338, 1, 0, 7, 0, "お腹が空いては\n修行ができません…！\n動きに障りない程度に\n栄養の補給を…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131601, 8339, 1, 0, 8, 0, "剣術とスイカ割りが\n上達する指南書…\n見つかりませんね…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131601, 8341, 1, 0, 10, 0, "さあ、ともに大海原を\n参りましょう！\nなんて…ふふっ♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131601, 8342, 1, 0, 11, 0, "まるでおとぎ話の\n登場人物になった\nような心持ちです…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131701, 8343, 1, 0, 1, 0, "あたしはなんで水着で\n室内をうろついてるの\nかしら…不思議だわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131701, 8344, 1, 0, 2, 0, "あ、天井が汚れてる\n仕方ないわね\nひとっ飛びしますか", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131701, 8345, 1, 0, 3, 0, "はい、お疲れさま♪\n出迎えの言葉は\nやっぱこれよね♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131701, 8346, 1, 0, 4, 0, "そういえばあたしって\n羽があるけど\n泳げるのかしら…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131701, 8347, 1, 0, 5, 0, "あいつのために\n浮き輪も用意したし\n抜かりはないわよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 131701, 8348, 1, 0, 101, 0, "海までのガイド計画も\nまとまったわね\nふふっ…楽しみだわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 131701, 8349, 1, 0, 102, 0, "この水着、あいつが\n選んでくれたのよね\n…大事にしないと", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131701, 8350, 1, 0, 1, 0, "ネビアは最近どう？\n迷惑かけてない？\n…かけてるのね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131701, 8351, 1, 0, 2, 0, "あんた…部屋の片付け\nくらい自分でしなさい\n散らかってたわよ？\n…片付けておいたけど", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131701, 8352, 1, 0, 3, 0, "もしかして暇なの？\nだったら記憶でも\n見せましょうか？\n凄く恥ずかしいやつ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131701, 8353, 1, 0, 4, 0, "夏ってこんなに\n暑いのね…\n海にばかり行く\n気持ちが分かったわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 131701, 8354, 1, 0, 101, 0, "あんたの水着って\n新しいの買わないの？\nなんならあたしが\n選んであげるわよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 131701, 8355, 1, 0, 102, 0, "気軽に触れ合える\n距離にいる…\nあたしにとって\nとても大事なことよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131701, 8356, 1, 1, 1, 0, "あらおはよう\n夏の朝は暑いわね\nシャワーでも浴びて\nさっぱりしなさい", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131701, 8357, 1, 2, 2, 0, "お昼は何を食べるの？\nガイド妖精として\n提案しましょうか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131701, 8358, 1, 3, 3, 0, "夜更かしは厳禁よ\n無理やり眠らせて\nあげましょうか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131701, 8360, 1, 0, 2, 0, "アメスさまだって\nゴロゴロするときは\nあるのよ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131701, 8361, 1, 0, 3, 0, "…ふふっ♪\n何よ、あたしが\nかわいいものを\n愛でちゃだめなの？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131701, 8362, 1, 0, 4, 0, "こういういい香り…\n塔の中じゃなかなか\n楽しめないのよね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131701, 8363, 1, 0, 5, 0, "水着を着てるのに\n温まるのって\n矛盾してない？\n気にしすぎかしら", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131701, 8365, 1, 0, 7, 0, "あんたと行動するなら\n沢山食べられるように\nならなきゃね…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131701, 8366, 1, 0, 8, 0, "文字を読むのと\nデータを読むのは\nまったく違うわ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131701, 8368, 1, 0, 10, 0, "へぇ、面白いじゃない\nこういう経験、昔じゃ\nできなかったわ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131701, 8369, 1, 0, 11, 0, "面白いクッションね\nところであんた…\nあたしで遊んでない？\n気のせいならいいけど", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118601, 8370, 1, 0, 1, 0, "また新しい悪戯\n思いついちゃった♪\n誰に仕掛けようかしら", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118601, 8371, 1, 0, 2, 0, "あ～あ、退屈…\nフィオでもからかって\nこようかしら♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118601, 8372, 1, 0, 3, 0, "ミソギの悪戯って\nレベル高いのよね…\n負けてらんないわ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118601, 8373, 1, 0, 4, 0, "家事ニガテなのよね～\nコッコロたちには\n感謝しないとね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118601, 8374, 1, 0, 5, 0, "私も魔物料理に\n慣れてきちゃった…\n毒されてるわね…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 118601, 8375, 1, 0, 101, 0, "あいつに仕掛ける\n次の悪戯は～…♪\nこれ…ううん…\nやっぱこっちかしら？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 118601, 8376, 1, 0, 102, 0, "結局ギルドハウスが\n一番落ち着くわね\n…私たちの家だもの", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118601, 8377, 1, 0, 1, 0, "悪戯のコツでも\n教えてあげよっか？\n…え～興味ないの？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118601, 8378, 1, 0, 2, 0, "これからもずーっと\n退屈しない毎日に\nしてあげる♪\n私の悪戯でね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118601, 8379, 1, 0, 3, 0, "ちょうど退屈してたの\nたーっぷりと\n構ってもらうわよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118601, 8380, 1, 0, 4, 0, "案内が必要なら\n頼ってくれていいわよ\n一応は私だって\nガイド妖精だし！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 118601, 8381, 1, 0, 101, 0, "冒険のガイドなら\n私に任せなさい♪\nある意味忘れられない\n旅にしてあげる♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 118601, 8382, 1, 0, 102, 0, "妖精がいる家は\n栄えるのよ♪　だから\nずっといてあげる\nあんたのそばにね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118601, 8383, 1, 1, 1, 0, "きゃははっ！\n寝ぐせついてるわよ？\nこのままにしましょ！\nだって面白いし！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118601, 8384, 1, 2, 2, 0, "自由に外へ行けるって\nすっごく幸せなことよ\nさ、散歩行きましょ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118601, 8385, 1, 3, 3, 0, "安心していいわ\n寝てるあんたに\n悪戯しないから\n…あんたにはね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118601, 8387, 1, 0, 2, 0, "悪くはないけど…\nあいつの頭の方が\n座り心地いいかも", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118601, 8388, 1, 0, 3, 0, "この子を使った\n悪戯は…\n…止めとこっと\nこんなにかわいいし♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118601, 8389, 1, 0, 4, 0, "このギルドハウスに\nピッタリな匂いね♪\n一言で言えば…\nカオスって感じ？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118601, 8390, 1, 0, 5, 0, "なんていうか…\n心があたたかいわ\n…変な感じ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118601, 8392, 1, 0, 7, 0, "ガイド妖精にも\n食事の機能があって\nよかったのかも\nしれないわね…もぐ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 118601, 8393, 1, 0, 8, 0, "じれったい物語ね…\nこんなの霧を使えば\n一瞬で終わるのに！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131801, 8395, 1, 0, 1, 0, "わる～い子は\nトゥンヌス漁船に\n乗ってもらおうかな～", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131801, 8396, 1, 0, 2, 0, "たまにはファミリーの\nみんなにもお休みを\nあげないとね～☆", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131801, 8397, 1, 0, 3, 0, "海辺に素敵な\nバーがあるんだ～\n彼が大人になったら\n一緒に行きたいな♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131801, 8398, 1, 0, 4, 0, "夏の風を肌で\n感じるの好きかも～\nラーゴはどう？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131801, 8399, 1, 0, 5, 0, "海岸線を\nのんびり散歩もいいね\n砂や貝殻に紛れた\nお宝探しの旅だよ～☆", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 131801, 8400, 1, 0, 101, 0, "みんなで集まって\nビーチパーティーでも\n開いちゃおっかな～♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 131801, 8401, 1, 0, 102, 0, "さて、この水着…\n彼は気に入って\nくれてるかな～？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131801, 8402, 1, 0, 1, 0, "心ゆくまで夏を\n楽しんじゃお～☆\nほら君も♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131801, 8403, 1, 0, 2, 0, "水着似合ってるって？\nふふ♪　うれしいこと\n言ってくれるね～☆", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131801, 8404, 1, 0, 3, 0, "熱中症には\n気を付けないと\nだめだよ～？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131801, 8405, 1, 0, 4, 0, "海の家ならうちの\nファミリーでやってる\nとこにしよっか☆\n豪遊しちゃお～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 131801, 8406, 1, 0, 101, 0, "君と一緒なら\nどんな夏になるのか\n楽しみだね～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 131801, 8407, 1, 0, 102, 0, "君といると子供心を\nくすぐられちゃうな～\n大人だって思いっきり\n遊んでいいんだよね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131801, 8408, 1, 1, 1, 0, "さ、今日は何をして\n過ごそっかな～…\nねえ、君は何したい？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131801, 8409, 1, 2, 2, 0, "ふふ♪\n波音が心地良い\n時間だね～☆", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131801, 8410, 1, 3, 3, 0, "今夜は星空の下で\n乾杯しよっか～☆\n飲み物取ってくるね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131801, 8412, 1, 0, 2, 0, "ここ涼しい～♪\nちょっと一休み\nしていこっか☆", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131801, 8413, 1, 0, 3, 0, "こういうのも良いけど\nやっぱりラーゴの方が\nかわいいかな～♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131801, 8414, 1, 0, 4, 0, "爽やかな香りだね～\nこういう感覚も絵で\n表現してみたいな～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131801, 8415, 1, 0, 5, 0, "続きを描く前に\n原稿を乾かさないとね\nインクがにじむと\n直すの大変なんだよ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131801, 8417, 1, 0, 7, 0, "今日は水着だし…\n食べるのは控えめに\nしておこっかな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131801, 8418, 1, 0, 8, 0, "話題の作品は\nチェックしないとね～\nあっ、この作家さん\n新刊出すんだ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131801, 8420, 1, 0, 10, 0, "うちの出店の客引きに\nこういう乗り物系を\n使うのもアリかな～☆", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131801, 8421, 1, 0, 11, 0, "これ、背景用の\n参考資料に\n一つ欲しいかも♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 181201, 8422, 1, 0, 1, 0, "ここに帰るのも\n久しぶり…あれから\n変わったところは\nあるかしら？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 181201, 8423, 1, 0, 2, 0, "バイトも復帰しなきゃ\nうちのギルドにとって\n食費は重要だもの…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 181201, 8424, 1, 0, 3, 0, "コッコロマ…っ\n…うぅ…\nこの癖は\n直らないのね…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 181201, 8425, 1, 0, 4, 0, "この世界はまだまだ\n知らないことだらけね\n『裏世界』とか…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 181201, 8426, 1, 0, 5, 0, "必ずみんなを\n助け出してみせるわ\n…もちろん、兄さんも", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 181201, 8427, 1, 0, 101, 0, "またみんなと\n暮らせるなんて…\n夢じゃ、ないのよね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 181201, 8428, 1, 0, 102, 0, "たくさんの人が\n私に優しくしてくれる\n…だからこそ、私も\n頼れる人になりたいわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 181201, 8429, 1, 0, 1, 0, "次はランドソルの\n街の外にも行きたいわ\nあの…よかったらまた\n一緒に回ってくれる？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 181201, 8430, 1, 0, 2, 0, "独りぼっちで眠るのは\nやっぱり寂しかったわ\n…みんなで雑魚寝？\nそんなの…絶対やるわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 181201, 8431, 1, 0, 3, 0, "ネビアってば大泣きで\n私を出迎えてくれたの\n…すごく嬉しかったわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 181201, 8432, 1, 0, 4, 0, "この翼、陽に当たると\n本当に綺麗よね…\n自分のだけれど、つい\nずっと見ちゃうもの", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 181201, 8433, 1, 0, 101, 0, "おにーたんっ♪\n…呼びたくなっただけ\n本当にそれだけよっ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 181201, 8434, 1, 0, 102, 0, "どうしたの？\n…ふふっ、安心して\nこれからもずっと\n私はここにいるわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 181201, 8435, 1, 1, 1, 0, "みんな起きだす時間ね\n街が活気づいてく感じ\n嫌いじゃないわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 181201, 8436, 1, 2, 2, 0, "【美食殿】のお昼は\nいつも美味しいから\nまた太っちゃいそうね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 181201, 8437, 1, 3, 3, 0, "おやすみなさい\n明日も、その先も\n一緒に冒険しましょう", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 181201, 8439, 1, 0, 2, 0, "休憩も大事…よね\nじゃあ遠慮なく…\nふぅ～…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 181201, 8440, 1, 0, 3, 0, "はぁ…ふわふわ…♪\nこのかわいさには\nもう抗えないわ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 181201, 8441, 1, 0, 4, 0, "落ち着く香り…\nこの匂いを嗅いでると\n帰ってきたなって\n思うわね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 181201, 8442, 1, 0, 5, 0, "みんなで野宿した時も\n焚火の明かりを見て\nすごく安心したのよね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 181201, 8444, 1, 0, 7, 0, "ペコリーヌさんには\n負けるけど…\n美味しくって手が\n止まらないわ…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 181201, 8445, 1, 0, 8, 0, "プリンセスフォーム…\n一体どういう仕組みで\n発動するのか、文献に\n書いてないかしら…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131901, 8447, 1, 0, 1, 0, "すごーく暑いけど、\nこの暑さをどうやって\n乗り越えるかって\n考えるのも楽しいかも", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131901, 8448, 1, 0, 2, 0, "氷で彫刻を作るの？\nそれは楽しそうだけど\nすぐに溶けちゃわ\nないかしら？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131901, 8449, 1, 0, 3, 0, "お日様の下で遊ぶのも\n部屋の中で涼むのも\nどっちも夏ならではの\n楽しみ方なのね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131901, 8450, 1, 0, 4, 0, "生き物の観察日記、\n私も書いてみたいな\nパックのお昼寝日記\nなんてどうかしら？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131901, 8451, 1, 0, 5, 0, "さまぁばけぇしょんは\n計画を立てるもの\nなんだって\n私も挑戦してみるわね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 131901, 8452, 1, 0, 101, 0, "この世界のことを\nもっと知って\nアーラム村のみんなに\nお話ししてあげるの", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 131901, 8453, 1, 0, 102, 0, "いつルグニカに帰るか\nわからないからこそ\n毎日頑張らなきゃね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131901, 8454, 1, 0, 1, 0, "今日はどこに行くの？\n私は、えーと…\nかき氷が食べたいわ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131901, 8455, 1, 0, 2, 0, "ここにも精霊が\n住んでたりするの？\n…精霊じゃなくて\nがいど妖精？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131901, 8456, 1, 0, 3, 0, "パックったら、\n近所の子にいっぱい\nなでなでされてたわ\nすごーく人気者ね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131901, 8457, 1, 0, 4, 0, "実は…線香花火の\n特訓をしてたの！\n次は負けないんだから", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 131901, 8458, 1, 0, 101, 0, "昨日、みんなの\n似顔絵も絵日記に\n描いてみたんだけど…\n笑っちゃダメよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 131901, 8459, 1, 0, 102, 0, "何気ない時間だって\n後で思い返せば\nずっと忘れない\n素敵な思い出になるの", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131901, 8460, 1, 1, 1, 0, "気持ちよく起きて、\nいい一日を過ごしたい\nそんなときには\nラジーオタイソーね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131901, 8461, 1, 2, 2, 0, "お昼ごはんの時間ね\n暑さに負けないように\nしっかり食べましょう", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131901, 8462, 1, 3, 3, 0, "外は涼しいみたいだし\n夜の散歩に行ってみる\nのはどうかしら？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131901, 8464, 1, 0, 2, 0, "遊び疲れたら\nここでひとやすみ…\nふわぁ…\n眠くなっちゃった…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131901, 8465, 1, 0, 3, 0, "細かいところまで\nすごーく丁寧に\n作られてる…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131901, 8466, 1, 0, 4, 0, "わっ、なんだか\nすーっとする香りね\nちょっぴり涼しく\nなったみたい", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131901, 8467, 1, 0, 5, 0, "海で遊ぶと\n体が冷えるから\nあったかいのも\nすごーく嬉しい…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131901, 8469, 1, 0, 7, 0, "これも美味しい…！\nペコリーヌが\nどんどん食べちゃう\n気持ちが分かるかも", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131901, 8470, 1, 0, 8, 0, "このお話も面白い…！\nあっという間に時間が\n過ぎちゃったわ…\n…ね、寝てません！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131901, 8472, 1, 0, 10, 0, "この子と一緒に海に\nいったら楽しそうね！\nどこに行けば\n会えるのかしら？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 131901, 8473, 1, 0, 11, 0, "ふかふかね！\nこうしてると波の音が\n聞こえてきそう…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132101, 8474, 1, 0, 1, 0, "こちらでは水着姿で\n過ごすのも\n珍しくないようですね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132101, 8475, 1, 0, 2, 0, "メイド服ではなく\n姉様と色違いの水着…\nレムは嬉しいです", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132101, 8476, 1, 0, 3, 0, "少し室温が高いかも\nしれませんね…\nいくつか氷を配置して\nおきましょうか", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132101, 8477, 1, 0, 4, 0, "清涼感があるとはいえ\n鉄球をビーチバレーの\nボールに代用するのは\n危ないでしょうか…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132101, 8478, 1, 0, 5, 0, "ふぅー…ふぅー…\nようやく浮輪が\n膨らみました\n姉様にお渡ししないと", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 132101, 8479, 1, 0, 101, 0, "海のことをもっと\n教えていただけますか\n…スバルくんと海の\n話をしたいんです", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 132101, 8480, 1, 0, 102, 0, "もし海に行く機会が\nあればレムにも…\nお声がけくださいね\nぜひ、お願いします", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132101, 8481, 1, 0, 1, 0, "何を着ていても\nメイドはメイド\n掃除も料理も\nなんでもござれです", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132101, 8482, 1, 0, 2, 0, "スバルくんとあなたは\n兄弟のように\n仲がいいですね\n姉様とレムと一緒です", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132101, 8483, 1, 0, 3, 0, "この姿で子どもたちと\n遊ぶのは…さすがに\nはばかられますね\nやはり着替えてきます", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132101, 8484, 1, 0, 4, 0, "ナンパ…というものが\n海にはあるそうです\nスバルくんがとても\n警戒していました", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 132101, 8485, 1, 0, 101, 0, "魚釣りでしたら\nレムもご一緒します\n氷漬けの魚の備蓄を\n補充したかったので", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 132101, 8486, 1, 0, 102, 0, "子どもたちと砂遊びの\n約束をしているんです\nあなたもご一緒に\nいかがですか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132101, 8487, 1, 1, 1, 0, "ラジーオタイソーは\nもうされましたか？\n体がほぐれますよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132101, 8488, 1, 2, 2, 0, "お昼ですね。では\n姉様に焼きそばを…\nもちろん皆様の分も\n用意してありますよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132101, 8489, 1, 3, 3, 0, "熱帯夜であっても\nお休みの際はお腹を\n冷やさないように\n気をつけてくださいね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132101, 8491, 1, 0, 2, 0, "とても気持ちよさそう\nです…！\nさっそく姉様に\nお教えしないと", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132101, 8492, 1, 0, 3, 0, "季節に合わせた\n着せ替えができれば\n子どもたちも\n楽しめそうです", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132101, 8493, 1, 0, 4, 0, "ふむふむ…この香りは\n疲れを癒やしてくれ\nますね。エミリア様に\nお持ちします", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132101, 8494, 1, 0, 5, 0, "身も心もほぐれます\n思っていたよりも\n体が冷えていたみたい\nですね…ふう…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132101, 8496, 1, 0, 7, 0, "もぐもぐ…んうっ！？\nこのすっぱさは…\n梅干し、ですか…\nくせになりますね…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132101, 8497, 1, 0, 8, 0, "海に関する詩文なども\nあるのでしょうか…\n気になります", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132101, 8499, 1, 0, 10, 0, "地竜とは違った\n乗り心地です…\n本物にも乗ってみたい\nものですね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132101, 8500, 1, 0, 11, 0, "この大きさで\n本物の貝であれば…\nどんな料理にするか\n想像が膨らみます", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132001, 8501, 1, 0, 1, 0, "これがこの世界の\n居住環境か…\n面白い構造だね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132001, 8502, 1, 0, 2, 0, "暇を持て余していてね\n誰か話し相手に\nなってくれないかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132001, 8503, 1, 0, 3, 0, "目新しいものが\nたくさんあるね\n胸が躍るよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132001, 8504, 1, 0, 4, 0, "頑丈そうな建物だ\nここなら彼女たちが\n暴れても大丈夫かな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132001, 8505, 1, 0, 5, 0, "力が制限されていて\n良かった面もある\n君も吐かずにすんだし", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 132001, 8506, 1, 0, 101, 0, "悪さなんてするわけ\nないじゃないか\n傷つくなぁ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 132001, 8507, 1, 0, 102, 0, "普段と違う格好で\n歩き回るのも\n悪くない経験だ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132001, 8508, 1, 0, 1, 0, "ボクに聞きたいことが\nあるなら遠慮なく\n質問するといい", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132001, 8509, 1, 0, 2, 0, "気軽に魔女に\n話しかけるなんて\n君は物好きだね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132001, 8510, 1, 0, 3, 0, "この世界においては\nボクは異物だからね\n大人しくしているさ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132001, 8511, 1, 0, 4, 0, "ボクの反応を\n見たいのかい？\n君の好奇心には\n恐れ入るよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 132001, 8512, 1, 0, 101, 0, "ボクの手を借りたい？\nもちろん構わないさ\n対価はいただくけどね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 132001, 8513, 1, 0, 102, 0, "ボクと海を見たい？\nふふ、魅力的な\nお誘いだね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132001, 8514, 1, 1, 1, 0, "お目覚めかな？\nさて、君の見た夢を\n教えてくれないかな？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132001, 8515, 1, 2, 2, 0, "食後のお茶を\n一緒にどうだろうか？\nさあ、席に座ってよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132001, 8516, 1, 3, 3, 0, "おや、お休みかな？\n魔女の前で無防備だね\n…無論、何もしないさ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132001, 8518, 1, 0, 2, 0, "こういう椅子は\n茶会には向かないね\nつい気が緩んでしまう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132001, 8519, 1, 0, 3, 0, "こういうものを好きな\n女の子がいいのかな？\n君の嗜好を知りたいな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132001, 8520, 1, 0, 4, 0, "いい香りだ\nこれにはどんな\n効能があるんだろう？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132001, 8521, 1, 0, 5, 0, "火の点け方に\nボクのいた世界との\n違いはないのかな？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132001, 8523, 1, 0, 7, 0, "これもまた未知の味だ\n存分に味わわせて\nもらうとしよう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132001, 8524, 1, 0, 8, 0, "素晴らしいね\nここには叡智が\n詰まっている", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132001, 8526, 1, 0, 10, 0, "魔物を模倣した\nぬいぐるみか…\n実物を見たくなるね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132001, 8527, 1, 0, 11, 0, "いい気持ちだ…ボクと\nしたことが、何も考え\nられなくなりそうだ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 109701, 8528, 1, 0, 103, 0, "願わずにいられません\nどうかこちらの\n世界には…悲劇が\n生まれませんようにと", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 109701, 8529, 1, 0, 103, 0, "食事のご希望があれば\nなんなりと。お腹が\nいっぱいになるまで\n沢山ご用意しますね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 109801, 8530, 1, 0, 103, 0, "あの男、これからも\n観察が必要ね\nラムの目の届く範囲に\n置く必要があるわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 109801, 8531, 1, 0, 103, 0, "素直なところは\nあの男の美点ね\n癪だけど、偶然にも\nラムと同じだわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 109901, 8532, 1, 0, 103, 0, "迷惑かけちゃったけど\n雪遊びは楽しかったわ\nまたやりたい…なんて\n言ったら怒られるかも", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 109901, 8533, 1, 0, 103, 0, "数えきれないくらいの\n思い出を、みんなで\n作りましょう！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132201, 8534, 1, 0, 1, 0, "わわっ、今すごく\nいい香りがしたけど…\n誰の香水かな\n教えてほしいな…♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132201, 8535, 1, 0, 2, 0, "錬金術用の釜、\n置くならどこかな？\n自分専用の錬金釜、\n憧れちゃうよね♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132201, 8536, 1, 0, 3, 0, "この匂い…誰か\n唐揚げ食べたみたい\nうぅ…お腹空いて\nきちゃうよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132201, 8537, 1, 0, 4, 0, "次の新曲のタイトルは\n『恋の錬金術』！\nうーん、ちょっと\nベタ過ぎるかな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132201, 8538, 1, 0, 5, 0, "あ、錬金の素材\n切らしてたんだ…\n彼を誘って採集に\n行ってこようかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 132201, 8539, 1, 0, 101, 0, "瞬時に着替えができる\n薬とかできないかな？\n舞台で衣装チェンジ！\nなーんて、楽しそう♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 132201, 8540, 1, 0, 102, 0, "彼が遊びに来た後は\n部屋の空気がほんのり\n彼の匂いなんだ…\nなんだか幸せだな♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132201, 8541, 1, 0, 1, 0, "今日もバイト？\nそれならこれ飲んで\n私のお手製ポーション\nがんばってきてね！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132201, 8542, 1, 0, 2, 0, "新しくできたカレー屋\n行ってみない？\n時々スパイスの香りが\n流れてくるんだよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132201, 8543, 1, 0, 3, 0, "私のフィギュア\nそっくりでしょ！\nルイちゃんが作って\nくれたんだ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132201, 8544, 1, 0, 4, 0, "あれ、シャンプー\n変えた？　私と同じの\nだよ！　どうしよう\nドキドキしちゃう…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 132201, 8545, 1, 0, 101, 0, "いらっしゃいませ♪\n何を作りますか？\nなーんてね。錬金堂で\nまた一日店長したいな", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 132201, 8546, 1, 0, 102, 0, "クンクン…彼の\nハンカチ…いい匂い♪\n私だけの秘密のお守り\nにしてるんだ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132201, 8547, 1, 1, 1, 0, "ねえねえ、天気も\nいいし一緒に錬金の\n素材集めに行こうよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132201, 8548, 1, 2, 2, 0, "お昼ご飯、用意したよ\nあはは、さすがに\nランチは錬成しないよ\n私の手作りだよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132201, 8549, 1, 3, 3, 0, "今日も一日お疲れさま\nこれ飲んでゆっくり\n休んで。薬じゃないよ\nホットミルクだよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132201, 8551, 1, 0, 2, 0, "昨日、遅くまで\n錬金術の勉強してた\nから眠くって…\nちょっと一休みっと", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132201, 8552, 1, 0, 3, 0, "この子にちょっとだけ\n香水かけて、と…\nんー！　だっこした時\nすっごくいい匂い♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132201, 8553, 1, 0, 4, 0, "花みたいな、\nお菓子みたいな\nいい香り。気持ちが\n穏やかになるね♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132201, 8554, 1, 0, 5, 0, "外でもこの暖かさを\n感じられる道具、\n作れたりしないかな？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132201, 8556, 1, 0, 7, 0, "素材集めに行くと\nお腹が空くんだよね\nイロハちゃんも\nこの味好きそう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132201, 8557, 1, 0, 8, 0, "錬金術にはたくさんの\n知識が必要だからね\nさあ、今日も調合書を\n読まなくちゃ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127701, 8559, 1, 0, 1, 0, "ここが騎士さんたちの\n住んでる場所…！\n粗相がないように\n気をつけなきゃ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127701, 8560, 1, 0, 2, 0, "ジオ・テオゴニアの\nみんな、私がいない間\nちゃんとお仕事\nしてればいいけど…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127701, 8561, 1, 0, 3, 0, "コッコロさんも\n従者として長いって\n聞いたような…\nあの年ですごい…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127701, 8562, 1, 0, 4, 0, "自分がもてなされる\n側になるのって\nやっぱりちょっと\n落ち着かないかも…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127701, 8563, 1, 0, 5, 0, "今度のお休みの日は\n何しよう…？\nあっ、最近できたお店\nヴルムと行こうかな？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 127701, 8564, 1, 0, 101, 0, "お買い物したら\nかわいいからって\nたくさんおまけして\nもらっちゃった…♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 127701, 8565, 1, 0, 102, 0, "ここにいると、つい\nぼんやりしちゃう…\nこれも騎士さんの力…\nなのかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127701, 8566, 1, 0, 1, 0, "お手伝いできることは\nありませんか？\nお世話でもなんでも\n任せてくださいっ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127701, 8567, 1, 0, 2, 0, "ランドソルに初めて\n来たときは驚きました\n砂糖の山と思ったら…\nあんなに寒いなんて！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127701, 8568, 1, 0, 3, 0, "あの、ヴルムを\n見ませんでしたか？\nもう…あの子ってば\nまた仕事をサボって…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127701, 8569, 1, 0, 4, 0, "家具や家が壊れても\n食べられないなんて\nなんだかもったいない\nですね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 127701, 8570, 1, 0, 101, 0, "とっておきの紅茶を\n手に入れたんです\n一杯いかがですか？\nその、日頃のお礼です", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 127701, 8571, 1, 0, 102, 0, "頑張ってて偉いって…\nほ、褒めてもなんにも\n出ませんからね！？\n…えへへ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127701, 8572, 1, 1, 1, 0, "おはようございます！\nモーニングティーは\nいかがですか？\n私、準備しますねっ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127701, 8573, 1, 2, 2, 0, "ライラエルさまも\nお昼寝はお好きです\nけど…だからって\n寝すぎはダメですよ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127701, 8574, 1, 3, 3, 0, "おやすみなさい\nくれぐれも寝不足には\n気をつけてくださいね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127701, 8576, 1, 0, 2, 0, "しっかりしないと…\nメイドの私が\n気を抜くなんて…\nそんな…ふぅ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127701, 8577, 1, 0, 3, 0, "わぁ、かわいい…♪\nってダメダメ！\nメイドなんだから\n落ち着かないと…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127701, 8578, 1, 0, 4, 0, "いい匂い…♪\nこの香り、\nライラエルさまも\n好きそうです！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127701, 8579, 1, 0, 5, 0, "ランドソルの環境に\n触れると、この光の\nありがたみが\n身に沁みますね…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127701, 8581, 1, 0, 7, 0, "甘くない食べ物が\n一般的なのも\n慣れてきました\nあむ…おいひぃ…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127701, 8582, 1, 0, 8, 0, "ランドソルの文字、\n私たちのところと\nちょっと違いますね…\n覚えられるかなぁ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127801, 8584, 1, 0, 1, 0, "甘い匂いがしない…\nジオ・テオゴニアとは\n別の世界なんですね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127801, 8585, 1, 0, 2, 0, "掃除めんど…\nん…このくらいで\nまー十分でしょ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127801, 8586, 1, 0, 3, 0, "そろそろ食事の\n用意もしなきゃ…\nあーやる事多い…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127801, 8587, 1, 0, 4, 0, "秘密の隠れ場所、\nまた姉さんにバレた…\n次のを探さないと", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127801, 8588, 1, 0, 5, 0, "知らない間に仕事が\n終わったり…しないか\nしてくれたらいいのに", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 127801, 8589, 1, 0, 101, 0, "きちんともてなすには\n騎士さんの好みも\n覚えてかないと…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 127801, 8590, 1, 0, 102, 0, "ご主人サマに\nご奉仕します～♪\n…キャラじゃないか\nやめやめ、恥ずかし…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127801, 8591, 1, 0, 1, 0, "居心地いいっすね…\n今日からここに\n住んでもいいですか", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127801, 8592, 1, 0, 2, 0, "あの、ちょっと姉さん\n怒らせちゃったんで\n匿ってくれません？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127801, 8593, 1, 0, 3, 0, "羽と尻尾が格好いい？\n意外と邪魔ですよ\n仰向けで寝にくいし", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127801, 8594, 1, 0, 4, 0, "ワークライフバランス\nっていい言葉ですね\nぜひライフ多めで", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 127801, 8595, 1, 0, 101, 0, "騎士さんって\n働き者ですね\nそのままあたしの\n仕事も…いえなんでも", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 127801, 8596, 1, 0, 102, 0, "熱心に見てても\n尻尾は触らせて\nあげませんってば\n角？　角なら…まぁ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127801, 8597, 1, 1, 1, 0, "寝ても寝ても\n寝足りない…ふぁ…\nあー…眠たい…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127801, 8598, 1, 2, 2, 0, "アフタヌーンティーの\n準備は得意ですよ\nつまみ食いとか\n…つまみ食いとか", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127801, 8599, 1, 3, 3, 0, "よく休んでください\n布団は干しといたんで\n…姉さんが", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127801, 8601, 1, 0, 2, 0, "ふー…\nこのままずーっと\nここに居たい…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127801, 8602, 1, 0, 3, 0, "あんたも一緒に\nサボっちゃうか～\n…なんて", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127801, 8603, 1, 0, 4, 0, "落ち着く匂い…\n騎士さんの好み\nなんですかね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127801, 8604, 1, 0, 5, 0, "こういうとこの\n掃除って面倒だし…\n今度でいっか", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127801, 8606, 1, 0, 7, 0, "ご飯の前に\nこんな食べたら\n姉さんきっと怒るな…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 127801, 8607, 1, 0, 8, 0, "食に関する本が\nやけに多い…\nお国柄ですかね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130201, 8609, 1, 0, 1, 0, "すんすん…\nお兄さんの匂いが\nぷんぷんする～♡\nザコくっさぁ♡", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130201, 8610, 1, 0, 2, 0, "ぴこーん！\nいひひっ、新作メカの\n面白いアイデア、\n思いついちった～♡", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130201, 8611, 1, 0, 3, 0, "メカもいじりたいし\n服もアクセもほしー…\nあ～、おこづかい\n足んないよ～！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130201, 8612, 1, 0, 4, 0, "お勉強でもしよっかな\nザコお兄さんには\n絶対わかんない\nよーなやつ♡", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130201, 8613, 1, 0, 5, 0, "お兄さんの部屋って\nどこどこぉ？\nメカの実験室に\n使えそうだよね～♡", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 130201, 8614, 1, 0, 101, 0, "次のデートコースは\nどんなのかなー？\n…いや、むしろ次は\nぼくが決めちゃうっ？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 130201, 8615, 1, 0, 102, 0, "強奪計画は念入りに♡\nお兄さんのハートも\n全力でザコザコにする\n計画、立てないとね♡", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130201, 8616, 1, 0, 1, 0, "ねー、ヒマヒマヒマ～\nかまってかまってー♡\nどっか連れてけー♡", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130201, 8617, 1, 0, 2, 0, "今日はこっちで\nご飯食べたいなー♡\n…帰っても５日連続の\nバカナ姉カレーだし…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130201, 8618, 1, 0, 3, 0, "依頼行くならぼくも\n連れてってよー♡\nお兄さんのザコっぷり\n見ててあげる～♡", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130201, 8619, 1, 0, 4, 0, "お兄さんはぼくより\nオトナだもんね～♡\nじゃあオモテナシ、\n期待してるから～♡", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 130201, 8620, 1, 0, 101, 0, "ヤマ姉になんかお土産\n持って帰ろうかなー？\nザコお兄さん、選ぶの\n付きあって～♡", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 130201, 8621, 1, 0, 102, 0, "遊びにいくときは\nザコお兄さんも\n誘ってあげるよ♡\nどうせヒマでしょ～？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130201, 8622, 1, 1, 1, 0, "ふぁ～、ねむぅ～…\n夜更かしぶん寝るから\nあとで起こして～♡\nあ、変なこと禁止～♡", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130201, 8623, 1, 2, 2, 0, "おなかすいたぁ～\nなんか食べ行こ♡\n甘いのおごって～♡", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130201, 8624, 1, 3, 3, 0, "えー、もう寝る時間？\nじゃあ…添い寝して\nあげよっか？\nなーんて、ウッソー♡", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130201, 8626, 1, 0, 2, 0, "おお～ふわっふわ～♪\nバンディシャーク号の\n座席に使いたーい！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130201, 8627, 1, 0, 3, 0, "カワイイ～♡\n改造したらもっと\nカワイくなるかな～？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130201, 8628, 1, 0, 4, 0, "くんくん…いい匂い♡\nザコお兄さんって\nこーゆー香りが\n好きなのかなぁ～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130201, 8629, 1, 0, 5, 0, "はー、あったかーい♡\nバンディシャーク号\nにも欲しいかもー♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130201, 8631, 1, 0, 7, 0, "食べれるときに\n食べるのが大事って\nヤマ姉言ってたしね～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130201, 8632, 1, 0, 8, 0, "あ、この本読んだよ♡\n参考文献リストが\nしっかりしてて\n勉強になったなー♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130001, 8634, 1, 0, 1, 0, "部屋は多少ゴチャつい\nてる方が落ち着くのさ\nバンディシャーク号\nみたいにね♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130001, 8635, 1, 0, 2, 0, "呆けてるのもいいが\nちいと物足りないねぇ\n麦しゅわもブドウ朱も\n無いなんてさ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130001, 8636, 1, 0, 3, 0, "この家セキュリティが\nなってないねぇ…\n盗賊目線でちょっくら\n指南でもしてやるか", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130001, 8637, 1, 0, 4, 0, "不思議だねぇ\nここで飲むと、酔いが\n回るのが早い気が\nするよ…ヒック", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130001, 8638, 1, 0, 5, 0, "ふむ…\nクルマが壊れた時は\nアジト代わりに\n使えそうだ♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 130001, 8639, 1, 0, 101, 0, "カカッ、いい部屋だ！\n妹を迎える準備は\nできてるみたいだねぇ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 130001, 8640, 1, 0, 102, 0, "…っと、油断したら\n落ち着いちまうね\nボーヤの甘っちょろい\n雰囲気のせいだよ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130001, 8641, 1, 0, 1, 0, "さすがにこの家から\n盗みはしないさ\nいずれは妹たちの\n共有財産になるからね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130001, 8642, 1, 0, 2, 0, "ボーヤ、暇なら\n博打でもやるかい？\nアタシが負けたら\n掃除でもしてやるよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130001, 8643, 1, 0, 3, 0, "立派な家じゃないか！\n一国一城の主とは\nちょっとボーヤのこと\n見くびってたかもねぇ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130001, 8644, 1, 0, 4, 0, "かわいい義弟のためだ\nアタシが盗ってきた\nお宝をくれてやるよ\n好きな場所に飾りな！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 130001, 8645, 1, 0, 101, 0, "妹と結ばれる前に\n他の女との関係は\n清算しとくように\nわかったね、ボーヤ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 130001, 8646, 1, 0, 102, 0, "お、ヤシオーリかい？\nフフン、義弟からの\nお酌とあっちゃあ\n断れないねぇ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130001, 8647, 1, 1, 1, 0, "うう…清々しい朝だが\n頭がいてぇ…\nちっと昨日は\n飲みすぎたか…？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130001, 8648, 1, 2, 2, 0, "ほらこれ、ワカナに\n作らせた弁当だ\nこれで昼からも\nバリバリやりな！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130001, 8649, 1, 3, 3, 0, "まだまだ夜はこれから\n…と言いたいとこだが\n早寝して立派に育ちな\nそれが妹たちの為だよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130001, 8651, 1, 0, 2, 0, "中々いい座り心地だが\n盗賊が腑抜ける訳には\nいかないね…が…\nもう少しだけ座るか…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130001, 8652, 1, 0, 3, 0, "こういうのの中に\n実はレアなブツが\n隠されてるとか\nありがちだねぇ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130001, 8653, 1, 0, 4, 0, "ふうん、ボーヤは\nこういう匂いが\n好きなのかい？\n妹たちに教えてやろう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130001, 8654, 1, 0, 5, 0, "体を温めんにゃ\n酵露をカッとやるのが\n一番だが、これも存外\n悪くないねぇ…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130001, 8656, 1, 0, 7, 0, "ガツガツ…\nん、悪くねェ味だ\nこれにヤシオーリでも\nありゃ文句はないね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130001, 8657, 1, 0, 8, 0, "本にも載ってない\nレアもんのお宝か…\nフッ、想像しただけで\n血沸き肉躍るねぇ…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130101, 8659, 1, 0, 1, 0, "今日の買い物は\nいつものお野菜と…\nあっ、確か特売が\nあったはず…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130101, 8660, 1, 0, 2, 0, "お掃除、お掃除…♪\nきゅっきゅっ…\nよいしょ、っと…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130101, 8661, 1, 0, 3, 0, "洗濯ものも\nたまらないうちに\nやっちゃわないと…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130101, 8662, 1, 0, 4, 0, "夕飯のメニュー…\n何にしよう？\n期限の早い食材は…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130101, 8663, 1, 0, 5, 0, "色々お菓子も\n作ってみたいけど…\n予算…足りるかなぁ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 130101, 8664, 1, 0, 101, 0, "騎士さん…どんな\n料理が好きなんだろ？\n前に作ったコロッケは\n評判よかったけど…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 130101, 8665, 1, 0, 102, 0, "騎士さんと\nおしゃべりすると、\n胸があたたかくなる…\nなんでだろ…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130101, 8666, 1, 0, 1, 0, "あっ、騎士さん\nどうかしましたか？\n御用ならいつでも…♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130101, 8667, 1, 0, 2, 0, "削っても削っても\n家計簿は赤字…\n騎士さ～ん…\n助けてください～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130101, 8668, 1, 0, 3, 0, "手が空いてたら\n日用品の買い出し、\n手伝ってもらっても\nいいですか…？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130101, 8669, 1, 0, 4, 0, "やっと一息つけそう…\nあっ、わたし\nお茶を淹れてきますね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 130101, 8670, 1, 0, 101, 0, "困りごとがあったら\n言ってくださいね\nわたし…騎士さんの\n力になりたいです", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 130101, 8671, 1, 0, 102, 0, "助けてもらった分は\n必ず返しますから…\nそれまでわたしのこと\n見ててくださいね…♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130101, 8672, 1, 1, 1, 0, "忘れ物はないですか？\n今日のお仕事も\n頑張りましょうね…！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130101, 8673, 1, 2, 2, 0, "お昼ご飯できてますよ\n大したものじゃなくて\n恐縮ですけど…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130101, 8674, 1, 3, 3, 0, "夜更かしはだめですよ\nわたしも家事を\n終わらせたら寝ないと", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130101, 8676, 1, 0, 2, 0, "これ…割れ物防止の\nクッションに\n使えるかも…？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130101, 8677, 1, 0, 3, 0, "こういうのいいなぁ…\nけど…フブキちゃんに\n絶対改造されちゃう…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130101, 8678, 1, 0, 4, 0, "いい匂い…でも\n切れかけてるかも？\nえっと補充するには…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130101, 8679, 1, 0, 5, 0, "あったかい…\nちょっとだけなら\n休んでも…いいよね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130101, 8681, 1, 0, 7, 0, "これがあったら\nもうお料理を\n作らなくて済むかも？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 130101, 8682, 1, 0, 8, 0, "新しい節約レシピは…\nあ、この草って\n食べられるんだ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132801, 8684, 1, 0, 1, 0, "愛溢れるランドソルの\nクリスマス…ですか\nいけませんね、つい\n胸が躍ってしまいます", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132801, 8685, 1, 0, 2, 0, "ツリーの飾りと\nわたしの髪…\nくるくるしていて\n似ているかも…くすっ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132801, 8686, 1, 0, 3, 0, "この姿で傲慢な\nわたしになったら…\nいつもより怖がらせて\nしまうでしょうね…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132801, 8687, 1, 0, 4, 0, "悪魔として、人を\nドキドキさせなくては\nい、イオから教わった\n…投げキスの練習を…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132801, 8688, 1, 0, 5, 0, "ジオ・テオゴニアの\nお菓子は、ツリーの\n飾りに向いていそう\nですね…ふふっ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 132801, 8689, 1, 0, 101, 0, "騎士のギルドハウス…\nクリスマスの時期も\n暖かいのですね…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 132801, 8690, 1, 0, 102, 0, "城の皆と過ごす宴も\n楽しいですが…\n騎士と二人の宴も\n幸せです…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132801, 8691, 1, 0, 1, 0, "騎士、あなたの\nもてなしに感謝します\nクリスマスの何たるか\n教えてくださいね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132801, 8692, 1, 0, 2, 0, "この黒い羽は\nどう手入れすれば？\nイオなら知っている\nでしょうか…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132801, 8693, 1, 0, 3, 0, "城の皆も連れてきたい\nほど、クリスマスは\n楽しい行事ですね\nお土産も迷います", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132801, 8694, 1, 0, 4, 0, "クリスマスの晩餐に\n招待されたのですが…\nその…辛いものは\nありませんよね…？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 132801, 8695, 1, 0, 101, 0, "クリスマスと言わず…\nいつでも星に祈って\nいますよ？　騎士達に\n幸せがあるように、と", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 132801, 8696, 1, 0, 102, 0, "悪魔として騎士を\nドキドキさせる…？\nそ、そのようなこと\nわたしには…うぅ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132801, 8697, 1, 1, 1, 0, "朝は寒いですね…\n雪…積もっている\nでしょうか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132801, 8698, 1, 2, 2, 0, "ふあぁ…いけません\n公務の滞在先で\nお昼寝など…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132801, 8699, 1, 3, 3, 0, "夜は悪魔として\n夢を見せましょう\n騎士はどんな夢が\n見たいですか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132801, 8701, 1, 0, 2, 0, "ふぅ…落ち着きます…\n家でゆっくりと過ごす\nクリスマスも\n良いものですね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132801, 8702, 1, 0, 3, 0, "リンドのプレゼントに\nぬいぐるみは…？\n…子ども扱いすると\n落ち込むでしょうか…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132801, 8703, 1, 0, 4, 0, "暖かいお部屋に\nこの香りを漂わせたら\nふふっ…素敵な時間を\n過ごせそうです", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132801, 8704, 1, 0, 5, 0, "これがあれば、\n寒い冬も安心ですね\n暖かくて…このまま\n眠ってしまいそうです", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132801, 8706, 1, 0, 7, 0, "悪魔の力を使ったら\nお腹が空いてきて\nしまって…\n恥ずかしいです…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132801, 8707, 1, 0, 8, 0, "クリスマスには多くの\n伝承があるのですね…\n天使の伝承まで\nありましたし…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132803, 8709, 1, 0, 14, 0, "まぁ…！\nよくできていますね\nサプライズプレゼント\n…でしょうか。ふふ♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132804, 8710, 1, 0, 15, 0, "ど、どうでしょう…？\n親善交流の催しに\nなりそうでしょうか…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132901, 8711, 1, 0, 1, 0, "ギルドハウスって\nあったかぁ～い♪\n心がぽかぽかするの！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132901, 8712, 1, 0, 2, 0, "クリスマスの悪魔の\nお仕事はないわね！\nだってここのみんな\nいい子なんですもの♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132901, 8713, 1, 0, 3, 0, "みんなちゃんと\n寝られてるかしら？\n夜更かしせずに、\nいい夢見てね♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132901, 8714, 1, 0, 4, 0, "毎回、来年が\n楽しみになるような\n素敵なクリスマスを\n過ごしたいわね♪", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132901, 8715, 1, 0, 5, 0, "訳ありケーキを見ると\n刑務作業を思い出すわ\n…あれも貴重な体験\nだったのかしら…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 132901, 8716, 1, 0, 101, 0, "ロマンチックな\nクリスマスに備えて\n今のうちに少女漫画で\n予習しておかないと！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 132901, 8717, 1, 0, 102, 0, "ほっぺにチューなら\nもう一回しても…\nや、やっぱり無理！\n恥ずかしいわ～！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132901, 8718, 1, 0, 1, 0, "またみんなで\nセッションしたいわね\nカラーベルの練習した\n成果を見せたいの♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132901, 8719, 1, 0, 2, 0, "君はサンタさんに\nどんなプレゼントを\n頼んでいたのかしら？\n気になるわ～！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132901, 8720, 1, 0, 3, 0, "愛が壊れても、\n何度でも治してあげる\n私たちの間に、\n愛の尽きる日はないわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132901, 8721, 1, 0, 4, 0, "冬休みって短いようで\n長く感じちゃうわよね\n学校のみんなと\n早く会いたいな～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 132901, 8722, 1, 0, 101, 0, "愛をテーマにした\n冬休みの作文の宿題、\n君の分を読むのが\n今から楽しみね♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 132901, 8723, 1, 0, 102, 0, "よかったらまた\n私をハニーって呼んで\nほしいの。ダーリン♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132901, 8724, 1, 1, 1, 0, "おはよう！\n冬の朝は冷えるから\n風邪を引かないように\n対策しましょうね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132901, 8725, 1, 2, 2, 0, "お昼ご飯は一緒に\n食べに行きましょう！\nクリスマス限定の\nランチコースよ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132901, 8726, 1, 3, 3, 0, "ねえねえ、キャンドル\nイルミネーションを\n見に行きましょうよ！\nロマンチックよ～♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132901, 8728, 1, 0, 2, 0, "暖かいクリスマスを\n過ごすのに必須よね♪\nふふ、柔らかぁい！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132901, 8729, 1, 0, 3, 0, "どの子もかわいい～♪\nクリスマスツリーの\n下で、一緒にお祝い\nしてもらいたいな♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132901, 8730, 1, 0, 4, 0, "なんだか胸の奥が\n温かくなる匂いね～\nリラックスしちゃう♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132901, 8731, 1, 0, 5, 0, "あったかぁい♪\nあ、でも夜になったら\nサンタさんが入れる\nように、火を消すのよ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132901, 8733, 1, 0, 7, 0, "寒い冬だからこそ\nしっかり食べて\n備えておかなきゃ！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132901, 8734, 1, 0, 8, 0, "『聖夜の６時間』…？\n気になる小説だけど…\n勝手に読んじゃ悪い\nわよね…ガマンよ…！", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132901, 8736, 1, 0, 14, 0, "きゃっ…！\nこんな不思議な本が\nあったのね～\nちょっと面白いかも♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 132901, 8737, 1, 0, 15, 0, "もふもふ、ふわふわで\nあったかくて\n気持ちいい～…♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129501, 8738, 1, 0, 1, 0, "冬といえばサマー！\n…って言うけど夏は\n何をＰＲするんだろ\nメルクリゾート…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129501, 8739, 1, 0, 2, 0, "奨学金の道は遠いな…\nあまりに遠すぎて\n最近忘れてたまである", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129501, 8740, 1, 0, 3, 0, "暇だし鏡でも見よっと\nはー、今日も顔が良…\nげ、若干むくんでる？\nはー、今日終わった", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129501, 8741, 1, 0, 4, 0, "水着…お姉ちゃんと\n貸し借りするんだけど\nこれって少数派姉妹？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129501, 8742, 1, 0, 5, 0, "ちゅむ、ツムギちゃん\n今日も可愛いねデュフ\n…なんで推し語りの声\nキモくなるんだろ私…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 129501, 8743, 1, 0, 101, 0, "あれ…なんで私\n水着のまま部屋の中\nうろついてるんだっけ\n…こわ！　てか寒っ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 129501, 8744, 1, 0, 102, 0, "先輩よく友達と海に\n行くって言ってたけど\n…え、誰とだろ？\nまあ誰でもいいけどさ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129501, 8745, 1, 0, 1, 0, "始まる…\nチエルの夏が始まる…\nあ冬でしたっけ？\nまどっちでもいいです", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129501, 8746, 1, 0, 2, 0, "こないだトミヱ先輩\n壁に喧嘩売ってました\n相変わらず元気ですね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129501, 8747, 1, 0, 3, 0, "ユニ先輩にお芝居の\n出来を聞かれたんで\n可愛いって答えました\n嘘は言ってません", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129501, 8748, 1, 0, 4, 0, "クロエ先輩の腹筋\nかっちょいーですよね\nさりげ触りたいけど\nあいつ隙ないんですよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 129501, 8749, 1, 0, 101, 0, "先輩もうカメラ係は\nやらないんです？\nチエルはやってますよ\n最高の被写体を常に", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 129501, 8750, 1, 0, 102, 0, "メリゾでカップル割の\n企画あるらしいんです\nちなみに先輩は…\nあ、いえ、何でも…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129501, 8751, 1, 1, 1, 0, "朝ごはんは抜いても\n朝ちぇるだけは\n抜いちゃ駄目ですよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129501, 8752, 1, 2, 2, 0, "お昼なに食べるか\n悩んでます？\nおごってくれるなら\nいい店教えますけど", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129501, 8753, 1, 3, 3, 0, "この時間帯になると\nユニ先輩がごそごそ\n蠢きだすんですよね…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129501, 8755, 1, 0, 2, 0, "世界の美少女が…\nこんな格好で\nくつろいでたら…\nまずい…事案になる", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129501, 8756, 1, 0, 3, 0, "え、この子って\nウォータープルーフ？\nメリゾにも連れて\nいけちゃうカンジ？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129501, 8757, 1, 0, 4, 0, "はぁ～、いい香り…\nそういえばメリゾの\nスタッフルームも\n『贅』な匂いしてた…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129501, 8758, 1, 0, 5, 0, "薄着で暖まるとか…\n冷静になっちゃ\n駄目なやつですよね\nこれ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129501, 8760, 1, 0, 7, 0, "おにぎり１個は\nちぇるに換算すると…\n２５ちぇるくらい？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129501, 8761, 1, 0, 8, 0, "あれー？　この辞書\nまだちぇるの項目\n載ってないんだ？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129501, 8763, 1, 0, 10, 0, "何これ…地味に楽しい\nあとたぶん\nクロエ先輩が好きそう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 129501, 8764, 1, 0, 11, 0, "これは良きですねー\n自宅でメリゾ気分とか\nユニ先輩もにっこり", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133001, 8765, 1, 0, 1, 0, "ここが騎士氏の\nお住まいですか\nふむ…地縛霊は\nいなさそうですね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133001, 8766, 1, 0, 2, 0, "ここは土足厳禁では\nないみたいですね\nま、私には関係ないか\n足、透っけ透けなので", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133001, 8767, 1, 0, 3, 0, "ランドソルの裏路地\nあそこは最高ですね\n暗くてじめじめしてて\n癒されるー", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133001, 8768, 1, 0, 4, 0, "っ！\n今ラップ音が…？\nまさか幽霊？\nやだ、怖い", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133001, 8769, 1, 0, 5, 0, "獣人族にエルフ族…\nなるほど騎士氏が\n幽霊程度では驚かない\n訳です…ちぇ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 133001, 8770, 1, 0, 101, 0, "今日の深夜\n騎士氏が起きるまで\n枕元でずーっと\n立っててみようかな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 133001, 8771, 1, 0, 102, 0, "騎士氏の驚く顔を\n見ると胸が高鳴る…\n変ですね、心臓なんて\n動いてないのに", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133001, 8772, 1, 0, 1, 0, "安心して下さい\n勝手にすり抜けて\n騎士氏の部屋は\n覗きませんよ…多分", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133001, 8773, 1, 0, 2, 0, "なんですか騎士氏\n怖い話でも聞きます？\nじゃあいきますね…\n今貴方の前に幽霊がっ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133001, 8774, 1, 0, 3, 0, "この家…幽霊が\n寄ってくる間取りに\nなってますね\nまあ、嘘ですけど", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133001, 8775, 1, 0, 4, 0, "料理はしませんよ\n指きったら痛いので\nえ…透ければいい？\n……天才ですか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 133001, 8776, 1, 0, 101, 0, "お散歩ですか？\n深夜なら付き合います\nあ、やっぱり断ります\n幽霊出たら怖いので", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 133001, 8777, 1, 0, 102, 0, "騎士氏の部屋のセンス\n好きですよ…もっと\nじめじめしていれば\nなおよし、ですが", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133001, 8778, 1, 1, 1, 0, "レイスになってから\nあまり言わなくなった\n挨拶第一位はこちら…\nおはようございます", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133001, 8779, 1, 2, 2, 0, "騎士氏。午後からも\n頑張って下さいね\n私は日陰で休みます\nでは、おつです", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133001, 8780, 1, 3, 3, 0, "夜も更けてきましたね\nここからがレイスの\n本領発揮…では早速\nう　ら　め　し　や～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133001, 8782, 1, 0, 2, 0, "中々座り心地が\nよろしいですね\nブロッケンに跨る方が\n安心はしますが", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133001, 8783, 1, 0, 3, 0, "知ってますよ\nどうせ貴方も\n夜中になったら\n動き回るんでしょ？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133001, 8784, 1, 0, 4, 0, "すう…はあ…\nすごく落ち着く香り\n…落ち着き過ぎて\n成仏しそうでした", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133001, 8785, 1, 0, 5, 0, "温度は感じませんが\nなぜかほっこり\n温かい気持ちです\nなんででしょうね？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133001, 8787, 1, 0, 7, 0, "もぐもぐ…\n食べ物は基本好きです\n消化の方法は…\n企業秘密で", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133001, 8788, 1, 0, 8, 0, "苦手なんですよね\nホラー小説\nだって幽霊とか\n怖いじゃないですか", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133201, 8790, 1, 0, 1, 0, "この得も言われぬ\n高揚感…やはり\n正月はいいものだな！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133201, 8791, 1, 0, 2, 0, "む、いかんな\n気付くと黒豆と\n栗きんとんばかり\n食べてしまう…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133201, 8792, 1, 0, 3, 0, "ギルドの連中にも\nお年玉を渡してみるか\n少しは士気が上がる\nかもしれないな！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133201, 8793, 1, 0, 4, 0, "正月くらいはのんびり\n羽を伸ばすとしよう\n適切な休暇も軍人の\n大事な責務だからな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133201, 8794, 1, 0, 5, 0, "正月で浮かれすぎた\n者も多いはずだ\n念のため街を見回りに\n行くとしよう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 133201, 8795, 1, 0, 101, 0, "む、このおせち料理は\n絶品だな！\nあいつの分も\nとっておいてやろう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 133201, 8796, 1, 0, 102, 0, "今年の抱負は…\nそうだな、あいつと\nさらに高みを目指す\nこれでいこう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133201, 8797, 1, 0, 1, 0, "えっ、お年玉！\n…い、いや、私は\n子どもではないからな\nえ、遠慮させてもらう", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133201, 8798, 1, 0, 2, 0, "凧あげすごろく\nこま回し、たとえ遊び\nでも手は抜かんぞ\nさあ、勝負だ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133201, 8799, 1, 0, 3, 0, "そのきな粉餅も\nうまそうだな…\n私のあんこと交換\nしないか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133201, 8800, 1, 0, 4, 0, "貴公、正月料理を\n食べ過ぎてないか？\nじ、実は私も少々、な", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 133201, 8801, 1, 0, 101, 0, "貴公、お参りに行くぞ\n何？　外が寒いだと？\n仕方ない、手ぐらいは\nつないでやろう", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 133201, 8802, 1, 0, 102, 0, "貴公と新たな年を\n迎えられてうれしいぞ\n今年も一つ\nよろしくたのむ！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133201, 8803, 1, 1, 1, 0, "起きろ貴公。早朝なら\n参拝客も少ないはず\n今のうちにお参りを\n済ませよう", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133201, 8804, 1, 2, 2, 0, "昼食に餅を焼いたぞ\nのどに詰まらせない\nようにゆっくり噛んで\n食べるんだ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133201, 8805, 1, 3, 3, 0, "夜ふかしはあまり\n褒められたものでは\nないが…正月くらいは\n目をつむろう", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133201, 8807, 1, 0, 2, 0, "これに座って飲む\n甘酒は格別だな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133201, 8808, 1, 0, 3, 0, "せっかくの正月だし\nこいつにも着物を\n着せてやりたいな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133201, 8809, 1, 0, 4, 0, "これは梅の香り…？\nふふ、なかなか\n風流ではないか", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133201, 8810, 1, 0, 5, 0, "冬の寒さが堪えるな…\nここでしっかりと\n暖をとるとしよう", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133201, 8812, 1, 0, 7, 0, "正月はのんびり\nしたいからな、勝手に\n食事が出てくるのは\n本当に助かる", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133201, 8813, 1, 0, 8, 0, "ふむ、これが世界の\n新年のお祝いか\nなかなか興味深いな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133501, 8815, 1, 0, 1, 0, "一年の計は元旦にあり\n計画を立てて、\n有意義な一年を\n過ごしましょ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133501, 8816, 1, 0, 2, 0, "年末年始はどうしても\n出費が増えちゃうし\nしばらくはきっちり\n節約しなきゃ…！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133501, 8817, 1, 0, 3, 0, "タタミにフトン、\nトーゴク文化の内装…\nランドソルでももっと\n流行らないかしら", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133501, 8818, 1, 0, 4, 0, "子どもたちが凧を\n手作りして遊んでたの\n偉いわよね、みんな…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133501, 8819, 1, 0, 5, 0, "お年玉をあげる側に\nなって、分かる苦労…\n大人になるって\nこういうことかしら", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 133501, 8820, 1, 0, 101, 0, "一応実家にも\n顔を出さないと…\nよかったらだけど、\nあんたも来る？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 133501, 8821, 1, 0, 102, 0, "毎日一緒にいて、\nささやかな幸せを\n同じように感じて…\nそれだけで十分ね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133501, 8822, 1, 0, 1, 0, "着物姿、もっと\nじっくり見たいの？\nさすがに照れるわよ、\nもう…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133501, 8823, 1, 0, 2, 0, "整理整頓、できてる？\nもし散らかってるなら\nお正月休みのうちに\n片づけちゃいましょ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133501, 8824, 1, 0, 3, 0, "今年もみんなが無事に\n過ごせますように…\nほら、一緒に\nお祈りしましょ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133501, 8825, 1, 0, 4, 0, "みんな、あんたと\n遊ぶのが楽しみだって\n子どもに好かれる\nタイプなのよね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 133501, 8826, 1, 0, 101, 0, "あんたの運勢は大吉！\nラッキーパーソンは\n金髪の幼なじみよ\n…なんてねっ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 133501, 8827, 1, 0, 102, 0, "初夢は実現するのよね\nそれじゃ、あたしは\nあんたと…んんっ\nなんでもないわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133501, 8828, 1, 1, 1, 0, "外が寒いと、その分\n目も冴えるわよね\nこのまま朝の散歩に\n行こうかしら", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133501, 8829, 1, 2, 2, 0, "お正月休みでも、\n食べてばっかじゃダメ\nしっかり動いて\n体を引き締めましょ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133501, 8830, 1, 3, 3, 0, "ちゃんとあったかく\nして寝るのよ\nお正月から\n風邪ひかないようにね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133501, 8832, 1, 0, 2, 0, "着物、しわに\nなっちゃいそう…\nでもそれ以上に、\n心地よくって…はぁ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133501, 8833, 1, 0, 3, 0, "門松みたいに\nこの子も飾ってみる？\n運勢がよくなるかも♪", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133501, 8834, 1, 0, 4, 0, "この匂い、もしかして\nトーゴクのお香？\nワビサビを感じるわ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133501, 8835, 1, 0, 5, 0, "ふぅ、あったかい…\n冷えた体に\n染み渡ってくるわ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133501, 8837, 1, 0, 7, 0, "んっ、おいしい！\nおせちばっかじゃ\nやっぱり飽きちゃう\nものね…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133501, 8838, 1, 0, 8, 0, "験担ぎとか縁起物とか\n調べてみると\nいろいろあって\n面白いのよね～", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133601, 8840, 1, 0, 1, 0, "ふーん、いい家ね\nアタシの巣に\nしちゃおうかしら？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133601, 8841, 1, 0, 2, 0, "お姉ちゃん…\n早く会いたいなぁ…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133601, 8842, 1, 0, 3, 0, "メドゥシアナも\n気に入るかしら？\n今度連れてこよっと！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133601, 8843, 1, 0, 4, 0, "お邪魔してるだけ\nなのも悪いわよね…\nお返しはしないとね\n…ネズミ退治とか？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133601, 8844, 1, 0, 5, 0, "何か平和よね\nランドソルって…\nアタシ的にはもう少し\n荒っぽくてもいいわ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 133601, 8845, 1, 0, 101, 0, "ランドソルも\n楽しい街よね…\n案内してくれる\nアイツのおかげ？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 133601, 8846, 1, 0, 102, 0, "アタシ、アイツに\n頼りすぎてるかも…？\nで、でも！　断らない\nアイツも問題だわ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133601, 8847, 1, 0, 1, 0, "ねぇ、つまんなーい！\nもっとアタシを\n楽しませなさいよねー", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133601, 8848, 1, 0, 2, 0, "この家悪くないけど…\nもっとムシムシ\nジメジメにできない？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133601, 8849, 1, 0, 3, 0, "何よ？\n用もないのに声掛けた\n…とか言ったら\n容赦しないわよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133601, 8850, 1, 0, 4, 0, "少し部屋が寂しいわね\nそうだ、アンタを\n石にして家具に…\n冗談よ、ふふんっ♪", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 133601, 8851, 1, 0, 101, 0, "ねぇ、もう少し\n話していかない？\nアンタの話なら…\n特別に聞いてあげるわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 133601, 8852, 1, 0, 102, 0, "い、いつも\nあ、あ、ありが…\nうぅ、何でもないー！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133601, 8853, 1, 1, 1, 0, "やっと起きたわね？\n寝ぼすけなんだから！\n尻尾でひっぱたいて\n起こそうかと思ったわ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133601, 8854, 1, 2, 2, 0, "人捜しは日の高い\nうちに限るわ！\nさぁ、メドゥシアナ！\n全速力で出発進行よ!", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133601, 8855, 1, 3, 3, 0, "アタシ…\n子どもじゃないもん…\n眠くなんか…\nないんだからぁ…", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133601, 8857, 1, 0, 2, 0, "ふわふわであったかい\nつい、とぐろを\n巻きたくなるわね…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133601, 8858, 1, 0, 3, 0, "アンタ、こんな\nヤワな体じゃ駄目よ\nウロコくらい\n生やしたらどうなの？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133601, 8859, 1, 0, 4, 0, "くんくん…\n…ふん\n悪くないじゃない\n…くんくんくんくん…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133601, 8860, 1, 0, 5, 0, "あったかいの大好き！\nジオ・ゲヘナでも\nよく日向ぼっこ\nしたもんだわ…ふわぁ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133601, 8862, 1, 0, 7, 0, "お姉ちゃんを捜すなら\n…もぐ\n体力つけないとね\n…もぐもぐ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133601, 8863, 1, 0, 8, 0, "何よ？　たまには\n勉強くらいするわよ\nお姉ちゃんに\n笑われちゃうもの", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133701, 8865, 1, 0, 1, 0, "この装いと力…\n今の世界との\n整合性を上手いこと\n合わせておかないとだ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133701, 8866, 1, 0, 2, 0, "この格好…開発してた\n当時のまんまだ\nサイズもそのまま♪\n体型維持も完璧っ！", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133701, 8867, 1, 0, 3, 0, "あとで装備の手入れも\nやっとかないと\n…剣なら研ぐだけで\n終わるんだけどなぁ", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133701, 8868, 1, 0, 4, 0, "自分の原点を\n振り返ってみるのも\nたまには必要だね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133701, 8869, 1, 0, 5, 0, "完璧じゃなくても\n今できる全力を尽くす\nその事に意味がある…\nそうだよね、少年", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 133701, 8870, 1, 0, 101, 0, "始まりのあの日、\nそして向かうべき道…\nいつも忘れずにいよう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 133701, 8871, 1, 0, 102, 0, "誰かを救うために\n許容される犠牲なんて\nあっちゃいけない…\n全部、助けてやる", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133701, 8872, 1, 0, 1, 0, "おっと少年、\n何か御用かな？\nキミに関わる事なら\n最優先で対応するよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133701, 8873, 1, 0, 2, 0, "困った事があれば\n頼ってもらって\n構わないからね！\nむしろもっと頼って！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133701, 8874, 1, 0, 3, 0, "そうだ、時間あるなら\n武器のメンテを\n手伝ってくれないかな\n数が多くてさ～", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133701, 8875, 1, 0, 4, 0, "遠距離戦の心得、\n少年にもレクチャー\nしてあげよっか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 133701, 8876, 1, 0, 101, 0, "無茶しないで、って\n言ってもするだろうし\nせめてアタシの手が\n届く範囲で頼むよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 133701, 8877, 1, 0, 102, 0, "虚実入り混じる\nこの世界でキミが…\n進むべきルートを\n照らしてくれたんだ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133701, 8878, 1, 1, 1, 0, "グッモーニン！\n朝だよ少年、今日も\n張り切っていこう！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133701, 8879, 1, 2, 2, 0, "クレープの代わりに\nガレットを焼いたんだ\nこの味で少年を\n唸らせてみせよう！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133701, 8880, 1, 3, 3, 0, "もうこんな時間だ\n遅くならないうちに\n作業を切り上げようか\n少年も早く寝なよ？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133701, 8882, 1, 0, 2, 0, "あえて緊張を解くのも\n戦いには必要だし…\nそうそう…大事大事…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133701, 8883, 1, 0, 3, 0, "愛着を抱かせる事を\n意図したデザインだね\n心理的効果の観点から\n分析してみようか…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133701, 8884, 1, 0, 4, 0, "香りは記憶と密接に\n結びつく事がある…\n懐かしい匂いだね…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133701, 8885, 1, 0, 5, 0, "癒されるな…\n何がこの感情変化を\n引き起こすんだろう？", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133701, 8887, 1, 0, 7, 0, "食の偉大さを感じるな\n戦闘糧食ってやっぱり\n味気ないしなぁ…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133701, 8888, 1, 0, 8, 0, "実用ばかりじゃなく\n詩や物語ってのも\n人には必要だよね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133901, 8890, 1, 0, 1, 0, "この場所の開発版も\n探せばあるかもな\nさて、どんなデザイン\nだったのやら…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133901, 8891, 1, 0, 2, 0, "今まで数々の波乱に\n身を投じたが…やはり\n波乱にはこの姿が一番\n似合いだよ☆", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133901, 8892, 1, 0, 3, 0, "むっ…この角に羽と\n尻尾は家具とぶつかり\nやすいな。兎の時は\n気にならなかったが…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133901, 8893, 1, 0, 4, 0, "いつもより闘争を\n求める気分になるな\n初心に帰るとは\nこのことか…？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133901, 8894, 1, 0, 5, 0, "未調整上等☆\nそれを上手く使い\nこなしてこその\nワタシだろう？", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 133901, 8895, 1, 0, 101, 0, "坊やとできそうな\n愉しいことは…\nふむ、多すぎて迷うな", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 133901, 8896, 1, 0, 102, 0, "時折、坊やの居心地の\nよさに絆されそうに\nなってしまうな\nワタシらしくもない…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133901, 8897, 1, 0, 1, 0, "どうだ、おろした\n髪型も艶やかだろう？\nそんじょそこらの\n賞賛では満足せんぞ☆", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133901, 8898, 1, 0, 2, 0, "外に出るぞ！\n共に退屈を殺しに\n行こうじゃないか☆", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133901, 8899, 1, 0, 3, 0, "角と翼も触りたい？\nワタシの虚数魔境に\n挑むというなら\n止めはせんよ☆", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133901, 8900, 1, 0, 4, 0, "なんだ、困りごとか？\nクリスティーナさんが\n何でも聞いてやろう☆\n解決するかは別だがな", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 133901, 8901, 1, 0, 101, 0, "力が欲しいか？\nならば呼ぶがいい！\nワタシの名を☆", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 133901, 8902, 1, 0, 102, 0, "悪魔との契約は絶対\nなのだそうだが…\nどうだ、坊や\n一つ契ってみるか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133901, 8903, 1, 1, 1, 0, "まだ眠い？　甘えるな\n寝首を掻かれたく\nなければ飛び起きて\nこい…☆", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133901, 8904, 1, 2, 2, 0, "昼食の準備を\nしてやろう。\n毒見はいらんぞ？\n安心するといい！", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133901, 8905, 1, 3, 3, 0, "坊やは寝る時間だ\n寝ないのならば…\n大人の時間に\n付き合ってもらうか☆", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133901, 8906, 1, 0, 1, 0, "角と翼、尻尾の\nコンディションまで\n完璧だな☆", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133901, 8907, 1, 0, 2, 0, "なかなかのフワモコ\n具合だが…ううむ…\n翼と尻尾が邪魔だな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133901, 8908, 1, 0, 3, 0, "うーむ、角と尻尾を\n追加して…さらに槍を\n持たせた方が見栄えが\nよさそうだ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133901, 8909, 1, 0, 4, 0, "いい香りだな\n血と砂塵の臭いで\n上書きするまでの\n間に合わせに最適だ☆", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133901, 8910, 1, 0, 5, 0, "生温いな\nもっと焦がれるほどの\n熱量が欲しいところだ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133901, 8911, 1, 0, 6, 0, "普段はないものが\n生えているのだから、\n運動でしっかり感覚を\n掴んでおかなければな", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133901, 8912, 1, 0, 7, 0, "腹が減っては戦は…\nと言うからな\nしっかり食べて\nおくとしようか☆", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133901, 8913, 1, 0, 8, 0, "『家庭を築くための\n絶対条件』…？\nふむ…興味がある\nタイトルだ", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133801, 8915, 1, 0, 1, 0, "奇妙な感覚です\nまるであの頃の自分に\n変装しているかのよう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133801, 8916, 1, 0, 2, 0, "グレゴール？\nおっと、そうでした\n今は昔の姿になって\n離れていましたね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133801, 8917, 1, 0, 3, 0, "このアバターでいると\n妙に子どもたちからの\n視線を感じますね…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133801, 8918, 1, 0, 4, 0, "リソースの割り振りが\n変わっただけなのに\n随分と身体が軽く\n感じますね", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133801, 8919, 1, 0, 5, 0, "普段グレゴールに\n任せている分身の管理\n自分でやるには\nいささか煩雑ですね…", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 133801, 8920, 1, 0, 101, 0, "長老に頼まれて描いた\n妖精の絵…いったい\n何に使ったのでしょう", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 133801, 8921, 1, 0, 102, 0, "私自身をモデルにした\n完全なるNPCたち\nそれが私の分身です", 1);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133801, 8922, 1, 0, 1, 0, "このアバターが\n気になるのですか？\n貴方はつくづく\n妖精に縁がありますね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133801, 8923, 1, 0, 2, 0, "ちょうどよかった\nグレゴールを見かけ\nませんでしたか？", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133801, 8924, 1, 0, 3, 0, "あの翅は飾りでは\nありません。ただ…\nグレゴールに乗って\n飛ぶ方が楽なだけです", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133801, 8925, 1, 0, 4, 0, "妖精とは本来神話に\nおけるトリックスター\n運命をかき回すことが\n存在意義なのです", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 133801, 8926, 1, 0, 101, 0, "変貌大妃の権能との\n相性を考えなければ\nこの姿こそが私たちの\n最強の姿です", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 133801, 8927, 1, 0, 102, 0, "変化とは一種の喪失\nだからこそ得られる物\nがある…貴方のそれも\n大切にするのですよ", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133801, 8928, 1, 1, 1, 0, "マサキからの定時連絡\nが来ませんね…心配は\n不要ですが、様子を\n見に行きましょうか", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133801, 8929, 1, 2, 2, 0, "昼食の時間ですね\nグレゴールのために\nカロリーの高い物を\n食べに行きましょう", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133801, 8930, 1, 3, 3, 0, "夜更かしはダメですよ\n悪いスパイが暗躍して\nいる時間帯ですからね", 2);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133801, 8932, 1, 0, 2, 0, "ふむ、座り心地は中々\nですが…私にはやはり\n玉座の方があいますね", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133801, 8933, 1, 0, 3, 0, "ふふ、いいでしょう\n我が配下に加えます\n存分に働きなさい", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133801, 8934, 1, 0, 4, 0, "時折晶からしていた\nあの甘い匂いは何の\nアロマだったのか…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133801, 8935, 1, 0, 5, 0, "燃え盛る炎もこうして\n暖炉の中なら安全です\nクリスもあの衝動を\n管理さえできれば…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133801, 8937, 1, 0, 7, 0, "そういえば真那が\n獄中で手持無沙汰に\nひたすらおにぎりを\n食べていましたね…", 3);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 133801, 8938, 1, 0, 8, 0, "これは、妖精学の本…\n長老の蔵書から借りて\n読みふけったものです", 3);
