INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 100101, 1, 1, "ここに困ってる人は\nいないよね…？\nよかった♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 100101, 2, 1, "冒険の準備準備…\nうん、忘れ物はなし！\n…あっ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 100101, 3, 1, "リズムを意識して…\nわんつーわんつー！\nうん、調子はバッチリ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4, 100101, 4, 1, "ここで特訓していい？\n物は壊さないように\n気をつけるね！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5, 100101, 5, 1, "なんか落ち着かないや\nもしかして、\n困ってる人が\n近くにいるのかな…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6, 100101, 101, 4, "えへへ…いつも\n一緒にいてくれて\nありがとねっ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7, 100101, 102, 8, "ちゃんと覚えてるよ\n騎士くんの声…\nあの時はありがとね！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8, 100101, 1, 1, "あははっ！\nやったなー！\nお返しだー！\nこちょこちょこちょ…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (9, 100101, 2, 1, "どうしたの？\nもしかして困ってる？\nあたしにまかせて！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (10, 100101, 3, 1, "えっとね…しっぽは\n触っちゃダメだよ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (11, 100101, 4, 1, "反応しない特訓…\nだったけど無理～！\n悔しいよぉ～！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (12, 100101, 101, 4, "ふぁふぃ？\nやられた～！　ほっぺ\nぷにぷにしないでぇ～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (13, 100101, 102, 8, "えへへ…\n騎士くんの手だ…\nあったかいな…♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (14, 100101, 1, 1, "おはよー！\n今日も元気に\nいっちゃうよー！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (15, 100101, 2, 1, "ユイちゃんに\nおやつ作って\nもらおうかな～♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (16, 100101, 3, 1, "寝るのも修行だって\nこないだレイさんが\n言ってたんだ…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (17, 100101, 1, 1, "ちゃんとケアして\n傷が残らないように\nしないと…", 3, 0, 1, 0);
