INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (19, 100101, 3, 1, "新しい練習相手に…\nって冗談だよ冗談！\n本気じゃないから！", 3, 0, 1, 0);
