INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (60, 100201, 3, 1, "困ったことが\nあったら\nいつでも話してね", 2, 0, 1, 0);
