INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (72, 100201, 6, 1, "騎士クンと一緒に\n冒険するなら\n体力がないと…！", 3, 0, 1, 0);
