INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (91, 100202, 3, 1, "もうこんな時間…\nそろそろ寝なくちゃ\nおやすみなさい", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (92, 100202, 1, 1, "よし…\nこれなら\n褒めてもらえるかな？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (93, 100202, 2, 1, "がんばることも\n必要だけど\nお休みも大切だよね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (94, 100202, 3, 1, "かわいい～♪\n騎士クンは\nこういうのって好き？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (95, 100202, 4, 1, "戦う前にもこうやって\n落ち着けたら\nいいのに", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (96, 100202, 5, 1, "あの…！\nよ、良かったら\n一緒に…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (97, 100202, 6, 1, "騎士クンと一緒に\n冒険するなら\n体力がないと…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (98, 100202, 7, 1, "最近服のサイズが\n合わないんだけど…\n食べ過ぎなのかな？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (99, 100202, 8, 1, "…やっぱり\n月刊恋愛指南は\nためになる…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (101, 100301, 1, 1, "装備の手入れでも\nしようかな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (102, 100301, 2, 1, "次に鍛えるべきは\n肉体面か精神面か\nどちらにしよう…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (103, 100301, 3, 1, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (104, 100301, 4, 1, "仲間なんていらない\nそう思ってた頃も\nあったな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (105, 100301, 5, 1, "考えごとをしていると\nつい部屋中を\n歩いてしまうな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (106, 100301, 101, 4, "彼は\nどこまで強くなるのか\n私も負けられないな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (107, 100301, 102, 8, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (108, 100301, 1, 1, "ああ、キミだったのか\n人の気配がしたから\n誰かと思ったよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (109, 100301, 2, 1, "斬りかかるつもりなら\nちゃんとかかってきて\n…呼んだだけ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (110, 100301, 3, 1, "なに？　私に相談？\n戦いについてなら\n喜んで", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (111, 100301, 4, 1, "今のが突き？\n軽い！　もっと鋭く！\nさあ、もう一回！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (112, 100301, 101, 4, "ひゃっ！　ど、どこを\n触ってるの！？\n…油断した", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (113, 100301, 102, 8, "キミにはこれからも\n私のそばに…\nいや、なんでもない", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (114, 100301, 1, 1, "おはよう\n今日も充実した\n一日にしていこう", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (115, 100301, 2, 1, "そろそろ\nお茶の時間か\nキミも一緒にどう？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (116, 100301, 3, 1, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (117, 100301, 1, 1, "戦いを経てもなお\n美しくあるように…\nこれでよし♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (118, 100301, 2, 1, "戦ってばかりで\n休まないと、また\nユイに怒られるな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (119, 100301, 3, 1, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (120, 100301, 4, 1, "これはいい…\n戦いの前の精神統一に\nもってこいじゃないか", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (121, 100301, 5, 1, "時々は落ち着いた\n時間を過ごすのも\n悪くない…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (122, 100301, 6, 1, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (123, 100301, 7, 1, "食事は大切だけど\n食べ過ぎには\n注意しておこう…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (124, 100301, 8, 1, "ユイ…！\nこ、こんな本を\n読んでいたのか…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (126, 100302, 1, 1, "海だけじゃなくて\n川でも釣りがしたいな\n道具も新調しよう", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (127, 100302, 2, 1, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (128, 100302, 3, 1, "彼は\nどこまで強くなるのか\n私も負けられないな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (129, 100302, 4, 1, "そろそろ修行の\n内容を新しくしよう\nより厳しいものにね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (130, 100302, 5, 1, "強くなって…誰にも\n妨げられない自由な\n生き方がしたい", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (131, 100302, 101, 8, "まさか風邪を引くとは\n彼の看病のおかげで\nすっかり良く\nなったけど", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (132, 100302, 102, 8, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (133, 100302, 1, 1, "どうしたの？\nなんでもない？\nそれなら良かった", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (134, 100302, 2, 1, "あんまり突っつくと\nお返しするよ？\n覚悟は出来てる？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (135, 100302, 3, 1, "ねぇ、今度また\n釣りに行かない？\n大きいのを狙おう", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (136, 100302, 4, 1, "修行がしたいの？\nそれを早く言ってよ\nすぐに出発しよう！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (137, 100302, 101, 8, "ひょっとして\n今のも一本だなんて\n言わないよね？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (138, 100302, 102, 8, "キミにはこれからも\n私のそばに…\nいや、なんでもない", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (139, 100302, 1, 1, "今日は仕事？\nそれとも修行？\n良ければ付き合うよ", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (140, 100302, 2, 1, "休息も時には\n必要だと、仲間から\n教わったよ", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (141, 100302, 3, 1, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (142, 100302, 1, 1, "髪の長さを少し\n変えてみようかな…\nわ、私は何を…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (143, 100302, 2, 1, "休息があってこそ\n万全な状態で\n戦えるんだ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (144, 100302, 3, 1, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (145, 100302, 4, 1, "体と同様に\n心もほぐれるね…\nはぁ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (146, 100302, 5, 1, "休む時は休む\nヒヨリもそろそろ\n覚えて欲しいな…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (147, 100302, 6, 1, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (148, 100302, 7, 1, "修行のせいか\n最近食べる量が\n増えてきたような…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (149, 100302, 8, 1, "ユイ…！\nこ、こんな本を\n読んでいたのか…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (151, 100401, 1, 1, "お宝の予感！\n部屋中を\n探検しちゃうぞ～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (152, 100401, 2, 1, "落とし穴…どこに\n作ったんだっけ？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (153, 100401, 3, 1, "うえ～ん！\n…嘘泣きしたら\n相手は油断するかな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (154, 100401, 4, 1, "ハニートラップって\nすごく甘いお菓子で\nイタズラするのかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (155, 100401, 5, 1, "かくれんぼする人\nこの指、と～まれ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (156, 100401, 101, 4, "ミソギ以外の人に\nイタズラされちゃ\nダメだよ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (157, 100401, 102, 8, "ミソギと遊ぶ？\nミソギに遊ばれる？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (158, 100401, 1, 1, "いいこと思いついた！\nい、イタズラとかじゃ\nないよ～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (159, 100401, 2, 1, "うわっ！？\n　にいちゃんが、\nミソギにイタズラ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (160, 100401, 3, 1, "にいちゃんの\n通りそうなところに\nバナナの皮を…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (161, 100401, 4, 1, "にいちゃんのオデコと\nホッペと鼻と目と耳に\n落書きを…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (162, 100401, 101, 4, "ここ、ミソギ探検隊の\n基地にしようよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (163, 100401, 102, 8, "だ～れだ…って？\nにいちゃんの声くらい\nわかるよ！\n大好きだもん♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (164, 100401, 1, 1, "にいちゃん、おはよ！\n目覚まし時計を\n止めておいたのに、\nよく起きられたね～♪", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (165, 100401, 2, 1, "にいちゃんが\n昼寝したら、\nイタズラし放題♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (166, 100401, 3, 1, "夜の探検も\nおもしろそう！\nねぇ出かけちゃダメ？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (167, 100401, 1, 1, "イタズラには\nお色気も必要…\nうん、完璧！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (168, 100401, 2, 1, "座ったら、ブーッって\nいう音が出たら、\nおもしろいな～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (169, 100401, 3, 1, "かわいい！\nミソギ探検隊の\nマスコットにしよう♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (170, 100401, 4, 1, "吸うとくしゃみが\n止まらなくなるお香を\nここに…くしゅっ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (171, 100401, 5, 1, "にひひ…イタズラの\n証拠、ここで\n燃やしちゃおう～！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (172, 100401, 6, 1, "探検家は\n体力勝負だよー！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (173, 100401, 7, 1, "みんなに分けて\nあげようかな～\n激辛ソースも\n入れちゃうけどね！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (174, 100401, 8, 1, "こういう所に\n宝の地図が\n隠れてるんだよね～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (176, 100402, 1, 1, "お宝の予感！\n部屋中を\n探検しちゃうぞ～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (177, 100402, 2, 1, "うぅ、探検に出たい！\nじっとしてなんか\nいられないよ～！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (178, 100402, 3, 1, "うえ～ん！\n…嘘泣きしたら\n相手は油断するかな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (179, 100402, 4, 1, "ハニートラップって\nすごく甘いお菓子で\nイタズラするのかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (180, 100402, 5, 1, "かくれんぼする人\nこの指、と～まれ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (181, 100402, 101, 8, "にいちゃんのオデコと\nホッペと鼻と目と耳に\n落書きを…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (182, 100402, 102, 8, "にいちゃんが喜ぶ\nイタズラ、何かない\nかな～？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (183, 100402, 1, 1, "ミソギがしかけた\nイタズラ\n見つけられるかな？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (184, 100402, 2, 1, "探検する？\n冒険する？\nそれとも…\nイタズラされる？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (185, 100402, 3, 1, "将来の夢は探検家！\n探したい物はないけど", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (186, 100402, 4, 1, "にいちゃんに宿題！\n落とし穴を10個\n掘ること！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (187, 100402, 101, 8, "だ～れだ…って？\nにいちゃんの声くらい\nわかるよ！\n大好きだもん♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (188, 100402, 102, 8, "ここ、ミソギ探検隊の\n基地にしようよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (189, 100402, 1, 1, "にいちゃん\n探検に行こうよ！\n今からなら遭難しても\n夜には帰れるよ！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (190, 100402, 2, 1, "にいちゃんが昼寝したら\nイタズラし放題♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (191, 100402, 3, 1, "夜の探検も\nおもしろそう！\nねぇ出かけちゃダメ？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (192, 100402, 1, 1, "イタズラには\nお色気も必要…\nうん、完璧！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (193, 100402, 2, 1, "ゆっくりまったりして\n次のイタズラを\n考えよ～！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (194, 100402, 3, 1, "かわいい！\nミソギ探検隊の\nマスコットにしよう♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (195, 100402, 4, 1, "吸うとくしゃみが\n止まらなくなるお香を\nここに…くしゅっ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (196, 100402, 5, 1, "にひひ…イタズラの\n証拠、ここで\n燃やしちゃおう～！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (197, 100402, 6, 1, "探検家は\n体力勝負だよー！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (198, 100402, 7, 1, "探検のおやつに\nぴったりかも～！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (199, 100402, 8, 1, "こういう所に\n宝の地図が\n隠れてるんだよね～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (201, 100601, 1, 1, "音楽は全部好きだけど\n一番好きなのは\nやっぱりジャズかな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (202, 100601, 2, 1, "はぁ、はぁ…\n一人でもいっぱい\nしないと…\n特訓…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (203, 100601, 3, 1, "お姉ちゃんには\nもっと自信を\n持ってほしいなぁ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (204, 100601, 4, 1, "お姉ちゃんのマネ\nしちゃおうかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (205, 100601, 5, 1, "双子って、\n好みのタイプも\n似ちゃうのかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (206, 100601, 101, 4, "お兄ちゃんと私は\n相性抜群です♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (207, 100601, 102, 8, "もう待ちきれない…\nお兄ちゃん、\n早く帰って来て！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (208, 100601, 1, 1, "あんっ…着替えよう\nと思ったのに\nお兄ちゃんのエッチ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (209, 100601, 2, 1, "うふふ、お兄ちゃん！\n今日は何を\nするんですか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (210, 100601, 3, 1, "そんなところ\n触っちゃダメ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (211, 100601, 4, 1, "お兄ちゃん、イジワル\nしないでください…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (212, 100601, 101, 4, "お兄ちゃん、もっと\n上級編のテクニック、\n教えてくださいね！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (213, 100601, 102, 8, "コーチ、\n今日も特訓\nお願いします！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (214, 100601, 1, 1, "おはようございます\n昨夜は楽しかった\nですね、うふふ♪", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (215, 100601, 2, 1, "こんな明るい時間から\nす、するんですか…\n二人っきりの特訓…", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (216, 100601, 3, 1, "夜はこれからですよ\nお兄ちゃん♪", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (217, 100601, 1, 1, "世界一、かわいく\nなるんだから♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (218, 100601, 2, 1, "気持ちいい♪\nもう立てないよ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (219, 100601, 3, 1, "かわいい♪\nアカリのライバル\nだね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (220, 100601, 4, 1, "はぁ…気持ちいい、\n私、このままダメに\nなっちゃいそう…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (221, 100601, 5, 1, "はぁ…アカリ、\nとろけちゃう…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (222, 100601, 6, 1, "はぁ…はぁはぁ、\n私…もう…ダメ\n限界です…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (223, 100601, 7, 1, "わぁ、おいしい～\nけど食べ過ぎは美容に\nよくないですよね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (224, 100601, 8, 1, "この楽譜、\nサックスで吹いて\nみたいなぁ♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (226, 100602, 1, 1, "このごろ一人でいると\nお兄ちゃんのこと\nばかり考えちゃう…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (227, 100602, 2, 1, "はぁ、はぁ…もっと\nいっぱいしなくちゃ\n…練習", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (228, 100602, 3, 1, "このお部屋、なんだか\n落ち着きます…\nお兄ちゃんが\nいるから？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (229, 100602, 4, 1, "みんなかわいいな～\n世界一かわいくなる\nのは、難しいな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (230, 100602, 5, 1, "双子って、\n好みのタイプも\n似ちゃうのかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (231, 100602, 101, 8, "いつか世界でいちばん\nかわいくなって\nお兄ちゃんと…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (232, 100602, 102, 8, "もう待ちきれない…\nお兄ちゃん、\n早く帰って来て！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (233, 100602, 1, 1, "あんっ…着替えよう\nと思ったのに\nお兄ちゃんのエッチ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (234, 100602, 2, 1, "二人きりの秘密の特訓\n今度はいつですか？\n待ち遠しいです…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (235, 100602, 3, 1, "え？　ここでするの？\nみんなの前で恥ずか\nしいな…筋トレ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (236, 100602, 4, 1, "お兄ちゃん、イジワル\nしないでください…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (237, 100602, 101, 8, "お兄ちゃんといると\nわ、私…ドキドキ\nしてきちゃう…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (238, 100602, 102, 8, "コーチ、\n今日も特訓\nお願いします！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (239, 100602, 1, 1, "おはようございます\n昨夜は楽しかった\nですね、うふふ♪", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (240, 100602, 2, 1, "お兄ちゃんに\n食べてもらいたいなぁ\n手作りのお昼ごはん", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (241, 100602, 3, 1, "お風呂入ってきますね\nそのあとは…\n二人でい～っぱい\n遊びましょうね", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (242, 100602, 1, 1, "お姉ちゃんのマネ\nしちゃおうかな？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (243, 100602, 2, 1, "気持ちいい♪\nもう立てないよ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (244, 100602, 3, 1, "かわいい♪\nアカリのライバル\nだね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (245, 100602, 4, 1, "はぁ…気持ちいい、\n私、このままダメに\nなっちゃいそう…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (246, 100602, 5, 1, "あったか～い…\nまるでお兄ちゃんが\n隣にいる時みたい", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (247, 100602, 6, 1, "汗をかくと\n気持ちいいですよね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (248, 100602, 7, 1, "お兄ちゃんの特訓に\n付き合うために\nたっぷり\n食べておかないと♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (249, 100602, 8, 1, "この楽譜\nサックスで吹いて\nみたいなぁ♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (251, 100701, 1, 1, "プリン、プリン、\nプリン、プリン、\nプリン、プリン～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (252, 100701, 2, 1, "う～ら～め～し～や～\nなの～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (253, 100701, 3, 1, "プリンがないなら\n死んだほうがマシなの\nあ、もう死んでたの～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (254, 100701, 4, 1, "幽霊の正体見たり、\nミヤコなの～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (255, 100701, 5, 1, "ミヤコがいきなり\n消えたら、みんな\n驚くかもなの～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (256, 100701, 101, 4, "アイツがいて、\nプリンもある。\nきっとここが天国なの", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (257, 100701, 102, 8, "アイツはプリンセス\nナイトじゃなくて\nプリンナイトなの～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (258, 100701, 1, 1, "姿を消して、オマエを\n見張ってやるの～\nむふふ～♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (259, 100701, 2, 1, "もし成仏した先に\nプリンがなかったら\n地獄なの～…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (260, 100701, 3, 1, "右にプリン左にプリン\n後ろにもプリン♪\n前にはオマエなの～！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (261, 100701, 4, 1, "死んだ人には\nお供え物をするの～\nミヤコにも、プリンを\n毎日お供えするの～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (262, 100701, 101, 4, "たくさんオマエと\n遊べて、\n成仏しそうなくらい\nうれしいの～！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (263, 100701, 102, 8, "オマエのこと\n気に入ったからずっと\nとり憑いてやるの～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (264, 100701, 1, 1, "う～　眠いの…\n二度目の二度寝を\nするの…", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (265, 100701, 2, 1, "お昼ご飯はプリンなの\n３時のおやつも、\nプリンなの～", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (266, 100701, 3, 1, "ディナーは豪華に、\nプリンのフルコース\nなの～♪", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (267, 100701, 1, 1, "体が透けちゃうから\nうまく使えないの～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (268, 100701, 2, 1, "まったりなの～\nあとはプリンがあれば\n完璧なの～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (269, 100701, 3, 1, "む～…かわいいけど\nプリンは分けて\nあげないの～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (270, 100701, 4, 1, "は～　いい気分なの…\n成仏しそうなの…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (271, 100701, 5, 1, "も、もしかして\nミヤコ、ここで\n火葬されちゃうの？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (272, 100701, 6, 1, "幽霊には、体力も\n筋力も関係ないの～！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (273, 100701, 7, 1, "ぱくぱくモグモグ…\nミヤコ、幽霊だけど、\nお腹はすくの～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (274, 100701, 8, 1, "プリンの写真集が\nあればいいのに\nなの～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (276, 100702, 1, 1, "うらめしや～…\n裏の飯より\nプリンがいいの～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (277, 100702, 2, 1, "プリン♪　プリン♪\nプリン♪　なの～♪　", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (278, 100702, 3, 1, "世界中のケーキ屋が\nプリンのおいしさを\n競い合えばいいの～！\n審査員はミヤコなの～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (279, 100702, 4, 1, "買ってきたプリンには\n全部ミヤコの名前を\n書いておくの～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (280, 100702, 5, 1, "プリンがないなら\n死んだほうがマシなの\nあ、もう死んでたの～ ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (281, 100702, 101, 8, "アイツがいて\nプリンもある\nきっとここが天国なの", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (282, 100702, 102, 8, "アイツはプリンセス\nナイトじゃなくて\nプリンナイトなの～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (283, 100702, 1, 1, "右にプリン左にプリン\n後ろにもプリン♪\n前にはオマエなの～！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (284, 100702, 2, 1, "もし成仏した先に\nプリンがなかったら\n地獄なの～…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (285, 100702, 3, 1, "今日はオマエと\nプリンを買いに行くの\n強制なの～　", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (286, 100702, 4, 1, "死んだ人には\nお供え物をするの～\nミヤコにも、プリンを\n毎日お供えするの～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (287, 100702, 101, 8, "たくさんオマエと\n遊べて\n成仏しそうなくらい\nうれしいの～！ ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (288, 100702, 102, 8, "ミヤコのプリン\nオマエにだったら\n分けてあげるの～ ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (289, 100702, 1, 1, "う～　眠いの…\n二度目の二度寝を\nするの… ", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (290, 100702, 2, 1, "う～ら～め～し～や～\n…明るい時間だと\n驚かせないの～ ", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (291, 100702, 3, 1, "ひゅ～どろどろ～ん☆\n真っ暗なうちに\nいたずらしまくるの～♪", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (292, 100702, 1, 1, "体が透けちゃうから\nうまく使えないの～ ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (293, 100702, 2, 1, "落ち着くの～\nいまプリン食べたら\nきっといつもより\nおいしいの～♪ ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (294, 100702, 3, 1, "む～…かわいいけど\nプリンは分けて\nあげないの～　", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (295, 100702, 4, 1, "は～　いい気分なの…\n成仏しそうなの…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (296, 100702, 5, 1, "も、もしかして\nミヤコ、ここで\n火葬されちゃうの？ ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (297, 100702, 6, 1, "幽霊には、体力も\n筋力も関係ないの～！　", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (298, 100702, 7, 1, "おいしいの～\nプリン味だったら\nもっとおいしいの～ ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (299, 100702, 8, 1, "プリンの写真集が\nあればいいのに\nなの～ ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (301, 100801, 1, 1, "もし太陽が滅んでも\nボクの美しさで大地を\n照らしてあげる！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (302, 100801, 2, 1, "う～ん、この部屋には\nボクの肖像画や\n彫像が必要だね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (303, 100801, 3, 1, "美の女神と愛の女神が\nボクを作る時、本気\n出しちゃったんだね♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (304, 100801, 4, 1, "ボクの誕生とともに\n全ての美しさは過去の\n遺物になったんだよ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (305, 100801, 5, 1, "うわっ昨日より可愛く\nなってる。明日は\nどうなっちゃう\nのかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (306, 100801, 101, 4, "このボクがいるだけで\nあの人への\nおもてなしだよね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (307, 100801, 102, 8, "ボクの可愛さは、\nふふ…あの人の\nためのものさ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (308, 100801, 1, 1, "ねぇ～、ボクって\n可愛い？\nふふ、知ってる☆", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (309, 100801, 2, 1, "呼んだ？\n可愛いボクと話したい\n気持ちはわかるよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (310, 100801, 3, 1, "ふふ、ボクに触ると\nキミも美しく\nなっちゃうかもよ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (311, 100801, 4, 1, "どうかな、\n今日のボク。\n美しい？　可愛い？\nねえ、どっち？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (312, 100801, 101, 4, "あの人も今、\n可愛いボクのこと\n考えてるかな…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (313, 100801, 102, 8, "美しい…\nキミの瞳に写るボクは\nより一層きれいだ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (314, 100801, 1, 1, "美しいボクの\n美しい一日が\n始まるよ！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (315, 100801, 2, 1, "さーて、午後からも\n可愛く美しく\n華麗に優雅に♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (316, 100801, 3, 1, "バラの花びら\nいっぱい浮かべた\nお風呂に入ってから\n寝ようっと♪", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (317, 100801, 1, 1, "鏡よ、鏡。この世で\n一番可愛いボクに\nお似合いの人はだれ？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (318, 100801, 2, 1, "ボクが座ったソファは\n「美しいソファ」に\nランクアップした！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (319, 100801, 3, 1, "わあ、可愛い！\n…まあ、ボクほど\nじゃないけどさ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (320, 100801, 4, 1, "服を羽織るように\n香りをまとうボク…\n美しい、美しすぎる！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (321, 100801, 5, 1, "気安く触ったら\n火傷する…\n炎はボクに似ているね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (322, 100801, 6, 1, "運動は苦手なのに\n運動するボクの姿は\n可愛いなんて…罪♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (323, 100801, 7, 1, "ボクが食べると、\n食べ物まで\n可愛いなあ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (324, 100801, 8, 1, "ボクが触った瞬間に、\nこの本は美を\n携えてしまうんだ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (326, 100802, 1, 1, "ボクの美しさは\nみんなのために\nあるんだよ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (327, 100802, 2, 1, "う～ん、この部屋には\nボクの肖像画や\n彫像が必要だね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (328, 100802, 3, 1, "これ以上美しくなると\nボクをめぐって\n大陸中で戦争が\n始まっちゃう！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (329, 100802, 4, 1, "ボクの誕生とともに\n全ての美しさは過去の\n遺物になったんだよ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (330, 100802, 5, 1, "ボクの可愛さについて\nみんな話し合わない\nのかな…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (331, 100802, 101, 8, "このボクがいるだけで\nあの人への\nおもてなしだよね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (332, 100802, 102, 8, "あの人も今\n可愛いボクのこと\n考えてるかな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (333, 100802, 1, 1, "美の女神と愛の女神が\nボクを作る時、本気\n出しちゃったんだね♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (334, 100802, 2, 1, "ボクの美しさに嫉妬\nしたって意味ないよね\n次元が違うんだから", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (335, 100802, 3, 1, "ボクの化粧品使う？\n一緒に可愛くなろうよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (336, 100802, 4, 1, "ボクがいるだけで\nこの部屋は美の神殿♪\nランドソルの新たな\n観光名所になるね！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (337, 100802, 101, 8, "呼んだ？\n可愛いボクと話したい\n気持ちはわかるよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (338, 100802, 102, 8, "美しい…\nキミの瞳に写るボクは\nより一層綺麗だ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (339, 100802, 1, 1, "朝日と共に\nボクの美しさも\n周りを照らすよ", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (340, 100802, 2, 1, "ボクの美しさを\n維持するために\nお昼ご飯食べなくちゃ", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (341, 100802, 3, 1, "バラの花びらいっぱい\n浮かべたお風呂に\n入ってから\n寝ようっと♪", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (342, 100802, 1, 1, "鏡よ、鏡。この世で\n一番可愛いボクに\nお似合いの人はだれ？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (343, 100802, 2, 1, "ボクが座ったソファは\n「美しいソファ」に\nランクアップした！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (344, 100802, 3, 1, "可愛いものを\n可愛いって言える\nボクって可愛いよね！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (345, 100802, 4, 1, "服を羽織るように\n香りをまとうボク…\n美しい、美しすぎる！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (346, 100802, 5, 1, "冷えは美容の大敵\nなんだから！\nここ、ボクの場所ね！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (347, 100802, 6, 1, "運動は苦手なのに\n運動するボクの姿は\n可愛いなんて…罪♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (348, 100802, 7, 1, "ボクが食べると\n食べ物まで\n可愛いなあ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (349, 100802, 8, 1, "ボクが触った瞬間に\nこの本は美を\n携えてしまうんだ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (351, 100901, 1, 1, "「第１０部２０章\n終焉戦争、開幕」\n…これでよし！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (352, 100901, 2, 1, "今度シグルドに\n私の魔法を一つ\n伝授してやるか", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (353, 100901, 3, 1, "ここでは風が\n吹いてこないな…\n換気したい！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (354, 100901, 4, 1, "そうだ、ナナカに\nこの間借りた本を\n返さないと…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (355, 100901, 5, 1, "まずは包帯をとって\n手から龍の形の\n炎を出して…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (356, 100901, 101, 4, "シグルド…\n今頃何処で何を\nしているのだろう…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (357, 100901, 102, 8, "最近、彼の姿が\nまぶしく見える…\nどうして…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (358, 100901, 1, 1, "辞書を知らないか？\n…探している\n理由は聞くな", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (359, 100901, 2, 1, "そうだ！\n貴様の技にも\n名前をつけてやろう！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (360, 100901, 3, 1, "私に触れても力を\n奪われないのは\n貴様くらいだぞ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (361, 100901, 4, 1, "何事だ？\nまさか我が瞳に\n魅入られたのか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (362, 100901, 101, 4, "貴様がそばにいると\nとても安心できる…\n…不思議だ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (363, 100901, 102, 8, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (364, 100901, 1, 1, "「覚醒」めたか…\n行くぞ、悪を討つ\n戦いに出発だ！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (365, 100901, 2, 1, "昼間は「ヤツら」も\n襲ってはこない…\n束の間の休息だ", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (366, 100901, 3, 1, "どうした？　夜はまだ\nこれから…ふぁ…\nね、眠くなどないぞ！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (367, 100901, 1, 1, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (368, 100901, 2, 1, "くっ…！\n私を眠りの国へ\n誘うつもりか！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (369, 100901, 3, 1, "このようなものに\n懐柔されるものか！\n…されるものか！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (370, 100901, 4, 1, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (371, 100901, 5, 1, "これが我らの求めた\n安寧か…\n悪くない…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (372, 100901, 6, 1, "この程度\n前世での激闘に\n比べれば…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (373, 100901, 7, 1, "最近金欠でな…\nいやぁありがたい…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (374, 100901, 8, 1, "ここにも\n前世の記憶が\n眠っていたとは…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (376, 100902, 1, 1, "「第１３部１章\n閃光のシグルド」\n…ついに登場だな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (377, 100902, 2, 1, "何故だ…何故\nこうもあっさり金欠に\n逆戻りするのだ！？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (378, 100902, 3, 1, "やはり転生者という\n設定はカッコイイ…\n天才だな、私！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (379, 100902, 4, 1, "そうだ、ナナカに\nこの間借りた本を\n返さないと…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (380, 100902, 5, 1, "そろそろ今日の分の\n魔力を溜めないと\nまた魔力切れが…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (381, 100902, 101, 1, "最近奴がそばに\nいるだけで、鼓動が\n早くなるのは何故だ？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (382, 100902, 102, 8, "シ、シグルド…！\nだめだ…妄想でも\n上手く言えない…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (383, 100902, 1, 8, "なんだ？　新たな\n賞金の情報を\n持ってきたのか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (384, 100902, 2, 1, "名乗りやポーズは\nその強度で相手を\n圧倒する技術だ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (385, 100902, 3, 1, "いいか、魔法の\n詠唱中は絶対に\n声をかけるなよ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (386, 100902, 4, 1, "おお、ちょうど今\n貴様専用の設定集を\n作成中だぞ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (387, 100902, 101, 1, "貴様がそばにいると\nとても安心できる…\n…不思議だ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (388, 100902, 102, 8, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (389, 100902, 1, 8, "夜更かしのせいか\nまだ眠気が…いや！\nこれは組織の罠だ！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (390, 100902, 2, 1, "平穏な時に\n出来るだけ魔力を\n溜めておかねば…", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (391, 100902, 3, 1, "夜になると、力が\n増した気分になるな\n…なりませんか？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (392, 100902, 1, 1, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (393, 100902, 2, 1, "お、おのれ\nヒュプノス…\n私は負けないぞ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (394, 100902, 3, 1, "くっくっくっ…\n貴様は特別団員に\nしてやろう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (395, 100902, 4, 1, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (396, 100902, 5, 1, "これが我らの求めた\n安寧か…\n悪くない…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (397, 100902, 6, 1, "新たな戦いに備えて\n魔力だけでなく\n体力も鍛えねば！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (398, 100902, 7, 1, "食事は魔力に\n変換する体力の\n元になるのだ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (399, 100902, 8, 1, "フフフ…\nこれを覚えれば\nもっと強く…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (401, 101001, 1, 1, "王子はんほんまに\nはんなりしてて、\n王子はんって感じやわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (402, 101001, 2, 1, "この絵本の王子はん、\n王子はんみたいで\nええなぁ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (403, 101001, 3, 1, "うちがガラスの靴を\n落としたら、王子はん\n見つけてくれるやろか", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (404, 101001, 4, 1, "マホマホ王国\n次期国王の王子はん\n…見てみたいわ～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (405, 101001, 5, 1, "王子はんのために\n糸を紡いで、\n編み物でもしまひょか", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (406, 101001, 101, 4, "マホ姫と王子はん、\n絵本の世界より\nベストカップルや", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (407, 101001, 102, 8, "王子はんと\n一緒にいると\n胸がせわしのー\nうごいてかなん", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (408, 101001, 1, 1, "うちら並んでたら\nお雛様みたいに\n見えると思いまへん？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (409, 101001, 2, 1, "王子はんを\n国に連れて帰ったら\nみんな大歓迎して\nくれるやろなぁ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (410, 101001, 3, 1, "王子はんは、\n絵本から出てきた人\nみたいやね～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (411, 101001, 4, 1, "うちの話し方\nのんびりしてます\nやろか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (412, 101001, 101, 4, "マホ姫の手は\nつべたいから、\n王子はんが温めて？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (413, 101001, 102, 8, "うちがもし悪い魔法に\nかかったら、王子はん\n口づけしてくれはる？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (414, 101001, 1, 1, "朝は小鳥はんも\nおなかがすいてるから\nよう飛び回りはるねえ", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (415, 101001, 2, 1, "お茶請けはお大福が\nよろしおすけど\nクッキーもええなあ", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (416, 101001, 3, 1, "うち、普段から\n夢見がちやけど…\nほんに眠い時間やわぁ", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (417, 101001, 1, 1, "王子はんに\n会いに行くなら、\nおめかしせな\nあかんなぁ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (418, 101001, 2, 1, "はんなりした\nええお椅子やわぁ\nほっこりしますなあ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (419, 101001, 3, 1, "かいらしもんが\nいっぱいで、\nマホマホ王国みたい\nやわぁ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (420, 101001, 4, 1, "心地ええどす。まるで\nお花畑にいるようやね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (421, 101001, 5, 1, "ぽかぽかどす。\n心まであったかく\nなりますわぁ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (422, 101001, 6, 1, "はぁはぁ、お姫はんは\n激しい運動は\nしぃひんもんどす～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (423, 101001, 7, 1, "もぐもぐ…あんまり\n速う食べられへんけど\n美味しいわぁ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (424, 101001, 8, 1, "絵本、絵本～\n幸せやわぁ♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (426, 101002, 1, 1, "王子はんにとって\n自慢の姫になるえ～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (427, 101002, 2, 1, "カボチャの馬車に\nガラスの靴…\n欲しいなぁ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (428, 101002, 3, 1, "うちがガラスの靴を\n落としたら、王子はん\n見つけてくれるやろか", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (429, 101002, 4, 1, "マホマホ王国\n次期国王の王子はん\n…見てみたいわ～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (430, 101002, 5, 1, "ほんまなぁ\nこの絵本が面白いんよ\n王子はんに\n読んであげよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (431, 101002, 101, 8, "マホ姫と王子はん\n絵本の世界より\nベストカップルや", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (432, 101002, 102, 8, "王子はんと\n一緒にいると\n胸がせわしのー\nうごいてかなん", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (433, 101002, 1, 1, "たまには、王子はんの\n好きな絵本も\n知りたいな～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (434, 101002, 2, 1, "王子はんを\n国に連れて帰ったら\nみんな大歓迎して\nくれるやろなぁ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (435, 101002, 3, 1, "そのうち、王子はんの\nお国にも連れてって\nもらえますやろか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (436, 101002, 4, 1, "絵本読むんも\nええなぁ…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (437, 101002, 101, 8, "うちがもし悪い魔法に\nかかったら、王子はん\n口づけしてくれはる？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (438, 101002, 102, 8, "うちら並んでたら\nお雛様みたいに\n見えると思いまへん？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (439, 101002, 1, 1, "朝は小鳥はんも\nおなかがすいてるから\nよう飛び回りはるねえ", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (440, 101002, 2, 1, "マホマホ王国では\nこの時分は\nみんなお昼寝\nしてたんよ", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (441, 101002, 3, 1, "夜に飛んだり鳴いたり\nする鳥はんもおるんよ\n昼は寝てはるんかねぇ", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (442, 101002, 1, 1, "王子はんに\n会いに行くなら\nおめかしせな\nあかんなぁ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (443, 101002, 2, 1, "王子はんも一緒に\nゆっくりしていっても\nええんよ？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (444, 101002, 3, 1, "かいらしい子やねぇ\nうちとお友だちに\nなってくれるん？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (445, 101002, 4, 1, "癒しアイテムって\nほんま、好っきゃわぁ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (446, 101002, 5, 1, "うちのお城にも\nほしいわぁ。帰ったら\nさっそく準備せな\nあかんなぁ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (447, 101002, 6, 1, "部屋の中で運動\nできるのも、なかなか\n面白いもんやわぁ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (448, 101002, 7, 1, "おいひいわ～\nマホマホ王国には\nない味やわぁ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (449, 101002, 8, 1, "しっかり知識を\n身につけんと\n魔法も使えへん\nからなぁ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (451, 101101, 1, 1, "１、２、３、４…\nあれ？　いま、\n何段目まで\n編んだっけ？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (452, 101101, 2, 1, "おやつのケーキは\n苺かチョコ…\nどっちも食べよう！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (453, 101101, 3, 1, "い、いけない！\n縫い針落としちゃった", 1, 0, 1, 0);
