INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (636, 101702, 4, 1, "ハイターイ！　元気な\nあいさつが一番さ～！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (637, 101702, 101, 8, "キミと踊ってる時が\n一番幸せさ～\nキミもそう思ってたら\nうれしいさ～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (638, 101702, 102, 8, "海に沈む夕日は\nとっても綺麗さ～！\n今度一緒に見ようね～ ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (639, 101702, 1, 1, "気持ちのいい朝さー！\nひとっ走り\nしてくるさー！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (640, 101702, 2, 1, "午後も頑張るために\nお昼ご飯は\nてびちにするさー！ ", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (641, 101702, 3, 1, "故郷では見えない星が\n見られるんだねえ\nなんか不思議さぁ ", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (642, 101702, 1, 1, "夢中で踊ってたら\n髪の毛ぼさぼさに\nなってるさ～！？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (643, 101702, 2, 1, "よく食べてよく踊って\nよく休む\nそれが元気の秘訣さ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (644, 101702, 3, 1, "ちゅらかーぎーねー！\nキミはどこから\n来たのー？ ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (645, 101702, 4, 1, "いい香り～、って\n私もカオリだったさ！\nなんだか照れるね☆ ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (646, 101702, 5, 1, "故郷はあんまり暖房を\n使わなかったから\n新鮮さー！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (647, 101702, 6, 1, "徹夜で踊り続ける\nためにも、まずは\n体を鍛えるさ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (648, 101702, 7, 1, "もぐもぐ…もっと\nおかわりがほしいさー", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (649, 101702, 8, 1, "おおー、故郷の民話！\nこんなとこで見るとは\n懐かしいさぁ～！ ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (651, 101801, 1, 1, "はあ…\nすてきな小説だった～\nキュンキュンしちゃう", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (652, 101801, 2, 1, "このお部屋\n女の子ばかりね。\n魔族の女子校を\n思い出しちゃう♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (653, 101801, 3, 1, "次のテスト\n恋愛小説から\n出題したら\nダメかしら…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (654, 101801, 4, 1, "キンコンカンコ～ン♪\n授業をはじめま～す", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (655, 101801, 5, 1, "はー…\nちょっと肩が\nこっちゃった…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (656, 101801, 101, 4, "私、あの子のこと\n無意識に特別扱い\nしちゃってるかな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (657, 101801, 102, 8, "物語だと生徒が先生を\n好きになるのにな…\n逆の場合は…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (658, 101801, 1, 1, "あん！　見ちゃダメよ\n明日のテストの問題を\n作ってるとこなの！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (659, 101801, 2, 1, "勉強勉強で\n疲れたよね？\n膝枕してあげる！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (660, 101801, 3, 1, "テストがんばったね～\nご褒美に先生が\n特別な授業\nしてあげるね？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (661, 101801, 4, 1, "学院の外でも\n私は君の先生よ\nなんでも聞いてね♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (662, 101801, 101, 4, "今読んでるのは\nヒロインが年下の子と\n恋に落ちる話…あ、\n深い意味はないのよ～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (663, 101801, 102, 8, "生徒は全員可愛いけど\n君は少し特別…\nみんなには内緒よ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (664, 101801, 1, 1, "学校に\n遅刻しないように\n早く準備しないとね", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (665, 101801, 2, 1, "男の子といっしょに\nお弁当を食べるの、\n夢なんだ～", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (666, 101801, 3, 1, "明日の予習はやった？\n先生も手伝うから\n寝る前にやりましょ♪", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (667, 101801, 1, 1, "もしかして…\n少し露出多いかしら？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (668, 101801, 2, 1, "リラックスすると\nα波が出て、勉強が\nはかどるんですって", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (669, 101801, 3, 1, "かわいい♪　あなたも\n私の生徒になる？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (670, 101801, 4, 1, "学院にもこういう\n安らぐ設備が\n必要かしらね？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (671, 101801, 5, 1, "まだ寒いわぁ\n誰か隣に\n来てくれたら…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (672, 101801, 6, 1, "運動部の顧問も\nできるかしら？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (673, 101801, 7, 1, "学校の給食でも\n食べたいわぁ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (674, 101801, 8, 1, "いい参考書を\n見つけちゃった♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (676, 101802, 1, 1, "私の生徒に\n不良なんていないわ\nみーんないい子♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (677, 101802, 2, 1, "このお部屋\n女の子ばかりね\n魔族の女子校を\n思い出しちゃう♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (678, 101802, 3, 1, "あの子、まだかな…\n宿題も見てあげたいし\n早く来ないかしら？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (679, 101802, 4, 1, "はあ…\nすてきな小説だった～\nキュンキュンしちゃう", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (680, 101802, 5, 1, "学校の図書室に\n恋愛小説を\n置いて欲しいなあ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (681, 101802, 101, 8, "私、あの子のこと\n無意識に特別扱い\nしちゃってるかな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (682, 101802, 102, 8, "物語だと生徒が先生を\n好きになるのにな…\n逆の場合は…　", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (683, 101802, 1, 1, "あん！　見ちゃダメよ\n明日のテストの問題を\n作ってるとこなの！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (684, 101802, 2, 1, "勉強勉強で\n疲れたよね？\n膝枕してあげる！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (685, 101802, 3, 1, "宿題はもうやった？\n苦手なところは\n先生が見てあげる♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (686, 101802, 4, 1, "んん？　なんだか\n元気がないみたい\n先生がぎゅぅ～って\nしてあげる…　", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (687, 101802, 101, 8, "君のお勉強も\nだいぶ進んできたね～\n補習卒業も近いかな？\n少し寂しいけど…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (688, 101802, 102, 8, "テストがんばったね～\nご褒美に先生が\n特別な授業\nしてあげるね？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (689, 101802, 1, 1, "朝の特別授業\nしてあげましょうか？", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (690, 101802, 2, 1, "午後の授業って\n眠いでしょう？\n気持ちは分かるけど\nがんばってね♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (691, 101802, 3, 1, "明日の予習はやった？\n先生も手伝うから\n寝る前にやりましょ♪", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (692, 101802, 1, 1, "もしかして…\n少し露出多いかしら？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (693, 101802, 2, 1, "私の膝枕と、どっちが\nフカフカかしら？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (694, 101802, 3, 1, "可愛いわね～\n教室に置いたら\n生徒が喜ぶかしら？　", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (695, 101802, 4, 1, "は～落ち着くなぁ～\n教師には\nこういうゆとりも\n必要よね♪　", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (696, 101802, 5, 1, "まだ寒いわぁ\n誰か隣に\n来てくれたら…　", 3, 0, 1, 0);
