INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (698, 101802, 7, 1, "あん！\nお胸にこぼしちゃった", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (699, 101802, 8, 1, "あぁーん♪\nこういう物語みたいな\n恋愛あこがれる～　", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (701, 102001, 1, 1, "おにいちゃんは、\nにんじん食べられる？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (702, 102001, 2, 1, "えへへ、\nお絵かきしよ～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (703, 102001, 3, 1, "あのね、ミミと\nあそんでほしいな～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (704, 102001, 4, 1, "さっきね、きれいな\nお花見つけたよ。\nおにいちゃんにも\n見せてあげたいな～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (705, 102001, 5, 1, "うさぎさん、\nぴょんぴょん\nぴょーん♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (706, 102001, 101, 4, "おにいちゃんの絵、\nかいてあげるね♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (707, 102001, 102, 8, "おっきくなったら\nおにいちゃんの\nお嫁さんになるんだ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (708, 102001, 1, 1, "ふわぁ！\nおにいちゃんの手、\nおっきいね～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (709, 102001, 2, 1, "やった～！\nおにいちゃんが\nほめてくれた～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (710, 102001, 3, 1, "ねえねえ\nおにいちゃん、\n一緒にあそぼっ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (711, 102001, 4, 1, "おにいちゃん、\n遊んでくれるの？\n　わぁい♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (712, 102001, 101, 4, "わぁい！\nおにいちゃんだ～♪\nぎゅ～っ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (713, 102001, 102, 8, "あのねあのね、\nミミの頭ポンポンって\nしてほしいな", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (714, 102001, 1, 1, "おひさまさん、\nおはよ～！\n毎朝、早おきだね～", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (715, 102001, 2, 1, "うわぁ！\nお日さまがすごーく\n高いところにある～", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (716, 102001, 3, 1, "おにいちゃん\nおやすみのご本\nよんで～", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (717, 102001, 1, 1, "ミミも、\nうさぎさんみたいに、\nかわいくなりたいな♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (718, 102001, 2, 1, "のんびり～♪\nゆったり～♪\nまったり～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (719, 102001, 3, 1, "わぁ～かわいい～♪\nミミ、幸せ～♪\n抱きついていいかな？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (720, 102001, 4, 1, "いいにおい～♪\nお鼻ピクピク♪\nうさぎさんも喜ぶよ♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (721, 102001, 5, 1, "あったか～♪\nぬくぬく～♪\nポカポカ～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (722, 102001, 6, 1, "ふ、ふぇ～　ミミ、\n運動苦手だよ～", 3, 0, 1, 0);
