INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (804, 102201, 4, 1, "ア、アイツと同じ\n部屋にいるのよね…\nって、だから何よ！？", 1, 0, 1, 0);
