INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (808, 102201, 1, 1, "ゲームに負けた罰\nだからって、\nくすぐるのは…\nあはは…ダメだって！", 2, 0, 1, 0);
