INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (810, 102201, 3, 1, "待ちきれない…\n新作ゲームの発売日が\n待ちきれない…", 2, 0, 1, 0);
