INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (814, 102201, 1, 1, "おおおおはよう！\n…よし！　ちゃんと\nあいさつできたわ！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (815, 102201, 2, 1, "一緒にごはんとか…\nううん、やっぱいい！\nなんでもないからっ！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (816, 102201, 3, 1, "夜はこれからよ！\n朝まで\nゲームするからね！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (817, 102201, 1, 1, "アカリみたいに\n可愛くなるのは\n難しいかな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (818, 102201, 2, 1, "寝っ転がりながら\nゲームができれば\n気分が良さそう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (819, 102201, 3, 1, "可愛い…けど、\n私には似合わないかな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (820, 102201, 4, 1, "はあ、リラックスして\nゲームができるわ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (821, 102201, 5, 1, "ん…暖かくて、\n寝落ちしちゃいそう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (822, 102201, 6, 1, "ゲーム感覚でやると、\n体動かすのも\n楽しいわね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (823, 102201, 7, 1, "ゲームは頭使うから\nそのぶん\n栄養がいるのよ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (824, 102201, 8, 1, "ふむふむ、なるほど\n友だちを作るには\n笑顔が大事っと…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (826, 102202, 1, 1, "さあ、今日は\n何のゲームで\n遊ぼうかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (827, 102202, 2, 1, "一緒にゲームができる\n人がいるっていうのも\n新鮮だな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (828, 102202, 3, 1, "レアカードをゲット！\nすごいでしょ！？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (829, 102202, 4, 1, "アカリってどうやって\n友達作ってるんだろ？\nコツがあるのかな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (830, 102202, 5, 1, "楽しいゲームって\nやめどきが\n見つからないわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (831, 102202, 101, 8, "うぅ…最近\nアイツを見るとなんか\nドキドキするのよね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (832, 102202, 102, 8, "ね、ねえ\nもう少しその…\n一緒にゲームしない？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (833, 102202, 1, 1, "ねえ、人見知りを\n治すための練習に\n付き合ってくれない？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (834, 102202, 2, 1, "欲しいゲームが\n見つからなくて…\n探すの手伝って！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (835, 102202, 3, 1, "ねえ、見て！\n新しいゲームを\n買って来たの！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (836, 102202, 4, 1, "ゲームに負けた罰\nだからって、くすぐる\nのは…あ、あはは！\nだ、ダメだって！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (837, 102202, 101, 8, "ア、アンタってさ\n好きな人とかい…\nなな、何でもない！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (838, 102202, 102, 8, "あ、あのさ…\nもう少し、そこに\nいてくれてもいいわよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (839, 102202, 1, 1, "おおおおはよう！\n…よし！　ちゃんと\nあいさつできたわ！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (840, 102202, 2, 1, "一緒にごはんとか…\nううん、やっぱいい！\nなんでもないからっ！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (841, 102202, 3, 1, "夜はこれからよ！\n朝まで\nゲームするからね！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (842, 102202, 1, 1, "もっと女の子らしく\nなったらアイツも\n嬉しいかな…って、\n何考えてるの！？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (843, 102202, 2, 1, "ふああ…\n徹夜明けには\n辛い誘惑だな…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (844, 102202, 3, 1, "可愛い…けど、\n私には似合わないかな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (845, 102202, 4, 1, "こういうのに詳しいと\n女の子っぽく\n見えたりするのかな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (846, 102202, 5, 1, "寒い日は\nここでゲームするのが\n最高のぜいたくよね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (847, 102202, 6, 1, "最近ゲームばかりだし\nたまにはいいかな？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (848, 102202, 7, 1, "ゲームは頭使うから\nそのぶん\n栄養がいるのよ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (849, 102202, 8, 1, "ふむふむ、なるほど\n友だちを作るには\n笑顔が大事っと…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (851, 102301, 1, 1, "じゃんけんぽん！\nあっち向いてほい！\nあ～ん、負けたぁ\n　『勝ったぜ！』", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (852, 102301, 2, 1, "ぷうきちがいれば、\nなんでも出来ちゃうよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (853, 102301, 3, 1, "発声練習しよっと\nぷうきち\nフルスイ～ング！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (854, 102301, 4, 1, "ここで、ぷうきち\nフルスイングは、\nまずいかな～？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (855, 102301, 5, 1, "この部屋、ぷうきちを\n置く場所はないのかな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (856, 102301, 101, 4, "お兄ちゃんって…\nちょっとぷうきちに\n似てるかも♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (857, 102301, 102, 8, "お兄ちゃんにも、\nぷうきちのこと\n好きになって\nもらいたいな～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (858, 102301, 1, 1, "おやつは、\n私とぷうきちの\n二人分用意してね♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (859, 102301, 2, 1, "ぷうきちが、お腹\nペコペコだって～\n何かちょうだい！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (860, 102301, 3, 1, "『アヤネに手出したら\n…坊ちゃんでも、\n分かってんな？』", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (861, 102301, 4, 1, "ぷうきちとの\n連携を磨いてるの！\nもっと仲良くなるよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (862, 102301, 101, 4, "特別だからね？\nもっと触っていいよ\n…ぷうきちのこと", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (863, 102301, 102, 8, "お兄ちゃんのこと、\n「ぷう兄」って\n呼ぼうかな？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (864, 102301, 1, 1, "お兄ちゃんって\n朝早いんだね～\n私はまだ眠いよ～", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (865, 102301, 2, 1, "ふぁ～…ぷうきちと\nお昼寝してくるね…", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (866, 102301, 3, 1, "ぷうきちと\nおしゃべりしてたら\nもうこんな時間…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (868, 102301, 2, 1, "『座り心地がいいな』\nぷうきちは座れない\nでしょ？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (869, 102301, 3, 1, "かわいい～！\nぷうきちのお友達に\nしちゃおうかな？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (870, 102301, 4, 1, "いいニオイ～♪\nぷうきちも\n気に入ったみたい！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (871, 102301, 5, 1, "ぽわぽわする～\nぷうきちも\n気持ちいい？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (873, 102301, 7, 1, "おいしい～\nぷうきちも\n食べてみる？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (874, 102301, 8, 1, "ぷうきち！\nこの本、読んで～！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (901, 102501, 1, 1, "どうして失敗ばっかり\nこうなったら\n腹を切るしか～！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (902, 102501, 2, 1, "あれ？　あれ？\nお部屋の中で迷子に\nなっちゃいました～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (903, 102501, 3, 1, "どうしたら転ばなく\nなるんでしょうか？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (904, 102501, 4, 1, "わわわわ～～っ\nふう…\nきゃ、きゃあっ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (905, 102501, 5, 1, "私のドジで、\nこのお部屋を\n壊しちゃったら\nどうしましょう～～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (906, 102501, 101, 4, "おいしい料理で\nお出迎えしなくちゃ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (907, 102501, 102, 8, "一生懸命\nご奉仕しますよ～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (908, 102501, 1, 1, "耳かきしましょうか？\nえ？　こわい？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (909, 102501, 2, 1, "お疲れでしたら、\nお昼寝しませんか？\n膝枕もしますよ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (910, 102501, 3, 1, "おかえりなさいませ\nご主人さま♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (911, 102501, 4, 1, "すみません\nすみません\nすみません～～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (912, 102501, 101, 4, "メイドの私が、\nこんな気持ちを持って\nいいんでしょうか…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (913, 102501, 102, 8, "私のこと、\n専属メイドと思って\n頼ってくださいね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (914, 102501, 1, 1, "おはようございます♪\n今日も一日がんば…\nきゃ、きゃあ！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (915, 102501, 2, 1, "お昼ごはん…\n失敗しちゃいましたぁ\nお、おやつでリベンジします！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (916, 102501, 3, 1, "明日の目標は\nドジをしないです！\nき、昨日の目標も\nドジをしないでした", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (917, 102501, 1, 1, "あ、メイド服が\nほつれてる\n後でお裁縫しなくちゃ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (918, 102501, 2, 1, "休憩が終わったら\nお洗濯にお料理に\nお掃除もしないと！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (919, 102501, 3, 1, "かわいい♪\nあとでお洗濯\nしてあげますね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (920, 102501, 4, 1, "いい香り…\nお嬢様のお部屋にも\n置いてあげたいです", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (921, 102501, 5, 1, "ここでお洗濯ものを\n乾かしましょうか", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (922, 102501, 6, 1, "あわわわっ…\nこ、転びそうです～！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (923, 102501, 7, 1, "これくらい\nおいしいものを\n作ってみたいです～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (924, 102501, 8, 1, "物語のメイドさんって\n御主人さまを好きに\nなるんですよね…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (926, 102502, 1, 1, "失敗したら、この\nお部屋追い出され\nちゃうかも～？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (927, 102502, 2, 1, "あれ？　あれ？\nお部屋の中で迷子に\nなっちゃいました～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (928, 102502, 3, 1, "どうやったら、立派な\nメイドになれるのかな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (929, 102502, 4, 1, "塩にはＳマーク\n砂糖にはＳマーク\n…あ、あれ！？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (930, 102502, 5, 1, "ご用があれば\nなんでも言って\nくださいね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (931, 102502, 101, 8, "迷惑をかけすぎて…\n嫌われちゃったら\nどうしましょう", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (932, 102502, 102, 8, "メイドの私が\nこんな気持ちを持って\nいいんでしょうか…　", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (933, 102502, 1, 1, "おでかけですか？\nいってらっしゃいませ\nご主人さま♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (934, 102502, 2, 1, "どうして失敗ばっかり\nこうなったら\n腹を切るしか～！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (935, 102502, 3, 1, "私、またなにか\nやっちゃいましたか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (936, 102502, 4, 1, "おいしいお茶っ葉が\n手に入りました～♪\n一緒に飲みませんか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (937, 102502, 101, 8, "転んだふりであの人に\n…って！\nなんでもないですよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (938, 102502, 102, 8, "私、できることなら\nなんでもやりますよ\nあなたのためですから", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (939, 102502, 1, 1, "おはようございます♪\n今日も一日がんば…\nう、うわあ！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (940, 102502, 2, 1, "お昼ごはん…\n失敗しちゃいましたぁ\nお、おやつでリベンジします！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (941, 102502, 3, 1, "明日の目標は\nドジをしないです！\nき、昨日の目標も\nドジをしないでした", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (942, 102502, 1, 1, "あ、メイド服が\nほつれてる\n後でお裁縫しなくちゃ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (943, 102502, 2, 1, "いい座り心地です～\nこれならお嬢さまも\nゆったりできますね♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (944, 102502, 3, 1, "かわいい♪\nあとでお洗濯\nしてあげますね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (945, 102502, 4, 1, "いい香り…\nお嬢様のお部屋にも\n置いてあげたいです", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (946, 102502, 5, 1, "暖かいですけど\n…私が火に近づいて\n大丈夫でしょうか！？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (947, 102502, 6, 1, "運動しようとすると\nいつも怪我するん\nですよね…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (948, 102502, 7, 1, "こんなに美味しいもの\nメイドの私には\nもったいないです～！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (949, 102502, 8, 1, "物語のメイドさんって\n御主人さまを好きに\nなるんですよね…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (951, 102701, 1, 1, "あの方と私だけの\n部屋にしたいですわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (952, 102701, 2, 1, "あの方は私のもの…\n眼も耳も鼻も口も髪も\n首も胸も腰も手も脚も\nぬくもりも心も…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (953, 102701, 3, 1, "運命とは決して\n抗えないもの…\nクスクス…クスクス", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (954, 102701, 4, 1, "部屋からあの方の\n香りが…\nああ、至福ですわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (955, 102701, 5, 1, "毒にも薬にもならない\n存在なんて嫌ですわ\nどうせならとびきりの\n毒でありたい…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (956, 102701, 101, 4, "クスクス…\nあの方とデートする\nイメトレ中ですわ\nクスクスクスクス…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (957, 102701, 102, 8, "記憶を戻す研究より\n記憶をねつ造する研究\nのほうが良いかしら？\n伴侶という設定で…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (958, 102701, 1, 1, "運命を信じますか？\nもちろん絶対当然\n信じますわよね？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (959, 102701, 2, 1, "この薬は起床時就寝前\n食前食後食間に、毎日\n服用してください。\n惚れ薬ですわ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (960, 102701, 3, 1, "お願いがあるのです。\n新しい研究の実験台に\n…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (961, 102701, 4, 1, "こっそり栽培している\n毒きのこが毒々しく\n育ちましたわ…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (962, 102701, 101, 4, "あなた様の前では\n普通の女の子…\n少し毒薬づくりが\n得意なだけの…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (963, 102701, 102, 8, "私の身も心も、全てが\nあなた様のもの。\nあなた様の全ても…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (964, 102701, 1, 1, "おはようございます\n一晩中、あなた様の\n寝顔を見ていましたわ", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (965, 102701, 2, 1, "お昼を用意しましたの\n何も入れていませんわ\nクスクス…", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (966, 102701, 3, 1, "あなた様が眠るまで\nお傍で見守りますわ。\n…あの、どうして\n眠らないのですか？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (967, 102701, 1, 1, "いっそ鏡の中に\n閉じ込めてしまえたら\n私だけのものに…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (968, 102701, 2, 1, "はっ！\nリラックスしたら\n閃きましたわ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (969, 102701, 3, 1, "あら、キメラの素体に\nぴったりですわ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (970, 102701, 4, 1, "このお部屋は\n良い香りがしますわ。\nはっ、あなた様の匂い\nなのでは…！？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (971, 102701, 5, 1, "あたたかい…\n加熱調合にも\n使えそうですわね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (972, 102701, 6, 1, "この運動エネルギー\n何かにうまく\n利用できないかしら？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (973, 102701, 7, 1, "美味しい。あの方にも\n差し上げましょう…\n薬を入れて…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (974, 102701, 8, 1, "あの方を虜にする\n方法がどこかに\n載ってるはずですわ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (976, 102702, 1, 1, "あの方と私だけの\n部屋にしたいですわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (977, 102702, 2, 1, "こっそり栽培している\n毒きのこが毒々しく\n育ちましたわ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (978, 102702, 3, 1, "新しい薬\nどうやってあの方に\n飲ませようかしら", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (979, 102702, 4, 1, "部屋からあの方の\n香りが…\nああ、至福ですわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (980, 102702, 5, 1, "記憶を戻す研究より\n記憶をねつ造する研究\nのほうが良いかしら？\n伴侶という設定で…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (981, 102702, 101, 8, "クスクス…\nあの方とデートする\nイメトレ中ですわ\nクスクスクスクス…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (982, 102702, 102, 8, "魔法も科学も錬金術も\n全て駆使して\nあの方のために…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (983, 102702, 1, 1, "この部屋には雌犬…\n失礼、女の方がとても\n沢山いますのね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (984, 102702, 2, 1, "あなた様には私の伴侶\nとして高い教養を…\nあ、逃げられてしまい\nましたわ…！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (985, 102702, 3, 1, "お願いがあるのです\n新しい研究の\n実験台に…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (986, 102702, 4, 1, "あなた様が寝ている時\n私は…クスクス\nこれ以上は言えません", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (987, 102702, 101, 8, "想像の中のあなた様は\nすっかり私の虜なのに\n現実は…\nもどかしいですわね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (988, 102702, 102, 8, "あなた様の前では\n普通の女の子…\n少し毒薬づくりが\n得意なだけの…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (989, 102702, 1, 1, "おはようございます\n一晩中、あなた様の\n寝顔を見ていましたわ", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (990, 102702, 2, 1, "さあ、お食事ですよ\n味は保証しますわ\n特製ソースで少しだけ\n体が痺れますけど…", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (991, 102702, 3, 1, "あなた様が眠るまで\nお傍で見守りますわ\n…あの、どうして\n眠らないのですか？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (992, 102702, 1, 1, "私ももう少し\n外見に気を配る\nべきでしょうか…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (993, 102702, 2, 1, "はっ！\nリラックスしたら\n閃きましたわ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (994, 102702, 3, 1, "この私を差し置いて\nあなた様に媚びるなど\n言語道断…っ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (995, 102702, 4, 1, "このお部屋は\n良い香りがしますわ\nはっ、あなた様の匂い\nなのでは…！？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (996, 102702, 5, 1, "素敵なぬくもり…\nまるであの方に\n抱きしめられたような", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (997, 102702, 6, 1, "この運動エネルギー\n何かにうまく\n利用できないかしら？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (998, 102702, 7, 1, "美味しい。あの方にも\n差し上げましょう…\n薬を入れて…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (999, 102702, 8, 1, "あの方を虜にする\n方法がどこかに\n載ってるはずですわ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1001, 102801, 1, 1, "子供たちの遊びって\nあたしが子供の頃と\nあまり変わらないのね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1002, 102801, 2, 1, "使えるものは\n再利用しないと\n…あ、これとか\nまだ使えるわね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1003, 102801, 3, 1, "贅沢は敵！　とはいえ\n気持ちに余裕のない\n暮らしはダメよね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1004, 102801, 4, 1, "お腹空いたわね、\n残り物で\n何か作ろうかしら", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1005, 102801, 5, 1, "商売はノンゼロサムが\n理想だなぁ。誰も損を\nしないってことだもの", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1006, 102801, 101, 4, "あんたが手伝って\nくれるから、仕事も\n前より楽しいのよね♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1007, 102801, 102, 8, "焼きそばパンが\n食べたいな～\nあいつと…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1008, 102801, 1, 1, "ねぇあんた、どこか\n安いお店知らない？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1009, 102801, 2, 1, "あたし、エレガントに\n振る舞えてる？\n…そう？　嬉しい♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1010, 102801, 3, 1, "いっぱい働いて遊んで\nよく休んでまた働く。\nそれが健全な生活よね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1011, 102801, 4, 1, "一緒に部屋の\n整頓でもする？\nハマると楽しいわよ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1012, 102801, 101, 4, "あんたがいると\n子供たちも喜ぶわ♪\nもちろんあたしもね！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1013, 102801, 102, 8, "ね、あんた今、幸せ？\n何びっくりしてるのよ\n大事なことじゃないの", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1014, 102801, 1, 1, "面倒な用事は午前中に\n片づけてしまうのが、\n時間の使い方のコツよ", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1015, 102801, 2, 1, "午後のティータイム\nとかどう？\n良いカフェがあるの♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1016, 102801, 3, 1, "さ、エレガントな夜を\n楽しみましょ。\nモダンな音楽でも\nかけましょうか？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1017, 102801, 1, 1, "レディたるもの\n身嗜みはしっかりとね\n見本にならないと♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1018, 102801, 2, 1, "これで仕事は一段落ね\nちょっと休憩…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1019, 102801, 3, 1, "うふふ、\nかわいいものを\n見るのは幸せね～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1020, 102801, 4, 1, "部屋中がいい香り…\nなんだかすごい贅沢\nしてる気分♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1021, 102801, 5, 1, "風邪を引かないように\n身体を温めなくちゃ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1022, 102801, 6, 1, "さっ！　家の中でも\n効率的に\nトレーニングよ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1023, 102801, 7, 1, "もぐもぐ…はっ、\n口の周り、\n汚れてないわよね？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1024, 102801, 8, 1, "知識から過去を学んで\n未来に活かすわよ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1026, 102802, 1, 1, "安売りのお店の情報\n整理しないと…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1027, 102802, 2, 1, "どんなに苦労してても\nそう見せないのが真の\nエレガントなレディよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1028, 102802, 3, 1, "安く済んであいつが\n喜ぶ料理とか…\nないかしら", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1029, 102802, 4, 1, "子供たちの遊びって\nあたしが子供の頃と\nあまり変わらないのね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1030, 102802, 5, 1, "商売はノンゼロサムが\n理想だなぁ。誰も損を\nしないってことだもの", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1031, 102802, 101, 8, "焼きそばパンが\n食べたいな～\nあいつと…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1032, 102802, 102, 8, "あいつと一緒に…\n色々なところに\nいきたいな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1033, 102802, 1, 1, "今度買い物に\n付き合いなさい\nセールがあるのよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1034, 102802, 2, 1, "あんたがいると子供\nたちも喜ぶわ♪\nもちろんあたしもね♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1035, 102802, 3, 1, "いっぱい働いて遊んで\nよく休んでまた働く\nそれが健全な生活よね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1036, 102802, 4, 1, "あんたとこうして\nのんびりするのも\nいいものね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1037, 102802, 101, 8, "ねえ、今度遊びに\n行きたいな\nもちろん一緒に、よ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1038, 102802, 102, 8, "あんたが手伝って\nくれるから、仕事も\n前より楽しいのよね♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1039, 102802, 1, 1, "面倒な用事は午前中に\n片づけてしまうのが\n時間の使い方のコツよ", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1040, 102802, 2, 1, "ランチにして午後の\n仕事に備えましょ！\nやることは一杯よ♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1041, 102802, 3, 1, "ふわあ～\nもうこんな時間\n子供たちは\nもう寝たかしら", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1042, 102802, 1, 1, "忙しい時こそ\n身だしなみは隙なく\nぴしっとね！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1043, 102802, 2, 1, "これで仕事は一段落ね\nちょっと休憩…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1044, 102802, 3, 1, "ふふふ\nずっと眺めてたく\nなっちゃうわね", 3, 0, 1, 0);
