INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1051, 102901, 1, 1, "可愛い子が\nいっぱいいるなぁ…\n誰か私とユニット\n組んでくれないかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1052, 102901, 2, 1, "今度のライブ会場、\nいつもより大きいから\n楽しみなんだ～！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1053, 102901, 3, 1, "新曲の衣装、もうすぐ\n出来あがるんだって！\n楽しみだな～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1054, 102901, 4, 1, "彼とデュエット\nしてみたいな～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1055, 102901, 5, 1, "キラキラ無病息災、\nドキドキ一日一善…\n作詞って難しい～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1056, 102901, 101, 4, "ファンはもちろん\n大事だけど…\n彼のことは、もっと\n大切にしたいな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1057, 102901, 102, 8, "写真集見なくても、\n本物がここに\nいるのにな～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1058, 102901, 1, 1, "握手会の練習、\n付き合ってくれない？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1059, 102901, 2, 1, "写真集のポーズ\n練習中～！\nど、どうかな？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1060, 102901, 3, 1, "何て書いてあるか\n読めないって？\nもー！\nこれは私のサイン！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1061, 102901, 4, 1, "ね、次のライブ\n観に来てくれる？\nいい席とっとくから！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1062, 102901, 101, 4, "ねぇ、台本読むの\nつきあってくれる？\n恋人役に\nなってほしいんだ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1063, 102901, 102, 8, "新曲、聞いてくれた？\nキミとのことを\nテーマにしたんだよ。\nどこかは秘密だけど♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1064, 102901, 1, 1, "朝の発声練習に\n行くけど、キミも\n一緒に来ない？", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1065, 102901, 2, 1, "この時間は\nお腹すくけど…\n水着撮影があるから\nお昼抜いてるんだ", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1066, 102901, 3, 1, "夜の帳が二人を包み\n…こういう方向性の\n歌詞も悪くないなぁ。", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1067, 102901, 1, 1, "身だしなみオッケー！\nアイドルはいつでも\n完璧でいなくちゃ！", 3, 0, 1, 0);
