INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1069, 102901, 3, 1, "かわいい～♪\nこれは強力な\nライバル登場かな！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1070, 102901, 4, 1, "ステキな香り～♪\n歌詞のイメージが\n浮かんできそう！", 3, 0, 1, 0);
