INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1074, 102901, 8, 1, "ステキなお話…\n新曲の作詞の\n参考になりそう♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1076, 102902, 1, 1, "このお部屋すてき！\nファンに追われた時の\n隠れ家にピッタリね！", 1, 0, 1, 0);
