INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1085, 102902, 3, 1, "キミに響かないなら\nファンにも響くわけ\nないよ！　練習練習！", 2, 0, 1, 0);
