INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1101, 103001, 1, 1, "手裏剣の数が\n合わないデス！\nどこかに落とした\nデスか…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1102, 103001, 2, 1, "クセモノ、隠れても\nむだデス！\n…気のせいデシタ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1103, 103001, 3, 1, "金は天下の\nマワリモノ！\nところで、\nどういう意味デス？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1104, 103001, 4, 1, "むむ、アクダイカンの\n気配がするデース", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1105, 103001, 5, 1, "クニトリ合戦、\n楽しみデース！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1106, 103001, 101, 4, "ショーグンと\n出会えて、\nワタシは果報者デス！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1107, 103001, 102, 8, "最近、ショーグンの\nそばにいると\nドキドキするデス…", 1, 0, 1, 0);
