INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1159, 103101, 2, 1, "今日のあなたの運勢は\n大きょ…\n大吉です", 2, 0, 1, 0);
