INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1161, 103101, 4, 1, "小僧！\nシノブに触るとは\nいい度胸じゃねぇか！", 2, 0, 1, 0);
