INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1197, 103102, 6, 1, "はぁはぁ…\nお、お父さん、\n代わりに運動して…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1198, 103102, 7, 1, "お父さんの分も、\n私が食べるね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1199, 103102, 8, 1, "この本の占い\n私の占いと違う…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1201, 103201, 1, 1, "私だって、一人で\n買い物ぐらい\nできますわ…きっと", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1202, 103201, 2, 1, "そろそろ新しい\n案件を始めないと\nいけませんわね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1203, 103201, 3, 1, "一日も早く、\n一人前の商人に\nなりたいものです", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1204, 103201, 4, 1, "平和を買うのには\nどれほどのお金が\n必要なのかしら？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1205, 103201, 5, 1, "ここももう少し広いと\n落ち着くのですけど。\n改築しませんこと？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1206, 103201, 101, 4, "あの方はお金で雇った\nわけではないのに\n私といてくださる…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1207, 103201, 102, 8, "たとえ私が間違っても\nきっとあの方が\n正してくれますわ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1208, 103201, 1, 1, "私と、庶民向けの\nお店へ一緒に\n参りませんか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1209, 103201, 2, 1, "資産運用？\nお幾らほど\n増やしたいのです？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1210, 103201, 3, 1, "どんぶり勘定だとよく\n言われるのですが…\n例えが小さすぎます！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1211, 103201, 4, 1, "お金で解決するのは\n悪いことでは\nありませんわよね？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1212, 103201, 101, 4, "その、よろしければ\n私とお散歩など\nいかがです？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1213, 103201, 102, 8, "もしあなたさまが\n道に迷われるなら、\n私もお供いたします！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1214, 103201, 1, 1, "早起きは三ルピの徳と\n申しますが…\nもっとお得ですわね", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1215, 103201, 2, 1, "昼食は町の屋台で\n立ったまま食べて\nみたいですわ！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1216, 103201, 3, 1, "よろしければ\nお休み前のミルクを\n御一緒にいかが？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1217, 103201, 1, 1, "だらしない身なりでは\n取引相手に信用して\nいただけませんものね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1218, 103201, 2, 1, "この感触と安らぎ…\n素晴らしい\n費用対効果ですわ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1219, 103201, 3, 1, "何の見返りも求めず\nただひたすら愛でて\nいたいのです！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1220, 103201, 4, 1, "目に見えないものに\nお金をかけるのが\n真の贅沢ですわ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1221, 103201, 5, 1, "誰ひとり寒い思いを\nしない町にするために\n私にできる事は…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1222, 103201, 6, 1, "健康はお金では\n買えませんが\n運動器具は別ですわ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1223, 103201, 7, 1, "なかなか\n美味ですわ\nシェフはどちらに？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1224, 103201, 8, 1, "経済の仕組みを\nしっかりと\n学ばなくては…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1226, 103202, 1, 1, "そろそろ新しい\n案件を始めないと\nいけませんわね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1227, 103202, 2, 1, "ええと、これは\nおいくらで買ったん\nでしたっけ？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1228, 103202, 3, 1, "お金を見せたら\n態度が変わる人は\n信用できませんわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1229, 103202, 4, 1, "地図が読めずとも\n道に迷わない方って\nいらっしゃるのですね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1230, 103202, 5, 1, "この狭さも\n慣れれば\n快適ですわ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1231, 103202, 101, 8, "あの方はお金で雇った\nわけではないのに\n私といてくださる…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1232, 103202, 102, 8, "たとえ私が間違っても\nきっとあの方が\n正してくれますわ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1233, 103202, 1, 1, "一人前の商人に\n相応しい居振る舞いを\nしたいものですわ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1234, 103202, 2, 1, "幼い頃\nお店屋さんごっこを\nなさいましたか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1235, 103202, 3, 1, "資産運用？\nお幾らほど\n増やしたいのです？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1236, 103202, 4, 1, "今度一緒に…\nパーティにでも\n行きませんこと？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1237, 103202, 101, 8, "その、よろしければ\n私とお散歩など\nいかがです？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1238, 103202, 102, 8, "あなたさまの優しさは\nお金には\n代えられませんわね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1239, 103202, 1, 1, "なぜだか\n眠り足りないような\n感じがしますわ…", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1240, 103202, 2, 1, "昼食は町の屋台で\n立ったまま食べて\nみたいですわ！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1241, 103202, 3, 1, "よろしければ\nお休み前のミルクを\n御一緒にいかが？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1242, 103202, 1, 1, "ドレスは汚れていない\nようですが…やだ\n髪がほつれてますわ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1243, 103202, 2, 1, "こういったものに\n腰を沈めると…\nき、気が緩んで…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1244, 103202, 3, 1, "何の見返りも求めず\nただひたすら愛でて\nいたいのです！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1245, 103202, 4, 1, "香りは質のいいものと\nそうでないものが\nはっきり分かりますわ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1246, 103202, 5, 1, "誰ひとり寒い思いを\nしない町にするために\n私にできる事は…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1247, 103202, 6, 1, "目標は遠くに\n置いた方が励みに\nなりますわ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1248, 103202, 7, 1, "なかなか\n美味ですわ\nシェフはどちらに？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1249, 103202, 8, 1, "商人って、物語では\n悪役にされやすいの\nですね…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1251, 103301, 1, 1, "家の中のものを使って\n物ボケ、考えるべ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1252, 103301, 2, 1, "毎日の日課…それは\n炊事、洗濯、大喜利\nだべ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1253, 103301, 3, 1, "うちに塀ができたら、\nへー、って言いたい！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1254, 103301, 4, 1, "オラが作る料理は\nこの後スタッフで\n美味しくいただくべよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1255, 103301, 5, 1, "エリザベスも\n連れてきてぇ～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1256, 103301, 101, 4, "あんちゃんも、\nエリザベスと同じ位\nめんこいべ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1257, 103301, 102, 8, "オラとあんちゃん、\n息がぴったりに\nなってきたべ…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1258, 103301, 1, 1, "おぉ！　そんな急に\nツッコまれたら、\nびっくらこくべやー！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1259, 103301, 2, 1, "ネタはオラが書くけど\nあんちゃんが\n書きたかったら\n書いてもいいんだぞ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1260, 103301, 3, 1, "あんちゃん、オラには\n容赦なんかせずに\nツッコんでくれて\nいいから！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1261, 103301, 4, 1, "押すなよ、\n絶対押すなよ\n…なんて\n言ってないべさ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1262, 103301, 101, 4, "うう…あんちゃんに\nツッコまれたくて\nうずうずしてきたべ～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1263, 103301, 102, 8, "将来的には夫婦漫才\nってのも…あ、ひ、\nひとりごとだべさ～！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1264, 103301, 1, 1, "おはよう！　一緒に\n牛の乳しぼりいくべ？", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1265, 103301, 2, 1, "お昼だ～腹が減っては\n笑いはできぬ～！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1266, 103301, 3, 1, "今日もよく働いたべ！\nさ、今からは\nお笑いの練習だ～！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1267, 103301, 1, 1, "舞台に上がる前に\n身なりは整えねぇと\nだめだべ～！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1268, 103301, 2, 1, "ナイスなイスに\n座りたい、\nなんちゃって！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1269, 103301, 3, 1, "めんこいのは、\nスベっても許される\nからずるいべな～？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1270, 103301, 4, 1, "必要以上に\n癒されちまうべや～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1271, 103301, 5, 1, "ぽかぽかだ…\nウケた会場みてぇだ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1272, 103301, 6, 1, "お笑い芸人は、\n体が資本だべさ～！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1273, 103301, 7, 1, "もぐもぐ…\nボケることも\n忘れちまう美味さだ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1274, 103301, 8, 1, "ネタ作りのヒントが\n載ってねぇか探すべ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1276, 103302, 1, 1, "さぁ今日も張り切って\n漫才の練習するべさ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1277, 103302, 2, 1, "この部屋でも、商品\n売っていいんだべか？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1278, 103302, 3, 1, "眼鏡を頭の上に乗せて\n「メガネメガネ～」\nって言う練習した方が\nいいべか？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1279, 103302, 4, 1, "漫才を持ち時間に\n収めるには、何度も\n練習するしかねぇべ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1280, 103302, 5, 1, "エリザベスも\n連れてきてぇ～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1281, 103302, 101, 8, "うう…あんちゃんに\nツッコまれたくて\nうずうずしてきたべ～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1282, 103302, 102, 8, "あんちゃんに\n採れたての食材を\n食わしてやりてぇ～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1283, 103302, 1, 1, "押すなよ\n絶対押すなよ\n…なんて\n言ってないべさ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1284, 103302, 2, 1, "あんちゃん、百連\nツッコミ特訓だ～！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1285, 103302, 3, 1, "あんちゃん、オラには\n容赦なんかせずに\nツッコんでくれて\nいいから！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1286, 103302, 4, 1, "「なんでだべ～！」\nって言いながら\nツッコむといいべ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1287, 103302, 101, 8, "あはは～あんちゃんの\nツッコミはやっぱり\n気持ちいいなぁ～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1288, 103302, 102, 8, "将来的には夫婦漫才\nってのも…あ、ひ\nひとりごとだべさ～！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1289, 103302, 1, 1, "おはよう！\n今日もいい\nネタ作り日和だ！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1290, 103302, 2, 1, "お昼だな\nご飯を食べたら、\nネタの練習するべ！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1291, 103302, 3, 1, "もうこんな時間だべ\nネタ作りは\n明日にするべ…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1292, 103302, 1, 1, "うーん…衣装、\nずれてねぇべか？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1293, 103302, 2, 1, "ナイスなイスに\n座りたい\nなんちゃって！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1294, 103302, 3, 1, "はう～\nなまらめんこいべや～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1295, 103302, 4, 1, "必要以上に\n癒されちまうべや～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1296, 103302, 5, 1, "笑いをとることを\n忘れるほど\nあったけぇべ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1297, 103302, 6, 1, "たくさん運動して\n動きでも笑いがとれる\nように頑張るべ～！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1298, 103302, 7, 1, "もぐもぐ…\nボケることも\n忘れちまう美味さだ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1299, 103302, 8, 1, "幅広く教養を\n身につけて\nいいネタ作るべ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1301, 103401, 1, 1, "通りにいる\nカップル…爆発\nすればいいのに…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1302, 103401, 2, 1, "う～ん、仕事なんて\nしないで、\n飲んでたいな～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1303, 103401, 3, 1, "今日は一日、\n外に出ない！\nもう決めたわ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1304, 103401, 4, 1, "お腹空いた…\nけど、外に出るのは\n面倒だなぁ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1305, 103401, 5, 1, "うぅ…頭がガンガン\nする…昨日、\n飲み過ぎたかな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1306, 103401, 101, 4, "デートとか…\n誘っても嫌がられ\nないかな…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1307, 103401, 102, 8, "もしかして運命の\n相手だったり…\nするのかな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1308, 103401, 1, 1, "やっぱり一杯目が\n一番おいしいわ～！\n…え、見てた！？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1309, 103401, 2, 1, "頼れるお姉さんだって\nところ、もっと見せて\nあげるからね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1310, 103401, 3, 1, "ねえ、一緒に買い物に\n行かない？　その…\nの、飲み物とか…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1311, 103401, 4, 1, "ひっく、うふふ…\nもっとお姉さんと\nお話しようよ～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1312, 103401, 101, 4, "極力人付き合いは\nしない主義なんだけど\nキミは例外かも…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1313, 103401, 102, 8, "いつか…\nキミと一緒に飲めたら\n楽しいだろうな", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1314, 103401, 1, 1, "おはよう…朝は\nちょっとつらいな…\n低血圧なのよ…", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1315, 103401, 2, 1, "おっひるー♪\nなに食べよっかな～♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1316, 103401, 3, 1, "ふわあ…\n眠る前にちょっと\n一杯…冗談よ", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1317, 103401, 1, 1, "うう…\n髪がボサボサ…\nちゃんとしなきゃ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1318, 103401, 2, 1, "自然体でゴロゴロ\nするのって\n幸せだなぁ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1319, 103401, 3, 1, "こういうの男の子に\nプレゼントして\nもらいたいな…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1320, 103401, 4, 1, "うわあ、\nこれ、私の好きな\n香りだー！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1321, 103401, 5, 1, "ぽっかぽか…\nあとは熱い一杯が\nあれば…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1322, 103401, 6, 1, "家でゴロゴロして\n付いちゃったお肉を\n落とさないと…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1323, 103401, 7, 1, "もぐもぐ…\nおーいしー！\n…太らないよね？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1324, 103401, 8, 1, "大人の女らしく\n色んなことを学んで\nおかなくっちゃ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1326, 103402, 1, 1, "仕事終わりの一杯の\nために、今日も\n頑張らないと", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1327, 103402, 2, 1, "今日は\nのんびり\n過ごそうかな～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1328, 103402, 3, 1, "あ…\nお皿、洗って\nなかった…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1329, 103402, 4, 1, "もう少し、大人の\n魅力が出せるように\nなりたいな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1330, 103402, 5, 1, "素敵な彼氏…\n欲しいなぁ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1331, 103402, 101, 8, "彼と私\n釣り合うかな…\n大丈夫よね？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1332, 103402, 102, 8, "今後のために\nちょっとは花嫁修業\nしてみようかな…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1333, 103402, 1, 1, "何事も\nお姉さんと一緒に\n楽しく頑張りましょ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1334, 103402, 2, 1, "ね、ねえ！\nキミは年上と年下\nどっちが好きかな？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1335, 103402, 3, 1, "もう…\n構ってくれないと\n泣いちゃうぞ～？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1336, 103402, 4, 1, "お姉さんが～\n可愛がって\nあげようか～？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1337, 103402, 101, 8, "極力人付き合いは\nしない主義なんだけど\nキミは例外かも…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1338, 103402, 102, 8, "お姉さんとチュー\nする？　ふふふ～\n冗談で～す！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1339, 103402, 1, 1, "さーて、今日も\nいっちょやるぞー！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1340, 103402, 2, 1, "後でのんびり\nストレッチでも\nしようっと♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1341, 103402, 3, 1, "一日よく働いた～！\n自分にごほうび\nあげたいなー♪", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1342, 103402, 1, 1, "仕事の前に、髪とか\n整えないと…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1343, 103402, 2, 1, "自然体でゴロゴロ\nするのって\n幸せだなぁ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1344, 103402, 3, 1, "もう大人だし…\nあんまり可愛いのは\n似合わないかな…？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1345, 103402, 4, 1, "うわあ\nこれ、私の好きな\n香りだー！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1346, 103402, 5, 1, "あったかーい…\nだ、だらけ\nちゃいそう…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1347, 103402, 6, 1, "地道な努力が\nリア充への\n第一歩！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1348, 103402, 7, 1, "おーいしっ♪\nこのために\n生きてるわねえ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1349, 103402, 8, 1, "大人の女らしく\n色んなことを学んで\nおかなくっちゃ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1351, 103801, 1, 1, "大切な人に\n本をすすめるのって\nけっこう難しいなぁ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1352, 103801, 2, 1, "き、きらーん☆\nお姉ちゃんに\n似てるかな…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1353, 103801, 3, 1, "そろそろ好きな\n作家さんの新刊が\n出るから楽しみ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1354, 103801, 4, 1, "ちょっと、休憩…\n本でも読もうかな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1355, 103801, 5, 1, "もっとたくさんの本に\n囲まれて生活したいな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1356, 103801, 101, 4, "むかしむかしある所に\nシオリと\nあの人が…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1357, 103801, 102, 8, "この本の主人公、\nあの人に\n似ているかも…\nかっこいいな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1358, 103801, 1, 1, "ケホケホ…\nコンコン…\nゴホッゴホッ…\nへ、平気ですよ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1359, 103801, 2, 1, "あ…お姉ちゃんと\n約束があったんだ…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1360, 103801, 3, 1, "しおりを探している？\nあ、本に挟む方ですね\nドキドキしました…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1361, 103801, 4, 1, "最近調子がいいのは\n牧場の牛乳やチーズの\nおかげでしょうか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1362, 103801, 101, 4, "読みたい本も\nあなたと行きたい所も\n増えるばっかりです", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1363, 103801, 102, 8, "私の病気、\n悪化してるのかも！？\nすごく胸がドキドキ\nするんです…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1364, 103801, 1, 1, "えっと、朝食の前には\nこの薬を飲んで…\n食後にはこの薬を…", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1365, 103801, 2, 1, "ふぅ…午前中\n動いたので体力が…\nすこし休みますね", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1366, 103801, 3, 1, "早く寝ないと\n体に良くないですよね\nでも気になっている\n本があって…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1367, 103801, 1, 1, "寝てばかりなので、\n髪が乱れてないかな\n…？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1368, 103801, 2, 1, "少し休もう\n…やっぱり\nたくさん休もう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1369, 103801, 3, 1, "かわいいものが\n枕元にあると\n安心できるんですよ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1370, 103801, 4, 1, "いい香り…\n病は気からと言うので\n雰囲気も大事ですね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1371, 103801, 5, 1, "こうして温まって\nいると、病気も\n治る気がします", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1372, 103801, 6, 1, "運動…イメージ\nトレーニングだけじゃ\nだめでしょうか…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1373, 103801, 7, 1, "少食ですけど\nこれならたくさん\n食べられそうです", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1374, 103801, 8, 1, "続きが気になって\n読むのを止める\nタイミングが…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1376, 103802, 1, 1, "しっかり休まないと\n早く回復するように", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1377, 103802, 2, 1, "き、きらーん☆\nお姉ちゃんに\n似てるかな…？　", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1378, 103802, 3, 1, "病室よりも…\nずっとこの部屋に\nいたいですね…　", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1379, 103802, 4, 1, "そろそろ好きな\n作家さんの新刊が\n出るから楽しみ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1380, 103802, 5, 1, "大切な人に\n本をすすめるのって\nけっこう難しいなぁ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1381, 103802, 101, 8, "むかしむかしある所に\nシオリと\nあの人が…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1382, 103802, 102, 8, "あの人の\nためにも早く\n病気を治さないと…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1383, 103802, 1, 1, "ケホケホ…\nコンコン…\nゴホッゴホッ…\nへ、平気ですよ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1384, 103802, 2, 1, "新記録です！\n一週間連続、\n平熱を保ちました！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1385, 103802, 3, 1, "あ…お薬の時間…\n苦いけど我慢しなきゃ　", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1386, 103802, 4, 1, "最近調子がいいのは\n牧場の牛乳やチーズの\nおかげでしょうか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1387, 103802, 101, 8, "しおりを探している？\nあ、本に挟む方ですね\nドキドキしました…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1388, 103802, 102, 8, "私の病気\n悪化してるのかも！？\nすごく胸がドキドキ\nするんです…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1389, 103802, 1, 1, "えっと、朝食の前には\nこの薬を飲んで…\n食後にはこの薬を…", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1390, 103802, 2, 1, "…お昼の薬？\nあ、飲み忘れてました\nありがとうございます", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1391, 103802, 3, 1, "この薬を飲むと\n眠くなっちゃうんです\nでもまだ、あなたと\nお話したいし～", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1392, 103802, 1, 1, "寝てばかりなので、\n髪が乱れてないかな\n…？　", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1393, 103802, 2, 1, "少し休もう\n…やっぱり\nたくさん休もう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1394, 103802, 3, 1, "この子がいれば\n病室に一人でも\n寂しくないですね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1395, 103802, 4, 1, "いろんな効能が？\n健康になる香りも\nあるのでしょうか？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1396, 103802, 5, 1, "こうして温まって\nいると、病気も\n治る気がします", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1397, 103802, 6, 1, "私も、少しは\n体力をつけないと\nいけませんよね　", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1398, 103802, 7, 1, "いっぱい食べて\n健康な体にならないと", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1399, 103802, 8, 1, "続きが気になって\n読むのを止める\nタイミングが…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1401, 104001, 1, 1, "友達ができたら、\n山の上でお弁当…\n夢が広がります…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1402, 104001, 2, 1, "あ、忘れずに\nお花に水を\nあげなきゃ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1403, 104001, 3, 1, "お菓子を作って、\nみなさんに振る舞って\n友達作戦！　とか…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1404, 104001, 4, 1, "そろそろ新しい\n草人形を作って\n友達作りの特訓を…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1405, 104001, 5, 1, "植物のお世話って、\nぼっちにとっては、\n救い…なんです…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1406, 104001, 101, 4, "ＢＢ団の活動日…\nまだかな…\nうふふふっ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1407, 104001, 102, 8, "もっともっとあの人の\nそばで、一緒に友達を\n作っていきたいな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1408, 104001, 1, 1, "あ、こんにちは！\n今日もＢＢ団で\nお世話になります！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1409, 104001, 2, 1, "あの…私の友達作りの\n練習方法、間違って\nませんよね…？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1410, 104001, 3, 1, "そうだ、今度、\n森に一緒に木の苗を\n植えに行きませんか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1411, 104001, 4, 1, "人とお話するのって、\n私、大好きです！\n友達ふやすぞ～！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1412, 104001, 101, 4, "あなたと話してると、\nまるで私がぼっちじゃ\nないみたいです！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1413, 104001, 102, 8, "友達もほしいけど…\nあなたに会えたことが\n私の一番の幸せです", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1414, 104001, 1, 1, "おはようございます！\n今日もＢＢ団、\nがんばるぞー！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1415, 104001, 2, 1, "なんだか、お腹が\n空いてきました。\nお昼ごはんー♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1416, 104001, 3, 1, "もう眠いかもです…\n明日は友達、\nできるといいなあ…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1417, 104001, 1, 1, "私がもっと\n可愛ければ、\n友達できるかなあ？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1418, 104001, 2, 1, "ふわあ…のんびり\nしすぎないように\n気を付けなきゃです", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1419, 104001, 3, 1, "かわいいなあ。\nこの子とも\n友達になりたい…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1420, 104001, 4, 1, "いい香りです…\n精神統一して\n友達のイメトレ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1421, 104001, 5, 1, "灯りを見てると、\n心がなごみます～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1422, 104001, 6, 1, "もぐもぐ…\nおいしいです…！\nみなさんもぜひ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1423, 104001, 7, 1, "おいしいです…\n私に友達がいたら、\n分けてあげたいな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1424, 104001, 8, 1, "友達作りの\n参考になりそうなこと\n書いてないかな～？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1426, 104002, 1, 1, "友達作りは、焦らず\n慌てず、じっくりと…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1427, 104002, 2, 1, "ひとつ屋根の下\nって感じ…\nいいですねえ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1428, 104002, 3, 1, "煮汁、煮汁が\n噴きこぼれて\nいませんか～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1429, 104002, 4, 1, "今度、みなさんと\nピクニックとか…\nいってみたいな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1430, 104002, 5, 1, "ぼっちでもいい\nたくましく育てば\n…なんて", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1431, 104002, 101, 8, "最近、毎日あの人の\nこと考えちゃいます\nどうしてだろう…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1432, 104002, 102, 8, "もっともっとあの人の\nそばで、一緒に友達を\n作っていきたいな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1433, 104002, 1, 1, "あ！　ＢＢ団の活動\nですか？　嬉しくて\n飛び跳ねちゃいます！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1434, 104002, 2, 1, "あなたとのお話で友達\n作りの練習になるなら\n一石二鳥です！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1435, 104002, 3, 1, "私少しは人と話しても\nテンパらなくなって\nきましたかね…？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1436, 104002, 4, 1, "あの、友達作りの\n新しい作戦\n考えてみたんですっ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1437, 104002, 101, 8, "あなたとお話する時の\nこの気持ち、私の宝物\nなんです…えへへ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1438, 104002, 102, 8, "友達もほしいけど…\nあなたに会えたことが\n私の一番の幸せです", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1439, 104002, 1, 1, "朝ですね！\nこんないい朝は\n友達作りのチャンス！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1440, 104002, 2, 1, "あの…そろそろお腹\nすきませんか？\nお昼ごはんは…", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1441, 104002, 3, 1, "もう眠いかもです…\n明日は友達\nできるといいなあ…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1442, 104002, 1, 1, "私…見た目\nそこまで変じゃ\nないですよね…？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1443, 104002, 2, 1, "ほわあ～…\nふかふかって\nいいものですね～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1444, 104002, 3, 1, "この子みたいな\nあいくるしさを\n目指したいです！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1445, 104002, 4, 1, "灯りを見てると\n心がなごみます～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1446, 104002, 5, 1, "ぬくぬくで、なんだか\n眠くなっちゃいますね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1447, 104002, 6, 1, "身体を鍛えて\nみなさんを\n守ります！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1448, 104002, 7, 1, "もぐもぐ…\nおいしいです…！\nみなさんもぜひ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1449, 104002, 8, 1, "友達作りの\n参考になりそうなこと\n書いてないかな～？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1451, 104201, 1, 1, "唱喚の歌は、みだりに\n歌っていいものでは\nないんです", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1452, 104201, 2, 1, "ここでアイドルの\n練習は、さすがに\n迷惑ですよね？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1453, 104201, 3, 1, "練習してもあんまり\n疲れなくなった…\nアイドルのおかげかな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1454, 104201, 4, 1, "人々の平和を守る旅に\n同行してくれる人を\n探していて…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1455, 104201, 5, 1, "あとで曲を復習して\nおかないと…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1456, 104201, 101, 4, "踊りを見られるのは\nまだ恥ずかしいけど\nあの人になら…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1457, 104201, 102, 8, "浜辺で曲を書こうと\n思うんですけど…\n一緒に行きませんか？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1458, 104201, 1, 1, "いつも応援ありがとう\nございます♪　これは\nあ、握手の練習です！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1459, 104201, 2, 1, "え、もう練習の時間？\nごめんなさい\nすぐ準備します！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1460, 104201, 3, 1, "守るべき人たちに\n笑顔でいてもらうのも\n大切なんですね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1461, 104201, 4, 1, "歌える曲を増やそうと\n思って。どんな曲が\n聞きたいですか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1462, 104201, 101, 4, "～～♪　あれ？\n今私、歌ってました？\n気づきませんでした", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1463, 104201, 102, 8, "…歌って欲しい？\n喜んで♪\nどの歌にしましょう？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1464, 104201, 1, 1, "おはようございます♪\n少し歌の練習を\nしてきますね", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1465, 104201, 2, 1, "もうお昼なんですか？\n練習に夢中で\n気づきませんでした", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1466, 104201, 3, 1, "明日の予定は…\nアイドルはやることが\n多いみたいで…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1467, 104201, 1, 1, "見た目については\n前よりも気をつけてと\nノゾミに言われてます", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1468, 104201, 2, 1, "歌の調子をよくする\nためには休息も\n必要ですよね…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1469, 104201, 3, 1, "この子たちを\n相手に練習して\nみようかな…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1470, 104201, 4, 1, "…疲れてるのかな\n落ち着けているのが\n自分でもわかります", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1471, 104201, 5, 1, "ここにいると\n穏やかな旋律が\n浮かんできます…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1472, 104201, 6, 1, "歌うためには\n体力づくりは\n欠かせません", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1473, 104201, 7, 1, "歌った後は\nお腹が空いて\nしまうんです", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1474, 104201, 8, 1, "音楽について\n理論も勉強\nしないと…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1476, 104202, 1, 1, "歌うことの楽しさ\n分かってきました\nもっと歌いたいな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1477, 104202, 2, 1, "練習してもあんまり\n疲れなくなった…\nアイドルのおかげかな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1478, 104202, 3, 1, "精霊も強くなったし\nもっとたくさんの人を\n守りたいです", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1479, 104202, 4, 1, "最近、よく頭の中に\n歌が浮かんできます\n不思議な感覚…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1480, 104202, 5, 1, "楽譜を買いました\n思いついた歌を\n書き留めておくために", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1481, 104202, 101, 8, "浜辺で曲を書こうと\n思うんですけど…\n一緒に行きませんか？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1482, 104202, 102, 8, "私の中にある\n私だけの歌…\n作ってみようかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1483, 104202, 1, 1, "守るべき人たちに\n笑顔でいてもらうのも\n大切なんですね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1484, 104202, 2, 1, "歌える曲を増やそうと\n思って。どんな曲が\n聞きたいですか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1485, 104202, 3, 1, "魔物退治ですか！？\n…違います？\n良かった…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1486, 104202, 4, 1, "いつも応援ありがとう\nございます♪　これは\nあ、握手の練習です！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1487, 104202, 101, 8, "…歌って欲しい？\n喜んで♪\nどの歌にしましょう？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1488, 104202, 102, 8, "～～♪　あれ？\n今私、歌ってました？\n気づきませんでした", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1489, 104202, 1, 1, "呼吸を深く、大きく…\nこれでちゃんと\n目が覚めますよ", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1490, 104202, 2, 1, "少し休んだら\n午後の練習に\n行ってきますね", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1491, 104202, 3, 1, "曲を作っていたら\nもうこんな時間…\nおやすみなさい", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1492, 104202, 1, 1, "見た目については\n前よりも気をつけてと\nノゾミに言われてます", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1493, 104202, 2, 1, "あ～…♪\nよく体がほぐれます…\nいい歌が歌えそうです", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1494, 104202, 3, 1, "かわいいと思う\n気持ちを、歌にしても\nいいかも知れませんね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1495, 104202, 4, 1, "…疲れてるのかな\n落ち着けているのが\n自分でもわかります", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1496, 104202, 5, 1, "ここにいると\n穏やかな旋律が\n浮かんできます…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1497, 104202, 6, 1, "もっと体力をつけて\n最高のライブを\nファンに届けます！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1498, 104202, 7, 1, "これでも一応\n体形には気をつけて\n食べてるんですよ？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1499, 104202, 8, 1, "読書は歌に\n新しい風を\n吹き込んでくれます", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1501, 104301, 1, 1, "店の新メニューでも\n考えるかな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1502, 104301, 2, 1, "あたしの店から、\n出前を届けようか？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1503, 104301, 3, 1, "パトロール、\nパトロール…\nここは異常なしかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1504, 104301, 4, 1, "腹へったら食う！\n悩んだら寝る！\nそれが一番だろ？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1505, 104301, 5, 1, "のんびりし過ぎるのも\n…性に合わねぇな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1506, 104301, 101, 4, "そろそろあいつ、\n腹すかしてるん\nじゃないかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1507, 104301, 102, 8, "今日の献立は…\nあいつの好きなものに\nしてやるか", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1508, 104301, 1, 1, "強くなりたいなら、\nあたしがビシバシ\n鍛えてやるよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1509, 104301, 2, 1, "天気がいい日は\n外で運動したく\nならねぇか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1510, 104301, 3, 1, "腹減ったか？　なんか\n作ってやろうか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1511, 104301, 4, 1, "ん？　なんだよ、\n相談事か？\nなんでも言ってくれ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1512, 104301, 101, 4, "お前が好きな飯って\nなんだ？\n今度、作ってやるよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1513, 104301, 102, 8, "お前って不思議だよな\n一緒にいるとなんだか\n…なんでもねーよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1514, 104301, 1, 1, "気持ちいい朝だな！\n体を動かしたい気分だ\n付き合ってくれるか", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1515, 104301, 2, 1, "昼飯もう食ったか？\nまだなら、あたしが\nなんか作ってやるよ", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1516, 104301, 3, 1, "日中たっぷり動いたら\nそのぶん夜はしっかり\n休んどけよな", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1517, 104301, 1, 1, "た、たまにはその…\nかわいい格好とか\nしても…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1518, 104301, 2, 1, "ふわぁ、\n一休みすっかなぁ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1519, 104301, 3, 1, "うっ！　か、かわいい\nじゃねぇか", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1520, 104301, 4, 1, "へー　いい匂いだな\nあいつもこういうの\n好きなのかな…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1521, 104301, 5, 1, "ふわぁ〜！\n温かいと、つい\nウトウトしちまうな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1522, 104301, 6, 1, "オラオラオラーーー！\n燃えるぜーーー！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1523, 104301, 7, 1, "いい味してるぜ！\nうちの店でも\n出してみたいな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1524, 104301, 8, 1, "文字を読んでると\nどうしても眠く\nなっちまうんだよなぁ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1526, 104302, 1, 1, "パトロール終了～\n本日も異常なしっと！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1527, 104302, 2, 1, "あたしの店から\n出前を届けようか？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1528, 104302, 3, 1, "あいつ、早く\n帰ってこないかな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1529, 104302, 4, 1, "あいつ\n腹すかせてないと\nいいんだけど…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1530, 104302, 5, 1, "のんびりし過ぎるのも\n…性に合わねぇな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1531, 104302, 101, 8, "あいつの事を思うと\n胸があったかくなる？\nなんだコレ…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1532, 104302, 102, 8, "今日の献立は…\nあいつの好きなものに\nしてやるか", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1533, 104302, 1, 1, "ん？　なんだよ\n相談事か？\nなんでも言ってくれ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1534, 104302, 2, 1, "天気がいい日は\n外で運動したく\nならねぇか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1535, 104302, 3, 1, "腹減ったか？　なんか\n作ってやろうか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1536, 104302, 4, 1, "腹へったら食う！\n悩んだら寝る！\nそれが一番だろ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1537, 104302, 101, 8, "あのさ…できれば\nもっとそばに…いや\nなんでもねぇ忘れろ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1538, 104302, 102, 8, "お前って不思議だよな\n一緒にいるとなんだか\n…なんでもねーよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1539, 104302, 1, 1, "気持ちいい朝だな！\n体を動かしたい気分だ\n付き合ってくれるか？", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1540, 104302, 2, 1, "昼飯もう食ったか？\nまだなら、あたしが\nなんか作ってやるよ", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1541, 104302, 3, 1, "あんま夜更しすんなよ\n明日に響いたら\n大変だろ？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1542, 104302, 1, 1, "た、たまにはその…\nかわいい格好とか\nしても…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1543, 104302, 2, 1, "ふぅ～♪\n疲れがとれるな～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1544, 104302, 3, 1, "うっ！　か、かわいい\nじゃねぇか", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1545, 104302, 4, 1, "へー　いい匂いだな\nあいつもこういうの\n好きなのかな…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1546, 104302, 5, 1, "ふわぁ～！\n温かいと、つい\nウトウトしちまうな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1547, 104302, 6, 1, "オラオラオラーーー！\n燃えるぜーーー！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1548, 104302, 7, 1, "いい味してるぜ！\nうちの店でも\n出してみたいな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1549, 104302, 8, 1, "料理だけじゃなく\n経営術とかも\n学んどかねぇと…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1551, 104501, 1, 1, "最近なにも\nしてくれません…\n焦らしてるんですね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1552, 104501, 2, 1, "ぐふふ、じゅるり…\nい、いけない、また\nいつの間にかよだれが", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1553, 104501, 3, 1, "好きな武器といえば\n剣より槍より\nやっぱり鞭…ぐふふ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1554, 104501, 4, 1, "「無数の触手が\nクウカの柔肌に\n食い込み」…ぐふふ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1555, 104501, 5, 1, "もしドＳさんが\nＭに転じたらと思うと\n身の毛がよだちます！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1556, 104501, 101, 4, "ドＳさんを登場\nさせるだけで、妄想に\n無限の広がりが！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1557, 104501, 102, 8, "あっ…\nジロジロ見てたの\n気づいちゃいました？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1558, 104501, 1, 1, "視線を感じると\n思ったら…やはり\nドＳさんでしたか", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1559, 104501, 2, 1, "今日は\nどんなヒドイことを\nしてくれるんですか？\nぐふふふ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1560, 104501, 3, 1, "いつの間にそこに！？\nまさかクウカを監視\nしていたのですか！？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1561, 104501, 4, 1, "クウカに触るつもり\nですか？　触るつもり\nなのですねっ！？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1562, 104501, 101, 4, "クウカにナニをする\nつもりですか…\n期待してしまいます！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1563, 104501, 102, 8, "ドＳさんの視界に入る\nだけでドキドキが…\nこれが恋！？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1564, 104501, 1, 1, "おはようございます。\n朝からお盛んですね？\nぐふふ…", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1565, 104501, 2, 1, "こんなにも日の高い\nうちからドＳさんは…\nじゅるり", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1566, 104501, 3, 1, "夜はこれから、\nですよ…\nぐふ、ぐふふふ…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1567, 104501, 1, 1, "キレイにおめかし\nしたのに力ずくで…\nぐふふふ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1568, 104501, 2, 1, "ソファの背にクウカを\n押し付けて…ぐふふ\nいいですね\nソファドン", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1569, 104501, 3, 1, "「見た目とは裏腹に\nそれは激しく攻め\nたてて」…ぐふふふ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1570, 104501, 4, 1, "香りを吸ったクウカの\n頬は紅潮し目はうるみ\nもはや抗う力も…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1571, 104501, 5, 1, "ああ、身体が\n火照って…\nぐふ、ぐふふ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1572, 104501, 6, 1, "はぁ、はぁ、こうして\nクウカの体力を\n奪ってから…ぐふふ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1573, 104501, 7, 1, "食欲が満たされたら\nつ、次はやはり…！\nそうなのですね！？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1574, 104501, 8, 1, "この本の下に、\nそういう本を\nカムフラージュ？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1576, 104502, 1, 1, "「ドＳさんと踊り子\n乱舞の一夜」…\n次の日記はこれで…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1577, 104502, 2, 1, "最近なにも\nしてくれません…\n焦らしてるんですね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1578, 104502, 3, 1, "も、妄想が\n足りません…\n禁断症状がっ…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1579, 104502, 4, 1, "もしドＳさんが\nＭに転じたらと思うと\n身の毛がよだちます！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1580, 104502, 5, 1, "次はどんなドＳを\nされるのか、想像する\nだけで…ぐふふふ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1581, 104502, 101, 8, "じっとしてると\nドＳさんのことばかり\n考えてしまいますね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1582, 104502, 102, 8, "妄想が止まらなくて\nクウカどうにかなって\nしまいそうです！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1583, 104502, 1, 1, "ドＳさんといると体が\n火照って…クウカは\nいやらしい子です！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1584, 104502, 2, 1, "あっ…\nジロジロ見てたの\n気づいちゃいました？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1585, 104502, 3, 1, "ドＳさんのおかげで\n新しい扉が開けそう\nです…じゅるり", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1586, 104502, 4, 1, "いやー！　ドＳさんに\n襲われ…襲って\nくれないんですか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1587, 104502, 101, 8, "クウカはドＳさんに\n身も心も堕とされて\nしまったのですね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1588, 104502, 102, 8, "ドＳさんがお望みなら\nクウカは\nどんなことだって！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1589, 104502, 1, 1, "朝だからこそ\nできる妄想が\nあります…ぐふふ", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1590, 104502, 2, 1, "こんな日の高いうち\nからドＳさんは…\nじゅるり", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1591, 104502, 3, 1, "今夜は一晩中…\nなんですね？\nぐふふ…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1592, 104502, 1, 1, "意外と難しいんですよ\n正しい亀甲縛りと\nいうものは…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1593, 104502, 2, 1, "くつろいで油断\nしているスキに何を\nされるのでしょう！？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1594, 104502, 3, 1, "「見た目とは裏腹に\nそれは激しく攻め\nたてて」…ぐふふふ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1595, 104502, 4, 1, "これは禁断の香り…\nああっ一段階上の\n妄想が開花するうぅ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1596, 104502, 5, 1, "ああっ、暖気がまるで\nロウソクのように\nクウカを攻めますっ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1597, 104502, 6, 1, "激しい攻めを受けきる\nためには、鍛えないと\nいけないのですね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1598, 104502, 7, 1, "こんなにいっぱい\n入りません…\nお腹にっ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1599, 104502, 8, 1, "どんな本も\nよく見ると\nいやらしいです…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1601, 104601, 1, 1, "この前遊んだ猫、\nモッフモフ\nだったにゃあ…♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1602, 104601, 2, 1, "ほっぺやお腹が、\n肉球ぐらいぷにぷに…\nこれはまずいにゃ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1603, 104601, 3, 1, "大きな仕事が\n決まったにゃ！\n腕が鳴るにゃ～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1604, 104601, 4, 1, "おやつのたい焼き、\nつぶあんとこしあん、\nどっちに\nしようかにゃ？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1605, 104601, 5, 1, "うにゃー…\nもうちょっとお昼寝\nしたかったにゃ…\nむにゃむにゃ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1606, 104601, 101, 4, "キミに呼ばれると、\n猫まっしぐらな気分に\nなっちゃうにゃ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1607, 104601, 102, 8, "キミのおかげで\nギルドの仕事も\n大成功だにゃ♪\nありがとにゃ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1608, 104601, 1, 1, "うずうず…その指、\nじゃれついても\nいいかにゃ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1609, 104601, 2, 1, "にゃ？　あたしに何か\nご入り用かにゃ？\nお安くしとくにゃ～♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1610, 104601, 3, 1, "うーん、なんだか\n退屈にゃ～\n遊んで、遊んで～？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1611, 104601, 4, 1, "素早い動きのコツ？\nタダじゃ教えて\nあげられないにゃ～♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1612, 104601, 101, 4, "そんなにあたしを\nなでたいにゃ？\nキミならなでさせて\nあげてもいいかにゃー", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1613, 104601, 102, 8, "ふにゃあ、ねえねえ、\n一緒にゴロゴロ\nしようにゃあ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1614, 104601, 1, 1, "いい朝にゃ！\n顔を洗ってくるにゃ！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1615, 104601, 2, 1, "むーん、そろそろ\nお昼…猫たちにも\n猫缶を…", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1616, 104601, 3, 1, "ふわあ…眠いにゃ…\n猫は布団で\n丸くなるにゃ", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1617, 104601, 1, 1, "客商売では、\n外見のチェックも\n欠かせないのにゃ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1618, 104601, 2, 1, "うにゃ～！\nお昼寝に\nぴったりにゃ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1619, 104601, 3, 1, "これ…量産したら\n子どもにバカ売れ\nしそうだにゃ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1620, 104601, 4, 1, "この灯り、まるで\n猫みたいな\n癒し効果にゃ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1621, 104601, 5, 1, "ふにゃあ、\nあったかいと猫は丸く\nなっちゃうのにゃあ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1622, 104601, 6, 1, "本当は外で\n運動したいん\nだけどにゃ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1623, 104601, 7, 1, "むーん、\n美味しいにゃー！\nたい焼きに\n匹敵するにゃ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1624, 104601, 8, 1, "ふむふむ…猫の\nしつけ方は、と…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1626, 104602, 1, 1, "かわいいネコの置物を\nゲットしたにゃ！\nどこに飾ろうか\nにゃ～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1627, 104602, 2, 1, "仕事の交渉がうまく\n行ったにゃ～♪\nいい気分だにゃ～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1628, 104602, 3, 1, "ねこじゃらしには\n食いついちゃうにゃ\nびしっ！　びしっ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1629, 104602, 4, 1, "ほっぺやお腹が、肉球\nぐらいぷにぷに…\nこれはまずいにゃ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1630, 104602, 5, 1, "さっきのたいやき\nおいしかったにゃ♪\nごろごろごろ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1631, 104602, 101, 8, "手作りのたい焼き\nあいつは食べて\nくれるかにゃ…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1632, 104602, 102, 8, "あいつには、いつか\nたっぷり恩返しが\nしたいにゃ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1633, 104602, 1, 1, "にゃーに？　あたしの\nことがそんなに気に\nなるのかにゃ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1634, 104602, 2, 1, "あたしのこと…\nしっかり捕まえ\nといてにゃ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1635, 104602, 3, 1, "あたしの力が\n必要かにゃ？　猫の手\n貸してあげるにゃ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1636, 104602, 4, 1, "たいやきとクレープを\n合わせれば…\n新しい商売のニオイが\nするにゃ…！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1637, 104602, 101, 8, "ふにゃあ、ねえねえ\n一緒にゴロゴロ\nしようにゃあ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1638, 104602, 102, 8, "キミと一緒にいると\n楽しくなってつい\nじゃれちゃうにゃ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1639, 104602, 1, 1, "いい朝にゃ！\n顔を洗ってくるにゃ！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1640, 104602, 2, 1, "そろそろおっひる～\nにゃーんか\n食べたい…", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1641, 104602, 3, 1, "うーん、眠くなって\nきたにゃ。あたしは\n夜行性じゃないのにゃ", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1642, 104602, 1, 1, "にゃにゃ…\n髪が乱れてるにゃ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1643, 104602, 2, 1, "ふっかふかにゃ～\n猫がねころん\nじゃうにゃ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1644, 104602, 3, 1, "これ、猫と匹敵する\nぐらいかわいいにゃ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1645, 104602, 4, 1, "猫でもこわくない\n優しい灯りにゃ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1646, 104602, 5, 1, "ぽっかぽか\nしあわせな気分にゃ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1647, 104602, 6, 1, "雨の日でも運動\nできるのは、濡れ\nなくて便利だにゃ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1648, 104602, 7, 1, "美味しいにゃ～！\nこれ、朝昼晩\n食べたいにゃ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1649, 104602, 8, 1, "本を読んで\n猫の気持ちを\n勉強するのにゃ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1651, 104801, 1, 1, "内職手伝える人\nいないかしら？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1652, 104801, 2, 1, "次の依頼は、\nこういう計画で…\nうん、完璧ね！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1653, 104801, 3, 1, "次の特売セールに\n向けて、しっかり\n準備しないと！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1654, 104801, 4, 1, "配達の後に売り子\n次に採取のバイト…\nうん、効率的ね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1655, 104801, 5, 1, "一日に受ける\nバイトの数、もう少し\n増やしたいわね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1656, 104801, 101, 4, "師匠として\n頼りにされたいし\nもっと頑張らなきゃ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1657, 104801, 102, 8, "男の子と効率的に\n仲良くなる方法…\nこればっかりは難題ね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1658, 104801, 1, 1, "あら、\nちょうどよかった\n今から一緒に\nバイトでもどう？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1659, 104801, 2, 1, "相談事かしら？\nそれくらいなら\n無料でいいわよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1660, 104801, 3, 1, "背後の気配なら\nすぐ分かるわ\n試してみる？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1661, 104801, 4, 1, "道具やお金の管理は\n大丈夫？　手伝うから\nいつでも言って", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1662, 104801, 101, 4, "暇ができたから\n働きたいけど…\nキミとの時間も大切ね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1663, 104801, 102, 8, "ねえ、あの…\n私はキミにとって…\nいや、何でもないわ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1664, 104801, 1, 1, "おはよう！\n今日も朝から\nバイトよ！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1665, 104801, 2, 1, "お昼時ね。\n少し休憩したら、\nまた働くわよ！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1666, 104801, 3, 1, "もうこんな時間！\n明日もバイトで\n朝早いのよ…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1667, 104801, 1, 1, "接客業の依頼が\nある時は、外見も\nキレイにしないとね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1668, 104801, 2, 1, "はぁ～バイトで\n疲れたから癒される～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1669, 104801, 3, 1, "これを内職で\n作ったら\n儲かるかしら？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1670, 104801, 4, 1, "リフレッシュできたわ\nこれでまた、バリバリ\n働けるわね！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1671, 104801, 5, 1, "風邪を引いたら効率が\n落ちちゃうから、\n暖めないと！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1672, 104801, 6, 1, "働き続けるには\n健康第一！　それには\n運動が一番よ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1673, 104801, 7, 1, "もぐもぐ…\n腹が減っては\n戦ができぬ、よ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1674, 104801, 8, 1, "日々絶えず学んで、\n生活に活かすのよ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1676, 104802, 1, 1, "家計簿をつけなきゃ！\n覚えている時に\nやっておかないとね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1677, 104802, 2, 1, "次の依頼は\nこういう計画で…\nうん、完璧ね！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1678, 104802, 3, 1, "ギルドの経費\nあとちょっとだけ\n削れそうね…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1679, 104802, 4, 1, "少しでも時給のいい\nバイトがしたいけど\nなかなか\n出会えないのよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1680, 104802, 5, 1, "今度のバイトも\nなかなか大変そう\nね…腕が鳴るわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1681, 104802, 101, 8, "男の子と効率的に\n仲良くなる方法…\nこればっかりは難題ね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1682, 104802, 102, 8, "私、どうしたんだろ？\n仕事中もずっと\n彼のこと考えてる…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1683, 104802, 1, 1, "この間はヘルプに\n来てくれてありがとう\n頼んで正解だったわ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1684, 104802, 2, 1, "見つけた！　ちょうど\n頼みたいことが\nあるんだけど、いい？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1685, 104802, 3, 1, "今から\n休憩するところなの\n一緒にどう？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1686, 104802, 4, 1, "ん？　どうしたの？\n私のことが\n気になるの？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1687, 104802, 101, 8, "…たまには甘えても\nいい？　私だって\n弱る時はあるのよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1688, 104802, 102, 8, "暇ができたから\n働きたいけど…\nキミとの時間も大切ね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1689, 104802, 1, 1, "おはよう！\nいい朝ね。一緒に\nランニングでもいく？", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1690, 104802, 2, 1, "お昼時ね\n少し休憩したら\nまた働くわよ！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1691, 104802, 3, 1, "もうこんな時間！\n明日もバイトで\n朝早いのよ…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1692, 104802, 1, 1, "女の子らしく…\nどうも忘れちゃう\nのよね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1693, 104802, 2, 1, "ここで少し\n休んだら\nまたバイトよ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1694, 104802, 3, 1, "これを内職で\n作ったら\n儲かるかしら？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1695, 104802, 4, 1, "仕事から帰ってきて\nこういうのがあると\n素敵ね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1696, 104802, 5, 1, "しっかり身体を\n温めて、血流を\nよくしなくっちゃ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1697, 104802, 6, 1, "これで休憩中も\n無駄なく\n鍛えられるわ♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1698, 104802, 7, 1, "もぐもぐ…よく\n食べて、よく働く！\nそれが私のモットーよ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1699, 104802, 8, 1, "日々絶えず学んで\n生活に活かすのよ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1701, 104901, 1, 1, "暇だし、お料理と\nお掃除とお洗濯と\n買い物しようかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1702, 104901, 2, 1, "こうして歩いていれば\n弟くんが声をかけて\nくれるはず…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1703, 104901, 3, 1, "弟くんがどんな生活を\nしてるのか調べるのも\nお姉ちゃんの務め！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1704, 104901, 4, 1, "くんくん、\n弟くんのにおい…\n近くにいるね？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1705, 104901, 5, 1, "お姉ちゃんの痕跡を\n部屋中に\n残しちゃおっと♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1706, 104901, 101, 4, "この部屋を、\n私と弟くんの\n愛の巣に…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1707, 104901, 102, 8, "はぁ～…弟くん\n禁断症状だよ～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1708, 104901, 1, 1, "弟くんの部屋は\nお姉ちゃんの\n部屋だよね～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1709, 104901, 2, 1, "いいこいいこ～♪\nどう、お姉ちゃんの\nナデナデは？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1710, 104901, 3, 1, "姉と弟の外出は\nデートって\nいうのかな？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1711, 104901, 4, 1, "もう！\nイタズラする子には\nお姉ちゃんチョップ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1712, 104901, 101, 4, "お姉ちゃんが\nぎゅ～ってして\nあげるね♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1713, 104901, 102, 8, "あ、あのね…\nたまにはお姉ちゃんも\n甘えていい？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1714, 104901, 1, 1, "着替えは？　荷物は？\nお出かけの準備は\nお姉ちゃんが手伝うね", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1715, 104901, 2, 1, "出かけるなら\n明るいうちに\n帰ってくるんだよ！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1716, 104901, 3, 1, "まだ起きてるの？\nそれじゃあ、\nお姉ちゃんも\n夜更かししちゃお☆", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1717, 104901, 1, 1, "きれいなお姉ちゃん\n好きだよね♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1718, 104901, 2, 1, "ここにお姉ちゃんの\n匂いを、た～っぷり\n残しておこうっと♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1719, 104901, 3, 1, "かわいい♪\nお姉ちゃんチョップ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1720, 104901, 4, 1, "この香り、\n弟くん好きかな？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1721, 104901, 5, 1, "これ、弟くんみたいに\nあったかいね～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1722, 104901, 6, 1, "ダイエットダイエット\nきれいなお姉ちゃんに\nならなくちゃ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1723, 104901, 7, 1, "あ、これ\n弟くんが好きな\n味付けだね！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1724, 104901, 8, 1, "弟くんが読んでいいか\nお姉ちゃんが先に\n中を確認しなくちゃ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1726, 104902, 1, 1, "暇だし、お料理と\nお掃除とお洗濯と\n買い物しようかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1727, 104902, 2, 1, "こうして歩いていれば\n弟くんが声をかけて\nくれるはず…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1728, 104902, 3, 1, "弟くんのお友達は\nみんな私の妹だよ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1729, 104902, 4, 1, "弟くんがどんな生活を\nしてるのか調べるのも\nお姉ちゃんの務め！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1730, 104902, 5, 1, "はっ！\n弟くんが困ってる\n気がする！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1731, 104902, 101, 8, "はぁ～…弟くん、\n禁断症状だよ～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1732, 104902, 102, 8, "お姉ちゃんの痕跡を\n部屋中に\n残しちゃおっと♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1733, 104902, 1, 1, "お姉ちゃん\nキミのお願いなら\nなんでも聞いて\nあげちゃうぞっ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1734, 104902, 2, 1, "弟くんの部屋は\nお姉ちゃんの\n部屋だよね～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1735, 104902, 3, 1, "お姉ちゃんの胸に\n飛び込んでおいでっ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1736, 104902, 4, 1, "いいこいいこ～♪\nどう、お姉ちゃんの\nナデナデは？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1737, 104902, 101, 8, "あ、あのね…\nたまにはお姉ちゃんも\n甘えていい？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1738, 104902, 102, 8, "お風呂にする？\nごはんにする？\nそれとも…うふふ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1739, 104902, 1, 1, "着替えは？　荷物は？\nお出かけの準備は\nお姉ちゃんが手伝うね", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1740, 104902, 2, 1, "お姉ちゃんが\nお昼ごはん\n作ってあげようか？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1741, 104902, 3, 1, "眠れないの？\nお姉ちゃんが子守歌\n歌ってあげるね！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1742, 104902, 1, 1, "弟くんを\n誘惑しちゃうぞ～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1743, 104902, 2, 1, "ここにお姉ちゃんの\n匂いを、た～っぷり\n残しておこうっと♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1744, 104902, 3, 1, "かわいい♪\nお姉ちゃんチョップ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1745, 104902, 4, 1, "お肌に潤いが\nでてきたみたい～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1746, 104902, 5, 1, "これ、弟くんみたいに\nあったかいね～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1747, 104902, 6, 1, "ダイエットダイエット\nきれいなお姉ちゃんに\nならなくちゃ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1748, 104902, 7, 1, "弟くんの手作りなら\nもっとおいしくなると\n思うんだけどなぁ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1749, 104902, 8, 1, "弟くんをきちんと\n教育するために\nお姉ちゃんは勉強を\nかかさないのだ☆", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1751, 105001, 1, 1, "ん～、エレガントで\n大人なお部屋ね！\nアタシにぴったり！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1752, 105001, 2, 1, "身長とバスト以外は\n完璧な大人\nなのになぁ～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1753, 105001, 3, 1, "アタシの大人コーデで\nあいつも夢中ね♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1754, 105001, 4, 1, "朝顔の観察したら\n理系女子になれる\nかしら？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1755, 105001, 5, 1, "レディは、どんな時も\nおしとやかに…って\nアタシも騒ぎたいー！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1756, 105001, 101, 4, "大人っぽい子が\n多いわね…アタシも\n負けてられないわ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1757, 105001, 102, 8, "アタシが待ってる\nなんて、アイツは\n幸せものよねー", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1758, 105001, 1, 1, "わーい、アメだー♪\n食べる食べるー♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1759, 105001, 2, 1, "熱いハニーラテが\n飲みたーい！\n淹れてー淹れてー！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1760, 105001, 3, 1, "なによ？　もしかして\n好きな子にイタズラ\nしたくなるってやつ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1761, 105001, 4, 1, "アタシに合う服\n雑誌で探そうっと！\nサイズじゃないわ、\nデザインとかの話よ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1762, 105001, 101, 4, "ねーねー！\nアタシ、かわいい？\nかわいいわよね？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1763, 105001, 102, 8, "これから出かけるの？\nしっかりアタシを\nエスコートしてね！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1764, 105001, 1, 1, "朝のスキンケアは\n大事なの！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1765, 105001, 2, 1, "お腹すいたー\nダイエット\nやめちゃおうかなー", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1766, 105001, 3, 1, "エイジングケアを\nしないとねー", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1767, 105001, 1, 1, "この角度のほうが\n大人っぽく見えるかな\nそれともこっちかしら", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1768, 105001, 2, 1, "あ！　これ雑誌で\n見たことあるわ～\nだからきっと\nオシャレなのよ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1769, 105001, 3, 1, "か、かわいい♪　けど\n大人はこんなもの…\nでも、かわいいし～！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1770, 105001, 4, 1, "オシャレ～♪\n大人なレディって\n感じよね♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1771, 105001, 5, 1, "あったかーい♪\nここで宿題\nしようかしら？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1772, 105001, 6, 1, "運動したら成長\nしないかしら？\n背とか胸とか…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1773, 105001, 7, 1, "食べすぎると\n太っちゃうな…\nでも、おいしいし～っ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1774, 105001, 8, 1, "おもしろーい！\n…でも絵本って\n子供っぽいかしら？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1776, 105002, 1, 1, "レディは、どんな時も\nおしとやかに…って\nアタシも騒ぎたいー！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1777, 105002, 2, 1, "恋人にするなら\nイケメンか\nただイケよね～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1778, 105002, 3, 1, "もう少し背が欲しいな\n…もっと牛乳を\n飲まなくちゃ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1779, 105002, 4, 1, "朝顔の観察したら\n理系女子になれる\nかしら？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1780, 105002, 5, 1, "こ、これは補習の\nお知らせプリント！\nアイツに見つかる前に\n隠さなくちゃ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1781, 105002, 101, 8, "アイツおっそいなー\n別に会いたいわけじゃ\nないけど…\n来ないと気になる…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1782, 105002, 102, 8, "アタシが待ってる\nなんて、アイツは\n幸せものよねー ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1783, 105002, 1, 1, "なーに？\nアタシに甘えたいの？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1784, 105002, 2, 1, "アタシに合う服\n雑誌で探そうっと！\nサイズじゃないわ\nデザインとかの話よ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1785, 105002, 3, 1, "頭撫でないで！\nお子様みたいに\nあつかわないでって\n言ってるのに～！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1786, 105002, 4, 1, "壁ドンの練習…\nあれ？　壁ドンって\n男がするんだっけ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1787, 105002, 101, 8, "ねーねー！\nアタシ、かわいい？\nかわいいわよね？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1788, 105002, 102, 8, "これから出かけるの？\nしっかりアタシを\nエスコートしてね！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1789, 105002, 1, 1, "レディの朝は\n忙しいのよ", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1790, 105002, 2, 1, "お腹すいたー\nダイエット\nやめちゃおうかなー", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1791, 105002, 3, 1, "エイジングケアを\nしないとねー", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1792, 105002, 1, 1, "身だしなみチェック\nオッケー！　これで\nいつアイツが来ても\n大丈夫ね！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1793, 105002, 2, 1, "あ！　これ雑誌で\n見たことあるわ～\nだからきっと\nオシャレなのよ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1794, 105002, 3, 1, "あたしがもっと\nオシャレにかわいく\nしてあげようか？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1795, 105002, 4, 1, "オシャレ～♪\n大人なレディって\n感じよね♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1796, 105002, 5, 1, "あったかーい♪\nここで宿題\nしようかしら？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1797, 105002, 6, 1, "運動したら成長\nしないかしら？\n背とか胸とか…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1798, 105002, 7, 1, "レディは、食べ物にも\n気を使うのよ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1799, 105002, 8, 1, "おもしろーい！\n…でも絵本って\n子供っぽいかしら？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1801, 105101, 1, 1, "魔法と科学\nどっちも極めて\nみせるわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1802, 105101, 2, 1, "仕事も片付いたし\n部屋で\n実験実験…♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1803, 105101, 3, 1, "たまった治療代の\n請求書を\nまとめないと…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1804, 105101, 4, 1, "新薬が出来たし\nすぐに試したいけど…\n実験台がいないわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1805, 105101, 5, 1, "部屋にこもりすぎると\n曜日の感覚が\nなくなるわね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1806, 105101, 101, 4, "あの子の記憶を\n取り戻せる薬…\n作れないものかしら", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1807, 105101, 102, 8, "好意に気づいて貰える\n薬って、どうすれば\n作れるのかしら", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1808, 105101, 1, 1, "あら、ヒマなら\n薬草の整理を\n手伝って", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1809, 105101, 2, 1, "もしかして実験に\n付き合ってくれるの？\nえ？　違う？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1810, 105101, 3, 1, "具合が悪い？\nなら診てあげる\nまずは触診から…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1811, 105101, 4, 1, "植物に興味あるの？\nなら教えてあげるわ\n色々とね…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1812, 105101, 101, 4, "キミの傍にいるだけで\n高揚しちゃうのは\n何故かしら…？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1813, 105101, 102, 8, "私はおしおきする\n側のことが多いけど…\nキミにはされたいかも", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1814, 105101, 1, 1, "お昼までに調合用の\n花を集めたいの\n手伝ってくれる？", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1815, 105101, 2, 1, "あら、もうお昼？\nそろそろ一息\n入れましょうか", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1816, 105101, 3, 1, "夜も更けて来たわね\n私は朝まで実験だけど\n見学していく？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1817, 105101, 1, 1, "医者たるもの\nいつだって\n清潔に…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1818, 105101, 2, 1, "いいわねこれ\n仮眠の時に\n使わせてもらうわ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1819, 105101, 3, 1, "ぬいぐるみなら\n動物も…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1820, 105101, 4, 1, "いい香り…\n仕事場にも\n置いてみようかしら", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1821, 105101, 5, 1, "ここにいると\nついウトウト\nしちゃうわ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1822, 105101, 6, 1, "座ってる時間が多いと\n運動不足になるし\nたまにはいいわね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1823, 105101, 7, 1, "研究していると\nつい食べるのを\n忘れちゃうのよね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1824, 105101, 8, 1, "実験もいいけど\nいつか成果を\n本にしないと…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1826, 105102, 1, 1, "徹夜はツラいけど\n集中すると時間の\n感覚がなくなって…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1827, 105102, 2, 1, "痛みに悶える顔って\nどうしてあんなに\n興奮するのかしら？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1828, 105102, 3, 1, "彼に罵られたら\nどんな気分かしら\n意外と快感かも？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1829, 105102, 4, 1, "毒を薄めると\n薬になるのって\n今でも不思議だわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1830, 105102, 5, 1, "綺麗なバラには\nトゲがあるけど\n魅力の一つよね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1831, 105102, 101, 8, "カレをさりげなく\n惚れさせる方法を\n研究するわよ…♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1832, 105102, 102, 8, "好意に気づいて貰える\n薬って、どうすれば\n作れるのかしら", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1833, 105102, 1, 1, "構って欲しいの？\nじゃあ、君の体を\n調べさせて？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1834, 105102, 2, 1, "実験に付き合って\n大丈夫よ、試すのは\nただの栄養剤だから", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1835, 105102, 3, 1, "欲しい薬が\nあったら言ってね\n調合してみるから", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1836, 105102, 4, 1, "おしおきされたいの？\nキミにだったら\nいつでもしてあげる", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1837, 105102, 101, 8, "キミの傍にいるだけで\n高揚しちゃうのは\n何故かしら…？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1838, 105102, 102, 8, "私はおしおきする\n側のことが多いけど…\nキミにはされたいかも", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1839, 105102, 1, 1, "朝しか取れない\n植物があるの\n採取しに行くわよ", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1840, 105102, 2, 1, "お昼時ってやけに\n眠たくなるわよね\n…調べたくなるわ", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1841, 105102, 3, 1, "今日くらいは\nゆっくり眠りたいわ\n…キミと一緒にね", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1842, 105102, 1, 1, "身なりについて\n研究するのも\n楽しそうね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1843, 105102, 2, 1, "休息に必要な道具は\n妥協せずに選ぶ！\nこれだけは譲れないわ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1844, 105102, 3, 1, "たまにはこういう\n可愛らしいものに\n触れないとね♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1845, 105102, 4, 1, "いい香り…\n仕事場にも\n置いてみようかしら", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1846, 105102, 5, 1, "仕事がなかったら\nこのまますーっと\n眠りたいのに…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1847, 105102, 6, 1, "運動しないと\n体型の維持が…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1848, 105102, 7, 1, "適度な食事も\n大切よね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1849, 105102, 8, 1, "まさか私が\n恋愛読本を読む\n日が来るなんて…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1851, 105201, 1, 1, "ぺっぺっ！\n…い、いけないっ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1852, 105201, 2, 1, "いっけな～い！\nひづめで床に傷が！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1853, 105201, 3, 1, "太っちゃったかも～！\n伐採でダイエット\nしなくっちゃ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1854, 105201, 4, 1, "きちんと生活すると\n毛づやが良くなる\nみたいなのよね～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1855, 105201, 5, 1, "みんな、体重\nどれくらいなのかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1856, 105201, 101, 4, "体力なら自信\nあるんだけど…\n恋にはちょっぴり\n臆病になっちゃう", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1857, 105201, 102, 8, "いつか人間の女の子\nみたいなデート、\nしてみたいな～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1858, 105201, 1, 1, "私ね「どこにでもいる\n普通の女の子」って\n言葉が憧れなんだ～♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1859, 105201, 2, 1, "チャームポイント？\n自分では分からない\nけど…まつげかなぁ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1860, 105201, 3, 1, "リマリマ～♪\nはっ！？\nわ、私ったら今何を？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1861, 105201, 4, 1, "力仕事ならまかせて！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1862, 105201, 101, 4, "人間になりたいな～\nそれか、あなたが\n私みたいな姿に…♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1863, 105201, 102, 8, "しょうがないなあ、\nこんなにモフらせるの\nあなただけだよ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1864, 105201, 1, 1, "今朝は寝癖がひどいわ\nしっぽの辺りとか～", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1865, 105201, 2, 1, "ランチは\nおしゃれなカフェで\nしてみたいな～♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1866, 105201, 3, 1, "お肌のケアに\nパックしながら\n寝ようかな～", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1867, 105201, 1, 1, "やだっ、\nまた寝ぐせついてる！\n直さないと！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1868, 105201, 2, 1, "ミシッっていった！\n私、重すぎるかも！？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1869, 105201, 3, 1, "あ！　あなたも\n美人さんね～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1870, 105201, 4, 1, "はぁ、まったり…\n四足歩行しちゃい\nそうだわ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1871, 105201, 5, 1, "あったか～い♪\n…私、毛皮だから\n暑すぎるかも", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1872, 105201, 6, 1, "な、なんだか野生に\n帰っちゃいそう！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1873, 105201, 7, 1, "ぱくぱく、もぐもぐ、\nぺっぺっ…\nやだ、はしたない！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1874, 105201, 8, 1, "きゃーーー！\nこの恋愛小説\nとっても\nドキドキしちゃう～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1876, 105202, 1, 1, "素敵ね～、居るだけで\n女子力があがりそうな\nお部屋だわ～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1877, 105202, 2, 1, "私、大丈夫かしら？\n他の子に怖がられたり\nしないかな…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1878, 105202, 3, 1, "動物苑…って\n嫌なギルド名ね…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1879, 105202, 4, 1, "好き、嫌い、好き…\nあっ！　うっかりして\n花占いのお花\n食べちゃった！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1880, 105202, 5, 1, "ここ、女の子らしい\nものでいっぱいね～！\nときめいちゃうわ～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1881, 105202, 101, 8, "体力なら自信\nあるんだけど…\n恋にはちょっぴり\n臆病になっちゃう", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1882, 105202, 102, 8, "あの人が\n来てくれるまで薪割り\nでもしてよっかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1883, 105202, 1, 1, "一緒にお出かけ？\nうれしいけど…\n心の準備が！\nはわわわ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1884, 105202, 2, 1, "どうしたの？\n急に抱きついてきて…\n癒されたいの？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1885, 105202, 3, 1, "ぺっぺっ！\n…い、いけないっ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1886, 105202, 4, 1, "メタモルアップルを\nここで食べたら、\nみんな驚くわよね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1887, 105202, 101, 8, "しょうがないなあ、\nこんなにモフらせるの\nあなただけだよ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1888, 105202, 102, 8, "人間になりたいな～\nそれか、あなたが\n私みたいな姿に…♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1889, 105202, 1, 1, "今朝は寝癖がひどいわ\nしっぽの辺りとか～", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1890, 105202, 2, 1, "ダイエット中だから\nお昼は抜いてるの～", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1891, 105202, 3, 1, "お肌のケアに\nパックしながら\n寝ようかな～", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1892, 105202, 1, 1, "ちゃんとかわいく\nしてないと刈られ…\n狩られちゃうもんね！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1893, 105202, 2, 1, "ミシッっていった！\n私、重すぎるかも！？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1894, 105202, 3, 1, "あ！　あなたも\n美人さんね～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1895, 105202, 4, 1, "いい香り～\n森の中にいるみたいに\nとっても落ち着く～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1896, 105202, 5, 1, "素敵～♪　…\nきゃっ！　私の毛が\nたいへんなことに～！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1897, 105202, 6, 1, "な、なんだか野生に\n帰っちゃいそう！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1898, 105202, 7, 1, "何を食べるかより\n誰と食べるかが\n大事なのよね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1899, 105202, 8, 1, "きゃーーー！\nこの恋愛小説\nとっても\nドキドキしちゃう～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1901, 105301, 1, 1, "ヴァイスフリューゲル\nを盛り上げる方法、\n共に語らおう！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1902, 105301, 2, 1, "私は子供ではない…！\nが、お菓子は\nいただこう！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1903, 105301, 3, 1, "部屋はきちんと\n片づけておかなくては\n部屋の汚れは\n心の汚れだ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1904, 105301, 4, 1, "むむむ…明日\n目が覚めたら、身長が\n伸びていないものか…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1905, 105301, 5, 1, "お菓子が常備されて\nいる部屋。そんな\nものが、私の好みだ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1906, 105301, 101, 4, "あいつとの新たな\n鍛錬が\n待ち遠しいな…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1907, 105301, 102, 8, "最近、あいつと\n過ごしていて、毎日が\n充実しているな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1908, 105301, 1, 1, "軍人の本分は日々の\n研鑽にあるからな。\n今日も鍛錬に\n行かないか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1909, 105301, 2, 1, "毎日牛乳を飲んでいる\nのだが、なかなか背が\n伸びないものだな…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1910, 105301, 3, 1, "貴公にも、飴をくれて\nやろうか？\nあまいぞ～！\n美味しいぞ～！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1911, 105301, 4, 1, "私ともっと高く、\n共に飛ぼう！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1912, 105301, 101, 4, "貴公は、本当に頼りに\nなる男だな…いや、\nふとそう思っただけだ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1913, 105301, 102, 8, "貴公と共にいられる\nことは私の喜びだ。\nこれからも\nよろしく頼むぞ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1914, 105301, 1, 1, "うむ、いい朝だ。\n本国の夜明けを\n思い出してしまうな！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1915, 105301, 2, 1, "そろそろ昼食に\nするか。腹が減っては\n戦はできないからな", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1916, 105301, 3, 1, "ふわああ…私は子供\nではないが…眠い時は\n眠いのだ…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1917, 105301, 1, 1, "身だしなみは、心の\n在りようを映すから\n気をつけなくてはな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1918, 105301, 2, 1, "ふう…軍人たるもの\n常日頃、油断は\nできないのだが…\nお、落ち着くな…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1919, 105301, 3, 1, "うむ…このかわいさ…\n本国に持って\n帰りたい…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1920, 105301, 4, 1, "このともし火、戦いに\n明け暮れる我らの心を\n癒すものだな…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1921, 105301, 5, 1, "はあ～、ぽかぽか…\nい、いや！\n気を緩めないぞっ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1922, 105301, 6, 1, "うおおおおおっ！\n鍛錬、鍛錬、\n鍛錬っ！！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1923, 105301, 7, 1, "いつ戦いになっても\n困らぬように、\n腹八分目に\nしておくのだ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1924, 105301, 8, 1, "これを読んで、新たな\n戦術を編み出すぞっ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1926, 105302, 1, 1, "たまには掃除でも\nするか。部屋が\n綺麗になるのは\n喜ばしいことだ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1927, 105302, 2, 1, "部屋の中では\n剣を振り回せないのが\n玉に瑕だな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1928, 105302, 3, 1, "お菓子が常備されて\nいる部屋。そんな\nものが、私の好みだ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1929, 105302, 4, 1, "毎日牛乳を飲んでいる\nのだが、なかなか背が\n伸びないものだな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1930, 105302, 5, 1, "家庭を守れない者が\n国を守れるはずが\nない。この部屋も\n私が守るぞ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1931, 105302, 101, 8, "あいつは今頃、何を\nしているのだろうな…\nふふっ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1932, 105302, 102, 8, "あいつと会える日が\nこんなにも待ち遠しく\nなるとはな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1933, 105302, 1, 1, "軍人の本分は日々の\n研鑽にあるからな\n今日も鍛錬に\n行かないか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1934, 105302, 2, 1, "おお、今日も鍛錬の\n時間だったか！\nすまないな\nすぐ行く！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1935, 105302, 3, 1, "私は子供ではない！\nが…たまには童心に\n帰りたい時もある…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1936, 105302, 4, 1, "貴公にも、飴を\nくれてやろうか？\nあまいぞ～！\n美味しいぞ～！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1937, 105302, 101, 8, "私と歩きたいのか…？\nそうだな。二人で\n散歩でも行こうか", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1938, 105302, 102, 8, "貴公と共にいられる\nことは私の喜びだ\nこれからも\nよろしく頼むぞ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1939, 105302, 1, 1, "うむ、いい朝だ\n本国の夜明けを\n思い出してしまうな！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1940, 105302, 2, 1, "そろそろ昼時だな\n鍛錬の手を緩めて\n昼食にするか", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1941, 105302, 3, 1, "寝る前に…あと少し\nだけ、鍛錬を…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1942, 105302, 1, 1, "ヴァイス\nフリューゲルの一員と\nして恥ずかしくないよう\n身なりを整えなくては", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1943, 105302, 2, 1, "ふう…軍人たるもの\n常日頃、油断は\nできないのだが…\nお、落ち着くな…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1944, 105302, 3, 1, "うむ…このかわいさ…\n本国に持って\n帰りたい…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1945, 105302, 4, 1, "このともし火\n戦いに明け暮れる\n我らの心を\n癒すものだな…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1946, 105302, 5, 1, "はあ～、ぽかぽか…\nい、いや！\n気を緩めないぞっ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1947, 105302, 6, 1, "うおおおおおっ！\n鍛錬、鍛錬\n鍛錬っ！！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1948, 105302, 7, 1, "いつ戦いになっても\n困らぬように\n腹八分目に\nしておくのだ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1949, 105302, 8, 1, "うむ…脳細胞が\n活性化してきたぞ\n読書は大事だな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1951, 105701, 1, 1, "私たちの騎空艇\nどんなデザインに\nしよう…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1952, 105701, 2, 1, "団員募集の\nポスター\n作ろうっと♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1953, 105701, 3, 1, "父さん…\n今頃どこで\n何してるんだろ？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1954, 105701, 4, 1, "騎空艇に乗って、\n誰もいったことがない\n場所を見つけたいな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1955, 105701, 5, 1, "父さん以上の\n世界一の\n騎空士になりたい！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1956, 105701, 101, 4, "仲間と行く空の旅…\n何度想像しても\nドキドキするなぁ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1957, 105701, 102, 8, "父さん…\n私にもね、大切な\n仲間ができたよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1958, 105701, 1, 1, "仕事の依頼？\nすぐに支度\nしてくるね！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1959, 105701, 2, 1, "ねえねえ！\n団員募集のポスター\n一緒に作ろうよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1960, 105701, 3, 1, "なーにー？\nどうしたの？\n今呼んだよね？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1961, 105701, 4, 1, "今は休憩中だよ？\nだからあなたも\nゆっくりしてて", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1962, 105701, 101, 4, "私にとってあなたは\nとっても頼りになる、\n自慢の団員だよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1963, 105701, 102, 8, "お父さんに会えたら、\n大切な仲間として\nあなたを紹介するね！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1964, 105701, 1, 1, "おはよう！\n体の調子はどう？\n今日は頑張れそう？", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1965, 105701, 2, 1, "ねえねえ、お昼\n食べに行こうよ！\nお腹空いちゃった", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1966, 105701, 3, 1, "…あれ？\nおしゃべりしてたら\nもうこんな時間？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1967, 105701, 1, 1, "どうせならオシャレな\n騎空士って\n呼ばれたいな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1968, 105701, 2, 1, "ふぅ、疲れた～…\nちょっとだけ\n休んじゃおっと", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1969, 105701, 3, 1, "よし！　キミは\n今から団長代理だ！\n…なんてね♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1970, 105701, 4, 1, "いい香り～♪\n気持ちが\n落ち着く～…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1971, 105701, 5, 1, "これって\n騎空艇に\n置けるかな…？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1972, 105701, 6, 1, "どんな仕事でも\nこなせるような\n体力がないとね…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1973, 105701, 7, 1, "あむっ…うん！\n美味しい～！\n今度作ってみよう♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1974, 105701, 8, 1, "空を冒険するには\n色んなことを\n知っておかないと…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1976, 105702, 1, 1, "…戻らなくなったよ\nどうすれば元に\n戻る…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1977, 105702, 2, 1, "暇だよ…\nどこかに強い人は\nいないか？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1978, 105702, 3, 1, "ジョブチェンジ！\n…叫んでも\n戻らないよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1979, 105702, 4, 1, "今のうちに、\n魔物退治の\n仕事に行くね！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1980, 105702, 5, 1, "ハァ～…\nうん、気の巡りは\n問題ないね！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1981, 105702, 101, 8, "私の力の源は\n仲間との\n絆だよ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1982, 105702, 102, 8, "いつかこの拳を\n空を越えた、星まで\n届かせたいね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1983, 105702, 1, 1, "功夫が足りない人は\nどうすればいいか？\nもちろん修行だよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1984, 105702, 2, 1, "私はいつも通りに\n喋ってるつもり\nだよ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1985, 105702, 3, 1, "どうした？\nもしかして手合せ\nしたいか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1986, 105702, 4, 1, "騎空士としても\n拳法家としても\n目指すは世界一ね！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1987, 105702, 101, 8, "背中はあなたに\n預けたよ！\nよろしくね！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1988, 105702, 102, 8, "あなたと一緒なら\nきっと、今よりもっと\n強くなれるよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1989, 105702, 1, 1, "ちゃんと起きてるか？\n寝ぼけてたら\nダメよ？", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1990, 105702, 2, 1, "腹減ったね…\nなにか食べに\n行くよ！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1991, 105702, 3, 1, "修行のしすぎは\n良くないよ？\n気をつけるね", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1992, 105702, 1, 1, "美しい技は\n美しい体が\nあってこそ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1993, 105702, 2, 1, "よいしょっと…\nちょっとのんびり\nさせてもらうよ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1994, 105702, 3, 1, "…練習に\n使うのはさすがに\n可哀そうね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1995, 105702, 4, 1, "これは…\n瞑想の時にも\n使ってみたいね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1996, 105702, 5, 1, "出来ればもっと\n熱いほうが\n嬉しいよ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1997, 105702, 6, 1, "ここでも\n修行出来るなんて\n最高だよ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1998, 105702, 7, 1, "気力の源は体力！\n体力の源は\n健康とご飯！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1999, 105702, 8, 1, "んー…\n難しいことは\nわかんないよ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2001, 105801, 1, 1, "わたしたちの\nギルドハウスへ\nようこそ～！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2002, 105801, 2, 1, "ぐるるる～…\nあ、お腹が\n鳴っちゃいました…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2003, 105801, 3, 1, "ん？\n食べ物のにおい？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2004, 105801, 4, 1, "みんなと仲良く食卓を\n囲みたいですね～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2005, 105801, 5, 1, "この部屋が、わたしの\n帰る場所です♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2006, 105801, 101, 4, "あの人を見ると\n胸が満たされます～\nお腹は空いてますが！", 1, 0, 1, 0);
