INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2101, 105901, 1, 1, "この空間は主さまの\n香りがします\n…ふふふ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2102, 105901, 2, 1, "炊事、洗濯、お掃除、\nお買いものもしなくて\nはいけませんね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2103, 105901, 3, 1, "アメスさまへの、\nお祈りができそうな\n場所は…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2104, 105901, 4, 1, "さすが都会…\nおいしいものが\nたくさんあるのですね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2105, 105901, 5, 1, "主さまが不自由なく\n使える部屋に\nしなくては…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2106, 105901, 101, 4, "できるなら…\n主さまと二人だけの\n部屋の方が…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2107, 105901, 102, 8, "主さまの隣には\nわたくしが…\nわたくしの隣には、\n主さまが…幸せです", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2108, 105901, 1, 1, "わたくし、背が伸び\nましたでしょうか？\n…そうですか", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2109, 105901, 2, 1, "もしお疲れでしたら\n肩でも\nお揉みしましょうか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2110, 105901, 3, 1, "主さま、なにか\nしてほしいことは\nありませんか？\n欲しいものは？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2111, 105901, 4, 1, "今日のおこづかいです\n何かおいしいものを\n召し上がってください", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2112, 105901, 101, 4, "主さまのおてて\n温かいですね…♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2113, 105901, 102, 8, "主さまのおそばに\nいられて、わたくし、\n嬉しいです…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2114, 105901, 1, 1, "おはようございます。\n洗濯をしなければ…", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2115, 105901, 2, 1, "そろそろお昼ですね。\nおいしいものを\n食べましょう", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2116, 105901, 3, 1, "おやすみになる時は、\nお声をかけてください\nお供致します", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2117, 105901, 1, 1, "もう少し…\nおっきくなりたい\nです", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2118, 105901, 2, 1, "このふかふかの優しさ\n主さまのようです", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2119, 105901, 3, 1, "かわいい♪\n主さまにそっくり♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2120, 105901, 4, 1, "故郷の香りとは\n違いますが、これも\n心地よいものです…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2121, 105901, 5, 1, "ここで洗濯物を\n乾かせるでしょうか", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2122, 105901, 6, 1, "都会の人々は\nこんなふうに\n運動を…！？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2123, 105901, 7, 1, "これは…主さまにも\n食べさせて\n差し上げたい…\nあーんって♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2124, 105901, 8, 1, "よく学んで、\n主さまのお役に\n立たなければ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2126, 105902, 1, 1, "主さまがいつ戻って\n来ても平気なよう\n部屋を綺麗に保たねば", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2127, 105902, 2, 1, "節約のためにも\n自家菜園などするのは\nどうでしょう…　", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2128, 105902, 3, 1, "アメスさまへの\nお祈りができそうな\n場所は…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2129, 105902, 4, 1, "瞑想をすると\n心が安らぎます…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2130, 105902, 5, 1, "都会の方の身なりを\n勉強した方が\n良いのでしょうか？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2131, 105902, 101, 8, "わたくしの居場所は\n主さまのお傍です\nずっと、ずっと…♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2132, 105902, 102, 8, "主さまの隣には\nわたくしが…\nわたくしの隣には\n主さまが…幸せです", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2133, 105902, 1, 1, "主さま、なにか\nしてほしいことは\nありませんか？\n欲しいものは？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2134, 105902, 2, 1, "わたくし、背が伸び\nましたでしょうか？\n…そうですか", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2135, 105902, 3, 1, "お出かけですか？\nわたくしもお供\nいたします", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2136, 105902, 4, 1, "ひゃん！\n主さま…\nいじわるです", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2137, 105902, 101, 8, "主さま、なにか御用は\nありませんか？\nわたくし、主さまの\nお役にたちたいのです", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2138, 105902, 102, 8, "主さまの優しさに\nわたくしはいつも\n癒されています…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2139, 105902, 1, 1, "おはようございます♪\n主さまの可愛らしい\n寝顔、堪能致しました", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2140, 105902, 2, 1, "のんびりした時間帯\nですね…ふぁ～…\nし、失礼致しました", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2141, 105902, 3, 1, "ふむ、都会は夜でも\nこんなにも、にぎやか\nなのですね", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2142, 105902, 1, 1, "主さまに会う前に\n身なりを整えなくては\nいけませんね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2143, 105902, 2, 1, "このふかふかの優しさ\n主さまのようです", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2144, 105902, 3, 1, "かわいい♪\n主さまにそっくり♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2145, 105902, 4, 1, "この心地よさ…\n主さまのようです♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2146, 105902, 5, 1, "ここで洗濯物を\n乾かせるでしょうか　", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2147, 105902, 6, 1, "はぁっはぁっはぁっ\n…すべては\n主さまのため…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2148, 105902, 7, 1, "都会の味…\nとてもおいしいです\n…もぐもぐもぐ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2149, 105902, 8, 1, "都会のことを\n学ばないと…\nふむふむ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2151, 190801, 1, 1, "ふぁ～…あっ\n見られちゃい\nましたか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2152, 190801, 2, 1, "ギルドハウスの\n使い心地は\nどうですか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2153, 190801, 3, 1, "ご用があれば\nなんでも言って\nくださいね～♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2154, 190801, 4, 1, "ときどきみなさんと\nクエストに行きたいな\nって思っちゃいます", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2155, 190801, 1, 1, "おはようございます！\nお掃除の方、\nはじめていきますね", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2156, 190801, 2, 1, "皆さん、お昼ごはんの\nご要望がありましたら\n用意致しますよ♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2157, 190801, 3, 1, "ここは夜でも\nにぎやかですね", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2158, 104701, 1, 1, "…\n少しうとうと\nしてしまった", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2159, 104701, 2, 1, "室内だし兜を脱ぐか\n…いや\nやはりやめておこう", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2160, 104701, 3, 1, "部屋にいると\n鎧が蒸し暑くなるが\n我慢せねば…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2161, 104701, 4, 1, "仕事が門番だからか\n部屋にいても\nじっとしてしまう…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2162, 104701, 5, 1, "少年と会話したことを\n思いだすと\n自然と楽しくなるな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2163, 104701, 101, 4, "少年のことを考えると\n顔がにやけてしまう", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2164, 104701, 102, 8, "少年は今頃\n平和に過ごせて\nいるかな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2165, 104701, 1, 1, "ん？　悪いが\n兜は脱がないぞ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2166, 104701, 2, 1, "私と遊びたいのか？\nそう言われても\n何をしていいやら…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2167, 104701, 3, 1, "鎧が重くないかって？\n大丈夫。けっこう\n鍛えているからな", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2168, 104701, 4, 1, "少年、寒くはないか？\nいや自分がこうなので\n人が薄着に見えてね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2169, 104701, 101, 4, "君といると、兜の中が\nいつもより暑いんだ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2170, 104701, 102, 8, "私としゃべりたい？\n物好きだな\nでも、嬉しいよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2171, 104701, 1, 1, "早いな少年\nえ？　私の仕事に\n合わせてくれたのか", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2172, 104701, 2, 1, "昼食の誘い？\n嬉しいけど\n人前では食べないんだ", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2173, 104701, 3, 1, "王宮騎士団の\n日報を書かなければ…\n『何事もなし』、と", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2175, 104701, 2, 1, "鎧を着ているから\n感触が分かりづらい\nのが残念だ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2176, 104701, 3, 1, "か、かわいいな…\n私には似合わないが\nぎゅ…ってしよう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2177, 104701, 4, 1, "良い香りだ…\n普段はどうしても\n鉄くさくなるからな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2178, 104701, 5, 1, "わかっていたけど\n鎧を着たまま暖炉に\nあたると暑い…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2180, 104701, 7, 1, "兜を脱がずに食べる\nうん、段々コツが\n掴めてきた…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2181, 104701, 8, 1, "兜があっても\n意外と本くらいは\n難なく読めるんだ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2183, 102601, 1, 1, "居心地よくて\nゴロゴロしちゃうよ～\n…いつも通りだね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2184, 102601, 2, 1, "ドングリの木\n近くに生えてない？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2185, 102601, 3, 1, "快適な寝床があれば\nそれだけで幸せだねぇ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2186, 102601, 4, 1, "あたしと一緒に\nだらけてくれる人が\nいそうだねぇ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2187, 102601, 5, 1, "ここなら牧場よりも\n速く配達が\n届きそうだね～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2188, 102601, 101, 4, "あんパン買ったし\nあいつにも\n分けてあげよう", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2189, 102601, 102, 8, "あいつと一緒に\nだらけられたら\n最高に幸せだね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2190, 102601, 1, 1, "ドングリでも\nつまみながら\n一緒にゲームしようよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2191, 102601, 2, 1, "横になりたいから\n枕になっておくれよ～\nあんたあったかいしさ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2192, 102601, 3, 1, "ドングリあげるから\nあんパン買ってきて～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2193, 102601, 4, 1, "う～\n疲れちゃったよ～\nおんぶして運んで～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2194, 102601, 101, 4, "あんたといると\nリア充って\n感じがするよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2195, 102601, 102, 8, "あんたが一生\n世話してくれたら\n人生安泰なんだけどね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2196, 102601, 1, 1, "ふわ～っ…\nおはよう…あたしは\nまだ眠いよ", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2197, 102601, 2, 1, "そろそろお昼寝でも\nしたい気分だねぇ", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2198, 102601, 3, 1, "徹夜でゲームするけど\nあんたもどうだい？\nあたしは強いよ", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2200, 102601, 2, 1, "あ…だめだこれ。\n気持ち良くて\n落ちそう…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2201, 102601, 3, 1, "かわいいものに\n囲まれて寝るのも\nよさそうだねぇ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2202, 102601, 4, 1, "心身ともに\nリラックスした状態で\nぐうたら出来そうだね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2203, 102601, 5, 1, "ふい～…\nごくらくごくらく…\n暖かいねぇ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2205, 102601, 7, 1, "食べてすぐ寝ると\n牛になるって\n本当なのかな？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2206, 102601, 8, 1, "おー！　この本なら\nあたしも居眠りせずに\n読めそうだよ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2208, 106301, 1, 1, "ランドソル…\nおっきな街だなぁ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2209, 106301, 2, 1, "ロザリア…\nきっと、いるよね？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2210, 106301, 3, 1, "最近、弓の練習\nサボっちゃってるな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2211, 106301, 4, 1, "今日の私は\n森番じゃなくて\n家番だよ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2212, 106301, 5, 1, "ちょっとだけ\n慣れてきたかも？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2213, 106301, 101, 4, "いつか、\nあの人と一緒に…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2214, 106301, 102, 8, "…なんか最近、\nあの人のこと\nばっかり…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2215, 106301, 1, 1, "もしよかったら\n街に出かけない？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2216, 106301, 2, 1, "この部屋、\n妖精さんも\n気に入るかも！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2217, 106301, 3, 1, "今日は\nなににしよっか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2218, 106301, 4, 1, "今日はどこ行く？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2219, 106301, 101, 4, "たまには二人で…\nな、なんちゃって！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2220, 106301, 102, 8, "最近、\nずっと楽しいんだ！\nあなたのおかげかな", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2221, 106301, 1, 1, "おはよう！\n今日も一緒に\n頑張ろうね！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2222, 106301, 2, 1, "お昼はどうする？\nあなたのオススメに\n連れて行って欲しいな", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2223, 106301, 3, 1, "ランドソルは\n夜でも賑やかだね！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2225, 106301, 2, 1, "わぁっ！\nすっごく\nふわふわだよ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2226, 106301, 3, 1, "かわい～！\nぎゅってしたく\nなっちゃうかも！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2227, 106301, 4, 1, "くんくん\nなんか…いい匂い？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2228, 106301, 5, 1, "すっごいなー\nあったかいなー！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2230, 106301, 7, 1, "わぁっ、おいしい！\nもっと食べたい\nかも…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2231, 106301, 8, 1, "も、文字は苦手…\nでも、頑張らないと\nだよね…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2233, 103601, 1, 1, "にんじんとピーマンを\n食べないでいい方法\nないかな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2234, 103601, 2, 1, "カップケーキが\n食べたいな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2235, 103601, 3, 1, "予習が終わりました！\nさて、次は復習を\nしないと…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2236, 103601, 4, 1, "あの人の部屋にいる\n私って…もしかして\nふしんしゃなんじゃ？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2237, 103601, 5, 1, "男の人は、みんな\nへんたいだと\n聞きました…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2238, 103601, 101, 4, "あの人に、一緒に\n遊んでほしいけど…\nな、なんていえば？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2239, 103601, 102, 8, "早く大人になりたいな\n…あの人と同じくらい", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2240, 103601, 1, 1, "ぼ、防犯魔石を\n鳴らしますよ！？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2241, 103601, 2, 1, "へんたい！？　…\nあ、あなたでしたか～\nやっぱりへんたーい！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2242, 103601, 3, 1, "これ宿題です\nやっておいて\nくださいね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2243, 103601, 4, 1, "あ、遅刻ですよ\n待っていたんですから", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2244, 103601, 101, 4, "にらめっこですか？\nあ、あなたの前で変顔\nするのはちょっと…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2245, 103601, 102, 8, "あの…何歳まで\n子供だと思いますか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2246, 103601, 1, 1, "顔を洗って、着替えて\n朝ごはんを食べて、\n歯を磨いて…", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2247, 103601, 2, 1, "て、鉄棒の練習！？　\nほ、他のことを\nしませんか？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2248, 103601, 3, 1, "ね、眠くないです\n子供ではありません\nから…ふわぁ", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2250, 103601, 2, 1, "ひと休みしよっと\n………うん、\nひと休みおしまい♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2251, 103601, 3, 1, "つ、つぶらな瞳には\n弱いんです", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2252, 103601, 4, 1, "リラックスできますね\n集中力も\n上がりそうです", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2253, 103601, 5, 1, "あったかいと\n眠くなっちゃうな…\n勉強しなくちゃなのに", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2255, 103601, 7, 1, "ピーマンもこれくらい\nおいしければ\nいいのに…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2256, 103601, 8, 1, "この本、むずかしい\n…もっと勉強\nがんばらなくちゃ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2258, 101501, 1, 1, "今日はみんなに\nなにを読んで\nあげようかしら", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2259, 101501, 2, 1, "子どもたちと同じ\n目線で考えるのが\n大事よね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2260, 101501, 3, 1, "お天気が良い日は\nみんなでお散歩に\n行きたいわね～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2261, 101501, 4, 1, "鬼ごっこにかくれんぼ\n…みんなは\nどっちが好きかしら", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2262, 101501, 5, 1, "お遊戯会の配役、\n考え出すと\n大変だわ～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2263, 101501, 101, 4, "彼は優しいから、\nきっと良い\nお父さんになるわね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2264, 101501, 102, 8, "彼には私がいないと\n…っていうのは少し\n過保護すぎるかしら？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2265, 101501, 1, 1, "保育園の行事を\n考えてるんだけど…\n良い案ないかしら？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2266, 101501, 2, 1, "今度、子どもたちと\nおやつを作るから、\n食べに来てね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2267, 101501, 3, 1, "よかったら一緒に\n絵本を作らない？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2268, 101501, 4, 1, "あなたはいつも元気ね\n先生も見習わないと♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2269, 101501, 101, 4, "先生は\nずーっと、ずーっと、\nあなたの味方だからね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2270, 101501, 102, 8, "あなたみたいな\nいい子をお世話できて\n先生は幸せものだわ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2271, 101501, 1, 1, "おはよう～\nちゃんと朝ごはんは\n食べた？", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2272, 101501, 2, 1, "お昼寝の時間ね♪\nあなたも一緒に\n寝ましょうか", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2273, 101501, 3, 1, "すっかり真っ暗ね。\n今日も一日、\nよくできました♪", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2275, 101501, 2, 1, "ふかふかねぇ♪\n年甲斐もなく、\n飛び跳ねちゃいそう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2276, 101501, 3, 1, "あらあら、かわいい！\n保育園のみんなに\n見せてあげたいわ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2277, 101501, 4, 1, "いい香りね～\n保育園にも\n置こうかしら", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2278, 101501, 5, 1, "寒い日は、ここで\n子どもたちに絵本を\n読んであげましょ♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2280, 101501, 7, 1, "美味しい～♪\n遠足に持っていったら\nみんな喜びそう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2281, 101501, 8, 1, "みんなが\n楽しめる読み聞かせの\n方法特集♪　素敵ねぇ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2283, 104401, 1, 1, "世界の前に、\nこの部屋を征服\nしてやろう♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2284, 104401, 2, 1, "拠点とはやはり\n斯くあるべきじゃの", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2285, 104401, 3, 1, "眷属の物はわらわの物\n故に、この部屋は\nわらわの部屋じゃな！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2286, 104401, 4, 1, "永く眠っている間に\n随分と世界は変わった\nようじゃな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2287, 104401, 5, 1, "うるさいのう～\nまったく、近頃の\n若いもんは…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2288, 104401, 101, 4, "あやつが来てから\n動悸が治まらぬ…\n如何ような病で\nあろうかの…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2289, 104401, 102, 8, "眷属よ、どこに行った\nのじゃ？\nわらわの世話を\n許可するのじゃ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2290, 104401, 1, 1, "いずれ世界は\n我が手中に収まる\nふふふ、\n楽しみじゃのう", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2291, 104401, 2, 1, "眷属よ、わらわから\n離れてはならんぞ！\n子供に戻ってしまう\nではないか！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2292, 104401, 3, 1, "トマトジュースが\n欲しいのじゃ！\n野菜ジュースでは\nダメだぞ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2293, 104401, 4, 1, "蚊にくわれたぞ！\nわらわの血を吸う\nとは～！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2294, 104401, 101, 4, "こら、頭をなでるな\nお主はこの姿でも\n子供扱いするのか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2295, 104401, 102, 8, "お主が望むなら世界の\n半分をくれてやっても\n良いのじゃぞ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2296, 104401, 1, 1, "朝早くから感心じゃの\nさすがわらわの\n眷属じゃ", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2297, 104401, 2, 1, "太陽は苦手じゃ…\nこの部屋の中で\n過ごそうぞ", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2298, 104401, 3, 1, "夜も更けてきた！\nわらわ達の時間は\nこれからじゃ～♪", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2300, 104401, 2, 1, "よい座り心地じゃ\nわらわの玉座としよう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2301, 104401, 3, 1, "可愛いではないか\nわらわの眷属に\nしてやろうかのう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2302, 104401, 4, 1, "うむ、良い気持ちじゃ\nこれを作った者、\n褒めてつかわすぞ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2303, 104401, 5, 1, "あたたかい…\n身も心も\n浄化されるようじゃ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2305, 104401, 7, 1, "吸血鬼のわらわが、\nこのようなもの…\nうまい♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2306, 104401, 8, 1, "このまま大人の姿を\n保つ方法…どこかに\n書いてないかのう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2308, 105401, 1, 1, "いろんな人がいて\nいろんな服を着てて…\n目の保養になります♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2309, 105401, 2, 1, "レイ様への新作\nどんな服にしよう…\n方向性が絞れない～！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2310, 105401, 3, 1, "忘れないうちに\n隠し糸のメンテナンス\nやっておこうっと！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2311, 105401, 4, 1, "むっ！　あの人の\nスリーサイズは…\nほほう…なるほど♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2312, 105401, 5, 1, "ノゾミさんの\n衣装はああして\nチカさんのは…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2313, 105401, 101, 4, "私がアイドル…\n…実感ないけど\n精一杯やってみよう", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2314, 105401, 102, 8, "まさか私が\nアイドルになるなんて\n思ってなかったです", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2315, 105401, 1, 1, "なんですかぁ？\n衣装作りで疲れて\n眠いんですぅ…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2316, 105401, 2, 1, "あーっ！　また\n服がほつれてる…\nしょうがないですね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2317, 105401, 3, 1, "はっ！　ちょうど今\n新しいデザインが\n思い浮かびました！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2318, 105401, 4, 1, "暇そうですね？\n時間あるなら仕事\n手伝ってください！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2319, 105401, 101, 4, "どうしました？\n新しい服が\n欲しくなりましたか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2320, 105401, 102, 8, "騎士さんのおかげで\nカルミナでも\n頑張れてますよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2321, 105401, 1, 1, "おはようございます…\nなんで朝って毎日\nやってくるんでしょう", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2322, 105401, 2, 1, "お腹空きましたね\n騎士さんはちゃんと\n御飯食べましたか？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2323, 105401, 3, 1, "夜の方が\n作業が捗るのは\n私だけですかね？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2325, 105401, 2, 1, "このふかふか具合\nいい素材を\n使ってますね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2326, 105401, 3, 1, "小さくて可愛い♪\nそうだ！　レイ様の\nぬいぐるみを…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2327, 105401, 4, 1, "こういう時くらい\n何も考えずに\nリラックスしないと♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2328, 105401, 5, 1, "炎を使った服って\nカッコ良いかも…\nでも材料がな～…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2330, 105401, 7, 1, "何をするにも\n食事は大切！\n体力が資本です！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2331, 105401, 8, 1, "私もいつか\nデザインを\nまとめて本に…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2333, 107501, 1, 1, "またお魚を\n捕りにいこうかな♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2334, 107501, 2, 1, "そろそろみんなも\nお腹が空くころ\nじゃないですか～？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2335, 107501, 3, 1, "ここなら、\n日焼けの心配は\nありませんね！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2336, 107501, 4, 1, "お刺身に焼き貝…\nお腹が魚介類を\n求めてますっ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2337, 107501, 5, 1, "泳ぐのも食べるのも\n大得意です！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2338, 107501, 101, 4, "あの人と二人で\n浜辺を散歩…\n素敵ですねぇ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2339, 107501, 102, 8, "あの人に水着を\n褒めてもらえたら、\nすごく嬉しいなぁ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2340, 107501, 1, 1, "たまには釣りじゃなく\n一緒に素潜りでも\n行きませんか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2341, 107501, 2, 1, "男の子に水着を\n見られるのって\n少し緊張しちゃいます", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2342, 107501, 3, 1, "ギルドハウスの\nみんなとも、\n海に行きたいですね！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2343, 107501, 4, 1, "あなたの水着、\n私が選んであげます！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2344, 107501, 101, 4, "泳ぎの練習なら\n付き合いますよ！\n手取り足取り♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2345, 107501, 102, 8, "水着姿が好きなら\nいくらでも\n着てあげますよぉ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2346, 107501, 1, 1, "朝の海も\n幻想的で\nいいですよねぇ～！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2347, 107501, 2, 1, "お昼はみんなで、\n海鮮バーベキューとか\nどうでしょう！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2348, 107501, 3, 1, "夜の海は星が\nきれいですよ！\n見に行きましょう！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2350, 107501, 2, 1, "泳ぎ疲れたので\nちょっと休憩です～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2351, 107501, 3, 1, "あなたにも、\n可愛い水着を\n着せてあげたいです", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2352, 107501, 4, 1, "なんだか、\nリゾート気分です…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2353, 107501, 5, 1, "ふい～、泳ぎで冷えた\n身体を暖めましょう～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2355, 107501, 7, 1, "おいひ～です～\nこれ、海水浴にも\n持っていきたいですね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2356, 107501, 8, 1, "南国のフルーツ図鑑\n…じゅるり…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2358, 107501, 10, 1, "わ～♪　おいしそ…\nか、かわいいですね～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2359, 107501, 11, 1, "これくらい大きな貝…\n食べたいな～\n食べたいな～～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2360, 107601, 1, 1, "この格好で\n部屋をうろつくのは\n不思議な感じがします", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2361, 107601, 2, 1, "南国の果実を使って\n主さまにデザートを\nお作りしましょう", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2362, 107601, 3, 1, "せっかくの水着ですし\nこれから定期的に\n海水浴に行きたいです", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2363, 107601, 4, 1, "海の生き物も、\n森の生き物に負けず\n可愛かったです…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2364, 107601, 5, 1, "クロールの練習なら、\n部屋でもできそうです", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2365, 107601, 101, 4, "主さまに、水着姿を\n褒めていただけた…\nえへへ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2366, 107601, 102, 8, "水着で主さまの\nお世話をしたら、\n喜びますでしょうか…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2367, 107601, 1, 1, "お疲れでしたら\nわたくしの膝を\nお使いくださいね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2368, 107601, 2, 1, "主さま、海の家で\nつかうおこづかいを\n渡しておきますね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2369, 107601, 3, 1, "海の美しさは、\n森とはちがう\n魅力がありました", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2370, 107601, 4, 1, "この髪飾りですか？\n造花ですから\n水の中も平気です", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2371, 107601, 101, 4, "水着でくっついて\n眠ると、いつもより\n密着して暖かいです", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2372, 107601, 102, 8, "はじめての海が、\n主さまと一緒で\n幸せです♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2373, 107601, 1, 1, "おはようございます。\n泳ぎに行く準備は\n万端です", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2374, 107601, 2, 1, "午後は泳ぐのをやめて\nスイカ割りなど\nいかがでしょう？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2375, 107601, 3, 1, "夜も更けてきました…\n波の音を聞きながら\n少しお話しませんか？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2377, 107601, 2, 1, "あぅ…この家具には、\n人をダメにする魔法が\nかけられています…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2378, 107601, 3, 1, "ふふっ♪\nあなたも、海水浴に\n行きますか？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2379, 107601, 4, 1, "この香り…\nきっと主さまも\n気に入ります", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2380, 107601, 5, 1, "これは暖かいです…\n後ほど主さまと一緒に\nあたりましょう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2382, 107601, 7, 1, "泳いだ後の食事は\n一段と美味しく\n感じます", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2383, 107601, 8, 1, "深海魚の生態…\nこれは主さまにも\nお伝えしなくては…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2385, 107601, 10, 1, "顔立ちが、主さまに\n似ております…\nかわいらしい♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2386, 107601, 11, 1, "ふかふかですね\n主さまのようです♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2387, 107701, 1, 1, "お掃除、お掃除\nふんふふ～ん☆\nあ、聞かれてました？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2388, 107701, 2, 1, "日焼けには回復魔法が\n効きますよ。どうして\n逃げるんですか～！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2389, 107701, 3, 1, "今日はドジを\nしませんように！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2390, 107701, 4, 1, "接客業を極めれば\nメイドとして\n成長できるかも？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2391, 107701, 5, 1, "水着って\n動きやすいんですけど\n人の目が気になります", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2392, 107701, 101, 4, "あの人の気持ちを知る\n魔法とかあれば…\nな、なんでもないです", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2393, 107701, 102, 8, "水着でご奉仕したら、\nあの人は喜ぶかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2394, 107701, 1, 1, "暑いのでしたら、\n私が魔法で涼しく…\nえ？　いらない？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2395, 107701, 2, 1, "何か御用ですか？\n特にないのでしたら、\nお話ししましょう♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2396, 107701, 3, 1, "水着姿でも\nメイドはメイドです\nご奉仕しますよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2397, 107701, 4, 1, "ひゃっ！　あ、危うく\n転ぶところでした…\n…きゃうっ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2398, 107701, 101, 4, "どうかしましたか？\nあなたの御用なら、\n喜んでお受けしますよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2399, 107701, 102, 8, "水着姿をあなたに\n見られると、\nドキドキしちゃいます", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2400, 107701, 1, 1, "朝は暑くてやる気が\n出ない？　でしたら、\nえぇい、元気になぁれ", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2401, 107701, 2, 1, "お腹がぺこぺこなら、\nお昼は海の家で\n食べましょう♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2402, 107701, 3, 1, "夜の浜辺でお散歩も\nいいですね。たくさん\n転びそうですけど…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2404, 107701, 2, 1, "休憩しながら、\nお掃除すれば\n一石二鳥です！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2405, 107701, 3, 1, "きれいにすれば\nかわいさも倍増\nですよ～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2406, 107701, 4, 1, "素敵な香りです～\n救護院に置けば、\nみんな喜ぶかも～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2407, 107701, 5, 1, "あったかいですね…\nはっ！　いけません！\nメイドがサボるなんて", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2409, 107701, 7, 1, "料理のできない\nメイドなんて…\nはぐはぐ、おいしい♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2410, 107701, 8, 1, "水着姿で接客をすると\n売り上げが倍になる\n…本当ですか、これ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2412, 107701, 10, 1, "えへへ♪\n南の島まで\n乗せてください！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2413, 107701, 11, 1, "なんだか、\n人魚姫に\nなった気分です♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2414, 107801, 1, 1, "みんなで過ごす\n休日っていいわよね…\nずっと続かないかな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2415, 107801, 2, 1, "海に浮輪でぷかぷか\n浮かぶのって\n癖になりそう", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2416, 107801, 3, 1, "ビーチにいると\n日焼けが心配だわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2417, 107801, 4, 1, "水着姿って楽よね。\n室内だとちょっと\n落ち着かないけど…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2418, 107801, 5, 1, "お、泳ぎの練習なんて\nしないからね！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2419, 107801, 101, 4, "楽しすぎて立場を\n忘れそう…あんたの\nせいなんだから！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2420, 107801, 102, 8, "みんなで遊んで\n美味しいもの食べて\nほんっとに楽しいっ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2421, 107801, 1, 1, "マジであっついわね…\nよっし！　海に入って\n涼みましょ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2422, 107801, 2, 1, "シャワー浴びたばかり\nなのに…また、遊び\nたくなってきたわ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2423, 107801, 3, 1, "遊び足りないって？\nしょうがないわね…\n付き合ってあげるわ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2424, 107801, 4, 1, "一応伝えておくけど\n猫って水が苦手なの！\nわかった！？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2425, 107801, 101, 4, "あたしと話したいの？\nふふ。\nまぁ、いいわよ。\nほら隣に座りなさい", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2426, 107801, 102, 8, "こんなに楽しいの\n初めてかも…\nずっと続けばいいのに", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2427, 107801, 1, 1, "は、早起きしたのは、\n楽しみで眠れなかった\nからじゃないわよ！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2428, 107801, 2, 1, "昼食後のストレッチ\n終わり！　さあ、\nビーチが待ってるわ！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2429, 107801, 3, 1, "夜の海に散歩に\n行こうかな～\nあんたも来なさいよ☆", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2431, 107801, 2, 1, "海ではしゃぎすぎて\n疲れたわ…ちょっと\n休もうかしら？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2432, 107801, 3, 1, "こんな魔物がいたら\n操るのも楽しいかも", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2433, 107801, 4, 1, "にゃう～♪\nまるで南国にいる\n気分ね♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2434, 107801, 5, 1, "あったかぁい♪\n身体が冷えてきたから\n丁度いいわ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2436, 107801, 7, 1, "運動後のご飯って\nなんでこんなに\n美味しいんだろ♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2437, 107801, 8, 1, "どんなカナヅチでも、\n絶対必ず泳げる方法、\n書いてないかしら？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2439, 107801, 10, 1, "あんたもあたしが\n操ってあげる！\nなんてね～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2440, 107801, 11, 1, "リゾートリゾートっ\nのんびりしましょ♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2441, 108001, 1, 1, "ここなら思う存分\nデスクワークに\n励めるわね！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2442, 108001, 2, 1, "武器のメンテナンスも\nしっかりしないと\n最高効率は望めないわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2443, 108001, 3, 1, "準備をしっかり整えて\nもっともっと\n稼がなくちゃね！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2444, 108001, 4, 1, "少し休んだら\n次のバイトに\n行ってくるわね！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2445, 108001, 5, 1, "この格好でここに\nいるのって\n気恥ずかしいわね…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2446, 108001, 101, 4, "キミが望むなら\n今日のバイト\n休んでもいいわよ？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2447, 108001, 102, 8, "キミだけね…\n私のことをここまで\n理解してくれるのは", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2448, 108001, 1, 1, "普段と違う格好\nだからって\nそんなに見ないでよ…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2449, 108001, 2, 1, "キミを立派な\nバイト戦士にするのが\n師匠としての役目よね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2450, 108001, 3, 1, "そろそろ次のバイトに\n行かないと…\nキミも一緒に来る？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2451, 108001, 4, 1, "効率は大事だけど\nゆっくりするのも\n悪くないわね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2452, 108001, 101, 4, "一緒の時間が楽しくて\nバイトのことを\n忘れちゃいそうね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2453, 108001, 102, 8, "キミと一緒なら\nどんなミッションも\n楽勝ね！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2454, 108001, 1, 1, "おはよう、いい朝ね！\n今日もめいっぱい\n稼ぐわよ！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2455, 108001, 2, 1, "もうお昼だったのね…\n内職のバイトに\n集中しすぎちゃったわ", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2456, 108001, 3, 1, "スケジュールは、と…\nこういう管理の徹底が\n効率を生み出すのよ♪", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2458, 108001, 2, 1, "しっかり働いて休む…\n最高効率にまた一歩\n近づいたわね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2459, 108001, 3, 1, "なごんじゃうわね…\nよぉし、次のバイトも\n頑張らないと！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2460, 108001, 4, 1, "いい香りね…\n働く意欲が\n湧いてくるわ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2461, 108001, 5, 1, "身体が冷えたから\n助かるわ…冷えは\n女の天敵だもの", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2463, 108001, 7, 1, "海のバイトは\n体力勝負よ\nエネルギーはしっかり\n摂取しないとね！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2464, 108001, 8, 1, "座学も大切ね\nもっと知識を\n身につけないと…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2466, 108001, 10, 1, "海の楽園みたいね…\n今だけは効率を\n忘れちゃいそう…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2467, 108001, 11, 1, "落ち着くわ…\n海とひとつになった\n気分ね！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2468, 107901, 1, 1, "ちょっと休憩にゃ～\n潮風は体に\n良くないにゃ～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2469, 107901, 2, 1, "猫グッズで\n海の家を飾りたいにゃ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2470, 107901, 3, 1, "海の幸は美味しいけど\n水はイヤだにゃ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2471, 107901, 4, 1, "開店にゃ！\nこのたい焼きなら\nきっと大繁盛にゃ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2472, 107901, 5, 1, "海と言えば水着にゃ\n似合ってるかにゃ？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2473, 107901, 101, 4, "キミが頼むなら\n手伝ってあげても\nいいにゃ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2474, 107901, 102, 8, "あたしの宝は\nここにあるにゃ～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2475, 107901, 1, 1, "にゃ？\nそんなに見つめても\n何も出ないにゃ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2476, 107901, 2, 1, "今からキミを\nあたしの相棒に\n任命するにゃ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2477, 107901, 3, 1, "これから\n開店の準備にゃ\nキミも手伝って\nくれるのかにゃ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2478, 107901, 4, 1, "海の家で出す\n新しいメニューを\n考え中なのにゃ～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2479, 107901, 101, 4, "キミと一緒なら\n売上げナンバーワンに\nなれるにゃ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2480, 107901, 102, 8, "キミと一緒にいると\n楽しくて、もっと\nいたくなるにゃ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2481, 107901, 1, 1, "おはようにゃ！\n今日も頑張って\nいくにゃー！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2482, 107901, 2, 1, "お腹すいたにゃ～…\nお魚、ちょっとだけ、\nつまみ食いするにゃ", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2483, 107901, 3, 1, "明日の準備は万端にゃ\nいつでも\n出発できるにゃ", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2485, 107901, 2, 1, "このふかふか具合\nまるで子猫の\n毛並みのようにゃ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2486, 107901, 3, 1, "にゃにゃ～\nなんてかわいさにゃ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2487, 107901, 4, 1, "この匂い…\nもう少し香ばしいと\nいいかもしれないにゃ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2488, 107901, 5, 1, "にゃ～…\nあったかくて\nぬくぬくだにゃ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2490, 107901, 7, 1, "働くにはまず体力にゃ\nしっかり食べて\nスタミナアップ\nだにゃ!", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2491, 107901, 8, 1, "ふむふむ…\nここにお宝が\n眠ってるんだにゃ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2493, 107901, 10, 1, "シードレイクにゃ！\n本物は怖かったけど、\nぬいぐるみなら\nかわいいにゃ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2494, 107901, 11, 1, "貝なのにふかふかの\nクッションにゃ！\n屋台の前にも\n置いてみたいにゃあ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2495, 103701, 1, 1, "人の家って、道場とは\n違った香りがするね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2496, 103701, 2, 1, "ちょっと退屈だね…\nあの人をからかいに\n行こうかな♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2497, 103701, 3, 1, "普通の女の子みたいに\n遊びたいって思う\nこともあるんだよね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2498, 103701, 4, 1, "剣の手入れを怠らない\nようにしないと。\nいざという時、困るし", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2499, 103701, 5, 1, "もっと\n上手く立ち回って\n出世したいな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2500, 103701, 101, 4, "おかえりなさい！\nふふ、なんだか奥さん\nになった気分だよ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2501, 103701, 102, 8, "恥ずかしくて、あなた\nを見られないよ…\n私らしくないなぁ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2502, 103701, 1, 1, "私、お話好きなんだ。\nどうせなら楽しい話を\nしたいな♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2503, 103701, 2, 1, "女の子っぽい子と、\n男勝りな子、あなたは\nどっちが好き？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2504, 103701, 3, 1, "そろそろ見回りの\n時間かな。あなたも\n一緒に行く？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2505, 103701, 4, 1, "構って欲しいのかな？\nそれじゃ一緒に\nランニングに行こうか", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2506, 103701, 101, 4, "二人で出かけるなら、\n手を繋がないとね。\nあはは、冗談冗談♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2507, 103701, 102, 8, "ねえ、あなた♪\nおや、顔が真っ赤だ！\n…ふふ、私も、かな？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2508, 103701, 1, 1, "私は朝から鍛錬だよ。\n慣れてるけど大変だ。\nお互いに頑張ろうね", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2509, 103701, 2, 1, "鍛錬でお腹ぺこぺこ。\nお昼ごはんが\n楽しみだね", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2510, 103701, 3, 1, "今日も一日見回りと\n鍛錬を頑張ったよ。\nあなたもお疲れさま", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2512, 103701, 2, 1, "いざという時のため\nにも、休憩は大事だね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2513, 103701, 3, 1, "こういうものには\n今まで\n縁がなかったけど。\nうん、可愛いね♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2514, 103701, 4, 1, "精神統一に通じる\nものがあるね。\nふぅ、癒されるなぁ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2515, 103701, 5, 1, "あったかいなぁ。\nたまにはのんびり\nしようか", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2517, 103701, 7, 1, "鍛錬後はすごくお腹が\n空くんだよね。\nう～ん、おいしい♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2518, 103701, 8, 1, "剣術書だけで強くなる\nには飛びぬけた才能と\n努力が必要だよね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2520, 100501, 1, 1, "正義の刃で\n悪を挫く！\n…キマったッス♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2521, 100501, 2, 1, "ヒーローの\n秘密特訓を\n始めるッスよ～！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2522, 100501, 3, 1, "正義の味方目指して\n今日も明日も\n全力ッスよ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2523, 100501, 4, 1, "一日一善！\nヒーローだったら\n当たり前ッス♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2524, 100501, 5, 1, "どうッスか\nこのポーズ！\nイイ感じッスか？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2525, 100501, 101, 4, "た、たまには\nよく頑張ったねって\n褒めてほしいッス…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2526, 100501, 102, 8, "自分を信じてくれる\nあんたと一緒なら\n何でもやれるッス！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2527, 100501, 1, 1, "おつかれッス♪\n冒険の準備は\nばっちりッスよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2528, 100501, 2, 1, "どうしたんッスか？\n…はっ！　もしや敵が\n迫ってるんスかっ！？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2529, 100501, 3, 1, "べ、別に\n頭を撫でてほしいとか\n思ってないッスよ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2530, 100501, 4, 1, "スーパーヒーロー\nトラタイガーは\n絶対無敵ッス！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2531, 100501, 101, 4, "あんたと自分で\n正義の心を燃やして\n悪を一網打尽ッス！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2532, 100501, 102, 8, "あんたと一緒だと\n幸せな気持ちに\nなるんッス…♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2533, 100501, 1, 1, "お、早いッスね～♪\n今日も一日一善\nよろしくッス！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2534, 100501, 2, 1, "一休みしながら\nヒーローについて\n語り合うッスよ～！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2535, 100501, 3, 1, "夜のパトロールで\n一緒に平和を\n守るッス！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2537, 100501, 2, 1, "ふぅぅ…\n落ち着くッスね\n戦士の休息ッス♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2538, 100501, 3, 1, "くぅぅ、可愛いッス…\nヒーローが守るべき\n愛らしさッスね！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2539, 100501, 4, 1, "なんスかこの香り？\nすぅぅ…ふぅぅ…\n力がみなぎるッス！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2540, 100501, 5, 1, "優しい光ッスね…\nヒーローの優しさ\nそのものッス！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2542, 100501, 7, 1, "あむあむあむ…\nんぅう、元気が溢れて\n止まらないッス！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2543, 100501, 8, 1, "トラタイガーのお話は\n笑いあり涙ありで\n超おすすめッス！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2545, 108101, 1, 1, "ハッピーハロウィン\nお父さん、お菓子は…\n食べられないね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2546, 108101, 2, 1, "トリックオアトリート\nお菓子をくれないと\n不吉な占いをします", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2547, 108101, 3, 1, "ふふ、あなたの後ろで\n霊のみなさんが\n踊り始めました", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2548, 108101, 4, 1, "霊感がいつも以上に\n冴えているので\n占いの調子も良好です", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2549, 108101, 5, 1, "父はハロウィンが\n大好きだそうです\n大胆な格好をする子が\n多いので…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2550, 108101, 101, 4, "あなたは霊たちにも\n大人気ですよね\n私も見てほしいな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2551, 108101, 102, 8, "ハロウィンの賑やかさ\n今は好きです…\nあなたといられるので", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2552, 108101, 1, 1, "霊のみなさんとの\nダンスパーティー…\n楽しみです", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2553, 108101, 2, 1, "お父さん、元気だね\nえ？　ここは色んな\n女の子に会えて幸せ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2554, 108101, 3, 1, "ハロウィンのお菓子\n食べすぎちゃったな…\nでも、美味しかった…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2555, 108101, 4, 1, "あなたの女難の相…\n悪化していますね\nお祓いが必要かも", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2556, 108101, 101, 4, "あの、霊のみなさんが\nあなたと踊りたいと…\nえっと、私も…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2557, 108101, 102, 8, "あなたと手を繋ぐと\n胸が苦しくなります\nでも、繋ぎたいな…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2558, 108101, 1, 1, "ハッピーハロウィン…\n父が元気なので\n今日の運気は良好です", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2559, 108101, 2, 1, "父はお昼寝中です\n本当に寝ているかは\n私にも分かりませんが", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2560, 108101, 3, 1, "お父さん、\nもう眠っちゃったの？\n…眠れるんだったっけ", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2562, 108101, 2, 1, "落ち着きますね…\n霊のみなさんも\n喜んでいます", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2563, 108101, 3, 1, "つん、つん。ふふっ♪\n癒やされます…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2564, 108101, 4, 1, "精神が研ぎ澄まされる\n香りですね…\n占いが捗ります", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2565, 108101, 5, 1, "あったかいね\nお父さん…\nえ？　分からない？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2567, 108101, 7, 1, "霊のみなさんは\n食べられないので…\n私がいただきます", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2568, 108101, 8, 1, "お父さんを元に戻す\n手掛かり…\n書かれていないかな？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2570, 108101, 12, 1, "わぁ、お菓子が沢山…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2572, 108201, 1, 1, "無料でプリンが\nもらえるなんて、\nハロウィンはすごいの", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2573, 108201, 2, 1, "生きてるときも、\nミヤコはハロウィンが\n好きだった気がするの", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2574, 108201, 3, 1, "みんなに仮装を\n見せつけて、\nプリンをもらうの～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2575, 108201, 4, 1, "この仮装で、\nみんなに恐怖を\n教えてやるの！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2576, 108201, 5, 1, "アオーン、アオーン！\nミヤコ、狼女に\nなりきってるの～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2577, 108201, 101, 4, "アイツに秘密で、\nパーティーの\n飾り付けをするの♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2578, 108201, 102, 8, "アイツが狼男の\n仮装をすれば、\nおそろいなの～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2579, 108201, 1, 1, "プリンをくれなきゃ、\nオマエをカボチャに\nしてやるの～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2580, 108201, 2, 1, "ハロウィンは幽霊でも\n参加できるから\n楽しいの～！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2581, 108201, 3, 1, "プリンがないなら\nカボチャプリンを\n食べればいいの！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2582, 108201, 4, 1, "プリンの可能性は\n無限大なの～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2583, 108201, 101, 4, "オマエの隣で\nプリンを食べると、\nいつもより美味しいの", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2584, 108201, 102, 8, "オマエが死んでも、\nミヤコはずーっと\n一緒にいてやるの～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2585, 108201, 1, 1, "朝ごはんは\nカボチャプリンなの！\nハロウィンっぽいの～", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2586, 108201, 2, 1, "ランチは豪華に\nプリンアラモードなの", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2587, 108201, 3, 1, "夜は幽霊たちと\nハロウィンするの！\nオマエも来るの！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2589, 108201, 2, 1, "幽霊にも休息は\n必要なの！\nプリンもってこいなの", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2590, 108201, 3, 1, "オマエもミヤコの\nパーティーに\n招待してあげるの～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2591, 108201, 4, 1, "癒されるの～\nまるで天国なの～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2592, 108201, 5, 1, "ここでなら、\n焼きプリンが\n作れるの！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2594, 108201, 7, 1, "腹が減っては\nハロウィンは\nできぬなの！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2595, 108201, 8, 1, "丸ごとカボチャ\nプリン特集！？\n美味しそうなの～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2597, 108201, 12, 1, "お菓子がたくさんで、\nミヤコはとっても\nハッピーなの♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2599, 108301, 1, 1, "魔女以外にも、\n色々な仮装を\nしてみたいわね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2600, 108301, 2, 1, "チョコやアメよりも、\nアタシに相応しい\nスイーツがあるはず…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2601, 108301, 3, 1, "お菓子の食べ過ぎは、\nお母さんに\n怒られるかも…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2602, 108301, 4, 1, "この格好で学院に\n行ったら、\nみんな驚くかしら", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2603, 108301, 5, 1, "大人のレディは、\nどういうハロウィンを\n過ごすべきかしら…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2604, 108301, 101, 4, "お菓子、アイツの分も\nとっといてあげよ！\n大人の余裕ってやつね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2605, 108301, 102, 8, "アイツを魅了する\n魔法とかないかしら？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2606, 108301, 1, 1, "おしゃれスイーツを\nくれなきゃ\nイタズラするわよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2607, 108301, 2, 1, "アタシの大人センスで\nアンタに似合う仮装を\n選んであげるわ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2608, 108301, 3, 1, "不気味よねぇ…\nジャックオーランタン\nこ、怖くないわよ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2609, 108301, 4, 1, "ハロウィンが楽しくて\n宿題やり忘れてたわ…\nて、手伝って～！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2610, 108301, 101, 4, "お菓子をもらうのも\nアンタと一緒なら\n恥ずかしくないわ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2611, 108301, 102, 8, "アタシのエスコートを\n任せられるのは、\nアンタだけなんだから", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2612, 108301, 1, 1, "朝食はオシャレに\nグリーンスムージー！\n…に、にがぁ", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2613, 108301, 2, 1, "昼下がりはカフェで\n優雅なティータイム\n…憧れるわ～", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2614, 108301, 3, 1, "ハロウィンナイトの\nはじまりよ！\n街に繰り出しましょ！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2616, 108301, 2, 1, "あう～、お菓子の\n食べすぎで\n動けな～い", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2617, 108301, 3, 1, "アンタも仮装する？\nいまよりもーっと\nかわいくなるわよ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2618, 108301, 4, 1, "大人な香りに\n包まれてる～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2619, 108301, 5, 1, "ポカポカね～♪\n…ああー！\nチョコが溶けてる！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2621, 108301, 7, 1, "おいしい～♪\n…じゃなくて！\nび、美味ですわ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2622, 108301, 8, 1, "この服かわい～♪\nって、なにこの値段！\nお小遣い何年分よ！？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2624, 108301, 12, 1, "お、お菓子なんかで\n喜ぶほど、子供じゃ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2626, 107101, 1, 1, "次はどんな策で団長を\nからかってみようか…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2627, 107101, 2, 1, "む？\n血の臭いがするな\n祭りの時間か☆", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2628, 107101, 3, 1, "平和に飼い殺される\n前に、混沌の愛を\n育みたいね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2629, 107101, 4, 1, "本気を出した団長と\nいつか手加減なしに\n殺り合いたいな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2630, 107101, 5, 1, "おばさん呼ばわりする\n新米騎士の小娘を\n懲らしめてやろうか", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2631, 107101, 101, 4, "胸を触られるまで\n誓約のことなど\nすっかり忘れていたな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2632, 107101, 102, 8, "まさかこのワタシが\nプロポーズするとはな\n…笑えるじゃないか", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2633, 107101, 1, 1, "面白い暇つぶしを\n思いついたんだ\n戯れに付き合え☆", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2634, 107101, 2, 1, "退屈で死にそうだ\n愉しい話題を\nワタシに提供してくれ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2635, 107101, 3, 1, "なんだ、暇なのか？\n心躍ることでも探しに\n出かけようではないか", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2636, 107101, 4, 1, "このワタシを愉快に\nさせてくれる事でも\nあったのか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2637, 107101, 101, 4, "ワタシに求婚した男の\n数だと？\nいちいち覚えてないな", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2638, 107101, 102, 8, "最強無敵の女と\n結婚できるんだ\n誇りに思えよ☆", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2639, 107101, 1, 1, "なにか愉快なことが\n起こりそうな朝だ☆\nワクワクするね", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2640, 107101, 2, 1, "うーむ、腹がすいたな\n愉快なことで\nこの空腹を満たしたい", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2641, 107101, 3, 1, "これからは大人の\n時間だ。愉しみ方を\n知りたいのかい？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2643, 107101, 2, 1, "このワタシとした事が\n穏やかな気分に\nなってしまうとは…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2644, 107101, 3, 1, "可愛い姿をしたものが\n惨たらしい\n醜態を晒すのを\n想像してしまう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2645, 107101, 4, 1, "悪くはない香りだ\nだが、ワタシは血の\n臭いを嗅ぎたいぞ☆", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2646, 107101, 5, 1, "ぬるい、ぬるすぎる！\n地獄の業火には\nほど遠い熱さだ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2648, 107101, 7, 1, "ワタシの腕前に\n勝るとも劣らない\nなかなかの味だ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2649, 107101, 8, 1, "意中の男性を絶対に\n落とす方法…？\nく、下らんっ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2651, 105601, 1, 1, "明日はあの釣り場に\n行くとしようかな\nあの子も誘ってみるか", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2652, 105601, 2, 1, "ぷはーっ！\n体を動かしたあとの\n一杯は格別だね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2653, 105601, 3, 1, "天気が良いと\n昼寝か釣りかで\n迷っちまうな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2654, 105601, 4, 1, "大物が釣れたときの\n快感は、他じゃ\n味わえないね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2655, 105601, 5, 1, "ふあぁ～…\n昼寝でもするか", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2656, 105601, 101, 4, "ふふっ…\nあの子、見た目より\n根性あるんだね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2657, 105601, 102, 8, "あの子が強くなるには\nどんな魚を食べさせて\nあげればいいのやら…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2658, 105601, 1, 1, "姉御って呼ぶんじゃ\nないよ。からかうと\nタダじゃおかないよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2659, 105601, 2, 1, "釣りのコツ？\n何事も経験が大事だ\n今から行くかい？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2660, 105601, 3, 1, "どうしたのさ？\n助太刀が必要かい？\nアタシにまかせときな", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2661, 105601, 4, 1, "困ってる人がいたら\nまた力を貸しておくれ\n頼りにしてるよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2662, 105601, 101, 4, "なにか困りごとかい？\nどうやら昼寝は\nまた今度だね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2663, 105601, 102, 8, "よしよし！\nお人好しのお前さんを\n労ってあげるよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2664, 105601, 1, 1, "おはよう、お前さん\n魚屋は早起きでなきゃ\nつとまらないよ？", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2665, 105601, 2, 1, "昼飯かい？　今朝\n仕入れた魚があるんだ\n食べてみるかい？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2666, 105601, 3, 1, "夜釣りにぴったりの\n月が出てるよ\nいい釣りが出来そうだ", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2668, 105601, 2, 1, "コイツに座って\nのんびり釣りが\nできりゃいいのにな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2669, 105601, 3, 1, "よしよし…♪\nおっと、かわいい物も\nつい抱きしめちまうな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2670, 105601, 4, 1, "これで魚の臭いを\n消せれば、猫も\n寄らなくなるのかね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2671, 105601, 5, 1, "冬場の釣りも\nこれくらい暖かけりゃ\nいいんだけどね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2673, 105601, 7, 1, "腹が減っては\n釣りもできぬ、ってな\nまずは腹ごしらえだ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2674, 105601, 8, 1, "へぇ…こんな魚が\nいたとは…いつか\n釣りたいもんだね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2676, 101301, 1, 1, "流石に室内で\n魔法ぶっぱしたら\n怒られるよねぇ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2677, 101301, 2, 1, "可愛い女の子が\nたくさんいる！\nここは楽園か！？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2678, 101301, 3, 1, "みんなに漫画とか\n勧めてみようかな！\n布教用のがあるし！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2679, 101301, 4, 1, "わ、私一人の\n部屋じゃないから\n色々自重せねば！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2680, 101301, 5, 1, "「ぬるぽ」\n…くっ、\n誰も反応なしか！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2681, 101301, 101, 4, "次はどの漫画を\n貸そうかなー♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2682, 101301, 102, 8, "次の定期巡回で\nとっておきの店に\n連れてってあげよう！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2683, 101301, 1, 1, "君に似合いそうな\n衣装を買ったよ！\n君もいざ魔法少女に！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2684, 101301, 2, 1, "さあ！\n暇なら定期巡回に\nレッツラゴー！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2685, 101301, 3, 1, "戦闘だけじゃなくて、\n日常にも役立つ\n魔法を習得したいね！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2686, 101301, 4, 1, "新魔法思いついた！\n私って天才！？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2687, 101301, 101, 4, "決め台詞は超大事！\n君の決め台詞も\n私が考えてあげよう！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2688, 101301, 102, 8, "私ってもしやリア充？\n…そんなバカな！\n非リア代表のはずが！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2689, 101301, 1, 1, "おはよー…\n徹夜で本読んでたから\nまだ眠い～", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2690, 101301, 2, 1, "昼飯食べて、\n午後の定期巡回も\n頑張りましょー！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2691, 101301, 3, 1, "夜からが、\nオタ活本番でしょ！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2693, 101301, 2, 1, "ふいー！\nくつろいで本を読む！\n最高だねー！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2694, 101301, 3, 1, "君、可愛いね～♪\n魔法少女の\n使い魔みたい！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2695, 101301, 4, 1, "良い匂いに\n釣られてしまった…\nまさか、孔明の罠！？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2696, 101301, 5, 1, "暖かいけど、\nここで本を読むのは\nちょっち危ないかな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2698, 101301, 7, 1, "ウマ―！\nこれの詳細きぼんぬ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2699, 101301, 8, 1, "こ、古代語の本！？\n現代語でおk…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2701, 190901, 1, 1, "ふふふっ♪\n次はどんな悪戯を\nしてやろうかしら♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2702, 190901, 2, 1, "幻影湖の霧を吸った\n人間たちの\nリアクションって、\nサイコーよねぇ～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2703, 190901, 3, 1, "室内だけとはいえ…\n自由に動けるって、\nやっぱりいいわ～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2704, 190901, 4, 1, "改めて見ると良い\n部屋ね…悪戯とか\nしやすそうだしぃ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2705, 190901, 5, 1, "私を封じていた部屋\nなのに…急に居心地が\n良くなったわね♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2706, 190901, 1, 1, "あんたの頭、座り心地\n良いわね…私専用の\n椅子にしてあげる♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2707, 190901, 2, 1, "外は大変みたいねぇ…\nまあ、私は好き勝手に\nやらせてもらうわ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2708, 190901, 3, 1, "顔に落書された？\n誰か知らないけど、\n悪いことする奴も\nいるものねぇ～♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2709, 190901, 4, 1, "フィオのやつ…\nどこをウロチョロ\nしてるのかしら？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2710, 190901, 1, 1, "人間の朝は大変ねぇ…\n私はその様を見ながら\nゆっくりするわね♪", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2711, 190901, 2, 1, "う～私も、お日様を\n浴びながら、お外を\nお散歩したーい！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2712, 190901, 3, 1, "この暗さ…閉じ込め\nられていた時を\n思い出すわね…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2714, 190901, 2, 1, "悪戯しすぎて\n疲れたから、ちょっと\n休憩しよっと♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2715, 190901, 3, 1, "ガイド妖精たる私が、\nこんな造形物に\n惹かれる…わけが……\nかわいい♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2716, 190901, 4, 1, "この香りを囮にして、\n周りに悪戯を仕込む…\n完璧ね♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2717, 190901, 5, 1, "不思議ねぇ…私は\n温度とか関係ないのに\n離れられないわ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2719, 190901, 7, 1, "食事の必要がない体…\nつまり、食べたければ\n食べていいのよね～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2720, 190901, 8, 1, "へぇ…最近はこんな\n悪戯もあるのねぇ…\nあいつで試さないと♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2722, 108401, 1, 1, "メリークリスマス！\n歌で幸せを\n届けます…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2723, 108401, 2, 1, "あなたと出会えたのは\n偶然ではなくて…\nクリスマスの奇跡だと\n思うんです", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2724, 108401, 3, 1, "クリスマスは華やかで\nつい子供のように\nはしゃいでしまいます", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2725, 108401, 4, 1, "曲が書けるのは\nあなたが側に\nいてくれるからです…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2726, 108401, 5, 1, "感動を与えられる歌…\nあなたと一緒になら\n生み出せるはず…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2727, 108401, 101, 4, "クリスマスの幸せを\nあなたと\n感じ合えたらな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2728, 108401, 102, 8, "あなたの幸せを願った\nクリスマスソング…\nいつか披露しますね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2729, 108401, 1, 1, "みなさんの笑顔を\n守りたいから…\n私は歌い続けます", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2730, 108401, 2, 1, "歌を届ける\nサンタさんに\nなりたいな…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2731, 108401, 3, 1, "お暇でしたら\nクリスマスのお祝いを\nしませんか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2732, 108401, 4, 1, "ステージに立つ前に\n喉を整えないと…\n発声練習は念入りに…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2733, 108401, 101, 4, "まわりはかわいい子\nばっかりだけど…\nこの服なら\nアピールになるかな？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2734, 108401, 102, 8, "歌でならいくらでも\nあの人への気持ちを\n込められるのに…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2735, 108401, 1, 1, "今日も一日\n幸せな歌で世界が\n包まれますように…", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2736, 108401, 2, 1, "お茶を飲みながら\n好きな音楽を\n語り合いませんか？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2737, 108401, 3, 1, "ふふ、寝かしつけの\n子守唄を\nお望みですか？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2739, 108401, 2, 1, "ふかふかですね\n心地良すぎて\n眠ってしまいそう…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2740, 108401, 3, 1, "素敵…\n優しい歌みたいに\n気持ちが落ち着く…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2741, 108401, 4, 1, "インスピレーションが\n溢れてくる…\nいい曲が書けそう…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2742, 108401, 5, 1, "この光みたいに\n灯火になるような\n歌を歌いたい…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2744, 108401, 7, 1, "喉の健康のためにも\n食事はしっかり\nとるようにしています", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2745, 108401, 8, 1, "物語には\n曲作りのヒントが\n詰まっているんです", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2747, 108401, 14, 1, "絵が飛び出してくる\nなんて…精霊が宿って\nいるのでしょうか？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2748, 108401, 15, 1, "か、可愛い…♪\nちょっとだけ触っても\nいいですよね…？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2749, 108501, 1, 1, "クリスマスでも、\n演劇の練習は\nしっかりやらないと！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2750, 108501, 2, 1, "あうぅ…\nクリスマスの準備が\n終わらないよぅ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2751, 108501, 3, 1, "アヤネちゃんが\n摘み食いしないように\n見ておかないと…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2752, 108501, 4, 1, "サンタさんみたいに、\n皆に笑顔で接する…\nうぅ…私には無理ぃ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2753, 108501, 5, 1, "新しいお洋服～♪\nスズメお姉ちゃんに\nお礼をいわないと♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2754, 108501, 101, 4, "お兄ちゃんの為に、\nプレゼントを\n用意しないと♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2755, 108501, 102, 8, "クリスマス…\n恋人の日…ひゃわっ！\nわ、私ってば何を…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2756, 108501, 1, 1, "メリー\nクリスマスです、\nお兄ちゃん♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2757, 108501, 2, 1, "そ、そんなに\n見られると…\n恥ずかしいよぉ…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2758, 108501, 3, 1, "ひゃわぁっ！？\n急に触られると、\n驚くからやめてぇ…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2759, 108501, 4, 1, "あ…クリスマスの\n準備を手伝って\nくれるんですか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2760, 108501, 101, 4, "お兄ちゃんと\n過ごすクリスマス、\n楽しみですぅ…♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2761, 108501, 102, 8, "クリスマスはその…\n二人で…あうぅ…\nなんでもないですぅ…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2762, 108501, 1, 1, "うう…パーティーが\n楽しみで、あんまり\n眠れなかったよぅ…", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2763, 108501, 2, 1, "ケーキを食べられなく\nなっちゃうから、\nお昼は軽めにしないと", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2764, 108501, 3, 1, "ちょっと眠いけど…\n今日は少しだけ\n夜更かししたいです♪", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2766, 108501, 2, 1, "ふかふかぁ…\nクリスマスの準備中に\n眠くなっちゃいますぅ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2767, 108501, 3, 1, "この子と一緒に\nおままごとしたいな♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2768, 108501, 4, 1, "いい匂い…♪\nみんなで\nアロマパーティー\nしたいなぁ…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2769, 108501, 5, 1, "ぬっくぬく…♪\nもう、ここから\n離れたくないよぉ…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2771, 108501, 7, 1, "はむはむ…♪\nクリスマスのお料理\nより美味しいかも～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2772, 108501, 8, 1, "すごい…\nクリスマスの\n演劇って、\nこんなにあるんだ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2774, 108501, 14, 1, "すごい…まるで、\nクリスマスの演劇を\n観てるようですぅ…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2775, 108501, 15, 1, "とっても可愛いです♪\nお芝居の相手役に\nしたいなぁ…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2776, 108601, 1, 1, "甘ぁいケーキも、\nデッカイ七面鳥も、\nぜーんぶ私のだー♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2777, 108601, 2, 1, "毎日が\nクリスマスだったら\nいいのにな～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2778, 108601, 3, 1, "プレゼントはあるし、\n料理は美味しいし、\nクリスマス最高ー♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2779, 108601, 4, 1, "ここでぷうきちの\nクリスマス一発芸♪\n『無茶ぶりするな！』", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2780, 108601, 5, 1, "ぷうきちがいつもより\n重いかも…太ったの？\n『衣装の重さだよ！』", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2781, 108601, 101, 4, "お兄ちゃんが\n一緒にいてくれると\nクリスマス楽しいね♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2782, 108601, 102, 8, "お兄ちゃんに抱きつく\nと、なんだかドキドキ\nする…なんでかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2783, 108601, 1, 1, "メリークリスマス♪\nプレゼントは、\nいつでも受付中だよ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2784, 108601, 2, 1, "このクマさん耳\n可愛いでしょ♪\nぷうきちと\nお揃いなんだー♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2785, 108601, 3, 1, "お兄ちゃん…私、\n甘ぁいケーキが\n食べたいなぁ…♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2786, 108601, 4, 1, "クリスマスぷうきちは\nぬくぬく感が凄いよ！\nお兄ちゃんもさわる？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2787, 108601, 101, 4, "えへへ…お兄ちゃんの\n手は、大きくて、\n暖かくて大好き～♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2788, 108601, 102, 8, "来年のクリスマスも、\nこうやって\nお兄ちゃんに\n甘えていたいな～♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2789, 108601, 1, 1, "あれぇ…\n私のケーキはぁ…？\n『ちゃんと起きろ』", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2790, 108601, 2, 1, "お昼はデッカイ\n七面鳥が食べたーい♪\n『夜まで我慢しろ』", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2791, 108601, 3, 1, "『もう寝る時間だぞ』\nサンタさんが来る\nまで起きてるぅ…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2793, 108601, 2, 1, "このふかふか具合…\nぷうきち！\n負けてられないよ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2794, 108601, 3, 1, "この子、\nすっごくかわいい～♪\n『ライバル登場か！』", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2795, 108601, 4, 1, "良い匂い…あっ！\nぷうきちにこの\n匂いを移せば…！\n『匂い袋にするな』", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2796, 108601, 5, 1, "あったか～い♪\nえへへ、ぷうきちも\nあっためてあげる♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2798, 108601, 7, 1, "ぷうきち！　これ、\nとってもおいしいよ♪\n『食べすぎるなよ』", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2799, 108601, 8, 1, "お、大人の私なら、\nこれぐらい…読め…\n『無理するな…』", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2801, 108601, 14, 1, "絵が飛び出す…\nぷうきちに絵本を\n入れれば、イタズラに\n使えるかも…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2802, 108601, 15, 1, "か、可愛いけど…\nぷうきちサンタの方が\n可愛いんだからぁ…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2803, 108901, 1, 1, "着物も慣れると\n動きやすいものさ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2804, 108901, 2, 1, "鼻緒が緩んでしまった\n下駄で修業は\n無理があったかな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2805, 108901, 3, 1, "小腹が空いた…\nユイが作った餅、\nまだ食べられるかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2806, 108901, 4, 1, "服装も変わったし\nいつもと違う戦い方を\n想定しないと…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2807, 108901, 5, 1, "…ヒヨリは動きすぎて\n着崩れしてないか\n心配だ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2808, 108901, 101, 4, "これからもずっと\n仲間と、彼と\n一緒に…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2809, 108901, 102, 8, "願いを叶えるために\nもっと自分を\n鍛えていかないと！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2810, 108901, 1, 1, "頭の飾りがずれてた？\nそうか、直してくれて\nありがとう", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2811, 108901, 2, 1, "着物がそんなに\n珍しい？\nキミも着てみる？\n…冗談だよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2812, 108901, 3, 1, "肩掛けの触り心地が\n気に入った？\nならもっと触る？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2813, 108901, 4, 1, "ああ、この刀？\n釣り仲間に見繕って\nもらったんだ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2814, 108901, 101, 4, "キミといると\n気持ちが段々と\n安らいでいくよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2815, 108901, 102, 8, "キミは私の行く先を\n照らしてくれる\n光のような存在だよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2816, 108901, 1, 1, "おはよう\n朝の修行を始めるけど\nキミもどう？", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2817, 108901, 2, 1, "この時間に軽く\n休憩をとるといい\n能率がよくなるよ", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2818, 108901, 3, 1, "もう夜か…\n明日もまた頑張ろう\nそれじゃ、おやすみ", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2820, 108901, 2, 1, "…疲れていたのかな\nもう少しで眠る\nところだった…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2821, 108901, 3, 1, "ふふっ、正月衣装を\n着せてやるのも\nよさそうだ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2822, 108901, 4, 1, "ふぅ…いい香りだ\n慌ただしい日々から\n心が解放される", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2823, 108901, 5, 1, "この心地よさ…\n寝てしまわないよう\n気をつけないと", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2825, 108901, 7, 1, "おいしいけど\n帯がきつくなる前に\n手を止めよう…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2826, 108901, 8, 1, "外を出歩くのも\nいいけど、中で\n読書もありだな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2828, 108801, 1, 1, "…騎士クンって\nおせち料理で\n何が好きなんだろう？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2829, 108801, 2, 1, "着物でお掃除や\nお洗濯するの\n慣れてきた♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2830, 108801, 3, 1, "今年は\nどんな福袋を\n買おうかな…♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2831, 108801, 4, 1, "お餅に使う調味料\nみんなで違うから\n色々用意しなきゃ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2832, 108801, 5, 1, "ほっぺがお餅みたいに\nぷにぷに…\n食べ過ぎちゃった…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2833, 108801, 101, 4, "来年も、再来年も\nみんなで一緒に\n新年を迎えたいな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2834, 108801, 102, 8, "お願いです…\nいつかこの気持ちが\n伝わりますように…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2835, 108801, 1, 1, "髪飾りが落ちそう？\nじゃあ…騎士クン\nつけ直してくれる？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2836, 108801, 2, 1, "着物が着たいの？\nじゃあ今度一緒に\n買いに…行く？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2837, 108801, 3, 1, "あっ、騎士クン\nお雑煮のお餅\nいくつ入れたい？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2838, 108801, 4, 1, "外、寒かったよね？\nはいこれ。熱い甘粥\n飲むと温まるよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2839, 108801, 101, 4, "今年は騎士クンと…\nえっ？　騎士クン！？\nな、なんでもないよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2840, 108801, 102, 8, "騎士クンの願い事\nわたしが\n叶えてあげたいな…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2841, 108801, 1, 1, "おはよう\n太陽の光に当たると\n元気が出るよ", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2842, 108801, 2, 1, "お昼ごはん\n作らなきゃ…\n騎士クンも食べる？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2843, 108801, 3, 1, "一日が終わるのは\nあっという間だよね\n一年も同じなのかな？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2845, 108801, 2, 1, "ふぅ…\n帯も少し\nゆるめちゃおう…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2846, 108801, 3, 1, "お正月用に\n着物を縫って\nあげたいな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2847, 108801, 4, 1, "これでお肌が\n保湿出来たら\nいいのになぁ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2848, 108801, 5, 1, "ん～…\nぬくぬくしてて\n動きたくないよ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2850, 108801, 7, 1, "…冬眠しないのに\n熊さんみたいに\n食べちゃいそう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2851, 108801, 8, 1, "年末年始は\n告白のチャンス…\nむ、無理無理！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2853, 108701, 1, 1, "みんな忙しそう…\n待っててね、\nすぐ助けるから！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2854, 108701, 2, 1, "ユイちゃんと\nレイさん…よし！\nどっちも手伝おう！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2855, 108701, 3, 1, "あっ、着崩れてる！\nわわっ、どうしよ！\n帯が結べないよー！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2856, 108701, 4, 1, "初詣楽しみー！\n出店はどこから\n行こうかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2857, 108701, 5, 1, "お正月だし\nみんなで楽しく\n遊んじゃおー！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2858, 108701, 101, 4, "うーん…\n騎士くんはお正月\n何してるんだろ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2859, 108701, 102, 8, "ううぅー…\n騎士くんを見てると\nドキってなっちゃう…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2860, 108701, 1, 1, "あたしから\nおいしそうな匂い？\nくんくん…ソースだ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2861, 108701, 2, 1, "ヘアピンが…\n拾ってくれたんだ！\nありがとう騎士くん！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2862, 108701, 3, 1, "髪飾りがかわいい？\n耳の近く触りたい？\n…優しくね？　", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2863, 108701, 4, 1, "グローブで食べるの\n大変そう？　特訓して\n今はお箸も使えるよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2864, 108701, 101, 4, "えっ、ほっぺが赤い？\nそうかな…？\n気のせい気のせい！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2865, 108701, 102, 8, "騎士くんといるとね…\nなんでもない！\nまだ内緒っ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2866, 108701, 1, 1, "おはよー！\n今日も明るく元気に\nがんばろー！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2867, 108701, 2, 1, "お腹すいてない？\n一緒にご飯食べよ！\n何にしようかなー♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2868, 108701, 3, 1, "今日もいっぱい\nがんばったー！\nいつも？　…そう？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2870, 108701, 2, 1, "休憩休憩♪\nちょっと休んで\nガンガン動くぞー！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2871, 108701, 3, 1, "かわいい♪\nでも、壊しそうで\nちょっと怖い…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2872, 108701, 4, 1, "はぁー♪\nいい香りー…\n気分スッキリ♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2873, 108701, 5, 1, "ほっこりしてて\nぬくぬく…\n丸まりたい…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2875, 108701, 7, 1, "ど、どうしよう…！\n手が、止まんないよ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2876, 108701, 8, 1, "へー…お年玉って\n昔はお餅だったんだ\n…今のほうがいいね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2878, 109001, 1, 1, "甘いチョコの香りを\n漂わせて…ふふっ\nこれで準備万端ですわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2879, 109001, 2, 1, "ここを私のチョコで\n埋め尽くしますわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2880, 109001, 3, 1, "エプロンを着て\nあの方を待つ私…\nまるで…クスクス", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2881, 109001, 4, 1, "キッチンはどこかしら\n今のうちに作って\nおきたいものが…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2882, 109001, 5, 1, "あのお方を私のチョコ\nなしでは生きられない\n体に…ふふっ\n冗談ですわ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2883, 109001, 101, 4, "このチョコは運命の味\n一口食べれば私の虜…\nそれも運命ですわ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2884, 109001, 102, 8, "私ともあろうものが\nなんだかドキドキして\nきましたわ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2885, 109001, 1, 1, "あなた様。私に\n隠しているものなど\nありませんよね…？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2886, 109001, 2, 1, "私の手作りチョコです\n味の方にも自信\nありますのよ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2887, 109001, 3, 1, "少し苦いのは\nチョコがビターなだけ\nお気になさらず…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2888, 109001, 4, 1, "解毒薬はどこに\nいれたかしら…？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2889, 109001, 101, 4, "運命で結ばれている\nあなた様と私…\nチョコも特別でないと\nいけませんわ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2890, 109001, 102, 8, "チョコを食べた後は\nメインディッシュ…\nさぁ、私を召し上がれ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2891, 109001, 1, 1, "目覚めのチョコですわ\nすっきり目が覚めます\nしばらく眠れなく\nなりますけど…", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2892, 109001, 2, 1, "おやつのチョコですわ\nデザートにどうぞ\n空腹を感じなく\nなりますわ…クスクス", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2893, 109001, 3, 1, "お夜食のチョコですわ\nとてもよく眠れます\nさ、溶けてしまう前に", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2895, 109001, 2, 1, "この心地良さ…\n参考になりますわ\n次の薬は…クスクス", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2896, 109001, 3, 1, "この中、何か\n入りそうですわね\n…チョコレート、とか", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2897, 109001, 4, 1, "いい香り…この香りと\n同じものを調合して\n私も持っていたい", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2898, 109001, 5, 1, "この暖かさ…\nチョコが溶けないよう\n気を付けなくては", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2900, 109001, 7, 1, "いい味をしていますわ\n私も腕を磨きましょう\n料理と、調合の腕を…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2901, 109001, 8, 1, "勉強になりますわ\n知識はいくらあっても\n困りませんもの\n特に、人体の知識は…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2903, 109101, 1, 1, "弟くんのことを\n考えると\nあっという間に時間が\n過ぎちゃう…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2904, 109101, 2, 1, "お姉ちゃんの\n元気の源は、弟くんの\n喜ぶ顔なんだから♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2905, 109101, 3, 1, "弟くんと会ったら\nまずは抱きしめて\nそれからそれから…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2906, 109101, 4, 1, "今日のお姉ちゃんは\n昨日のお姉ちゃんより\nいいお姉ちゃんだよ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2907, 109101, 5, 1, "弟くん、もっと\nお姉ちゃんに\n甘えてもいいのに♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2908, 109101, 101, 4, "お姉ちゃんのチョコ\n食べさせてあげたい…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2909, 109101, 102, 8, "ドキドキしてきた…\nお姉ちゃんなのに\nどうしてかなぁ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2910, 109101, 1, 1, "お姉ちゃんを\n好きなだけ\n触っていいよ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2911, 109101, 2, 1, "そんなにお姉ちゃんの\nチョコが欲しいの？\n食いしん坊さん♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2912, 109101, 3, 1, "ナデナデするのは\nお姉ちゃんの仕事！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2913, 109101, 4, 1, "ありがとうなんて…\nお姉ちゃんとして\n当たり前だよ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2914, 109101, 101, 4, "どうしたのかなっ？\nお姉ちゃんはいつでも\n弟くんのそばにいるよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2915, 109101, 102, 8, "チョコを作ったよ\n隠し味は、たくさんの\n弟くんへの愛情♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2916, 109101, 1, 1, "おはよ。お姉ちゃんが\n顔を洗うの\n手伝ってあげるね♪", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2917, 109101, 2, 1, "ご飯を食べたら\n眠たくなるよね…\nお姉ちゃんと寝る？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2918, 109101, 3, 1, "そろそろ眠くなった？\nお姉ちゃんが添い寝\nしてあげるね♪", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2920, 109101, 2, 1, "この気持ちよさは\nお姉ちゃんも太鼓判♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2921, 109101, 3, 1, "このかわいさは\nお姉ちゃんも大満足♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2922, 109101, 4, 1, "この香り、チョコに\n使えるかな…？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2923, 109101, 5, 1, "弟くんと一緒に\nぬくぬくしたいな…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2925, 109101, 7, 1, "すごくおいしいねっ♪\nあとで運動しなきゃ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2926, 109101, 8, 1, "本を読んでる\n知的なお姉ちゃん\n素敵でしょ♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2928, 106101, 1, 1, "アタシって…\nここにいても\nいいのか…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2929, 106101, 2, 1, "ちゃんとした建物に\nいるのが久しぶりで\nそわそわする…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2930, 106101, 3, 1, "ここで天楼覇断剣\n出す練習したら\nアイツら怒るかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2931, 106101, 4, 1, "ギルドハウスか…\nこういう隠れ家\n欲しかったんだよな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2932, 106101, 5, 1, "めちゃくちゃ暇だ…\n…なんかやること\nないのか？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2933, 106101, 101, 4, "みんなが早く\nアタシのこと\n思い出さないかな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2934, 106101, 102, 8, "アイツはいいヤツだし\n戦う時はアタシも\n協力してやるか！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2935, 106101, 1, 1, "ちゃんはいらん！\nノウェムでいい！\n分かったか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2936, 106101, 2, 1, "どうした？\n戦うのか！？\n…なんだ、違うのか", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2937, 106101, 3, 1, "身長のことは\nあんまり言うな！\n気にしてるからな！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2938, 106101, 4, 1, "アタシの戦い方は\nなんでもアリだ！\nだから見習うなよ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2939, 106101, 101, 4, "オマエとの思い出か…\n敵として戦ってばっか\nだったな！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2940, 106101, 102, 8, "オマエやオクトーと\n一緒に、あちこち\nいってみたいんだ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2941, 106101, 1, 1, "よっ！　おはよう！\n朝だぞー！\n起きてるかー？", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2942, 106101, 2, 1, "腹減ったー！\n飯食うぞ、飯！\nオマエも行くよな？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2943, 106101, 3, 1, "ふわぁ…\nまだ起きてんのか？\n元気だなオマエ…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2945, 106101, 2, 1, "ふぃ～…\n極楽極楽…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2946, 106101, 3, 1, "おいオマエ！\n寝技の練習に\n付き合ってもらうぞ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2947, 106101, 4, 1, "くんくん…\nたまにはこういうのも\n悪くないかもな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2948, 106101, 5, 1, "あー…\nもっと前にコイツと\n出会いたかった…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2950, 106101, 7, 1, "VRでも飯はうまい！\n腹が減るのはちゃんと\n生きてる気がするな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2951, 106101, 8, 1, "勉強は大事だな！\nうん！…だめだ！\n全然わからん！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2953, 101401, 1, 1, "探偵というのは\n謎を追い求める\n生き物なのさっ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2954, 101401, 2, 1, "事件のオチは\n往々にして\n取るに足らないものさ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2955, 101401, 3, 1, "読書で得る知識が\n探偵の\n血肉になるんだよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2956, 101401, 4, 1, "この前の\n捜査資料…\nどこにあったかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2957, 101401, 5, 1, "どんな小さな謎も\n私にとっては\n宝石みたいなものさ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2958, 101401, 101, 4, "助手くんと一緒だと\nよい発想が浮かぶ…\nどうしてだろうね？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2959, 101401, 102, 8, "助手くんの好きな\nタイプって…\nす、推理してみるっ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2960, 101401, 1, 1, "珈琲、淹れたてだよ\n一杯どうだい？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2961, 101401, 2, 1, "事件の考察を\nまとめてみたんだけど\n聞いてくれるかな？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2962, 101401, 3, 1, "小さな違和感を\n見逃さない…\n探偵の心得だよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2963, 101401, 4, 1, "機能的な手帳とペン…\nこの仕事には\n欠かせないね！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2964, 101401, 101, 4, "助手くんに\n珈琲…\n淹れてほしいなぁ～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2965, 101401, 102, 8, "助手くんは\n私にとって\n最高の相棒だよ☆", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2966, 101401, 1, 1, "朝はいいひらめきが\n得られるんだ…\nさあ捜査を始めよう！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2967, 101401, 2, 1, "一休みしたら\n推理の答え合わせを\nしてみようか？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2968, 101401, 3, 1, "夜の張りこみかい？\nなら私も\n同行させてほしいな！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2970, 101401, 2, 1, "脳を酷使した後は\nゆっくり休む…\nこれも仕事だよ？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2971, 101401, 3, 1, "この愛らしさ…\n要捜査対象だね！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2972, 101401, 4, 1, "思考が活性化される\n素敵な香りだね…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2973, 101401, 5, 1, "くぅ、心地よすぎる…\n今だけは捜査も\n一休みだね♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2975, 101401, 7, 1, "いっぱい食べて\nいっぱい考える\n探偵の本分だね！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2976, 101401, 8, 1, "探偵だからね\nミステリには\n一家言あるんだ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2978, 105501, 1, 1, "先輩、素敵だなぁ…\nもっと近づければ\nいいのに…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2979, 105501, 2, 1, "せ、先輩がいた部屋！\n私には刺激が強い…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2980, 105501, 3, 1, "先輩を観察しやすい\n場所はあるかな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2981, 105501, 4, 1, "バ、バイト代出たし、\nせ、先輩をご飯とかに\n誘いたい…な", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2982, 105501, 5, 1, "今頃先輩は\nあの辺かな…？\n見に行ってみよう！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2983, 105501, 101, 4, "私と先輩の出会いって\n思い返すと少女漫画\nみたい…えへへ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2984, 105501, 102, 8, "せ、先輩になら\n観察されてもいいなぁ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2985, 105501, 1, 1, "バイト先に新しい子が\n入って来たんです…\nわ、私、先輩です♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2986, 105501, 2, 1, "せ、先輩…？\nえっと、あの、その、\nあわわわわ…！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2987, 105501, 3, 1, "えへへ…えへへ～\nせ、先輩から\n話しかけてくれました", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2988, 105501, 4, 1, "ま、また…\n先輩と一緒に\nバイトしたい…です", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2989, 105501, 101, 4, "私と先輩が同じ空間に\n…ゆ、夢みたいです", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2990, 105501, 102, 8, "せ、先輩のそばに\n居られて、私\n今、とっても\n幸せです…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2991, 105501, 1, 1, "せ、先輩…！\nおひゃっ！\nおはようございます！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2992, 105501, 2, 1, "先輩ランチに\n行きましょう！\nよし、練習はバッチリ", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2993, 105501, 3, 1, "あ、せ、先輩。\nえと、そ、外…\nもう真っ暗ですねぇ", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2995, 105501, 2, 1, "こ、ここで先輩も\n休んだり…はっ！\nわ、私ったらなにを！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2996, 105501, 3, 1, "かわいいですねー\n君も一緒に、\n先輩を観察する？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2997, 105501, 4, 1, "ここから先輩の\n香りがすればなあ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2998, 105501, 5, 1, "先輩と一緒に\nぬくぬくできたら\n最高なんだけどなぁ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3000, 105501, 7, 1, "おいし～\nこれは\n先輩も好きな味かな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3001, 105501, 8, 1, "す、好きな人に\n近づく方法…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3003, 109201, 1, 1, "私の力は、大切な\n人たちを護るために\n役立てたいんだ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3004, 109201, 2, 1, "きれいな景色と\n美味しいご飯！\nこの世界も素敵だね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3005, 109201, 3, 1, "この世界のみんなとも\nもっと仲良く\nなりたいな！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3006, 109201, 4, 1, "わー！\nあれもこれも\n気になっちゃうな！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3007, 109201, 5, 1, "しっかりしなきゃって\n思ってても、\nグレアといるとつい\n甘えちゃうんだよね～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3008, 109201, 101, 4, "違う世界でも\n友だちができて、\nすごくうれしいよ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3009, 109201, 102, 8, "みんなとも一緒に\n授業とか受けたいな～\n絶対楽しそうだもん！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3010, 109201, 1, 1, "歩き方がきれい？\nえへへ、そうかな…？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3011, 109201, 2, 1, "勉強は好きだよ！\n知識が増えていくのは\nやっぱり楽しいから", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3012, 109201, 3, 1, "私たちの国マナリアは\n人々の間に魔法が\n伝えられた、その\n始まりの地なんだ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3013, 109201, 4, 1, "この部屋にも、\nもっとたくさん本棚が\n増えないかな～…？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3014, 109201, 101, 4, "あなたも魔法、\n覚えてみる？\n私、教えるよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3015, 109201, 102, 8, "お茶の時間に\nしたいな～…\n一緒にどう？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3016, 109201, 1, 1, "おはようっ！\n今日も素敵な\n一日にしようね！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3017, 109201, 2, 1, "お昼ご飯は\nちゃんと食べた？\n午後も頑張ろうね！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3018, 109201, 3, 1, "つい本の続きが\n気になって、夜更かし\nしちゃうんだよね～…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3020, 109201, 2, 1, "はぁ、気持ちいい～…\nちょっとだけ\n眠っちゃおうかな…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3021, 109201, 3, 1, "わあ、これ可愛い！\nグレアにも見せて\nあげたいな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3022, 109201, 4, 1, "いい香り～！\nなんだか気持ちが\n落ち着くね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3023, 109201, 5, 1, "あぁ、あったかくて\n幸せ～♪　身体が\nポカポカしてきたよ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3025, 109201, 7, 1, "こ、これ美味し～！\n止められないよー！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3026, 109201, 8, 1, "ふんふん…\n…はっ！　む、夢中に\nなっちゃってた…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3028, 109301, 1, 1, "新しい建物に入ると\n必ず一度は迷子に\nなっちゃうですよ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3029, 109301, 2, 1, "おやつはどこですか？\nお腹が空いて\nきちゃったですぅ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3030, 109301, 3, 1, "宿題、７つぐらい\n出しそびれたですけど\nまあ大丈夫ですぅ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3031, 109301, 4, 1, "不思議なこと\nばかりですけど…\nまぁ大丈夫です！\nこれもせいしゅんです", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3032, 109301, 5, 1, "なにか面白そうなもの\nないですかね～？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3033, 109301, 101, 4, "ここのみんなとも\nもうお友だちですぅ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3034, 109301, 102, 8, "おなかが減ったです…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3035, 109301, 1, 1, "ごはんも美味しいし\nここはいいところです\nルゥは幸せですぅ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3036, 109301, 2, 1, "魔法の話ですか？\nそれはアンせんぱいに\n聞いてほしいですぅ…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3037, 109301, 3, 1, "あなたもお勉強\n苦手ですか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3038, 109301, 4, 1, "ルゥの帽子、\nかわいいです？\nお気に入りですぅ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3039, 109301, 101, 4, "お友だちいっぱいで、\nルゥはうれしいです♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3040, 109301, 102, 8, "一緒にお勉強…！？\nぐえぇ、イヤですぅ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3041, 109301, 1, 1, "朝ですねぇ…ふわわ…\nルゥはまだ夢の中に\nいたいですぅ…", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3042, 109301, 2, 1, "午後もいっぱい\n遊ぶですよ～！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3043, 109301, 3, 1, "夜ふかしって\nドキドキするです！\nせいしゅんですぅ！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3045, 109301, 2, 1, "ふかふかですぅ～♪\nおひるねが\nしたくなるですぅ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3046, 109301, 3, 1, "これふかふかですぅ！\n机でうとうとする時の\n枕によさそうですぅ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3047, 109301, 4, 1, "ふぁぁ、いいにおい…\nなんだか気持ちが\nよくなってきたですぅ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3048, 109301, 5, 1, "ぽっかぽかですぅ～！\nあったまったら\nまた遊びにいくですぅ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3050, 109301, 7, 1, "お、おいしいですぅ！\n全部ルゥに\nくださいですぅ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3051, 109301, 8, 1, "本は…読むと、\nすぐ眠気がぁ…\n…ぐぅ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3053, 109401, 1, 1, "ピアノ…\nどこかにないかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3054, 109401, 2, 1, "ふぅ…\n落ち着くな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3055, 109401, 3, 1, "マナリアはいつも\n賑やかなんだ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3056, 109401, 4, 1, "みんな…\n今頃何してるかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3057, 109401, 5, 1, "まさか違う世界に\n来ちゃうなんて…\nびっくりだよね…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3058, 109401, 101, 4, "人と話すのは\n得意じゃないな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3059, 109401, 102, 8, "ここのみんなと一緒に\n学院に通えたら…\nちょっと面白いかも", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3060, 109401, 1, 1, "やっぱり、私の翼や\n尻尾…気になる？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3061, 109401, 2, 1, "一番得意なのは、\nやっぱり炎魔法かな…\n加減は難しいけど…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3062, 109401, 3, 1, "私の故郷は竜の国…\n険しい山脈を\n越えた先にあるんだ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3063, 109401, 4, 1, "アン…？\nあっ、ご、ごめんね！\n間違えちゃった…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3064, 109401, 101, 4, "ふん…ふん…♪\n…わっ！\n今の、聞いてた…？\nは、恥ずかしい…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3065, 109401, 102, 8, "私に手伝えること、\nなにかある…？\nなんでも言ってね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3066, 109401, 1, 1, "朝の空気って\nすごく\n静かで透き通ってて…\nなんだか落ち着くよね", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3067, 109401, 2, 1, "学院食堂のランチも\n美味しいけど…\nここのご飯も好きかな", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3068, 109401, 3, 1, "そろそろベッドに\n行かないとだね\n…寝相、今夜こそ\n気を付けよう…！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3070, 109401, 2, 1, "羽を伸ばしたく\nなっちゃうね…ふふ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3071, 109401, 3, 1, "わ、私が持つには\nかわいすぎるかな…\nでも、素敵だね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3072, 109401, 4, 1, "いい香り…\nこれ、\nアンも好きそう…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3073, 109401, 5, 1, "火を見ていると\n少し落ち着くんだ…\nへ、変かな？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3075, 109401, 7, 1, "わ、美味しい…！\nこれ、どうやって\n作ったのかな…？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3076, 109401, 8, 1, "この本…アンにも\n見せてあげたら\n喜ぶかな、ふふっ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3078, 109501, 1, 1, "着物はきれいですが\n歩きにくいです…\nいっそ脱いで…ぐふふ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3079, 109501, 2, 1, "あっ、いけません\n着物がはだけて…はっ\nもしやドＳさんが…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3080, 109501, 3, 1, "この部屋は妄想する\nのにうってつけです…\nぐふふ…じゅるり", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3081, 109501, 4, 1, "扇をゆっくりと開く\nこの感触…なぜだか\n艶めかしさを感じます", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3082, 109501, 5, 1, "「マッサージをすると\n言いながらドＳさんの\n手は次第に」…ぐふふ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3083, 109501, 101, 4, "…ドＳさんの視線を\n思い出しながら踊りの\n練習です…イイ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3084, 109501, 102, 8, "よいではないか\nよいではないか～\nいけませんご堪忍を～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3085, 109501, 1, 1, "あ～れ～～～！\nドＳさんもお好きな人\nですねぇ…ぐふふ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3086, 109501, 2, 1, "どこにいてもドＳさん\nの気配を感じ取れる\nようになりました…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3087, 109501, 3, 1, "こんな場所で\nなんというプレイを！\n…妄想でした", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3088, 109501, 4, 1, "クウカを押し倒して\nそのまま…じゅるり\nああっ、着物が…！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3089, 109501, 101, 4, "ドＳさんは優しいです\n優しいのにドＳとは\nさすが人格破綻者…！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3090, 109501, 102, 8, "ドＳさんの考えている\nことが伝わってきます\nはい…クウカ脱ぎます", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3091, 109501, 1, 1, "眩しい朝日がクウカの\n体を焦がします\n…それもイイ！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3092, 109501, 2, 1, "お昼のランチは\nクウカがいいだなんて\n大胆過ぎです…じゅる", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3093, 109501, 3, 1, "…今夜のドＳさんは\n大胆です…ぐふふ…\nはっ、視線が…！？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3095, 109501, 2, 1, "この心地良さ、\n心が休まりすぎて\n体が疼いてきます…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3096, 109501, 3, 1, "すごく愛らしいです…\n妄想も出てこない\nくらい…じゅるり", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3097, 109501, 4, 1, "この香りに包まれて\nいると、いい気持ちに\nなって…いきますぅ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3098, 109501, 5, 1, "「熱を帯びた体は\nどうしようもなく\n誰かを求め…」ぐふふ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3100, 109501, 7, 1, "お腹が膨らんで\nいきます…それは\nまるで…ぐふふ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3101, 109501, 8, 1, "クウカの探している\nジャンルはどこに\nありますかね？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3103, 109601, 1, 1, "カチョーフーゲツは\nトーゴクの心を\n表した言葉デスね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3104, 109601, 2, 1, "オー！　新しい忍法を\n思いついたデス！\nさっそく試すデス！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3105, 109601, 3, 1, "トーゴクの天気には\n槍が降る日も\nあるそうデス…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3106, 109601, 4, 1, "我がショーグンは\n永遠にフメツデース\nいざ出陣デース！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3107, 109601, 5, 1, "壁に耳あり\n障子に…ん？\nメアリーって誰デス？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3108, 109601, 101, 4, "ショーグンがいれば\nワタシはどんな\n忍法も使いこなせマス", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3109, 109601, 102, 8, "不届き者はどこで\nゴザルデス？　忍法で\n退治してくれヨウ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3110, 109601, 1, 1, "ショーグン！　新しい\n忍法を覚えたデス！\n見てほしいデース！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3111, 109601, 2, 1, "レッツ手裏剣道デス！\n投げてソウロウで\nゴザルデース！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3112, 109601, 3, 1, "ふりかかる火の粉は\nワタシが払うデス！\nバンバン！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3113, 109601, 4, 1, "とっておきの忍法が\n吹き荒れるデス！\nよそ見はダメデスヨ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3114, 109601, 101, 4, "ショーグンは\n何階建てのお城を\n建てたいデスか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3115, 109601, 102, 8, "テンシュカクから\n平和な城下町を\n眺めてみたいデス…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3116, 109601, 1, 1, "おはようございマス！\n本日はオヒガラモヨク\n良い日になりそうデス", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3117, 109601, 2, 1, "お腹すいたデース…\nショーグンもご飯に\nしまショウ！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3118, 109601, 3, 1, "闇討ちに遭わぬよう\n夜は気を付けて\n歩くデスよー？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3120, 109601, 2, 1, "天下分け目の\n戦いに備えて…\nマッタリデース", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3121, 109601, 3, 1, "口寄せの術で\n召喚したいくらい\nかわいいデース", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3122, 109601, 4, 1, "いい匂いデ～ス\n香り玉の開発の\n参考になりそうデスね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3123, 109601, 5, 1, "ワタシの懐よりも\nゾーリが暖まりマス…\nライバル出現デス…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3125, 109601, 7, 1, "腹が減っては\n戦はできぬデース！\n天下統一に向けて\n腹ごしらえデス！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3126, 109601, 8, 1, "ムム、暗号文デス！？\n解読には時間が\nかかりそうデース…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3128, 109701, 1, 1, "掃除も終わりましたし\nそろそろ、食事の\n準備をしなくては", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3129, 109701, 2, 1, "異世界の演劇…\n許されるならば、\n観てみたいです", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3130, 109701, 3, 1, "こちらの世界には、\nマヨネーズが普通に\n存在するのですね…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3131, 109701, 4, 1, "こちらの世界は食材も\n面白い物が多いですね\nなにかレシピを\n考えてみましょうか", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3132, 109701, 5, 1, "みょんみょんみょん…\n魔法の練習は\nこれぐらいですね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3133, 109701, 101, 4, "倒木の撤去で思わぬ\n収入となりました\nサレン様に\nお渡ししておかないと", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3134, 109701, 102, 8, "料理を美味しく\n召し上がって頂けると\nメイド冥利につきます", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3135, 109701, 1, 1, "お出かけでしたら、\nレムも同行\nさせてくださいね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3136, 109701, 2, 1, "レムにできること\nでしたら、なんでも\nお申し付けください", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3137, 109701, 3, 1, "じーー。いえ、\nその髪の長さが少し、\nとても少し、かなり\n少し気になったもので", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3138, 109701, 4, 1, "鉄球ですか？\nあれは護身用ですので\n問題はありませんよ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3139, 109701, 101, 4, "計算の勉強は\n進んでおりますか？\nレムでよろしければ、\nいつでもお教えします", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3140, 109701, 102, 8, "次はもっと、大きな\n魔物料理に挑戦します\nワイバーンの姿焼き\nなんてどうでしょうか", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3141, 109701, 1, 1, "おはようございます\n朝食の準備が\nできておりますので、\n食卓におこしください", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3142, 109701, 2, 1, "昼食の時間ですね\n何か食べたいものは\nありますか？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3143, 109701, 3, 1, "寝ないのか、ですか？\nメイドの就寝は、\n屋敷で最後と\n決まっておりますので", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3145, 109701, 2, 1, "ふんわり…\nこのふんわりさは、\n鬼をも黙らせます…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3146, 109701, 3, 1, "ふふっ…異世界でも\nかわいいものは\n共通なのですね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3147, 109701, 4, 1, "よい香りですね\n魔女教の悪臭とは\n大違いです", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3148, 109701, 5, 1, "ぬくぬくです…はっ！\nこれはいけません！\n人を…いえ、\n鬼をだめにします！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3150, 109701, 7, 1, "はむはむ…美味しい！\nこの味を再現できたら\n褒めてもらえる\nかもしれません…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3151, 109701, 8, 1, "絵がいっぱい…\n異世界の本は\nとても面白いですね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3153, 109801, 1, 1, "異世界であろうと、\nラムのやるべきことは\n変わらないわ\nまずは休憩するわよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3154, 109801, 2, 1, "マヨネーズがある\nということは、\nバルスの故郷が近い\nのかもしれないわね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3155, 109801, 3, 1, "この世界では、バルス\nの言う言葉が使われる\nときがあるのね\n忌々しいかぎりだわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3156, 109801, 4, 1, "ラムたち以外にも\nメイドが居るのね\nラムが…いえ、\n仕事を押しつけるわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3157, 109801, 5, 1, "…千里眼で視るかぎり\n周囲に異変はないわね\n危険がないなら、\nあとはレムに任せるわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3158, 109801, 101, 4, "…ラムもまた、\n「お姉ちゃん」って\n呼ばれる日が\nくるのかしらね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3159, 109801, 102, 8, "ハッ！　ラムが作る\n蒸かし芋は完璧ね\n…気が向いたら、また\n作ってやろうかしら", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3160, 109801, 1, 1, "ラムに構う暇が\nあるなら働きなさい\nそうすれば、\nラムが楽をできるわ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3161, 109801, 2, 1, "ラムに何かを\n期待しても無駄よ\nラムに命令できるのは\nロズワール様だけだわ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3162, 109801, 3, 1, "暇なら買い物に\n付き合いなさい\n荷物を\n持たせてあげるわ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3163, 109801, 4, 1, "ラムになんの用？\nふざけた内容なら\nねじ切るわよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3164, 109801, 101, 4, "そういえば、あなたは\n記憶喪失だったわね…\n思い出す努力をなさい\n…家族が悲しむわよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3165, 109801, 102, 8, "蒸かし芋が欲しい？\n随分と厚かましいわね\n…まだ残りがあるから\n好きに食べなさい", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3166, 109801, 1, 1, "だらしない顔ね\n顔を洗って\nしゃんとなさい", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3167, 109801, 2, 1, "お腹が空く時間ね\nラムの昼食を\n用意させてあげるわ", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3168, 109801, 3, 1, "メイドが寝るのは\n屋敷で最後…\nラムが寝るために、\nさっさと寝なさい", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3170, 109801, 2, 1, "…ここから動かない\nという、誓いを\n立てたくなるわね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3171, 109801, 3, 1, "これは…かわいいわね\nもちろん、\nラムほどではないけど", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3172, 109801, 4, 1, "…香炉かしら？\nこちらの世界にも\n質のよい物があるのね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3173, 109801, 5, 1, "見つけてしまったの\nかもしれないわ…\nラムの楽園を！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3175, 109801, 7, 1, "…はむっ\nラムの蒸かし芋ほど\nではないけど、\nなかなかいけるわね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3176, 109801, 8, 1, "異世界ならではの\n面白い内容もあるわね\nとても興味深いわ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3178, 109901, 1, 1, "ねえパック、\nにゃんにゃか\nにゃかにゃんしよっか", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3179, 109901, 2, 1, "時間もあるし、\nこっちの世界の石で\n石積みしようかな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3180, 109901, 3, 1, "私もお姉さんだし、\nサレンさんを\n見習わないと…うん！\n私、すごーく頑張る！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3181, 109901, 4, 1, "この世界は、銀髪で\nハーフエルフでも受け\n入れてくれるのね…\nなんだか嬉しいな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3182, 109901, 5, 1, "今日のご飯…\nピーマルは\n入ってないわよね…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3183, 109901, 101, 4, "また、みんなで\n一緒に買い物に\n行きたいな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3184, 109901, 102, 8, "パック以外から\n家族って呼ばれたの\n初めて…ふふっ♪\nちょっと嬉しいかも！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3185, 109901, 1, 1, "今からパックの\n毛繕いをするけど…\n一緒にする？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3186, 109901, 2, 1, "私も何かお手伝いを…\nあ、そうだ！\n料理とかどうかな！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3187, 109901, 3, 1, "ねえ、ここで\n歌の練習って、\nしてもいいかな？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3188, 109901, 4, 1, "…あ、ごめんなさい！\n話しかけられるのって\nあまり慣れてなくて…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3189, 109901, 101, 4, "すごーく頑張るから、\nこれからも勉強を\n手伝ってね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3190, 109901, 102, 8, "せーの、びくとりー！\nふふっ…また、一緒に\nラジーオタイソー\nしようね♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3191, 109901, 1, 1, "ちゃんと起きてる？\nふふっ…よかった。\nあ、べ、別に心配した\nわけじゃないからね？", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3192, 109901, 2, 1, "お昼ご飯が楽しみね♪\nあ、こっちの世界って\nまよねーずはあるの？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3193, 109901, 3, 1, "そろそろ寝る時間よ。\n夜更かししたら、\nめっ！　だからね！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3195, 109901, 2, 1, "勉強も終わったし…\nちょっ、ちょっとだけ\n休んでもいいよね…？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3196, 109901, 3, 1, "か、かわいい…！\nパックのかわいさに\n匹敵するかも…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3197, 109901, 4, 1, "良い香り…\nロズワールの屋敷にも\nこういうのって\nあるのかな？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3198, 109901, 5, 1, "あったかい…\nずっとここに…はっ！\nダメダメ！　王選の\nために勉強しないと！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3200, 109901, 7, 1, "あ…これ美味しい！\nこっちの世界は色んな\n食べ物があるのね。", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3201, 109901, 8, 1, "知らないことが\nいっぱい書いてある…\n異世界の本って、\n面白いのね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3203, 110001, 1, 1, "海ってマジ最高！\nビーチの撮影も\n超楽しいしー♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3204, 110001, 2, 1, "20パーって何パ？\n今度ヒデサイに\n聞いてみよっ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3205, 110001, 3, 1, "夏の思い出を\nヒデサイと一緒に\n作りたいな～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3206, 110001, 4, 1, "テンションあがる\n水着ゲットして\n超ラッキーだよ～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3207, 110001, 5, 1, "休憩が終わったら\n撮影にもどらないと！\n気合入れてこ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3208, 110001, 101, 4, "ヒデサイとビーチで\nパーティーしたいな～\n超楽しそうだよね！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3209, 110001, 102, 8, "ヒデサイといると\n楽しくてうれしくて…\nカレシってこんな感じ\nなのかなぁ？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3210, 110001, 1, 1, "見て見て～！\n夏らしいネイルに\nしてみたんだ～♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3211, 110001, 2, 1, "ヒデサイ～\nこの水着どうかな？\nイケてるっしょ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3212, 110001, 3, 1, "水着姿の写真？\nもちいいよ！\nかわいく撮ってね！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3213, 110001, 4, 1, "うちだって\n夏の海みたいに\nキラキラしちゃうよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3214, 110001, 101, 4, "ヒデサイとうちの\nフィーリングって\nめっちゃエモいよね♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3215, 110001, 102, 8, "ねーねーヒデサイ！\n一緒に泳ぎにいこっ！\nほら、手繋いで♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3216, 110001, 1, 1, "水着オッケー！\nネイルもばっちり！\n朝から遊んじゃおー♪", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3217, 110001, 2, 1, "お昼食べたら\n昼寝して\nリフレッシュしよー♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3218, 110001, 3, 1, "水着ってマジで\n最強かも…このまま\nお風呂にも入れるし♪", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3220, 110001, 2, 1, "マジ気持ちいいー♪\nおに癒やされるん\nですけど～！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3221, 110001, 3, 1, "やばばばばー！\nかわいすぎだってば！\nマジらぶだよ～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3222, 110001, 4, 1, "あ、この香り超好き！\nモデル仲間に\n教えたげよ～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3223, 110001, 5, 1, "あったか～い♪\n夏の太陽みたい！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3225, 110001, 7, 1, "マジ美味しいねこれ♪\nビーチで\n流行っちゃうかも！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3226, 110001, 8, 1, "夏のために\n最新ファッションを\n勉強ちう！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3228, 110001, 10, 1, "なにコレもにカワ～♪\nなんて生き物？\nヒデサイ知ってる？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3229, 110001, 11, 1, "うわっ超フカフカ～\nなんかうち眠く\nなってきちゃった…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3230, 110101, 1, 1, "海の家でのんびり\n過ごしましょう\n熱中症には注意してね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3231, 110101, 2, 1, "はぁ、どうやったら\n泳げるように\nなれるのかしら～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3232, 110101, 3, 1, "海で日焼け止めを\n塗らないと、あとで\n大変なことになるわよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3233, 110101, 4, 1, "久しぶりに水着を\n着たんだけど\n変じゃなかったかな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3234, 110101, 5, 1, "波の音って落ち着くわ\n授業中に聞いたら\nみんな寝ちゃうかも？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3235, 110101, 101, 4, "海が楽しいって\n思えたのも\nあの子のおかげね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3236, 110101, 102, 8, "あの子と二人で海に\n来られてよかったわ\n一日があっという間ね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3237, 110101, 1, 1, "男の子と海だなんて\n少女漫画みたいで\n憧れてたの～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3238, 110101, 2, 1, "夏休みの宿題は\nちゃんとやった？\n遊んでばかりではダメ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3239, 110101, 3, 1, "昼の海と夜の海\nどっちが好き？　私は\n君といる海が一番よ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3240, 110101, 4, 1, "ふう、疲れたわ～\n冷たいジュースで\n元気回復ね！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3241, 110101, 101, 4, "海で見た君の背中\nすごく大きく見えたわ\nやっぱり男の子なのね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3242, 110101, 102, 8, "君と一緒に見た\n夜の海…\n忘れられそうにないわ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3243, 110101, 1, 1, "準備ができたら\n海に出かけようよ\n今日も楽しみましょう", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3244, 110101, 2, 1, "あら、もうお昼？\n海の家でランチに\nしましょ♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3245, 110101, 3, 1, "夜の海岸についてきて\nくれる？　暗がりは\n一人じゃ怖くて…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3247, 110101, 2, 1, "泳ぐ練習をしたら\n疲れちゃったわ～\nちょっと一休み…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3248, 110101, 3, 1, "かわいいわね～\n浮き輪に乗せて\n海に浮かべたいわ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3249, 110101, 4, 1, "この匂い…\nなんだか夏の海を\n思い出すわ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3250, 110101, 5, 1, "夜の海風で冷えない\nように、しっかりと\n身体を暖めましょうね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3252, 110101, 7, 1, "海で遊ぶ前に\nしっかり食べて\n体力つけましょう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3253, 110101, 8, 1, "夏休み中でも\nしっかりお勉強\nしないと！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3255, 110101, 10, 1, "この子に乗れば\n海の上でも\nすいすい進めるかしら", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3256, 110101, 11, 1, "ビーナスの誕生！\n…なんちゃって\nはしゃぎすぎかしら？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3257, 110301, 1, 1, "夏の海には商機が\nあるはず…\nのがす手はないわね♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3258, 110301, 2, 1, "あたしが作った\n日焼け止め…\nこれは売れそうね！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3259, 110301, 3, 1, "子どもたちのために\n浮き輪を用意して\nおかないと…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3260, 110301, 4, 1, "海っていいわよね！\nみんなで羽を伸ばしに\n行きたいわ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3261, 110301, 5, 1, "波の音を聞いて\nのんびりするのも\n悪くないわね…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3262, 110301, 101, 4, "ただの砂遊びも\nあんたと一緒なら\n楽しいから不思議ね…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3263, 110301, 102, 8, "あんたがあたしの\nなんであれ、大切な\n存在ではあるのよね…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3264, 110301, 1, 1, "夏の海って\nすごい人よね…\n迷子にならないでよ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3265, 110301, 2, 1, "ぼんやり海に浮かんで\n波に揺られるのも\n悪くないわよ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3266, 110301, 3, 1, "ビーチで子どもたちと\n一緒に砂のお城を\n作りたいわ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3267, 110301, 4, 1, "海で食べる\n焼きそばパン…\nいいかもしれないわね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3268, 110301, 101, 4, "あたしの水着\n似合ってる？\nそう、ありがとう♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3269, 110301, 102, 8, "あんたが居ると\nうれしい…って、\n子どもたちが\n言ってたわ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3270, 110301, 1, 1, "朝の浜辺って\nいいわよね\n散歩しに行きたいわ", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3271, 110301, 2, 1, "海の家のご飯って\nなんであんなに\nおいしいのかしら♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3272, 110301, 3, 1, "夜の海って、\nロマンチックよね。\nずっと眺めてたいわ…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3274, 110301, 2, 1, "やわらかくて\n気持ちいい…\nこのまま寝ちゃいそう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3275, 110301, 3, 1, "海に一緒に入れる\nぬいぐるみとか\nないかしら？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3276, 110301, 4, 1, "夏らしい\nいい香りね…\nなんの香りかしら？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3277, 110301, 5, 1, "あったかい…\n濡れた体を乾かすには\nちょうどいいわね♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3279, 110301, 7, 1, "泳いだ後の食事は\n普段の何倍も\nおいしく感じるわね！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3280, 110301, 8, 1, "海に行く前にいろいろ\n調べなきゃね。備え\nあれば憂いなしよ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3282, 110301, 10, 1, "海の守り神？\nずいぶんかわいらしい\n神様ね♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3283, 110301, 11, 1, "このぐらい\n大きな貝があったら\n食費も浮くかしら？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3284, 110401, 1, 1, "浜辺で食うものって\nなんであんなに\nうまいのかな～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3285, 110401, 2, 1, "いてて…\nちょっと日焼け\nしすぎたかな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3286, 110401, 3, 1, "砂浜で走ると\nいいトレーニングに\nなるんだぜ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3287, 110401, 4, 1, "夏ならではの\n料理…か\n何がいいかな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3288, 110401, 5, 1, "やっぱりここは\n涼しくて快適だな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3289, 110401, 101, 4, "泳ぎの練習だったら\nいつでも付き合って\nやるからな！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3290, 110401, 102, 8, "水着で部屋の中に\nいるのって\n変な感じだな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3291, 110401, 1, 1, "うおっ！\nきゅ、急に\n触るなって…\nびっくりした", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3292, 110401, 2, 1, "え、砂がついてた？\n払ってくれたのか\nありがとよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3293, 110401, 3, 1, "夏バテしてないか？\nここで休んでけよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3294, 110401, 4, 1, "…そう見られると\n照れるっていうか…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3295, 110401, 101, 4, "カキ氷とか\n食いたくないか？\n氷、削ってやるよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3296, 110401, 102, 8, "おまえと\n夏の思い出ができて\nよかったよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3297, 110401, 1, 1, "ふう…\n朝一番で泳ぐの\nやっぱ気持ちいいな", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3298, 110401, 2, 1, "一番あちぃ時間だ\n水分補給は\nこまめにな！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3299, 110401, 3, 1, "今日もお疲れ\n明日のパトロールも\nよろしくな", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3301, 110401, 2, 1, "泳ぎ疲れたからな\n少し休憩だ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3302, 110401, 3, 1, "か、かわいい…\nいや、なんでもねぇ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3303, 110401, 4, 1, "この香り落ち着くな\nこういうのも\n悪くねーかも…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3304, 110401, 5, 1, "あったけえ…！\n泳ぎ終わった後って\nけっこう体冷えてよ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3306, 110401, 7, 1, "海で食べると\nまた一味違うよな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3307, 110401, 8, 1, "夏のサバイバル特集…\nちょっと読んでみるか", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3309, 110401, 10, 1, "へー！　こいつ、\nよくできてんな！\nいいなあ、実物も\n乗れたらいいのに", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3310, 110401, 11, 1, "か、かわ…！\nいや、じゃなくて\nあ、あたしがここに\n寝ていいのか…？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3311, 110501, 1, 1, "故郷の花を飾っても\nいいかな～？\n部屋が楽しくなるよ～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3312, 110501, 2, 1, "水着で歩いてると\n泳ぎたくなるさ～\n我慢できないさ～！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3313, 110501, 3, 1, "夏が眩しいのは\nてぃーだがにこにこ\n笑ってるからさ～！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3314, 110501, 4, 1, "泳ぎ疲れたら\n涼しい木陰で\nお昼寝するさ～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3315, 110501, 5, 1, "クロールも\n犬かきも\n大得意さ～！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3316, 110501, 101, 4, "なんだか歌いたい\n気分さ～\nキミもどう～？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3317, 110501, 102, 8, "海の近くに住んで\nキミと歌って踊って\nそれが理想さ～！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3318, 110501, 1, 1, "私とキミ\nどっちがたくさん\n泳げるか\n競争するさ～！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3319, 110501, 2, 1, "キミも三線に興味が\nあるのかな～？\n私が教えるさ～！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3320, 110501, 3, 1, "嫌なことがあっても\n私と歌って踊れば\nなんくるないさー！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3321, 110501, 4, 1, "たまには時間を忘れて\n私とのーんびり\n海を眺めるさ～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3322, 110501, 101, 4, "ぶるぶるぶるっ！\n毛が濡れちゃっても\nへっちゃらさ～！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3323, 110501, 102, 8, "泳ぐ前は準備体操～！\n背中押してあげるから\nしっかりやるさ～！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3324, 110501, 1, 1, "朝の浜辺は\nちゅらさんよ～\n一緒に歩こうよー！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3325, 110501, 2, 1, "お昼ご飯は\n決まったかな～？\n私はタコライスさ～！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3326, 110501, 3, 1, "星と波音の中で\n三線鳴らすのも\nオツなものさ～", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3328, 110501, 2, 1, "ちょうどよかったさー\nゆっくり休んだら\nまた泳ぎに行くさ～！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3329, 110501, 3, 1, "砂浜に飾って\nおきたいさ～\nきっとかわいいさ～！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3330, 110501, 4, 1, "くんくん…はあ\n癒されるさ…\n故郷の夏の匂いさ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3331, 110501, 5, 1, "おおー…！\n濡れた髪が\nみるみる乾いてくさ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3333, 110501, 7, 1, "ん～！　これ、\n海で食べたら\nもっとおいしいさ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3334, 110501, 8, 1, "海の生物図鑑ね～\nこの前みかけた魚も\n載ってるかな～？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3336, 110501, 10, 1, "わぁ～！　みてみて！\n背中に乗れるさー！\nキミと一緒に\n乗りたいさ～！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3337, 110501, 11, 1, "ふかふかさ～\n海の底の\n人魚になった気分さ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3338, 105801, 103, 12, "あの人が\nわたしの騎士だったら\nいいのにな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3339, 105801, 103, 12, "あなたのおかげで\nわたしの心はいつも\nお腹いっぱいです♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3340, 105901, 103, 12, "許されるのならば、\n今すぐにでも、\n主さまのおそばに\n行きたいです…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3341, 105901, 103, 12, "主さま…\nお慕いしております…\nいつまでも、おそばに\n居させてください…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3342, 106001, 103, 12, "なんか最近、\nあいつのことしか\n考えてない…\nあたし…どうしよう…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3343, 106001, 103, 12, "そ、外に行くなら、\n連れていきなさいよね\nその…あたしが\n守ってあげるから…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3344, 110601, 1, 1, "部屋の中は涼しくて\n気持ちええどすなぁ\n天国やわぁ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3345, 110601, 2, 1, "クラゲはんって\nフリルいっぱいの\nドレスみたいやねぇ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3346, 110601, 3, 1, "外でセミはんが\n元気に鳴いてはるねぇ\n今日もええ天気やわぁ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3347, 110601, 4, 1, "こないに暑いと\n海に入って\n涼みたくなるわぁ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3348, 110601, 5, 1, "いつか\nマホマホ王国にも\n海の家作りまひょ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3349, 110601, 101, 4, "うちも\n付き合いますえ\n泳ぎの練習しまひょ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3350, 110601, 102, 8, "うちの里帰り\n王子はんも一緒に\n来まへんか？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3351, 110601, 1, 1, "王子はんは日焼けより\n色白美人の方が\n好きでっしゃろか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3352, 110601, 2, 1, "うちと一緒に\n浮き輪で海、\nプカプカしまへん？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3353, 110601, 3, 1, "日が落ちたら\n線香花火\nやりたいわぁ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3354, 110601, 4, 1, "木陰でのんびり\nお昼寝…\n最高やね～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3355, 110601, 101, 4, "お祭ではぐれても\n王子はんなら見つけて\nくれはります？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3356, 110601, 102, 8, "楽しい夏やわぁ\nずっとこんな日が\nつづくとええね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3357, 110601, 1, 1, "うちにしては早起き？\n暑くて目が早う\n覚めてもうたんどす", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3358, 110601, 2, 1, "お昼は冷たい\nおそうめんとか\nどうでっしゃろ", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3359, 110601, 3, 1, "夜は星がきれいやね\n二人で浜辺でも\n行きまへんか？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3361, 110601, 2, 1, "うーん、遊び疲れた\n身体にちょうどええ\nほっこり具合やわぁ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3362, 110601, 3, 1, "かいらしい子は\nみーんな\nマホマホ王国の\n住人どすえ♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3363, 110601, 4, 1, "この香り…\n南国にいる気分やねぇ\n癒されるわぁ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3364, 110601, 5, 1, "これで\n冷えた身体も\nポカポカやねぇ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3366, 110601, 7, 1, "外はまだ暑いやろうし\nバテんよう\nしっかり食べんとね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3367, 110601, 8, 1, "海の中にもお城が\nあるんやねぇ\n行ってみたいわぁ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3369, 110601, 10, 1, "この子、ふかふかやね\nこのままマホマホ王国\n乗せてってくれはる？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3370, 110601, 11, 1, "人魚姫になっても\n最後は王子はんと\nハッピーエンド\nどすえ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3371, 107001, 1, 1, "真似したくなる\n人がここには\n大勢いますね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3372, 107001, 2, 1, "偽物を一人\n紛れこませました\n…冗談です", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3373, 107001, 3, 1, "動物に変身して\nその辺りを\n散歩してきます", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3374, 107001, 4, 1, "失った記憶は\nどの程度回復するの\nでしょうね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3375, 107001, 5, 1, "人や物だけでなく\nいずれは世界すら\n真似てみせます", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3376, 107001, 101, 4, "次は彼に\nどんなことを\n教えましょうか…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3377, 107001, 102, 8, "知識はともかく\n私の力はまだ\n必要なようですね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3378, 107001, 1, 1, "どうしました？\n…何でもない？\n…そうですか", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3379, 107001, 2, 1, "退屈しています\n芸でもして\n楽しませなさい", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3380, 107001, 3, 1, "研究のためです\n少し話し相手に\nなりなさい", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3381, 107001, 4, 1, "何か真似して欲しい？\n私の能力を何だと\n思っているのですか", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3382, 107001, 101, 4, "貴方にはまだ色々と\n教えるべきことが\nありますね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3383, 107001, 102, 8, "随分多くの女性と\n知り合いなのですね\n驚きました", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3384, 107001, 1, 1, "…\nおはようございます\n…まだ朝ですよ", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3385, 107001, 2, 1, "食事にします\n…どうしました？\n早く持ってきなさい", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3386, 107001, 3, 1, "早く寝なさい\n夜更かしすると\n背が伸びませんよ？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3388, 107001, 2, 1, "マサキより\n座り心地は\nいいですね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3389, 107001, 3, 1, "意外に複雑な\n構造ですね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3390, 107001, 4, 1, "真似るなら\n香りまで\n再現しなくては", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3391, 107001, 5, 1, "発火現象の\n模倣…なかなか\n難しいものです", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3393, 107001, 7, 1, "偽物や分身を作るには\nエネルギーが\n必要不可欠なのです", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3394, 107001, 8, 1, "暇つぶしには\nちょうどよさそうです\n…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3396, 110701, 1, 1, "学院かぁ…\n友達の作り方の\n授業はないかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3397, 110701, 2, 1, "学院でぼっちに\nなったときのために、\n避難場所を\n探しておかないと…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3398, 110701, 3, 1, "ぼっちの私が学院…\nな、なんだか\n目まいがしてきました", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3399, 110701, 4, 1, "クラスメイトに\n話しかける練習…\nむ、難しすぎます…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3400, 110701, 5, 1, "目指せ友達100人…\n無理かな…10人…\nや、やっぱり一人！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3401, 110701, 101, 4, "あなたと学院生活が\n送れるなんて、\nとっても\nうれしいです！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3402, 110701, 102, 8, "えへへ…\n学院でも、\nあなたと一緒に\n居られてうれしいな♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3403, 110701, 1, 1, "が、学院ってあんなに\n人が居るんですね…\n友達作りのイメトレを\nやり直さないと！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3404, 110701, 2, 1, "私にとって\n部活動とはＢＢ団！\n今日もたくさん\n活動しましょうね！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3405, 110701, 3, 1, "休み時間は寝たふり…\nご飯はおトイレ…\nぼっち対策は完璧です", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3406, 110701, 4, 1, "学院で友達を作る\n勉強は完璧ですよ！\n購買部でパンを買って\n渡すんですよね！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3407, 110701, 101, 4, "学院でも、\nBB団の活動を\nしていきましょうね♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3408, 110701, 102, 8, "ぼっちに学院生活は\n厳しいですね…でも、\nあなたと一緒なら\n乗りこえられそうです", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3409, 110701, 1, 1, "もうこんな時間…\nあの…一緒に学院に\n行ってくれませんか？", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3410, 110701, 2, 1, "お昼ですね！\n言ってくれれば、\nパンでもお握りでも\n買ってきますよ！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3411, 110701, 3, 1, "今日もぼっち…\nいつか友達と一緒に、\nパジャマパーティー\nしたいなぁ…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3413, 110701, 2, 1, "森とは違う\n心地良さが…このまま\n眠っちゃいそうです…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3414, 110701, 3, 1, "かわいい…この子を、\n話す練習の相手役に\nしてみようかな？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3415, 110701, 4, 1, "いい香り…\nいつ友達ができても\nいいように、私の\n部屋にも置こうかな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3416, 110701, 5, 1, "こ、これは…\nぼっちで\n日陰者の私には\n暖かすぎます！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3418, 110701, 7, 1, "おいしい…！\nお弁当にして、学院に\n持っていけないかな？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3419, 110701, 8, 1, "学院の授業に\nついていくためにも、\n勉強しておこうかな…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3421, 110801, 1, 1, "あんた、何してんの。\nいいけど、なんでも", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3422, 110801, 2, 1, "邪魔。どいて。\nいや怒ってないし。\nコレがフツーだから", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3423, 110801, 3, 1, "いいけど、\n散らかしめの部屋だと\n片付けたくなるよね…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3424, 110801, 4, 1, "暇なときは明日の\nコーデ考えてるよ。\n考えるのはタダだし", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3425, 110801, 5, 1, "昔よくシオリン\n構いすぎてハツネに\n文句言われたなァ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3426, 110801, 101, 4, "なんかさ。あんたって\n謎に女子トモ多いよね\n…いいけど、べつに", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3427, 110801, 102, 8, "ねえ。あのさあ。\nあんた弁当で好きな\n具とかってあんの", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3428, 110801, 1, 1, "あ？　何。なんか用。\nや、ないならないで\nいいけど、べつに", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3429, 110801, 2, 1, "ごめ、ちょっと\n黙っててくんない。\n明日の試験ヤバだから", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3430, 110801, 3, 1, "歩き方が静か？\n癖になってるんだよね\n足音を殺すの", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3431, 110801, 4, 1, "うちの血液型？\n大雑把のОだけど。\nなんか文句あんの", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3432, 110801, 101, 4, "ひゃんっ！\nや、だからさ…急に\n話しかけんなっての", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3433, 110801, 102, 8, "な、何…あんまジッと\n見ないでくんない…\nなんか、その、ハズい", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3434, 110801, 1, 1, "早起きは得意な方だと\n思うけど。昔から\nそれがデフォだったし", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3435, 110801, 2, 1, "こんな時間か…\n昼ごはん\nサクッと作っちゃお", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3436, 110801, 3, 1, "あ？　夜遊び？\nはッ…そんなん\n５歳で卒業したっての", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3438, 110801, 2, 1, "あーヤバじゃんコレ…\nふかついてる場合\nじゃないっつーのに…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3439, 110801, 3, 1, "なんだこれ\nかわいいかよ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3440, 110801, 4, 1, "ふーん。\nこの匂い…\nなんかいいじゃん", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3441, 110801, 5, 1, "暖炉はアドじゃん。\n煮物、焼き物、蒸し物\nいろいろ作れそう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3443, 110801, 7, 1, "何コレ\n食べ物困んないじゃん\nうちにも一台くんない", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3444, 110801, 8, 1, "本かァー…んー\n学校の勉強だけで\nいいかなァ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3446, 111101, 1, 1, "イタズラってなにを\nしたらいいのかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3447, 111101, 2, 1, "カップケーキ\n上手に作れました\n食べてくれるかな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3448, 111101, 3, 1, "あの人、\nお菓子くれるかな？\nくれなかったら…ふふ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3449, 111101, 4, 1, "オバケ…いないよね？\n怖くなんてないけど！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3450, 111101, 5, 1, "ハロウィンは\n楽しいけどオバケの\nお祭りなんだよね…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3451, 111101, 101, 4, "にゃ～、にゃお～！\nちゃんと怖いかな？\n早く帰ってこないかな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3452, 111101, 102, 8, "あの人のことだから\nお菓子欲しがるよね…\n準備はバッチリです♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3453, 111101, 1, 1, "えへへ。猫さんのお耳\nよく出来てるでしょ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3454, 111101, 2, 1, "触りましたね？\nイタズラしましたね？\nへんた～い！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3455, 111101, 3, 1, "フー！　頭を撫でたら\n怒りますよ！　あれ？\n猫みたいでした？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3456, 111101, 4, 1, "きゃっ！　も、もう、\n脅かさないでください\n怖くありませんけど！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3457, 111101, 101, 4, "お菓子ですか？\nありがとうございます\nハッピーハロウィン♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3458, 111101, 102, 8, "ゴロゴロ…\nね、猫さんの真似です\n…似てましたか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3459, 111101, 1, 1, "さぁ、お菓子作りを\n始めますよ。下準備は\n大事ですからね！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3460, 111101, 2, 1, "今、つまみ食い\nしましたね！？　あっ\nおやつの時間でしたか", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3461, 111101, 3, 1, "さぁ、ハロウィンの\n始まりです…ふぁ…\nね、眠くありません！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3463, 111101, 2, 1, "ちょっと疲れたかも…\nパーティの時間まで\nひと休みしなきゃ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3464, 111101, 3, 1, "私も今、こんな感じ\nなのかな？　こんなに\nかわいいといいなぁ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3465, 111101, 4, 1, "甘い匂いがいっぱい…\nお菓子の香りかな？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3466, 111101, 5, 1, "温かくなると、\n丸まりたくなって…\n…猫になっちゃう！？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3468, 111101, 7, 1, "ニンジンもこれくらい\nおいしければ\nいいのに…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3469, 111101, 8, 1, "ハロウィンの本、\nオバケがたくさん\n載ってて怖いかも…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3471, 111101, 12, 1, "美味しそうなお菓子が\nいっぱい…これなら\nイタズラされませんね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3473, 111201, 1, 1, "たくさんいたずら\nできるから\nハロウィン大好き！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3474, 111201, 2, 1, "ハロウィンだから\nいつもよりすごい\nいたずらしたいな～！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3475, 111201, 3, 1, "カボチャを綺麗に\nくり抜いてすごく怖い\nランタン作っちゃお！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3476, 111201, 4, 1, "お菓子がたくさんで\nどれから食べるか\n迷っちゃうな～！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3477, 111201, 5, 1, "どうすればみんなを\nびっくりさせられる\nかな～？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3478, 111201, 101, 4, "カボチャ見てたら\nお腹空いて来たな～\nお菓子食べちゃお♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3479, 111201, 102, 8, "にいちゃんには\n特別ないたずらを\n用意してあげるね！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3480, 111201, 1, 1, "カボチャにクラッカー\nを仕掛けて…わっ！？\nみ、見ちゃダメだよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3481, 111201, 2, 1, "ん？　なぁに？\nお菓子ほしいの？\nもう、しかたないな～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3482, 111201, 3, 1, "にいちゃんの\n面白い仮装が\n見てみたいな～！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3483, 111201, 4, 1, "お菓子くれたお礼に\nとびっきりの\nいたずらしちゃうね！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3484, 111201, 101, 4, "にいちゃんは\nハロウィンでも最高の\nいたずら相手だよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3485, 111201, 102, 8, "にいちゃんと一緒に\nハロウィンパレード\n歩きたいな～！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3486, 111201, 1, 1, "おはようにいちゃん！\n仮装して出かける\n準備しなきゃ！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3487, 111201, 2, 1, "お腹すいたな～\nもらったお菓子\n食べちゃだめかな？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3488, 111201, 3, 1, "たくさんいたずらして\nもうクタクタ～\nミソギもう寝るね…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3490, 111201, 2, 1, "やわらか～い…\nここでひと休みして\nまたいたずらしよう！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3491, 111201, 3, 1, "かわいいもので\n油断させてわっ！\nってするのどうかな？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3492, 111201, 4, 1, "いいにお～い！\nいたずらのアイデアが\nわいてくるよ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3493, 111201, 5, 1, "もらったお菓子\nあったかくしたら\nおいしいかな～？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3495, 111201, 7, 1, "お菓子ばっかりじゃ\n飽きちゃうもんね！\nご飯も食べよう！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3496, 111201, 8, 1, "本のなかに\nいたずらのヒント\nないかな～？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3498, 111201, 12, 1, "お菓子がたくさん！\n一人で食べていいの？\nやっぱりだめか～！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3500, 101101, 103, 12, "お勉強は順調ですよ\nやっぱりお兄ちゃんの\nおかげなんでしょうか", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3501, 101101, 103, 12, "お兄ちゃんとの\n思い出の場所\nまた行きましょうね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3502, 111301, 1, 1, "えへへ～♪\nハロウィンの\nミイラさんうさぎさん\nぴょんぴょ～ん！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3503, 111301, 2, 1, "るんたった～\nハロウィンハロウィン\n楽しいな～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3504, 111301, 3, 1, "ハロウィンって\nどんなお菓子を\nもらえるのかな～？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3505, 111301, 4, 1, "お菓子をもらう\n練習をしなくちゃ！\nトリックオア\nトリート～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3506, 111301, 5, 1, "おにいちゃんと\nハロウィンするの\nミミ楽しみだな～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3507, 111301, 101, 4, "おにいちゃんと\nいっしょにお菓子を\n食べたいな～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3508, 111301, 102, 8, "おにいちゃんと\n毎日ハロウィン\nできたらいいな～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3509, 111301, 1, 1, "えへへ～\nおにいちゃん！\nハッピーハロウィン♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3510, 111301, 2, 1, "おにいちゃんも\nいっしょに\nハロウィンパレード\nしようね～！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3511, 111301, 3, 1, "ミイラさんうさぎさん\nだぞ～…えへへ\nミミこわかったかな？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3512, 111301, 4, 1, "おにいちゃん！\nハロウィンのお菓子\nおいしいね～♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3513, 111301, 101, 4, "おにいちゃんと\nいっしょなら\nおばけさんも\nこわくないよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3514, 111301, 102, 8, "ミミね\nハロウィンも\nおにいちゃんも\nだ～いすき♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3515, 111301, 1, 1, "おひさまさんも\nハロウィンの\nおようふく\n着たいかな～？", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3516, 111301, 2, 1, "ねえねえおにいちゃん\nお昼ごはんはもらった\nお菓子でもい～い？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3517, 111301, 3, 1, "まだハロウィン…？\nうぅん…眠くないよぅ\nでもちょっと…ふぁ～", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3519, 111301, 2, 1, "ふかふか～♪\nおばけさんが\n中にいるのかな～？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3520, 111301, 3, 1, "わぁ、かわいい～♪\nミミといっしょに\nあそぼ～！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3521, 111301, 4, 1, "ふわぁ～\nミミ、このにおい\nだ～いすき♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3522, 111301, 5, 1, "あったかいね～♪\nおばけさんも\nポカポカしてるかな？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3524, 111301, 7, 1, "おいしい～♪\nおにいちゃんも\n食べようよー！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3525, 111301, 8, 1, "この本\nおにいちゃんと\nいっしょに\n読みたいな～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3527, 111301, 12, 1, "ハッピーハロウィン♪\nえへへ、お菓子が\nいっぱいだね～！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3529, 111401, 1, 1, "ここ、好きだよ。\nみんな優しいから…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3530, 111401, 2, 1, "なんかさみしいな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3531, 111401, 3, 1, "知らない人怖いな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3532, 111401, 4, 1, "お部屋おっきいねー", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3533, 111401, 5, 1, "ルナね…\nお昼寝したいかも", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3534, 111401, 101, 4, "おにいさん！\n今日は何して遊ぶの？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3535, 111401, 102, 8, "あのね、\nお料理教えてほしい！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3536, 111401, 1, 1, "あっ、おにいさんだ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3537, 111401, 2, 1, "ルナは元気だよ！\nいいこだもん！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3538, 111401, 3, 1, "今日は何しようかな", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3539, 111401, 4, 1, "ルナと遊んでよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3540, 111401, 101, 4, "ねーねー、\nルナとお出かけして！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3541, 111401, 102, 8, "おにいさんと、\n遊びたいなー！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3542, 111401, 1, 1, "ふふっ！\nなんかまぶしいね！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3543, 111401, 2, 1, "ルナ、お腹空いたな", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3544, 111401, 3, 1, "どこに行こうかな？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3546, 111401, 2, 1, "ルナ、これ好きだな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3547, 111401, 3, 1, "かわいい！　好き！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3548, 111401, 4, 1, "なんかいい匂い！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3549, 111401, 5, 1, "あったかいねー", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3551, 111401, 7, 1, "ふふっ\nおいしいね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3552, 111401, 8, 1, "ルナ、\n眠くなっちゃう…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3554, 105201, 103, 12, "えへへ。あの人と\nランドソルでデート\nできたらうれしいな♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3555, 105201, 103, 12, "あなたとなら\nどんなことだって\nできる気がするわ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3556, 106501, 1, 1, "あーあ、退屈だな\nカレでも探しに\nいこうかな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3557, 106501, 2, 1, "この前のケンカは\n最高に燃えたなぁ\n…拳が疼いてきたぜ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3558, 106501, 3, 1, "一体、この世界は\nどうなっちまったって\nいうんだ…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3559, 106501, 4, 1, "もう仕事の時間か\nボスも人使いが荒いぜ\n…ま、いいけどさ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3560, 106501, 5, 1, "オレたち以外に\nドラゴン族はあと何人\nいるんだろうな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3561, 106501, 101, 4, "カレの真剣な顔\n面白かったな\nまた遊びたいぜ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3562, 106501, 102, 8, "どうしてカレがそばに\nいると、腹の底から\n力が湧いてくるんだ？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3563, 106501, 1, 1, "よっす！\nなんかオイシイ話でも\nあったりしない？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3564, 106501, 2, 1, "なあ、オレと遊ぼうぜ\n心配すんなよ。危ない\nことはよしとくからさ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3565, 106501, 3, 1, "どうした。オレに\nケンカ売ろうってか？\n…ははは、冗談だよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3566, 106501, 4, 1, "ちょうど退屈してた\nところだ、特訓に\n付き合ってくれよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3567, 106501, 101, 4, "ったくよぉ…\nオレがかわいいなんて\n冗談はもうよせよ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3568, 106501, 102, 8, "次の仕事も一緒に\n来るだろ？　断っても\n連れて行くけどな！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3569, 106501, 1, 1, "おはよう！　今日の\n予定はまずケンカして\nで、その後もケンカだ", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3570, 106501, 2, 1, "よっす！\n腹ごしらえが済んだら\nオレと遊ぼうぜ！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3571, 106501, 3, 1, "夜は危ないから\nあんま出歩くな？\n…だから面白えんだろ", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3573, 106501, 2, 1, "次のケンカに備えて\n今は休んでおくか…\n全力で暴れたいからな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3574, 106501, 3, 1, "ボスの飼ってる\nラーゴが、中に\n入ってるんじゃ？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3575, 106501, 4, 1, "ああ、たぎる匂いが\nする…ひと暴れ\nしたくなってきた！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3576, 106501, 5, 1, "おおぉ…\nオレの拳もなんだか\n熱くなってきたぜ…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3578, 106501, 7, 1, "いつでも全力で\nケンカするために\n力を蓄えないとな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3579, 106501, 8, 1, "やっぱりな…オレが\n知ってる地図とは\nまるで違ってやがる", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3581, 111501, 1, 1, "さぁプレゼントで\n埋め尽くしてやろう！\n大いに喜ぶがいいよ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3582, 111501, 2, 1, "メリークリスマス☆\n聖夜の宴だ\n派手に楽しむとしよう", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3583, 111501, 3, 1, "煙突から入る？\nいいや、ワタシは\nいつでも正面からさ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3584, 111501, 4, 1, "サンタクロースの\nお出ましだ！\n歓迎するがいいよ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3585, 111501, 5, 1, "飾り付けが足りん！\nワタシが演出して\nやろうじゃないか！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3586, 111501, 101, 4, "人の喜ぶ顔を見るのも\n悪くはないな…おっと\nワタシらしくないな☆", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3587, 111501, 102, 8, "プレゼントはまだか？\nワタシを喜ばせたら\nお返しは弾むぞ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3588, 111501, 1, 1, "ワタシを呼んだか？\n退屈な要件だったら\n覚悟しておくことだ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3589, 111501, 2, 1, "なんだ？\nワタシへの挑戦か？\nよし、受けた♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3590, 111501, 3, 1, "おっと、当たらんよ\nワタシの乱数聖域は\n絶対だからな…♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3591, 111501, 4, 1, "なんだ、オマエも\n暇なのか？\n奇遇だな、ワタシもさ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3592, 111501, 101, 4, "なにか愉しいことでも\nしようじゃないか\nさぁ、何をする？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3593, 111501, 102, 8, "こうもワタシに\n気安く触れるのは\nオマエくらいだよ…♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3594, 111501, 1, 1, "朝だろうと関係ない！\nさぁ愉しく苛烈に\n騒ごうじゃないか♪", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3595, 111501, 2, 1, "調子の出てくる時間だ\nしっかりワタシを\n愉しませてくれよ♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3596, 111501, 3, 1, "眠いなどと言わせんぞ\nまだまだ一日は\n終わらんからな！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3598, 111501, 2, 1, "いい具合じゃないか\nこのまま持って帰って\nしまおうか…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3599, 111501, 3, 1, "なかなか愛らしい\n造形をしているな？\nワタシには負けるがな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3600, 111501, 4, 1, "ほう、悪くない香りだ\n戦いの匂いをうまく\n隠してくれそうだ…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3601, 111501, 5, 1, "体が温まってくると\nひと暴れしたくなるな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3603, 111501, 7, 1, "ワタシの舌は厳しいぞ\n…ふーむ、なるほど\n認めざるをえんな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3604, 111501, 8, 1, "クリスティーナさんは\n知識の収集も\n欠かさないんだよ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3606, 111501, 14, 1, "これは驚いたな\nだがもっと、もっとだ\nワタシを退屈させるな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3607, 111501, 15, 1, "こんな戯れだって\n軽くこなしてしまう\nクリスティーナさんさ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3608, 111601, 1, 1, "メリークリスマス！\nさあ、パーティーを\n盛り上げるよ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3609, 111601, 2, 1, "衣装一つで全然\n気分が違うね！\n最高のクリスマスに\nなりそうだよ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3610, 111601, 3, 1, "ツリーのてっぺんの\n星って、なんだか\n欲しくなるんだよね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3611, 111601, 4, 1, "めいっぱい\nクリスマスを楽しむよ\n遊びだって本気\nなんだからね！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3612, 111601, 5, 1, "赤、白、緑、金\nクリスマスカラー\nってお洒落だよね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3613, 111601, 101, 4, "雪、降らないかな？\nホワイトクリスマス\nってちょっと\n憧れるなぁ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3614, 111601, 102, 8, "キミと過ごすこの\n楽しい時間がずーっと\n続けばいいのにな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3615, 111601, 1, 1, "クリスマスライブ\nチケット用意するから\n絶対来てよね！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3616, 111601, 2, 1, "カンパーイ！\nジュースだけど\nちょっと大人の\n気分だね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3617, 111601, 3, 1, "クリスマス衣装どう？\nかわいいでしょ\nあっ、どこ見てるの！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3618, 111601, 4, 1, "ねえ、後で街に\n行こうよ\n大きなツリーを\n一緒に見たいんだ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3619, 111601, 101, 4, "ねえ、ダンスの練習に\n付き合ってくれない？\n大丈夫、手取り足取り\n教えてあげるから♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3620, 111601, 102, 8, "来年のクリスマスも\n隣にキミが\nいてくれたら\nうれしいのにな", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3621, 111601, 1, 1, "クリスマスって朝から\nドキドキしちゃうね\nなんだか子どもみたい", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3622, 111601, 2, 1, "パーティーの準備も\n大分進んだね\n一息ついて\nランチにしよっか", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3623, 111601, 3, 1, "静かな夜だね\n心臓の音まで\n聞こえちゃいそう…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3625, 111601, 2, 1, "あ～、この絶妙な\n柔らかさ…\nいい仕事してるなぁ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3626, 111601, 3, 1, "アイドル顔負けの\nかわいさ、ズルい！\nでもかわいい！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3627, 111601, 4, 1, "ん～、落ち着くなぁ\n今日はゆっくり\n眠れそうだよ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3628, 111601, 5, 1, "ぬくぬく～\nどうしよう、ここから\n動きたくないなぁ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3630, 111601, 7, 1, "ダンスの練習もしたし\nちょっとくらいなら\nいいよね！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3631, 111601, 8, 1, "アイドルといえど\nかわいいだけじゃダメ\n知識も必要だよね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3633, 111601, 14, 1, "面白ーい！\nこの演出、ライブでも\nできないかな？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3634, 111601, 15, 1, "もふもふ～♪\n寒い日には最高の\n抱き心地だね！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3635, 101801, 103, 12, "彼ともっとお話し\nしたいな…もう少し\n補習を増やそうかしら", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3636, 101801, 103, 12, "君のこともっともっと\n教えて欲しいな。私に\n補習授業してくれる？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3637, 111701, 1, 1, "この姿ならあやつも\n子ども扱いできまい！\n…じゃがなぜか、少し\n物足りないような…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3638, 111701, 2, 1, "時間もあるし、\n聖夜の宴の\n準備をするかのう♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3639, 111701, 3, 1, "…わらわの知る聖夜と\n随分違うが、宴は宴\n楽しませて\nもらおうかのう♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3640, 111701, 4, 1, "大人の姿で\nできること…ふむ\n少しあやつを\nからかってやるか♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3641, 111701, 5, 1, "…クリスマスは\n恋人たちの夜！？\nち、近頃の若いもんは\n破廉恥なのじゃ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3642, 111701, 101, 4, "…あやつがおらんと、\n宴も物足りぬな\n…馬鹿者が\n早く帰ってこい…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3643, 111701, 102, 8, "誰も見ておらぬな…？\n…よし、あやつへの\nプレゼントを\n準備するとしようか！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3644, 111701, 1, 1, "クリスマスの\nチキンにケーキ…\nべ、別に楽しみになど\nしておらぬぞ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3645, 111701, 2, 1, "この姿では、わらわの\n頭をなでるのは\n難しかろう…べ、\n別に残念ではないわ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3646, 111701, 3, 1, "メリー\nクリスマスじゃ！\nさあ、わらわのために\n宴の準備をするのじゃ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3647, 111701, 4, 1, "そんなにこの衣装が\n気になるのか？\n…だったら、直に\n触ってみるがよい♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3648, 111701, 101, 4, "いつもと逆に、お主を\n子ども扱いしてやるか\nほれ、膝枕をしてやる\nからそばに来い♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3649, 111701, 102, 8, "…せ、聖夜の予定は\n空けておくのじゃぞ？\nなんでって…\nな、なんでもじゃ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3650, 111701, 1, 1, "もう朝か…聖夜の宴が\n楽しみで…ではなく！\n準備のせいで、\nあまり眠れんかったぞ", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3651, 111701, 2, 1, "…ずっと雪が\n降っておれば、\n陽光が強いこの時間も\n怯えずにすむのにのう", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3652, 111701, 3, 1, "時は来た…\nさあ眷属よ！\nわらわと共に、\n聖夜の宴を楽しもうぞ", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3654, 111701, 2, 1, "良い椅子ではないか\nここから聖夜の宴を\n眺めるのもよいかもな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3655, 111701, 3, 1, "か、かわい…こほん\nなかなか良い\nデザインをしておるな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3656, 111701, 4, 1, "ふむ、良い香りじゃ\n聖夜の宴でも\nこれを使うとしよう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3657, 111701, 5, 1, "これが癒し…はっ！\nあやうく浄化される\nところであった…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3659, 111701, 7, 1, "ニ、ニンニクは\n入っておらぬよな？\n…うむ、美味じゃ♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3660, 111701, 8, 1, "聖夜は恋人たちの夜…\n…き、気になるわけ\nではないが、\n少し調べてみるか", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3662, 111701, 14, 1, "ほう…これは面白い\n魔法の使い方じゃな\nまるで本物の\n演劇のようじゃ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3663, 111701, 15, 1, "こ、この程度の\nかわいさに誘惑される\nわらわでは…\nふわもこなのじゃ～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3664, 112001, 1, 1, "おせち料理も\n魔物尽くしだったり\nしないわよね！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3665, 112001, 2, 1, "初詣で何をお祈り\nしようかしら…\n定番でいいわよね？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3666, 112001, 3, 1, "初日の出って\n特別な感じがして\nいいわよね…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3667, 112001, 4, 1, "寒い中並んで飲む\n甘酒って不思議なほど\nおいしいわよね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3668, 112001, 5, 1, "羽根つきで勝負…\nあいつにだったら\n勝てるかしら？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3669, 112001, 101, 4, "年賀状になんて書こう\n…いろいろと\n恥ずかしくなるのよね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3670, 112001, 102, 8, "今年もあいつと\n居られること…\n結構うれしいわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3671, 112001, 1, 1, "何じっと\n見てんのよ？\nお年玉はあげないわよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3672, 112001, 2, 1, "おせち料理一緒に\n食べる？\n結構おいしいわよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3673, 112001, 3, 1, "羽根つきで負けたら\n顔に墨塗られるのよ\n覚悟しなさい！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3674, 112001, 4, 1, "門松飾るから\nちょっと手伝って\nくれる？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3675, 112001, 101, 4, "あんたと一緒に\n初詣行くのも悪くない\n…かも", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3676, 112001, 102, 8, "初詣で何をお祈り\nしたか？　うっさい！\n内緒よ！内緒！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3677, 112001, 1, 1, "もう朝なの…\n正月くらいゆっくり\n寝ていたいわよね…", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3678, 112001, 2, 1, "おせちに飽きたら\n次は何を食べよう\nかしら…", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3679, 112001, 3, 1, "そろそろ寝ましょ\nいい初夢が見られると\nいいわね", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3681, 112001, 2, 1, "この格好でいるのも\nなかなか疲れるわね\n休憩～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3682, 112001, 3, 1, "か、かわいい…う～ん\n鏡餅も顔を描けば\nかわいくなるかしら？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3683, 112001, 4, 1, "いい匂いね\n落ち着いて\n眠れそう…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3684, 112001, 5, 1, "ここでお餅焼いたら\n一段とおいしく\nなりそうね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3686, 112001, 7, 1, "正月に食べるものは\nなんでもおいしく\n感じちゃうわね！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3687, 112001, 8, 1, "魔物を使わない\nおせちを\n調べておかなきゃ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3689, 112101, 1, 1, "新年を気持ちよく\n過ごせるように\nお掃除がんばります！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3690, 112101, 2, 1, "神殿に通っていたら\nいつの間にか足腰が\n鍛えられたような…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3691, 112101, 3, 1, "神殿付近は屋台も\nあるので、良い香りが\n漂ってくるんですよ～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3692, 112101, 4, 1, "おせちを作れるように\nなれば、子どもたちも\n喜んでくれるかな…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3693, 112101, 5, 1, "コホォォォォォ…！\nふぅ、深呼吸すると\n落ち着きますね♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3694, 112101, 101, 4, "お仕事以外の時も\nあなたと…い、いえ！\nなんでもないです！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3695, 112101, 102, 8, "あなたと一緒だと\nなんだかポカポカ\nしてくるんです…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3696, 112101, 1, 1, "この格好をしていると\nなんだか神殿にいる\n気分になりますね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3697, 112101, 2, 1, "そういえば神殿で\n甘酒をもらったんです\nあなたも飲みますか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3698, 112101, 3, 1, "あっ、髪飾りが\nズレちゃってました！\nいけないいけない…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3699, 112101, 4, 1, "ひゃわっ！？\nく、くすぐったり\nしないでください～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3700, 112101, 101, 4, "そ、そんなに\n見つめられると\nて、照れちゃいます…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3701, 112101, 102, 8, "今度、私が考えた\n運気上昇のおまじない\nかけてあげますね♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3702, 112101, 1, 1, "おはようございます\n神殿のお手伝いに行く\n準備もしなきゃですね", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3703, 112101, 2, 1, "お昼になりましたし\nご飯は私が作…\nえっ、ダメですか！？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3704, 112101, 3, 1, "明日も早いですし\nそろそろ寝る支度を…\nふわぁ…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3706, 112101, 2, 1, "お掃除も終わったので\n少し休憩を…ふわぁ…\n急に眠気が…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3707, 112101, 3, 1, "新年を気持ちよく\n過ごせるようこの子も\nきれいにしましょう♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3708, 112101, 4, 1, "神殿のお香のような\n香りだと落ち着いて\n過ごせそうですよね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3709, 112101, 5, 1, "ぽかぽかですね～…\n甘酒も飲んで体の\n芯から温めましょう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3711, 112101, 7, 1, "今年こそ\n料理も上手に…\nもぐもぐもぐもぐ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3712, 112101, 8, 1, "失敗続きだと\n逆に運が良くなる…\nほ、本当ですかぁ…？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3714, 103401, 103, 12, "キミにならもう\nどんな私を見られても\n大丈夫かな…♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3715, 103401, 103, 12, "なになにどしたのぉ？\nお姉さんが撫でたげる\nこっちにきなさぁい♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3716, 111901, 1, 1, "わたくしの舞を\n主さまは喜んで\nくださるでしょうか…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3717, 111901, 2, 1, "年を越したあとに\n食べるおそばも\nおいしいです", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3718, 111901, 3, 1, "甘酒を飲むと\n心まで温まる\n気がいたします", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3719, 111901, 4, 1, "主さまのために\n真心を込めたおせちを\nご用意しなくては", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3720, 111901, 5, 1, "おみくじを引くのは\nドキドキしつつも\n楽しいものです", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3721, 111901, 101, 4, "主さまの温もりは\n心まで温めてくれる\nようです", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3722, 111901, 102, 8, "うまくできなくとも\n心を込めて精いっぱい\n舞いましょう", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3723, 111901, 1, 1, "主さま、この晴れ着は\n似合っている\nでしょうか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3724, 111901, 2, 1, "お餅はゆっくりと\n召し上がって\nくださいまし", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3725, 111901, 3, 1, "主さまのお顔の\n福笑いなら、完璧に\nできる気がいたします", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3726, 111901, 4, 1, "主さまはどのような\n初夢をご覧になった\nのでしょう？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3727, 111901, 101, 4, "甘酒の飲み過ぎは\nいけませんよ？\nめっ、でございます", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3728, 111901, 102, 8, "二人羽織とは\nあんなにも暖かい\nものなのですね…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3729, 111901, 1, 1, "おはようございます\n寒い季節の朝日は\nとても美しいですね", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3730, 111901, 2, 1, "おいしくとも\n食べ過ぎは正月太りの\nもとでございます", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3731, 111901, 3, 1, "いい夢が見られるよう\n暖かくして\n眠るといたしましょう", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3733, 111901, 2, 1, "ふかふか…\nこのまま\n寝てしまいそうです", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3734, 111901, 3, 1, "あなたと一緒に\nお正月遊びを\n楽しんでみたいです", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3735, 111901, 4, 1, "神殿の独特な香りを\nおうちに持ち込むのは\nどうでしょうか", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3736, 111901, 5, 1, "風邪をひきませんよう\n暖かくいたしましょう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3738, 111901, 7, 1, "食べ過ぎは\n正月太りの\nもとだというのに…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3739, 111901, 8, 1, "主さまのために\n参拝の作法を\n予習しておきましょう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3741, 112201, 1, 1, "…この杖、変身前には\n無いパーツがあるけど\nこれはどこから…うん\n考えないようにしよう", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3742, 112201, 2, 1, "フ、フリフリの\nかわいい衣装…はっ！\n素に戻ってはダメだ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3743, 112201, 3, 1, "魔法探偵\nミスティ★カスミ！\n魔法の力で捜査開始！\n…練習終わりっと", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3744, 112201, 4, 1, "怪しい視線を感じる…\nまさか、近くに\n闇の力を持つ人が！？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3745, 112201, 5, 1, "時間もあるし、\n魔法少女の修行を…\nじゃ、じゃなくて！\n探偵の仕事をしよう！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3746, 112201, 101, 4, "助手くんとずっと\n一緒に居られるし、\n魔法少女も\n案外悪くないかも♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3747, 112201, 102, 8, "魔法少女の訓練のため\n助手くんの所に行こう\nかな…あ、あくまで\n訓練のためにね！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3748, 112201, 1, 1, "…髪型が急に\n変わって驚いた？\nき、気になるなら…\n少し触ってみるかい？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3749, 112201, 2, 1, "助手くんは魔法少女に\nなりたいのかい？\nその場合は魔法騎士…\nいや、魔法助手とか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3750, 112201, 3, 1, "頭に浮かぶ呪文…\n強制変身…助手くん\n魔法少女とは\n呪いか何かなのかな？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3751, 112201, 4, 1, "あ、あまり触らないで\nくれ…魔法少女になる\nと感覚が鋭くなって、\n耳も敏感に…ひゃう！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3752, 112201, 101, 4, "や、やっぱりこの\n格好は変かな…え、\nかわいい？　じゃあ…\nいや、でも、う～ん", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3753, 112201, 102, 8, "…この変身は助手くん\nのせいでもあるんだよ\nだから…せ、責任を\n取ってもらうからね！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3754, 112201, 1, 1, "おはよう助手くん\nさあ、そろそろ\n街のパトロールに\n行こうじゃないか！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3755, 112201, 2, 1, "もうお昼…助手くん、\nそろそろ本業に戻ろう\n…その顔は何かな？\n私の本業は探偵だぞ！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3756, 112201, 3, 1, "夜だけど、私は\nもう少し仕事をするよ\n魔法少女の強い体は\nこんなときに便利だね", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3758, 112201, 2, 1, "気持ちいい…\nこの癒しを、魔法と\n論理の二つの観点から\n解明したいね…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3759, 112201, 3, 1, "ダ、ダメ！！\nここで感情のままに\n飛びついたら、私の\nイメージがぁ～！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3760, 112201, 4, 1, "心が安らぐ香りだね…\n…今なら、魔法少女も\nすんなりと受け入れ…\nてはダメだっ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3761, 112201, 5, 1, "あったかい…\n魔法少女だろうと\n休息は必要だし、少し\n休んでいこうかな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3763, 112201, 7, 1, "魔法少女も探偵も、\nやっぱり体が資本！\n食べられるときに\n食べておかないとね♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3764, 112201, 8, 1, "…魔法少女\nセリイちゃんの本は\nどこにあるのかな？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3766, 112301, 1, 1, "魔法少女かぁ…\nせっかくだし思う存分\n体を動かしてみたいな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3767, 112301, 2, 1, "ニャットちゃん…\nこの格好を見て\n私って気づくかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3768, 112301, 3, 1, "ふふ…\n今日はなんだか\n調子がいいかも", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3769, 112301, 4, 1, "魔法は便利だけど\nあまり頼りすぎちゃ\nダメだよね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3770, 112301, 5, 1, "さっき街で小さな子に\n声かけられちゃった\n照れるけどうれしいな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3771, 112301, 101, 4, "あの人がいると\nなんだかホッとする\n不思議…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3772, 112301, 102, 8, "魔法少女になっても\nあの人への気持ちは\nずっと同じ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3773, 112301, 1, 1, "今日の私…ですか？\nはいとっても元気です\n…あなたのおかげかも", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3774, 112301, 2, 1, "よかったら魔法の\n練習に付き合って\nくれませんか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3775, 112301, 3, 1, "あの…\nこの格好どうですか？\n似合ってると…いいな", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3776, 112301, 4, 1, "…な、なんですか？\nあまり見られると\n恥ずかしいです…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3777, 112301, 101, 4, "私、あなたのそばに\nいられたら、それで…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3778, 112301, 102, 8, "想い人と結ばれる\n魔法は…ひゃっ！？\n今の聞いてましたか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3779, 112301, 1, 1, "おはようございます\n今日も街の平和の\nために頑張らなきゃ", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3780, 112301, 2, 1, "お腹すきましたね。\nあの…一緒にランチは\nいかがですか？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3781, 112301, 3, 1, "あ、もうこんな時間…\nふふ、まだこんなに\n体力が余ってるのに", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3783, 112301, 2, 1, "気持ちいい…\nこの気持ち良さは\nどんな魔法も勝てない", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3784, 112301, 3, 1, "何これ、かわいい…\nお姉ちゃんにも\n見せてあげたいな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3785, 112301, 4, 1, "ん～、いい香り\n…これ、魔法で\n出せたりしないかな？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3786, 112301, 5, 1, "あったか～い\nえへへ…なんだか\nお姉ちゃんみたい", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3788, 112301, 7, 1, "おいしいなぁ、これ\n魔法少女になってから\n食欲旺盛かも…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3789, 112301, 8, 1, "ふふ…\n魔法少女になっても\n読書はやめられないな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3791, 101001, 103, 12, "みらくるまほりん\nくるりんぱ～♪\n王子はんのために\n花嫁修業や～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3792, 101001, 103, 12, "あ、あのな王子はん\nできたらその…\nお姫さまだっこを\nお願いしてもええ…？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3793, 180401, 1, 1, "やっぱりわたしたちの\nギルドハウスの\n居心地は最高ですね☆", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3794, 180401, 2, 1, "王宮じゃできないこと\nやっちゃいましょう♪\n例えば、えっと～…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3795, 180401, 3, 1, "そうだ、隠しておいた\nおやつがありました！\nまだ食べられますね☆", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3796, 180401, 4, 1, "ごはんはみんなで\n楽しく食べましょう！\nそれが一番の\n調味料です♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3797, 180401, 5, 1, "やっぱりここが、\nわたしの帰る場所です\n…ただいま☆", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3798, 180401, 101, 4, "なんだか物足りません\nきっと、あの人が隣に\nいないからですね…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3799, 180401, 102, 8, "どこを見ても、大切な\n思い出がたくさん\n詰まっていますね♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3800, 180401, 1, 1, "無礼者～、なんて\n冗談です♪　どんどん\n触っちゃってください", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3801, 180401, 2, 1, "ぷにぷにでしょう～？\nい～っぱい食べるのが\nぷにぷにの秘訣です！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3802, 180401, 3, 1, "握手したいんですか？\nはい、にぎにぎ～！\nもう離しませんよ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3803, 180401, 4, 1, "どうかしましたか？\n退屈してるんでしたら\nお散歩に行きましょう", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3804, 180401, 101, 4, "ごはん王子♪\nわたしは王女ですから\nこれでピッタリですね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3805, 180401, 102, 8, "大好きです～\n愛してます～\n…ほんと、ですよ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3806, 180401, 1, 1, "朝ごはんは大事です！\n山盛りごはんを食べて\n元気いっぱいですよ☆", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3807, 180401, 2, 1, "お腹ぺこぺこになる\n時間ですね～！\nごはん特盛、一つ！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3808, 180401, 3, 1, "お腹いっぱい、今日は\nよく眠れそうですね♪\n明日もおいしい一日を", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3810, 180401, 2, 1, "休憩休憩～♪\nお腹を休めて、次の\nごはんに挑みます…☆", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3811, 180401, 3, 1, "かわいらしい\nぬいぐるみですね～\nキャルちゃんみたい☆", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3812, 180401, 4, 1, "すんすん…この匂い\n食欲増進の効果が\nありそうですね…？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3813, 180401, 5, 1, "体温が上がってくると\n眠くなっちゃいます…\nほわぁ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3815, 180401, 7, 1, "もぐもぐもぐもぐもぐ\nむぐむぐむぐむぐむぐ\nはぐはぐはぐはぐはぐ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3816, 180401, 8, 1, "教養を身に着けるには\n読書が一番です！\nえっと次のレシピは～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3818, 112401, 1, 1, "アイドルらしくなる\nコツって、何か\nあるんでしょうか？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3819, 112401, 2, 1, "色んな人と話すのが\n好きなんです。つい\n長くなっちゃって…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3820, 112401, 3, 1, "アイドルを目指すって\n決めてから、毎日が\nワクワクなんです♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3821, 112401, 4, 1, "ちゃんとストレッチ\nしておかなくっちゃ\nよいしょ、よいしょ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3822, 112401, 5, 1, "リンちゃんミオちゃん\n二人に負けないように\n私、頑張ります！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3823, 112401, 101, 4, "私の笑顔は素敵だって\n誰かに褒められた…\nそんな気がするんです", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3824, 112401, 102, 8, "私たちを支えてくれる\n誰かが、ずっと一緒に\nいてくれたような…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3825, 112401, 1, 1, "どうかしましたか？\n何か困ってるのなら\nお手伝いしますよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3826, 112401, 2, 1, "みなさん個性的で\n普通じゃない人ばかり\nいるような気が…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3827, 112401, 3, 1, "今日はポカポカした\nいい天気ですね～\nお散歩日和です！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3828, 112401, 4, 1, "今、ミルクティーを\n淹れてたんです♪\nあなたも飲みますか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3829, 112401, 101, 4, "私の得意なこと？\nうーんなんでしょう…\nあっ、肩たたきです！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3830, 112401, 102, 8, "生・ハム・メローン！\nあ、ライブの掛け声を\n練習してたんですよ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3831, 112401, 1, 1, "おはようございます！\n今日も一日\n頑張りましょうね！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3832, 112401, 2, 1, "しっかりお昼を食べて\n午後からも張り切って\nいきましょうね♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3833, 112401, 3, 1, "ぐっすり眠れるよう\n早めに用事を済ませて\nお休みしましょうね", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3835, 112401, 2, 1, "ちょっと休んだら\nレッスンの続きも\n頑張ります！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3836, 112401, 3, 1, "えへへ～\nふわふわもこもこで\nかわいいです～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3837, 112401, 4, 1, "リラックスできる\nいい香りですね～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3838, 112401, 5, 1, "わぁ、ポカポカです♪\n温かい飲み物も\nほしくなりますね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3840, 112401, 7, 1, "レッスンしたら\nお腹が空いちゃって…\nぱくぱくもぐもぐ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3841, 112401, 8, 1, "レッスンに役立つ\n運動の情報とか\n載ってるでしょうか…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3843, 112601, 1, 1, "きらきらーん☆\nよし、次のステージは\nこれで行けるかも！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3844, 112601, 2, 1, "私の元気でみんなも\n元気になってくれたら\nうれしいな～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3845, 112601, 3, 1, "ちょっとレッスンで\n疲れちゃったかも…\nでも頑張るぞっ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3846, 112601, 4, 1, "晩ごはん何にしよう？\nそーだ、しぶりんにも\n聞いてみよっと！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3847, 112601, 5, 1, "元気だから笑顔になる\nじゃなくて\n笑顔だから\n元気になるんだよね～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3848, 112601, 101, 4, "声援を聞いてる時ね\nすごく懐かしいって\n思うことがあるんだ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3849, 112601, 102, 8, "私を応援してくれる\n一番大事な人が\n隣にいた気がする…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3850, 112601, 1, 1, "なになにー？\nミオちゃんに\n何か用かな～？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3851, 112601, 2, 1, "おっ、やっほー！\n元気そうだね～、キミ\n私？\nもちろん元気～！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3852, 112601, 3, 1, "ねぇねぇ、ちょっと\n遊びに行かない？\nたまには息抜きで！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3853, 112601, 4, 1, "ね、この魔物料理さ\nプリンって言い張れば\n食べてもらえそうかな", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3854, 112601, 101, 4, "えへへ\nいつもありがとねっ！\nパワーもらってるよ～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3855, 112601, 102, 8, "どうしたどうした～\nミオちゃんの魅力に\nメロメロなのかい？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3856, 112601, 1, 1, "おっはよ～！\n一日の始まりだし\nテンション上げてこ！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3857, 112601, 2, 1, "お腹空いた～…\nねぇねぇ、一緒に\nお昼食べようよ～", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3858, 112601, 3, 1, "ふわぁ～…\n眠くなってきちゃった\nそろそろ休もっかなぁ", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3860, 112601, 2, 1, "わぁ、ふっかふか～！\nレッスンの疲れも\n吹っ飛んじゃうね☆", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3861, 112601, 3, 1, "何これ…かわいーっ！\n早くしまむーに\n教えてあげなきゃ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3862, 112601, 4, 1, "すぅ～…\nうんっ、これ私の\n好きな香りだな～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3863, 112601, 5, 1, "ポッカポカだね～♪\n…あ～ん、ここから\n動きたくないーっ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3865, 112601, 7, 1, "わっ、おーいしー！\nこれはテンション\n上がっちゃうね♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3866, 112601, 8, 1, "ふむふむ…\nふむふむふむ…！\n勉強になった、ブイ☆", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3868, 100101, 103, 12, "なんでドキドキが\n止まらないの…？\n騎士くんのせいだー！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3869, 100101, 103, 12, "ドキドキしてるの\nあたしだけ、かな…\n騎士くんはどう…？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3870, 100201, 103, 12, "騎士クンの前で\n好きって言えるように\n練習しないと…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3871, 100201, 103, 12, "騎士クン…大好き\n…き、騎士クン！？\nなんでここに！？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3872, 100301, 103, 12, "困ったな…\n彼が居ないだけで、\nここまで\n寂しいと思うなんて…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3873, 100301, 103, 12, "えっと…\n今日は訓練じゃなくて\nその…二人でゆっくり\n過ごしたいな…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3874, 112501, 1, 1, "アイドルの心構え…\nどういう風に考えると\nしっくりくるのかな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3875, 112501, 2, 1, "何か始めるのって\nドキドキするよね\nだから最近楽しいんだ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3876, 112501, 3, 1, "冒険者の時って毎日\nバタバタしてたけど…\n目標ができると違うね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3877, 112501, 4, 1, "少しお腹が空いた…\nチョコレート一粒だけ\n食べようかな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3878, 112501, 5, 1, "ウヅキもミオも\n頑張ってるし\n私も負けられないね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3879, 112501, 101, 4, "レッスンしてる時さ\n何か、誰かが足りない\nそう思うことがあるの", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3880, 112501, 102, 8, "大事なのは笑顔…\n誰か、大切な人から\n言われた気がするんだ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3881, 112501, 1, 1, "どうしたの、何か用？\n手伝えることがあれば\nすぐに言ってね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3882, 112501, 2, 1, "姿勢がいい？\n普段から鍛えてるし\n自然とそうなるのかも", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3883, 112501, 3, 1, "さっき散歩してたら\n犬を見かけたんだ\n人懐っこい子だったよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3884, 112501, 4, 1, "今から冒険？\nなら私も一緒に行くよ\n前衛は任せておいて", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3885, 112501, 101, 4, "今からレッスンに\n行くつもりだったんだ\n少し手伝ってくれる？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3886, 112501, 102, 8, "蒼の剣を受けよ！\nえ、何してるのって？\nただの発声練習だけど", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3887, 112501, 1, 1, "おはよう、早いね\n今日は依頼もないし\nゆっくりしようかな", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3888, 112501, 2, 1, "そろそろお昼だね\nうーん、今日は\n何を食べよう…", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3889, 112501, 3, 1, "もうこんな時間…\n明日も早いし\nそろそろ寝なくちゃ", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3891, 112501, 2, 1, "レッスンで疲れたし\nちょっとだけ\n休んでおこうかな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3892, 112501, 3, 1, "ふふっ、かわいい\nふわふわしたものって\n撫でたくなるんだよね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3893, 112501, 4, 1, "うん、いい香り…\nこういうものがあると\n落ち着くからいいよね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3894, 112501, 5, 1, "温かい場所って\n居心地がいいから\nのんびりしちゃうよ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3896, 112501, 7, 1, "レッスン頑張ったら\nすぐお腹が空くし\n食べておかないと…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3897, 112501, 8, 1, "アイドルのことを\nもっと勉強して\n成長しなくちゃ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3899, 111001, 1, 1, "ああ、知りたい…\nなんでもかんでも\nとにかく知りたい…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3900, 111001, 2, 1, "ああ、眠い…\nいつでもどこでも\nとにかく眠い…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3901, 111001, 3, 1, "君、気を付けたまえ\nぼーっと歩いていると\nほわっ！　転んだ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3902, 111001, 4, 1, "ミツキ師にいただいた\n新作サプリ…丁度よい\n被験体はいないものか", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3903, 111001, 5, 1, "サレンディア救護院に\nユニちゃん８歳として\n潜入済みだが何か？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3904, 111001, 101, 4, "君がユニちゃんズの\n同志であること、\nぼくは誇りに思うぞ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3905, 111001, 102, 8, "ぼくは青春がしたい\nおい、君\nぼくと青春しないか", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3906, 111001, 1, 1, "ああ、そうだな。\nぼくもそう思う。\n……えっ、何が？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3907, 111001, 2, 1, "待て、話しかけるな。\n今の閃きをメモに…\n…ああっ、忘れた！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3908, 111001, 3, 1, "歩き方が綺麗？\n何を今更なこと\n言っとるんだ君は", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3909, 111001, 4, 1, "ぼくの血液型？\n大らかのОだ。\n何か異論あるかね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3910, 111001, 101, 4, "君の声は精神に\n高揚をもたらすな…\n波形を分析したいぞ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3911, 111001, 102, 8, "妙だな…最近\n君に話しかけられると\n心拍に乱れを覚える", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3912, 111001, 1, 1, "おっと、朝だな。\nそろそろ寝るか、\n朝更かしは体に悪い", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3913, 111001, 2, 1, "ふむ、昼餉時か…\n今日のランチは\nサプリ定食にしよう", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3914, 111001, 3, 1, "もう夜だな、\nそろそろ起きるか。\n最高の一日にするぞ！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3916, 111001, 2, 1, "ほわぁ～\n副交感神経が…\n優位に…ふふっひ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3917, 111001, 3, 1, "かわいすぎるな…\nセクシーユニには\nミスマッチか…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3918, 111001, 4, 1, "ほぁあアあァぁー…\nいい香りー…\nあアァあ…イイ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3919, 111001, 5, 1, "炎はいいぞ\nそこには人類発展の\nドラマがある", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3921, 111001, 7, 1, "フオオー！\n次から次へと固形食物\n原理気になるー！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3922, 111001, 8, 1, "良書は時間を忘れる\n…ふむ、今度\n布団を持ってこよう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3924, 101201, 103, 12, "きらーん☆\nハツネちゃんに\n不可能はないのだ☆", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3925, 101201, 103, 12, "キミと一緒だと\n寝ちゃうのが\nもったいないよ～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3933, 192101, 1, 1, "ちぇるーん☆", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3951, 110901, 1, 1, "ちぇる～ん☆\n今日もチエルは\nかわいいですよね～？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3952, 110901, 2, 1, "どこかに楽しいもの\n落ちてないかにゃー", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3953, 110901, 3, 1, "チエルのオキニは\n甘々カフェラテ！\nシクヨロでぇ～す☆", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3954, 110901, 4, 1, "先輩、ひまですよね？\nチエル遊んであげても\nいいですけど？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3955, 110901, 5, 1, "はぁー…しゅき…\nカルミナ尊い…\n一生すこれる…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3956, 110901, 101, 4, "先輩、ひまー\nちょっとは構えよーう\nデュクシ！デュクシ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3957, 110901, 102, 8, "先輩なんか\nおごって～！\nてか…おごらなくても\nいいから遊んでよ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3958, 110901, 1, 1, "え、なんですか？\n告白だったら\n間に合ってますけど", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3959, 110901, 2, 1, "ちょっと静かに！\n今ヤバかわポーズの\nイメトレ中なんです！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3960, 110901, 3, 1, "足取りが軽やか？\nあー…ダンスとか\n習ってるからですかね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3961, 110901, 4, 1, "チエルの血液型？\n大型新人のОです\n何か問題でも？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3962, 110901, 101, 4, "先輩って何気に\nいい声してますよね\n言うことは変だけど", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3963, 110901, 102, 8, "え、なんですか？\nたまにだったら\n告白してもいいですよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3964, 110901, 1, 1, "おはちぇる～ん☆\nハイ、ご一緒に～？", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3965, 110901, 2, 1, "今日のランチは\nどこで\n映えよっかにゃー", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3966, 110901, 3, 1, "こんばんちぇるるん☆\nハイ、ご一緒に～？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3968, 110901, 2, 1, "ふかふかチエルン\n略して、ふかルン♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3969, 110901, 3, 1, "かわいいこの子と\nかわいいチエル！\n映ぁ～え～る～！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3970, 110901, 4, 1, "かわいい香り～！\n持って帰ろっと！\nすぅーっ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3971, 110901, 5, 1, "マシュマロは\n焼かない方のが\nかわいくないです？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3973, 110901, 7, 1, "チエル食べても\n太らない体質\nなんでぇ～！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3974, 110901, 8, 1, "読書は好きですよ\nシンプルに楽しいし\nギャップ萌えでしょ？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3976, 112701, 1, 1, "みんなのあたしを\n見る目が違うね\nま、気にしないけどね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3977, 112701, 2, 1, "今日も疲れた～\nやる気ゼロだし\nもう寝ちゃおうかな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3978, 112701, 3, 1, "あたしの代わりに誰か\n働いてくれないかね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3979, 112701, 4, 1, "労働の後に食べる\nあんパン最高～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3980, 112701, 5, 1, "仕事の合間に\nちょっと一息いれよ\nあ～ドングリ美味し～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3981, 112701, 101, 4, "報酬も入ったことだし\nあいつに何か\n買ってあげようかな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3982, 112701, 102, 8, "次の休みはあいつと\n一日中だらだら\nしたいな～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3983, 112701, 1, 1, "あたしは褒められて\n伸びるタイプなんだ\nだから褒めて褒めて", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3984, 112701, 2, 1, "お、差し入れ？\n気が利くねぇ\nあんがと", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3985, 112701, 3, 1, "新しい依頼？\n今日はもう無理～\n閉店で～す", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3986, 112701, 4, 1, "サ、サボってないよ！\nちょっと休憩してた\nだけだから！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3987, 112701, 101, 4, "ねえ、一緒に\nサボろうよ？\n働き過ぎはよくないよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3988, 112701, 102, 8, "あんたが近くにいると\n仕事の疲れも\n和らぐんだよね～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3989, 112701, 1, 1, "はぁ…もう起きなきゃ\nつらい労働の\nはじまりだ～", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3990, 112701, 2, 1, "うぅ、お昼に\nあんパン食べ過ぎた…\n苦しい…眠い…", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3991, 112701, 3, 1, "明日は仕事ないし\nガッツリ夜ふかし\nしちゃうよ～", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3993, 112701, 2, 1, "猫みたいに\n丸くなっちゃうよ～\nあたしリスだけどね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3994, 112701, 3, 1, "ふかふか～\nあたしの尻尾と\n互角とはやるね～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3995, 112701, 4, 1, "あんパンの香りの\nアロマとか\nあればいいのにな～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3996, 112701, 5, 1, "これは危険な\nあたたかさだね～\n労働意欲が\nなくなるよ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3998, 112701, 7, 1, "いっぱい働いたから\nいっぱい食べても\nカロリー０だよ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3999, 112701, 8, 1, "あたしだってたまには\n本くらい読むって\n全然似合わないけどね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4001, 104601, 103, 12, "あたしの作った\nオリジナルたい焼き…\nあいつ、\n喜んでくれるかにゃ？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4002, 104601, 103, 12, "あ、お帰りにゃー♪\nえへへ…キミに\nお帰りって言えるのが\nなんかうれしいにゃ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4003, 112801, 1, 1, "フロンティア\nスピリッツを\nお見せするべ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4004, 112801, 2, 1, "この鎧を着ると\n気が引き締まる\n感じがするべ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4005, 112801, 3, 1, "搾りたての牛乳飲んで\n今日も一日\nがんばるべ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4006, 112801, 4, 1, "みんなに牧場の\n乳製品を\n味わってもらいてぇべ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4007, 112801, 5, 1, "ここはいいとこだべ\nうちの動物たちも\n連れてきてやりてぇな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4008, 112801, 101, 4, "みんなオラより\n若ぇのに働いてて\n立派だべな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4009, 112801, 102, 8, "牧場もギルドの\nみんなもオラが\nまとめて面倒見るべ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4010, 112801, 1, 1, "さあ、今日も\n忙しくなるべよ\n気合入れてくべ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4011, 112801, 2, 1, "お、あんちゃん\n丁度いいとこに！\n一緒に街に\n行商しに行くべ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4012, 112801, 3, 1, "牧場仕事で培った力が\n戦いでも役立つなら\n一石二鳥だべさ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4013, 112801, 4, 1, "疲れた時は牧場産の\nミルク菓子で\n糖分補給だべ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4014, 112801, 101, 4, "これ、牧場の\n新製品だべ\n一番最初に味見\nしてほしいべさ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4015, 112801, 102, 8, "働きづめはよくねぇベ\n膝枕してやっから\nこっち来るべさ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4016, 112801, 1, 1, "ふぁあ…\n酪農家の朝は\n早ぇんだべ…", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4017, 112801, 2, 1, "ごはんの時間だべ！\n腹が減っては酪農も\n戦いもできねえべ！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4018, 112801, 3, 1, "そろそろ寝るべ！\n健全なお笑いは\n健全な肉体に宿るべ！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4020, 112801, 2, 1, "牧草みたいに\nふっかふかだべ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4021, 112801, 3, 1, "この子、目の感じが\nエリザベスに似てるべ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4022, 112801, 4, 1, "いい匂いだべ～\n疲れた体に\nしみるべな～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4023, 112801, 5, 1, "あったけぇな～\n牧場にも\n置いてみっかな～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4025, 112801, 7, 1, "うまいべ！\n生産者のこだわりを\n感じるべさ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4026, 112801, 8, 1, "酪農も戦いも\nまずは知識だべ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4028, 113001, 1, 1, "ぴょん、ぴょん♪\nこの服のおかげで、\n体が軽いなぁ…♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4029, 113001, 2, 1, "今の内に部屋の\n掃除をしておこっと…\nみんな喜んで\nくれるといいなぁ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4030, 113001, 3, 1, "耳と尻尾…この服、\n本当にウサギさん\nみたいだなぁ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4031, 113001, 4, 1, "なんだか先輩の\n気配がするような…\n気のせいかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4032, 113001, 5, 1, "ウサギさんの真似を\nしたら、存在感が薄い\nとか言われないかな？\nア、アユミだぴょん♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4033, 113001, 101, 4, "えへへ…\nいつもとは違う先輩が\nたくさん見られて、\nうれしいな♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4034, 113001, 102, 8, "本の中の世界で先輩と\n旅をするなんて…\n幸せすぎて、罰が\n当たりそうだよぉ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4035, 113001, 1, 1, "じ～…はっ！？\nす、すみません！\n話しかけてもらえる\nなんて思ってなくて！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4036, 113001, 2, 1, "先輩がすぐそばに…\nし、幸せすぎて\n倒れそうですぅ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4037, 113001, 3, 1, "え、えっと…あの…！\nうう…いざ先輩と\n向き合うと\n緊張しちゃうよぉ…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4038, 113001, 4, 1, "はうっ！？\nしょ、正面から見る\n先輩は、私にとって\n神々しすぎますぅ…！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4039, 113001, 101, 4, "先輩…その…\nい、一緒にご飯でも\nどうでひゅか！\nうう…またかんだぁ…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4040, 113001, 102, 8, "せ、先輩…\nハーブティーを\n入れたんですが、\n一緒にどうですか…？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4041, 113001, 1, 1, "せ、先輩！\nおはようございます！\nえへへ、練習通りに\nバッチリ言えたぁ♪", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4042, 113001, 2, 1, "あ、もうお昼ですね\n時計を持ってて\n良かったです", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4043, 113001, 3, 1, "もうこんな時間…\nお休みする\n準備をしちゃいますね", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4045, 113001, 2, 1, "あ…ちょっとだけ\n先輩の匂いがする…\nえへへ、幸せだなぁ♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4046, 113001, 3, 1, "…私もウサギさんの\nぬいぐるみとして、\n先輩のそばに…はっ！\n私ったら何を…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4047, 113001, 4, 1, "あ、いい香り…\n先輩もこういうのが\n好きなのかな？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4048, 113001, 5, 1, "先輩が隣に\n居たら、もっと\n暖かいのになぁ…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4050, 113001, 7, 1, "おいしい…でも、\n先輩と一緒に\n食べたら、もっと\nおいしいだろうな…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4051, 113001, 8, 1, "ウサギさんって、穴を\n掘ったりするんだ…\n私も、隠れたいときに\n掘ってみようかな？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4053, 180501, 1, 1, "ネビアさまのように\n普段からパタパタと\n飛んでみるのも…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4054, 180501, 2, 1, "ご立派になられた\n主さまと並べるように\nもっと精進しなくては", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4055, 180501, 3, 1, "月々の食費が\n増えてしまいました\n申し訳ございません…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4056, 180501, 4, 1, "ワンワンのほうの\n主さまは、この姿に\n驚くでしょうか…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4057, 180501, 5, 1, "主さまと離れていても\n主さまとの絆は\n胸に感じております…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4058, 180501, 101, 4, "このギルドハウスは\nみなさまの帰る場所…\nわたくしが守ります", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4059, 180501, 102, 8, "どんな未来が\n待ち受けようとも\n主さまと添い遂げます", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4060, 180501, 1, 1, "おでかけですか？\nではわたくしも\nお供いたします", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4061, 180501, 2, 1, "もう二度と主さまと\n離れたくはありません\nいつ何時も…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4062, 180501, 3, 1, "なにかお困りごと\nでございますか？\nわたくしに何なりと…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4063, 180501, 4, 1, "どうぞゆっくりと\n心ゆくまでくつろいで\nくださいまし、主さま", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4064, 180501, 101, 4, "この羽で主さまを\nどこまでも遠く…\n高くへと導きます", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4065, 180501, 102, 8, "主さま、主さま…♪\nおそばにいられるのが\nうれしくて、つい…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4066, 180501, 1, 1, "おはようございます\n今日も一日、主さまの\nサポートをいたします", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4067, 180501, 2, 1, "今日も頑張っている\n主さまに、スタンプを\nもう一つ…ポン！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4068, 180501, 3, 1, "寝付けないのでしたら\nわたくしが子守唄を\n歌ってさしあげます", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4070, 180501, 2, 1, "ふかふか…\nまるで主さまのような\n柔らかさでございます", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4071, 180501, 3, 1, "あなたも一緒に\n主さまと遊びますか？\n至福の時間ですよ♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4072, 180501, 4, 1, "主さまの優しくて\nくすぐったい匂いが\nたまらなく恋しいです", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4073, 180501, 5, 1, "主さまの心と\n繋がったときのような\n温もりを感じます", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4075, 180501, 7, 1, "いけませんっ…！\n元の姿に戻ると、また\n食べてしまうのに…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4076, 180501, 8, 1, "主さまに読み聞かせる\n絵本は…妖精が主役の\n物語にいたしましょう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4078, 104801, 103, 12, "…彼のこと弟みたいに\n思ってきたのに…私、\nどうしたらいいの…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4079, 104801, 103, 12, "よ、予定がなかったら\nでいいんだけど…\nその、ふ、二人で\n訓練とか…どうかな？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4080, 112901, 1, 1, "おめかしもしたし\nあとはお兄ちゃんを\n待つだけですね～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4081, 112901, 2, 1, "お兄ちゃんの身の回り\nをお片付け～、これぞ\nデキる妹アピール！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4082, 112901, 3, 1, "あ、衣装がほつれてる\nこれくらいなら自分で\n繕えますね…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4083, 112901, 4, 1, "この格好で歩いてると\n見慣れた景色も\n違うものに見えますね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4084, 112901, 5, 1, "勉強もちゃんとします\n…でも、一人だと\n集中力がもちません～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4085, 112901, 101, 4, "お兄ちゃんを大好きな\n妹が待ってますよ～\n…まだですかね～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4086, 112901, 102, 8, "お兄ちゃんのために\n掃除洗濯をする私って\nお嫁さんみたい…\n…えへへ～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4087, 112901, 1, 1, "おに～いちゃん！\n縫い物、一緒に\nやりましょうよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4088, 112901, 2, 1, "かわいい妹を\n愛でたくなりました？\n思う存分、どうぞ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4089, 112901, 3, 1, "わっ、ビックリした～\nもう、お兄ちゃんてば\nイタズラっ子ですね～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4090, 112901, 4, 1, "一緒にお買い物\n行きませんか？\n妹とデートですよっ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4091, 112901, 101, 4, "お兄ちゃん…頭を\n撫でてくれませんか？\nえへへ、これが一番♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4092, 112901, 102, 8, "お兄ちゃん大好き♪\n何度でも言いますよ！\nお兄ちゃん大好きっ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4093, 112901, 1, 1, "おはよ～お兄ちゃん！\n妹モーニングコールの\n時間ですよっ♪", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4094, 112901, 2, 1, "そろそろ休憩ですね！\n甘いお菓子で\n元気回復しましょ～！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4095, 112901, 3, 1, "夜更かし厳禁！\nでも、もう少しだけ\nお話ししたいなぁ～", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4097, 112901, 2, 1, "ふっかふかですよ！\nお兄ちゃんと一緒に\n座りたいなぁ…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4098, 112901, 3, 1, "このぬいぐるみの\nかわいさ…う～ん\n負けられません…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4099, 112901, 4, 1, "香りにも気をつかって\nパーフェクトな妹に\nなっちゃいますから！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4100, 112901, 5, 1, "はぁ～ぬくぬくです～\nお兄ちゃんと一緒に\n温まりたいなぁ…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4102, 112901, 7, 1, "これは止まりませんね\nもぐもぐ…いくらでも\n食べられちゃいます", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4103, 112901, 8, 1, "これ、デザインの\n参考になりそう！\n私にも作れるかなぁ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4105, 106601, 1, 1, "退屈です…\nなにか楽しいことは\nないですか？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4106, 106601, 2, 1, "ギャングの仕事は\n今日はお休みです\nのんびりするですよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4107, 106601, 3, 1, "ひうっ！　あたしの\n尻尾なんて食べても\nおいしくないですよ？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4108, 106601, 4, 1, "ふう…どうやら\n例の件はまだボスに\nバレてないですね…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4109, 106601, 5, 1, "下克上…いい言葉です\nって、ボスに聞かれて\nないですよね…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4110, 106601, 101, 4, "いつか絶対ビッグに\nなってやるです！\nあたしを信じるです！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4111, 106601, 102, 8, "あたしが覚醒したら\nきっと誰よりも\n強くなるはずです！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4112, 106601, 1, 1, "なんですか？\n貢ぎ物なら\nいつでも歓迎ですよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4113, 106601, 2, 1, "あたしだって\nやる時はやるですよ！\nその時がこないだけ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4114, 106601, 3, 1, "しりとり勝負ですか？\nいい度胸ですね、\nかかってくるです！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4115, 106601, 4, 1, "お兄さんは\nヒマそうですね…\nうらやましいです", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4116, 106601, 101, 4, "かまってほしいです？\nまったく…いいですよ\nしょうがないですね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4117, 106601, 102, 8, "お兄さんのことは\nこのあたしが\n守ってやるですよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4118, 106601, 1, 1, "デキるギャングの\n朝は早いのです…\nふぁあ…", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4119, 106601, 2, 1, "お腹がすいたですね\nなにか食べにいくです\nお兄さんのおごりで！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4120, 106601, 3, 1, "ふぁあ…むにゅう…\nまだ起きてるですよぉ", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4122, 106601, 2, 1, "はぁ～…\nもうここから一歩も\n動きたくないです", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4123, 106601, 3, 1, "なかなかいいモフりを\nしてやがるです…\n舎弟にしてやるですよ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4124, 106601, 4, 1, "なんかおしゃれな\n匂いがするですね…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4125, 106601, 5, 1, "ふふーん！\nあたしのブレスの方が\n火力は上ですね！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4127, 106601, 7, 1, "あたしに貢ぎ物とは\nよく分かってるですね\n食べてやるですよ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4128, 106601, 8, 1, "ふーむ…\nもっと楽しい本は\nないですかね？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4130, 113201, 1, 1, "海に眠る秘宝…\n心躍る響きだ！\n早く見つけたい…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4131, 113201, 2, 1, "うう～、暑いぃ…\nかき氷を食べて\nすっきりしたいぃ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4132, 113201, 3, 1, "アターック！\nいや、もっとこう…\nアタァーック！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4133, 113201, 4, 1, "スイカ割りで\n目を回さない\nコツはなんだろうか…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4134, 113201, 5, 1, "せっかく海に来たのだ\n泳ぎも上手に\nなりたいものだな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4135, 113201, 101, 4, "ふっふっふ…\n疼く…疼くぞ！\n日焼けの痕が…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4136, 113201, 102, 8, "シグルドと私は\n来世でも共にある…\nつまり我らはずっと…\nはわわ…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4137, 113201, 1, 1, "シグルド、私と共に\n日光浴に行かないか？\n光の力を集めよう！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4138, 113201, 2, 1, "この格好だからできる\n必殺技を考えている\n何か案はないか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4139, 113201, 3, 1, "シグルド～！\nスイカを貰ったから\n一緒に食べよう！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4140, 113201, 4, 1, "こう日差しが強いと\n日焼けしそうだな\n跡になってないか…？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4141, 113201, 101, 4, "我らの夏の黙示録\nしっかりと記録\nしておかねばな", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4142, 113201, 102, 8, "ちゃんと日焼けどめを\n塗ってもらうべき\nだったかな…？\nシ、シグルドに…！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4143, 113201, 1, 1, "朝だぞシグルド！\nさぁ、日光を浴びて\n光の加護を得るのだ！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4144, 113201, 2, 1, "もう昼食の時間か…\n私の魔力も減ったし\n早く補給しなければ", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4145, 113201, 3, 1, "夜でも我らの活動は\n終わることはない！\nさぁ準備を…ふわぁ…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4147, 113201, 2, 1, "海で遊んでいると\n疲れてしまうからな\n少し休まるとしよう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4148, 113201, 3, 1, "えへへ～、君の名前は\n何にしまちょうかね♪\n…はっ、見たのか！？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4149, 113201, 4, 1, "心地よい香りで\n我が精神も安らぐ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4150, 113201, 5, 1, "別に寒くなどないぞ！\n寒くないが、ちょっと\n暖まりたくなってな！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4152, 113201, 7, 1, "もぐ…食べ過ぎた分は\n魔力に変換されるのだ\n…たぶん", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4153, 113201, 8, 1, "…なな、なんだ！？\nシグルドはこういう\n趣味なのか…！？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4155, 113201, 10, 1, "むむ、この乗り心地…\nいざゆかん、\nシードラゴンよ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4156, 113201, 11, 1, "疾風の冥姫の名に\nふさわしき\n深海の玉座よ…\nふああ…柔らかい～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4157, 180201, 1, 1, "この姿でいると\nわたしもお姫様に\nなれた気がする…♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4158, 180201, 2, 1, "お腹空いてきちゃった\nまずは買い出しに…\n…変身解かなくちゃ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4159, 180201, 3, 1, "胸のところが空いてて\nスースーするの\nまだ慣れないな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4160, 180201, 4, 1, "治癒魔法に使ってた\n魔力を攻撃魔法に…\n頭の中でも練習練習", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4161, 180201, 5, 1, "何もできなかった\nわたしから、少しは\n変われたのかな…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4162, 180201, 101, 4, "騎士クン…\n今どこにいるの…？\n…会いたいな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4163, 180201, 102, 8, "離れてても分かるよ…\n温かい力で騎士クンに\n守られてるの", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4164, 180201, 1, 1, "わたしが戦えてるのは\n騎士クンのおかげだよ\nいつもありがとう！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4165, 180201, 2, 1, "お腹空いてそう…？\n…内緒で作ったおやつ\n騎士クンも食べる？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4166, 180201, 3, 1, "ひゃうっ！　え！？\nせ、背中はダメ～！\n言ってから触って～！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4167, 180201, 4, 1, "この格好、どうかな？\nえ？　似合ってる…？\n本当？　…うれしい！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4168, 180201, 101, 4, "かわいい？　そうだね\nひらひらが…わたし？\nええっ、そ、そんな！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4169, 180201, 102, 8, "騎士クン、これからも\nずっと…わたしの\nそばにいてください", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4170, 180201, 1, 1, "朝からこんなに\n食べる日が来るなんて\n思ってなかったよ…", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4171, 180201, 2, 1, "お昼を作らなくちゃ\n騎士クン、一緒に\nお料理してみる？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4172, 180201, 3, 1, "騎士クン、もう眠い？\n眠るまで、わたしが\nずっとそばにいるね", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4174, 180201, 2, 1, "攻撃参加するからこそ\n適度に体を休めるのは\n大切だよね！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4175, 180201, 3, 1, "かわいい…騎士クンと\nシェフィちゃんにも\nプレゼントしたいな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4176, 180201, 4, 1, "いい香り～…\nあれ？　お腹が空いて\nきちゃった…！？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4177, 180201, 5, 1, "温かい…\n騎士クンがくれる\n力みたい…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4179, 180201, 7, 1, "ぱくっ、\nあむ、はむっ…\nううぅ～…やっぱり\n手が止まらないよぉ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4180, 180201, 8, 1, "新しく覚えたいな…\nみんなで食べられる\n大皿向けの料理♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4182, 104901, 103, 12, "お姉ちゃんの愛\nこのままじゃ\nあふれちゃうよ…♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4183, 104901, 103, 12, "弟くん♪\n弟くんっ♪\nおっとうっとくんっ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4184, 113101, 1, 1, "泳ぐのもいいけど\n今日は砂浜で\nのんびりしようかね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4185, 113101, 2, 1, "さてと\nちょっくらひと泳ぎ\n行ってくるかね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4186, 113101, 3, 1, "あっついねぇ…\nうちわがないと\nしんどいったらないよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4187, 113101, 4, 1, "冷たすぎる飲み物で\n体を冷やさないよう\n気をつけないと…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4188, 113101, 5, 1, "夏はあっという間だし\n悔いがないように\n楽しまないとね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4189, 113101, 101, 4, "きっとこの夏は\nアタシにとって大事な\n想い出になるね…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4190, 113101, 102, 8, "…前に比べたら\nアタシたちの距離も\n近づいたみたいだね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4191, 113101, 1, 1, "もし暇なら\n今日も笹舟釣り\n一緒にどうだい？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4192, 113101, 2, 1, "お前さん、釣りに\n興味が湧いたなら今度\n道具を見に行こうよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4193, 113101, 3, 1, "今から砂浜に行くんだ\nお前さんも一緒に\n散歩するかい？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4194, 113101, 4, 1, "え、日焼け跡？\nおや…本当だ\nすぐ焼けちまうねえ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4195, 113101, 101, 4, "ひゃっ！？\nちょっと！\n日焼け跡をいきなり\n触らないでおくれ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4196, 113101, 102, 8, "お前さんは、その…\nアタシと一緒にいて\n楽しかったかい？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4197, 113101, 1, 1, "おはようさん\n今日も砂浜に行って\nゆっくりしようか", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4198, 113101, 2, 1, "お昼か…\nそうだ、よかったら\n刺身定食、作ろうか？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4199, 113101, 3, 1, "夜の空を見てるとさ\nなつかしい気持ちに\nなるんだよね", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4201, 113101, 2, 1, "適度に休憩して\n夏バテしないよう\n気をつけないとね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4202, 113101, 3, 1, "あんたたちも\n海を楽しみたいって\n思っているのかね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4203, 113101, 4, 1, "ん～…香りでも\nバカンスらしさを\n楽しめるもんだね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4204, 113101, 5, 1, "こういう格好だと\n少し冷えちまうんだよ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4206, 113101, 7, 1, "もぐもぐもぐ…\n遊んだ分、お腹が\n空いちまってねぇ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4207, 113101, 8, 1, "…ん、なんだいこの本\n催眠…うーん読めない\nナナカに聞いてみるか", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4209, 113101, 10, 1, "へえ…\nなかなかいい\n乗り心地じゃないか", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4210, 113101, 11, 1, "そうそうハマグリって\n竿でも釣れるんだよ\n知ってたかい？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4211, 113301, 1, 1, "海を満喫しなきゃね\nちょっとひと泳ぎ\n行ってこようかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4212, 113301, 2, 1, "う～ん…♪\nいつもと違った\n開放感～\nなんちゃって！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4213, 113301, 3, 1, "今のナナカちゃんは\nたぶん水属性！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4214, 113301, 4, 1, "水着もバッチリ！\nサイカワサイツヨ\nナナカちゃ～ん☆", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4215, 113301, 5, 1, "んっふっふ\nドキ！　水着だらけの\nギルドハウス！\nとかどうかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4216, 113301, 101, 4, "そんなに見られると\nちょ～っと\n恥ずかしいかも…\nなんてねなんてね！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4217, 113301, 102, 8, "ちょっとくらい\nドキッとしてくれても\nいいのにな～、もう…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4218, 113301, 1, 1, "水着魔法少女の\n相方がほしいなぁ～…\nね、君やってみない？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4219, 113301, 2, 1, "いつもの格好と同じ？\nも～、わかってない！\nセクスィー度が違う！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4220, 113301, 3, 1, "どうかなどうかな？\nこのひらひらのとこが\nかわいーかなー\nとか思うんだけど！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4221, 113301, 4, 1, "せっかくだから\nギャルゲーイベント\nごっことか\nやってみる？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4222, 113301, 101, 4, "ナナカちゃんの\n好感度を上げたい？\nならおでかけだー！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4223, 113301, 102, 8, "おうちで水着って\nなんだかコスプレ\nみたいで楽しいかも♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4224, 113301, 1, 1, "おっはよーう！\n今日はバカンス日和！\n海へゴーですぞ～♪", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4225, 113301, 2, 1, "お昼ご飯どうしよ～…\nあ、ナナカちゃんと\nどっか食べに行く？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4226, 113301, 3, 1, "夜は本を読むのが\n捗りまくりですな～！\n今日はなに読もっかな", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4228, 113301, 2, 1, "次のイベントもあるし\nちょっと休んで体力を\n回復しなきゃね～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4229, 113301, 3, 1, "こういうマスコット\n私にもいたらなぁ\nいっそ作っちゃう…？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4230, 113301, 4, 1, "水着魔法少女に\n似合う香りってのも\n考えてみよっかな♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4231, 113301, 5, 1, "夜になると急に冷えて\n肌寒いんだよね～…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4233, 113301, 7, 1, "あんまり食べると\nお腹が出て…でも…\nうう、止まらない～！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4234, 113301, 8, 1, "この催眠魔法の本\n誰にもばれないように\n保管しとかなきゃね…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4236, 113301, 10, 1, "ゴーゴーゴーゴーゴー\nナナカちゃーん♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4237, 113301, 11, 1, "ナナカちゃんの\n誕生～\nなんちゃって", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4238, 113401, 1, 1, "ファイトファイト☆\n一生懸命\n応援するね！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4239, 113401, 2, 1, "みんながんばってる！\n私も応援でみんなを\n盛り上げなきゃ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4240, 113401, 3, 1, "もし元気のない人が\nいたら…応援で少し\nでも元気にしたいな！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4241, 113401, 4, 1, "がんばれ～☆\nよし、発声練習OK！\n応援は、声も大事！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4242, 113401, 5, 1, "せっかく水着なんだし\n泳いだり潜ったりして\n遊びたいな～！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4243, 113401, 101, 4, "チアリーダーとして\nキミを精一杯\n応援したいな…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4244, 113401, 102, 8, "キミへの応援\nちゃんと\n届いてるかな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4245, 113401, 1, 1, "えへへ…今ね、\nキミへの応援歌を\n考えてたのだ～♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4246, 113401, 2, 1, "どうしたの？\n元気がないなら\n私が応援するよ☆", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4247, 113401, 3, 1, "がんばったあとは休憩\nしなきゃね！　私と\n一緒にお昼寝する？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4248, 113401, 4, 1, "シオリンとおそろいの\n水着で応援…\nしてみたいなぁ～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4249, 113401, 101, 4, "次はなにして遊ぶ？\nキミがしたいことを\n教えてよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4250, 113401, 102, 8, "キミを応援\nすることができて…\n私、すごく幸せ…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4251, 113401, 1, 1, "キミって早起き\nなんだね！　私も\n見習わなきゃ！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4252, 113401, 2, 1, "ちょっとお疲れかな？\nそれじゃあ\n応援してあげるね♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4253, 113401, 3, 1, "遅い時間まで\nがんばってえらいね！\nでも無理はダメだよ♪", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4255, 113401, 2, 1, "ちょっとだけ休憩…\n…あう、座り心地が\n良すぎて…眠気が…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4256, 113401, 3, 1, "ここにいるみんなを\n私の応援でもっと\n元気にするのだ～！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4257, 113401, 4, 1, "元気の出る香り～♪\nこれもうまく応援に\n活かせないかな～？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4258, 113401, 5, 1, "ぽかぽかあったまって\n元気をチャージ\nするのだ～☆", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4260, 113401, 7, 1, "いっぱい食べて、\nみんなを応援する\nパワーをつけなきゃ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4261, 113401, 8, 1, "みんなを元気にする\n方法は…どこかに\n書いてないかなぁ…？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4263, 113401, 10, 1, "わぁ、この子\n怪獣さんみたいで\nかわいいね～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4264, 113401, 11, 1, "水着でこれに\n乗ってると海にいる\n気分になれるね！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4265, 113501, 1, 1, "すごく忙しいけど\nとても充実した気分\nこれが青春なのかしら", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4266, 113501, 2, 1, "森の静かな雰囲気も\n海の開放的な空気も\nどちらも素敵ね～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4267, 113501, 3, 1, "汚れたお洗濯物は\nみんなが頑張った証\nなんだか愛おしいわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4268, 113501, 4, 1, "みんなを見てると\n何だか私も\n走りたくなっちゃうわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4269, 113501, 5, 1, "せっかくの水着だし\n泳ぐ練習でも\nしてみようかしら", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4270, 113501, 101, 4, "みんな～ファイト～！\n応援するのも大事よね\n練習しておかなきゃ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4271, 113501, 102, 8, "先生に教えられること\nまだまだい～っぱいね\n次は何がいいかしら♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4272, 113501, 1, 1, "今日も暑くなりそうね\nこまめな水分補給を\n忘れないでね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4273, 113501, 2, 1, "手伝ってくれるの？\nありがとう♪\n先生、頼りにしてるわ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4274, 113501, 3, 1, "休憩はこまめに\nとりましょうね\n無理は厳禁よ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4275, 113501, 4, 1, "海の遊びって\nどんなのがあるの？\n先生に教えてね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4276, 113501, 101, 4, "水着は初めてだけど\nどう？　変じゃない？\nふふ、ありがとう", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4277, 113501, 102, 8, "あなたと過ごした\nこの夏は、きっと…\n忘れられない思い出ね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4278, 113501, 1, 1, "おはよう～\n朝練前にちゃんと\nごはん食べましょうね", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4279, 113501, 2, 1, "お弁当作ってきたの\n栄養いっぱい摂って\n午後も頑張ろうね", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4280, 113501, 3, 1, "もうお休みしなきゃね\n先生が子守唄を歌って\nあげましょうか？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4282, 113501, 2, 1, "ふふ、いい気持ちねぇ\n先生、少しだけ\n休憩しちゃおうかしら", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4283, 113501, 3, 1, "あらかわいい♪\nチームにもこういう\nマスコットが欲しいわ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4284, 113501, 4, 1, "あら、海の香り？\n波の音まで聞こえて\nきちゃいそうだわ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4285, 113501, 5, 1, "うふふ\nこれなら濡れた水着を\n乾かすのも簡単ね♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4287, 113501, 7, 1, "これ、美味しいわね♪\nでも、食べ過ぎは\n禁物よ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4288, 113501, 8, 1, "先生だって日々勉強よ\n新しいことを知るのは\nとても楽しいわ♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4290, 113501, 10, 1, "あらあら、楽しいわね\nみんなもきっと\nおおはしゃぎね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4291, 113501, 11, 1, "ふふ、これで\n夏らしい気分を\n満喫するわよ♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4292, 102301, 103, 12, "サンドイッチも\n作ったし、\nお兄ちゃんのお出迎え\n準備は完璧だね♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4293, 102301, 103, 12, "えへへ、お兄ちゃんと\nぷうきちが居てくれる\nだけで、なんだか\nうれしくなっちゃう♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4294, 113601, 1, 1, "少し赤くなっているな\n日焼けなんてしたのは\nいつ以来だろう", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4295, 113601, 2, 1, "この格好で\n過ごすのは\n少し勇気がいるな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4296, 113601, 3, 1, "暑いのは平気だが\n肌が外気に触れるの\nにはまだ慣れないな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4297, 113601, 4, 1, "この格好でも\n堂々としていれば\n恥ずかしくはない…\nのかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4298, 113601, 5, 1, "おっと…鎧姿の時と\n重心が違うせいか\nちょっと不安定だな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4299, 113601, 101, 4, "この水着…\n私に似合って\nいるのだろうか…？\n少年には聞きづらいな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4300, 113601, 102, 8, "少年との\n海のバカンス…\nまた行きたい\nものだな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4301, 113601, 1, 1, "あまり見るのは\nよしてくれ…\n恥ずかしいじゃ\nないか…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4302, 113601, 2, 1, "どうかしたのかい？\n私でよければ\n話し相手くらいには\nなろうじゃないか", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4303, 113601, 3, 1, "ひゃっ！\nす、すまない\nちょっと過敏に\nなっているようだ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4304, 113601, 4, 1, "…っくしゅん！\nすまない\n薄着には慣れて\nないものでね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4305, 113601, 101, 4, "まあ、少年になら\n見られても…\nい、いや！\nなんでもない！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4306, 113601, 102, 8, "私がこうして\nゆっくりできるのは\n少年がそばにいて\nくれるからだよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4307, 113601, 1, 1, "この時間なら\nビーチにも人は\n少ないだろうね…\n遊びに行くかい？", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4308, 113601, 2, 1, "昼食のお誘いかい？\nいいよ、付き合おう", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4309, 113601, 3, 1, "もう夜か…\n一日が経つのは\nはやいものだね", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4311, 113601, 2, 1, "これは楽でいいな…\nいつもは鎧姿で\n家具を傷つけないか\n気を使っているからね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4312, 113601, 3, 1, "うん…ふわふわの\n感触が\nよくわかるな…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4313, 113601, 4, 1, "いつもは\n鎧に匂いが\n移ってしまうのが\n悩みなんだよ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4314, 113601, 5, 1, "うーむ…\n涼しいのに暖かい\nなんだか奇妙な気分だ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4316, 113601, 7, 1, "兜を気にせず\n食べられるのは\n楽でいいな…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4317, 113601, 8, 1, "ふむ…鎧がないと\nページがめくりやすい", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4319, 113601, 10, 1, "身軽でいいな…\n鎧を着ていると\n騎乗訓練の時\n馬に申し訳がなくてね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4320, 113601, 11, 1, "貝をうまく使えば\n水中でも\n守りを固められそうだ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4321, 106801, 1, 1, "どこをアジトと\nつなげようかな～…\nこのへんにしよっと♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4322, 106801, 2, 1, "ここってちょっと\n変わった建物だね\n妖精がいるでしょ？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4323, 106801, 3, 1, "色んな人を観察できて\n眼福眼福…♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4324, 106801, 4, 1, "家具の配置変更なら\nアタシにおまかせあれ\n…家の形は変えない？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4325, 106801, 5, 1, "新メニューどうしよう\n思いついた人がいたら\nアタシに教えてね！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4326, 106801, 101, 4, "少年、早く帰って\nこないかな…\nよし、迎えに行こう！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4327, 106801, 102, 8, "結局またあちこち\n移動する日々だし…\n少年に会いたいな～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4328, 106801, 1, 1, "どしたの少年？\nお姉さんに何でも\n相談してみな～？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4329, 106801, 2, 1, "あれをああして…\nおや少年！　ごめんね\nすぐに気づけなくて！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4330, 106801, 3, 1, "何？　クリスが\nアタシを探してる？\n…いないって言って！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4331, 106801, 4, 1, "よければリノちゃんと\nシズルちゃんにも\n顔を見せてあげて", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4332, 106801, 101, 4, "少年～！\nもうヘトヘトだよ～！\nお願い癒して～！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4333, 106801, 102, 8, "…アタシが素直に\n助けてって言えるのは\n少年だけだよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4334, 106801, 1, 1, "朝だよ少年！\n爽やかな朝の\nクレープはいかが？", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4335, 106801, 2, 1, "お腹空いたな～\nお昼のクレープ\n少年は食べる？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4336, 106801, 3, 1, "夜までクレープは\nやめとけ？　ごめん\nそれだけは無理…！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4338, 106801, 2, 1, "アタシこんなに\nゆっくりしちゃって\nいいんだっけ～？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4339, 106801, 3, 1, "…これ\nネネカが変身\nしてないよね？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4340, 106801, 4, 1, "いい香りだ…\n解析完了\n今度生成しようっと♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4341, 106801, 5, 1, "安心するな～…\nアジトに置けないから\n羨ましいよ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4343, 106801, 7, 1, "食べ物を消化するって\n仕組み、どんなものか\n気になる…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4344, 106801, 8, 1, "速読できるけど\n本を読む時くらい\n文字にじっくり\n浸かりたいんだよね…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4346, 113701, 1, 1, "エンジェルとして\nみんなを元気に…！\nアカリ、頑張ります♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4347, 113701, 2, 1, "お姉ちゃんと一緒に\nお兄ちゃんを…えへへ\nまだかな、お兄ちゃん", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4348, 113701, 3, 1, "エンジェルの音色を\n目指して、今日も\nサックスの練習です♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4349, 113701, 4, 1, "お兄ちゃんとの特訓も\n頑張らないと…\n厳しくしてくださいね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4350, 113701, 5, 1, "かわいいエンジェルの\n格好、お兄ちゃんなら\n喜んでくれますよね♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4351, 113701, 101, 4, "エンジェルの力で\nお兄ちゃんのハートも\nイチコロですよ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4352, 113701, 102, 8, "アカリが一番\n元気にしたいのは\nお兄ちゃんですよ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4353, 113701, 1, 1, "お兄ちゃん？\n触りたいなら…\nこことかどうですか♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4354, 113701, 2, 1, "特訓の時間ですか？\nアカリ、楽しみに\nしてたんですよ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4355, 113701, 3, 1, "あ、お兄ちゃん！\nそろそろアカリと\n休憩…しませんか♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4356, 113701, 4, 1, "この羽、触り心地\nいいですか？　あんっ\nくすぐったいですよぉ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4357, 113701, 101, 4, "お兄ちゃんも元気に\nなりたいんですか？\nアカリにお任せです♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4358, 113701, 102, 8, "やんっ♪\nお兄ちゃんったら\nダイタンですね♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4359, 113701, 1, 1, "お兄ちゃん、朝ですよ\nすっごく気持ちのいい\nお天気ですよ…♪", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4360, 113701, 2, 1, "お昼寝しませんか\nお兄ちゃん？\nアカリの膝枕で…♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4361, 113701, 3, 1, "夜ですね、お兄ちゃん\nアカリ、なんだか胸が\nドキドキしちゃいます", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4363, 113701, 2, 1, "フカフカですね…\n隣にお兄ちゃんが\nいてくれたらなぁ…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4364, 113701, 3, 1, "えへへ、アカリも\nこんな風にかわいく\nなりたいな～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4365, 113701, 4, 1, "お兄ちゃんが好きな\n匂いなのかな？\n参考にしますね！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4366, 113701, 5, 1, "体が火照ってきて\nなんだかアカリ…\nクラクラしちゃう…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4368, 113701, 7, 1, "はむ、んぐ…あっ\nほっぺについちゃった\nベトベトですよぉ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4369, 113701, 8, 1, "エンジェルって色んな\n物語に出てきますよね\nもっと調べなくちゃ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4371, 103201, 103, 12, "あの方の前では\n常に気高く\nありたいですわね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4372, 103201, 103, 12, "どんなに道に迷っても\nあなたさまの元には\n絶対に辿り着く\n自信がありますわ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4373, 102801, 103, 12, "あんたと一緒に\nいるときが一番楽しい\n一時かもしれないわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4374, 102801, 103, 12, "いつかあんたと一緒に\nサレンディア救護院を\n切り盛り…なんてね♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4375, 113801, 1, 1, "今日も\nエンジェルとして、\nみんなの幸せの\nために頑張らないと", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4376, 113801, 2, 1, "せっかく羽が\nついてるのに\n飛べないのが残念だわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4377, 113801, 3, 1, "この格好、子供たちに\n妙にウケがいいわね\nま、悪くないけど", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4378, 113801, 4, 1, "や、やっぱりみんな\n注目してくるわね…\nエンジェルって珍しい\nからしかたないか", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4379, 113801, 5, 1, "エンジェルになると\n人見知りがなおる！\n…わけないか", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4380, 113801, 101, 4, "エンジェルの活動は\n大変だけど、自分が\n成長してるって\n実感がうれしいわね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4381, 113801, 102, 8, "もし誰かの願いを\nかなえられるなら\n私はアイツの…\nって何考えてるのよ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4382, 113801, 1, 1, "何ボサっとしてるの！\nさあ、街に出て\n人助けするわよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4383, 113801, 2, 1, "エンジェリック\nアロー！\nなんてね…え、アンタ\nいつから見てたのよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4384, 113801, 3, 1, "コラ！\n羽は触らないで！\n壊れたらどうするのよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4385, 113801, 4, 1, "エンジェルじゃなく\nても、アンタはいつも\n人助けしてるわよね\n…ちょっと尊敬するわ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4386, 113801, 101, 4, "この服なら他の子より\n私を見てくれるかも…\nって、な、何\n見てるのよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4387, 113801, 102, 8, "ね、ねえ、もし私が\n困ってたら、一番に\n駆けつけて…やっぱり\n何でもないわ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4388, 113801, 1, 1, "おはよう…あ、あの、\nエンジェルとして\n町に行くんだけど…\n一緒に行かない？", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4389, 113801, 2, 1, "ふぅ、いいことした\n後のランチは\nおいしいわね", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4390, 113801, 3, 1, "もうこんな時間…\n夜更かししてないで\n早く寝なさいよね", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4392, 113801, 2, 1, "いい座り心地ね～\nまさに天にも昇る\n心地よさだわ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4393, 113801, 3, 1, "ふふ、かわいい…\nあ、羽付けたら\nもっとかわいく\nなるんじゃないかな？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4394, 113801, 4, 1, "はぁ～落ち着くわ～\n回復アイテムみたいで\n癒やされちゃうわ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4395, 113801, 5, 1, "わっ、とと…\n危ない、羽が\nこげちゃうところ\nだったわ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4397, 113801, 7, 1, "人の役に立つのって\nすごく大変だから、\nこれでライフ回復よ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4398, 113801, 8, 1, "エンジェルに関する\n本はあるかしら…あ！\nこれ探してた攻略本！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4400, 113901, 1, 1, "ふっふっふ\n吸血鬼らしく\n驚かせてあげましょう", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4401, 113901, 2, 1, "獲物はどこですか～！\nう～ん、もうちょっと\n迫力が欲しいですね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4402, 113901, 3, 1, "トリックオアトリート\n…返事がないので\nイタズラしておきます", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4403, 113901, 4, 1, "ハロウィンですし\nたまには騎士さんでも\n誘ってあげますかね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4404, 113901, 5, 1, "マントは動きに\nメリハリがついて\nいいですね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4405, 113901, 101, 4, "色んな仮装が見られて\nやっぱりハロウィンは\n最高ですね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4406, 113901, 102, 8, "吸血鬼らしい行動って\n噛みついたり…いや\nそれは大胆過ぎです！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4407, 113901, 1, 1, "呼びましたか？\n手短にお願いしますよ\n忙しいんですから！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4408, 113901, 2, 1, "え？　衣装に穴が？\nありがとうございます\n…よく見てるんですね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4409, 113901, 3, 1, "ふわあ…吸血鬼の\n姿でも、眠い時は\n眠いんですよね…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4410, 113901, 4, 1, "ハロウィンってライブ\nとは違った賑やかさが\nあってワクワクします", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4411, 113901, 101, 4, "私を驚かそうとしても\n無駄ですよ！\nすぐ分かるんですから", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4412, 113901, 102, 8, "騎士さん、一緒に\nハロウィンを…いいえ\nなんでもないですっ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4413, 113901, 1, 1, "仕事で寝不足だから\n太陽がまぶしいです…\n私、吸血鬼っぽいかも", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4414, 113901, 2, 1, "お昼ご飯は真っ赤な…\nトマト尽くしのお弁当\nにしてみました", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4415, 113901, 3, 1, "夜はハロウィンが\n盛り上がる時間…\n行きますよ騎士さん！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4417, 113901, 2, 1, "このフィット感\nどうにか服作りにも\n活かせれば…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4418, 113901, 3, 1, "よく出来てますね…！\nぬいぐるみの服を作る\nのも楽しそうですね！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4419, 113901, 4, 1, "いい感じですね…\n服に合わせた香りって\nいうのも、いいかも…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4420, 113901, 5, 1, "はぁ、温まりますね…\nおっと、マントに火が\n点いちゃいそうです！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4422, 113901, 7, 1, "あぁ、美味しい～\n食べた分は、働いて\n燃焼しますからね！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4423, 113901, 8, 1, "伝説の吸血鬼…\n夜を統べる者…\nほうほう…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4425, 113901, 12, 1, "やっぱりハロウィンと\nいえばお菓子です！\nさぁ配りましょうか！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4427, 114101, 1, 1, "この格好…\nカッコイイのに\n動きやすくて\n着心地も最高ッス！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4428, 114101, 2, 1, "キメポーズはこう…\nいやこっちのほうが…\nなやましいッス…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4429, 114101, 3, 1, "トリックオア\nジャスティス！\nって言ったら何か\nもらえるッスかね？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4430, 114101, 4, 1, "新しい必殺技の\n特訓もしなくちゃ\nいけないッスね…\n忙しいッス！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4431, 114101, 5, 1, "自分のこの姿\nトモね～ちゃんにも\nみせてあげるッス！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4432, 114101, 101, 4, "ホワイトタイガー\n参上ッス！\n今のポーズは\nキマったッス！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4433, 114101, 102, 8, "あの人と一緒だと\nほんとにヒーローに\nなれる気がするッス！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4434, 114101, 1, 1, "どうッスか？\nこの色、自分に\n似合ってるッスかね？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4435, 114101, 2, 1, "ヒマだったら\n必殺技の特訓に\n付き合ってもらって\nいいッスか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4436, 114101, 3, 1, "ちょうどいいところに\n来たッスね！\nこのキメポーズ\nどうッスか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4437, 114101, 4, 1, "お！　この格好、\n気になるッスか？\nじっくり見ても\nいいッスよ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4438, 114101, 101, 4, "あんたも一緒に\n新しい技の練習\nどうッスか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4439, 114101, 102, 8, "今日もあんたと一緒に\nランドソルの平和を\n守るッスよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4440, 114101, 1, 1, "朝ッス！\n一緒に早朝特訓\nやるッスよ！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4441, 114101, 2, 1, "お昼休憩ッス！\nヒーローにも\n休息は必要ッス！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4442, 114101, 3, 1, "闇夜に映える\n白い衣装で\n平和を守るッスよ！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4444, 114101, 2, 1, "トラも狩りの時以外は\nごろごろしてるって\n本で読んだッスよー", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4445, 114101, 3, 1, "もこもこッスね♪\nこのかわいらしさを\n守ってあげるのも\nヒーローの役目ッス！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4446, 114101, 4, 1, "いい匂いッスね…\nリラックスして\n瞑想するッス！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4447, 114101, 5, 1, "燃える炎は\nヒーローの心意気ッス\nいつまでも\n見てられるッスよ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4449, 114101, 7, 1, "もぐもぐ…\nいっぱい食べて\n強いヒーローに\nなるッスよ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4450, 114101, 8, 1, "ちゃんと勉強の本も\n読んでるッスよ…！\nヒーローには賢さも\n必要らしいッス！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4452, 114101, 12, 1, "わわっ、お菓子が\nいっぱいッス！\nみんなで\n食べるッスよ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4454, 103001, 103, 12, "ワタシがチューシンと\nなった今、天下統一は\n目の前デス！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4455, 103001, 103, 12, "もうショーグンってば\nそれはセイシツに\nすることデ～ス…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4456, 114001, 1, 1, "女幽霊…うらめしや…\nうーん、これは\nちょっと違う気が\nするな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4457, 114001, 2, 1, "何だか寒気がする…\nま、まさか\nこの部屋にも幽霊が？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4458, 114001, 3, 1, "ハロウィンは楽しいね\n童心に帰ったような\n気分だよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4459, 114001, 4, 1, "今度のハロウィンは\nユイとヒヨリを\n誘ってみようかな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4460, 114001, 5, 1, "ハロウィンのお菓子は\nどれもかわいいね\n…一つくらいなら\nいいかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4461, 114001, 101, 4, "次は彼と\nどんなハロウィンを\n過ごそうかな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4462, 114001, 102, 8, "こんな賑やかさも\nいいものだね\n彼といられるのなら\nなおさら…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4463, 114001, 1, 1, "…いたずらしようと\nしてない、キミ？\nふふ、お見通しだよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4464, 114001, 2, 1, "え、私にもお菓子を？\nありがとう…ふふ\nうれしいものだね\nハッピーハロウィン！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4465, 114001, 3, 1, "どうしたの？\n出かけるなら\n私も同行しようか", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4466, 114001, 4, 1, "この格好、舞踏会にも\n出られそうだね\n一曲、踊ってみる？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4467, 114001, 101, 4, "やるからには本気で\nやらせてもらおう\nトリックオア\nトリート！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4468, 114001, 102, 8, "他の子に目移りする\nのも分かるが、その…\nせっかくの特別な衣装\n見てほしいな", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4469, 114001, 1, 1, "寝起きの体を\n慣らしておこうか\nほら、剣を取って", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4470, 114001, 2, 1, "お菓子もいいけど\nちゃんと昼食も\nとるんだよ", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4471, 114001, 3, 1, "さあ、行こうか！\nハロウィンは\nこれからが本番だよ", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4473, 114001, 2, 1, "ハロウィンはまだ\nこれからだ\n一休みしようか", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4474, 114001, 3, 1, "キミたちも\n仮装に興味は\nあるかな？　…ふふ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4475, 114001, 4, 1, "いい香りだけど\n幽霊が集まったり\nしないだろうか…？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4476, 114001, 5, 1, "暖かい…一息ついたら\nハロウィンの\nパレードを\n見に行こうかな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4478, 114001, 7, 1, "ハロウィンを本気で\n楽しむなら\n食事もしっかり\nとっておかないとね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4479, 114001, 8, 1, "ハロウィンの\nホラー小説か…\nけ、結構\n怖いものだね…？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4481, 114001, 12, 1, "お菓子がいっぱいだ…\nこういうのって\n見ているだけで心が\n躍ってしまうね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4483, 114201, 1, 1, "こんなにかわいい\n武器で戦えるとは…\nすごいものだな\n魔法少女は", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4484, 114201, 2, 1, "決めポーズの訓練も\nしっかりやっておくか\n…誰も見ていないな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4485, 114201, 3, 1, "こういう格好は\nユキあたりがすごく\n似合いそうだな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4486, 114201, 4, 1, "子どもたちにサインを\n求められてしまった…\nこれもまた私の使命！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4487, 114201, 5, 1, "この格好は目立つな…\nいや、提督とは人々の\n視線を集めるものだ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4488, 114201, 101, 4, "あいつと作戦会議を\nしておくか。あれで\nなかなか頼れるからな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4489, 114201, 102, 8, "あいつには私の勇姿を\nちゃんと見ていて\nほしいものだな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4490, 114201, 1, 1, "一仕事終えたことだし\n一緒に甘いものでも\n食べに行くか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4491, 114201, 2, 1, "怪しげな気配がするな\n貴公も警戒を\n怠るなよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4492, 114201, 3, 1, "シオリに魔法少女の\n本を借りたんだ\n一緒に読まないか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4493, 114201, 4, 1, "巡回の時間まで\nお茶にでもするか\n貴公も付き合うがいい", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4494, 114201, 101, 4, "貴公がサポートして\nくれるのなら心強いな\n頼りにしているぞ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4495, 114201, 102, 8, "頭を撫でるな～！\n…まったく、貴公だけ\nだぞ、こんな風に私を\n撫でてくるのは…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4496, 114201, 1, 1, "起きろ貴公！\n魔法少女の朝は\n早いぞ！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4497, 114201, 2, 1, "ほら、食後の\nデザートだ\nて、適度な糖分は\n体に必要なんだ！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4498, 114201, 3, 1, "ふわぁ…魔法少女も…\n眠気、には…い、いや\n寝てないぞ！？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4500, 114201, 2, 1, "任務や訓練で\n疲れた体に\nこれは効くなぁ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4501, 114201, 3, 1, "うむ、かわいいな\nモーラのやつを隣に\n並べようか", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4502, 114201, 4, 1, "ああ、身も心も\n安らぐ…\nまるで魔法だな…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4503, 114201, 5, 1, "この温かさ…\n愛と希望に\n通じるものがある…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4505, 114201, 7, 1, "食事の大切さは\n魔法少女も軍人も\n変わりない\nしっかり食べるんだ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4506, 114201, 8, 1, "魔法少女の本を\n読んでるのだが\nわ、私もこんななのか", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4508, 103301, 103, 12, "オラとあんちゃんは\nボケもツッコミも\nあうんの呼吸だべ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4509, 103301, 103, 12, "あんちゃんと二人で\nネタ合わせ…初めての\n共同作業だべ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4510, 192501, 1, 1, "ぴっぷる！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4511, 192501, 2, 1, "ぴっぷるぴっぷる♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4512, 192501, 3, 1, "ぴっ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4513, 192501, 4, 1, "ぷっぷー", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4514, 192501, 5, 1, "ぴぃ？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4515, 192501, 1, 1, "ぴっぷるんるん♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4516, 192501, 2, 1, "ぷぅ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4517, 192501, 3, 1, "ぴぴっ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4518, 192501, 4, 1, "ぷぅー…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4519, 192501, 1, 1, "ちゅ！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4520, 192501, 2, 1, "ぷはー", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4521, 192501, 3, 1, "ぴぇー", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4531, 114301, 1, 1, "魔法少女になったら\n髪型も変わるんだね\n色々試してみようかな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4532, 114301, 2, 1, "…煌く剣技で、\n悪を断つ！\nうん、今日も\n完璧なポーズだね♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4533, 114301, 3, 1, "ミクマ流剣術で、\n魔法少女の技に使え\nそうなものは…よし、\n早速試してみよっと♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4534, 114301, 4, 1, "魔法少女になったから\nには、サインの練習も\nしないと…\nよし、頑張るぞ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4535, 114301, 5, 1, "魔法少女の姿はすごく\nかわいいけど…この\n姿はジュンさんたちに\n見せられないなぁ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4536, 114301, 101, 4, "あの人は魔法少女\nのこと好きなのかな？\n…好きだったら\nうれしいな♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4537, 114301, 102, 8, "…やっぱり、\nあの人がいないと\n特訓に身が入らない…\n早く帰ってこないかな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4538, 114301, 1, 1, "もし時間があるなら、\n私と街の見回りに\n行かない？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4539, 114301, 2, 1, "あ、いいところに！\n新しい魔法剣術を\n編み出したから、\n見てくれないかな？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4540, 114301, 3, 1, "魔法少女の姿が気に\nなるのは分かるけど…\nず、ずっと見られる\nのは照れるからやめて", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4541, 114301, 4, 1, "変身ポーズ中の動き、\n少し変えた方が\nいいかな…\nあなたはどう思う？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4542, 114301, 101, 4, "よければ、\n魔法少女の特訓に\n付き合ってくれない？\nその…二人きりでさ…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4543, 114301, 102, 8, "私…あなただけの\n魔法少女になっても…\nふふっ、な～んてね♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4544, 114301, 1, 1, "今日もいい朝だね\nさあ、一緒に\n街の見回りに行こうか", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4545, 114301, 2, 1, "もう昼か…なにか\n食べたいものある？\n簡単なのでいいなら、\n私が作ってあげるよ", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4546, 114301, 3, 1, "そろそろ休もうか\n…なんなら、添い寝\nしてあげようか？\nふふっ、冗談だよ♪", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4548, 114301, 2, 1, "これは…\n心身ともに安らぐね\nなんだか\n眠くなっちゃいそう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4549, 114301, 3, 1, "か、かわいい…これ、\n魔法で動かせたり\nしないかな？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4550, 114301, 4, 1, "いい香りだね\nなんだか頭が冴えて\nきたし、新しい技でも\n考えようかな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4551, 114301, 5, 1, "あったかいな…\nこの温もりは、\n魔法少女の魔法でも\n再現不可能だね…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4553, 114301, 7, 1, "こ、これは…\nおいしすぎて手が\n止まらないかも", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4554, 114301, 8, 1, "この本の\nキャラの動き、\n変身ポーズの\n参考にできるかも！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4556, 114401, 1, 1, "メリークリスマス！\nさあ、素敵な\nパーティーを始めると\nいたしましょう！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4557, 114401, 2, 1, "ふふっ、\nいくつになっても、\nやはりクリスマスは、\nワクワクしますわね♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4558, 114401, 3, 1, "自分で料理をしてみて\nシェフたちのすごさが\n改めて分かりましたわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4559, 114401, 4, 1, "何事も自分で\n体験してみると\n新しい発見が\nあるものですわね♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4560, 114401, 5, 1, "やはり、クリスマスは\n街が活発になり\nますわね…これは、\n絶好の商機ですわ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4561, 114401, 101, 4, "他のクリスマス料理も\n作れるようになったら\nあのお方は食べて\nくださるかしら…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4562, 114401, 102, 8, "あのお方に贈る\nプレゼント…\nこれはかなりの\n難題ですわね…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4563, 114401, 1, 1, "メリークリスマス！\nよろしければ、一緒に\nクリスマスの準備を\nいたしませんか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4564, 114401, 2, 1, "まあ、クリスマスの\n飾りつけを手伝って\nいただけるのですか？\nありがとうございます", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4565, 114401, 3, 1, "クリスマスに合わせて\nあつらえた自慢の衣装\nなのですが…その、\nいかがでしょうか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4566, 114401, 4, 1, "幼い頃はサンタさんが\nいらっしゃるのを\n待ちわびたものです\n今は…内緒ですわ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4567, 114401, 101, 4, "も、もしお時間が\nあるようでしたら、\nクリスマスの街を\n一緒に歩きませんか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4568, 114401, 102, 8, "来年のクリスマスも\nあのお方と一緒に\n迎えられたら…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4569, 114401, 1, 1, "時は金なり…\nせっかくの朝ですもの\n時間を有効活用\nいたしましょう！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4570, 114401, 2, 1, "お昼になりましたわね\nさあ、\nパーティーの準備を\n進めますわよ！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4571, 114401, 3, 1, "静かな夜ですわね…\nもうちょっとだけ、\nあなたさまとお話を\nしていたい気分です", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4573, 114401, 2, 1, "適度な休息は\n成功の秘訣ですわ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4574, 114401, 3, 1, "このかわいらしさ…\nいつまでも見て\nいられそうですわ♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4575, 114401, 4, 1, "人をもてなすには\nこうしたところにも\n気を配らないと\nいけませんわね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4576, 114401, 5, 1, "暖かい…\nこういう憩いの\nひと時こそ、大事に\nしたいものですわね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4578, 114401, 7, 1, "この奥深い味わい…\n何か隠し味など\n使われていまして？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4579, 114401, 8, 1, "クリスマス料理の\nレシピも、\nこれだけあると\n迷ってしまいますわね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4581, 114401, 14, 1, "この絵本、とっても\n面白いつくりですわね\nうちの商品に\nしたいぐらいですわ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4582, 114401, 15, 1, "これだけ大きな\n着ぐるみ…\n職人の技術が\n光りますわね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4583, 114601, 1, 1, "クリスマスといえば\nパーティーよね！\nいっぱい飲むわよ～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4584, 114601, 2, 1, "私を麦しゅわだけの\n女と思ったら\n大間違いなんだから♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4585, 114601, 3, 1, "クリスマスらしい\n飲み物も結構いいわね\n私も今度\n作ってみようかしら", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4586, 114601, 4, 1, "暖かい部屋で飲む\n麦しゅわも\nまた格別なのよね…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4587, 114601, 5, 1, "今日はオフの日だし\n朝から飲んでも\n怒られないわよね？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4588, 114601, 101, 4, "クリスマスの\nデートスポットで\n彼と一緒に…ってなに\n考えてるのユカリ！？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4589, 114601, 102, 8, "背伸びしなくても\nありのままの自分を\n受け入れてくれるって\nすごく…幸せ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4590, 114601, 1, 1, "大丈夫よ～\n飲んでない\n飲んでないひゃら～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4591, 114601, 2, 1, "クリスマスは\n特別だもの\n特別な一杯を\n飲まないとね♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4592, 114601, 3, 1, "メリークリスマース！\nほらほらぁ\n乾杯しましょ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4593, 114601, 4, 1, "お姉さんの\n頼れるところ\n見せてあげるわね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4594, 114601, 101, 4, "何かあったら\n頼ってくれても\nいいんだからね？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4595, 114601, 102, 8, "ふふ…これからも\nキミのこと…\n頼りにしちゃうからね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4596, 114601, 1, 1, "さわやかな朝ね\n…二日酔いがないと\n朝ってこんなに\n気持ちいいのね", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4597, 114601, 2, 1, "昼間から飲むのって\nまた格別よね…って\nガマン、ガマンよ…", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4598, 114601, 3, 1, "もうこんな時間かあ…\nあとは寝るだけだし\nちょっとくらいは…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4600, 114601, 2, 1, "ゴロゴロするのって\n最高ね～\nあとは麦しゅわが\nあればなぁ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4601, 114601, 3, 1, "私だって…\nかわいいものは\n好きでいいわよね？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4602, 114601, 4, 1, "んっ～…いい香りね\n日頃の疲れが\n取れる気がするわ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4603, 114601, 5, 1, "ポカポカする～\n…なんだかのどが\nかわいてきたわね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4605, 114601, 7, 1, "もぐもぐ…労働の\n後のご飯はやっぱり\nおいしいわぁ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4606, 114601, 8, 1, "クリスマスデート\n完全攻略マニュアル？\n…読んでおかなくちゃ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4608, 114601, 14, 1, "あら、絵が飛び出して\n見えるけど…\n酔っ払ってるせいじゃ\nないわよね…？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4609, 114601, 15, 1, "いい手触りね～\nウチにもほしいけど\n麦しゅわをこぼしたら\n大変そうだわ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4610, 101601, 103, 12, "ヒデサイの視線\nずっと独り占め\nしてたいな～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4611, 101601, 103, 12, "ヒデサイといるだけで\nうち、おに楽しいし！\nヒデサイもっしょ～？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4612, 114501, 1, 1, "メリークリスマス！\nふふ、口にするだけで\n何だか楽しくなるわね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4613, 114501, 2, 1, "いつもは子どもたちを\n見守る方だけど\n今ははしゃいでも\nいいわよね？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4614, 114501, 3, 1, "ツリーや料理\nそしてプレゼント！\nいくつになっても\nやっぱり心躍るわね♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4615, 114501, 4, 1, "飾り付けも料理も\nいいセンスしてるわね\n救護院のパーティーの\n参考にさせてもらうわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4616, 114501, 5, 1, "焼きそばパンを\nクリスマスの定番に\nするにはどうすれば…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4617, 114501, 101, 4, "これ、あいつの\n好きそうな料理だわ\n取っといて食べさせて\nあげようかしら♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4618, 114501, 102, 8, "あいつのプレゼント\nちょっとだけ奮発\nしたのよね…\n喜んでくれるかしら", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4619, 114501, 1, 1, "このドレス\n似合ってるかしら？\n今だけは見とれても\n許してあげるわ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4620, 114501, 2, 1, "見て、このケーキ！\nおいしそうでしょ\nたまにはこんな贅沢も\nいいわよね♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4621, 114501, 3, 1, "料理も飲み物も\nおいしいわね！\nいっぱい食べて\n元を取らないと♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4622, 114501, 4, 1, "子どもたちと一緒に\n雪遊び、楽しそうね\nあんたもどう？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4623, 114501, 101, 4, "せっかくの\nクリスマスなんだもの\nエスコート\nよろしくね♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4624, 114501, 102, 8, "寒い？　そう…\nなら、もっとこっち\n来ていいわよ…？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4625, 114501, 1, 1, "ほら、早く起きて？\nパーティー用の\n買い出しに\n付き合ってちょうだい", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4626, 114501, 2, 1, "パーティーの料理の\n準備をしながらお昼も\n作っておいたわ\n我ながらいい手際ね", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4627, 114501, 3, 1, "にぎやかなパーティー\nもいいけど、聖夜の\n余韻を感じるのも\n結構素敵ね", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4629, 114501, 2, 1, "いい座り心地だわ～\n腕のいい職人の仕事ね\nうちでも扱いたいわ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4630, 114501, 3, 1, "かわいい！\n子どもたちに\nプレゼントしたら\n喜んでくれるかも", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4631, 114501, 4, 1, "華やかな香りね\nクリスマスに\nピッタリだわ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4632, 114501, 5, 1, "寒い日にはやっぱり\nこれよね…\nはぁ、極楽極楽♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4634, 114501, 7, 1, "んー、おいしいわ！\n焼きそばパン味の\nものも、作れない\nかしら？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4635, 114501, 8, 1, "たまにはのんびり\n読書でも…なんだか\nラブロマンスばかりね\nクリスマスだから？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4637, 114501, 14, 1, "これ、面白いわね！\n子どもたちの\nプレゼントに\nピッタリだわ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4638, 114501, 15, 1, "こんないい毛並み\n初めて見るわ…\n一体どこで手に\n入るのかしら？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4639, 114701, 1, 1, "アタシもみんなと\n一緒に正月を\n楽しんでいいのか？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4640, 114701, 2, 1, "今年こそ、もう少し\n身長が伸びます\nように…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4641, 114701, 3, 1, "げっ！　お年玉が\n全然残ってないぞ！？\nそんな、嘘だろ…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4642, 114701, 4, 1, "大勢で過ごす\n正月か…初めてだけど\n悪くないもんだな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4643, 114701, 5, 1, "んー…ここじゃ\n天楼覇断剣の\n出番はなさそうだな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4644, 114701, 101, 4, "アイツらと作る\n戦い以外の思い出も\nもっと増やしたいな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4645, 114701, 102, 8, "こんなに賑やかな\n正月は初めてだ\n…温かくて、いいな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4646, 114701, 1, 1, "記憶がなくったって\n楽しい思い出は\n新しく作れるよな？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4647, 114701, 2, 1, "早くでかけるぞ！\nアタシはもっと\n正月を楽しみたい！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4648, 114701, 3, 1, "なあ、オマエはもう\nノウェムって名前\n覚えてくれたよな？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4649, 114701, 4, 1, "この服、アタシに\n似合ってるのか？\n…そっか。\nありがとな！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4650, 114701, 101, 4, "オマエらと見た朝日は\nきっと忘れないぞ！\n思い出ができたな！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4651, 114701, 102, 8, "いつかオクトーも\n一緒に、みんなで\n正月を過ごせたらな…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4652, 114701, 1, 1, "朝から元気だな！\nアタシもいっちょ\n見習ってみるか！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4653, 114701, 2, 1, "昼飯は何にする？\n正月はうまいものが\n多くて迷うよな！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4654, 114701, 3, 1, "いい夜だな！\nアタシと一緒に\n夜更かしを楽しむか？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4656, 114701, 2, 1, "正月らしく、たまには\nゆっくりするのも\nいいかもな…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4657, 114701, 3, 1, "なんだ？\nオマエも初詣に\n行きたいのか？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4658, 114701, 4, 1, "すーはー…\n神殿のニオイがするな\nいい事ありそうだ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4659, 114701, 5, 1, "コイツがあれば\nうまい魚が\n焼けそうだな…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4661, 114701, 7, 1, "ううー\n食べ過ぎて帯が\nきつくなってきたぞ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4662, 114701, 8, 1, "おみくじって\n書いてあることが\nむずかしいんだよな…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4664, 180101, 1, 1, "困ってる人は\nいないみたいだし…\nちょっとここで、\n特訓しようかな♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4665, 180101, 2, 1, "空が飛べるんだから\nもっとたくさんの人を\n助けにいけちゃう！\n…ここじゃ飛べないや", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4666, 180101, 3, 1, "炎の翼\nかっこいいなー！\nこれ、もっと大きく\nできないのかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4667, 180101, 4, 1, "やっぱり変身すると\nお腹すくんだね…\n…今ならペコさんの\n食欲の理由が分かるよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4668, 180101, 5, 1, "色なんかは違うけど…\nマントがあるから\n騎士くんとお揃いに\nなったみたい♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4669, 180101, 101, 4, "…騎士くんがいないと\nなんか調子でない…\n早く会いたいよぉ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4670, 180101, 102, 8, "この姿になると、\n騎士くんを近くに\n感じる…なんだろ\nドキドキしちゃう…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4671, 180101, 1, 1, "騎士くん騎士くん！\n今大丈夫？\nトレーニングメニュー\n考えるの手伝って！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4672, 180101, 2, 1, "…うにゃっ！？\nこ、この姿になっても\n尻尾は触っちゃ\nダメー！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4673, 180101, 3, 1, "もしかして、この服が\n気になってるの？\nだったら、もっと\n近くで見ていいよ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4674, 180101, 4, 1, "あ、騎士くん！\n困ってる人がいないか\n見回りに行くんだけど\nついてきて欲しいな！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4675, 180101, 101, 4, "耳に触りたいの？\nだったら頭もなでて！\nそしたら、好きなだけ\n触っていいよ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4676, 180101, 102, 8, "…あたし、強くなって\n騎士くんを守るよ！\nだから…これからも\n一緒にいていい？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4677, 180101, 1, 1, "おっはよー！\nさあさあ騎士くん！\n今日も一緒に\nはりきっていこー♪", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4678, 180101, 2, 1, "お昼、お昼～♪\n今日のご飯は何かな～\nあ、騎士くん！\n早くお昼食べ行こ！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4679, 180101, 3, 1, "今日もお疲れ様ー！\n騎士くん、あたしが\nマッサージしよっか？\n結構評判なんだよ！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4681, 180101, 2, 1, "うにゃぁ…\nこの気持ちよさ…\n溶けちゃいそう…\nにゃぁ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4682, 180101, 3, 1, "か、かわいい～\nこれ、なでなで\nしていいんだよね…？\nよしよし…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4683, 180101, 4, 1, "とってもいい香り！\n気分も落ち着くし、\n特訓の後なんかに\nいいかもー！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4684, 180101, 5, 1, "ぬくぬくだぁ…\nなんだか、\nふにゃふにゃに\nなっちゃうよぉ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4686, 180101, 7, 1, "お、おいひぃ…！\nこの姿になると\nお腹すくし、すっごく\n助かっちゃった！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4687, 180101, 8, 1, "この本で勉強して、\nぶんぶりょーどーの\n超ウルトラヒヨリに\nなっちゃうぞー！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4689, 111801, 1, 1, "今年もみんなで\nおいしいものを\nたくさん食べたいです", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4690, 111801, 2, 1, "挑戦は大切ですよね☆\nというわけで\nおせちに魔物を…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4691, 111801, 3, 1, "おせちは縁起物です！\n食べれば食べるほど\n幸せになるはずです！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4692, 111801, 4, 1, "食べ物がたくさん\n入った福袋は\nないんでしょうか…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4693, 111801, 5, 1, "初詣で何を\nお願いしましょう…\n五穀豊穣？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4694, 111801, 101, 4, "ずっとこうして\n年の始まりを\n過ごしたいですね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4695, 111801, 102, 8, "いつか二人きりで\n初日の出を\n見たいです", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4696, 111801, 1, 1, "お餅おかわりです！\nいくらでも\n食べられますよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4697, 111801, 2, 1, "おせちの食材を\n取りに行きませんか？\nいい運動です☆", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4698, 111801, 3, 1, "凧揚げって\n難しいですね～\n糸が絡まっちゃいます", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4699, 111801, 4, 1, "一年の計は元旦にあり\n今年一年分の\n献立を作りましょう！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4700, 111801, 101, 4, "ほらほら！\nもっと近くで着物を\n見ていいんですよ☆", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4701, 111801, 102, 8, "あなたはどんな\n初夢を見ましたか？\nわたしは…内緒です☆", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4702, 111801, 1, 1, "年明けの朝は\n心なしか\n気持ちいいですね☆", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4703, 111801, 2, 1, "お雑煮のあとは\nおしるこで\nおやつタイムです！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4704, 111801, 3, 1, "あたたかくして\n幸せな初夢を\n見ましょうね", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4706, 111801, 2, 1, "はぁ～屋台巡りを\n満喫しました☆\n一休み一休み", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4707, 111801, 3, 1, "縁起のいい\nぬいぐるみを並べて\n福を呼びましょう！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4708, 111801, 4, 1, "おしるこのアロマって\nないんでしょうか？\nそれなら作るしか…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4709, 111801, 5, 1, "ここでお餅を\n焼いたらおいしく\nなりそうですね～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4711, 111801, 7, 1, "おいしすぎて\n食べる手が\n止まりません～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4712, 111801, 8, 1, "新しいおせち料理の\nヒントは…なるほど…\nふむふむ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4714, 100601, 103, 12, "お兄ちゃんとあんな\n特訓やこんな特訓が\nしたいなぁ、えへへ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4715, 100601, 103, 12, "お兄ちゃんに褒めて\nもらうと、身体が\nじぃーんと\n熱くなっちゃいます♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4716, 115001, 1, 1, "見目麗しい衣装に\n身を包むのも\n悪い気分では\nありませんね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4717, 115001, 2, 1, "縁起物で\n験を担ぐというのも\nたまにはよいでしょう", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4718, 115001, 3, 1, "非効率なので\nあまり行いませんが\n私も手料理くらいは\n作れるのですよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4719, 115001, 4, 1, "研究のためなら\n時に遠路を超え\n実地へ赴くことも\n必要なのです", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4720, 115001, 5, 1, "トゥンヌスの\n突然変異…\n大いに興味を\nそそられますね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4721, 115001, 101, 4, "漁師の技法も\n再現できたことで\nまた一つ、仮説が\n実証できました", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4722, 115001, 102, 8, "正月くらいは\n私ものんびりしたい\n気持ちなのですよ\n意外ですか？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4723, 115001, 1, 1, "模倣に頼らずとも\n衣装に合わせた\n所作程度は\nできて当然です", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4724, 115001, 2, 1, "お年玉の使い道は\n思いつきましたか\nゆっくり考えても\nいいのですよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4725, 115001, 3, 1, "トランプでも花札でも\n相手をしましょう\n手心が必要なら\nいつでも言いなさい", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4726, 115001, 4, 1, "初詣でお祈りする\n内容ですか\n…ヒミツ、として\nおきましょう", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4727, 115001, 101, 4, "私たちでオーマ中の\nトゥンヌスを\n釣り上げましょうか\n…冗談ですよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4728, 115001, 102, 8, "見返りを求めない願い\nそれは祈りに通じるの\nかもしれませんね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4729, 115001, 1, 1, "…貴方がその眠たげな\n顔を洗ってくる間に、\n温かいお茶でも\n淹れておきましょう", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4730, 115001, 2, 1, "さて、本格的に\n活動を再開します\n貴方も同行しなさい\n昼食は済みましたか？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4731, 115001, 3, 1, "私はもう少し\n作業を片付けてから\n寝ることにします\n早く眠りなさい", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4733, 115001, 2, 1, "少し休みます…\n時間が来たら\n起こすように", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4734, 115001, 3, 1, "贈り物を模倣して\n潜入…試してみる\n価値はありそうですね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4735, 115001, 4, 1, "嗅覚から得られる\n情報量は侮れません\n動物の模倣において\n注意すべき点ですよ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4736, 115001, 5, 1, "穏やかな炎による\nリラックス効果は\n本能によるものかも\nしれませんね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4738, 115001, 7, 1, "いかなる強者でも\n兵站、補給は\n必要なものです", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4739, 115001, 8, 1, "あらゆる学問は\n完璧な模倣のための\n大きな礎となります", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4741, 115501, 1, 1, "新たに得たこの力で\n仲間と目指す未来を\n切り拓いてみせます", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4742, 115501, 2, 1, "ここは、みなさまと…\nわたくしの\n帰る場所でございます", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4743, 115501, 3, 1, "主さまも精霊の召喚を\n試してみますか？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4744, 115501, 4, 1, "主さま、おこづかいは\n足りていますか？\n遠慮は無用ですよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4745, 115501, 5, 1, "この装束をまとうと\n自然と背筋が伸びる\n思いがいたします", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4746, 115501, 101, 4, "進化した精霊に\n負けぬよう\n修行に励みます", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4747, 115501, 102, 8, "仲間たちといられる\nこの幸せが、末永く\n続きますように…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4748, 115501, 1, 1, "主さまのお手手は\n大きくて温かいですね\nぎゅっぎゅっ…ふふ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4749, 115501, 2, 1, "今度、主さまにも\n里に伝わる昔話を\nお話しいたしましょう", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4750, 115501, 3, 1, "里で採れた果物を\n持って参りました\n召し上がってください", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4751, 115501, 4, 1, "里のお土産を\nここのみなさまに\nお配りいたしましょう", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4752, 115501, 101, 4, "仲間と囲む食卓…この\n温もりは、みなさまの\n心の温かさなのですね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4753, 115501, 102, 8, "主さまに名前を\n呼んでいただくと\n心が温かくなります♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4754, 115501, 1, 1, "ふふっ…主さま\n寝癖がついて\nおりますよ？", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4755, 115501, 2, 1, "今日の昼食は\n里の伝統料理です\nお口に合うでしょうか", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4756, 115501, 3, 1, "主さまが眠れるよう\n安らぎの風を…\nおやすみなさいませ", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4758, 115501, 2, 1, "この癒し…\n主さまにも味わって\nいただきたい…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4759, 115501, 3, 1, "この愛らしさ…\n精霊たちと似たものを\n感じます…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4760, 115501, 4, 1, "精霊よ…この安らぐ\n香りを、隅々まで\n届けてくださいまし", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4761, 115501, 5, 1, "瞑想するのに\nちょうどよい暖かさで\nございます", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4763, 115501, 7, 1, "進化した精霊を\n使役するためにも\nよく食べて体力を…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4764, 115501, 8, 1, "風の精霊について\nもっと詳しく知って\nおかなければ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4766, 115601, 1, 1, "儀式の時を思い出して\n精霊に呼びかける…\n出ておいで～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4767, 115601, 2, 1, "ひゃうっ！　もう…\n急に背中を触るなんて\nいたずら好きな精霊に\n育てた覚えはないのに", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4768, 115601, 3, 1, "儀式用の衣装…\nあんまり激しい動きは\nできないよね…でも\n戦う時は気にしない！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4769, 115601, 4, 1, "そうだ…精霊に\n騎士クンのそばで\n見守っていてって\n頼んでみようかな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4770, 115601, 5, 1, "ふふっ、こうやって、\n楽しく精霊とお話し\nしていられる、平穏な\n日々が続くといいな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4771, 115601, 101, 4, "き、きき、騎士クン！\nわたしと、あの…！\nうう…練習ですら\n上手く誘えないよぉ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4772, 115601, 102, 8, "あっ、精霊が…\nふふ、応援ありがとう\nわたしの想い…いつか\n必ず伝えるね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4773, 115601, 1, 1, "新しく覚えた料理\n作ってみたんだけど\n騎士クンも食べない？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4774, 115601, 2, 1, "騎士クン、\n疲れてない？\nわたしと精霊で\n治療するよ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4775, 115601, 3, 1, "もう、いたずらしない\n…騎士クン！？\nご、ごめんなさい！\n精霊だと思って！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4776, 115601, 4, 1, "騎士クン！\n時間が合ったら、\n精霊と一緒に\nお話しない？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4777, 115601, 101, 4, "…こういう服着るの\n初めてなんだけど…\nえっ、かわいい…？\nあ、ありがとう…！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4778, 115601, 102, 8, "困ったことがあったら\n相談してね。わたし、\n騎士クンのためなら…\n色々できるよ、色々！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4779, 115601, 1, 1, "おはよう騎士クン！\n今からお外で\n瞑想するんだけど…\n騎士クンもどうかな？", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4780, 115601, 2, 1, "お昼の時間だ\nご飯ならわたしが\n用意するから\nゆっくりしてて", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4781, 115601, 3, 1, "もうこんな時間…\n騎士クン、はいどうぞ\n寝る前の温かいミルク\nぐっすり眠れるよ♪", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4783, 115601, 2, 1, "…うん、落ち着く\n精霊との魔法で\nこんな風に誰かを\n安心させたいな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4784, 115601, 3, 1, "かわいい…\n精霊も気に入ってる…\nそうだ、同じ物\n作ってあげようかな？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4785, 115601, 4, 1, "いい香り…これ、\nうちに置いたら、\nお母さん喜ぶかも！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4786, 115601, 5, 1, "あったかい…\nまるで誰かが\n抱きしめてくれた\nみたい…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4788, 115601, 7, 1, "自然の恵みに\n感謝して…\nいただきます♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4789, 115601, 8, 1, "もっと精霊について\n勉強して、みんなを\n守れるように\nなりたいな…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4791, 102201, 103, 12, "アイツといると胸が\nぎゅーっとなる…\nこれってもしかして…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4792, 102201, 103, 12, "アンタとならきっと\nどんなゲームでも…\nそ、その、たた、\n楽しいって思えるわ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4793, 106401, 1, 1, "色んな人と話したら\n記憶探しの手がかりが\n見つかるかも…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4794, 106401, 2, 1, "アイスクリームでも\n作ろうかしら？\nバニラにミントに…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4795, 106401, 3, 1, "ネビアに話しかけると\n逃げられるのよね…\nもう、襲わないのに…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4796, 106401, 4, 1, "「コッコロさん」、\n「コッコロさん」…\n…間違えないように…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4797, 106401, 5, 1, "最近少し太ったかも？\nきっとおいしいもの、\n食べ過ぎね…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4798, 106401, 101, 4, "ここにいると、\nとても落ち着くわ…\n自分の家、だものね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4799, 106401, 102, 8, "みんなと一緒の時間、\nずっと記憶に残したい\n…そう思っているのよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4800, 106401, 1, 1, "ドラゴン族って、\nそんなに珍しいの？\nあまり実感ないけど…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4801, 106401, 2, 1, "料理、練習してるのよ\n…味見してもらえる？\n期待はしないでね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4802, 106401, 3, 1, "頭を叩いたら、\n記憶が戻ったり\nしないかしら…？\nやらなくていいわよ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4803, 106401, 4, 1, "赤ちゃんみたいだった\n時のことは、忘れて…\nお願いだから…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4804, 106401, 101, 4, "ひゃんっ！？\nし、しっぽを掴むのは\nダメよ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4805, 106401, 102, 8, "記憶が戻っても…\n私は、あなたたちと\n一緒にいたいわ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4806, 106401, 1, 1, "おはよう。\n寝ぐせついてるわよ？\n…ふふっ、これでよし", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4807, 106401, 2, 1, "そろそろお昼かしら？\nおいしいごはんを、\nみんなで一緒に…ね", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4808, 106401, 3, 1, "もうこんな時間ね。\nそろそろ休みましょ？\n…ふわぁ…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4810, 106401, 2, 1, "ふかふか、ぎゅう～…\n…あっ、これはその…\nストレッチしててっ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4811, 106401, 3, 1, "あまり、興味ないわ。\nけど…けど…\n…か、かわいい…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4812, 106401, 4, 1, "いい香りね。\nとっても落ち着くわ…\n…っくしゅっ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4813, 106401, 5, 1, "冷やす方が得意だけど\nあったかいのも、\nキライじゃないのよ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4815, 106401, 7, 1, "別に、食いしん坊\nじゃないのよ？\nでも、もう一口だけ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4816, 106401, 8, 1, "ん…なるほどね。\n勉強になるわ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4818, 115701, 1, 1, "水着の探偵か…\nちょっと恥ずかしい\n気がしてきたよ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4819, 115701, 2, 1, "依頼があれば\n海でも山でも\n赴くさ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4820, 115701, 3, 1, "今のうちに\n助手くんの秘密を…\nわ…私はなにをっ！？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4821, 115701, 4, 1, "休暇も時には必要さ\n海でゆっくりと…\nむっ？　事件の気配！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4822, 115701, 5, 1, "探偵の秘密道具…\n水着だと隠す\n場所がない…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4823, 115701, 101, 4, "そろそろ助手くんが\n現れる時間かな？\nふふっ、楽しみだね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4824, 115701, 102, 8, "真実は私と助手くん\n二人だからこそ\n解き明かせるのさ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4825, 115701, 1, 1, "ひゃああっ！？\nわ、私の不意を突く\nとは…やるね助手くん", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4826, 115701, 2, 1, "さて、なにか気づいた\nことはあるかな？\n言ってごらん♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4827, 115701, 3, 1, "この感触…分かった！\n助手くんだね！\nふふっ、やっぱり♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4828, 115701, 4, 1, "水着…どうかな？\n探偵らしさは\n残してるつもりだけど", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4829, 115701, 101, 4, "ん？　依頼かな？\n助手くんの頼みなら\nなんでもお安い御用さ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4830, 115701, 102, 8, "助手くん、私と海に\n行かないかな\n…事件じゃないよ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4831, 115701, 1, 1, "珈琲でも淹れて\n目を覚ますとしよう\n一緒に飲むかい？", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4832, 115701, 2, 1, "昼食は済ませた？\nなにを食べたか\n当ててあげよう！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4833, 115701, 3, 1, "夜は謎が深まる時間…\n闇に覆い隠された真実\n必ず暴いてみせる！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4835, 115701, 2, 1, "さて、推理の時間だ\n…サボってないよ？\n考えてるんだからね？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4836, 115701, 3, 1, "これはもしや、中に\n秘密のメッセージが？\n…かわいいだけだね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4837, 115701, 4, 1, "ふむ、なんだか\n頭がスッキリするね\n…閃いた！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4838, 115701, 5, 1, "ポカポカするね…\n助手くん\n君もおいでよ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4840, 115701, 7, 1, "はむ、はむ…んぐっ\nた、食べてるところを\n見つめられると～…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4841, 115701, 8, 1, "秘密の依頼とか\nこういう場所に\n隠したりするよね…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4843, 115701, 10, 1, "これはいい乗り心地だ\n事件現場にこれで登場\n…は、やめておこう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4844, 115701, 11, 1, "不思議な感触だね…\nでも、いい発想が\n生まれてきそうだ…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4845, 180601, 1, 1, "これはあたしが\n前へ進むために\nあいつがくれた\n大切な力…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4846, 180601, 2, 1, "チェンジ・\nプリンセスフォーム！\n練習練習、っと", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4847, 180601, 3, 1, "もうあいつに助けて\nもらいっぱなしの\nあたしは…\n卒業しなきゃね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4848, 180601, 4, 1, "気ままに、お気楽に\nあいつと冒険する…\nそれって幸せよね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4849, 180601, 5, 1, "プリンセスフォーム…\nお姫様みたいね\nあたしがお姫様か…\nそっかぁ…ふふ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4850, 180601, 101, 4, "あいつと繋がって\n生まれたこの絆は\nあたしの宝物だわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4851, 180601, 102, 8, "あたしの命の使い方…\n全て受け止めてくれた\nあいつにだったら…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4852, 180601, 1, 1, "ふふ～ん♪\nプリンセスフォームの\n天才魔法使い\nキャル様に用かしら？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4853, 180601, 2, 1, "これからは\nあたしの力で\nなんでもパパっと\n解決してあげるわね！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4854, 180601, 3, 1, "魔力がみなぎってる…\nねえ、もしかして\n今のあたしって本気で\nすごいんじゃない？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4855, 180601, 4, 1, "変身の反動で\nお腹が空いても\n魔物料理だけは\nいやだからね！？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4856, 180601, 101, 4, "ねぇ、あんたになら\nあたしの心…\n全部預けていいかも", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4857, 180601, 102, 8, "あいつと一緒に\nいられたら、他には\n何も…って、あああ\nあんたいつの間に！？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4858, 180601, 1, 1, "早起きも悪くないわね\n読みかけの魔導書\n今のうちに\n読んじゃおうかしら", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4859, 180601, 2, 1, "ねぇ～お腹すいたー\nあんたなんか\n作りなさいよ\n魔物料理以外でね！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4860, 180601, 3, 1, "ふぁぁ…眠すぎて\n今なら変身しても\nお腹が減らない\n自信があるわ…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4862, 180601, 2, 1, "あー♪　最っ高！\n休んで魔力を高めて…\nあいつの役に\n立ってあげなきゃね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4863, 180601, 3, 1, "なにこれかわいい…！\nコレクションにしたく\nなっちゃうじゃない", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4864, 180601, 4, 1, "この香り…すごく\nリラックスできるわ…\nあ、いい感じの魔法\n思いついちゃったかも", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4865, 180601, 5, 1, "あったかい…\n落ち着くわ…\nあいつの手の\nぬくもりみたいね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4867, 180601, 7, 1, "プリンセスフォーム…\nこ、こんなに\nお腹が減るのね\n甘く見てたわ…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4868, 180601, 8, 1, "ま、魔物料理の本！？\n誰よこんな本\n買ったの！　って…\n一人しかいないか", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4870, 115801, 1, 1, "憧れの舞踏会…\n私、きちんと\n踊れるかしら", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4871, 115801, 2, 1, "アン・ドゥ・トロワ\nアン・ドゥ・トロワ\n練習はばっちりね♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4872, 115801, 3, 1, "こんなにすてきな\nドレスを着られて\n本当に夢みたいだわ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4873, 115801, 4, 1, "魔法はいつか解ける…\nでも、その時間は心に\n残り続けるわ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4874, 115801, 5, 1, "ガラスの靴は夢へ\n踏み出す魔法の道具…\n私、がんばっちゃう！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4875, 115801, 101, 4, "舞踏会であなたと\n一緒に踊れたら\nいいのにな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4876, 115801, 102, 8, "舞踏会に行く前に\n運命の王子様には\n出会ってるのよね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4877, 115801, 1, 1, "どうかしら？\n私、きちんとドレス\n着られてる？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4878, 115801, 2, 1, "あなたも一緒に\n踊ってみる？\nしゃなりしゃなり♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4879, 115801, 3, 1, "あなたにエスコート\nされてきれいなお城を\n歩いてみたいわ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4880, 115801, 4, 1, "どう？　レッスンの\n成果はきちんと\n出てるかしら？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4881, 115801, 101, 4, "あなたが隣にいれば\n私も立派なお姫様に\n見えるかしら♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4882, 115801, 102, 8, "私にとって\n一番の魔法はあなたが\nくれる勇気よ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4883, 115801, 1, 1, "さぁ！　今日も一日\n立派な淑女を目指して\nいきましょう♪", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4884, 115801, 2, 1, "お昼ごはん代わりに\nメタモルアップルを\n食べておきましょう", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4885, 115801, 3, 1, "そろそろ魔法が解ける\n時間…でも淑女の\n心得は忘れないわ♪", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4887, 115801, 2, 1, "淑女にも休息は\n必要よね…少しだけ\n休ませてもらおっと", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4888, 115801, 3, 1, "かわいいものに\n囲まれた部屋も\n女の子の夢よね…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4889, 115801, 4, 1, "すてきな香り…\nお花かしら？\n森で探してみようかな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4890, 115801, 5, 1, "暖かい…暖炉のそばで\nおとぎ話を聞くのも\nいいわよね♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4892, 115801, 7, 1, "レッスンのために\n栄養補給して\nおかないと！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4893, 115801, 8, 1, "教養を身に着ける\nことも大切よね！\nなになに…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4895, 115901, 1, 1, "や、やっぱスカートは\n落ち着かねえな…\nなんか、\nスースーするしよ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4896, 115901, 2, 1, "せっかくこんな格好\nしてるし、いつもとは\n違う剣術の訓練を\nしてみるかな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4897, 115901, 3, 1, "さて、他の連中が\n帰ってくる前に、\nなんか料理でも\n作っといてやるか", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4898, 115901, 4, 1, "こ、こんな服着てる\nわけだし、紅茶とか\n楽しんじゃっても\n変じゃないよな！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4899, 115901, 5, 1, "暇だし運動でも…\nあ、あぶねえ！\nスカートなの忘れてた\n…誰も見てねえよな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4900, 115901, 101, 4, "紅茶が切れてるな…\nよし、あいつと一緒に\n買い物にでも行くか♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4901, 115901, 102, 8, "…あいつがいねえと、\nつまんねえな…ったく\n飯用意してやるから、\n早く帰ってこいよな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4902, 115901, 1, 1, "あ、あんまり\nジロジロ見んなよ\n…結構、\n恥ずかしいんだからな", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4903, 115901, 2, 1, "ダンスの相手は\n無理だが、\n剣の訓練なら\nいつでも付き合うぜ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4904, 115901, 3, 1, "この服に\n触りたいのか？\nすごい作りだし、\n気持ちは分かるぜ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4905, 115901, 4, 1, "お、いいところに！\nクッキーを焼く\nところだったんだが、\nおまえも食うか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4906, 115901, 101, 4, "おまえにもこういう服\n似合うんじゃねえか？\n今度あたしが\n選んでやるよ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4907, 115901, 102, 8, "…おまえの前でなら、\nこういう格好を\nすんのも…\nな、なんでもねえよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4908, 115901, 1, 1, "朝の訓練を始めるか\n時間あるなら、\nおまえも付き合えよな", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4909, 115901, 2, 1, "もうこんな時間か…\n昼飯作るけど、\nなんか\n食いたいものあるか？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4910, 115901, 3, 1, "眠れないなら、\nホットミルク飲むか？\n体あったまって、\n眠くなると思うぜ", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4912, 115901, 2, 1, "ちょっと休憩して…\nおっと、服にしわが\nできないように\nしねえとな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4913, 115901, 3, 1, "だ、誰も\n見てねえよな？\n…よ～しよしよし、\nかわいいなおまえー♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4914, 115901, 4, 1, "すげぇいい香りだな\nうちの居酒屋にも…\nいや、合わねえか", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4915, 115901, 5, 1, "あったけぇ…\nちょっとだけなら、\n寝ちまっても\nいいよな…？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4917, 115901, 7, 1, "これうめえな！\nたぶん材料は…よし！\nあたしも作ってみるか", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4918, 115901, 8, 1, "この本は…\n誰でも分かる作法…か\n最低限このぐらいは\n勉強しとくか…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4920, 102001, 103, 12, "ミミがおとなに\nなっても\nおにいちゃんとは\nずーっといっしょ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4921, 102001, 103, 12, "ミミとくべつなだっこ\nしてほしいな…うん！\nおひめさまだっこ～♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4922, 100401, 103, 12, "次はにいちゃんに\nどんなイタズラしよう\n早く会いたいな～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4923, 100401, 103, 12, "ミソギの特別な\nイタズラ見てくれる？\nいいよねっ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4924, 103601, 103, 12, "大人になったら、\nあの人と何しようかな\n…楽しみだなぁ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4925, 103601, 103, 12, "頭を撫でないで\nください！　もう、\n少しだけ、ですからね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4926, 116001, 1, 1, "くるくる〜♪\nうちのドレス\nかわええなぁ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4927, 116001, 2, 1, "日差しは苦手やけど\nたまにはひなたの下で\nぽかぽかしたいわぁ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4928, 116001, 3, 1, "外はお花が咲いてる\n久しぶりにお花を\n活けたいなぁ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4929, 116001, 4, 1, "お花をたくさん集めて\nギルドハウスに飾れば\n喜んでくれはるかな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4930, 116001, 5, 1, "あたたかい日は\n紅茶を飲みながら\nゆっくりしたいなぁ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4931, 116001, 101, 4, "お稽古事を始めるなら\nうちに言ってな\nどんなことも教えたる", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4932, 116001, 102, 8, "うちにいろんな夢を\n見せてくれはって\nほんま、おおきにな♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4933, 116001, 1, 1, "王子はん\n一緒におとぎの国へ\nお散歩にいきまひょ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4934, 116001, 2, 1, "ふんふんふ〜ん♪\n今な、王子はんに\n花冠を作ってるんよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4935, 116001, 3, 1, "王子はんは\nお菓子好きどす？\nうち、作りますわ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4936, 116001, 4, 1, "うーん。ドレスの柄は\nお花とお星さま\nどっちにしまひょか", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4937, 116001, 101, 4, "王子はんとの\n思い出が増えるたび\nうちの宝物も増えます", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4938, 116001, 102, 8, "王子はんと一緒なら\nどんな場所でも\n夢の国になりますわ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4939, 116001, 1, 1, "夢の中におりたいけど\n王子はんに会いたくて\n起きてもうたわ♪", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4940, 116001, 2, 1, "王子はん一緒に\nアフタヌーンティー\nいかがやろか？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4941, 116001, 3, 1, "夜の舞踏会…\n夢が広がるわぁ\nもちろんお相手は…♪", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4943, 116001, 2, 1, "ふわふわや〜\nなんや心地よぉて\n眠ってしまいそうや…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4944, 116001, 3, 1, "かいらしいなぁ\n新しいお洋服でも\n作ってあげまひょか♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4945, 116001, 4, 1, "お花やろうか…\nリラックスできて\nええ香りやねぇ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4946, 116001, 5, 1, "こういう場所で\n絵本を読むと\n気分が出てええなぁ♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4948, 116001, 7, 1, "舞踏会を楽しむなら\nたくさん食べて\nたくさん踊りまひょ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4949, 116001, 8, 1, "うち、この本好きや\n王子さまと結ばれる\nお姫さまのお話なんよ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4951, 116301, 1, 1, "はぁ、宿題めんどい…\nこれは楽しくない…\nこんな青春いらない…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4952, 116301, 2, 1, "奨学金の道は遠いなぁ\nメルクリちゃんたちが\nお金くれればいいのに", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4953, 116301, 3, 1, "アオイちゃん元気かな\nチエルに会いたくて\n泣いてるんだろうな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4954, 116301, 4, 1, "暇だし鏡でも見るか…\nうわ、顔良っ！\nえ誰この超絶美少女！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4955, 116301, 5, 1, "ワンツーワンツー♪\nうーん…カルミナの\n振りコピ難しいにゃー", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4956, 116301, 101, 4, "先輩も運動してます？\nチエルが一緒に走って\nあげてもいいですよ？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4957, 116301, 102, 8, "来年のテレフェスも\n今から楽しみだなあ…\nまた一緒にお化け屋敷\n入ってくださいね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4958, 116301, 1, 1, "ちぇる～ん☆\nフェス仕様でも\nかわいいちえるん！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4959, 116301, 2, 1, "いま話しかけないで！\n来年の聖学祭ライブの\nイメトレ中なんです！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4960, 116301, 3, 1, "クロエ先輩のウェア姿\nめっちゃカッコよ！\n思わずおへそ触ったら\nぶたれましたけど", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4961, 116301, 4, 1, "ユニ先輩の体操着姿…\nあれはあれで実に良き\n一生見てられますよね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4962, 116301, 101, 4, "先輩って体操着持って\nないんですか？　ハ？\nチエルのを貸せと…？\nえっ…変態かな？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4963, 116301, 102, 8, "テレフェスでは先輩も\n地味に頑張ってました\nね。まあ格好よかった\nんじゃないですか…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4964, 116301, 1, 1, "おはちぇる～ん☆\nさあ今日も楽しいを\n探しに行きますよー！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4965, 116301, 2, 1, "先輩ビッグニュース！\n今からお昼ごはん\nおごらせてあげます！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4966, 116301, 3, 1, "こんばんちぇるるん☆\nさあ朝まで楽しいを\n探しに行きますよー！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4968, 116301, 2, 1, "はぁ…ふかふか～…\n運動の後は…眠たく…\nなり…ま…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4969, 116301, 3, 1, "ユニ先輩に似合いそ！\nいやクロエ先輩かな？\nチエルは当然すぎて…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4970, 116301, 4, 1, "やん、いい香り～！\nまるでチエルのために\n存在するみたい！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4971, 116301, 5, 1, "この火を使って\n聖火リレーしましょう\n先輩、一本抜いて！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4973, 116301, 7, 1, "あむあむ…ちゃんと\n食べないとユニ先輩\nみたいになっちゃう！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4974, 116301, 8, 1, "ふむふむ…ちゃんと\n読まないとクロエ先輩\nみたいになっちゃう！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4976, 102901, 103, 12, "今なら書ける気がする\n私の本当の\n想いを込めた\nラブソングを！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4977, 102901, 103, 12, "輝きを増した私を\nいっぱい見てね！\n…ちょ、ちょっと\n見つめすぎだよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4978, 116201, 1, 1, "テレフェスかぁー…\nあんまいい記憶ないな\nよし、忘れとこ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4979, 116201, 2, 1, "暇だし鏡でも見るか…\nふふ～ん…いいじゃん\nあ？　何見てんだコラ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4980, 116201, 3, 1, "ワンツー、ワンツー♪\nああクソ、難しいな…\nあ？　何見てんだコラ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4981, 116201, 4, 1, "アオイ元気してるかな\nまた絶叫したり白目\n剥いたりしてんのかな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4982, 116201, 5, 1, "ミフユねえさんが\nバイトリーダーしてる\n工場…うちも行くか…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4983, 116201, 101, 4, "うーん…今日の晩御飯\n何にすっかなァ…\nあんたも食べてく？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4984, 116201, 102, 8, "来年のテレフェスは\nどーなンだかねえ？\nま、一緒にまた弟の\n相手してやってよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4985, 116201, 1, 1, "体操着なんてフツーに\n着てもつまんないし？\nその辺センス出るよね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4986, 116201, 2, 1, "奨学金の道、遠くね？\nアキノ先輩と仲良く\nなった方が早いかも…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4987, 116201, 3, 1, "あんた体操着は持って\nないの。ハ？　うちの\n貸せって？　いいけど\n…いや、よくねえよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4988, 116201, 4, 1, "にゃんっ！\nや、だからさ…急に\n話しかけんなっての", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4989, 116201, 101, 4, "え、なに？　この服？\nハ？　似合ってる？\n…え、聞こえなかった\nちょ、もっかい言って", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4990, 116201, 102, 8, "あんたもテレフェスで\n一応は頑張ってたよね\nま格好よかったんじゃ\nないの。知んないけど", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4991, 116201, 1, 1, "ふぁ～あ…おはーす\n昨日も寝たの遅くて\nまだ目がショボだよ…", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4992, 116201, 2, 1, "なに。あんた昼御飯\nまだなの。じゃ、うち\n適当になんか作ろうか", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4993, 116201, 3, 1, "夜っていいよね。色々\n見えすぎない感じとか\n…この話したっけ？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4995, 116201, 2, 1, "あ、ヤバ…運動の後に\nこのフカついたのは…\n秒でスヤれる…すやぁ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4996, 116201, 3, 1, "へーこんなんチエルに\n似合いそうじゃん。\n…いやユニ先輩かな？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4997, 116201, 4, 1, "お、なんか良さげな\n香りじゃん。これは\n何の…いや分かんね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (4998, 116201, 5, 1, "熱ッ！　この野郎なに\n爆ぜてンだコラァ！\n濡らすぞ木、てめえ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5000, 116201, 7, 1, "何コレよく分かんない\nけど、今日モヤシしか\n食ってないから助かる", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5001, 116201, 8, 1, "今日、体操着だしね。\n読書には逆アドかな。\nうん帰ろ。いやあ残念", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5003, 116501, 1, 1, "ビッグボス・イノリ様\nふっふっふ…\nしびれる響きです！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5004, 116501, 2, 1, "いつかお兄さんを\nあたしの側近に\nしてやるですよ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5005, 116501, 3, 1, "ビッグボスになったら\nみんなからたくさん\nお菓子をもらうです！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5006, 116501, 4, 1, "ふふん！\nこの服はあたしにしか\n着こなせませんよ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5007, 116501, 5, 1, "ばーんと立って\nどーんと腕を組む！\nこれがボスのポーズ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5008, 116501, 101, 4, "イノリ様の側近に\n選ばれるなんて\n名誉なことですよ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5009, 116501, 102, 8, "未来の側近として\nあたしをしっかり\n甘やかすですよ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5010, 116501, 1, 1, "あたしがお兄さんを\n立派なギャングに\nしてあげますからね！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5011, 116501, 2, 1, "側近たるもの\nボスを甘やかなさいと\nだめですよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5012, 116501, 3, 1, "なに見てやがるです…\nくぅ、分かりました！\nお菓子は半分こです！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5013, 116501, 4, 1, "お兄さんも\nあたしがボスなら\nうれしいですよね！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5014, 116501, 101, 4, "お兄さんが側近なら\nあたしも安心して\n夜眠れるですよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5015, 116501, 102, 8, "ビッグボスになったら\nお兄さんにいい思い\nさせてあげますから！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5016, 116501, 1, 1, "ボスたるもの\n早起きは鉄則ですよ！\nふぁあ…", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5017, 116501, 2, 1, "お腹が空きました…\nご飯食べにいきますよ", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5018, 116501, 3, 1, "ボスたるものぉ〜…\n夜更かしぐらい当然…\nぐー…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5020, 116501, 2, 1, "は〜これは人をだめに\nしやがるやつですね\nあたし専用です", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5021, 116501, 3, 1, "これはけしからん\nかわいらしさですね\nあたしにぴったりです", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5022, 116501, 4, 1, "高級な香りがするです\nボスになったら\n部屋に置いてやるです", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5023, 116501, 5, 1, "あったかいですねぇ\nちょっとこのまま\n一休憩です…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5025, 116501, 7, 1, "はむはむ♪\nボスたるもの食べねば\n戦はできませんよ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5026, 116501, 8, 1, "ボスなら経済書ぐらい\n…う。難しくて\n読めねーです…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5028, 102501, 103, 12, "あなたはドジな私を\n受け入れてくれる…\nだから、頑張れます！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5029, 102501, 103, 12, "何でも仰ってください\nちょっぴり大胆な\nことでも…な～んて♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5030, 116601, 1, 1, "あーあー、よし\nのどの調子もいいし\nはりきってガイド\nしてくるか！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5031, 116601, 2, 1, "さあ、そろそろ行くか\nガイドが\n遅刻したんじゃ\n話になんないからな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5032, 116601, 3, 1, "しれっとドラゴン族が\n旅行に来たり\nしてくれないかな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5033, 116601, 4, 1, "たぎるガイド先…\n火山、魔物の巣…\nダメだ、そんなとこ\nオレしか行けないや", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5034, 116601, 5, 1, "この前行った絶景\nスポットよかったな\nキレイな景色を見る\nのもいいもんだぜ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5035, 116601, 101, 4, "お、今日はカレと\n一緒か。なんだか\nやる気出てきたぜ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5036, 116601, 102, 8, "カレにガイドして\nもらったら\nどこに行っても\n楽しいだろうな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5037, 116601, 1, 1, "よっしゃあ！\nどっちが上手く\nガイドできるか\n勝負しようぜ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5038, 116601, 2, 1, "飯でも食いがてら\nおススメの店の\n下調べでもしようぜ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5039, 116601, 3, 1, "なかなかいいだろ？\nこの格好。キミも\n作ってもらったら\nどうだ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5040, 116601, 4, 1, "普通は行けない\n場所とか行きたいよな\nキミならどんな\nとこがいい？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5041, 116601, 101, 4, "ガイドの仕事は\nキミの方が得意そうだ\n参考にさせてもらうぜ\nよろしくな！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5042, 116601, 102, 8, "キミの探してきた店\n結構よさそうじゃん\nそうだ、今度二人で\n下見に行こうぜ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5043, 116601, 1, 1, "いい朝だな。絶好の\n仕事日和だぜ！\n今日もよろしくな！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5044, 116601, 2, 1, "よし、昼飯にしよう\nこの後も激務だし\n飯抜きじゃあ\nぶっ倒れちまうからな", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5045, 116601, 3, 1, "お疲れさん！　さーて\n次はオレと遊んで\nくれよ。まだまだ\nいけるだろ？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5047, 116601, 2, 1, "ガイドは一日\n歩きっぱなしだからな\nゆっくり休ませて\nもらうぜ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5048, 116601, 3, 1, "これ、どこで\n売ってるんだろ？\n土産の情報も\n仕入れとかなきゃな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5049, 116601, 4, 1, "アロマとかよく\n分かんねえけど\n土産にはいいかもな\n覚えておくぜ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5050, 116601, 5, 1, "体が温まると心も\n熱くなるんだよな\nいい仕事が\nできそうだぜ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5052, 116601, 7, 1, "食べ放題か\nこれはお客さんも\n喜んでくれそうだな！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5053, 116601, 8, 1, "へえ、こんなスポット\nあったんだ。やっぱ\nたまには勉強も\nしとくもんだな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5055, 116701, 1, 1, "バイト先になじめる\nように笑顔の練習を！\nに、にへぇ〜…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5056, 116701, 2, 1, "働いて働いて働いて\n…いつかその先に\nぼっちの出口が\n見えるのかも…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5057, 116701, 3, 1, "バイトのイメトレを\n始めます。ポーズを\n決めて…よし！\n角度を変えて…よし！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5058, 116701, 4, 1, "バイト仲間に入る\nためには厳しい修業が\n必須と聞きました…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5059, 116701, 5, 1, "どんどん技術を磨いて\nバイト仲間として\n認めてもらうんです！\n目指せ職人技…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5060, 116701, 101, 4, "あの人が応援して\nくれる…だから必ず\nバイト仲間の輪に\n入ってみせます…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5061, 116701, 102, 8, "あの人がいてくれると\n勇気が湧いてきます…\nぼっち卒業を夢見ても\nいいんだ、って…♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5062, 116701, 1, 1, "緊張するけど\n思い切って声をかけて\nみます…バイト仲間に\n入るために…！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5063, 116701, 2, 1, "ぼっちの私が\nおもちゃ作りで\n人と共同作業…！\nか、感動です…！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5064, 116701, 3, 1, "好きなおもちゃって\nありますか？　私は\nもちろんマイフレンド\nくんです！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5065, 116701, 4, 1, "私が作ったおもちゃも\n誰かの友達に\nなってたら\nいいなぁ…♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5066, 116701, 101, 4, "今日も明日もバイト\nですね！　バイト\n仲間ができる日が\n楽しみです…♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5067, 116701, 102, 8, "脱ぼっちを目指して\nこれからも一緒に\n頑張りましょう！\n目指せ、バイト仲間！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5068, 116701, 1, 1, "おはようございます\n今日も気合いを入れて\nおもちゃ作りします！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5069, 116701, 2, 1, "ランチの時間です…♪\nもちろん工場の隅で\nぼっち飯です…！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5070, 116701, 3, 1, "ふわぁ…今日も\nお疲れさまでした\n明日も働きます～！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5072, 116701, 2, 1, "バイトで頑張った日は\nふわふわの誘惑に\n勝てません…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5073, 116701, 3, 1, "こういうおもちゃの\nアイデアも\n素敵かも…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5074, 116701, 4, 1, "いい香りがする\nおもちゃはどうかな…\n森の薬草を使って…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5075, 116701, 5, 1, "ぽかぽか…\n幸せなひとときですね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5077, 116701, 7, 1, "もぐもぐ…たくさん\n食べて厳しいバイトを\n乗り切ります…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5078, 116701, 8, 1, "ふむふむ…おもちゃの\n設計図を見るのも\n勉強になりますね！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5080, 116801, 1, 1, "う～ん\n次はどんなたい焼きを\n作ろうかにゃぁ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5081, 116801, 2, 1, "あたしが売る\nたい焼きには\n夢が詰まってるにゃ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5082, 116801, 3, 1, "いつか絶対に\nたい焼き工場を\n建て直してみせるにゃ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5083, 116801, 4, 1, "たまにたい焼き以外の\nおやつも食べるにゃ\nたとえば煮干しとか！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5084, 116801, 5, 1, "悪い商売人がいたら\nファントムキャッツに\n知らせるにゃ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5085, 116801, 101, 4, "キミと一緒にいると\nたい焼きのアイデアが\n次々出てくるにゃ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5086, 116801, 102, 8, "出来ないことも\nキミと一緒にいれば\n出来る気がするにゃ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5087, 116801, 1, 1, "なんにゃ～？\nお姉さんにかまって\nほしいのかにゃ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5088, 116801, 2, 1, "キミ、いいところに！\nあたしとライバル店へ\n視察にいくにゃ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5089, 116801, 3, 1, "くんくん…この匂い！\nたい焼き持ってるなら\nちょうだいにゃ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5090, 116801, 4, 1, "次はどんなたい焼きを\n売ろうかにゃ～\nキミの案も欲しいにゃ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5091, 116801, 101, 4, "ファントムキャッツに\n相棒が必要にゃ\nたとえばキミとか…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5092, 116801, 102, 8, "相棒！\n…ってキミのこと\n呼んでいいかにゃ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5093, 116801, 1, 1, "早起きは三文の徳にゃ\n今日もたい焼き\n売れますよーににゃ！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5094, 116801, 2, 1, "お腹空いたにゃ…\n賄いのたい焼きを\nいただくにゃ！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5095, 116801, 3, 1, "猫は夜が本番にゃ\nでもあたしは…\nふわぁ～眠いにゃ…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5097, 116801, 2, 1, "ふかふかにゃ～\nここで体を丸めて\n一眠りしたいにゃ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5098, 116801, 3, 1, "これは…！\n猫にも負けない\nかわいさだにゃ…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5099, 116801, 4, 1, "いい香りにゃ～\nゴロゴロのどが\n鳴りそうだにゃ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5100, 116801, 5, 1, "ふにゃ～…\nこのまま働かずに\nまるまってたいにゃ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5102, 116801, 7, 1, "商売は戦いにゃ\nたくさん食べて\nたくさん働くにゃ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5103, 116801, 8, 1, "ふむふむ。たい焼きの\nアレンジレシピ集…\nためになるにゃ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5105, 102101, 103, 12, "お芝居の前に\nお兄ちゃんの顔を\n見ると、とっても\n落ち着きますぅ…♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5106, 102101, 103, 12, "お兄ちゃんにもっと\n見てもらいたい…\nだから私…\n強くなりたいです…！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5107, 116901, 1, 1, "さらなる効率化で\nみんなといい仕事を\nしたいわね！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5108, 116901, 2, 1, "他のバイトとの\nスケジュールを\n調整しないと…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5109, 116901, 3, 1, "時給がアップしたわ！\n働きが認められると\nやる気が出るわね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5110, 116901, 4, 1, "効率的に動ければ\n業務を追加できる…\n燃えてきたわ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5111, 116901, 5, 1, "帳簿付けのコツ…\nユカリさんに教わって\nみようかしら", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5112, 116901, 101, 4, "バイトリーダーとして\nみんなをビシバシ\n鍛えていくわ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5113, 116901, 102, 8, "戦いでもバイトでも\nキミなら安心して\n背中を預けられるわね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5114, 116901, 1, 1, "どうしたの？\n仕事のことなら\nなんでも相談してね！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5115, 116901, 2, 1, "さすがは私の弟子ね\nこれなら安心して\n仕事を任せられるわ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5116, 116901, 3, 1, "みんなのお手本になる\n働きぶりを見せないと\nいけないわね！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5117, 116901, 4, 1, "キミのこと…もっと\n頼りにしちゃっても\nいいのかしら…？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5118, 116901, 101, 4, "ふふ、キミも一人前の\nバイト戦士として\n立派に成長したわね！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5119, 116901, 102, 8, "新しいおもちゃの\n企画書を作ってみたの\nちょっと見てくれる？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5120, 116901, 1, 1, "朝は一番効率がいい\n時間帯よ！\nバリバリ働くわよ！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5121, 116901, 2, 1, "さ、お昼に行きましょ\n今日の日替わり定食は\n何かしら…楽しみね♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5122, 116901, 3, 1, "もうこんな時間…\nちゃんと寝て明日に\n備えなくちゃね", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5124, 116901, 2, 1, "適度な労働と休息の\nサイクルが最大効率を\n生み出すのよ…ふぅ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5125, 116901, 3, 1, "はぁ～なんて素敵な\n抱き心地なのかしら…\n最高効率の癒やしね♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5126, 116901, 4, 1, "落ち着く香りね…\nなんだか集中力が\nアップした気がするわ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5127, 116901, 5, 1, "ふぅ…癒されるわね…\n仕事のオンオフの\n切り替えは重要よね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5129, 116901, 7, 1, "働くためには\nエネルギーをちゃんと\n摂っておかないと", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5130, 116901, 8, 1, "効率を上げるため\n生産管理の勉強も\nしておきたいわね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5132, 180301, 1, 1, "変身している間に\n色々と試しておこう\nまずは…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5133, 180301, 2, 1, "飛べるというのは\n便利だな。料理が\n暖かいうちに\n持ち帰れる", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5134, 180301, 3, 1, "プリンセスか…\n柄ではないけれど\n嫌な気はしないな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5135, 180301, 4, 1, "この力で\n何を成すべきなのか…\n剣に聞いてみよう", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5136, 180301, 5, 1, "変身したとて私は私\n一時的に強くなろうと\n元が弱くてはだめだ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5137, 180301, 101, 4, "変身していない時でも\n彼がそばにいると\n力が湧いてくる…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5138, 180301, 102, 8, "私の思い…\nいつか伝えてみせるよ\n…きっといつか！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5139, 180301, 1, 1, "何者かと戦うの？\nそれとも修行？\nどちらだとしても\n付き合うよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5140, 180301, 2, 1, "変身し続けたら\nどれだけ空腹に…\n私は試さないからね？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5141, 180301, 3, 1, "本物のプリンセスに\nなるのは御免だが\nナイトがいて\nくれるのは頼もしいよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5142, 180301, 4, 1, "いつかこの力を\n誰かを救うために…\n戦い以外に使える時が\nくることを願うよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5143, 180301, 101, 4, "キミに感じてほしいな\n私に流れてくる\nキミからの力を…\nその…私の、思いも…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5144, 180301, 102, 8, "思いを告げる…\nそれがこんなにも\n難しいなんて…\nでも、いつか必ず…！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5145, 180301, 1, 1, "おはよう！\nいい変身日和だ！\n…ふふっ、冗談だよ", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5146, 180301, 2, 1, "いつもより多く\n昼食を用意しよう\n変身が解けたら大変だ", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5147, 180301, 3, 1, "一日お疲れ様\n私もそろそろ変身を\n解いて休むとするよ", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5149, 180301, 2, 1, "変身してしまうと\n動かずにいても\n空腹になるのかな…？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5150, 180301, 3, 1, "私もキミたちくらい\n可愛げがある姿に\nなっているかな？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5151, 180301, 4, 1, "香りに対する感覚も\nいつもより鋭く\nなっているみたいだ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5152, 180301, 5, 1, "ありがたいな\n少し足元が\n冷えていたところだ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5154, 180301, 7, 1, "これは…！\n持ち運べるように\n改良できないかな？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5155, 180301, 8, 1, "祖父に貰った\n技法書を読んで\n初心に帰ろう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5157, 117001, 1, 1, "最強の惚れ薬を作って\nあのおかたの心を\n私の思いのままに…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5158, 117001, 2, 1, "夏といえば海にスイカ\n花火に水着…\nそして惚れ薬ですわ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5159, 117001, 3, 1, "あのおかたの心を\n手に入れるための\n準備を進めましょう\nこの夏こそ確実に…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5160, 117001, 4, 1, "あのおかたに近づく\n虫どもを、どうやって\n片付けようかしら…\nまったく忌々しいこと", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5161, 117001, 5, 1, "…クスクス\n薬の調合を考えるのは\n至福の時間ですわ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5162, 117001, 101, 4, "あのおかたの全てが\n早く私のものに\nなりますように…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5163, 117001, 102, 8, "かき氷も花火も海も…\nあなた様がいてこそ…\nそう、あなた様は\n私の全てなのですわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5164, 117001, 1, 1, "二人だけの夏を\n楽しみましょう…？\n…クスクス", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5165, 117001, 2, 1, "あら、あなた様\n私に秘密でどこに\n行こうとして\nいますの…？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5166, 117001, 3, 1, "海に行きませんか？\nあなた様と一緒に\n過ごすには\n最高の場所ですから…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5167, 117001, 4, 1, "ちょうどいいところに\nいらっしゃいました\nできたての薬を…\nさぁどうぞ…クスクス", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5168, 117001, 101, 4, "夏の海のきらめきも\nあなた様の存在が\nなければ色あせて\nしまいますわ…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5169, 117001, 102, 8, "私とあなた様は\n結ばれる運命に\nあるのです…\n逃がしませんわ…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5170, 117001, 1, 1, "起きてすぐに会いに\n来てくださるなんて\nようやく私の伴侶たる\n自覚が…クスクス", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5171, 117001, 2, 1, "お食事はまだですか？\n特製の薬草ランチを\n召し上がれ…クスクス", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5172, 117001, 3, 1, "私の夢を見られる薬を\n差し上げますわ…\nおやすみなさいませ\nあなた様…クスクス", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5174, 117001, 2, 1, "いい肌触りですわ…\n考え事にもぴったり\n眠くなるのが\n玉に瑕ですが…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5175, 117001, 3, 1, "こういったものに\n揮発性の薬を仕込む\nのも一つの手ですわね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5176, 117001, 4, 1, "この香り…開発中の\n薬に使えそうな気が\nしますわ…クスクス", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5177, 117001, 5, 1, "あのおかたの\nぬくもりを\n思い出しますわ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5179, 117001, 7, 1, "美味しい…この味なら\n薬を混ぜても違和感は\nなさそうですわ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5180, 117001, 8, 1, "この薬の調合法…\n今度試してみましょう\nもちろんあのおかたに\n飲んでいただいて…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5182, 117001, 10, 1, "二人乗りができると\nいいですわね…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5183, 117001, 11, 1, "ふかふかですわ…\nあのおかたの分も\n欲しくなります…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5184, 101701, 103, 12, "今日はキミと\nいつまでも歌って\n踊りたい気分さ～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5185, 101701, 103, 12, "あは～♪\nキミの隣は、故郷より\n落ち着くかも～…♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5186, 117101, 1, 1, "この水着…\n一番に弟くんに\n見せたいな♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5187, 117101, 2, 1, "弟くんをこっそり\n海に運んで…\nサプライズしちゃお♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5188, 117101, 3, 1, "いつもと違う髪型に\nしてみたけど…\n気に入ってくれるかな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5189, 117101, 4, 1, "海は楽しみだけど\n日焼けしないように\n気を付けなきゃね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5190, 117101, 5, 1, "まだかな、弟くん\n水着のお姉ちゃんが\nお出迎えだよっ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5191, 117101, 101, 4, "海が見えるおうちで\n弟くんと一緒に\n暮らしたいなぁ～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5192, 117101, 102, 8, "弟くんの水着…\nどんな感じかなぁ\n…あ、想像してたら\nドキドキして…えへへ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5193, 117101, 1, 1, "こーら、水着の\nお姉ちゃんは\nおさわり禁止♪\n…なんてね？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5194, 117101, 2, 1, "なぁに？\nふふっ、何回でも\n呼んでいいんだよ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5195, 117101, 3, 1, "海で迷子に\nならないように\n手を繋ごうね♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5196, 117101, 4, 1, "お姉ちゃんが\n海の楽しみ方を\n教えてあげるねっ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5197, 117101, 101, 4, "わっ、弟くん\nどこ触ってるの？\nも～驚いちゃったぞ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5198, 117101, 102, 8, "お姉ちゃん、海でも\nずっとキミを見てるよ\n…安心してね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5199, 117101, 1, 1, "まだ眠そうだね？\nほら、海が待ってるよ\n早く起きて起きて♪", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5200, 117101, 2, 1, "大丈夫、疲れてない？\n海で遊び疲れた時は\n素直に休憩しようね", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5201, 117101, 3, 1, "ほら、早く寝ないと\n明日もお姉ちゃんと\n遊べないぞ♪", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5203, 117101, 2, 1, "だらけたお姉ちゃんは\n弟くんには\n見せられないな～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5204, 117101, 3, 1, "ふふっ、かわいいね♪\n弟くんが持つと\nもっとかわいいよっ♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5205, 117101, 4, 1, "いい香りだね…\n一味違うお姉ちゃんに\nなれそう…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5206, 117101, 5, 1, "あったか～い♪\nほら、弟くんも\nおいでよ♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5208, 117101, 7, 1, "よく食べて、いつでも\n弟くんの呼び声に\n応える準備を…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5209, 117101, 8, 1, "弟くんの水着の写真\nこの辺りに隠して\nおこうかな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5211, 117101, 10, 1, "この子の背に乗って\nお姉ちゃんと弟くんの\n楽園へ…な～んて♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5212, 117101, 11, 1, "海を感じる感触だね\n弟くんとの楽しい夏を\n思い出しちゃうな…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5213, 117201, 1, 1, "この夏、誰よりも\nキラキラしてみせる！\nだって私は\nアイドルだから！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5214, 117201, 2, 1, "日傘にアームカバー\n帽子にサングラス…\n紫外線対策は完璧！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5215, 117201, 3, 1, "とっておきの\nサマーソングを\nみんなに届けるよ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5216, 117201, 4, 1, "なんにもしないのって\n苦手かも…\n彼、早く来ないかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5217, 117201, 5, 1, "ラムネって\n夏って感じするよね\n後で彼と飲もっと♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5218, 117201, 101, 4, "夏は恋の季節！\n燃えるような恋…\nいつかしたいな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5219, 117201, 102, 8, "今年の夏の目標は\n…彼との思い出を\nいっぱい作ること！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5220, 117201, 1, 1, "私の水着写真集\n見てくれたんだ！\nどう、かわいかった？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5221, 117201, 2, 1, "スイカ割りしたいの？\nいいね、楽しそう！\nみんなも誘おうよ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5222, 117201, 3, 1, "ねぇ、見て見て！\n水着の映える\nポーズ考えたんだ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5223, 117201, 4, 1, "ライブ中に\n花火を打ち上げたら\n絶対盛り上がるよね！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5224, 117201, 101, 4, "最近、忙しいけど…\nキミの顔見たら\n疲れなんて\n消えちゃった！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5225, 117201, 102, 8, "この夏もいーっぱい\nドキドキさせるから！\n覚悟しててよね♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5226, 117201, 1, 1, "外は朝から暑いね\nけど、負けないから！\nお仕事、頑張るぞー！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5227, 117201, 2, 1, "自分へのご褒美に、\nアイス食べちゃお♪\nキミも一緒に食べよ！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5228, 117201, 3, 1, "夏の夜って\nワクワクするね♪\nホタル見に行こうよ！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5230, 117201, 2, 1, "ふわぁ…眠い…\n仕事とかの疲れ、\nたまってるのかな…？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5231, 117201, 3, 1, "私のぬいぐるみが\nグッズになったら…\n彼にかわいがって\nもらえるかな？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5232, 117201, 4, 1, "この香り…\n夏限定のやつでしょ？\nいいセンスしてるね♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5233, 117201, 5, 1, "暑～い！\nけど…汗かいて\n気持ちいいかも！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5235, 117201, 7, 1, "レッスンの後のご飯は\n特別おいしい♪\n…もうひとつだけ\n食べちゃおっかな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5236, 117201, 8, 1, "あ、この前撮影した\n水着グラビアだ\nうんうん、いい感じ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5238, 117201, 10, 1, "この子、もふもふだね\n触るのクセになりそう\nもふもふもふもふ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5239, 117201, 11, 1, "わ、かわいい♪\n今度のグラビア撮影で\nこれ使ってもいい？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5240, 117401, 1, 1, "水着っていいですよね\n海に浮かぶ一輪の花\nって感じがして！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5241, 117401, 2, 1, "自分の水着を作るのも\n誰かの水着を作るのも\n楽しいですね♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5242, 117401, 3, 1, "水着の最終チェック！\n伸縮性や動きやすさは\n…いい感じですね！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5243, 117401, 4, 1, "振り付けや衣装作りに\n歌や踊りの練習…\n…やることがいっぱい", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5244, 117401, 5, 1, "はぁ…レイ様の水着…\n私に作らせて\nもらえないかなぁ～…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5245, 117401, 101, 4, "騎士さんのことは\n気に入らないですけど\n別に嫌いという\nわけじゃなくて…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5246, 117401, 102, 8, "騎士さん暇そうですね\nに、荷物持ちとして\n誘ってみようかな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5247, 117401, 1, 1, "騎士さんいいところに\nこの衣装\nどう思います？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5248, 117401, 2, 1, "何見てるんですか？\nもしかしてこの水着の\n良さが分かります！？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5249, 117401, 3, 1, "真夏でも涼しい…\nそれも水着の良さの\n一つですね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5250, 117401, 4, 1, "仕立て屋も\nアイドルも\n諦めません！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5251, 117401, 101, 4, "夏と言えば\nバカンスですけど…\n予定、あるんですか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5252, 117401, 102, 8, "う～ん…騎士さんを\n海へ誘うには…って\nなんでもないですよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5253, 117401, 1, 1, "ふー…夏はやっぱり\n朝が一番過ごしやすい\nですね～！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5254, 117401, 2, 1, "暑い時こそ海です！\n体力づくりのためにも\n泳ぎにいきますよ！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5255, 117401, 3, 1, "外で虫が鳴いてる…\n夏って感じがして\n風流ですねぇ…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5257, 117401, 2, 1, "少し休憩です…\nこのまま寝ないよう\n気をつけないと…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5258, 117401, 3, 1, "か、かわいい…！\nこの感動を次の\nステージ衣装にも…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5259, 117401, 4, 1, "いい香りですね～\n波の音が聞こえて\nきそうです…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5260, 117401, 5, 1, "泳いだ後は\n少し寒いので\nちょうどいいですね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5262, 117401, 7, 1, "夏バテしないように\n栄養はしっかり\n取らないと！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5263, 117401, 8, 1, "次のトレンドは…\nふむふむ、やっぱり\nこう来ましたか…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5265, 117401, 10, 1, "デザインはもちろん\n触り心地も\n最高ですね♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5266, 117401, 11, 1, "夏の撮影スポットに\n活用できそうですね\nさっそく衣装考えて…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5267, 104001, 103, 12, "えへへ…♪\n団長さんが初めての\n友達で本当に良かった", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5268, 104001, 103, 12, "あっ、団長さん！\n今日のＢＢ団の活動\nなんですけど…二人で\n大縄跳びしませんか！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5269, 117301, 1, 1, "この衣装で\nライブするのは\n恥ずかしいな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5270, 117301, 2, 1, "せっかくの夏だし\n肝試しに\n行ってみたいかも…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5271, 117301, 3, 1, "歌もダンスも\nより上を目指すために\n夏合宿をしよう…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5272, 117301, 4, 1, "夏の暑さにも負けない\n熱いライブに\nしてみせる…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5273, 117301, 5, 1, "新曲のアイデア\nまとめたいな…\n少し海辺を散歩しよう", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5274, 117301, 101, 4, "かき氷、食べたいな\n…あの人は何味が\n好きなんだろう？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5275, 117301, 102, 8, "水着、似合ってるって\n言ってもらえるかな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5276, 117301, 1, 1, "サインして、ですか？\nいいですけど…\n少し照れますね…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5277, 117301, 2, 1, "少し待ってください\n今、新曲の楽譜を\n書いているので…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5278, 117301, 3, 1, "なんでしょう？\nもしかして…\nボイトレに興味が？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5279, 117301, 4, 1, "今度のライブでは\n夏らしい爽やかな歌を\nファンにお届けします", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5280, 117301, 101, 4, "…水鉄砲で遊ぼう？\nふふ。楽しそうですね\nお手柔らかに…♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5281, 117301, 102, 8, "あなたの声を\n聴いていると、\n何だか落ち着きます…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5282, 117301, 1, 1, "今日も暑くなりそう…\nお互いしっかりと\n水分補給しましょうね", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5283, 117301, 2, 1, "お腹空きましたよね\n海の家で、\nなにか買ってきます", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5284, 117301, 3, 1, "夜の海で月明かりと\n波の音を楽しもうかと\n…一緒に行きますか？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5286, 117301, 2, 1, "レッスンだけでなく\nリラックスすることも\n大事、ですよね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5287, 117301, 3, 1, "ふふ、かわいい\n精霊も負けないくらい\nかわいいですけどね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5288, 117301, 4, 1, "夏らしい爽やかで\nいい匂い…あなたに\nピッタリな香りですね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5289, 117301, 5, 1, "海に入った後は、\n体が冷えてしまいます\n一緒に温まりましょう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5291, 117301, 7, 1, "アイドルとして、\n食べすぎには\n気をつけないと…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5292, 117301, 8, 1, "夏は恋の季節…\nガンガン攻めるべし…\nなるほど…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5294, 117301, 10, 1, "この子に乗りながら\nライブをする…\nというのはどうですか", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5295, 117301, 11, 1, "まるで人魚になった\n気分…なんて、ふふ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5296, 118301, 1, 1, "お姉ちゃんとして\nシオリンにかっこいい\nとこ見せなくっちゃ☆", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5297, 118301, 2, 1, "いつかシオリンと\n一緒に空を飛んで\n空中散歩してみたいな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5298, 118301, 3, 1, "すこし眠くなってきた\nかも…シオリンと\nお昼寝しようかな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5299, 118301, 4, 1, "お散歩してこよっと！\nシオリンとお出かけ\nするのにいい場所\n探さなくっちゃ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5300, 118301, 5, 1, "シオリンに過保護って\n言われちゃった…♪\nそれでも心配しちゃう\nのが姉心…だよね？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5301, 118301, 101, 4, "気持ちいい日差し！\nシオリンと彼と、\nお昼寝したら\n気持ちよさそ～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5302, 118301, 102, 8, "今度天気がいい日には\nシオリンと彼を誘って\n一緒にお出かけ\nしたいな～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5303, 118301, 1, 1, "今日はご機嫌なんだ♪\nだって、シオリンの\n調子がいいんだもん☆", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5304, 118301, 2, 1, "今からシオリンと\nピクニックに\n行くんだ♪　そうだ！\nキミも一緒にどう？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5305, 118301, 3, 1, "わっ、びっくりした！\nシオリンから借りた本\n読むのに集中\nしてたから…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5306, 118301, 4, 1, "うん？　どうしたの？\nあっ、もしかして\nこの服、気になる？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5307, 118301, 101, 4, "シオリンもキミも元気\nいっぱいだと、私も\n元気になっちゃうよ☆", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5308, 118301, 102, 8, "シオリンの前だけ\nじゃなくて、キミにも\nかっこいい私の姿を\n見てほしいな☆", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5309, 118301, 1, 1, "おはよう！　えへへっ\n今日は早起きなんだ♪\nシオリンにかっこ悪い\nとこ見せたくないから", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5310, 118301, 2, 1, "おなかすいたなぁ～…\nシオリンと一緒に\nおいしいもの食べに\n行こうかな！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5311, 118301, 3, 1, "もうこんな時間！？\nシオリンと一緒だと\n時間が早く\n過ぎていっちゃうね！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5313, 118301, 2, 1, "この快適な寝心地…！\nシオリンにもおすすめ\nしなきゃ…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5314, 118301, 3, 1, "わあ、かわいい！\nそれにさわり心地も\nいいし、抱き枕に\nちょうどいいかも！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5315, 118301, 4, 1, "いいにおい…♪\nなんだかリラックス\nできて…すやぁ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5316, 118301, 5, 1, "ぽかぽか～☆\nシオリンと一緒に\nぬくぬくしたら\n気持ちよさそう♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5318, 118301, 7, 1, "仲のいい人が一緒だと\nつい食が進んじゃうね\nえへへ…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5319, 118301, 8, 1, "ふむふむ…この本…\nシオリンが好きかも！\n今度おすすめしよっと", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5322, 118401, 1, 1, "お姉ちゃん、\n私のために無理は\nしてないかな…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5323, 118401, 2, 1, "新しく買った絵本…\nこういうのなら\nお姉ちゃんと楽しく\n読めるよね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5324, 118401, 3, 1, "ニャットちゃんを\nここに連れてきて\nみんなで遊べないかな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5325, 118401, 4, 1, "本屋さんに行こうかな\nみんなで読める\n面白い本が\nあるといいなぁ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5326, 118401, 5, 1, "たまには…読むだけ\nじゃなくて、自分で\nお話を書いて\nみようかな…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5327, 118401, 101, 4, "時々…お姉ちゃんと\nあの人が似てるって\n思うことがあります…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5328, 118401, 102, 8, "お姉ちゃんとあの人…\n優しくて頼もしい\n二人に囲まれて…\n私、幸せ者だなぁ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5329, 118401, 1, 1, "どうかしましたか？\nもしかして…あなたも\n一緒にこの本を\n読みたい…とか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5330, 118401, 2, 1, "けほっ…けほっ…\nあっ、心配いりません\n飲み物を飲んで\nむせただけですから", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5331, 118401, 3, 1, "この格好…\n似合ってますか…？\nえへへ、照れますね…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5332, 118401, 4, 1, "なぁに、お姉ちゃん？\n…あっ、すみません\n読書に集中していて…\n間違えちゃいました…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5333, 118401, 101, 4, "お姉ちゃん、時々無理\nしちゃうから…\nあなたも一緒だと\n安心できます", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5334, 118401, 102, 8, "お姉ちゃんとあなたと\n頼れる人に囲まれて\n私は幸せものですね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5335, 118401, 1, 1, "なんだか朝から\n調子がいい…今日は\nいい日になりそうです", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5336, 118401, 2, 1, "せっかくだから、\nお姉ちゃんを誘って\nお外でランチも\nいいかも", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5337, 118401, 3, 1, "お姉ちゃんに心配\nかけないように…もう\nちょっと本を読んだら\n寝なくちゃね", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5339, 118401, 2, 1, "ふんわり柔らかくて\n気持ちいい手触り…\nお姉ちゃんが寝るのに\nちょうどよさそう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5340, 118401, 3, 1, "ふふっ、かわいい♪\n絵本の物語みたいに\nおしゃべりできない\nかな…？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5341, 118401, 4, 1, "素敵な落ち着く香り♪\nなんだかお姉ちゃん\nみたい、ふふっ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5342, 118401, 5, 1, "あったかくてほっと\nするなぁ\nここでゆっくり\n読書しようかな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5344, 118401, 7, 1, "この味…\nお姉ちゃんが好きそう\n少しもらって、\n持って行こうかな…？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5345, 118401, 8, 1, "この本みたいな、\n心躍る大冒険も\nお姉ちゃんと一緒なら\nできちゃいそう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5348, 117501, 1, 1, "いい部屋ね\nここを私の分院に\nするのもいいかも\nしれないわね…♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5349, 117501, 2, 1, "珍しいカンポーが\n手に入ったから\n色々試してみたいわ\n色々と、ね♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5350, 117501, 3, 1, "この服を着ると\n自分が医者だって\nより実感するわね\nまあ、闇医者だけどね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5351, 117501, 4, 1, "新薬開発のために\n薬草を採取して\nおこうかしら", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5352, 117501, 5, 1, "いつ急患が来ても\n対応できるように\nしておきましょうか", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5353, 117501, 101, 4, "あの子の研究資料も\nだいぶ増えてきたわね\nちゃんとまとめて\nおかないと♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5354, 117501, 102, 8, "あの子は大事な\n研究対象。だから\nいつまでも健康で\nいてもらわないとね♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5355, 117501, 1, 1, "薬に興味があるの？\nなら、今度ゆっくりと\n教えてあげるわね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5356, 117501, 2, 1, "少し時間があいたから\n一緒に新薬の実験を\nしましょうか♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5357, 117501, 3, 1, "どうしたの？\nまさか急患…！？\nあら、違うのね\nならよかったわ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5358, 117501, 4, 1, "具合が悪い時はすぐに\n言うのよ。君には\nいつでも健康でいて\nほしいから", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5359, 117501, 101, 4, "少し仮眠したいわね\n肩…いいえ、膝を\n貸してもらえる？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5360, 117501, 102, 8, "たまには…君の隣なら\n闇医者も悪い研究者も\nお休みにするのも\n悪くないわね♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5361, 117501, 1, 1, "たまには朝の陽ざしを\n浴びるのも…\n悪くないわね", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5362, 117501, 2, 1, "天気がいい日は\nいい薬草が採れるの\n君も集めるの、\n手伝ってくれる？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5363, 117501, 3, 1, "いけない、もうこんな\n時間なのね\n診察に没頭すると\n時間感覚がなくなるわ", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5365, 117501, 2, 1, "もう少し固い方が\n私好みだけど…\n悪くない寝心地ね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5366, 117501, 3, 1, "このぬいぐるみ…\n闇医者として\n興味深いわね…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5367, 117501, 4, 1, "落ち着く香りね\n私も診察室で\n使ってみようかしら", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5368, 117501, 5, 1, "冷えは病につながるわ\nしっかり温めて\n体をいたわりましょ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5370, 117501, 7, 1, "おいしい♪\n研究のお供に\nもってこいの\n味だわ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5371, 117501, 8, 1, "医学は日進月歩…\n闇医者とはいえ\n最新の論文には\n目を通しておかないと", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5400, 117601, 1, 1, "白衣の天使かあ\nまさにボクのための\n呼び方だね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5401, 117601, 2, 1, "美しさとは\n日々の努力が\n作るものなんだよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5402, 117601, 3, 1, "大変だ…\nボクは美しく\nなりすぎたかも", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5403, 117601, 4, 1, "ボクのかわいさで\n癒やされて\nみんな健康に\nなっちゃうね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5404, 117601, 5, 1, "どんな服装をしても\nボクの美しさは\n変わらないね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5405, 117601, 101, 4, "これからはボクも\n美の伝道師として\nがんばらないとね♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5406, 117601, 102, 8, "ボクのかわいい笑顔を\n近くで見る以上の\n癒やしはないよね♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5407, 117601, 1, 1, "どう？　看護師姿の\nボクもかわいいでしょ\nもっと近くで見る？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5408, 117601, 2, 1, "ほら！　美しいボクの\n看護師姿を見たら\n元気でちゃうでしょ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5409, 117601, 3, 1, "白衣の天使のボクも\n最高に美しいね\n存分に癒されてよ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5410, 117601, 4, 1, "具合が悪いの？\nならボクの美しい顔を\n処方してあげる♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5411, 117601, 101, 4, "さあ今日もボクと\n一緒に美の追求に\n付き合ってもらうよ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5412, 117601, 102, 8, "かわいい天使のボクが\nいつもそばにいるんだ\nキミって幸せ者だね♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5413, 117601, 1, 1, "さすがボク\n目が覚めるような\n美しさでしょ？", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5414, 117601, 2, 1, "美は食で作られる\n美容にいいものを\n食べようよ！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5415, 117601, 3, 1, "ふああ…早く寝よ？\n夜更かしは美容の\n大敵だからね", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5417, 117601, 2, 1, "ふう～…\nリラックスする\nボクも美しい", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5418, 117601, 3, 1, "ボクが抱きしめると\n普段よりかわいく\n見えちゃうね♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5419, 117601, 4, 1, "美しいボクは\n常にいい香りを\nしていなくちゃね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5420, 117601, 5, 1, "光がボクを照らしてる\nんじゃなくて、ボクが\n光を輝かせてるのさ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5422, 117601, 7, 1, "バランスのいい食事が\n美しいボクを\n作るのさ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5423, 117601, 8, 1, "ふむふむ…なるほど\nこうすればより美しく\nなれるかもしれない", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5425, 105701, 103, 12, "仲間のためなら\nきっと…どこまでも\n強くなれるよね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5426, 105701, 103, 12, "プリンセスフォーム？\n私と同じような力を\n持ってる人がいるの？\n会ってみたいな～！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5427, 118001, 1, 1, "ここの空気は、\nどうもゆるすぎて…\n落ち着きませんわね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5428, 118001, 2, 1, "陛下のお越しになる\n別宅としては、\n少し警備が\n甘すぎでは？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5429, 118001, 3, 1, "あらあら？\nサッシにホコリが！\nちょっとネコ助！\n掃除は念入りに！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5430, 118001, 4, 1, "いつまで入り浸って\nいるのかと、\n秘書に叱られて\nしまいましたわ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5431, 118001, 5, 1, "もういっそ、\nここに間借りして\n仕事しようかしら…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5432, 118001, 101, 4, "邪魔している身ですし\n家事くらい\nお手伝いさせなさいな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5433, 118001, 102, 8, "本当は、意外と\n居心地が良いのかも…\n陛下の気持ち、\n少し分かりましたわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5434, 118001, 1, 1, "ちょっ…わたくしを\n叩いても、ホコリなど\nあまり出ませんわよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5435, 118001, 2, 1, "陛下っ？\nなんだ…貴方でしたの\n用件は手短にお願いね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5436, 118001, 3, 1, "別に、好き嫌いは\nありませんけれど…\n魔物料理って、意外と\n美味なものですわね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5437, 118001, 4, 1, "言っておきますけれど\nわたくしは商売人です\n荒事は専門外ですわよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5438, 118001, 101, 4, "プリンセスナイトの\n能力…なにか儲け話に\n使えませんかしらね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5439, 118001, 102, 8, "貴方がたのことも、\n少しくらいは\n信用してないことも\nありませんでしてよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5440, 118001, 1, 1, "おはようございます\nさ、今日もバリバリ\n働きますわよ！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5441, 118001, 2, 1, "お昼ですわね\n栄養補給をしつつ\n働きますわよ！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5442, 118001, 3, 1, "定時ですって？\n関係ありません！\nわたくし、まだまだ\n働きますわよ～っ！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5444, 118001, 2, 1, "うっ…いけませんわ\n日頃の疲れが…\nふわぁ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5445, 118001, 3, 1, "もふもふ…ふふっ♪\n…なんですのっ、\nなにかおかしくて！？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5446, 118001, 4, 1, "いい香りですわね\nわたくしの執務室にも\n置きたいくらいですわ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5447, 118001, 5, 1, "いけませんわ…\nこんなに暖かいと\n日頃の疲れが…ふわぁ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5449, 118001, 7, 1, "もう一つ食べたら\n仕事に戻りますわよ？\nあと一つだけ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5450, 118001, 8, 1, "商人にとって\n知識は武器ですわ\n陛下のため、貴方も\nもっと勉強なさい？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5452, 117701, 1, 1, "故郷のお菓子を\nいろいろ作ったんだ～\nみんなに\n配ってくるさ～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5453, 117701, 2, 1, "彼ってどんな仮装が\n似合うかな～？\nシーサーとか？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5454, 117701, 3, 1, "カボチャチャンプルー\n作ってみよっと！\nきっとおいしいさ～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5455, 117701, 4, 1, "視線を感じるさ～\nもしかして幽霊？\nおしゃべりするさ～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5456, 117701, 5, 1, "さっき作った\nサーターアンダギー、\n早く彼に\n食べてほしいさ～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5457, 117701, 101, 4, "彼のために、\n特別なイタズラを\n考えとくさ～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5458, 117701, 102, 8, "いつか彼と、\n故郷のハロウィンを\n楽しみたいさ～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5459, 117701, 1, 1, "ハロウィンは\n私たちが守るさ～♪\nさ、見回りに行こ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5460, 117701, 2, 1, "この制服、\nハロウィンらしくて\n大好きさ～♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5461, 117701, 3, 1, "イタズラする気ね～？\nどんなことされるのか\n楽しみさ～♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5462, 117701, 4, 1, "カボチャのランタン、\n一緒に作ろ！\n絶対楽しいよ～♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5463, 117701, 101, 4, "お菓子くれるの？\nあは～、ありがとー♪\nもぐもぐ…\n優しい味がするさ～♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5464, 117701, 102, 8, "キミと一緒に過ごす\nハロウィンは、\nドキドキとワクワクが\n止まらないさ～♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5465, 117701, 1, 1, "ハロウィンは\n朝から大忙しさ～！\nお菓子作り、\n手伝ってほしいさ～", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5466, 117701, 2, 1, "お化け屋敷に行こ！\n一緒にいっぱい\nドキドキしようね～♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5467, 117701, 3, 1, "ここからが\nハロウィン本番さ～！\n幽霊たちと\nいっぱい遊ぶさ～♪", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5469, 117701, 2, 1, "ちょっと休んだら、\nお菓子作りを\n再開するさ～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5470, 117701, 3, 1, "でーじかわいいね～♪\nこの子に仮装させたら\nきっと似合うさ～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5471, 117701, 4, 1, "お菓子みたいに\n甘い匂い…\nハロウィンらしい\nいい香りさ～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5472, 117701, 5, 1, "ランドソルの秋って\n冷えるけど…\nこれがあれば\n体も心もぽかぽかさ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5474, 117701, 7, 1, "故郷のご飯も\nおいしいけど、\nこっちのご飯も\nでーじおいしいさ～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5475, 117701, 8, 1, "パンプキンパイの\nレシピは…なるほど～\n勉強になるさ～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5477, 117701, 12, 1, "全部おいしそうで\nどれから食べるか\n迷っちゃうさ～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5479, 117801, 1, 1, "ムっ！　幽霊の気配を\n感じるデス！\nさあ姿を現すデス！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5480, 117801, 2, 1, "ムムム…\n妖魔の力を忍法に\n使えないでショウか…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5481, 117801, 3, 1, "怪しい妖魔を\n成敗するデス！\nハロウィンの平和は\nワタシが守りマスよ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5482, 117801, 4, 1, "ワタシの祭囃子が\n聞こえマスかー？\nさあ集まるデース！\n陽気に踊りまショウ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5483, 117801, 5, 1, "お祭りは\n楽しんだ者勝ちデス！\n踊らにゃ\nソンソンデス！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5484, 117801, 101, 4, "幽霊になる前に\n天下統一を\n果たしたいデスね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5485, 117801, 102, 8, "あの世に行っても\nショーグンは\nワタシの大事な\nショーグンデス！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5486, 117801, 1, 1, "ショーグン！\n悪霊退治に\n出かけるデース！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5487, 117801, 2, 1, "妖魔を家来に\nしてみるのは\nどうデス？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5488, 117801, 3, 1, "マントを使って\nウツセミの術の精度を\n高めるデス！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5489, 117801, 4, 1, "ハロウィンにちなんだ\n新しい忍法…\nすなわち妖忍法の\n開発を急ぐデス！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5490, 117801, 101, 4, "あの世でも\nショーグンに\nお仕えしマスよー！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5491, 117801, 102, 8, "ショーグンもワタシと\nハロウィンパレードを\n踊り明かしまセンか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5492, 117801, 1, 1, "おはようございマス！\n天下統一に向けて\n急いで準備するデス！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5493, 117801, 2, 1, "お腹減ってまセンか？\nハロウィンスイーツで\n休憩するデ〜ス", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5494, 117801, 3, 1, "夜更かしすると幽霊が\nでるかもしれまセン…\nヒュ〜ドロロロ〜…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5496, 117801, 2, 1, "もふもふなところが\nわたあめちゃんに\n似ていマス。わんっ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5497, 117801, 3, 1, "ぬいぐるみや人形には\n魂が宿るという\n言い伝えがありマス…\nマカフシギデ〜ス", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5498, 117801, 4, 1, "ウ〜ム…\nなんとも怪しい\n香りがしマスね！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5499, 117801, 5, 1, "パレードの熱気に\n負けないくらい\n熱いデス！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5501, 117801, 7, 1, "ついつい食べすぎて\nしまうのは\n妖魔のせいデスか！？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5502, 117801, 8, 1, "トーゴク妖怪の\n図鑑デス！　個性的な\n妖怪ばかりデスね！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5504, 117801, 12, 1, "ハロウィンの甘味を\n集めまシタよ！\nショーグンへの\n貢ぎ物デス！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5531, 101301, 103, 12, "彼と二人で\n買い物に…って…\nこれってデートに\nなっちゃうのかな！？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5532, 101301, 103, 12, "とうっ！　どうどう？\n今の決めポーズ！\nかっこいいよね♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5533, 117901, 1, 1, "ヒデサイとおそろの\nハロウィンコスとか\nしてみたいな～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5534, 117901, 2, 1, "カボチャ料理かぁ…\n作ったらヒデサイ\n食べてくれるかな～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5535, 117901, 3, 1, "ハロウィンスイーツ…\nどれもおいしすぎて\n激ヤバなんですけど！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5536, 117901, 4, 1, "霊界ってどんなとこ\nなんだろ～\nわりと近所だったり？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5537, 117901, 5, 1, "服をハロウィンっぽく\nアレンジするのも\nたのしーよね♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5538, 117901, 101, 4, "ヒデサイと一緒に\nハロウィンパーティー\n…えへへ、楽しみ～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5539, 117901, 102, 8, "ヒデサイといると\nドキドキする…\nなんでだろ～？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5540, 117901, 1, 1, "ねねね、うちらも\nハロウィンパレード\n見に行こー？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5541, 117901, 2, 1, "ハロウィンセイバーズ\nしゅつどーう！\nヒデサイも行こ～♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5542, 117901, 3, 1, "マントのロゴ\nかっこいいっしょ～？\nカボきちってゆーんだ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5543, 117901, 4, 1, "トリックオア\nトリート！\nねえねえヒデサイ\nお菓子ちょうだい♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5544, 117901, 101, 4, "ヒデサイも\nメイクしてみる？\nハロウィンぽいやつ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5545, 117901, 102, 8, "ヒデサイのこと\nうちがいつでも\nあっためたげるね！\n…あ！　手、手の話！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5546, 117901, 1, 1, "ハロウィンのイベント\n朝からでも\nやってるかな～？", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5547, 117901, 2, 1, "カボチャでおやつ\n作ってみたんだ～\nヒデサイ食べてみて！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5548, 117901, 3, 1, "ハロウィンの夜って\nやっぱテンション\n上がるよね～♪", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5550, 117901, 2, 1, "超ふかふかしてる～\nちょっと休憩させて\nもらっちゃお", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5551, 117901, 3, 1, "はぁ…\nもにカワすぎて\nつらいんですけど～！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5552, 117901, 4, 1, "ハロウィン限定の\nフレグランスじゃん！\nいい匂いする～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5553, 117901, 5, 1, "やばばば…\nあったかくて\n眠くなっちゃう～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5555, 117901, 7, 1, "これ食べて\nもうひとがんばり\nしよーっと！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5556, 117901, 8, 1, "ハロウィンっぽい\nファッションとか\n載ってないかなぁ？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5558, 117901, 12, 1, "お菓子いっぱいだ～！\nあとでヒデサイにも\n持ってってあげよ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5560, 118501, 1, 1, "お掃除が終わったら\n次は書類を片付けて…\nその次は…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5561, 118501, 2, 1, "皆さんが冒険している\n間の留守は私に\nお任せください♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5562, 118501, 3, 1, "美食殿の皆さんが\n持ってきてくれる\nお土産が楽しみです♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5563, 118501, 4, 1, "ネビアさんとは\nときどき一緒にお茶\nする仲なんです", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5564, 118501, 5, 1, "ギルドハウスの乱れは\nギルドの乱れ\n整理整頓が大切です", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5565, 118501, 101, 4, "素敵なギルドハウスの\n管理人ができて\nとっても幸せです♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5566, 118501, 102, 8, "皆さんをほんの少し\n特別扱いしちゃう\n私がいます", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5567, 118501, 1, 1, "何かわからないことが\nあるときは私に\n聞いてくださいね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5568, 118501, 2, 1, "書類を書いていると\n落ち着きます…\n一緒にどうですか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5569, 118501, 3, 1, "あなたのまわりは\nいつも賑やかで\n楽しいですね♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5570, 118501, 4, 1, "あっ！　お頼みしたい\nことがあって\n探してたんです♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5571, 118501, 101, 4, "ギブアンドテイクは\n大切です。たまには\nお礼させてください♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5572, 118501, 102, 8, "陰に日向にあなたを\n支えていきます♪\n期待していてください", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5573, 118501, 1, 1, "今日も一日\n管理人として\nがんばります♪", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5574, 118501, 2, 1, "お腹がすきました…\nそろそろお昼に\nしませんか？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5575, 118501, 3, 1, "もうこんな時間…\n皆さんといると一日が\nあっという間です", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5577, 118501, 2, 1, "良質なお仕事は\n良質な休憩から\nです♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5578, 118501, 3, 1, "かわいいものに\n囲まれてお仕事できる\nのは幸せですね♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5579, 118501, 4, 1, "お仕事に集中できる\nアロマを設置するのも\nよさそうですね♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5580, 118501, 5, 1, "ポカポカ温まりながら\n書類仕事をするのも\nいいものですね…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5582, 118501, 7, 1, "栄養補給しないと\n頭が回りませんからね\nきちんと食べましょう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5583, 118501, 8, 1, "新条例はこのように\n活用されている、と…\nふむふむ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5585, 106701, 1, 1, "会合用の資料と\n連載のネーム…\nどっちを片付けよう？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5586, 106701, 2, 1, "イノリちゃん、また\nお金くすねてる…\n帰ってきたら\nお尻ペンペンだ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5587, 106701, 3, 1, "カヤちゃんの装備\n傷むの早いなぁ〜\nメンテしてあげないと", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5588, 106701, 4, 1, "そうだ、ラーゴのご飯\n買いに行かないと\n今日は何がいいかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5589, 106701, 5, 1, "ここの人たちは見てて\n飽きない人ばっかり♪\n漫画の参考にしよ〜", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5590, 106701, 101, 4, "また一緒に買い物にも\n行きたいし、冒険も\nしたいな〜…♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5591, 106701, 102, 8, "…君は見てるだけで\n満足なの？ ちゃんと\n触って欲しいな…♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5592, 106701, 1, 1, "私の目が見たいの？\nいいけど…魅了されて\n魂が壊れちゃうよ…？\n…冗談で〜す☆", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5593, 106701, 2, 1, "ラーゴがね、君に\n遊んで欲しいって☆\n仲良くしてあげてね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5594, 106701, 3, 1, "アシスタントの仕事\n気に入ってくれたなら\nまたお願いしていい？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5595, 106701, 4, 1, "私が覚醒できるか？\n見せてあげたいけど…\nすごいことになるから\nまた今度で♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5596, 106701, 101, 4, "楽しいトラブルが\nあったら、とりあえず\n声はかけて欲しいな♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5597, 106701, 102, 8, "マッサージお願い♪\n変なところは…\n少しだけなら☆", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5598, 106701, 1, 1, "おはよう〜♪\n今日も一日、一緒に\nがんばっていこうね", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5599, 106701, 2, 1, "お腹空いちゃった…\n一緒にお昼行かない？\nいいお店があるんだ〜", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5600, 106701, 3, 1, "君、眠れないの？\n誰かに見られてる？\n…きっと気のせいだよ", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5602, 106701, 2, 1, "ちょっと休憩〜…\nこのまま原稿すれば\n疲れなくなるのかな？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5603, 106701, 3, 1, "かわいい〜♪\nラーゴのお友達に\nなってくれる？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5604, 106701, 4, 1, "いい香り〜♪\nこの目でどう見えるか\n伝えられたらいいのに", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5605, 106701, 5, 1, "ほどほどなのが\nちょうどいい時も\nあるよね…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5607, 106701, 7, 1, "この味付け…\nあっちでも再現\nできるかな？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5608, 106701, 8, 1, "ふふっ、面白い♪\nイノリちゃんの買う\n漫画にハズレなし☆", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5610, 119001, 1, 1, "私…23歳だもん\n魔法少女だなんて\n名乗れないわ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5611, 119001, 2, 1, "あん、どぅ、とろわ〜\nみんなイイ子に\nな〜る♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5612, 119001, 3, 1, "あっ！　イケナイ子の\n気配がするわ！\n風紀の乱れは\n先生が許しませんっ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5613, 119001, 4, 1, "ここで闇の力が\n発動したら大変だわ！\nやだっ…そう思ったら\n緊張して…ダメぇ〜！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5614, 119001, 5, 1, "オイタした子はみんな\nどうしてうれしそうに\n私の愛のムチを\n欲しがるのかしら…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5615, 119001, 101, 4, "あの子に次は何を\n教えてあげようかしら\n愛？　希望？\nそれとも…♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5616, 119001, 102, 8, "あの子のことを\n考えると、愛と希望で\n胸がいっぱいになるの", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5617, 119001, 1, 1, "先生なのに闇の力を\n持ってるのは秘密よ？\n他の子には\n知られたくないから…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5618, 119001, 2, 1, "秘密の授業の内容？\nそれは…あんなことや\nこんなことよ♪\nそれから、それから…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5619, 119001, 3, 1, "しーっ！　私の正体が\nルーセント学院の\n先生だって、ここでは\n秘密にしててね？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5620, 119001, 4, 1, "あら？　補習して\nほしいの？　いいわよ\n君のお願いなら\nなんでも聞いちゃう♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5621, 119001, 101, 4, "イイ子にはごほうびを\nあげなくちゃ！\n君はなにが欲しい？\n遠慮しちゃダメよ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5622, 119001, 102, 8, "君との秘密が\nまた一つ\n増えちゃったね♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5623, 119001, 1, 1, "今日も一日\n元気に過ごしましょう\n先生が指導してあげる", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5624, 119001, 2, 1, "夜まで頑張れるように\nお昼ご飯はしっかり\n食べましょう〜！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5625, 119001, 3, 1, "夜更かしはダメよ？\nイケナイ子が\nすることです！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5627, 119001, 2, 1, "闇の力に振り回されて\nもうヘトヘトよ〜\n心も体も\n癒されたいわ〜", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5628, 119001, 3, 1, "『秘密の学び舎』に\nこっそり連れて\n行っちゃおうかしら♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5629, 119001, 4, 1, "イケナイ甘い香り…\nなんだか胸の奥が\n熱くなってきたわ…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5630, 119001, 5, 1, "やんっ♪\n燃え上がる愛みたい♪\n火照ってきちゃった♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5632, 119001, 7, 1, "生徒の見本に\nなれるように\n好き嫌いはしないの", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5633, 119001, 8, 1, "『学び舎に咲く禁断の\n愛』？　気になるわ…\nううん、ダメよっ！\nでも…ちょっとだけ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5635, 106301, 103, 12, "次に会う時は、\nまたあそこに一緒に…\nえへへ…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5636, 106301, 103, 12, "横顔見てるの、\nバレてないよね…？\n…ちらっ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5637, 105301, 103, 12, "背が少し伸びたか！？\nあ、あいつのところへ\n報告に行かねばっ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5638, 105301, 103, 12, "私も貴公の隣に\n立っていられるよう\n日々精進していくぞ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5639, 119101, 1, 1, "ああ～\nドＭミストが\nあふれてしまいます～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5640, 119101, 2, 1, "魔法少女として\nクウカ頑張ります～！\n愛と希望の妄想を…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5641, 119101, 3, 1, "決めポーズの練習より\nやられポーズの練習を\nしておきます…♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5642, 119101, 4, 1, "快感を溜めに溜めて\n放つドＭビームは\n最高ですよ～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5643, 119101, 5, 1, "はっ！\nこれは闇の気配！\n…クウカ自身でした", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5644, 119101, 101, 4, "ドＳさん…ぐふふ…\nクウカを縛りあげ…\nじゅるりぃ～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5645, 119101, 102, 8, "ドＳさんに攻められる\nことで、クウカは進化\nしてゆくのです～！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5646, 119101, 1, 1, "はぅっ！\nもっと激しく…！\n情熱的に～～～！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5647, 119101, 2, 1, "クウカの変身シーンを\nじっくり眺めたい！？\nは、恥ずかしいです～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5648, 119101, 3, 1, "「本当に魔法少女か\n確かめてやるよ…！」\nドＳさんはそう言って\nクウカを…ぐふふ…♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5649, 119101, 4, 1, "愛と希望の力が\nクウカの中に…\nこの胸の高鳴りが…？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5650, 119101, 101, 4, "ひゃあんっ！\nそこです～っ！\nもっと、もっと～♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5651, 119101, 102, 8, "クウカをこんなに\n悦ばせるのは\nドＳさんだけです…♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5652, 119101, 1, 1, "明るい時間に出歩くと\n目立ちますね\n魔法少女の衣装…", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5653, 119101, 2, 1, "お昼の見回りです～♪\nああ～うっかりドＭを\n増やしてしまいました", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5654, 119101, 3, 1, "夜！　それはクウカが\n活性化する時間です！\n闇の活動開始です～♪", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5656, 119101, 2, 1, "気の緩んだクウカを\nドＳさんの鋭い牙が\n狙っています～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5657, 119101, 3, 1, "魔法少女のマスコット\nにいいかもしれません\nもっと鎖とか飾りを…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5658, 119101, 4, 1, "魅惑的な香りです～\nドＭミストの香りに\n似て…いや違いますね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5659, 119101, 5, 1, "火照る体…紅潮した\nクウカの肢体を熱く\n見つめるドＳさん…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5661, 119101, 7, 1, "食欲を満たした次は\n何を満たせば\nいいですかねぇ～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5662, 119101, 8, 1, "魔法少女がひどい目に\n遭う物語は…\nなさそうですね…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5664, 119901, 1, 1, "ケーキも悪くないけど\nプリンが一番なの\nでもせっかくだから\n両方もらってやるの！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5665, 119901, 2, 1, "クリスマスって聞くと\nなんだかあったかい\n気分になるの～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5666, 119901, 3, 1, "煙突がなくても\nミヤコはどこからでも\nおうちに入れるの♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5667, 119901, 4, 1, "イブもプリン！\nクリスマスもプリン！\nプレゼントもプリン！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5668, 119901, 5, 1, "クリスマスツリーの\n高いところの\n飾りつけは、ミヤコに\nまかせろなの～！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5669, 119901, 101, 4, "アイツのプレゼント\n楽しみなの～\nきっと最っ高に特別な\nプリンなの～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5670, 119901, 102, 8, "ミヤコがサンタなら\nアイツはトナカイなの\nつまりお似合いって\nことなの～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5671, 119901, 1, 1, "ミヤコ、プレゼントを\n配りにいくの～♪\nオマエも手伝えなの！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5672, 119901, 2, 1, "オマエにも飾りつけ\nしてやるの！\n頭にプリンを乗せると\nかわいいの♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5673, 119901, 3, 1, "幽霊のミヤコが\nいるなら、サンタも\nいて当たり前なの～！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5674, 119901, 4, 1, "靴下にプリンを\n入れたらつぶれるの\nだからプリンは直接\nミヤコによこすの～！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5675, 119901, 101, 4, "今日はプリンだけじゃ\n満足してやらないの！\nミヤコをたくさん\n甘やかせなの！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5676, 119901, 102, 8, "ミヤコのプリン\nオマエになら一個…\nうう～、一口だけ\nあげてもいいの！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5677, 119901, 1, 1, "サンタの朝は早いの～\nミヤコも早起きして\nプレゼント準備したの\nとってもえらいの♪", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5678, 119901, 2, 1, "今日は一日中\nみんなと遊ぶの～！\nオマエも来いなの！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5679, 119901, 3, 1, "みんなが寝たら\nチャンスなの～\nこっそりプレゼント\n配りにいくの♪", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5681, 119901, 2, 1, "むふー、気持ちいいの\nこういうプレゼントも\nいいかもしれないの", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5682, 119901, 3, 1, "これ気に入ったの～\nプリンとアイツの\n次くらいなの♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5683, 119901, 4, 1, "いいにおいなの～\nうっかり成仏\nしちゃいそうなの…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5684, 119901, 5, 1, "ここに飾っといた\n雪だるま\nなくなっちゃったの\nきっと成仏したの～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5686, 119901, 7, 1, "これはオマエがくれる\nプリンとは\n別腹なの～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5687, 119901, 8, 1, "プリンケーキ…\nこれならミヤコにも\n作れそうなの！\nさっそく作るの！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5689, 119901, 14, 1, "びっくりしたの～！\n幽霊のミヤコを\n驚かすなんて…\nなかなかやるの！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5690, 119901, 15, 1, "コイツの中…\nあったかいの～♪\n昼寝にぴったりなの～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5691, 119201, 1, 1, "メリークリスマス！\n聖夜に笑顔を\nお届けするべ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5692, 119201, 2, 1, "クリスマス前は\n乳製品がよく売れて\nありがてぇけんど…\n目が回る忙しさだ～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5693, 119201, 3, 1, "牧場で売る\nクリスマスリースを\n作っておかねぇと…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5694, 119201, 4, 1, "うちの動物たちにも\nクリスマスプレゼント\n用意しなきゃなぁ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5695, 119201, 5, 1, "クリスマスに向けた\n限定商品とか\n作ってみるべか？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5696, 119201, 101, 4, "あんちゃんへの\nプレゼント選び…\nここでボケるか…？\nやめておくべか…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5697, 119201, 102, 8, "オラ、あんちゃんから\n本当にたくさんの物を\nもらってんなぁ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5698, 119201, 1, 1, "雪遊びがしたいべか？\nへへっ、あんちゃん\nには負けねぇど！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5699, 119201, 2, 1, "ディナーと一緒に\n聖夜のお笑いライブを\nすんのはどうだべか？\nツッコミは任せたべ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5700, 119201, 3, 1, "寒くなってくると\n鍋が食べたくなるべや\nあんちゃんはどうだ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5701, 119201, 4, 1, "あんちゃんは\nサンタさんのこと\n信じてるだか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5702, 119201, 101, 4, "どでかいクリスマス\nケーキをこさえるべ\nあんちゃんも作るの\n手伝ってくんろ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5703, 119201, 102, 8, "あんちゃんが\nオラの旦那だったら…\nって、あんちゃん！\n今の聞いてただか！？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5704, 119201, 1, 1, "牧場の朝は早いど！\nさぁてと、まずは\n雪かきしに行くべや", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5705, 119201, 2, 1, "あんちゃんも一緒に\nお昼食べてくか？\n採れたての冬野菜は\nうんまいど～♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5706, 119201, 3, 1, "よい子は早く寝ねぇと\nサンタさんが\n来てくれねぇべよ？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5708, 119201, 2, 1, "こういうのリンリンが\n好きそうだな…\nプレゼント候補に\n入れておくべ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5709, 119201, 3, 1, "もふもふしてて\nなまらめんこくて…\nあぁ…癒されるべや…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5710, 119201, 4, 1, "この匂い…\n気持ちが和むべや\n夢心地になっちまうな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5711, 119201, 5, 1, "あったけぇところは\n落ち着くなぁ…\nつい眠くなっちまう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5713, 119201, 7, 1, "こ、この味は…！\n…うーん、もっと\nリアクション芸も\n練習しねぇとな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5714, 119201, 8, 1, "シオシオって\nどんな本読んでんだ？\n…難しい字が\nぎっしりだべや…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5716, 119201, 14, 1, "この仕掛け…\nオラのコントに\n活かせるかも\nしんねぇな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5717, 119201, 15, 1, "この着ぐるみで\nリマリマのモノマネを\n練習してみるべか…？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5718, 105001, 103, 12, "もっとキレイになって\nアイツにぎゃふんと\n言わせてやるんだから", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5719, 105001, 103, 12, "アタシのこと\nどう思う？\nかわいいって…そこは\nキレイって言ってよね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5720, 119301, 1, 1, "ハートマークの刺繍…\nは、やりすぎですかね\nいやちょっとくらい\nアピールしてみても…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5721, 119301, 2, 1, "雪降らないかな～\nそしたらお兄ちゃんと\n雪だるま作ったり\nできるのに～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5722, 119301, 3, 1, "プレゼントを入れる\n靴下…いくつ編んでも\n足りません～！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5723, 119301, 4, 1, "お兄ちゃんとの\nクリスマスデートは\n早い者勝ちですからね\n負けませんよ～！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5724, 119301, 5, 1, "クリスマスだし…\n受験勉強は今日だけ\nお休みです♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5725, 119301, 101, 4, "お兄ちゃんと\nおそろいのコーデで\nクリスマスデート…\nしたいなぁ～！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5726, 119301, 102, 8, "お兄ちゃんに\nプレゼントする編み物\nこれで３着目～と♪\nあれ、作りすぎ？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5727, 119301, 1, 1, "えへへ、もっと近くで\n見たくありませんか？\n私のとっておきの\nクリスマスコーデ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5728, 119301, 2, 1, "お兄ちゃんが風邪を\n引いたら一大事です！\n私の編み物で暖かく\nしてあげます♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5729, 119301, 3, 1, "お兄ちゃん、ほっぺに\nケーキのクリームが\nついちゃってますよ♪\n私がとってあげます！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5730, 119301, 4, 1, "お兄ちゃん！\nケーキ、あーんって\nしてあげます！\nほら、あーん♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5731, 119301, 101, 4, "クリスマスは大切な\n人と過ごすものっ！\nお兄ちゃん…妹の予定\n空いてますよっ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5732, 119301, 102, 8, "クリスマスはずっと、\nお兄ちゃんと一緒に\nいたい…って言ったら\n欲張り…すぎですか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5733, 119301, 1, 1, "早起きは３本お得！\n…クリスマスなら\n追加で10本ぐらい\nもらえちゃうかも！？", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5734, 119301, 2, 1, "お昼はケーキ？\nそれとも七面鳥？\nごちそういっぱいで\n迷っちゃいますね～♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5735, 119301, 3, 1, "お兄ちゃんと\nクリスマスに夜更かし\n…えへへ、ちょっぴり\nドキドキしちゃう♪", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5737, 119301, 2, 1, "編み物をするには\n絶好のくつろぎ空間\nですね～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5738, 119301, 3, 1, "なんというかわいさ…\nクリスマス\nプレゼントに\nもってこいですね！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5739, 119301, 4, 1, "これがお兄ちゃんの\nお部屋の香り…？\nんん～！　ずっと\nかいでいたいです～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5740, 119301, 5, 1, "ふぁ、あったか～い…\n冬場はこういうのが\nありがたいですね～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5742, 119301, 7, 1, "ごちそうだぁ～♪\nほら、お兄ちゃんも\n食べて食べてっ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5743, 119301, 8, 1, "クリスマスデートの\n必勝法…こ、これは\n読むしかないです！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5745, 119301, 14, 1, "わっ、すごいです～！\nこれを飾っておけば\nクリスマスが一層\n賑やかになりますね♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5746, 119301, 15, 1, "さぷらーいずっ！\nリマちゃんじゃなくて\nリノちゃんでした～！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5747, 120601, 1, 1, "ミミにもミソギにも\nあの人にも頼りに\nしてもらえるように\nしっかりしなきゃ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5748, 120601, 2, 1, "ミミはにんじんを\n食べられてすごいな…\n私も見習わなきゃ\nがんばろう…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5749, 120601, 3, 1, "ミソギから怖い話\n聞いちゃった…\nうぅ…誰か一緒に\nいてくれないかな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5750, 120601, 4, 1, "立派な大人に\nなれるように\nいっぱい勉強\nしておかなくっちゃ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5751, 120601, 5, 1, "なんだか…あの人の\nあやしい視線を\n感じるような…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5752, 120601, 101, 4, "あの人に勉強を\n教えられるように\nしっかり予習を\nしておこうっと…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5753, 120601, 102, 8, "今日はあの人と\n一緒に冒険できる…\nなんだかワクワク\nしてきちゃった…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5754, 120601, 1, 1, "あ、いいとこに来て\nくれましたね！\nあなたに頼みたい\nことがあるんです！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5755, 120601, 2, 1, "きゃあっ！？\nへ、変なところを\n触らないでください！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5756, 120601, 3, 1, "どうですかこの格好？\nかわいい、ですか…？\nそ、そうですか…\nありがとうございます", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5757, 120601, 4, 1, "ひゃあっ…！\nすみません、勉強に\n集中してたので\n驚いちゃいました", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5758, 120601, 101, 4, "一緒に勉強して立派な\n大人になりましょうね\n私がそばで見張ってて\nあげます", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5759, 120601, 102, 8, "まだ立派ではないかも\nですけど、私よりも\n大人なあなたのことは\n頼りにしてますからね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5760, 120601, 1, 1, "立派な大人は\n朝の時間の使い方が\n上手だと聞きました\nあなたはどうですか？", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5761, 120601, 2, 1, "ミミとミソギと\nあなたと…大事な人と\n一緒のお昼ごはんは\nやさしい味がしますね", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5762, 120601, 3, 1, "うぅ…怖い話を\n思い出しちゃいました\n寝るまで一緒に\nいてくれますか…？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5764, 120601, 2, 1, "すごい座り心地…！\nとっても勉強が\nはかどりそう…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5765, 120601, 3, 1, "この子…なんだか\nミミに似てるような\n気がする…ふふっ♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5766, 120601, 4, 1, "いい香り…♪\n勉強で疲れた\n頭がリフレッシュ\nできそうです", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5767, 120601, 5, 1, "ぽかぽかだ…♪\nミミとミソギと\nあの人と一緒に\nいるときみたい", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5769, 120601, 7, 1, "いっぱい食べたら\n早く大人になれたり\nしないかな…？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5770, 120601, 8, 1, "あう…\n知らない単語が…\nちゃんと調べて\n覚えなくっちゃ…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5773, 120401, 1, 1, "うさぎさんが\nぴょ～んぴょん♪\nみんなえがおで\nるんたった～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5774, 120401, 2, 1, "キョウカちゃんも\nいつかにんじんさんを\n好きになってくれたら\nうれしいな～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5775, 120401, 3, 1, "今日のミソギちゃんの\nイタズラ、びっくり\nしちゃったけど\nおもしろかったな～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5776, 120401, 4, 1, "このおうち、いつも\nいろんな人がいて\nドキドキぴょんぴょん\nって楽しいな～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5777, 120401, 5, 1, "いいお天気～♪\nお外でみんなと\nいっぱい遊びたいな♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5778, 120401, 101, 4, "ミソギちゃんも\nキョウカちゃんも\nおにいちゃんがいると\nずっと笑顔だね～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5779, 120401, 102, 8, "おにいちゃんにはやく\nあいたいな～♪\nまだかな、まだかな♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5780, 120401, 1, 1, "えへへ♪\nおにいちゃんに\nなでなでされると\nぽかぽかするね♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5781, 120401, 2, 1, "聞いて、おにいちゃん\nさっきね、かわいい\nちょうちょさんに\n会ったんだよ～♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5782, 120401, 3, 1, "ふわっ…！\nおにいちゃんの顔を\nお絵かきしてたから\nびっくりしちゃった♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5783, 120401, 4, 1, "おにいちゃんも\n遊びたいの？\nそれじゃあ二人も\n呼んで遊ぼう♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5784, 120401, 101, 4, "おにいちゃんも\nみんなも、優しい人\nばっかりで\nミミ幸せ～♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5785, 120401, 102, 8, "おにいちゃん♪\n高い高いしてほしいな\nすっごくぴょんぴょん\nできて、好きなんだ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5786, 120401, 1, 1, "おはよう～♪\nおにいちゃん、今日は\nおひさまみたいに\nきらきら元気だね～♪", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5787, 120401, 2, 1, "おひるごはんだね～♪\nにんじんさんを\nいっぱい食べて\n元気いっぱい♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5788, 120401, 3, 1, "ふわあ…\n眠くなってきちゃった\nおにいちゃん、\n一緒に寝よう～？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5790, 120401, 2, 1, "ふわぁ、気持ちいい♪\nずっとこうやって\n座ってたくなるね～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5791, 120401, 3, 1, "わあ、かわいい～\nぎゅ～っ、ぎゅ～っ♪\nこれで仲よしだね\nえへへ♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5792, 120401, 4, 1, "気持ちいいにおい～♪\nぴょんぴょんしたく\nなってきちゃった♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5793, 120401, 5, 1, "あったかぽかぽか♪\nおにいちゃんも\nきてくれたら、もっと\nあったかくなりそう♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5795, 120401, 7, 1, "もぐもぐ…ごっくん♪\nおっきくなりたいから\nいっぱい食べるよ～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5796, 120401, 8, 1, "えへへ、おもしろ～い\nこの絵本ね、\nキョウカちゃんから\n教えてもらったんだ♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5799, 120501, 1, 1, "この部屋…探索の\nしがいがありそう！\nついでにイタズラも\nしかけちゃお♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5800, 120501, 2, 1, "ミミってああ見えて\n実は大胆で度胸も\nあるよね…ちょっと\nかっこいいな…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5801, 120501, 3, 1, "怖い話を聞いた時の\nキョウカって\n面白いんだよね～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5802, 120501, 4, 1, "にいちゃんが\n慣れちゃわないように\nまた新しいイタズラを\n考えなきゃ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5803, 120501, 5, 1, "イタズラもいいけど\nミミとキョウカと\nにいちゃんと、普通に\n遊ぶのも楽しいなぁ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5804, 120501, 101, 4, "ミミにキョウカ、\nそれににいちゃん！\nみんなそろえば愉快な\n冒険が始まりそう！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5805, 120501, 102, 8, "次はどんなイタズラを\nしたら、にいちゃんは\n喜んでくれるかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5806, 120501, 1, 1, "今ミソギに触ったね？\nヤバいよにいちゃん…\n逃げないとトラップが\n発動しちゃうよ…！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5807, 120501, 2, 1, "わわっ！？　びっくり\nしたあ…！　新しい\nトラップを作ってて\n気づかなかったよ～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5808, 120501, 3, 1, "お？　にいちゃん、\nイタズラする側にも\n興味があるの？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5809, 120501, 4, 1, "にいちゃんなにか用？\nあ、もしかして…\nミソギと遊びたく\nなっちゃった？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5810, 120501, 101, 4, "ベストタイミングだよ\nにいちゃん！\n今からみんなで探検に\n行くところなんだ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5811, 120501, 102, 8, "にいちゃんってさ\nホントに優しいよね\nなにしても怒らないし\n…ありがとね♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5812, 120501, 1, 1, "にいちゃんおはよー！\n景気づけに本日\n一発目のイタズラ、\nやっちゃうよ～♪", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5813, 120501, 2, 1, "お昼ごはんはロシアン\nサンドイッチだよ！\nさあ、にいちゃん！\n好きなの選んでよ！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5814, 120501, 3, 1, "夜ってなんだか\nワクワクしてなかなか\n眠れないんだよね\nにいちゃんはどう？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5816, 120501, 2, 1, "この座り心地のよさ…\nなにかのトラップに\nうまく使えないかな？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5817, 120501, 3, 1, "こういうのに細工して\nミミやキョウカを\n驚かせると、いい反応\nするんだよね♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5818, 120501, 4, 1, "においをうまく使った\nイタズラって…\nなにかないかな？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5819, 120501, 5, 1, "あったまりながら\n新しいイタズラを\n考えると、いい案が\n出て来たりするんだ♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5821, 120501, 7, 1, "おいしくてついつい\n食べ過ぎちゃうなぁ…\nはっ…！？　これって\nまさかトラップ…！？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5822, 120501, 8, 1, "このキョウカから\n借りた冒険物語\nすっごく\nおもしろいんだよね！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5825, 120801, 1, 1, "晴れ着を着ると\nなんだか心が躍るね\n足取りも軽く\nなるってもんさ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5826, 120801, 2, 1, "正月の食べ物ってのは\n美味いもんだね\nいくらでも\n食べれちまうよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5827, 120801, 3, 1, "普段は好き勝手してる\nアタシらも\n正月くらいは\n大人しくするさ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5828, 120801, 4, 1, "年越しそばに雑煮\nこの季節は\n温かいものが\n体に沁みるね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5829, 120801, 5, 1, "正月だからって\nのんびりしすぎたね\nちょっと釣りにでも\n行こうかね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5830, 120801, 101, 4, "いい正月だね\nいつかあの子と\n一杯やりたいもんだよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5831, 120801, 102, 8, "大事な人と過ごす\n正月ってのがこんなに\n楽しいもんだとはね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5832, 120801, 1, 1, "部屋があったかくて\n顔が火照っちまったよ\nちょいと外に涼みに\n行かないか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5833, 120801, 2, 1, "餅ってのは\n何をつけてもうまいね\nお前さんは\nどの味が好きだい？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5834, 120801, 3, 1, "お祝いの鯛が\n足りないって？\nそれじゃあアタシが\n釣ってきてやるよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5835, 120801, 4, 1, "おせちが美味いのは\n分かるけど\n食べ過ぎには\n気を付けるんだよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5836, 120801, 101, 4, "お前さんのおかげで\nいい正月を\n迎えられたよ\nありがとさん！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5837, 120801, 102, 8, "慣れない晴れ着も\nお前さんに見て\nもらえたなら\n着たかいがあったね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5838, 120801, 1, 1, "おはようさん\n寒いのは分かるけど\nいい加減布団から\n出て顔洗ってきな", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5839, 120801, 2, 1, "今日の昼飯は雑煮だ\nアタシの郷土の味\nしっかり味わって\nおくれよ", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5840, 120801, 3, 1, "おや、お前さんも\n夜ふかしかい？\n退屈してたところさ\n付き合っておくれよ", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5842, 120801, 2, 1, "晴れ着だと座り方にも\n気を遣うね\nしわになったら大変さ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5843, 120801, 3, 1, "せっかくだし\nこの子にも晴れ着を\n着せてあげたいね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5844, 120801, 4, 1, "これは冬の花の香り…\n風情があって\nいいじゃないか", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5845, 120801, 5, 1, "あったかいねぇ…\nこんな寒い日には\nまさに天国だよ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5847, 120801, 7, 1, "いつも食べてる\nものでも正月に\n食べると\n新鮮な気がするね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5848, 120801, 8, 1, "せっかくの正月休みだ\nたまにはゆっくりと\n本でも読もうかね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5850, 120701, 1, 1, "新年のあいさつ…\n私も街のみんなに\nしてみようかしら…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5851, 120701, 2, 1, "正月はゆっくり過ごす\nのよね…ゆっくりって\n何したらいいのかしら", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5852, 120701, 3, 1, "この格好も\n慣れてくれば\n動きやすいわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5853, 120701, 4, 1, "う～ん、今年の抱負…\nやりたいことが\nたくさんあるのよね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5854, 120701, 5, 1, "おせち料理\nネビアの分も\n用意しなくちゃね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5855, 120701, 101, 4, "ふふっ\n正月っていいわね\nみんな楽しそうだもの", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5856, 120701, 102, 8, "新年の思い出…\nこれからもっと作るわ\nそれが私の抱負よ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5857, 120701, 1, 1, "今日は家で過ごすの？\nあなたと一緒なら\nきっと楽しいわね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5858, 120701, 2, 1, "どう？　この格好？\nコッコロさんは\n褒めてくれたけど…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5859, 120701, 3, 1, "ちょうど良かったわ\nあなたも大掃除\n手伝ってくれる？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5860, 120701, 4, 1, "これから料理するけど\nお雑煮と焼き餅\nどっちが好きかしら？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5861, 120701, 101, 4, "『今の私』にとって\n初めての正月だもの\nあなたと楽しまないと", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5862, 120701, 102, 8, "去年よりも今年…\nもっと成長するからね\n…お兄ちゃん♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5863, 120701, 1, 1, "おはよう\nせっかくの正月だもの\n朝から楽しまないとね", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5864, 120701, 2, 1, "ねぇ…\n良かったら正月の遊び\n教えてくれるかしら？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5865, 120701, 3, 1, "ふわぁ～…今日が\n終わっちゃうのが…\nもったいないわね…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5867, 120701, 2, 1, "ふぅ…お参り後だから\n心地よすぎて眠って\nしまいそうだわ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5868, 120701, 3, 1, "ふふっ\nかわいいものって\n癒されるわね…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5869, 120701, 4, 1, "…ん\nこれが梅の香り？\n…いい匂いね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5870, 120701, 5, 1, "キャルじゃないけど…\nこのまま丸く\nなりたいところね…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5872, 120701, 7, 1, "はむはむっ…♪\nせっかくの正月だもの\n堪能しなくちゃね♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5873, 120701, 8, 1, "…ふんふん\n帯の結び方って\nこんなにあるのね…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5875, 104201, 103, 12, "今の私が作れる\n最高の一曲…\nあの人に届くといいな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5876, 104201, 103, 12, "あの、歌を…\n聞いてくれませんか？\nあなたに贈る\n特別な歌を", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5877, 120901, 1, 1, "ひい、ふう…\nむむ、みなに配る\nお年玉のポチ袋が\n残りわずかなのじゃ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5878, 120901, 2, 1, "宴の準備はわらわに\n任せておくのじゃ！\n支配者の手腕、\n見せつけてくれる！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5879, 120901, 3, 1, "新年の願掛けなら\nわらわにすればよい\n支配者に頼むほうが\n確実じゃろう？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5880, 120901, 4, 1, "みなからもらった\nこの年賀状とやら…\nわらわも\n書いてみようかの♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5881, 120901, 5, 1, "正月飾りにもいろいろ\n種類があるんじゃの～\nどれも華々しくて\nわらわ好みじゃ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5882, 120901, 101, 4, "あやつへのお年玉は\nちと多めにしておくか\n…日ごろの感謝も\n込めてのう", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5883, 120901, 102, 8, "あやつのことを想うと\nそれだけで行く年\n来る年が楽しみになる\nふふ、愉快なやつじゃ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5884, 120901, 1, 1, "みなに晴れ着を\n褒められて、とても\nよい気分なのじゃ♪\n次はお主の番じゃぞ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5885, 120901, 2, 1, "お主へのお年玉は\nいちばん最後じゃ！\n残り物には福が宿る\nそうじゃからのう", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5886, 120901, 3, 1, "この季節は手が\nかじかむのう…ほれ、\nお主！　わらわの手を\nあたためるのじゃ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5887, 120901, 4, 1, "お主のために雑煮を\n手作りしてみたのじゃ\n食べていくがよい♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5888, 120901, 101, 4, "眷属のお主に\n正月休みはまだ早い！\nずっとわらわのそばに\n控えておくのじゃ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5889, 120901, 102, 8, "ふふ、これほど楽しい\n正月は初めてじゃ…\nお主と過ごす時間は\nいつだって特別じゃな", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5890, 120901, 1, 1, "早起きしてわらわを\n待っておったのか？\nさすがはわらわの眷属\n殊勝な心がけじゃな♪", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5891, 120901, 2, 1, "食事どきじゃな\n正月の宴会料理が\nわらわたちを\n待っておるぞ♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5892, 120901, 3, 1, "夜も更けてきたのう\n冬の夜は長い…\n心ゆくまでお主と\n過ごせるわけじゃな♪", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5894, 120901, 2, 1, "実に心地がよい♪\nこのまま寝正月も\n悪くないかも\nしれぬのう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5895, 120901, 3, 1, "愛いやつじゃのう\nわらわのそばに置いて\nやってもよいぞ♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5896, 120901, 4, 1, "品のある香りじゃのう\n寝室に置いておけば\nなにかご利益に\nあずかれそうじゃな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5897, 120901, 5, 1, "心地よい…\nわらわはしばらく\nここから動かぬぞ\nお主も付き合うがよい", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5899, 120901, 7, 1, "舌鼓が鳴るのう♪\n正月の宴には\n持ってこいの品じゃ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5900, 120901, 8, 1, "ほう、新春にはそんな\n習わしが…正月を\n制するには、まだまだ\n勉強が必要じゃのう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5902, 121001, 1, 1, "久しぶりの我が家って\n感じです！　やっぱり\nここが一番ですね〜！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5903, 121001, 2, 1, "さあ食べましょう！\nみんなで囲める食卓を\n心から味わいますよ☆", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5904, 121001, 3, 1, "ランドソルに\n帰ってきた途端に\nお腹が空きました〜！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5905, 121001, 4, 1, "ただいま☆\n幸せ特盛りの\nわたしの日常！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5906, 121001, 5, 1, "ようやくランドソルに\n帰ってきたことですし\nやっぱり魔物料理を\nいただきましょう☆", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5907, 121001, 101, 4, "あの人との絆を\n守るために\nもっともっと食べて\n強くならなくちゃ☆", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5908, 121001, 102, 8, "いつもありがとうって\nあの人を思いっきり\n抱きしめたいです☆", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5909, 121001, 1, 1, "あなたの想いは\nこの身をかけて\n全力で受け止めます！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5910, 121001, 2, 1, "おかえりなさい☆\nここにいるみんなも\nあなたの帰りをずっと\n待っていましたよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5911, 121001, 3, 1, "このギルドハウスで\n冒険の疲れを\n癒しましょう〜！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5912, 121001, 4, 1, "あなたの想いが\n全力全開を超える\n力をくれました！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5913, 121001, 101, 4, "日常が続く幸せを\nあなたと一緒に\nかみしめたいです☆", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5914, 121001, 102, 8, "ぎゅ〜っ！\nわたしたちは\nどんなことがあっても\nずっと一緒ですよ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5915, 121001, 1, 1, "おはようございます！\nあなたと過ごせる\n幸せを、今日も\n味わいますよ〜！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5916, 121001, 2, 1, "ご飯の時間ですよー！\nあなたも一緒に\n食べましょうね！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5917, 121001, 3, 1, "明日もあなたが隣に\nいてくれることを\n心から願ってますよ\nおやすみなさい☆", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5919, 121001, 2, 1, "ふい〜…\n安心できる場所で\nくつろげるって\n幸せですね〜…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5920, 121001, 3, 1, "じゅるり…\n食べたくなっちゃう\nくらい、かわいい☆", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5921, 121001, 4, 1, "くんくん…おおー！\nステーキの香り！\nやばいですね☆", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5922, 121001, 5, 1, "体がぽかぽかしてきて\nわたしの食欲も\n燃えてきましたよー！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5924, 121001, 7, 1, "わたしの食欲に\nついてこられますか？\nまだまだ食べますよ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5925, 121001, 8, 1, "これがキャルちゃんの\n言っていた、キスで\n目覚める王子様の\nおとぎ話ですか〜☆", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5927, 121201, 1, 1, "自分で自分を\n分析するのも、これは\nこれで面白い…♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5928, 121201, 2, 1, "強過ぎる力の扱いには\n注意しないと…\n七冠がいい例だね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5929, 121201, 3, 1, "少年の交友関係は\n計り知れないな…\nアタシも含むのか！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5930, 121201, 4, 1, "原理を徹底解明して\n再発を防ぐぞー！\n…誰か手伝って～！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5931, 121201, 5, 1, "これは祝福にも\n呪いにもなる…\n忘れずにいよう", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5932, 121201, 101, 4, "少年が\n目を覚ましてくれて\n本当によかった…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5933, 121201, 102, 8, "彼以外を選んでいたら\n今回の事態は\n乗り切れなかったな…\nさすがだよ、少年", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5934, 121201, 1, 1, "少年！　調子はどう？\nクレープ食べられる？\nっていうか食べて？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5935, 121201, 2, 1, "元気そうで何よりだ\nまた店を手伝いに\nきてよ。二人もキミに\n会いたがってたしさ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5936, 121201, 3, 1, "キミの診察もあるし\n今はなるべくそばに\nいるよ。迷惑じゃ\nなければだけど", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5937, 121201, 4, 1, "やっぱり少年は\nあちこちで人を\n助けてる時が\n一番輝いてるよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5938, 121201, 101, 4, "アタシは大したこと\nしてないけど\nみんなのことは\n褒めてあげてね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5939, 121201, 102, 8, "みんなを笑顔に\nできるのは\nやっぱり少年だね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5940, 121201, 1, 1, "やあ少年\n朝寝坊はしてない？\nそれじゃ、よい１日を", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5941, 121201, 2, 1, "おや、少年！\nまかないのクレープ\n作ったけど、食べる？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5942, 121201, 3, 1, "ふわぁ…もう夜か…\n研究してたら\nあっという間だね…\nおやすみ、少年", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5944, 121201, 2, 1, "体がなまってたのか\n前線に出ると疲れる…\nまさか…年のせい！？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5945, 121201, 3, 1, "キミたちも今の\nアタシと同じ感じに\nアレンジしてあげよう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5946, 121201, 4, 1, "誰が置いたんだろう？\nいい香りだねぇ…\nうん、気に入った♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5947, 121201, 5, 1, "何も考えてない時間も\n実は思考にとって\n大事なんだよね～…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5949, 121201, 7, 1, "食の大切さは\nいつの食事からも\n感じられるね♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5950, 121201, 8, 1, "ふむ…面白い！\n今度全巻\n借りにこようっと", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5952, 116401, 1, 1, "聖学祭か…\n良い記憶はないな\nよし忘れよう", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5953, 116401, 2, 1, "健全なる精神は\n健全なる肉体に宿る…\nはっ、知ったことかよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5954, 116401, 3, 1, "暇だし鏡でも見るか…\nえへへ、ユニだよ♪\nよし子供料金いけるな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5955, 116401, 4, 1, "アオイ君は元気に\nしているだろうか…\n彼女は人一倍ぼくに\n懐いていたからな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5956, 116401, 5, 1, "そろそろシズル君は\n姉という幻想から\n目覚めただろうか…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5957, 116401, 101, 4, "ぼくは来年も聖学祭に\n行くさ…卒業しても\n君らとは永遠だ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5958, 116401, 102, 8, "最近同志の登校率が\n減少傾向だな…\nまあ仕方ないのだが…\nないのだが…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5959, 116401, 1, 1, "ほわあああ！\nおい急に話しかけるな\n学びがこぼれる", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5960, 116401, 2, 1, "この体操着、周囲から\n大層評判が良いのだよ\nたいそうだけにな\nどやっ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5961, 116401, 3, 1, "何を着ていようと\nぼくはぼくさ\n外見など所詮は\n魂の識別符に過ぎん", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5962, 116401, 4, 1, "救護院のアヤネ君が\n最近ぼくの自称年齢に\n疑念を持ち始めてな…\nあの熊…殺るか…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5963, 116401, 101, 4, "うん？　なんだね？\nなに、用は無い？\n用も無いのに呼ぶとは\nもしや…これが友達！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5964, 116401, 102, 8, "この服が似合ってる？\nな、なんだね突然…\nやめたまえよでゅふふ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5965, 116401, 1, 1, "ふぁ～あ…\n朝も更けてきたな…\nそろそろ寝るか…", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5966, 116401, 2, 1, "ふぁ～あ…もう昼だ…\n人間の起きている\n時間ではないな…\n早く寝なくては…", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5967, 116401, 3, 1, "うーん、よく寝た！\n気持ちのいい夜だな\n今日も一日がんばるぞ", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5969, 116401, 2, 1, "ほわぁ～…\nもしやこれは都で噂の\n人を駄目にするやつ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5970, 116401, 3, 1, "ふむ、愛くるしいな\nだが似合い過ぎる…\nぼくが持つと我ながら\n子供にしか見えん", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5971, 116401, 4, 1, "アロマか、興味深いな\nミツキ印ポーションも\n気化させて鼻腔から\n吸入してみるか…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5972, 116401, 5, 1, "んふ、んふふ…\n火ってイイよね…\nずっと見てられるね…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5974, 116401, 7, 1, "や、やめろ…\nとめどなく固形食物を\n生産するのはやめろ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5975, 116401, 8, 1, "この可愛げのない筆致\nやはり作者は理系か！\nぐああ目が理系になる", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5977, 121101, 1, 1, "…あいつのためなら\n無理くらいいくらでも\nしてやるわよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5978, 121101, 2, 1, "この姿、\n魔力の制御ムズ過ぎ…\nま、あたしは優秀だし\nどうとでもなるけど！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5979, 121101, 3, 1, "あいつのこと、\n全部受け入れるって\n決めたから！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5980, 121101, 4, 1, "今なら誰だろうと\n負ける気がしないわ\nドラゴンでもなんでも\nかかってこいっての！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5981, 121101, 5, 1, "散々助けられた分、\n今度はあたしが\nあいつのこと助けるわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5982, 121101, 101, 4, "あいつとの\nこれまでの冒険は\nどれも、あたしの\n大切な思い出…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5983, 121101, 102, 8, "もしまたあいつが\n寝たままになったら\nなんだってするわ\nキ…キスでも…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5984, 121101, 1, 1, "なに、お腹空いたの？\nなら、おいしいご飯を\n食べに行きましょ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5985, 121101, 2, 1, "やっぱ、\nあんたのアホ面見ると\n安心するわ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5986, 121101, 3, 1, "【美食殿】はあんたと\nぺコリーヌがいなきゃ\n始まんないっての", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5987, 121101, 4, 1, "ひゃあっ！？\n急に声かけないで！？\n魔力が暴発しても\n知らないわよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5988, 121101, 101, 4, "…あんたとこうして\nぼーっと過ごす時間、\nまあ…嫌いじゃないわ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5989, 121101, 102, 8, "あんたが信じて\n見守っててくれれば…\nあたし、\nなんだってできるわ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5990, 121101, 1, 1, "おはよ\n早く顔洗いなさいよ、\nこのねぼすけ♪", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5991, 121101, 2, 1, "今日のお昼は\nどこで食べる？\nあんたが\n決めていいわよ", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5992, 121101, 3, 1, "もしあんたがまた\n夢に囚われても\nあたしが助けるわ\n安心して寝なさいよ", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5994, 121101, 2, 1, "気持ちいい～…♪\nあいつもたまには\nこうして休ませなきゃ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5995, 121101, 3, 1, "一目惚れしたかも…！\nこの子、\n部屋に飾りたいわー♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5996, 121101, 4, 1, "へぇ、あいつって\nこういう匂いが\n好きなのかしら…？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5997, 121101, 5, 1, "暖かくて最高…♪\n丸くなっちゃいそう…\nうにゃあ…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (5999, 121101, 7, 1, "イケるわね、これ！\nやっぱり食べるって\n大事よねー♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6000, 121101, 8, 1, "ファーストキスは\nどんな味、ね…\nふ、ふ～ん…？\nなるほど…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6002, 118101, 1, 1, "お友だち…\nできるかしら…？\nこんなわたしでも…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6003, 118101, 2, 1, "あまり、きょろきょろ\nしちゃダメよね…\n変な子だと、\n思われてしまうわ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6004, 118101, 3, 1, "広い場所、\n落ち着かない…\nはぅ…どこにいれば…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6005, 118101, 4, 1, "～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6006, 118101, 5, 1, "【レイジ・レギオン】\n…またいつか、\nみんなと…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6007, 118101, 101, 4, "…一度でいいから、\nわたしもママって…\nあっ、その…\n…な、なんでもないわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6008, 118101, 102, 8, "ここが、今のわたしに\nとっての帰る場所…\n…そう思っても、\nいいのかしら…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6009, 118101, 1, 1, "ええと…\nお腹、空いてる？\nおねむじゃない？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6010, 118101, 2, 1, "ここにいると\n邪魔かしら…？\nならもっと隅っこに…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6011, 118101, 3, 1, "お喋り…？\nわたしでいいの…？\nうれしい…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6012, 118101, 4, 1, "あなたの好きな歌…\n教えて…？\n練習、するから…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6013, 118101, 101, 4, "ここは賑やかだけど…\nうるさいとは感じない\nむしろ、心地いい…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6014, 118101, 102, 8, "安心、して…\nわたしの歌で、\nあなたを守るから…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6015, 118101, 1, 1, "ちょっとだけ…\n朝は、苦手…\n…ふぁ…", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6016, 118101, 2, 1, "お昼は、食べた？\n良かったら、一緒に…\n少し…焦げてるけど…", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6017, 118101, 3, 1, "眠れないのかしら…\nじゃ、じゃあ…\n子守歌…聴く？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6019, 118101, 2, 1, "こんな特等席…\n恐縮、です…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6020, 118101, 3, 1, "い、今のうちに、\nこの子でお世話の\n練習を…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6021, 118101, 4, 1, "いい香り…\n…つらい気持ちも\n癒してくれる", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6022, 118101, 5, 1, "あったかい…\nいつまでも、\nこうしていられそう…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6024, 118101, 7, 1, "おいしく出来てる…\nすごいわ…\n見習わなくっちゃ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6025, 118101, 8, 1, "『赤ちゃんとママの\nスキンシップ入門』…\n…べ、勉強になるわ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6027, 121301, 1, 1, "きれいなお部屋…！\n立派な舞台の\nセットみたい…♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6028, 121301, 2, 1, "お芝居では私が先輩\nなんだから、しっかり\n私がミサキちゃんを\nエスコートしなきゃ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6029, 121301, 3, 1, "～～♪\n歌うお芝居も多いから\nもっと上手く\nなりたいな…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6030, 121301, 4, 1, "ここにいる人たちと\n仲良くなりたいな…\nそしたらきっと演技の\n幅も広げられるよね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6031, 121301, 5, 1, "機会があったら\nアヤネちゃんとも\nお芝居してみたいな♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6032, 121301, 101, 4, "お兄ちゃんに褒めて\nもらえるように\nお芝居の練習\nいっぱいしなきゃ…♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6033, 121301, 102, 8, "あう…お兄ちゃんを\n意識したら、台詞が\n飛んじゃったよぅ…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6034, 121301, 1, 1, "あっ、お兄ちゃん…♪\nお芝居の練習を\n見てくれませんか…？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6035, 121301, 2, 1, "いまの発声練習\nどうでしたか？\nひゃん…！？\n頭は撫でないでぇ～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6036, 121301, 3, 1, "ミサキちゃんも\nお芝居、とっても\n上達してるんですよ\n私も負けられません♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6037, 121301, 4, 1, "また物語を聞かせて\nほしい、ですか…？\n私なんかでよければ\nもちろんよろこんで♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6038, 121301, 101, 4, "新しいお話を\n考えてみました\n聞いてくれますか…？\nよかった…♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6039, 121301, 102, 8, "いつか…舞台上じゃ\nなくても、照れずに\nお兄ちゃんに気持ちを\n伝えてみせますね…♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6040, 121301, 1, 1, "おはようございます♪\nマヒルさんにもらった\nミルク、一緒に\n飲みませんか？", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6041, 121301, 2, 1, "牧場のみなさんに\nドングリパンを\nいただきました～\nおひとつどうぞ♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6042, 121301, 3, 1, "寝る前にまたココアを\n飲みながら、お話を\n聞いてもらえますか？\nえへへ、うれしいな♪", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6044, 121301, 2, 1, "お芝居の練習の\nあとは、ここで\nお兄ちゃんと\nゆっくりしたいな♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6045, 121301, 3, 1, "今日も演技の\n練習に付き合ってね♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6046, 121301, 4, 1, "素敵な香り…\nこのイメージも\nお芝居に\n活かせないかな…？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6047, 121301, 5, 1, "私もお芝居で\nみんなの心を\nぽかぽか温められる\nようになりたいな…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6049, 121301, 7, 1, "舞台に立つんだから\n食べ過ぎないように\n気をつけないと。\nでも…おいしいなぁ♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6050, 121301, 8, 1, "このお話、面白い…\n脚本を書く\n参考にもなりそう…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6052, 107501, 103, 12, "えへへ…♪\n二人で見た夕日…\nキレイでしたね…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6053, 107501, 103, 12, "これから泳ぎの\n練習ですか？\n付き合いますよ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6054, 107601, 103, 12, "主さまを導くことで\nわたくしも未知なる\n領域へと辿り着けます", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6055, 107601, 103, 12, "主さまとなら\n海よりも深く\n空よりも高い場所へも\n目指せそうです", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6056, 107801, 103, 12, "あいつと二人で\n夏の遠い海に…って\nあたしってば\nなに考えてるのよ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6057, 107801, 103, 12, "あたしが楽しく\n過ごせるのはあんたが\nいてくれたからよ\nか、感謝してるわ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6058, 121401, 1, 1, "はぁ…アタシってば\nこんなにかわいくって\nキレイなんて罪よね～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6059, 121401, 2, 1, "舞台の衣装を\nクリーニング\nしとかなくちゃ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6060, 121401, 3, 1, "舞台映えするメイク…\nスズナなら\n知ってるかしら？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6061, 121401, 4, 1, "物産展の会計係と\n山盛りの算数の宿題…\nどっちが簡単かしら…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6062, 121401, 5, 1, "牧場と温泉の仕事に\nお芝居の練習に…\nちょ、ちょっと休憩～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6063, 121401, 101, 4, "お客さんの視線も\nうれしいけど…\nあいつも見てて\nくれてるかしら？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6064, 121401, 102, 8, "もっともっと\nセクシーになって\nあいつをビックリ\nさせてやるわ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6065, 121401, 1, 1, "アンタも休憩？\nしっかり働くために\n休むのも大事よね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6066, 121401, 2, 1, "ふふん♪　アタシも\n料理ができるように\nなったんだから！\n簡単なのだけだけど…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6067, 121401, 3, 1, "温泉ってお肌に\nいいのよね？\nもっとキレイに\nなったらどうしよう〜", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6068, 121401, 4, 1, "物産展の荷物は\nこれでよし…っと\nそれじゃアンタも\n運ぶの手伝ってよね♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6069, 121401, 101, 4, "いつもと違うアタシに\n見とれちゃった？\n仕方ないわね～♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6070, 121401, 102, 8, "お花見って風流で\n大人って感じ♪\n…でもお団子も\n忘れないでよね！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6071, 121401, 1, 1, "みんなに朝ごはんを\n作ってみたら\n喜んでくれるかしら？", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6072, 121401, 2, 1, "お客さんがいっぱい\n来る時間帯ね！\n元気出していくわ！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6073, 121401, 3, 1, "そろそろ寝ないと…\n睡眠不足はお肌の\n大敵なんだから", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6075, 121401, 2, 1, "ふかふかしてて\n気持ちいい～♪\n疲れが癒されるぅ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6076, 121401, 3, 1, "この子もアタシと\nおそろいの衣装に\nしてあげようかしら♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6077, 121401, 4, 1, "素敵な衣装で\nいい香りを\n身にまとう…ふふ、\n大人のたしなみよね♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6078, 121401, 5, 1, "あったか～い♪\nここでお昼寝したら\n気持ちよさそう…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6080, 121401, 7, 1, "お仕事がんばると\nいつもよりお腹\n空いちゃう～！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6081, 121401, 8, 1, "最新のトレンド\n載ってないかしら？\n物産展の売り物に\n取り入れなくちゃ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6083, 121501, 1, 1, "怪盗のこと…\nお父さんお母さんに\nもし知られたら\nなんて説明しよう…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6084, 121501, 2, 1, "たとえ怪盗でも、人の\n家にお邪魔するときは\n身だしなみをしっかり\nしないと…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6085, 121501, 3, 1, "鍵開けって難しい…\nうーん、もっと\n練習しておかなくちゃ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6086, 121501, 4, 1, "姿が見えない\n怪盗の噂…？\nわ、私のことじゃ\nありませんように…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6087, 121501, 5, 1, "予告状って…\n切手はどこに貼れば\nいいんだろう…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6088, 121501, 101, 4, "怪盗活動にも\n慣れてきたけど…\nやっぱり先輩が\nいないと心細いなぁ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6089, 121501, 102, 8, "せ、先輩の心も\nいつか盗めるかな…？\n…わわわ、私ってば\nなに言ってるんだろ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6090, 121501, 1, 1, "怪盗が大活躍って\n新聞で読んだ…？\nだっ、誰のこと\nでしょうね…あはは", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6091, 121501, 2, 1, "尾行に潜入…\n存在感がないのって\n意外と役立つことも\nあるんですね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6092, 121501, 3, 1, "せせせ、先輩っ！\n帽子をぎゅーって\nかぶったら、抜けなく\nなっちゃいました…！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6093, 121501, 4, 1, "ぬ、盗み聞きなんて\nしてませんよ？\nみんなが私に気づかず\n話し始めるだけで…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6094, 121501, 101, 4, "秘密道具が見たい？\nそ、それは内緒です！\n秘密道具ですから…\nで、でも先輩になら…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6095, 121501, 102, 8, "えへへ\n見つかっちゃいました\n先輩はいつも私を\n見つけてくれますね…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6096, 121501, 1, 1, "おはようございまふ…\n秘密道具のお手入れで\n夜更かししちゃって…\nちょっと眠たいです～", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6097, 121501, 2, 1, "怪盗が昼に活動する…\n案外そのほうが\n意外性が\nあるのかも…？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6098, 121501, 3, 1, "外が暗くなりましたね\n…そろそろ怪盗の活動\n始めましょうか？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6100, 121501, 2, 1, "ここでくつろぐと\n疲れが取れます～\n…今日も怪盗\nがんばろっと", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6101, 121501, 3, 1, "わぁ、かわいい…！\n心を盗まれるって\nこういうことかな…？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6102, 121501, 4, 1, "素敵な香り…でも\n服に香りがついたら\n隠れてるときに\n見つかっちゃうかな…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6103, 121501, 5, 1, "ぽかぽかで\nいい気持ち…\n夜道を歩く前に\n温まっておこうっと…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6105, 121501, 7, 1, "怪盗活動に備えて\n今のうちにしっかり\n食べておかなきゃ…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6106, 121501, 8, 1, "女の子は秘密が\n多いほうが魅力的…？\nそ、そうなんだ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6108, 104501, 103, 12, "ドＳさんはいつも\nクウカの妄想の中に…\nぐふふ、じゅるり～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6109, 104501, 103, 12, "やさしい触り方も\n嫌いじゃありません…\nは…恥ずかしいですが", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6110, 121601, 1, 1, "この大怪盗さまに\n盗めないものなんて\nないです！　たぶん！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6111, 121601, 2, 1, "あたしにふさわしい\nビッグなお宝は\nどこにあるですかね～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6112, 121601, 3, 1, "そろーりそろーり…\n気配を消せばお菓子も\n食べ放題…にやり", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6113, 121601, 4, 1, "またサボってたのが\nバレたです…\nボスの地獄耳～！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6114, 121601, 5, 1, "ギャングで大怪盗…！\nこの肩書の多さ！\nまさに有能の証です！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6115, 121601, 101, 4, "お兄さんだったら…\nお宝を山分けして\nあげてもいいですよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6116, 121601, 102, 8, "お兄さんはアレですよ\n大怪盗イノリさまの\n相棒みたいなものです", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6117, 121601, 1, 1, "何事も用心深さが\n大切ですよ。ビ…\nビビりじゃないです！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6118, 121601, 2, 1, "ぬ、盗み聞き？\nしてないですよっ？\nぴゅーぴゅーぴゅー", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6119, 121601, 3, 1, "情報が欲しければ\nお菓子と交換ですよ\nさぁ、交換交換っ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6120, 121601, 4, 1, "しーっ！\n今、尾行中なんです\n話はあとで…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6121, 121601, 101, 4, "お兄さんと話してると\n時間が速いような…？\nこの時間泥棒！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6122, 121601, 102, 8, "ちょうどよかった！\nいい話があるんです\n一口乗りませんか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6123, 121601, 1, 1, "おてんとうさまが\n出てやがるですね…\n悪事がやりにくいです", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6124, 121601, 2, 1, "もうこんな時間ですか\nご飯にお昼寝…\nいやー忙しいです♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6125, 121601, 3, 1, "ふっふっふ！　夜こそ\n大怪盗の時間です！\n……ふぁあ", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6127, 121601, 2, 1, "うーん…\n警備の目がないと\n気楽でいいですねぇ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6128, 121601, 3, 1, "くう…！\nこのかわいさは\n盗めないです…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6129, 121601, 4, 1, "犯行現場に残ったのは\n香水の匂いだけ…\nふふ、おしゃれ大怪盗", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6130, 121601, 5, 1, "意外とこういう場所に\n隠し扉があったり…\nしないです？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6132, 121601, 7, 1, "もぐもぐ…\nこの美味しさ…\nとんでもない罠ですね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6133, 121601, 8, 1, "むむむ…ちっとも\n分からないです…\nほとんど暗号ですよ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6135, 121701, 1, 1, "あら、サレンさんは\nどこに行かれた\nのかしら？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6136, 121701, 2, 1, "ふんふんふーん♪\nなんだか華やいだ\n気分ですわ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6137, 121701, 3, 1, "さて…次の事業は\nどうしましょう\n何か面白いことは…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6138, 121701, 4, 1, "おーっほっほっほ！\nあら…楽しくて、つい\n笑ってしまいましたわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6139, 121701, 5, 1, "センスがあって\nいいお部屋ですわね\n私も見習いたいですわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6140, 121701, 101, 4, "サレンさんとあのお方\n仲がいいですわね\nも、もちろん仲がいい\nのはよいことですわ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6141, 121701, 102, 8, "どんなときでも\n正々堂々！\nそれが私の\n誇りですわ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6142, 121701, 1, 1, "ふふ、ごきげんよう\n私、絶好調でしてよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6143, 121701, 2, 1, "どうかされましたか？\n何かお困りごと\nでしたら\n是非力になりますわよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6144, 121701, 3, 1, "たまにはこうして\nゆっくりと\nお話しするのも\nいいものですわね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6145, 121701, 4, 1, "次の事業に関して\n考え事をしてましたの\nよければ相談に乗って\nくださるかしら？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6146, 121701, 101, 4, "よろしければ\nお話しませんこと？\nサレンさんのお話を\n聞かせてほしいですわ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6147, 121701, 102, 8, "ふふっ…\n先ほどサレンさんと\nあなたさまのお話を\nしていたところですわ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6148, 121701, 1, 1, "さあ！\n今日も一日\n全力で参りますわ！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6149, 121701, 2, 1, "お昼はどうしましょう\nサレンさんをお誘い\nしてみようかしら", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6150, 121701, 3, 1, "もう少しだけ…\n明日の準備をして\nおかないと…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6152, 121701, 2, 1, "はぁ～…いいですわね\nはっ、私としたことが\nはしたないですわね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6153, 121701, 3, 1, "かわいらしいですわ\nいくつになっても\nぎゅっと\nしたくなりますわね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6154, 121701, 4, 1, "落ち着きますわね\nサレンさんはどんな\n香りが好きなのかしら", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6155, 121701, 5, 1, "ふふっ、燃え盛る\n情熱の炎…\nいいですわね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6157, 121701, 7, 1, "このおいしさで\n元手がゼロ…\nすばらしい\n商品ですわ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6158, 121701, 8, 1, "これは…ふむふむ\n新しい事業のヒントに\nなりそうですわ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6161, 121801, 1, 1, "さて、と…\n今日は何をしなくちゃ\nいけないんだっけ？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6162, 121801, 2, 1, "やることは山積み\nだけど、手を動かす\nのは嫌いじゃないわ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6163, 121801, 3, 1, "あら？　アキノさんの\n声が聞こえたような\n気がしたんだけど…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6164, 121801, 4, 1, "アキノさんといると\nついこっちまで笑顔に\nなっちゃうのよね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6165, 121801, 5, 1, "弱音なんか\nはいてられないわ\nアキノさんに\n笑われちゃうもの", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6166, 121801, 101, 4, "気が付けばアキノさん\nのペースだけど…\n不思議と嫌では\nないのよね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6167, 121801, 102, 8, "あたしを好敵手って\n言ってくれる\nアキノさんのためにも\nしっかりしなくちゃ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6168, 121801, 1, 1, "どうしたの？\nアキノさんなら\nさっきそこに\nいなかった？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6169, 121801, 2, 1, "あたしなら大丈夫\n救護院だって\nみんなが助けて\nくれるからね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6170, 121801, 3, 1, "さっきアキノさんが\nあんたを探してたわよ\nなにかしたの？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6171, 121801, 4, 1, "こう見えてヒマじゃ\nないんだけど…\n少しくらいなら\n付き合ってあげるわ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6172, 121801, 101, 4, "ねえ、あんたと\nアキノさんって…\nううん、やっぱり\nなんでもないわ…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6173, 121801, 102, 8, "アキノさんも誘って\nお茶でもしない？\n話したいことが\nたくさんあるの♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6174, 121801, 1, 1, "早起きは三文の得よ\nいい一日を迎えるため\n目を覚まさなきゃ", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6175, 121801, 2, 1, "今日のお昼は…\nアキノさんと同席する\n予定だったわね\nあんたもどう？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6176, 121801, 3, 1, "ふぁ…っと、だめね\nたるんだところを\nアキノさんに\n見せるわけには", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6178, 121801, 2, 1, "ん～…\n落ち着くわね\nこのまま\n寝ちゃいそう…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6179, 121801, 3, 1, "救護院の皆も\nぬいぐるみが\n大好きなのよ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6180, 121801, 4, 1, "いい香りね…\nこれはアキノさんの\n趣味かしら？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6181, 121801, 5, 1, "こんなに激しく\n燃えているのに\nあったかくて落ち着く\nまるでアキノさんね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6183, 121801, 7, 1, "どんどん出てくる…\nこれ、救護院にも\nほしいわね…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6184, 121801, 8, 1, "ふむふむ…\n前にアキノさんが\n言ってたのは\nこのことかしら？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6187, 122001, 1, 1, "この衣装、私には\nちょっとかわい過ぎて\n恥ずかしいな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6188, 122001, 2, 1, "一部の人からは\n海賊より海賊らしいと\n評判の父です", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6189, 122001, 3, 1, "潮風で錆びないよう\n武器の手入れを\nしておきましょうか", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6190, 122001, 4, 1, "宝探しのお手伝いなら\n力になれると思います\n父のやる気次第ですが", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6191, 122001, 5, 1, "除霊は気を遣います\n一歩間違えると父まで\n成仏させてしまうので", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6192, 122001, 101, 4, "あの人と見た\n海の景色を…ずっと\n忘れずにいたいな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6193, 122001, 102, 8, "幽霊になっても\n一目会えるなら…\n私はきっとあの人に…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6194, 122001, 1, 1, "ボートを漕ぐの、少し\n上手になったんですよ\n今度お見せしますね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6195, 122001, 2, 1, "航海に向けた\n買い出しが必要ですね\n予定は空いていますか", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6196, 122001, 3, 1, "呪われた財宝の\n解呪依頼が多くて…\nふぅ…えっと次は…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6197, 122001, 4, 1, "夜の航海では\n父が明かり代わりに\nなってくれるんです", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6198, 122001, 101, 4, "船の上では食材が\n限られるので料理の\nいい練習になります", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6199, 122001, 102, 8, "この先もずっと\nあなたと一緒に\nたくさんの思い出を\n作っていきたいです", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6200, 122001, 1, 1, "あの人の寝顔…\nもう少しだけ\n眺めていたいな…", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6201, 122001, 2, 1, "船の上では\n規則正しい生活が\n重要ですから", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6202, 122001, 3, 1, "夜の海は幽霊が\n見分けやすいんです\n青白く光るので", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6204, 122001, 2, 1, "海に揺られるような\n心地よさですね\n気が休まります…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6205, 122001, 3, 1, "あなたも一緒に\n海を見に行きますか？\n…なんて。ふふっ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6206, 122001, 4, 1, "爽やかな香りですね…\n一面の青い海が\n目に浮かぶようです", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6207, 122001, 5, 1, "温かい…\n潮風で冷えた体に\n沁みますね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6209, 122001, 7, 1, "あ、おいしい…\n日持ちするなら船旅に\n持っていこうかな…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6210, 122001, 8, 1, "アンナさんに教えて\nもらった小説は…\nあ、これですね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6212, 102601, 103, 12, "なにもしないを\nしてるんだよ\nあ～いそがしいそがし", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6213, 102601, 103, 12, "はぁ～あんたといると\nどこまでも気持ちよく\nダラダラできるね～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6214, 121901, 1, 1, "暗黒騎士にして、\n伝説の海賊でもある\n私に不可能はないっ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6215, 121901, 2, 1, "海賊団の船員、\n随時募集中です…と\nこれで新たな盟友が\n増えるはずだ…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6216, 121901, 3, 1, "肩に乗せるペットが\nほしいな。今のうちに\nペット用品を買いに\n行かなくては…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6217, 121901, 4, 1, "財宝の地図はどこで\n売っているのだろう…\n古本屋？　古物商？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6218, 121901, 5, 1, "この海賊衣装に\n身を包んでいると\n前世の新たな記憶が\nよみがえってくるな！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6219, 121901, 101, 4, "いずれはシグルドと\n海原への冒険に\n繰り出したいな…\nきっと楽しいぞ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6220, 121901, 102, 8, "待ちわびたぞ盟友よ！\n私と貴様あっての\n海賊団…だからその…\n今日はそばにいてくれ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6221, 121901, 1, 1, "シグルド！　港町で\nお魚のおすそ分けを\nもらったんだ！\n一緒に食べよう！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6222, 121901, 2, 1, "シグルドもこのコート\n羽織ってみないか？\nふふっ、きっとすごく\n似合うと思うぞ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6223, 121901, 3, 1, "海賊船の新たな\n秘密兵器を作ったぞ！\nシグルド、これから\n見に来ないか！？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6224, 121901, 4, 1, "私は伝説の海賊だから\n泳ぐのだって得意だ！\nふ、深いところは\n少し怖いが…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6225, 121901, 101, 4, "ふふ、いつ無人島に\n漂着してもいいように\n非常食は準備済みだ\nシグルドの分もある！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6226, 121901, 102, 8, "冥風戦記の新章が\n完成したぞ…！\nシグルド、まずは\n貴様に読んでほしい！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6227, 121901, 1, 1, "ふっ、天気明朗\n絶好の航海日和だ！\n早速海に繰り出そう！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6228, 121901, 2, 1, "昼食用に魚を\n釣っておいたぞ！\nふふ、いかにも\n海賊っぽいだろう？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6229, 121901, 3, 1, "夜の海は真っ黒で\n少しだけ怖いな…\nシグルド、私の\nそばを離れるなよ…！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6231, 121901, 2, 1, "大海賊がくつろぐのに\n相応しい逸品だ…！\nえっと…これ、どこの\nお店で買えますか？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6232, 121901, 3, 1, "わわ、かわいい…！\n船室のベッドに\n置いておきたいな…\n連れてってはダメか？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6233, 121901, 4, 1, "おぉ、この香りは…！\n集中力が上がりそうだ\n海賊船の船長室に\nぜひ置きたいな！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6234, 121901, 5, 1, "これが船乗りの\n伝承に聞く、海原を\nさまよう神秘の火か！\n…暖まるな！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6236, 121901, 7, 1, "腹が減っては\n海賊はできぬ！\n…小説も書けぬ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6237, 121901, 8, 1, "女海賊と騎士はその後\n恋に落ち…？　恋愛\nモノではないかっ！？\nき、興味深くはあるが", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6239, 122101, 1, 1, "いつもの山籠もりが\nブームになるなんて…\n人生、なにが起きるか\n分からないなぁ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6240, 122101, 2, 1, "キャンプ道具…\nいえ、キャンプギア！\nカッコいい響き…♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6241, 122101, 3, 1, "レッツ、ＢＢキャン！\nこのフレーズ\n流行るかも…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6242, 122101, 4, 1, "キャンプ仲間…\n募集してみようかな？\n友達も兼任で…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6243, 122101, 5, 1, "ユカリさんと\nジュンさん…元気かな\nまた一緒に\nキャンプしたいなぁ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6244, 122101, 101, 4, "私の取材が載った本…\n【フォレスティエ】に\n送らなくちゃ…♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6245, 122101, 102, 8, "もし、あの人が\n落ち込むことが\nあったら…その時は\n私が力になりたいな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6246, 122101, 1, 1, "ブッシュクラフトは\nマイフレンドくんの\nカスタムにも\n活かせる技術です！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6247, 122101, 2, 1, "会話に花が咲く…\nって言いますよね\n私の場合、会話相手が\nお花そのものですけど", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6248, 122101, 3, 1, "この服、実は…\n昔ミサト先生に\nこしらえてもらった\n物なんです！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6249, 122101, 4, 1, "昔はやまびこで会話の\n練習をしてたんです\nほとんどオウム返しで\n気まずかったなぁ～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6250, 122101, 101, 4, "どうもマイスターです\n…あ、すみません\nみなさんの期待に\n応えようと練習中で…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6251, 122101, 102, 8, "たとえぼっちを\n卒業しても…\n私は一生ＢＢ団です！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6252, 122101, 1, 1, "小鳥さんのさえずりで\n目が覚める…\n心地いいものですね", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6253, 122101, 2, 1, "キャンプといえば\nおいしいご飯です！\n腕を振るいますよ！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6254, 122101, 3, 1, "すっかり夜ですね…\nおやすみの前に\n星でも見ませんか？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6256, 122101, 2, 1, "都会でくつろぎ…\n自然の中でくつろぐ…\nくつろぎ無限ループ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6257, 122101, 3, 1, "しっかり観察すれば\nブッシュクラフトで\n再現できるかも…？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6258, 122101, 4, 1, "はぁ～…やっぱり\n木の香りを嗅ぐと\n肩の力が抜けますねぇ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6259, 122101, 5, 1, "ああ…あったまります\nなんだか心に\nゆとりがでますね…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6261, 122101, 7, 1, "このおいしさ…！\n今度はキャンプに\n持っていこうかな…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6262, 122101, 8, 1, "ふむふむ…\nいろんな知識があると\nキャンプはもっと\n楽しくなりますよね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6264, 122201, 1, 1, "部屋がキャンプ用品で\n埋まっていく…\nキャンプ…なんて\n恐ろしい趣味なの…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6265, 122201, 2, 1, "仕事でも\nアウトドアでも\n頼れるお姉さんに\nなれたらいいなぁ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6266, 122201, 3, 1, "この服、かわいい上に\n本当に快適ね～♪\nおしゃれにキャンプを\n楽しめて最高だわ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6267, 122201, 4, 1, "ん〜次はどんな\nキャンプ飯を\n作ろうかしら？\n彼の好物は、っと…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6268, 122201, 5, 1, "早く週末が\n来ないかしら…\n仕事を忘れてパーっと\nキャンプ飲みを…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6269, 122201, 101, 4, "新しいキャンプ飯の\nレシピを覚えたわ！\n彼も喜んで\nくれるはず…よね♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6270, 122201, 102, 8, "彼の分の食器も\nこっそり買い揃えちゃ\nおうかな…そ、それは\nさすがにまだ早いか…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6271, 122201, 1, 1, "今度、財団主催で\n大規模なキャンプを\nやってみるのも\nいいかもしれないわね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6272, 122201, 2, 1, "ソロキャンプも\n悪くはないけど\n私はやっぱりキミと…\nな、なんでもない！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6273, 122201, 3, 1, "キャンプのことなら\n私に聞いて。なんでも\n教えてあげるから！\n…調べた上でね！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6274, 122201, 4, 1, "キャンプ場で飲む\n最高の一杯は\n他じゃ味わえないわ！\nさあ、乾杯っ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6275, 122201, 101, 4, "うん、決めた！　次は\n禁しゅわキャンプ！\nだってキミとゆっくり\n話したいんだもん♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6276, 122201, 102, 8, "キミのおかげで\n本当の最高の一杯が\n味わえたわ…\nありがとう♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6277, 122201, 1, 1, "んー、気持ちいい！\nキャンプが趣味に\nなってから、寝覚めが\nよくなったわ♪", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6278, 122201, 2, 1, "お昼は私がごちそう\nするわ。キャンプ飯で\n鍛えた料理の腕前を\n披露しちゃうわよ～！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6279, 122201, 3, 1, "そろそろ寝ましょ？\n明日も朝日を眺める\nためにね。もちろん\nコーヒー付きで♪", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6281, 122201, 2, 1, "くつろぐのに\n持ってこいね～\nテントの中に置いても\nよさそうだわ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6282, 122201, 3, 1, "ふふ、かわいいわね～\n…今度キャンプ場に\n連れていこうかしら？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6283, 122201, 4, 1, "うん、いい匂い…♪\nキャンプ場を思い出す\n自然の香りだわ〜", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6284, 122201, 5, 1, "はぁ、温まるぅ～\n冬のキャンプで\n使ってみたいわね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6286, 122201, 7, 1, "おいしい～♪\nキャンプ飲みの\nおつまみに\nぴったりね！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6287, 122201, 8, 1, "へえ…こんなキャンプ\n飯もあるんだ？\n彼、好きだったり\nするかな…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6289, 100701, 103, 12, "一生ついててやるの\n幽霊の一生は\nなっが～～～いの！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6290, 100701, 103, 12, "む～…あんまり触ると\nすり抜けてやるの！\nウソなの。むふふ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6291, 122301, 1, 1, "んー…\n今日はベスちゃんと\nなにして遊ぼーかなー", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6292, 122301, 2, 1, "ヴァンピィちゃんも\nけんぞくの友だちと\n仲良くなりたいな♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6293, 122301, 3, 1, "けんぞくのお部屋は\nヴァンピィちゃんが\n守ってあげなくちゃ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6294, 122301, 4, 1, "お掃除するなら\nヴァンピィちゃんも\n手伝ってあげよー♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6295, 122301, 5, 1, "ふーん、ふーん♪\nけんぞくってこーゆー\nお部屋が好きなんだー", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6296, 122301, 101, 4, "ひまだなー\nけんぞくをさそって\nお散歩いこっかな♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6297, 122301, 102, 8, "今日のスープに\nけんぞくの大好物を\n入れてあげよっと♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6298, 122301, 1, 1, "けんぞくー！\nヴァンピィちゃんと\nお出かけしよ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6299, 122301, 2, 1, "よしよし！\n今日のけんぞくも\nいい子だね♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6300, 122301, 3, 1, "んんー…\nやっぱりけんぞくと\nベスちゃんは似てる？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6301, 122301, 4, 1, "いーい？\nヴァンピィちゃんの\nいうこと聞くのよ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6302, 122301, 101, 4, "ヴァンピィちゃんの\nけんぞくだもん！\n大切にするからね！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6303, 122301, 102, 8, "えへへっ！\nやっぱりけんぞくって\nやさしー匂いがする♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6304, 122301, 1, 1, "おはよー…\nヴァンピィちゃんは\nもうちょっと眠るねー", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6305, 122301, 2, 1, "ヴァンピィちゃん\nひなたぼっこ\nだーいすき！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6306, 122301, 3, 1, "んー！　夜だ♪\nヴァンピィちゃん\n絶好調だよ！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6308, 122301, 2, 1, "わー！\nふかふかだ！\nきもちぃー♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6309, 122301, 3, 1, "かわいー♪\nベスちゃんに負けない\n抱き心地！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6310, 122301, 4, 1, "この香り…なんだろ？\nヴァンピィちゃん\n気に入っちゃった♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6311, 122301, 5, 1, "あったかいねー…\nヴァンピィちゃん\nずっとここにいるー…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6313, 122301, 7, 1, "はむはむ…\nんー！\nおいしー♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6314, 122301, 8, 1, "ヴァンピィちゃん\nこの本\n好きなんだよねー♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6316, 122501, 1, 1, "夏といえば\nかき氷だよね\n近くにお店は…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6317, 122501, 2, 1, "この格好でいるのも\nなじんできたし\nちょっと鍛錬でも…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6318, 122501, 3, 1, "動きやすいけれど…\nいつもと違う服装は\n少し落ち着かないな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6319, 122501, 4, 1, "夏の空気に\n当てられると思わず\n気が緩んでしまうね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6320, 122501, 5, 1, "砂浜で走ると\nいい具合に体幹を\n鍛えられそうだ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6321, 122501, 101, 4, "終わらない夏は\nないけれど…\nつい願ってしまうな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6322, 122501, 102, 8, "胸を張って彼と\n並び立てるよう\n私も、もっと強く…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6323, 122501, 1, 1, "どうしたの？\n行きたい場所が\nあるなら付き合おうか", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6324, 122501, 2, 1, "快晴で風は穏やか…\n絶好の釣り日和だね\nさあ、出かけようか", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6325, 122501, 3, 1, "今日の予定を\n立ててるんだけど…\nキミの希望も\n聞きたいな", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6326, 122501, 4, 1, "ビーチスポーツか…\nキミとなら\nいい勝負ができそうだ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6327, 122501, 101, 4, "私たちの夏に\n遠慮は無用だよ\n思いきり楽しもう！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6328, 122501, 102, 8, "いつかまた一緒に\n花火を見に行こう\n…約束だよ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6329, 122501, 1, 1, "潮騒の音で\n目覚めるのも\nいいものだね", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6330, 122501, 2, 1, "今朝釣ってきた魚が\nあるんだ。これで\nお昼ご飯にしようか", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6331, 122501, 3, 1, "夜にもイベントが\nあるみたいなんだ\n一緒に見に行かない？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6333, 122501, 2, 1, "気持ちが安らぐね…\nうっかり眠って…\nしまい…そうだ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6334, 122501, 3, 1, "肌触りのよさが\nこの格好だと\nよく分かるよ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6335, 122501, 4, 1, "海をイメージさせる\n香りだね…\n爽やかで落ち着くよ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6336, 122501, 5, 1, "濡れたままだと\n風邪を引いてしまうよ\nほら、キミもおいで", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6338, 122501, 7, 1, "夏を楽しみきるには\nエネルギーが\n必要だからね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6339, 122501, 8, 1, "大切な人と過ごせる\n観光スポット紹介…\n…こ、このページは\nもういいかな！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6341, 122501, 10, 1, "濡らしてもいいなら\n海に持っていって\n乗るのもよさそうだ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6342, 122501, 11, 1, "こういう名画を\n見た気がするな…\nこんな視点なんだね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6343, 122401, 1, 1, "水着って\n動きやすくて\nいいよねー♪\nいっちにーさんしー♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6344, 122401, 2, 1, "ユイちゃんにもらった\n日焼け止め\n塗っておいて\nよかった～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6345, 122401, 3, 1, "なんだか暑いなあ…\nせっかくだし\nプールで泳いで\nこようかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6346, 122401, 4, 1, "この格好で\n出かけようとしたら\nレイさんに\n怒られちゃった…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6347, 122401, 5, 1, "たとえ水着姿でも\n困ってる人を見たら\n助けに行くからね！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6348, 122401, 101, 4, "バカンス楽しかった～\nまた騎士くんと\n一緒に行きたいな♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6349, 122401, 102, 8, "この水着、あたしに\n似合ってるかなあ？\n騎士くんはどう\n思ってるんだろ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6350, 122401, 1, 1, "騎士くん！\n海に行くなら\n準備運動\n忘れずにね！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6351, 122401, 2, 1, "泳ぐ練習がしたいの？\nいいよ！　水着だから\nなにかあったらすぐに\n助けてあげるね！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6352, 122401, 3, 1, "騎士くんの水着姿も\nかっこよかったよ！\nまた遊びに行こ！\n約束ね！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6353, 122401, 4, 1, "このグローブの向日葵\nかわいいでしょ？\nユイちゃんが\n選んでくれたんだ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6354, 122401, 101, 4, "実はこの水着\nいつも着てる\n服とおなじなんだ…\nって、冗談だよ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6355, 122401, 102, 8, "変な日焼けしてないか\n見てもらってもいい？\n…なし！　今のなし！\nやっぱりなしー！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6356, 122401, 1, 1, "夏のお日様に\n負けないよう\n今日も元気いっぱい\nがんばるぞー！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6357, 122401, 2, 1, "お腹減ってきちゃった\n騎士くんはお腹\n空いてない？\n…聞こえちゃった♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6358, 122401, 3, 1, "今日も一日\n楽しかったー！\n明日もまた\n楽しもうね！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6360, 122401, 2, 1, "ふわあ…\nふわふわなのが\nいつもより\nよくわかるよー…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6361, 122401, 3, 1, "そうだ！　水着なら\n濡れてもいいし\n今度洗ってあげるね♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6362, 122401, 4, 1, "んー…いい匂い♪\nなんだか\n身体がぽかぽか\nしてきちゃった！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6363, 122401, 5, 1, "水着だから、えっと…\n暖か涼しい…！\n不思議な感じー", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6365, 122401, 7, 1, "思いっきり身体を\n動かした後だから\nいくらでも\n食べちゃえるよー！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6366, 122401, 8, 1, "ふむふむ…\nなるほどー\n水着の種類って\nそんなにあるんだぁ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6368, 122401, 10, 1, "乗り心地バッチリだね\nしゅっぱつしんこー！\nなんちゃって♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6369, 122401, 11, 1, "わっ、ぽよんぽよん！\nトランポリンみたいで\nた～のし～！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6370, 101501, 103, 12, "彼のことを考えてると\n幸せで胸が温かくなる\n…私ったら、もう", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6371, 101501, 103, 12, "うふふふふ\nくすぐったいわ～♪\nもう、イタズラっ子ね\n次は先生の番よ～♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6372, 122601, 1, 1, "騎士クンの汗\n拭いてあげたいな…\n余計なお世話かな…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6373, 122601, 2, 1, "夏バテ気味かも…\nそうだ、今夜は\nかば焼きにしよう♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6374, 122601, 3, 1, "青い空、白い雲\n眩しい日差し…！\n夏って好きだな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6375, 122601, 4, 1, "ヒリヒリする…？\n日に焼けちゃったかな\nふー…ふー…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6376, 122601, 5, 1, "花火大会があるんだ…\n夜、浜辺…2人きり…\nむ、無理だよ〜！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6377, 122601, 101, 4, "水着って不思議…\n引っ込み思案が少し\nおさまる…気がする", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6378, 122601, 102, 8, "騎士クンとまた\n海に行きたいな…\nまた、一緒に…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6379, 122601, 1, 1, "ダ、ダイエット！？\nしてないよ！？\n元々これ位だよ…？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6380, 122601, 2, 1, "スイカ割りのコツ？\nわたしは、騎士クンに\n教わりたいな…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6381, 122601, 3, 1, "騎士クン。うちわで\n扇いであげるね\nどこに置いたかな…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6382, 122601, 4, 1, "うぅ…暑いね…\n騎士クン、温度計は\n見ない方がいいよ～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6383, 122601, 101, 4, "じ、じっと見ないで…\nええっと…ちょっと！\nちょっとならいいよ…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6384, 122601, 102, 8, "え、肝試し？\nう〜ん、うう〜ん…\n…騎士クンとなら！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6385, 122601, 1, 1, "おはよう騎士クン\n見て、あっちの空\nもう入道雲が出てる", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6386, 122601, 2, 1, "夕立、来るかなぁ…\nもし雷が鳴ったら\nそばにいてくれる…？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6387, 122601, 3, 1, "しー。…ほら、虫の声\n…わ、わたしの心臓の\n音は気にしないで…！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6389, 122601, 2, 1, "ふふ、水着でゴロゴロ\nするのってなんだか…\nいけないことみたい", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6390, 122601, 3, 1, "かわいい～♪\n日光浴させて\nあげたいな♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6391, 122601, 4, 1, "ほんのり潮の香りが…\n香ばしくて塩辛くて…\nあ…おなか鳴っちゃう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6392, 122601, 5, 1, "水着、乾かせるかも…\nそうだ、みんなの分も\n持ってこよう！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6394, 122601, 7, 1, "夏バテなんてしてたら\nみんなの足を\n引っ張っちゃうよね\nいただきます！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6395, 122601, 8, 1, "騎士クンの、どうして\n海はしょっぱいの？\nって質問の答えは…\n分かんないよぉ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6397, 122601, 10, 1, "顔は怒ってるのに\nなんだかかわいい…\n誰かに似てる…？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6398, 122601, 11, 1, "これを枕に、ビーチで\n騎士クンと…なんて…\nなんて…なんて…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6399, 122901, 1, 1, "夏休み、\n毎日たのしいな～♪\nつぎはなにしよう？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6400, 122901, 2, 1, "自由研究、\nなににしよっかな～？\nプチグリフォンさんの\nかんさつにっきとか？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6401, 122901, 3, 1, "夏休みって、\n給食ないから…\nそれはちょっぴり\nさみしいかも…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6402, 122901, 4, 1, "砂浜をはだしで歩くの\nきもちいいよね♪\nくすぐったいけど\nミミ、好きなんだ～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6403, 122901, 5, 1, "きれいな貝がら\n見つけちゃった♪\nミミのたからもの！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6404, 122901, 101, 4, "ミミ、浴衣を\n買ってもらったんだ♪\n今度、おにいちゃんに\n見せてあげたいな！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6405, 122901, 102, 8, "ミミ、おにいちゃんと\nいっぱい夏休みの\n思い出つくりたいな♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6406, 122901, 1, 1, "ねえ、おにいちゃん…\n夏休みの宿題、\nわかんないとこ\nおしえてくれる…？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6407, 122901, 2, 1, "おかあさんにね、\n麦茶つくるの上手って\nほめられたんだ～♪\n飲んでみて♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6408, 122901, 3, 1, "夏休みの宿題で\nポスターかくんだ～♪\nおにいちゃんを\nかいてもいい？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6409, 122901, 4, 1, "線香花火やりたいな♪\nミミ、長持ちさせるの\n上手なんだよ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6410, 122901, 101, 4, "おにいちゃんの\n好きな本ってなあに？\nその本で読書感想文\n書こうかな♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6411, 122901, 102, 8, "海であそんだ後ね、\nおにいちゃんに\n髪を拭いてもらうの\nすきなんだ～♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6412, 122901, 1, 1, "夏休みって、\nワクワクして\n早起きしちゃうよね♪", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6413, 122901, 2, 1, "わぁ♪　お昼は、\n流しそうめんなんだ？\nミミ、流してみたい♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6414, 122901, 3, 1, "こわい話聞いて、\nねむれないの…\nねえ、おにいちゃん、\nいっしょに寝ていい？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6416, 122901, 2, 1, "ちょっぴりやすんだら\nまた、いっしょに\n海であそぼうね♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6417, 122901, 3, 1, "すっごくかわいいね♪\nこの子のこと、\n絵日記にかこうっと♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6418, 122901, 4, 1, "くんくん…\nスイカのにおいだね♪\nこのにおいだいすき！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6419, 122901, 5, 1, "およいだあとは、\nちゃんと体を\nあっためなくちゃね！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6421, 122901, 7, 1, "夏休みのご飯って\nなんだかとくべつ\nおいしい気がするね♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6422, 122901, 8, 1, "夏の星座って\nすっごくきれいだね♪\nいつかみんなと\nいっしょに見たいな♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6424, 122901, 10, 1, "わぁ♪　この子、\nとってもかわいいね！\nおともだちに\nなりたいな♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6425, 122901, 11, 1, "ミミ、人魚姫に\nなったみたい！\nおにいちゃん、\nとなりにすわって♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6426, 122801, 1, 1, "水着姿になると\nバカンス気分を\n味わえちゃうね！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6427, 122801, 2, 1, "海に行くの待ちきれ\nなくて、もう水着に\n着替えちゃった！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6428, 122801, 3, 1, "海に行ったら\nずぶぬれ系のイタズラ\nたくさんしかけよう！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6429, 122801, 4, 1, "部屋の中で泳ぎの練習\nしよう！　にいちゃん\nも水着に着替えて！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6430, 122801, 5, 1, "お日さまとお揃いの\n水着なんだ～♪\n似合ってるかなぁ？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6431, 122801, 101, 4, "海といえば冒険！\n海底に眠る財宝を\n見つけちゃおう！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6432, 122801, 102, 8, "にいちゃんと一緒だと\n部屋にいても海気分♪\nスイカわりしよっか！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6433, 122801, 1, 1, "にいちゃん！\n早く海に行く準備\nしなよ～！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6434, 122801, 2, 1, "ねーにいちゃん\n何秒息止められるか\n勝負しよーよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6435, 122801, 3, 1, "にいちゃんはアイスと\nかき氷どっちが好き？\nミソギは…どっちも！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6436, 122801, 4, 1, "日焼け止めクリーム\nキョウカに\nもらったんだ～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6437, 122801, 101, 4, "水中メガネがあれば\n水鉄砲くらっても\n平気だね！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6438, 122801, 102, 8, "水着なら、毎日夏休み\n気分！　にいちゃんも\n着てみなよ～！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6439, 122801, 1, 1, "にいちゃんおはよー！\n早く顔洗って、\nいっぱい遊んじゃお！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6440, 122801, 2, 1, "にいちゃんデザートは\nイチゴのかき氷だよ！\n…激辛ってバレた？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6441, 122801, 3, 1, "今日は楽しかった～！\n明日もイタズラ日和に\nなるといいな！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6443, 122801, 2, 1, "落とし穴の下に\nやわらかいの置けば\nケガしないよね！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6444, 122801, 3, 1, "かわいいね～！\nこの子も海に\n連れてっちゃおうよ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6445, 122801, 4, 1, "このにおいを利用して\nイタズラするには…\nにいちゃんどう思う？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6446, 122801, 5, 1, "水着を着て暖炉で\nあったまるのって、\n変でおもしろいね～！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6448, 122801, 7, 1, "海辺で食べる\nお昼ごはんって最高♪\nってことで海行こう！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6449, 122801, 8, 1, "いつか海を越えて、\n地図にも載ってない\n場所を見つけたいな！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6451, 122801, 10, 1, "この口から水が出たら\nみんな驚くよね！\nぷぷ…細工しちゃお！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6452, 122801, 11, 1, "変な音がするイタズラ\nを仕込んどいて…\n誰か座らないかな～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6453, 102701, 103, 12, "あなた様と私の愛を\n周りに見せつけて\n差し上げましょう♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6454, 102701, 103, 12, "呼びましたか？\n呼びましたね？\nでは愛を語りましょう\nそうしましょう", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6455, 122701, 1, 1, "水着で歩き回るのは\n恥ずかしいな…\n誰も見てないよね？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6456, 122701, 2, 1, "夏はやっぱり\nかき氷ですよね\n何色がいいかなぁ…♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6457, 122701, 3, 1, "あの人、泳げないって\n聞きました！\n私が教えてあげないと", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6458, 122701, 4, 1, "夏休みの宿題は\n計画的に進めましょう\n当たり前のことです！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6459, 122701, 5, 1, "防犯魔石、どこに\n着けておこうかなぁ\n…ここにしようかな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6460, 122701, 101, 4, "海の楽しみ方は\n予習済みです！\nあとは実践ですね♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6461, 122701, 102, 8, "水着…きっとあの人に\n見られちゃいますよね\n…褒めてくれるかな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6462, 122701, 1, 1, "触りたいんですか？\n…いいですよ\n帽子くらいなら", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6463, 122701, 2, 1, "夏は体調管理に\n気をつけましょう\n私が管理してあげます", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6464, 122701, 3, 1, "海に行くのが楽しみで\nたまらないんですか？\n私はそこまで…嘘です", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6465, 122701, 4, 1, "いつもより薄着だから\nなんだか恥ずかしい…\nみ、見ないでください", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6466, 122701, 101, 4, "へんたいふしんしゃ～\nえっ？　ああ、これは\n海に叫ぶ練習です", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6467, 122701, 102, 8, "あなたと一緒に\n海に行くなんて…\nなんだか大人…えへへ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6468, 122701, 1, 1, "おはようございます\nさぁ、海に行って\n私と運動しましょう", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6469, 122701, 2, 1, "あっつ～い…\nはっ！？　ダメですよ\nシャキッとしないと！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6470, 122701, 3, 1, "明日も海で\n遊ぶために、しっかり\nお休みしましょうね", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6472, 122701, 2, 1, "夏を楽しむときは\n休憩も大事ですよね\n計画的に、計画的に！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6473, 122701, 3, 1, "かわいいなぁ～\n海のお土産屋さんに\n売ってるかなぁ？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6474, 122701, 4, 1, "大人な香りがします\n私も違いが分かる\n素敵な女性になります", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6475, 122701, 5, 1, "濡れた体を\n乾かしましょう\nタオル、タオル…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6477, 122701, 7, 1, "泳いだ後は\n栄養補給…だけど\n…おなか膨らんじゃう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6478, 122701, 8, 1, "遊ぶだけじゃなくて\nお勉強もしてますよ\nあなたもどうですか？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6480, 122701, 10, 1, "ミミが喜びそう…♪\nわ、私ははしゃいだり\nしませんけど…っ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6481, 122701, 11, 1, "ミソギがいたずらに\n使いそう…しっかりと\n試しておかないと！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6482, 123001, 1, 1, "あたしがまさか\nここにいるなんて…\n自分でも不思議だわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6483, 123001, 2, 1, "昔みたいにあちこち\n飛び回れないのって\nちょっと不便かも", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6484, 123001, 3, 1, "こうして誰かと一緒に\n暮らせるのもあいつや\nみんなのおかげね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6485, 123001, 4, 1, "ここで厄介になるなら\nあたしも家事とか\n手伝わないと…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6486, 123001, 5, 1, "…だめ！　誰かの\n世話をせずには\nいられないわ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6487, 123001, 101, 4, "あいつたちが\n帰ってくるところだし\n綺麗にしないと…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6488, 123001, 102, 8, "せっかくあたしが\nいるっていうのに\n少しは構いなさいよ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6489, 123001, 1, 1, "おいしい店とか\n知らないの？\n食べに行きましょ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6490, 123001, 2, 1, "あんた暇でしょ？\nあたしのやること\n手伝いなさい", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6491, 123001, 3, 1, "コッコロたんと\n遊んでくるから\n留守番よろしく♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6492, 123001, 4, 1, "あらおかえり…って\nボロボロじゃない！\nまた人助けしてたの？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6493, 123001, 101, 4, "やっぱりあたしは\nあんたのそばが\n一番落ち着くみたい", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6494, 123001, 102, 8, "どこまでだって\nあんたと一緒に\nいてやるんだから", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6495, 123001, 1, 1, "ふわぁ…おはよう\n…大きくなったせいか\n朝は特に体が重いわ", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6496, 123001, 2, 1, "お昼ご飯が食べたい？\nしょうがないわね…\nなんか作って\nあげるから", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6497, 123001, 3, 1, "あんたまだ成長期だし\nさっさと寝なさい\n大きくなれないわよ？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6499, 123001, 2, 1, "たまにはあたしも\nゆっくりしないと\nもたないのよ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6500, 123001, 3, 1, "かわいいじゃな～い♪\nコッコロたんに\nプレゼントしたいわ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6501, 123001, 4, 1, "いい香りね\n塔にもこういうの\n置いておこうかしら", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6502, 123001, 5, 1, "炎か…前は\n羽が焼けそうで\n怖かったっけ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6504, 123001, 7, 1, "あむ…妖精に\n体重増減の設定って\nあったっけ…？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6505, 123001, 8, 1, "記憶のデータと違って\n物語はいいわね\n結末が読めなくて", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6507, 123101, 1, 1, "冒険のお土産\nみんなにおすそわけ\nせなあきまへんな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6508, 123101, 2, 1, "おとぎ話よりも\n不思議なことが\nあるもんやねぇ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6509, 123101, 3, 1, "冒険しに出かけるなら\n動きやすい服装に\nせななぁ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6510, 123101, 4, 1, "地図に水筒に…\n他に忘れもんは\nあらへんやろか？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6511, 123101, 5, 1, "これまでの冒険のこと\nノートに書いて\nまとめてますんよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6512, 123101, 101, 4, "王子はんとの冒険は\nどれもマホマホ王国の\n大事な歴史や♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6513, 123101, 102, 8, "どこへ冒険するときも\nうちと王子はんは\n一緒どす♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6514, 123101, 1, 1, "魔法で火ぃつける\n練習してますんよ～\nよう爆発しますけど…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6515, 123101, 2, 1, "うちと王子はんで\n冒険道具をお揃いに\nするんもええなぁ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6516, 123101, 3, 1, "テント張りも随分\n上達したんよ\n王子はんのおかげやね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6517, 123101, 4, 1, "ランスロットたちの\n新しい冒険衣装\n思いついたんよ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6518, 123101, 101, 4, "迷子になっても\n王子はんと一緒なら\nきっと大丈夫やね♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6519, 123101, 102, 8, "王子はんのこと、\nきっちり守れるように\nなってみせますえ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6520, 123101, 1, 1, "お寝坊さんは\n布団めくって\n起こしにいきますえ～", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6521, 123101, 2, 1, "支度が終わったら\n今日の冒険に\n出かけまひょか", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6522, 123101, 3, 1, "もうちょっと\nおしゃべりしたいん？\nあと少しだけやで♪", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6524, 123101, 2, 1, "冒険で疲れた体に\nこの癒しぱわーは\nあきまへん…すや…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6525, 123101, 3, 1, "ふふ、あんさんも\nうちと一緒に\n冒険します？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6526, 123101, 4, 1, "虫除けのアロマは\n冒険の必需品どす\n忘れんようにせんと", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6527, 123101, 5, 1, "うちも魔法使わんでも\n火ぃ起こせるよう\n練習せんとあかんなぁ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6529, 123101, 7, 1, "この料理…\n冒険するときに\n作ってみまひょか", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6530, 123101, 8, 1, "王子はんとの\n愛と冒険のおとぎ話…\nはぁ…素敵やわぁ♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6532, 103101, 103, 12, "あの人との\n最良の未来のために\n占いも霊能力ももっと\n精進しなきゃ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6533, 103101, 103, 12, "あなたともっと深く\nつながりたいです\n絆も、そして魂までも", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6534, 104401, 103, 12, "絆の契約…\nあやつが望むのなら\n考えぬでもないがのう", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6535, 104401, 103, 12, "なんじゃ？\nわらわの姿に\n見惚れたかの？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6536, 123201, 1, 1, "旅の荷物って多いな…\nねぇぷうきち～\nすこしは持ってよぉ～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6537, 123201, 2, 1, "傘はなくてもいっか\nぷうきちがいるし♪\n『おいなんだと…？』", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6538, 123201, 3, 1, "旅先で人見知りなんか\nしてちゃだめだよね…\nまずはあいさつだ…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6539, 123201, 4, 1, "おみやげはやっぱり\nご当地ぬいぐるみ\nだよね♪　ワクワク…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6540, 123201, 5, 1, "知らない場所かぁ…\nうぅ…考えただけで\nすでにホームシック…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6541, 123201, 101, 4, "野宿するってことは…\nお兄ちゃんのマントで\n寝るチャンスかも…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6542, 123201, 102, 8, "どんなに危険な冒険も\nお兄ちゃんと一緒なら\n大丈夫！　…だよね？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6543, 123201, 1, 1, "ぷうきちのアイマスク\nも腹巻も持ってくの！\n旅の必需品なの～！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6544, 123201, 2, 1, "これ全部背負うのって\nけっこうむずかしいの\n順番まちがえると大変", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6545, 123201, 3, 1, "えへへ～\nなでなでして？　帽子\nの上からでもいいよ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6546, 123201, 4, 1, "『悪いが絆創膏の部分\nはアンタッチャブルだ\n綿が飛び出ちまう』", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6547, 123201, 101, 4, "お兄ちゃんと一緒に\nもっともっといろんな\n景色が見たいな！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6548, 123201, 102, 8, "旅って、終わる時は\nきっとさびしいよね…\nお兄ちゃん、帰り道は\n手をつないでもいい？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6549, 123201, 1, 1, "出発が楽しみすぎて\n寝られなかった…\n『旅行のお約束だな』", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6550, 123201, 2, 1, "わぁ　いいお天気！\n旅の間もずーっと\nぴーかんだといいな♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6551, 123201, 3, 1, "知らない場所で寝るの\n怖いな…寝る時だけ\n帰ってこようかな…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6553, 123201, 2, 1, "旅先にはクッションも\nソファもないよね…\n『俺でいいだろ』", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6554, 123201, 3, 1, "ぬいぐるみのみんな\n旅の無事を\n祈ってて…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6555, 123201, 4, 1, "旅先の香りも\n思い出に持って\n帰れたらいいのにな～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6556, 123201, 5, 1, "旅行用に携帯用暖炉\nがあるといいかも…！\n『まだ荷物増やす？』", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6558, 123201, 7, 1, "『もう出発だぜ？』\nえーまってまって！\n腹ごしらえがまだ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6559, 123201, 8, 1, "世界ってこんなに広い\nんだ…！　はやく自分\nの目で見てみたいな～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6561, 123301, 1, 1, "はちゃめちゃヒマ～\nなーんか楽し～コト\nないかなー", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6562, 123301, 2, 1, "ここ、いートコだねー\nこんどカリザきゅんも\nつれてこよー", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6563, 123301, 3, 1, "かわゆいショタが\n見た～い♪\n触りた～～～い♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6564, 123301, 4, 1, "なんか服きっついなー\nちょい太ったかー？\nん～\nま・気のせーっしょ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6565, 123301, 5, 1, "あ～やべ\nおねえちゃんに\nメッセすんの忘れたー\nまいっかー", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6566, 123301, 101, 4, "パンケーキ食いてー\n騎士きゅん\n誘ってみよーっと♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6567, 123301, 102, 8, "きょーも～\n好きぴに囲まれて～\nシ・ア・ワ・セ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6568, 123301, 1, 1, "アタシと遊ばな～い？\nってなんか\nナンパみスゴー\nウケる～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6569, 123301, 2, 1, "んー？\n今はだるいからパース\nあとでねー", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6570, 123301, 3, 1, "カリザきゅん\n見なかった～？\nおねえちゃんから\n逃げるとかオシオキー", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6571, 123301, 4, 1, "ネイルしたいけど\n無理なんよねー\n爪の間にめっちゃ\nスライム入っからさー", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6572, 123301, 101, 4, "おねえちゃんに\nかまってほし～の？\nかわゆ～い♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6573, 123301, 102, 8, "な～に騎士きゅん♪\nここでイチャイチャ～\nし・ちゃ・う～？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6574, 123301, 1, 1, "ねんむ～\nけど外から\nショタの声したんで～\nつい起きちった～", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6575, 123301, 2, 1, "ランチの後はやっぱ\nデザートっしょー\nベツバラってヤツ～♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6576, 123301, 3, 1, "こんな時間まで\n起きてるコは～\n食べちゃうぞ～♪", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6578, 123301, 2, 1, "あーラクー\nいっしょーここで\nキューケーできるわー", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6579, 123301, 3, 1, "なにこれ～\nかわいいじゃ～ん\nショタの次くらいに～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6580, 123301, 4, 1, "いーニオイする～\nなんのニオイとかー\nよくわかんないけどー", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6581, 123301, 5, 1, "あったか～♪\n溶けてスライム\n戻りそ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6583, 123301, 7, 1, "ン・ン・ン～♪\nお・い・し～♪\nも～っと食べるぅ～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6584, 123301, 8, 1, "わ～なんかムズー\n読めなー\nウケるー", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6586, 123501, 1, 1, "あんパンって\nお菓子に入るのかな？\nハロウィンで\nもらえたりしない？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6587, 123501, 2, 1, "お菓子をくれなきゃ\nぐうたらするぞ～！\nハロウィン以外でも！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6588, 123501, 3, 1, "お化けになったら\n仕事しないで\nゴロゴロし放題\nなのかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6589, 123501, 4, 1, "おっと…この仮装\n袖がすごく長いからさ\n引っ掛けないように\n気を付けないとね～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6590, 123501, 5, 1, "イタズラするのも\n体力がいるよね～\n楽にできそうなの\n何かないかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6591, 123501, 101, 4, "あいつって\n仮装とかするのかな？\n似合いそうなのは\nどんなお化けだろ？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6592, 123501, 102, 8, "ずっとハロウィンなら\nあいつと遊び放題\nなんだけどな～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6593, 123501, 1, 1, "今の街はハロウィン\n一色だからね～\n人混みが大変だよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6594, 123501, 2, 1, "この仮装、どうかな？\n気に入ったなら\n来年もしてあげるよ～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6595, 123501, 3, 1, "実はこの長い袖\nドングリとかを\nため込めるんだよね…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6596, 123501, 4, 1, "カボチャランタンが\nあるんなら\nドングリランタンも\nあっていいよね～♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6597, 123501, 101, 4, "ハロウィンでも\nやる気があれば\nぐうたらできる…\nうん、いいこと言った", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6598, 123501, 102, 8, "一緒にお菓子食べよ？\n寝転びながら堪能する\nハロウィンスイーツは\n最高だからね～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6599, 123501, 1, 1, "お化けは寝る時間…\n今のあたしはお化け…\nだから、おやすみ～…", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6600, 123501, 2, 1, "お昼か～…\n食後のデザートは\nハロウィンのお菓子で\n決まりだね～♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6601, 123501, 3, 1, "ふぁ～、寝よ…\nお化けは夜に\n出るものだけど\nあたしはリスだからね", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6603, 123501, 2, 1, "ハロウィンを楽しむ\nためにも、ぐっすり\n寝とかないとね～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6604, 123501, 3, 1, "…袖から手が\n出せないから\nこのまま撫でよう\nよしよし", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6605, 123501, 4, 1, "リラックスできる\n素敵な香りだね～\n成仏しそうだよ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6606, 123501, 5, 1, "あったかいね～\nここで寝てもいい？\n…着替えないとダメか", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6608, 123501, 7, 1, "もぐもぐ…\nお化けもリスも\nお腹は空くんだよ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6609, 123501, 8, 1, "キョンシーって\nどんなお化けなのか\n本で調べようかな？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6611, 123501, 12, 1, "より取り見取りだね～\nとりあえず、これと\nこのお菓子はキープで", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6613, 123601, 1, 1, "よし、衣装もばっちり\nハロウィンパレードへ\n出かけよう♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6614, 123601, 2, 1, "仮装してても\n剣は手放せないね\nほとんど体の一部だよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6615, 123601, 3, 1, "見回りに誘うふりして\n二人でパレードに\n参加しちゃおうかな♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6616, 123601, 4, 1, "…あれ？　手錠の鍵\n落としちゃった…？\nやば、出かける前に\n見つけないと…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6617, 123601, 5, 1, "お家でコスプレも\nこれはこれで楽しいな\n決めポーズの練習\nたくさんしておこう！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6618, 123601, 101, 4, "他にも、お姫様に\nメイドさんに学生服…\nやりたいコスプレが\n多すぎるよ～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6619, 123601, 102, 8, "漫画もゲームも好き！\n仮装するのも大好き！\n…好きを言葉にすると\nもっと好きになる♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6620, 123601, 1, 1, "ずっと目が合ってるね\n見つめてくれるのは\nいいんだけど、今日は\n衣装を見てほしいな♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6621, 123601, 2, 1, "トリックオアトリート\nお菓子をくれなきゃ\n逮捕しちゃうぞ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6622, 123601, 3, 1, "あなたにだけはこの姿\nいつでも見せてあげる\nふふ、なんちゃって♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6623, 123601, 4, 1, "どうしたの？\nイタズラするなら\n隙を突かなきゃ\nこんなふうに、ね♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6624, 123601, 101, 4, "コスプレしてても\n剣の稽古は休まないよ\nちょっと相手になって\nくれるかな？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6625, 123601, 102, 8, "ひゃっ、びっくりした\n急に触らないでよ～\nハロウィンだからって\nイタズラしないでね？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6626, 123601, 1, 1, "おはよう！\n今日も朝から\nパトロールに行こう！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6627, 123601, 2, 1, "カボチャのケーキを\n用意してるよ！\nデザートを食べて\n午後も頑張ろう！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6628, 123601, 3, 1, "暗くなってきたね\nカボチャのランタンに\n火を灯そうよ♪", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6630, 123601, 2, 1, "ふかふか気持ちいい～\nあっでも衣装がシワに\nなっちゃうかも…？\n…でも気持ちいい～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6631, 123601, 3, 1, "…かわいい\nこの子もパレードに\n連れて行きたいな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6632, 123601, 4, 1, "はぁ～癒される♪\nハロウィンの見回りが\n終わった後は、\nまったりくつろごう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6633, 123601, 5, 1, "ここで二人寄り添って\nカボチャスープ飲もう\n…なんて想像したら\nポカポカしてきた…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6635, 123601, 7, 1, "パレードに参加すると\n体力を使うから、\nいっぱい食べておこう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6636, 123601, 8, 1, "この絵本のお姫様\nかわいいね\nこの仮装もいいな♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6638, 123601, 12, 1, "わあ、おいしそう！\n見回りに行く前に\n少しつまんじゃおう♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6640, 103701, 103, 12, "今日こそは、あの人に\n気持ちを打ち明けて…\nそのあとは…うう\nど、どうしよう？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6641, 103701, 103, 12, "私が剣なら、あなたは\nさしずめ私の鞘…\nふふ、私の帰る\n場所ってことだよ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6642, 123701, 1, 1, "やっぱり\nコスプレしてると\nテンションあがる～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6643, 123701, 2, 1, "キャラコスにも\n興味あるけど…\nマジカル、ラブリー\nどっちにしよっかな～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6644, 123701, 3, 1, "キャラコスといえば\nミスティーシャイニー\nもいいんだよねえ…\nうはー！　悩むー！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6645, 123701, 4, 1, "ギルドハウスで\n宅コスしてる人\n実は結構\nいるんじゃないかな～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6646, 123701, 5, 1, "コスプレだけど普段着\nって言いはれそう…\nって、ダメダメ！\nオタクは節度が大事！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6647, 123701, 101, 4, "フェスで買ってきた\n薄い本～♪\nギルドハウスに持って\nきちゃおっかな～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6648, 123701, 102, 8, "ようこそ\nコスプレ沼へ～\nなんちゃって♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6649, 123701, 1, 1, "どうどう？\nナナカちゃんの\nハロウィンコスプレ\n似合ってる？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6650, 123701, 2, 1, "ハロウィン魔法少女\n参上カボ～！\n…やっぱ今の語尾\nナシで！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6651, 123701, 3, 1, "ハロウィンといえば\nトリックオア～…\nコスプレ！\nなんちゃって", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6652, 123701, 4, 1, "ランフェスの\n戦利品～♪\nねえねえ\n見たい？　見たい？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6653, 123701, 101, 4, "私にしてほしい\nコスプレがあったら\n教えて♪　君のために\nや～ってやるぜぇ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6654, 123701, 102, 8, "一緒にコスプレしたい\nならナナカちゃんは\nいつでも大歓迎だよ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6655, 123701, 1, 1, "ふぁあ…ねむ\nって、ありゃりゃ　\n昨日コスプレしたまま\n寝ちゃってたかあ…", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6656, 123701, 2, 1, "今日のお昼は\nなんにしよ？\nカボチャ料理に\nしよっかな～", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6657, 123701, 3, 1, "もうこんな時間かあ\nそういえばオタクって\n夜行性の人が多い\n気がするぜぇ…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6659, 123701, 2, 1, "ふっふっふ\nこのパニエは\n座るときはちゃんと\nふにゃってなるのさ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6660, 123701, 3, 1, "いや～\nかわいいものをみると\n癒されますな～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6661, 123701, 4, 1, "新しい薄い本の匂いの\nアロマとか…ダメ？\n私はあの匂い、なんか\n好きなんだよね～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6662, 123701, 5, 1, "暖か～い…\nけど近づきすぎて\n衣装が燃えないように\n気を付けなきゃ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6664, 123701, 7, 1, "コス衣装のために\n体型維持は必須だけど\nついつい\n食べちゃうんだよね～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6665, 123701, 8, 1, "おっ、次はこの本に\nのってるキャラのコス\nしてみよっかな？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6667, 123701, 12, 1, "おおっ！\nお菓子がいっぱい！\nベルルちゃんチョコ\n入ってないかな～？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6669, 123801, 1, 1, "むっ…\nこの耳の手触り…\n驚嘆に値する…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6670, 123801, 2, 1, "獲物を狩りまくる…\nそんなウサギがいても\nいいだろう？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6671, 123801, 3, 1, "ワナなど怖くないぞ？\nむしろ積極的に\n踏んでいく性分だ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6672, 123801, 4, 1, "一匹狼ならぬ\n一匹ウサギか\nどっちでもかまわん♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6673, 123801, 5, 1, "人間とて本を正せば獣\n本能に従えばよいのだ\n遠慮は無用さ☆", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6674, 123801, 101, 4, "脱兎のごとく…\n逃げるものか\n坊やを追い回すのさ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6675, 123801, 102, 8, "獣の道は険しいが…\n坊やめ、音を上げずに\nちゃんとついてきたな\n褒めてやるとしよう♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6676, 123801, 1, 1, "ニンジンはいらん\n好物は好敵手だ\nさぁどんどんおくれ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6677, 123801, 2, 1, "尻尾に触りたいだと？\nワタシの乱数聖域を\n崩せたらな…☆", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6678, 123801, 3, 1, "頭で考えるな\n体に率直になれ\n己を野放しにするのさ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6679, 123801, 4, 1, "そうさモコフワさ♪\nなで心地は最高だが\n意外にどう猛だぞ…？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6680, 123801, 101, 4, "ワタシにピョンピョン\n跳ねてほしいのか？\nあとでじっくり、な♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6681, 123801, 102, 8, "ワタシと番になるか？\nオマエさえよければ\nいつでもかまわんぞ☆", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6682, 123801, 1, 1, "太陽と共に目覚め\n活動する\nそれが獣の生き方さ☆", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6683, 123801, 2, 1, "昼寝をすればいい\nワタシが周囲を\n警戒していてやる", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6684, 123801, 3, 1, "暗くなれば眠るだけさ\nそうすれば余計な\nストレスなどたまらん", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6686, 123801, 2, 1, "むむっ！？\n尻尾がつっかえて\nうまく座れんな…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6687, 123801, 3, 1, "愛らしさでワタシに\n張り合おうというのか\n５億年後に出直せ☆", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6688, 123801, 4, 1, "野生の芳香か…\n本能が呼び覚まされる\nいいセンスだ♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6689, 123801, 5, 1, "寒さも野生のうちだ\n…とはいえ\n温まっていくか♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6691, 123801, 7, 1, "悪くはないが物足りん\nこう見えて肉食なのだ\n肉はないのか肉は☆", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6692, 123801, 8, 1, "ウサギ族は地獄耳？\nワタシとの共通点だな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6694, 106401, 103, 12, "ここが私の家…\nそれはこれからも\nずっと変わらないわ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6695, 106401, 103, 12, "ええと、その…今から\nデート…じゃなくって\n散歩でもどうかしら？\n…ふ、二人きりで…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6696, 123901, 1, 1, "ギルドハウスに\nヒーロー参上！\nみんな仲良くッスよ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6697, 123901, 2, 1, "料理もできて\n鎧も作れる！\nお母さんはすごいッス", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6698, 123901, 3, 1, "ううう…\nまたマントをドアに\n挟んじゃったッス…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6699, 123901, 4, 1, "ずっと鎧を着ていれば\nジュンさんに近づける\nかも？　燃えるッス！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6700, 123901, 5, 1, "サインの練習…\nもっとしておいた\nほうがいいッスかね～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6701, 123901, 101, 4, "半人前でも\n胸を張るッス！\nそのうち似合う日が\nくるッスから！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6702, 123901, 102, 8, "種族の違いも\n強い魂があれば\n超えられるッス！\n信じてるッスよ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6703, 123901, 1, 1, "困ったときは\n自分を呼ぶッス！\nあと楽しいときにも\n呼んでほしいッス！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6704, 123901, 2, 1, "特訓でも行くッスか？\n望むところッス！\n目指せ、一人前！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6705, 123901, 3, 1, "新しいショーの台本を\n読みこんでるッス！\nところで…この言葉の\n意味、分かるッスか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6706, 123901, 4, 1, "ヒミツがなくなると\nなんか恥ずかしいッス\nあんたはどうッスか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6707, 123901, 101, 4, "ヒーローは遅れて\nやってくる…\nあんたもそうッスね♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6708, 123901, 102, 8, "誰よりも早く\nあんたに見せたいッス\n一人前になった自分！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6709, 123901, 1, 1, "うーん、今日も\nよく眠れたッス～！\n絶好調、間違いなし！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6710, 123901, 2, 1, "おお、もうお昼ッスか\nどうりでお腹が空くと\n思ったッス～えへへ", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6711, 123901, 3, 1, "パトロールのあとは\n歯みがきに宿題に…\n大忙しッスね、夜！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6713, 123901, 2, 1, "こんなにリラックス\nできるなんて…\n平和な証拠ッスね～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6714, 123901, 3, 1, "か、かわいい…♪\nさあ、自分と握手…\nううん、抱っこッス♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6715, 123901, 4, 1, "優しい匂いッスね…♪\nこれくらいみんなを\n包み込めるように\nなりたいッス～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6716, 123901, 5, 1, "ひっくちゅ！　うー…\nちょっと冷えるッスね\nぬくぬくするッス～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6718, 123901, 7, 1, "特訓した分、\nいっぱい食べるッス！\nでっかくなるッス！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6719, 123901, 8, 1, "いつか自分の\nヒミツ図鑑が出たり\nして…いやいやいや", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6721, 106901, 1, 1, "牢獄よりは\nマシな環境ね\n我慢してあげるわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6722, 106901, 2, 1, "退屈ね…\nキャルでもいじって\n暇をつぶそうかしら", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6723, 106901, 3, 1, "ここにいる連中\n私を天気予報士と\n勘違いしてない？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6724, 106901, 4, 1, "音波情報が多いわね…\nここ、もう少し静かに\nならないの…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6725, 106901, 5, 1, "共同生活なんて\n久しぶりだわ\n…懐かしいわね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6726, 106901, 101, 4, "あの子たちのギルド\n経営が雑すぎるわ…！\n教育が必要みたいね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6727, 106901, 102, 8, "とどめを刺される\n準備はできているのに\nまだなのかしら…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6728, 106901, 1, 1, "私と戦う気になった？\nあら、まだなのね…\nその時を待ってるわよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6729, 106901, 2, 1, "私というリソースは\n有効活用しなさい\n子守なんてごめんだわ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6730, 106901, 3, 1, "どうしてそんなに\n頭がいいか？\n生まれつきよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6731, 106901, 4, 1, "視力は権能に\n影響しないわよ\n当たり前じゃない", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6732, 106901, 101, 4, "あなたまで天気を\n教えろですって…？\nぶっ殺すわよ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6733, 106901, 102, 8, "暇なら付き合いなさい\n好きなゲームでいいわ\nどうせ私が勝つもの", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6734, 106901, 1, 1, "人生朝露…\n悔いのない一日を\n過ごしなさい", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6735, 106901, 2, 1, "午睡はいいわよ…\nふわぁ…眠りは思考に\n必要不可欠なの…", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6736, 106901, 3, 1, "夙興夜寝なんて\nやめておきなさい\n休息は大切よ", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6738, 106901, 2, 1, "私を満足させるなんて\n大したものね…晶に\n大量生産させるわ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6739, 106901, 3, 1, "キャルの代わりに\nズタズタにする物が\n欲しかったの…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6740, 106901, 4, 1, "いい香りじゃない\n見たことのない調合ね\n誰が用意したの？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6741, 106901, 5, 1, "パワーが足りないわね\nもっとよ、もっと私を\n寛がせなさい", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6743, 106901, 7, 1, "栄養価はともかく\n味のバランスは\n悪くないわね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6744, 106901, 8, 1, "つまらない本は\n私が書き直すわ\nこれはどうかしら？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6746, 124001, 1, 1, "今年はアカリと\nお姉ちゃんでサンタが\n二人…つまり幸せも\n二倍ですね♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6747, 124001, 2, 1, "クリスマスに向けての\nサックス練習…\nお兄ちゃんと一緒に\n猛特訓です！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6748, 124001, 3, 1, "クリスマスツリーの\n飾りつけ、完璧です♪\nお兄ちゃんお姉ちゃん\n喜んでくれるかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6749, 124001, 4, 1, "わあ、お外が真っ白！\n生クリームが\nいーっぱいの\nケーキみたいです♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6750, 124001, 5, 1, "煙突から入ると\nお洋服が汚れちゃう…\n玄関からじゃ\nダメなのかなぁ？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6751, 124001, 101, 4, "お兄ちゃんが来たら\nサンタのお洋服\nいっぱい褒めて\nもらおっと♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6752, 124001, 102, 8, "聖夜をお兄ちゃんと\n過ごせたら…\nアカリもちょっぴり\n大人になれるかも♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6753, 124001, 1, 1, "今のアカリは\nサンタさんですから♪\nなんでもお願いして\nくださいね？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6754, 124001, 2, 1, "お外は寒いですから\nアカリとくっついて\nお出かけしましょ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6755, 124001, 3, 1, "お兄ちゃんは\nアカリのトナカイさん\nですから、ずーっと\nそばにいてください♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6756, 124001, 4, 1, "お兄ちゃん、助けて！\nアカリのカラダで\nラッピング練習したら\n絡まっちゃいました…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6757, 124001, 101, 4, "プレゼント、いっぱい\n準備できました♪\nお兄ちゃんの分は…\nまだヒミツでーす♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6758, 124001, 102, 8, "アカリ、プレゼントは\n大人っぽいのがいいな\n早くお兄ちゃんに\n追いつきたいから♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6759, 124001, 1, 1, "おはようございます♪\nふふ、アカリサンタは\nいい子のお手本なので\n早寝早起きなんです♪", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6760, 124001, 2, 1, "あれ、もうお昼？\nクリスマスナンバーの\n練習してたら\nもうこんな時間…", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6761, 124001, 3, 1, "冬の夜空は星が\nキレイですね～\nお兄ちゃん、一緒に\n見に行きませんか？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6763, 124001, 2, 1, "はぁ、快適すぎて\nとろけちゃいそう…\nこういうプレゼントも\nいいかもです…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6764, 124001, 3, 1, "やん、かわいいです♪\nえへへ、お兄ちゃんは\nどんなプレゼントが\n欲しいですか～？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6765, 124001, 4, 1, "わわ、大人な香り…\nロマンチックな\nクリスマスが\n過ごせそうです♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6766, 124001, 5, 1, "これでカラダを温めて\nアカリの温もり…\nお兄ちゃんに\nおすそ分けしよっと♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6768, 124001, 7, 1, "お腹がパンパンです…\nでもこれで\nちょっとサンタさんに\n近づけたかも…？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6769, 124001, 8, 1, "クリスマスイブの\n恋人との過ごし方…？\nはわわ、アカリには\nまだ早いかもです…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6771, 124001, 14, 1, "わっ、すごいすごい！\n不思議な絵本ですね♪\nこの本でお兄ちゃんに\nイタズラしちゃお♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6772, 124001, 15, 1, "アカリ、ホカホカに\nなっちゃいました～♪\n…ぎゅってしても\nいいですよ？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6773, 124101, 1, 1, "またエンジェルになる\nかもとは思ってたけど\n…サンタさん要素の\n追加は想定外だったわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6774, 124101, 2, 1, "ラフィたちがいない\nときは私がなんとか\nしないとね\n一応…エンジェルだし", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6775, 124101, 3, 1, "部屋の飾りつけとか\nするのも…あり？\nふふ、浮かれすぎかな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6776, 124101, 4, 1, "パーティーだったら\nみんなで大人数向けの\nボードゲームもできる\nかも…やったっ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6777, 124101, 5, 1, "この服のモコモコ…\n気づいたら触っちゃう\n…やるわね、ラフィ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6778, 124101, 101, 4, "アイツと一緒にいると\n人見知りとは違う\n緊張があるのよね…\nって今の聞いてたっ？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6779, 124101, 102, 8, "ぬいぐるみ…\nいじめてないわよね？\n…大事にしてるって？\nそれはそれで…あうぅ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6780, 124101, 1, 1, "いい子と悪い子って\nどう見分けるのかしら\n…まさか素直さとか？\nだったら困る！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6781, 124101, 2, 1, "プレゼントといえば\nやっぱりゲーム！\n年末商戦サイコー♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6782, 124101, 3, 1, "サンタさんはお仕事を\n見られちゃ\nいけないのよね？\n…天職？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6783, 124101, 4, 1, "この服は汚れ対策も\nバッチリなのよね\n煙突に入れるように\n…いや私はNGよ！？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6784, 124101, 101, 4, "アンタのお願い…\n今なら一つくらい\n聞いてあげるわよ？\nほら、サンタ衣装だし", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6785, 124101, 102, 8, "私たちって\nなぜかぬいぐるみと\n縁があるわよね\n…ふふ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6786, 124101, 1, 1, "お、おはよっ\n…ふぅ、寝ぐせを\n直すのが間に合って\nよかったぁ", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6787, 124101, 2, 1, "え…一緒にごはん？\nそ、そう…うん。OK\nまあ、せっかくだし？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6788, 124101, 3, 1, "この時間のゲームって\n妙に楽しいのよね\n…ということで、\nちょっと遊ばない？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6790, 124101, 2, 1, "ああ～ダメになるぅ…\nもうここでずっと\nゲームしてた～い", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6791, 124101, 3, 1, "プレゼントのために\n研究は必要よね、うん\n…はぁ、かわいい～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6792, 124101, 4, 1, "集中力を高める香りも\nあるのよね…\n勝負をするときには\n持ち歩こうかしら", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6793, 124101, 5, 1, "なんだかまったり\nしちゃうわね…\n時間が溶けるわ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6795, 124101, 7, 1, "お腹いっぱい\n食べられるのも\n幸せなことよね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6796, 124101, 8, 1, "ちっちゃいときは\nアカリと一緒に絵本を\n読んだなぁ…懐かしい", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6798, 124101, 14, 1, "飛び出す絵本…\nこれでゲームブックを\n作ったらおもしろく\nなる気がするわ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6799, 124101, 15, 1, "あったまる…\nモフモフ…ふわふわ…\nちょっと休憩しても…\nいいよね…？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6800, 105101, 103, 12, "あら、どうしたの？\nもしかして、私と\n新しい実験を\n始めたくなった？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6801, 105101, 103, 12, "調子が悪いときは\n診療所に来て\n君は特別に、じっくり\n診察してあげるわ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6802, 124201, 1, 1, "特別じゃなくても\nまったり過ごす\nクリスマスも\nいいものだね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6803, 124201, 2, 1, "ニットの帽子に\nマフラーに耳当て…\n素顔用の防寒対策を\n整えなければ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6804, 124201, 3, 1, "靴下が\n見当たらなかったから\n枕元にはかわりに\n鎧を置いておこう", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6805, 124201, 4, 1, "聖女なんて言われる程\n素晴らしくはないけど\n心は聖くいたいものだ\n日々精進だね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6806, 124201, 5, 1, "兜がないから\nイルミネーションが\nいつもよりまぶしく\n感じるよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6807, 124201, 101, 4, "皆の幸福を\n祈願して\nクリスマスリースを\n飾ろうか", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6808, 124201, 102, 8, "私にはかわいすぎる\n衣装だが…開き直って\nクリスマス気分を\n楽しんでしまおう", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6809, 124201, 1, 1, "ツリーの飾りつけ\n手伝ってくれるかい？\nセンスは少年に\nお任せするよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6810, 124201, 2, 1, "パーティーは\n用意してる時も楽しい\nねえ少年、この気持ち\n分かってくれるかい？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6811, 124201, 3, 1, "なんでトナカイの鼻は\n赤いのかって？\n無灯ソリ運転は違反で\n取り締まられるからだ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6812, 124201, 4, 1, "トモちゃんや\nマツリちゃんは\nいい子だから、きっと\nサンタさんに会えるね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6813, 124201, 101, 4, "ヒイラギの髪飾り\n実は気にいってるんだ\n少年にも\n褒めてもらいたいな", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6814, 124201, 102, 8, "ねえ少年…そろそろ\n見慣れてほしい\nそんなにまじまじ\n見つめられると照れる", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6815, 124201, 1, 1, "朝日に照らされて\n街がキラキラ白銀に\n輝いてるよ", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6816, 124201, 2, 1, "お昼ご飯を食べて\nちょっと散歩しようか\n祝祭に沸く街は\nとてもきれいだよ", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6817, 124201, 3, 1, "やっぱりクリスマスは\n夜が本番って感じだね\nキャンドルを灯して\n厳かに過ごそうか", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6819, 124201, 2, 1, "鎧姿では無縁だったが\nふかふかしていて\n気持ちいいな…\nふかふか…ふかふか…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6820, 124201, 3, 1, "ふふ、かわいいね\n抱きしめると\nあの雪の日に産まれた\n赤ちゃんを思い出す", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6821, 124201, 4, 1, "兜を脱いだおかげで\nこの香りに出会えた\n…クリスマスの魔法も\n悪くないかもね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6822, 124201, 5, 1, "指先から体の芯まで\n暖まるね\nこれなら雪の日でも\n安心だよ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6824, 124201, 7, 1, "もぐ、もぐ…\nあまり食べるところ\n見ないでほしい", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6825, 124201, 8, 1, "素敵なレストランの\n特集記事が載ってる\n聖夜に予約が取れたら\n最高に幸せだろうね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6827, 124201, 14, 1, "わっ！\n…………いや\n驚いてなどいない", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6828, 124201, 15, 1, "ふふ…頬に直接\nふかふかを感じられる\n温かくて気持ちいいよ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6829, 124301, 1, 1, "ピアノに読書…\nグレアと一緒に\nなにをしようかな～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6830, 124301, 2, 1, "元の世界へ戻った時\n授業に遅れないよう\nグレアと勉強しとこう", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6831, 124301, 3, 1, "街に魔物料理屋さんが\nあるんだ！　グレアと\n食べに行ってみよう♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6832, 124301, 4, 1, "せっかく新しい魔法を\n覚えたし、グレアにも\n見てもらいたいな！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6833, 124301, 5, 1, "グレアも誘って\nランドソルをもっと\n探索したい！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6834, 124301, 101, 4, "私たちも\nあの人を見習って\nバイトとかしよう！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6835, 124301, 102, 8, "いつかあの人を\n私たちの学院に\n招待したいな！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6836, 124301, 1, 1, "あのね！\nこれからグレアと\n魔法愛好会に\n行くんだ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6837, 124301, 2, 1, "これからなにするの？\n私たちもついて行って\nいいかな！？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6838, 124301, 3, 1, "グレアと一緒に\nあなたも魔法の練習\nしてみない？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6839, 124301, 4, 1, "グレアとよく話すの\nマナリアの制服\nあなたに着せたいって", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6840, 124301, 101, 4, "グレアのそばって\n温かいんだ～！\nぬくぬくだよ～♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6841, 124301, 102, 8, "あなたは\nグレアと私の友達！\nこれからもずっと！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6842, 124301, 1, 1, "おはよう！\n今朝はグレアと一緒に\n外を散歩してきたよ！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6843, 124301, 2, 1, "お腹空いたな…\nよし、グレアと一緒に\n魔物料理を作ろう！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6844, 124301, 3, 1, "もうこんな時間だね\n明日のために\nそろそろ寝ようかな！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6846, 124301, 2, 1, "はぁ～この感触…\nグレアの尻尾みたいで\n安らぐ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6847, 124301, 3, 1, "ぬいぐるみって\nいいよね…私もよく\nグレアに抱き付くんだ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6848, 124301, 4, 1, "いい香り～…\nグレアもこういうの\n好きなんだよ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6849, 124301, 5, 1, "あったかいね～…\nグレアにくっついてる\n時を思い出すよ～…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6851, 124301, 7, 1, "この世界にしかない\nおいしいものが\nたくさんあるよね！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6852, 124301, 8, 1, "世界を繋ぐ魔法の\nヒント…本に書いて\nないかなぁ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6855, 124401, 1, 1, "ピアノに読書…\n今日のアンは\nなにをするのかな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6856, 124401, 2, 1, "私はアンほど\n勉強はできないから\nちゃんと自習しないと", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6857, 124401, 3, 1, "一度始めたら\nとことん追求するのが\nアンなんだよね…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6858, 124401, 4, 1, "ふふ…夢中になってる\nアンを眺めてるだけで\n一日があっという間…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6859, 124401, 5, 1, "ランドソルは広いね…\nアンと一緒にいろんな\n場所へ行ってみたい", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6860, 124401, 101, 4, "アンやあの人の笑顔を\n見てると、すごく\n安心するんだ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6861, 124401, 102, 8, "もしあの人が私たちの\n学院に編入したら…\nふふ、楽しそうだね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6862, 124401, 1, 1, "アンもあなたも\nすごいな…\nみんなとすぐに\n仲良くなっちゃうから", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6863, 124401, 2, 1, "あなたはこれから\nなにをするの？\nその…アンと一緒に\nついていってもいい？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6864, 124401, 3, 1, "アンから魔法の練習に\n誘われた？　実は私も\n…一緒にどうかな？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6865, 124401, 4, 1, "あなたがマナリアに\n編入したら\n楽しいのになって\nアンとよく話すんだ…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6866, 124401, 101, 4, "こっちの世界にも\nアンのように\n私を受け入れてくれる\n人たちがいるんだね…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6867, 124401, 102, 8, "元の世界へ帰る日が\n来たとしても私たちは\n「さよなら」より\n「またね」がいいよね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6868, 124401, 1, 1, "おはよう。さっき\nランドソルの朝日を\nアンと見てきたんだ", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6869, 124401, 2, 1, "私たちは\nこれからなんだけど\nあなたはお昼ごはん\n何を食べたの？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6870, 124401, 3, 1, "そろそろ\nアンが眠る時間だ…\nあなたもおやすみ", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6872, 124401, 2, 1, "はぁ、疲れが取れる…\nこの尻尾…\nちょっと重いんだよね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6873, 124401, 3, 1, "これ…\nアンに見せたら\n喜ぶだろうな…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6874, 124401, 4, 1, "いい香り…\nアンの感想も\n聞いてみたいな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6875, 124401, 5, 1, "あったかい…\nアンがここにいたら\n本を読みながら\n寝ちゃうんだろうな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6877, 124401, 7, 1, "おいしいからたくさん\n食べれちゃうけど\nまた尻尾…\n大きくなっちゃうかな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6878, 124401, 8, 1, "アンがおススメ\nしてくれた本は\nこれかな…？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6881, 124701, 1, 1, "凶、凶、凶…大凶？\nなによこのおみくじ\n自宅用だからって\n排出率が適当すぎよ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6882, 124701, 2, 1, "今年こそ最高の新薬が\nもはや偶然に近い\n最小限の手間で\n作れますように", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6883, 124701, 3, 1, "喉に詰まる前に\n溶けてなくなるお餅…\n最優先の研究課題だわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6884, 124701, 4, 1, "よし、完成♪\nミツキ特製…\n「新解釈おせち料理」\n医学的根拠が満載よ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6885, 124701, 5, 1, "お年玉を彼に…\nあげるべき…？\n色んな意味で微妙ね…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6886, 124701, 101, 4, "ふふ、今年もまた一年\n彼のバイタルを\n記録できるのね♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6887, 124701, 102, 8, "今年こそ\n私の薬で彼を魔改造…\nううん、大人の男に！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6888, 124701, 1, 1, "コマ回しのコマを\n見てたら目が回った？\nそう…手当は不要よ…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6889, 124701, 2, 1, "私の顔で\n福笑いを作った？\nあら…隻眼まで\n再現度が高いわね♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6890, 124701, 3, 1, "門松よし、しめ縄よし\nこんなギルドでもね\n一応縁起を担ぐのよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6891, 124701, 4, 1, "本気の薬草を煮込んだ\n本気の七草粥よ\n色は気にしちゃダメ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6892, 124701, 101, 4, "君が今年も健康で\nつつがなく…実験台を\n務められますように", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6893, 124701, 102, 8, "いくつ新年を迎えても\n俗世の垢にまみれない\nそんな君こそが…\n私には最高のお薬よ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6894, 124701, 1, 1, "お正月なんだから\nいつまでも寝てないで\nさぁ、実験開始よ♪", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6895, 124701, 2, 1, "カルタも双六もしたし\nあとは実験くらいしか\nやることがないわね", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6896, 124701, 3, 1, "日頃からたいがい\n意味深な夢を見るけど\n初夢はまた別かしら…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6898, 124701, 2, 1, "さすがにこの格好で\n寝正月を敢行するのは\nためらわれるわね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6899, 124701, 3, 1, "や、闇医者たる者\nこんな愛くるしい物に\n心奪われるわけには…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6900, 124701, 4, 1, "お雑煮のいい香り…\n食欲が増進されるわ\nアロマも立派なお薬ね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6901, 124701, 5, 1, "胸元は閉じないわよ？\nでも時々ここで\nこっそり暖まるの…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6903, 124701, 7, 1, "無から有が生まれてる\n新年早々物理の法則を\n無視してくるのね…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6904, 124701, 8, 1, "どれもこれも読んだわ\n新しい婚活本の\n入荷はまだかしら…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6906, 124601, 1, 1, "彼が一生懸命\n書いてくれた年賀状\n気持ちがこもっていて\nすごくうれしいわ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6907, 124601, 2, 1, "しっかり丁寧に\n大掃除したから\nとってもいい気持ちで\n新年を迎えられたわ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6908, 124601, 3, 1, "彼の着物姿\nきっと凛々しいわよね\n想像しただけで\n笑顔になるわ～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6909, 124601, 4, 1, "お雑煮は地域によって\n違いがあるけれど\n愛情が隠し味なのは\nどこも一緒よね♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6910, 124601, 5, 1, "彼と凧揚げして\n遊びたいけど\n森では引っかかって\nできないかしら…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6911, 124601, 101, 4, "今年も彼が素直に\n成長できるように\nたくさんいい子いい子\nしてあげましょう…♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6912, 124601, 102, 8, "絵馬に彼と\n書いた通り\n今年もみんなが笑って\n過ごせますように", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6913, 124601, 1, 1, "先生と初詣に\n行きましょう〜♪\n迷子にならないように\n手を繋いで、ね？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6914, 124601, 2, 1, "初売りセールいいわね\nでも気を付けないと\nあれこれ欲しくなって\n買い過ぎちゃうかも…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6915, 124601, 3, 1, "風邪をひかないように\n温かくするんですよ～\nえ？　先生、温めて？\nふふ、甘えん坊さんね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6916, 124601, 4, 1, "あなたのために\nちょっと豪華なおせち\n用意したのよ\nお口に合うかしら？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6917, 124601, 101, 4, "あなたも着物着たい？\nふふ、貫禄出ちゃって\n先生ドキドキ\nしちゃうかも…♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6918, 124601, 102, 8, "あなたの成長は\nとってもうれしいけど\n今年もまだ、あなたの\n先生でいたいな…♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6919, 124601, 1, 1, "あら、起きたの？\nお雑煮できてるから\nちゃんとお顔\n洗ってらっしゃいね", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6920, 124601, 2, 1, "お昼ご飯を\nしっかり食べて\nみんなでかるた大会\nしましょう〜♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6921, 124601, 3, 1, "は～い、良い子は\nもう寝る時間ですよ\n素敵な初夢が\n見られますように♪", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6923, 124601, 2, 1, "このフカフカちゃんも\n天日干ししたいけど…\nその前にちょっと\n休憩しちゃおうかしら", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6924, 124601, 3, 1, "うふふ、かわいい～\nアオイちゃんや\nハツネちゃんも\nきっと気に入るわね♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6925, 124601, 4, 1, "お正月は空気も\n透き通ってるから\nいつも以上に\n素敵な香りに感じるわ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6926, 124601, 5, 1, "火のぬくもりに\n抱かれていると\n不思議と眠気が…\nふああ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6928, 124601, 7, 1, "愛情のこもった料理は\nおいしいだけじゃなく\n心も満たされちゃう♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6929, 124601, 8, 1, "お正月のお作法も\nたくさんあるのね～\n先生も日々勉強だわ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6931, 104701, 103, 12, "少年への感謝をどんな\n形で伝えるべきか…\n言葉よりも物の方が\n良いのだろうか？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6932, 104701, 103, 12, "話し相手になって\nもらってもいいかな？\n少年と一緒にいると\nとても楽しいんだ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6933, 124501, 1, 1, "お正月だからって\nギャングのボスは\n油断できないよ～？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6934, 124501, 2, 1, "イノリちゃんがお年玉\n欲しいんだって♪\n悪い子だったし\n10ルピでいいかな☆", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6935, 124501, 3, 1, "お正月だからお雑煮を\n作ったんだけど\nカヤちゃんには\n大好評だったよ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6936, 124501, 4, 1, "ラーゴがお餅を\n食べたがってるけど…\nこの子にあげるのは\nちょっと心配かな～？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6937, 124501, 5, 1, "お正月は晴れ着の子が\nやっぱり多いよね～\n今度、私の漫画でも\n出してみようかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6938, 124501, 101, 4, "君と二人っきりで\n神殿に初詣…\n行ってみたいな～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6939, 124501, 102, 8, "君とお正月を\n過ごしていると\n面倒なことも\n全部忘れられそう…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6940, 124501, 1, 1, "この着物、どう思う？\n似合ってたら\nうれしいけどな～♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6941, 124501, 2, 1, "袖を引っ張ったり\nしないでね？\n着崩れしちゃうから♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6942, 124501, 3, 1, "お正月ならではの\n漫画ネタってある？\n参考にしたいんだよね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6943, 124501, 4, 1, "昔は凧あげとか\n駒回しとかしてたけど\n大人になるとそういう\n機会も減るよね…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6944, 124501, 101, 4, "君にもお年玉を\nあげようか？\n好きな額を言ってね☆", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6945, 124501, 102, 8, "来年も、再来年も…\nこうしてみんなで\n新年がお祝いできると\n素敵だね～♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6946, 124501, 1, 1, "おはよ～♪\n朝ごはんは\n焼いたお餅とか\nどうかな？", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6947, 124501, 2, 1, "お昼だね～\nおせちを用意したから\nみんなで食べよっか", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6948, 124501, 3, 1, "もうこんな時間だね～\n一日中着物だったから\n肩が凝っちゃったよ", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6950, 124501, 2, 1, "このまま寝たら\nしわになるかな？\n着替えた方がいい？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6951, 124501, 3, 1, "こういうのって\nラーゴのおもちゃに\nちょうどいいかも♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6952, 124501, 4, 1, "いい香りだけど\n着物に移らないように\n気をつけないとね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6953, 124501, 5, 1, "ちょうどいい\n火加減だね♪\nぽかぽかするよ～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6955, 124501, 7, 1, "着物の時は\nほどほどにしないと…\n欲張ったら、お腹が\n苦しくなるからね～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6956, 124501, 8, 1, "ふーん\nイノリちゃんは最近\nこんな漫画を\n読んでるんだ～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6958, 124801, 1, 1, "楽園の気配は…ナシ！\nみんな前を向いてるね\nそうこなくっちゃ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6959, 124801, 2, 1, "レッスンの成果も\nこの格好くらい\n目に見えたら\n分かりやすいのになぁ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6960, 124801, 3, 1, "次の曲はワイルドで\n悪い感じもいいなぁ…\nカヤとムイミちゃん\nみたいなイメージで！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6961, 124801, 4, 1, "私…服、着てるよね？\nうんうん、着てる！\nあんまり体が軽いと\n急に焦る瞬間あるな～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6962, 124801, 5, 1, "最近ファンの年齢層が\n幅広くなってきたかも\nこれって私…国民的\nアイドルに近づいた？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6963, 124801, 101, 4, "パティスリーとコラボ\nしたらどんな感じかな\n…ノゾミンマカロン♪\nとか？　…いけるかも", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6964, 124801, 102, 8, "彼と一緒にいると…\nうれしくって苦しいな\nでも両方諦められない\n…アイドルらしいや", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6965, 124801, 1, 1, "この格好、チカと\nツムギも似合うと\n思うんだけどな～\nキミもそう思うよね？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6966, 124801, 2, 1, "街を歩いてたらね？\nライブとか写真集の\n告知イベントだって\n思われちゃった！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6967, 124801, 3, 1, "私が持ってた剣に\n結んであったリボン…\n何か気づいてくれた？\nヒントは…色だよ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6968, 124801, 4, 1, "キミの気配ってすごく\n分かりやすいんだよ\n近くにいたらすぐに\nピンとくるんだ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6969, 124801, 101, 4, "あ、それ…はじめて\nキミにあげた音響盤！\nね、一緒に聴こうよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6970, 124801, 102, 8, "ぼうっとしてたら\nイタズラしちゃうよ♪\nもっともっと私のこと\n見てないとダーメ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6971, 124801, 1, 1, "朝は今日のはじまり！\n昨日よりももっと\nいい一日にしようね♪", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6972, 124801, 2, 1, "レッスンがんばったし\nまだお昼だし…\nランチ大盛りにしても\nセーフだよね！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6973, 124801, 3, 1, "寝る前に明日の予定を\n確認しておかないと！\nえっと、集合場所は…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6975, 124801, 2, 1, "一休みのつもりが\nなが～い休憩になるの\nあるあるだよね～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6976, 124801, 3, 1, "ノゾミンの熱烈ハグ♪\n…ここだけ聞いたら\nスクープになっちゃう\nかも？　なーんて！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6977, 124801, 4, 1, "香りってなんだか\n記憶に残るよねー\n…こういうの私だけ？\nみんなも同じだよね？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6978, 124801, 5, 1, "なんだかライブの\n熱気を思い出して\nきちゃった…！\nレッスンしよっと！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6980, 124801, 7, 1, "お腹が減ってたら\nいいパフォーマンスは\nできないもんね！\n…んー、おいし～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6981, 124801, 8, 1, "いい歌詞を書くには\nしっくりくる言葉を\n知っておかないと！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6983, 124901, 1, 1, "うだうだ悩んでる\nヤツがいたら\nオレのとこ連れてこい\n背中押してやるからさ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6984, 124901, 2, 1, "最近どことなく\nファミリーの連中が\n浮き足立ってんだよな\n…なんでだ？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6985, 124901, 3, 1, "イノリのヤツ\nやたらと脇腹\nつついてきやがって…\nくすぐったいっての", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6986, 124901, 4, 1, "身軽になったのは\nいいが…くそっ\nどこもかしこも\nスースーする…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6987, 124901, 5, 1, "思い出に浸るのも\n悪くはないが\nやっぱ人間\n未来に生きないとな！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6988, 124901, 101, 4, "またムイミやノゾミと\nツルむのもいいな\nそこにカレもいたら\nなおいいぜ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6989, 124901, 102, 8, "未来のことを考えると\nなぜかいつもカレが\n頭に浮かんじまう…\nええい、やめだやめ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6990, 124901, 1, 1, "ちょっくらオレと\n腕相撲しねえか？\nこの新しい力\n試させてくれ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6991, 124901, 2, 1, "なあ、悪いが上着\n貸してくれないか？\nこのカッコ、割と\n肌寒いんだよ…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6992, 124901, 3, 1, "おっ、生まれ変わった\n喧嘩屋にご用命か？\nいいぜ、キミの\n依頼なら二つ返事だ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6993, 124901, 4, 1, "なんか悩みごとが\nあるなら、なんでも\n話してくれよな\nオレとキミの仲だろ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6994, 124901, 101, 4, "このカッコが\nきれいだって？\nや、やめろよ…\n照れくさいだろ…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6995, 124901, 102, 8, "キミがいると、なにを\nするにも退屈しないぜ\nだから今日も\n付き合ってくれよな！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6996, 124901, 1, 1, "おはよう！　新しい\n未来がやってきたぜ！\n一秒だって時間は\n無駄にするなよ？", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6997, 124901, 2, 1, "そろそろ昼飯に\nしないか？\n穴場のうまい店に\n案内してやるからさ", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (6998, 124901, 3, 1, "夜更かしは\n未来の前借りだ\n体壊さないように\nほどほどにしとけよ？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7000, 124901, 2, 1, "お、いいなこれ\nファミリーの\nアジトにも\n置いときたいぜ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7001, 124901, 3, 1, "べ、別にかわいいもん\nなんて、そんなに\n好きじゃないし…\n嫌いでもないけどさ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7002, 124901, 4, 1, "嗅いだことない\n香りだな\nなかなか気に入った！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7003, 124901, 5, 1, "薄着で暖を取るのって\n最高にぜいたくだよな\nへへっ、堪能させて\nもらうとするか", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7005, 124901, 7, 1, "こいつはいいや\nこのカッコだと\n消耗が激しくて\nやたら腹が減るからな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7006, 124901, 8, 1, "甘辛コーデ…\nへぇ、そんなのが\nあるのか…\nわ、悪くねえかもな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7008, 105501, 103, 12, "はぁ…\nもっともっと長い時間\n先輩と過ごせたらなぁ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7009, 105501, 103, 12, "えへへ…♪\n先輩が、すぐそばに\nいてくれるなんて…\n幸せだなぁ…♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7010, 125001, 1, 1, "はーっはっは！\n強くてカッコいい\n大悪党、ここに参上！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7011, 125001, 2, 1, "みんなおしゃれで\nカッコいいけど\n今のアタシも\n負けてないぞ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7012, 125001, 3, 1, "ギャングのアジトも\nよかったけど\nここも賑やかで\n悪くないな！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7013, 125001, 4, 1, "悪党にも休息は必要だ\nここならゆっくり\n休めそうだ！\n邪魔するぞ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7014, 125001, 5, 1, "いっぱい人がいるな…\nアタシの悪党っぷりを\n広めるチャンスだ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7015, 125001, 101, 4, "ここにいれば\nいい思い出がたくさん\n作れそうだな！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7016, 125001, 102, 8, "いつかアタシも\nオクトーと一緒に\nこんなアジトを手に\n入れてやるからな！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7017, 125001, 1, 1, "ん？　アタシの格好が\n気になるのか？\nならもっと近くで\n見てもいいぞ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7018, 125001, 2, 1, "おい、暇だから\nアタシと遊べ！\nカヤとノゾミも呼んで\n大騒ぎするぞ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7019, 125001, 3, 1, "オマエ、前より強く\nなってないか！？\nくっそー！　アタシも\n負けてらんないぞー！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7020, 125001, 4, 1, "今日は特別にアタシの\n思い出話を聞かせて\nやる！　さーて、どの\n悪事から話そうか…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7021, 125001, 101, 4, "オマエとの付き合いも\n結構長くなったよな？\nこれからも\nよろしく頼むぞ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7022, 125001, 102, 8, "オマエまでいなく\nなったらアタシが困る\n勝手にどっか\n行くんじゃないぞ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7023, 125001, 1, 1, "起きろ、起きろー！\n大悪党の朝は早いんだ\nさっさと\n顔洗ってこい！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7024, 125001, 2, 1, "昼飯どうしようか…\nそうだ！　オマエの\nおススメに連れてけ！\nいい店期待してるぞ！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7025, 125001, 3, 1, "明日を思い切り\n楽しむためにも\n早く寝とけよ！\nまた明日な！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7027, 125001, 2, 1, "こうやって座ると…\nおお！　すっごく悪党\nっぽいぞ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7028, 125001, 3, 1, "オマエ、仲間に\nなりたそうな目を\nしてるな！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7029, 125001, 4, 1, "うん、いい匂いだ！\n何の匂いかは全然\n分かんないけどな！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7030, 125001, 5, 1, "炎ってなんだか\nカッコいいよな！\n見てるだけで心が\n燃えてくるぞ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7032, 125001, 7, 1, "いっぱい食べれば\nちょっとは\n背が伸びるかな…？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7033, 125001, 8, 1, "ぐぬぅ…たまには\n本でもと思ったけど\nやっぱりアタシは\nバトル担当だな！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7035, 118201, 1, 1, "お邪魔しまーす★\nなーんてもう\nすっかりくつろいで\nますけどー♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7036, 118201, 2, 1, "騎士さんの\nギルドハウス…★\nなんだかドキドキ\nしちゃいます♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7037, 118201, 3, 1, "ぺんぽこりんの\n置き場所はー…\nま、このへんで★", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7038, 118201, 4, 1, "うーん、これは\n壊しがいがありそ…\nウソウソ、冗談です★", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7039, 118201, 5, 1, "オトモダチでも\n作ってみますかー\nくすっ★", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7040, 118201, 101, 4, "退屈ですねー\n騎士さん、構って\nくれないかなー", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7041, 118201, 102, 8, "もっともっと早く\n騎士さんに逢えてたら\n…なんて、わたし\nらしくないですね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7042, 118201, 1, 1, "わたし一人にするの\n不安だったりします？\nちゃーんとイイコに\nしてますってー★", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7043, 118201, 2, 1, "お出かけします？\nそれともおうちで\nまったりしますー？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7044, 118201, 3, 1, "女の子をすぐ家に\nあげちゃうなんて…\nふふ、やっぱり隅に\n置けないですねー★", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7045, 118201, 4, 1, "騎士さん騎士さーん★\nウザ絡みしても\nいいですかー？\n騎士さんってばー★", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7046, 118201, 101, 4, "一緒にゴロゴロ\nしませんかー？\nコイビトっぽくて\n楽しそうですし♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7047, 118201, 102, 8, "うふふ★\nこーやってあなたに\n優しくされると…\nときめいちゃいます♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7048, 118201, 1, 1, "おはようございます★\nあれれー？\nまだおねむですかー？", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7049, 118201, 2, 1, "騎士さん、お昼は\nもう食べましたー？\nわたし、ラーメンが\n食べたいでーす★", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7050, 118201, 3, 1, "あ、夜更かししてちゃ\nダメですよー？\nほらほら、お布団に\nごーごー★", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7052, 118201, 2, 1, "ふかふかのふわふわー\nついついまーったり\nしちゃいます★", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7053, 118201, 3, 1, "かっわいー★\n名前はー、んー…\nぱんぱかぷんで★", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7054, 118201, 4, 1, "くんくん★\nうーん…\nたぶん素敵な香り♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7055, 118201, 5, 1, "あったかーい★\n心も体も\nぽっかぽかですねー★", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7057, 118201, 7, 1, "もぐもぐ★\nんー、おいしくって\n止まらないですねー★", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7058, 118201, 8, 1, "へー★\nほほー★\nふんふーん★", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7060, 125101, 1, 1, "さ～て時間もあるし\nラテアートの練習でも\nするかにゃ～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7061, 125101, 2, 1, "カフェの猫たちも\n元気に働いてくれて\nお客さんからの評判も\n上々にゃ～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7062, 125101, 3, 1, "カフェのカウンターに\nファントムキャッツの\n目安箱を置こうかにゃ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7063, 125101, 4, 1, "あんこもコーヒーも\n原料は豆にゃ～\nそりゃ相性いいに\n決まってるにゃ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7064, 125101, 5, 1, "あいつの為に\nタマキ特製ブレンドを\n開発したいにゃ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7065, 125101, 101, 4, "あいつと働いてると\nなぜかあっという間に\n閉店時間が\n来ちゃうのにゃ～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7066, 125101, 102, 8, "あいつといると\n新しいアイデアが\n溢れるほど\n出てきちゃうのにゃ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7067, 125101, 1, 1, "キミがコーヒーを\n淹れてくれたのにゃ？\nにゃはは、常温で\n猫舌にも優しいにゃ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7068, 125101, 2, 1, "料理が上手くなったら\nカフェご飯メニューも\n充実させたいにゃ～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7069, 125101, 3, 1, "お店の経営は大変\nだけど、キミとなら\n乗り越えられるって\n確信してるにゃ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7070, 125101, 4, 1, "ん？　この制服\n似合ってるにゃ？\nにゃはは！　褒めても\n１ルピも出ないにゃ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7071, 125101, 101, 4, "あたしが好きなのは\n猫に好かれる人にゃ♪\n…ん、キミ猫に懐かれ\nまくってるにゃ…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7072, 125101, 102, 8, "キミの制服姿\nけっこーイケてるにゃ\nあたしがお客さんなら\n通いつめちゃうにゃ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7073, 125101, 1, 1, "朝は一杯の\nコーヒーから始まる…\nにゃふふん♪\nシャレた朝だにゃ～♪", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7074, 125101, 2, 1, "お昼はたい焼きと\nコーヒーのセットで\nまったり休憩\nしようにゃ～♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7075, 125101, 3, 1, "コーヒー淹れたにゃ\n星空を見ながら\n二人で飲もうにゃ…♪", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7077, 125101, 2, 1, "ふにゃ～ん…ゴロゴロ\nコーヒーと同じくらい\n癒されるにゃ～…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7078, 125101, 3, 1, "こいつかわいいにゃ～\nうちの猫たちとも\nいい勝負なのにゃ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7079, 125101, 4, 1, "ん～いい香りだにゃ～\nコーヒーもそうだけど\n香りの癒し効果は\n抜群にゃ～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7080, 125101, 5, 1, "猫に暖かいものは\nまるまれと言ってる\nようなもんにゃ…♪\nまるまる…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7082, 125101, 7, 1, "にゃは～うまいにゃ♪\nカフェの新メニューに\n採用しちゃおう\nかにゃ～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7083, 125101, 8, 1, "にゃるほど…\n調べれば調べるほど\nコーヒーは\n奥が深いのにゃ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7085, 107101, 103, 12, "ワタシの手料理は\n坊やの口に合うらしい\n胃袋は討ち取ったぞ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7086, 107101, 103, 12, "胸に再度触れると\n誓約は無効になるのか\n…だと？　鋭い質問だ\n安心しろ。ならん☆", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7087, 125201, 1, 1, "ようやくこの服にも\n慣れてきたかな？\nふふっ、たまには\nこういう服も悪くない", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7088, 125201, 2, 1, "今日も店は好調だが、\n私は軍人だからな\n当初の目的も\n忘れないようにしよう", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7089, 125201, 3, 1, "猫が近くに来ると\nついつい見てしまうな\n仕事が終わったら\n遊んでやるかな♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7090, 125201, 4, 1, "コーヒーの淹れ方が\nうまくなってきたな♪\nどうせならお菓子も\n研究してみるか！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7091, 125201, 5, 1, "利益を考えると\n価格をあげるべきか？\n…いや、利益よりも\n皆の笑顔の方が大事だ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7092, 125201, 101, 4, "またあいつを\n呼んでしまったな…\n今度、コーヒーでも\n淹れて労ってやるか♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7093, 125201, 102, 8, "次に入荷するお菓子は\nあいつの意見を聞くか\nどんな味が好きか\n気になるしな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7094, 125201, 1, 1, "なに、コーヒーを\n淹れてほしい？\nふふっ♪\n仕方のない奴だな！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7095, 125201, 2, 1, "…そうだ。\nコーヒーと乾パンは\n合うのではないか？\n今度試してみよう！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7096, 125201, 3, 1, "コーヒーもいいが、\nココアも悪くないぞ♪\n貴公も一緒にどうだ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7097, 125201, 4, 1, "魔法少女に比べれば\n大人しい服だろう？\nだが、カフェには\nこの服でバッチリだ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7098, 125201, 101, 4, "貴公はコーヒーを\n飲めるのかっ？それは\nうらやましいな…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7099, 125201, 102, 8, "貴公の働きぶりには\n目を見張るものがある\nその姿は、その…\n結構かっこいいぞっ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7100, 125201, 1, 1, "さぁ、今日も一日、\n頑張るぞっ！\n気合いを入れて\n開店準備だっ！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7101, 125201, 2, 1, "昼は稼ぎ時だからな！\n荒波を乗り切るため\n貴公も一緒に\n頑張ってくれるか？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7102, 125201, 3, 1, "ふあぁ…もう夜か\n今日は頑張ったからな\nはやめに休むとしよう", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7104, 125201, 2, 1, "はふぅ～…\nたまにはこうして\nゆったりするのも\n悪くないな…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7105, 125201, 3, 1, "猫もかわいいが、\nこいつもなかなか…\n見ていると、\n撫でたくなるな♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7106, 125201, 4, 1, "これは良い香りだな\nふむ、コーヒーも\nより香り高いものが\n作れるよう試すか！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7107, 125201, 5, 1, "ふぅ～…\n体を温めなければ、\nすぐに動けないからな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7109, 125201, 7, 1, "これもうまいが、\n私はお菓子も…\nいや、食べすぎは\n体に良くないな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7110, 125201, 8, 1, "…書物は参考になるな\n次はこのやり方を\n試してみるかな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7112, 120001, 1, 1, "愛と希望の魔法少女！\n弟くんの平和は\nお姉ちゃんが守るよ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7113, 120001, 2, 1, "魔法少女の戦い方を\n見せてあげる！\nお姉ちゃんチョップ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7114, 120001, 3, 1, "闇の力が高まってる…\n早く弟くんを\nギュ～ってしないと！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7115, 120001, 4, 1, "ミニ弟くんを\n部屋中に呼び出して\n弟くんハウスに～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7116, 120001, 5, 1, "決めポーズの練習でも\nしよっかな…\nはい、ポーズ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7117, 120001, 101, 4, "魔法少女の力で\n弟くんのそばに\n瞬間移動したいなぁ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7118, 120001, 102, 8, "禁じられた必殺技\nその名も──\n闇のお姉ちゃん頭突き", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7119, 120001, 1, 1, "魔法少女な\nお姉ちゃんに\n興味津々だねっ？\n全部教えてあげるね♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7120, 120001, 2, 1, "闇のお姉ちゃんとして\n世界を弟くんのものに\n…な～んて冗談冗談♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7121, 120001, 3, 1, "弟くんって魔法少女に\nなりたがってたよね？\n…うん。応援するね！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7122, 120001, 4, 1, "サインが欲しいの？\nふふっ、任せて！\n愛する弟くんへ、と♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7123, 120001, 101, 4, "ふんふふ～ん♪\n魔法少女お姉ちゃんの\nテーマソング完成っ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7124, 120001, 102, 8, "えへへ、弟くんからの\n愛と希望を感じるよ…\n更に変身しちゃうぞ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7125, 120001, 1, 1, "弟くんの朝を魔法で\n守りに来たよ！\n希望の朝だねっ♪", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7126, 120001, 2, 1, "弟くんのお昼を魔法で\n守りに来たよ！\n愛のお弁当をどうぞ♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7127, 120001, 3, 1, "弟くんの夜を魔法で\n守りに来たよ！\nここからは闇の時間♪", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7129, 120001, 2, 1, "魔法少女の一休み♪\n愛と希望と…闇の力が\n回復していくね～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7130, 120001, 3, 1, "かわいいかわいい♪\nお姉ちゃんの魔法で\n増やしちゃうぞ…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7131, 120001, 4, 1, "この香り…\n弟くんと同じ…\nくんくん、くんくん…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7132, 120001, 5, 1, "体を温めておいて…\nこの熱を、ぜ～んぶ\n弟くんに伝えるよ…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7134, 120001, 7, 1, "ミニ弟くんを呼ぶには\nいっぱい食べないと…\n魔法ってお腹減るから", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7135, 120001, 8, 1, "弟くんの平和を守る…\nそのためには\nチェックチェック…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7137, 125301, 1, 1, "この服も、最初は\n慣れなかったのですが\n今ではすっかり\n体に馴染んでおります", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7138, 125301, 2, 1, "自然を守るためにも\nお勉強は欠かせません\n日々精進でございます", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7139, 125301, 3, 1, "採取した薬草を\n調合しておきましょう\nいつ必要になるか\n分かりませんからね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7140, 125301, 4, 1, "合宿は過酷でしたが\nおかげでちょっと\n力持ちになったような\n気がいたします…♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7141, 125301, 5, 1, "わんわんのほうの\n主さまに、ごはんを\nあげる時間ですね…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7142, 125301, 101, 4, "もっともっと訓練や\nお勉強を重ねて\n主さまのお役に\n立たなくては…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7143, 125301, 102, 8, "おや、そろそろ\nわんわんのほうの\n主さまをお散歩に\n連れていく時間です", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7144, 125301, 1, 1, "主さま、よろしければ\nお散歩に行きませんか\n今日の森は穏やかで\n過ごしやすいかと", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7145, 125301, 2, 1, "わんわんのほうの\n主さまも、今では\n【美食殿】の大切な\n仲間でございます", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7146, 125301, 3, 1, "森には危険が\nいっぱいでございます\nケガをなさらないよう\nお気をつけください", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7147, 125301, 4, 1, "もし森や動物のことで\n気になることがあれば\n気軽にお尋ねください", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7148, 125301, 101, 4, "森へ行く前には\n必ず装備の点検を\nお願いいたします\n約束ですよ、主さま？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7149, 125301, 102, 8, "森の中で主さまたちと\nゆったり過ごす時間は\nわたくしにとって\n大切なひとときです", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7150, 125301, 1, 1, "おはようございます\n朝ごはんをしっかり\n食べて、お勤めに\n備えましょう", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7151, 125301, 2, 1, "わんわんのほうの\n主さまにおやつを\nあげる時間です\nどうぞ、召し上がれ", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7152, 125301, 3, 1, "どれだけ野営に\n慣れたとしても\nベッドで眠れるなら\nそうしたいところです", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7154, 125301, 2, 1, "こちらなのですが\nわんわんのほうの\n主さまもお気に入りの\nようです", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7155, 125301, 3, 1, "わんわんのほうの\n主さまにも、こうした\n玩具を与えるべき\nでしょうか…？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7156, 125301, 4, 1, "心地よい香りです\n次は、自然由来の\n香料も試しましょう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7157, 125301, 5, 1, "暖をとるのは\n野営の時などに\nとても大事なことで\nございますね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7159, 125301, 7, 1, "食べられる時に食べて\nおくのも、大自然では\n重要でございます", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7160, 125301, 8, 1, "より知識を蓄え\n自然の力を活用できる\nようにいたしましょう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7162, 100901, 103, 12, "今回の「冥風戦記」も\nなかなかよく書けたぞ\nふふっ、シグルドも\n喜んでくれるかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7163, 100901, 103, 12, "貴様のためならば\n我が真なる魔力\nいつでも\n解き放とうぞ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7164, 125401, 1, 1, "ニャットちゃんに\nブラッシングを\nしてあげようかな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7165, 125401, 2, 1, "ふふ…\nこれがレンジャーの\n制服ですか", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7166, 125401, 3, 1, "そろそろ\nパトロールの\n時間だ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7167, 125401, 4, 1, "魔物について\nもっと知りたいな\n本を探しに行こう", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7168, 125401, 5, 1, "レンジャーになって\n体調を崩す日が\n減った気がします…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7169, 125401, 101, 4, "本だけじゃわからない\n世界のことを、\nもっと知りたいです…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7170, 125401, 102, 8, "こんな私にも\nできることはある…\nそう信じています", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7171, 125401, 1, 1, "あの…良かったら\n一緒に見回りへ\n行きませんか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7172, 125401, 2, 1, "またあなたと一緒に\nレンジャーの仕事が\nできたらうれしいです", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7173, 125401, 3, 1, "けほっこほっ\n…だ、大丈夫です\nたまに出るだけなので", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7174, 125401, 4, 1, "ニャットちゃん\nあなたにも\n懐いてきましたね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7175, 125401, 101, 4, "ニャットちゃんと\n散歩に出かけようかな\n…あの人も誘って", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7176, 125401, 102, 8, "あなたと肩を並べて\nレンジャーの仕事が\nできてうれしいです…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7177, 125401, 1, 1, "おはようございます\nレンジャー・シオリ\n元気に出勤します", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7178, 125401, 2, 1, "ニャットちゃんにも\nお昼ごはんを\n用意しなくちゃ", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7179, 125401, 3, 1, "明日は仕事だから\n早起きしなくちゃ\nおやすみなさい", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7181, 125401, 2, 1, "ふぅ…仕事で\n走り回ったので\n少し休憩します", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7182, 125401, 3, 1, "仕事が終わった後に\nこうして触れていると\n癒されますね…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7183, 125401, 4, 1, "森の香りがします…\n今日はよく\n眠れそうです…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7184, 125401, 5, 1, "ニャットちゃんと\n同じぐらい\n暖かいです…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7186, 125401, 7, 1, "レンジャーの仕事を\nしていると\nお腹が空きます", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7187, 125401, 8, 1, "どんなに忙しくても\n読書だけは\nやめられませんね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7189, 125801, 1, 1, "兄様のお留守の間\nわたくしが、兄様の\n御家を守ります！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7190, 125801, 2, 1, "に、に、兄様の御家…\n招待いただいた以上は\n失礼のないよう\n振る舞わなくては…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7191, 125801, 3, 1, "ふふっ…兄様の素敵な\nお人柄が、家中から\nにじみ出ています…♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7192, 125801, 4, 1, "靴を脱がずに\n御家に上がるのに\n慣れていなくて…あ！\n飛べばいいのでは…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7193, 125801, 5, 1, "ここに花を生けたら\n華やぎそうですね\n兄様はお花…\n好きでしょうか…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7194, 125801, 101, 4, "ずっとここにいたい…\nなどと言ったら\n兄様を困らせて\nしまいますよね…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7195, 125801, 102, 8, "お料理などを作って\n兄様を出迎えられたら\nいいのに…料理の腕も\n磨かなくては…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7196, 125801, 1, 1, "このような姿で\n失礼しております…！\nネ、ネズミがいたので\nつい本気を…！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7197, 125801, 2, 1, "おかえりなさいませ\nお忙しい御身をどうぞ\n癒してくださいね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7198, 125801, 3, 1, "兄様の話し相手…！？\nど、どうしましょう…\nわたくしには面白い\n話なんて…はうぅ…！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7199, 125801, 4, 1, "なぜか、動物などに\n好かれる体質で…\n迷い猫捜しのバイトの\nお役に立てますか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7200, 125801, 101, 4, "に、兄様の家に来たら\n『添い寝』をするのが\n礼儀とリアさんが…\n本当…でしょうか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7201, 125801, 102, 8, "わたくしはここに\nいて…よいのですね？\n『仲間』として…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7202, 125801, 1, 1, "朝は得意なんです\nもし兄様が苦手なら\nわたくしが起こして\n差し上げますよ", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7203, 125801, 2, 1, "兄様…昼餉を作って\nみたのですが、失敗\nしてしまいました…", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7204, 125801, 3, 1, "ふあぁ…\nはっ！　お…\nお恥ずかしい所を…！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7206, 125801, 2, 1, "こ、これは…！\n人様の御家なのに\nつい…駄目になって…\nしまいますぅ…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7207, 125801, 3, 1, "と、飛びかかっては\nこない…ですよね…？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7208, 125801, 4, 1, "すぅ…はぁ…\nこのかぐわしさ\n心が洗われます…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7209, 125801, 5, 1, "なんて暖かい…\n兄様の優しくお広い\n御心のようです…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7211, 125801, 7, 1, "こんなに食べていたら\nはしたない…\nでしょうか？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7212, 125801, 8, 1, "兄様の本棚はまるで\n知識の宝庫ですね\n己の世間知らずぶりを\n実感してしまいます", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7214, 125501, 1, 1, "次の冒険に向けて\n騎空士弁当を作ろっと\nまずはおにぎりから！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7215, 125501, 2, 1, "魔法の修行しなくちゃ\nもっともっと頼れる\n団長になりたいから！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7216, 125501, 3, 1, "ビカラちゃんを\n遊びに誘いたいけど…\nどういう場所が\n好きなんだろう？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7217, 125501, 4, 1, "仕事を真面目に\nしっかりこなすことが\n騎空艇への\n一番の近道だよね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7218, 125501, 5, 1, "ここにいる人の中で\n騎空団に興味ある人\nいたりしないかな…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7219, 125501, 101, 4, "これまでも想い出は\nいっぱいできたけど…\nこれからもたくさん\n想い出を作りたいな！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7220, 125501, 102, 8, "仲間がいるから\n騎空士は限界だって\n超えられるんだね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7221, 125501, 1, 1, "この衣装\n魔法使いらしいでしょ\n服に着られないよう\n頑張らなくちゃね！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7222, 125501, 2, 1, "あなたとなら\nどんな相手だって\n怖くなんてないよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7223, 125501, 3, 1, "カリンさんから\n新しい依頼が来たよ！\n一緒に頑張ろ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7224, 125501, 4, 1, "操舵士になるのは\nずいぶん難しそう…\nいつか頼れる仲間が\n見つかるといいな", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7225, 125501, 101, 4, "手合わせしたいの？\nいいよ、たまには\n団長らしいところ\n見せてあげるね！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7226, 125501, 102, 8, "まだ二人きりだけど…\nこの騎空団は\n私の宝物だよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7227, 125501, 1, 1, "おはよう～…\n昨日は遅くまで\n魔法の研究してて…\nふわぁ…眠いなぁ…", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7228, 125501, 2, 1, "さてと、お昼休み！\n紅茶淹れてくるから\n一緒に飲もうよ！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7229, 125501, 3, 1, "遅くまでお疲れさま！\n無理しない程度に\nお互い頑張ろう！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7231, 125501, 2, 1, "魔法の研究は\n一旦、休憩！\n頭を休めなくちゃ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7232, 125501, 3, 1, "魔法みたいに\nかわいい！\n騎空団に入る？\nなんてね！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7233, 125501, 4, 1, "いい匂い…\nそれになんだか\n集中力が増したかも", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7234, 125501, 5, 1, "体だけじゃなくて\n心までポカポカする…\nなんだか魔法みたい", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7236, 125501, 7, 1, "いろいろ考え事したら\nお腹が空いちゃった！\nいただきまーす！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7237, 125501, 8, 1, "『摂理の彼方へ\n至るには…』\n…この魔導書\n勉強になるなあ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7239, 125701, 1, 1, "こちらの服を着ると\n冒険に出かけたく\nなっちゃいますね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7240, 125701, 2, 1, "冒険者の心得その一\n準備はできる限り\nしっかりとこなすべし\nなんてどうでしょう？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7241, 125701, 3, 1, "調合に必要な素材は\nあれとこれとそれと…\n時間があるとついつい\n考えちゃいますね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7242, 125701, 4, 1, "せっかくですから\nより良い素材を\n自分で集めたいですね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7243, 125701, 5, 1, "このポーションが\nみなさんの役に立つと\nうれしいですね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7244, 125701, 101, 4, "冒険者の心得その二\n危なくなった時は\nまず逃げるべし！\n大事なことですね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7245, 125701, 102, 8, "あの人の冒険は\nいつも楽しそうで\n元気がもらえます", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7246, 125701, 1, 1, "ぜひおすすめしたい\n依頼があるんです\nこちらをどうぞ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7247, 125701, 2, 1, "ほしいお薬があれば\n錬金術で作って\nさしあげますよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7248, 125701, 3, 1, "依頼で気になる\nところがあれば\n何でもご確認ください", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7249, 125701, 4, 1, "このギルドハウスに\n錬金術の実験設備を…\nえ？　だめですか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7250, 125701, 101, 4, "ポーションをどうぞ\n大丈夫、効果は\n確認済みですので♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7251, 125701, 102, 8, "次はどんな冒険に\n行かれるんですか？\nいつでもお供しますよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7252, 125701, 1, 1, "今日もいい一日に\nなりそうですね\n冒険日和といった\nところでしょうか", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7253, 125701, 2, 1, "いい時間ですし\nランチにしませんか？\n次の冒険の\n作戦会議も兼ねて♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7254, 125701, 3, 1, "夜も遅いですし\n明日の冒険のため\n今日は早く寝ましょう", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7256, 125701, 2, 1, "ふう…やはり冒険は\n事務仕事と違いますね\n少し休憩を…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7257, 125701, 3, 1, "冒険は緊張の連続です\nかわいい物で\n心を癒しましょう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7258, 125701, 4, 1, "ふむ、この香りは…\n素晴らしいバランスで\n調合されていますね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7259, 125701, 5, 1, "この火力…\n錬金術の調合に\n使えそうですね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7261, 125701, 7, 1, "冒険の際は食べられる\n時に食べておく！\n…とペコリーヌさんも\nおっしゃってましたね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7262, 125701, 8, 1, "ふむふむ…なるほど\nこんな調合も\nあるのですね…\n奥が深いです", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7264, 106101, 103, 12, "アイツどこ行った…？\n戦うならアタシも\n連れてけよなー！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7265, 106101, 103, 12, "今日も悪いヤツ退治…\nの前に腹ごしらえだ！\nなんか食べに行くぞ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7266, 125601, 1, 1, "みんなー！\nギルドハウスで\nぼくと握手！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7267, 125601, 2, 1, "種族も年齢も違うけど\nみんな楽しそうだね！\nやっぱりここはぼくの\nゆ～とぴあさ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7268, 125601, 3, 1, "ハハッ！　まるで\nパーティー会場だね！\nこれは楽しませがいが\nあるってものだよ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7269, 125601, 4, 1, "やぁみんな！\nご利益満点のネズ耳は\nいかがかな？\nひとつ３ルピだよ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7270, 125601, 5, 1, "ここにいれば\n遊び相手に困らないね\nかしこみかしこみ～！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7271, 125601, 101, 4, "どこか遊びに\n行こうかな♪\nそうだ、あの人にも\n声をかけてみよう！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7272, 125601, 102, 8, "あの人といると\nぼくまで楽しくなって\nくるんだよね！\n毎日パレード気分さ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7273, 125601, 1, 1, "ネズ耳の在庫が\n切れたんだ！\n作るのを\n手伝ってくれるかい？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7274, 125601, 2, 1, "ん？　握手かい？\nいいとも！\nいつでも何度でも\nしてあげるよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7275, 125601, 3, 1, "この衣装に興味が\nあるのかい？\nきみ、なかなか\n見る目があるね！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7276, 125601, 4, 1, "しばらくここに厄介に\nなるつもりなんだ\nあとで街を案内\nしてくれるかい？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7277, 125601, 101, 4, "とっておきのチーズ\nきみと半分こだ！\nみんなには内緒だよ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7278, 125601, 102, 8, "きみはいてほしい時に\nいつもそばに\nいてくれるね\n本当にかしこみだよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7279, 125601, 1, 1, "やぁ、おはよう！\n気持ちのいい朝だね\n今日も楽しくネズ耳\n売りにはげもうか！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7280, 125601, 2, 1, "お昼だね！\nおいしいチーズを\n持ってきたから\nぼくと半分こだ！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7281, 125601, 3, 1, "今日も一日\n楽しかったね！\nいい夢が見られること\n間違いなしさ！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7283, 125601, 2, 1, "ぼくにも休息は\n必要さ！\nリラックス〜\nリラックス〜♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7284, 125601, 3, 1, "ぼくのぬいぐるみも\n作って売ろうかな…\n修繕費も一気に\n集まるかもね！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7285, 125601, 4, 1, "いい香りだね！\nトーゴクのお香に\nよく似てるよ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7286, 125601, 5, 1, "はぁ…温まるねぇ…\nそうだ、この火で\nチーズでも炙って\nみようかな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7288, 125601, 7, 1, "うーん、この味…\nトーゴクを思い出すよ\nどれ、もう一つ\nいただこうか！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7289, 125601, 8, 1, "これはランドソルの\nガイド本だね！\nありがたく参考に\nさせてもらうよ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7291, 126201, 1, 1, "まさか、わらわが\nこのような装束を\n纏う日がくるとはな…\n感慨深いものじゃ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7292, 126201, 2, 1, "わらわの美貌で\nこのギルドハウスも\nより華やかに\nなったようじゃな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7293, 126201, 3, 1, "つまらぬ戦いで\n晴れ着を汚しとうない\n今日は一日\nゆっくりするかの", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7294, 126201, 4, 1, "今日はわらわが食事の\n準備をして、あやつの\n帰りを待ってやろう\n献立は何がよいかの…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7295, 126201, 5, 1, "ユキといったか…\n掴みどころのない\nやつじゃが、根性は\n認めてやるかの", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7296, 126201, 101, 4, "こうして、仲間と\nのんびり過ごす時間も\n悪くないのう♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7297, 126201, 102, 8, "あやつを誘って\n月を見に行こうかの\n夜が楽しみじゃ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7298, 126201, 1, 1, "いかなる衣装で\nあろうとも、わらわは\n完璧に着こなして\nしまうのじゃ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7299, 126201, 2, 1, "なんじゃ？\nわらわの姿に\n見惚れておったとな？\nす、素直でよいのじゃ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7300, 126201, 3, 1, "吸血鬼であるわらわに\n相応しい装束じゃろう\nあとは真っ赤な血を\n添えたいのう…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7301, 126201, 4, 1, "お主、疲れておるな？\nわらわが癒してやろう\nほれ、近う寄るのじゃ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7302, 126201, 101, 4, "お主がおるだけで\nギルドハウスが\n賑やかになるのう\nある種の才能じゃな♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7303, 126201, 102, 8, "眷属よ、風呂にするか\nご飯にするか\nそれとも、わらわに…\nいや、忘れるのじゃ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7304, 126201, 1, 1, "朝一番、わらわに\n会いたかったとな…\n眷属よ\n褒めてつかわすぞ♪", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7305, 126201, 2, 1, "眷属よ、わらわが\n食事を用意したぞ\n忌々しい太陽に負けず\n頑張るのじゃ", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7306, 126201, 3, 1, "眠るには惜しい夜じゃ\n眷属よ、もうしばらく\nわらわの傍に\nおるがよい", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7308, 126201, 2, 1, "ふかふかじゃの♪\n今度あやつにも\n座らせてやろう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7309, 126201, 3, 1, "愛いやつじゃ\n少しばかり、わらわが\n遊んでやろうかの", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7310, 126201, 4, 1, "気品ある香りじゃのう\nわらわの古城にも\n一つ欲しいところじゃ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7311, 126201, 5, 1, "よいぬくもりじゃ…\n忌々しい太陽とは\n大違いじゃの", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7313, 126201, 7, 1, "味付けの参考に\nあと、もう一つだけ…\n止まらんのじゃ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7314, 126201, 8, 1, "吸血鬼の弱点について\n記されておるのか…\nふむ、眠気が\nさめてしもうたぞ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7316, 100801, 103, 12, "このままボクが美しく\nなり続けても、彼なら\nきっと隣にいてくれる\nはずだよね！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7317, 100801, 103, 12, "もっとそばに来なよ\nボクの隣は\nキミのための\n特等席なんだから", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7318, 126301, 1, 1, "ドレスの影響かな…\n前より黒や赤が\n好きになったかも", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7319, 126301, 2, 1, "ボクがいるだけで\nここがぐんっと\n華やかになったね♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7320, 126301, 3, 1, "ボクを描いた絵画\n今度持ってきて\n飾ってあげようかな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7321, 126301, 4, 1, "ボクはボクに\n魅了されっぱなしさ♪\nまさに魔性の存在だよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7322, 126301, 5, 1, "美のアドバイスを\n聞きたい人いるかい？\nボクがもっともっと\n輝かせてあげるよ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7323, 126301, 101, 4, "あの人、今度はボクを\nどう褒めるだろう？\nもし語彙が尽きても\n許してあげよっと♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7324, 126301, 102, 8, "あの人に似合うコーデ\n考えてあげよっと\n案外、なんでも\n着こなしそうだよね♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7325, 126301, 1, 1, "吸血鬼になるなんて\nやっぱりゴメンだね！\n永遠の美よりも\n大事なことがあるのさ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7326, 126301, 2, 1, "お肌にいいものは\n大体好きだよ\nトマトジュースとかね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7327, 126301, 3, 1, "銀のアクセサリーが\nボクは大好きでね\nいつでもプレゼント\n待ってるよ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7328, 126301, 4, 1, "イリヤみたいにお城に\n住んでみたいんだ♪\n美を統べる者に\n相応しいでしょ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7329, 126301, 101, 4, "今のボクがカップを\n触ると壊しちゃうんだ\nだからほら、キミが\nボクに紅茶を飲ませて", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7330, 126301, 102, 8, "キミも美を磨けば\nもっと輝けるはずさ！\nボクがメイクとか\nしてあげようか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7331, 126301, 1, 1, "んん、まだ眠い…\n美のためにも、\nもうひと眠りするよ…", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7332, 126301, 2, 1, "ボクにぴったりな\n日傘を買ったんだ♪\n散歩にでも行こうよ！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7333, 126301, 3, 1, "夜こそボクの時間！\nさぁ、あまねく人々へ\nボクの美を広めよう！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7335, 126301, 2, 1, "リラックスは大事だよ\n心の余裕こそが\n美の秘訣だからね♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7336, 126301, 3, 1, "キミもボクの\n美の虜にしてあげる♪\nうれしいでしょ？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7337, 126301, 4, 1, "人々を魅了するほどの\n魔性の香り…\nボクにふさわしいね♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7338, 126301, 5, 1, "冷えは美の大敵さ\nボクのように\n美しくなりたいなら\n体を温めるべきだよ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7340, 126301, 7, 1, "食事をするボクの\nなまめかしさ…♪\n吸血鬼すらも\nドキッとするだろうね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7341, 126301, 8, 1, "吸血鬼って\n美しい存在だよね…\nボクほどではないけど", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7343, 126401, 1, 1, "ここがナイトさんの\nお家なんですねーっ！\n雰囲気そっくりっ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7344, 126401, 2, 1, "ドアも壁もお菓子じゃ\nないんですよねっ？\n不思議ですねーっ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7345, 126401, 3, 1, "失礼のないように\n気をつけないとっ！\nライラエルさまとも\n約束してますからっ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7346, 126401, 4, 1, "向こうに帰るころには\nお土産話がいっぱい\nできてるといいな～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7347, 126401, 5, 1, "戦闘用のアームの他に\n観光用のアームも\nあったらいいのにな～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7348, 126401, 101, 4, "ナイトさんの充填と\nライラエルさまの充填\nどっちも違って\nどっちも大好きです♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7349, 126401, 102, 8, "…ナイトさんが\nマスターになって\nくれたら…えへへっ\nなんでもないですっ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7350, 126401, 1, 1, "お手伝いだったら\n任せてくださいっ！\nお家の移転とか\nしちゃいますかっ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7351, 126401, 2, 1, "こっちに来るときは\nポポおじいさんが\nしっかりメンテして\nくれるんですよっ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7352, 126401, 3, 1, "お家の中にいるのも\nいいですけど、お外も\n気になります～っ！\nお出かけしましょう♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7353, 126401, 4, 1, "ヴォルガーノさんに\nそっくりの子猫さんを\n見かけたんですっ！\nご親戚ですかねっ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7354, 126401, 101, 4, "ここだけの話ですよ？\n…実はケーブルって\n触られると、ちょっと\nくすぐったいんですっ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7355, 126401, 102, 8, "このお家の持ち主は\nナイトさんですよね？\nそれじゃここでは\nご主人さまですねっ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7356, 126401, 1, 1, "おはようございますっ\n今日もフルパワーで\nがんばりましょうっ♪", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7357, 126401, 2, 1, "お昼の時間ですねっ！\nランチの充填、\nお願いしまーすっ♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7358, 126401, 3, 1, "そろそろ眠らなくて\n大丈夫ですかっ？\n夜ふかしさんなら\nお付き合いしますよ♪", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7360, 126401, 2, 1, "エネルギーの消費を\n抑えるのも大切\nですよね～っ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7361, 126401, 3, 1, "わたしが触っても\n平気なんて…！\nかわいくて頑丈っ！\n好きですっ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7362, 126401, 4, 1, "センサーによると…\nこの香りはいい香りに\n該当するみたいですっ\nなるほどーっ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7363, 126401, 5, 1, "あったかいのって\nいいですよねっ！\nみなさんうれしそう\nですしっ♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7365, 126401, 7, 1, "これがこっちの世界の\nごはんなんですねっ\nおいしい気がします！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7366, 126401, 8, 1, "そーっとそーっと…\n本を破かないように…\n書いてある内容より\n難しいです～っ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7368, 126701, 1, 1, "ウィスタリア家の\nプライベートビーチも\nご案内しますわね♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7369, 126701, 2, 1, "世界を巡る\nリゾートクルーズも\nご用意できますわよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7370, 126701, 3, 1, "避暑地に専用の\n別荘がありますの\n今度ご招待しますわね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7371, 126701, 4, 1, "日焼けは乙女の\n天敵ですわね\n備えはしっかりと…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7372, 126701, 5, 1, "線香花火だけでは\n物足りませんわね\nもっとドカーンと…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7373, 126701, 101, 4, "ギルド活動も全力で！\nバカンスも全力で！\nそれが私ですわ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7374, 126701, 102, 8, "私のセンスで選んだ\n特注の水着ですもの\n似合って当然ですわ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7375, 126701, 1, 1, "ご希望ならすぐにでも\n夏の飛空艇遊覧ツアー\nへご招待しますわ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7376, 126701, 2, 1, "夏の賑やかな\n雰囲気は好きですが…\n暑さには参りますわね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7377, 126701, 3, 1, "日の光を浴びるほどに\n輝きを増す夏の私！\n今日も絶好調ですわ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7378, 126701, 4, 1, "夏祭りに協力するなら\n資金と人材、どちらも\n用意しないとですわね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7379, 126701, 101, 4, "あなたさま…その…\nそうも見つめられると\n照れてしまいます…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7380, 126701, 102, 8, "タマキさんの働く\n海の家も訪れて\nみたいですわね♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7381, 126701, 1, 1, "港の市場が\n賑わう頃ですわね！\n私たちも参りますわよ", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7382, 126701, 2, 1, "冷たい飲み物でも\nご一緒にいかが？\nうちのシェフに用意\nさせてありますの", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7383, 126701, 3, 1, "まだもう少し…\nこの夏の夜を\nあなたさまと…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7385, 126701, 2, 1, "あなたさまも\n良ければこちらへ\nいらっしゃいな♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7386, 126701, 3, 1, "夏に合わせた装いに\n替えてみるのも\n楽しそうですわね♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7387, 126701, 4, 1, "この夏めいた爽やかな\n香り…期間限定で\n売り出したいですわ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7388, 126701, 5, 1, "く…我慢です…\nこの暑さに耐えてこそ\n美しさにも磨きが…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7390, 126701, 7, 1, "暑い季節に働く\nみなさまへ差し入れも\n必要ですわね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7391, 126701, 8, 1, "夏のトレンドを\n押さえておくのも\n大事ですわね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7393, 126701, 10, 1, "上手くいけば\nヒット商品になる\nポテンシャルを\n感じますわね…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7394, 126701, 11, 1, "この極上の肌触り…\n言い値で買いますわ\n一体おいくらですの？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7395, 126601, 1, 1, "せっかくのバカンス…\n自由に羽を\n伸ばすとしましょう", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7396, 126601, 2, 1, "たとえどのような\n秘密を持とうとも\n私には筒抜けですよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7397, 126601, 3, 1, "子供だましの怪談に\n関心はありませんが…\n霊には興味があります\n観察対象として、ね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7398, 126601, 4, 1, "彼の絵日記…\nあとでゆっくりと\n読ませてもらいます", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7399, 126601, 5, 1, "セミの鳴き声に入道雲\nそしてクリームソーダ\nこれでこそ夏ですね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7400, 126601, 101, 4, "夏休みの宿題でも\n作るとしましょうか\n彼の限界を少し超えた\n負荷を与えるには…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7401, 126601, 102, 8, "彼と過ごす夏を\n特別なものにしたい…\nそれが私の望みです", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7402, 126601, 1, 1, "夏だからといって\n浮かれすぎないように\n節度を持って\n楽しみなさい？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7403, 126601, 2, 1, "ああ見えてもマサキは\n私のプリンセスナイト\nあまり侮られては\n困りますよ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7404, 126601, 3, 1, "私の秘密ですか？\nまんじゅうが怖い…\nというのはどうです？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7405, 126601, 4, 1, "ここは誰かの秘密を\n盗み聞くのに\n最適な場所ですね\n…ジョークですよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7406, 126601, 101, 4, "一緒に風鈴の音を\nのんびり楽しもう…？\n…別に構いませんよ\n貴方とならば", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7407, 126601, 102, 8, "貴方に約束しましょう\n決して忘れられない\n夏にしてあげますよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7408, 126601, 1, 1, "早起きとは感心ですね\nでは一緒に\n体操でもしましょうか", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7409, 126601, 2, 1, "昼食は流しそうめん？\n非効率的ですね\n…食べないとは\n一言も言っていません", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7410, 126601, 3, 1, "…貴方が望むならば\nいくらでも怖い話を\nしてあげますが…\nいかがですか？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7412, 126601, 2, 1, "七冠にも休息は\n必要なのです\nしばらくここで\n休ませてもらいますよ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7413, 126601, 3, 1, "なかなか気に入った…\nというのは\n誰にも秘密です", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7414, 126601, 4, 1, "薫風を思わせる\nこのさわやかな香り…\n悪くないセンスですね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7415, 126601, 5, 1, "夏だからこそ\n冷え対策は\n重要なのです\n…とはいえ暑いですね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7417, 126601, 7, 1, "私がどのような味が\n好きなのかは…\n当然秘密です", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7418, 126601, 8, 1, "夏を舞台にした\n小説は数多くあります\n彼が好きそうな本を\n何冊か見繕いましょう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7420, 126601, 10, 1, "見た目も手触りも\n完璧に覚えました\nいつでも模倣できます", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7421, 126601, 11, 1, "ふむ…夏らしい\nクッションですね\nナイトプールに\n映えそうです", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7422, 105401, 103, 12, "レイ様も格好良いけど\n騎士さんもなかなか…\nって、今の聞こえて\nませんよねっ？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7423, 105401, 103, 12, "騎士さんはシンプルな\n服が似合いそうです！\n今度、一緒に\n見に行きましょう♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7424, 126801, 1, 1, "最初はどうかな～って\n思ったけど…\n水着姿でも案外\n問題ないのよね…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7425, 126801, 2, 1, "水着で飲みすぎると\nお腹が冷える、って\n言うけど…麦しゅわ\nなら問題ないわね！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7426, 126801, 3, 1, "暑ければ暑いほど\nその後の麦しゅわは\nおいしくなるのよ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7427, 126801, 4, 1, "しっかりと仕事を\nこなしてから、\nしっかりと麦しゅわ！\nやっぱりこれね！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7428, 126801, 5, 1, "ふふっ、デザインも\n着心地も最高ね\n奮発して用意して\n正解だったわ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7429, 126801, 101, 4, "伝説の麦しゅわ、\n飲めるものなら\nまた飲みたいわね…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7430, 126801, 102, 8, "二つ名に恥じない\n聖騎士として\n私…飲むわ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7431, 126801, 1, 1, "この格好なら水に\n濡れても大丈夫！\nなんだけど、さすがに\n書類は濡らせないわね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7432, 126801, 2, 1, "そうやってじっと\n見られると…ちょっと\n恥ずかしいわ…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7433, 126801, 3, 1, "今日もいい天気ね\nふふっ、あとで\nビーチに遊びに\n行きましょ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7434, 126801, 4, 1, "水着姿も慣れると\n案外快適ね\nそうだ、キミも\n水着になっちゃう？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7435, 126801, 101, 4, "例え水着姿であろうと\n聖騎士としての誇りは\n忘れてないわ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7436, 126801, 102, 8, "キミとだったら\n麦しゅわなしでも\n最高の夏のバカンスに\nなると思うわ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7437, 126801, 1, 1, "仕事終わりの\n麦しゅわのために\n今日も一日\n頑張るわよ♪", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7438, 126801, 2, 1, "今日のお昼は\nどうしようかしら…\nさ、さすがに昼からは\n自重してるわよ？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7439, 126801, 3, 1, "さ、一日の疲れを\n洗い流すために…\n軽～く飲むわよ！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7441, 126801, 2, 1, "こうやって\n直に触れると\n肌触りの良さが\nよく分かるわ♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7442, 126801, 3, 1, "今日もかわいいわね～\nどう？　今度\nお姉さんの一人飲みに\n付き合わない？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7443, 126801, 4, 1, "いい香りね～…はっ！\n麦しゅわの匂いの\nアロマポットなんて\nあったらよくない？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7444, 126801, 5, 1, "こんな格好で火に\n当たってると焙られる\nスルメの気分が\nちょっと分かるわ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7446, 126801, 7, 1, "もぐもぐ…うん、\n結構おつまみにも\nむいてそうね～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7447, 126801, 8, 1, "試験に使った参考書、\nたまには読み返して\nおかないとね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7449, 126801, 10, 1, "ふふ♪\nいい手触りね～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7450, 126801, 11, 1, "…はふう～…\nゴロゴロするのって\n最高ね～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7451, 126901, 1, 1, "ん…やっぱり\nこの水着…\n大胆、なんじゃ…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7452, 126901, 2, 1, "海でもお世話って、\nできるのかしら…？\n浮き輪代わりになる、\nとか…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7453, 126901, 3, 1, "～♪\nふふ…海に行く\n準備も、楽しい…かも", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7454, 126901, 4, 1, "あうぅ…水着で\n歩いてると…お尻…\nすぐ食い込んじゃう…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7455, 126901, 5, 1, "海では、はしゃいだ\nほうがいいのよね…\nわ、わー、い…♪\n…こうかしら？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7456, 126901, 101, 4, "あなたとなら…\n楽しい夏の思い出、\n作れる…気がするの…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7457, 126901, 102, 8, "また来年も…\n再来年も…\nあなたと海へ\n行けますように…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7458, 126901, 1, 1, "水着の着方を\n練習してるの…\n後ろ、変じゃない…？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7459, 126901, 2, 1, "海に行く支度…\n進んでるかしら…？\nお手伝い、する…？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7460, 126901, 3, 1, "ひ、日除けの準備は\n万全よ…\nずっと太陽の下に\nいたら…溶けちゃう…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7461, 126901, 4, 1, "泳ぐ練習…\nこ、ここでするのは\n…さすがに変？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7462, 126901, 101, 4, "人混みに入るのは\nまだ不安で…\nでも、あなたといれば\nきっと…大丈夫…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7463, 126901, 102, 8, "あなたのおかげで\n海の楽しいところを\nたくさん知ったわ…\nありがとう…♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7464, 126901, 1, 1, "お出かけの日の朝は\n早い、のよね…？\nだから、ずっと\n起きていたわ…", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7465, 126901, 2, 1, "今頃ビーチの日差しは\n強いんでしょうね…\nそ、想像しただけで…\nめまいが…", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7466, 126901, 3, 1, "ビーチの夜といえば\n花火って、聞いたわ…\nバン、って言わない\n花火…ある…？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7468, 126901, 2, 1, "素肌にふわふわが…\n気持ちいい…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7469, 126901, 3, 1, "うふふ…\n一緒に海…行く？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7470, 126901, 4, 1, "ふぅ…♪\n気分まで…\n晴れやかになるわ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7471, 126901, 5, 1, "暑いような…\n寒いような…\n不思議な気持ちに\nなるわ…ね？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7473, 126901, 7, 1, "泳いだ後には\nいっぱい食べたほうが\n体によさそう、ね…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7474, 126901, 8, 1, "ビーチの日陰で\n読書するのも…\n気持ちよさそう…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7476, 126901, 10, 1, "これで合ってる…？\n遊び方わからなくて…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7477, 126901, 11, 1, "ふわふわ…♪\n海の底にいるみたい…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7478, 127001, 1, 1, "恥ずかしい格好で\n室内を歩くこの快感\nクセになりそうです…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7479, 127001, 2, 1, "この水着…\nどうしてかポーズを\n決めたくなりますね…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7480, 127001, 3, 1, "海にはどんな生き物が\nいるのでしょう～\n胸が高鳴ります～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7481, 127001, 4, 1, "夏は暑いですね…\nクウカの体が熱いのも\n夏のせい…ぐふふ…♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7482, 127001, 5, 1, "ドＳさんと無人島に\n取り残されて…という\n妄想はかなりイイです", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7483, 127001, 101, 4, "ドＳさんの\n欲望に満ちた眼差し…\nクウカ、受け止めます", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7484, 127001, 102, 8, "海に行くのが\n楽しみです…\nドＳさんと…ぐふふ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7485, 127001, 1, 1, "「もっと過激な水着を\n見せてみろよ」なんて\nいけませんドＳさん…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7486, 127001, 2, 1, "水着のままで\n踊ればいいのですね…\nドＳさんの望みなら！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7487, 127001, 3, 1, "ドＳさんの熱い視線を\n感じます…じゅるり…\nもっと見てください…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7488, 127001, 4, 1, "こんな格好のクウカを\n放っておくなんて…\nさすがですドＳさん！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7489, 127001, 101, 4, "海で水着といえば\n何かが起きる予感…！\nドＳさんの新たな攻め\n期待していますね…！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7490, 127001, 102, 8, "恥ずかしいですけど\n頑張っちゃいました\nドＳさん…こういう\n水着、好きですか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7491, 127001, 1, 1, "こんな朝からクウカを\n水着に着替えさせて…\n本当にドＳさんは\n欲望に正直ですね！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7492, 127001, 2, 1, "こんな昼間から\nドＳさんがヌルヌルの\n液体をクウカに～！\nえっ…日焼け止め？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7493, 127001, 3, 1, "ドＳさん…これから\nクウカと夜の海を\n見に行きませんか？\nきっと綺麗ですよ…♪", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7495, 127001, 2, 1, "ここなら妄想に\n集中できます～♪\nじゅるり～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7496, 127001, 3, 1, "こうもかわいいと\n妄想に登場させるのも\n気が引けます…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7497, 127001, 4, 1, "クウカを昂らせる\nこの香り…！\nは！　ドＳさんの罠！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7498, 127001, 5, 1, "体を熱くさせて\n水着を脱がそうと！？\nテクニカルです…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7500, 127001, 7, 1, "クウカの口の中に\nたくさん…♪\n止まりません…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7501, 127001, 8, 1, "読書は心が穏やかに\nなりますね…！\nぐふふ…穏やかに…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7503, 127001, 10, 1, "いい乗り心地です…♪\nあっ…普通に楽しんで\nしまいました…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7504, 127001, 11, 1, "これは…\n妄想に役立ちそうな\n感触です…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7505, 100501, 103, 12, "成長した自分に\nあわせて、もっと\nかっこいいポーズを\n研究するッス！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7506, 100501, 103, 12, "これからも一緒に\n最高のヒーローを\n目指していくッスよ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7507, 127101, 1, 1, "お父さん、娘の\n水着姿がそんなに\nうれしいの？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7508, 127101, 2, 1, "怖い話を子どもたちに\n聞かせたら泣かれて\nしまいました…\n加減が難しいです…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7509, 127101, 3, 1, "海に行くと父が\n水着の女性ばかり\n追いかけて困ります…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7510, 127101, 4, 1, "拾った貝殻で\n新しい占いでも\nはじめてみようかな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7511, 127101, 5, 1, "貝殻を耳に当てると\n波の音が…あっ\nこれはダメです\nよくない声がします", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7512, 127101, 101, 4, "この飲み物、スーッと\n体に沁み込んでくる…\nあの人にも\n教えてあげたいです", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7513, 127101, 102, 8, "強い日差しも騒がしい\n海も苦手なのに\nあの人となら不思議と\n楽しく思えるんです", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7514, 127101, 1, 1, "夏限定、水と氷を\n使った特別な占いは\nいかがですか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7515, 127101, 2, 1, "暑い日々にはやはり\n怪談ですね\n厳選フルコースで\nお聞かせします", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7516, 127101, 3, 1, "寒気がする？　それは\n霊ではなくかき氷の\n食べ過ぎですよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7517, 127101, 4, 1, "今日の占い、あなたの\nラッキーパーソンは…\n白い水着の女性です", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7518, 127101, 101, 4, "あの、よければ\n日焼け止めを塗って\nもらえませんか？\n背中は届かなくて…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7519, 127101, 102, 8, "海に行きませんか？\n二人きりで静かな\n砂浜を散歩したいです", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7520, 127101, 1, 1, "潮騒だけが響く朝の海\n心が落ち着きます\nこれから一緒に散歩に\n行ってみませんか？", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7521, 127101, 2, 1, "不思議です。どうして\n普通のご飯でも、海で\n食べると特別おいしく\n感じるのでしょう…", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7522, 127101, 3, 1, "夜の海に行きませんか\n今なら夜光虫が\n見られるかも\nしれませんよ", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7524, 127101, 2, 1, "夏は知らぬ間に体力を\n消耗しています\n適度に休息して\nいきましょう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7525, 127101, 3, 1, "このぬいぐるみ…\n大丈夫です\n何も憑いては\nいませんよ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7526, 127101, 4, 1, "ほのかにお線香\nみたいな香り…\nなんだか\n落ち着きます", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7527, 127101, 5, 1, "人魂、鬼火\nセントエルモの火…\n炎を見ていると色々と\n連想してしまいます", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7529, 127101, 7, 1, "お腹を壊さぬよう\n食い合わせには\nご注意ください", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7530, 127101, 8, 1, "これはいい\n怪談集ですね…\nつい読みふけって\nしまいます", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7532, 127101, 10, 1, "ふふ、こういうのに\n乗るとなんだか\n子どもに戻った\nみたいですね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7533, 127101, 11, 1, "こ、これは…\nちょっとメルヘン\n過ぎて恥ずかしい\nですね…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7534, 126501, 1, 1, "本当に素敵なお家…♪\n騎士たちの人柄を\n感じられるようで…\n不思議と落ち着きます", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7535, 126501, 2, 1, "調度品をつい間食…\nという習慣がこちらの\n世界にはないのですね\n…気をつけないと", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7536, 126501, 3, 1, "手土産のお菓子は\nどちらに置けば\nよいでしょうか？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7537, 126501, 4, 1, "まるでおとぎ話の中に\n入ってしまったかの\nよう…別世界とは\nよく言ったものです", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7538, 126501, 5, 1, "お忍び…ふふっ♪\nときめく響きです\nなんだかお茶目で…\n密やかな印象が", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7539, 126501, 101, 4, "ふわ…\nああ、ついあくびが…\n居心地がよすぎるのも\n困り物ですね…ふふ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7540, 126501, 102, 8, "騎士には不思議な力が\nあるだけでなく\n何者かの加護が\nあるようですね…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7541, 126501, 1, 1, "この『王家の装備』は\n少し気難しいのです\nわたし以外が触れると\nその…ビリビリっと", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7542, 126501, 2, 1, "子どもたちに新しい\n遊びを教わりました♪\nにーらめっこしましょ\nあっぷっ…ぷ～っ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7543, 126501, 3, 1, "実は憧れていたのです\n友人の家を訪ねると\nいう経験に…ふふっ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7544, 126501, 4, 1, "ぎるどはうす…\nぎるど活動の拠点だと\n管理協会？　の方に\nうかがいました", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7545, 126501, 101, 4, "あなたは運命を\n信じますか？\nわたしは…信じます\nよい運命だけですが♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7546, 126501, 102, 8, "もしわたしが本当に\n天使だったとしたら…\nどうされますか？\n…ふふ、冗談です♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7547, 126501, 1, 1, "パンケーキの香りで\n目覚める朝は\nとても良いものですよ\nぜひともお試しを♪", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7548, 126501, 2, 1, "ひと休みの時間ですね\n食事にお昼寝…\nすべきことを行って\n英気を養いましょう", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7549, 126501, 3, 1, "そろそろ休まれては？\n眠れないようであれば\n寝物語か子守歌でも\nふふ、遠慮は無用です", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7551, 126501, 2, 1, "すう………はっ！\n眠ってはいませんよ？\nいくら居心地が良い\nからといって…ふわぁ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7552, 126501, 3, 1, "まあ、あいらしい…♪\nぜひわたしの寝室に\n招待させてください", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7553, 126501, 4, 1, "なんて、かぐわしい…\nオーブンからただよう\n焼き菓子の香りとは\nまた異なる魅力が…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7554, 126501, 5, 1, "みなで暖をとりながら\n互いに語り笑い合う\n…心休まる時間ですね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7556, 126501, 7, 1, "食は心と体に\nゆとりを生みます\n…辛い食べ物の場合？\nそれは、その…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7557, 126501, 8, 1, "こちらの世界の知識を\n蓄えておかなければ\nテーブルマナーに\nついてのページは…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7559, 127201, 1, 1, "新しい色の蛍光ペン\n買ったのよね\nこれで勉強が\n楽しくなるわ～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7560, 127201, 2, 1, "学食のランチ、結構\nおいしいのよね～\n今度ペコリーヌに\n教えてやろうかしら？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7561, 127201, 3, 1, "ふふふ…扉に黒板消し\n仕掛けてるの…あいつ\n引っかかるかしら？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7562, 127201, 4, 1, "部活するなら運動部も\n悪くないかもね～\n夕日に向かって\n突っ走ってやるわ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7563, 127201, 5, 1, "毎日窮屈な制服着て\n学生も大変ね～\n…ま、かわいいけど", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7564, 127201, 101, 4, "あいつに貸したノート\n変な落書きとか\nしてなかったよね…？\n…その、相合傘とか…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7565, 127201, 102, 8, "昨日の家庭科で\n作ったクッキー…\nあいつにどうやって\n渡そうかな…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7566, 127201, 1, 1, "宿題教えてって？\nしょうがないわね～\nじゃあ一緒に\nやりましょ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7567, 127201, 2, 1, "あ、明日あたしたち\n日直当番よね？\nめんどい仕事は\nあんたに任せるわ～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7568, 127201, 3, 1, "寮生活は楽しいけど\n就寝時間が早すぎよね\nいきなり消灯されると\nビックリするわ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7569, 127201, 4, 1, "校則で禁止だけど、\nにけつでほうき通学\nとか青春っぽくない？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7570, 127201, 101, 4, "制服って好きな人とも\n自然にお揃いになれて\nいいわよね…って別に\nあんたとって訳じゃ…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7571, 127201, 102, 8, "あんたネクタイ\n曲がってるわよ～？\nほら直したげるから\nこっち向いて？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7572, 127201, 1, 1, "おはよ～！\n今日も元気に登校よ！\n忘れ物ないようにね！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7573, 127201, 2, 1, "午前の授業疲れたわ～\nしっかりお昼を食べて\n午後も頑張るわよ！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7574, 127201, 3, 1, "うう…学校の怪談\n思い出しちゃった…\n…別に怖くないけど！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7576, 127201, 2, 1, "ふかふか～\n…気持ち良すぎて\n制服のまま\n寝ちゃいそうね…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7577, 127201, 3, 1, "かわいくて癒されるわ\n寮に持ち込んでも\n大丈夫かしら…？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7578, 127201, 4, 1, "集中力の高まる香りで\n勉強の効率爆上がりね\n次のテストで\nいい点取るわよ～！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7579, 127201, 5, 1, "うう、ちょっと冷えて\n来たわね…\nスカートの下に\nジャージ穿こうかしら", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7581, 127201, 7, 1, "もぐっ…もぐっ…\n食べとかないと教室で\nお腹なっちゃうわ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7582, 127201, 8, 1, "図書室で借りた本\n明日返却日なのよね…\n今日中に読むわよ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7584, 104301, 103, 12, "あいつに背中を\n預けるのもいい気分だ\nいつかは、全部…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7585, 104301, 103, 12, "な、なんだよ？\nじっと見るんじゃねぇ\n尻尾が揺れちまうだろ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7586, 127301, 1, 1, "魔法の練習！\nむむむ～…\nそれっ…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7587, 127301, 2, 1, "そだ！　これ着て\nルー学行こっかな？　\n制服自慢しちゃお♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7588, 127301, 3, 1, "赤点回避の魔法とか\nあったら最高じゃね？\n誰かうちに教えて～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7589, 127301, 4, 1, "制服が一番映える\n場所ってどこだろ？\n教室？　遊園地？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7590, 127301, 5, 1, "ベンキョーつらたん…\n今度キャルっちに\n助けてもらお", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7591, 127301, 101, 4, "部活か…\nヒデサイと一緒に\nやったら楽し～かも♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7592, 127301, 102, 8, "男子の制服\nもにカワだった…\n今度ヒデサイに\n借りよっかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7593, 127301, 1, 1, "ちょすー、うちの\nさいきょー魔法で\nテンアゲしてあげる～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7594, 127301, 2, 1, "九九って呪文ぽくね？\nほら、例えばヒチシ…\nヒチシ、なんだっけ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7595, 127301, 3, 1, "ヒデサイセンセー\nうちにベンキョー\n教えて！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7596, 127301, 4, 1, "学割マジ助かるー\nヒデサイ、一緒に\nどんどん使ってこー♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7597, 127301, 101, 4, "今度ヒデサイに\nお弁当作ったげる\n屋上で一緒に食べよ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7598, 127301, 102, 8, "ヒデサイのバイブス\n上がる言葉教えてよ\nつらたんな時、うちが\nいっぱい言ったげるよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7599, 127301, 1, 1, "ちょすちょす～\n今日も朝から\nバイブス十分♪", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7600, 127301, 2, 1, "お昼どうしよっかな\nたまにはガッツリ\nいっちゃう系？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7601, 127301, 3, 1, "やべ、宿題まだ\nできてないし…\nヒデサイ助けて～", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7603, 127301, 2, 1, "充電ちう～\n…ガッコーのイスも\nこれにして～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7604, 127301, 3, 1, "かわいいし、\n癒されるし、キュン死\nしちゃうじゃん…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7605, 127301, 4, 1, "リフレッシュの\n魔法発見！\nいい香り～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7606, 127301, 5, 1, "ぽかぽかサイコー♪\n今日はもーずーっと\nここにいたいかも～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7608, 127301, 7, 1, "超おいし～♪\n学食にあったら\n毎日リピする～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7609, 127301, 8, 1, "ふむふむ…\nねむみパない…\n続きはまた今度～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7611, 127501, 1, 1, "今度は子どもたちも\nサラサリアに連れて\n行ってあげたいわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7612, 127501, 2, 1, "子どもたちから\nお姫さまみたいって\n言われちゃった♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7613, 127501, 3, 1, "この剣…火を点けたり\nするのに便利なのよ\nコントロールも効くし", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7614, 127501, 4, 1, "あのサラサリアの\nカラっとした暑さが\nちょっと恋しいわ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7615, 127501, 5, 1, "買ってきたお土産、\nお世話になってる\nみんなに配りましょ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7616, 127501, 101, 4, "あんたと一緒なら\nどこの国でだって\n暮らしていけそうね♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7617, 127501, 102, 8, "今の事業を大きくして\nあんたと世界中の国を\n飛び回ったり…なんて\nすてきじゃない？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7618, 127501, 1, 1, "サラサリアで食べた\n料理が恋しい…？\nもう…また今度\n作ってあげるわよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7619, 127501, 2, 1, "今度旅行するなら\nどこに行こうかしら？\n希望があれば教えてね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7620, 127501, 3, 1, "サラサリアの香辛料、\nランドソルでも\n人気が出そうよね\nいいビジネスの予感！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7621, 127501, 4, 1, "そんなにじっと\n見られるとさすがに\n恥ずかしいってば…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7622, 127501, 101, 4, "お嫁さんは憧れるけど\nお婿さんになって\nもらうのもありよね…\nあんたはどう思う？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7623, 127501, 102, 8, "やっぱり、あんたには\nいつもあたしの隣に\n居てほしいな…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7624, 127501, 1, 1, "陽射しが強くなる前に\n雑多な用事は\n片付けちゃいましょ", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7625, 127501, 2, 1, "サラサリアで食べた\n料理を再現してみたの\n味見してくれる？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7626, 127501, 3, 1, "暑さに負けないように\n睡眠はちゃんと\n取っておかないと", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7628, 127501, 2, 1, "こっちでもあっちでも\n忙しいのは変わらない\nわね…ちょっと休憩～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7629, 127501, 3, 1, "サラサリアで見た\nぬいぐるみも\nかわいかったわね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7630, 127501, 4, 1, "お香って地域ごとの\n特色が出るわよね\nこの香りは…\nちょっと神秘的？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7631, 127501, 5, 1, "砂漠地方も夜は冷える\nのよね。温かいものが\nあると安心するわ…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7633, 127501, 7, 1, "食べ慣れた味って\nどうしてほっと\nするのかしら…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7634, 127501, 8, 1, "今後の取引相手に\nなるかもしれないし\n向こうの文化も\n勉強しておかないと…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7636, 101401, 103, 12, "犯人はこの中にいる！\n探偵として一度は\n言ってみたいセリフさ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7637, 101401, 103, 12, "助手くんの情報を\n仕入れておかないと…\n理想の私を、ずっと\n見ていてほしいからね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7638, 127601, 1, 1, "久々の長旅で\n里心がついちまったね\nしばらくランドソルで\nのんびりしようか", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7639, 127601, 2, 1, "たまには違う服装\n違う得物を使うのも\n悪くないね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7640, 127601, 3, 1, "サラサリアにいた時は\n砂まみれで大丈夫かと\n思ってたけど、今じゃ\n懐かしく感じるよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7641, 127601, 4, 1, "魔法の絨毯、あれは\n楽だし速いし便利\nだったねえ…土産に\n一枚欲しかったな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7642, 127601, 5, 1, "砂上での戦いは\nなかなかいい鍛錬に\nなったね…今度は\n修行しに行くとしよう", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7643, 127601, 101, 4, "サラサリアの人たちも\n気のいい連中だったが\n元気にやってるかねえ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7644, 127601, 102, 8, "旅の恥はかき捨てって\nいうけど、悪い旅じゃ\nなかったね。次は\nアイツとどこに行こう", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7645, 127601, 1, 1, "この格好を冷やかしに\n来たのかい？\n生憎、もうすっかり\n慣れちまったよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7646, 127601, 2, 1, "ギルドの連中に\n土産をわたしに\n行くところなんだ\n付き合っておくれよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7647, 127601, 3, 1, "久々に水場で\n釣りがしたいねえ…\nもちろんお前さんも\nくるんだろう？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7648, 127601, 4, 1, "おっと\nサラサリアの連中から\n便りが来てるよ\nさ、一緒に読もうか", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7649, 127601, 101, 4, "最近サラサリアの踊り\nを練習してるんだ\nあとで振付けを\n見ておくれよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7650, 127601, 102, 8, "なんだいお前さん？\n…なんだか若奥さん\nっぽいって？　まあ、\nそりゃあ、ねえ…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7651, 127601, 1, 1, "いい時間だね\n日が昇りきる前に\nやることをすませ\nとかないと", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7652, 127601, 2, 1, "昼はどうしようかね\nサラサリア風の\n食事でもつくろうか", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7653, 127601, 3, 1, "さて、早めに寝ようか\n砂漠の夜は冷えるから\n…っと、まだ旅行中の\n気分でいたよ", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7655, 127601, 2, 1, "良い座り心地だ\nサラサリア宮殿の\n応接間を\n思い出すねえ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7656, 127601, 3, 1, "ランプの魔神に\nあげたら喜ぶかねえ？\n次に会う時までに\n見繕ってみようか", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7657, 127601, 4, 1, "この香り…\nサラサリア土産の\n香木かい？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7658, 127601, 5, 1, "旅先での焚火を\n思い出すよ\nなんだかほっとするね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7660, 127601, 7, 1, "ふふ、慣れてる味も\nいいけど、むこうの\n味付けが懐かしく\nなっちまうね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7661, 127601, 8, 1, "ふむふむ…\n千夜一夜物語か\nおもしろいもんだねえ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7663, 126001, 1, 1, "立派なギルドハウス♪\n帰る場所が素敵だと\n冒険に出るのも\n楽しくなりそうです♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7664, 126001, 2, 1, "お掃除でも…と\n思いましたけど、\nさすがお兄さんのお家\nきれいにしてますね♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7665, 126001, 3, 1, "わたしたちもいつか\n立派なギルドハウスが\n持てる冒険者に\nなりたいです…♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7666, 126001, 4, 1, "わたし冒険者ですけど\n家を守ってお兄さんの\n帰りを待つのも\n悪くない…かも？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7667, 126001, 5, 1, "お菓子焼こうかな…♪\nちょっと多めに作って\nお兄さんにも\n食べてもらおうっと♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7668, 126001, 101, 4, "このお家、とっても\n大好きになりました♪\nだからその…もう少し\nいてもいいですか…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7669, 126001, 102, 8, "お兄さんに\n感謝の手紙を書いて\n枕元に隠して\nおこうかな…♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7670, 126001, 1, 1, "…お兄さん\nお暇なんですか？\nじゃあ一緒に冒険へ\n出かけましょう♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7671, 126001, 2, 1, "お兄さんは苦手な食材\nとかありますか？\nあの、もしよければ\n今から手料理を…♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7672, 126001, 3, 1, "まったり過ごす\nお家時間もいいですね\n冒険者には羽休めも\n大事ですから♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7673, 126001, 4, 1, "いつでもお兄さんを\n守れるように\nこの姿でいることに\n慣れておきますね…！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7674, 126001, 101, 4, "お兄さん眠そうです…\nちょっとお昼寝\nしちゃいましょうか♪\nはい、膝枕…♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7675, 126001, 102, 8, "お兄さん怪我を…！？\nわたしが治療を…\nふー…ふー…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7676, 126001, 1, 1, "おはようございます♪\n朝、元気に挨拶すれば\n幸せな一日が過ごせる\nそうですよ♪", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7677, 126001, 2, 1, "お昼ごはんの時間です\n…手がふさがってる？\nわかりました、じゃあ\nあ～んしますね♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7678, 126001, 3, 1, "ふわぁ…眠くなって\nきちゃいました…\nお兄さん…お泊りして\nいいですか…？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7680, 126001, 2, 1, "ふにゃ～～…♪\nこんな姿、他の人には\n見せられません…♪\nもふもふ～～…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7681, 126001, 3, 1, "お兄さんこういうの\n好きなのかな～…\nふふっ、わたしも♪\nぎゅ～～～っ♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7682, 126001, 4, 1, "アロマの癒し効果…\n上手くアレンジして\nみなさんの治療に\n活かせないかな～？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7683, 126001, 5, 1, "は～気持ちいい～♪\n身体が暖まると\n心もぽかぽかです♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7685, 126001, 7, 1, "うふふ、おいしい♪\nプレシアちゃんなら\n無限にぱくぱく～って\nしちゃいそうです…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7686, 126001, 8, 1, "本はわたしを\n知らない世界に\n連れていってくれる…\nまるでお兄さんです♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7688, 103801, 103, 12, "話題の新刊…\nあの人も読んでるかな\n今度会う時、感想\n聞いてみたいな…♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7689, 103801, 103, 12, "うにゃっ…あ、熱を\n測ってくれたんですか\nありがとうございます\nバッチリ元気ですよ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7690, 126101, 1, 1, "お兄の、けはい…\nん、みつけた\nぎゅ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7691, 126101, 2, 1, "きょろきょろ\nここも\nぱとろる、するよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7692, 126101, 3, 1, "おうち\nにぎやか\nさみしく、ない", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7693, 126101, 4, 1, "きょだい、おにぎり\nきょだい、肉まん\nきょだい…お兄？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7694, 126101, 5, 1, "はっぴーちゃん\nどこ？\nお兄、たべた？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7695, 126101, 101, 4, "くんくん…この、へや\nお兄の、におい\nすんでる\nすめる？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7696, 126101, 102, 8, "おそうじ、にがて\nでも\nれんしゅう\nきゅっきゅ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7697, 126101, 1, 1, "みみ、さわり、たい？\nんー…だめ…\nむずむず\nなので", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7698, 126101, 2, 1, "くろぷれしあ\nかっこいい？\nん…あざます", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7699, 126101, 3, 1, "お兄の、おうち\nきょうみ、ましまし\nたんけん\nするね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7700, 126101, 4, 1, "なでなで、される…と\nねむ…く…\nぶぅ…ぶぅ…ぶぅ…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7701, 126101, 101, 4, "かみ、りぼん\nおてつだい\nよろ\nです", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7702, 126101, 102, 8, "ここ\nすむ\nね？\nまいにち、いっしょ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7703, 126101, 1, 1, "ぐっもにん\nあさごはん\nあさごはん", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7704, 126101, 2, 1, "お兄\nおつかれ？\nれっつ、まいぺーす", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7705, 126101, 3, 1, "ひとりで、ねれる\nでも\nひとりじゃ、なくても\nねれる、よ…？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7707, 126101, 2, 1, "ふかふか\nパパの、おなか\nみたい", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7708, 126101, 3, 1, "こんにちは\nぷれしあ、です\nおともだち、なろ？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7709, 126101, 4, 1, "いい、におい\nでも、お兄\nもっと、いい、におい", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7710, 126101, 5, 1, "とりま\nお肉、やこ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7712, 126101, 7, 1, "もぐもぐ…\nむげん肉まん、も\nきぼう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7713, 126101, 8, 1, "ぜんぶ、よんだ\nもっと、ぶあついほん\nでも、いーよ？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7715, 127901, 1, 1, "幸せなクリスマスを\n過ごすため\nギルドハウスを\nきれいにしなくちゃ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7716, 127901, 2, 1, "えへへ♪\n彼へのプレゼント…\n枕元にそっと\nおいておきましょう☆", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7717, 127901, 3, 1, "クリスマスといえば\n七面鳥の丸焼き…☆\nよだれを拭いて\nいざ準備です♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7718, 127901, 4, 1, "彼に用意した\nプレゼント…\n大きすぎて\nくつ下に入りません…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7719, 127901, 5, 1, "プレゼントは\n貰う方も、贈る方も\nどちらも幸せを\n感じますね…☆", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7720, 127901, 101, 4, "夜景が見える\nレストランであの人と\nクリスマスデート…\n憧れちゃいますね☆", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7721, 127901, 102, 8, "プレゼントはわたし…\n恥ずかしいですけど\nこの贈り物、彼に\n喜んでほしいです…☆", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7722, 127901, 1, 1, "オーナメントを\nたくさん用意したので\n二人でツリーに\n飾りつけましょう☆", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7723, 127901, 2, 1, "あはは、お腹\n空いちゃいました？\nパーティーまで\n我慢してください♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7724, 127901, 3, 1, "ジングルベ～ル♪\nあなたも一緒に\nクリスマスソング\n歌っちゃいましょう♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7725, 127901, 4, 1, "サプライズプレゼント\nの用意をして…って\nあなた！？　い、今の\n聞いてませんよね…？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7726, 127901, 101, 4, "クリスマスの夜は\nパーティーしましょう\nできれば…その…\n二人っきりで…☆", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7727, 127901, 102, 8, "あなたと過ごした\n最高のクリスマス…\nずっとずっと心の奥に\nしまっておきますね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7728, 127901, 1, 1, "おはようございます！\n皆の願いが叶うような\n素敵な一日に\nしましょうね☆", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7729, 127901, 2, 1, "お昼ですね！\nディナーの買い出しが\n済んだら、少しお昼寝\nしちゃいましょうか☆", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7730, 127901, 3, 1, "そろそろ就寝の\n時間ですね～\n枕元にくつ下の準備を\nお忘れなく～☆", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7732, 127901, 2, 1, "ふ～気持ちいい…☆\nお家でまったり過ごす\nクリスマスも\nいいですよね…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7733, 127901, 3, 1, "かわいいですね～♪\nサンタさんからの\n贈り物でしょうか～？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7734, 127901, 4, 1, "すう～…はあ～…\nすごく素敵な香り…\n炊き立てのご飯と\nいい勝負です…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7735, 127901, 5, 1, "暖かいですけど…\nあの日の山小屋を\n思い出して\n恥ずかしいですね…☆", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7737, 127901, 7, 1, "むしゃむしゃもぐもぐ\n止まりませんよ\nクリスマスですから\nむしゃむしゃもぐもぐ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7738, 127901, 8, 1, "物語に出てくる\nサンタさんみたいに\nわたしも幸せを\n運びたいです…☆", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7740, 127901, 14, 1, "わっ！\nこ…これは素敵な\nサプライズプレゼント\nですね…☆", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7741, 127901, 15, 1, "これを着ればわたしも\n立派なサンタさんに\nなれますかね…？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7742, 106601, 103, 12, "ここが今のあたしの\n居場所ってやつです\nふふっ、なかなか\n悪くねー気分ですよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7743, 106601, 103, 12, "これからはボス代行\nとしてブイブイ\nいわせてやるです\nついてくるですよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7744, 128001, 1, 1, "陛下～♪\n貴女のクレジッタが\nおめかしして\nはせ参じましたわ～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7745, 128001, 2, 1, "パーティーとは景気の\n良さを見せつける場…\n倹約などは\nもっての外でしてよ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7746, 128001, 3, 1, "この天井の高さなら\n搬入できそうですわね\n陛下用の特注ケーキ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7747, 128001, 4, 1, "…ぶぇっくしゅ！\nふう…少し背中を\n開けすぎたかしら…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7748, 128001, 5, 1, "ここは秘書の目も\n届きませんから\nチクりメモの心配も\nありませんのよね～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7749, 128001, 101, 4, "いくらでも二度見して\nよろしくてよ？\n本当にわたくしだと\n心から納得できるまで", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7750, 128001, 102, 8, "袖の下以外の贈り物も\nなかなかどうして…\n悪くないものですのね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7751, 128001, 1, 1, "こう見えてダンスの\nたしなみはありますの\nお相手してあげても\nよろしくってよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7752, 128001, 2, 1, "ドレスはもちろん\n特注品ですわ♪\nもしや貸衣装だと\n思いまして？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7753, 128001, 3, 1, "バッグの中身？\n仕事に必要なものと…\n心を潤わせるための\n宝石がいくつか♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7754, 128001, 4, 1, "やる気というものは\n仕事に必要な条件です\nいわばドレスコードの\nようなものですわね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7755, 128001, 101, 4, "全ての仕事は自分の\n血となり肉となり\nお金にもなるもの\nなのです。経験談です", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7756, 128001, 102, 8, "今年の教訓を活かして\n来年は更に素晴らしい\nクリスマスにして\n差し上げますわ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7757, 128001, 1, 1, "おはようございます\n今日という稼ぎ時の\nはじまりですわよ！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7758, 128001, 2, 1, "安くて早くておいしい\nそれがランチに必須の\n栄養素ですわ！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7759, 128001, 3, 1, "宵越しの仕事は\n安眠の敵！\n熟睡したいのであれば\n今が勝負時ですわよ！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7761, 128001, 2, 1, "たまにはこうして\n休息を取ることも…\n…はっ！？\nドレスにシワが～っ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7762, 128001, 3, 1, "…わたくしの勘が\n言っていますわ\nこの愛らしさ！\n爆売れ確・定！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7763, 128001, 4, 1, "いい香りですわね…\nお金の匂いには\n敵いませんけれども♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7764, 128001, 5, 1, "……ハッ！？\n完全にボーッとして\nしまいましたわ…！\nぬくもり…恐ろしい！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7766, 128001, 7, 1, "考えませんわ…\n考えませんわよ！\nカロリーのことはっ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7767, 128001, 8, 1, "ビジネス本以外の本を\n読むのも、たまには\n悪くありませんわね…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7769, 128001, 14, 1, "よくできていますわね\nこの絵本！\n子ども向けだと甘く\n見ていましたわ…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7770, 128001, 15, 1, "この視界の狭さ…\n思い出しますわね\nティッシュ配りの\nアルバイト時代を…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7771, 128101, 1, 1, "ふふふ…こっそり\nスイーツを食べる\nリノちゃん見～っけ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7772, 128101, 2, 1, "お姉ちゃんの抜き打ち\nお部屋チェック～♪\n…ケーキの食べこぼし\nこれはリノちゃんだね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7773, 128101, 3, 1, "この衣装は目立つから\nどこにいても弟くんに\n気付いてもらえるね！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7774, 128101, 4, 1, "弟くん、協力して！\nリノちゃんにお勉強\nしなさいって圧を\nかけるんだよっ…♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7775, 128101, 5, 1, "絆の力を感じるよ\nこれならもっと万全な\n弟くんのお世話が\nできそうだねっ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7776, 128101, 101, 4, "お姉ちゃんもたまには\nリノちゃんみたいに\n甘えてみようかな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7777, 128101, 102, 8, "弟くんとリノちゃん\nどっちも大切な\n私のきょうだいだよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7778, 128101, 1, 1, "分かってるよ♪\nお姉ちゃんの膝枕を\nご所望だねっ？　え？\n違う？　違わないよ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7779, 128101, 2, 1, "リノちゃんがね\nお姉ちゃんチョップを\n防ぐ妹白刃取りを\n訓練してるんだよね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7780, 128101, 3, 1, "お姉ちゃん頭突きは\n封印しないとね…\nだって、今使うと\n頭飾りで威力が…ね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7781, 128101, 4, 1, "ふふっ♪\n目をつぶってても\n弟くんは見えてるよ\n…そこでしょ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7782, 128101, 101, 4, "お姉ちゃんも\nリノちゃんみたいに\nかわいさいっぱいに\n振舞っちゃうぞ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7783, 128101, 102, 8, "弟くんっ♪\nいつもお姉ちゃんの\n声が届く範囲にいてね\n…絶対だよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7784, 128101, 1, 1, "おはよう、弟くん！\nリノちゃんが起こして\nくれたんだね♪\n…先を越されちゃった", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7785, 128101, 2, 1, "お姉ちゃんランチの\n時間だぞ♪\nさ、食べさせてあげる\nお口を開けて…♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7786, 128101, 3, 1, "おやすみの時間だよ\n…弟くんは真ん中ね\n左右には私たち姉妹が\nいるから安心だよ♪", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7788, 128101, 2, 1, "お姉ちゃんは全力で\nリラックスしてても\n弟くんの声一つで\nすぐにでも動けるよ♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7789, 128101, 3, 1, "リノちゃんこういうの\n好きそうだよね\n今度プレゼントして\nみようかな？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7790, 128101, 4, 1, "いい匂いがするね～\nあれ？\nリノちゃんのケーキの\n甘い匂いも混ざってる", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7791, 128101, 5, 1, "お姉ちゃんの\n弟くんへの愛も\n熱く燃えあがるね…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7793, 128101, 7, 1, "たくさん食べて\n栄養を補給して\nお姉ちゃん力に\n全て変換するよっ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7794, 128101, 8, 1, "ことわざ辞典…\nリノちゃんにこれを\n読ませようかな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7797, 128201, 1, 1, "そろ～り、そろり…\nお姉ちゃんが居ぬ間に\nスイーツを～…♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7798, 128201, 2, 1, "いつか\nお姉ちゃんに似合う服\n作ってみたいですね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7799, 128201, 3, 1, "あいたっ！？\nひらひらしたとこ\n踏んづけちゃいました", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7800, 128201, 4, 1, "お姉ちゃんと\nお兄ちゃんの二人から\n勉強しろって圧力が～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7801, 128201, 5, 1, "私の中にも秘めた力が\n眠っているはず…\nそう、妹パワーが", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7802, 128201, 101, 4, "私もお兄ちゃんを\n甘やかしてみたいです\n…お姉ちゃんみたいに", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7803, 128201, 102, 8, "お兄ちゃんの部屋を\nこっそり拝見～♪\nはっ！？　これは\nお姉ちゃんの視線…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7804, 128201, 1, 1, "お姉ちゃん頭突きは\n大地を揺らす威力です\n私とお兄ちゃんは\n挨拶感覚で喰らいます", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7805, 128201, 2, 1, "お姉ちゃんチョップは\n雲を裂く切れ味です\n早く妹白刃取りを\n完成させないと…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7806, 128201, 3, 1, "お姉ちゃんみたいに\n大人っぽく着こなす\nには一体どうしたら…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7807, 128201, 4, 1, "弓のデザインも\nかわいいんですよっ♪\n常に持ち歩きたいです\nけど…危ないですよね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7808, 128201, 101, 4, "きれいな服を着てると\nお姫さま気分に\nなれますね…あとは\n王子さまがいれば…♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7809, 128201, 102, 8, "お兄ちゃんっ！\n私は褒められて伸びる\nタイプなのでもっと\nナデナデしてください", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7810, 128201, 1, 1, "妹モーニングコール♪\nお姉ちゃんより先に\n起こしに来れました！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7811, 128201, 2, 1, "ああっ、お姉ちゃん！\nお兄ちゃんにアーン\nするなんてずるいです\n私もしたいですっ！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7812, 128201, 3, 1, "おやすみの時間ですね\nえへへ、きょうだいで\n並んで寝るのって\n楽しいですよね！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7814, 128201, 2, 1, "スイーツを食べた後は\nお腹を休めましょう～\n平気平気…ちゃぁんと\n運動もしますからぁ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7815, 128201, 3, 1, "お姉ちゃんも\nこういうかわいいもの\n好きなんですかね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7816, 128201, 4, 1, "くんくん…\nなんの匂いでしょうか\n新しいスイーツ？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7817, 128201, 5, 1, "きょうだい３人\n肩を寄せて暖まるとか\nよくないですかっ？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7819, 128201, 7, 1, "いつも元気な妹で\nいるためには\nよく食べるのが大事！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7820, 128201, 8, 1, "なんか姉に関する本が\nちらほらと混ざって…\nこれお姉ちゃんの\n仕業ですね！？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7823, 128301, 1, 1, "ふんふんふ～ん\nお正月で世間は\n浮かれてるですねえ\nま、いいですけど♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7824, 128301, 2, 1, "は～、お正月は\nだらだらできて\n最高ですね\n…いつも通りですけど", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7825, 128301, 3, 1, "ビッグな幹部は\nビッグなお年玉が\nもらえるはずです\n楽しみですね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7826, 128301, 4, 1, "今度ドラちゃんに\nお年玉を持っていって\nやりたいですね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7827, 128301, 5, 1, "そういえばアユアユと\n約束してたですよ…\nそろそろ出かける\nですかね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7828, 128301, 101, 4, "お年玉がもらえたら\n何に使うか…\n今のうちに\n考えとくです", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7829, 128301, 102, 8, "年神の報酬の駄菓子が\nたまってきたですね…\nガキどもにでも\n配っちまうですよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7830, 128301, 1, 1, "あたしのレアな\n晴れ着姿、今のうちに\nしっかり目に焼き付け\nとくがいいですよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7831, 128301, 2, 1, "お兄さんは今日も\nヒマそうですねぇ…\nしょうがないから\nかまってやるですよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7832, 128301, 3, 1, "そういえばカヤぴぃ\n見なかったですか？\n喧嘩屋初売りセール\nとか言ってたですけど", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7833, 128301, 4, 1, "お正月ならではの\n大きなシノギ…\nなにかないですかね？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7834, 128301, 101, 4, "いいとこに来たですよ\nさっき走ったら帯が\n曲がっちゃったです\n直すの手伝うです！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7835, 128301, 102, 8, "年神の仕事ですか？\nこれからいくとこです\nお兄さんも一緒に\n来やがるですよ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7836, 128301, 1, 1, "ふぁあ…\nどうしたですか？\nお正月に早起きなんて\n無粋ってヤツですよ…", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7837, 128301, 2, 1, "小腹が減ったですね\n一緒におせちでも\nつつきにいくですよ！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7838, 128301, 3, 1, "夜はあたしの時間\nと言いたいとこですが\n初夢のために早く\n寝とくですかね…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7840, 128301, 2, 1, "正月は食っちゃ寝\nするのが仕事…\nって聞いたこと\nあるですよ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7841, 128301, 3, 1, "ふふっ、晴れ着の\nあたしの次くらいには\nかわいいですね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7842, 128301, 4, 1, "なんかいい匂い…？\nよく知らんですけど\n晴れ着からも同じ\n匂いがしてるですね…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7843, 128301, 5, 1, "ふ～あったまるです…\nお正月の唯一の弱点は\n寒いことですからね…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7845, 128301, 7, 1, "もぐもぐ…正月太り？\nあたしには関係ない\n言葉ですね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7846, 128301, 8, 1, "あったあった…\nトーゴクには\nドラゴンの神宮も\nあるんですね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7848, 128401, 1, 1, "お正月のトラブルも\nハツネちゃんが\nめでたく解決\nしてみせるのだ～！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7849, 128401, 2, 1, "そうだ、新年会に\nサプライズ大作戦を\nしかけちゃおう！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7850, 128401, 3, 1, "イノリちゃんも\nカスミンも振袖姿が\nかわいかったな～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7851, 128401, 4, 1, "シオリンに\nお年玉あげたいし\n彼にバイトを紹介\nしてもらおうかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7852, 128401, 5, 1, "今度はどんな凧を\n飛ばそうかな？\nみんなが驚くような\n凧がいいよね～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7853, 128401, 101, 4, "ひらめいた！\n彼への年賀状は\nきらーんっ☆って\n感じにしよう！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7854, 128401, 102, 8, "来年も彼の隣で\n初日の出が\n見られるといいな～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7855, 128401, 1, 1, "今日はキミが\nギルドハウスに来る\n予知夢を見たんだよ～\nふふ、正夢になったね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7856, 128401, 2, 1, "この振袖どうかな？\nすっごくかわいいよね\nえへへ、ありがとう～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7857, 128401, 3, 1, "ハツネちゃんおみくじ\n開始！　きらーんっ☆\nキミは大吉なのだ～\nどう元気出たかな？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7858, 128401, 4, 1, "今度エルフの森にある\n薬草を使って七草粥を\n作ろうと思うんだ～\nぜひ食べてほしいな♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7859, 128401, 101, 4, "お正月っぽい遊びを\n色々と用意したのだ～\nたまにはギルドハウス\nでゆっくり過ごそうよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7860, 128401, 102, 8, "もしかして眠たいの？\n…じゃあ、また膝枕\nしてあげてもいいよ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7861, 128401, 1, 1, "いっぱい寝たから\n元気いっぱいだよ～\n今日もがんばろう！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7862, 128401, 2, 1, "お昼だね～\nおせちがあるから\nキミも食べるでしょ？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7863, 128401, 3, 1, "そろそろ寝る時間だね\n今日はどんな夢を\n見られるかな～", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7865, 128401, 2, 1, "いい座り心地～\n寝正月で過ごすのも\n最高なのだ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7866, 128401, 3, 1, "かわいいな～\n癒されてぐっすり\n眠れそうだよ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7867, 128401, 4, 1, "いい香り～\nリラックスすると\n眠たくなっちゃうよ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7868, 128401, 5, 1, "ポカポカいい気持ち～\n初日の出の温もりを\n思い出しちゃうな～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7870, 128401, 7, 1, "もぐもぐ、おいし～\nでもいっぱい食べたら\n眠くなっちゃった…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7871, 128401, 8, 1, "ふむふむ\nお正月料理も\n色々あるんだね～\n今度作ってみようかな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7873, 106501, 103, 12, "最近カレを見てたら\n何故かおいしそうに\n思えてくるんだよな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7874, 106501, 103, 12, "手握っててくれるか？\nじゃないとオレ…\n力が抑えきれない…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7875, 128501, 1, 1, "つかの間の平和を享受\nしながらぬくぬくと\n読書に耽る…\nふふ、最高だね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7876, 128501, 2, 1, "お正月飾りなんて\n置いたらはしゃいでる\nように見えるかな…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7877, 128501, 3, 1, "ま、まずい…\n一人遊びしすぎて\nお正月遊びをほぼ\n極めつつある…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7878, 128501, 4, 1, "ミステリーは\n時と場所を選ばない\nつまり…新年でも\n探偵は休まないよ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7879, 128501, 5, 1, "お世話になった人に\n年賀状でも書こうかな\n助手くん、喜んで\nくれるかな…えへへっ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7880, 128501, 101, 4, "絵馬に願い事を書こう\n助手くんと一緒に\nいられますように…♪\n…は、恥ずかしいな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7881, 128501, 102, 8, "助手くんはもっと\n派手な着物のほうが\n好みだったかな…？\nき、気になる…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7882, 128501, 1, 1, "やあ。助手くんも\nお正月休みを\n持て余してるのかい？\nふふ、似た者同士だね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7883, 128501, 2, 1, "助手くん助手くん！\n凧あげしにいこう！\n…そ、捜査の一環で", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7884, 128501, 3, 1, "着物は転びそうで\n怖いんだよなあ…\nお、押さないでね？\nフリじゃないよ！？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7885, 128501, 4, 1, "ひ、人目を憚る\n本を読むなら\nみんなが帰省中の今！\nはっ！　助手くん！？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7886, 128501, 101, 4, "ね、ねえ…助手くん…\n一緒に初詣いかない？\nあ、いや！　深い\n意味はないんだけど！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7887, 128501, 102, 8, "あはは…助手くんと\n一緒だと家でごろごろ\nしてるだけでも\n楽しいや…♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7888, 128501, 1, 1, "ふぁ～…\nお正月休みのおかげで\n昼夜逆転中だよ…", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7889, 128501, 2, 1, "お正月も依頼が\nひっきりなしだね\nさあ、街へ出よう！\nもちろん君も一緒にね", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7890, 128501, 3, 1, "も、もっと\n近くにきてもいいよ？\n冬の夜は…寒いからね", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7892, 128501, 2, 1, "ううぅ～…！\nこのままじゃ\n寝正月だぁ…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7893, 128501, 3, 1, "みんなが帰省中でも\nこれがあれば\n寂しくない…かな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7894, 128501, 4, 1, "うーん…♪\nお正月休みの\nリフレッシュに\nちょうどいいや♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7895, 128501, 5, 1, "ふぅ…あったまるな\n冬は寒いから\n助かるよ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7897, 128501, 7, 1, "うーん、おいしい♪\n読書のお供に\nちょうどいい", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7898, 128501, 8, 1, "時間に余裕があると\n読書が進むよ\nいざ、本棚読破だ♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7900, 128801, 1, 1, "この新しい力…\nさらに使いこなせる\nようになるには…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7901, 128801, 2, 1, "私に眠る属性の力…\n風の力以外には\nどんな力があるのかな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7902, 128801, 3, 1, "今の私なら\nどこまでいけるのか…\nやれるだけ\nやってみよう", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7903, 128801, 4, 1, "風の力の制御…\n段々とコツが\n掴めてきたな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7904, 128801, 5, 1, "風の力か…\n応用の幅は広そうだ\n何ができそうか\n色々試してみよう", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7905, 128801, 101, 4, "今度、私とキミで\n料理対決でもする？\n簡単には負けないよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7906, 128801, 102, 8, "この胸に溢れる\n温かな気持ちは…\nきっと彼への…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7907, 128801, 1, 1, "一段と剣技が\n冴えてきたような…\nいや…油断禁物だ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7908, 128801, 2, 1, "かっこいいって？\nありがとう…そんなに\n真っすぐ褒められると\n照れてしまうな…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7909, 128801, 3, 1, "どれほど厚い壁が\n立ち塞がろうと\n打ち破ってみせる", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7910, 128801, 4, 1, "手が空いてるなら\n剣の稽古に少し\n付き合ってくれるかな", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7911, 128801, 101, 4, "試練を乗り越えて…\n私の中にあるものが\nはっきりと分かったよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7912, 128801, 102, 8, "キミから流れてくる\n力の温かさ…これが\n私たちの絆なんだ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7913, 128801, 1, 1, "朝だよ、そろそろ\n起きる時間だ\nそれとも私に起こして\nほしいのかな？", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7914, 128801, 2, 1, "昼食は私が作るよ\nリクエストはある？\nできる範囲で応えるよ", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7915, 128801, 3, 1, "おやすみ。また明日も\n一緒に冒険しよう\n約束だよ？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7917, 128801, 2, 1, "休める時は休む…\n戦い続けるには\n必要なことだからね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7918, 128801, 3, 1, "心の安らぎの大切さ…\nそれを知れたのは\nキミたちのおかげだね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7919, 128801, 4, 1, "気持ちが落ち着くな…\nこの香り、風の力で\n纏えたりしないかな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7920, 128801, 5, 1, "暖まった空気の流れ…\n以前より風の変化に\n気づくようになったな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7922, 128801, 7, 1, "この味付け…\n今度試してみようか\n味見は任せたからね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7923, 128801, 8, 1, "心技体、すべてを\n鍛えるには座学も\n必要だからね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7925, 128901, 1, 1, "瞑想するのに\nいいところはあるかな\n集中できて…お昼寝は\n我慢できるところ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7926, 128901, 2, 1, "ここにはあったかい\n空気が流れてるなぁ…\nみんなが受け入れて\nくれるおかげだね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7927, 128901, 3, 1, "まるで心の中に\n光が満ちてるみたい…\nこの気持ち、みんなに\nおすそ分けしたいな♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7928, 128901, 4, 1, "この衣装は属性の力を\n反映しやすいんだよね\nキラキラしてるのも\n納得だよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7929, 128901, 5, 1, "エルフの里のみなさん\n元気かなぁ…\nまた会いに行きたいな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7930, 128901, 101, 4, "まだ変身できてる…\nわたしと騎士クンの\n修行の成果\n出てるなぁ…ふふっ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7931, 128901, 102, 8, "この服を脱いだら\nどうなるのかな…\nた、試す勇気は\nないけど…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7932, 128901, 1, 1, "ふぅ…あ、騎士クン\nちょうど瞑想してたの\n一緒に…する？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7933, 128901, 2, 1, "不思議だけど\nいつの間にか\n出たり消えたりするの\n…背中の輪っか！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7934, 128901, 3, 1, "この服って肌触りが\nとってもいいんだよ\nさ、触ってみる？\nスベスベだよ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7935, 128901, 4, 1, "アストラルの力…\nすごく持続できてるね\n修行の成果が出てるよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7936, 128901, 101, 4, "この力があれば\n暗いところで\n落とし物をしても\n見つけやすいかも…？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7937, 128901, 102, 8, "冒険の準備なら\nできてるよ\nいつでも声をかけてね\n…楽しみにしてる！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7938, 128901, 1, 1, "おはよう騎士クン\n一緒にお散歩とか…\nどうかな？", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7939, 128901, 2, 1, "お昼ご飯のあとの\n瞑想って難しいんだよ\n眠くなっちゃって…", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7940, 128901, 3, 1, "夜ふかしさんは\nほどほどにね？\n一日頑張った自分を\n休ませてあげないと", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7942, 128901, 2, 1, "のんびりしてたら\nケーキが食べたく\nなっちゃうなぁ…\nでも太る…よね…？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7943, 128901, 3, 1, "わあ、かわいい…！\n見てるだけで\nにやけちゃう…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7944, 128901, 4, 1, "これは…花の香りかな\n知ってる花かも…？\n当ててみようっ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7945, 128901, 5, 1, "あったかいと\nどうしてこんなに\n落ち着くんだろう…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7947, 128901, 7, 1, "食べ過ぎたらお腹が\n目立っちゃうかもって\n分かってるのに…！\n止められないよ～っ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7948, 128901, 8, 1, "こ、恋の精霊とか\nいたりするのかな…？\nはっ！　ダメダメっ！\n実力で頑張らないと", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7950, 128701, 1, 1, "お部屋の中でも\n修行、修行っ♪\nでも燃やさないように\n注意しなきゃだね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7951, 128701, 2, 1, "うーん…この衣装\n厚着か薄着か\nどっちなんだろう…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7952, 128701, 3, 1, "いつもの肉球がない分\nお片付けしやすいな♪\n騎士くんのお部屋\nピカピカにしとこー！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7953, 128701, 4, 1, "お休みは騎士くんと\nＢＢＱに行きたいなー\nあたしと一緒なら\n火の心配はいらないよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7954, 128701, 5, 1, "この姿ならもっと\n人を助けられる…！\nえへへっ！\n燃えてきたーーっ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7955, 128701, 101, 4, "騎士くんが帰ったら\nアイラみたいな笑顔で\nお疲れさまさまって\nお出迎えしよっと…♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7956, 128701, 102, 8, "この姿の時、いつも\n胸が熱く燃えてるのは\nずっと騎士くんのこと\n考えてるからかな…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7957, 128701, 1, 1, "騎士くん騎士くん！\nあたしに触れると\nやけどしちゃうよ…？\nなんちゃってー♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7958, 128701, 2, 1, "おっ、騎士くん\n修行に行きたいの？\nえへへっ、いいねー♪\n二人で燃えちゃおー！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7959, 128701, 3, 1, "そうだ騎士くん\nじゃんけんしよっか？\nグローブ外してるから\nチョキも出せるよっ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7960, 128701, 4, 1, "この姿でいられる時間\nすごく増えてきた…！\nえへへ！　あたしたち\n修行の成果出てるね！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7961, 128701, 101, 4, "じっと見つめないでよ\n顔が真っ赤に…やば！\nほんとに顔から\n火噴いちゃいそう…！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7962, 128701, 102, 8, "火には強くなれても\n騎士くんに触られた\nほっぺはずーっと\n熱くてたまんないや…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7963, 128701, 1, 1, "おはよー騎士くん！\n今日も一日\n燃え滾っていくよー！\nファイヤーッ！！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7964, 128701, 2, 1, "騎士くんお昼だよ！\nちゃんとご飯食べて\n午後からの修行も\n乗り切ろうねー！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7965, 128701, 3, 1, "今日もお疲れさま！\nそうだ、寝る前に\nキャンプファイヤー\n囲んじゃおっか♪", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7967, 128701, 2, 1, "ぼふーんっ！\nえへへ、気持ちいい…\n修行の前に\nちょっと休憩ー♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7968, 128701, 3, 1, "かわいいな～♪\nなでなでなでなでなで\nなでなでなでなでっ！\nあははっ、摩擦熱！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7969, 128701, 4, 1, "ん～いい香り♪\n少し熱したほうが\n香りが広がるね…って\n蒸発しちゃった！！！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7970, 128701, 5, 1, "外でも自分の火で\nあったまれるけど\nお部屋でぬくぬく\nするのが一番だよね♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7972, 128701, 7, 1, "もぐもぐっ！\nお腹が減っては\n人助けはできずっ！\nもぐもぐもぐっ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7973, 128701, 8, 1, "ふむふむ…\nなるほどなるほど…\n…これが上手な\nＢＢＱのやり方か…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7975, 129001, 1, 1, "…壁も扉も柱も\n全て記録で見たまま\n本当に…来ているのね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7976, 129001, 2, 1, "…騒がしい場所\n騎士クンが受け入れて\nくれるのをいいことに", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7977, 129001, 3, 1, "ここが…騎士クンの\n守りたかった場所\n騎士クンの思い出が\n詰まった場所…ね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7978, 129001, 4, 1, "視線を感じる…\nこちらの様子を\n探るような…心の動き\n取るに足らないけれど", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7979, 129001, 5, 1, "…そこかしこで…\n言葉にもしたくない\n相手が過ごしていた\n記録を思い出す…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7980, 129001, 101, 4, "人は多いけれど…\n雪深い山中よりは\n過ごしやすい…か", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7981, 129001, 102, 8, "自覚したことは\nあまりなかったけれど\nひどく…諦めの悪い女\nなのかもしれない", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7982, 129001, 1, 1, "お招きありがとう\nそれで…何をして\nいればいい…かな？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7983, 129001, 2, 1, "ここは…明るいね\nわたしの城と比べると\nなんだか眩しいくらい", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7984, 129001, 3, 1, "仮面はもう必要ないよ\nだって…わたしの顔を\n騎士クンに見て\nもらえなくなるから", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7985, 129001, 4, 1, "騎士クンさえよければ\nわたしの城に専用の\n部屋を用意するよ\n…いつでも", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7986, 129001, 101, 4, "…騎士クンのマント\nまた貸してほしいな\nきれいに使うから\n…だめ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7987, 129001, 102, 8, "いつか二人の願いが\n重なり合えるって…\n信じているね\n…いつまでだって…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7988, 129001, 1, 1, "おはよう、騎士クン\n…昨日は夢の中で\nお話ししすぎちゃって\nごめんね。ふふっ", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7989, 129001, 2, 1, "お腹は…空いてない？\n食べ物はないけれど\n空腹を忘れる魔法なら\nかけてあげられるよ", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7990, 129001, 3, 1, "騎士クン…\nまだ、眠らないの？\n…そう。それなら…\nわたしも起きているね", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7992, 129001, 2, 1, "肩の力を抜くには…\nまだ時間がかかりそう\nきっと…騎士クンと\n二人きりなら別なのに", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7993, 129001, 3, 1, "ぺんぽこりん…\nそう、確かあれも\nこんな感じだった…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7994, 129001, 4, 1, "花の香りは…好き\nつかの間\n心を穏やかに\nしてくれるから", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7995, 129001, 5, 1, "あたたかい…けれど\n騎士クンの優しさに\n比べれば、まだまだ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7997, 129001, 7, 1, "おい、しい…？\nこの感覚が、それ？\nなぜか…納得できる", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (7998, 129001, 8, 1, "情報を入手するには\nわざわざ本を開く\n必要があるだなんて…\nプレイヤーは面倒ね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8000, 129301, 1, 1, "メルクリゾートか…\n良い記憶はないな\nよし忘れよう", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8001, 129301, 2, 1, "知性の戦士ユニ！\nその恐るべき筋力！\nふおお！　うにゃー！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8002, 129301, 3, 1, "ぽーん！\n石用の水着ください！\nセクシーロゼッタが、\n渚の視線を独占です！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8003, 129301, 4, 1, "学院から奨学金を\n勝ち取るより某財団を\nだまくらかした方が\n早い気がしてきたな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8004, 129301, 5, 1, "わんつぅ、わんつぅ…\nんぎゃあ足攣ったー！\nおのれカルミナとやら\n絶対に許さん！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8005, 129301, 101, 4, "ところで…なぜぼくは\nプールでもないのに\n水着のままなんだ…\nおのれ世界め！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8006, 129301, 102, 8, "彼と二人で撮った動画\nうう、凝視に耐えん…\nなんだこのムズ痒さは", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8007, 129301, 1, 1, "なに？　水着姿で\n寒くないのかだと？\n普段からもっと薄着の\n娘が沢山いるだろう", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8008, 129301, 2, 1, "シノブ君にメリゾの\n招待状を送ったのだが\n御尊父の暴走を\n心配していたな…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8009, 129301, 3, 1, "ミミ君らにメリゾの\n招待状を送ったのだよ\n迎える準備をせねば…\nおしゃぶり持ってる？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8010, 129301, 4, 1, "本質の賢者…？\nいや元気にしているが\nなぜ君がぼくの身内を\n知っているんだ…？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8011, 129301, 101, 4, "水着が似合ってる？\nな、なんだね突然…\nやめたまえよふひひ…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8012, 129301, 102, 8, "今度は夏にメリゾへ\n行きたいものだ\nむろん君も一緒だぞ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8013, 129301, 1, 1, "ぐああっ、朝陽が…！\n朝陽が昇っている…！\n夜行性ユニ死んじゃう", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8014, 129301, 2, 1, "はぁ…もう昼か…\nそろそろ寝ないとな\n規則正しい生活を\nしないと不健康だ…", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8015, 129301, 3, 1, "ひゃっはー、夜だー！\nオレたちの時間だぜ！\nふおおブチ上がり～！", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8017, 129301, 2, 1, "これは…ぼくにここで\n寝ろということか？\n一向に構わんが…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8018, 129301, 3, 1, "きゃっきゃっ！\nこれかわい～！\n…よし、今日も\n子供の擬態完璧だな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8019, 129301, 4, 1, "ふむ、良い香りだ\nだがそれだけか…\nとくに精神高揚効果は\nないのか…ちぇー", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8020, 129301, 5, 1, "火を見ていると…\n魔導罐を思いだすな…\nかに座、元気かな…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8022, 129301, 7, 1, "ええい、\n固形食物はいらん！　\nポーションを出せ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8023, 129301, 8, 1, "やはり書は文系に限る\nなんと心地よい筆致…\nぐああ突然理系文が！\nひぃ目が理系になる！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8025, 129301, 10, 1, "ほああ～！\n知性の海上騎士ユニ！\nこれは新たな可能性！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8026, 129301, 11, 1, "これは…なんだ？\n何が面白いんだ？\n難しいな…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8027, 108901, 103, 12, "大切な人と過ごす\n正月の思い出、か…\n昔の私だったら\n考えられなかったな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8028, 108901, 103, 12, "叶えたい願いなら\nたくさんあるよ\nそれでも一番を選ぶ\nなら、やはりキミと…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8029, 108801, 103, 12, "神殿にあった\n恋愛おみくじ…\n引いてみようかな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8030, 108801, 103, 12, "騎士クン。来年も\nまた一緒におみくじ\n結ぼうね。約束だよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8031, 108701, 103, 12, "今度は騎士くんと\n一緒にうつった写真、\nおばあちゃんに\n送ろうっと♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8032, 108701, 103, 12, "あ、騎士くん！\n今年も…ううん\nこれからも、よろしく\nおねがいします♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8033, 129401, 1, 1, "旧校舎の地下牢…\n真冬のサマー計画…\n等身大のＰＲ…\nうッ、頭が…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8034, 129401, 2, 1, "暇だし鏡でも見るか。\nお、いい腹筋育って…\nあ？　何見てんだコラ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8035, 129401, 3, 1, "オレは金色の疾風！\nんん…なんか違うな。\n我が名は金色の…\nあ？　何見てんだコラ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8036, 129401, 4, 1, "モニカとユニ先輩って\n口調と身長似てるけど\n子供扱いに対する\n反応は真逆だよね…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8037, 129401, 5, 1, "シオリン元気かな…\n昔くちゃくちゃに\n撫でくり回して\n嫌がられたっけなァ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8038, 129401, 101, 4, "べつにいんだけどさ…\nあんたの知り合いって\n普段から水着みたいな\nカッコの女多くない？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8039, 129401, 102, 8, "そだ、弟らがあんたに\n会いたがってたンだ。\n暇だったらいつでも\nウチ来てやってよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8040, 129401, 1, 1, "特別講座は色々あった\nけど…メリゾ自体は\nまあ悪くなかったよね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8041, 129401, 2, 1, "水着と合わせる小物に\nセンス出るよね、\nって話をミィコ先輩が\nしててマジわかりみ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8042, 129401, 3, 1, "水着のミフユねえさん\nやっぱ仕上がってたね\nあれが竜殺しの体か…\nバリかっけーな…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8043, 129401, 4, 1, "はにょんっ！？\nや、だからさ…\n後ろから声かけんな、\nつってンじゃん…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8044, 129401, 101, 4, "メリゾの特別講座が\n上手くいったのも\n敏腕カメラマンがいた\nお陰だよ。ありがとね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8045, 129401, 102, 8, "あ？　この水着が？\n似合っ…え、なんて？\nごめ、聞こえんくて。\nもっかい言ってみて", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8046, 129401, 1, 1, "もう朝とかマジないわ\n昨日二人に付き合って\nオールでカラオケだよ", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8047, 129401, 2, 1, "はぁ授業行かなきゃ…\n真っ昼間から勉強とか\nマジつらたん過ぎ…", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8048, 129401, 3, 1, "今日これから夜勤で…\nいやバイトちゃーくて\nボランティアの話よ？\n校則違反よくない", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8050, 129401, 2, 1, "うわ、ふっかふか…\nオニほど安らぐわ…\nやば、ユニってくる…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8051, 129401, 3, 1, "うちも妹いりゃ\nこーゆーのもなァ…\nま自分用でもいいか", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8052, 129401, 4, 1, "ふーん…\nいいじゃんこの香り\n抜群に嗅げるわ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8053, 129401, 5, 1, "うちン家のルーツの森\n大火災でなくなった\nらしンだよね…\n生まれる前の話だけど", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8055, 129401, 7, 1, "何コレありがてぇー\n食料ほくほく過ぎて草\n米ガチで尊いわァ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8056, 129401, 8, 1, "いいけど、なんでうち\n水着で本読んでンだよ\nどーゆープレイだよ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8058, 129401, 10, 1, "金色の海上騎士クロエ\n推して参る！　覇ァ！\nあ？　何見てんだコラ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8059, 129401, 11, 1, "何コレ、貝の椅子？\nどーやって遊ぶんだ…\nいや座って終わりか…\nまァそりゃそーか…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8060, 129801, 1, 1, "さてと…\n明日の訓練メニュー\n考えねえとな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8061, 129801, 2, 1, "また新入りの女子に\n弁当もらっちまった…\nありがたく食うけどよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8062, 129801, 3, 1, "種族なんて関係なく\nみんなが力を合わせて\nランドソルを守る…\nいい時代になったよな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8063, 129801, 4, 1, "誰にだって長所はある\n新入りのいいところを\n見つけてやるのも\n教官の仕事だ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8064, 129801, 5, 1, "厳し過ぎても\n甘過ぎてもダメ…\n教官ってのは難しいぜ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8065, 129801, 101, 4, "戦いが苦手な新入りを\nどうフォローするか…\n後であいつと\n相談してみるか", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8066, 129801, 102, 8, "あいつは優し過ぎて\n戦いに向いてねえ\n…そういう甘さ\n嫌いじゃねえけどな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8067, 129801, 1, 1, "強くなる近道はねえ\n地道な鍛錬あるのみだ\nさ、訓練始めるぞ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8068, 129801, 2, 1, "新入りにも\nいつも言ってるが…\n装備の手入れは\n念入りにしろよな？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8069, 129801, 3, 1, "座学も受けてえのか？\nいい心がけだな！\nなんでも教えてやるよ\n…魔法以外ならな", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8070, 129801, 4, 1, "新入りたちがよく\n恋バナしててな…\nお、おまえは…誰か\n気になるやついるか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8071, 129801, 101, 4, "なあ、ストレッチ\n手伝ってくれねえか？\n訓練前にしっかり\n筋肉ほぐさねえと…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8072, 129801, 102, 8, "ずいぶん頑張って\n鍛えたよな、おまえ\nよし！　ご褒美に\nお菓子作ってやるよ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8073, 129801, 1, 1, "訓練日和だな！\nさぁて…まずは\n走り込みといくか！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8074, 129801, 2, 1, "昼飯はしっかり食えよ\nでなきゃ、\nこの後もたねえぜ？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8075, 129801, 3, 1, "とっくに消灯時間だ\nさっさと寝な？\n明日も早いんだからよ", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8077, 129801, 2, 1, "休むのも訓練のうちだ\n今はのんびり\nさせてもらうぜ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8078, 129801, 3, 1, "いい面構えだ\n気に入ったぜ！\nあたしの班に入るか？\n…なんてな♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8079, 129801, 4, 1, "…いやされる匂いだな\n新入りたちにも\nすすめてみるか…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8080, 129801, 5, 1, "あったけえからって\n寝ないようにしねえと\n教官としての\n威厳に関わるからな…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8082, 129801, 7, 1, "食事も訓練の一環だ\nとはいえ食い過ぎには\n気をつけねえと…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8083, 129801, 8, 1, "トーゴクの剣術…か\nなにか訓練に\n活かせるかもしれねえ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8085, 129901, 1, 1, "あのおかたに近付く\n不埒な輩には、特別な\n訓練で地獄を見て\nいただきましょう", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8086, 129901, 2, 1, "これだけ広ければ\n多少派手に\n動き回っても\n問題なさそうですね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8087, 129901, 3, 1, "…今誰か私を\n「鬼」教官と\n呼びませんでしたか？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8088, 129901, 4, 1, "新しく開発したこの薬\n誰か試してくれる人は\nいませんか？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8089, 129901, 5, 1, "厳しくすれば投げ出し\n優しくすればサボる\n人というのはどこまで\n度し難いのでしょう", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8090, 129901, 101, 4, "はじめましょう\nあのおかたの心を\n射貫くための\nシミュレーションを", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8091, 129901, 102, 8, "あらゆる苦難と\n忌々しい女から守る\nためにも、私自身も\n鍛えておかねば", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8092, 129901, 1, 1, "あなた様、そんなに\n見つめられると…え？\nエリコはカッコいい？\nええ、当然ですとも", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8093, 129901, 2, 1, "不調があればすぐ\n報告してください\n体のケアも教官の\n大事な役目ですので", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8094, 129901, 3, 1, "新しい訓練器具の\n使い心地はどう…え？\n重すぎる？　これでも\n軽くしたのですが…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8095, 129901, 4, 1, "運動後には特製の\n栄養ドリンクをどうぞ\nクスクス、何も\n入れたりしてませんわ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8096, 129901, 101, 4, "よくがんばりました\nあなた様。あぁ…\nお疲れになったお顔も\n素敵ですわ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8097, 129901, 102, 8, "あなた様、二人だけの\n遠征訓練へ\nまいりましょう。\n既に準備万端ですわ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8098, 129901, 1, 1, "まだ眠いですか？\nならばストレッチで\n体を痛めつ…ではなく\nほぐしていきましょう", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8099, 129901, 2, 1, "お昼はお弁当をご用意\nいたしました。量も\nスタミナも増し増し\n残さず食べてください", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8100, 129901, 3, 1, "よい睡眠こそ健やかな\n体に不可欠。ですので\n私が寝ずの番をさせて\nいただきますわ", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8102, 129901, 2, 1, "休憩用に使おうと思い\nましたが、心地よくて\n立てなくなって\nしまいますわ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8103, 129901, 3, 1, "中に重しを詰めれば\n愛でながら運動が\n可能ですわ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8104, 129901, 4, 1, "香りでリラックスさせ\n同時に暗示を\nかけてみましょうか", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8105, 129901, 5, 1, "…くっ、眠気を誘う\nこの暖かさ…\n油断も隙もありません", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8107, 129901, 7, 1, "主食はこちらで十分\nまかなえますわね\nそれでは私はおかずを\n作りましょう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8108, 129901, 8, 1, "まあ、実用書の類も\n揃っているのですね\nとても助かりますわ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8110, 129601, 1, 1, "……（そわそわ）", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8111, 129601, 2, 1, "ここに寄らずの花陣…\n作ったらダメですよね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8112, 129601, 3, 1, "部屋の隅が\n落ち着きます\nそれか、陽の当たる所", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8113, 129601, 4, 1, "身を隠せる場所が\nあると…\n助かるんですけど", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8114, 129601, 5, 1, "複雑な造りの家ですね\n触って壊れたり─\nしませんか？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8115, 129601, 101, 4, "出入りするひと\nみんな、笑顔…\n…変わった家ですね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8116, 129601, 102, 8, "この家なら安全─\nかもしれません\n魔物が出ないので", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8117, 129601, 1, 1, "これも、視察の一環\n遊びに来たわけじゃ\nないです", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8118, 129601, 2, 1, "長話─苦手です\n疲れるので…\nふぅ…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8119, 129601, 3, 1, "ここで友達を作れと\nネアさんが…\nそう、言われても…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8120, 129601, 4, 1, "世話をするのは\n得意です\n…花の、ですよ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8121, 129601, 101, 4, "媛へのお土産を\n探してるんですけど…\n…貴方のおすすめ、\n聞いておきます", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8122, 129601, 102, 8, "平和でのんきで…\n貴方らしい家です\n一応、褒めてます", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8123, 129601, 1, 1, "花に、お水─？\n…いりません", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8124, 129601, 2, 1, "日が高いうちは\nむやみに外へ出ません\n面倒事、多いので…", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8125, 129601, 3, 1, "夜番。私がします\nあまり眠らない\n性質なので…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8127, 129601, 2, 1, "ここなら根を張れる\nかもしれません\n…張りませんけど", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8128, 129601, 3, 1, "こんなにやわな体じゃ\n身を守れませんよ─\n…あ。生き物じゃ\nないんですね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8129, 129601, 4, 1, "花みたいに─\nいい匂い、です", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8130, 129601, 5, 1, "火を見てると、\nジオ・ゲヘナを\n思い出します…ふぅ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8132, 129601, 7, 1, "食事も立派な戦支度\n媛が言ってました", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8133, 129601, 8, 1, "知識も武器の一つだと\n媛が…でも、\nこれでどうやって…？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8135, 130701, 1, 1, "天気もいいしお花畑に\nピクニックでも\n行こうかな♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8136, 130701, 2, 1, "あ、どこからか\nお花の匂いが…\nいい香りですね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8137, 130701, 3, 1, "新しいシャベルとか\nじょうろもほしいなぁ\nお小遣いしっかり\nためないとね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8138, 130701, 4, 1, "お花飾りたいけど\n花瓶はどこかな…\nあとであの人に\n聞いてみよっと", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8139, 130701, 5, 1, "お花の観察日記\n書かなきゃ\n日々勉強は\n欠かせませんよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8140, 130701, 101, 4, "衣装にお花を足して\nみたけど、あの人\n気付いてくれるかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8141, 130701, 102, 8, "この花冠、上手に\n作れたかも…\n日頃のお礼にあの人に\nあげようかな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8142, 130701, 1, 1, "あなたは何のお花が\n好きですか？　ほら、\n一応知っておいたら\n役に立つかもですし", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8143, 130701, 2, 1, "そのお花、乱暴に\n扱っちゃダメですよ\n花粉が服について\nとれなくなりますから", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8144, 130701, 3, 1, "土いじりしたから\n服が汚れてますよ\nはたいてあげますから\nじっとしててください", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8145, 130701, 4, 1, "そんなにすぐに芽は\n出ませんよ\nまあ、楽しみなのは\n私も同じですけど…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8146, 130701, 101, 4, "この衣装、そんなに\n気になります？　なら\nこうしてお花を\n着ければおそろいです", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8147, 130701, 102, 8, "お花の種を私に？\nありがとうございます\nせっかくなので一緒に\nお世話しませんか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8148, 130701, 1, 1, "お花にお水をあげるの\n手伝ってもらえます？\nお水は朝にあげるのが\nいいんですよ", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8149, 130701, 2, 1, "とっておきのお花見\nスポットに\nご案内しますよ\nお弁当も忘れずに！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8150, 130701, 3, 1, "もうこんな時間\n早く寝ましょう\n草木も眠るなんとか\nって言いますからね", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8152, 130701, 2, 1, "お花のお手入れって\nけっこう疲れますね…\nここでちょっと一息\nしちゃおっと", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8153, 130701, 3, 1, "耳にお花を着ければ…\nえへへ、すっごく\nかわいくなったね！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8154, 130701, 4, 1, "これはラベンダー\nこっちはミモザ\nフローラル系の香り\nっていいですねぇ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8155, 130701, 5, 1, "ポカポカしますね～\nまるで春の陽気\nみたいです～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8157, 130701, 7, 1, "お花を一輪さすだけで\nご飯の時間がとても\n華やかになりますね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8158, 130701, 8, 1, "お花の本が\nいっぱいある！\n今日はこれで\nお勉強です！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8160, 130801, 1, 1, "ふんふ～ん♪\n華やかな服を着ると\n心がウキウキします♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8161, 130801, 2, 1, "花祭りで出会った\nみなさん…\nお元気でしょうか？\nまたお会いしたいです", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8162, 130801, 3, 1, "はっ！　そうでした\nこの服の時は\nメイドのお仕事は\nお休みでした…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8163, 130801, 4, 1, "この服は替えが\nありませんから\n大きなドジには\n気を付けないと…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8164, 130801, 5, 1, "目立ったほつれもなし\n…うん、いい感じに\n補修できました♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8165, 130801, 101, 4, "メイド姿でなくても\n心はいつでも\nお嬢さまの\nメイドです♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8166, 130801, 102, 8, "この服を着ると心が\n浮き立つのはあのかた\nと一緒にいる気持ちに\nなるからでしょうか？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8167, 130801, 1, 1, "はい、今日は一日\nこの服で過ごそうと\n思います\n変じゃないですよね？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8168, 130801, 2, 1, "花祭りのおかげで\n少しだけお花に\n詳しくなった…\nような気がします！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8169, 130801, 3, 1, "花祭り、今度は\nお嬢さまと救護院の\nみんなと一緒に\n行ってみたいですね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8170, 130801, 4, 1, "今度キョウカちゃんと\n花祭りの服でお花を\n見にお出かけ\nするんです", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8171, 130801, 101, 4, "メイド服を全部洗濯\nしちゃうなんて…\nドジっちゃいましたぁ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8172, 130801, 102, 8, "花祭りの服は私たちの\n思い出の服ですから…\nこれからも\n大切にします♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8173, 130801, 1, 1, "はう、そうでした\nお花にお水を\nあげる時間です！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8174, 130801, 2, 1, "お昼ご飯は\nバスケットを持って\nお花畑に食べ\nに行きませんか？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8175, 130801, 3, 1, "ふぁあ…繕い物を\nしていたらこんな時間\nになってしまいました\n早く寝ないと…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8177, 130801, 2, 1, "ふわふわしてて\nきもちいいです～\nどんな生地を\nつかってるんだろ～？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8178, 130801, 3, 1, "かわいいですね～\nぬいぐるみさんにも\n服をつくって\nあげたいです♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8179, 130801, 4, 1, "いい香りです～\nあれ？　もしかして\n私の服についてる花と\n同じ香りが…？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8180, 130801, 5, 1, "暖かいですけど\nあまり近づいちゃうと\n頭の花飾りが乾燥\nしちゃいそうですね…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8182, 130801, 7, 1, "もぐもぐ…\nいい花を咲かせるには\nしっかりとした栄養が\n必要だそうです！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8183, 130801, 8, 1, "なるほどなるほど…\nお花のお手入れは\n奥が深いですね…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8185, 131001, 1, 1, "マホマホ王国にも\nサーカス団設立や\n夢と希望を届けますえ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8186, 131001, 2, 1, "せっかくの晴れ舞台や\nかっこいいポーズも\nできるようにしたいわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8187, 131001, 3, 1, "むむむむ～っ。うーん\nクルルはんみたいに\n胸から宝石を出すんは\n難しいみたいやわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8188, 131001, 4, 1, "あむっ…んん～♪\nやっぱり練習のあとは\n甘いものに限るわぁ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8189, 131001, 5, 1, "アーサーたちの衣装も\n今のうちに考えんと…\n生地、足りるやろか", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8190, 131001, 101, 4, "王子はんとなら\nどんなアイデアも\n見つけられます♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8191, 131001, 102, 8, "うちの晴れ姿が\n王子はんの目に一等\n輝いて見えますように", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8192, 131001, 1, 1, "キリっとしたうちも\nかっこいい？\nもう、上手やなぁ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8193, 131001, 2, 1, "新しい演目考えたんよ\nなぁ王子はん、意見\n聞かせてもらえます？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8194, 131001, 3, 1, "聞いてや王子はん\n動物さんたちとさらに\n息が合うてきたんよ\nみんな頑張り屋さんや", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8195, 131001, 4, 1, "演技の感想なら\n大歓迎どすえ♪\nたんと聞かせてや～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8196, 131001, 101, 4, "王子はん、本番当日は\n最前席で見たってな\nそしたらうち、もっと\n頑張れますさかい♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8197, 131001, 102, 8, "この技をあなたに\n捧げます…なんて♪\n驚いたやろ？\nどっきり作戦成功や♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8198, 131001, 1, 1, "素敵なショーのためや\n今日も焦らず一歩ずつ\n成長していきまひょ\nな、王子はん♪", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8199, 131001, 2, 1, "お弁当、王子はんも\n一緒にいかがどす？\nみんなで食べて団結や", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8200, 131001, 3, 1, "夜のサーカスも\n幻想的でとっても\n魅力的なんよ\n一緒にいきまひょか♪", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8202, 131001, 2, 1, "練習が終わったあとは\nの～んびりして\n体力を回復せな…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8203, 131001, 3, 1, "この子たちとなら\n空中ブランコや\n玉乗りもええなぁ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8204, 131001, 4, 1, "いい香りやなぁ\n本番前の緊張も\n和らげてくれそうやわ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8205, 131001, 5, 1, "このあったかい感じ…\nスポットライトの熱を\n思い出すわぁ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8207, 131001, 7, 1, "ちゃんと食べへんと\nいい演技はできまへん\n団長はんも言うてたわ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8208, 131001, 8, 1, "動物さんたちのことも\nもっと勉強せな\n大事な仲間やさかい", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8210, 105601, 103, 12, "器用じゃないが、\n情には厚いよ\nこういう女を「一途」\nってそう言うんだろ？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8211, 105601, 103, 12, "斬れないものはないと\n思ってきたさ。だけど\n…この気持ちだけは\n斬れないね、絶対に", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8212, 130901, 1, 1, "んしょ、んしょっ…！\n日頃の感謝を込めて\nヒーローさんのお家を\nきれいにしますです♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8213, 130901, 2, 1, "ふむふむ…\n新アトラクションは\nこのギルドハウスを\nモチーフに…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8214, 130901, 3, 1, "ごそごそ、こそこそ…\nヒーローさんに内緒で\nどこかにお礼の宝石を\n隠しておきましょう…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8215, 130901, 4, 1, "パークの運営が\n落ち着いたら\nわたしもギルド活動？\nしてみたいですます♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8216, 130901, 5, 1, "こんなすてきな\nおうちに住めたら…\nいえ！　うさぎ小屋で\n十分です～っ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8217, 130901, 101, 4, "ヒーローさんのおうち\nだって意識したら\nアトラクションよりも\nドキドキしてきます…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8218, 130901, 102, 8, "ヒーローさんのことを\n考えると顔が赤く…\nこれってもしかして\nひょっとして…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8219, 130901, 1, 1, "あっ、ヒーローさん！\n話しかけてくれた記念\nということで、宝石\n受け取ってください♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8220, 130901, 2, 1, "元気がない時は\n語尾にぴょんですっ！\nさあヒーローさんも\nやってみるぴょん♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8221, 130901, 3, 1, "ヒーローさんへのご恩\nおばあちゃんうさぎに\nなるまでに、お返し\nできるでしょうか…？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8222, 130901, 4, 1, "ヒーローさん！\n二人で一緒に\nパレードの練習\nしませんか～♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8223, 130901, 101, 4, "あはは♪\nヒーローさんは\n人を笑顔にする力が\nありますです♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8224, 130901, 102, 8, "ヒーローさんに\n一日一恩返しですっ！\n返し終わるまで\nやめませんですます♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8225, 130901, 1, 1, "おはようございます♪\nんしょ、んしょっ…！\n気持ちよく起きられた\n記念に宝石どうぞ♪", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8226, 130901, 2, 1, "お昼にフードコートで\nかばばん君バーガー\n買ってきましたよ！\n一緒に食べますです♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8227, 130901, 3, 1, "今日も一日\nお疲れさまですっ♪\nマッサージしますから\n寝そべってください♪", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8229, 130901, 2, 1, "はふぅ～…♪\nお仕事の疲れも\nぴょんぴょ～んって\n飛んでっちゃいます♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8230, 130901, 3, 1, "わぁっ、とっても\nかわいいですますっ！\nギルドハウスの\nマスコットですね♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8231, 130901, 4, 1, "ヒーローさんの好きな\n香り…これを宝石に\nつければ、受け取って\nもらえるでしょうか？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8232, 130901, 5, 1, "はあ、気持ちいい♪\nヒーローさんの\n好きな温度に\n調整しときますです♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8234, 130901, 7, 1, "支配人は体が資本っ！\nたくさん食べ…すぎて\n動けなくなっちゃい\nましたぁ～…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8235, 130901, 8, 1, "パークの\nガイドブックとか\nあったらいいですね♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8237, 131101, 1, 1, "外あつすぎ溶けるわ～\nやっぱ部屋ん中で\n水着がラクだねー", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8238, 131101, 2, 1, "ン～\nカリザきゅんどこー？\nもしかして逃げた？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8239, 131101, 3, 1, "ミソラちゃんと\nリリちゃん誘って\nそろそろイイコト\nしよっかなー？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8240, 131101, 4, 1, "おねえちゃんに\n頼まれたシゴトは\nまた今度にすっかー\n夏休みだしー♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8241, 131101, 5, 1, "騎士きゅんの\nプリンセスフォームも\n見てみたいんだよなー\nンフ♡", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8242, 131101, 101, 4, "スライムを\nこーしてあーしたら\n騎士きゅんの泳ぐ\n練習に使えるっぽ？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8243, 131101, 102, 8, "おかえり～♡\nって水着で迎えたら\n騎士きゅん喜んで\nくれるかなー？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8244, 131101, 1, 1, "騎士きゅん\nこのサングラス\nかけてみる？\nぜってー似合うよー", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8245, 131101, 2, 1, "日焼け痛かったら\nいつでも言ってねー\nスライムでひんやり\nしてあげるよ～♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8246, 131101, 3, 1, "今から外で\n焼いてこっかなー\n騎士きゅんもどー？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8247, 131101, 4, 1, "スライムマッサージ\nしてあげよっかー\nほーれ、もみもみ～♡", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8248, 131101, 101, 4, "騎士きゅんが選んだ\n水着も着てみたーい\nネアおねえちゃんに\nどんなの着てほしい？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8249, 131101, 102, 8, "騎士きゅ～ん\n尻相撲しよっかー♪\nおねえちゃんに\n勝てたらご褒美だぞ～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8250, 131101, 1, 1, "暑すぎてムダに\n早起きしちった～\n二度寝すっか～\n…ふぁあ…", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8251, 131101, 2, 1, "夏バテしても\nスイカとかかき氷なら\n全然ウェルカム\nなんだよなー", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8252, 131101, 3, 1, "今日はこのまま\n水着で寝るかー\n夢ん中で泳ごー", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8254, 131101, 2, 1, "いったんキューケイ…\nンー…よし\n充電カンリョ～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8255, 131101, 3, 1, "仲間になりたそうに\nこっち見てんねー\nきゃわいいしおっけ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8256, 131101, 4, 1, "ン～いいニオーイ\nお日様あたった\n騎士きゅんの\n次くらいかな～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8257, 131101, 5, 1, "カラダ冷えてきたから\nたすかるーおかげで\nまだまだ水着イケるわ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8259, 131101, 7, 1, "うめ～\nとまんねぇ～\n夏バテどこいった～？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8260, 131101, 8, 1, "ふむふむ…\nこれは意外と\nぬるっと読めるっぽー", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8262, 131101, 10, 1, "ンフフ～\n海の戦士ってカンジ？\nアリよりのアリだね～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8263, 131101, 11, 1, "あーやべ\n一瞬で寝そー\nヤバだわ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8264, 129701, 1, 1, "服や紙が易々と\n燃えぬとは\nランドソルとはまこと\n住み易き国で候", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8265, 129701, 2, 1, "どの世界にあっても\n民は活力に溢れ\n街は賑わうもの…\n善哉、善哉", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8266, 129701, 3, 1, "食べ歩きに世界征服…\nギルドとはかくも\n自由気儘な徒党に\nありんすなぁ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8267, 129701, 4, 1, "連絡は…今日もなし\n我が妹子は相変わらず\n放埓なことよ喃", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8268, 129701, 5, 1, "モネは相変わらず\n警戒しきりなれど…\nそのようなところも\n愛らしくあり申す", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8269, 129701, 101, 4, "なんと長閑な世にや\nありけむ…この平和は\n騎士殿たちの努力の\n賜物にありんす喃", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8270, 129701, 102, 8, "『王家の装備』は\n使わぬのかと？\n…フフ。すでに\n用いており申す", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8271, 129701, 1, 1, "今ばかりは媛という\n立場は置いて\nこの地の者らと\n歓談したく候", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8272, 129701, 2, 1, "強さの秘密？　…フフ\n女子の秘密を暴くのは\n野暮にござんす\nご寛恕いただきたく候", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8273, 129701, 3, 1, "此方の家屋、\n見目以上に\n堅牢であり申す\n何ぞ仕掛けがありや？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8274, 129701, 4, 1, "心地よい時間とは\n疾く過ぎ去りゆくもの\n此処で過ごすと改めて\n実感できる喃", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8275, 129701, 101, 4, "視線が気になる？\nこれはすみませぬ\nなにぶん、お前さまは\n見ていて飽きませぬ故", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8276, 129701, 102, 8, "お前さまの前では\n誰もが心を開く…\nなんとまぁ罪作りな\n色男にありんすな？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8277, 129701, 1, 1, "よく眠れましたかの？\n寝る子は良く育つ、と\n古くより申します由", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8278, 129701, 2, 1, "美食殿の作法に倣い\n皆で共に昼餉を頂くと\n致しんしょう", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8279, 129701, 3, 1, "逢魔ヶ時は望まぬ客も\n参るもの…お前さまも\n気をつけなんし", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8281, 129701, 2, 1, "わっちの蛇に\n勝るとも劣らぬ\n座り心地で候", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8282, 129701, 3, 1, "魂、万物に宿る\nとの云いあらば\n情愛を注いでやるのが\nよかろうの", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8283, 129701, 4, 1, "いと香しき哉…\n騎士殿は香の道にも\n詳しくあられるか", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8284, 129701, 5, 1, "ジオ・ゲヘナの\n灼熱と比ぶれば\nそよ風と変わり\nござんせん", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8286, 129701, 7, 1, "美味なる哉…この味\nぺコリーヌ姫が好むも\n道理にありんす", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8287, 129701, 8, 1, "トーゴクに、オーマ…\n書で見るだけでも\n心惹かれる土地よの", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8289, 131201, 1, 1, "暑い日には\n冷たいものですよねー\nかき氷とか食べて\nすっきりしたいです★", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8290, 131201, 2, 1, "海への行き来は\nテレポーテーションに\nお任せです★", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8291, 131201, 3, 1, "トロピカルジュースが\n飲みたいでーす♪\n海の家で見たんですよ\nカップル用ジュース★", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8292, 131201, 4, 1, "ご飯どうしようかなー\nあ、海鮮ラーメンとか\n美味しそうかも★", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8293, 131201, 5, 1, "水鉄砲、普通に撃つと\n少し物足りないかも…\nどるるるる～ん★って\n感じが欲しいですよね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8294, 131201, 101, 4, "コイビトっぽいこと\nアオハルっぽいこと\nいっぱいしたいです★\n修羅場とかも♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8295, 131201, 102, 8, "騎士さんが溺れたら\n絶対助けに行きますね\n人工呼吸でもなんでも\nしてあげますから★", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8296, 131201, 1, 1, "騎士さーん\n日焼け止めの塗り直し\nお願いします★\n優しくしてくださいね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8297, 131201, 2, 1, "海で食べたフルーツ？\nはい、まだありますよ\nこっちでもあーんして\n食べさせましょうか★", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8298, 131201, 3, 1, "ぺんぽこりんは\nどうしたのかって？\nどうしたんでしょうね\nどーでもいいです★", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8299, 131201, 4, 1, "今日はどんな\nイイコトしましょうか\n思い出作り？\n人助けもいいですね★", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8300, 131201, 101, 4, "ヒモを引っ張ったら…\n「セ・キ・ニ・ン」\n取ってもらいますよ？\nなーんてウソですけど", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8301, 131201, 102, 8, "みんなでイイコトも\n楽しいですけど～\n…二人でイイコト\nしちゃいますか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8302, 131201, 1, 1, "朝ですよー！\n今日も一日、\nイイコト日和ですね♪", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8303, 131201, 2, 1, "日差しにご注意ですね\n日焼け止め…誰かに\n塗ってほしいなー？\n…ちらっ★", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8304, 131201, 3, 1, "夜の海って\n惹かれちゃいますよね\n…ロマンチックです★", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8306, 131201, 2, 1, "う～ん、まったり～\nこれぞ新ジャンル\nおうちバカンス★", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8307, 131201, 3, 1, "よーしよーし★\nわたしとイイコト\n一緒にしましょうねー", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8308, 131201, 4, 1, "いい香りですねー\nリラックスタイムに\nちょうどよさそう★", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8309, 131201, 5, 1, "水遊びのあとは\nしっかり体を\n温めないとですよね★", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8311, 131201, 7, 1, "栄養補給もバッチリ★\n遊びもイイコトも\nお腹が減ってたら\nできませんからねー？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8312, 131201, 8, 1, "今だと海関連の本とか\n興味あるんですよねー\n魚とか海難救助とか★", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8314, 131201, 10, 1, "かっこいー★\n名前はー…\nぷんぷくぽんとか？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8315, 131201, 11, 1, "映えますねー\n写真撮ったら\n素敵な思い出に\nなりそうです★", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8316, 131601, 1, 1, "海でもないのに\nこの姿でいるのは…\nやはり、場違いでは\nありませんか…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8317, 131601, 2, 1, "スイカとは割ることと\n見つけたり！\n…ですが目隠しすれば\n見当たらないもの…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8318, 131601, 3, 1, "まぁ、膝に砂が…\n気がつきませんでした\nまるでわんぱくな\nお子様のよう…ふふっ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8319, 131601, 4, 1, "家の中でスイカを割る\nわけにはまいりません\nここは潔く包丁で\n切りましょう", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8320, 131601, 5, 1, "ミソラ様やネア様の\n大胆さを見習って…\nき、騎士きゅーん…★\n難しいですね…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8321, 131601, 101, 4, "きゅ、急に暗く…！？\n…失礼しました\n鉢巻きが下がってきた\nだけでした…ううぅ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8322, 131601, 102, 8, "わたくしに剣の才は\nあるのでしょうか…\nいえ、何事もまずは\nやってみなくては", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8323, 131601, 1, 1, "なにか羽織った方が\nよろしいですよね…？\nそのままでいい？\nそれはそれで…その…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8324, 131601, 2, 1, "あの、兄様…？\nスイカの種を飛ばす\n遊びもあるというのは\n…本当なのでしょうか", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8325, 131601, 3, 1, "強くなるための修行は\nまだ始まったばかり\nまずは己の目指すべき\n道を見定めなくては", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8326, 131601, 4, 1, "同じ薄着であっても\n黒い天使の姿より…\n気が楽です", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8327, 131601, 101, 4, "はい、ただいまー！\n…あっ！\n海の家でのお手伝いの\nくせが、つい…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8328, 131601, 102, 8, "兄様さえよろしければ\nまた一緒に海へ…\nあ…申し訳ありません\n浮ついてしまいました", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8329, 131601, 1, 1, "本日もイイコトをして\n清々しい一日を\n過ごしたいものです\nまさに一日一善ですね", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8330, 131601, 2, 1, "お昼時になると\n海の家の料理を\n思い出してしまいます\n焼きそばにかき氷…♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8331, 131601, 3, 1, "兄様…よろしければ\n浜辺を歩きませんか？\nきっと、月が\nきれいでしょうから", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8333, 131601, 2, 1, "ふわふわ心地よすぎて\n眠くなってしまいます\n水着姿では\nいけません…ふぁ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8334, 131601, 3, 1, "なんてよい手触り…\n癖になってしまいます\nふふ、よしよし…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8335, 131601, 4, 1, "爽やかな香り…♪\n日焼け止めクリームを\n思い出してしまいます", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8336, 131601, 5, 1, "冷たい水で冷えた体が\n温まります…♪\nまるで兄様のようです", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8338, 131601, 7, 1, "お腹が空いては\n修行ができません…！\n動きに障りない程度に\n栄養の補給を…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8339, 131601, 8, 1, "剣術とスイカ割りが\n上達する指南書…\n見つかりませんね…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8341, 131601, 10, 1, "さあ、ともに大海原を\n参りましょう！\nなんて…ふふっ♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8342, 131601, 11, 1, "まるでおとぎ話の\n登場人物になった\nような心持ちです…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8343, 131701, 1, 1, "あたしはなんで水着で\n室内をうろついてるの\nかしら…不思議だわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8344, 131701, 2, 1, "あ、天井が汚れてる\n仕方ないわね\nひとっ飛びしますか", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8345, 131701, 3, 1, "はい、お疲れさま♪\n出迎えの言葉は\nやっぱこれよね♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8346, 131701, 4, 1, "そういえばあたしって\n羽があるけど\n泳げるのかしら…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8347, 131701, 5, 1, "あいつのために\n浮き輪も用意したし\n抜かりはないわよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8348, 131701, 101, 4, "海までのガイド計画も\nまとまったわね\nふふっ…楽しみだわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8349, 131701, 102, 8, "この水着、あいつが\n選んでくれたのよね\n…大事にしないと", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8350, 131701, 1, 1, "ネビアは最近どう？\n迷惑かけてない？\n…かけてるのね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8351, 131701, 2, 1, "あんた…部屋の片付け\nくらい自分でしなさい\n散らかってたわよ？\n…片付けておいたけど", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8352, 131701, 3, 1, "もしかして暇なの？\nだったら記憶でも\n見せましょうか？\n凄く恥ずかしいやつ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8353, 131701, 4, 1, "夏ってこんなに\n暑いのね…\n海にばかり行く\n気持ちが分かったわ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8354, 131701, 101, 4, "あんたの水着って\n新しいの買わないの？\nなんならあたしが\n選んであげるわよ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8355, 131701, 102, 8, "気軽に触れ合える\n距離にいる…\nあたしにとって\nとても大事なことよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8356, 131701, 1, 1, "あらおはよう\n夏の朝は暑いわね\nシャワーでも浴びて\nさっぱりしなさい", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8357, 131701, 2, 1, "お昼は何を食べるの？\nガイド妖精として\n提案しましょうか？", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8358, 131701, 3, 1, "夜更かしは厳禁よ\n無理やり眠らせて\nあげましょうか？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8360, 131701, 2, 1, "アメスさまだって\nゴロゴロするときは\nあるのよ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8361, 131701, 3, 1, "…ふふっ♪\n何よ、あたしが\nかわいいものを\n愛でちゃだめなの？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8362, 131701, 4, 1, "こういういい香り…\n塔の中じゃなかなか\n楽しめないのよね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8363, 131701, 5, 1, "水着を着てるのに\n温まるのって\n矛盾してない？\n気にしすぎかしら", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8365, 131701, 7, 1, "あんたと行動するなら\n沢山食べられるように\nならなきゃね…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8366, 131701, 8, 1, "文字を読むのと\nデータを読むのは\nまったく違うわ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8368, 131701, 10, 1, "へぇ、面白いじゃない\nこういう経験、昔じゃ\nできなかったわ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8369, 131701, 11, 1, "面白いクッションね\nところであんた…\nあたしで遊んでない？\n気のせいならいいけど", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8370, 118601, 1, 1, "また新しい悪戯\n思いついちゃった♪\n誰に仕掛けようかしら", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8371, 118601, 2, 1, "あ～あ、退屈…\nフィオでもからかって\nこようかしら♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8372, 118601, 3, 1, "ミソギの悪戯って\nレベル高いのよね…\n負けてらんないわ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8373, 118601, 4, 1, "家事ニガテなのよね～\nコッコロたちには\n感謝しないとね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8374, 118601, 5, 1, "私も魔物料理に\n慣れてきちゃった…\n毒されてるわね…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8375, 118601, 101, 4, "あいつに仕掛ける\n次の悪戯は～…♪\nこれ…ううん…\nやっぱこっちかしら？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8376, 118601, 102, 8, "結局ギルドハウスが\n一番落ち着くわね\n…私たちの家だもの", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8377, 118601, 1, 1, "悪戯のコツでも\n教えてあげよっか？\n…え～興味ないの？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8378, 118601, 2, 1, "これからもずーっと\n退屈しない毎日に\nしてあげる♪\n私の悪戯でね♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8379, 118601, 3, 1, "ちょうど退屈してたの\nたーっぷりと\n構ってもらうわよ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8380, 118601, 4, 1, "案内が必要なら\n頼ってくれていいわよ\n一応は私だって\nガイド妖精だし！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8381, 118601, 101, 4, "冒険のガイドなら\n私に任せなさい♪\nある意味忘れられない\n旅にしてあげる♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8382, 118601, 102, 8, "妖精がいる家は\n栄えるのよ♪　だから\nずっといてあげる\nあんたのそばにね♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8383, 118601, 1, 1, "きゃははっ！\n寝ぐせついてるわよ？\nこのままにしましょ！\nだって面白いし！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8384, 118601, 2, 1, "自由に外へ行けるって\nすっごく幸せなことよ\nさ、散歩行きましょ♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8385, 118601, 3, 1, "安心していいわ\n寝てるあんたに\n悪戯しないから\n…あんたにはね♪", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8387, 118601, 2, 1, "悪くはないけど…\nあいつの頭の方が\n座り心地いいかも", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8388, 118601, 3, 1, "この子を使った\n悪戯は…\n…止めとこっと\nこんなにかわいいし♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8389, 118601, 4, 1, "このギルドハウスに\nピッタリな匂いね♪\n一言で言えば…\nカオスって感じ？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8390, 118601, 5, 1, "なんていうか…\n心があたたかいわ\n…変な感じ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8392, 118601, 7, 1, "ガイド妖精にも\n食事の機能があって\nよかったのかも\nしれないわね…もぐ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8393, 118601, 8, 1, "じれったい物語ね…\nこんなの霧を使えば\n一瞬で終わるのに！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8395, 131801, 1, 1, "わる～い子は\nトゥンヌス漁船に\n乗ってもらおうかな～", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8396, 131801, 2, 1, "たまにはファミリーの\nみんなにもお休みを\nあげないとね～☆", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8397, 131801, 3, 1, "海辺に素敵な\nバーがあるんだ～\n彼が大人になったら\n一緒に行きたいな♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8398, 131801, 4, 1, "夏の風を肌で\n感じるの好きかも～\nラーゴはどう？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8399, 131801, 5, 1, "海岸線を\nのんびり散歩もいいね\n砂や貝殻に紛れた\nお宝探しの旅だよ～☆", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8400, 131801, 101, 4, "みんなで集まって\nビーチパーティーでも\n開いちゃおっかな～♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8401, 131801, 102, 8, "さて、この水着…\n彼は気に入って\nくれてるかな～？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8402, 131801, 1, 1, "心ゆくまで夏を\n楽しんじゃお～☆\nほら君も♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8403, 131801, 2, 1, "水着似合ってるって？\nふふ♪　うれしいこと\n言ってくれるね～☆", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8404, 131801, 3, 1, "熱中症には\n気を付けないと\nだめだよ～？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8405, 131801, 4, 1, "海の家ならうちの\nファミリーでやってる\nとこにしよっか☆\n豪遊しちゃお～♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8406, 131801, 101, 4, "君と一緒なら\nどんな夏になるのか\n楽しみだね～♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8407, 131801, 102, 8, "君といると子供心を\nくすぐられちゃうな～\n大人だって思いっきり\n遊んでいいんだよね♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8408, 131801, 1, 1, "さ、今日は何をして\n過ごそっかな～…\nねえ、君は何したい？", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8409, 131801, 2, 1, "ふふ♪\n波音が心地良い\n時間だね～☆", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8410, 131801, 3, 1, "今夜は星空の下で\n乾杯しよっか～☆\n飲み物取ってくるね♪", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8412, 131801, 2, 1, "ここ涼しい～♪\nちょっと一休み\nしていこっか☆", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8413, 131801, 3, 1, "こういうのも良いけど\nやっぱりラーゴの方が\nかわいいかな～♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8414, 131801, 4, 1, "爽やかな香りだね～\nこういう感覚も絵で\n表現してみたいな～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8415, 131801, 5, 1, "続きを描く前に\n原稿を乾かさないとね\nインクがにじむと\n直すの大変なんだよ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8417, 131801, 7, 1, "今日は水着だし…\n食べるのは控えめに\nしておこっかな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8418, 131801, 8, 1, "話題の作品は\nチェックしないとね～\nあっ、この作家さん\n新刊出すんだ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8420, 131801, 10, 1, "うちの出店の客引きに\nこういう乗り物系を\n使うのもアリかな～☆", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8421, 131801, 11, 1, "これ、背景用の\n参考資料に\n一つ欲しいかも♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8422, 181201, 1, 1, "ここに帰るのも\n久しぶり…あれから\n変わったところは\nあるかしら？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8423, 181201, 2, 1, "バイトも復帰しなきゃ\nうちのギルドにとって\n食費は重要だもの…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8424, 181201, 3, 1, "コッコロマ…っ\n…うぅ…\nこの癖は\n直らないのね…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8425, 181201, 4, 1, "この世界はまだまだ\n知らないことだらけね\n『裏世界』とか…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8426, 181201, 5, 1, "必ずみんなを\n助け出してみせるわ\n…もちろん、兄さんも", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8427, 181201, 101, 4, "またみんなと\n暮らせるなんて…\n夢じゃ、ないのよね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8428, 181201, 102, 8, "たくさんの人が\n私に優しくしてくれる\n…だからこそ、私も\n頼れる人になりたいわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8429, 181201, 1, 1, "次はランドソルの\n街の外にも行きたいわ\nあの…よかったらまた\n一緒に回ってくれる？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8430, 181201, 2, 1, "独りぼっちで眠るのは\nやっぱり寂しかったわ\n…みんなで雑魚寝？\nそんなの…絶対やるわ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8431, 181201, 3, 1, "ネビアってば大泣きで\n私を出迎えてくれたの\n…すごく嬉しかったわ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8432, 181201, 4, 1, "この翼、陽に当たると\n本当に綺麗よね…\n自分のだけれど、つい\nずっと見ちゃうもの", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8433, 181201, 101, 4, "おにーたんっ♪\n…呼びたくなっただけ\n本当にそれだけよっ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8434, 181201, 102, 8, "どうしたの？\n…ふふっ、安心して\nこれからもずっと\n私はここにいるわ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8435, 181201, 1, 1, "みんな起きだす時間ね\n街が活気づいてく感じ\n嫌いじゃないわ", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8436, 181201, 2, 1, "【美食殿】のお昼は\nいつも美味しいから\nまた太っちゃいそうね", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8437, 181201, 3, 1, "おやすみなさい\n明日も、その先も\n一緒に冒険しましょう", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8439, 181201, 2, 1, "休憩も大事…よね\nじゃあ遠慮なく…\nふぅ～…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8440, 181201, 3, 1, "はぁ…ふわふわ…♪\nこのかわいさには\nもう抗えないわ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8441, 181201, 4, 1, "落ち着く香り…\nこの匂いを嗅いでると\n帰ってきたなって\n思うわね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8442, 181201, 5, 1, "みんなで野宿した時も\n焚火の明かりを見て\nすごく安心したのよね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8444, 181201, 7, 1, "ペコリーヌさんには\n負けるけど…\n美味しくって手が\n止まらないわ…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8445, 181201, 8, 1, "プリンセスフォーム…\n一体どういう仕組みで\n発動するのか、文献に\n書いてないかしら…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8447, 131901, 1, 1, "すごーく暑いけど、\nこの暑さをどうやって\n乗り越えるかって\n考えるのも楽しいかも", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8448, 131901, 2, 1, "氷で彫刻を作るの？\nそれは楽しそうだけど\nすぐに溶けちゃわ\nないかしら？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8449, 131901, 3, 1, "お日様の下で遊ぶのも\n部屋の中で涼むのも\nどっちも夏ならではの\n楽しみ方なのね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8450, 131901, 4, 1, "生き物の観察日記、\n私も書いてみたいな\nパックのお昼寝日記\nなんてどうかしら？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8451, 131901, 5, 1, "さまぁばけぇしょんは\n計画を立てるもの\nなんだって\n私も挑戦してみるわね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8452, 131901, 101, 4, "この世界のことを\nもっと知って\nアーラム村のみんなに\nお話ししてあげるの", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8453, 131901, 102, 8, "いつルグニカに帰るか\nわからないからこそ\n毎日頑張らなきゃね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8454, 131901, 1, 1, "今日はどこに行くの？\n私は、えーと…\nかき氷が食べたいわ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8455, 131901, 2, 1, "ここにも精霊が\n住んでたりするの？\n…精霊じゃなくて\nがいど妖精？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8456, 131901, 3, 1, "パックったら、\n近所の子にいっぱい\nなでなでされてたわ\nすごーく人気者ね！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8457, 131901, 4, 1, "実は…線香花火の\n特訓をしてたの！\n次は負けないんだから", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8458, 131901, 101, 4, "昨日、みんなの\n似顔絵も絵日記に\n描いてみたんだけど…\n笑っちゃダメよ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8459, 131901, 102, 8, "何気ない時間だって\n後で思い返せば\nずっと忘れない\n素敵な思い出になるの", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8460, 131901, 1, 1, "気持ちよく起きて、\nいい一日を過ごしたい\nそんなときには\nラジーオタイソーね！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8461, 131901, 2, 1, "お昼ごはんの時間ね\n暑さに負けないように\nしっかり食べましょう", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8462, 131901, 3, 1, "外は涼しいみたいだし\n夜の散歩に行ってみる\nのはどうかしら？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8464, 131901, 2, 1, "遊び疲れたら\nここでひとやすみ…\nふわぁ…\n眠くなっちゃった…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8465, 131901, 3, 1, "細かいところまで\nすごーく丁寧に\n作られてる…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8466, 131901, 4, 1, "わっ、なんだか\nすーっとする香りね\nちょっぴり涼しく\nなったみたい", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8467, 131901, 5, 1, "海で遊ぶと\n体が冷えるから\nあったかいのも\nすごーく嬉しい…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8469, 131901, 7, 1, "これも美味しい…！\nペコリーヌが\nどんどん食べちゃう\n気持ちが分かるかも", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8470, 131901, 8, 1, "このお話も面白い…！\nあっという間に時間が\n過ぎちゃったわ…\n…ね、寝てません！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8472, 131901, 10, 1, "この子と一緒に海に\nいったら楽しそうね！\nどこに行けば\n会えるのかしら？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8473, 131901, 11, 1, "ふかふかね！\nこうしてると波の音が\n聞こえてきそう…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8474, 132101, 1, 1, "こちらでは水着姿で\n過ごすのも\n珍しくないようですね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8475, 132101, 2, 1, "メイド服ではなく\n姉様と色違いの水着…\nレムは嬉しいです", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8476, 132101, 3, 1, "少し室温が高いかも\nしれませんね…\nいくつか氷を配置して\nおきましょうか", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8477, 132101, 4, 1, "清涼感があるとはいえ\n鉄球をビーチバレーの\nボールに代用するのは\n危ないでしょうか…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8478, 132101, 5, 1, "ふぅー…ふぅー…\nようやく浮輪が\n膨らみました\n姉様にお渡ししないと", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8479, 132101, 101, 4, "海のことをもっと\n教えていただけますか\n…スバルくんと海の\n話をしたいんです", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8480, 132101, 102, 8, "もし海に行く機会が\nあればレムにも…\nお声がけくださいね\nぜひ、お願いします", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8481, 132101, 1, 1, "何を着ていても\nメイドはメイド\n掃除も料理も\nなんでもござれです", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8482, 132101, 2, 1, "スバルくんとあなたは\n兄弟のように\n仲がいいですね\n姉様とレムと一緒です", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8483, 132101, 3, 1, "この姿で子どもたちと\n遊ぶのは…さすがに\nはばかられますね\nやはり着替えてきます", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8484, 132101, 4, 1, "ナンパ…というものが\n海にはあるそうです\nスバルくんがとても\n警戒していました", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8485, 132101, 101, 4, "魚釣りでしたら\nレムもご一緒します\n氷漬けの魚の備蓄を\n補充したかったので", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8486, 132101, 102, 8, "子どもたちと砂遊びの\n約束をしているんです\nあなたもご一緒に\nいかがですか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8487, 132101, 1, 1, "ラジーオタイソーは\nもうされましたか？\n体がほぐれますよ", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8488, 132101, 2, 1, "お昼ですね。では\n姉様に焼きそばを…\nもちろん皆様の分も\n用意してありますよ", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8489, 132101, 3, 1, "熱帯夜であっても\nお休みの際はお腹を\n冷やさないように\n気をつけてくださいね", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8491, 132101, 2, 1, "とても気持ちよさそう\nです…！\nさっそく姉様に\nお教えしないと", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8492, 132101, 3, 1, "季節に合わせた\n着せ替えができれば\n子どもたちも\n楽しめそうです", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8493, 132101, 4, 1, "ふむふむ…この香りは\n疲れを癒やしてくれ\nますね。エミリア様に\nお持ちします", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8494, 132101, 5, 1, "身も心もほぐれます\n思っていたよりも\n体が冷えていたみたい\nですね…ふう…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8496, 132101, 7, 1, "もぐもぐ…んうっ！？\nこのすっぱさは…\n梅干し、ですか…\nくせになりますね…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8497, 132101, 8, 1, "海に関する詩文なども\nあるのでしょうか…\n気になります", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8499, 132101, 10, 1, "地竜とは違った\n乗り心地です…\n本物にも乗ってみたい\nものですね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8500, 132101, 11, 1, "この大きさで\n本物の貝であれば…\nどんな料理にするか\n想像が膨らみます", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8501, 132001, 1, 1, "これがこの世界の\n居住環境か…\n面白い構造だね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8502, 132001, 2, 1, "暇を持て余していてね\n誰か話し相手に\nなってくれないかな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8503, 132001, 3, 1, "目新しいものが\nたくさんあるね\n胸が躍るよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8504, 132001, 4, 1, "頑丈そうな建物だ\nここなら彼女たちが\n暴れても大丈夫かな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8505, 132001, 5, 1, "力が制限されていて\n良かった面もある\n君も吐かずにすんだし", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8506, 132001, 101, 4, "悪さなんてするわけ\nないじゃないか\n傷つくなぁ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8507, 132001, 102, 8, "普段と違う格好で\n歩き回るのも\n悪くない経験だ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8508, 132001, 1, 1, "ボクに聞きたいことが\nあるなら遠慮なく\n質問するといい", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8509, 132001, 2, 1, "気軽に魔女に\n話しかけるなんて\n君は物好きだね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8510, 132001, 3, 1, "この世界においては\nボクは異物だからね\n大人しくしているさ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8511, 132001, 4, 1, "ボクの反応を\n見たいのかい？\n君の好奇心には\n恐れ入るよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8512, 132001, 101, 4, "ボクの手を借りたい？\nもちろん構わないさ\n対価はいただくけどね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8513, 132001, 102, 8, "ボクと海を見たい？\nふふ、魅力的な\nお誘いだね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8514, 132001, 1, 1, "お目覚めかな？\nさて、君の見た夢を\n教えてくれないかな？", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8515, 132001, 2, 1, "食後のお茶を\n一緒にどうだろうか？\nさあ、席に座ってよ", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8516, 132001, 3, 1, "おや、お休みかな？\n魔女の前で無防備だね\n…無論、何もしないさ", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8518, 132001, 2, 1, "こういう椅子は\n茶会には向かないね\nつい気が緩んでしまう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8519, 132001, 3, 1, "こういうものを好きな\n女の子がいいのかな？\n君の嗜好を知りたいな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8520, 132001, 4, 1, "いい香りだ\nこれにはどんな\n効能があるんだろう？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8521, 132001, 5, 1, "火の点け方に\nボクのいた世界との\n違いはないのかな？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8523, 132001, 7, 1, "これもまた未知の味だ\n存分に味わわせて\nもらうとしよう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8524, 132001, 8, 1, "素晴らしいね\nここには叡智が\n詰まっている", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8526, 132001, 10, 1, "魔物を模倣した\nぬいぐるみか…\n実物を見たくなるね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8527, 132001, 11, 1, "いい気持ちだ…ボクと\nしたことが、何も考え\nられなくなりそうだ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8528, 109701, 103, 12, "願わずにいられません\nどうかこちらの\n世界には…悲劇が\n生まれませんようにと", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8529, 109701, 103, 12, "食事のご希望があれば\nなんなりと。お腹が\nいっぱいになるまで\n沢山ご用意しますね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8530, 109801, 103, 12, "あの男、これからも\n観察が必要ね\nラムの目の届く範囲に\n置く必要があるわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8531, 109801, 103, 12, "素直なところは\nあの男の美点ね\n癪だけど、偶然にも\nラムと同じだわ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8532, 109901, 103, 12, "迷惑かけちゃったけど\n雪遊びは楽しかったわ\nまたやりたい…なんて\n言ったら怒られるかも", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8533, 109901, 103, 12, "数えきれないくらいの\n思い出を、みんなで\n作りましょう！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8534, 132201, 1, 1, "わわっ、今すごく\nいい香りがしたけど…\n誰の香水かな\n教えてほしいな…♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8535, 132201, 2, 1, "錬金術用の釜、\n置くならどこかな？\n自分専用の錬金釜、\n憧れちゃうよね♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8536, 132201, 3, 1, "この匂い…誰か\n唐揚げ食べたみたい\nうぅ…お腹空いて\nきちゃうよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8537, 132201, 4, 1, "次の新曲のタイトルは\n『恋の錬金術』！\nうーん、ちょっと\nベタ過ぎるかな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8538, 132201, 5, 1, "あ、錬金の素材\n切らしてたんだ…\n彼を誘って採集に\n行ってこようかな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8539, 132201, 101, 4, "瞬時に着替えができる\n薬とかできないかな？\n舞台で衣装チェンジ！\nなーんて、楽しそう♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8540, 132201, 102, 8, "彼が遊びに来た後は\n部屋の空気がほんのり\n彼の匂いなんだ…\nなんだか幸せだな♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8541, 132201, 1, 1, "今日もバイト？\nそれならこれ飲んで\n私のお手製ポーション\nがんばってきてね！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8542, 132201, 2, 1, "新しくできたカレー屋\n行ってみない？\n時々スパイスの香りが\n流れてくるんだよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8543, 132201, 3, 1, "私のフィギュア\nそっくりでしょ！\nルイちゃんが作って\nくれたんだ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8544, 132201, 4, 1, "あれ、シャンプー\n変えた？　私と同じの\nだよ！　どうしよう\nドキドキしちゃう…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8545, 132201, 101, 4, "いらっしゃいませ♪\n何を作りますか？\nなーんてね。錬金堂で\nまた一日店長したいな", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8546, 132201, 102, 8, "クンクン…彼の\nハンカチ…いい匂い♪\n私だけの秘密のお守り\nにしてるんだ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8547, 132201, 1, 1, "ねえねえ、天気も\nいいし一緒に錬金の\n素材集めに行こうよ！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8548, 132201, 2, 1, "お昼ご飯、用意したよ\nあはは、さすがに\nランチは錬成しないよ\n私の手作りだよ♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8549, 132201, 3, 1, "今日も一日お疲れさま\nこれ飲んでゆっくり\n休んで。薬じゃないよ\nホットミルクだよ？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8551, 132201, 2, 1, "昨日、遅くまで\n錬金術の勉強してた\nから眠くって…\nちょっと一休みっと", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8552, 132201, 3, 1, "この子にちょっとだけ\n香水かけて、と…\nんー！　だっこした時\nすっごくいい匂い♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8553, 132201, 4, 1, "花みたいな、\nお菓子みたいな\nいい香り。気持ちが\n穏やかになるね♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8554, 132201, 5, 1, "外でもこの暖かさを\n感じられる道具、\n作れたりしないかな？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8556, 132201, 7, 1, "素材集めに行くと\nお腹が空くんだよね\nイロハちゃんも\nこの味好きそう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8557, 132201, 8, 1, "錬金術にはたくさんの\n知識が必要だからね\nさあ、今日も調合書を\n読まなくちゃ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8559, 127701, 1, 1, "ここが騎士さんたちの\n住んでる場所…！\n粗相がないように\n気をつけなきゃ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8560, 127701, 2, 1, "ジオ・テオゴニアの\nみんな、私がいない間\nちゃんとお仕事\nしてればいいけど…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8561, 127701, 3, 1, "コッコロさんも\n従者として長いって\n聞いたような…\nあの年ですごい…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8562, 127701, 4, 1, "自分がもてなされる\n側になるのって\nやっぱりちょっと\n落ち着かないかも…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8563, 127701, 5, 1, "今度のお休みの日は\n何しよう…？\nあっ、最近できたお店\nヴルムと行こうかな？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8564, 127701, 101, 4, "お買い物したら\nかわいいからって\nたくさんおまけして\nもらっちゃった…♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8565, 127701, 102, 8, "ここにいると、つい\nぼんやりしちゃう…\nこれも騎士さんの力…\nなのかな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8566, 127701, 1, 1, "お手伝いできることは\nありませんか？\nお世話でもなんでも\n任せてくださいっ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8567, 127701, 2, 1, "ランドソルに初めて\n来たときは驚きました\n砂糖の山と思ったら…\nあんなに寒いなんて！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8568, 127701, 3, 1, "あの、ヴルムを\n見ませんでしたか？\nもう…あの子ってば\nまた仕事をサボって…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8569, 127701, 4, 1, "家具や家が壊れても\n食べられないなんて\nなんだかもったいない\nですね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8570, 127701, 101, 4, "とっておきの紅茶を\n手に入れたんです\n一杯いかがですか？\nその、日頃のお礼です", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8571, 127701, 102, 8, "頑張ってて偉いって…\nほ、褒めてもなんにも\n出ませんからね！？\n…えへへ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8572, 127701, 1, 1, "おはようございます！\nモーニングティーは\nいかがですか？\n私、準備しますねっ", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8573, 127701, 2, 1, "ライラエルさまも\nお昼寝はお好きです\nけど…だからって\n寝すぎはダメですよ！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8574, 127701, 3, 1, "おやすみなさい\nくれぐれも寝不足には\n気をつけてくださいね", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8576, 127701, 2, 1, "しっかりしないと…\nメイドの私が\n気を抜くなんて…\nそんな…ふぅ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8577, 127701, 3, 1, "わぁ、かわいい…♪\nってダメダメ！\nメイドなんだから\n落ち着かないと…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8578, 127701, 4, 1, "いい匂い…♪\nこの香り、\nライラエルさまも\n好きそうです！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8579, 127701, 5, 1, "ランドソルの環境に\n触れると、この光の\nありがたみが\n身に沁みますね…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8581, 127701, 7, 1, "甘くない食べ物が\n一般的なのも\n慣れてきました\nあむ…おいひぃ…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8582, 127701, 8, 1, "ランドソルの文字、\n私たちのところと\nちょっと違いますね…\n覚えられるかなぁ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8584, 127801, 1, 1, "甘い匂いがしない…\nジオ・テオゴニアとは\n別の世界なんですね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8585, 127801, 2, 1, "掃除めんど…\nん…このくらいで\nまー十分でしょ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8586, 127801, 3, 1, "そろそろ食事の\n用意もしなきゃ…\nあーやる事多い…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8587, 127801, 4, 1, "秘密の隠れ場所、\nまた姉さんにバレた…\n次のを探さないと", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8588, 127801, 5, 1, "知らない間に仕事が\n終わったり…しないか\nしてくれたらいいのに", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8589, 127801, 101, 4, "きちんともてなすには\n騎士さんの好みも\n覚えてかないと…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8590, 127801, 102, 8, "ご主人サマに\nご奉仕します～♪\n…キャラじゃないか\nやめやめ、恥ずかし…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8591, 127801, 1, 1, "居心地いいっすね…\n今日からここに\n住んでもいいですか", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8592, 127801, 2, 1, "あの、ちょっと姉さん\n怒らせちゃったんで\n匿ってくれません？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8593, 127801, 3, 1, "羽と尻尾が格好いい？\n意外と邪魔ですよ\n仰向けで寝にくいし", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8594, 127801, 4, 1, "ワークライフバランス\nっていい言葉ですね\nぜひライフ多めで", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8595, 127801, 101, 4, "騎士さんって\n働き者ですね\nそのままあたしの\n仕事も…いえなんでも", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8596, 127801, 102, 8, "熱心に見てても\n尻尾は触らせて\nあげませんってば\n角？　角なら…まぁ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8597, 127801, 1, 1, "寝ても寝ても\n寝足りない…ふぁ…\nあー…眠たい…", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8598, 127801, 2, 1, "アフタヌーンティーの\n準備は得意ですよ\nつまみ食いとか\n…つまみ食いとか", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8599, 127801, 3, 1, "よく休んでください\n布団は干しといたんで\n…姉さんが", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8601, 127801, 2, 1, "ふー…\nこのままずーっと\nここに居たい…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8602, 127801, 3, 1, "あんたも一緒に\nサボっちゃうか～\n…なんて", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8603, 127801, 4, 1, "落ち着く匂い…\n騎士さんの好み\nなんですかね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8604, 127801, 5, 1, "こういうとこの\n掃除って面倒だし…\n今度でいっか", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8606, 127801, 7, 1, "ご飯の前に\nこんな食べたら\n姉さんきっと怒るな…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8607, 127801, 8, 1, "食に関する本が\nやけに多い…\nお国柄ですかね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8609, 130201, 1, 1, "すんすん…\nお兄さんの匂いが\nぷんぷんする～♡\nザコくっさぁ♡", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8610, 130201, 2, 1, "ぴこーん！\nいひひっ、新作メカの\n面白いアイデア、\n思いついちった～♡", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8611, 130201, 3, 1, "メカもいじりたいし\n服もアクセもほしー…\nあ～、おこづかい\n足んないよ～！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8612, 130201, 4, 1, "お勉強でもしよっかな\nザコお兄さんには\n絶対わかんない\nよーなやつ♡", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8613, 130201, 5, 1, "お兄さんの部屋って\nどこどこぉ？\nメカの実験室に\n使えそうだよね～♡", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8614, 130201, 101, 4, "次のデートコースは\nどんなのかなー？\n…いや、むしろ次は\nぼくが決めちゃうっ？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8615, 130201, 102, 8, "強奪計画は念入りに♡\nお兄さんのハートも\n全力でザコザコにする\n計画、立てないとね♡", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8616, 130201, 1, 1, "ねー、ヒマヒマヒマ～\nかまってかまってー♡\nどっか連れてけー♡", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8617, 130201, 2, 1, "今日はこっちで\nご飯食べたいなー♡\n…帰っても５日連続の\nバカナ姉カレーだし…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8618, 130201, 3, 1, "依頼行くならぼくも\n連れてってよー♡\nお兄さんのザコっぷり\n見ててあげる～♡", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8619, 130201, 4, 1, "お兄さんはぼくより\nオトナだもんね～♡\nじゃあオモテナシ、\n期待してるから～♡", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8620, 130201, 101, 4, "ヤマ姉になんかお土産\n持って帰ろうかなー？\nザコお兄さん、選ぶの\n付きあって～♡", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8621, 130201, 102, 8, "遊びにいくときは\nザコお兄さんも\n誘ってあげるよ♡\nどうせヒマでしょ～？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8622, 130201, 1, 1, "ふぁ～、ねむぅ～…\n夜更かしぶん寝るから\nあとで起こして～♡\nあ、変なこと禁止～♡", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8623, 130201, 2, 1, "おなかすいたぁ～\nなんか食べ行こ♡\n甘いのおごって～♡", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8624, 130201, 3, 1, "えー、もう寝る時間？\nじゃあ…添い寝して\nあげよっか？\nなーんて、ウッソー♡", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8626, 130201, 2, 1, "おお～ふわっふわ～♪\nバンディシャーク号の\n座席に使いたーい！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8627, 130201, 3, 1, "カワイイ～♡\n改造したらもっと\nカワイくなるかな～？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8628, 130201, 4, 1, "くんくん…いい匂い♡\nザコお兄さんって\nこーゆー香りが\n好きなのかなぁ～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8629, 130201, 5, 1, "はー、あったかーい♡\nバンディシャーク号\nにも欲しいかもー♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8631, 130201, 7, 1, "食べれるときに\n食べるのが大事って\nヤマ姉言ってたしね～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8632, 130201, 8, 1, "あ、この本読んだよ♡\n参考文献リストが\nしっかりしてて\n勉強になったなー♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8634, 130001, 1, 1, "部屋は多少ゴチャつい\nてる方が落ち着くのさ\nバンディシャーク号\nみたいにね♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8635, 130001, 2, 1, "呆けてるのもいいが\nちいと物足りないねぇ\n麦しゅわもブドウ朱も\n無いなんてさ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8636, 130001, 3, 1, "この家セキュリティが\nなってないねぇ…\n盗賊目線でちょっくら\n指南でもしてやるか", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8637, 130001, 4, 1, "不思議だねぇ\nここで飲むと、酔いが\n回るのが早い気が\nするよ…ヒック", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8638, 130001, 5, 1, "ふむ…\nクルマが壊れた時は\nアジト代わりに\n使えそうだ♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8639, 130001, 101, 4, "カカッ、いい部屋だ！\n妹を迎える準備は\nできてるみたいだねぇ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8640, 130001, 102, 8, "…っと、油断したら\n落ち着いちまうね\nボーヤの甘っちょろい\n雰囲気のせいだよ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8641, 130001, 1, 1, "さすがにこの家から\n盗みはしないさ\nいずれは妹たちの\n共有財産になるからね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8642, 130001, 2, 1, "ボーヤ、暇なら\n博打でもやるかい？\nアタシが負けたら\n掃除でもしてやるよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8643, 130001, 3, 1, "立派な家じゃないか！\n一国一城の主とは\nちょっとボーヤのこと\n見くびってたかもねぇ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8644, 130001, 4, 1, "かわいい義弟のためだ\nアタシが盗ってきた\nお宝をくれてやるよ\n好きな場所に飾りな！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8645, 130001, 101, 4, "妹と結ばれる前に\n他の女との関係は\n清算しとくように\nわかったね、ボーヤ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8646, 130001, 102, 8, "お、ヤシオーリかい？\nフフン、義弟からの\nお酌とあっちゃあ\n断れないねぇ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8647, 130001, 1, 1, "うう…清々しい朝だが\n頭がいてぇ…\nちっと昨日は\n飲みすぎたか…？", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8648, 130001, 2, 1, "ほらこれ、ワカナに\n作らせた弁当だ\nこれで昼からも\nバリバリやりな！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8649, 130001, 3, 1, "まだまだ夜はこれから\n…と言いたいとこだが\n早寝して立派に育ちな\nそれが妹たちの為だよ", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8651, 130001, 2, 1, "中々いい座り心地だが\n盗賊が腑抜ける訳には\nいかないね…が…\nもう少しだけ座るか…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8652, 130001, 3, 1, "こういうのの中に\n実はレアなブツが\n隠されてるとか\nありがちだねぇ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8653, 130001, 4, 1, "ふうん、ボーヤは\nこういう匂いが\n好きなのかい？\n妹たちに教えてやろう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8654, 130001, 5, 1, "体を温めんにゃ\n酵露をカッとやるのが\n一番だが、これも存外\n悪くないねぇ…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8656, 130001, 7, 1, "ガツガツ…\nん、悪くねェ味だ\nこれにヤシオーリでも\nありゃ文句はないね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8657, 130001, 8, 1, "本にも載ってない\nレアもんのお宝か…\nフッ、想像しただけで\n血沸き肉躍るねぇ…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8659, 130101, 1, 1, "今日の買い物は\nいつものお野菜と…\nあっ、確か特売が\nあったはず…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8660, 130101, 2, 1, "お掃除、お掃除…♪\nきゅっきゅっ…\nよいしょ、っと…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8661, 130101, 3, 1, "洗濯ものも\nたまらないうちに\nやっちゃわないと…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8662, 130101, 4, 1, "夕飯のメニュー…\n何にしよう？\n期限の早い食材は…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8663, 130101, 5, 1, "色々お菓子も\n作ってみたいけど…\n予算…足りるかなぁ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8664, 130101, 101, 4, "騎士さん…どんな\n料理が好きなんだろ？\n前に作ったコロッケは\n評判よかったけど…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8665, 130101, 102, 8, "騎士さんと\nおしゃべりすると、\n胸があたたかくなる…\nなんでだろ…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8666, 130101, 1, 1, "あっ、騎士さん\nどうかしましたか？\n御用ならいつでも…♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8667, 130101, 2, 1, "削っても削っても\n家計簿は赤字…\n騎士さ～ん…\n助けてください～！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8668, 130101, 3, 1, "手が空いてたら\n日用品の買い出し、\n手伝ってもらっても\nいいですか…？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8669, 130101, 4, 1, "やっと一息つけそう…\nあっ、わたし\nお茶を淹れてきますね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8670, 130101, 101, 4, "困りごとがあったら\n言ってくださいね\nわたし…騎士さんの\n力になりたいです", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8671, 130101, 102, 8, "助けてもらった分は\n必ず返しますから…\nそれまでわたしのこと\n見ててくださいね…♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8672, 130101, 1, 1, "忘れ物はないですか？\n今日のお仕事も\n頑張りましょうね…！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8673, 130101, 2, 1, "お昼ご飯できてますよ\n大したものじゃなくて\n恐縮ですけど…", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8674, 130101, 3, 1, "夜更かしはだめですよ\nわたしも家事を\n終わらせたら寝ないと", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8676, 130101, 2, 1, "これ…割れ物防止の\nクッションに\n使えるかも…？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8677, 130101, 3, 1, "こういうのいいなぁ…\nけど…フブキちゃんに\n絶対改造されちゃう…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8678, 130101, 4, 1, "いい匂い…でも\n切れかけてるかも？\nえっと補充するには…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8679, 130101, 5, 1, "あったかい…\nちょっとだけなら\n休んでも…いいよね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8681, 130101, 7, 1, "これがあったら\nもうお料理を\n作らなくて済むかも？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8682, 130101, 8, 1, "新しい節約レシピは…\nあ、この草って\n食べられるんだ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8684, 132801, 1, 1, "愛溢れるランドソルの\nクリスマス…ですか\nいけませんね、つい\n胸が躍ってしまいます", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8685, 132801, 2, 1, "ツリーの飾りと\nわたしの髪…\nくるくるしていて\n似ているかも…くすっ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8686, 132801, 3, 1, "この姿で傲慢な\nわたしになったら…\nいつもより怖がらせて\nしまうでしょうね…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8687, 132801, 4, 1, "悪魔として、人を\nドキドキさせなくては\nい、イオから教わった\n…投げキスの練習を…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8688, 132801, 5, 1, "ジオ・テオゴニアの\nお菓子は、ツリーの\n飾りに向いていそう\nですね…ふふっ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8689, 132801, 101, 4, "騎士のギルドハウス…\nクリスマスの時期も\n暖かいのですね…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8690, 132801, 102, 8, "城の皆と過ごす宴も\n楽しいですが…\n騎士と二人の宴も\n幸せです…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8691, 132801, 1, 1, "騎士、あなたの\nもてなしに感謝します\nクリスマスの何たるか\n教えてくださいね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8692, 132801, 2, 1, "この黒い羽は\nどう手入れすれば？\nイオなら知っている\nでしょうか…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8693, 132801, 3, 1, "城の皆も連れてきたい\nほど、クリスマスは\n楽しい行事ですね\nお土産も迷います", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8694, 132801, 4, 1, "クリスマスの晩餐に\n招待されたのですが…\nその…辛いものは\nありませんよね…？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8695, 132801, 101, 4, "クリスマスと言わず…\nいつでも星に祈って\nいますよ？　騎士達に\n幸せがあるように、と", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8696, 132801, 102, 8, "悪魔として騎士を\nドキドキさせる…？\nそ、そのようなこと\nわたしには…うぅ…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8697, 132801, 1, 1, "朝は寒いですね…\n雪…積もっている\nでしょうか？", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8698, 132801, 2, 1, "ふあぁ…いけません\n公務の滞在先で\nお昼寝など…", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8699, 132801, 3, 1, "夜は悪魔として\n夢を見せましょう\n騎士はどんな夢が\n見たいですか？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8701, 132801, 2, 1, "ふぅ…落ち着きます…\n家でゆっくりと過ごす\nクリスマスも\n良いものですね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8702, 132801, 3, 1, "リンドのプレゼントに\nぬいぐるみは…？\n…子ども扱いすると\n落ち込むでしょうか…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8703, 132801, 4, 1, "暖かいお部屋に\nこの香りを漂わせたら\nふふっ…素敵な時間を\n過ごせそうです", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8704, 132801, 5, 1, "これがあれば、\n寒い冬も安心ですね\n暖かくて…このまま\n眠ってしまいそうです", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8706, 132801, 7, 1, "悪魔の力を使ったら\nお腹が空いてきて\nしまって…\n恥ずかしいです…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8707, 132801, 8, 1, "クリスマスには多くの\n伝承があるのですね…\n天使の伝承まで\nありましたし…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8709, 132803, 14, 1, "まぁ…！\nよくできていますね\nサプライズプレゼント\n…でしょうか。ふふ♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8710, 132804, 15, 1, "ど、どうでしょう…？\n親善交流の催しに\nなりそうでしょうか…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8711, 132901, 1, 1, "ギルドハウスって\nあったかぁ～い♪\n心がぽかぽかするの！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8712, 132901, 2, 1, "クリスマスの悪魔の\nお仕事はないわね！\nだってここのみんな\nいい子なんですもの♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8713, 132901, 3, 1, "みんなちゃんと\n寝られてるかしら？\n夜更かしせずに、\nいい夢見てね♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8714, 132901, 4, 1, "毎回、来年が\n楽しみになるような\n素敵なクリスマスを\n過ごしたいわね♪", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8715, 132901, 5, 1, "訳ありケーキを見ると\n刑務作業を思い出すわ\n…あれも貴重な体験\nだったのかしら…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8716, 132901, 101, 4, "ロマンチックな\nクリスマスに備えて\n今のうちに少女漫画で\n予習しておかないと！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8717, 132901, 102, 8, "ほっぺにチューなら\nもう一回しても…\nや、やっぱり無理！\n恥ずかしいわ～！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8718, 132901, 1, 1, "またみんなで\nセッションしたいわね\nカラーベルの練習した\n成果を見せたいの♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8719, 132901, 2, 1, "君はサンタさんに\nどんなプレゼントを\n頼んでいたのかしら？\n気になるわ～！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8720, 132901, 3, 1, "愛が壊れても、\n何度でも治してあげる\n私たちの間に、\n愛の尽きる日はないわ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8721, 132901, 4, 1, "冬休みって短いようで\n長く感じちゃうわよね\n学校のみんなと\n早く会いたいな～♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8722, 132901, 101, 4, "愛をテーマにした\n冬休みの作文の宿題、\n君の分を読むのが\n今から楽しみね♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8723, 132901, 102, 8, "よかったらまた\n私をハニーって呼んで\nほしいの。ダーリン♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8724, 132901, 1, 1, "おはよう！\n冬の朝は冷えるから\n風邪を引かないように\n対策しましょうね", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8725, 132901, 2, 1, "お昼ご飯は一緒に\n食べに行きましょう！\nクリスマス限定の\nランチコースよ♪", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8726, 132901, 3, 1, "ねえねえ、キャンドル\nイルミネーションを\n見に行きましょうよ！\nロマンチックよ～♪", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8728, 132901, 2, 1, "暖かいクリスマスを\n過ごすのに必須よね♪\nふふ、柔らかぁい！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8729, 132901, 3, 1, "どの子もかわいい～♪\nクリスマスツリーの\n下で、一緒にお祝い\nしてもらいたいな♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8730, 132901, 4, 1, "なんだか胸の奥が\n温かくなる匂いね～\nリラックスしちゃう♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8731, 132901, 5, 1, "あったかぁい♪\nあ、でも夜になったら\nサンタさんが入れる\nように、火を消すのよ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8733, 132901, 7, 1, "寒い冬だからこそ\nしっかり食べて\n備えておかなきゃ！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8734, 132901, 8, 1, "『聖夜の６時間』…？\n気になる小説だけど…\n勝手に読んじゃ悪い\nわよね…ガマンよ…！", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8736, 132901, 14, 1, "きゃっ…！\nこんな不思議な本が\nあったのね～\nちょっと面白いかも♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8737, 132901, 15, 1, "もふもふ、ふわふわで\nあったかくて\n気持ちいい～…♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8738, 129501, 1, 1, "冬といえばサマー！\n…って言うけど夏は\n何をＰＲするんだろ\nメルクリゾート…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8739, 129501, 2, 1, "奨学金の道は遠いな…\nあまりに遠すぎて\n最近忘れてたまである", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8740, 129501, 3, 1, "暇だし鏡でも見よっと\nはー、今日も顔が良…\nげ、若干むくんでる？\nはー、今日終わった", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8741, 129501, 4, 1, "水着…お姉ちゃんと\n貸し借りするんだけど\nこれって少数派姉妹？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8742, 129501, 5, 1, "ちゅむ、ツムギちゃん\n今日も可愛いねデュフ\n…なんで推し語りの声\nキモくなるんだろ私…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8743, 129501, 101, 4, "あれ…なんで私\n水着のまま部屋の中\nうろついてるんだっけ\n…こわ！　てか寒っ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8744, 129501, 102, 8, "先輩よく友達と海に\n行くって言ってたけど\n…え、誰とだろ？\nまあ誰でもいいけどさ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8745, 129501, 1, 1, "始まる…\nチエルの夏が始まる…\nあ冬でしたっけ？\nまどっちでもいいです", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8746, 129501, 2, 1, "こないだトミヱ先輩\n壁に喧嘩売ってました\n相変わらず元気ですね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8747, 129501, 3, 1, "ユニ先輩にお芝居の\n出来を聞かれたんで\n可愛いって答えました\n嘘は言ってません", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8748, 129501, 4, 1, "クロエ先輩の腹筋\nかっちょいーですよね\nさりげ触りたいけど\nあいつ隙ないんですよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8749, 129501, 101, 4, "先輩もうカメラ係は\nやらないんです？\nチエルはやってますよ\n最高の被写体を常に", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8750, 129501, 102, 8, "メリゾでカップル割の\n企画あるらしいんです\nちなみに先輩は…\nあ、いえ、何でも…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8751, 129501, 1, 1, "朝ごはんは抜いても\n朝ちぇるだけは\n抜いちゃ駄目ですよ", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8752, 129501, 2, 1, "お昼なに食べるか\n悩んでます？\nおごってくれるなら\nいい店教えますけど", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8753, 129501, 3, 1, "この時間帯になると\nユニ先輩がごそごそ\n蠢きだすんですよね…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8755, 129501, 2, 1, "世界の美少女が…\nこんな格好で\nくつろいでたら…\nまずい…事案になる", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8756, 129501, 3, 1, "え、この子って\nウォータープルーフ？\nメリゾにも連れて\nいけちゃうカンジ？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8757, 129501, 4, 1, "はぁ～、いい香り…\nそういえばメリゾの\nスタッフルームも\n『贅』な匂いしてた…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8758, 129501, 5, 1, "薄着で暖まるとか…\n冷静になっちゃ\n駄目なやつですよね\nこれ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8760, 129501, 7, 1, "おにぎり１個は\nちぇるに換算すると…\n２５ちぇるくらい？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8761, 129501, 8, 1, "あれー？　この辞書\nまだちぇるの項目\n載ってないんだ？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8763, 129501, 10, 1, "何これ…地味に楽しい\nあとたぶん\nクロエ先輩が好きそう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8764, 129501, 11, 1, "これは良きですねー\n自宅でメリゾ気分とか\nユニ先輩もにっこり", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8765, 133001, 1, 1, "ここが騎士氏の\nお住まいですか\nふむ…地縛霊は\nいなさそうですね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8766, 133001, 2, 1, "ここは土足厳禁では\nないみたいですね\nま、私には関係ないか\n足、透っけ透けなので", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8767, 133001, 3, 1, "ランドソルの裏路地\nあそこは最高ですね\n暗くてじめじめしてて\n癒されるー", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8768, 133001, 4, 1, "っ！\n今ラップ音が…？\nまさか幽霊？\nやだ、怖い", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8769, 133001, 5, 1, "獣人族にエルフ族…\nなるほど騎士氏が\n幽霊程度では驚かない\n訳です…ちぇ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8770, 133001, 101, 4, "今日の深夜\n騎士氏が起きるまで\n枕元でずーっと\n立っててみようかな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8771, 133001, 102, 8, "騎士氏の驚く顔を\n見ると胸が高鳴る…\n変ですね、心臓なんて\n動いてないのに", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8772, 133001, 1, 1, "安心して下さい\n勝手にすり抜けて\n騎士氏の部屋は\n覗きませんよ…多分", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8773, 133001, 2, 1, "なんですか騎士氏\n怖い話でも聞きます？\nじゃあいきますね…\n今貴方の前に幽霊がっ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8774, 133001, 3, 1, "この家…幽霊が\n寄ってくる間取りに\nなってますね\nまあ、嘘ですけど", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8775, 133001, 4, 1, "料理はしませんよ\n指きったら痛いので\nえ…透ければいい？\n……天才ですか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8776, 133001, 101, 4, "お散歩ですか？\n深夜なら付き合います\nあ、やっぱり断ります\n幽霊出たら怖いので", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8777, 133001, 102, 8, "騎士氏の部屋のセンス\n好きですよ…もっと\nじめじめしていれば\nなおよし、ですが", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8778, 133001, 1, 1, "レイスになってから\nあまり言わなくなった\n挨拶第一位はこちら…\nおはようございます", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8779, 133001, 2, 1, "騎士氏。午後からも\n頑張って下さいね\n私は日陰で休みます\nでは、おつです", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8780, 133001, 3, 1, "夜も更けてきましたね\nここからがレイスの\n本領発揮…では早速\nう　ら　め　し　や～", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8782, 133001, 2, 1, "中々座り心地が\nよろしいですね\nブロッケンに跨る方が\n安心はしますが", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8783, 133001, 3, 1, "知ってますよ\nどうせ貴方も\n夜中になったら\n動き回るんでしょ？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8784, 133001, 4, 1, "すう…はあ…\nすごく落ち着く香り\n…落ち着き過ぎて\n成仏しそうでした", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8785, 133001, 5, 1, "温度は感じませんが\nなぜかほっこり\n温かい気持ちです\nなんででしょうね？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8787, 133001, 7, 1, "もぐもぐ…\n食べ物は基本好きです\n消化の方法は…\n企業秘密で", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8788, 133001, 8, 1, "苦手なんですよね\nホラー小説\nだって幽霊とか\n怖いじゃないですか", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8790, 133201, 1, 1, "この得も言われぬ\n高揚感…やはり\n正月はいいものだな！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8791, 133201, 2, 1, "む、いかんな\n気付くと黒豆と\n栗きんとんばかり\n食べてしまう…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8792, 133201, 3, 1, "ギルドの連中にも\nお年玉を渡してみるか\n少しは士気が上がる\nかもしれないな！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8793, 133201, 4, 1, "正月くらいはのんびり\n羽を伸ばすとしよう\n適切な休暇も軍人の\n大事な責務だからな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8794, 133201, 5, 1, "正月で浮かれすぎた\n者も多いはずだ\n念のため街を見回りに\n行くとしよう", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8795, 133201, 101, 4, "む、このおせち料理は\n絶品だな！\nあいつの分も\nとっておいてやろう", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8796, 133201, 102, 8, "今年の抱負は…\nそうだな、あいつと\nさらに高みを目指す\nこれでいこう", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8797, 133201, 1, 1, "えっ、お年玉！\n…い、いや、私は\n子どもではないからな\nえ、遠慮させてもらう", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8798, 133201, 2, 1, "凧あげすごろく\nこま回し、たとえ遊び\nでも手は抜かんぞ\nさあ、勝負だ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8799, 133201, 3, 1, "そのきな粉餅も\nうまそうだな…\n私のあんこと交換\nしないか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8800, 133201, 4, 1, "貴公、正月料理を\n食べ過ぎてないか？\nじ、実は私も少々、な", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8801, 133201, 101, 4, "貴公、お参りに行くぞ\n何？　外が寒いだと？\n仕方ない、手ぐらいは\nつないでやろう", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8802, 133201, 102, 8, "貴公と新たな年を\n迎えられてうれしいぞ\n今年も一つ\nよろしくたのむ！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8803, 133201, 1, 1, "起きろ貴公。早朝なら\n参拝客も少ないはず\n今のうちにお参りを\n済ませよう", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8804, 133201, 2, 1, "昼食に餅を焼いたぞ\nのどに詰まらせない\nようにゆっくり噛んで\n食べるんだ", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8805, 133201, 3, 1, "夜ふかしはあまり\n褒められたものでは\nないが…正月くらいは\n目をつむろう", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8807, 133201, 2, 1, "これに座って飲む\n甘酒は格別だな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8808, 133201, 3, 1, "せっかくの正月だし\nこいつにも着物を\n着せてやりたいな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8809, 133201, 4, 1, "これは梅の香り…？\nふふ、なかなか\n風流ではないか", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8810, 133201, 5, 1, "冬の寒さが堪えるな…\nここでしっかりと\n暖をとるとしよう", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8812, 133201, 7, 1, "正月はのんびり\nしたいからな、勝手に\n食事が出てくるのは\n本当に助かる", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8813, 133201, 8, 1, "ふむ、これが世界の\n新年のお祝いか\nなかなか興味深いな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8815, 133501, 1, 1, "一年の計は元旦にあり\n計画を立てて、\n有意義な一年を\n過ごしましょ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8816, 133501, 2, 1, "年末年始はどうしても\n出費が増えちゃうし\nしばらくはきっちり\n節約しなきゃ…！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8817, 133501, 3, 1, "タタミにフトン、\nトーゴク文化の内装…\nランドソルでももっと\n流行らないかしら", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8818, 133501, 4, 1, "子どもたちが凧を\n手作りして遊んでたの\n偉いわよね、みんな…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8819, 133501, 5, 1, "お年玉をあげる側に\nなって、分かる苦労…\n大人になるって\nこういうことかしら", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8820, 133501, 101, 4, "一応実家にも\n顔を出さないと…\nよかったらだけど、\nあんたも来る？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8821, 133501, 102, 8, "毎日一緒にいて、\nささやかな幸せを\n同じように感じて…\nそれだけで十分ね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8822, 133501, 1, 1, "着物姿、もっと\nじっくり見たいの？\nさすがに照れるわよ、\nもう…", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8823, 133501, 2, 1, "整理整頓、できてる？\nもし散らかってるなら\nお正月休みのうちに\n片づけちゃいましょ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8824, 133501, 3, 1, "今年もみんなが無事に\n過ごせますように…\nほら、一緒に\nお祈りしましょ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8825, 133501, 4, 1, "みんな、あんたと\n遊ぶのが楽しみだって\n子どもに好かれる\nタイプなのよね", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8826, 133501, 101, 4, "あんたの運勢は大吉！\nラッキーパーソンは\n金髪の幼なじみよ\n…なんてねっ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8827, 133501, 102, 8, "初夢は実現するのよね\nそれじゃ、あたしは\nあんたと…んんっ\nなんでもないわ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8828, 133501, 1, 1, "外が寒いと、その分\n目も冴えるわよね\nこのまま朝の散歩に\n行こうかしら", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8829, 133501, 2, 1, "お正月休みでも、\n食べてばっかじゃダメ\nしっかり動いて\n体を引き締めましょ", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8830, 133501, 3, 1, "ちゃんとあったかく\nして寝るのよ\nお正月から\n風邪ひかないようにね", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8832, 133501, 2, 1, "着物、しわに\nなっちゃいそう…\nでもそれ以上に、\n心地よくって…はぁ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8833, 133501, 3, 1, "門松みたいに\nこの子も飾ってみる？\n運勢がよくなるかも♪", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8834, 133501, 4, 1, "この匂い、もしかして\nトーゴクのお香？\nワビサビを感じるわ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8835, 133501, 5, 1, "ふぅ、あったかい…\n冷えた体に\n染み渡ってくるわ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8837, 133501, 7, 1, "んっ、おいしい！\nおせちばっかじゃ\nやっぱり飽きちゃう\nものね…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8838, 133501, 8, 1, "験担ぎとか縁起物とか\n調べてみると\nいろいろあって\n面白いのよね～", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8840, 133601, 1, 1, "ふーん、いい家ね\nアタシの巣に\nしちゃおうかしら？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8841, 133601, 2, 1, "お姉ちゃん…\n早く会いたいなぁ…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8842, 133601, 3, 1, "メドゥシアナも\n気に入るかしら？\n今度連れてこよっと！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8843, 133601, 4, 1, "お邪魔してるだけ\nなのも悪いわよね…\nお返しはしないとね\n…ネズミ退治とか？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8844, 133601, 5, 1, "何か平和よね\nランドソルって…\nアタシ的にはもう少し\n荒っぽくてもいいわ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8845, 133601, 101, 4, "ランドソルも\n楽しい街よね…\n案内してくれる\nアイツのおかげ？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8846, 133601, 102, 8, "アタシ、アイツに\n頼りすぎてるかも…？\nで、でも！　断らない\nアイツも問題だわ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8847, 133601, 1, 1, "ねぇ、つまんなーい！\nもっとアタシを\n楽しませなさいよねー", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8848, 133601, 2, 1, "この家悪くないけど…\nもっとムシムシ\nジメジメにできない？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8849, 133601, 3, 1, "何よ？\n用もないのに声掛けた\n…とか言ったら\n容赦しないわよ？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8850, 133601, 4, 1, "少し部屋が寂しいわね\nそうだ、アンタを\n石にして家具に…\n冗談よ、ふふんっ♪", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8851, 133601, 101, 4, "ねぇ、もう少し\n話していかない？\nアンタの話なら…\n特別に聞いてあげるわ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8852, 133601, 102, 8, "い、いつも\nあ、あ、ありが…\nうぅ、何でもないー！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8853, 133601, 1, 1, "やっと起きたわね？\n寝ぼすけなんだから！\n尻尾でひっぱたいて\n起こそうかと思ったわ", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8854, 133601, 2, 1, "人捜しは日の高い\nうちに限るわ！\nさぁ、メドゥシアナ！\n全速力で出発進行よ!", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8855, 133601, 3, 1, "アタシ…\n子どもじゃないもん…\n眠くなんか…\nないんだからぁ…", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8857, 133601, 2, 1, "ふわふわであったかい\nつい、とぐろを\n巻きたくなるわね…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8858, 133601, 3, 1, "アンタ、こんな\nヤワな体じゃ駄目よ\nウロコくらい\n生やしたらどうなの？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8859, 133601, 4, 1, "くんくん…\n…ふん\n悪くないじゃない\n…くんくんくんくん…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8860, 133601, 5, 1, "あったかいの大好き！\nジオ・ゲヘナでも\nよく日向ぼっこ\nしたもんだわ…ふわぁ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8862, 133601, 7, 1, "お姉ちゃんを捜すなら\n…もぐ\n体力つけないとね\n…もぐもぐ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8863, 133601, 8, 1, "何よ？　たまには\n勉強くらいするわよ\nお姉ちゃんに\n笑われちゃうもの", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8865, 133701, 1, 1, "この装いと力…\n今の世界との\n整合性を上手いこと\n合わせておかないとだ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8866, 133701, 2, 1, "この格好…開発してた\n当時のまんまだ\nサイズもそのまま♪\n体型維持も完璧っ！", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8867, 133701, 3, 1, "あとで装備の手入れも\nやっとかないと\n…剣なら研ぐだけで\n終わるんだけどなぁ", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8868, 133701, 4, 1, "自分の原点を\n振り返ってみるのも\nたまには必要だね", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8869, 133701, 5, 1, "完璧じゃなくても\n今できる全力を尽くす\nその事に意味がある…\nそうだよね、少年", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8870, 133701, 101, 4, "始まりのあの日、\nそして向かうべき道…\nいつも忘れずにいよう", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8871, 133701, 102, 8, "誰かを救うために\n許容される犠牲なんて\nあっちゃいけない…\n全部、助けてやる", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8872, 133701, 1, 1, "おっと少年、\n何か御用かな？\nキミに関わる事なら\n最優先で対応するよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8873, 133701, 2, 1, "困った事があれば\n頼ってもらって\n構わないからね！\nむしろもっと頼って！", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8874, 133701, 3, 1, "そうだ、時間あるなら\n武器のメンテを\n手伝ってくれないかな\n数が多くてさ～", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8875, 133701, 4, 1, "遠距離戦の心得、\n少年にもレクチャー\nしてあげよっか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8876, 133701, 101, 4, "無茶しないで、って\n言ってもするだろうし\nせめてアタシの手が\n届く範囲で頼むよ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8877, 133701, 102, 8, "虚実入り混じる\nこの世界でキミが…\n進むべきルートを\n照らしてくれたんだ", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8878, 133701, 1, 1, "グッモーニン！\n朝だよ少年、今日も\n張り切っていこう！", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8879, 133701, 2, 1, "クレープの代わりに\nガレットを焼いたんだ\nこの味で少年を\n唸らせてみせよう！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8880, 133701, 3, 1, "もうこんな時間だ\n遅くならないうちに\n作業を切り上げようか\n少年も早く寝なよ？", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8882, 133701, 2, 1, "あえて緊張を解くのも\n戦いには必要だし…\nそうそう…大事大事…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8883, 133701, 3, 1, "愛着を抱かせる事を\n意図したデザインだね\n心理的効果の観点から\n分析してみようか…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8884, 133701, 4, 1, "香りは記憶と密接に\n結びつく事がある…\n懐かしい匂いだね…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8885, 133701, 5, 1, "癒されるな…\n何がこの感情変化を\n引き起こすんだろう？", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8887, 133701, 7, 1, "食の偉大さを感じるな\n戦闘糧食ってやっぱり\n味気ないしなぁ…", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8888, 133701, 8, 1, "実用ばかりじゃなく\n詩や物語ってのも\n人には必要だよね", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8890, 133901, 1, 1, "この場所の開発版も\n探せばあるかもな\nさて、どんなデザイン\nだったのやら…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8891, 133901, 2, 1, "今まで数々の波乱に\n身を投じたが…やはり\n波乱にはこの姿が一番\n似合いだよ☆", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8892, 133901, 3, 1, "むっ…この角に羽と\n尻尾は家具とぶつかり\nやすいな。兎の時は\n気にならなかったが…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8893, 133901, 4, 1, "いつもより闘争を\n求める気分になるな\n初心に帰るとは\nこのことか…？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8894, 133901, 5, 1, "未調整上等☆\nそれを上手く使い\nこなしてこその\nワタシだろう？", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8895, 133901, 101, 4, "坊やとできそうな\n愉しいことは…\nふむ、多すぎて迷うな", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8896, 133901, 102, 8, "時折、坊やの居心地の\nよさに絆されそうに\nなってしまうな\nワタシらしくもない…", 1, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8897, 133901, 1, 1, "どうだ、おろした\n髪型も艶やかだろう？\nそんじょそこらの\n賞賛では満足せんぞ☆", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8898, 133901, 2, 1, "外に出るぞ！\n共に退屈を殺しに\n行こうじゃないか☆", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8899, 133901, 3, 1, "角と翼も触りたい？\nワタシの虚数魔境に\n挑むというなら\n止めはせんよ☆", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8900, 133901, 4, 1, "なんだ、困りごとか？\nクリスティーナさんが\n何でも聞いてやろう☆\n解決するかは別だがな", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8901, 133901, 101, 4, "力が欲しいか？\nならば呼ぶがいい！\nワタシの名を☆", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8902, 133901, 102, 8, "悪魔との契約は絶対\nなのだそうだが…\nどうだ、坊や\n一つ契ってみるか？", 2, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8903, 133901, 1, 1, "まだ眠い？　甘えるな\n寝首を掻かれたく\nなければ飛び起きて\nこい…☆", 2, 1, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8904, 133901, 2, 1, "昼食の準備を\nしてやろう。\n毒見はいらんぞ？\n安心するといい！", 2, 2, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8905, 133901, 3, 1, "坊やは寝る時間だ\n寝ないのならば…\n大人の時間に\n付き合ってもらうか☆", 2, 3, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8906, 133901, 1, 1, "角と翼、尻尾の\nコンディションまで\n完璧だな☆", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8907, 133901, 2, 1, "なかなかのフワモコ\n具合だが…ううむ…\n翼と尻尾が邪魔だな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8908, 133901, 3, 1, "うーむ、角と尻尾を\n追加して…さらに槍を\n持たせた方が見栄えが\nよさそうだ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8909, 133901, 4, 1, "いい香りだな\n血と砂塵の臭いで\n上書きするまでの\n間に合わせに最適だ☆", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8910, 133901, 5, 1, "生温いな\nもっと焦がれるほどの\n熱量が欲しいところだ", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8911, 133901, 6, 1, "普段はないものが\n生えているのだから、\n運動でしっかり感覚を\n掴んでおかなければな", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8912, 133901, 7, 1, "腹が減っては戦は…\nと言うからな\nしっかり食べて\nおくとしようか☆", 3, 0, 1, 0);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (8913, 133901, 8, 1, "『家庭を築くための\n絶対条件』…？\nふむ…興味がある\nタイトルだ", 3, 0, 1, 0);
