INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "もうこんな時間…\nそろそろ寝なくちゃ\nおやすみなさい", 100202, 91);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "よし…\nこれなら\n褒めてもらえるかな？", 100202, 92);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "がんばることも\n必要だけど\nお休みも大切だよね", 100202, 93);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいい～♪\n騎士クンは\nこういうのって好き？", 100202, 94);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "戦う前にもこうやって\n落ち着けたら\nいいのに", 100202, 95);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あの…！\nよ、良かったら\n一緒に…！", 100202, 96);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "騎士クンと一緒に\n冒険するなら\n体力がないと…！", 100202, 97);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "最近服のサイズが\n合わないんだけど…\n食べ過ぎなのかな？", 100202, 98);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "…やっぱり\n月刊恋愛指南は\nためになる…", 100202, 99);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "装備の手入れでも\nしようかな…", 100301, 101);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "次に鍛えるべきは\n肉体面か精神面か\nどちらにしよう…？", 100301, 102);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？", 100301, 103);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "仲間なんていらない\nそう思ってた頃も\nあったな…", 100301, 104);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "考えごとをしていると\nつい部屋中を\n歩いてしまうな", 100301, 105);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "彼は\nどこまで強くなるのか\n私も負けられないな", 100301, 106);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？", 100301, 107);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ああ、キミだったのか\n人の気配がしたから\n誰かと思ったよ", 100301, 108);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "斬りかかるつもりなら\nちゃんとかかってきて\n…呼んだだけ？", 100301, 109);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "なに？　私に相談？\n戦いについてなら\n喜んで", 100301, 110);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "今のが突き？\n軽い！　もっと鋭く！\nさあ、もう一回！", 100301, 111);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ひゃっ！　ど、どこを\n触ってるの！？\n…油断した", 100301, 112);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "キミにはこれからも\n私のそばに…\nいや、なんでもない", 100301, 113);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよう\n今日も充実した\n一日にしていこう", 100301, 114);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "そろそろ\nお茶の時間か\nキミも一緒にどう？", 100301, 115);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？", 100301, 116);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "戦いを経てもなお\n美しくあるように…\nこれでよし♪", 100301, 117);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "戦ってばかりで\n休まないと、また\nユイに怒られるな", 100301, 118);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう", 100301, 119);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "これはいい…\n戦いの前の精神統一に\nもってこいじゃないか", 100301, 120);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "時々は落ち着いた\n時間を過ごすのも\n悪くない…", 100301, 121);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！", 100301, 122);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "食事は大切だけど\n食べ過ぎには\n注意しておこう…", 100301, 123);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ユイ…！\nこ、こんな本を\n読んでいたのか…！", 100301, 124);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "海だけじゃなくて\n川でも釣りがしたいな\n道具も新調しよう", 100302, 126);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？", 100302, 127);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "彼は\nどこまで強くなるのか\n私も負けられないな", 100302, 128);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "そろそろ修行の\n内容を新しくしよう\nより厳しいものにね", 100302, 129);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "強くなって…誰にも\n妨げられない自由な\n生き方がしたい", 100302, 130);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 8, "まさか風邪を引くとは\n彼の看病のおかげで\nすっかり良く\nなったけど", 100302, 131);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？", 100302, 132);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "どうしたの？\nなんでもない？\nそれなら良かった", 100302, 133);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "あんまり突っつくと\nお返しするよ？\n覚悟は出来てる？", 100302, 134);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "ねぇ、今度また\n釣りに行かない？\n大きいのを狙おう", 100302, 135);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "修行がしたいの？\nそれを早く言ってよ\nすぐに出発しよう！", 100302, 136);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 8, "ひょっとして\n今のも一本だなんて\n言わないよね？", 100302, 137);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "キミにはこれからも\n私のそばに…\nいや、なんでもない", 100302, 138);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "今日は仕事？\nそれとも修行？\n良ければ付き合うよ", 100302, 139);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "休息も時には\n必要だと、仲間から\n教わったよ", 100302, 140);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？", 100302, 141);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "髪の長さを少し\n変えてみようかな…\nわ、私は何を…！", 100302, 142);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "休息があってこそ\n万全な状態で\n戦えるんだ", 100302, 143);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう", 100302, 144);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "体と同様に\n心もほぐれるね…\nはぁ…", 100302, 145);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "休む時は休む\nヒヨリもそろそろ\n覚えて欲しいな…", 100302, 146);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！", 100302, 147);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "修行のせいか\n最近食べる量が\n増えてきたような…", 100302, 148);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ユイ…！\nこ、こんな本を\n読んでいたのか…！", 100302, 149);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "お宝の予感！\n部屋中を\n探検しちゃうぞ～", 100401, 151);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "落とし穴…どこに\n作ったんだっけ？", 100401, 152);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "うえ～ん！\n…嘘泣きしたら\n相手は油断するかな", 100401, 153);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "ハニートラップって\nすごく甘いお菓子で\nイタズラするのかな？", 100401, 154);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "かくれんぼする人\nこの指、と～まれ！", 100401, 155);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "ミソギ以外の人に\nイタズラされちゃ\nダメだよ！", 100401, 156);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "ミソギと遊ぶ？\nミソギに遊ばれる？", 100401, 157);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "いいこと思いついた！\nい、イタズラとかじゃ\nないよ～", 100401, 158);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "うわっ！？\n　にいちゃんが、\nミソギにイタズラ？", 100401, 159);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "にいちゃんの\n通りそうなところに\nバナナの皮を…", 100401, 160);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "にいちゃんのオデコと\nホッペと鼻と目と耳に\n落書きを…", 100401, 161);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ここ、ミソギ探検隊の\n基地にしようよ！", 100401, 162);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "だ～れだ…って？\nにいちゃんの声くらい\nわかるよ！\n大好きだもん♪", 100401, 163);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "にいちゃん、おはよ！\n目覚まし時計を\n止めておいたのに、\nよく起きられたね～♪", 100401, 164);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "にいちゃんが\n昼寝したら、\nイタズラし放題♪", 100401, 165);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜の探検も\nおもしろそう！\nねぇ出かけちゃダメ？", 100401, 166);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "イタズラには\nお色気も必要…\nうん、完璧！", 100401, 167);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "座ったら、ブーッって\nいう音が出たら、\nおもしろいな～♪", 100401, 168);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいい！\nミソギ探検隊の\nマスコットにしよう♪", 100401, 169);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "吸うとくしゃみが\n止まらなくなるお香を\nここに…くしゅっ！", 100401, 170);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "にひひ…イタズラの\n証拠、ここで\n燃やしちゃおう～！", 100401, 171);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "探検家は\n体力勝負だよー！", 100401, 172);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "みんなに分けて\nあげようかな～\n激辛ソースも\n入れちゃうけどね！", 100401, 173);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "こういう所に\n宝の地図が\n隠れてるんだよね～", 100401, 174);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "お宝の予感！\n部屋中を\n探検しちゃうぞ～", 100402, 176);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "うぅ、探検に出たい！\nじっとしてなんか\nいられないよ～！", 100402, 177);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "うえ～ん！\n…嘘泣きしたら\n相手は油断するかな", 100402, 178);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "ハニートラップって\nすごく甘いお菓子で\nイタズラするのかな？", 100402, 179);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "かくれんぼする人\nこの指、と～まれ！", 100402, 180);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 8, "にいちゃんのオデコと\nホッペと鼻と目と耳に\n落書きを…", 100402, 181);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "にいちゃんが喜ぶ\nイタズラ、何かない\nかな～？", 100402, 182);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ミソギがしかけた\nイタズラ\n見つけられるかな？", 100402, 183);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "探検する？\n冒険する？\nそれとも…\nイタズラされる？", 100402, 184);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "将来の夢は探検家！\n探したい物はないけど", 100402, 185);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "にいちゃんに宿題！\n落とし穴を10個\n掘ること！", 100402, 186);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 8, "だ～れだ…って？\nにいちゃんの声くらい\nわかるよ！\n大好きだもん♪", 100402, 187);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "ここ、ミソギ探検隊の\n基地にしようよ！", 100402, 188);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "にいちゃん\n探検に行こうよ！\n今からなら遭難しても\n夜には帰れるよ！", 100402, 189);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "にいちゃんが昼寝したら\nイタズラし放題♪", 100402, 190);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜の探検も\nおもしろそう！\nねぇ出かけちゃダメ？", 100402, 191);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "イタズラには\nお色気も必要…\nうん、完璧！", 100402, 192);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ゆっくりまったりして\n次のイタズラを\n考えよ～！", 100402, 193);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいい！\nミソギ探検隊の\nマスコットにしよう♪", 100402, 194);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "吸うとくしゃみが\n止まらなくなるお香を\nここに…くしゅっ！", 100402, 195);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "にひひ…イタズラの\n証拠、ここで\n燃やしちゃおう～！", 100402, 196);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "探検家は\n体力勝負だよー！", 100402, 197);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "探検のおやつに\nぴったりかも～！", 100402, 198);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "こういう所に\n宝の地図が\n隠れてるんだよね～", 100402, 199);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "音楽は全部好きだけど\n一番好きなのは\nやっぱりジャズかな", 100601, 201);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "はぁ、はぁ…\n一人でもいっぱい\nしないと…\n特訓…", 100601, 202);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "お姉ちゃんには\nもっと自信を\n持ってほしいなぁ", 100601, 203);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "お姉ちゃんのマネ\nしちゃおうかな？", 100601, 204);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "双子って、\n好みのタイプも\n似ちゃうのかな？", 100601, 205);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "お兄ちゃんと私は\n相性抜群です♪", 100601, 206);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "もう待ちきれない…\nお兄ちゃん、\n早く帰って来て！", 100601, 207);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "あんっ…着替えよう\nと思ったのに\nお兄ちゃんのエッチ！", 100601, 208);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "うふふ、お兄ちゃん！\n今日は何を\nするんですか？", 100601, 209);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "そんなところ\n触っちゃダメ♪", 100601, 210);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "お兄ちゃん、イジワル\nしないでください…", 100601, 211);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "お兄ちゃん、もっと\n上級編のテクニック、\n教えてくださいね！", 100601, 212);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "コーチ、\n今日も特訓\nお願いします！", 100601, 213);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようございます\n昨夜は楽しかった\nですね、うふふ♪", 100601, 214);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "こんな明るい時間から\nす、するんですか…\n二人っきりの特訓…", 100601, 215);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜はこれからですよ\nお兄ちゃん♪", 100601, 216);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "世界一、かわいく\nなるんだから♪", 100601, 217);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "気持ちいい♪\nもう立てないよ～", 100601, 218);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいい♪\nアカリのライバル\nだね", 100601, 219);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "はぁ…気持ちいい、\n私、このままダメに\nなっちゃいそう…", 100601, 220);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "はぁ…アカリ、\nとろけちゃう…", 100601, 221);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "はぁ…はぁはぁ、\n私…もう…ダメ\n限界です…！", 100601, 222);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "わぁ、おいしい～\nけど食べ過ぎは美容に\nよくないですよね", 100601, 223);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "この楽譜、\nサックスで吹いて\nみたいなぁ♪", 100601, 224);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "このごろ一人でいると\nお兄ちゃんのこと\nばかり考えちゃう…", 100602, 226);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "はぁ、はぁ…もっと\nいっぱいしなくちゃ\n…練習", 100602, 227);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "このお部屋、なんだか\n落ち着きます…\nお兄ちゃんが\nいるから？", 100602, 228);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "みんなかわいいな～\n世界一かわいくなる\nのは、難しいな…", 100602, 229);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "双子って、\n好みのタイプも\n似ちゃうのかな？", 100602, 230);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 8, "いつか世界でいちばん\nかわいくなって\nお兄ちゃんと…", 100602, 231);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "もう待ちきれない…\nお兄ちゃん、\n早く帰って来て！", 100602, 232);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "あんっ…着替えよう\nと思ったのに\nお兄ちゃんのエッチ！", 100602, 233);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "二人きりの秘密の特訓\n今度はいつですか？\n待ち遠しいです…", 100602, 234);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "え？　ここでするの？\nみんなの前で恥ずか\nしいな…筋トレ", 100602, 235);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "お兄ちゃん、イジワル\nしないでください…", 100602, 236);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 8, "お兄ちゃんといると\nわ、私…ドキドキ\nしてきちゃう…", 100602, 237);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "コーチ、\n今日も特訓\nお願いします！", 100602, 238);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようございます\n昨夜は楽しかった\nですね、うふふ♪", 100602, 239);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お兄ちゃんに\n食べてもらいたいなぁ\n手作りのお昼ごはん", 100602, 240);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "お風呂入ってきますね\nそのあとは…\n二人でい～っぱい\n遊びましょうね", 100602, 241);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "お姉ちゃんのマネ\nしちゃおうかな？", 100602, 242);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "気持ちいい♪\nもう立てないよ～", 100602, 243);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいい♪\nアカリのライバル\nだね", 100602, 244);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "はぁ…気持ちいい、\n私、このままダメに\nなっちゃいそう…", 100602, 245);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったか～い…\nまるでお兄ちゃんが\n隣にいる時みたい", 100602, 246);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "汗をかくと\n気持ちいいですよね", 100602, 247);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "お兄ちゃんの特訓に\n付き合うために\nたっぷり\n食べておかないと♪", 100602, 248);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "この楽譜\nサックスで吹いて\nみたいなぁ♪", 100602, 249);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "プリン、プリン、\nプリン、プリン、\nプリン、プリン～♪", 100701, 251);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "う～ら～め～し～や～\nなの～♪", 100701, 252);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "プリンがないなら\n死んだほうがマシなの\nあ、もう死んでたの～", 100701, 253);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "幽霊の正体見たり、\nミヤコなの～", 100701, 254);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ミヤコがいきなり\n消えたら、みんな\n驚くかもなの～♪", 100701, 255);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "アイツがいて、\nプリンもある。\nきっとここが天国なの", 100701, 256);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "アイツはプリンセス\nナイトじゃなくて\nプリンナイトなの～", 100701, 257);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "姿を消して、オマエを\n見張ってやるの～\nむふふ～♪", 100701, 258);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "もし成仏した先に\nプリンがなかったら\n地獄なの～…", 100701, 259);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "右にプリン左にプリン\n後ろにもプリン♪\n前にはオマエなの～！", 100701, 260);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "死んだ人には\nお供え物をするの～\nミヤコにも、プリンを\n毎日お供えするの～", 100701, 261);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "たくさんオマエと\n遊べて、\n成仏しそうなくらい\nうれしいの～！", 100701, 262);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "オマエのこと\n気に入ったからずっと\nとり憑いてやるの～", 100701, 263);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "う～　眠いの…\n二度目の二度寝を\nするの…", 100701, 264);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼ご飯はプリンなの\n３時のおやつも、\nプリンなの～", 100701, 265);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "ディナーは豪華に、\nプリンのフルコース\nなの～♪", 100701, 266);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "体が透けちゃうから\nうまく使えないの～", 100701, 267);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "まったりなの～\nあとはプリンがあれば\n完璧なの～", 100701, 268);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "む～…かわいいけど\nプリンは分けて\nあげないの～", 100701, 269);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "は～　いい気分なの…\n成仏しそうなの…", 100701, 270);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "も、もしかして\nミヤコ、ここで\n火葬されちゃうの？", 100701, 271);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "幽霊には、体力も\n筋力も関係ないの～！", 100701, 272);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "ぱくぱくモグモグ…\nミヤコ、幽霊だけど、\nお腹はすくの～", 100701, 273);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "プリンの写真集が\nあればいいのに\nなの～", 100701, 274);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "うらめしや～…\n裏の飯より\nプリンがいいの～", 100702, 276);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "プリン♪　プリン♪\nプリン♪　なの～♪　", 100702, 277);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "世界中のケーキ屋が\nプリンのおいしさを\n競い合えばいいの～！\n審査員はミヤコなの～", 100702, 278);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "買ってきたプリンには\n全部ミヤコの名前を\n書いておくの～", 100702, 279);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "プリンがないなら\n死んだほうがマシなの\nあ、もう死んでたの～ ", 100702, 280);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 8, "アイツがいて\nプリンもある\nきっとここが天国なの", 100702, 281);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "アイツはプリンセス\nナイトじゃなくて\nプリンナイトなの～", 100702, 282);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "右にプリン左にプリン\n後ろにもプリン♪\n前にはオマエなの～！", 100702, 283);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "もし成仏した先に\nプリンがなかったら\n地獄なの～…", 100702, 284);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "今日はオマエと\nプリンを買いに行くの\n強制なの～　", 100702, 285);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "死んだ人には\nお供え物をするの～\nミヤコにも、プリンを\n毎日お供えするの～", 100702, 286);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 8, "たくさんオマエと\n遊べて\n成仏しそうなくらい\nうれしいの～！ ", 100702, 287);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "ミヤコのプリン\nオマエにだったら\n分けてあげるの～ ", 100702, 288);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "う～　眠いの…\n二度目の二度寝を\nするの… ", 100702, 289);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "う～ら～め～し～や～\n…明るい時間だと\n驚かせないの～ ", 100702, 290);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "ひゅ～どろどろ～ん☆\n真っ暗なうちに\nいたずらしまくるの～♪", 100702, 291);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "体が透けちゃうから\nうまく使えないの～ ", 100702, 292);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "落ち着くの～\nいまプリン食べたら\nきっといつもより\nおいしいの～♪ ", 100702, 293);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "む～…かわいいけど\nプリンは分けて\nあげないの～　", 100702, 294);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "は～　いい気分なの…\n成仏しそうなの…", 100702, 295);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "も、もしかして\nミヤコ、ここで\n火葬されちゃうの？ ", 100702, 296);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "幽霊には、体力も\n筋力も関係ないの～！　", 100702, 297);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "おいしいの～\nプリン味だったら\nもっとおいしいの～ ", 100702, 298);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "プリンの写真集が\nあればいいのに\nなの～ ", 100702, 299);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "もし太陽が滅んでも\nボクの美しさで大地を\n照らしてあげる！", 100801, 301);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "う～ん、この部屋には\nボクの肖像画や\n彫像が必要だね", 100801, 302);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "美の女神と愛の女神が\nボクを作る時、本気\n出しちゃったんだね♪", 100801, 303);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "ボクの誕生とともに\n全ての美しさは過去の\n遺物になったんだよ！", 100801, 304);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "うわっ昨日より可愛く\nなってる。明日は\nどうなっちゃう\nのかな？", 100801, 305);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "このボクがいるだけで\nあの人への\nおもてなしだよね", 100801, 306);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "ボクの可愛さは、\nふふ…あの人の\nためのものさ", 100801, 307);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ねぇ～、ボクって\n可愛い？\nふふ、知ってる☆", 100801, 308);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "呼んだ？\n可愛いボクと話したい\n気持ちはわかるよ", 100801, 309);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "ふふ、ボクに触ると\nキミも美しく\nなっちゃうかもよ？", 100801, 310);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "どうかな、\n今日のボク。\n美しい？　可愛い？\nねえ、どっち？", 100801, 311);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "あの人も今、\n可愛いボクのこと\n考えてるかな…", 100801, 312);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "美しい…\nキミの瞳に写るボクは\nより一層きれいだ", 100801, 313);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "美しいボクの\n美しい一日が\n始まるよ！", 100801, 314);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "さーて、午後からも\n可愛く美しく\n華麗に優雅に♪", 100801, 315);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "バラの花びら\nいっぱい浮かべた\nお風呂に入ってから\n寝ようっと♪", 100801, 316);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "鏡よ、鏡。この世で\n一番可愛いボクに\nお似合いの人はだれ？", 100801, 317);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ボクが座ったソファは\n「美しいソファ」に\nランクアップした！", 100801, 318);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "わあ、可愛い！\n…まあ、ボクほど\nじゃないけどさ！", 100801, 319);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "服を羽織るように\n香りをまとうボク…\n美しい、美しすぎる！", 100801, 320);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "気安く触ったら\n火傷する…\n炎はボクに似ているね", 100801, 321);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "運動は苦手なのに\n運動するボクの姿は\n可愛いなんて…罪♪", 100801, 322);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "ボクが食べると、\n食べ物まで\n可愛いなあ…", 100801, 323);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ボクが触った瞬間に、\nこの本は美を\n携えてしまうんだ", 100801, 324);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ボクの美しさは\nみんなのために\nあるんだよ！", 100802, 326);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "う～ん、この部屋には\nボクの肖像画や\n彫像が必要だね", 100802, 327);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "これ以上美しくなると\nボクをめぐって\n大陸中で戦争が\n始まっちゃう！", 100802, 328);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "ボクの誕生とともに\n全ての美しさは過去の\n遺物になったんだよ！", 100802, 329);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ボクの可愛さについて\nみんな話し合わない\nのかな…？", 100802, 330);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 8, "このボクがいるだけで\nあの人への\nおもてなしだよね", 100802, 331);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "あの人も今\n可愛いボクのこと\n考えてるかな…", 100802, 332);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "美の女神と愛の女神が\nボクを作る時、本気\n出しちゃったんだね♪", 100802, 333);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "ボクの美しさに嫉妬\nしたって意味ないよね\n次元が違うんだから", 100802, 334);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "ボクの化粧品使う？\n一緒に可愛くなろうよ", 100802, 335);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ボクがいるだけで\nこの部屋は美の神殿♪\nランドソルの新たな\n観光名所になるね！", 100802, 336);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 8, "呼んだ？\n可愛いボクと話したい\n気持ちはわかるよ", 100802, 337);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "美しい…\nキミの瞳に写るボクは\nより一層綺麗だ", 100802, 338);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "朝日と共に\nボクの美しさも\n周りを照らすよ", 100802, 339);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "ボクの美しさを\n維持するために\nお昼ご飯食べなくちゃ", 100802, 340);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "バラの花びらいっぱい\n浮かべたお風呂に\n入ってから\n寝ようっと♪", 100802, 341);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "鏡よ、鏡。この世で\n一番可愛いボクに\nお似合いの人はだれ？", 100802, 342);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ボクが座ったソファは\n「美しいソファ」に\nランクアップした！", 100802, 343);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "可愛いものを\n可愛いって言える\nボクって可愛いよね！", 100802, 344);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "服を羽織るように\n香りをまとうボク…\n美しい、美しすぎる！", 100802, 345);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "冷えは美容の大敵\nなんだから！\nここ、ボクの場所ね！", 100802, 346);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "運動は苦手なのに\n運動するボクの姿は\n可愛いなんて…罪♪", 100802, 347);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "ボクが食べると\n食べ物まで\n可愛いなあ…", 100802, 348);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ボクが触った瞬間に\nこの本は美を\n携えてしまうんだ", 100802, 349);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "「第１０部２０章\n終焉戦争、開幕」\n…これでよし！", 100901, 351);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "今度シグルドに\n私の魔法を一つ\n伝授してやるか", 100901, 352);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ここでは風が\n吹いてこないな…\n換気したい！", 100901, 353);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "そうだ、ナナカに\nこの間借りた本を\n返さないと…", 100901, 354);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "まずは包帯をとって\n手から龍の形の\n炎を出して…", 100901, 355);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "シグルド…\n今頃何処で何を\nしているのだろう…", 100901, 356);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "最近、彼の姿が\nまぶしく見える…\nどうして…？", 100901, 357);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "辞書を知らないか？\n…探している\n理由は聞くな", 100901, 358);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "そうだ！\n貴様の技にも\n名前をつけてやろう！", 100901, 359);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "私に触れても力を\n奪われないのは\n貴様くらいだぞ？", 100901, 360);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "何事だ？\nまさか我が瞳に\n魅入られたのか？", 100901, 361);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "貴様がそばにいると\nとても安心できる…\n…不思議だ", 100901, 362);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！", 100901, 363);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "「覚醒」めたか…\n行くぞ、悪を討つ\n戦いに出発だ！", 100901, 364);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "昼間は「ヤツら」も\n襲ってはこない…\n束の間の休息だ", 100901, 365);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "どうした？　夜はまだ\nこれから…ふぁ…\nね、眠くなどないぞ！", 100901, 366);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな", 100901, 367);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "くっ…！\n私を眠りの国へ\n誘うつもりか！", 100901, 368);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "このようなものに\n懐柔されるものか！\n…されるものか！", 100901, 369);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ", 100901, 370);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "これが我らの求めた\n安寧か…\n悪くない…", 100901, 371);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "この程度\n前世での激闘に\n比べれば…！", 100901, 372);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "最近金欠でな…\nいやぁありがたい…", 100901, 373);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ここにも\n前世の記憶が\n眠っていたとは…", 100901, 374);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "「第１３部１章\n閃光のシグルド」\n…ついに登場だな", 100902, 376);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "何故だ…何故\nこうもあっさり金欠に\n逆戻りするのだ！？", 100902, 377);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "やはり転生者という\n設定はカッコイイ…\n天才だな、私！", 100902, 378);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "そうだ、ナナカに\nこの間借りた本を\n返さないと…", 100902, 379);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "そろそろ今日の分の\n魔力を溜めないと\nまた魔力切れが…", 100902, 380);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 1, "最近奴がそばに\nいるだけで、鼓動が\n早くなるのは何故だ？", 100902, 381);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "シ、シグルド…！\nだめだ…妄想でも\n上手く言えない…", 100902, 382);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 8, "なんだ？　新たな\n賞金の情報を\n持ってきたのか？", 100902, 383);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "名乗りやポーズは\nその強度で相手を\n圧倒する技術だ", 100902, 384);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "いいか、魔法の\n詠唱中は絶対に\n声をかけるなよ？", 100902, 385);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "おお、ちょうど今\n貴様専用の設定集を\n作成中だぞ！", 100902, 386);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 1, "貴様がそばにいると\nとても安心できる…\n…不思議だ", 100902, 387);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！", 100902, 388);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 8, "夜更かしのせいか\nまだ眠気が…いや！\nこれは組織の罠だ！", 100902, 389);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "平穏な時に\n出来るだけ魔力を\n溜めておかねば…", 100902, 390);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜になると、力が\n増した気分になるな\n…なりませんか？", 100902, 391);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな", 100902, 392);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "お、おのれ\nヒュプノス…\n私は負けないぞ…", 100902, 393);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "くっくっくっ…\n貴様は特別団員に\nしてやろう", 100902, 394);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ", 100902, 395);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "これが我らの求めた\n安寧か…\n悪くない…", 100902, 396);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "新たな戦いに備えて\n魔力だけでなく\n体力も鍛えねば！", 100902, 397);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "食事は魔力に\n変換する体力の\n元になるのだ！", 100902, 398);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "フフフ…\nこれを覚えれば\nもっと強く…！", 100902, 399);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "王子はんほんまに\nはんなりしてて、\n王子はんって感じやわ", 101001, 401);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "この絵本の王子はん、\n王子はんみたいで\nええなぁ♪", 101001, 402);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "うちがガラスの靴を\n落としたら、王子はん\n見つけてくれるやろか", 101001, 403);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "マホマホ王国\n次期国王の王子はん\n…見てみたいわ～", 101001, 404);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "王子はんのために\n糸を紡いで、\n編み物でもしまひょか", 101001, 405);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "マホ姫と王子はん、\n絵本の世界より\nベストカップルや", 101001, 406);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "王子はんと\n一緒にいると\n胸がせわしのー\nうごいてかなん", 101001, 407);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "うちら並んでたら\nお雛様みたいに\n見えると思いまへん？", 101001, 408);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "王子はんを\n国に連れて帰ったら\nみんな大歓迎して\nくれるやろなぁ", 101001, 409);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "王子はんは、\n絵本から出てきた人\nみたいやね～", 101001, 410);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "うちの話し方\nのんびりしてます\nやろか？", 101001, 411);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "マホ姫の手は\nつべたいから、\n王子はんが温めて？", 101001, 412);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "うちがもし悪い魔法に\nかかったら、王子はん\n口づけしてくれはる？", 101001, 413);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "朝は小鳥はんも\nおなかがすいてるから\nよう飛び回りはるねえ", 101001, 414);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お茶請けはお大福が\nよろしおすけど\nクッキーもええなあ", 101001, 415);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "うち、普段から\n夢見がちやけど…\nほんに眠い時間やわぁ", 101001, 416);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "王子はんに\n会いに行くなら、\nおめかしせな\nあかんなぁ", 101001, 417);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "はんなりした\nええお椅子やわぁ\nほっこりしますなあ", 101001, 418);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かいらしもんが\nいっぱいで、\nマホマホ王国みたい\nやわぁ", 101001, 419);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "心地ええどす。まるで\nお花畑にいるようやね", 101001, 420);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ぽかぽかどす。\n心まであったかく\nなりますわぁ", 101001, 421);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "はぁはぁ、お姫はんは\n激しい運動は\nしぃひんもんどす～", 101001, 422);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "もぐもぐ…あんまり\n速う食べられへんけど\n美味しいわぁ", 101001, 423);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "絵本、絵本～\n幸せやわぁ♪", 101001, 424);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "王子はんにとって\n自慢の姫になるえ～", 101002, 426);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "カボチャの馬車に\nガラスの靴…\n欲しいなぁ", 101002, 427);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "うちがガラスの靴を\n落としたら、王子はん\n見つけてくれるやろか", 101002, 428);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "マホマホ王国\n次期国王の王子はん\n…見てみたいわ～", 101002, 429);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ほんまなぁ\nこの絵本が面白いんよ\n王子はんに\n読んであげよ", 101002, 430);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 8, "マホ姫と王子はん\n絵本の世界より\nベストカップルや", 101002, 431);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "王子はんと\n一緒にいると\n胸がせわしのー\nうごいてかなん", 101002, 432);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "たまには、王子はんの\n好きな絵本も\n知りたいな～", 101002, 433);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "王子はんを\n国に連れて帰ったら\nみんな大歓迎して\nくれるやろなぁ", 101002, 434);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "そのうち、王子はんの\nお国にも連れてって\nもらえますやろか？", 101002, 435);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "絵本読むんも\nええなぁ…", 101002, 436);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 8, "うちがもし悪い魔法に\nかかったら、王子はん\n口づけしてくれはる？", 101002, 437);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "うちら並んでたら\nお雛様みたいに\n見えると思いまへん？", 101002, 438);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "朝は小鳥はんも\nおなかがすいてるから\nよう飛び回りはるねえ", 101002, 439);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "マホマホ王国では\nこの時分は\nみんなお昼寝\nしてたんよ", 101002, 440);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜に飛んだり鳴いたり\nする鳥はんもおるんよ\n昼は寝てはるんかねぇ", 101002, 441);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "王子はんに\n会いに行くなら\nおめかしせな\nあかんなぁ", 101002, 442);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "王子はんも一緒に\nゆっくりしていっても\nええんよ？", 101002, 443);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かいらしい子やねぇ\nうちとお友だちに\nなってくれるん？", 101002, 444);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "癒しアイテムって\nほんま、好っきゃわぁ", 101002, 445);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "うちのお城にも\nほしいわぁ。帰ったら\nさっそく準備せな\nあかんなぁ", 101002, 446);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "部屋の中で運動\nできるのも、なかなか\n面白いもんやわぁ", 101002, 447);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "おいひいわ～\nマホマホ王国には\nない味やわぁ", 101002, 448);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "しっかり知識を\n身につけんと\n魔法も使えへん\nからなぁ", 101002, 449);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "１、２、３、４…\nあれ？　いま、\n何段目まで\n編んだっけ？", 101101, 451);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "おやつのケーキは\n苺かチョコ…\nどっちも食べよう！", 101101, 452);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "い、いけない！\n縫い針落としちゃった", 101101, 453);
